const List deAburidamuhammad = [
  {"surah_number": 1, "verse_number": 1, "content": "Im Namen Allahs, des Allerbarmers, des Barmherzigen"},
  {"surah_number": 1, "verse_number": 2, "content": "Alles Lob gebuhrt Allah, dem Herrn der Welten"},
  {"surah_number": 1, "verse_number": 3, "content": "dem Allerbarmer, dem Barmherzigen"},
  {"surah_number": 1, "verse_number": 4, "content": "dem Herrscher am Tage des Gerichts"},
  {"surah_number": 1, "verse_number": 5, "content": "Dir (allein) dienen wir, und Dich (allein) bitten wir um Hilfe"},
  {"surah_number": 1, "verse_number": 6, "content": "Fuhre uns den geraden Weg"},
  {"surah_number": 1, "verse_number": 7, "content": "den Weg derer, denen Du Gnade erwiesen hast, nicht (den Weg) derer, die(Deinen) Zorn erregt haben, und nicht (den Weg) der Irregehenden"},
  {"surah_number": 2, "verse_number": 1, "content": "Alif Lam Mim"},
  {"surah_number": 2, "verse_number": 2, "content": "Dies ist (ganz gewiß) das Buch (Allahs), das keinen Anlaß zum Zweifel gibt, (es ist) eine Rechtleitung fur die Gottesfurchtigen"},
  {"surah_number": 2, "verse_number": 3, "content": "die an das Verborgene glauben und das Gebet verrichten und von dem ausgeben, was Wir ihnen beschert haben"},
  {"surah_number": 2, "verse_number": 4, "content": "und die an das glauben, was auf dich und vor dir herabgesandt wurde, und die mit dem Jenseits fest rechnen"},
  {"surah_number": 2, "verse_number": 5, "content": "Diese folgen der Leitung ihres Herrn und diese sind die Erfolgreichen"},
  {"surah_number": 2, "verse_number": 6, "content": "Wahrlich, denen, die unglaubig sind, ist es gleich, ob du sie warnst oder nicht warnst: sie glauben nicht"},
  {"surah_number": 2, "verse_number": 7, "content": "Versiegelt hat Allah ihre Herzen und ihr Gehor; und uber ihren Augen liegt ein Schleier; ihnen wird eine gewaltige Strafe zuteil sein"},
  {"surah_number": 2, "verse_number": 8, "content": "Und manche Menschen sagen: \"Wir glauben an Allah und an den Jungsten Tag\", doch sie sind keine Glaubigen"},
  {"surah_number": 2, "verse_number": 9, "content": "Sie versuchen, Allah und die Glaubigen zu betrugen, und doch betrugen sie nur sich selbst, ohne daß sie dies empfinden"},
  {"surah_number": 2, "verse_number": 10, "content": "In ihren Herzen ist eine Krankheit, und Allah mehrt ihre Krankheit, und fur sie ist eine schmerzliche Strafe dafur (bestimmt), daß sie logen"},
  {"surah_number": 2, "verse_number": 11, "content": "Und wenn ihnen gesagt wird: \"Stiftet kein Unheil auf der Erde\", so sagen sie: \"Wir sind doch die, die Gutes tun"},
  {"surah_number": 2, "verse_number": 12, "content": "Gewiß jedoch sind sie die, die Unheil stiften, aber sie empfinden es nicht"},
  {"surah_number": 2, "verse_number": 13, "content": "Und wenn ihnen gesagt wird: \"Glaubt wie die Menschen geglaubt haben\", sagen sie: \"Sollen wir etwa wie die Toren glauben?\" Gewiß jedoch sind sie selbst die Toren, aber sie wissen es nicht"},
  {"surah_number": 2, "verse_number": 14, "content": "Und wenn sie mit den Glaubigen zusammentreffen, so sagen sie: \"Wir glauben\". Wenn sie aber mit ihren Satanen allein sind, sagen sie: \"Wir sind ja mit euch; wir treiben ja nur Spott"},
  {"surah_number": 2, "verse_number": 15, "content": "Allah verspottet sie und laßt sie weiter verblendet umherirren"},
  {"surah_number": 2, "verse_number": 16, "content": "Diese sind es, die das Irregehen gegen die Rechtleitung eingetauscht haben, doch ihr Handel brachte ihnen weder Gewinn, noch werden sie rechtgeleitet"},
  {"surah_number": 2, "verse_number": 17, "content": "Ihr Beispiel ist dem Beispiel dessen gleich, der ein Feuer anzundet; und als es nun alles um ihn herum erleuchtet hatte, ließ Allah ihr Licht verschwinden und ließ sie in Finsternissen zuruck, und sie sahen nichts"},
  {"surah_number": 2, "verse_number": 18, "content": "taub, stumm und blind; und so kehrten sie nicht um"},
  {"surah_number": 2, "verse_number": 19, "content": "Oder (ihr Beispiel ist) gleich (jenen bei) einem Regenguß vom Himmel, voller Finsternisse, Donner und Blitz; sie stecken ihre Finger in ihre Ohren in Todesangst vor den Donnerschlagen. Und Allah hat die Unglaubigen in Seiner Gewalt"},
  {
    "surah_number": 2,
    "verse_number": 20,
    "content": "Der Blitz raubt ihnen beinahe ihr Augenlicht: Sooft er ihnen Licht gibt, gehen sie hindurch, und wenn es dunkel um sie wird, so bleiben sie stehen. Und wenn Allah wollte, hatte Er ihnen gewiß Gehor und Augenlicht genommen. Wahrlich, Allah ist uber alle Dinge Machtig"
  },
  {"surah_number": 2, "verse_number": 21, "content": "O ihr Menschen, dient eurem Herrn, Der euch und diejenigen vor euch erschaffen hat, damit ihr gottesfurchtig sein moget"},
  {"surah_number": 2, "verse_number": 22, "content": "Der euch die Erde zu einer Ruhestatte und den Himmel zu einem Bau gemacht hat und vom Himmel Wasser herniedersandte und dadurch Fruchte als Gabe fur euch hervorbrachte, darum setzt Allah nichts gleich, wo ihr doch wisset"},
  {"surah_number": 2, "verse_number": 23, "content": "Und wenn ihr im Zweifel seid uber das, was Wir auf Unseren Diener herabgesandt haben, so bringt doch eine Sura gleicher Art herbei und beruft euch auf eure Zeugen außer Allah, wenn ihr wahrhaftig seid"},
  {"surah_number": 2, "verse_number": 24, "content": "Und wenn ihr es aber nicht tut und ihr werdet es bestimmt nicht tun so furchtet das Feuer, dessen Brennstoff Menschen und Steine sind; es ist fur die Unglaubigen vorbereitet"},
  {
    "surah_number": 2,
    "verse_number": 25,
    "content":
        "Und verkunde die frohe Botschaft denjenigen, die glauben und Gutes tun, auf daß ihnen Garten zuteil werden, in deren Niederungen Bache fließen; und sooft sie eine Frucht daraus bekommen, sagen sie: \"Das ist doch das, was wir schon fruher zu essen bekamen.\" Doch ihnen wird nur Ahnliches gegeben. Und ihnen gehoren darin Gattinnen vollkommener Reinheit und sie werden ewig darin bleiben"
  },
  {
    "surah_number": 2,
    "verse_number": 26,
    "content":
        "Wahrlich, Allah schamt sich nicht, irgendein Gleichnis zu pragen von einer Mucke oder von etwas Hoherem. Nun diejenigen, die glauben, wissen, daß es die Wahrheit von ihrem Herrn ist. Diejenigen aber, die unglaubig sind, sagen: \"Was wollte denn Allah mit einem solchen Gleichnis?\" Er fuhrt damit viele irre und leitet viele auch damit recht. Doch die Frevler fuhrt Er damit irre"
  },
  {"surah_number": 2, "verse_number": 27, "content": "die den Bund Allahs brechen, nachdem dieser geschlossen wurde, und die zerreißen, was nach Allahs Gebot zusammengehalten werden soll, und Unheil auf der Erde anrichten. Diese sind die Verlierer"},
  {"surah_number": 2, "verse_number": 28, "content": "Wie konnt ihr Allah leugnen, wo ihr doch tot waret und Er euch lebendig machte und euch dann sterben laßt und euch dann (am Jungsten Tag) lebendig macht, an dem ihr zu Ihm zuruckkehrt"},
  {"surah_number": 2, "verse_number": 29, "content": "Er ist es, Der fur euch alles auf der Erde erschuf; als dann wandte Er Sich den Himmeln zu und richtete sie zu sieben Himmeln auf; und Er ist aller (Dinge) kundig"},
  {
    "surah_number": 2,
    "verse_number": 30,
    "content":
        "Und als dein Herr zu den Engeln sprach: \"Wahrlich, Ich werde auf der Erde einen Nachfolger einsetzen\", sagten sie: \"Willst Du auf ihr jemanden einsetzen, der auf ihr Unheil anrichtet und Blut vergießt, wo wir doch Dein Lob preisen und Deine Herrlichkeit ruhmen?\" Er sagte: \"Wahrlich, Ich weiß, was ihr nicht wisset"
  },
  {"surah_number": 2, "verse_number": 31, "content": "Und Er brachte Adam alle Namen bei, dann brachte Er diese vor die Engel und sagte: \"Nennt mir die Namen dieser Dinge, wenn ihr wahrhaftig seid"},
  {"surah_number": 2, "verse_number": 32, "content": "Sie sprachen: \"Gepriesen seist Du. Wir haben kein Wissen außer dem, was Du uns gelehrt hast; wahrlich, Du bist der Allwissende, der Allweise"},
  {
    "surah_number": 2,
    "verse_number": 33,
    "content": "Er sprach: \"O Adam, nenne ihnen ihre Namen!\" Und als er ihnen ihre Namen nannte, sprach Er: \"Habe Ich nicht gesagt, daß Ich das Verborgene der Himmel und der Erde kenne, und daß Ich kenne, was ihr offenbart und was ihr verborgen gehalten habt"
  },
  {"surah_number": 2, "verse_number": 34, "content": "Und als Wir zu den Engeln sprachen: \"Werft euch vor Adam nieder\", da warfen sie sich nieder bis auf Iblis; er weigerte sich und war hochmutig. Und damit wurde er einer der Unglaubigen"},
  {"surah_number": 2, "verse_number": 35, "content": "Und Wir sprachen: \"O Adam, verweile du und deine Gattin im Paradies und esset uneingeschrankt von seinen Fruchten, wo immer ihr wollt! Kommt jedoch diesem Baum nicht nahe, sonst wurdet ihr zu den Ungerechten gehoren"},
  {
    "surah_number": 2,
    "verse_number": 36,
    "content": "Doch Satan ließ sie dort straucheln und brachte sie aus dem Zustand heraus, in dem sie waren. Da sprachen Wir: \"Geht (vom Paradies) hinunter! Der eine von euch sei des anderen Feind. Und ihr sollt auf der Erde Wohnstatten und Versorgung auf beschrankte Dauer haben"
  },
  {"surah_number": 2, "verse_number": 37, "content": "Da empfing Adam von seinem Herrn Worte, worauf Er ihm verzieh; wahrlich, Er ist der Allverzeihende, der Barmherzige"},
  {"surah_number": 2, "verse_number": 38, "content": "Wir sprachen: \"Geht hinunter von hier allesamt!\" Und wenn dann zu euch Meine Rechtleitung kommt, brauchen diejenigen, die Meiner Rechtleitung folgen, weder Angst zu haben, noch werden sie traurig sein"},
  {"surah_number": 2, "verse_number": 39, "content": "Diejenigen aber, die unglaubig sind und Unsere Zeichen fur Luge erklaren, werden Bewohner des Feuers sein, in dem sie auf ewig verweilen sollen"},
  {"surah_number": 2, "verse_number": 40, "content": "O ihr Kinder Israels! Gedenkt Meiner Gnade, die Ich euch erwiesen habe und erfullt euer Versprechen Mir gegenuber, so erfulle Ich Mein Versprechen euch gegenuber. Und Mich allein sollt ihr furchten"},
  {
    "surah_number": 2,
    "verse_number": 41,
    "content": "Und glaubt an das, was Ich als Bestatigung dessen herabgesandt habe, was bei euch ist, und seid nicht die ersten, die dies verleugnen! Und tauscht Meine Zeichen nicht ein gegen einen geringen Preis, und Mir allein gegenuber sollt ihr ehrfurchtig sein"
  },
  {"surah_number": 2, "verse_number": 42, "content": "Und mischt nicht Wahrheit mit Unrecht durcheinander! Und verschweigt nicht die Wahrheit, wo ihr (sie) doch kennt"},
  {"surah_number": 2, "verse_number": 43, "content": "Und verrichtet das Gebet und entrichtet die Zakah und verneigt euch mit den Sich- Verneigenden"},
  {"surah_number": 2, "verse_number": 44, "content": "Wollt ihr den Menschen Aufrichtigkeit gebieten und euch selbst vergessen, wo ihr doch das Buch lest! Habt ihr denn keinen Verstand"},
  {"surah_number": 2, "verse_number": 45, "content": "Und helft euch durch Geduld und Gebet; dies ist wahrlich schwer, außer fur Demutige"},
  {"surah_number": 2, "verse_number": 46, "content": "welche es ahnen, daß sie ihrem Herrn begegnen und daß sie zu Ihm heimkehren werden"},
  {"surah_number": 2, "verse_number": 47, "content": "O ihr Kinder Israels! Gedenkt Meiner Gnade, mit der Ich euch begnadete und (denkt daran,) daß Ich euch allen Welten vorgezogen habe"},
  {"surah_number": 2, "verse_number": 48, "content": "Und meidet den Tag, an dem keine Seele fur eine andere burgen kann und von ihr weder Fursprache noch Losegeld angenommen wird; und ihnen wird nicht geholfen"},
  {"surah_number": 2, "verse_number": 49, "content": "Und denkt daran, daß Wir euch vor den Leuten des Pharao retteten, die euch schlimme Pein zufugten, indem sie eure Sohne abschlachteten und eure Frauen am Leben ließen. Darin lag eine schwere Prufung von eurem Herrn"},
  {"surah_number": 2, "verse_number": 50, "content": "Und denkt daran, daß Wir fur euch das Meer teilten und euch retteten, wahrend Wir die Leute des Pharao vor euren Augen ertrinken ließen"},
  {"surah_number": 2, "verse_number": 51, "content": "Und denkt daran, daß Wir Uns mit Moses vierzig Nachte verabredeten, als ihr dann hinter seinem Rucken das Kalb nahmt und damit Unrecht begingt"},
  {"surah_number": 2, "verse_number": 52, "content": "Alsdann vergaben Wir euch, auf daß ihr dankbar sein moget"},
  {"surah_number": 2, "verse_number": 53, "content": "Und denkt daran, daß Wir Moses das Buch gaben, sowie die Unterscheidung, auf daß ihr rechtgeleitet werden moget"},
  {
    "surah_number": 2,
    "verse_number": 54,
    "content":
        "Und da sagte Moses zu seinen Leuten: \"O meine Leute! Ihr habt auf euch selbst eine schwere Schuld geladen, indem ihr euch das Kalb nahmt; so kehrt reumutig zu eurem Schopfer zuruck und totet selbst eure Schuldigen. Dies ist fur euch besser bei eurem Schopfer.\" Alsdann vergab Er euch; wahrlich, Er ist der Allvergebende, der Barmherzige"
  },
  {"surah_number": 2, "verse_number": 55, "content": "Und als ihr sagtet: \"O Moses! Wir werden dir gewiß nicht glauben, bis wir Allah unverhullt sehen\", da traf euch der Blitzschlag, wahrend ihr zuschautet"},
  {"surah_number": 2, "verse_number": 56, "content": "Dann erweckten Wir euch wieder nach eurem Tode, auf daß ihr dankbar sein moget"},
  {"surah_number": 2, "verse_number": 57, "content": "und Wir ließen die Wolken uber euch Schatten werfen und sandten euch Manna und Wachteln herab: \"Esset von den guten Dingen, die Wir euch gegeben haben\"; sie schadeten Uns aber nicht; vielmehr schadeten sie sich selbst"},
  {
    "surah_number": 2,
    "verse_number": 58,
    "content": "Und Wir sagten: \"Tretet ein in diese Stadt und esset von dort wo immer ihr wollt nach Herzenslust, und tretet durch das Tor ein, indem ihr euch niederwerft und sagt: \"Vergebung!\", auf daß Wir euch eure Missetaten vergeben. Und Wir werden den Rechtschaffenen mehr geben"
  },
  {"surah_number": 2, "verse_number": 59, "content": "Doch die Ungerechten vertauschen das Wort mit einem, das ihnen nicht gesagt wurde. Da sandten Wir auf die Ungerechten eine Strafe vom Himmel herab, weil sie gefrevelt hatten"},
  {
    "surah_number": 2,
    "verse_number": 60,
    "content": "Und als Moses fur sein Volk um Wasser bat, da sagten Wir: \"Schlag mit deinem Stock auf den Felsen.\" Da sprudelten aus ihm zwolf Quellen heraus. So kannte jeder Stamm seine Trinkstelle. \"Esset und trinkt von dem, was Allah euch gegeben hat, und richtet auf Erden kein Unheil an"
  },
  {
    "surah_number": 2,
    "verse_number": 61,
    "content":
        "Und als ihr sagtet: \"O Moses, wir konnen uns mit einer einzigen Speise nicht mehr zufriedengeben. Bitte also deinen Herrn fur uns, daß Er uns (Speise) von dem hervorbringe, was die Erde wachsen laßt, (von) Krautern, Gurken, Knoblauch, Linsen und Zwiebeln Da sagte er: \"Wollt ihr etwa das, was geringer ist, in Tausch nehmen fur das, was besser ist? Geht doch zuruck in eine Stadt. Dort werdet ihr das erhalten, was ihr verlangt!\" Und Schande und Elend kamen uber sie und sie verfielen dem Zorn Allahs. Dies (geschah deshalb), weil sie immer wieder die Zeichen Allahs leugneten und die Propheten zu Unrecht toteten; dies (geschah), weil sie sich auflehnten und immer wieder ubertraten"
  },
  {"surah_number": 2, "verse_number": 62, "content": "Wahrlich, diejenigen, die glauben, und die Juden, die Christen und die Sabaer, wer an Allah und den Jungsten Tag glaubt und Gutes tut diese haben ihren Lohn bei ihrem Herrn und sie werden weder Angst haben noch werden sie traurig sein"},
  {"surah_number": 2, "verse_number": 63, "content": "Und als Wir mit euch einen Bund schlossen und uber euch den Berg emporragen ließen (und zu euch sagten): \"Haltet fest an dem, was Wir euch gebracht haben, und gedenkt dessen, was darin enthalten ist; vielleicht werdet ihr gottesfurchtig sein"},
  {"surah_number": 2, "verse_number": 64, "content": "da habt ihr euch abgewandt; und wenn nicht die Gnade Allahs und Seine Barmherzigkeit uber euch gewesen waren, so waret ihr gewiß unter den Verlierenden gewesen"},
  {"surah_number": 2, "verse_number": 65, "content": "Und gewiß habt ihr diejenigen unter euch gekannt, die das SabbatGebot brachen. Da sprachen Wir zu ihnen: \"Werdet ausgestoßene Affen"},
  {"surah_number": 2, "verse_number": 66, "content": "Und Wir machten dies zu einem warnenden Beispiel fur alle Zeiten und zu einer Lehre fur die Gottesfurchtigen"},
  {"surah_number": 2, "verse_number": 67, "content": "Und als Moses zu seinem Volk sagte: \"Wahrlich, Allah befiehlt euch, eine Kuh zu schlachten\" sagten sie: \"Willst du dich uber uns lustig machen?\" Er sagte: \"Allah bewahre mich davor, einer der Unwissenden zu sein"},
  {
    "surah_number": 2,
    "verse_number": 68,
    "content": "Sie sagten: \"Bitte fur uns deinen Herrn, daß Er uns erklare, wie sie sein soll.\" Er sagte: \"Wahrlich, Er sagt, sie soll eine Kuh sein, die nicht zu alt und nicht zu jung ist, sondern ein Alter dazwischen hat. So tut das, was euch befohlen wird"
  },
  {"surah_number": 2, "verse_number": 69, "content": "Sie sagten: \"Rufe fur uns deinen Herrn an, daß Er uns erklare, welche Farbe sie haben soll.\" Er (Moses) sagte: \"Wahrlich, Er sagt, es soll eine gelbe Kuh sein von lebhafter Farbe, die die Schauenden erfreut"},
  {"surah_number": 2, "verse_number": 70, "content": "Sie sagten: \"Rufe fur uns deinen Herrn an, daß Er uns erklare, wie sie sein soll. Fur uns sind die Kuhe einander ahnlich; und wenn Allah will, werden wir gewiß rechtgeleitet sein"},
  {
    "surah_number": 2,
    "verse_number": 71,
    "content":
        "Er (Moses) sagte: \"Wahrlich, Er sagt, es soll eine Kuh sein, die nicht abgerichtet ist, die weder den Boden pflugt noch den Acker bewassert, makellos, ohne jeglichen Flecken.\" Da sagten sie: \"Jetzt bist du mit der Wahrheit gekommen.\" So schlachteten sie sie, und beinahe hatten sie es nicht getan"
  },
  {"surah_number": 2, "verse_number": 72, "content": "Und als ihr jemanden getotet und daruber untereinander gestritten hattet, da sollte Allah ans Licht bringen, was ihr verborgen hieltet"},
  {"surah_number": 2, "verse_number": 73, "content": "Da sagten Wir: \"Beruhrt ihn mit einem Stuck von ihr!\" So bringt Allah die Toten wieder zum Leben und zeigt euch Seine Zeichen; vielleicht werdet ihr es begreifen"},
  {
    "surah_number": 2,
    "verse_number": 74,
    "content":
        "Sodann verharteten sich eure Herzen, so daß sie wie Steine wurden oder noch harter. Es gibt doch Steine, aus denen Bache hervorsprudeln, und es gibt auch welche unter ihnen, die bersten und aus denen Wasser herausfließt. Und es gibt welche unter ihnen, die herniedersturzen aus Furcht vor Allah. Und Allah ist eures Tuns nicht achtlos"
  },
  {"surah_number": 2, "verse_number": 75, "content": "Verlangt ihr denn, daß sie euch glauben, wo doch eine Schar von ihnen das Wort Allahs bereits gehort und es dann, nachdem sie es begriffen hatten, bewußt verfalschten"},
  {
    "surah_number": 2,
    "verse_number": 76,
    "content":
        "Und wenn sie mit denen zusammentreffen, die glauben, so sagen sie: \"Wir glauben\". Und wenn sie aber untereinander allein sind, sagen sie: \"Sprecht ihr zu ihnen uber das, was Allah euch eroffnet hat, damit sie es vor eurem Herrn als Argument gegen euch verwenden? Begreift ihr denn nicht"
  },
  {"surah_number": 2, "verse_number": 77, "content": "Als ob sie nicht wußten, daß Allah weiß, was sie verheimlichen und was sie kundtun"},
  {"surah_number": 2, "verse_number": 78, "content": "Es gibt Ungelehrte unter ihnen, die das Buch nicht kennen, sondern nur Wunschvorstellungen; und sie stellen nichts anderes als Vermutungen an"},
  {
    "surah_number": 2,
    "verse_number": 79,
    "content": "Doch wehe denen, die das Buch mit ihren eigenen Handen schreiben und dann sagen: \"Dies ist von Allah\", um dafur einen geringen Preis zu erlangen! Wehe ihnen also ob dessen, was ihre Hande geschrieben und wehe ihnen ob dessen, was sie erworben haben"
  },
  {
    "surah_number": 2,
    "verse_number": 80,
    "content": "Und sie sagen: \"Gewiß wird uns das Feuer nicht beruhren, außer auf abgezahlte Tage!\" Sprich: \"Habt ihr etwa ein Versprechen (daruber) von Allah erhalten? Dann wird Allah Sein Versprechen bestimmt nicht brechen oder wollt ihr uber Allah (etwas) sagen, wovon ihr kein Wissen besitzt"
  },
  {"surah_number": 2, "verse_number": 81, "content": "Doch! Wer sich Ubeles erworben hat und sich in seiner Sundhaftigkeit eingefangen halt diese werden Bewohner des Feuers sein. Darin werden sie ewig bleiben"},
  {"surah_number": 2, "verse_number": 82, "content": "Diejenigen aber, die glauben und gute Werke tun, werden die Bewohner des Paradieses sein. Darin werden sie ewig bleiben"},
  {
    "surah_number": 2,
    "verse_number": 83,
    "content":
        "Und als Wir mit den Kindern Israels einen Bund schlossen: \"Ihr sollt niemanden außer Allah anbeten, euch den Eltern, Verwandten, Waisen und Armen gegenuber wohltatig erweisen, freundlich zu den Menschen sprechen, das Gebet verrichten und die Zakah entrichten\", so habt ihr euch danach abgewendet bis auf wenige unter euch, indem ihr abtrunnig bliebt"
  },
  {"surah_number": 2, "verse_number": 84, "content": "Und als Wir mit euch einen Bund schlossen: \"Ihr sollt weder euer Blut vergießen noch euch gegenseitig aus euren Hausern vertreiben\", habt ihr es dann zugesagt und es bezeugt"},
  {
    "surah_number": 2,
    "verse_number": 85,
    "content":
        "Dennoch seid gerade ihr es, die ihr euch gegenseitig totet und einen Teil von euch aus seinen Hausern vertreibt, indem ihr gemeinsam gegen sie vorgeht in Sunde und Unrecht. Wenn sie jedoch als Gefangene zu euch kommen, kauft ihr sie los, wo euch doch ihre Vertreibung verboten worden ist. Glaubt ihr denn an einen Teil des Buches und leugnet einen anderen Teil? Fur diejenigen unter euch, die solches tun, gibt es aber keine andere Vergeltung außer Schande in diesem Leben; und am Tage der Auferstehung werden sie der strengsten Bestrafung zugefuhrt werden. Und Allah ist eures Tuns nicht achtlos"
  },
  {"surah_number": 2, "verse_number": 86, "content": "Diese sind es, die das diesseitige Leben um das jenseitige erkauft haben; deshalb wird ihnen die Strafe nicht erleichtert und ihnen nicht geholfen werden"},
  {
    "surah_number": 2,
    "verse_number": 87,
    "content":
        "Wahrlich, Wir gaben Moses das Buch und ließen ihm die Gesandten nachfolgen; und Wir gaben Jesus, dem Sohn Marias, die klaren Beweise und unterstutzten ihn durch heilige Eingebung. Doch sooft euch ein Gesandter etwas brachte, was euch nicht behagte, waret ihr hochmutig und erklartet einige fur Lugner und erschluget andere"
  },
  {"surah_number": 2, "verse_number": 88, "content": "Und sie sagten: \"Unsere Herzen sind unempfindlich.\" Aber nein! Allah hat sie wegen ihres Unglaubens verflucht. Darum sind sie wenig glaubig"},
  {
    "surah_number": 2,
    "verse_number": 89,
    "content": "Und als zu ihnen ein Buch von Allah kam, das bestatigend, was ihnen vorlag und zuvor hatten sie (Ihn) um den Sieg angefleht uber diejenigen, die unglaubig waren; als aber zu ihnen das kam, was sie schon kannten, da leugneten sie es. Darum laste der Fluch Allahs auf den Unglaubigen"
  },
  {
    "surah_number": 2,
    "verse_number": 90,
    "content":
        "Schlecht ist das, wofur sie ihre Seelen verkauft haben, indem sie das leugnen, was Allah herabgesandt hat, aus Mißgunst, daß Allah etwas von Seiner Huld herabkommen lasse auf wen von Seinen Dienern Er auch immer will. So haben sie Zorn uber Zorn auf sich geladen, und den Unglaubigen wird eine erniedrigende Strafe zuteil sein"
  },
  {
    "surah_number": 2,
    "verse_number": 91,
    "content":
        "Wenn ihnen gesagt wird: \"Glaubt an das, was Allah herabgesandt hat\", sagen sie: \"Wir glauben an das, was uns herabgesandt wurde\", wahrend sie das leugnen, was danach kam, obgleich es um die Wahrheit geht, die das bestatigt, was in ihrem Besitz ist. Sprich: \"Warum habt ihr also die Propheten Allahs vordem getotet, wenn ihr Glaubige seid"
  },
  {"surah_number": 2, "verse_number": 92, "content": "Und Moses war zu euch mit den klaren Beweisen gekommen. Dann nahmt ihr euch das Kalb, nachdem er weggegangen war, und habt unrecht getan"},
  {
    "surah_number": 2,
    "verse_number": 93,
    "content":
        "Und als Wir mit euch einen Bund schlossen und uber euch den Berg emporragen ließen: \"Haltet fest an dem, was Wir euch gegeben haben und horet\", da sagten sie: \"Wir horen, doch wir widersetzen uns.\" Und sie wurden in ihren Herzen durch das Kalb trunken gemacht um ihres Unglaubens willen. Sprich: \"Schlecht ist das, was euer Glaube euch befiehlt, wenn ihr Glaubige seid"
  },
  {"surah_number": 2, "verse_number": 94, "content": "Sprich: \"Wenn die Wohnstatte des Jenseits bei Allah nur euch gehort, unter Ausschluß anderer Menschen, dann wunscht euch den Tod, wenn ihr wahrhaftig seid"},
  {"surah_number": 2, "verse_number": 95, "content": "Doch nie werden sie ihn herbeiwunschen wegen dessen, was ihre Hande vorausgeschickt haben, und Allah kennt die Ungerechten"},
  {
    "surah_number": 2,
    "verse_number": 96,
    "content":
        "Und bestimmt wirst du sie unter allen Menschen am gierigsten nach Leben finden, und mehr noch als diejenigen, die Gotzen anbeten. Manch einer von ihnen mochte, daß ihm ein Leben von tausend Jahren gewahrt wird; doch er halt sich dadurch von der Strafe nicht fern, (auch) wenn ihm ein hohes Alter gewahrt wurde. Und Allah sieht wohl, was sie tun"
  },
  {"surah_number": 2, "verse_number": 97, "content": "Sprich: \"Wer auch immer Gabriel zum Feind nimmt, so hat er ihn (den Quran) doch mit Ermachtigung Allahs in dein Herz herabgesandt als Bestatigung dessen, was vor ihm war, und als Rechtleitung und frohe Botschaft fur die Glaubigen"},
  {"surah_number": 2, "verse_number": 98, "content": "Wer auch immer zum Feind wurde gegen Allah und Seine Engel und Seine Gesandten und Gabriel und Michael, so ist wahrlich Allah den Unglaubigen ein Feind"},
  {"surah_number": 2, "verse_number": 99, "content": "Und Wir haben dir gewiß klare Zeichen herabgesandt und niemand leugnet sie außer den Frevlern"},
  {"surah_number": 2, "verse_number": 100, "content": "Ist es denn nicht immer so, daß jedesmal, wenn sie ein Bundnis eingegangen sind, ein Teil von ihnen es verwirft? Die meisten von ihnen glauben es doch nicht"},
  {"surah_number": 2, "verse_number": 101, "content": "Und als nunmehr zu ihnen ein Gesandter von Allah kam, das bestatigend, was in ihrem Besitz ist, da hat ein Teil von ihnen, denen das Buch gegeben wurde, das Buch Allahs hinter ihren Rucken geworfen, als ob sie nichts wußten"},
  {
    "surah_number": 2,
    "verse_number": 102,
    "content":
        "Und sie folgten dem, was die Satane wahrend der Herrschaft Salomos vortrugen; doch nicht Salomo war unglaubig, sondern. die Satane waren unglaubig; sie brachten den Menschen die Zauberei bei sowie das, was den beiden Engeln in Babel, Harut und Marut, herabgesandt wurde. Die beiden jedoch haben niemanden etwas gelehrt, ohne daß sie gesagt hatten: \"Wir sind nur eine Versuchung, so werde nicht unglaubig!\" Und sie lernten von den beiden das, womit man zwischen dem Mann und seiner Gattin Zwietracht herbeifuhrt. Doch sie fugten damit niemandem Schaden zu, es sei denn mit der Ermachtigung Allahs. Und sie lernten, was ihnen schadet und ihnen nichts nutzt. Und doch wußten sie, daß, wer es erkauft, keinen Anteil am Jenseits hat. Schlecht ist das wahrlich, wofur sie ihre Seelen verkauft haben, hatten sie es (nur) gewußt"
  },
  {"surah_number": 2, "verse_number": 103, "content": "Hatten sie aber geglaubt und waren gottesfurchtig gewesen, so ware eine Belohnung (fur sie) von Allah besser gewesen, hatten sie es nur gewußt"},
  {"surah_number": 2, "verse_number": 104, "content": "O ihr, die ihr glaubt, sagt nicht: \"Achte auf uns!\" sondern sagt: \"Schau auf uns!\" und hort (auf den Propheten) Und den Unglaubigen wird eine schmerzliche Strafe zuteil sein"},
  {
    "surah_number": 2,
    "verse_number": 105,
    "content": "Diejenigen, die unglaubig sind unter den Besitzern des Buches und den Gotzenanbetern, mochten nicht, daß euch etwas Gutes von eurem Herrn herabgesandt werde, doch Allah zeichnet mit Seiner Barmherzigkeit aus, wen Er will, und Allah besitzt die große Huld"
  },
  {"surah_number": 2, "verse_number": 106, "content": "Wenn Wir eine Aya aufheben oder der Vergessenheit anheimfallen lassen, so bringen Wir eine bessere als sie oder eine gleichwertige hervor. Weißt du denn nicht, daß Allah Macht uber alle Dinge hat"},
  {"surah_number": 2, "verse_number": 107, "content": "Weißt du denn nicht, daß Allah die Herrschaft uber die Himmel und die Erde gehort? Und außer Allah habt ihr weder Freund noch Helfer"},
  {"surah_number": 2, "verse_number": 108, "content": "Oder wollt ihr euren Gesandten ausfragen, wie fruher Moses ausgefragt wurde? Und wer den Unglauben gegen den Glauben eintauscht, der ist gewiß vom rechten Weg abgeirrt"},
  {
    "surah_number": 2,
    "verse_number": 109,
    "content":
        "Viele von den Besitzern des Buches mochten euch, nachdem ihr glaubig geworden seid, gern wieder zu Unglaubigen machen, aus Neid in ihren Seelen, nachdem ihnen die Wahrheit klar gemacht wurde. Doch vergebt und seid nachsichtig, bis Allah Seine Entscheidung ergehen laßt. Wahrlich, Allah hat zu allem die Macht"
  },
  {"surah_number": 2, "verse_number": 110, "content": "Und verrichtet das Gebet und gebt die Zakah, und was ihr fur euch an Gutem vorausschickt, das werdet ihr bei Allah vorfinden. Wahrlich! Allah sieht wohl, was ihr tut"},
  {"surah_number": 2, "verse_number": 111, "content": "Und sie sagen: \"Es wird niemand in das Paradies eingehen außer Juden und Christen.\" Dies sind Wunschvorstellungen. Sprich: \"Bringt euren Beweis her, wenn ihr wahrhaftig seid"},
  {"surah_number": 2, "verse_number": 112, "content": "Doch wer sich Allah hingibt und Gutes tut, der hat seinen Lohn bei seinem Herrn; und diese werden weder Angst haben noch werden sie traurig sein"},
  {
    "surah_number": 2,
    "verse_number": 113,
    "content":
        "Und die Juden sagen: \"Die Christen stutzen sich auf nichts\", und die Christen sagen: \"Die Juden stutzen sich auf nichts\", wobei sie doch das Buch lesen. Genau so, wie sie reden, redeten diejenigen, die kein Wissen besitzen. Allah wird dann am Tag der Auferstehung zwischen ihnen uber das richten, woruber sie gestritten haben"
  },
  {
    "surah_number": 2,
    "verse_number": 114,
    "content":
        "Wer begeht mehr Unrecht als derjenige, der verhindert, daß in den Gebetsstatten Allahs Sein Name gerufen wird, und der fur ihre Zerstorung eifert? Jene durfen sie nicht anders als in Furcht betreten. Fur sie gibt es im Diesseits Schande und im Jenseits wird ihnen eine schwere Strafe zuteil sein"
  },
  {"surah_number": 2, "verse_number": 115, "content": "Und Allah gehort der Osten und der Westen; wo immer ihr euch also hinwendet, dort ist das Antlitz Allahs. Wahrlich, Allah ist Allumfassend, Allwissend"},
  {"surah_number": 2, "verse_number": 116, "content": "Und sie sagen: \"Allah hat Sich einen Sohn genommen.\" Gepriesen sei Er! Wahrlich, Ihm gehort, was in den Himmeln und auf der Erde ist alles ist Ihm untertan"},
  {"surah_number": 2, "verse_number": 117, "content": "Dem Schopfer der Himmel und der Erde! Wenn Er eine Sache beschließt, so sagt Er nur zu ihr: \"Sei!\" und sie ist"},
  {
    "surah_number": 2,
    "verse_number": 118,
    "content": "Und diejenigen, die kein Wissen besitzen, sagen: \"Warum spricht Allah nicht zu uns und sendet uns kein Zeichen?\" So, wie sie reden, redeten auch diejenigen vor ihnen. Ihre Herzen sind einander gleich. Wir haben die Ayat klargemacht fur Leute mit sicherem Glauben"
  },
  {"surah_number": 2, "verse_number": 119, "content": "Wir haben dich mit der Wahrheit gesandt als Verkunder der frohen Botschaft und Warner, und du bist fur die Gefahrten der Gahim nicht verantwortlich"},
  {
    "surah_number": 2,
    "verse_number": 120,
    "content":
        "Mit dir werden weder die Juden noch die Christen zufrieden sein, bis du ihrem Bekenntnis gefolgt bist. Sprich: \"Die Rechtleitung Allahs ist doch die wahre Rechtleitung.\" Und wenn du ihrem Ansinnen folgst, nachdem zu dir das Wissen gekommen ist, so wirst du vor Allah weder Freund noch Helfer haben"
  },
  {"surah_number": 2, "verse_number": 121, "content": "Diejenigen, denen Wir das Buch gegeben haben, lesen es, wie man es lesen soll. Diese glauben daran. Wer aber daran nicht glaubt diese sind doch die Verlierenden"},
  {"surah_number": 2, "verse_number": 122, "content": "O ihr Kinder Israels! Gedenkt Meiner Gnade, die Ich euch gewahrt habe, und (denkt daran,) daß Ich euch den Vorrang vor den Volkern gegeben habe"},
  {"surah_number": 2, "verse_number": 123, "content": "Und furchtet den Tag, an dem keine Seele fur eine andere etwas ubernehmen kann und von ihr weder Losegeld angenommen noch eine Fursprache ihr (etwas) nutzen wird. Diesen wird kein Beistand zuteil"},
  {
    "surah_number": 2,
    "verse_number": 124,
    "content":
        "Und als Abraham von seinem Herrn durch Worte gepruft wurde und er diese vollbrachte, (da) sprach Er: \"Ich werde dich zu einem Imam fur die Menschen machen.\" Da bat Abraham: \"Auch von meiner Nachkommenschaft.\" Er sprach: \"Mein Versprechen erstreckt sich nicht auf die, die Unrecht tun"
  },
  {
    "surah_number": 2,
    "verse_number": 125,
    "content":
        "Und als Wir das Haus zu einem Ort der Einkehr fur die Menschen machten sowie zu einer Sicherheit (sprachen Wir): \"Nehmt euch die Statte Abrahams zum Gebetsort.\" Und Wir haben Abraham und Ismael auferlegt: \"Reinigt Mein Haus fur die es Umkreisenden und (sich dorthin) Zuruckziehenden, die Sich-Verneigenden und SichNiederwerfenden"
  },
  {
    "surah_number": 2,
    "verse_number": 126,
    "content":
        "Und als Abraham sagte: \"Mein Herr, mach dies zu einem sicheren Ort und gib Fruchte den dort Wohnenden, wer von ihnen an Allah und den Jungsten Tag glaubt\", sagte Er: \"Auch den, der unglaubig ist, werde Ich fur kurze Zeit versorgen, als dann ihn ausliefern der Bestrafung des Feuers; und welch ein schlechtes Los ist das"
  },
  {"surah_number": 2, "verse_number": 127, "content": "Und als Abraham mit Ismael die Grundmauern des Hauses errichtete (, sagte er): \"Unser Herr, nimm von uns an; denn wahrlich, Du bist der Allhorende, der Allwissende"},
  {
    "surah_number": 2,
    "verse_number": 128,
    "content": "Und, unser Herr, mach uns Dir ergeben und aus unserer Nachkommenschaft eine Gemeinde, die Dir ergeben ist. Und zeige uns, wie wir Dich anbeten sollen und wende uns Deine Gnade wieder zu; denn wahrlich, Du bist der gnadig Sich-wieder- Zuwendende, der Barmherzige"
  },
  {"surah_number": 2, "verse_number": 129, "content": "Und, unser Herr, erwecke unter ihnen einen Gesandten aus ihrer Mitte, der ihnen Deine Worte verliest und sie das Buch und die Weisheit lehrt und sie lautert; denn wahrlich, Du bist der Allmachtige, der Allweise"},
  {"surah_number": 2, "verse_number": 130, "content": "Und wer verschmaht den Glauben Abrahams außer dem, der sich selbst zum Toren macht? Denn Wir hatten ihn bereits im Diesseits auserwahlt, und im Jenseits wird er gewiß unter den Rechtschaffenen sein"},
  {"surah_number": 2, "verse_number": 131, "content": "Als sein Herr zu ihm sagte: \"Ergib dich!\" sagte er: \"Ich ergebe mich dem Herrn der Welten"},
  {"surah_number": 2, "verse_number": 132, "content": "Und Abraham befahl es seinen Sohnen an und ebenso Jakob: \"Meine Sohne, Allah hat fur euch die Religion auserwahlt, deshalb sterbt nicht anders als (Allah) ergeben zu sein"},
  {
    "surah_number": 2,
    "verse_number": 133,
    "content": "Waret ihr etwa Zeugen als Jakob im Sterben lag, und als er zu seinen Sohnen sagte: \"Wem werdet ihr dienen, wenn ich weg bin?\" sagten sie: \"Wir dienen deinem Gott, dem Gott deiner Vater Abraham, Ismael und Isaak, dem Einzigen Gott, und Ihm sind wir ergeben"
  },
  {"surah_number": 2, "verse_number": 134, "content": "Dies ist eine Gemeinde der Vergangenheit; ihr wird zuteil, was sie sich erworben hat, und euch wird zuteil, was ihr euch erworben habt. Und ihr werdet nicht fur das verantwortlich sein, was jene getan haben"},
  {"surah_number": 2, "verse_number": 135, "content": "Und sie sagen: \"Seid ihr Juden oder Christen, dann werdet ihr rechtgeleitet sein.\" Sprich: \"Doch! Die Religion Abrahams, der rechtglaubig war und nicht den Gotzenanbetern angehorte"},
  {
    "surah_number": 2,
    "verse_number": 136,
    "content":
        "Sprecht: \"Wir glauben an Allah und an das, was uns herabgesandt worden ist, und was Abraham, Ismael, Isaak, Jakob und den Stammen (Israels) herabgesandt wurde, und was Moses und Jesus gegeben wurde, und was den Propheten von ihrem Herrn gegeben worden ist. Wir machen zwischen ihnen keinen Unterschied und Ihm sind wir ergeben"
  },
  {"surah_number": 2, "verse_number": 137, "content": "enn sie so glauben, wie ihr glaubt, dann werden sie rechtgeleitet sein; wenn sie sich aber abwenden, so sind sie nur in Abspaltung geraten. Doch Allah wird dir wider sie genugen, und Er ist der Allhorende, der Allwissende"},
  {"surah_number": 2, "verse_number": 138, "content": "Dies ist die Weisung Allahs, und wer hat eine schonere Weisung als Allah! Und Ihm dienen wir"},
  {"surah_number": 2, "verse_number": 139, "content": "Sprich: \"Wollt ihr etwa mit uns uber Allah streiten, wo Er unser Herr und euer Herr ist? Doch wir haben unsere Taten und ihr habt eure Taten, und Ihm sind wir aufrichtig zugetan"},
  {
    "surah_number": 2,
    "verse_number": 140,
    "content":
        "Oder wollt ihr etwa sagen, daß Abraham, Ismael, Isaak, Jakob und die Stamme (Israels) Juden oder Christen waren?\" Sprich: \"Wisset ihr es besser oder Allah?\" Und wer ist ungerechter als derjenige, der ein Zeugnis verbirgt, das er von Allah erhalten hat! Und Allah ist dessen nicht achtlos, was ihr tut"
  },
  {"surah_number": 2, "verse_number": 141, "content": "Dies ist eine Gemeinde der Vergangenheit; ihr wird zuteil, was sie sich erworben hat, und euch wird zuteil, was ihr euch erworben habt. Und ihr werdet nicht fur das verantwortlich sein, was jene getan haben"},
  {"surah_number": 2, "verse_number": 142, "content": "Die Toren unter den Menschen werden sagen: \"Was hat sie bewogen, sich von ihrer Qibla abzuwenden, nach der sie sich bisher gerichtet hatten?\" Sprich: \"Allah gehort der Osten und der Westen; Er leitet, wen Er will, zu einem geraden Weg"},
  {
    "surah_number": 2,
    "verse_number": 143,
    "content":
        "Und so machten Wir euch zu einer Gemeinde der Mitte, auf daß ihr Zeugen seiet uber die Menschen und auf daß der Gesandte Zeuge sei uber euch. Und Wir haben die Qibla, nach der du dich bisher gerichtet hattest, nur gemacht, damit Wir denjenigen, der dem Gesandten folgt, von demjenigen unterscheiden, der auf seinen Fersen eine Kehrtwendung macht; und dies war wahrlich schwer, außer fur diejenigen, die Allah rechtgeleitet hat. Und es ist nicht Allah, Der euren Glauben verloren gehen laßt; wahrlich, Allah ist gegenuber den Menschen Mitleidig, Barmherzig"
  },
  {
    "surah_number": 2,
    "verse_number": 144,
    "content":
        "Wir sehen, wie dein Gesicht sich dem Himmel suchend zukehrt, und Wir werden dich nun zu einer Qibla wenden, mit der du zufrieden sein wirst. So wende dein Gesicht in Richtung der heiligen Moschee, und wo immer ihr auch seid, wendet eure Gesichter in ihre Richtung. Diejenigen, denen das Buch gegeben wurde, wissen bestimmt, daß es die Wahrheit von ihrem Herrn ist. Und Allah ist dessen nicht achtlos, was sie tun"
  },
  {
    "surah_number": 2,
    "verse_number": 145,
    "content":
        "Doch, wenn du denjenigen, denen das Buch gegeben wurde, alle Beweise brachtest, wurden sie deine Qibla nicht befolgen. Und du befolgst ihre Qibla nicht; sie befolgen ja selbst untereinander ihre jeweilige Qibla nicht. Doch solltest du ihrem Ansinnen folgen nach dem, was dir an Wissen zugekommen ist, so wurdest du bestimmt zu denen gehoren, die Unrecht tun"
  },
  {"surah_number": 2, "verse_number": 146, "content": "Diejenigen, denen Wir das Buch gegeben haben, kennen es, wie sie ihre eigenen Sohne kennen; und dennoch verbergen einige von ihnen die Wahrheit, wo sie (sie) doch kennen"},
  {"surah_number": 2, "verse_number": 147, "content": "Dies ist die Wahrheit von deinem Herrn, darum sei nicht einer von denen, die daran zweifeln"},
  {"surah_number": 2, "verse_number": 148, "content": "Jeder hat eine Richtung, der er sich zuwendet. So wetteifert miteinander in guten Werken. Wo immer ihr auch seid, Allah wird euch allesamt herbeifuhren; wahrlich, Allah hat Macht uber alle Dinge"},
  {"surah_number": 2, "verse_number": 149, "content": "Und von wo du auch herkommst, wende dein Gesicht in Richtung der heiligen Moschee; denn dies ist gewiß die Wahrheit von deinem Herrn. Und Allah ist dessen nicht achtlos, was ihr tut"},
  {
    "surah_number": 2,
    "verse_number": 150,
    "content":
        "Und von wo du auch herkommst, wende dein Gesicht in Richtung der heiligen Moschee. Und wo immer ihr auch seid, wendet eure Gesichter in ihre Richtung, damit die Menschen keinen Beweisgrund gegen euch haben außer denjenigen unter ihnen, die unrecht tun; so furchtet nicht sie, sondern furchtet Mich. Und damit Ich Meine Gnade an euch vollende und damit ihr euch vielleicht werdet rechtleiten lassen"
  },
  {"surah_number": 2, "verse_number": 151, "content": "Wie Wir auch unter euch einen Gesandten aus eurer Mitte erstehen ließen, der euch Unsere Verse verliest und euch lautert und euch das Buch und die Weisheit lehrt und euch lehrt, was ihr nicht wußtet"},
  {"surah_number": 2, "verse_number": 152, "content": "so gedenkt also Meiner, damit Ich euer gedenke; und seid Mir dankbar und verleugnet Mich nicht"},
  {"surah_number": 2, "verse_number": 153, "content": "O ihr, die ihr glaubt, sucht Hilfe in der Geduld und im Gebet; wahrlich Allah ist mit den Geduldigen"},
  {"surah_number": 2, "verse_number": 154, "content": "Und nennt nicht diejenigen, die auf Allahs Weg getotet wurden, \"Tote\". Denn sie leben, ihr aber nehmt es nicht wahr"},
  {"surah_number": 2, "verse_number": 155, "content": "Und gewiß werden Wir euch prufen durch etwas Angst, Hunger und Minderung an Besitz, Menschenleben und Fruchten. Doch verkunde den Geduldigen eine frohe Botschaft"},
  {"surah_number": 2, "verse_number": 156, "content": "die, wenn sie ein Ungluck trifft, sagen: \"Wir gehoren Allah und zu Ihm kehren wir zuruck"},
  {"surah_number": 2, "verse_number": 157, "content": "Auf diese laßt ihr Herr Segnungen und Barmherzigkeit herab und diese werden rechtgeleitet sein"},
  {
    "surah_number": 2,
    "verse_number": 158,
    "content": "Wahrlich, As Safa und Al-Marwa gehoren zu den Kultstatten Allahs; und wer zu dem Hause pilgert oder die `Umra vollzieht, fur den ist es kein Vergehen, wenn er zwischen beiden hin- und herschreitet. Und wenn einer freiwillig Gutes tut, so ist Allah Erkenntlich, Allwissend"
  },
  {"surah_number": 2, "verse_number": 159, "content": "Diejenigen, die verbergen, was Wir von den klaren Beweisen und der Rechtleitung herabsandten, nachdem Wir es den Menschen im Buch erklart hatten, diese verflucht Allah, und diese verfluchen auch die Fluchenden"},
  {"surah_number": 2, "verse_number": 160, "content": "außer denjenigen, die sich reuevoll zuwenden, sich bessern und klarstellen. Denen wende Ich Meine Gnade wieder zu; denn Ich bin der gnadig Sich-wieder-Zuwendende, der Barmherzige"},
  {"surah_number": 2, "verse_number": 161, "content": "Wahrlich, diejenigen, die unglaubig sind und in ihrem Unglauben sterben, auf denen lastet der Fluch Allahs und der Engel und der Menschen insgesamt"},
  {"surah_number": 2, "verse_number": 162, "content": "Darin werden sie ewig sein. Die Strafe wird ihnen nicht erleichtert, und es wird ihnen kein Aufschub gewahrt"},
  {"surah_number": 2, "verse_number": 163, "content": "Euer Gott ist ein Einziger Gott, es ist kein Gott außer Ihm, dem Sich-Erbarmenden, dem Barmherzigen"},
  {
    "surah_number": 2,
    "verse_number": 164,
    "content":
        "Wahrlich, im Erschaffen der Himmel und der Erde und im Wechsel von Nacht und Tag und in den Schiffen, die im Meer fahren mit dem, was den Menschen nutzt, und in dem, was Allah vom Himmel an Wasser herniedersandte und Er gab der Erde damit Leben, nachdem sie tot war und ließ auf ihr allerlei Getier sich ausbreiten und im Wechsel der Winde und den dienstbaren Wolken zwischen Himmel und Erde, (in all dem) sind Zeichen fur Leute, die begreifen"
  },
  {
    "surah_number": 2,
    "verse_number": 165,
    "content":
        "Und es gibt unter den Menschen einige, die sich außer Allah Seinesgleichen (zum Anbeten) nehmen und lieben, wie man (nur) Allah lieben soll. Die aber, die glauben, lieben Allah noch mehr. Und wenn diejenigen, die Unrecht tun, angesichts der Strafe sehen, daß die Macht ganzlich bei Allah ist und daß Allah streng in der Bestrafung ist"
  },
  {"surah_number": 2, "verse_number": 166, "content": "(Dereinst,) wenn sich diejenigen, denen (im Diesseits) gefolgt wurde, von jenen lossagen, die ihnen gefolgt sind, und sie die Strafe sehen, wahrend ihnen die Bindungen abgeschnitten sind"},
  {
    "surah_number": 2,
    "verse_number": 167,
    "content": "Und (wenn) diejenigen, die (ihnen) gefolgt sind, sagen: \"Wenn wir zuruckkehren konnten, so wurden wir uns von ihnen lossagen, wie sie sich von uns losgesagt haben!\" So zeigt ihnen Allah ihre Taten als gramvolle Reue, und sie kommen aus dem Feuer nicht heraus"
  },
  {"surah_number": 2, "verse_number": 168, "content": "O ihr Menschen, esset von dem, was es auf der Erde an Erlaubtem und Gutem gibt, und folgt nicht den Fußstapfen des Satans; denn er ist euer offenkundiger Feind"},
  {"surah_number": 2, "verse_number": 169, "content": "Er gebietet euch nur Boses und Abscheuliches, und daß ihr uber Allah sagen sollt, was ihr nicht wisset"},
  {"surah_number": 2, "verse_number": 170, "content": "Und wenn ihnen gesagt wird: \"Folgt dem, was Allah herabgesandt hat\", so sagen sie: \"Wir folgen doch dem, bei dem wir unsere Vater vorgefunden haben\", auch, wenn ihre Vater nichts begriffen hatten und nicht rechtgeleitet gewesen waren"},
  {"surah_number": 2, "verse_number": 171, "content": "Das Gleichnis derjenigen, die unglaubig sind, ist wie das Gleichnis derjenigen, die irgendein (Tier) anschreien, das nichts hort außer Lauten und Zurufen. Taub, stumm und blind sind sie, darum begreifen sie nichts"},
  {"surah_number": 2, "verse_number": 172, "content": "O ihr, die ihr glaubt, esset von den guten Dingen, die Wir euch bereitet haben, und seid Allah dankbar, wenn ihr Ihm allein dient"},
  {
    "surah_number": 2,
    "verse_number": 173,
    "content":
        "Verboten hat Er euch nur (den Genuß von) naturlich Verendetem, Blut, Schweinefleisch und dem, woruber etwas anderes als Allah angerufen worden ist. Wenn aber jemand (dazu) gezwungen ist, ohne (es) zu begehren und ohne das Maß zu uberschreiten, so trifft ihn keine Schuld; wahrlich, Allah ist Allverzeihend, Barmherzig"
  },
  {
    "surah_number": 2,
    "verse_number": 174,
    "content":
        "Diejenigen, die verbergen, was Allah von dem Buch herabgesandt hat, und es um einen geringen Preis verkaufen, diese verzehren in ihren Bauchen nichts als Feuer. Und Allah wird zu ihnen am Tage der Auferstehung weder sprechen noch wird Er sie lautern; und ihnen wird eine schmerzliche Strafe zuteil sein"
  },
  {"surah_number": 2, "verse_number": 175, "content": "Sie sind es, die den Irrtum fur die Rechtleitung erkauft haben und die Strafe fur die Vergebung. Wie konnen sie dem Feuer gegenuber standhaft sein"},
  {"surah_number": 2, "verse_number": 176, "content": "Dies geschieht darum weil Allah das Buch mit der Wahrheit herabgesandt hat. Und diejenigen, die sich uber das Buch streiten, befinden sich gewiß in großer Feindschaft"},
  {
    "surah_number": 2,
    "verse_number": 177,
    "content":
        "Es ist keine Frommigkeit, wenn ihr eure Angesichter in Richtung Osten oder Westen wendet; Frommigkeit ist vielmehr, daß man an Allah glaubt, den Jungsten Tag, die Engel, das Buch und die Propheten und vom Besitz obwohl man ihn liebt den Verwandten gibt, den Waisen, den Armen, dem Sohn des Weges, den Bettlern und (fur den Freikauf von) Sklaven, daß man das Gebet verrichtet und die Zakah entrichtet. Es sind diejenigen, die ihr Versprechen einhalten, wenn sie es gegeben haben; und diejenigen, die in Elend, Not und in Kriegszeiten geduldig sind; sie sind es, die wahrhaftig und gottesfurchtig sind"
  },
  {
    "surah_number": 2,
    "verse_number": 178,
    "content":
        "O ihr, die ihr glaubt! Es ist euch die Wiedervergeltung vorgeschrieben fur die Getoteten: der Freie fur den Freien, der Sklave fur den Sklaven, das Weibliche fur das Weibliche. Doch wenn jemandem von seinem Bruder etwas vergeben wird, so soll der Vollzug auf geziemende Art und die Leistung ihm gegenuber auf wohltatige Weise geschehen. Dies ist eine Erleichterung von eurem Herrn und eine Barmherzigkeit. Wer nun von jetzt an (die Gesetze) ubertritt, dem wird eine schmerzliche Strafe zuteil sein"
  },
  {"surah_number": 2, "verse_number": 179, "content": "In der Wiedervergeltung ist Leben fur euch, o ihr, die ihr einsichtig seid! Vielleicht werdet ihr Allah furchten"},
  {
    "surah_number": 2,
    "verse_number": 180,
    "content": "Es ist euch vorgeschrieben, daß, wenn sich bei einem von euch der Tod einstellt, sofern er Gut hinterlaßt, den Eltern und den Verwandten auf geziemende Art ein Vermachtnis gemacht wird. Dies ist eine Verpflichtung gegenuber den Gottesfurchtigen"
  },
  {"surah_number": 2, "verse_number": 181, "content": "Doch wer es umandert, nachdem er es gehort hat so liegt die Schuld dafur nur bei denen, die es umandern. Wahrlich, Allah ist Allhorend, Allwissend"},
  {"surah_number": 2, "verse_number": 182, "content": "Wer aber seitens des Erblassers Unrecht oder Vergehen befurchtet und zwischen ihnen Frieden stiftet, so trifft ihn keine Schuld. Wahrlich, Allah ist Allverzeihend, Barmherzig"},
  {"surah_number": 2, "verse_number": 183, "content": "O ihr, die ihr glaubt! Das Fasten ist euch vorgeschrieben, so wie es denen vorgeschrieben war, die vor euch waren. Vielleicht werdet ihr (Allah) furchten"},
  {
    "surah_number": 2,
    "verse_number": 184,
    "content":
        "Es sind nur abgezahlte Tage. Und wer von euch krank ist oder sich auf einer Reise befindet, soll eine Anzahl anderer Tage (fasten) Und denen, die es mit großer Muhe ertragen konnen, ist als Ersatz die Speisung eines Armen auferlegt. Und wenn jemand freiwillig Gutes tut, so ist es besser fur ihn. Und daß ihr fastet, ist besser fur euch, wenn ihr es (nur) wußtet"
  },
  {
    "surah_number": 2,
    "verse_number": 185,
    "content":
        "Der Monat Ramadan ist es, in dem der Quran als Rechtleitung fur die Menschen herabgesandt worden ist und als klarer Beweis der Rechtleitung und der Unterscheidung. Wer also von euch in dem Monat zugegen ist, der soll in ihm fasten. Und wer krank ist oder sich auf einer Reise befindet, soll eine Anzahl anderer Tage (fasten) Allah will es euch leicht, Er will es euch nicht schwer machen damit ihr die Frist vollendet und Allah ruhmt, daß Er euch geleitet hat. Vielleicht werdet ihr dankbar sein"
  },
  {"surah_number": 2, "verse_number": 186, "content": "Und wenn dich Meine Diener uber Mich befragen, so bin Ich nahe; Ich hore den Ruf des Rufenden, wenn er Mich ruft. Deshalb sollen sie auf Mich horen und an Mich glauben. Vielleicht werden sie den rechten Weg einschlagen"},
  {
    "surah_number": 2,
    "verse_number": 187,
    "content":
        "Es ist euch erlaubt, euch in der Nacht des Fastens euren Frauen zu nahern; sie sind Geborgenheit fur euch und ihr seid Geborgenheit fur sie. Allah weiß, daß ihr gegen euch selbst trugerisch gehandelt habt, und Er wandte euch Seine Gnade wieder zu und vergab euch. So pflegt nun Verkehr mit ihnen und trachtet nach dem, was Allah fur euch bestimmt hat. Und esset und trinkt, bis der weiße Faden von dem schwarzen Faden der Morgendammerung fur euch erkennbar wird. Danach vollendet das Fasten bis zur Nacht. Und pflegt keinen Verkehr mit ihnen, wahrend ihr euch in die Moscheen zuruckgezogen habt. Dies sind die Schranken Allahs, so kommt ihnen nicht nahe! So erklart Allah den Menschen Seine Zeichen. Vielleicht werden sie (Ihn) furchten"
  },
  {"surah_number": 2, "verse_number": 188, "content": "Und verschlingt nicht euren Besitz untereinander in ungerechter Weise und bietet ihn nicht den Behorden an, um einen Teil vom Besitz der Menschen in sundhafter Weise zu verschlingen, wo ihr doch wisset"},
  {
    "surah_number": 2,
    "verse_number": 189,
    "content":
        "Sie fragen dich nach den Neumonden. Sprich: \"Sie sind festgesetzte Zeiten fur die Menschen und den Hagg.\" Und es ist keine Frommigkeit, wenn ihr Hauser von der Ruckseite betretet. Frommigkeit ist vielmehr, (Allah) zu furchten. So geht in die Hauser durch ihre Turen hinein und furchtet Allah. Vielleicht werdet ihr erfolgreich sein"
  },
  {"surah_number": 2, "verse_number": 190, "content": "Und kampft auf dem Weg Allahs gegen diejenigen, die gegen euch kampfen, doch ubertretet nicht. Wahrlich, Allah liebt nicht diejenigen, die ubertreten"},
  {
    "surah_number": 2,
    "verse_number": 191,
    "content":
        "Und totet sie, wo immer ihr auf sie stoßt, und vertreibt sie, von wo sie euch vertrieben haben; denn die Verfuhrung (zum Unglauben) ist schlimmer als Toten. Und kampft nicht gegen sie bei der heiligen Moschee, bis sie dort gegen euch kampfen. Wenn sie aber gegen euch kampfen, dann totet sie. Solcherart ist der Lohn der Unglaubigen"
  },
  {"surah_number": 2, "verse_number": 192, "content": "Wenn sie aber aufhoren, so ist Allah Allverzeihend, Barmherzig"},
  {"surah_number": 2, "verse_number": 193, "content": "Und kampft gegen sie, bis es keine Verwirrung (mehr) gibt und die Religion Allah gehort. Wenn sie aber aufhoren, so soll es keine Gewalttatigkeit geben außer gegen diejenigen, die Unrecht tun"},
  {
    "surah_number": 2,
    "verse_number": 194,
    "content":
        "Der heilige Monat ist fur den heiligen Monat, und fur die geschutzten Dinge ist Wiedervergeltung (bestimmt) Wer nun gegen euch gewalttatig handelt, gegen den handelt in gleichem Maße gewalttatig, wie er gegen euch gewalttatig war, und furchtet Allah und wisset, daß Allah mit den Gottesfurchtigen ist"
  },
  {"surah_number": 2, "verse_number": 195, "content": "Und spendet auf dem Weg Allahs und sturzt euch nicht mit eigenen Handen ins Verderben und tut Gutes! Wahrlich, Allah liebt diejenigen, die Gutes tun"},
  {
    "surah_number": 2,
    "verse_number": 196,
    "content":
        "Und vollendet den Hagg und die `Umra fur Allah. Und wenn ihr daran gehindert werdet, so bringt ein Opfertier dar, das euch (zu opfern) leicht fallt. Und schert nicht eure Haupter, bis das Opfertier seinen Bestimmungsort erreicht hat. Und wer von euch krank ist oder an seinem Haupt ein Leiden hat, der soll Ersatz leisten durch Fasten, Mildtatigkeit oder Darbringen eines Schlachtopfers. Und wenn ihr euch sicher fuhlt, dann soll der, der die 'Umra mit dem Hagg vollziehen mochte, ein Opfertier (darbringen), das ihm (zu opfern) leicht fallt. Und wer keines zu finden vermag, soll drei Tage wahrend des Hagg fasten und sieben, wenn ihr zuruckgekehrt seid. Dies sind zehn insgesamt. Dies gilt fur den, dessen Angehorige nicht nahe der heiligen Moschee wohnen. Und seid gottesfurchtig und wisset, daß Allah streng ist im Strafen"
  },
  {
    "surah_number": 2,
    "verse_number": 197,
    "content":
        "Fur den Hagg sind bekannte Monate (vorgesehen) Wer sich in ihnen zum Hagg entschlossen hat, der enthalte sich des Beischlafs und begehe weder Frevel noch unziemliche Rede wahrend des Hagg. Und was ihr an Gutem tut, Allah weiß es. Und sorgt fur die Reise, doch wahrlich, die beste Vorsorge ist Gottesfurcht. Und furchtet Mich, o ihr, die ihr einsichtig seid"
  },
  {
    "surah_number": 2,
    "verse_number": 198,
    "content": "Es ist kein Vergehen von euch, wenn ihr nach der Gunst eures Herrn strebt. Und wenn ihr von `Arafat herbeieilt, dann gedenkt Allahs bei AI-Masari-l- haram. Und gedenkt Seiner, wie Er euch rechtgeleitet hat, obwohl ihr wahrlich vordem unter jenen waret, die irregingen"
  },
  {"surah_number": 2, "verse_number": 199, "content": "Dann eilt dorthin weiter, von wo die Menschen weitereilen, und bittet Allah um Vergebung. Wahrlich, Allah ist Allverzeihend, Barmherzig"},
  {
    "surah_number": 2,
    "verse_number": 200,
    "content":
        "Und wenn ihr eure heiligen Riten beendet habt, dann gedenkt Allahs, so wie ihr eurer Vater zu gedenken pflegt oder vielmehr mit noch starkerem Gedenken. Denn unter den Menschen sind welche, die sagen: \"Unser Herr, gib uns Gutes in dieser Welt.\" Doch haben sie nicht im Jenseits Anteil am Guten"
  },
  {"surah_number": 2, "verse_number": 201, "content": "Und unter ihnen sind manche, die sagen: \"Unser Herr, gib uns in dieser Welt Gutes und im Jenseits Gutes und verschone uns vor der Strafe des Feuers"},
  {"surah_number": 2, "verse_number": 202, "content": "Diesen wird ein Anteil von dem (gewahrt), was sie erworben haben; und Allah ist schnell im Abrechnen"},
  {
    "surah_number": 2,
    "verse_number": 203,
    "content": "Und gedenkt Allahs wahrend einer bestimmten Anzahl von Tagen; und wer eilig in zwei Tagen aufbricht, den trifft keine Schuld, und wer langer bleibt, den trifft keine Schuld, wenn er gottesfurchtig ist. Und furchtet Allah und wisset, daß ihr zu Ihm gefuhrt werdet"
  },
  {"surah_number": 2, "verse_number": 204, "content": "Und unter den Menschen gibt es manch einen, dessen Rede uber diese Welt dich in Verwunderung versetzen mag; und er ruft Allah zum Zeugen an fur das, was in seinem Herzen ist. Und dabei ist er der streitsuchtigste Widersacher"},
  {"surah_number": 2, "verse_number": 205, "content": "Wenn er sich abwendet, bemuht er sich, uberall auf der Erde Unheil zu stiften, und vernichtet das Ackerland und die Nachkommenschaft. Und Allah liebt das Unheil nicht"},
  {"surah_number": 2, "verse_number": 206, "content": "Und wenn ihm gesagt wird: \"Furchte Allah\", uberwaltigt ihn sundhafter Stolz. Gahannam ist ein angemessenes Entgelt fur ihn was fur eine schlechte Ruhestatte"},
  {"surah_number": 2, "verse_number": 207, "content": "Und unter den Menschen ist manch einer, der sich selbst hergibt im Verlangen nach Allahs Wohlgefallen. Und Allah ist wahrlich gutig gegen die Diener"},
  {"surah_number": 2, "verse_number": 208, "content": "O ihr, die ihr glaubt! Tretet allesamt ein in das Heil und folgt nicht den Fußstapfen des Satans; wahrlich, er ist euer offenkundiger Feind"},
  {"surah_number": 2, "verse_number": 209, "content": "Und wenn ihr straucheln solltet, nachdem die Beweise zu euch gekommen sind, dann wisset, daß Allah Allmachtig ist und Allweise"},
  {"surah_number": 2, "verse_number": 210, "content": "Erwarten sie etwa, daß Allah ihnen beschirmt von Wolken erscheine und mit Ihm die Engel doch dann ist die Sache schon entschieden. Und zu Allah werden alle Dinge zuruckgefuhrt"},
  {"surah_number": 2, "verse_number": 211, "content": "Frage die Kinder Israels, wieviel klare Beweise Wir ihnen gegeben haben. Doch wer die Gnade Allahs eintauscht, nachdem sie ihm zuteil geworden ist dann ist Allah wahrlich streng im Strafen"},
  {
    "surah_number": 2,
    "verse_number": 212,
    "content": "Anziehend erscheint denen, die unglaubig sind, das diesseitige Leben, und sie verhohnen diejenigen, die glauben. Doch diejenigen, die gottesfurchtig sind, werden am Tag der Auferstehung uber ihnen stehen. Und Allah beschert, wem Er will, ohne Maß"
  },
  {
    "surah_number": 2,
    "verse_number": 213,
    "content":
        "Die Menschen waren eine einzige Gemeinschaft. Dann entsandte Allah die Propheten als Bringer froher Botschaft und als Warner. Und Er offenbarte ihnen das Buch mit der Wahrheit, um zwischen den Menschen zu richten uber das, woruber sie uneins waren. Uneins aber waren nur jene, denen es gegeben wurde, nachdem klare Beweise zu ihnen gekommen waren, aus Mißgunst untereinander. Doch Allah leitet mit Seiner Erlaubnis diejenigen, die glaubig sind, zur Wahrheit, uber die sie uneins waren. Und Allah leitet, wen Er will, auf einen geraden Weg"
  },
  {
    "surah_number": 2,
    "verse_number": 214,
    "content":
        "Oder meint ihr etwa, ihr wurdet ins Paradies eingehen, ohne (daß etwas) Ahnliches uber euch gekommen sei wie uber diejenigen, die vor euch dahingegangen sind? Not und Unheil erfaßte sie, und sie sind erschuttert worden, bis der Gesandte und diejenigen, die mit ihm glaubig waren, sagten: \"Wann kommt die Hilfe Allahs?\" Doch wahrlich, Allahs Hilfe ist nahe"
  },
  {"surah_number": 2, "verse_number": 215, "content": "Sie befragen dich, was sie spenden sollen. Sprich: \"Was immer ihr spendet an Gutem, das sei fur die Eltern und Verwandten und die Waisen und die Armen und den Sohn des Weges. Und was immer ihr an Gutem tut, wahrlich, Allah weiß es wohl"},
  {
    "surah_number": 2,
    "verse_number": 216,
    "content": "Zu kampfen ist euch vorgeschrieben auch wenn es euch widerwartig ist. Doch es mag sein, daß euch etwas widerwartig ist, was gut fur euch ist, und es mag sein, daß euch etwas lieb ist, was ubel fur euch ist. Und Allah weiß es, doch ihr wisset es nicht"
  },
  {
    "surah_number": 2,
    "verse_number": 217,
    "content":
        "Sie befragen dich uber das Kampfen im heiligen Monat. Sprich: \"Das Kampfen in ihm ist schwerwiegend. Doch das Abbringen vom Weg Allahs und nicht an Ihn zu glauben und (den Zutritt) zur heiligen Moschee (zu verwehren) und deren Bewohner daraus zu vertreiben, ist schwerwiegender vor Allah. Und die Verfuhrung ist schwerwiegender als Toten.\" Und sie werden nicht ablassen, gegen euch zu kampfen, bis sie euch von eurem Glauben abbringen, wenn sie dazu imstande sind. Wer sich aber von euch von seinem Glauben abbringen laßt und als Unglaubiger stirbt das sind diejenigen, deren Taten wertlos sein werden in dieser Welt und im Jenseits. Sie werden die Bewohner des Feuers sein, und darin werden sie ewig verweilen"
  },
  {"surah_number": 2, "verse_number": 218, "content": "Wahrlich, jene, die glauben und ausgewandert sind und sich auf dem Weg Allahs mit aller Kraft einsetzen, die sind es, die auf die Barmherzigkeit Allahs hoffen. Und Allah ist Allverzeihend, Barmherzig"},
  {
    "surah_number": 2,
    "verse_number": 219,
    "content":
        "Sie befragen dich uber Berauschendes und Glucksspiel. Sprich: \"In beiden liegt großes Ubel und Nutzen fur die Menschen. Doch ihr Ubel ist großer als ihr Nutzen.\" Und sie befragen dich was sie spenden sollen. Sprich: \"Den Uberschuß.\" So macht euch Allah die Zeichen klar, damit ihr nachdenken moget"
  },
  {
    "surah_number": 2,
    "verse_number": 220,
    "content":
        "uber diese Welt und das Jenseits. Sie befragen dich uber die Waisen. Sprich: \"Ihre Lage zu verbessern ist gut. Und wenn ihr ihre Angelegenheiten mit den euren zusammentut, so sind sie eure Geschwister.\" Und Allah weiß den Unheilstifter von dem zu unterscheiden, der Gutes tut. Und wenn Allah es gewollt hatte, hatte Er euch in Bedrangnis gebracht. Wahrlich, Allah ist Allmachtig, Allweise"
  },
  {
    "surah_number": 2,
    "verse_number": 221,
    "content":
        "Und heiratet keine Gotzenanbeterinnen, ehe sie glauben. Und eine glaubige Dienerin ist besser als eine Gotzenanbeterin, mag sie euch auch noch so gut gefallen. Und verheiratet nicht (glaubige Frauen) mit Gotzenanbetern, ehe sie glauben. Und ein glaubiger Diener ist besser als ein Gotzenanbeter, mag er euch auch noch so gut gefallen. Jene rufen zum Feuer, doch Allah ruft zum Paradies und zur Verzeihung mit Seiner Erlaubnis und macht den Menschen Seine Zeichen klar, damit sie Seiner gedenken mogen"
  },
  {
    "surah_number": 2,
    "verse_number": 222,
    "content":
        "Und sie befragen dich uber die Menstruation. Sprich: \"Sie ist ein Leiden. So haltet euch von den Frauen wahrend der Menstruation fern und kommt ihnen nicht nahe, bis sie rein sind; und wenn sie rein sind, dann geht zu ihnen, wie Allah es euch geboten hat. Wahrlich, Allah liebt diejenigen, die sich (Ihm) reuevoll zuwenden und die sich reinigen"
  },
  {"surah_number": 2, "verse_number": 223, "content": "Eure Frauen sind ein Saatfeld fur euch: darum bestellt euer Saatfeld wie ihr wollt. Doch schickt (Gutes) fur euch voraus. Und furchtet Allah und wisset, daß ihr Ihm begegnen werdet. Und verheiße den Glaubigen die frohe Botschaft"},
  {"surah_number": 2, "verse_number": 224, "content": "Und macht Allah nicht bei euren Schwuren zum Hinderungsgrund, ehrlich und gottesfurchtig zu sein und Frieden zwischen den Menschen zu stiften. Und Allah ist Allhorend, Allwissend"},
  {"surah_number": 2, "verse_number": 225, "content": "Allah wird euch nicht Unachtsamkeit in euren Schwuren zum Vorwurf machen, doch macht Er euch das zum Vorwurf, was eure Herzen erworben haben. Und Allah ist Allverzeihend, Nachsichtig"},
  {"surah_number": 2, "verse_number": 226, "content": "Diejenigen, die Enthaltsamkeit von ihren Frauen beschworen, sollen vier Monate warten. Wenn sie sich dann von ihrem Schwur entbinden wollen, ist Allah wahrlich Allverzeihend, Barmherzig"},
  {"surah_number": 2, "verse_number": 227, "content": "Doch wenn sie den festen Entschluß zur Scheidung gefaßt haben, dann ist Allah wahrlich Allhorend, Allwissend"},
  {
    "surah_number": 2,
    "verse_number": 228,
    "content":
        "Geschiedene Frauen sollen selbst drei Perioden abwarten, und es ist ihnen nicht erlaubt, zu verbergen, was Allah in ihrer Gebarmutter erschaffen hat, wenn sie an Allah und an den Jungsten Tag glauben. Und ihre Ehemanner haben vorrangig das Anrecht, sie dann zuruckzunehmen, wenn sie eine Versohnung anstreben. Und den (Frauen) stehen die gleichen Rechte zu wie sie (die Manner) zur gutigen Ausubung uber sie haben. Doch die Manner stehen eine Stufe uber ihnen. Und Allah ist Allmachtig, Allweise"
  },
  {
    "surah_number": 2,
    "verse_number": 229,
    "content":
        "Die Scheidung ist zweimal. Dann (sollen die Manner die Frauen) in angemessener Weise behalten oder im Guten entlassen. Und es ist euch nicht erlaubt, irgend etwas von dem zuruckzunehmen, was ihr ihnen (als Brautgabe) gegeben habt, es sei denn, beide (Mann und Frau) befurchten, die Schranken Allahs nicht einhalten zu konnen. Und wenn ihr befurchtet, daß sie die Schranken Allahs nicht einhalten konnen, dann liegt kein Vergehen fur sie beide in dem, was sie hingibt, um sich damit loszukaufen. Dies sind die Schranken Allahs, so ubertretet sie nicht. Und wer die Schranken Allahs ubertritt das sind diejenigen, die Unrecht tun"
  },
  {
    "surah_number": 2,
    "verse_number": 230,
    "content":
        "Und wenn er sie entlaßt, dann ist sie ihm nicht mehr erlaubt, solange sie nicht einen anderen Mann geheiratet hat. Wenn dieser sie entlaßt, ist es kein Vergehen fur beide, wenn sie zueinander zuruckkehren, sofern sie annehmen, daß sie die Gebote Allahs einhalten konnen. Dies sind die Schranken Allahs, die Er denjenigen klarmacht, die wissen"
  },
  {
    "surah_number": 2,
    "verse_number": 231,
    "content":
        "Und wenn ihr euch von den Frauen scheidet und sie sich der Erfullung ihrer Wartezeit nahern, dann behaltet sie in gutiger Weise oder entlaßt sie in gutiger Weise. Doch behaltet sie nicht aus Schikane, um zu ubertreten. Und wer dies tut, der fugt sich selbst Unrecht zu. Und macht euch nicht uber die Zeichen Allahs lustig, und gedenkt der Gnade Allahs, die Er euch erwiesen hat und dessen, was Er euch vom Buch und der Weisheit herabgesandt hat, um euch damit zu ermahnen. Und furchtet Allah und wisset, daß Allah uber alles Bescheid weiß"
  },
  {
    "surah_number": 2,
    "verse_number": 232,
    "content":
        "Und wenn ihr die Frauen entlaßt und sie ihren Termin erreichen, dann haltet sie nicht davon ab, ihre Gatten zu heiraten, wenn sie sich in gutiger Weise einigen. Dies ist eine Ermahnung fur denjenigen unter euch, der an Allah und an den Jungsten Tag glaubt. Das ist besser fur eure Lauterkeit und Reinheit. Und Allah weiß, doch ihr wisset nicht"
  },
  {
    "surah_number": 2,
    "verse_number": 233,
    "content":
        "Und die Mutter stillen ihre Kinder zwei volle Jahre. (Das gilt) fur die, die das Stillen vollenden wollen. Und es obliegt dem, dem das Kind geboren wurde, fur ihre Nahrung und Kleidung auf gutige Weise Sorge zu tragen. Von keiner Seele soll etwas gefordert werden uber das hinaus, was sie zu leisten vermag. Einer Mutter soll nicht wegen ihres Kindes Schaden zugefugt werden, und dem, dem das Kind geboren wurde, nicht wegen seines Kindes. Und fur den Erben gilt das gleiche. Und wenn sie beide in gegenseitigem Einvernehmen und nach Beratung (das Kind vorzeitig) entwohnen wollen, dann liegt darin kein Vergehen fur sie. Und wenn ihr eure Kinder stillen lassen wollt, so ist es kein Vergehen fur euch, sofern ihr das, was ihr vereinbart habt, in gutiger Weise bezahlt. Und furchtet Allah und wisset, daß Allah wohl sieht, was ihr tut"
  },
  {
    "surah_number": 2,
    "verse_number": 234,
    "content":
        "Und wenn diejenigen von euch, die abberufen werden, Gattinnen zurucklassen, so sollen diese (Witwen) vier Monate und zehn Tage abwarten. Und wenn sie dann ihren Termin erreicht haben, so ist es kein Vergehen fur euch, wenn sie in gutiger Weise uber sich selbst verfugen. Und Allah ist wohl vertraut mit dem, was ihr tut"
  },
  {
    "surah_number": 2,
    "verse_number": 235,
    "content":
        "Und es ist kein Vergehen fur euch, wenn ihr ihnen gegenuber Heiratsabsichten andeutet oder euch insgeheim mit diesem Gedanken tragt. Allah wußte, daß ihr an sie denken werdet. Doch verabredet euch nicht heimlich mit ihnen, außer ihr sprecht ein geziemendes Wort. Und faßt keinen festen Entschluß zum Ehebund, bevor die Wartezeit erfullt ist. Und wisset, daß Allah dessen gewahr ist, was in euren Seelen ist. Darum seid vor Ihm auf der Hut. Und wisset, daß Allah Allverzeihend und Nachsichtig ist"
  },
  {
    "surah_number": 2,
    "verse_number": 236,
    "content":
        "Es ist kein Vergehen fur euch, wenn ihr die Frauen entlaßt, bevor ihr sie beruhrt oder ihnen eine Brautgabe gewahrt habt. Doch gewahrt ihnen Versorgung: der Wohlhabende (nach dem,) was er vermag, und der Minderbemittelte nach dem, was er vermag eine Versorgung auf gutige Weise. (Dies ist) eine Verpflichtung fur die Gutigen"
  },
  {
    "surah_number": 2,
    "verse_number": 237,
    "content":
        "Und wenn ihr sie entlaßt, bevor ihr sie beruhrt habt, jedoch nachdem ihr ihnen eine Brautgabe ausgesetzt habt, dann zahlt die Halfte dessen, was ihr ausgesetzt habt, es sei denn, sie erlassen es (euch) oder der, in dessen Hand der Ehebund ist, erlaßt es. Und wenn ihr es erlaßt, so kommt das der Gottesfurchtigkeit naher. Und vergesset nicht, einander Gute zu erweisen. Wahrlich, Allah sieht wohl, was ihr tut"
  },
  {"surah_number": 2, "verse_number": 238, "content": "Haltet die Gebete ein, so wie das mittlere Gebet. Und steht in demutiger Ergebenheit vor Allah"},
  {"surah_number": 2, "verse_number": 239, "content": "Doch wenn ihr in Furcht seid, dann betet zu Fuß oder im Reiten. Und wenn ihr in Sicherheit seid, gedenkt Allahs, wie Er euch das gelehrt hat, was ihr nicht wußtet"},
  {
    "surah_number": 2,
    "verse_number": 240,
    "content":
        "Und diejenigen von euch, die abberufen werden und Gattinnen zurucklassen, sollen ihren Gattinnen Versorgung fur ein Jahr vermachen, ohne daß sie vertrieben werden. Gehen sie jedoch weg, so ist es kein Vergehen fur euch, wenn sie zu ihrem Besten uber sich selbst verfugen. Und Allah ist Erhaben, Allweise"
  },
  {"surah_number": 2, "verse_number": 241, "content": "Und den geschiedenen Frauen eine Versorgung auf gutige Weise (zu geben, ist) eine Verpflichtung fur die Gottesfurchtigen"},
  {"surah_number": 2, "verse_number": 242, "content": "So macht euch Allah Seine Zeichen klar; vielleicht werdet ihr (es) begreifen"},
  {
    "surah_number": 2,
    "verse_number": 243,
    "content": "Hast du nicht uber jene nachgedacht, die in Todesfurcht zu Tausenden aus ihren Hausern auszogen? Allah sprach zu ihnen: \"Sterbt!\" Dann gab Er ihnen das Leben (wieder) Wahrlich, Allah ist huldvoll gegen die Menschen, doch die meisten Menschen sind nicht dankbar"
  },
  {"surah_number": 2, "verse_number": 244, "content": "Und kampft auf dem Weg Allahs und wisset, daß Allah Allhorend und Allwissend ist"},
  {"surah_number": 2, "verse_number": 245, "content": "Wer ist es, der Allah ein gutes Darlehen gibt, damit Er es ihm um ein vielfaches verdoppele? Und Allah schmalert und mehrt, und zu Ihm werdet ihr zuruckgebracht"},
  {
    "surah_number": 2,
    "verse_number": 246,
    "content":
        "Hast du nicht nachgedacht uber die Vornehmen von den Kindern Israels (in der Zeit) nach Moses, als sie zu einem Propheten unter ihnen sagten: \"Setze fur uns einen Konig ein, damit wir auf dem Weg Allahs kampfen!\" Er sagte: \"Ist es nicht moglich, daß ihr, wenn euch vorgeschrieben ist zu kampfen, doch nicht kampfen werdet?\" Sie sagten: \"Was sollte uns dazu bewegen, daß wir nicht auf dem Weg Allahs kampfen, wo wir doch aus unseren Hausern vertrieben und von unseren Sohnen (getrennt) worden sind?\" Doch als ihnen vorgeschrieben wurde zu kampfen, da wandten sie sich ab bis auf wenige von ihnen. Und Allah kennt die Ungerechten"
  },
  {
    "surah_number": 2,
    "verse_number": 247,
    "content":
        "Und ihr Prophet sagte zu ihnen: \"Wahrlich, Allah hat bereits Saul zum Konig uber euch eingesetzt.\" Da fragten sie: \"Wie kann ihm die Herrschaft uber uns zustehen, wo wir doch das (großere) Anrecht auf die Herrschaft haben als er und ihm nicht genugend Besitz gegeben ist?\" Er sagte: \"Wahrlich, Allah hat ihn vor euch auserwahlt und hat ihm reichlich Wissen und korperliche Vorzuge verliehen. Und Allah gibt Seine Herrschaft, wem Er will.\" Und Allah ist Allumfassend, Allwissend"
  },
  {
    "surah_number": 2,
    "verse_number": 248,
    "content":
        "Und ihr Prophet sagte zu ihnen: \"Wahrlich, ein Zeichen fur seine Herrschaft soll sein, daß die Bundeslade zu euch (zuruck) kommen wird; darin ist Frieden von eurem Herrn und ein Vermachtnis von dem, was die Nachkommen von Moses und die Nachkommen von Aaron hinterlassen haben. Sie wird von Engeln getragen. Wahrlich, darin ist ein Zeichen fur euch, wenn ihr Glaubige seid"
  },
  {
    "surah_number": 2,
    "verse_number": 249,
    "content":
        "Und als Saul mit den Heerscharen auszog, sagte er: \"Wahrlich, Allah wird euch an einem Fluß prufen. Wer aus ihm trinkt, gehort nicht zu mir, und wer nicht davon kostet, der gehort wahrlich zu mir, außer dem, der nur eine Handvoll mit seiner Hand (daraus) schopft.\" Und sie tranken davon bis auf wenige von ihnen. Und als sie den Fluß uberquert hatten, er und diejenigen, die mit ihm glaubig waren, sagten sie: \"Wir haben heute keine Kraft gegen Goliath und seine Heerscharen.\" Doch diejenigen, die damit rechneten, daß sie Allah begegnen wurden, sagten: \"Wie oft hat nicht eine geringe Schar uber eine große Schar gesiegt mit Allahs Erlaubnis! Und Allah ist mit den Geduldigen"
  },
  {"surah_number": 2, "verse_number": 250, "content": "Und als sie gegen Goliath und seine Heerscharen in den Kampf zogen, sagten sie: \"Unser Herr, verleih uns reichlich Geduld und festige unsere Schritte und hilf uns gegen das Volk der Unglaubigen"},
  {
    "surah_number": 2,
    "verse_number": 251,
    "content":
        "Und sie schlugen sie mit Allahs Erlaubnis, und David erschlug Goliath, und Allah gab ihm die Herrschaft und die Weisheit, und Er lehrte ihn, was Er wollte. Und wenn Allah nicht die einen Menschen durch die anderen zuruckgehalten hatte, dann ware die Erde wahrhaftig von Unheil erfullt. Doch Allah ist huldvoll gegen alle Welten"
  },
  {"surah_number": 2, "verse_number": 252, "content": "Dies sind die Zeichen Allahs. Wir tragen sie dir in Wahrheit vor. Und wahrlich, du bist einer der Gesandten"},
  {
    "surah_number": 2,
    "verse_number": 253,
    "content":
        "Dies sind die Gesandten. Wir haben einigen von ihnen den Vorrang uber andere gegeben. Unter ihnen sind welche, zu denen Allah gesprochen hat, und einige, die Er um Rangstufen erhoht hat. Und Wir gaben Jesus, dem Sohn Marias, die klaren Beweise und unterstutzten ihn durch heilige Eingebung, und wenn Allah es so gewollt hatte, dann hatten sich diejenigen, die nach ihnen kamen, nicht gegenseitig bekampft, nachdem klare Beweise zu ihnen gekommen waren. Sie wurden jedoch uneins. Die einen von ihnen waren glaubig, die anderen unglaubig. Wenn Allah es so gewollt hatte, dann hatten sie sich nicht gegenseitig bekampft. Doch Allah tut, was Er will"
  },
  {"surah_number": 2, "verse_number": 254, "content": "O ihr, die ihr glaubt! Spendet von dem, was Wir euch (an Gut) beschert haben, bevor ein Tag kommt, an dem es kein Handeln, keine Freundschaft und keine Fursprache (mehr) geben wird. Die Unglaubigen sind es, die Unrecht tun"},
  {
    "surah_number": 2,
    "verse_number": 255,
    "content":
        "Allah kein Gott ist da außer Ihm, dem Lebendigen, dem Ewigen. Ihn ergreift weder Schlummer noch Schlaf. Ihm gehort, was in den Himmeln und was auf der Erde ist. Wer ist es, der bei Ihm Fursprache einlegen konnte außer mit Seiner Erlaubnis? Er weiß, was vor ihnen und was hinter ihnen liegt; sie aber begreifen nichts von Seinem Wissen, es sei denn das, was Er will. Weit reicht Sein Thron uber die Himmel und die Erde, und es fallt Ihm nicht schwer, sie (beide) zu bewahren. Und Er ist der Hohe, der Erhabene"
  },
  {
    "surah_number": 2,
    "verse_number": 256,
    "content":
        "Es gibt keinen Zwang im Glauben. Der richtige Weg ist nun klar erkennbar geworden gegenuber dem unrichtigen. Der also, der nicht an falsche Gotter glaubt, aber an Allah glaubt, hat gewiß den sichersten Halt ergriffen, bei dem es kein Zerreißen gibt. Und Allah ist Allhorend, Allwissend"
  },
  {
    "surah_number": 2,
    "verse_number": 257,
    "content": "Allah ist der Beschutzer derjenigen, die glauben. Er fuhrt sie aus der Finsternis ins Licht. Diejenigen aber die unglaubig sind, deren Freunde sind die Gotzen. Sie fuhren sie aus dem Licht in die Finsternis. Sie werden die Bewohner des Feuers sein, darin werden sie ewig bleiben"
  },
  {
    "surah_number": 2,
    "verse_number": 258,
    "content":
        "Hast du nicht uber jenen nachgedacht, der uber seinen Herrn mit Abraham ein Wortgefecht fuhrte, weil Allah ihm die Herrschaft gegeben hatte? Als Abraham sagte: \"Mein Herr ist Derjenige, Der lebendig macht und sterben laßt\", sagte er: \"Ich bin es, der lebendig macht und sterben laßt.\" Da sagte Abraham: \"Doch es ist Allah Der die Sonne im Osten aufgehen laßt; so laß du sie im Westen aufgehen.\" Da war der Unglaubige verwirrt. Und Allah leitet nicht die ungerechten Leute"
  },
  {
    "surah_number": 2,
    "verse_number": 259,
    "content":
        "Oder (hast du auch nicht uber) den (nachgedacht), der an einer Stadt voruberkam, die wust in Trummern lag? Da sagte er: \"Oh, wie soll Allah dieser nach ihrer Zerstorung wieder Leben geben?\" Und Allah ließ ihn fur hundert Jahre tot sein. Dann erweckte Er ihn wieder. Er sprach: \"Wie lange hast du verharrt?\" Er sagte: \"Ich verharrte einen Tag oder den Teil eines Tages.\" Da sprach Er: \"Doch du verharrtest einhundert Jahre. Nun betrachte deine Speise und deinen Trank. Sie sind nicht verdorben. Und betrachte deinen Esel. Wir machen dich damit zu einem Zeichen fur die Menschen. Und betrachte die Knochen, wie Wir sie zusammensetzen und dann mit Fleisch bekleiden.\" Und als ihm dies klargemacht worden war, sagte er: \"Ich weiß, daß Allah Macht hat uber alle Dinge"
  },
  {
    "surah_number": 2,
    "verse_number": 260,
    "content":
        "Und gedenke, als Abraham sagte: \"Mein Herr, laß mich sehen, wie Du die Toten wieder zum Leben bringst\", sprach Er: \"Glaubst du denn nicht?\" Er sagte: \"Doch! Aber (ich frage) um mein Herz zu beruhigen.\" Er sagte: \"Nimm dir vier Vogel und zerstuckle sie, dann setze auf jeden Berg einige Teile davon. Hierauf rufe sie. Sie werden eilends zu dir kommen. Und wisse, daß Allah Allmachtig und Allweise ist"
  },
  {
    "surah_number": 2,
    "verse_number": 261,
    "content": "Das Gleichnis derjenigen, die ihr Vermogen auf dem Weg Allahs ausgeben, ist wie das Gleichnis eines Samenkorns, das sieben Ahren wachsen laßt, in jeder Ahre hundert Korner. Und Allah vervielfacht es, wem Er will. Und Allah ist Allumfassend, Allwissend"
  },
  {
    "surah_number": 2,
    "verse_number": 262,
    "content": "Diejenigen, die ihren Besitz auf dem Weg Allahs spenden, dann dem, was sie gespendet haben, weder Vorhaltungen noch Ungebuhrlichkeiten folgen lassen, denen wird ihr Lohn von ihrem Herrn zuteil werden, und weder Angst wird uber sie kommen noch werden sie traurig sein"
  },
  {"surah_number": 2, "verse_number": 263, "content": "Gutige Rede und Verzeihung sind besser als ein Almosen, das von einem Ubel befolgt wird; und Allah ist Reich und Milde"},
  {
    "surah_number": 2,
    "verse_number": 264,
    "content":
        "O ihr, die ihr glaubt, vereitelt nicht eure Almosen durch Vorhaltungen und Ungemach, gleich dem, der sein Gut ausgibt, um von den Leuten gesehen zu werden, und nicht an Allah glaubt und an den Jungsten Tag. Das Gleichnis dessen ist das Gleichnis eines Felsens mit Erdreich daruber, und es trifft ihn ein Platzregen und laßt ihn hart. Sie richten nichts aus mit ihrem Verdienst; denn Allah leitet nicht die unglaubigen Leute"
  },
  {
    "surah_number": 2,
    "verse_number": 265,
    "content":
        "Das Gleichnis jener aber, die ihr Gut ausgeben im Trachten nach Allahs Wohlgefallen und zur Starkung ihrer Seele, ist das Gleichnis eines Gartens auf einem Hugel: es trifft ihn ein Platzregen, und da bringt er seine Fruchte zweifach (hervor) Und wenn ihn kein Platzregen trifft, so doch Tau. Und Allah durchschaut euer Tun"
  },
  {
    "surah_number": 2,
    "verse_number": 266,
    "content":
        "Wunscht einer von euch, daß er einen Garten besitze mit Palmen und Reben, durcheilt von Bachen, in dem er allerlei Fruchte habe, und daß ihn das Alter treffe, wahrend er (noch) schwache Sproßlinge habe, und (daß) ihn (den Garten) ein feuriger Wirbelsturm da treffe und er verbrenne? So erklart euch Allah die Zeichen. vielleicht denkt ihr (daruber) nach"
  },
  {
    "surah_number": 2,
    "verse_number": 267,
    "content":
        "O ihr, die ihr glaubt, spendet von dem Guten, das ihr erwarbt, und von dem, was Wir fur euch aus der Erde hervorkommen lassen, und sucht darunter zum Spenden nicht das Schlechte aus, das ihr selber nicht nahmet, ohne dabei die Augen zuzudrucken; und wisset, Allah ist Reich und Gepriesen"
  },
  {"surah_number": 2, "verse_number": 268, "content": "Satan droht euch Armut an und befiehlt euch Schandliches, Allah aber verheißt euch Seine Vergebung und Huld. Und Allah ist Allumfassend und Allwissend"},
  {"surah_number": 2, "verse_number": 269, "content": "Er gibt die Weisheit, wem Er will, und wem da Weisheit gegeben wurde, dem wurde hohes Gut gegeben; aber diejenigen, die sich mahnen lassen, sind die, die Verstand haben"},
  {"surah_number": 2, "verse_number": 270, "content": "Und was ihr an Spende gebt oder als Gelubde versprecht, seht, Allah weiß es, und die Ungerechten finden keine Helfer"},
  {"surah_number": 2, "verse_number": 271, "content": "Wenn ihr Almosen offenkundig gebt, so ist es angenehm, und wenn ihr sie verbergt und sie den Armen gebt, so ist es besser fur euch und suhnt eure Missetaten. Und Allah ist eures Tuns kundig"},
  {
    "surah_number": 2,
    "verse_number": 272,
    "content":
        "Ihre Rechtleitung obliegt nicht dir, sondern Allah leitet recht, wen Er will. Was immer ihr an Gutem spendet, das ist fur euch selbst, und ihr (sollt) nicht spenden, es sei denn aus Verlangen nach dem Angesicht Allahs. Und was immer ihr an Gutem spendet, das soll euch voll zuruckerstattet werden, und es soll euch kein Unrecht zugefugt werden"
  },
  {
    "surah_number": 2,
    "verse_number": 273,
    "content":
        "(Dies ist) fur die Armen, die auf dem Weg Allahs (daran) gehindert werden, sich frei im Land zu bewegen. Der Unwissende halt sie fur reich wegen (ihrer) Zuruckhaltung. Du aber erkennst sie an ihrem Auftreten. Sie betteln die Menschen nicht aufdringlich an. Und was immer ihr an Gutem spendet, wahrlich, Allah weiß es"
  },
  {"surah_number": 2, "verse_number": 274, "content": "Diejenigen, die ihren Besitz bei Nacht und Tag, geheim oder offen, spenden denen ist ihr Lohn von ihrem Herrn (gewiß), und sie brauchen keine Angst zu haben noch werden sie traurig sein"},
  {
    "surah_number": 2,
    "verse_number": 275,
    "content":
        "Diejenigen, die Zinsen verschlingen, sollen nicht anders dastehen als wie einer, der vom Satan erfaßt und zum Wahnsinn getrieben wird. Dies (soll so sein,) weil sie sagen: \"Handel ist dasselbe wie Zinsnehmen.\" Doch Allah hat den Handel erlaubt und das Zinsnehmen verboten. Und wenn zu jemandem eine Ermahnung von seinem Herrn kommt und er dann aufhort dem soll verbleiben, was bereits geschehen ist. Und seine Sache ist bei Allah. Wer es aber von neuem tut die werden Bewohner des Feuers sein, darin werden sie ewig SO bleiben"
  },
  {"surah_number": 2, "verse_number": 276, "content": "Allah wird den Zins dahinschwinden lassen und die Mildtatigkeit vermehren. Und Allah liebt keinen, der ein hartnackiger Unglaubiger und Ubeltater ist"},
  {"surah_number": 2, "verse_number": 277, "content": "Wahrlich, denjenigen, die glaubig sind und gute Werke tun und das Gebet verrichten und die Zakah entrichten, ist ihr Lohn von ihrem Herrn (gewiß,) und sie brauchen keine Angst haben noch werden sie traurig sein"},
  {"surah_number": 2, "verse_number": 278, "content": "O ihr, die ihr glaubt, furchtet Allah und verzichtet auf das, was noch ubrig ist an Zinsen, wenn ihr Glaubige seid"},
  {"surah_number": 2, "verse_number": 279, "content": "Und wenn ihr dies nicht tut, dann ist euch Krieg angesagt von Allah und Seinem Gesandten. Doch wenn ihr bereut, dann soll euch euer Kapital zustehen, so daß weder ihr Unrecht tut, noch euch Unrecht zugefugt wird"},
  {"surah_number": 2, "verse_number": 280, "content": "Wenn jemand in Schwierigkeiten ist, dann gewahrt ihm Aufschub, bis eine Erleichterung (eintritt) Doch wenn ihr mildtatig seid, so ist es besser fur euch, wenn ihr es nur wußtet"},
  {"surah_number": 2, "verse_number": 281, "content": "Und furchtet den Tag, an dem ihr zu Allah zuruckgebracht werdet. Dann wird jeder Seele das zuruckerstattet, was sie erworben hat, und ihnen wird kein Unrecht geschehen"},
  {
    "surah_number": 2,
    "verse_number": 282,
    "content":
        "O ihr, die ihr glaubt, wenn ihr eine Anleihe gewahrt oder aufnehmt zu einer festgesetzten Frist, dann schreibt es nieder. Und ein Schreiber soll es in eurem Beisein getreulich niederschreiben. Und kein Schreiber soll sich weigern zu schreiben, so wie Allah es gelehrt hat. So schreibe er also, und der Schuldner soll es diktieren und Allah, seinen Herrn, furchten und nichts davon weglassen. Und wenn der Schuldner schwachsinnig oder schwach ist oder unfahig, selbst zu diktieren, dann soll sein Sachwalter getreulich fur ihn diktieren. Und lasset zwei Zeugen unter euren Mannern es bezeugen, und wenn es keine zwei Manner gibt, dann (sollen es bezeugen) ein Mann und zwei Frauen von denen, die euch als Zeugen geeignet erscheinen, damit, wenn sich eine der beiden irrt, die andere von ihnen sie (daran) erinnert. Und die Zeugen sollen sich nicht weigern, wenn sie gerufen werden. Und verschmaht nicht, es niederzuschreiben (seien es) große oder kleine (Betrage) bis zur festgesetzten Frist. Das ist rechtschaffener vor Allah und zuverlassiger, was die Bezeugung angeht und bewahrt euch eher vor Zweifeln, es sei denn es handelt sich um eine sogleich verfugbare Ware, die von Hand zu Hand geht unter euch; dann ist es kein Vergehen fur euch, wenn ihr es nicht niederschreibt. Und nehmt Zeugen, wenn ihr miteinander Handel treibt. Und weder dem Schreiber noch dem Zeugen soll Schaden zugefugt werden. Und wenn ihr es tut, dann ist es wahrlich ein Frevel von euch. Und furchtet Allah. Und Allah lehrt euch, und Allah ist uber alles kundig"
  },
  {
    "surah_number": 2,
    "verse_number": 283,
    "content":
        "Und wenn ihr auf einer Reise seid und keinen Schreiber findet, dann soll ein Pfand in Empfang genommen werden. Und wenn einer von euch dem anderen (etwas) anvertraut, dann soll der, dem es anvertraut wurde, das ihm Anvertraute zuruckgeben und Allah, seinen Herrn, furchten. Und unterdruckt das Zeugnis nicht. Und wer es verbirgt, dessen Herz ist wahrlich mit Schuld befleckt. Und Allah ist dessen kundig, was ihr tut"
  },
  {
    "surah_number": 2,
    "verse_number": 284,
    "content": "Allah gehort das, was in den Himmeln und was in der Erde ist. Und ob ihr kundtut, was in euren Seelen ist, oder es geheimhaltet, Allah wird euch dafur zur Rechenschaft ziehen. Dann verzeiht Er, wem Er will, und bestraft, wen Er will. Und Allah hat Macht uber alle Dinge"
  },
  {
    "surah_number": 2,
    "verse_number": 285,
    "content":
        "Der Gesandte glaubt an das, was ihm von seinem Herrn herabgesandt worden ist, ebenso die Glaubigen; sie alle glauben an Allah und an Seine Engel und an Seine Bucher und an Seine Gesandten. Wir machen keinen Unterschied zwischen Seinen Gesandten. Und sie sagen: \"Wir horen und gehorchen. Gewahre uns Deine Vergebung, unser Herr, und zu Dir ist die Heimkehr"
  },
  {
    "surah_number": 2,
    "verse_number": 286,
    "content":
        "Allah fordert von keiner Seele etwas uber das hinaus, was sie zu leisten vermag. Ihr wird zuteil, was sie erworben hat, und uber sie kommt, was sie sich zuschulden kommen laßt. Unser Herr, mache uns nicht zum Vorwurf, wenn wir (etwas) vergessen oder Fehler begehen. Unser Herr, und erlege uns keine Burde auf, so wie Du sie jenen aufgeburdet hast, die vor uns waren. Unser Herr, und lade uns nichts auf, wofur wir keine Kraft haben. Und verzeihe uns und vergib uns und erbarme Dich unser. Du bist unser Beschutzer. So hilf uns gegen das Volk der Unglaubigen"
  },
  {"surah_number": 3, "verse_number": 1, "content": "Alif Lam Mim"},
  {"surah_number": 3, "verse_number": 2, "content": "Allah kein Gott ist da außer Ihm, dem Lebendigen, dem Bestandigen"},
  {"surah_number": 3, "verse_number": 3, "content": "Er hat das Buch mit der Wahrheit auf dich herabgesandt als Bestatigung dessen, was vor ihm war. Und Er hat die Thora und das Evangelium herabgesandt"},
  {"surah_number": 3, "verse_number": 4, "content": "vordem als Rechtleitung fur die Menschen, und Er hat (das Buch zur) Unterscheidung herabgesandt. Wahrlich, denjenigen, die die Zeichen Allahs verleugnen, wird eine strenge Strafe zuteil sein. Und Allah ist Allmachtig und Herr der Vergeltung"},
  {"surah_number": 3, "verse_number": 5, "content": "Vor Allah ist wahrlich nichts verborgen, weder in der Erde noch im Himmel"},
  {"surah_number": 3, "verse_number": 6, "content": "Er ist es, Der euch im Mutterschoß gestaltet, wie Er will. Es ist kein Gott außer Ihm, dem Allmachtigen, dem Allweisen"},
  {
    "surah_number": 3,
    "verse_number": 7,
    "content":
        "Er ist es, Der dir das Buch herabgesandt hat. Darin sind eindeutig klare Verse sie sind die Grundlage des Buches und andere, die verschieden zu deuten sind. Doch diejenigen, in deren Herzen (Neigung zur) Abkehr ist, folgen dem, was darin verschieden zu deuten ist, um Zwietracht herbeizufuhren und Deutelei zu suchen, (indem sie) nach ihrer abwegigen Deutung trachten. Aber niemand kennt ihre Deutung außer Allah. Diejenigen aber, die ein tiefbegrundetes Wissen haben, sagen: \"Wir glauben wahrlich daran. Alles ist von unserem Herrn.\" Doch niemand bedenkt dies außer den Einsichtigen"
  },
  {"surah_number": 3, "verse_number": 8, "content": "Unser Herr, laß unsere Herzen sich nicht (von Dir) abkehren, nachdem Du uns rechtgeleitet hast. Und schenke uns Barmherzigkeit von Dir; denn Du bist ja wahrlich der unablassig Gebende"},
  {"surah_number": 3, "verse_number": 9, "content": "Unser Herr, Du wirst die Menschen zusammenfuhren an einem Tag, uber den es keinen Zweifel gibt. Wahrlich, Allah verfehlt niemals Seinen Termin"},
  {"surah_number": 3, "verse_number": 10, "content": "Wahrlich, denen, die unglaubig sind, werden weder ihr Vermogen noch ihre Kinder bei Allah etwas helfen. Sie sind es, die zum Brennstoff des Feuers werden"},
  {"surah_number": 3, "verse_number": 11, "content": "Nach dem Brauch des Volkes Pharaos und derer, die vor ihnen waren, verwarfen sie Unsere Zeichen als Luge. Da ergriff Allah sie in ihrer Schuld. Und Allah ist streng im Strafen"},
  {"surah_number": 3, "verse_number": 12, "content": "Sprich zu denen, die unglaubig sind: \"Bald schon werdet ihr besiegt sein und in Gahannam versammelt werden was fur eine schlechte Ruhestatte"},
  {
    "surah_number": 3,
    "verse_number": 13,
    "content":
        "Es war euch ein Zeichen zuteil geworden in den beiden Scharen, die aufeinandertrafen: Die eine Schar kampfte auf dem Weg Allahs, die andere war unglaubig. Sie sahen mit eigenen Augen (ihre Gegner) doppelt (so zahlreich) Und Allah starkt mit Seinem Beistand, wen Er will. Wahrlich, darin liegt eine Lehre fur die, die Einsicht haben"
  },
  {
    "surah_number": 3,
    "verse_number": 14,
    "content": "Zum Genuß wird den Menschen die Freude gemacht an ihrem Trieb zu Frauen und Kindern und aufgespeicherten Mengen von Gold und Silber und Rassepferden und Vieh und Saatfeldern. Dies ist der Genuß des irdischen Lebens; doch bei Allah ist die schonste Heimkehr"
  },
  {
    "surah_number": 3,
    "verse_number": 15,
    "content": "Sprich: \"Soll ich euch Besseres als dies verkunden?\" Fur die Gottesfurchtigen gibt es bei ihrem Herrn Garten, durcheilt von Bachen, darin werden sie auf ewig bleiben, sowie reine Gattinnen und Allahs Wohlgefallen. Und Allah durchschaut die Diener"
  },
  {"surah_number": 3, "verse_number": 16, "content": "die sagen: \"Unser Herr, siehe, wir glauben; darum vergib uns unsere Sunden und behute uns vor der Strafe des Feuers"},
  {"surah_number": 3, "verse_number": 17, "content": "Die Geduldigen und die Wahrhaften und die Andachtsvollen und die Spendenden und diejenigen, die um Vergebung bitten im Morgengrauen"},
  {"surah_number": 3, "verse_number": 18, "content": "Bezeugt hat Allah, daß kein Gott da ist außer Ihm Selbst; und die Engel und die Wissenden (bezeugen es); Er sorgt fur die Gerechtigkeit. Es ist kein Gott außer Ihm, dem Allmachtigen, dem Allweisen"},
  {
    "surah_number": 3,
    "verse_number": 19,
    "content": "Wahrlich, die Religion bei Allah ist der Islam. Und diejenigen, denen die Schrift gegeben wurde, wurden erst uneins, als das Wissen zu ihnen gekommen war aus Mißgunst untereinander. Und wer die Zeichen Allahs verleugnet siehe, Allah ist schnell im Rechnen"
  },
  {
    "surah_number": 3,
    "verse_number": 20,
    "content":
        "Und wenn sie mit dir streiten, so sprich: \"Ich habe mich ganz und gar Allah ergeben, und ebenso, wer mir nachfolgt.\" Und sprich zu jenen, denen die Schrift gegeben wurde, und zu den Unbelehrten: \"Werdet ihr Muslime?\" Und wenn sie Muslime werden, sind sie geleitet; kehren sie sich jedoch ab, so obliegt dir nur die Verkundigung. Und Allah durchschaut die Diener"
  },
  {"surah_number": 3, "verse_number": 21, "content": "Wahrlich, jenen, die nicht an Allahs Zeichen glauben und die Propheten ohne rechtlichen Grund toten und jene Menschen toten, die zur Rechtschaffenheit ermahnen, denen verkunde schmerzliche Strafe"},
  {"surah_number": 3, "verse_number": 22, "content": "Sie sind es, deren Werke nichtig sind in dieser Welt und im Jenseits; und sie haben keine Helfer"},
  {"surah_number": 3, "verse_number": 23, "content": "Sahst du nicht auf jene, denen ein Teil von der Schrift gegeben wurde, als sie aufgefordert wurden zum Buch Allahs, daß es richte zwischen ihnen? Alsdann kehrte ein Teil von ihnen den Rucken und wandte sich ab"},
  {"surah_number": 3, "verse_number": 24, "content": "indem sie sagten: \"Nimmer wird uns das Feuer beruhren, es sei denn fur abgezahlte Tage.\" Und es betrog sie in ihrem Glauben, was sie selber erdachten"},
  {"surah_number": 3, "verse_number": 25, "content": "Aber wie, wenn Wir sie versammeln an einem Tag, uber den kein Zweifel herrscht, und jeder Seele vergolten wird, was sie verdient hat, und sie kein Unrecht erleiden sollen"},
  {
    "surah_number": 3,
    "verse_number": 26,
    "content": "Sprich: \"O Allah, Herrscher des Konigtums, Du gibst das Konigtum, wem Du willst, und nimmst das Konigtum, wem Du willst; und Du ehrst, wen Du willst, und erniedrigst, wen Du willst. In Deiner Hand ist das Gute; wahrlich, Du hast Macht uber alle Dinge"
  },
  {"surah_number": 3, "verse_number": 27, "content": "Du laßt die Nacht ubergehen in den Tag und laßt den Tag ubergehen in die Nacht; und Du laßt das Lebendige aus dem Toten erstehen und laßt das Tote aus dem Lebendigen erstehen, und versorgst, wen Du willst, ohne Maß"},
  {
    "surah_number": 3,
    "verse_number": 28,
    "content": "Die Glaubigen sollen die Unglaubigen nicht statt der Glaubigen zu Beschutzern nehmen; und wer solches tut, der findet von Allah in nichts Hilfe außer ihr furchtet euch vor ihnen. Und Allah warnt euch vor Sich Selber, und zu Allah ist die Heimkehr"
  },
  {"surah_number": 3, "verse_number": 29, "content": "Sprich: \"Ob ihr verbergt, was in eurer Brust ist, oder ob ihr es kundtut, Allah weiß es; Er weiß, was in den Himmeln und was auf der Erde ist; und Allah hat Macht uber alle Dinge"},
  {
    "surah_number": 3,
    "verse_number": 30,
    "content": "An jenem Tage wird jede Seele bereit finden, was sie an Gutem getan hat; und was sie an Bosem getan hat wunschen wird sie, daß zwischen ihr und ihm eine weite Zeitspanne lage. Und Allah warnt euch vor Sich Selber; und Allah ist gutig gegen Seine Diener"
  },
  {"surah_number": 3, "verse_number": 31, "content": "Sprich: \"Wenn ihr Allah liebt, so folgt mir. Lieben wird euch Allah und euch eure Sunden vergeben; denn Allah ist Allvergebend, Barmherzig"},
  {"surah_number": 3, "verse_number": 32, "content": "Sprich: \"Gehorcht Allah und dem Gesandten\"; denn wenn sie den Rucken kehren siehe, Allah liebt die Unglaubigen nicht"},
  {"surah_number": 3, "verse_number": 33, "content": "Wahrlich, Allah erwahlte Adam und Noah und das Haus Abraham und das Haus `Imran vor allen Welten"},
  {"surah_number": 3, "verse_number": 34, "content": "ein Geschlecht, von dem einer aus dem anderen stammt, und Allah ist Allhorend, Allwissend"},
  {"surah_number": 3, "verse_number": 35, "content": "Damals sagte die Frau `lmrans: \"Mein Herr, siehe, ich gelobe Dir, was in meinem Leibe ist, zu weihen; so nimm es von mir an; siehe, Du bist der Allhorende, der Allwissende"},
  {
    "surah_number": 3,
    "verse_number": 36,
    "content":
        "Und als sie es geboren hatte, sagte sie: \"Mein Herr, siehe, ich habe es als Madchen geboren.\" Und Allah wußte wohl, was sie geboren hatte; denn der Knabe ist nicht wie das Madchen. \"Und ich habe sie Maria genannt, und siehe, ich mochte, daß sie und ihre Nachkommen bei Dir Zuflucht nehmen vor dem gesteinigten Satan"
  },
  {
    "surah_number": 3,
    "verse_number": 37,
    "content":
        "Und so nahm sie Allah gnadig an und ließ sie in schoner Weise in der Obhut des Zacharias heranwachsen. Sooft Zacharias zu ihr in den Tempel hineintrat, fand er Speise bei ihr. Da sagte er: \"O Maria, woher kommt dir dies zu?\" Sie sagte: \"Es ist von Allah; siehe, Allah versorgt unbegrenzt, wen Er will"
  },
  {"surah_number": 3, "verse_number": 38, "content": "Dort rief Zacharias seinen Herrn an und sagte: \"Mein Herr, gib mir als Geschenk von Dir gute Nachkommenschaft, wahrlich, Du bist Der Erhorer des Gebets"},
  {"surah_number": 3, "verse_number": 39, "content": "Und da riefen ihm die Engel zu, wahrend er zum Gebet in dem Tempel stand: \"Siehe, Allah verheißt dir Johannes, den Bestatiger eines Wortes von Allah, einen Vornehmen, einen Asketen und Propheten, einen von den Rechtschaffenen"},
  {"surah_number": 3, "verse_number": 40, "content": "Er sagte: \"Mein Herr, soll mir ein Knabe (geschenkt) werden, wo mich das Alter doch uberkommen hat und meine Frau unfruchtbar ist?\" Er sprach: \"Allah tut ebenso, was Er will"},
  {"surah_number": 3, "verse_number": 41, "content": "Er sagte: \"Mein Herr, gib mir ein Zeichen.\" Er sprach: \"Dein Zeichen ist, daß du drei Tage lang zu den Menschen nicht sprechen wirst außer durch Gesten. Und gedenke deines Herrn haufig und preise Ihn am Abend und am Morgen"},
  {"surah_number": 3, "verse_number": 42, "content": "Und damals sprachen die Engel: \"O Maria, siehe, Allah hat dich auserwahlt und gereinigt und erwahlt vor den Frauen der Welten"},
  {"surah_number": 3, "verse_number": 43, "content": "O Maria, sei vor deinem Herrn voller Andacht und wirf dich nieder und beuge dich mit den Sich- Beugenden"},
  {"surah_number": 3, "verse_number": 44, "content": "Dies ist eine der Verkundungen des Verborgenen, die Wir dir offenbaren. Denn du warst nicht bei ihnen, als sie ihre Losrohrchen warfen, wer von ihnen Maria pflegen sollte. Und du warst nicht bei ihnen, als sie miteinander stritten"},
  {"surah_number": 3, "verse_number": 45, "content": "Damals sprachen die Engel: \"O Maria siehe, Allah verkundet dir ein Wort von Ihm; sein Name ist der Messias, Jesus, der Sohn der Maria, angesehen im Diesseits und im Jenseits, und einer von denen, die (Allah) nahestehen"},
  {"surah_number": 3, "verse_number": 46, "content": "Und reden wird er in der Wiege zu den Menschen und auch als Erwachsener, und er wird einer der Rechtschaffenen sein"},
  {"surah_number": 3, "verse_number": 47, "content": "Sie sagte: \"Mein Herr, soll mir ein Sohn (geboren) werden, wo mich doch kein Mann beruhrte?\" Er sprach: \"Allah schafft ebenso, was Er will; wenn Er etwas beschlossen hat, spricht Er nur zu ihm: \"Sei!\" und es ist"},
  {"surah_number": 3, "verse_number": 48, "content": "Und Er wird ihn das Buch lehren und die Weisheit und die Thora und das Evangelium"},
  {
    "surah_number": 3,
    "verse_number": 49,
    "content":
        "und wird ihn entsenden zu den Kindern Israels. (Sprechen wird er:) \"Seht, ich bin zu euch mit einem Zeichen von eurem Herrn gekommen. Seht, ich erschaffe fur euch aus Ton die Gestalt eines Vogels und werde in sie hauchen, und sie soll mit Allahs Erlaubnis ein Vogel werden; und ich heile den Blindgeborenen und den Aussatzigen und mache die Toten mit Allahs Erlaubnis lebendig, und ich verkunde euch, was ihr esset und was ihr in eueren Hausern speichert. Wahrlich, darin ist ein Zeichen fur euch, wenn ihr glaubig seid"
  },
  {"surah_number": 3, "verse_number": 50, "content": "Und als ein Bestatiger der Thora, die vor mir da war, und um euch einen Teil von dem zu erlauben, was euch verboten war, bin ich zu euch gekommen mit einem Zeichen von eurem Herrn. So furchtet Allah und gehorcht mir"},
  {"surah_number": 3, "verse_number": 51, "content": "wahrlich, Allah ist mein Herr und euer Herr, darum dienet Ihm. Dies ist ein gerader Weg"},
  {"surah_number": 3, "verse_number": 52, "content": "Und als Jesus ihren Unglauben wahrnahm, sagte er: \"Wer ist mein Helfer (auf dem Weg) zu Allah?\" Die Junger sagten: \"Wir sind Allahs Helfer; wir glauben an Allah, und (du sollst) bezeugen, daß wir (Ihm) ergeben sind"},
  {"surah_number": 3, "verse_number": 53, "content": "Unser Herr, wir glauben an das, was Du herabgesandt hast, und folgen dem Gesandten. Darum fuhre uns unter den Bezeugenden auf"},
  {"surah_number": 3, "verse_number": 54, "content": "Und sie schmiedeten eine List, und Allah schmiedete eine List; und Allah ist der beste Listenschmied"},
  {
    "surah_number": 3,
    "verse_number": 55,
    "content":
        "Damals sprach Allah: \"O Jesus, siehe, Ich will dich verscheiden lassen und will dich zu Mir erhohen und will dich von den Unglaubigen befreien und will deine Anhanger uber die Unglaubigen setzen bis zum Tag der Auferstehung. Alsdann werdet ihr zu Mir wiederkehren, und Ich will zwischen euch richten uber das, woruber ihr uneins waret"
  },
  {"surah_number": 3, "verse_number": 56, "content": "Was aber die Unglaubigen anbelangt, so werde Ich sie schwer im Diesseits und im Jenseits bestrafen; und sie werden keine Helfer finden"},
  {"surah_number": 3, "verse_number": 57, "content": "Was aber diejenigen anbelangt, die glauben und gute Werke tun, so wird Er ihnen ihren vollen Lohn geben. Und Allah liebt nicht die Ungerechten"},
  {"surah_number": 3, "verse_number": 58, "content": "Dies verlesen Wir dir von den Zeichen und der weisen Ermahnung"},
  {"surah_number": 3, "verse_number": 59, "content": "Wahrlich, Jesus ist vor Allah gleich Adam; Er erschuf ihn aus Erde, als dann sprach Er zu ihm: \"Sei!\" und da war er"},
  {"surah_number": 3, "verse_number": 60, "content": "(Dies) ist die Wahrheit von deinem Herrn! Darum sei keiner der Zweifler"},
  {
    "surah_number": 3,
    "verse_number": 61,
    "content":
        "Und wenn sich jemand mit dir uber sie streitet, nachdem das Wissen zu dir kam, so sprich: \"Kommt her, lasset uns rufen unsre Sohne und eure Sohne, unsre Frauen und eure Frauen und unsre Seelen und eure Seelen. Alsdann wollen wir zu Allah flehen und mit Allahs Fluch die Lugner bestrafen"
  },
  {"surah_number": 3, "verse_number": 62, "content": "Wahrlich, dies ist die Geschichte der Wahrheit, und es ist kein Gott außer Allah, und Allah Er ist wahrlich der Allmachtige, der Allweise"},
  {"surah_number": 3, "verse_number": 63, "content": "Und wenn sie sich abwenden, so kennt Allah die Missetater"},
  {
    "surah_number": 3,
    "verse_number": 64,
    "content":
        "Sprich: \"O Volk der Schrift, kommt herbei zu einem gleichen Wort zwischen uns und euch, daß wir namlich Allah allein dienen und nichts neben Ihn stellen und daß nicht die einen von uns die anderen zu Herren nehmen außer Allah.\" Und wenn sie sich abwenden, so sprecht: \"Bezeugt, daß wir (Ihm) ergeben sind"
  },
  {"surah_number": 3, "verse_number": 65, "content": "O Volk der Schrift, warum streitet ihr uber Abraham, wo die Thora und das Evangelium doch erst (spater) nach ihm herabgesandt worden sind? Habt ihr denn keinen Verstand"},
  {"surah_number": 3, "verse_number": 66, "content": "Ihr habt da uber etwas gestritten, wovon ihr Wissen habt; weshalb aber streitet ihr uber das, wovon ihr kein Wissen habt? Allah weiß, ihr aber wisset nicht"},
  {"surah_number": 3, "verse_number": 67, "content": "Abraham war weder Jude noch Christ; vielmehr war er lauteren Glaubens, ein Muslim, und keiner von denen, die (Allah) Gefahrten beigesellen"},
  {"surah_number": 3, "verse_number": 68, "content": "Wahrlich, die Menschen, die Abraham am nachsten stehen, sind jene, die ihm folgen, und dieser Prophet (Muhammad) und die Glaubigen. Und Allah ist der Beschutzer der Glaubigen"},
  {"surah_number": 3, "verse_number": 69, "content": "Eine Gruppe von den Leuten der Schrift mochte euch gern verfuhren, doch verfuhren sie nur sich selber und wissen es nicht"},
  {"surah_number": 3, "verse_number": 70, "content": "Ihr Leute der Schrift, warum verleugnet ihr die Zeichen Allahs, wo ihr sie doch bezeugt"},
  {"surah_number": 3, "verse_number": 71, "content": "Ihr Leute der Schrift, weshalb vermengt ihr die Wahrheit mit der Luge und verbergt die Wahrheit gegen euer Wissen"},
  {"surah_number": 3, "verse_number": 72, "content": "Und da sagte eine Gruppe von den Leuten der Schrift: \"Glaubt bei Tagesanbruch an das, was zu den Glaubigen hinabgesandt wurde, und leugnet es ab bei seinem Ende; vielleicht werden sie umkehren"},
  {
    "surah_number": 3,
    "verse_number": 73,
    "content":
        "Und glaubt nur denen, die eure Religion befolgen.\" Sprich: \"Seht, die (wahre) Rechtleitung ist die Rechtleitung Allahs.\" Daß (auch) einem (anderen) gegeben werde was euch gegeben worden ist, oder wenn sie mit euch vor eurem Herrn streiten, so sprich: \"Seht, die Huld ist in Allahs Hand, Er gewahrt sie, wem Er will. Und Allah ist Allumfassend und Allwissend"
  },
  {"surah_number": 3, "verse_number": 74, "content": "Er zeichnet mit Seiner Barmherzigkeit aus, wen Er will. Und Allah ist voll großer Huld"},
  {
    "surah_number": 3,
    "verse_number": 75,
    "content":
        "Und unter den Leuten der Schrift gibt es welche, die, wenn du ihnen eine große Summe anvertraust, dir diese aushandigen. Und unter ihnen gibt es auch solche, die, wenn du ihnen einen Dinar anvertraust, ihn dir nur aushandigen, wenn du stets hinter ihnen her bist. Dies geschieht deshalb, weil sie sagen: \"Uns obliegt gegen die Unbelehrbaren keine Pflicht.\" Und sie sprechen eine Luge gegen Allah und wissen es"
  },
  {"surah_number": 3, "verse_number": 76, "content": "Wer jedoch seiner Verpflichtung nachkommt und gottesfurchtig ist siehe, Allah liebt die Gottesfurchtigen"},
  {
    "surah_number": 3,
    "verse_number": 77,
    "content":
        "Wahrlich, diejenigen, welche ihren Bund mit Allah und ihre Eide um einen geringen Preis verkaufen, haben keinen Anteil am Jenseits, und Allah spricht nicht zu ihnen, und Er schaut sie nicht an am Tag der Auferstehung, und Er reinigt sie nicht, und ihnen wird eine schmerzliche Strafe zuteil sein"
  },
  {
    "surah_number": 3,
    "verse_number": 78,
    "content": "Wahrlich, ein Teil von ihnen verdreht seine Zunge mit der Schrift, damit ihr es fur einen Teil der Schrift haltet, obwohl es nicht zur Schrift gehort: Und sie sagen: \"Es ist von Allah\"; jedoch ist es nicht von Allah, und sie sprechen eine Luge gegen Allah, obwohl sie es wissen"
  },
  {
    "surah_number": 3,
    "verse_number": 79,
    "content":
        "Es darf nicht sein, daß ein Mensch, dem Allah die Schrift und die Weisheit und das Prophetentum gegeben hat, als dann zu den Leuten sprache: \"Seid meine Diener neben Allah.\" Vielmehr (soll er sagen): \"Seid Gottesgelehrte mit dem, was ihr gelehrt habt und mit dem, was ihr studiert habt"
  },
  {"surah_number": 3, "verse_number": 80, "content": "Und Er gebietet euch nicht, euch die Engel oder die Propheten zu Herren zu nehmen. Sollte Er euch den Unglauben gebieten, nachdem ihr (Ihm) ergeben geworden seid"},
  {
    "surah_number": 3,
    "verse_number": 81,
    "content":
        "Und da nahm Allah von den Propheten den Bund entgegen (des Inhalts:) \"Wenn Ich euch das Buch und die Weisheit gebe, dann wird zu euch ein Gesandter kommen und das bestatigen, was ihr habt. Wahrlich, ihr sollt ihm glauben und sollt ihm helfen.\" Er sprach: \"Erkennt ihr das an und nehmt ihr unter dieser (Bedingung) das Bundnis mit Mir an?\" Sie sagten: \"Wir erkennen es an.\" Er sprach: \"So bezeugt es, und Ich will mit euch (ein Zeuge) unter den Zeugen sein"
  },
  {"surah_number": 3, "verse_number": 82, "content": "Wer sich nach diesem abwendet das sind die Frevler"},
  {"surah_number": 3, "verse_number": 83, "content": "Verlangen sie etwa eine andere als Allahs Religion? Ihm ergibt sich, was in den Himmeln und auf der Erde ist, gehorsam oder wider Willen, und zu Ihm kehren sie zuruck"},
  {
    "surah_number": 3,
    "verse_number": 84,
    "content":
        "Sprich: \"Wir glauben an Allah und an das, was auf uns herabgesandt worden ist, und was herabgesandt worden ist auf Abraham und Ismael und Isaak und Jakob und die Stamme (Israels), und was gegeben worden ist Moses und Jesus und den Propheten von ihrem Herrn; wir machen keinen Unterschied zwischen ihnen, und Ihm sind wir ergeben"
  },
  {"surah_number": 3, "verse_number": 85, "content": "Und wer eine andere Religion als den Islam begehrt: nimmer soll sie von ihm angenommen werden, und im Jenseits wird er unter den Verlierern sein"},
  {"surah_number": 3, "verse_number": 86, "content": "Wie soll Allah ein Volk leiten, das unglaubig wurde nach seinem Glauben und nachdem es bezeugt hatte, daß der Gesandte wahrhaftig sei, und nachdem die deutlichen Zeichen zu ihnen gekommen waren? Und Allah leitet die Ungerechten nicht"},
  {"surah_number": 3, "verse_number": 87, "content": "Ihr Lohn ist, daß auf ihnen der Fluch Allahs und der Engel und der Menschen insgesamt lastet"},
  {"surah_number": 3, "verse_number": 88, "content": "Ewig bleiben sie darin; ihnen wird die Strafe nicht erleichtert und ihnen wird kein Aufschub gewahrt"},
  {"surah_number": 3, "verse_number": 89, "content": "außer denen, die danach umkehren und gute Werke verrichten. Denn siehe, Allah ist Allverzeihend und Barmherzig"},
  {"surah_number": 3, "verse_number": 90, "content": "Siehe, wer nach seinem Glauben unglaubig wird und immer mehr dem Unglauben verfallt dessen Reue wird nicht angenommen, und dies sind die Irrenden"},
  {"surah_number": 3, "verse_number": 91, "content": "Wahrlich, jene, die unglaubig sind und im Unglauben sterben niemals wird von ihnen eine Erde voll Gold angenommen werden, auch wenn sie sich damit loskaufen wollten. Diese haben eine schmerzhafte Strafe, und sie werden keine Helfer haben"},
  {"surah_number": 3, "verse_number": 92, "content": "Ihr werdet das Gutigsein nicht erlangen, ehe ihr nicht von dem spendet, was ihr liebt; und was immer ihr spendet, seht, Allah weiß es"},
  {"surah_number": 3, "verse_number": 93, "content": "Alle Speise war den Kindern Israels erlaubt, außer was Israel sich selber verwehrte, bevor die Thora herabgesandt wurde. Sprich: \"So bringt die Thora und lest sie, wenn ihr wahrhaftig seid"},
  {"surah_number": 3, "verse_number": 94, "content": "Und diejenigen, die danach eine Luge gegen Allah erdichten, sind die Ungerechten"},
  {"surah_number": 3, "verse_number": 95, "content": "Sprich: \"Allah spricht die Wahrheit. So folgt der Religion Abrahams, des Lauteren im Glauben, der neben Allah keine Gotter setzte"},
  {"surah_number": 3, "verse_number": 96, "content": "Wahrlich, das erste Haus, das fur die Menschen gegrundet wurde, ist das in Bakka ein gesegnetes und eine Leitung fur die Welten"},
  {
    "surah_number": 3,
    "verse_number": 97,
    "content": "In ihm sind deutliche Zeichen die Statte Abrahams. Und wer es betritt, ist sicher. Und der Menschen Pflicht gegenuber Allah ist die Pilgerfahrt zum Hause, wer da den Weg zu ihm machen kann. Wer aber unglaubig ist wahrlich, Allah ist nicht auf die Welten angewiesen"
  },
  {"surah_number": 3, "verse_number": 98, "content": "Sprich: \"O Leute der Schrift, warum verleugnet ihr die Zeichen Allahs, wo Allah Zeuge eures Tuns ist"},
  {"surah_number": 3, "verse_number": 99, "content": "Sprich: \"O Leute der Schrift, warumwendet ihr die Glaubigen von Allahs Weg ab? Ihr trachtet ihn krumm zu machen, wo ihr ihn doch bezeugt. Doch Allah ist eures Tuns nicht achtlos"},
  {"surah_number": 3, "verse_number": 100, "content": "O ihr, die ihr glaubt, wenn ihr einem Teil jener, denen die Schrift gegeben wurde, gehorcht, so werden sie euch nach eurem Glauben wieder unglaubig machen"},
  {"surah_number": 3, "verse_number": 101, "content": "Wie aber werdet ihr unglaubig werden, wo euch die Verse Allahs verlesen werden und unter euch Sein Gesandter weilt? Und wer an Allah festhalt, der ist auf den rechten Weg geleitet"},
  {"surah_number": 3, "verse_number": 102, "content": "O ihr, die ihr glaubt, furchtet Allah in geziemender Furcht und sterbt nicht anders denn als Muslime"},
  {
    "surah_number": 3,
    "verse_number": 103,
    "content":
        "Und haltet insgesamt an Allahs Seil fest und zerfallet nicht und gedenkt der Gnade Allahs gegen euch, da ihr Feinde waret und Er eure Herzen so zusammenschloß, daß ihr durch Seine Gnade Bruder wurdet; und da ihr am Rande einer Feuergrube waret und Er euch ihr entriß. So macht Allah euch Seine Zeichen klar, auf daß ihr wurdet euch rechtleiten lassen"
  },
  {"surah_number": 3, "verse_number": 104, "content": "Und aus euch soll eine Gemeinde werden, die zum Guten einladt und das gebietet, was Rechtens ist, und das Unrecht verbietet; und diese sind die Erfolgreichen"},
  {"surah_number": 3, "verse_number": 105, "content": "Und seid nicht wie jene, die gespalten und uneins sind, nachdem die deutlichen Zeichen zu ihnen kamen; und jene erwartet eine schmerzliche Strafe"},
  {"surah_number": 3, "verse_number": 106, "content": "an einem Tag, da Gesichter weiß werden und Gesichter schwarz werden. Und was jene anbelangt, deren Gesichter schwarz werden: \"Wurdet ihr nach eurem Glauben unglaubig? So kostet darum die Strafe, weil ihr unglaubig wurdet"},
  {"surah_number": 3, "verse_number": 107, "content": "Und was jene anbelangt, deren Gesichter weiß werden: diese sollen in Allahs Gnade sein, und darin werden sie ewig bleiben"},
  {"surah_number": 3, "verse_number": 108, "content": "Dies sind die Zeichen Allahs; Wir verkunden sie dir in Wahrheit. Und Allah will keine Ungerechtigkeit gegen die Welten"},
  {"surah_number": 3, "verse_number": 109, "content": "Und Allahs ist, was in den Himmeln und was auf der Erde ist, und zu Allah kehren alle Dinge zuruck"},
  {
    "surah_number": 3,
    "verse_number": 110,
    "content":
        "Ihr seid die beste Gemeinde, die fur die Menschen entstand. Ihr gebietet das, was Rechtens ist, und ihr verbietet das Unrecht, und ihr glaubt an Allah. Und wenn die Leute der Schrift geglaubt hatten, wahrlich, es ware gut fur sie gewesen! Unter ihnen sind Glaubige, aber die Mehrzahl von ihnen sind Frevler"
  },
  {"surah_number": 3, "verse_number": 111, "content": "Niemals werden sie euch ein Leid zufugen, es sei denn einen (geringen) Schaden, und wenn sie gegen euch kampfen, werden sie euch den Rucken kehren; als dann werden sie nicht siegreich werden"},
  {
    "surah_number": 3,
    "verse_number": 112,
    "content":
        "Erniedrigung ist fur sie vorgeschrieben, wo immer sie getroffen werden, außer sie waren in Sicherheit (auf Grund) ihrer Verbindung mit Allah oder mit den Menschen. Und sie ziehen sich den Zorn Allahs zu, und fur sie ist die Armut vorgeschrieben, weil sie Allahs Zeichen verleugneten und die Propheten ungerechterweise ermordeten. Solches (geschieht), da sie widerspenstig und Ubertreter waren"
  },
  {"surah_number": 3, "verse_number": 113, "content": "Sie sind aber nicht (alle) gleich. Unter den Leuten der Schrift gibt es (auch) eine Gemeinschaft, die stets die Verse Allahs zur Zeit der Nacht verlesen und sich dabei niederwerfen"},
  {"surah_number": 3, "verse_number": 114, "content": "Diese glauben an Allah und an den Jungsten Tag und gebieten das, was Rechtens ist, und verbieten das Unrecht und wetteifern in guten Werken; und diese gehoren zu den Rechtschaffenen"},
  {"surah_number": 3, "verse_number": 115, "content": "Und was sie an Gutem tun, wird ihnen niemals bestritten; und Allah kennt die Gottesfurchtigen"},
  {"surah_number": 3, "verse_number": 116, "content": "Wahrlich, denjenigen, die unglaubig sind, werden ihr Gut und ihre Kinder keinesfalls etwas vor Allah helfen; und jene sind die Gefahrten des Feuers, und ewig sollen sie darin bleiben"},
  {
    "surah_number": 3,
    "verse_number": 117,
    "content":
        "Das Gleichnis dessen, was sie in diesem irdischen Leben spenden, ist das Gleichnis des eiskalten Windes, welcher den Acker von Leuten trifft, die gegen sich selber sundigten. Und so vernichtet er ihn, und nicht Allah war gegen sie ungerecht, sondern gegen sich selber waren sie ungerecht"
  },
  {
    "surah_number": 3,
    "verse_number": 118,
    "content":
        "O ihr, die ihr glaubt, schließt keine Freundschaft, außer mit euresgleichen. Sie werden nicht zaudern, euch zu verderben, und wunschen euren Untergang. Schon wurde Haß aus ihrem Mund offenkundig, aber das, was ihr Inneres verbirgt, ist schlimmer. Schon machten Wir euch die Zeichen klar, wenn ihr es begreift"
  },
  {
    "surah_number": 3,
    "verse_number": 119,
    "content":
        "Seht da! Ihr seid es, die ihr sie liebt, doch sie lieben euch nicht; und ihr glaubt an das ganze Buch. Und wenn sie euch begegnen, sagen sie: \"Wir glauben\"; sind sie jedoch allein, so beißen sie sich in die Fingerspitzen aus Grimm gegen euch. Sprich: \"Sterbt an eurem Grimm.\" Siehe, Allah kennt das Innerste eurer Herzen"
  },
  {
    "surah_number": 3,
    "verse_number": 120,
    "content": "Wenn euch etwas Gutes trifft, empfinden sie es als Ubel, und wenn euch ein Ubel trifft, so freuen sie sich dessen. Aber wenn ihr geduldig und gottesfurchtig seid, wird ihre List euch keinen Schaden zufugen. Seht, Allah umfaßt (mit Seinem Wissen) das, was sie tun"
  },
  {"surah_number": 3, "verse_number": 121, "content": "Und damals verließest du deine Familie in der Fruhe, um die Glaubigen in die Stellungen des Kampfes einzuweisen; und Allah ist Allhorend, Allwissend"},
  {"surah_number": 3, "verse_number": 122, "content": "Da verloren zwei Gruppen von euch beinahe den Mut, und Allah war beider Beschutzer. Und auf Allah sollen sich die Glaubigen verlassen"},
  {"surah_number": 3, "verse_number": 123, "content": "Und da verhalf Allah euch bei Badr zum Sieg, wahrend ihr verachtlich erschient; darum furchtet Allah; vielleicht werdet ihr dankbar sein"},
  {"surah_number": 3, "verse_number": 124, "content": "Als du zu den Glaubigen sagtest: \"Genugt es euch denn nicht, daß euer Herr euch mit dreitausend herniedergesandten Engeln hilft"},
  {"surah_number": 3, "verse_number": 125, "content": "Ja, wenn ihr geduldig und gottesfurchtig seid und sie sofort uber euch kommen, wird euer Herr euch mit funftausend Engeln in Kampfbereitschaft helfen"},
  {"surah_number": 3, "verse_number": 126, "content": "Und dies machte Allah allein als frohe Botschaft fur euch, und auf daß eure Herzen ruhig waren denn der Sieg kommt nur von Allah, dem Allmachtigen, dem Allweisen"},
  {"surah_number": 3, "verse_number": 127, "content": "auf daß Er den Unglaubigen einen Teil (ihrer Macht) abschneide oder sie niederwerfe, so daß sie enttauscht heimkehren"},
  {"surah_number": 3, "verse_number": 128, "content": "Von dir ist es gar nicht abhangig, ob Er Sich ihnen wieder verzeihend zuwendet oder ob Er sie straft; denn sie sind ja Frevler"},
  {"surah_number": 3, "verse_number": 129, "content": "Und Allahs ist, was in den Himmeln und was auf der Erde ist; Er verzeiht, wem Er will, und straft, wen Er will, und Allah ist Allverzeihend und Barmherzig"},
  {"surah_number": 3, "verse_number": 130, "content": "O ihr, die ihr glaubt, verschlingt nicht die Zinsen in mehrfacher Verdoppelung, sondern furchtet Allah; vielleicht werdet ihr erfolgreich sein"},
  {"surah_number": 3, "verse_number": 131, "content": "Und furchtet das Feuer, das fur die Unglaubigen vorbereitet ist"},
  {"surah_number": 3, "verse_number": 132, "content": "und gehorcht Allah und dem Gesandten; vielleicht werdet ihr Erbarmen finden"},
  {"surah_number": 3, "verse_number": 133, "content": "Und wetteifert nach der Vergebung eures Herrn und nach einem Garten, dessen Breite der von Himmel und Erde entspricht, der fur die Gottesfurchtigen vorbereitet ist"},
  {"surah_number": 3, "verse_number": 134, "content": "die da spenden in Freud und Leid und den Groll unterdrucken und den Menschen vergeben. Und Allah liebt die Rechtschaffenen"},
  {
    "surah_number": 3,
    "verse_number": 135,
    "content": "Und diejenigen, die wenn sie etwas Schandliches getan oder gegen sich gesundigt haben Allahs gedenken und fur ihre Sunden um Vergebung flehen; und wer vergibt die Sunden außer Allah? und diejenigen, die nicht auf dem beharren, was sie wissentlich taten"
  },
  {"surah_number": 3, "verse_number": 136, "content": "fur diese besteht ihr Lohn aus Vergebung von ihrem Herrn und aus Garten, durch die Bache fließen; darin werden sie ewig sein, und herrlich ist der Lohn der Wirkenden"},
  {"surah_number": 3, "verse_number": 137, "content": "Schon vor euch hat es Lebensweisen gegeben. So durchwandert die Erde und schaut, wie der Ausgang derer war, welche (die Wahrheit) fur eine Luge erklart haben"},
  {"surah_number": 3, "verse_number": 138, "content": "Dies ist eine Bekanntmachung an die Menschen und eine Leitung und eine Ermahnung fur die Gottesfurchtigen"},
  {"surah_number": 3, "verse_number": 139, "content": "Und seid nicht verzagt und traurig; ihr werdet siegen, wenn ihr glaubig seid"},
  {
    "surah_number": 3,
    "verse_number": 140,
    "content": "Wenn euch eine Harte getroffen hat, so hat eine Harte gleich schon andere Leute getroffen. Und diese Tage (des Sieges und der Niederlage) lassen Wir wechseln unter den Menschen, damit Allah die Glaubigen erkennt und Sich aus euch Zeugen erwahlt. Und Allah liebt die Ungerechten nicht"
  },
  {"surah_number": 3, "verse_number": 141, "content": "Und damit Allah die Glaubigen lautert und die Unglaubigen dahinschwinden laßt"},
  {"surah_number": 3, "verse_number": 142, "content": "Oder meint ihr, in das Paradies einzugehen, ohne daß Allah diejenigen erkennt, die unter euch gekampft haben, und (ohne) daß Er die Geduldigen erkennt"},
  {"surah_number": 3, "verse_number": 143, "content": "Und ihr wunschtet doch auch den Tod, bevor ihr ihm begegnetet. Nun sahet ihr ihn mit eigenen Augen"},
  {
    "surah_number": 3,
    "verse_number": 144,
    "content": "Und Muhammad ist nur ein Gesandter; schon vor ihm gingen die Gesandten dahin. Und ob er stirbt oder getotet wird, werdet ihr auf euren Fersen umkehren? Und wer auf seinen Fersen umkehrt nimmer schadet er Allah etwas; aber Allah wird wahrlich die Dankbaren belohnen"
  },
  {
    "surah_number": 3,
    "verse_number": 145,
    "content": "Keiner wird sterben ohne Allahs Erlaubnis; (denn dies geschieht) gemaß einer zeitlichen Vorherbestimmung. Und dem, der den Lohn der Welt begehrt, geben Wir davon, und dem, der den Lohn des Jenseits begehrt, geben Wir davon; wahrlich, Wir werden die Dankbaren belohnen"
  },
  {"surah_number": 3, "verse_number": 146, "content": "Und wie viele Propheten kampften gegen (einen Feind,) gefolgt von vielen Gottesfurchtigen. Aber sie verzagten nicht bei dem, was sie auf Allahs Weg traf, und sie wurden weder schwach noch demutigten sie sich. Und Allah liebt die Geduldigen"},
  {"surah_number": 3, "verse_number": 147, "content": "Und ihr Wort war nicht anders, als daß sie sagten: \"Unser Herr, vergib uns unsre Sunden und unser Vergehen in unserer Sache; und festige unsere Schritte und hilf uns gegen das unglaubige Volk"},
  {"surah_number": 3, "verse_number": 148, "content": "Und Allah gab ihnen den Lohn dieser Welt und den schonsten Lohn des Jenseits. Und Allah liebt diejenigen, die Gutes tun"},
  {"surah_number": 3, "verse_number": 149, "content": "O ihr, die ihr glaubt, wenn ihr den Unglaubigen gehorcht, kehren sie euch auf euren Fersen um, und ihr werdet die Verlierer sein"},
  {"surah_number": 3, "verse_number": 150, "content": "Doch Allah ist euer Beschutzer, und Er ist der beste Helfer"},
  {"surah_number": 3, "verse_number": 151, "content": "Wir werden in die Herzen der Unglaubigen Schrecken werfen; deshalb, weil sie Allah Gotter (zur Seite) setzten, wozu Er keine Ermachtnis niedersandte; und ihre Wohnstatte wird das Feuer sein, und schlimm ist die Herberge der Ungerechten"},
  {
    "surah_number": 3,
    "verse_number": 152,
    "content":
        "Und wahrlich, Allah hatte euch gegenuber schon Sein Versprechen gehalten, als ihr sie mit Seiner Erlaubnis vernichtetet, bis daß ihr verzagtet und uber die Sache strittet und ungehorsam wurdet, nachdem Er euch hatte sehen lassen, was ihr wunschtet. Einige von euch verlangten nach dieser Welt und andere verlangten nach dem Jenseits. Alsdann kehrte Er euch von ihnen (zur Flucht) ab, um euch zu prufen; und wahrlich, jetzt hat Er euch vergeben. Denn Allah ist voller Huld gegen die Glaubigen"
  },
  {
    "surah_number": 3,
    "verse_number": 153,
    "content": "Als ihr hinaufstiegt und auf niemanden achtetet, wahrend der Gesandte hinter euch her rief, da belohnte Er euch mit Kummer fur Kummer, damit ihr nicht traurig sein solltet uber das euch Entgangene und uber das, was ihr erlitten habt. Und Allah kennt das, was ihr tut"
  },
  {
    "surah_number": 3,
    "verse_number": 154,
    "content":
        "Alsdann sandte Er auf euch nach dem Kummer Sicherheit (in der Art von) Schlafrigkeit nieder. Mudigkeit uberkam eine Gruppe von euch; eine andere Gruppe war aber sorgenvoll mit sich selbst beschaftigt; ungerecht denken sie uber Allah in heidnischem Denken. Sie sagten: \"Haben wir irgend etwas von der Sache?\" Sprich: \"Seht, die ganze Sache ist Allahs.\" Sie verbargen in ihren Seelen, was sie dir nicht kundtaten, indem sie sagten: \"Hatten wir etwas von der Sache gehabt, lagen wir hier nicht erschlagen!\" Sprich: \"Waret ihr auch in euren Hausern gewesen, wahrlich, jene waren hinaus gezogen, denen der Tod bei ihren Ruhestatten vorgezeichnet war und (es geschah,) damit Allah prufe, was in eurem Innern war, und erforsche, was in euren Herzen war. Und Allah kennt das Innerste (der Menschen)"
  },
  {
    "surah_number": 3,
    "verse_number": 155,
    "content": "Wahrlich, diejenigen von euch, welche am Tage des Zusammenstoßes der beiden Scharen (Allah) den Rucken kehrten nur Satan verleitete sie zum Straucheln fur etwas von ihrem Tun. Aber wahrlich, nunmehr hat Allah ihnen vergeben; seht, Allah ist Allverzeihend und Milde"
  },
  {
    "surah_number": 3,
    "verse_number": 156,
    "content":
        "O ihr, die ihr glaubt, seid nicht gleich den Unglaubigen, die da sprechen von ihren Brudern, als sie das Land durchwanderten oder Streiter waren: \"Waren sie bei uns geblieben, waren sie nicht gestorben und nicht erschlagen worden.\" Allah bestimmte dies als Kummer fur ihre Herzen. Und Allah macht lebendig und laßt sterben, und Allah durchschaut euer Tun"
  },
  {"surah_number": 3, "verse_number": 157, "content": "Und wahrlich, wenn ihr auf dem Wege Allahs erschlagen werdet oder sterbt, wahrlich, Allahs Vergebung und Barmherzigkeit ist besser als das, was ihr zusammenscharrt"},
  {"surah_number": 3, "verse_number": 158, "content": "Und wenn ihr sterbt oder erschlagen werdet, werdet ihr vor Allah versammelt"},
  {
    "surah_number": 3,
    "verse_number": 159,
    "content":
        "Und in Anbetracht der Barmherzigkeit Allahs warst du mild zu ihnen; warst du aber rauh und harten Herzens gewesen, so waren sie dir davongelaufen. Darum vergib ihnen und bitte fur sie um Verzeihung und ziehe sie in der Sache zu Rate; und wenn du entschlossen bist, dann vertrau auf Allah; denn wahrlich, Allah liebt diejenigen, die auf Ihn vertrauen"
  },
  {"surah_number": 3, "verse_number": 160, "content": "Wahrlich, wenn Allah euch zum Sieg verhilft, so gibt es keinen, der uber euch siegen konnte; wenn Er euch aber im Stich laßt, wer konnte euch da helfen ohne Ihn? Wahrlich, auf Allah sollen die Glaubigen vertrauen"},
  {
    "surah_number": 3,
    "verse_number": 161,
    "content":
        "Und kein Prophet darf (etwas von der Beute) unterschlagen. Und wer (etwas) unterschlagt, soll das, was er unterschlagen hat, (zu seiner eigenen Belastung) am Tag der Auferstehung bringen. Alsdann wird jeder Seele nach ihrem Verdienst vergolten werden, und es soll ihnen kein Unrecht geschehen"
  },
  {"surah_number": 3, "verse_number": 162, "content": "Und ist denn der, welcher das Wohlgefallen Allahs erstrebte, wie der, welcher sich den Zorn Allahs zuzog und dessen Herberge Gahannam ist? Und schlimm ist das Ende"},
  {"surah_number": 3, "verse_number": 163, "content": "Sie stehen in verschiedenem Rang bei Allah, und Allah durchschaut ihr Tun"},
  {
    "surah_number": 3,
    "verse_number": 164,
    "content": "Wahrlich, huldreich war Allah gegen die Glaubigen, da Er unter ihnen einen Gesandten aus ihrer Mitte erweckte, um ihnen Seine Verse zu verlesen und sie zu reinigen und das Buch und die Weisheit zu lehren; denn siehe, sie hatten sich zuvor in einem offenkundigen Irrtum befunden"
  },
  {"surah_number": 3, "verse_number": 165, "content": "Und als euch ein Ungluck traf, nachdem ihr das Doppelte erlangtet, sprachet ihr da etwa: \"Woher (kommt) dies?\" Sprich: \"Es kommt von euch selber.\" Siehe, Allah hat Macht uber alle Dinge"},
  {"surah_number": 3, "verse_number": 166, "content": "Und was euch am Tage des Zusammenstoßes der beiden Scharen traf, das geschah mit Allahs Erlaubnis, und damit Er die Glaubigen erkennt"},
  {
    "surah_number": 3,
    "verse_number": 167,
    "content":
        "und (damit) Er die Heuchler erkennt, zu denen gesprochen wurde: \"Heran! Kampft auf Allahs Weg oder wehrt ab!\" Sie aber sagten: \"Wenn wir zu kampfen gewußt hatten, wahrlich, wir waren euch gefolgt!\" Sie waren an jenem Tage dem Unglauben naher als dem Glauben. Sie sagten mit ihrem Munde das, was nicht in ihren Herzen lag; und Allah weiß wohl, was sie verbergen"
  },
  {"surah_number": 3, "verse_number": 168, "content": "(Das sind) diejenigen, die zu ihren Brudern sagten, wahrend sie daheim blieben: \"Hatten sie uns gehorcht, waren sie nicht erschlagen worden.\" Sprich: \"So wehrt von euch den Tod ab, wenn ihr wahrhaftig seid"},
  {"surah_number": 3, "verse_number": 169, "content": "Und betrachte nicht diejenigen, die auf Allahs Weg gefallen sind, als tot; doch sie leben bei ihrem Herrn, und sie werden dort versorgt"},
  {"surah_number": 3, "verse_number": 170, "content": "Sie freuen sich uber das, was Allah ihnen von Seiner Huld gab, und von Freude erfullt (sind sie) uber diejenigen, die ihnen noch nicht gefolgt sind, so daß keine Furcht uber sie kommen wird und sie nicht trauern werden"},
  {"surah_number": 3, "verse_number": 171, "content": "Von Freude erfullt (sind sie) uber die Gnade von Allah und uber Seine Huld und daruber, daß Allah den Lohn der Glaubigen nicht verlorengehen laßt"},
  {"surah_number": 3, "verse_number": 172, "content": "Diejenigen, die da auf Allah und den Gesandten horten, nachdem sie eine Niederlage erlitten hatten: Fur diejenigen von ihnen, welche Gutes taten und gottesfurchtig waren, ist großer Lohn"},
  {"surah_number": 3, "verse_number": 173, "content": "Diejenigen, zu denen die Leute sagten: \"Seht, die Leute haben sich bereits gegen euch geschart; furchtet sie darum!\" nur starker wurden sie im Glauben und sagten: \"Uns genugt Allah, und Er ist der beste Anwalt"},
  {"surah_number": 3, "verse_number": 174, "content": "Daher kehrten sie mit Allahs Gnade und Huld zuruck, ohne daß sie ein Ubel getroffen hatte, und sie folgten dem Wohlgefallen Allahs; und Allah ist voll großer Huld"},
  {"surah_number": 3, "verse_number": 175, "content": "Jener Satan will nur seine Helfer furchten lassen; furchtet sie aber nicht, sondern furchtet Mich, wenn ihr glaubig seid"},
  {"surah_number": 3, "verse_number": 176, "content": "Und laß dich nicht von jenen betruben, die energisch dem Unglauben nacheilen; siehe, nimmer konnen sie Allah etwas zuleide tun. Allah will ihnen keinen Anteil am Jenseits geben, und fur sie ist eine große Strafe (bestimmt)"},
  {"surah_number": 3, "verse_number": 177, "content": "Wahrlich, wer den Glauben fur den Unglauben verkauft nimmer vermogen sie Allah etwas zuleide zu tun, und fur sie ist eine schmerzliche Strafe (bereitet)"},
  {"surah_number": 3, "verse_number": 178, "content": "Und die Unglaubigen sollen nicht wahnen, daß das, was Wir ihnen an Frist gewahren, fur sie gut sei; Wir geben ihnen nur langes Leben, so daß sie in Sunde wachsen. Und fur sie ist eine schandende Strafe (bestimmt)"},
  {
    "surah_number": 3,
    "verse_number": 179,
    "content":
        "Allah gedenkt die Glaubigen nur so lange in der Lage zu belassen, in welcher ihr seid, bis daß Er die Schlechten von den Guten gesondert hat. Und Allah gedenkt nicht, euch das Verborgene zu offenbaren, sondern Allah erwahlt von Seinen Gesandten, wen Er will; so glaubt an Allah und Seine Gesandten; und wenn ihr glaubt und gottesfurchtig seid, so wird euch ein gewaltiger Lohn zuteil sein"
  },
  {
    "surah_number": 3,
    "verse_number": 180,
    "content":
        "Und diejenigen, die mit dem geizen, was Allah (ihnen) von Seiner Huld gegeben hat, sollen ja nicht meinen, das sei so besser fur sie. Nein, zum Bosen soll es ihnen dienen. Als Halsband sollen sie am Tag der Auferstehung das tragen, womit sie geizig waren. Und Allahs ist das Erbe der Himmel und der Erde, und Allah kennt euer Tun"
  },
  {"surah_number": 3, "verse_number": 181, "content": "Wahrlich, Allah hat das Wort jener gehort, die da sagten: \"Siehe, Allah ist arm und wir sind reich.\" Wir wollen ihre Worte und ihr ungerechtes Ermorden der Propheten niederschreiben und sprechen: \"Kostet die Strafe des Brennens"},
  {"surah_number": 3, "verse_number": 182, "content": "Dies ist fur das, was eure Hande vorausschickten, und Allah ist nicht ungerecht gegen die Diener"},
  {
    "surah_number": 3,
    "verse_number": 183,
    "content":
        "die da sagen: \"Siehe, Allah hat uns verpflichtet, keinem Gesandten zu glauben, bevor er uns ein Opfer bringt, welches das Feuer verzehrt!\"\" Sprich: \"Schon vor mir kamen zu euch Gesandte mit den deutlichen Zeichen und mit dem, wovon ihr sprecht. Weshalb denn ermordetet ihr sie, wenn ihr wahrhaftig seid"
  },
  {"surah_number": 3, "verse_number": 184, "content": "Und wenn sie dich der Luge bezichtigen, so sind schon vor dir Gesandte der Luge bezichtigt worden, obwohl sie mit den deutlichen Zeichen, den Schriften und dem erleuchtenden Buch kamen"},
  {
    "surah_number": 3,
    "verse_number": 185,
    "content": "Jede Seele wird den Tod kosten, und euch wird euer Lohn am Tag der Auferstehung vollstandig gegeben; und wer da vom Feuer ferngehalten und ins Paradies gefuhrt wird, der soll glucklich sein. Und das irdische Leben ist nichts als ein trugerischer Nießbrauch"
  },
  {
    "surah_number": 3,
    "verse_number": 186,
    "content":
        "Wahrlich, ihr sollt gepruft werden in eurem Gut und an euch selber, und wahrlich, ihr sollt viele verletzende Außerungen von denen horen, welchen die Schrift vor euch gegeben wurde und von denen, die Allah Gefahrten (zur Seite) setzten. Wenn ihr jedoch geduldig und gottesfurchtig seid dies gehort wahrlich zu den Dingen der Entschlossenheit"
  },
  {
    "surah_number": 3,
    "verse_number": 187,
    "content": "Und als Allah den Bund mit denen schloß, welchen die Schrift gegeben wurde, (und sprach:) \"Wahrlich, tut sie den Menschen kund und verbergt sie nicht\" da warfen sie sie uber ihre Schulter und verkauften sie fur einen winzigen Preis. Und schlimm ist das, was sie (dafur) erkaufen"
  },
  {"surah_number": 3, "verse_number": 188, "content": "Du sollst nicht meinen, daß diejenigen, die sich ihrer Tat freuen und geruhmt zu werden wunschen fur das, was sie nicht getan haben, der Strafe entronnen seien. Ihnen wird eine schmerzliche Strafe zuteil sein"},
  {"surah_number": 3, "verse_number": 189, "content": "Und Allahs ist das Reich der Himmel und der Erde, und Allah hat Macht uber alle Dinge"},
  {"surah_number": 3, "verse_number": 190, "content": "Wahrlich, in der Schopfung der Himmel und der Erde und in dem Wechsel der Nacht und des Tages, liegen wahre Zeichen fur die Verstandigen"},
  {
    "surah_number": 3,
    "verse_number": 191,
    "content": "die Allahs gedenken im Stehen und im Sitzen und (Liegen) auf ihren Seiten und uber die Schopfung der Himmel und der Erde nachdenken (und sagen): \"Unser Herr, Du hast dieses nicht umsonst erschaffen. Gepriesen seist Du, darum hute uns vor der Strafe des Feuers"
  },
  {"surah_number": 3, "verse_number": 192, "content": "Unser Herr, wahrlich, wen Du ins Feuer fuhrst, den fuhrst Du in Schande, und die Ungerechten haben keine Helfer"},
  {
    "surah_number": 3,
    "verse_number": 193,
    "content": "Unser Herr, wahrlich, wir horten einen Rufer, der zum Glauben aufrief (und sprach:) \"Glaubt an euren Herrn!\" und so glauben wir. Unser Herr, und vergib uns darum unsere Sunden und tilge unsere Missetaten und laß uns mit den Frommen verscheiden"
  },
  {"surah_number": 3, "verse_number": 194, "content": "Unser Herr, und gib uns, was Du uns durch Deine Gesandten versprochen hast, und fuhre uns nicht in Schande am Tag der Auferstehung. Wahrlich, Du brichst nicht (Dein) Versprechen"},
  {
    "surah_number": 3,
    "verse_number": 195,
    "content":
        "Da erhorte sie ihr Herr (und sprach): \"Seht, Ich lasse kein Werk der Wirkenden unter euch verlorengehen, sei es von Mann oder Frau; die einen von euch sind von den anderen. Und diejenigen, die da auswanderten und aus ihren Hausern vertrieben wurden und auf Meinem Weg litten und kampften und fielen wahrlich, tilgen will Ich ihre Missetaten, und wahrlich, fuhren will Ich sie in Garten, durch die Bache eilen, als Lohn von Allah.\" Und bei Allah ist die beste Belohnung"
  },
  {"surah_number": 3, "verse_number": 196, "content": "Laß dich nicht trugen durch den Wandel der Unglaubigen in den Landern"},
  {"surah_number": 3, "verse_number": 197, "content": "Ein winziger Nießbrauch dann ist Gahannam ihre Herberge, und schlimm ist das Lager"},
  {"surah_number": 3, "verse_number": 198, "content": "Wer jedoch seinen Herrn furchtet denen werden Garten zuteil sein, durcheilt von Bachen; ewig werden sie darin bleiben, eine Bewirtung von Allah und was bei Allah bereitsteht, ist fur die Frommen besser"},
  {
    "surah_number": 3,
    "verse_number": 199,
    "content":
        "Und wahrlich, unter den Leuten der Schrift gibt es solche, die an Allah glauben und an das, was zu euch hinabgesandt worden ist, und was hinabgesandt worden ist zu ihnen. Dabei sind sie demutig gegen Allah und verkaufen Seine Zeichen nicht gegen einen winzigen Preis. Jene haben ihren Lohn bei ihrem Herrn. Wahrlich, Allah ist schnell im Abrechnen"
  },
  {"surah_number": 3, "verse_number": 200, "content": "O ihr, die ihr glaubt, ubt Geduld und wetteifert in Geduld und seid standhaft und furchtet Allah; vielleicht werdet ihr erfolgreich sein"},
  {
    "surah_number": 4,
    "verse_number": 1,
    "content":
        "O ihr Menschen, furchtet euren Herrn, Der euch erschaffen hat aus einem einzigen Wesen und aus ihm erschuf Er seine Gattin, und aus den beiden ließ Er viele Manner und Frauen entstehen. Und furchtet Allah, in Dessen Namen ihr einander bittet, sowie (im Namen eurer) Blutsverwandtschaft. Wahrlich, Allah wacht uber euch"
  },
  {"surah_number": 4, "verse_number": 2, "content": "Und gebt den Waisen ihr Gut, und tauscht nicht (euer) Schlechtes mit (ihrem) Guten ein, und zehrt nicht ihr Gut zu dem eurigen hinzu; seht, das ist ein großes Verbrechen"},
  {
    "surah_number": 4,
    "verse_number": 3,
    "content":
        "Und wenn ihr furchtet, nicht gerecht gegen die Waisen zu sein, so heiratet, was euch an Frauen gut ansteht, zwei, drei oder vier; und wenn ihr furchtet, nicht billig zu sein, (heiratet) eine oder was im Besitz eurer rechten (Hand ist). So konnt ihr am ehesten Ungerechtigkeit vermeiden"
  },
  {"surah_number": 4, "verse_number": 4, "content": "Und gebt den Frauen ihre Brautgabe als Schenkung. Und wenn sie euch gern etwas davon erlassen, so konnt ihr dies unbedenklich zum Wohlsein verbrauchen"},
  {"surah_number": 4, "verse_number": 5, "content": "Und gebt nicht den Schwachsinnigen euer Gut, das Allah euch zum Unterhalt gegeben hat. Versorgt sie davon und kleidet sie und sprecht zu ihnen mit freundlichen Worten"},
  {
    "surah_number": 4,
    "verse_number": 6,
    "content":
        "Und pruft die Waisen, bis sie die Ehereife erreicht haben; und wenn ihr in ihnen Vernunft wahrnehmt, so handigt ihnen ihr Gut aus. Und zehrt nicht auf verschwenderisch und in Eile (in der Erwartung), daß sie nicht großjahrig wurden. Und wer (als Vormund) reich ist, der soll sich zuruckhalten, und wer arm ist, der soll nach Billigkeit zehren. Und wenn ihr ihnen ihr Gut aushandigt, lasset dies vor ihnen bezeugen. Es genugt jedoch, daß Allah die Rechenschaft vornimmt"
  },
  {
    "surah_number": 4,
    "verse_number": 7,
    "content": "Den Mannern steht ein Teil von der Hinterlassenschaft ihrer Eltern und Verwandten zu, und ebenfalls den Frauen steht ein Teil von der Hinterlassenschaft ihrer Eltern und Verwandten zu. Sei es wenig oder viel. (Das gilt) als vorgeschriebener Anteil"
  },
  {"surah_number": 4, "verse_number": 8, "content": "Und wenn bei der Teilung die Verwandten und die Waisen und die Armen anwesend sind, so schenkt ihnen etwas davon und sprecht freundliche Worte zu ihnen"},
  {"surah_number": 4, "verse_number": 9, "content": "Und furchten sollen sich diejenigen, die, wenn sie schwache Nachkommen hinterließen, fur sie bangen wurden; Allah sollen sie furchten und geziemende Worte sprechen"},
  {"surah_number": 4, "verse_number": 10, "content": "Wahrlich, diejenigen, die der Waisen Gut ungerecht aufzehren, die zehren (in Wirklichkeit) Feuer in ihre Bauche auf und werden in einem Hollenfeuer brennen"},
  {
    "surah_number": 4,
    "verse_number": 11,
    "content":
        "Allah schreibt euch hinsichtlich eurer Kinder vor: Auf eines mannlichen Geschlechts kommt (bei der Erbteilung) gleichviel wie auf zwei weiblichen Geschlechts. Sind es aber (nur) Frauen, mehr als zwei, sollen sie zwei Drittel der Hinterlassenschaft erhalten. Ist es nur eine, soll sie die Halfte haben. Und jedes Elternteil soll den sechsten Teil der Hinterlassenschaft erhalten, wenn er (der Verstorbene) Kinder hat; hat er jedoch keine Kinder, und seine Eltern beerben ihn, steht seiner Mutter der dritte Teil zu. Und wenn er Bruder hat, soll seine Mutter den sechsten Teil, nach Bezahlung eines etwa gemachten Vermachtnisses oder einer Schuld, erhalten. Eure Eltern und eure Kinder ihr wisset nicht, wer von beiden euch an Nutzen naher steht. (Dies ist) ein Gebot von Allah; wahrlich, Allah ist Allwissend, Allweise"
  },
  {
    "surah_number": 4,
    "verse_number": 12,
    "content":
        "Und ihr bekommt die Halfte von dem, was eure Frauen hinterlassen, falls sie keine Kinder haben; haben sie aber Kinder, dann erhaltet ihr ein Viertel von ihrer Erbschaft, nach allen etwa von ihnen gemachten Vermachtnissen oder Schulden. Und ihnen steht ein Viertel von eurer Erbschaft zu, falls ihr keine Kinder habt; habt ihr aber Kinder, dann erhalten sie ein Achtel von eurer Erbschaft, nach allen etwa von euch gemachten Vermachtnissen oder Schulden. Und wenn es sich um einen Mann handelt oder eine Frau, dessen Erbschaft geteilt werden soll, und der weder Eltern noch Kinder, aber einen Bruder oder eine Schwester hat, dann erhalten diese je ein Sechstel. Sind aber mehr (Geschwister) vorhanden, dann sollen sie sich ein Drittel teilen, nach allen etwa gemachten Vermachtnissen oder Schulden, ohne Beeintrachtigung (dies ist) eine Vorschrift von Allah, und Allah ist Allwissend, Milde"
  },
  {"surah_number": 4, "verse_number": 13, "content": "Dies sind die Schranken Allahs; und den, der Allah und Seinem Gesandten gehorcht, fuhrt Er in Garten ein, durch die Bache fließen; darin sollen sie ewig weilen; und das ist die große Gluckseligkeit"},
  {"surah_number": 4, "verse_number": 14, "content": "Und wer Allah und Seinem Gesandten den Gehorsam versagt und Seine Schranken ubertritt, den fuhrt Er ins Feuer; darin muß er ewig bleiben; und ihm wird eine schmahliche Strafe zuteil"},
  {"surah_number": 4, "verse_number": 15, "content": "Und wenn einige eurer Frauen eine Hurerei begehen, dann ruft vier von euch als Zeugen gegen sie auf; bezeugen sie es, dann schließt sie in die Hauser ein, bis der Tod sie ereilt oder Allah ihnen einen Ausweg gibt"},
  {"surah_number": 4, "verse_number": 16, "content": "Und wenn zwei von euch (Mannern) es begehen, dann fugt ihnen Ubel zu. Wenn sie (aber) umkehren und sich bessern, dann lasset ab von ihnen; denn Allah ist Gnadig und Barmherzig"},
  {"surah_number": 4, "verse_number": 17, "content": "Nur diejenigen haben bei Allah Vergebung zu erwarten, die in Unwissenheit Boses tun und hierauf beizeiten umkehren. Diesen wendet Sich Allah wieder gnadig zu; und Allah weiß Bescheid und ist Allweise"},
  {
    "surah_number": 4,
    "verse_number": 18,
    "content": "Diejenigen aber haben keine Vergebung zu erwarten, die schlechte Taten begehen, und die erst, wenn sie zum Sterben kommen, sagen: \"Jetzt kehre ich um.\" Auch diejenigen nicht, die als Unglaubige sterben. Fur sie haben Wir eine schmerzhafte Strafe bereitet"
  },
  {
    "surah_number": 4,
    "verse_number": 19,
    "content":
        "O ihr, die ihr glaubt, euch ist nicht erlaubt, Frauen gegen ihren Willen zu beerben. Und hindert sie nicht (an der Verheiratung mit einem anderen), um einen Teil von dem zu nehmen, was ihr ihnen (als Brautgabe) gabt, es sei denn, sie hatten offenkundig Hurerei begangen. Verkehrt in Billigkeit mit ihnen; und wenn ihr Abscheu gegen sie empfindet, empfindet ihr vielleicht Abscheu gegen etwas, in das Allah reiches Gut gelegt hat"
  },
  {"surah_number": 4, "verse_number": 20, "content": "Und wenn ihr eine Gattin gegen eine andere eintauschen wollt und ihr habt der einen ein Talent (als Brautgabe) gegeben, so nehmt nichts von ihm fort. Wollt ihr es etwa in Verleumdung und offenbarer Sunde fortnehmen"},
  {"surah_number": 4, "verse_number": 21, "content": "Und wie konntet ihr es fortnehmen, wo ihr einander bereits beiwohntet, und sie mit euch einen festen Bund schlossen"},
  {"surah_number": 4, "verse_number": 22, "content": "Und heiratet keine Frauen, die eure Vater geheiratet hatten, es sei denn, es geschah bereits zuvor. Wahrlich, es ist eine Schande und ein Abscheu und ein ubler Weg"},
  {
    "surah_number": 4,
    "verse_number": 23,
    "content":
        "erboten sind euch (zur Heirat) eure Mutter, eure Tochter, eure Schwestern, eure Vaterschwestern und Mutterschwestern, eure Brudertochter und Schwestertochter, eure Nahrmutter, die euch gestillt haben, und eure Milchschwestern und die Mutter eurer Frauen und eure Stieftochter, die in eurem Schutze sind, von euren Frauen, mit denen ihr (die eheliche Beziehung) vollzogen habt. Habt ihr dies jedoch noch nicht mit ihnen vollzogen, so ist es keine Sunde. Ferner die Ehefrauen eurer Sohne aus eurer Abstammung, und ihr sollt nicht zwei Schwestern zusammen haben, es sei denn (es ist) bereits geschehen. Seht, Allah ist Allverzeihend und Barmherzig"
  },
  {
    "surah_number": 4,
    "verse_number": 24,
    "content":
        "Und (verwehrt sind euch) verheiratete Frauen außer denen, die ihr von Rechts wegen besitzt. Dies ist Allahs Vorschrift fur euch. Und erlaubt ist euch außer diesem, daß ihr mit eurem Geld Frauen begehrt, zur Ehe und nicht zur Hurerei. Und gebt denen, die ihr genossen habt, ihre Brautgabe. Dies ist eine Vorschrift; doch soll es keine Sunde sein, wenn ihr uber die Vorschrift hinaus miteinander eine Ubereinkunft trefft. Seht, Allah ist Allwissend und Allweise"
  },
  {
    "surah_number": 4,
    "verse_number": 25,
    "content":
        "Und wer von euch nicht vermogend genug ist, um glaubige Frauen zu heiraten, der heirate von dem Besitz eurer rechten Hand unter euren glaubigen Magden; und Allah kennt euren Glauben sehr wohl. Ihr seid einer vom anderen. Darum heiratet sie mit Erlaubnis ihrer Familien und gebt ihnen ihre Brautgabe nach Billigkeit, wenn sie keusch sind, weder Unzucht treiben noch insgeheim Liebhaber nehmen. Und wenn sie, nachdem sie verheiratet sind, der Unzucht schuldig werden, dann sollen sie die Halfte der Strafe erleiden, die fur freie Frauen vorgeschrieben ist. Diese (Erleichterung) ist fur diejenigen von euch (vorgesehen), die furchten, in Bedrangnis zu kommen. Daß ihr Geduld ubt, ist besser fur euch; und Allah ist Allverzeihend, Barmherzig"
  },
  {"surah_number": 4, "verse_number": 26, "content": "Allah will euch die Wege derer klar machen, die vor euch waren, und euch dahin leiten und Sich in Gnade zu euch kehren. Und Allah ist Allwissend, Allweise"},
  {"surah_number": 4, "verse_number": 27, "content": "Und Allah will Sich in Gnade zu euch kehren; und diejenigen aber, die den niederen Gelusten folgen, wollen, daß ihr (vom rechten Weg) vollig abweicht"},
  {"surah_number": 4, "verse_number": 28, "content": "Allah will eure Burde erleichtern; denn der Mensch ist schwach erschaffen"},
  {
    "surah_number": 4,
    "verse_number": 29,
    "content": "O Ihr Glaubigen! Bringt euch nicht untereinander in betrugerischer Weise um euer Vermogen! Anders ist es, wenn es sich um ein Geschaft handelt, das ihr in gegenseitigem Ubereinkommen abschließt. Und totet euch nicht (gegenseitig)! Allah verfahrt barmherzig mit euch"
  },
  {"surah_number": 4, "verse_number": 30, "content": "Wenn einer dies in Ubertretung und in frevelhafter Weise tut, werden Wir ihn im Feuer brennen lassen, und das ist Allah ein leichtes"},
  {"surah_number": 4, "verse_number": 31, "content": "Wenn ihr euch von den schwereren unter den euch verbotenen Dingen fernhaltet, dann werden Wir eure geringeren Ubel von euch hinwegnehmen und euch an einen ehrenvollen Platz fuhren"},
  {
    "surah_number": 4,
    "verse_number": 32,
    "content":
        "Und begehrt nicht das, womit Allah die einen von euch vor den anderen ausgezeichnet hat. Die Manner sollen ihren Anteil nach ihrem Verdienst erhalten, und die Frauen sollen ihren Anteil nach ihrem Verdienst erhalten. Und bittet Allah um Seine Huld. Wahrlich, Allah hat vollkommene Kenntnis von allen Dingen"
  },
  {"surah_number": 4, "verse_number": 33, "content": "Und einem jeden haben Wir Erben bestimmt fur das, was Eltern und Verwandte und jene, mit denen eure Eide einen Bund bekraftigten, hinterlassen haben. So gebt ihnen denn ihren Anteil. Seht, Allah ist von allem Zeuge"},
  {
    "surah_number": 4,
    "verse_number": 34,
    "content":
        "Die Manner stehen den Frauen in Verantwortung vor, weil Allah die einen vor den anderen ausgezeichnet hat und weil sie von ihrem Vermogen hingeben. Darum sind tugendhafte Frauen die Gehorsamen und diejenigen, die (ihrer Gatten) Geheimnisse mit Allahs Hilfe wahren. Und jene, deren Widerspenstigkeit ihr befurchtet: ermahnt sie, meidet sie im Ehebett und schlagt sie! Wenn sie euch dann gehorchen, so sucht gegen sie keine Ausrede. Wahrlich, Allah ist Erhaben und Groß"
  },
  {
    "surah_number": 4,
    "verse_number": 35,
    "content": "Und wenn ihr einen Bruch zwischen beiden befurchtet, dann sendet einen Schiedsrichter von seiner Familie und einen Schiedsrichter von ihrer Familie. Wollen sie sich aussohnen, so wird Allah Frieden zwischen ihnen stiften. Wahrlich, Allah ist Allwissend, Allkundig"
  },
  {
    "surah_number": 4,
    "verse_number": 36,
    "content":
        "Und dient Allah und setzt Ihm nichts zur Seite; und seid gut zu den Eltern und zu den Verwandten, den Waisen, den Armen, dem Nachbar, sei er verwandt oder aus der Fremde, dem Begleiter an der Seite, dem Sohn des Weges und zu dem (Sklaven), den ihr von Rechts wegen besitzt. Seht, Allah liebt nicht den Hochmutigen, den Prahler"
  },
  {"surah_number": 4, "verse_number": 37, "content": "die da geizig sind und den Leuten gebieten, geizig zu sein, und verbergen, was Allah ihnen in Seiner Huld gegeben hat; und den Unglaubigen haben Wir eine schandende Strafe bereitet"},
  {"surah_number": 4, "verse_number": 38, "content": "(Ebenfalls) denjenigen, die da ihr Gut spenden, um von den Menschen gesehen zu werden, und nicht an Allah glauben und an den Jungsten Tag; und wer den Satan zum Nachsten hat, der hat einen schlimmen Nachsten"},
  {"surah_number": 4, "verse_number": 39, "content": "Was aber kame uber sie, wenn sie an Allah glaubten und an den Jungsten Tag und von dem spendeten, was Allah ihnen beschert hat? Und Allah weiß uber sie Bescheid"},
  {"surah_number": 4, "verse_number": 40, "content": "Wahrlich, Allah tut kein Unrecht; auch nicht vom Gewicht eines Staubchens. Und ist da irgendeine gute Tat, so vervielfacht Er sie und gibt von Sich aus gewaltigen Lohn"},
  {"surah_number": 4, "verse_number": 41, "content": "Und wie, wenn Wir aus jedem Volk einen Zeugen herbeibringen und dich als Zeugen gegen diese herbeibringen"},
  {"surah_number": 4, "verse_number": 42, "content": "An jenem Tag werden diejenigen, welche unglaubig waren und dem Gesandten den Gehorsam versagten, wunschen, daß doch die Erde uber ihnen geebnet werde, und sie werden kein Wort vor Allah verbergen konnen"},
  {
    "surah_number": 4,
    "verse_number": 43,
    "content":
        "O ihr, die ihr glaubt, nahet nicht dem Gebet, wenn ihr betrunken seid, bis ihr versteht, was ihr sprecht, noch im Zustande der Unreinheit ausgenommen als Reisende unterwegs, bis ihr den Gusl vorgenommen habt. Und wenn ihr krank seid oder euch auf einer Reise befindet oder einer von euch von der Notdurft zuruckkommt, oder wenn ihr die Frauen beruhrt habt und kein Wasser findet, dann sucht guten (reinen) Sand und reibt euch dann Gesicht und Hande ab. Wahrlich, Allah ist Allverzeihend, Allvergebend"
  },
  {"surah_number": 4, "verse_number": 44, "content": "Hast du nicht jene gesehen, denen ein Teil der Schrift gegeben wurde? Sie erkaufen Irrtum und wunschen, daß ihr auch vom Wege abirrt"},
  {"surah_number": 4, "verse_number": 45, "content": "Und Allah kennt am besten eure Feinde, und Allah genugt als Beschutzer, und Allah genugt als Helfer"},
  {
    "surah_number": 4,
    "verse_number": 46,
    "content":
        "Es gibt welche unter den Juden, die Worte aus ihren Stellungen verdrehen und sagen: \"Wir horen und wir gehorchen nicht\", und \"Hore, ohne gehort zu werden\", und \"Sei uns nachsichtig\", indem sie mit ihren Zungen lugen und den Glauben lastern. Und hatten sie gesagt: \"Wir horen und wir gehorchen\", und \"Hore\", und \"Schaue gnadig auf uns\", ware es besser fur sie gewesen und aufrechter. Aber Allah hat sie zur Strafe fur ihren Unglauben verflucht; darum glauben sie nur wenig"
  },
  {
    "surah_number": 4,
    "verse_number": 47,
    "content":
        "O ihr, denen die Schrift gegeben wurde, glaubt an das, was Wir herabgesandt haben und welches das bestatigt, was euch schon vorliegt, bevor Wir manche Gesichter vernichten und sie auf ihre Rucken werfen oder sie verfluchen, wie Wir die Sabbatleute verfluchten. Und Allahs Befehl wird mit Sicherheit ausgefuhrt"
  },
  {"surah_number": 4, "verse_number": 48, "content": "Wahrlich, Allah wird es nicht vergeben, daß Ihm Gotter zur Seite gestellt werden; doch Er vergibt das, was geringer ist als dies, wem Er will. Und wer Allah Gotter zur Seite stellt, der hat wahrhaftig eine gewaltige Sunde begangen"},
  {"surah_number": 4, "verse_number": 49, "content": "Hast du nicht jene gesehen, die sich selber reinsprechen? Allah ist es aber, der reinspricht, wen Er will, und ihnen wird kein Fadchen Unrecht getan"},
  {"surah_number": 4, "verse_number": 50, "content": "Schau, wie sie Lugen gegen Allah erdichten. Und das allein genugt als offenkundige Sunde"},
  {"surah_number": 4, "verse_number": 51, "content": "Hast du nicht jene gesehen, denen ein Teil der Schrift gegeben wurde? Sie glauben an Zauberei und Gotzen, und sie sagen von den Unglaubigen: \"Sie sind in der Lehre besser geleitet als die Glaubigen"},
  {"surah_number": 4, "verse_number": 52, "content": "Diese sind es, die Allah verflucht hat; und fur den, den Allah verflucht, wirst du keinen Helfer finden"},
  {"surah_number": 4, "verse_number": 53, "content": "Oder haben sie (etwa) Anteil an der Herrschaft? Dann wurden sie den Menschen nicht einmal so viel wie die Rille eines Dattelkerns abgeben"},
  {"surah_number": 4, "verse_number": 54, "content": "Oder beneiden sie die Menschen um das, was Allah ihnen aus Seiner Huld gegeben hat? Nun, Wir gaben wohl dem Haus Abrahams das Buch und die Weisheit, und Wir gaben ihnen ein machtiges Reich"},
  {"surah_number": 4, "verse_number": 55, "content": "Und einige von ihnen glaubten daran, andere aber wandten sich davon ab. Und Gahannam ist schlimm genug als ein Flammenfeuer"},
  {"surah_number": 4, "verse_number": 56, "content": "Diejenigen, die nicht an Unsere Zeichen glauben, die werden Wir im Feuer brennen lassen: Sooft ihre Haut verbrannt ist, geben Wir ihnen eine andere Haut, damit sie die Strafe kosten. Wahrlich, Allah ist Allmachtig, Allweise"},
  {
    "surah_number": 4,
    "verse_number": 57,
    "content": "Diejenigen aber, die glauben und gute Werke tun, wollen Wir in Garten eingehen lassen, durch die Bache fließen, darin werden sie ewig weilen; dort sollen sie reine Gattinnen haben, und Wir werden sie in einen wohltatigen Ort mit reichlich Schatten eingehen lassen"
  },
  {"surah_number": 4, "verse_number": 58, "content": "Allah befiehlt euch, die anvertrauten Guter ihren Eigentumern zuruckzugeben; und wenn ihr zwischen Menschen richtet, nach Gerechtigkeit zu richten. Wahrlich, billig ist, wozu Allah euch ermahnt. Allah ist Allhorend, Allsehend"},
  {
    "surah_number": 4,
    "verse_number": 59,
    "content":
        "O ihr, die ihr glaubt, gehorcht Allah und gehorcht dem Gesandten und denen, die unter euch Befehlsgewalt besitzen. Und wenn ihr uber etwas streitet, so bringt es vor Allah und den Gesandten, wenn ihr an Allah glaubt und an den Jungsten Tag. Das ist das Beste und nimmt am ehesten einen guten Ausgang"
  },
  {
    "surah_number": 4,
    "verse_number": 60,
    "content":
        "Hast du nicht jene gesehen, die behaupteten, an das zu glauben, was zu dir und was vor dir hinabgesandt worden ist? Sie wollen (nun) eine rechtswirksame Entscheidung beim Teufel suchen, wo ihnen doch befohlen worden ist, nicht daran zu glauben; und Satan will sie weit verwirren lassen"
  },
  {"surah_number": 4, "verse_number": 61, "content": "Und wenn ihnen gesagt wird: \"Kommt her zu dem, was Allah herabgesandt hat, und zu dem Gesandten\", siehst du die Heuchler sich in Widerwillen von dir abwenden"},
  {"surah_number": 4, "verse_number": 62, "content": "Aber wie, wenn ein Unheil sie trifft fur die fruheren Werke ihrer Hande, dann kommen sie zu dir und schworen bei Allah: \"Wir wollten es ja nur gut machen und (zwischen ihnen) schlichten"},
  {"surah_number": 4, "verse_number": 63, "content": "Diese sind es, von denen Allah wohl Bescheid weiß, was in ihren Herzen ist. So wende dich von ihnen ab und ermahne sie und sprich zu ihnen uber sie selbst ein eindringliches Wort"},
  {
    "surah_number": 4,
    "verse_number": 64,
    "content":
        "Und Wir haben keinen Gesandten geschickt, ohne daß ihm mit Allahs Erlaubnis gehorcht worden ware. Und waren sie zu dir gekommen, nachdem sie sich gegen sich selber vergangen hatten, und hatten sie zu Allah um Verzeihung gefleht, und hatte der Gesandte fur sie um Verzeihung gebeten, hatten sie gewiß Allah Allvergebend, Barmherzig gefunden"
  },
  {"surah_number": 4, "verse_number": 65, "content": "Doch nein, bei deinem Herrn; sie sind nicht eher Glaubige, bis sie dich zum Richter uber alles machen, was zwischen ihnen strittig ist, und dann in ihren Herzen keine Bedenken gegen deine Entscheidung finden und sich voller Ergebung fugen"},
  {
    "surah_number": 4,
    "verse_number": 66,
    "content":
        "Und hatten Wir ihnen vorgeschrieben: \"Totet euch selbst oder verlaßt eure Hauser\", so wurden sie es nicht tun, ausgenommen einige wenige von ihnen; hatten sie aber das getan, wozu sie aufgefordert worden waren, so ware es wahrlich besser fur sie gewesen und starkend (fur ihren Glauben)"
  },
  {"surah_number": 4, "verse_number": 67, "content": "Dann wurden Wir ihnen gewiß einen großen Lohn von Uns aus geben"},
  {"surah_number": 4, "verse_number": 68, "content": "Und Wir wurden sie sicher auf den geraden Weg leiten"},
  {"surah_number": 4, "verse_number": 69, "content": "Und wer Allah und dem Gesandten gehorcht, soll unter denen sein, denen Allah Seine Huld gewahrt, unter den Propheten, den Wahrhaftigen, den Zeugen und den Rechtschaffenen welch gute Gefahrten"},
  {"surah_number": 4, "verse_number": 70, "content": "Derart ist eine Gnade von Allah, und Allah genugt als Allwissender"},
  {"surah_number": 4, "verse_number": 71, "content": "O ihr, die ihr glaubt, seid auf eurer Hut! Dann zieht entweder truppweise aus oder alle zusammen"},
  {"surah_number": 4, "verse_number": 72, "content": "Unter euch ist wohl mancher, der zuruckbleibt, und wenn euch ein Ungluck trifft, sagt er: \"Wahrlich, Allah ist gnadig zu mir gewesen, daß ich nicht bei ihnen zugegen war"},
  {"surah_number": 4, "verse_number": 73, "content": "Wenn euch aber eine Huld von Allah beschieden ist, dann sagt er, als sei keine Freundschaft zwischen euch und ihm: \"Ware ich doch bei ihnen gewesen, dann hatte ich einen großen Erfolg errungen"},
  {"surah_number": 4, "verse_number": 74, "content": "Laßt also fur Allahs Sache diejenigen kampfen, die das irdische Leben um den Preis des jenseitigen Lebens verkaufen. Und wer fur Allahs Sache kampft, als dann getotet wird oder siegt, dem werden Wir einen gewaltigen Lohn geben"},
  {
    "surah_number": 4,
    "verse_number": 75,
    "content": "Und warum wollt ihr nicht fur Allahs Sache kampfen und fur die der Schwachen Manner, Frauen und Kinder, die sagen: \"Unser Herr, fuhre uns heraus aus dieser Stadt, deren Bewohner Bedrucker sind, und gib uns von Dir einen Beschutzer, und gib uns von Dir einen Helfer"
  },
  {"surah_number": 4, "verse_number": 76, "content": "Die da glauben, kampfen fur Allahs Sache, und die nicht glauben, kampfen fur die Sache des Teufels; darum kampft gegen die Anhanger des Satans! Wahrlich, die List des Satans ist schwach"},
  {
    "surah_number": 4,
    "verse_number": 77,
    "content":
        "Hast du nicht jene gesehen, zu denen man sagte: \"Haltet eure Hande zuruck, verrichtet das Gebet und entrichtet die Zakah.\" Doch wenn ihnen der Kampf verordnet wurde, da furchtete ein Teil von ihnen die Menschen wie in Furcht vor Allah oder mit noch großerer Furcht; und sie sagten: \"Unser Herr, warum hast Du uns den Kampf verordnet? Mochtest Du uns nicht noch eine Weile Aufschub gewahren?\" Sprich: \"Die Nutznießung dieser Welt ist gering, und das Jenseits wird fur die Gottesfurchtigen besser sein; und kein Fadchen Unrecht sollt ihr erleiden"
  },
  {
    "surah_number": 4,
    "verse_number": 78,
    "content":
        "Wo auch immer ihr seid, der Tod ereilt euch doch, und waret ihr in hohen Burgen. Und wenn ihnen Gutes begegnet, sagen sie: \"Das ist von Allah\"; und wenn ihnen Schlimmes begegnet, sagen sie: \"Das ist von dir.\" Sprich: \"Alles ist von Allah.\" Warum verstehen denn diese Leute kaum etwas von dem, was ihnen gesagt wird"
  },
  {"surah_number": 4, "verse_number": 79, "content": "Was dich an Gutem trifft, kommt von Allah, und was dich an Schlimmem trifft, kommt von dir selbst. Und Wir haben dich als einen Gesandten zu den Menschen entsandt. Und Allah genugt als Zeuge"},
  {"surah_number": 4, "verse_number": 80, "content": "Wer dem Gesandten gehorcht, der hat Allah gehorcht; und wenn sich jemand abwendet, so haben Wir dich nicht zum Huter uber sie gesandt"},
  {
    "surah_number": 4,
    "verse_number": 81,
    "content": "Und sie sagen: \"Gehorsam\"; doch wenn sie von dir weggehen, dann munkelt ein Teil von ihnen von etwas anderem, als von dem, was du sagst. Allah aber zeichnet auf, woruber sie munkeln. So wende dich von ihnen ab und vertraue auf Allah. Und Allah genugt als Sachwalter"
  },
  {"surah_number": 4, "verse_number": 82, "content": "Sie machen sich keine Gedanken uber den Quran. Ware er von einem anderen als Allah, so wurden sie darin gewiß viel Widerspruch finden"},
  {
    "surah_number": 4,
    "verse_number": 83,
    "content":
        "Und wenn ihnen etwas zu Ohren kommt, das Sicherheit oder Furcht betrifft, machen sie es bekannt. Hatten sie es aber vor den Gesandten und vor jene gebracht, die unter ihnen die Befehlsgewalt besitzen, dann wurden es sicherlich die unter ihnen, die es entschleiern konnten, wissen. Und ware nicht Allahs Gnade uber euch und Seine Barmherzigkeit, waret ihr alle dem Satan gefolgt, bis auf wenige Ausnahmen"
  },
  {
    "surah_number": 4,
    "verse_number": 84,
    "content": "Kampfe darum fur Allahs Sache du wirst fur keinen verantwortlich gemacht außer fur dich selbst und feuere die Glaubigen zum Kampf an. Vielleicht wird Allah die Gewalt derer, die unglaubig sind, aufhalten; und Allahs Gewalt ist viel großer und Er ist strenger im Strafen"
  },
  {"surah_number": 4, "verse_number": 85, "content": "Wer eine gute Fursprache einlegt, dem soll ein Anteil daran zukommen, und wer eine schlechte Fursprache einlegt, bekommt, was ihr entspricht. Und Allah hat Macht uber alle Dinge"},
  {"surah_number": 4, "verse_number": 86, "content": "Und wenn ihr mit einem Gruß gegrußt werdet, so grußt mit einem schoneren wieder oder erwidert ihn. Wahrlich, Allah legt Rechenschaft uber alle Dinge ab"},
  {"surah_number": 4, "verse_number": 87, "content": "(Er ist) Allah; es ist kein Gott außer Ihm. Er wird euch versammeln am Tage der Auferstehung, uber den es keinen Zweifel gibt. Und wer ist glaubwurdiger in der Aussage als Allah"},
  {
    "surah_number": 4,
    "verse_number": 88,
    "content":
        "Was ist euch denn widerfahren, daß ihr in der Angelegenheit der Heuchler in zwei Parteien gespalten seid? Und Allah hat sie verstoßen wegen dem was sie begangen haben. Wollt ihr denn rechtleiten, wen Allah ins Verderben hat gehen lassen? Und fur den, den Allah ins Verderben gehen laßt, findest du keinen Weg"
  },
  {
    "surah_number": 4,
    "verse_number": 89,
    "content":
        "Sie wunschen, daß ihr unglaubig werdet, wie sie unglaubig sind, so daß ihr alle gleich werdet. Nehmt euch daher keine Beschutzer von ihnen, solange sie nicht auf Allahs Weg wandern. Und wenn sie sich abwenden, dann ergreift sie und totet sie, wo immer ihr sie auffindet; und nehmt euch keinen von ihnen zum Beschutzer oder zum Helfer"
  },
  {
    "surah_number": 4,
    "verse_number": 90,
    "content":
        "mit Ausnahme derer, die zu Leuten gelangen, mit denen ihr ein Bundnis habt, und die zu euch kommen, weil ihre Herzen davor zuruckschrecken, gegen euch oder gegen ihr eigenes Volk zu kampfen. Und wenn Allah es gewollt hatte, hatte Er ihnen Macht uber euch geben konnen; dann hatten sie sicherlich gegen euch gekampft. Darum, wenn sie sich von euch fernhalten und nicht gegen euch kampfen, sondern euch Frieden bieten; dann hat Allah euch keinen Grund gegen sie gegeben"
  },
  {
    "surah_number": 4,
    "verse_number": 91,
    "content":
        "Ihr werdet andere finden, die vor euch und vor ihren Leuten Sicherheit haben wollen. Sooft sie wieder zur Feindseligkeit verleitet werden, sturzen sie kopfuber hinein. Wenn sie sich also weder von euch fernhalten noch euch Frieden bieten noch ihre Hande zugeln, dann ergreift sie und totet sie, wo immer ihr sie auffindet. Denn gegen diese haben Wir euch volle Gewalt gegeben"
  },
  {
    "surah_number": 4,
    "verse_number": 92,
    "content":
        "Keinem Glaubigen steht es zu, einen anderen Glaubigen zu toten, es sei denn aus Versehen. Und wer einen Glaubigen aus Versehen totet: dann soll er einen glaubigen Sklaven befreien und Blutgeld an seine Erben zahlen, es sei denn, sie erlassen es aus Mildtatigkeit. War er (der Getotete) aber von einem Volk, das euer Feind ist, und ist der (der ihn getotet hat) glaubig: dann soll er einen glaubigen Sklaven befreien; war er aber von einem Volk, mit dem ihr ein Bundnis habt: dann soll er Blutgeld an seine Erben zahlen und einen glaubigen Sklaven befreien. Wer (das) nicht kann: dann (soll er) zwei Monate hintereinander fasten (dies ist) eine Vergebung von Allah. Und Allah ist Allwissend, Allweise"
  },
  {"surah_number": 4, "verse_number": 93, "content": "Und wer einen Glaubigen vorsatzlich totet, dessen Lohn ist Gahannam, worin er auf ewig bleibt. Allah wird ihm zurnen und ihn von Sich weisen und ihm eine schwere Strafe bereiten"},
  {
    "surah_number": 4,
    "verse_number": 94,
    "content":
        "O ihr, die ihr glaubt, wenn ihr auszieht auf dem Weg Allahs, so stellt erst gehorig Nachforschungen an und sagt zu keinem, der euch den Friedensgruß bietet: \"Du bist kein Glaubiger.\" Ihr trachtet nach den Gutern des irdischen Lebens, doch bei Allah ist des Guten Fulle. So waret ihr einst, dann aber hat Allah euch Seine Huld erwiesen; darum stellt erst gehorig Nachforschungen an. Seht, Allah ist eures Tuns wohl kundig"
  },
  {
    "surah_number": 4,
    "verse_number": 95,
    "content":
        "Diejenigen unter den Glaubigen, die daheim bleiben ausgenommen die Gebrechlichen, und die, welche fur Allahs Sache ihr Gut und Blut im Kampf einsetzen, sind nicht gleich. Allah hat die mit ihrem Gut und Blut Kampfenden uber die, die daheim bleiben, im Rang um eine Stufe erhoht. Jeden von beiden aber hat Allah Gutes verheißen; doch die Kampfenden hat Allah vor den Daheimbleibenden durch großen Lohn ausgezeichnet"
  },
  {"surah_number": 4, "verse_number": 96, "content": "(mit) Rangstufen von Ihm und Vergebung und Barmherzigkeit; und Allah ist Allvergebend, Barmherzig"},
  {
    "surah_number": 4,
    "verse_number": 97,
    "content":
        "Zu jenen, die Unrecht gegen sich selbst verubt haben, sagen die Engel, wenn sie sie abberufen: \"In welchen Umstanden habt ihr euch befunden?\" Sie antworten: \"Wir wurden als Schwache im Lande behandelt.\" Da sprechen jene: \"War Allahs Erde nicht weit genug fur euch, daß ihr darin hattet auswandern konnen?\" Sie sind es, deren Herberge Gahannam sein wird, und schlimm ist das Ende"
  },
  {"surah_number": 4, "verse_number": 98, "content": "Ausgenommen davon sind die unterdruckten Manner, Frauen und Kinder, die uber keinerlei Moglichkeit verfugen und keinen Ausweg finden"},
  {"surah_number": 4, "verse_number": 99, "content": "Diese sind es, denen Allah vergeben moge; denn Allah ist Allvergebend, Allverzeihend"},
  {
    "surah_number": 4,
    "verse_number": 100,
    "content":
        "Und wer fur die Sache Allahs auswandert, der wird auf Erden genug Statten der Zuflucht und der Fulle finden. Und wer seine Wohnung verlaßt und zu Allah und Seinem Gesandten auswandert und dabei vom Tode ereilt wird, fur dessen Lohn sorgt Allah, und Allah ist Allverzeihend, Barmherzig"
  },
  {"surah_number": 4, "verse_number": 101, "content": "Und wenn ihr durch das Land zieht, so ist es keine Sunde fur euch, wenn ihr das Gebet verkurzt, wenn ihr furchtet, die Unglaubigen konnten euch bedrangen. Wahrlich, die Unglaubigen sind eure offenkundigen Feinde"},
  {
    "surah_number": 4,
    "verse_number": 102,
    "content":
        "Und wenn du unter ihnen bist und fur sie das Gebet anfuhrst, so soll ein Teil von ihnen (fur das Gebet) bei dir stehen, doch sollen sie ihre Waffen tragen. Und wenn sie sich niederwerfen, so sollen sie hinter euch treten und eine andere Abteilung, die noch nicht gebetet hat, soll mit dir beten; doch sollen sie auf der Hut sein und ihre Waffen bei sich haben. Die Unglaubigen sahen es gerne, daß ihr eure Waffen und euer Gepack außer acht ließet, so daß sie euch auf einmal uberfallen konnten. Und es ist keine Sunde fur euch, wenn ihr eure Waffen ablegt, falls ihr unter Regen leidet oder krank seid. Seid jedoch (immer) auf eurer Hut. Wahrlich, Allah hat fur die Unglaubigen eine schmahliche Strafe bereitet"
  },
  {"surah_number": 4, "verse_number": 103, "content": "Und wenn ihr das Gebet verrichtet habt, dann gedenket Allahs im Stehen, Sitzen und im Liegen. Und wenn ihr in Sicherheit seid, dann verrichtet das Gebet; wahrlich das Gebet zu bestimmten Zeiten ist fur die Glaubigen eine Pflicht"},
  {"surah_number": 4, "verse_number": 104, "content": "Und lasset nicht nach, die Schar (der Unglaubigen) aufzuspuren. Leidet ihr, so leiden sie gerade so, wie ihr leidet. Doch ihr erhoffet von Allah, was sie nicht erhoffen. Und Allah ist Allwissend, Allweise"},
  {"surah_number": 4, "verse_number": 105, "content": "Wahrlich, zu dir haben Wir das Buch mit der Wahrheit niedergesandt, auf daß du zwischen den Menschen richten mogest, wie Allah es dir gezeigt hat. Sei also nicht ein Verfechter der Treulosen"},
  {"surah_number": 4, "verse_number": 106, "content": "Und bitte Allah um Vergebung. Wahrlich, Allah ist Allverzeihend, Barmherzig"},
  {"surah_number": 4, "verse_number": 107, "content": "Und setze dich nicht fur diejenigen ein, die sich selbst betrugen. Wahrlich, Allah liebt nicht denjenigen, der ein Betruger, ein Sunder ist"},
  {"surah_number": 4, "verse_number": 108, "content": "Sie mochten sich vor den Menschen verbergen, doch vor Allah konnen sie sich nicht verborgen halten; und Er ist bei ihnen, wenn sie sich auf verwerfliche Intrigen vorbereiten. Und Allah ist ihres Tuns kundig"},
  {"surah_number": 4, "verse_number": 109, "content": "Ihr habt euch also fur sie in diesem irdischen Leben eingesetzt. Wer aber wird sich fur sie vor Allah am Tage der Auferstehung einsetzen? Oder wer wird ihr Beschutzer sein"},
  {"surah_number": 4, "verse_number": 110, "content": "Und wer Boses tut oder sich gegen sich selbst vergeht und dann Allah um Vergebung bittet, der findet Allah Allvergebend, Barmherzig"},
  {"surah_number": 4, "verse_number": 111, "content": "Und wer eine Sunde begeht, der begeht sie gegen sich selbst; und Allah ist Allwissend, Allweise"},
  {"surah_number": 4, "verse_number": 112, "content": "Und wer einen Fehler oder eine Sunde begeht und sie dann einem Unschuldigen zur Last legt, der tragt eine Verleumdung und eine offenkundige Sunde"},
  {
    "surah_number": 4,
    "verse_number": 113,
    "content":
        "Und ware dir nicht Allahs Huld und Barmherzigkeit zuteil gewesen, so hatte eine Schar von ihnen beschlossen, dich irrezufuhren. Doch zum Irrweg fuhren sie sich selbst; und dir konnen sie keinerlei Schaden zufugen, und Allah hat das Buch und die Weisheit auf dich herabgesandt und dich gelehrt, was du nicht wußtest, und Allahs Huld, die Er dich erwiesen hat, ist wahrlich großartig"
  },
  {
    "surah_number": 4,
    "verse_number": 114,
    "content": "Nichts Gutes ist in vielen ihrer Besprechungen, es sei denn in solchen, die zur Mildtatigkeit oder zur Gute oder zum Friedenstiften unter den Menschen ermahnen. Und wer das im Trachten nach Allahs Wohlgefallen tut, dem werden Wir einen großen Lohn geben"
  },
  {
    "surah_number": 4,
    "verse_number": 115,
    "content": "Wer sich aber mit dem Gesandten verfeindet, nachdem ihm der rechte Weg klar geworden ist, und einen anderen Weg befolgt als den der Glaubigen, den werden Wir verfolgen lassen, was er verfolgt, und werden ihn dann in Gahannam brennen lassen; und schlimm ist sein Ende"
  },
  {"surah_number": 4, "verse_number": 116, "content": "Wahrlich, Allah wird es nicht vergeben, daß Ihm Gotter zur Seite gestellt werden: doch Er vergibt, was geringer ist als dies, wem Er will. Und wer Allah Gotter zur Seite stellt, der ist in der Tat weit irregegangen"},
  {"surah_number": 4, "verse_number": 117, "content": "Wahrlich, sie rufen statt Seiner nur weibliche Wesen an; dabei rufen sie nur einen rebellischen Satan"},
  {"surah_number": 4, "verse_number": 118, "content": "den Allah verflucht hat und der dies erwiderte: \"Ich werde von Deinen Dienern einen bestimmten Teil nehmen"},
  {
    "surah_number": 4,
    "verse_number": 119,
    "content":
        "und ich werde sie irreleiten und ihre Hoffnungen anregen und ihnen Befehle erteilen, dem Vieh die Ohren aufzuschlitzen, und ich werde ihnen befehlen, und sie werden Allahs Schopfung verandern.\" Und wer sich Satan statt Allah zum Beschutzer nimmt, der hat sicherlich einen offenkundigen Verlust erlitten"
  },
  {"surah_number": 4, "verse_number": 120, "content": "er macht ihnen Versprechungen und erweckt Wunsche in ihnen, und was Satan ihnen verspricht, ist Trug"},
  {"surah_number": 4, "verse_number": 121, "content": "Diese haben Gahannam zur Herberge, und sie werden keinen Ausweg daraus finden"},
  {
    "surah_number": 4,
    "verse_number": 122,
    "content": "Diejenigen aber, die glauben und gute Werke tun, wollen Wir in Garten fuhren, durch welche Bache fließen, darin werden sie auf ewig und immerdar verweilen (dies ist) eine wahrhaftige Verheißung Allahs; und wer ist glaubwurdiger in der Aussage als Allah"
  },
  {"surah_number": 4, "verse_number": 123, "content": "Es ist weder nach euren Wunschen noch nach den Wunschen der Leute der Schrift. Wer Boses tut, dem wird es vergolten werden; und er wird fur sich außer Allah weder Freund noch Helfer finden"},
  {"surah_number": 4, "verse_number": 124, "content": "Diejenigen aber, die handeln, wie es recht ist sei es Mann oder Frau und dabei glaubig sind, werden ins Paradies eingehen und nicht im geringsten Unrecht erleiden"},
  {"surah_number": 4, "verse_number": 125, "content": "Und wer hat eine schonere Religion als jener, der sich Allah ergibt und dabei Gute ubt und dem Glauben Abrahams folgt, des Aufrechten? Und Allah nahm Sich Abraham zum Freund"},
  {"surah_number": 4, "verse_number": 126, "content": "Allahs ist alles, was in den Himmeln und was auf Erden ist; und Allah umfaßt alle Dinge"},
  {
    "surah_number": 4,
    "verse_number": 127,
    "content":
        "Und sie fragen dich um Belehrung uber die Frauen. Sprich: \"Allah hat euch uber sie belehrt; und das, was euch in dem Buch verlesen wird, betrifft die Waisenmadchen, denen ihr nicht gebt, was fur sie vorgeschrieben ist, und die ihr doch zu heiraten wunscht, und die Schwachen unter den Kindern und daß ihr euch nach Billigkeit fur die Waisen einsetzt. Und was ihr an Gutem tut, Allah weiß daruber Bescheid"
  },
  {
    "surah_number": 4,
    "verse_number": 128,
    "content":
        "Und wenn eine Frau von ihrem Ehemann rohe Behandlung oder Gleichgultigkeit befurchtet, so soll es keine Sunde fur beide sein, wenn sie sich auf geziemende Art miteinander versohnen; denn Versohnung ist gut. Die Menschen sind auf Habsucht eingestellt. Tut ihr jedoch Gutes und seid gottesfurchtig, dann ist Allah eures Tuns kundig"
  },
  {
    "surah_number": 4,
    "verse_number": 129,
    "content":
        "Und ihr konnt zwischen den Frauen keine Gerechtigkeit ausuben, so sehr ihr es auch wunschen mogt. Aber neigt euch nicht ganzlich (einer) zu, so daß ihr die andere gleichsam in der Schwebe lasset. Und wenn ihr es wiedergutmacht und gottesfurchtig seid, so ist Allah Allverzeihend, Barmherzig"
  },
  {"surah_number": 4, "verse_number": 130, "content": "Und wenn sie sich trennen, so wird Allah beiden aus Seiner Fulle Genuge tun; denn Allah ist Huldreich und Allweise"},
  {
    "surah_number": 4,
    "verse_number": 131,
    "content":
        "Und Allahs ist, was in den Himmeln und was auf Erden ist. Und Wir haben jenen, denen vor euch die Schrift gegeben wurde, und euch selbst auferlegt, Allah zu furchten. Wenn ihr jedoch unglaubig werdet, dann ist Allahs, was in den Himmeln und was auf Erden ist; und Allah ist auf keinen angewiesen und des Lobes wurdig"
  },
  {"surah_number": 4, "verse_number": 132, "content": "Allahs ist, was in den Himmeln und auf Erden ist, und Allah genugt als Beschutzer"},
  {"surah_number": 4, "verse_number": 133, "content": "Wenn Er will, so wird Er euch fortschaffen, ihr Menschen, und andere bringen; und Allah ist dessen Machtig"},
  {"surah_number": 4, "verse_number": 134, "content": "Wer den Lohn dieser Welt begehrt so ist der Lohn dieser und jener Welt bei Allah; und Allah ist Allhorend, Allsehend"},
  {
    "surah_number": 4,
    "verse_number": 135,
    "content":
        "O ihr, die ihr glaubt, seid auf der Hut bei der Wahrnehmung der Gerechtigkeit und seid Zeugen fur Allah, auch dann, wenn es gegen euch selbst oder gegen Eltern und Verwandte geht. Ob der eine reich oder arm ist, so ist Allah beiden naher; darum folgt nicht der personlichen Neigung, auf daß ihr gerecht handeln konnt. Und wenn ihr aber (die Wahrheit) verdreht oder euch von (der Wahrheit) abwendet, so ist Allah eures Tuns kundig"
  },
  {
    "surah_number": 4,
    "verse_number": 136,
    "content":
        "O ihr, die ihr glaubig geworden seid, glaubt an Allah und Seinen Gesandten und an das Buch, das Er auf Seinen Gesandten herabgesandt hat, und an die Schrift, die Er zuvor herabsandte. Und wer nicht an Allah und Seine Engel und Seine Bucher und Seine Gesandten und an den Jungsten Tag glaubt, der ist wahrlich weit irregegangen"
  },
  {"surah_number": 4, "verse_number": 137, "content": "Wahrlich, diejenigen, die glaubig sind und hernach unglaubig werden, dann wieder glauben, dann abermals unglaubig werden und noch heftiger im Unglauben werden, denen wird Allah nimmermehr vergeben noch sie des Weges leiten"},
  {"surah_number": 4, "verse_number": 138, "content": "Verkundige den Heuchlern die \"frohe\" Botschaft, daß ihnen schmerzliche Strafe zuteil werde"},
  {"surah_number": 4, "verse_number": 139, "content": "jenen, die sich Unglaubige als Beschutzer vor den Glaubigen nehmen. Suchen sie etwa Macht und Ansehen bei ihnen? Wahrlich, Allah allein gehort alle Erhabenheit"},
  {
    "surah_number": 4,
    "verse_number": 140,
    "content":
        "Und Er hat euch schon in dem Buch herabgesandt, daß wenn ihr hort, daß die Zeichen Allahs geleugnet und verspottet werden ihr nicht bei ihnen sitzt, bis sie zu einem anderen Gesprach ubergehen; ihr waret sonst wie sie. Wahrlich, Allah wird die Heuchler und die Unglaubigen allesamt in Gahannam versammeln"
  },
  {
    "surah_number": 4,
    "verse_number": 141,
    "content":
        "die auf Nachrichten uber euch harren. Wenn euch ein Sieg von Allah beschieden wird, sagen sie: \"Waren wir nicht mit euch?\" Haben aber die Unglaubigen einen Anteil (am Erfolg), sagen sie (zu den Unglaubigen): \"Haben wir nicht Oberhand uber euch bekommen und euch vor den Glaubigen beschutzt?\" Allah wird als dann zwischen euch am Tage der Auferstehung richten; und Allah wird niemals den Unglaubigen die Oberhand uber die Glaubigen geben"
  },
  {
    "surah_number": 4,
    "verse_number": 142,
    "content": "Wahrlich, die Heuchler versuchen, Allah zu uberlisten; doch Er wird sie uberlisten. Und wenn sie sich zum Gebet hinstellen, dann stehen sie ungern auf; (sie tun dies nur), um von den Menschen gesehen zu werden, und sie gedenken Allahs nur selten"
  },
  {"surah_number": 4, "verse_number": 143, "content": "Unentschlossen schwanken sie zwischen diesen und jenen und gelangen weder zu diesen noch zu jenen. Und wen Allah irrefuhrt, fur den wirst du nimmermehr einen Ausweg finden"},
  {"surah_number": 4, "verse_number": 144, "content": "O ihr, die ihr glaubt, nehmt euch keine Unglaubigen zu Beschutzern anstelle der Glaubigen. Wollt ihr Allah offenkundige Beweise gegen euch selbst geben"},
  {"surah_number": 4, "verse_number": 145, "content": "Wahrlich, die Heuchler befinden sich auf dem untersten Grund des Hollenfeuers; und du findest fur sie keinen Helfer"},
  {"surah_number": 4, "verse_number": 146, "content": "außer jenen, die es bereut haben und sich bessern und zu Allah Zuflucht nehmen und die sich mit ihrem Glauben nur an Allah richten. Diese gehoren also zu den Glaubigen. Und Allah wird den Glaubigen einen gewaltigen Lohn geben"},
  {"surah_number": 4, "verse_number": 147, "content": "Was wird Allah aus eurer Bestrafung machen, wenn ihr dankbar seid und glaubt? Und Allah ist Dankend, Allwissend"},
  {"surah_number": 4, "verse_number": 148, "content": "Allah liebt nicht, daß bose Worte laut vernehmbar gebraucht werden, außer wenn einem Unrecht geschieht; wahrlich, Allah ist Allhorend, Allwissend"},
  {"surah_number": 4, "verse_number": 149, "content": "Ob ihr etwas Gutes kundtut oder es geheimhaltet oder etwas Boses verzeiht, so ist Allah wahrlich Allvergebend, Allmachtig"},
  {"surah_number": 4, "verse_number": 150, "content": "Wahrlich, diejenigen, die nicht an Allah und Seine Gesandten glauben und eine Trennung zwischen Allah und Seinen Gesandten machen und sagen: \"Wir glauben an die einen und verwerfen die anderen\" und einen Zwischenweg einschlagen mochten"},
  {"surah_number": 4, "verse_number": 151, "content": "diese sind die Unglaubigen im wahren Sinne, und bereitet haben Wir den Unglaubigen eine schmahliche Strafe"},
  {"surah_number": 4, "verse_number": 152, "content": "Diejenigen aber, die an Allah und an Seine Gesandten glauben und zwischen ihnen keinen Unterschied machen, sind es, denen Er ihren Lohn geben wird; und Allah ist Allvergebend, Barmherzig"},
  {
    "surah_number": 4,
    "verse_number": 153,
    "content":
        "Die Leute der Schrift verlangen von dir, daß du ein Buch vom Himmel zu ihnen herabgelangen laßt. Von Moses aber verlangten sie etwas Großeres als dies, da sie sagten: \"Zeig uns Allah offensichtlich!\" Da traf sie der Blitzschlag wegen ihres Frevels. Danach nahmen sie sich das Kalb, nachdem ihnen doch deutliche Zeichen zuteil geworden waren: aber Wir vergaben das. Und Wir verliehen Moses offensichtliche Beweismacht"
  },
  {
    "surah_number": 4,
    "verse_number": 154,
    "content": "Und Wir erhoben anlaßlich des Bundes mit ihnen den Berg uber sie empor und sprachen zu ihnen: \"Tretet durch das Tor in Unterwurfigkeit ein\"; und Wir sprachen zu ihnen: \"Ubertretet nicht das Sabbatgebot.\" Und Wir schlossen einen starken Bund mit ihnen"
  },
  {
    "surah_number": 4,
    "verse_number": 155,
    "content": "Als sie dann ihren Bund brachen und die Zeichen Allahs verleugneten und die Propheten widerrechtlich toteten und sagten: \"Unsere Herzen sind hinter einem Schleier\", aber nein, Allah hat diese wegen ihres Unglaubens verschlossen, so daß sie nur wenig glauben"
  },
  {"surah_number": 4, "verse_number": 156, "content": "Und wegen ihres Unglaubens und wegen ihrer Behauptung, die sie gegen Maria mit einer enormen Luge vorbrachten"},
  {
    "surah_number": 4,
    "verse_number": 157,
    "content":
        "und wegen ihrer Rede: \"Wir haben den Messias, Jesus, den Sohn der Maria, den Gesandten Allahs, getotet\", wahrend sie ihn doch weder erschlagen noch gekreuzigt hatten, sondern dies wurde ihnen nur vorgetauscht; und jene, die in dieser Sache uneins sind, sind wahrlich im Zweifel daruber; sie haben keine Kenntnis davon, sondern folgen nur einer Vermutung; und sie haben ihn nicht mit Gewißheit getotet"
  },
  {"surah_number": 4, "verse_number": 158, "content": "Vielmehr hat Allah ihn zu Sich emporgehoben, und Allah ist Allmachtig, Allweise"},
  {"surah_number": 4, "verse_number": 159, "content": "Und es gibt keinen unter den Leuten der Schrift, der nicht vor seinem Tod daran glauben wird; und am Tage der Auferstehung wird er ein Zeuge gegen sie sein"},
  {"surah_number": 4, "verse_number": 160, "content": "Und der Sunde der Juden wegen haben Wir ihnen gute Dinge verboten, die ihnen erlaubt waren, wie auch, weil sie viele Hindernisse in Allahs Weg legten"},
  {"surah_number": 4, "verse_number": 161, "content": "und weil sie Zins nahmen, obgleich es ihnen untersagt war, und weil sie das Gut der Leute widerrechtlich aufzehrten. Und Wir haben den Unglaubigen unter ihnen eine schmerzliche Strafe bereitet"},
  {
    "surah_number": 4,
    "verse_number": 162,
    "content":
        "Aber denen von ihnen, die ein grundliches Wissen haben, und den Glaubigen, die da an das glauben, was zu dir hinabgesandt wurde und was vor dir hinabgesandt wurde, und denjenigen, die das Gebet verrichten und die Zakah entrichten, und denen, die an Allah und an den Jungsten Tag glauben ihnen werden Wir einen großen Lohn gewahren"
  },
  {
    "surah_number": 4,
    "verse_number": 163,
    "content": "Wahrlich, Wir haben dir offenbart, wie Wir Noah und den Propheten nach ihm offenbart haben. Und Wir offenbarten Abraham, Ismael, Isaak, Jakob, den Stammen (Israels), Jesus, Hiob, Jonas, Aaron und Salomo; und Wir haben David einen Zabur gegeben"
  },
  {"surah_number": 4, "verse_number": 164, "content": "Es sind Gesandte, von denen Wir dir bereits berichtet haben, und Gesandte, von denen Wir dir nicht berichtet haben und Allah hat mit Moses wirklich gesprochen"},
  {"surah_number": 4, "verse_number": 165, "content": "(Es sind) Gesandte, Uberbringer froher Botschaften und Warner, so daß die Menschen nach den Gesandten keinen Beweisgrund gegen Allah haben. Und Allah ist Allmachtig, Allweise"},
  {"surah_number": 4, "verse_number": 166, "content": "Doch Allah bezeugt durch das, was Er zu dir hinabgesandt hat, daß Er es mit Seinem Wissen sandte; und die Engel bezeugen es; und Allah genugt als Zeuge"},
  {"surah_number": 4, "verse_number": 167, "content": "Diejenigen aber, die unglaubig sind und sich von Allahs Weg abwenden, sie sind wahrlich weit in die Irre gegangen"},
  {"surah_number": 4, "verse_number": 168, "content": "Diejenigen, die unglaubig sind und Unrecht verubt haben ihnen wird Allah weder vergeben noch sie zu einem Weg leiten"},
  {"surah_number": 4, "verse_number": 169, "content": "es sei denn den Weg zu Gahannam, in dem sie in aller Ewigkeit bleiben werden. Und dies ist fur Allah ein leichtes"},
  {
    "surah_number": 4,
    "verse_number": 170,
    "content": "O ihr Menschen, der Gesandte ist nunmehr zu euch mit der Wahrheit von eurem Herrn gekommen; glaubt darum, das gereicht euch zum Guten. Seid ihr aber unglaubig, dann ist Allahs, was in den Himmeln und was auf Erden ist; und Allah ist Allwissend, Allweise"
  },
  {
    "surah_number": 4,
    "verse_number": 171,
    "content":
        "O Leute der Schrift, ubertreibt nicht in eurem Glauben und sagt von Allah nichts als die Wahrheit. Wahrlich, der Messias, Jesus, Sohn der Maria, ist nur der Gesandte Allahs und Sein Wort, das Er Maria entboten hat, und von Seinem Geist. Darum glaubt an Allah und Seine Gesandten, und sagt nicht: \"Drei.\" Lasset (davon) ab (das) ist besser fur euch. Allah ist nur ein einziger Gott. Es liegt Seiner Herrlichkeit fern, Ihm ein Kind zuzuschreiben. Sein ist, was in den Himmeln und was auf Erden ist; und Allah genugt als Anwalt"
  },
  {"surah_number": 4, "verse_number": 172, "content": "Der Messias wird es niemals verschmahen, Diener Allahs zu sein; ebenso nicht die (Allah) nahestehenden Engel; und wer es verschmaht, Ihn anzubeten, und sich dazu zu erhaben fuhlt so wird Er sie alle zu Sich versammeln"},
  {"surah_number": 4, "verse_number": 173, "content": "Denen aber, die glauben und gute Werke tun, wird Er den vollen Lohn und noch mehr von Seiner Huld geben; die aber, die verschmahen und stolz sind, die wird Er schmerzlich bestrafen. Und außer Allah finden sie weder Freund noch Helfer"},
  {"surah_number": 4, "verse_number": 174, "content": "O ihr Menschen, zu euch ist in Wahrheit ein deutlicher Beweis von eurem Herrn gekommen; und Wir sandten zu euch ein klares Licht hinab"},
  {"surah_number": 4, "verse_number": 175, "content": "Was aber diejenigen angeht, die an Allah glauben und an Ihm festhalten diese wird Er in Seine Barmherzigkeit und Huld aufnehmen und sie auf dem geraden Weg zu Sich fuhren"},
  {
    "surah_number": 4,
    "verse_number": 176,
    "content":
        "Sie fragen dich um Belehrung. Sprich: \"Allah belehrt euch uber die seitliche Verwandtschaft: Wenn ein Mann stirbt und keine Kinder hinterlaßt, aber eine Schwester hat, dann erhalt sie die Halfte seiner Erbschaft; und er beerbt sie, wenn sie keine Kinder hat. Sind es aber zwei (Schwestern), dann erhalten sie zwei Drittel von seiner Erbschaft. Und wenn sie Geschwister sind, Manner und Frauen, kommt auf eines mannlichen Geschlechts gleichviel wie auf zwei weiblichen Geschlechts.\" Allah macht euch das klar damit ihr nicht irrt; und Allah weiß uber alle Dinge Bescheid"
  },
  {
    "surah_number": 5,
    "verse_number": 1,
    "content": "O ihr, die ihr glaubt, erfullt die Vertrage. Erlaubt ist euch jede Art des Viehs, mit Ausnahme dessen, was euch (in der Schrift) bekanntgegeben wird; nicht, daß ihr die Jagd als erlaubt ansehen durft, wahrend ihr pilgert; wahrlich, Allah richtet, wie Er will"
  },
  {
    "surah_number": 5,
    "verse_number": 2,
    "content":
        "O ihr, die ihr glaubt! Entweihet weder die Glaubensausubung zur Verherrlichung Allahs, noch den heiligen Monat, noch die Opfertiere, noch die geweihten Opfertiere, noch die nach dem heiligen Hause Ziehenden, die da Gunst und Wohlgefallen von ihrem Herrn suchen. Wenn ihr den Weihezustand beendet habt, durft ihr jagen; ihr durft dazu nicht bewegt werden durch den Haß, den ihr gegen Leute hegt, weil sie euch von der heiligen Moschee abgehalten haben, so daß ihr eine Ubertretung begeht. Und helft einander in Rechtschaffenheit und Frommigkeit; doch helft einander nicht in Sunde und Ubertretung. Und furchtet Allah; denn Allah ist streng im Strafen"
  },
  {
    "surah_number": 5,
    "verse_number": 3,
    "content":
        "Verboten ist euch das Verendete sowie Blut und Schweinefleisch und das, woruber ein anderer als Allahs Name angerufen wurde; das Erdrosselte, das zu Tode Geschlagene, das zu Tode Gesturzte oder Gestoßene und das, was Raubtiere angefressen haben, außer dem, was ihr geschlachtet habt, ferner das, was auf einem heidnischen Opferstein geschlachtet worden ist, und ferner (ist euch verboten), daß ihr durch Lospfeile das Schicksal zu erkunden sucht. Das ist eine Freveltat. Heute haben die Unglaubigen vor eurem Glauben resigniert; also furchtet nicht sie, sondern furchtet Mich. Heute habe Ich euch eure Religion vervollkommnet und Meine Gnade an euch vollendet und euch den Islam zum Glauben erwahlt. Wer aber durch Hungersnot gezwungen wird, ohne sundhafte Neigung so ist Allah Allverzeihend, Barmherzig"
  },
  {
    "surah_number": 5,
    "verse_number": 4,
    "content":
        "Sie fragen dich, was ihnen erlaubt sei. Sprich: \"Alle guten Dinge sind euch erlaubt: und was ihr die Jagdtiere gelehrt habt, indem ihr sie zur Jagd abrichtet und sie lehrt, was Allah euch gelehrt hat.\" Also esset von dem, was sie fur euch fangen, und sprecht Allahs Namen daruber aus. Und furchtet Allah; denn Allah ist schnell im Abrechnen"
  },
  {
    "surah_number": 5,
    "verse_number": 5,
    "content":
        "Heute sind euch alle guten Dinge erlaubt. Und die Speise derer, denen die Schrift gegeben wurde, ist euch erlaubt, wie auch eure Speise ihnen erlaubt ist. Und ehrbare glaubige Frauen und ehrbare Frauen unter den Leuten, denen vor euch die Schrift gegeben wurde, wenn ihr ihnen die Brautgabe gebt, und nur fur eine Ehe und nicht fur Unzucht und heimliche Liebschaften. Und wer den Glauben verleugnet, dessen Tat ist ohne Zweifel zunichte geworden; und im Jenseits wird er unter den Verlierern sein"
  },
  {
    "surah_number": 5,
    "verse_number": 6,
    "content":
        "O ihr, die ihr glaubt! Wenn ihr euch zum Gebet begebt, so wascht euer Gesicht und eure Hande bis zu den Ellenbogen und streicht uber euren Kopf und wascht eure Fuße bis zu den Knocheln. Und wenn ihr im Zustande der Unreinheit seid, so reinigt euch. Und wenn ihr krank seid oder euch auf einer Reise befindet oder einer von euch von der Notdurft zuruckkommt, oder wenn ihr Frauen beruhrt habt und kein Wasser findet, so sucht reinen Sand und reibt euch damit Gesicht und Hande ab. Allah will euch nicht mit Schwierigkeiten bedrangen, sondern Er will euch nur reinigen und Seine Gnade an euch erfullen, auf daß ihr dankbar sein moget"
  },
  {"surah_number": 5, "verse_number": 7, "content": "und gedenkt der Gnade Allahs gegen euch und des Bundes, den Er mit euch schloß, als ihr spracht: \"Wir horen und gehorchen.\" Und furchtet Allah; wahrlich, Allah weiß, was die Herzen verbergen"},
  {
    "surah_number": 5,
    "verse_number": 8,
    "content": "O ihr, die ihr glaubt! Setzt euch fur Allah ein und seid Zeugen der Gerechtigkeit. Und der Haß gegen eine Gruppe soll euch nicht (dazu) verleiten, anders als gerecht zu handeln. Seid gerecht, das ist der Gottesfurcht naher. Und furchtet Allah; wahrlich, Allah ist eures Tuns kundig"
  },
  {"surah_number": 5, "verse_number": 9, "content": "Allah hat denen, die glauben und gute Werke tun, verheißen, daß sie Vergebung und großen Lohn erlangen werden"},
  {"surah_number": 5, "verse_number": 10, "content": "Die aber unglaubig sind und Unsere Zeichen verleugnen, werden Insassen der Gahim sein"},
  {"surah_number": 5, "verse_number": 11, "content": "O ihr, die ihr glaubt! Gedenkt der Gnade Allahs uber euch, als eine Gruppe die Hande nach euch auszustrecken trachtete. Er aber hielt ihre Hande von euch zuruck. Und furchtet Allah; auf Allah sollen die Glaubigen vertrauen"},
  {
    "surah_number": 5,
    "verse_number": 12,
    "content":
        "Wahrlich, Allah hatte einen Bund mit den Kindern Israels geschlossen; und Wir erweckten aus ihnen zwolf Fuhrer. Und Allah sprach: \"Seht, Ich bin mit euch, wenn ihr das Gebet verrichtet und die Zakah entrichtet und an Meine Gesandten glaubt und sie unterstutzt und Allah ein gutes Darlehen gebt, dann werde Ich eure Missetaten tilgen und euch in Garten fuhren, in denen Bache fließen. Wer von euch aber hierauf in den Unglauben zuruckfallt, der ist vom rechten Weg abgeirrt"
  },
  {
    "surah_number": 5,
    "verse_number": 13,
    "content":
        "Deshalb, weil sie ihren Bund brachen, haben Wir sie verflucht und haben ihre Herzen verhartet. Sie entstellten die Schrift an ihren richtigen Stellen und sie haben einen Teil von dem vergessen, woran sie gemahnt wurden. Und du wirst nicht aufhoren, auf ihrer Seite bis auf einige von ihnen Verrat zu entdecken. Also vergib ihnen und wende dich (von ihnen) ab. Wahrlich, Allah liebt jene, die Gutes tun"
  },
  {
    "surah_number": 5,
    "verse_number": 14,
    "content":
        "Und auch mit denen, die sagen: \"Wir sind Christen\", schlossen Wir einen Bund; auch diese haben dann einen Teil von dem vergessen, woran sie gemahnt wurden. Darum erregten Wir Feindschaft und Haß unter ihnen bis zum Tage der Auferstehung. Und Allah wird sie wissen lassen, was sie getan haben"
  },
  {
    "surah_number": 5,
    "verse_number": 15,
    "content": "O Leute der Schrift, Unser Gesandter ist nunmehr zu euch gekommen, um euch vieles zu enthullen, was ihr von der Schrift geheim gehalten habt, und (er ist zu euch gekommen,) um gegen vieles Nachsicht zu uben. Wahrlich, zu euch sind ein Licht von Allah und ein klares Buch gekommen"
  },
  {"surah_number": 5, "verse_number": 16, "content": "Damit leitet Allah jene, die Sein Wohlgefallen suchen, auf die Wege des Friedens, und Er fuhrt sie mit Seiner Erlaubnis aus den Finsternissen zum Licht und fuhrt sie auf einen geraden Weg"},
  {
    "surah_number": 5,
    "verse_number": 17,
    "content":
        "Wahrlich, unglaubig sind diejenigen, die sagen: \"Allah ist der Messias, der Sohn der Maria.\" Sprich: \"Wer vermochte wohl etwas gegen Allah, wenn Er den Messias, den Sohn der Maria, seine Mutter und jene, die allesamt auf der Erde sind, vernichten will?\" Allahs ist das Konigreich der Himmel und der Erde und dessen, was zwischen beiden ist. Er erschafft, was Er will; und Allah hat Macht uber alle Dinge"
  },
  {
    "surah_number": 5,
    "verse_number": 18,
    "content":
        "Und es sagten die Juden und die Christen: \"Wir sind die Sohne Allahs und seine Lieblinge.\" Sprich: \"Warum bestraft Er euch dann fur eure Sunden? Nein, ihr seid Menschen von denen, die Er schuf.\" Er vergibt, wem Er will, und Er bestraft, wen Er will. Und Allahs ist das Konigreich der Himmel und der Erde und dessen, was zwischen beiden ist, und zu Ihm ist die Heimkehr"
  },
  {
    "surah_number": 5,
    "verse_number": 19,
    "content":
        "O Leute der Schrift, zu euch ist nunmehr Unser Gesandter nach einer Zeitspanne zwischen den Gesandten gekommen, um euch aufzuklaren, damit ihr nicht sagen konnt: \"Kein Bringer froher Botschaft und kein Warner ist zu uns gekommen.\" So ist nun in Wahrheit ein Bringer froher Botschaft und ein Warner zu euch gekommen. Und Allah hat Macht uber alle Dinge"
  },
  {"surah_number": 5, "verse_number": 20, "content": "Und (damals) als Moses zu seinem Volke sagte: \"O mein Volk, besinnt euch auf Allahs Huld gegen euch, als Er aus eurer Mitte Propheten erweckte und euch zu Konigen machte und euch gab, was Er keinem anderen auf der Welt gegeben hat"},
  {"surah_number": 5, "verse_number": 21, "content": "O mein Volk, betretet das heilige Land, das Allah fur euch bestimmt hat, und kehret (ihm) nicht den Rucken; denn dann werdet ihr als Verlorene umkehren"},
  {"surah_number": 5, "verse_number": 22, "content": "Sie sagten: \"O Moses, siehe, dort lebt ein tyrannisches Volk, und wir werden es (das Land) nicht betreten, ehe jene es nicht verlassen haben. Doch wenn sie es verlassen, dann wollen wir dort einziehen"},
  {
    "surah_number": 5,
    "verse_number": 23,
    "content": "Es sagten zwei Manner von denen, die gottesfurchtig waren, und denen Allah Seine Gnade erwiesen hatte: \"Zieht durch das Tor ein und wendet euch gegen sie; seid ihr eingezogen, dann werdet ihr siegreich sein. Und vertraut auf Allah, wenn ihr Glaubige seid"
  },
  {"surah_number": 5, "verse_number": 24, "content": "Sie sagten: \"O Moses, nimmermehr werden wir es betreten, solange jene dort sind. Gehe denn du mit deinem Herrn und kampft; wir bleiben hier sitzen"},
  {"surah_number": 5, "verse_number": 25, "content": "Er sagte: \"Ich habe nur Macht uber mich selbst und meinen Bruder; darum scheide Du uns von dem aufruhrerischen Volk"},
  {"surah_number": 5, "verse_number": 26, "content": "Er sprach: \"Wahrlich, es (das Land) soll ihnen vierzig Jahre lang verwehrt sein; sie sollen auf der Erde umherirren. Und betrube dich nicht wegen des aufruhrerischen Volkes"},
  {
    "surah_number": 5,
    "verse_number": 27,
    "content":
        "Und verlies ihnen in Wahrheit die Geschichte von den zwei Sohnen Adams, als sie beide ein Opfer darbrachten, und es von dem einen angenommen und von dem anderen nicht angenommen wurde. Da sagte dieser: \"Wahrhaftig, ich schlage dich tot.\" Jener erwiderte: \"Allah nimmt nur von den Gottesfurchtigen (Opfer) an"
  },
  {"surah_number": 5, "verse_number": 28, "content": "Wenn du auch deine Hand nach mir ausstreckst, um mich zu erschlagen, so werde ich doch nicht meine Hand nach dir ausstrecken, um dich zu erschlagen. Ich furchte Allah, den Herrn der Welten"},
  {"surah_number": 5, "verse_number": 29, "content": "Ich will, daß du die Last meiner Sunde und deiner Sunde tragst und so unter den Bewohnern des Feuers bist, und dies ist der Lohn der Frevler"},
  {"surah_number": 5, "verse_number": 30, "content": "Doch er erlag dem Trieb, seinen Bruder zu toten; also erschlug er ihn und wurde einer von den Verlierern"},
  {
    "surah_number": 5,
    "verse_number": 31,
    "content": "Da sandte Allah einen Raben, der auf dem Boden scharrte, um ihm zu zeigen, wie er den Leichnam seines Bruders verbergen konne. Er sagte: \"Wehe mir! Bin ich nicht einmal imstande, wie dieser Rabe zu sein und den Leichnam meines Bruders zu verbergen?\" Und da wurde er reumutig"
  },
  {
    "surah_number": 5,
    "verse_number": 32,
    "content":
        "Deshalb haben Wir den Kindern Israels verordnet, daß, wenn jemand einen Menschen totet, ohne daß dieser einen Mord begangen hatte, oder ohne daß ein Unheil im Lande geschehen ware, es so sein soll, als hatte er die ganze Menschheit getotet; und wenn jemand einem Menschen das Leben erhalt, es so sein soll, als hatte er der ganzen Menschheit das Leben erhalten. Und Unsere Gesandten kamen mit deutlichen Zeichen zu ihnen; dennoch, selbst danach begingen viele von ihnen Ausschreitungen im Land"
  },
  {
    "surah_number": 5,
    "verse_number": 33,
    "content":
        "Der Lohn derer, die gegen Allah und Seinen Gesandten Krieg fuhren und Verderben im Lande zu erregen trachten, soll sein, daß sie getotet oder gekreuzigt werden oder daß ihnen Hande und Fuße wechselweise abgeschlagen werden oder daß sie aus dem Lande vertrieben werden. Das wird fur sie eine Schmach in dieser Welt sein, und im Jenseits wird ihnen eine schwere Strafe zuteil"
  },
  {"surah_number": 5, "verse_number": 34, "content": "Ausgenommen davon sind jene, die bereuen, noch ehe ihr sie in eurer Gewalt habt. So wisset, daß Allah Allvergebend, Barmherzig ist"},
  {"surah_number": 5, "verse_number": 35, "content": "O ihr, die ihr glaubt, furchtet Allah und trachtet danach, Ihm nahezukommen und kampft auf Seinem Wege, auf daß ihr Erfolg haben moget"},
  {
    "surah_number": 5,
    "verse_number": 36,
    "content": "Wahrlich, die Unglaubigen hatten sie auch alles, was auf der Erde ist, und dann nochmal so viel, um sich damit am Tage der Auferstehung von der Strafe loszukaufen es wurde doch nicht von ihnen angenommen werden; und ihnen wird eine schmerzliche Strafe zuteil sein"
  },
  {"surah_number": 5, "verse_number": 37, "content": "Sie wollen dem Feuer entrinnen, doch sie werden nicht daraus entrinnen konnen, und ihre Pein wird immerwahrend sein"},
  {"surah_number": 5, "verse_number": 38, "content": "Dem Dieb und der Diebin schneidet ihr die Hande ab, als Vergeltung fur das, was sie begangen haben, und als abschreckende Strafe von Allah. Und Allah ist Allmachtig, Allweise"},
  {"surah_number": 5, "verse_number": 39, "content": "Aber wer es bereut nach seiner Freveltat und sich bessert, von dem wird Allah die Reue annehmen; denn Allah ist Allvergebend, Barmherzig"},
  {"surah_number": 5, "verse_number": 40, "content": "Hast du nicht gewußt, daß Allah es ist, Dem das Konigreich der Himmel und der Erde gehort? Er bestraft, wen Er will, und Er vergibt, wem Er will; und Allah hat Macht uber alle Dinge"},
  {
    "surah_number": 5,
    "verse_number": 41,
    "content":
        "O du Gesandter, es sollen dich nicht jene betruben, die hastig dem Unglauben verfallen, die mit dem Munde sagen: \"Wir glauben\", jedoch im Herzen nicht glauben. Und unter den Juden sind solche, die auf jede Luge horen; sie horen auf andere, die noch nicht zu dir gekommen sind. Sie rucken die Worte von ihren richtigen Stellen ab und sagen: \"Wenn euch dies (, was wir gutheißen wollen,) vorgebracht wird, so nehmt es an, doch wenn es euch nicht vorgebracht wird, dann seid auf der Hut!\" Und wen Allah in Versuchung fuhren will dem wirst du mit keiner Macht gegen Allah helfen konnen. Das sind die, deren Herzen Allah nicht reinigen wollte; fur sie ist in dieser Welt Schande, und im Jenseits wird ihnen eine große Strafe zuteil sein"
  },
  {
    "surah_number": 5,
    "verse_number": 42,
    "content":
        "Sie sind notorische Lauscher hinsichtlich der Falschheit, Verschlinger von Unerlaubtem. Wenn sie nun zu dir kommen, so richte zwischen ihnen oder wende dich von ihnen ab. Und wenn du dich von ihnen abwendest, so konnen sie dir keinerlei Schaden zufugen; richtest du aber, so richte zwischen ihnen in Gerechtigkeit. Wahrlich, Allah liebt die Gerechten"
  },
  {"surah_number": 5, "verse_number": 43, "content": "Wie aber wollen sie dich zum Richter berufen, wahrend sie doch die Thora in ihrem Besitz haben, worin Allahs Richtspruch ist? Hierauf und trotz alledem, kehren sie (Ihm) den Rucken; und sie sind nicht als Glaubige zu bezeichnen"},
  {
    "surah_number": 5,
    "verse_number": 44,
    "content":
        "Wahrlich, Wir hatten die Thora, in der Fuhrung und Licht war, hinabgesandt. Damit haben die Propheten, die sich (Allah) hingaben, den Juden Recht gesprochen, und so auch die Rabbiner und die Gelehrten; denn ihnen wurde aufgetragen, das Buch Allahs zu bewahren, und sie waren seine Huter. Darum furchtet nicht die Menschen, sondern furchtet Mich; und gebt nicht Meine Zeichen um einen geringen Preis hin. Und wer nicht nach dem richtet, was Allah hinabgesandt hat das sind die Unglaubigen"
  },
  {
    "surah_number": 5,
    "verse_number": 45,
    "content":
        "Wir hatten ihnen darin vorgeschrieben: Leben um Leben, Auge um Auge, Nase um Nase, Ohr um Ohr und Zahn um Zahn; und fur Verwundungen gerechte Vergeltung. Wer aber darauf verzichtet, dem soll das eine Suhne sein; und wer nicht nach dem richtet, was Allah hinabgesandt hat das sind die Ungerechten"
  },
  {
    "surah_number": 5,
    "verse_number": 46,
    "content":
        "Wir ließen ihnen Jesus, den Sohn der Maria, folgen; zur Bestatigung dessen, was vor ihm in der Thora war; und Wir gaben ihm das Evangelium, worin Rechtleitung und Licht war, zur Bestatigung dessen, was vor ihm in der Thora war und als Rechtleitung und Ermahnung fur die Gottesfurchtigen"
  },
  {"surah_number": 5, "verse_number": 47, "content": "Und die Leute des Evangeliums sollen sich nach dem richten, was Allah darin offenbart hat; und die sich nicht nach dem richten, was Allah herabgesandt hat das sind die (wahren) Frevler"},
  {
    "surah_number": 5,
    "verse_number": 48,
    "content":
        "Und Wir haben das Buch mit der Wahrheit zu dir herabgesandt, das bestatigt, was von der Schrift vor ihm da war und daruber Gewißheit gibt; richte also zwischen ihnen nach dem, was Allah herabgesandt hat und folge nicht ihren Neigungen, von der Wahrheit abzuweichen, die zu dir gekommen ist. Fur jeden von euch haben Wir Richtlinien und eine Laufbahn bestimmt. Und wenn Allah gewollt hatte, hatte Er euch zu einer einzigen Gemeinde gemacht. Er wollte euch aber in alledem, was Er euch gegeben hat, auf die Probe stellen. Darum sollt ihr um die guten Dinge wetteifern. Zu Allah werdet ihr allesamt zuruckkehren; und dann wird Er euch das kundtun, woruber ihr uneins waret"
  },
  {
    "surah_number": 5,
    "verse_number": 49,
    "content":
        "Und du sollst zwischen ihnen nach dem richten, was von Allah herabgesandt wurde; und folge nicht ihren Neigungen, und sei vor ihnen auf der Hut, damit sie dich nicht bedrangen und von einem Teil dessen, was Allah zu dir herabgesandt hat, wegtreiben. Wenden sie sich jedoch (von dir) ab, so wisse, daß Allah sie fur etliche ihrer Sunden zu treffen gedenkt. Wahrlich, viele der Menschen sind Frevler"
  },
  {"surah_number": 5, "verse_number": 50, "content": "Wunschen sie etwa die Richtlinien der Gahiliya? Und wer ist ein besserer Richter als Allah fur ein Volk, das fest im Glauben ist"},
  {"surah_number": 5, "verse_number": 51, "content": "O ihr, die ihr glaubt! Nehmt nicht die Juden und die Christen zu Beschutzern. Sie sind einander Beschutzer. Und wer sie von euch zu Beschutzern nimmt, der gehort wahrlich zu ihnen. Wahrlich, Allah weist nicht dem Volk der Ungerechten den Weg"},
  {
    "surah_number": 5,
    "verse_number": 52,
    "content": "Und du wirst jene sehen, deren Herzen von Krankheit befallen sind, (und) zu ihnen hineilen; sie sagen: \"Wir furchten, es konnte uns ein Ungluck befallen.\" Moge Allah den Sieg oder sonst ein Ereignis herbeifuhren. Dann werden sie bereuen, was sie in ihren Herzen verbargen"
  },
  {"surah_number": 5, "verse_number": 53, "content": "Und die Glaubigen werden sagen: \"Sind das etwa jene, die mit ihren feierlichsten Eiden bei Allah schworen, daß sie unverbruchlich zu euch stunden?\" Eitel sind ihre Werke, und sie sind zu Verlierern geworden"},
  {
    "surah_number": 5,
    "verse_number": 54,
    "content":
        "O ihr, die ihr glaubt, wer sich von euch von seinem Glauben abkehrt, wisset, Allah wird bald ein anderes Volk bringen, das Er liebt und das Ihn liebt, (das) demutig gegen die Glaubigen und hart gegen die Unglaubigen (ist); sie werden auf Allahs Weg kampfen und werden den Vorwurf des Tadelnden nicht furchten. Das ist Allahs Huld; Er gewahrt sie, wem Er will; denn Allah ist Allumfassend, Allwissend"
  },
  {"surah_number": 5, "verse_number": 55, "content": "Eure Beschutzer sind wahrlich Allah und Sein Gesandter und die Glaubigen, die das Gebet verrichten, die Zakah entrichten und sich (vor Allah) verneigen"},
  {"surah_number": 5, "verse_number": 56, "content": "Und wer Allah und Seinen Gesandten und die Glaubigen zu Beschutzern nimmt, der wisse, daß Allahs Schar ist es, die siegreich sein wird"},
  {"surah_number": 5, "verse_number": 57, "content": "O ihr, die ihr glaubt, nehmt euch nicht die zu Beschutzern von jenen, denen vor euch die Schrift gegeben wurde, und von den Unglaubigen, die mit eurem Glauben Spott und Scherz treiben und furchtet Allah, wenn ihr Glaubige seid"},
  {"surah_number": 5, "verse_number": 58, "content": "Und wenn ihr zum Gebet ruft, treiben sie damit ihren Spott und Scherz. Dies (ist so), weil sie Leute sind, die es nicht begreifen"},
  {"surah_number": 5, "verse_number": 59, "content": "Sprich: \"O Leute der Schrift, ihr grollt uns nur deswegen, weil wir an Allah und an das glauben, was zu uns herabgesandt und was schon vorher herabgesandt wurde, und weil die meisten von euch Frevler sind"},
  {
    "surah_number": 5,
    "verse_number": 60,
    "content":
        "Sprich: \"Soll ich euch uber die belehren, deren Lohn bei Allah noch schlimmer ist als das? Es sind, die Allah verflucht hat und denen Er zurnt und aus denen Er Affen, Schweine und Gotzendiener gemacht hat. Diese befinden sich in einer noch schlimmeren Lage und sind noch weiter vom rechten Weg abgeirrt"
  },
  {"surah_number": 5, "verse_number": 61, "content": "Und wenn sie zu euch kommen, sagen sie: \"Wir glauben\", wahrend sie doch mit Unglauben eintreten und in diesem fortgehen; und Allah weiß am besten, was sie verborgen halten"},
  {"surah_number": 5, "verse_number": 62, "content": "Und du siehst, wie viele von ihnen in Sunde und Ubertretung und im Verzehr verbotener Dinge wetteifern. Ubel ist wahrlich, was sie getan haben"},
  {"surah_number": 5, "verse_number": 63, "content": "Warum untersagen ihnen die Rabbiner und die Schriftgelehrten nicht ihre sundige Rede und ihren Verzehr von Verbotenem? Ubel ist wahrlich, was sie getrieben haben"},
  {
    "surah_number": 5,
    "verse_number": 64,
    "content":
        "Und die Juden sagen: \"Die Hand Allahs ist gefesselt.\" Ihre Hande sollen gefesselt sein, und sie sollen verflucht sein um dessentwillen, was sie da sprechen. Nein, Seine Hande sind weit offen; Er spendet, wie Er will. Und was auf dich von Deinem Herrn herabgesandt wurde, wird gewiß viele von ihnen in ihrem Aufruhr und Unglauben noch bestarken. Und Wir haben unter ihnen Haß und Zwietracht bis zum Tage der Auferstehung gesat. Sooft sie ein Feuer fur den Krieg anzundeten, loschte Allah es aus, und sie trachteten nur nach Unheil auf Erden; und Allah liebt nicht die Unheilstifter"
  },
  {"surah_number": 5, "verse_number": 65, "content": "Wenn die Leute der Schrift geglaubt hatten und gottesfurchtig gewesen waren, hatten Wir gewiß ihre Ubel von ihnen hinweggenommen und Wir hatten sie gewiß in die Garten der Wonne gefuhrt"},
  {
    "surah_number": 5,
    "verse_number": 66,
    "content": "Und hatten sie die Thora befolgt und das Evangelium und das, was zu ihnen von ihrem Herrn hinabgesandt wurde, wurden sie sicherlich uber sich und zu ihren Fußen Nahrung finden. Es gibt unter ihnen Leute, die Maßigung uben; doch viele von ihnen verrichten uble Dinge"
  },
  {
    "surah_number": 5,
    "verse_number": 67,
    "content": "O du Gesandter! Verkunde, was zu dir von deinem Herrn hinabgesandt wurde; und wenn du es nicht tust, so hast du Seine Botschaft nicht verkundigt. Und Allah wird dich vor den Menschen schutzen. Wahrlich, Allah weist den unglaubigen Leuten nicht den Weg"
  },
  {
    "surah_number": 5,
    "verse_number": 68,
    "content":
        "Sprich: \"O Leute der Schrift, ihr fußt auf nichts, ehe ihr nicht die Thora und das Evangelium und das in die Tat umsetzt, was von eurem Herrn zu euch herabgesandt wurde.\" Aber gewiß, das, was von deinem Herrn zu dir hinabgesandt wurde, wird viele von ihnen in ihrem Aufruhr und Unglauben bestarken. Darum betrube dich nicht uber die unglaubigen Leute"
  },
  {"surah_number": 5, "verse_number": 69, "content": "Jene, die geglaubt haben, und die Juden und die Sabaer und die Christen wer an Allah glaubt und gute Werke tut, keine Furcht soll uber sie kommen, noch sollen sie traurig sein"},
  {
    "surah_number": 5,
    "verse_number": 70,
    "content": "Wahrlich, Wir hatten einen Bund mit den Kindern Israels geschlossen und Gesandte zu ihnen geschickt. Sooft aber Gesandte zu ihnen mit etwas kamen, was ihre Herzen nicht begehrten, erklarten sie einige von ihnen fur Lugner und brachten andere um"
  },
  {"surah_number": 5, "verse_number": 71, "content": "Und sie dachten, dies wurde keine Verwirrung zur Folge haben; so wurden sie blind und taub. Dann wandte Sich Allah ihnen gnadig wieder zu; trotzdem wurden viele von ihnen abermals blind und taub; und Allah sieht wohl, was sie tun"},
  {
    "surah_number": 5,
    "verse_number": 72,
    "content":
        "Wahrlich, unglaubig sind diejenigen, die sagen: \"Allah ist der Messias, der Sohn der Maria\", wahrend der Messias doch selbst gesagt hat: \"O ihr Kinder Israels, betet zu Allah, meinem Herrn und eurem Herrn.\" Wer Allah Gotter zur Seite stellt, dem hat Allah das Paradies verwehrt, und das Feuer wird seine Herberge sein. Und die Frevler sollen keine Helfer finden"
  },
  {
    "surah_number": 5,
    "verse_number": 73,
    "content":
        "Wahrlich, unglaubig sind diejenigen, die sagen: \"Allah ist der Dritte von dreien\"; und es ist kein Gott da außer einem Einzigen Gott. Und wenn sie nicht von dem, was sie sagen, Abstand nehmen, wahrlich, so wird diejenigen unter ihnen, die unglaubig bleiben, eine schmerzliche Strafe ereilen"
  },
  {"surah_number": 5, "verse_number": 74, "content": "Wollen sie sich denn nicht reumutig Allah wieder zuwenden und Ihn um Verzeihung bitten? Und Allah ist Allverzeihend, Barmherzig"},
  {
    "surah_number": 5,
    "verse_number": 75,
    "content": "Der Messias, der Sohn der Maria, war nur ein Gesandter; gewiß, andere Gesandte sind vor ihm dahingegangen. Und seine Mutter war eine Wahrhaftige; beide pflegten, Speise zu sich zu nehmen. Siehe, wie Wir die Zeichen fur sie erklaren, und siehe, wie sie sich abwenden"
  },
  {"surah_number": 5, "verse_number": 76, "content": "Sprich: \"Wollt ihr statt Allah das anbeten, was nicht die Macht hat, euch zu schaden oder zu nutzen?\" Und Allah allein ist der Allhorende, der Allwissende"},
  {"surah_number": 5, "verse_number": 77, "content": "Sprich: \"O Leute der Schrift, ubertreibt nicht zu Unrecht in eurem Glauben und folgt nicht den bosen Neigungen von Leuten, die schon vordem irregingen und viele irregefuhrt haben und weit vom rechten Weg abgeirrt sind"},
  {"surah_number": 5, "verse_number": 78, "content": "Verflucht wurden diejenigen Unglaubigen von den Kindern Israels durch die Zunge Davids und Jesus', des Sohnes der Maria. Dies, weil sie ungehorsam waren und (gegen die Gebote) verstießen"},
  {"surah_number": 5, "verse_number": 79, "content": "Sie hinderten einander nicht an den Missetaten, die sie begingen. Ubel wahrlich war das, was sie zu tun pflegten"},
  {"surah_number": 5, "verse_number": 80, "content": "Du siehst, wie sich viele von ihnen die Unglaubigen zu Beschutzern nehmen. Wahrlich, ubel ist das, was sie selbst fur sich vorausgeschickt haben, so daß Allah ihnen zurnt; und in der Strafe werden sie auf ewig bleiben"},
  {"surah_number": 5, "verse_number": 81, "content": "Und hatten sie an Allah geglaubt und an den Propheten und an das, was zu ihm herabgesandt wurde, hatten sie sich jene nicht zu Beschutzern genommen; aber viele von ihnen sind Frevler"},
  {
    "surah_number": 5,
    "verse_number": 82,
    "content":
        "Sicherlich findest du, daß unter allen Menschen die Juden und die Gotzendiener die erbittertsten Gegner der Glaubigen sind. Und du wirst zweifellos finden, daß die, welche sagen: \"Wir sind Christen\" den Glaubigen am freundlichsten gegenuberstehen. Dies (ist so), weil es unter ihnen Priester und Monche gibt und weil sie nicht hochmutig sind"
  },
  {"surah_number": 5, "verse_number": 83, "content": "Und wenn sie horen, was zu dem Gesandten herabgesandt worden ist, siehst du ihre Augen von Tranen uberfließen ob der Wahrheit, die sie erkannt haben. Sie sagen: \"Unser Herr, wir glauben, so schreibe uns unter die Bezeugenden"},
  {"surah_number": 5, "verse_number": 84, "content": "Und weshalb sollten wir nicht an Allah glauben und an die Wahrheit, die zu uns gekommen ist, wo wir innig wunschen, daß unser Herr uns zu den Rechtschaffenen zahlen moge"},
  {"surah_number": 5, "verse_number": 85, "content": "Und um dessentwillen, was sie da gesagt haben, wird Allah sie mit Garten belohnen, durch die Bache fließen. Darin sollen sie ewig verweilen; und das ist der Lohn derer, die Gutes tun"},
  {"surah_number": 5, "verse_number": 86, "content": "Die aber, die nicht glauben und Unsere Zeichen verwerfen, das sind die Insassen der Gahim"},
  {"surah_number": 5, "verse_number": 87, "content": "O ihr, die ihr glaubt, erklart die guten Dinge, die Allah euch erlaubt hat, nicht fur verboten; doch ubertretet auch nicht. Denn Allah liebt die Ubertreter nicht"},
  {"surah_number": 5, "verse_number": 88, "content": "Und esset von dem, was Allah euch gegeben hat: Erlaubtes, Gutes. Und furchtet Allah, an Den ihr glaubt"},
  {
    "surah_number": 5,
    "verse_number": 89,
    "content":
        "Allah wird euch fur ein unbedachtes Wort in euren Eiden nicht zur Rechenschaft ziehen, doch Er wird von euch fur das Rechenschaft fordern, was ihr mit Bedacht geschworen habt. Die Suhne dafur sei dann die Speisung von zehn Armen in jenem Maß, wie ihr die Eurigen im Durchschnitt speist, oder ihre Bekleidung oder die Befreiung eines Sklaven. Wer es aber nicht kann, dann (soll er) drei Tage fasten. Das ist die Suhne fur eure Eide, wenn ihr sie geleistet habt. Und hutet ja eure Eide. So macht euch Allah Seine Zeichen klar, auf daß ihr dankbar sein moget"
  },
  {"surah_number": 5, "verse_number": 90, "content": "O ihr, die ihr glaubt! Berauschendes, Gluckspiel, Opfersteine und Lospfeile sind ein Greuel, das Werk des Satans. So meidet sie, auf daß ihr erfolgreich seid"},
  {"surah_number": 5, "verse_number": 91, "content": "Satan will durch das Berauschende und das Losspiel nur Feindschaft und Haß zwischen euch auslosen, um euch vom Gedenken an Allah und vom Gebet abzuhalten. Werdet ihr euch denn abhalten lassen"},
  {"surah_number": 5, "verse_number": 92, "content": "Und gehorcht Allah und gehorcht dem Gesandten und seid auf der Hut. Kehrt ihr euch jedoch von ihm ab, dann wisset, daß Unserem Gesandten nur die deutliche Verkundigung obliegt"},
  {
    "surah_number": 5,
    "verse_number": 93,
    "content": "Denen, die glauben und gute Werke tun, soll als Sunde nicht angerechnet werden, was sie essen, wenn sie gottesfurchtig sind und glauben und gute Werke tun, und abermals furchten und glauben, dann nochmals furchten und Gutes tun. Und Allah liebt jene, die Gutes tun"
  },
  {
    "surah_number": 5,
    "verse_number": 94,
    "content": "O ihr, die ihr glaubt! Allah will euch gewiß Prufungen aussetzen mit dem Wild, das eure Hande und eure Speere erreichen konnen, so daß Allah die auszeichnen moge, die Ihn im geheimen furchten. Wer sich darum noch nach diesem vergeht, dem wird eine schmerzliche Strafe zuteil sein"
  },
  {
    "surah_number": 5,
    "verse_number": 95,
    "content":
        "O ihr, die ihr glaubt! Totet kein Wild, wahrend ihr pilgert. Und (wenn) einer von euch ein Tier vorsatzlich totet, so ist die Ersatzleistung (dafur) ein gleiches Maß vom Vieh, wie das, was er getotet (hat), nach dem Spruch von zwei Redlichen unter euch, und das soll dann als Opfertier zu der Ka`ba gebracht werden; oder die Suhne sei die Speisung von Armen oder dementsprechendes Fasten, damit er die bosen Folgen seiner Tat koste. Allah vergibt das Vergangene; den aber, der es wieder tut, wird Allah der Vergeltung aussetzen. Und Allah ist Allmachtig und Herr der Vergeltung"
  },
  {"surah_number": 5, "verse_number": 96, "content": "Der Fang aus dem Meer und sein Genuß sind euch als Versorgung fur euch und fur die Reisenden erlaubt, doch verwehrt ist (euch) das Wild des Landes, solange ihr pilgert. Und furchtet Allah, vor Dem ihr versammelt werdet"},
  {
    "surah_number": 5,
    "verse_number": 97,
    "content":
        "Allah hat die Kaba, das unverletzliche Haus, zu einer Gebetsstatte fur die Menschen gemacht, ebenso den heiligen Monat und die Opfertiere und die Tiere mit dem Halsschmuck. Dies, geschah damit ihr wisset, daß Allah weiß, was in den Himmeln und was auf Erden ist, und daß Allah alle Dinge weiß"
  },
  {"surah_number": 5, "verse_number": 98, "content": "Wisset, daß Allah streng im Strafen ist und daß Allah Allverzeihend, Barmherzig ist"},
  {"surah_number": 5, "verse_number": 99, "content": "Dem Gesandten obliegt nur die Verkundigung. Und Allah weiß, was ihr kundtut und was ihr verborgen haltet"},
  {"surah_number": 5, "verse_number": 100, "content": "Sprich: \"Das Schlechte und das Gute sind nicht gleich\" obgleich dich auch die Menge des Schlechten in Erstaunen versetzen mag. Darum furchtet Allah, ihr Verstandigen, auf daß ihr erfolgreich sein moget"},
  {
    "surah_number": 5,
    "verse_number": 101,
    "content": "O ihr, die ihr glaubt! Fragt nicht nach Dingen, die, wenn sie euch enthullt wurden, euch unangenehm waren; und wenn ihr danach zur Zeit fragt, da der Quran niedergesandt wird, werden sie euch doch klar. Allah hat euch davon entbunden; und Allah ist Allverzeihend, Nachsichtig"
  },
  {"surah_number": 5, "verse_number": 102, "content": "Es haben schon vor euch Leute nach solchem gefragt, doch dann versagten sie ihm den Glauben"},
  {"surah_number": 5, "verse_number": 103, "content": "Allah hat keinerlei \"Bahira \" oder \"Saiba \" oder \"Wasila \" oder \"Ham \" geboten: vielmehr ersinnen die Unglaubigen eine Luge gegen Allah, und die meisten von ihnen begreifen es nicht"},
  {
    "surah_number": 5,
    "verse_number": 104,
    "content": "Und wenn ihnen gesagt wird: \"Kommt her zu dem, was von Allah herabgesandt wurde, und kommt zu dem Gesandten\", sagen sie: \"Uns genugt das, wobei wir unsere Vater vorfanden.\" Und selbst (dann,) wenn ihre Vater kein Wissen hatten und nicht auf dem rechten Wege waren"
  },
  {"surah_number": 5, "verse_number": 105, "content": "O ihr, die ihr glaubt! Wacht uber euch selbst. Wer irregeht, kann euch nicht schaden, wenn ihr nur selbst auf dem rechten Wege seid. Zu Allah ist euer aller Heimkehr; dann wird Er euch enthullen, was ihr zu tun pflegtet"},
  {
    "surah_number": 5,
    "verse_number": 106,
    "content":
        "O ihr, die ihr glaubt! Wenn der Tod an einen von euch herantritt, liegt die Zeugenschaft zum Zeitpunkt der Testamentseroffnung bei euch: (bei) zwei Redlichen unter euch, oder zwei anderen, die nicht zu euch gehoren, wenn ihr gerade im Land herumreist und euch das Ungluck des Todes trifft. Ihr sollt sie beide nach dem Gebet zuruckhalten; und wenn ihr zweifelt, so sollen sie beide bei Allah schworen: \"Wir erstehen damit keinen Gewinn, handelte es sich auch um einen nahen Verwandten, und wir verhehlen das Zeugnis Allahs nicht; wahrlich, wir waren sonst Sunder"
  },
  {
    "surah_number": 5,
    "verse_number": 107,
    "content":
        "Wenn es aber bekannt wird, daß die beiden (Zeugen) sich der Sunde schuldig gemacht haben, dann sollen an ihre Stelle zwei andere aus der Zahl derer treten, gegen welche die beiden ausgesagt haben, und die beiden (spateren Zeugen) sollen bei Allah schworen: \"Wahrlich, unser Zeugnis ist wahrhaftiger als das Zeugnis der beiden (fruheren), wahrlich, wir gehorten sonst zu den Ungerechten"
  },
  {"surah_number": 5, "verse_number": 108, "content": "So geschieht es eher, daß sie ein wahres Zeugnis ablegen oder daß sie furchten, es konnten andere Eide nach ihrem Eide gefordert werden. Und furchtet Allah und hort! Denn Allah weist nicht den ungehorsamen Leuten den Weg"},
  {"surah_number": 5, "verse_number": 109, "content": "Am Tage, an dem Allah die Gesandten versammelt und spricht: \"Welche Antwort empfingt ihr?\" sagen sie: \"Wir haben kein Wissen, Du allein bist der Allwissende des Verborgenen"},
  {
    "surah_number": 5,
    "verse_number": 110,
    "content":
        "Wenn Allah sagen wird: \"O Jesus, Sohn der Maria, gedenke Meiner Gnade gegen dich und gegen deine Mutter; wie Ich dich starkte mit der heiligen Eingebung du sprachst zu den Menschen sowohl in der Wiege als auch im Mannesalter; und wie Ich dich die Schrift und die Weisheit lehrte und die Thora und das Evangelium; und wie du mit Meiner Erlaubnis aus Ton bildetest, was wie Vogel aussah, du hauchtest ihm dann (Atem) ein, und es wurde mit Meiner Erlaubnis zu (wirklichen) Vogeln; und wie du mit Meiner Erlaubnis die Blinden und die Aussatzigen heiltest; und wie du mit Meiner Erlaubnis die Toten erwecktest; und wie Ich die Kinder Israels von dir abhielt als du zu ihnen mit deutlichen Zeichen kamst und die Unglaubigen unter ihnen aber sagten: \"Das ist nichts als offenkundige Zauberei"
  },
  {"surah_number": 5, "verse_number": 111, "content": "Und als Ich den Jungern eingab, an Mich und an Meinen Gesandten zu glauben, da sagten sie: \"Wir glauben, und sei Zeuge, daß wir (Dir) ergeben sind"},
  {"surah_number": 5, "verse_number": 112, "content": "Als die Junger sagten: \"O Jesus Sohn der Maria, ist dein Herr imstande, uns einen Tisch (mit Speisen) vom Himmel herabzusenden?\" sagte er: \"Furchtet Allah, wenn ihr Glaubige seid"},
  {"surah_number": 5, "verse_number": 113, "content": "Sie sagten: \"Wir wollen davon essen, und unsere Herzen sollen beruhigt sein, und wir wollen wissen, daß du in Wahrheit zu uns gesprochen hast, und wollen selbst dafur Zeugnis ablegen"},
  {
    "surah_number": 5,
    "verse_number": 114,
    "content": "Da sagte Jesus, der Sohn der Maria: \"O Allah, unser Herr, sende uns einen Tisch (mit Speise) vom Himmel herab, daß er ein Fest fur uns sei, fur den Ersten von uns und fur den Letzten von uns, und ein Zeichen von Dir; und versorge uns; denn Du bist der beste Versorger"
  },
  {"surah_number": 5, "verse_number": 115, "content": "Allah sprach: \"Siehe, Ich will ihn (den Tisch) zu euch niedersenden; wer von euch aber danach unglaubig wird, uber den werde Ich eine Strafe verhangen, mit welcher Ich keinen anderen auf der Welt bestrafen werde"},
  {
    "surah_number": 5,
    "verse_number": 116,
    "content":
        "Und wenn Allah sprechen wird: \"O Jesus, Sohn der Maria, hast du zu den Menschen gesagt: \"Nehmt mich und meine Mutter als zwei Gotter neben Allah?\" wird er antworten: \"Gepriesen seist Du. Nie konnte ich das sagen, wozu ich kein Recht hatte. Hatte ich es gesagt, wurdest Du es sicherlich wissen. Du weißt, was in meiner Seele ist, aber ich weiß nicht, was Du in Dir hegst. Du allein bist der Allwissende des Verborgenen"
  },
  {
    "surah_number": 5,
    "verse_number": 117,
    "content": "Nichts anderes sagte ich zu ihnen, als das, was Du mich geheißen hattest: \"Betet Allah an, meinen Herrn und euren Herrn.\" Und ich war ihr Zeuge, solange ich unter ihnen weilte, doch nachdem Du mich abberufen hattest, bist Du ihr Wachter gewesen; und Du bist der Zeuge aller Dinge"
  },
  {"surah_number": 5, "verse_number": 118, "content": "Wenn Du sie bestrafst, sind sie Deine Diener, und wenn Du ihnen verzeihst, bist Du wahrlich der Allmachtige, der Allweise"},
  {
    "surah_number": 5,
    "verse_number": 119,
    "content":
        "Allah wird sprechen: \"Das ist ein Tag, an dem den Wahrhaftigen ihre Wahrhaftigkeit nutzen soll. Fur sie gibt es Garten, durch welche Bache fließen; darin sollen sie verweilen auf ewig und immerdar. Allah hat an ihnen Wohlgefallen, und sie haben Wohlgefallen an Ihm; das ist die große Gluckseligkeit"
  },
  {"surah_number": 5, "verse_number": 120, "content": "Allahs ist das Konigreich der Himmel und der Erde und dessen, was zwischen ihnen ist; und Er hat Macht uber alle Dinge"},
  {"surah_number": 6, "verse_number": 1, "content": "Alles Lob gebuhrt Allah, Der Himmel und Erde erschaffen und die Finsternisse und das Licht gemacht hat; doch setzen jene, die da unglaubig sind, ihrem Herrn anderes gleich"},
  {"surah_number": 6, "verse_number": 2, "content": "Er ist es, Der euch aus Lehm erschaffen hat, und dann bestimmte Er (euch) eine (Lebens-) Frist. Und eine weitere Frist ist Ihm bekannt. Ihr aber zweifelt noch"},
  {"surah_number": 6, "verse_number": 3, "content": "Und Er ist Allah, (der Gott) in den Himmeln wie auch auf der Erde. Er kennt euer Verborgenes und euer Verlautbartes, und Er weiß, was ihr begeht"},
  {"surah_number": 6, "verse_number": 4, "content": "Es kommt zu ihnen auch nicht ein Zeichen von den Zeichen ihres Herrn, ohne daß sie sich davon abwenden"},
  {"surah_number": 6, "verse_number": 5, "content": "So haben sie die Wahrheit fur Luge erklart, als sie zu ihnen kam; bald aber soll ihnen von dem Kunde gegeben werden, was sie verspotteten"},
  {
    "surah_number": 6,
    "verse_number": 6,
    "content":
        "Sehen sie denn nicht, wie so manches Geschlecht Wir schon vor ihnen vernichtet haben? Diesen hatten Wir auf der Erde Macht gegeben, wie Wir sie euch nicht gegeben haben; und ihnen sandten Wir vom Himmel reichlich Regen; und unter ihnen ließen wir Bache fließen; dann aber tilgten Wir sie um ihrer Sunden willen aus und erweckten nach ihnen ein anderes Geschlecht"
  },
  {"surah_number": 6, "verse_number": 7, "content": "Wenn Wir dir auch eine Schrift auf einem Blatt Papier hinabgesandt hatten, welche sie mit Handen angefaßt hatten, die Unglaubigen hatten selbst dann gesagt: \"Das ist nichts als offenkundige Zauberei"},
  {"surah_number": 6, "verse_number": 8, "content": "Und sie sagen: \"Ware ein Engel zu ihm herabgesandt worden!\" Hatten Wir aber einen Engel herabgesandt, ware die Sache entschieden gewesen; dann hatten sie keinen Aufschub erlangt"},
  {"surah_number": 6, "verse_number": 9, "content": "Und wenn Wir ihn zu einem Engel gemacht hatten, hatten Wir ihn doch als Menschen erscheinen lassen, und so hatten Wir ihnen das noch mehr verwirrt, was sie selbst schon verwirrt"},
  {"surah_number": 6, "verse_number": 10, "content": "Schon vor dir wurden Gesandte verspottet, doch das, woruber sie spotteten, erfaßte die Spotter unter ihnen"},
  {"surah_number": 6, "verse_number": 11, "content": "Sprich: \"Wandert im Lande umher und seht, wie das Ende der Verleugner war"},
  {
    "surah_number": 6,
    "verse_number": 12,
    "content":
        "Sprich: \"Wem gehort das, was in den Himmeln und was auf Erden ist?\" Sprich: \"Allah.\" Er hat Sich Selbst Barmherzigkeit vorgeschrieben. Er wird euch gewiß weiter versammeln bis zum Tage der Auferstehung. Daruber besteht kein Zweifel. Jene aber, die ihrer selbst verlustig gegangen sind, glauben es nicht"
  },
  {"surah_number": 6, "verse_number": 13, "content": "Ihm gehort das, was in der Nacht und am Tage ruht. Und Er ist der Allhorende, Allwissende"},
  {
    "surah_number": 6,
    "verse_number": 14,
    "content": "Sprich: \"Sollte ich einen anderen zum Beschutzer nehmen als Allah, den Schopfer der Himmel und der Erde, Der Nahrung gibt und Selbst keine Nahrung nimmt?\" Sprich: \"Mir wurde geboten, daß ich der Erste sei, der sich ergebe.\" Und sei nicht einer der Gotzendiener"
  },
  {"surah_number": 6, "verse_number": 15, "content": "Sprich: \"Ich furchte die Strafe eines gewaltigen Tages, sollte ich meinem Herrn ungehorsam sein"},
  {"surah_number": 6, "verse_number": 16, "content": "Wer an jenem Tage davor bewahrt bleibt, dem hat Er Barmherzigkeit erwiesen. Das ist ein offenbarer Erfolg"},
  {"surah_number": 6, "verse_number": 17, "content": "Und wenn Allah dir Schaden zufugt, so kann ihn keiner als Er hinwegnehmen; und wenn Er dir Gutes beschert, so hat er die Macht, alles zu tun, was Er will"},
  {"surah_number": 6, "verse_number": 18, "content": "Er ist Der, Der uber Seine Diener Macht ausubt, und Er ist Allweise, Der wohl unterrichtet ist"},
  {
    "surah_number": 6,
    "verse_number": 19,
    "content":
        "Sprich: \"Was fur ein Zeugnis wiegt schwerer?\" Sprich: \"Allah ist mein und euer Zeuge. Und dieser Quran ist mir offenbart worden, auf daß ich euch damit warne und jeden, den er erreicht. Wolltet ihr wirklich bezeugen, daß es neben Allah andere Gotter gebe?\" Sprich: \"Ich bezeuge es nicht.\" Sprich: \"Er ist der Einzige Gott, und ich bin wahrlich fern von dem, was ihr anbetet"
  },
  {"surah_number": 6, "verse_number": 20, "content": "Sie, denen wir das Buch gaben, erkennen es, wie sie ihre Sohne erkennen. Jene aber, die ihrer selbst verlustig gegangen sind, glauben es nicht"},
  {"surah_number": 6, "verse_number": 21, "content": "Und wer ist ungerechter als der, der eine Luge gegen Allah ersinnt oder Seine Verse fur Luge erklart? Wahrlich, die Ungerechten erlangen keinen Erfolg"},
  {"surah_number": 6, "verse_number": 22, "content": "Und am Tage, an dem Wir sie alle versammeln werden, werden Wir zu denen, die Gotzen anbeten, sprechen: \"Wo sind nun eure Gotter, die ihr wahntet"},
  {"surah_number": 6, "verse_number": 23, "content": "Dann werden sie keine andere Ausrede haben als zu sagen: \"Bei Allah unserem Herrn, wir waren keine Gotzendiener"},
  {"surah_number": 6, "verse_number": 24, "content": "Schau wie sie sich selbst belugen und das, was sie sich ausdachten, sie im Stich laßt"},
  {
    "surah_number": 6,
    "verse_number": 25,
    "content":
        "Und unter ihnen sind manche, die dir Gehor schenken, doch Wir haben auf ihre Herzen Hullen und in ihre Ohren Taubheit gelegt, damit sie nicht begreifen. Selbst wenn sie jedes Zeichen sahen, wurden sie nicht daran glauben, so daß sie mit dir stritten, wenn sie zu dir kamen. Die Unglaubigen sagen: \"Das sind bloß Fabeln der Fruheren"
  },
  {"surah_number": 6, "verse_number": 26, "content": "Und sie hindern sich daran und halten sich selbst davon fern. Aber sie sturzen sich selbst ins Verderben; allein, sie begreifen es nicht"},
  {"surah_number": 6, "verse_number": 27, "content": "Und wenn du nur sehen konntest, wie sie vor das Feuer gestellt werden! Dann werden sie sagen: \"Ach, wurden wir doch zuruckgebracht! Wir wurden dann die Zeichen unseres Herrn nicht fur Luge erklaren, und wir wurden zu den Glaubigen zahlen"},
  {"surah_number": 6, "verse_number": 28, "content": "Nein, das, was sie ehemals zu verhehlen pflegten, ist ihnen nun klar geworden. Doch wenn sie auch zuruckgebracht wurden, kehrten sie ganz gewiß bald zu dem ihnen Verbotenen zuruck. Und sie sind gewiß Lugner"},
  {"surah_number": 6, "verse_number": 29, "content": "Und sie sagen: \"Es gibt kein anderes als unser irdisches Leben, und wir werden nicht wiedererweckt werden"},
  {
    "surah_number": 6,
    "verse_number": 30,
    "content": "Aber wenn du nur sehen konntest, wie sie vor ihren Herrn gestellt werden! Er wird sprechen: \"Ist dies nicht die Wahrheit?\" Sie werden antworten: \"Ja, bei unserem Herrn.\" Er wird sprechen: \"Dann kostet die Strafe dafur, daß ihr unglaubig waret"
  },
  {
    "surah_number": 6,
    "verse_number": 31,
    "content":
        "Wahrlich, die Verlierer sind diejenigen, die die Begegnung mit Allah leugnen. Wenn aber unversehens die Stunde uber sie kommt, werden sie sagen: \"O wehe uns, daß wir sie vernachlassigt haben!\" Und sie werden ihre Last auf dem Rucken tragen. Wahrlich, schlimm ist das, was sie tragen werden"
  },
  {"surah_number": 6, "verse_number": 32, "content": "Das Leben in dieser Welt ist wahrlich nichts als ein Spiel und Vergnugen. Und wahrlich, die Wohnstatte des Jenseits ist fur jene besser, die gottesfurchtig sind. Wollt ihr (das) denn nicht begreifen"},
  {"surah_number": 6, "verse_number": 33, "content": "Wir wissen wohl, daß dich das betrubt, was sie sagen; denn wahrlich, nicht dich bezichtigen sie der Luge, sondern es sind die Zeichen Allahs, welche die Ungerechten verwerfen"},
  {
    "surah_number": 6,
    "verse_number": 34,
    "content": "Es sind wohl vor dir Gesandte als lugenhaft gescholten worden; doch obgleich sie verleugnet und verfolgt wurden, blieben sie geduldig, bis Unsere Hilfe zu ihnen kam. Es gibt keinen, der die Worte Allahs zu andern vermag. Wahrlich, von den Gesandten ist die Kunde zu dir gekommen"
  },
  {
    "surah_number": 6,
    "verse_number": 35,
    "content":
        "Und wenn dir ihr Widerwille schmerzlich ist, nun wohl, falls du imstande bist, einen Schacht in die Erde oder eine Leiter in den Himmel zu finden, um ihnen ein Zeichen zu bringen (dann magst du es tun) Ware es Allahs Wille, Er hatte sie gewiß auf dem rechten Weg zusammengefuhrt. So sei nicht einer der Unwissenden"
  },
  {"surah_number": 6, "verse_number": 36, "content": "Nur die konnen (etwas) aufnehmen, die zuhoren. Diejenigen aber, die tot sind, wird Allah erwecken; dann sollen sie zu Ihm zuruckgebracht werden"},
  {"surah_number": 6, "verse_number": 37, "content": "Sie sagen: \"Ware ihm von seinem Herrn ein Zeichen niedergesandt worden!\" Sprich: \"Allah hat die Macht, ein Zeichen herabzusenden, doch die meisten von ihnen wissen es nicht"},
  {"surah_number": 6, "verse_number": 38, "content": "Es gibt kein Getier auf Erden und keinen Vogel, der auf seinen zwei Schwingen dahinfliegt, die nicht Gemeinschaften waren so wie ihr. Nichts haben Wir in dem Buch ausgelassen. Vor ihrem Herrn sollen sie dann versammelt werden"},
  {"surah_number": 6, "verse_number": 39, "content": "Die aber Unsere Zeichen leugnen, sind taub und stumm in Finsternissen. Allah fuhrt, wen Er will, in die Irre, und wen Er will, den fuhrt Er auf einen geraden Weg"},
  {"surah_number": 6, "verse_number": 40, "content": "Sprich: \"Was denkt ihr? Wenn die Strafe Allahs uber euch kommt oder die Stunde euch ereilt, werdet ihr dann zu einem anderen rufen als zu Allah, wenn ihr wahrhaftig seid"},
  {"surah_number": 6, "verse_number": 41, "content": "Nein, zu Ihm allein werdet ihr rufen; dann wird Er das hinwegnehmen, weshalb ihr (Ihn) anruft, wenn Er will, und ihr werdet vergessen, was ihr (Ihm) zur Seite stelltet"},
  {"surah_number": 6, "verse_number": 42, "content": "Wir schickten schon vor dir (Gesandte) zu den Volkern, dann suchten Wir sie mit Not und Drangsal heim, auf daß sie (Mich) anflehen mochten"},
  {"surah_number": 6, "verse_number": 43, "content": "Warum demutigten sie sich dann nicht, als Unsere Strafe uber sie kam? Jedoch ihre Herzen waren verhartet, und Satan ließ ihnen alles, was sie taten, als wohlgetan erscheinen"},
  {
    "surah_number": 6,
    "verse_number": 44,
    "content": "Als sie das vergaßen, woran sie erinnert worden waren, da offneten Wir ihnen die Tore aller Dinge. Als sie sich dann schließlich uber das freuten, was sie erhalten hatten, verhangten Wir plotzlich eine Strafe uber sie, und siehe, sie wurden in Verzweiflung gesturzt"
  },
  {"surah_number": 6, "verse_number": 45, "content": "So wurden die Leute ausgerottet, die Frevler waren; und alles Lob gebuhrt Allah, dem Herrn der Welten"},
  {"surah_number": 6, "verse_number": 46, "content": "Sprich: \"Was wahnt ihr? Wenn Allah euer Gehor und euer Gesicht wegnahme und eure Herzen versiegelte, welcher Gott außer Allah konnte euch das wiedergeben?\" Schau, wie mannigfach Wir die Zeichen dartun, und dennoch wenden sie sich (davon) ab"},
  {"surah_number": 6, "verse_number": 47, "content": "Sprich: \"Was wahnt ihr? Wenn Allahs Strafe unversehens oder offenkundig uber euch kommt, wer anders wird vernichtet werden als die Ungerechten"},
  {"surah_number": 6, "verse_number": 48, "content": "Wir schicken die Gesandten nur als Bringer froher Botschaft und als Warner. Uber die also, die da glauben und sich bessern, soll keine Furcht kommen, noch sollen sie traurig sein"},
  {"surah_number": 6, "verse_number": 49, "content": "Diejenigen aber, die Unsere Zeichen leugnen, wird die Strafe erfassen, weil sie ungehorsam sind"},
  {
    "surah_number": 6,
    "verse_number": 50,
    "content":
        "Sprich: \"Ich sage nicht zu euch: \"Bei mir sind Allahs Schatze\", noch kenne ich das Verborgene; auch sage ich nicht zu euch: \"Ich bin ein Engel\"; ich folge nur dem, was mir offenbart wurde.\" Sprich: \"Konnen wohl ein Blinder und ein Sehender einander gleichen? Wollt ihr denn nicht nachdenken"
  },
  {"surah_number": 6, "verse_number": 51, "content": "Und warne hiermit diejenigen, die da furchten, daß sie vor ihrem Herrn versammelt werden wo sie außer Ihm keinen Beschutzer noch Fursprecher haben werden, auf daß sie doch gottesfurchtig werden mogen"},
  {
    "surah_number": 6,
    "verse_number": 52,
    "content": "Und treibe nicht jene fort, die ihren Herrn am Morgen und am Abend im Trachten nach Seinem Angesicht anrufen. Du bist nicht verantwortlich fur sie, und sie sind nicht verantwortlich fur dich. Treibst du sie fort, so wirst du einer der Ungerechten"
  },
  {"surah_number": 6, "verse_number": 53, "content": "Und so haben Wir einige von ihnen durch andere auf die Probe gestellt, so daß sie sagen mogen: \"Sind es diese, denen Allah aus unserer Mitte huldreich gewesen ist?\" Kennt Allah denn die Dankbaren nicht am besten"},
  {
    "surah_number": 6,
    "verse_number": 54,
    "content": "Und wenn jene, die an Unsere Zeichen glauben, zu dir kommen, so sprich: \"Friede sei auf euch! Euer Herr hat Sich Selbst Barmherzigkeit vorgeschrieben; wenn einer von euch unwissentlich etwas Boses tut und es danach bereut und sich bessert, so ist Er Allvergebend, Barmherzig"
  },
  {"surah_number": 6, "verse_number": 55, "content": "So machen Wir die Zeichen klar, auf daß der Weg der Sunder erkannt werde"},
  {"surah_number": 6, "verse_number": 56, "content": "Sprich: \"Mir ist es verboten worden, daß ich diejenigen anbete, die ihr anruft außer Allah.\" Sprich: \"Ich folge euren bosen Gelusten nicht: ich wurde sonst wahrlich irregehen und ware nicht unter den Rechtgeleiteten"},
  {
    "surah_number": 6,
    "verse_number": 57,
    "content": "Sprich: \"Ich folge einem klaren Beweis von meinem Herrn, den ihr als Luge erklart. Es liegt nicht in meiner Macht (, herbeizufuhren), was ihr zu beschleunigen wunscht. Die Entscheidung liegt nur bei Allah. Er legt die Wahrheit dar, und Er ist der beste Richter"
  },
  {"surah_number": 6, "verse_number": 58, "content": "Sprich: \"Lage das in meiner Macht, was ihr zu beschleunigen wunscht, wahrlich, die Sache zwischen mir und euch ware entschieden.\" Und Allah kennt die Ungerechten am besten"},
  {
    "surah_number": 6,
    "verse_number": 59,
    "content":
        "Bei Ihm befinden sich die Schlussel zum Verborgenen; nur Er kennt sie. Und Er weiß, was auf dem Lande ist und was im Meer. Und nicht ein Blatt fallt nieder, ohne daß Er es weiß; und kein Kornchen ist in der Finsternis der Erde und nichts Feuchtes und nichts Trockenes, das nicht in einem deutlichen Buch (verzeichnet) ware"
  },
  {
    "surah_number": 6,
    "verse_number": 60,
    "content": "Und Er ist es, Der eure Seelen in der Nacht abruft und weiß, was ihr am Tage begeht, an dem Er euch dann wieder erweckt, auf daß die vorbestimmte Frist vollendet werde. Zu Ihm werdet ihr dann heimkehren; dann wird Er euch verkunden, was ihr getan habt"
  },
  {"surah_number": 6, "verse_number": 61, "content": "Und Er ist es, der alle Macht uber seine Diener hat, und Er sendet uber euch Wachter, bis endlich, wenn der Tod an einen von euch herantritt, Unsere Boten seine Seele dahinnehmen; und sie vernachlaßigen nichts"},
  {"surah_number": 6, "verse_number": 62, "content": "Dann werden sie zu Allah, ihrem Herrn, zuruckgebracht. Wahrlich, Sein ist das Urteil, und Er ist der Schnellste im Rechnen"},
  {"surah_number": 6, "verse_number": 63, "content": "Sprich: \"Wer errettet euch aus den Fahrnissen zu Lande und Meer, wenn ihr Ihn in Demut anruft und insgeheim (denkt): \"Wenn Er uns hieraus errettet, werden wir wahrlich dankbar sein"},
  {"surah_number": 6, "verse_number": 64, "content": "Sprich: \"Allah errettet euch daraus und aus aller Drangsal, dennoch stellt ihr Ihm Gotter zur Seite"},
  {
    "surah_number": 6,
    "verse_number": 65,
    "content":
        "Sprich: \"Er hat die Macht euch ein Strafgericht zu senden aus der Hohe oder (aus der Tiefe) unter euren Fußen, oder euch als Gruppen zusammenzufuhren und die einen der anderen Gewalttat kosten zu lassen.\" Schau, wie mannigfach Wir die Zeichen dartun, auf daß sie (sie) verstehen mogen"
  },
  {"surah_number": 6, "verse_number": 66, "content": "Und deine Leute haben es fur eine Luge gehalten, obwohl es die Wahrheit ist. Sprich: \"Ich bin nicht euer Wachter"},
  {"surah_number": 6, "verse_number": 67, "content": "Fur jede Weissagung ist eine Zeit festgesetzt, und bald werdet ihr es erfahren"},
  {
    "surah_number": 6,
    "verse_number": 68,
    "content": "Wenn du jene siehst, die uber Unsere Zeichen toricht reden, dann wende dich ab von ihnen, bis sie zu einem anderen Gesprach ubergehen. Und sollte dich Satan (dies) vergessen lassen, dann sitze nach dem Wiedererinnern nicht mit den Ungerechten (beisammen)"
  },
  {"surah_number": 6, "verse_number": 69, "content": "Den Gottesfurchtigen obliegt nicht die Verantwortung fur jene, sondern nur das Ermahnen, auf daß jene gottesfurchtig werden mogen"},
  {
    "surah_number": 6,
    "verse_number": 70,
    "content":
        "Und verlaßt jene, die mit ihrem Glauben ein Spiel treiben und ihn als Zerstreuung betrachten, und die vom irdischen Leben betort sind. Und ermahne sie hierdurch, auf daß nicht eine Seele der Verdammnis fur das anheimfalle, was sie begangen hat. Keinen Helfer noch Fursprecher soll sie haben außer Allah; auch wenn sie jegliches Losegeld bietet, wird es von ihr nicht angenommen. Das sind diejenigen, die fur ihre eigenen Taten dem Verderben preisgegeben werden. Ein Trunk siedenden Wassers wird ihr Anteil sein sowie eine schmerzliche Strafe, weil sie unglaubig waren"
  },
  {
    "surah_number": 6,
    "verse_number": 71,
    "content":
        "Sprich: \"Sollen wir statt Allah das anrufen, was uns weder nutzt noch schadet, und sollen wir auf unseren Fersen umkehren, nachdem Allah uns den Weg gewiesen hat, gleich einem, den die Satane verwirrt im Land herumgangeln? (Jedoch) er hat Gefahrten, die ihn zum rechten Weg rufen: \"Komm zu uns!\"\" Sprich: \"Allahs Fuhrung ist die eigentliche Fuhrung, und uns ist befohlen worden, daß wir uns dem Herrn der Welten ergeben sollen"
  },
  {"surah_number": 6, "verse_number": 72, "content": "Und (uns ist befohlen worden): \"Verrichtet das Gebet und furchtet Ihn, und Er ist es, vor Dem ihr versammelt werdet"},
  {
    "surah_number": 6,
    "verse_number": 73,
    "content":
        "Er ist es, Der in Wahrheit die Himmel und die Erde erschuf; und am Tage, da Er spricht: \"Sei!\" wird es so sein. Sein Wort ist die Wahrheit, und Sein ist das Reich an dem Tage, da in den Sur gestoßen wird. Kenner des Verborgenen und des Offenkundigen Er ist der Allweise, Der am besten unterrichtet ist"
  },
  {"surah_number": 6, "verse_number": 74, "content": "Und als Abraham zu seinem Vater Azar sagte: \"Nimmst du Gotzen zu Gottern? Ich sehe dich und dein Volk in einem offenbaren Irrtum"},
  {"surah_number": 6, "verse_number": 75, "content": "da zeigten Wir Abraham das Reich der Himmel und der Erde, auf daß er zu den Festen im Glauben zahlen moge"},
  {"surah_number": 6, "verse_number": 76, "content": "Als ihn nun die Nacht uberschattete, da erblickte er einen Stern. Er sagte: \"Das ist mein Herr.\" Doch da er unterging, sagte er: \"Ich liebe nicht die Untergehenden"},
  {"surah_number": 6, "verse_number": 77, "content": "Als er den Mond sah, wie er sein Licht ausbreitete, da sagte er: \"Das ist mein Herr.\" Doch da er unterging, sagte er: \"Wenn mein Herr mich nicht rechtleitet, werde ich gewiß unter den Verirrten sein"},
  {"surah_number": 6, "verse_number": 78, "content": "Als er die Sonne sah, wie sie ihr Licht ausbreitete, da sagte er: \"Das ist mein Herr, das ist noch großer.\" Da sie aber unterging, sagte er: \"O mein Volk, ich habe nichts mit dem zu tun, was ihr (Allah) zur Seite stellt"},
  {"surah_number": 6, "verse_number": 79, "content": "Seht, ich habe mein Angesicht in Aufrichtigkeit zu Dem gewandt, Der die Himmel und die Erde schuf, und ich gehore nicht zu den Gotzendienern"},
  {
    "surah_number": 6,
    "verse_number": 80,
    "content":
        "Und sein Volk stritt mit ihm. Da sagte er: \"Streitet ihr mit mir uber Allah, da Er mich schon recht geleitet hat? Und ich furchte nicht das, was ihr Ihm zur Seite stellt, sondern nur das, was mein Herr will. Mein Herr umfaßt alle Dinge mit Wissen. Wollt ihr euch denn nicht ermahnen lassen"
  },
  {
    "surah_number": 6,
    "verse_number": 81,
    "content": "Und wie sollte ich das furchten, was ihr (Allah) zur Seite stellt, wenn ihr nicht furchtet, Allah etwas zur Seite zu stellen, wozu Er euch keine Vollmacht niedersandte?\" Welche der beiden Parteien hat also ein großeres Anrecht auf Sicherheit, wenn ihr es wissen wurdet"
  },
  {"surah_number": 6, "verse_number": 82, "content": "Die da glauben und ihren Glauben nicht mit Ungerechtigkeiten vermengen sie sind es, die Sicherheit haben und die rechtgeleitet werden"},
  {"surah_number": 6, "verse_number": 83, "content": "Das ist Unser Beweis, den Wir Abraham seinem Volk gegenuber gaben. Wir erheben im Rang, wen Wir wollen. Wahrlich, dein Herr ist Allweise, Allwissend"},
  {"surah_number": 6, "verse_number": 84, "content": "Und Wir schenkten ihm Isaak und Jakob; jeden leiteten Wir recht, wie Wir vordem Noah rechtgeleitet hatten und von seinen Nachkommen David, Salomo, Hiob, Joseph, Moses und Aaron. So belohnen Wir diejenigen, die Gutes tun"},
  {"surah_number": 6, "verse_number": 85, "content": "Und (Wir leiteten) Zacharias, Johannes, Jesus und Elias; sie alle gehorten zu den Rechtschaffenen"},
  {"surah_number": 6, "verse_number": 86, "content": "Und (Wir leiteten) Ismael, Elisa, Jonas und Lot; und jeden (von ihnen) zeichneten Wir unter den Volkern aus"},
  {"surah_number": 6, "verse_number": 87, "content": "ebenso manche von ihren Vatern und ihren Nachkommen und ihren Brudern: Wir erwahlten sie und leiteten sie auf den geraden Weg"},
  {"surah_number": 6, "verse_number": 88, "content": "Das ist die Rechtleitung Allahs; damit leitet Er von Seinen Dienern, wen Er will. Hatten sie aber (etwas) anderes angebetet, wahrlich, all ihr Tun ware fur sie fruchtlos geblieben"},
  {"surah_number": 6, "verse_number": 89, "content": "Diese sind es, denen Wir die Schrift gaben und die Weisheit und das Prophetentum. Wenn diese das aber leugnen, so vertrauen Wir es einem Volke an, das es nicht leugnet"},
  {"surah_number": 6, "verse_number": 90, "content": "Das sind jene, die Allah rechtgeleitet hat: so folge ihrer Rechtleitung. Sprich: \"Ich verlange von euch dafur keinen Lohn. Es ist ja nichts anderes als eine Ermahnung fur die Welten"},
  {
    "surah_number": 6,
    "verse_number": 91,
    "content":
        "Sie haben Allah nicht richtig nach Seinem Wert eingeschatzt, wenn sie sagen: \"Allah hat keinem Menschen irgend etwas herabgesandt.\" Sprich: \"Wer sandte das Buch nieder, das Moses als ein Licht und eine Fuhrung fur die Menschen brachte obgleich ihr es zu Papyrusblattern macht, die ihr kundtut, wahrend ihr viel verbergt, und wo euch das gelehrt worden ist, was weder ihr noch eure Vater wußten?\" Sprich: \"Allah! \" Dann laß sie sich weiter vergnugen an ihrem eitlen Geschwatz"
  },
  {
    "surah_number": 6,
    "verse_number": 92,
    "content": "Und dies ist ein Buch, das Wir segensreich hinabsandten als Bestatigung dessen, was vor ihm war, auf daß du die Mutter der Stadte (Makka) und die rings um sie (Wohnenden) warnen mogest. Diejenigen, die an das Jenseits glauben, glauben daran, und sie halten ihre Gebete regelmaßig ein"
  },
  {
    "surah_number": 6,
    "verse_number": 93,
    "content":
        "Wer ist ungerechter als derjenige, der eine Luge gegen Allah erdichtet oder sagt: \"Mir wurde offenbart\", wahrend ihm doch nichts offenbart worden war, und der da sagt: \"Ich werde dergleichen hinabsenden, was Allah hinabgesandt hat?\" Aber konntest du die Frevler nur in des Todes Schlunden sehen, wenn die Engel ihre Hande ausstrecken: \"Liefert eure Seelen aus! Heute sei euer Lohn die Strafe der Schande als Vergeltung fur das, was ihr an Falschem gegen Allah gesprochen habt, und weil ihr euch hochmutig von Seinen Zeichen abgewendet habt"
  },
  {
    "surah_number": 6,
    "verse_number": 94,
    "content":
        "Und nun kommt ihr einzeln zu Uns, wie Wir euch zuerst erschufen, und habt, was Wir euch bescherten, hinter euch gelassen, und Wir sehen bei euch nicht eure Fursprecher, von denen ihr wahntet, sie seien Teilhaber an euren Sachen. Nun seid ihr voneinander abgeschnitten und das, was ihr wahntet, ist euch dahingeschwunden"
  },
  {"surah_number": 6, "verse_number": 95, "content": "Wahrlich, Allah ist es, Der das Korn und die Kerne keimen laßt. Er bringt das Lebendige aus dem Toten hervor, und Er ist der Hervorbringer des Toten aus dem Lebendigen. Das ist Allah; warum lasset ihr euch dann (von Ihm) abwenden"},
  {"surah_number": 6, "verse_number": 96, "content": "Er laßt den Tag anbrechen; und Er macht die Nacht zur Ruhe und Sonne und Mond zur Berechnung (von Tag und Nacht) Das ist die Anordnung des Allmachtigen, des Allwissenden"},
  {"surah_number": 6, "verse_number": 97, "content": "Und Er ist es, Der die Sterne fur euch geschaffen hat, auf daß ihr durch sie den Weg in den Finsternissen zu Land und Meer finden moget. Und so haben Wir bis ins einzelne die Zeichen fur die Menschen, die Wissen haben, dargelegt"},
  {"surah_number": 6, "verse_number": 98, "content": "Er ist es, Der euch aus einem einzigen Wesen hervorbrachte, alsdann fur euch eine Bleibe (im Mutterleib) und einen Aufbewahrungsort (im Grab bestimmte) Wir haben die Zeichen fur Leute dargelegt, die es begreifen"},
  {
    "surah_number": 6,
    "verse_number": 99,
    "content":
        "Und Er ist es, Der aus dem Himmel Wasser niedersendet; damit bringen Wir alle Arten von Pflanzen hervor; mit diesen bringen Wir dann Grunes hervor, woraus Wir Korn in Reihen sprießen lassen; und aus der Dattelpalme, aus ihren Blutendolden, (sprießen) niederhangende Datteltrauben, und Garten mit Beeren, und Olivenund Granatapfel-(Baume) einander ahnlich und nicht ahnlich. Betrachtet ihre Frucht, wenn sie Fruchte tragen, und ihr Reifen. Wahrlich, hierin sind Zeichen fur Leute, die glauben"
  },
  {"surah_number": 6, "verse_number": 100, "content": "Und doch haben sie die Ginn zu Allahs Teilhabern gemacht, obwohl Er sie geschaffen hat; und sie dichten Ihm ohne alles Wissen falschlicherweise Sohne und Tochter an. Gepriesen sei Er und Erhaben uber das, was sie (Ihm) zuschreiben"},
  {"surah_number": 6, "verse_number": 101, "content": "Schopfer der Himmel und der Erde! Wie sollte Er einen Sohn haben, wo Er keine Gefahrtin hat und wo Er alles erschuf und alle Dinge kennt"},
  {"surah_number": 6, "verse_number": 102, "content": "Er ist Allah, euer Herr. Es ist kein Gott außer Ihm, dem Schopfer aller Dinge; so betet Ihn an. Und Er ist der Huter aller Dinge"},
  {"surah_number": 6, "verse_number": 103, "content": "Blicke konnen Ihn nicht erreichen, Er aber erreicht die Blicke. Und Er ist der Allgutige, der Allkundige"},
  {"surah_number": 6, "verse_number": 104, "content": "Wahrlich, sichtbare Beweise sind zu euch nunmehr von eurem Herrn gekommen; wenn einer also sieht, so ist es zu seinem eigenen Besten; und wenn einer blind wird, so ist es zu seinem eigenen Schaden. Und ich bin nicht euer Wachter"},
  {"surah_number": 6, "verse_number": 105, "content": "So legen Wir die Verse dar, damit sie sagen konnen: \"Du hast geforscht\", und damit Wir sie fur die Leute, die Wissen haben, klar machen"},
  {"surah_number": 6, "verse_number": 106, "content": "Folge dem, was dir von deinem Herrn offenbart wurde es ist kein Gott außer Ihm und wende dich von den Gotzendienern ab"},
  {"surah_number": 6, "verse_number": 107, "content": "Und hatte Allah es gewollt, hatten sie (Ihm) keine Gotter zur Seite gesetzt. Wir haben dich weder zu ihrem Huter gemacht, noch bist du ihr Wachter"},
  {
    "surah_number": 6,
    "verse_number": 108,
    "content": "Und schmaht die nicht, welche sie statt Allah anrufen, sonst wurden sie aus Groll ohne Wissen Allah schmahen. Also lassen Wir jedem Volke sein Tun als wohlgefallig erscheinen. Dann aber werden sie zu ihrem Herrn heimkehren; und Er wird ihnen verkunden, was sie getan haben"
  },
  {"surah_number": 6, "verse_number": 109, "content": "Und sie haben bei Allah hoch und heilig geschworen, wenn zu ihnen nur ein Zeichen kame, wurden sie sicherlich daran glauben. Sprich: \"Uber die Zeichen verfugt Allah. Und was gibt euch die Sicherheit, daß sie glauben, wenn sie kommen"},
  {"surah_number": 6, "verse_number": 110, "content": "Und Wir werden ihre Herzen und ihre Augen verwirren, weil sie ja auch das erste Mal nicht daran glaubten, und Wir lassen sie sodann in ihrer Widerspenstigkeit verblendet irregehen"},
  {"surah_number": 6, "verse_number": 111, "content": "Und sendeten Wir auch Engel zu ihnen herab, und sprachen die Toten zu ihnen, und versammelten Wir alle Dinge ihnen gegenuber, sie wurden doch nicht glauben, es sei denn, Allah wollte es. Jedoch die meisten von ihnen sind unwissend"},
  {
    "surah_number": 6,
    "verse_number": 112,
    "content": "Und so hatten Wir fur jeden Propheten Feinde bestimmt: die Satane der Menschen und der Ginn. Sie geben einander zum Trug prunkende Rede ein und hatte es dein Herr gewollt, hatten sie es nicht getan; so uberlaß sie sich selbst mit dem, was sie erdichten"
  },
  {"surah_number": 6, "verse_number": 113, "content": "Und damit die Herzen derer, die nicht an das Jenseits glauben, sich demselben zuneigen und an diesem Gefallen finden und (fortfahren) mogen zu verdienen, was sie sich nun erwerben"},
  {
    "surah_number": 6,
    "verse_number": 114,
    "content":
        "Soll ich denn einen anderen Richter suchen als Allah und Er ist es, Der euch das Buch klar gemacht und herabgesandt hat? Und jene, denen Wir das Buch gegeben haben, wissen, daß es von deinem Herrn mit der Wahrheit herabgesandt wurde, deshalb solltest du nicht unter den Bestreitern sein"
  },
  {"surah_number": 6, "verse_number": 115, "content": "Und das Wort deines Herrn ist in Wahrheit und Gerechtigkeit vollendet worden. Keiner vermag Seine Worte zu andern, und Er ist der Allhorende, der Allwissende"},
  {"surah_number": 6, "verse_number": 116, "content": "Und wenn du den meisten derer auf der Erde gehorchst, werden sie dich von Allahs Weg irrefuhren. Sie folgen nur Vermutungen, und sie raten nur"},
  {"surah_number": 6, "verse_number": 117, "content": "Wahrlich, dein Herr kennt die am besten, die von Seinem Wege abirren; und Er kennt die Rechtgeleiteten am besten"},
  {"surah_number": 6, "verse_number": 118, "content": "So esset das, woruber Allahs Name ausgesprochen wurde, wenn ihr an Seine Zeichen glaubt"},
  {
    "surah_number": 6,
    "verse_number": 119,
    "content":
        "Warum solltet ihr denn von dem nicht essen, woruber Allahs Name ausgesprochen wurde, wo Er euch bereits erklart hat, was Er euch verboten hat das ausgenommen, wozu ihr gezwungen werdet? Und gewiß, viele fuhren mit ihren Gelusten durch Mangel an Wissen zum Irrweg. Wahrlich, dein Herr kennt die Ubertreter am besten"
  },
  {"surah_number": 6, "verse_number": 120, "content": "Und meidet die offenkundige und die geheime Sunde. Jene, die Sunde erwerben, werden den Lohn fur ihren Erwerb empfangen"},
  {"surah_number": 6, "verse_number": 121, "content": "Und esset nicht von dem, woruber Allahs Name nicht ausgesprochen wurde; denn wahrlich, das ist Frevel. Und gewiß werden die Satane ihren Freunden eingeben, mit euch zu streiten. Und wenn ihr ihnen gehorcht, so werdet ihr Gotzendiener sein"},
  {
    "surah_number": 6,
    "verse_number": 122,
    "content": "Kann wohl einer, der tot war und dem Wir Leben gaben und fur den Wir ein Licht machten, um damit unter den Menschen zu wandeln, dem gleich sein, der in Finsternissen ist und nicht daraus hervorzugehen vermag? Und so wurde den Unglaubigen schon gemacht, was sie zu tun pflegten"
  },
  {"surah_number": 6, "verse_number": 123, "content": "Und so haben Wir es in jeder Stadt mit den Großen ihrer Sunder gemacht: damit sie darin Ranke schmieden. Und sie schmieden nur Ranke gegen sich selbst, ohne daß sie es merken"},
  {
    "surah_number": 6,
    "verse_number": 124,
    "content":
        "Und wenn ein Zeichen zu ihnen kommt, sagen sie: \"Wir werden nicht eher glauben, als bis wir dasselbe erhalten, was die Gesandten Allahs erhalten haben.\" Allah weiß am besten, wem Er Seine Botschaft anvertraut. Wahrlich, Erniedrigung vor Allah und eine strenge Strafe wird die Sunder fur ihre Ranke treffen"
  },
  {
    "surah_number": 6,
    "verse_number": 125,
    "content": "Wen Allah aber leiten will, dem weitet Er die Brust fur den Islam; und wen Er in die Irre gehen lassen will, dem macht Er die Brust eng und bedruckt, wie wenn er in den Himmel emporsteigen wurde. So verhangt Allah die Strafe uber jene, die nicht glauben"
  },
  {"surah_number": 6, "verse_number": 126, "content": "Und dies ist der Weg deines Herrn, ein gerader (Weg) Wahrlich, Wir haben die Zeichen fur die Leute dargelegt, die sich ermahnen lassen"},
  {"surah_number": 6, "verse_number": 127, "content": "Ihnen wird ein Haus des Friedens bei ihrem Herrn zuteil sein, und Er ist ihr Beschutzer um ihrer Werke willen"},
  {
    "surah_number": 6,
    "verse_number": 128,
    "content":
        "Und an dem Tage, da Er sie allesamt versammelt, dann (spricht Er): \"O ihr Ginn, ihr habt euch viele Menschen verschafft.\" Und ihre Freunde unter den Menschen sagen: \"Unser Herr, einige von uns haben von anderen Vorteile genossen, nun aber stehen wir am Ende unserer Frist, die Du uns bestimmtest.\" Er spricht: \"Das Feuer sei eure Herberge, darin sollt ihr auf ewig bleiben, es sei denn, daß Allah es anders will.\" Wahrlich, dein Herr ist Allweise, Allwissend"
  },
  {"surah_number": 6, "verse_number": 129, "content": "Und so setzen Wir einige der Frevler uber die anderen um dessentwillen, was sie sich erwarben"},
  {
    "surah_number": 6,
    "verse_number": 130,
    "content":
        "O ihr Ginn und Menschen! Sind nicht aus eurer Mitte Gesandte zu euch gekommen, die euch Meine Zeichen berichteten und euch vor dem Eintreffen dieses eures Tages warnten?\" Sie sagen: \"Wir zeugen gegen uns selbst.\" Das irdische Leben hat sie betort, und sie werden gegen sich selbst das Zeugnis ablegen, daß sie Unglaubige waren"
  },
  {"surah_number": 6, "verse_number": 131, "content": "Dies, weil dein Herr nicht ungerechterweise die Stadte zerstoren wollte, wahrend ihre Bewohner ungewarnt waren"},
  {"surah_number": 6, "verse_number": 132, "content": "Jeder hat Rangstellungen fur das, was er geleistet hat; und dein Herr ubersieht nicht, was sie tun"},
  {
    "surah_number": 6,
    "verse_number": 133,
    "content": "Und dein Herr ist Der, Der auf keinen angewiesen ist, und Dem die Barmherzigkeit zu eigen ist. Wenn Er will, wird Er euch hinwegnehmen und an eurer Stelle folgen lassen, was Ihm beliebt, wie Er euch auch aus der Nachkommenschaft anderer entstehen ließ"
  },
  {"surah_number": 6, "verse_number": 134, "content": "Wahrlich, was euch versprochen wird, das wird geschehen, und ihr konnt es nicht vereiteln"},
  {"surah_number": 6, "verse_number": 135, "content": "Sprich: \"O Leute, handelt eurem Standpunkt gemaß, und ich werde (so) handeln. Bald werdet ihr erfahren, wer den endgultigen Lohn der Wohnstatt erhalten wird.\" Siehe, die Ungerechten sind nie erfolgreich"},
  {
    "surah_number": 6,
    "verse_number": 136,
    "content":
        "Und sie haben fur Allah einen Anteil an den Feldfruchten und dem Vieh, das Er wachsen ließ, ausgesetzt, und sie sagen: \"Das ist fur Allah\" wie sie meinen, \"und das ist fur unsere Teilhaber (Gotzen)\". Aber was fur ihre Teilhaber ist, das erreicht Allah nicht, wahrend das, was fur Allah ist, ihre Teilhaber erreicht. Ubel ist das, was sie beurteilen"
  },
  {
    "surah_number": 6,
    "verse_number": 137,
    "content":
        "Und ebenso haben ihre Teilhaber vielen der Gotzenanbeter das Toten ihrer Kinder als wohlgefallig erscheinen lassen, damit sie sie verderben und ihren Glauben verwirren konnen. Und hatte Allah Seinen Willen erzwungen, hatten sie das nicht getan; so uberlasse sie sich selbst mit dem, was sie erdichten"
  },
  {
    "surah_number": 6,
    "verse_number": 138,
    "content":
        "Und sie sagten: \"Dieses Vieh und diese Feldfruchte sind unantastbar; niemand soll davon essen, außer dem, dem wir es erlauben, wie sie meinten, und es gibt Tiere, deren Rucken (zum Reiten) verboten ist, und Tiere, uber die sie nicht den Namen Allahs aussprechen und so eine Luge gegen Allah erfinden. Bald wird Er ihnen vergelten, was sie erdichteten"
  },
  {
    "surah_number": 6,
    "verse_number": 139,
    "content":
        "Und sie sagen: \"Was im Schoße von diesen Tieren ist, das ist ausschließlich unseren Mannern vorbehalten und unseren Frauen verboten\": wird es aber tot (geboren), dann haben sie (alle) Anteil daran. Er wird ihnen den Lohn fur ihre Behauptung geben. Wahrlich, Er ist Allweise, Allwissend"
  },
  {
    "surah_number": 6,
    "verse_number": 140,
    "content": "Den Schaden tragen wahrlich jene, die ihre Kinder aus torichter Unwissenheit toten und das fur verboten erklaren, was Allah ihnen gegeben hat und so eine Luge gegen Allah erfinden. Sie sind wahrlich in die Irre gegangen, und sie sind nicht rechtgeleitet"
  },
  {
    "surah_number": 6,
    "verse_number": 141,
    "content":
        "Und Er ist es, Der Garten wachsen laßt, mit und ohne Pfahlwerk, und die Dattelpalme und die Getreidefelder, deren Fruchte von verschiedener Art sind, und Oliven und Granatapfel(Baume), einander ahnlich und unahnlich. Esset von ihren Fruchten, wenn sie Fruchte tragen, doch gebet davon am Tage der Ernte (dem Armen) seinen Anteil, seid (dabei aber) nicht verschwenderisch! Wahrlich, Er liebt diejenigen nicht, die nicht maßhalten"
  },
  {"surah_number": 6, "verse_number": 142, "content": "Unter dem Vieh sind Lasttiere und Schlachttiere. Esset von dem, was Allah euch gegeben hat und folgt nicht die Fußstapfen des Satans. Wahrlich, er ist euch ein offenkundiger Feind"},
  {
    "surah_number": 6,
    "verse_number": 143,
    "content": "Acht Paare: zwei von den Schafen und zwei von den Ziegen. Sprich: \"Sind es die beiden Mannchen, die Er (euch) verboten hat, oder die beiden Weibchen oder das, was der Mutterschoß der beiden Weibchen umschließt? Verkundet es mir mit Wissen, wenn ihr wahrhaftig seid"
  },
  {
    "surah_number": 6,
    "verse_number": 144,
    "content":
        "Und von den Kamelen zwei und von den Rindern zwei (Paare) Sprich: \"Sind es die beiden Mannchen, die Er (euch) verboten hat, oder die beiden Weibchen oder das, was der Mutterschoß der beiden Weibchen umschließt? Waret ihr dabei, als Allah euch dies gebot?\" Wer ist also ungerechter als der, welcher eine Luge gegen Allah ersinnt, um die Leute ohne Wissen irrezufuhren? Wahrlich, Allah leitet das ungerechte Volk nicht"
  },
  {
    "surah_number": 6,
    "verse_number": 145,
    "content":
        "Sprich: \"Ich finde in dem, was mir offenbart worden ist, nichts, was einem Essenden, der es essen mochte, verboten ware, es sei denn von selbst Verendetes oder ausgeflossenes Blut oder Schweinefleisch denn das ist eine Unreinheit oder ein Greuel, woruber ein anderer Name als der Allahs angerufen worden ist.\" Wenn aber jemand durch Not (dazu) getrieben wird und dabei keine Ungehorsamkeit oder Ubertretung begeht, dann ist dein Herr Allverzeihend, Allbarmherzig"
  },
  {
    "surah_number": 6,
    "verse_number": 146,
    "content":
        "Und den Juden haben Wir alles Getier untersagt, das Krallen hat; und von den Rindern und vom Kleinvieh haben Wir ihnen das Fett verboten, ausgenommen das, was an ihrem Rucken sitzt oder in den Eingeweiden oder mit den Knochen verwachsen ist. Damit haben Wir ihnen ihre Auflehnung vergolten. Und siehe, Wir sind Wahrhaftig"
  },
  {"surah_number": 6, "verse_number": 147, "content": "Wenn sie dich aber der Luge bezichtigen, so sprich: \"Euer Herr ist von allumfassender Barmherzigkeit, doch Seine Strenge soll nicht vom verbrecherischen Volk abgewendet werden"},
  {
    "surah_number": 6,
    "verse_number": 148,
    "content":
        "Die Gotzendiener werden sagen: \"Hatte Allah es gewollt, so hatten weder wir noch unsere Vater (Allah etwas) beigesellt; auch hatten wir nichts ohne Erlaubnis gemacht.\" Genauso leugneten schon jene, die vor ihnen waren, bis sie Unsere Strenge zu kosten bekamen. Sprich: \"Habt ihr irgendein Wissen? Dann bringt es fur uns zum Vorschein. Doch ihr geht nur Vermutungen nach; und ihr ratselt nur"
  },
  {"surah_number": 6, "verse_number": 149, "content": "Sprich: \"Allah hat den uberzeugenden Beweis. Hatte Er es gewollt, so hatte Er euch alle rechtgeleitet"},
  {
    "surah_number": 6,
    "verse_number": 150,
    "content":
        "Sprich: \"Her mit euren Zeugen, die bezeugen, Allah habe dies verboten!\" Wenn sie (dies) bezeugen, so lege du nicht mit ihnen Zeugnis ab und folge nicht den Neigungen derer, die Unsere Zeichen fur Luge erklart haben, und (den Neigungen) derer, die nicht an das Jenseits glauben und die andere Wesen ihrem Herrn gleichsetzen"
  },
  {
    "surah_number": 6,
    "verse_number": 151,
    "content":
        "Sprich: \"Kommt her, ich will verlesen, was euer Herr euch verboten hat: Ihr sollt Ihm nichts zur Seite stellen und den Eltern Gute erweisen; und ihr sollt eure Kinder nicht aus Armut toten, Wir sorgen ja fur euch und fur sie. Ihr sollt euch nicht den Schandlichkeiten nahern, seien sie offenkundig oder verborgen; und ihr sollt niemanden toten, dessen Leben Allah unverletzlich gemacht hat, außer wenn dies gemaß dem Recht geschieht. Das ist es, was Er euch geboten hat, auf daß ihr es begreifen moget"
  },
  {
    "surah_number": 6,
    "verse_number": 152,
    "content":
        "Und kommt dem Besitz der Waise nicht nahe, es sei denn zu ihrem Besten, bis sie ihre Volljahrigkeit erreicht hat. Und gebt volles Maß und Gewicht in Billigkeit. Wir fordern von keiner Seele etwas uber das hinaus, was sie zu leisten vermag. Und wenn ihr eine Aussage macht, so ubt Gerechtigkeit, auch wenn es einen nahen Verwandten (betrifft); und haltet den Bund Allahs ein. Das ist es, was Er euch gebietet, auf daß ihr ermahnt sein moget"
  },
  {"surah_number": 6, "verse_number": 153, "content": "Und dies ist Mein gerader Weg. So folgt ihm; und folgt nicht den (verschiedenen) Wegen, damit sie euch nicht weitab von Seinem Weg fuhren. Das ist es, was Er euch gebietet, auf daß ihr gottesfurchtig sein moget"},
  {"surah_number": 6, "verse_number": 154, "content": "Hierauf gaben Wir Moses das Buch (die Gnade) erfullend fur den, der das Gute tat, und als Klarlegung aller Dinge und als Fuhrung und als Barmherzigkeit auf daß sie an die Begegnung mit ihrem Herrn glauben mogen"},
  {"surah_number": 6, "verse_number": 155, "content": "Und das ist (auch) ein Buch, das Wir niedersandten voll des Segens. So folgt ihm und hutet euch vor Sunde, auf daß ihr Barmherzigkeit finden moget"},
  {"surah_number": 6, "verse_number": 156, "content": "damit ihr nicht sprechet: \"Nur zu zwei Volkern vor uns wurde die Schrift niedergesandt, und wir hatten in der Tat keine Kunde von ihrem Inhalt"},
  {
    "surah_number": 6,
    "verse_number": 157,
    "content":
        "oder damit ihr nicht sprechet: \"Ware das Buch zu uns niedergesandt worden, hatten wir uns wahrlich besser leiten lassen als sie.\" Nun ist zu euch ein deutlicher Beweis von eurem Herrn gekommen und eine Fuhrung und eine Barmherzigkeit. Wer ist also ungerechter als der, der Allahs Zeichen leugnet und sich von ihnen abkehrt? Wir werden uber die, die sich von Unseren Zeichen abkehren, eine schlimme Strafe verhangen, da sie sich abgewandt haben"
  },
  {
    "surah_number": 6,
    "verse_number": 158,
    "content":
        "Warten sie etwa darauf, daß Engel zu ihnen kommen oder daß dein Herr kommt oder daß einige Zeichen deines Herrn kommen? Am Tag, an dem einige Zeichen deines Herrn eintreffen, soll der Glaube an sie niemandem nutzen, der nicht vorher geglaubt oder in seinem Glauben Gutes gewirkt hat. Sprich: \"Wartet nur; auch wir warten"
  },
  {"surah_number": 6, "verse_number": 159, "content": "Mit jenen aber, die zur Spaltung ihrer Religion beitrugen und zu Parteien geworden sind, hast du nichts Gemeinsames. Ihre Angelegenheit wird sicherlich von Allah beurteilt werden; dann wird Er ihnen verkunden, was sie getan haben"},
  {"surah_number": 6, "verse_number": 160, "content": "Dem, der eine gute Tat vollbringt, soll (sie) zehnfach vergolten werden; derjenige aber, der eine bose Tat verubt, soll nur das Gleiche als Lohn empfangen; und sie sollen kein Unrecht erleiden"},
  {"surah_number": 6, "verse_number": 161, "content": "Sprich: \"Wahrlich, mich hat mein Herr auf einen geraden Weg geleitet zu dem rechten Glauben, dem Glauben Abrahams, des Aufrechten. Und er war keiner der Gotzendiener"},
  {"surah_number": 6, "verse_number": 162, "content": "Sprich: \"Mein Gebet und meine Opferung und mein Leben und mein Tod gehoren Allah, dem Herrn der Welten"},
  {"surah_number": 6, "verse_number": 163, "content": "Er hat niemanden neben Sich. Und so ist es mir geboten worden, und ich bin der Erste der Gottergebenen"},
  {
    "surah_number": 6,
    "verse_number": 164,
    "content":
        "Sprich: \"Sollte ich einen anderen Herrn als Allah suchen, wo Er doch der Herr aller Dinge ist?\" Und keine Seele wirkt, es sei denn gegen sich selbst, und keine lasttragende (Seele) soll die Last einer anderen tragen. Zu eurem Herrn werdet ihr dann heimkehren, und Er wird euch uber all das belehren, woruber ihr uneins waret"
  },
  {
    "surah_number": 6,
    "verse_number": 165,
    "content": "Und Er ist es, Der euch zu Nachfolgern auf der Erde machte und die einen von euch uber die anderen um Rangstufen erhohte, um euch durch das zu prufen, was Er euch gegeben hat. Wahrlich, dein Herr ist schnell im Strafen; und wahrlich, Er ist Allvergebend, Barmherzig"
  },
  {"surah_number": 7, "verse_number": 1, "content": "Alif Lam Mim Sad"},
  {"surah_number": 7, "verse_number": 2, "content": "(Dies ist) ein zu dir hinabgesandtes Buch; du sollst seinetwegen nicht bedruckt sein; und du sollst damit warnen; und es soll eine Ermahnung fur die Glaubigen (sein)"},
  {"surah_number": 7, "verse_number": 3, "content": "Folgt dem, was zu euch von eurem Herrn herabgesandt wurde, und folgt keinen anderen Beschutzern außer Ihm. Wie wenig seid ihr (dessen) eingedenk"},
  {"surah_number": 7, "verse_number": 4, "content": "Und wieviele Stadte haben Wir zerstort! Unsere Strafe kam uber sie bei Nacht oder wahrend sie (sich) am Mittag ausruhten"},
  {"surah_number": 7, "verse_number": 5, "content": "Ihr Ausruf war nichts anderes, als Unsere Strafe uber sie kam, als daß sie sagten: \" Wir waren wahrlich Frevler"},
  {"surah_number": 7, "verse_number": 6, "content": "Wahrlich, Wir werden jene fragen, zu denen (die Gesandten) geschickt wurden, und Wir werden die Gesandten fragen"},
  {"surah_number": 7, "verse_number": 7, "content": "Dann werden Wir ihnen mit Wissen berichten (was geschehen ist); denn Wir waren (ja) niemals abwesend"},
  {"surah_number": 7, "verse_number": 8, "content": "Und das Wagen an jenem Tage wird wahrhaftig sein. Diejenigen, deren Waagschale dann schwer ist, werden erfolgreich sein"},
  {"surah_number": 7, "verse_number": 9, "content": "Diejenigen, deren Waagschale aber leicht ist, sind jene, die ihrer selbst verlustig gegangen, weil sie sich gegen Unsere Zeichen vergingen"},
  {"surah_number": 7, "verse_number": 10, "content": "Wahrlich, Wir haben euch auf der Erde Macht verliehen und euch darin die Mittel zum Unterhalt bereitet. Wie wenig seid ihr dankbar"},
  {"surah_number": 7, "verse_number": 11, "content": "Und Wir hatten euch erschaffen, dann gaben Wir euch die Gestalt; dann sprachen Wir zu den Engeln: \"Werft euch vor Adam nieder\" und sie alle warfen sich nieder. Nur Iblis nicht; er gehorte nicht zu denen, die sich unterwarfen"},
  {"surah_number": 7, "verse_number": 12, "content": "Er sprach: \"Was hinderte dich daran, dich niederzuwerfen, nachdem Ich es dir befohlen habe?\" Er sagte: \" Ich bin besser als er. Du hast mich aus Feuer erschaffen, ihn aber erschufst Du aus Lehm"},
  {"surah_number": 7, "verse_number": 13, "content": "Er sprach: \"Hinab mit dir von hier; es ziemt sich nicht fur dich, hier hochmutig zu sein. Hinaus denn; du bist wahrlich einer der Erniedrigten"},
  {"surah_number": 7, "verse_number": 14, "content": "Er sagte: \"Gewahre mir Aufschub bis zu dem Tage, da sie auferweckt werden"},
  {"surah_number": 7, "verse_number": 15, "content": "Er sprach: \"Dir sei Aufschub gewahrt"},
  {"surah_number": 7, "verse_number": 16, "content": "Er sagte: \"Darum, daß Du mich hast abirren lassen, will ich ihnen gewiß auf Deinem geraden Weg auflauern"},
  {"surah_number": 7, "verse_number": 17, "content": "Dann will ich uber sie von vorne und von hinten kommen, von rechts und von links, und Du wirst die Mehrzahl von ihnen nicht dankbar finden"},
  {"surah_number": 7, "verse_number": 18, "content": "Er sprach: \"Hinweg mit dir, (sei) verachtet und verstoßen! Wahrlich, wer von ihnen dir folgt - Ich werde mit euch allesamt Gahannam fullen"},
  {"surah_number": 7, "verse_number": 19, "content": "O Adam, weile du mit deiner Gattin in dem Garten und esset, wovon immer ihr wollt, nur nahert euch nicht diesem Baum, sonst werdet ihr Ungerechte sein"},
  {"surah_number": 7, "verse_number": 20, "content": "doch Satan flusterte ihnen Boses ein, um ihnen das kundzutun, was ihnen von ihrer Scham verborgen war. Er sagte: \"Euer Herr hat euch diesen Baum nur deshalb verboten, damit ihr nicht Engel oder Ewiglebende werdet"},
  {"surah_number": 7, "verse_number": 21, "content": "Und er schwor ihnen: \"Gewiß, ich bin euch ein aufrichtiger Ratgeber"},
  {
    "surah_number": 7,
    "verse_number": 22,
    "content":
        "So verfuhrte er sie durch Trug. Und als sie von dem Baum kosteten, wurde ihnen ihre Scham offenbar und sie begannen, sich mit den Blattern des Gartens zu bekleiden; und ihr Herr rief sie: \"Habe Ich euch nicht diesen Baum verwehrt und euch gesagt: \"Wahrlich, Satan ist euer offenkundiger Feind"
  },
  {"surah_number": 7, "verse_number": 23, "content": "Sie sagten: \"Unser Herr, wir haben gegen uns selbst gesundigt; und wenn Du uns nicht verzeihst und Dich unser erbarmst, dann werden wir gewiß unter den Verlierern sein"},
  {"surah_number": 7, "verse_number": 24, "content": "Er sprach: \"Hinab mit euch; die einen von euch seien der anderen Feinde. Und es sei euch auf der Erde (nur) ein Aufenthaltsort und eine Versorgung auf Zeit bestimmt"},
  {"surah_number": 7, "verse_number": 25, "content": "Er sprach: \"Auf ihr sollt ihr leben, und auf ihr sollt ihr sterben, und aus ihr werdet ihr (wieder) hervorgebracht werden"},
  {"surah_number": 7, "verse_number": 26, "content": "O Kinder Adams, Wir gaben euch Kleidung, um eure Scham zu bedecken und zum Schmuck; doch das Kleid der Frommigkeit - das ist das beste. Dies ist eins der Zeichen Allahs, auf daß sie (dessen) eingedenk sein mogen"},
  {
    "surah_number": 7,
    "verse_number": 27,
    "content":
        "O Kinder Adams, lasset Satan euch nicht verfuhren, (so) wie er eure Eltern aus dem Garten vertrieb und ihnen ihre Kleidung entriß, um ihnen ihre Scham zu zeigen. Wahrlich, er sieht euch, er und seine Schar, von wo ihr sie nicht seht. Denn seht, Wir haben die Satane zu Freunden derer gemacht, die nicht glauben"
  },
  {"surah_number": 7, "verse_number": 28, "content": "Und wenn sie eine Schandtat begehen, sagen sie: \"Wir fanden unsere Vater dabei, und Allah hat sie uns befohlen.\" Sprich: \"Wahrlich, Allah befiehlt keine Schandtaten. Wollt ihr denn von Allah reden, was ihr nicht wisset"},
  {"surah_number": 7, "verse_number": 29, "content": "Sprich: \"Mein Herr hat Gerechtigkeit befohlen. Und ihr sollt euer Antlitz bei jeder Gebetsstatte (zu Ihm) richten, und ihr sollt Ihn in lauterem Gehorsam anrufen. Wie Er euch ins Dasein gebracht hat, so werdet ihr (zu Ihm) zuruckkehren"},
  {"surah_number": 7, "verse_number": 30, "content": "Eine Schar hat Er geleitet, einer anderen aber wurde nach Gebuhr Irrtum zuteil, da sie sich die Satane zu Beschutzern außer Allah genommen hatten; und (sie) meinen, sie seien rechtgeleitet"},
  {"surah_number": 7, "verse_number": 31, "content": "O Kinder Adams, habt eine gepflegte Erscheinung an jeder Gebetsstatte, und esset und trinkt, doch uberschreitet (dabei) das Maß nicht; wahrlich, Er liebt nicht diejenigen, die nicht maßhalten"},
  {
    "surah_number": 7,
    "verse_number": 32,
    "content":
        "Sprich: \"Wer hat die schonen Dinge Allahs verboten, die Er fur Seine Diener hervorgebracht hat und die guten Dinge der Versorgung?\" Sprich: \"Sie sind fur die Glaubigen in diesem Leben (und) ausschließlich (fur sie) am Tage der Auferstehung.\" So machen Wir die Zeichen klar fur Leute, die Wissen haben"
  },
  {
    "surah_number": 7,
    "verse_number": 33,
    "content":
        "Sprich: \"Mein Herr hat nur Schandlichkeiten verboten, seien sie offenkundig oder verborgen, dazu Sunde und ungerechte Gewalttat. Und (Er hat verboten,) daß ihr Allah das zur Seite setzt, wozu Er keine Befugnis herabsandte, und (Er hat verboten,) daß ihr (etwas) von Allah aussagt, was ihr nicht wisset"
  },
  {"surah_number": 7, "verse_number": 34, "content": "Jedem Volk ist eine Frist gesetzt; und wenn ihre Zeit gekommen ist, dann konnen sie (sie) auch nicht um eine Stunde hinausschieben, noch konnen sie (sie) vorverlegen"},
  {"surah_number": 7, "verse_number": 35, "content": "O Kinder Adams, wenn zu euch aus eurer Mitte Gesandte kommen, die euch Meine Zeichen verkunden uber diejenigen, die dann gottesfurchtig sind und gute Werke tun, soll keine Furcht kommen, noch sollen sie traurig sein"},
  {"surah_number": 7, "verse_number": 36, "content": "Diejenigen aber, die Unsere Zeichen verleugnen und sich hochmutig von ihnen abwenden, sollen die Bewohner des Feuers sein; darin werden sie auf ewig verweilen"},
  {
    "surah_number": 7,
    "verse_number": 37,
    "content":
        "Wer ist wohl frevelhafter als der, der eine Luge gegen Allah erdichtet oder Seine Zeichen der Luge bezichtigt? Diesen soll das bestimmte Los (zuteil) werden, bis Unsere Boten zu ihnen kommen, um ihnen den Tod zu bringen; sie werden sprechen: \"Wo ist nun das, was ihr statt Allah anzurufen pflegtet?\" Jene werden antworten: \"Wir konnen sie nicht finden\"; und sie werden gegen sich selbst Zeugnis ablegen, daß sie Unglaubige waren"
  },
  {
    "surah_number": 7,
    "verse_number": 38,
    "content":
        "Er wird sprechen: \"Tretet ein in das Feuer zu den Scharen der Ginn und der Menschen, die vor euch dahingingen.\" Sooft eine Schar eintritt, wird sie ihre Schwesterschar verfluchen, bis endlich, wenn sie alle nacheinander darin angekommen sind, die letzten zu den ersten sagen werden: \"Unser Herr, diese da haben uns irregefuhrt, so gib ihnen die Pein des Feuers mehrfach.\" Er wird sprechen: \"Jeder hat (sie) mehrfach, allein ihr wisset es nicht"
  },
  {"surah_number": 7, "verse_number": 39, "content": "Und die ersten werden zu den letzten sagen: \"So hattet ihr denn keinen Vorteil vor uns; kostet also die Strafe fur das, was ihr begangen habt"},
  {
    "surah_number": 7,
    "verse_number": 40,
    "content": "Wahrlich, denjenigen, die Unsere Zeichen fur Luge erklaren und sich mit Hochmut von ihnen abwenden, werden die Pforten des Himmels nicht geoffnet werden, noch werden sie in das Paradies eingehen, ehe denn ein Kamel durch ein Nadelohr geht. Und so belohnen Wir die Verbrecher"
  },
  {"surah_number": 7, "verse_number": 41, "content": "Sie sollen Gahannam zum Lager und als Decke uber sich haben. Und so belohnen Wir die Ungerechten"},
  {"surah_number": 7, "verse_number": 42, "content": "Diejenigen aber, die glauben und gute Werke tun - Wir belasten keine Seele uber ihr Vermogen hinaus -, sie sind die Bewohner des Paradieses; darin sollen sie auf ewig verweilen"},
  {
    "surah_number": 7,
    "verse_number": 43,
    "content":
        "Und Wir wollen alles hinwegraumen, was an Groll in ihren Herzen sein mag. Unter ihnen sollen Bache fließen. Und sie werden sagen: \"Alles Lob gebuhrt Allah, Der uns zu diesem (Paradies) geleitet hat! Wir hatten den Weg nicht zu finden vermocht, wenn Allah uns nicht geleitet hatte. Die Gesandten unseres Herrn haben in der Tat die Wahrheit gebracht.\" Und es soll ihnen zugerufen werden: \"Das ist das Paradies, das euch zum Erbe gegeben wird fur das, was ihr getan habt"
  },
  {
    "surah_number": 7,
    "verse_number": 44,
    "content":
        "Und die Bewohner des Paradieses rufen den Bewohnern der Holle zu: \"Seht, wir haben als Wahrheit vorgefunden, was unser Herr uns verhieß. Habt ihr auch als Wahrheit vorgefunden, was euer Herr (euch) verhieß?\" Jene sagen: \"Ja.\" Dann kundigt ein Ausrufer unter ihnen an: \" Der Fluch Allahs sei uber den Missetatern"
  },
  {"surah_number": 7, "verse_number": 45, "content": "die von Allahs Weg abhalten und ihn zu krummen suchen und nicht an das Jenseits glauben"},
  {
    "surah_number": 7,
    "verse_number": 46,
    "content": "Und zwischen den zweien soll eine Scheidewand sein; und in den Hohen sind Leute, die die beiden (Scharen) an ihren Merkmalen erkennen. Sie rufen der Schar des Paradieses zu: \"Friede sei auf euch!\" Diese sind (noch) nicht in (das Paradies) eingegangen, obwohl sie es erhoffen"
  },
  {"surah_number": 7, "verse_number": 47, "content": "Und wenn ihre Blicke sich in Richtung der Bewohner des Feuers wenden, sagen sie: \"Unser Herr, mache uns nicht zum Volk der Frevler"},
  {"surah_number": 7, "verse_number": 48, "content": "Und die (Leute) in den Hohen rufen den Leuten, die sie an ihren Merkmalen erkennen, zu (und) sagen: \"Nichts hat euch das gefruchtet, was ihr zusammengebracht habt, und auch euer Hochmut (hat euch nichts gefruchtet)"},
  {"surah_number": 7, "verse_number": 49, "content": "Sind das jene, von denen ihr (einst) geschworen habt, Allah wurde ihnen keine Barrnherzigkeit erweisen?\" Geht ein in das Paradies; keine Furcht soll uber euch kommen, noch sollt ihr traurig sein"},
  {"surah_number": 7, "verse_number": 50, "content": "Und die Bewohner des Feuers rufen den Bewohnern des Paradieses zu: \"Gießt etwas Wasser uber uns aus oder etwas von dem, was Allah euch gegeben hat.\" Sie sagen: \"Wahrlich, Allah hat beides den Unglaubigen verwehrt"},
  {
    "surah_number": 7,
    "verse_number": 51,
    "content": "die ihre Religion als Zerstreung betrachteten und ihr Spiel mit ihr trieben und vom irdischen Leben betort waren.\" An diesem Tage nun vergessen Wir sie, wie sie die Begegnung an diesem ihrem Tage vergaßen und wie sie Unsere Zeichen zu leugnen pflegten"
  },
  {"surah_number": 7, "verse_number": 52, "content": "Und wahrlich, Wir hatten ihnen ein Buch gebracht, das Wir mit Wissen darlegten als Richtschnur und Barmherzigkeit fur die Leute, die glaubig sind"},
  {
    "surah_number": 7,
    "verse_number": 53,
    "content":
        "Warten sie auf etwas (anderes) als auf seine Auslegung? An dem Tage, da seine Auslegung Wirklichkeit wird, werden jene sagen, die es vordem vergessen hatten: \"Die Gesandten unseres Herrn haben in der Tat die Wahrheit gebracht. Haben wir wohl Fursprecher, die fur uns Fursprache einlegen? Oder konnten wir zuruckgeschickt werden, auf daß wir anderes tun mogen, als wir zu tun pflegten?\" Sie haben ihre Seelen zugrunde gerichtet, und das, was sie zu erdichten gewohnt waren, hat sie im Stich gelassen"
  },
  {
    "surah_number": 7,
    "verse_number": 54,
    "content":
        "Seht, euer Herr ist Allah, Der die Himmel und die Erde in sechs Tagen erschuf, (und) Sich alsdann (Seinem) Reich majestatisch zuwandte: Er laßt die Nacht den Tag verhullen, der ihr eilends folgt. Und (Er erschuf) die Sonne und den Mond und die Sterne, Seinem Befehl dienstbar. Wahrlich, Sein ist die Schopfung und der Befehl! Segensreich ist Allah, der Herr der Welten"
  },
  {"surah_number": 7, "verse_number": 55, "content": "Ruft euren Herrn in Demut und im verborgenen an. Wahrlich, Er liebt die Ubertreter nicht"},
  {"surah_number": 7, "verse_number": 56, "content": "Und stiftet keinen Verderb auf Erden, nachdem dort Ordnung herrscht, und ruft Ihn in Furcht und Hoffnung an. Wahrlich, Allahs Barmherzigkeit ist denen nahe, die gute Werke tun"},
  {
    "surah_number": 7,
    "verse_number": 57,
    "content":
        "Er ist es, Der in Seiner Barmherzigkeit die Winde als frohe Botschaft schickt, bis daß Wir sie, wenn sie eine schwere Wolke tragen, zu einem toten Ort treiben; dann lassen Wir Wasser aus ihr herab, mit dem Wir Fruchte von jeglicher Art hervorbringen. So bringen Wir auch die Toten hervor, auf daß ihr dessen eingedenk sein moget"
  },
  {"surah_number": 7, "verse_number": 58, "content": "Und der gute Ort - seine Pflanzen sprießen nach der Erlaubnis seines Herrn hervor; der (Ort, der) aber schlecht ist - (seine Pflanzen) sprießen nur kummerlich. Und so wenden Wir die Zeichen fur Leute, die dankbar sind"},
  {"surah_number": 7, "verse_number": 59, "content": "Wir entsandten Noah zu seinem Volk, und er sagte: \"O mein Volk, dient Allah; ihr habt keinen anderen Gott außer Ihm. Wahrlich, ich furchte fur euch die Strafe eines großen Tages"},
  {"surah_number": 7, "verse_number": 60, "content": "Es sagten die Vornehmen seines Volkes: \"Wahrlich, wir sehen dich in einem offenkundigen Irrtum"},
  {"surah_number": 7, "verse_number": 61, "content": "Er sagte: \"O meine Leute, es ist kein Irrtum in mir, sondern ich bin ein Gesandter vom Herrn der Welten"},
  {"surah_number": 7, "verse_number": 62, "content": "Ich uberbringe euch die Botschaften meines Herrn und gebe euch aufrichtigen Rat, und ich weiß durch Allah, was ihr nicht wisset"},
  {"surah_number": 7, "verse_number": 63, "content": "Wundert ihr euch, daß eine Ermahnung von eurem Herrn durch einen Mann aus eurer Mitte zu euch gekommen ist, auf daß er euch warne, und auf daß ihr (Allah) furchten moget und vielleicht Erbarmen findet"},
  {"surah_number": 7, "verse_number": 64, "content": "Doch ziehen sie ihn der Luge; dann erretteten Wir ihn und die, die mit ihm im Schiff waren, und ließen jene ertrinken, die Unsere Zeichen verwarfen. Sie waren wahrlich eine blinde Schar"},
  {"surah_number": 7, "verse_number": 65, "content": "Und zu den Ad (entsandten Wir) ihren Bruder Hud. Er sagte: \"O mein Volk, dient Allah; ihr habt keinen anderen Gott außer Ihm. Wollt ihr nicht gottesfurchtig sein"},
  {"surah_number": 7, "verse_number": 66, "content": "Die unglaubigen Vornehmen seines Volkes sagten: \"Wahrlich wir sehen dich in Torheit, und wahrlich, wir erachten dich fur einen Lugner"},
  {"surah_number": 7, "verse_number": 67, "content": "Er sagte: \"O mein Volk, es ist keine Torheit in mir, sondern ich bin ein Gesandter vom Herrn der Welten"},
  {"surah_number": 7, "verse_number": 68, "content": "Ich uberbringe euch die Botschaften meines Herrn, und ich bin euch ein aufrichtiger und getreuer Ratgeber"},
  {
    "surah_number": 7,
    "verse_number": 69,
    "content":
        "Wundert ihr euch etwa, daß eine Ermahnung von eurem Herrn durch einen Mann aus eurer Mitte zu euch gekommen ist, auf daß er euch warne? Und gedenkt (der Zeit), da Er euch zu Erben der Leute Noahs einsetzte und euch ein Ubermaß an Korperwuchs verlieh. Gedenkt denn der Gnaden Allahs, auf daß ihr erfolgreich sein moget"
  },
  {"surah_number": 7, "verse_number": 70, "content": "Sie sagten: \"Bist du zu uns gekommen, damit wir Allah allein verehren und das verlassen (sollen), was unsere Vater anbeteten? Bring uns denn her, was du uns (an Drohung) versprichst, wenn du wahrhaftig bist"},
  {
    "surah_number": 7,
    "verse_number": 71,
    "content": "Er sagte: \"Wahrlich, fallig geworden ist nunmehr fur euch Strafe und Zorn von eurem Herrn. Wollt ihr mit mir uber die Namen streiten, die ihr nanntet - ihr und eure Vater -, wozu Allah keine Befugnis hinabsandte? Wartet denn, ich bin mit euch unter den Wartenden"
  },
  {"surah_number": 7, "verse_number": 72, "content": "Sodann erretteten Wir ihn und diejenigen, die bei ihm waren, durch Unsere Barmherzigkeit; und Wir schnitten den letzten Zweig derer ab, die Unsere Zeichen leugneten und nicht glaubig waren"},
  {
    "surah_number": 7,
    "verse_number": 73,
    "content":
        "Und zu den Tamud (entsandten Wir) ihren Bruder Salih. Er sagte: \" O mein Volk, dient Allah; ihr habt keinen anderen Gott außer Ihm. Wahrlich, nunmehr ist zu euch ein deutlicher Beweis von eurem Herrn gekommen - diese Kamelstute Allahs als Zeichen fur euch. So lasset sie auf Allahs Erde weiden und tut ihr nichts zuleide; (denn) sonst wurde euch eine schmerzliche Strafe treffen"
  },
  {
    "surah_number": 7,
    "verse_number": 74,
    "content": "Und gedenkt (der Zeit), da Er euch zu Erben der `Ad einsetzte und euch eine Statte im Land anwies; ihr erbaut Palaste in seinen Ebenen und grabt Wohnungen in die Berge. Seid also der Gnaden Allahs eingedenk und treibt im Land nicht (euer) Unwesen, indem ihr Unheil anrichtet"
  },
  {
    "surah_number": 7,
    "verse_number": 75,
    "content": "Die Vornehmen seines Volkes, die hochmutig waren, sagten zu denen, die unterdruckt wurden - das waren die Glaubigen unter ihnen: \"Seid ihr sicher, daß Salih von seinem Herrn gesandt worden ist?\" Sie sagten: \"Wahrlich, wir glauben an das, womit er gesandt worden ist"
  },
  {"surah_number": 7, "verse_number": 76, "content": "Da sagten die Hochmutigen: \"Wahrlich, wir verleugnen das, an das ihr glaubt"},
  {"surah_number": 7, "verse_number": 77, "content": "Dann schnitten sie der Kamelstute die Sehnen durch und trotzten dem Befehl ihres Herrn und sagten: \"O Salih, bring uns das her, was du uns (an Drohung) versprichst, wenn du einer der Gesandten bist"},
  {"surah_number": 7, "verse_number": 78, "content": "Da erfaßte sie das Beben; und am Morgen lagen sie in ihren Wohnungen auf dem Boden hingestreckt"},
  {"surah_number": 7, "verse_number": 79, "content": "Da wandte er sich von ihnen ab und sagte: \"O mein Volk, ich uberbrachte euch die Botschaft meines Herrn und bot euch aufrichtigen Rat an; ihr aber liebt die Ratgeber nicht"},
  {"surah_number": 7, "verse_number": 80, "content": "Und (Wir entsandten) Lot, da er zu seinem Volke sagte: \"Wollt ihr eine Schandtat begehen, wie sie keiner in der Welt vor euch je begangen hat"},
  {"surah_number": 7, "verse_number": 81, "content": "Ihr gebt euch in (eurer) Sinnenlust wahrhaftig mit Mannern statt mit Frauen ab. Nein, ihr seid ein ausschweifendes Volk"},
  {"surah_number": 7, "verse_number": 82, "content": "Da war die Antwort seines Volkes keine andere als die: \"Treibt sie aus eurer Stadt hinaus; denn sie sind Leute, die sich reinsprechen wollen"},
  {"surah_number": 7, "verse_number": 83, "content": "Sodann erretteten Wir ihn und die Seinen, mit Ausnahme seiner Frau; denn sie gehorte zu denen, die zuruckblieben"},
  {"surah_number": 7, "verse_number": 84, "content": "Und Wir ließen einen gewaltigen Regen auf sie niedergehen. Nun siehe, wie das Ende der Verbrecher war"},
  {
    "surah_number": 7,
    "verse_number": 85,
    "content":
        "Und zu den Madyan (entsandten Wir) ihren Bruder Suaib. Er sagte: \"O mein Volk, dient Allah; ihr habt keinen anderen Gott außer Ihm. Ein deutliches Zeichen ist nunmehr von eurem Herrn zu euch gekommen. Darum gebt volles Maß und Gewicht und schmalert den Menschen ihre Dinge nicht und stiftet nicht nach ihrer Ordnung Unheil auf Erden. Das ist besser fur euch, wenn ihr glaubig seid"
  },
  {
    "surah_number": 7,
    "verse_number": 86,
    "content": "Und lauert nicht drohend auf jedem Weg, indem ihr die von Allahs Weg abtrunnig machen moget, die an Ihn glauben, und indem ihr ihn (den Weg) zu krummen sucht. Und denkt daran, wie wenige ihr waret und (wie) Er euch mehrte. Und schaut, wie das Ende derer war, die Unheil stifteten"
  },
  {"surah_number": 7, "verse_number": 87, "content": "Und wenn unter euch solche sind, die an das glauben, womit ich gesandt worden bin, und andere, die nicht (daran) glauben, so habt die Geduld, bis Allah zwischen uns richtet; denn Er ist der beste Richter"},
  {
    "surah_number": 7,
    "verse_number": 88,
    "content": "Da sagten die Vornehmen seines Volkes, die hochmutig waren: \"O Suaib, wir wollen dich und mit dir die Glaubigen aus unserer Stadt hinaustreiben, wenn ihr nicht zu unserer Religion zuruckkehrt.\" Er sagte: \"Auch wenn wir (dazu) nicht willens sind"
  },
  {
    "surah_number": 7,
    "verse_number": 89,
    "content":
        "Wir wurden ja eine Luge gegen Allah erdichten, wenn wir zu eurer Religion zuruckkehrten, nachdem Allah uns daraus gerettet hat. Es ziemt sich fur uns nicht, dazu zuruckzukehren, es sei denn, daß Allah, unser Herr, es will. Unser Herr umfaßt alle Dinge mit Wissen. Auf Allah vertrauen wir. O unser Herr, entscheide denn Du zwischen uns und unseren Leuten nach der Wahrheit; denn Du bist es, Der am besten entscheidet"
  },
  {"surah_number": 7, "verse_number": 90, "content": "Und die Vornehmen seines Volkes, die unglaubig waren, sagten: \"Wenn ihr Suaib folgt, seid ihr wahrlich verloren"},
  {"surah_number": 7, "verse_number": 91, "content": "Dann erfaßte sie das Beben; und am Morgen lagen sie in ihren Wohnungen auf dem Boden hingestreckt"},
  {"surah_number": 7, "verse_number": 92, "content": "Diejenigen, die Suaib der Luge beschuldigt hatten, wurden (so zugerichtet), als hatten sie nie darin gewohnt. Diejenigen, die Suaib der Luge beschuldigt hatten, waren selbst die Verlierer"},
  {"surah_number": 7, "verse_number": 93, "content": "Dann wandte er sich von ihnen ab und sagte: \"O mein Volk, wahrlich, ich uberbrachte euch die Botschaften meines Herrn und gab euch aufrichtigen Rat. Wie sollte ich mich nun uber unglaubige Leute betruben"},
  {"surah_number": 7, "verse_number": 94, "content": "Nie sandten Wir einen Propheten in eine Stadt, ohne daß Wir ihre Bewohner mit Not und Drangsal heimsuchten, auf daß sie (Mich) demutig anflehen sollten"},
  {"surah_number": 7, "verse_number": 95, "content": "Darauf tauschten Wir das Ubel gegen etwas Gutes ein, bis sie anwuchsen und sagten: \"Auch unsere Vater erfuhren Leid und Freude.\" Dann erfaßten Wir sie unversehens, ohne daß sie es merkten"},
  {
    "surah_number": 7,
    "verse_number": 96,
    "content": "Hatten aber die Bewohner (jener) Stadte geglaubt und waren sie gottesfurchtig gewesen, so hatten Wir ihnen ganz gewiß die Segnungen von Himmel und Erde eroffnet. Doch sie leugneten; also erfaßten Wir sie um dessentwillen, was sie begangen hatten"
  },
  {"surah_number": 7, "verse_number": 97, "content": "Sind denn die Bewohner der Stadte sicher, daß Unsere Strafe nicht zur Nachtzeit uber sie kommt, wahrend sie noch schlafen"},
  {"surah_number": 7, "verse_number": 98, "content": "Oder sind die Bewohner der Stadte sicher, daß Unsere Strafe nicht vormittags uber sie kommt, wahrend sie beim Spiel sind"},
  {"surah_number": 7, "verse_number": 99, "content": "Sind sie denn sicher vor dem Plan Allahs? Aber niemand kann sich vor dem Plan Allahs sicher fuhlen, außer dem Volk der Verlierenden"},
  {"surah_number": 7, "verse_number": 100, "content": "Leuchtet das jenen nicht ein, die die Erde von ihren (fruheren) Bewohnern ererbt haben, daß Wir, wenn Wir wollen, sie fur ihre Sunden treffen konnen und ihre Herzen versiegeln, so daß sie nicht horen konnen"},
  {
    "surah_number": 7,
    "verse_number": 101,
    "content": "Dies sind die Stadte, deren Geschichte Wir dir erzahlt haben. Wahrlich, ihre Gesandten waren zu ihnen mit deutlichen Beweisen gekommen. Aber sie mochten nicht an das glauben, was sie zuvor geleugnet hatten. So versiegelt Allah die Herzen der Unglaubigen"
  },
  {"surah_number": 7, "verse_number": 102, "content": "Und bei den meisten von ihnen fanden Wir keine Vertragstreue, sondern Wir fanden die meisten von ihnen als Frevler vor"},
  {"surah_number": 7, "verse_number": 103, "content": "Hierauf, nach ihnen, entsandten Wir Moses mit Unseren Zeichen zu Pharao und seinen Vornehmen, doch sie frevelten an ihnen. Nun schau, wie das Ende derer war, die Unheil stifteten"},
  {"surah_number": 7, "verse_number": 104, "content": "Und Moses sagte: \"O Pharao, ich bin ein Gesandter vom Herrn der Welten"},
  {"surah_number": 7, "verse_number": 105, "content": "Es ziemt sich, daß ich von Allah nichts anderes als die Wahrheit rede. Ich bin zu euch mit einem deutlichen Beweis von eurem Herrn gekommen; so laß denn die Kinder Israels mit mir ziehen"},
  {"surah_number": 7, "verse_number": 106, "content": "Er sagte: \"Wenn du wirklich mit einem Zeichen gekommen bist, so weise es vor, wenn du zu den Wahrhaftigen gehorst"},
  {"surah_number": 7, "verse_number": 107, "content": "Da warf er seinen Stock nieder, und da war dieser (auf einmal) eine leibhaftige Schlange"},
  {"surah_number": 7, "verse_number": 108, "content": "Dann zog er seine Hand heraus und da sah sie (auf einmal) fur die Zuschauer weiß aus"},
  {"surah_number": 7, "verse_number": 109, "content": "Die Vornehmen von Pharaos Volk sagten: \"Wahrlich, das ist ein wissensreicher Zauberer"},
  {"surah_number": 7, "verse_number": 110, "content": "Er will euch aus eurem Land vertreiben. Was veranlaßt ihr nun"},
  {"surah_number": 7, "verse_number": 111, "content": "Sie sagten: \"Warte mit ihm und seinem Bruder noch eine Weile und sende Ausrufer zu einer Versammlung in die Stadte aus"},
  {"surah_number": 7, "verse_number": 112, "content": "auf daß sie jeden kundigen Zauberer zu dir bringen sollen"},
  {"surah_number": 7, "verse_number": 113, "content": "Und die Zauberer kamen zu Pharao (und) sagten: \"Uns wird doch gewiß eine Belohnung zuteil wenn wir siegen"},
  {"surah_number": 7, "verse_number": 114, "content": "Er sagte: \"Ja, und ihr sollt zu den Nachsten (von uns) gehoren"},
  {"surah_number": 7, "verse_number": 115, "content": "Sie sagten: \"O Moses, entweder wirfst du (den Stock) oder wir werfen (zuerst)"},
  {"surah_number": 7, "verse_number": 116, "content": "Er sagte: \"Ihr sollt werfen!\" Und als sie geworfen hatten, bezauberten sie die Augen der Leute und versetzten sie in Furcht und brachten einen gewaltigen Zauber hervor"},
  {"surah_number": 7, "verse_number": 117, "content": "Und Wir offenbarten Moses: \"Wirf deinen Stock!\" Und siehe, er verschlang alles, was sie an Trug vorgebracht hatten"},
  {"surah_number": 7, "verse_number": 118, "content": "So wurde die Wahrheit vollzogen, und ihre Werke erwiesen sich als nichtig"},
  {"surah_number": 7, "verse_number": 119, "content": "Dort wurden sie damals besiegt, und beschamt kehrten sie um"},
  {"surah_number": 7, "verse_number": 120, "content": "Und die Zauberer trieb es, in Anbetung niederzufallen"},
  {"surah_number": 7, "verse_number": 121, "content": "Sie sagten: \"Wir glauben an den Herrn der Welten"},
  {"surah_number": 7, "verse_number": 122, "content": "den Herrn von Moses und Aaron"},
  {"surah_number": 7, "verse_number": 123, "content": "Da sagte Pharao: \"Ihr habt an ihn geglaubt, ehe ich es euch erlaubte. Gewiß, das ist eine List, die ihr in der Stadt ersonnen habt, um ihre Bewohner daraus zu vertreiben; doch ihr sollt es bald erfahren"},
  {"surah_number": 7, "verse_number": 124, "content": "Wahrlich, ich werde wechselweise eure Hande und Fuße abhauen. Dann werde ich euch alle kreuzigen"},
  {"surah_number": 7, "verse_number": 125, "content": "Sie sagten: \"Dann kehren wir zu unserem Herrn zuruck"},
  {"surah_number": 7, "verse_number": 126, "content": "Du nimmst nur darum Rache an uns, weil wir an die Zeichen unseres Herrn glaubten, als sie zu uns kamen. Unser Herr, gib uns reichlich Geduld und laß uns als Muslime sterben"},
  {
    "surah_number": 7,
    "verse_number": 127,
    "content": "Die Vornehmen von Pharaos Volk sagten: \"Willst du zulassen, daß Moses und sein Volk Unheil im Land stiften und dich und deine Gotter verlassen?\"; er sagte: \"Wir wollen ihre Sohne umbringen und ihre Frauen am Leben lassen; denn wir haben Gewalt uber sie"
  },
  {"surah_number": 7, "verse_number": 128, "content": "Da sagte Moses zu seinem Volk: \"Fleht Allah um Hilfe an und seid geduldig. Wahrlich, die Erde ist Allahs; Er vererbt sie unter Seinen Dienern, wem Er will, und der Ausgang (aller Dinge) ist fur die Gottesfurchtigen"},
  {"surah_number": 7, "verse_number": 129, "content": "Sie sagten: \"Wir litten, ehe du zu uns kamst und nachdem du zu uns gekommen bist.\" Er sagte: \"Euer Herr moge bald eure Feinde zugrunde gehen lassen und euch die Folgeherrschaft im Land geben; und Er wird sehen, was ihr dann tut"},
  {"surah_number": 7, "verse_number": 130, "content": "Und Wir bestraften Pharaos Volk mit Durre und Mangel an Fruchten, auf daß sie sich ermahnen ließen"},
  {
    "surah_number": 7,
    "verse_number": 131,
    "content": "Doch als dann Gutes zu ihnen kam, sagten sie: \"Das gebuhrt uns.\" Und wenn sie ein Ubel traf, so schrieben sie das Unheil Moses und den Seinigen zu. Nun liegt doch gewiß ihr Unheil bei Allah allein, jedoch die meisten von ihnen wissen es nicht"
  },
  {"surah_number": 7, "verse_number": 132, "content": "Und sie sagten: \"Was du uns auch immer fur ein Zeichen bringen magst, um uns damit zu bezaubern, wir werden dir doch nicht glauben"},
  {"surah_number": 7, "verse_number": 133, "content": "Da sandten Wir die Flut uber sie, die Heuschrecken, die Lause, die Frosche und das Blut - deutliche Zeichen -, doch sie betrugen sich hochmutig und wurden ein sundiges Volk"},
  {
    "surah_number": 7,
    "verse_number": 134,
    "content": "Wann immer aber das Strafgericht uber sie kam, sagten sie: \"O Moses, bete fur uns zu deinem Herrn und berufe dich auf das, was Er dir verhieß! Wenn du die Strafe von uns wegnehmen laßt, so werden wir dir ganz gewiß glauben und die Kinder Israels ganz gewiß mit dir ziehen lassen"
  },
  {"surah_number": 7, "verse_number": 135, "content": "Doch als Wir ihnen die Strafe wegnahmen - fur eine Frist, die sie vollenden sollten -, siehe, da brachen sie ihr Wort"},
  {"surah_number": 7, "verse_number": 136, "content": "Darauf bestraften Wir sie und ließen sie im Meer ertrinken, weil sie Unsere Zeichen fur Luge erklarten und nicht auf sie achteten"},
  {
    "surah_number": 7,
    "verse_number": 137,
    "content":
        "Und Wir gaben dem Volk, das als schwach galt, die ostlichen Teile des Landes zum Erbe und dazu die westlichen Teile, die Wir gesegnet hatten. Und das gnadenvolle Wort deines Herrn wurde damit an den Kindern Israels erfullt, weil sie geduldig waren; und Wir zerstorten alles, was Pharao und sein Volk geschaffen und was sie an hohen Bauten erbaut hatten"
  },
  {"surah_number": 7, "verse_number": 138, "content": "Und Wir brachten die Kinder Israels durch das Meer; und sie kamen zu einem Volk, das seinen Gotzen ergeben war. Sie sagten: \"O Moses, mache uns (so) einen Gott, wie diese hier Gotter haben.\" Er sagte: \"Ihr seid ein unbelehrbares Volk"},
  {"surah_number": 7, "verse_number": 139, "content": "Diesen geht wahrlich (all) das zugrunde, was sie betreiben, und eitel wird all das sein, was sie tun"},
  {"surah_number": 7, "verse_number": 140, "content": "Er sagte: \"Soll ich fur euch einen anderen Gott fordern als Allah, obwohl Er euch vor allen Volkern ausgezeichnet hat"},
  {"surah_number": 7, "verse_number": 141, "content": "(Und gedenkt der Zeit,) da Wir euch vor den Leuten Pharaos erretteten, die euch mit bitterer Pein bedruckten, eure Sohne hinmordeten und eure Frauen am Leben ließen. Und hierin lag fur euch eine schwere Prufung von eurem Herrn"},
  {
    "surah_number": 7,
    "verse_number": 142,
    "content":
        "Und Wir verabredeten Uns mit Moses fur dreißig Nachte und erganzten sie mit zehn. So war die festgesetzte Zeit seines Herrn vollendet - vierzig Nachte. Und Moses sagte zu seinem Bruder Aaron: \"Vertritt mich bei meinem Volk und fuhre (es) richtig und folge nicht dem Weg derer, die Unheil stiften"
  },
  {
    "surah_number": 7,
    "verse_number": 143,
    "content":
        "Und als Moses zu Unserem Termin gekommen war und sein Herr zu ihm gesprochen hatte, sagte er: \"Mein Herr, zeige (Dich) mir, auf daß ich Dich schauen mag.\" Er sprach: \"Du wirst Mich nicht sehen, doch blicke auf den Berg; wenn er unverruckt an seinem Ort bleibt, dann wirst du Mich sehen.\" Als nun sein Herr dem Berg erschien, da ließ Er ihn zu Schutt zerfallen, und Moses sturzte ohnmachtig nieder. Und als er zu sich kam, sagte er: \"Gepriesen seist Du, ich bekehre mich zu Dir, und ich bin der Erste der Glaubigen"
  },
  {"surah_number": 7, "verse_number": 144, "content": "Er sprach: \"O Moses, Ich habe dich vor den Menschen durch Meine Botschaft und durch Mein Wort zu dir auserwahlt. So nimm denn hin, was Ich dir gegeben habe, und sei einer der Dankbaren"},
  {"surah_number": 7, "verse_number": 145, "content": "Und Wir schrieben ihm auf den Tafeln allerlei auf zur Ermahnung und Erklarung von allen Dingen: \"So halte sie fest und befiehl deinem Volk, das Beste davon zu befolgen\". Bald werde Ich euch die Statte der Frevler sehen lassen"},
  {
    "surah_number": 7,
    "verse_number": 146,
    "content":
        "Abwenden aber will Ich von Meinen Zeichen diejenigen, die sich im Lande hochmutig gegen alles Recht gebarden; und wenn sie auch alle Zeichen sehen, so wollen sie nicht daran glauben; und wenn sie den Weg der Rechtschaffenheit sehen, so wollen sie ihn nicht als Weg annehmen; sehen sie aber den Weg des Irrtums, so nehmen sie ihn als Weg an. Dies (ist so), weil sie Unsere Zeichen fur Lugen erklarten und sie nicht achteten"
  },
  {"surah_number": 7, "verse_number": 147, "content": "Diejenigen, die Unsere Zeichen und ihre Begegnung im Jenseits leugnen - deren Werke sind hinfallig. Konnen sie fur etwas anderes als das, was sie getan haben, belohnt werden"},
  {
    "surah_number": 7,
    "verse_number": 148,
    "content": "Und die Leute Moses' nahmen sich, nachdem er weggegangen war, aus ihren Schmucksachen ein leibhaftiges Kalb, das muhte. Sahen sie denn nicht, daß es nicht zu ihnen sprechen und sie nicht auf den rechten Weg fuhren konnte? Sie nahmen es sich, und sie wurden Frevler"
  },
  {"surah_number": 7, "verse_number": 149, "content": "Als sie dann von Reue erfaßt wurden und einsahen, daß sie wirklich irregegangen waren, da sagten sie: \"Wenn Sich unser Herr nicht unser erbarmt und uns verzeiht, so werden wir ganz gewiß unter den Verlierenden sein"},
  {
    "surah_number": 7,
    "verse_number": 150,
    "content":
        "Und als Moses zu seinen Leuten zuruckkehrte, zornig und voller Gram, da sagte er: \"Es ist schlimm, was ihr in meiner Abwesenheit an meiner Stelle verubt habt. Wolltet ihr den Befehl eures Herrn beschleunigen?\" Und er warf die Tafeln hin und packte seinen Bruder beim Kopf und zerrte ihn zu sich. Er (Aaron) sagte: \"Sohn meiner Mutter, siehe, das Volk hielt mich fur schwach, und fast hatten sie mich getotet. Darum laß die Feinde nicht uber mich frohlocken und weise mich nicht dem Volk der Ungerechten zu"
  },
  {"surah_number": 7, "verse_number": 151, "content": "Er (Moses) sagte: \"Mein Herr, vergib mir und meinem Bruder und gewahre uns Zutritt zu Deiner Barmherzig keit; denn Du bist der Barmherzigste Erbarmer"},
  {"surah_number": 7, "verse_number": 152, "content": "Wahrlich, diejenigen, die sich nun das Kalb nahmen, wird der Zorn ihres Herrn sowie Schmach im diesseitigen Leben treffen. Und so belohnen Wir diejenigen, die Lugen erdichten"},
  {"surah_number": 7, "verse_number": 153, "content": "Diejenigen aber, die Boses taten und es dann bereuten und glaubten - wahrlich, dein Herr ist hernach Allverzeihend, Barmherzig"},
  {"surah_number": 7, "verse_number": 154, "content": "Und als der Zorn von Moses abließ, nahm er die Tafeln, und in ihrer Niederschrift war Rechtleitung und Barmherzigkeit fur jene, die ihren Herrn furchten"},
  {
    "surah_number": 7,
    "verse_number": 155,
    "content":
        "Und Moses erwahlte aus seinem Volk siebzig Manner fur Unsere Verabredung. Doch als das Beben sie ereilte, sagte er: \"Mein Herr, hattest Du es gewollt, hattest Du sie zuvor vernichten konnen und mich ebenfalls. Willst Du uns denn vernichten um dessentwillen, was die Toren unter uns getan haben? Dies ist nur eine Prufung von Dir. Damit fuhrst Du irre, wen Du willst, und weist den Weg, wem Du willst. Du bist unser Beschutzer; so vergib uns denn und erbarme Dich unser; denn Du bist der Beste der Vergebenden"
  },
  {
    "surah_number": 7,
    "verse_number": 156,
    "content":
        "Und bestimme fur uns Gutes, sowohl im Diesseits als auch im Jenseits; denn zu Dir sind wir reuevoll zuruckgekehrt.\" Er sprach: \"Ich treffe mit Meiner Strafe, wen Ich will; doch Meine Barmherzigkeit umfaßt alle Dinge; so werde Ich sie bestimmen - sowohl fur jene, die (Mich) furchten und die Zakah entrichten als auch fur jene, die an Unsere Zeichen glauben"
  },
  {
    "surah_number": 7,
    "verse_number": 157,
    "content":
        "Dies sind jene, die dem Gesandten, dem Propheten folgen, der des Lesens und Schreibens unkundig ist; dort in der Thora und im Evangelium werden sie uber ihn (geschrieben) finden: er gebietet ihnen das Gute und verbietet ihnen das Bose, und er erlaubt ihnen die guten Dinge und verwehrt ihnen die schlechten, und er nimmt ihnen ihre Last hinweg und die Fesseln, die auf ihnen lagen -; die also an ihn glauben und ihn starken und ihm helfen und dem Licht folgen, das mit ihm hinabgesandt wurde, die sollen erfolgreich sein"
  },
  {
    "surah_number": 7,
    "verse_number": 158,
    "content":
        "Sprich: \"O ihr Menschen, ich bin fur euch alle ein Gesandter Allahs, Dessen das Konigreich der Himmel und der Erde ist. Es ist kein Gott außer Ihm. Er macht lebendig und laßt sterben. Darum glaubt an Allah und an Seinen Gesandten, den Propheten, der des Lesens und Schreibens unkundig ist, der an Allah und an Seine Worte glaubt; und folgt ihm, auf daß ihr rechtgeleitet werden moget"
  },
  {"surah_number": 7, "verse_number": 159, "content": "Und unter dem Volke Moses' gibt es eine Gemeinde, die in Wahrheit den Weg weist und danach Gerechtigkeit ubt"},
  {
    "surah_number": 7,
    "verse_number": 160,
    "content":
        "Und Wir teilten sie in zwolf Stamme zu Gemeinschaften auf, und Wir offenbarten Moses, als sein Volk von ihm etwas zu trinken forderte: \"Schlage mit \" deinem Stock an den Felsen.\" Da entsprangen ihm zwolf Quellen: so kannte jeder Stamm seinen Trinkplatz. Und Wir ließen sie von Wolken uberschatten und sandten ihnen Manna und Salwa hinab: \"Esset von den guten Dingen, die Wir euch beschert haben.\" Und sie schadigten nicht Uns, sondern sich selbst haben sie Schaden zugefugt"
  },
  {
    "surah_number": 7,
    "verse_number": 161,
    "content":
        "Und wenn ihnen gesagt wird: \"Wohnt in dieser Stadt und nehmet von ihr eure Speise, wo immer ihr wollt, und sprecht: \"Vergebung!\" und geht durch das Tor ein, indem ihr euch niederwerft! Dann werden Wir euch eure Sunden vergeben; wahrlich, Wir werden jenen, die Gutes tun, noch mehr (an Gnade) erweisen"
  },
  {"surah_number": 7, "verse_number": 162, "content": "Da vertauschten die Ungerechten unter ihnen den Ausspruch mit einem anderen als dem, der zu ihnen gesprochen worden war. Darum sandten Wir wegen ihres frevelhaften Tuns ein Strafgericht vom Himmel uber sie hernieder"},
  {
    "surah_number": 7,
    "verse_number": 163,
    "content": "Und frage sie nach der Stadt, die am Meer lag, und (danach,) wie sie den Sabbat entweihten, wie ihre Fische scharenweise an ihrem Sabbattage zu ihnen kamen. Doch an dem Tage, da sie den Sabbat nicht feierten, da kamen sie nicht zu ihnen. So pruften Wir sie, weil sie gefrevelt hatten"
  },
  {
    "surah_number": 7,
    "verse_number": 164,
    "content": "Und als eine Gemeinschaft unter ihnen sagte: \"Warum ermahnt ihr Leute, die Allah vernichten oder mit einer strengen Strafe bestrafen will?\" - da sagten sie (die Ermahner): \"Zur Entschuldigung vor eurem Herrn, und damit sie gottesfurchtig werden mogen"
  },
  {"surah_number": 7, "verse_number": 165, "content": "Und als sie das vergaßen, womit sie ermahnt worden waren, da retteten Wir jene, die das Bose verhindert hatten, und erfaßten die Ungerechten mit peinlicher Strafe, weil sie gefrevelt hatten"},
  {"surah_number": 7, "verse_number": 166, "content": "Und als sie trotzig bei dem verharrten, was ihnen verboten worden war, da sprachen Wir zu ihnen: \"Werdet denn verachtliche Affen"},
  {
    "surah_number": 7,
    "verse_number": 167,
    "content": "Und (gedenke der Zeit,) da dein Herr verkundete, Er wolle gewiß gegen sie bis zum Tage der Auferstehung solche entsenden, die sie mit grimmiger Pein bedrangen wurden. Wahrlich, dein Herr ist schnell im Strafen. Und wahrlich, Er ist Allvergebend, Barmherzig"
  },
  {"surah_number": 7, "verse_number": 168, "content": "Und Wir haben sie auf Erden in Gemeinschaften zerteilt. Unter ihnen sind Rechtschaffene, und unter ihnen gibt es welche, die nicht so sind. Und Wir pruften sie durch Gutes und durch Boses, auf daß sie sich bekehren mogen"},
  {
    "surah_number": 7,
    "verse_number": 169,
    "content":
        "Es folgten ihnen dann Nachkommen, die die Schrift erbten; sie greifen aber nach den armseligen Gutern dieser niedrigen (Welt) und sagen: \"Es wird uns verziehen werden.\" Doch wenn (abermals) derartige Guter zu ihnen kamen, griffen sie wiederum danach. Wurde denn der Bund der Schrift nicht mit ihnen geschlossen, damit sie von Allah nichts als die Wahrheit aussagen sollten? Und sie haben gelesen, was darin steht. Aber die Wohnstatte im Jenseits ist besser fur die Gottesfurchtigen. Wollt ihr es denn nicht begreifen"
  },
  {"surah_number": 7, "verse_number": 170, "content": "Und diejenigen, die an der Schrift festhalten und das Gebet verrichten Wir lassen den Rechtschaffenen den Lohn nicht verlorengehen"},
  {
    "surah_number": 7,
    "verse_number": 171,
    "content": "Und da Wir den Berg uber ihnen schuttelten, als ware er ein Schattenspender, und sie dachten, er wurde auf sie sturzen (, da sprachen Wir): \" Haltet fest, was Wir euch gegeben haben, und denkt daran, was darin steht, auf daß ihr gottesfurchtig werden moget"
  },
  {
    "surah_number": 7,
    "verse_number": 172,
    "content":
        "Und als dein Herr aus den Kindern Adams - aus ihren Lenden ihre Nachkommenschaft hervorbrachte und sie zu Zeugen gegen Sich Selbst machte (, indem Er sprach): \"Bin Ich nicht euer Herr?\", sagten sie: \"Doch, wir bezeugen es.\" (Dies ist so,) damit ihr nicht am Tage der Auferstehung sprecht: \"Siehe, wir wußten nichts davon"
  },
  {"surah_number": 7, "verse_number": 173, "content": "Oder (damit ihr nicht) sprecht: \"Es waren bloß unsere Vater, die vordem Gotzendiener waren; wir aber waren ein Geschlecht nach ihnen. Willst Du uns denn vernichten um dessentwillen, was die Verlogenen taten"},
  {"surah_number": 7, "verse_number": 174, "content": "Und so machen Wir die Zeichen klar, auf daß sie sich bekehren mogen"},
  {"surah_number": 7, "verse_number": 175, "content": "Und erzahle ihnen die Geschichte dessen, dem Wir Unsere Zeichen gaben, der aber an ihnen vorbeiglitt; so folgte Satan ihm nach, und er wurde einer der Irregegangenen"},
  {
    "surah_number": 7,
    "verse_number": 176,
    "content":
        "Und hatten Wir es gewollt, hatten Wir ihn dadurch erhohen konnen; doch er neigte der Erde zu und folgte seiner eigenen Neigung. Er gleicht daher einem Hunde: treibst du ihn fort, so hangt er seine Zunge heraus; laßt du aber von ihm ab, so hangt er auch seine Zunge heraus. Gerade so ergeht es Leuten, die Unsere Zeichen leugnen. Darum erzahle (ihnen) die Geschichten, auf daß sie sich besinnen mogen"
  },
  {"surah_number": 7, "verse_number": 177, "content": "Schlimm ist das Beispiel der Leute, die Unsere Zeichen leugnen und gegen sich selbst gesundigt haben"},
  {"surah_number": 7, "verse_number": 178, "content": "Derjenige, den Allah rechtleitet, ist auf dem rechten Weg. Diejenigen aber, die Er irrefuhrt, sind wahrlich jene, die verloren haben"},
  {
    "surah_number": 7,
    "verse_number": 179,
    "content":
        "Wir haben viele Ginn und Menschen erschaffen, deren Ende Gahannam sein wird! Sie haben Herzen, mit denen sie nicht begreifen, und sie haben Augen, mit denen sie nicht sehen, und sie haben Ohren, mit denen sie nicht horen; sie sind wie das Vieh; nein, sie irren noch eher (vom Weg) ab. Sie sind wahrlich unbedacht"
  },
  {"surah_number": 7, "verse_number": 180, "content": "Und Allahs sind die Schonsten Namen; so ruft Ihn mit ihnen an. Und lasset jene sein, die hinsichtlich Seiner Namen eine abwegige Haltung einnehmen. Ihnen wird das vergolten werden, was sie getan haben"},
  {"surah_number": 7, "verse_number": 181, "content": "Und unter denen, die Wir erschufen, gibt es eine Gemeinschaft, die mit der Wahrheit leitet und danach Gerechtigkeit ubt"},
  {"surah_number": 7, "verse_number": 182, "content": "Diejenigen aber, die Unsere Zeichen leugnen, werden Wir Schritt fur Schritt erniedrigen, ohne daß sie begreifen, wie dies geschah"},
  {"surah_number": 7, "verse_number": 183, "content": "Und Ich werde ihnen Aufschub gewahren; denn wahrlich, Meine Plane sind stark angelegt"},
  {"surah_number": 7, "verse_number": 184, "content": "Haben sie denn nicht daruber nachgedacht, daß ihr Gefahrte nicht besessen ist? Er ist nichts anderes als ein deutlicher Warner"},
  {
    "surah_number": 7,
    "verse_number": 185,
    "content": "Haben sie denn nicht das Reich der Himmel und der Erde betrachtet und alle Dinge, die Allah geschaffen hat, und (haben sie nicht bedacht,) daß sich ihre Lebensfrist vielleicht schon dem Ende nahert? Woran sonst wollen sie wohl nach dieser Verkundigung glauben"
  },
  {"surah_number": 7, "verse_number": 186, "content": "Fur den, den Allah irrefuhrt, kann es keinen geben, der ihn rechtleitet; und Er laßt sie in ihrer Widerspenstigkeit blindlings umherirren"},
  {
    "surah_number": 7,
    "verse_number": 187,
    "content":
        "Sie befragen dich nach der Stunde, wann sie wohl eintreten werde. Sprich: \"Das Wissen darum ist bei meinem Herrn. Keiner als Er kann sie (die Stunde) zu ihrer Zeit bekannt geben. Schwer lastet sie in den Himmeln und auf der Erde. Sie soll uber euch nur plotzlich hereinbrechen.\" Sie befragen dich, als ob du von ihr genaue Kenntnis besaßest. Sprich: \"Das Wissen darum ist bei meinem Herrn; doch die meisten Menschen wissen es nicht"
  },
  {
    "surah_number": 7,
    "verse_number": 188,
    "content":
        "Sprich: \"Ich habe nicht die Macht, mir selbst zu nutzen oder zu schaden, es sei denn, Allah will es. Und hatte ich Kenntnis von dem Verborgenen, wahrlich, ich hatte mir die Fulle des Guten zu sichern vermocht, und Ubeles hatte mich nicht beruhrt. Ich bin ja nur ein Warner und ein Bringer froher Botschaft fur die Leute, die glaubig sind"
  },
  {
    "surah_number": 7,
    "verse_number": 189,
    "content":
        "Er ist es, Der euch aus einer einzigen Seele erschuf; und aus ihm machte Er seine Gattin, damit er bei ihr ruhe. Als er ihr dann beigewohnt hatte, war sie mit einer leichten Last schwanger und ging mit ihr umher. Und wenn sie schwer wird, dann beten beide zu Allah, ihrem Herrn: \"Wenn Du uns ein gutes (Kind) gibst, so werden wir wahrlich unter den Dankbaren sein"
  },
  {"surah_number": 7, "verse_number": 190, "content": "Doch wenn Er ihnen dann ein gutes (Kind) gibt, so schreiben sie Seine ihnen gewahrte Gabe Gottern zu. Aber Allah ist uber alles Erhaben, was sie (Ihm) zur Seite stellen"},
  {"surah_number": 7, "verse_number": 191, "content": "Wollen sie denn jene Teilhaber (anbeten), die nichts erschaffen konnen und selbst (nur) Erschaffene sind"},
  {"surah_number": 7, "verse_number": 192, "content": "Und sie vermogen ihnen keine Hilfe zu gewahren, noch konnen sie sich selber helfen"},
  {"surah_number": 7, "verse_number": 193, "content": "Und wenn ihr sie zum rechten Weg ruft, dann folgen sie euch nicht. Es ist ganz gleich fur euch, ob ihr sie ruft oder ob ihr schweigt"},
  {"surah_number": 7, "verse_number": 194, "content": "Jene, die ihr statt Allah ruft, sind selbst erschaffene (Wesen) wie ihr. Ruft sie denn an und lasset sie euch Antwort geben, wenn ihr wahrhaftig seid"},
  {"surah_number": 7, "verse_number": 195, "content": "Haben sie etwa Fuße, um zu gehen, oder haben sie Hande, um zu greifen, oder haben sie Augen, um zu sehen, oder haben sie Ohren, um zu horen? Sprich: \"Ruft eure Gotter an; dann schmiedet Listen gegen mich und lasset mich nicht lange warten"},
  {"surah_number": 7, "verse_number": 196, "content": "Wahrlich, mein Beschutzer ist Allah, Der das Buch herabgesandt hat. Und Er beschutzt die Rechtschaffenen"},
  {"surah_number": 7, "verse_number": 197, "content": "Die aber, die ihr statt Ihm anruft, vermogen euch nicht zu helfen, noch konnen sie sich selber helfen"},
  {"surah_number": 7, "verse_number": 198, "content": "Und wenn ihr sie zum rechten Weg ruft, so horen sie (euch) nicht. Und du siehst sie nach dir schauen, doch sie sehen nicht"},
  {"surah_number": 7, "verse_number": 199, "content": "sbe Nachsicht, gebiete das Rechte und wende dich von den Unwissenden ab"},
  {"surah_number": 7, "verse_number": 200, "content": "Und wenn du zu einer bosen Tat vom Satan aufgestachelt worden bist, dann nimm deine Zuflucht bei Allah; wahrlich, Er ist Allhorend, Allwissend"},
  {"surah_number": 7, "verse_number": 201, "content": "Wahrlich, diejenigen, die dann gottesfurchtig sind, wenn sie eine Heimsuchung durch Satan trifft, und sich dann ermahnen lassen: siehe, gleich sehen sie (ihren klaren Weg) wieder"},
  {"surah_number": 7, "verse_number": 202, "content": "Aber ihre (heidnischen) Bruder treiben sie dazu, im Irrtum fortzufahren, und dann lassen sie (darin) nicht nach"},
  {
    "surah_number": 7,
    "verse_number": 203,
    "content": "Wenn du ihnen kein Zeichen bringst, sagen sie: \"Warum erfindest du es nicht?\" Sprich: \"Ich folge nur dem, was mir von meinem Herrn offenbart wurde. Dies sind sichtbare Beweise von eurem Herrn und eine Fuhrung und Barmherzigkeit fur glaubige Leute"
  },
  {"surah_number": 7, "verse_number": 204, "content": "Und wenn der Quran verlesen wird, so hort zu und schweigt in Aufmerksamkeit, auf daß ihr Erbarmen finden moget"},
  {"surah_number": 7, "verse_number": 205, "content": "Und gedenke deines Herrn in deinem Herzen in Demut und Furcht, ohne laut vernehmbare Worte am Morgen und am Abend; und sei nicht einer der Unachtsamen"},
  {"surah_number": 7, "verse_number": 206, "content": "Wahrlich, diejenigen, die bei deinem Herrn sind, sind nicht zu hochmutig dazu, Ihm zu dienen; sie lobpreisen Ihn und werfen sich vor Ihm nieder"},
  {"surah_number": 8, "verse_number": 1, "content": "Sie fragen dich nach der Beute. Sprich: \"Die Beute gehort Allah und dem Gesandten. Darum furchtet Allah und ordnet die Dinge in Eintracht unter euch und gehorcht Allah und Seinem Gesandten, wenn ihr Glaubige seid"},
  {"surah_number": 8, "verse_number": 2, "content": "Glaubig sind wahrlich diejenigen, deren Herzen erbeben, wenn Allah genannt wird, und die in ihrem Glauben gestarkt sind, wenn ihnen Seine Verse verlesen werden, und die auf ihren Herrn vertrauen"},
  {"surah_number": 8, "verse_number": 3, "content": "Das sind jene, die das Gebet verrichten und von dem spenden, was Wir ihnen gegeben haben"},
  {"surah_number": 8, "verse_number": 4, "content": "Diese sind die wahren Glaubigen. Sie genießen (die hohe) Wertschatzung ihres Herrn sowie Vergebung und eine ehrenvolle Versorgung"},
  {"surah_number": 8, "verse_number": 5, "content": "Dies (genauso), wie dein Herr dich in gerechter Weise aus deinem Hause fuhrte, wahrend ein Teil der Glaubigen abgeneigt war"},
  {"surah_number": 8, "verse_number": 6, "content": "Sie streiten mit dir uber die Wahrheit, nachdem sie (ihnen) doch deutlich kund geworden ist, als ob sie in den Tod getrieben wurden und (ihn) vor Augen hatten"},
  {
    "surah_number": 8,
    "verse_number": 7,
    "content": "Und damals verhieß Allah euch von einer der beiden Scharen, sie solle euch zufallen, und ihr wunschtet, daß diejenige ohne Kampfkraft fur euch bestimmt sei; Allah aber will, daß die Wahrheit durch Seine Worte vollbracht werde und daß die Wurzel der Unglaubigen ausgerottet werde"
  },
  {"surah_number": 8, "verse_number": 8, "content": "damit Er die Wahrheit an den Tag bringe und den Trug zunichte mache, mag es den Sundern auch zuwider sein"},
  {"surah_number": 8, "verse_number": 9, "content": "Da ihr zu eurem Herrn um Hilfe schriet, und Er euch erhorte und versprach: \"Ich will euch mit eintausend Engeln nacheinander beistehen"},
  {"surah_number": 8, "verse_number": 10, "content": "Allah sagte dies nur als frohe Botschaft, damit eure Herzen sich beruhigten. Jedoch die Hilfe kommt von Allah allein; wahrlich, Allah ist Erhaben, Allweise"},
  {
    "surah_number": 8,
    "verse_number": 11,
    "content": "denn Er ließ den Schlaf als eine Sicherheit von Ihm auf euch niedersinken; und Er sandte Wasser auf euch aus den Wolken nieder, um euch damit zu reinigen und Satans Befleckung von euch hinwegzunehmen, auf daß Er eure Herzen starkte und (eure) Schritte festigte"
  },
  {"surah_number": 8, "verse_number": 12, "content": "Da gab dein Herr den Engeln ein: \"Ich bin mit euch; so festigt denn die Glaubigen. In die Herzen der Unglaubigen werde Ich Schrecken werfen. Trefft (sie) oberhalb des Nackens und schlagt ihnen jeden Finger ab"},
  {"surah_number": 8, "verse_number": 13, "content": "Dies (war so), weil sie Allah und Seinem Gesandten trotzten. Wer aber Allah und Seinem Gesandten trotzt wahrlich, Allah ist streng im Strafen"},
  {"surah_number": 8, "verse_number": 14, "content": "Dies sollt ihr kosten; und (wisset), daß fur die Unglaubigen die Feuerspein bestimmt ist"},
  {"surah_number": 8, "verse_number": 15, "content": "O ihr, die ihr glaubt, wenn ihr auf die Unglaubigen stoßt, die im Heerzug vorrucken, so kehrt ihnen nicht den Rucken"},
  {"surah_number": 8, "verse_number": 16, "content": "Und derjenige, der ihnen an solch einem Tage den Rucken kehrt, es sei denn, er schwenke zur Schlacht oder zum Anschluß an einen Trupp ab, der ladt wahrlich Allahs Zorn auf sich, und seine Herberge soll Gahannam sein; und schlimm ist das Ende"},
  {"surah_number": 8, "verse_number": 17, "content": "Nicht ihr habt sie erschlagen, sondern Allah erschlug sie. Und nicht du hast geschossen, sondern Allah gab den Schuß ab; und prufen wollte Er die Glaubigen mit einer schonen Prufung von Ihm. Wahrlich, Allah ist Allhorend, Allwissend"},
  {"surah_number": 8, "verse_number": 18, "content": "Dies und (wisset), daß Allah die List der Unglaubigen kraftlos machen will"},
  {
    "surah_number": 8,
    "verse_number": 19,
    "content":
        "Verlangt ihr eine Entscheidung, dann ist die Entscheidung schon zu euch gekommen. Und wenn ihr absteht, so ist es besser fur euch; kehrt ihr jedoch (zur Feindseligkeit) zuruck, werden auch Wir (zu ihr) zuruckkehren, und eure Menge soll euch nichts nutzen, so zahlreich sie auch sein mag; denn (wisset), daß Allah mit den Glaubigen ist"
  },
  {"surah_number": 8, "verse_number": 20, "content": "O ihr, die ihr glaubt, gehorcht Allah und Seinem Gesandten, und wendet euch nicht von ihm ab wahrend ihr zuhort"},
  {"surah_number": 8, "verse_number": 21, "content": "Und seid nicht wie jene, die sagen: \"Wir horen\", und doch nicht horen"},
  {"surah_number": 8, "verse_number": 22, "content": "Wahrlich, die schlimmsten Tiere vor Allah sind die tauben und stummen, die nicht begreifen"},
  {"surah_number": 8, "verse_number": 23, "content": "Und hatte Allah etwas Gutes in ihnen erkannt, hatte Er sie gewiß horend gemacht. Und wenn Er sie horend macht, so werden sie sich in Widerwillen wegwenden"},
  {"surah_number": 8, "verse_number": 24, "content": "O ihr, die ihr glaubt, hort auf Allah und den Gesandten, wenn er euch zu etwas aufruft, das euch Leben verleiht, und wisset, daß Allah zwischen den Menschen und sein Herz tritt, und daß ihr vor Ihm versammelt werdet"},
  {"surah_number": 8, "verse_number": 25, "content": "Und hutet euch vor einer Drangsal, die gewiß nicht bloß die unter euch treffen wird, die Unrecht getan haben. Und wisset, daß Allah streng im Strafen ist"},
  {
    "surah_number": 8,
    "verse_number": 26,
    "content": "Und denkt daran, wie wenige ihr waret, im Land als schwach galtet, in Furcht schwebtet, die Leute konnten euch hinwegraffen: Er aber beschirmte euch und starkte euch durch Seine Hilfe und versorgte euch mit guten Dingen, auf daß ihr dankbar sein moget"
  },
  {"surah_number": 8, "verse_number": 27, "content": "O ihr, die ihr glaubt, handelt nicht untreu gegenuber Allah und dem Gesandten, noch seid wissentlich untreu in eurer Treuhandschaft"},
  {"surah_number": 8, "verse_number": 28, "content": "Und wisset, daß euer Gut und eure Kinder nur eine Versuchung sind und daß bei Allah großer Lohn ist"},
  {"surah_number": 8, "verse_number": 29, "content": "O ihr, die ihr glaubt, wenn ihr Allah furchtet, wird Er euch Entscheidungskraft gewahren und eure Ubel von euch nehmen und euch vergeben; und Allah ist voll großer Huld"},
  {"surah_number": 8, "verse_number": 30, "content": "Und damals schmiedeten die Unglaubigen gegen dich Plane, dich gefangen zu nehmen oder dich zu ermorden oder dich zu vertreiben. Sie schmiedeten Plane, (aber) auch Allah schmiedete Plane, und Allah ist der beste Planschmied"},
  {"surah_number": 8, "verse_number": 31, "content": "Und wenn ihnen Unsere Verse verlesen werden, sagen sie: \"Wir haben es gehort. Wollten wir es, konnten wir gewiß Derartiges außern; denn das sind ja Fabeln der Fruheren"},
  {"surah_number": 8, "verse_number": 32, "content": "Und da sagten sie: \"O Allah, wenn dies wirklich die Wahrheit von Dir ist, dann laß Steine vom Himmel auf uns niederregnen oder bringe eine schmerzliche Strafe auf uns herab"},
  {"surah_number": 8, "verse_number": 33, "content": "Allah aber wollte sie nicht bestrafen, solange du unter ihnen weiltest, noch wollte Allah sie bestrafen, wahrend sie um Vergebung baten"},
  {
    "surah_number": 8,
    "verse_number": 34,
    "content": "Aber, warum sollte Allah sie nicht bestrafen, wenn sie (die Glaubigen) von (dem Besuch) der heiligen Moschee abhalten, wo sie doch nicht deren Beschutzer sind? Ihre Beschutzer sind nur die Gottesfurchtigen, jedoch die meisten von ihnen wissen es nicht"
  },
  {"surah_number": 8, "verse_number": 35, "content": "Und ihr Gebet vor dem Haus (Ka`ba) ist nichts anderes als Pfeifen und Handeklatschen. \"Kostet denn die Strafe dafur, daß ihr unglaubig waret"},
  {"surah_number": 8, "verse_number": 36, "content": "Die Unglaubigen geben wahrlich ihr Vermogen (dafur) aus, um von Allahs Weg abzuhalten. Sie werden es ausgeben; dann aber werden sie daruber jammern, und dann werden sie besiegt werden. Und die Unglaubigen werden in Gahannam versammelt werden"},
  {"surah_number": 8, "verse_number": 37, "content": "auf daß Allah die Bosen von den Guten scheide, und die Bosen einen zum anderen und sie alle zusammen auf einen Haufen versammele (und) sie dann in Gahannam werfe. Diese sind wahrlich die Verlierer"},
  {"surah_number": 8, "verse_number": 38, "content": "Sprich zu denen, die unglaubig sind, daß ihnen das Vergangene verziehen wird, wenn sie (von ihrem Unglauben) absehen; kehren sie aber (zum Unglauben) zuruck, dann wahrlich, ist das Beispiel der Fruheren schon dagewesen"},
  {"surah_number": 8, "verse_number": 39, "content": "Und kampft gegen sie, damit keine Verfuhrung mehr stattfinden kann und (kampft,) bis samtliche Verehrung auf Allah allein gerichtet ist. Stehen sie jedoch (vom Unglauben) ab, dann, wahrlich, sieht Allah sehr wohl, was sie tun"},
  {"surah_number": 8, "verse_number": 40, "content": "Und wenn sie (euch) den Rucken kehren, dann wisset, daß Allah euer Beschutzer ist; welch bester Beschutzer und welch bester Helfer ist Er"},
  {
    "surah_number": 8,
    "verse_number": 41,
    "content":
        "Und wisset, was immer ihr erbeuten mogt, ein Funftel davon gehort Allah und dem Gesandten und der Verwandtschaft und den Waisen und den Bedurftigen und den Sohn des Weges, wenn ihr an Allah glaubt und an das, was Wir zu Unserem Diener niedersandten am Tage der Unterscheidung - dem Tage, an dem die beiden Heere zusammentrafen -; und Allah hat Macht uber alle Dinge"
  },
  {
    "surah_number": 8,
    "verse_number": 42,
    "content":
        "(Damals,) als ihr auf dieser Seite (des Tales) waret und sie auf jener Seite und die Karawane tiefer war als ihr. Und hattet ihr etwas verabreden wollen, waret ihr uneins uber den Zeitpunkt gewesen. Doch (das Treffen wurde herbeigefuhrt), damit Allah die Sache herbeifuhrte, die geschehen sollte; und damit diejenigen, die (dabei) umkamen, auf Grund eines klaren Beweises umkamen, und diejenigen, die am Leben blieben, auf Grund eines klaren Beweises am Leben bleiben wurden. Wahrlich, Allah ist Allhorend, Allwissend"
  },
  {
    "surah_number": 8,
    "verse_number": 43,
    "content": "Damals als Allah sie dir in deinem Traum in geringer Anzahl zeigte; und hatte Er sie dir in großer Anzahl gezeigt, waret ihr sicherlich Versager und uber die Sache uneins gewesen; Allah aber bewahrte (euch davor); wahrlich, Er kennt wohl, was in den Herzen ist"
  },
  {
    "surah_number": 8,
    "verse_number": 44,
    "content": "Zur Zeit eures Treffens geschah es, daß Er sie in euren Augen als zahlenmaßig gering erscheinen ließ und euch in ihren Augen als zahlenmaßig gering erscheinen ließ, auf daß Allah die Sache herbeifuhrte, die geschehen sollte. Und zu Allah werden alle Angelegenheiten zuruckgebracht"
  },
  {"surah_number": 8, "verse_number": 45, "content": "O ihr, die ihr glaubt, wenn ihr auf eine Schar stoßt, so bleibt fest und denkt eifrig an Allah, auf daß ihr erfolgreich sein moget"},
  {"surah_number": 8, "verse_number": 46, "content": "Und gehorcht Allah und Seinem Gesandten und hadert nicht miteinander, damit ihr nicht versaget und euch die Kampfkraft nicht verlaßt. Seid geduldig; wahrlich, Allah ist mit den Geduldigen"},
  {"surah_number": 8, "verse_number": 47, "content": "Und seid nicht wie jene, die prahlerisch und, um von den Leuten gesehen zu werden, aus ihren Wohnstatten auszogen, und die von Allahs Weg abhalten. Und Allah umfaßt das, was sie tun"},
  {
    "surah_number": 8,
    "verse_number": 48,
    "content":
        "Und da ließ Satan ihnen ihre Werke als wohlgefallig erscheinen und sagte: \"Keiner unter den Menschen soll heute etwas gegen euch ausrichten konnen, und ich bin eure Stutze.\" Als jedoch die beiden Heerscharen einander ansichtig wurden, da wandte er sich auf seinen Fersen um und sagte: \"Ich habe nichts mit euch zu schaffen; ich sehe, was ihr nicht seht. Ich furchte Allah; und Allah ist streng im Strafen"
  },
  {"surah_number": 8, "verse_number": 49, "content": "Da sagten die Heuchler und diejenigen, in deren Herzen Krankheit war: \"Ihr Glaube hat diese da hochmutig gemacht.\" Wer aber auf Allah vertraut - siehe, Allah ist Erhaben, Allweise"},
  {"surah_number": 8, "verse_number": 50, "content": "Konntest du nur sehen, wie die Engel die Seelen der Unglaubigen hinwegnehmen, wahrend sie ihnen Gesicht und Rucken schlagen und (sprechen): \"Kostet die Strafe des Verbrennens"},
  {"surah_number": 8, "verse_number": 51, "content": "Dies (geschieht) um dessentwillen, was eure Hande (euch) vorausgeschickt haben; und (wisset), daß Allah niemals ungerecht gegen die Diener ist"},
  {"surah_number": 8, "verse_number": 52, "content": "(Es wird ihnen) wie den Leuten Pharaos und denen (ergehen), die vor ihnen waren: Sie glaubten nicht an die Zeichen Allahs; darum bestrafte Allah sie fur ihre Sunden. Wahrlich, Allah ist Allmachtig und streng im Strafen"},
  {"surah_number": 8, "verse_number": 53, "content": "Dies (ist so), weil Allah niemals eine Gnade andern wurde, die Er einem Volk gewahrt hat, es sei denn, daß es seine eigene Einstellung anderte, und weil Allah Allhorend, Allwissend ist"},
  {
    "surah_number": 8,
    "verse_number": 54,
    "content": "(Es wird ihnen) wie den Leuten Pharaos und denen (ergehen), die vor ihnen waren: Sie hielten die Zeichen ihres Herrn fur eine Luge, darum ließen Wir sie zugrunde gehen um ihrer Sunden willen, und Wir ertrankten die Leute Pharaos; sie alle waren Frevler"
  },
  {"surah_number": 8, "verse_number": 55, "content": "Wahrlich, schlimmer als das Vieh sind bei Allah jene, die unglaubig sind und nicht glauben werden"},
  {"surah_number": 8, "verse_number": 56, "content": "es sind jene, mit denen du einen Bund geschlossen hast; dann brechen sie jedesmal ihren Bund, und sie furchten (Allah) nicht"},
  {"surah_number": 8, "verse_number": 57, "content": "Darum, wenn du sie im Kriege anpackst, verscheuche mit ihnen diejenigen, die hinter ihnen sind, auf daß sie ermahnt seien"},
  {"surah_number": 8, "verse_number": 58, "content": "Und wenn du von einem Volk Verrat furchtest, so verwirf (den) gegenseitigen (Vertrag) Wahrlich, Allah liebt nicht die Verrater"},
  {"surah_number": 8, "verse_number": 59, "content": "Laß die Unglaubigen nicht meinen, sie hatten (Uns) ubertroffen. Wahrlich, sie konnen nicht siegen"},
  {
    "surah_number": 8,
    "verse_number": 60,
    "content":
        "Und rustet gegen sie auf, soviel ihr an Streitmacht und Schlachtrossen aufbieten konnt, damit ihr Allahs Feind und euren Feind - und andere außer ihnen, die ihr nicht kennt abschreckt; Allah kennt sie (alle) Und was ihr auch fur Allahs Sache aufwendet, es wird euch voll zuruckgezahlt werden, und es soll euch kein Unrecht geschehen"
  },
  {"surah_number": 8, "verse_number": 61, "content": "Und wenn sie jedoch zum Frieden geneigt sind, so sei auch du ihm geneigt und vertraue auf Allah. Wahrlich, Er ist der Allhorende, der Allwissende"},
  {"surah_number": 8, "verse_number": 62, "content": "Wenn sie dich aber hintergehen wollen, dann laß es dir an Allah genugen. Er hat dich mit Seiner Hilfe und mit den Glaubigen gestarkt"},
  {
    "surah_number": 8,
    "verse_number": 63,
    "content": "Und Er hat zwischen ihren Herzen Freundschaft gestiftet. Hattest du auch alles aufgewandt, was auf Erden ist, du hattest doch nicht Freundschaft in ihre Herzen zu legen vermocht, Allah aber hat Freundschaft in sie gelegt. Wahrlich, Er ist Erhaben, Allweise"
  },
  {"surah_number": 8, "verse_number": 64, "content": "O Prophet, Allah soll dir vollauf genugen und denen, die dir folgen unter den Glaubigen"},
  {
    "surah_number": 8,
    "verse_number": 65,
    "content": "O Prophet, feuere die Glaubigen zum Kampf an. Sind auch nur zwanzig unter euch, die Geduld haben, so sollen sie zweihundert uberwaltigen; und sind einhundert unter euch, so werden sie eintausend von denen uberwaltigen, die unglaubig sind, weil das ein Volk ist, das nicht begreift"
  },
  {
    "surah_number": 8,
    "verse_number": 66,
    "content":
        "Jetzt aber hat Allah euch eure Burde erleichtert; denn Er weiß, daß ihr schwach seid. Wenn also unter euch einhundert sind, die Geduld haben, so sollen sie zweihundert uberwaltigen; und wenn eintausend unter euch sind, so sollen sie zweitausend mit der Erlaubnis Allahs uberwaltigen. Und Allah ist mit den Geduldigen"
  },
  {"surah_number": 8, "verse_number": 67, "content": "Einem Propheten geziemt es nicht, Gefangene zu (be-)halten, sofern er nicht heftig auf dieser Erde gekampft hat. Ihr wollt die Guter dieser Welt, Allah aber will (fur euch) das Jenseits. Und Allah ist Erhaben, Allweise"},
  {"surah_number": 8, "verse_number": 68, "content": "Ware nicht schon eine Bestimmung von Allah dagewesen, so hatte euch gewiß eine schwere Strafe getroffen um dessentwillen, was ihr (euch) genommen hattet"},
  {"surah_number": 8, "verse_number": 69, "content": "So esset von dem, was ihr erbeutet habt, soweit es erlaubt und gut ist, und furchtet Allah. Wahrlich, Allah ist Allvergebend, Barmherzig"},
  {
    "surah_number": 8,
    "verse_number": 70,
    "content": "O Prophet, sprich zu den (Kriegs-) Gefangenen, die in euren Handen sind: \"Erkennt Allah Gutes in euren Herzen, dann wird Er euch (etwas) Besseres geben als das, was euch genommen wurde, und wird euch vergeben. Denn Allah ist Allvergebend, Barmherzig"
  },
  {"surah_number": 8, "verse_number": 71, "content": "Wenn sie aber Verrat an dir uben wollen, so haben sie schon zuvor an Allah Verrat geubt. Er aber gab (dir) Macht uber sie; und Allah ist Allwissend, Allweise"},
  {
    "surah_number": 8,
    "verse_number": 72,
    "content":
        "Wahrlich, diejenigen, die geglaubt haben und ausgewandert sind und mit ihrem Gut und ihrem Blut fur Allahs Sache gekampft haben, und jene, die (ihnen) Herberge und Hilfe gaben - diese sind einander Freund. Fur den Schutz derjenigen aber, die glaubten, jedoch nicht ausgewandert sind, seid ihr keineswegs verantwortlich, sofern sie (nicht doch noch) auswandern. Suchen sie aber eure Hilfe fur den Glauben, dann ist das Helfen eure Pflicht, (es sei denn, sie bitten euch) gegen ein Volk (um Hilfe), zwischen dem und euch ein Bundnis besteht. Und Allah sieht euer Tun"
  },
  {"surah_number": 8, "verse_number": 73, "content": "Und die Unglaubigen - (auch) sie sind einander Beschutzer. Wenn ihr das nicht tut, wird Verwirrung im Lande und gewaltiges Unheil entstehen"},
  {
    "surah_number": 8,
    "verse_number": 74,
    "content": "Und diejenigen, die geglaubt haben und ausgewandert sind und fur Allahs Sache gekampft haben, und jene, die (ihnen) Herberge und Hilfe gaben - diese sind in der Tat wahre Glaubige. Ihnen wird Vergebung und eine ehrenvolle Versorgung zuteil sein"
  },
  {
    "surah_number": 8,
    "verse_number": 75,
    "content": "Und die, welche hernach glauben und auswandern und (fur Allahs Sache) an eurer Seite kampfen werden - sie gehoren zu euch; und (unter) Blutsverwandten stehen sich im Buche Allahs die einen naher als die anderen. Wahrlich, Allah weiß wohl alle Dinge"
  },
  {"surah_number": 9, "verse_number": 1, "content": "(Dies ist) eine Lossprechung (von jeglicher Verpflichtung) seitens Allahs und Seines Gesandten; (sie ist) an diejenigen Gotzendiener (gerichtet), mit denen ihr ein Bundnis abgeschlossen habt"},
  {"surah_number": 9, "verse_number": 2, "content": "So zieht denn vier Monate lang im Lande umher und wisset, daß ihr Allahs (Plan) nicht zuschanden machen konnt und daß Allah die Unglaubigen demutigen wird"},
  {
    "surah_number": 9,
    "verse_number": 3,
    "content":
        "Und (dies ist) eine Ankundigung von Allah und Seinem Gesandten an die Menschen am Tage der großen Pilgerfahrt, daß Allah der Gotzendiener ledig ist und ebenso Sein Gesandter. Bereut ihr also, so wird das besser fur euch sein; kehrt ihr euch jedoch ab, so wisset, daß ihr Allahs (Plan) nicht zuschanden machen konnt. Und verheiße denen schmerzliche Strafe, die unglaubig sind"
  },
  {
    "surah_number": 9,
    "verse_number": 4,
    "content":
        "Davon sind diejenigen Gotzendiener ausgenommen, mit denen ihr einen Vertrag eingegangen seid und die es euch an nichts haben fehlen lassen und die keine anderen gegen euch unterstutzt haben. Diesen gegenuber haltet den Vertrag bis zum Ablauf der Frist ein. Wahrlich, Allah liebt diejenigen, die (Ihn) furchten"
  },
  {
    "surah_number": 9,
    "verse_number": 5,
    "content":
        "Und wenn die heiligen Monate abgelaufen sind, dann totet die Gotzendiener, wo immer ihr sie findet, und ergreift sie und belagert sie und lauert ihnen aus jedem Hinterhalt auf. Wenn sie aber bereuen und das Gebet verrichten und die Zakah entrichten, dann gebt ihnen den Weg frei. Wahrlich, Allah ist Allvergebend, Barmherzig"
  },
  {"surah_number": 9, "verse_number": 6, "content": "und wenn einer der Gotzendiener bei dir Schutz sucht, dann gewahre ihm Schutz, bis er Allahs Worte vernehmen kann; hierauf lasse ihn den Ort seiner Sicherheit erreichen. Dies (soll so sein), weil sie ein unwissendes Volk sind"},
  {
    "surah_number": 9,
    "verse_number": 7,
    "content":
        "Wie kann es einen Vertrag geben zwischen den Gotzendienern und Allah und Seinem Gesandten - allein die ausgenommen, mit denen ihr bei der heiligen Moschee ein Bundnis eingingt -? Solange diese euch die Treue halten, haltet ihnen die Treue. Wahrlich, Allah liebt diejenigen, die (Ihn) furchten"
  },
  {"surah_number": 9, "verse_number": 8, "content": "Wie? Wurden sie doch, wenn sie euch besiegten, weder Bindungen noch Verpflichtungen euch gegenuber einhalten! Sie wurden euch mit dem Munde gefallig sein, indes ihre Herzen sich weigerten; und die meisten von ihnen sind Frevler"},
  {"surah_number": 9, "verse_number": 9, "content": "Sie verkaufen Allahs Zeichen fur einen geringen Preis und halten von Seinem Weg ab. Ubel ist wahrlich, was sie tun"},
  {"surah_number": 9, "verse_number": 10, "content": "Sie achten keine Bindung und keine Verpflichtung gegenuber einem Glaubigen; und sie sind die Ubertreter"},
  {"surah_number": 9, "verse_number": 11, "content": "Bereuen sie aber und verrichten sie das Gebet und entrichten sie die Zakah, so sind sie eure Bruder im Glauben. Und Wir machen die Zeichen klar fur die wissenden Leute"},
  {"surah_number": 9, "verse_number": 12, "content": "Wenn sie aber nach ihrem Vertrag ihre Eide brechen und euren Glauben angreifen, dann bekampft die Anfuhrer des Unglaubens - sie halten ja keine Eide -, so daß sie (davon) ablassen"},
  {
    "surah_number": 9,
    "verse_number": 13,
    "content": "Wollt ihr nicht gegen Leute kampfen, die ihre Eide gebrochen haben und die den Gesandten zu vertreiben planten - sie waren es ja, die euch zuerst angegriffen haben -? Furchtet ihr sie etwa? Allahs Wurde geziemt es eher, daß ihr Ihn furchtet, wenn ihr Glaubige seid"
  },
  {"surah_number": 9, "verse_number": 14, "content": "Bekampft sie; so wird Allah sie durch eure Hand bestrafen und demutigen und euch gegen sie helfen und den Herzen eines glaubigen Volkes Heilung bringen"},
  {"surah_number": 9, "verse_number": 15, "content": "und Er wird die Wut aus ihren Herzen bannen. Und Allah kehrt Sich gnadig dem zu, dem Er will. Und Allah ist Allwissend, Allweise"},
  {
    "surah_number": 9,
    "verse_number": 16,
    "content": "Meint ihr etwa, ihr wurdet verlassen sein, wo Allah doch noch nicht jene von euch gezeichnet hat, die (in Seiner Sache) kampfen und sich keinen zum Vertrauten nehmen außer Allah und Seinen Gesandten und die Glaubigen? Und Allah weiß recht wohl, was ihr tut"
  },
  {"surah_number": 9, "verse_number": 17, "content": "Die Gotzendiener durfen keine Erhaltung der Moscheen Allahs vornehmen, solange sie gegen sich selbst den Unglauben bezeugen. Sie sind es, deren Werke nichtig sein sollen, und sie mussen auf ewig im Feuer bleiben"},
  {
    "surah_number": 9,
    "verse_number": 18,
    "content": "Wahrlich, der allein vermag die Erhaltung der Moscheen Allahs vorzunehmen, der an Allah und an den Jungsten Tag glaubt und das Gebet verrichtet und die Zakah entrichtet und keinen außer Allah furchtet: diese also mogen unter denen sein, welche den rechten Weg finden"
  },
  {
    "surah_number": 9,
    "verse_number": 19,
    "content": "Wollt ihr etwa die Trankung der Pilger und die Erhaltung der heiligen Moschee (den Werken) dessen gleichsetzen, der an Allah und an den Jungsten Tag glaubt und auf Allahs Weg kampft? Vor Allah sind sie nicht gleich. Und Allah weist nicht den ungerechten Leuten den Weg"
  },
  {"surah_number": 9, "verse_number": 20, "content": "Diejenigen, die glauben und auswandern und mit ihrem Gut und ihrem Blut fur Allahs Sache kampfen, nehmen den hochsten Rang bei Allah ein; und sie sind es, die gewinnen werden"},
  {"surah_number": 9, "verse_number": 21, "content": "Ihr Herr verheißt ihnen Seine Barmherzigkeit und Sein Wohlgefallen und Garten, in deren ewiger Wonne sie sein werden"},
  {"surah_number": 9, "verse_number": 22, "content": "Dort werden sie auf ewig und immerdar verweilen. Wahrlich, bei Allah ist ein riesiger Lohn"},
  {"surah_number": 9, "verse_number": 23, "content": "O ihr, die ihr glaubt, nehmt nicht eure Vater und eure Bruder zu Beschutzern, wenn sie den Unglauben dem Glauben vorziehen. Und diejenigen von euch, die sie zu Beschutzern nehmen das sind die Ungerechten"},
  {
    "surah_number": 9,
    "verse_number": 24,
    "content":
        "Sprich: \"Wenn eure Vater und eure Sohne und eure Bruder und eure Frauen und eure Verwandten und das Vermogen, das ihr euch erworben habt, und der Handel, dessen Niedergang ihr furchtet, und die Wohnstatten, die ihr liebt, euch lieber sind als Allah und Sein Gesandter und das Kampfen fur Seine Sache, dann wartet, bis Allah mit Seiner Entscheidung kommt; und Allah weist den Ungehorsamen nicht den Weg"
  },
  {"surah_number": 9, "verse_number": 25, "content": "Wahrlich, Allah half euch schon an vielen Orten zum Sieg, und am Tage von Hunain, als eure große Zahl euch stolz machte - doch sie nutzte euch nichts, und die Erde wurde euch in ihrer Weite eng - da wandtet ihr euch zur Flucht"},
  {"surah_number": 9, "verse_number": 26, "content": "Dann sandte Allah Seinen Frieden auf Seinen Gesandten und auf die Glaubigen herab und sandte Heerscharen hernieder, die ihr nicht saht, und strafte jene, die unglaubig waren. Das ist der Lohn der Unglaubigen"},
  {"surah_number": 9, "verse_number": 27, "content": "Doch hernach kehrt Sich Allah gnadig dem zu, dem Er will; und Allah ist Allvergebend, Barmherzig"},
  {
    "surah_number": 9,
    "verse_number": 28,
    "content":
        "O ihr, die ihr glaubt! Wahrlich, die Gotzendiener sind unrein. Darum durfen sie sich nach diesem ihrem Jahr der heiligen Moschee nicht nahern. Und falls ihr Armut befurchtet, so wird euch Allah gewiß aus Seiner Fulle reich machen, wenn Er will. Wahrlich, Allah ist Allwissend, Allweise"
  },
  {
    "surah_number": 9,
    "verse_number": 29,
    "content":
        "Kampft gegen diejenigen, die nicht an Allah und an den Jungsten Tag glauben, und die das nicht fur verboten erklaren, was Allah und Sein Gesandter fur verboten erklart haben, und die nicht dem wahren Glauben folgen von denen, die die Schrift erhalten haben, bis sie eigenhandig den Tribut in voller Unterwerfung entrichten"
  },
  {"surah_number": 9, "verse_number": 30, "content": "Und die Juden sagen, Esra sei Allahs Sohn, und die Christen sagen, der Messias sei Allahs Sohn. Das ist das Wort aus ihrem Mund. Sie ahmen die Rede derer nach, die vordem unglaubig waren. Allahs Fluch uber sie! Wie sind sie (doch) irregeleitet"},
  {
    "surah_number": 9,
    "verse_number": 31,
    "content": "Sie haben sich ihre Schriftgelehrten und Monche zu Herren genommen außer Allah; und den Messias, den Sohn der Maria. Und doch war ihnen geboten worden, allein den Einzigen Gott anzubeten. Es ist kein Gott außer Ihm. Gepriesen sei Er uber das, was sie (Ihm) zur Seite stellen"
  },
  {"surah_number": 9, "verse_number": 32, "content": "Sie wollten Allahs Licht mit ihrem Munde ausloschen; jedoch Allah will nichts anderes, als Sein Licht zu vollenden; mag es den Unglaubigen auch zuwider sein"},
  {"surah_number": 9, "verse_number": 33, "content": "Er ist es, Der Seinen Gesandten mit der Fuhrung und der wahren Religion geschickt hat, auf daß Er sie uber alle (anderen) Religionen siegen lasse; mag es den Gotzendienern auch zuwider sein"},
  {
    "surah_number": 9,
    "verse_number": 34,
    "content": "O ihr, die ihr glaubt, wahrlich, viele der Schriftgelehrten und Monche verzehren das Gut der Menschen zu Unrecht und wenden sie von Allahs Weg ab. Und jenen, die Gold und Silber horten und es nicht fur Allahs Weg verwenden ihnen verheiße schmerzliche Strafe"
  },
  {
    "surah_number": 9,
    "verse_number": 35,
    "content": "An dem Tage, wo es (Gold und Silber) im Feuer der Gahannam gluhend gemacht wird und ihre Stirnen und ihre Seiten und ihre Rucken damit gebrandmarkt werden (, wird ihnen gesagt): \"Dies ist, was ihr fur euch selbst gehortet habt; kostet nun, was ihr zu horten pflegtet"
  },
  {
    "surah_number": 9,
    "verse_number": 36,
    "content":
        "Wahrlich, die Zahl der Monate bei Allah betragt zwolf Monate; (so sind sie) im Buche Allahs (festgelegt worden) seit dem Tage, da Er die Himmel und die Erde erschuf. Von diesen (Monaten) sind vier heilig. Das ist der bestandige Glaube. Darum versundigt euch nicht in diesen (Monaten) Und bekampft die Gotzendiener allesamt, wie sie euch allesamt bekampfen; und wisset, daß Allah mit denjenigen ist, die Ihn furchten"
  },
  {
    "surah_number": 9,
    "verse_number": 37,
    "content":
        "Das Verschieben (eines heiligen Monats) ist nur eine Steigerung des Unglaubens. Die Unglaubigen werden dadurch irregefuhrt. Sie erlauben es in einem Jahr und verbieten es in einem anderen Jahr, damit sie eine Ubereinstimmung in der Anzahl (der Monate) erreichen, die Allah heilig gemacht hat, und so erlauben sie das, was Allah verwehrt hat. Das Bose ihrer Taten wird ihnen schon vor Augen gefuhrt. Doch Allah weist dem unglaubigen Volk nicht den Weg"
  },
  {
    "surah_number": 9,
    "verse_number": 38,
    "content":
        "O ihr, die ihr glaubt, was ist mit euch, daß ihr euch schwer zur Erde sinken lasset, wenn euch gesagt wird: \"Zieht aus auf Allahs Weg\"? Wurdet ihr euch denn mit dem diesseitigen Leben statt mit jenem im Jenseits zufrieden geben? Doch der Genuß des irdischen Lebens ist gar gering, verglichen mit dem des Jenseits"
  },
  {"surah_number": 9, "verse_number": 39, "content": "Wenn ihr nicht auszieht, wird Er euch mit schmerzlicher Strafe bestrafen und wird an eurer Stelle ein anderes Volk erwahlen, und ihr werdet Ihm gewiß keinen Schaden zufugen. Und Allah hat Macht uber alle Dinge"},
  {
    "surah_number": 9,
    "verse_number": 40,
    "content":
        "Wenn ihr ihm nicht helft, so (wisset, daß) Allah ihm damals half, als die Unglaubigen ihn vertrieben haben -, wie sie da beide in der Hohle waren und er zu seinem Begleiter sagte: \"Sei nicht traurig; denn Allah ist mit uns.\" Da ließ Allah Seinen Frieden auf ihn herab und starkte ihn mit Heerscharen, die ihr nicht saht, und erniedrigte das Wort der Unglaubigen; und Allahs Wort allein ist das hochste. Und Allah ist Erhaben, Allweise"
  },
  {"surah_number": 9, "verse_number": 41, "content": "Zieht aus, leicht und schwer, und kampft mit eurem Gut und mit eurem Blut fur Allahs Sache! Das ist besser fur euch, wenn ihr es nur wußtet"},
  {
    "surah_number": 9,
    "verse_number": 42,
    "content":
        "Hatte es sich um einen nahen Gewinn und um eine kurze Reise gehandelt, waren sie dir gewiß gefolgt, doch die schwere Reise schien ihnen zu lang. Und doch werden sie bei Allah schworen: \"Hatten wir es vermocht, waren wir sicherlich mit euch ausgezogen.\" Sie fugen sich selbst Schaden zu; und Allah weiß, daß sie Lugner sind"
  },
  {"surah_number": 9, "verse_number": 43, "content": "Allah verzeihet dir! Warum erlaubtest du ihnen (zuruckzubleiben), bis die, welche die Wahrheit sagten, dir bekannt wurden und du die Lugner erkanntest"},
  {"surah_number": 9, "verse_number": 44, "content": "Diejenigen, die an Allah und an den Jungsten Tag glauben, bitten dich nicht um Erlaubnis, nicht mit ihrem Gut und ihrem Blut kampfen zu mussen, und Allah kennt diejenigen recht wohl, die (Ihn) furchten"},
  {"surah_number": 9, "verse_number": 45, "content": "Nur die werden dich um Erlaubnis bitten, (dem Kampf fernzubleiben,) die nicht an Allah und an den Jungsten Tag glauben und deren Herzen voller Zweifel sind; und in ihrem Zweifel schwanken sie"},
  {"surah_number": 9, "verse_number": 46, "content": "Waren sie aber zum Ausmarsch entschlossen gewesen, hatten sie sich doch gewiß fur ihn gerustet; doch Allah wollte ihren Abmarsch nicht. So hielt Er sie zuruck, und es wurde gesagt: \"Sitzet (daheim) bei den Sitzenden"},
  {"surah_number": 9, "verse_number": 47, "content": "Waren sie mit euch ausgezogen, hatten sie nur eure Sorgen vermehrt und waren in eurer Mitte hin- und hergelaufen und hatten Zwietracht unter euch erregt. Und unter euch sind manche, die auf sie gehort hatten, aber Allah kennt die Frevler wohl"},
  {"surah_number": 9, "verse_number": 48, "content": "Schon vorher trachteten sie nach Verwirrung und schmiedeten Plane gegen dich, bis die Wahrheit kam und Allahs Wille durchgesetzt wurde, obgleich es ihnen zuwider war"},
  {"surah_number": 9, "verse_number": 49, "content": "Und unter ihnen ist so mancher, der sagt: \"Erlaube mir (zuruckzubleiben), und stelle mich nicht auf die Probe.\" Hort! Ihre Probe hat sie ja schon ereilt. Und wahrlich, Gahannam wird die Unglaubigen einschließen"},
  {"surah_number": 9, "verse_number": 50, "content": "Geschieht dir etwas Gutes, so betrubt es sie; doch wenn dich ein Unheil trifft, sagen sie: \"Wir hatten uns ja schon vorher abgesichert.\" Und sie wenden sich voller Freude ab"},
  {"surah_number": 9, "verse_number": 51, "content": "Sprich: \"Nichts kann uns treffen außer dem, was Allah uns bestimmt hat. Er ist unser Beschutzer. Und auf Allah sollen die Glaubigen vertrauen"},
  {
    "surah_number": 9,
    "verse_number": 52,
    "content":
        "Sprich: \"Ihr wartet darauf, daß uns nur eines der beiden guten Dinge ereilt, wahrend wir, was euch betrifft, darauf warten, daß Allah euch mit einer Strafe treffen wird, die entweder von Ihm Selbst oder durch unsere Hande (uber euch kommen wird) Wartet denn ab, und wir werden mit euch abwarten"
  },
  {"surah_number": 9, "verse_number": 53, "content": "Sprich: \"Spendet willig oder unwillig, es wird doch nicht von euch angenommen. Denn wahrlich, ihr seid frevelhafte Leute"},
  {"surah_number": 9, "verse_number": 54, "content": "Ihre Spenden werden nur deshalb nicht angenommen, weil sie nicht an Allah und an Seinen Gesandten glauben und nur trage zum Gebet kommen und ihre Spenden nur widerwillig geben"},
  {"surah_number": 9, "verse_number": 55, "content": "Wundere dich weder uber ihr Gut noch uber ihre Kinder. Allah will sie damit nur im irdischen Leben bestrafen, und ihre Seelen sollen verscheiden, wahrend sie (noch) unglaubig sind"},
  {"surah_number": 9, "verse_number": 56, "content": "Und sie schworen bei Allah, daß sie wahrhaftig zu euch gehoren; doch sie gehoren nicht zu euch, sondern sie sind angstliche Leute"},
  {"surah_number": 9, "verse_number": 57, "content": "Konnten sie nur einen Zufluchtsort finden oder Hohlen oder ein Schlupfloch, wurden sie gewiß in wilder Hast dorthin eilen"},
  {"surah_number": 9, "verse_number": 58, "content": "Unter ihnen sind jene, die dir wegen der Almosen Vorwurfe machen. Erhalten sie welche, so sind sie zufrieden; erhalten sie aber keine, siehe, dann sind sie verdrossen"},
  {
    "surah_number": 9,
    "verse_number": 59,
    "content": "Waren sie mit dem zufrieden gewesen, was Allah und Sein Gesandter ihnen gegeben hatten, und hatten sie nur gesagt: \"Wir lassen uns an Allah genugen: Allah wird uns aus Seiner Fulle geben und ebenso Sein Gesandter. Zu Allah wollen wir uns als Bittende begeben"
  },
  {
    "surah_number": 9,
    "verse_number": 60,
    "content":
        "Wahrlich, die Almosen sind nur fur die Armen und Bedurftigen und fur die mit der Verwaltung (der Almosen) Beauftragten und fur die, deren Herzen gewonnen werden sollen, fur die (Befreiung von) Sklaven und fur die Schuldner, fur die Sache Allahs und fur den Sohn des Weges; (dies ist) eine Vorschrift von Allah. Und Allah ist Allwissend, Allweise"
  },
  {
    "surah_number": 9,
    "verse_number": 61,
    "content":
        "Und unter ihnen sind jene, die den Propheten kranken und sagen: \"Er hort (auf alles)\" Sprich: \"Er hort fur euch nur auf das Gute: Er glaubt an Allah und vertraut den Glaubigen und erweist denen unter euch Barmherzigkeit, die glaubig sind.\" Und denen, die den Gesandten Allahs kranken, wird eine schmerzliche Strafe zuteil sein"
  },
  {"surah_number": 9, "verse_number": 62, "content": "Sie schworen euch bei Allah, um euch zufriedenzustellen; jedoch Allahs und Seines Gesandten Wurde geziemt es mehr, Ihm zu gefallen, wenn sie glaubig sind"},
  {"surah_number": 9, "verse_number": 63, "content": "Wissen sie denn nicht, daß fur den, der Allah und Seinem Gesandten zuwiderhandelt, das Feuer der Gahannam bestimmt ist? Darin wird er auf ewig bleiben; das ist die große Demutigung"},
  {"surah_number": 9, "verse_number": 64, "content": "Die Heuchler furchten, es konnte gegen sie eine Sura herabgesandt werden, die ihnen ankundet, was in ihren Herzen ist. Sprich: \"Spottet nur! Allah wird alles ans Licht bringen, wovor ihr euch furchtet"},
  {"surah_number": 9, "verse_number": 65, "content": "Und wenn du sie fragst, so werden sie gewiß sagen: \"Wir plauderten nur und scherzten.\" Sprich: \"Galt euer Spott etwa Allah und Seinen Zeichen und Seinem Gesandten"},
  {"surah_number": 9, "verse_number": 66, "content": "Versucht euch nicht zu entschuldigen. Ihr seid unglaubig geworden, nachdem ihr geglaubt habt. Wenn Wir einem Teil von euch vergeben, so bestrafen (Wir) den anderen Teil deshalb, weil sie Sunder waren"},
  {"surah_number": 9, "verse_number": 67, "content": "Die Heuchler und Heuchlerinnen gehoren zueinander. Sie gebieten das Bose und verbieten das Gute; und ihre Hande bleiben geschlossen. Sie haben Allah vergessen, und so hat Er sie vergessen. Wahrlich, die Heuchler sind wahre Frevler"},
  {"surah_number": 9, "verse_number": 68, "content": "Allah hat den Heuchlern und Heuchlerinnen und den Unglaubigen das Feuer der Gahannam versprochen; darin werden sie auf ewig bleiben. Das wird genug fur sie sein. Und Allah hat sie verflucht, und ihnen wird eine dauernde Strafe zuteil sein"},
  {
    "surah_number": 9,
    "verse_number": 69,
    "content":
        "wie jenen, die vor euch waren. Sie waren machtiger als ihr an Kraft und reicher an Gut und Kindern. Sie erfreuten sich ihres Loses; auch ihr habt euch eures Loses erfreut, gerade so wie jene vor euch sich ihres Loses erfreuten. Und ihr ergotztet euch an mußiger Rede, wie jene sich an mußiger Rede ergotzten. Ihre Werke sollen ihnen nichts fruchten, weder in dieser Welt noch im Jenseits. Und sie sind die Verlierer"
  },
  {
    "surah_number": 9,
    "verse_number": 70,
    "content":
        "Hat sie nicht die Kunde von denen erreicht, die vor ihnen waren vom Volke Noahs, von `Ad und Tamud und vom Volke Abrahams und den Bewohnern Madyans und der zusammengesturzten Stadte? Ihre Gesandten kamen mit deutlichen Zeichen zu ihnen. Allah also wollte ihnen kein Unrecht tun, doch sie taten sich selber Unrecht"
  },
  {
    "surah_number": 9,
    "verse_number": 71,
    "content":
        "Und die glaubigen Manner und die glaubigen Frauen sind einer des anderen Freund: Sie gebieten das Gute und verbieten das Bose und verrichten das Gebet und entrichten die Zakah und gehorchen Allah und Seinem Gesandten. Sie sind es, derer Allah Sich erbarmen wird. Wahrlich, Allah ist Erhaben, Allweise"
  },
  {
    "surah_number": 9,
    "verse_number": 72,
    "content": "Allah hat den glaubigen Mannern und den glaubigen Frauen verheißen, immerdar in Garten zu verweilen, die von Bachen durchflossen werden, und (hat ihnen) herrliche Wohnstatten in den Garten von Eden (verheißen) Allahs Wohlgefallen aber ist noch großer. Das ist der gewaltige Gewinn"
  },
  {"surah_number": 9, "verse_number": 73, "content": "O Prophet, kampfe gegen die Unglaubigen und die Heuchler. Und sei streng mit ihnen. Ihre Herberge ist Gahannam, und schlimm ist das Ende"},
  {
    "surah_number": 9,
    "verse_number": 74,
    "content":
        "Sie schworen bei Allah, daß sie nichts gesagt hatten, doch sie fuhrten unzweifelhaft lasterliche Rede, und sie fielen in den Unglauben zuruck, nachdem sie den Islam angenommen hatten. Sie begehrten das, was sie nicht erreichen konnten. Und sie nahrten nur darum Haß, weil Allah und Sein Gesandter - sie in Seiner Huld reich gemacht hatten. Wenn sie nun bereuen, so wird es besser fur sie sein; wenden sie sich jedoch (vom Glauben) ab, so wird Allah sie in dieser Welt und im Jenseits mit schmerzlicher Strafe bestrafen, und sie haben auf Erden weder Freund noch Helfer"
  },
  {"surah_number": 9, "verse_number": 75, "content": "Und unter ihnen sind so manche, die Allah versprachen: \"Wenn Er uns aus Seiner Fulle gibt, dann wollen wir bestimmt Almosen geben und rechtschaffen sein"},
  {"surah_number": 9, "verse_number": 76, "content": "Doch als Er ihnen dann aus Seiner Fulle gab, geizten sie damit und wandten sich in Abneigung ab"},
  {"surah_number": 9, "verse_number": 77, "content": "Zur Vergeltung pflanzte Er Heuchelei in ihre Herzen. (Sie wahrt) bis zu dem Tage, an dem sie Ihm begegnen werden, weil sie Allah nicht gehalten haben, was sie Ihm versprochen hatten, und weil sie gelogen hatten"},
  {"surah_number": 9, "verse_number": 78, "content": "Wußten sie denn nicht, daß Allah ihre Geheimnisse und ihre vertraulichen Beratungen kennt, und daß Allah der Kenner des Verborgenen ist"},
  {
    "surah_number": 9,
    "verse_number": 79,
    "content": "Diejenigen, die da jene Glaubigen schelten, die freiwillig Almosen geben, wie auch jene, die nichts (zu geben) finden als ihre eigene Leistung, und sie deswegen verhohnen, denen wird Allah ihren Hohn vergelten, und ihnen wird eine schmerzliche Strafe zuteil sein"
  },
  {
    "surah_number": 9,
    "verse_number": 80,
    "content":
        "Ob du fur sie um Verzeihung bittest oder nicht um Verzeihung fur sie bittest, oder ob du siebzigmal fur sie um Verzeihung bittest, Allah wird ihnen niemals verzeihen. Deshalb, weil sie nicht an Allah und Seinen Gesandten glaubten. Und Allah weist den frevelhaften Leuten nicht den Weg"
  },
  {
    "surah_number": 9,
    "verse_number": 81,
    "content":
        "Jene, die zuruckgelassen worden waren, freuten sich ihres Daheimbleibens hinter dem (Rucken des) Gesandten Allahs und waren nicht geneigt, mit ihrem Gut und mit ihrem Blut fur Allahs Sache zu kampfen. Sie sagten: \"Zieht doch nicht in der Hitze aus.\" Sprich: \"Das Feuer der Gahannam ist von starkerer Hitze.\" Wenn sie doch nur begreifen konnten"
  },
  {"surah_number": 9, "verse_number": 82, "content": "Sie sollten wenig lachen und viel weinen uber das, was sie sich erworben haben"},
  {
    "surah_number": 9,
    "verse_number": 83,
    "content":
        "Und wenn Allah dich zu einer Gruppe von ihnen heimkehren laßt und sie dich um Erlaubnis bitten, auszuziehen, dann sprich: \"Nie sollt ihr mit mir ausziehen und nie einen Feind an meiner Seite bekampfen. Es gefiel euch, das erste Mal daheim sitzen zu bleiben, so sitzet nun (wieder) bei denen, die zuruckbleiben"
  },
  {"surah_number": 9, "verse_number": 84, "content": "Und bete nie fur einen von ihnen, der stirbt, noch stehe an seinem Grabe; (denn) sie glaubten nicht an Allah und an Seinen Gesandten, und sie starben als Frevler"},
  {"surah_number": 9, "verse_number": 85, "content": "Wundere dich weder uber ihr Gut noch uber ihre Kinder. Allah will sie damit nur in dieser Welt bestrafen, und ihre Seelen sollen verscheiden, wahrend sie noch unglaubig sind"},
  {"surah_number": 9, "verse_number": 86, "content": "Und wenn eine Sura hinabgesandt wird (des Inhalts): \"Glaubt an Allah und kampft an der Seite Seines Gesandten\", dann bitten dich die Reichen unter ihnen um Erlaubnis und sagen: \"Laß uns bei denen sein, die daheim bleiben"},
  {"surah_number": 9, "verse_number": 87, "content": "Sie sind damit zufrieden, bei den Zuruckbleibenden zu sein, und ihre Herzen sind versiegelt, so daß sie nichts begreifen"},
  {"surah_number": 9, "verse_number": 88, "content": "Jedoch der Gesandte und die Glaubigen mit ihm, die mit ihrem Gut und mit ihrem Blut kampfen, sind es, denen Gutes zuteil werden soll; und sie sind es, die Erfolg haben werden"},
  {"surah_number": 9, "verse_number": 89, "content": "Allah hat Garten fur sie bereitet, durch welche Bache fließen; darin sollen sie auf ewig verweilen. Das ist die große Gluckseligkeit"},
  {
    "surah_number": 9,
    "verse_number": 90,
    "content": "Und es kamen solche Wustenaraber, die Ausreden gebrauchten, um (vom Kampf) ausgenommen zu werden; und jene blieben (daheim), die falsch gegen Allah und Seinen Gesandten waren. Wahrlich, getroffen von einer schmerzlichen Strafe werden jene unter ihnen sein, die unglaubig sind"
  },
  {"surah_number": 9, "verse_number": 91, "content": "Kein Tadel trifft die Schwachen und die Kranken und diejenigen, die nichts zum Ausgeben finden, wenn sie nur gegen Allah und Seinen Gesandten aufrichtig sind. Kein Vorwurf trifft jene, die Gutes tun und Allah ist Allverzeihend, Barmherzig"},
  {
    "surah_number": 9,
    "verse_number": 92,
    "content":
        "Noch (trifft) jene (ein Tadel), die zu dir kamen, damit du ihnen die Moglichkeit zu reiten verschafftest, und (zu denen) du sagtest: \"Ich kann nichts finden, womit ich euch beritten machen konnte.\" Da kehrten sie um, wahrend ihre Augen von Tranen uberflossen aus Kummer daruber, daß sie nichts fanden, was sie hatten ausgeben konnen"
  },
  {"surah_number": 9, "verse_number": 93, "content": "Ein Vorwurf trifft nur jene, die dich um Erlaubnis bitten, obwohl sie reich sind. Sie sind damit zufrieden, bei den Zuruckbleibenden zu sein. Allah hat ein Siegel auf ihre Herzen gelegt, so daß sie kein Wissen haben"},
  {
    "surah_number": 9,
    "verse_number": 94,
    "content":
        "Sie werden euch Entschuldigungen vorbringen, wenn ihr zu ihnen zuruckkehrt. Sprich: \"Bringt keine Entschuldigungen vor; wir glauben euch doch nicht. Allah hat uns schon uber eure Angelegenheit belehrt. Allah und Sein Gesandter werden auf euer Tun schauen; dann werdet ihr zu dem Kenner des Verborgenen und des Offenbaren zuruckgebracht werden, und Er wird euch alles verkunden, was ihr zu tun pflegtet"
  },
  {
    "surah_number": 9,
    "verse_number": 95,
    "content": "Sie werden vor euch bei Allah schworen, wenn ihr zu ihnen zuruckkehrt, daß ihr sie sich selbst uberlassen sollt. Uberlaßt sie also sich selbst. Sie sind eine Plage, und ihre Herberge ist Gahannam, als Entgelt fur das, was sie sich selbst erwarben"
  },
  {"surah_number": 9, "verse_number": 96, "content": "Sie werden euch schworen, daß ihr mit ihnen wohl zufrieden sein konntet. Doch waret ihr auch mit ihnen zufrieden, Allah wurde doch nicht mit einer Schar von Frevlern zufrieden sein"},
  {"surah_number": 9, "verse_number": 97, "content": "Die Wustenaraber sind am hartesten in Unglauben und Heuchelei und sind eher dazu geneigt, die Schranken nicht anzuerkennen, die Allah Seinem Gesandten offenbart hat. Und Allah ist Allwissend, Allweise"},
  {"surah_number": 9, "verse_number": 98, "content": "Und unter den Wustenarabern sind so manche, die das, was sie spenden, als eine erzwungene Buße ansehen; und sie warten nur auf euer Mißgeschick. Allein sie selbst wird ein unheilvolles Mißgeschick treffen. Und Allah ist Allhorend, Allwissend"},
  {
    "surah_number": 9,
    "verse_number": 99,
    "content":
        "Doch unter den Wustenarabern sind auch solche, die an Allah und an den Jungsten Tag glauben und die das, was sie spenden, als ein Mittel betrachten, sich Allah zu nahern und die Segnungen des Propheten (zu empfangen) Wahrlich, fur sie ist es ein Mittel der Annaherung. Allah wird sie bald in Seine Barmherzigkeit einfuhren. Wahrlich, Allah ist Allvergebend, Barmherzig"
  },
  {
    "surah_number": 9,
    "verse_number": 100,
    "content":
        "Die Allerersten, die ersten der Auswanderer und der Helfer und jene, die ihnen auf die beste Art gefolgt sind - mit ihnen ist Allah wohl zufrieden und sie sind wohl zufrieden mit Ihm; und Er hat ihnen Garten vorbereitet, durch welche Bache fließen. Darin sollen sie verweilen auf ewig und immerdar. Das ist der gewaltige Gewinn"
  },
  {
    "surah_number": 9,
    "verse_number": 101,
    "content": "Und unter den Wustenarabern, die in eurer Gegend wohnen, gibt es auch Heuchler, wie im Volk von Madina. Sie sind verstockt in ihrer Heuchelei. Du kennst sie nicht; Wir aber kennen sie. Wir werden sie zweimal bestrafen. Hierauf werden sie einer gewaltigen Strafe zugewiesen werden"
  },
  {"surah_number": 9, "verse_number": 102, "content": "Und es gibt andere, die ihre Schuld bekennen. Sie vermischten eine gute Tat mit einer anderen, schlechten. Bald wird Allah Sich mit Erbarmen zu ihnen wenden. Wahrlich, Allah ist Allvergebend, Barmherzig"},
  {"surah_number": 9, "verse_number": 103, "content": "Nimm Almosen von ihrem Besitz, auf daß du sie dadurch reinigen und lautern mogest. Und bete fur sie; denn dein Gebet verschafft ihnen Beruhigung. Und Allah ist Allhorend, Allwissend"},
  {"surah_number": 9, "verse_number": 104, "content": "Wissen sie denn nicht, daß es Allah allein ist, Der von Seinen Dienern Reue annimmt und Almosen entgegennimmt, und daß Allah der Allvergebende, der Barmherzige ist"},
  {"surah_number": 9, "verse_number": 105, "content": "Und sprich: \"Wirkt! Allah wird euer Wirken sehen, und so (auch) Sein Gesandter und die Glaubigen. Und ihr sollt zu dem Kenner des Verborgenen und des Offenbaren zuruckgebracht werden; dann wird Er euch verkunden, was ihr zu tun pflegtet"},
  {"surah_number": 9, "verse_number": 106, "content": "Und es gibt andere, die auf Allahs Entscheidung warten mussen. Er mag sie bestrafen, oder Er mag Sich mit Erbarmen zu ihnen wenden; und Allah ist Allwissend, Allweise"},
  {
    "surah_number": 9,
    "verse_number": 107,
    "content":
        "Und (es gibt) jene, die eine Moschee erbaut haben, um Unheil, Unglauben und Spaltung unter den Glaubigen zu stiften, und um einen Hinterhalt fur den (zu schaffen), der zuvor gegen Allah und Seinen Gesandten Krieg fuhrte. Und sie werden sicherlich schworen: \"Wir bezwecken nur Gutes\"; doch Allah ist Zeuge, daß sie bloß Lugner sind"
  },
  {
    "surah_number": 9,
    "verse_number": 108,
    "content": "Stehe nie (zum Gebet) darin (in dieser Moschee) Eine Moschee, die vom allerersten Tag an auf Frommigkeit gegrundet wurde, ist wahrlich wurdiger, um darin zu stehen. In ihr sind Manner, die sich gerne reinigen; und Allah liebt diejenigen, die sich reinigen"
  },
  {
    "surah_number": 9,
    "verse_number": 109,
    "content":
        "Ist nun dieser besser, der sein Gebaude aus Furcht vor Allah und (um Sein) Wohlgefallen gegrundet hat, oder jener, der sein Gebaude auf den Rand einer wankenden, unterspulten Sandbank grundete, die mit ihm in das Feuer der Gahannam gesturzt ist? Und Allah weist nicht den frevelhaften Leuten den Weg"
  },
  {"surah_number": 9, "verse_number": 110, "content": "Das Gebaude, das sie errichtet haben, wird nicht aufhoren, Zweifel in ihren Herzen zu errregen, bis ihre Herzen in Stucke gerissen sind. Und Allah ist Allwissend, Allweise"},
  {
    "surah_number": 9,
    "verse_number": 111,
    "content":
        "Allah hat von den Glaubigen ihr Leben und ihr Gut fur das Paradies erkauft: Sie kampfen fur Allahs Sache, sie toten und werden getotet; eine Verheißung - bindend fur Ihn - in der Thora und im Evangelium und im Quran. Und wer halt seine Verheißung getreuer als Allah? So freut euch eures Handels, den ihr mit Ihm abgeschlossen habt; denn dies ist wahrlich die große Gluckseligkeit"
  },
  {
    "surah_number": 9,
    "verse_number": 112,
    "content": "Denjenigen, die sich in Reue (zu Allah) wenden, (Ihn) anbeten, (Ihn) lobpreisen, die (in Seiner Sache) umherziehen, die sich beugen und niederwerfen, die das Gute gebieten und das Bose verbieten und die Schranken Allahs achten verkunde (diesen) Glaubigen die frohe Botschaft"
  },
  {"surah_number": 9, "verse_number": 113, "content": "Es kommt dem Propheten und den Glaubigen nicht zu, fur die Gotzendiener um Verzeihung zu flehen, und waren es selbst ihre nachsten Verwandten, nachdem ihnen deutlich geworden ist, daß jene Bewohner der Gahim sind"},
  {
    "surah_number": 9,
    "verse_number": 114,
    "content": "Daß Abraham um Verzeihung bat fur seinen Vater, war nur wegen eines Versprechens, das er ihm gegeben hatte; doch als ihm klar wurde, daß jener ein Feind Allahs war, sagte er sich von ihm los. Abraham war doch gewiß zartlichen Herzens und sanftmutig"
  },
  {"surah_number": 9, "verse_number": 115, "content": "Es ist nicht Allah, Der ein Volk irregehen laßt, nachdem Er ihm den Weg gewiesen und ihm klar gemacht hat, wovor es sich zu huten habe. Wahrlich, Allah weiß uber alle Dinge Bescheid"},
  {"surah_number": 9, "verse_number": 116, "content": "Allah ist es, Dem das Konigreich der Himmel und der Erde gehort. Er macht lebendig und laßt sterben. Und ihr habt keinen Beschutzer noch Helfer außer Allah"},
  {
    "surah_number": 9,
    "verse_number": 117,
    "content":
        "Allah hat Sich wahrlich gnadenvoll dem Propheten zugewandt und den Auswanderern und den Helfern, die ihm in der Stunde der Not gefolgt sind, nachdem die Herzen einiger von ihnen fast gewankt hatten. Er aber wandte Sich ihnen abermals mit Erbarmen zu. Wahrlich, Er ist gegen sie Gutig, Barmherzig"
  },
  {
    "surah_number": 9,
    "verse_number": 118,
    "content":
        "Und auch den Dreien (wandte Er Sich wieder gnadig zu), die zuruckgeblieben waren, bis die Erde ihnen in ihrer Weite zu eng wurde und ihre Seelen ihnen zugeschnurt wurden, und sie wußten, daß es keine Rettung vor Allah gibt, es sei denn die Zuflucht bei Ihm. Da kehrte Er Sich ihnen mit Erbarmen zu, auf daß sie sich bekehren mochten. Wahrlich, Allah ist der Gnadige, der Barmherzige"
  },
  {"surah_number": 9, "verse_number": 119, "content": "O ihr, die ihr glaubt, furchtet Allah und seid mit den Wahrhaftigen"},
  {
    "surah_number": 9,
    "verse_number": 120,
    "content":
        "Es ziemt sich nicht fur die Bewohner von Madina, noch fur die sie umgebenden Wustenaraber, hinter dem Gesandten Allahs zuruckzubleiben und ihr Leben dem seinigen vorzuziehen. Dies (ist so), weil weder Durst noch Muhsal noch Hunger sie auf Allahs Weg erleiden, auch betreten sie keinen Weg, der die Unglaubigen erzurnt, noch fugen sie einem Feind Leid zu, ohne daß ihnen ein verdienstliches Werk angeschrieben wurde. Wahrlich, Allah laßt den Lohn derer, die Gutes tun, nicht verloren gehen"
  },
  {"surah_number": 9, "verse_number": 121, "content": "Und sie spenden keine Summe, sei sie groß oder klein, und sie durchziehen kein Tal, ohne daß es ihnen angeschrieben wurde, auf daß Allah ihnen den besten Lohn gebe fur das, was sie getan haben"},
  {
    "surah_number": 9,
    "verse_number": 122,
    "content":
        "Die Glaubigen durfen nicht alle auf einmal ausziehen. Warum ruckt dann nicht aus jeder Gruppe nur eine Abteilung aus, auf daß sie (die Zuruckbleibenden) in Glaubensfragen wohl bewandert wurden? Und nach ihrer Ruckkehr konnten sie (die Zuruckbleibenden) ihre (ausgezogenen) Leute belehren, damit sie sich in acht nahmen"
  },
  {"surah_number": 9, "verse_number": 123, "content": "O ihr, die ihr glaubt, kampft gegen jene, die euch nahe sind unter den Unglaubigen, und lasset sie euch hart vorfinden; und wisset, daß Allah mit den Gottesfurchtigen ist"},
  {"surah_number": 9, "verse_number": 124, "content": "Sooft eine Sura herabgesandt wird, gibt es welche unter ihnen, die sagen: \"Wen von euch hat sie im Glauben bestarkt?\" Die aber glaubig sind, starkt sie in ihrem Glauben, und sie freuen sich daruber"},
  {"surah_number": 9, "verse_number": 125, "content": "Jenen aber, in deren Herzen Krankheit ist, fugt sie zu ihrem Ubel noch Ubel hinzu, und sie sterben als Unglaubige"},
  {"surah_number": 9, "verse_number": 126, "content": "Sehen sie denn nicht, daß sie in jedem Jahr einmal oder zweimal gepruft werden? Dennoch bereuen sie nicht und lassen sich nicht ermahnen"},
  {"surah_number": 9, "verse_number": 127, "content": "Und sooft eine Sura herabgesandt wird, schauen sie einander an (und sagen): \"Sieht euch jemand?\" Dann wenden sie sich ab. Allah hat ihre Herzen abgewendet, weil sie Leute sind, die es nicht begreifen"},
  {"surah_number": 9, "verse_number": 128, "content": "Wahrlich, ein Gesandter aus eurer Mitte ist zu euch gekommen; es schmerzt ihn sehr, wenn ihr unter etwas leidet; er setzt sich eifrig fur euer Wohl ein; gegen die Glaubigen ist er mitleidig und barmherzig"},
  {"surah_number": 9, "verse_number": 129, "content": "Doch wenn sie sich abwenden, so sprich: \"Allah allein soll mir genugen. Es ist kein Gott außer Ihm. Auf Ihn vertraue ich, und Er ist der Herr des gewaltigen Throns"},
  {"surah_number": 10, "verse_number": 1, "content": "Alif Lam Ra ' Dies sind die Verse des vollkommenen Buches"},
  {
    "surah_number": 10,
    "verse_number": 2,
    "content":
        "Scheint es den Menschen so verwunderlich, daß Wir einem Manne aus ihrer Mitte eingegeben haben: \"Warne die Menschen und verkunde die frohe Botschaft denjenigen, die da glauben, einen wirklichen Rang bei ihrem Herrn innezuhaben?\" Die Unglaubigen sagen: \"Wahrlich, das ist ein offenkundiger Zauberer"
  },
  {
    "surah_number": 10,
    "verse_number": 3,
    "content":
        "Wahrlich, euer Herr ist Allah, Der die Himmel und die Erde in sechs Tagen erschuf, (und) Sich alsdann (Seinem) Reich majestatisch zuwandte: Er sorgt fur alles. Es gibt keinen Fursprecher, es sei denn mit Seiner Erlaubnis. Dies ist Allah, euer Herr, so betet Ihn an. Wollt ihr euch denn nicht ermahnen lassen"
  },
  {
    "surah_number": 10,
    "verse_number": 4,
    "content":
        "Zu Ihm werdet ihr alle heimkehren; (dies ist) die Verheißung Allahs in Wahrheit. Er bringt die Schopfung hervor; dann laßt Er sie wiederholen, auf daß Er jene, die glauben und gute Werke tun, nach Billigkeit belohne; denen aber, die unglaubig sind, wird ein Trunk siedenden Wassers zuteil werden und schmerzliche Strafe, weil sie unglaubig waren"
  },
  {
    "surah_number": 10,
    "verse_number": 5,
    "content":
        "Er ist es, Der die Sonne zur Helligkeit und den Mond zu einem Licht machte und ihm Stationen zuwies, damit ihr die Anzahl der Jahre und die Berechnung (der Zeit) beherrschen konnt. Allah hat dies nicht anders als in Gerechtigkeit erschaffen. Er legt die Zeichen fur die Leute dar, die Wissen besitzen"
  },
  {"surah_number": 10, "verse_number": 6, "content": "Wahrlich, in dem Wechsel von Nacht und Tag und in allem, was Allah in den Himmeln und auf der Erde erschaffen hat, sind Zeichen fur gottesfurchtige Leute"},
  {"surah_number": 10, "verse_number": 7, "content": "Die aber, die nicht mit der Begegnung mit Uns rechnen und mit dem diesseitigen Leben zufrieden sind und sich darauf verlassen und Unsere Zeichen nicht beachten"},
  {"surah_number": 10, "verse_number": 8, "content": "diese sind es, deren Herberge das Feuer ist, um dessentwillen, was sie sich erworben haben"},
  {"surah_number": 10, "verse_number": 9, "content": "Jene jedoch, die da glauben und gute Werke tun, wird ihr Herr um ihres Glaubens willen leiten. Bache werden unter ihnen in den Garten der Wonne fließen"},
  {"surah_number": 10, "verse_number": 10, "content": "Ihr Ruf dort wird sein: \"Preis Dir, o Allah!\" Und ihr Gruß dort wird \"Frieden!\" sein. Und zuletzt werden sie rufen: \" Alles Lob gebuhrt Allah, dem Herrn der Welten"},
  {
    "surah_number": 10,
    "verse_number": 11,
    "content": "Und wenn Allah den Menschen das Unheil so eilig zukommen ließe, wie sie es mit dem Guten eilig haben, so ware ihre Lebensfrist schon zu Ende. Und so lassen Wir die, welche nicht mit der Begegnung mit Uns rechnen, in ihrer Widersetzlichkeit verblenden"
  },
  {
    "surah_number": 10,
    "verse_number": 12,
    "content":
        "Und wenn den Menschen ein Schaden trifft, ruft er Uns an, ob er nun auf der Seite liegt oder sitzt oder steht; haben Wir aber den Schaden von ihm fortgenommen, dann geht er seines Weges, als hatte er Uns nie um (die Befreiung) vom Schaden, der ihn getroffen hat, angerufen. Also zeigt sich den Maßlosen das in schonem Licht, was sie begangen haben"
  },
  {"surah_number": 10, "verse_number": 13, "content": "Und Wir vernichteten die Geschlechter vor euch, als sie frevelten; denn zu ihnen kamen ihre Gesandten mit deutlichen Zeichen, sie aber wollten nicht glauben. Also vergelten Wir (dies) der verbrecherischen Schar"},
  {"surah_number": 10, "verse_number": 14, "content": "Danach machten Wir euch zu ihren Nachfolgern auf der Erde, auf daß Wir schauten, wie ihr handeln wurdet"},
  {
    "surah_number": 10,
    "verse_number": 15,
    "content":
        "Und wenn ihnen Unsere deutlichen Verse verlesen werden, sagen jene, die nicht mit der Begegnung mit Uns rechnen: \"Bring einen Quran, der anders ist als dieser oder andere ihn.\" Sprich: \"Es steht mir nicht zu, ihn aus eigenem Antrieb zu andern. Ich folge nur dem, was mir offenbart wurde. Ich furchte, falls ich meinem Herrn ungehorsam bin, die Strafe eines gewaltigen Tages"
  },
  {"surah_number": 10, "verse_number": 16, "content": "Sprich: \"Hatte Allah es gewollt so hatte ich ihn euch nicht verlesen, noch hatte Er ihn euch kundgetan. Ich habe doch wahrlich ein Menschenalter unter euch gelebt, bevor (der Quran da war) Wollt ihr denn nicht begreifen"},
  {"surah_number": 10, "verse_number": 17, "content": "Wer ist wohl ungerechter als jener, der eine Luge gegen Allah erdichtet oder Seine Zeichen fur Lugen erklart? Wahrlich, die Verbrecher haben keinen Erfolg"},
  {
    "surah_number": 10,
    "verse_number": 18,
    "content":
        "Sie verehren statt Allah das, was ihnen weder schaden noch nutzen kann; und sie sagen: \"Das sind unsere Fursprecher bei Allah.\" Sprich: \"Wollt ihr Allah von etwas Nachricht geben, was Ihm in den Himmeln oder auf der Erde unbekannt ist?\" Gepriesen sei Er, und hoch Erhaben ist Er uber das, was sie (Ihm) zur Seite stellen"
  },
  {"surah_number": 10, "verse_number": 19, "content": "Die Menschen waren einst nur eine einzige Gemeinde, dann aber wurden sie uneins; und ware nicht ein Wort von deinem Herrn vorausgegangen, ware zwischen ihnen bereits uber das, woruber sie uneins waren, entschieden worden"},
  {"surah_number": 10, "verse_number": 20, "content": "Und sie sagen: \"Warum ist nicht ein Zeichen zu ihm von seinem Herrn herabgesandt worden?\" Sprich: \"Das Verborgene gehort Allah allein. Darum wartet ab; seht, ich warte auch mit euch ab"},
  {
    "surah_number": 10,
    "verse_number": 21,
    "content":
        "Und wenn Wir die Menschen Barmherzigkeit erleben lassen, nachdem Unheil sie getroffen hat, siehe, dann beginnen sie wieder, gegen Unsere Zeichen Plane zu schmieden. Sprich: \"Allah schmiedet die Plane schneller.\" Unsere Boten schreiben wahrlich alles nieder, was ihr an Planen schmiedet"
  },
  {
    "surah_number": 10,
    "verse_number": 22,
    "content":
        "Er ist es, Der euch zu Lande und zur See Wege bereitet. Und da sie an (Bord) der Schiffe sind und mit ihnen (den Passagieren) bei gutem Wind dahinsegeln und sich daruber freuen, werden sie plotzlich von einem Sturm erfaßt, und die Wogen schlagen von allen Seiten uber sie zusammen. Und sie meinen schon, sie seien rings umschlossen - da rufen sie Allah in vollem, aufrichtigen Glauben an: \"Wenn Du uns aus diesem (Sturm) errettest, so werden wir sicherlich unter den Dankbaren sein"
  },
  {
    "surah_number": 10,
    "verse_number": 23,
    "content":
        "Doch wenn Er sie dann errettet hat, siehe, schon beginnen sie wieder, ohne Berechtigung auf Erden Gewalt zu veruben. O ihr Menschen, eure Gewalttat richtet sich nur gegen euch selbst. (Genießt) die Gaben des diesseitigen Lebens. Zu Uns sollt ihr dann heimkehren; dann werden Wir euch verkunden, was ihr getan habt"
  },
  {
    "surah_number": 10,
    "verse_number": 24,
    "content":
        "Das Gleichnis des irdischen Lebens ist nur wie das Wasser, das Wir aus den Wolken herabsenden; damit vermischen sich dann die Gewachse der Erde, wovon Mensch und Vieh sich nahren, bis zu ihr wenn die Erde ihren Prunk angelegt und sich schon geschmuckt hat und ihre Bewohner glauben, sie hatten Macht uber sie - Unser Befehl in der Nacht oder am Tage kommt und Wir sie zu einem niedergemahten Acker machen, als ware sie nicht am Tage zuvor gediehen. Also machen Wir die Zeichen fur die Leute klar, die nachdenken"
  },
  {"surah_number": 10, "verse_number": 25, "content": "Und Allah ladt ein zum Haus des Friedens und leitet, wen Er will, zum geraden Weg"},
  {"surah_number": 10, "verse_number": 26, "content": "Denen, die Gutes tun, soll das Beste zuteil sein und noch mehr. Weder Betrubnis noch Schmach soll ihre Gesichter bedecken. Sie sind die Bewohner des Paradieses; darin werden sie auf ewig verweilen"},
  {
    "surah_number": 10,
    "verse_number": 27,
    "content":
        "Fur diejenigen aber, die bose Taten begangen haben, ist eine Strafe in gleichem Ausmaße (wie dem der bosen Taten) bereitet. Schmach wird sie bedecken; keinen Schutz werden sie vor Allah haben, (und es soll so sein,) als ob ihre Gesichter mit Fetzen einer finsteren Nacht bedeckt waren. Sie sind die Bewohner des Feuers; darin werden sie auf ewig bleiben"
  },
  {
    "surah_number": 10,
    "verse_number": 28,
    "content": "Und (gedenke) des Tages, da Wir sie versammeln werden allzumal; dann werden Wir zu denen, die Gotzen anbeteten, sprechen: \"An euren Platz, ihr und eure Teilhaber! \"Dann scheiden Wir sie voneinander, und ihre Teilhaber werden sagen: \"Nicht uns habt ihr angebetet"
  },
  {"surah_number": 10, "verse_number": 29, "content": "Allah genugt als Zeuge gegen uns und euch. Wir haben wahrhaftig nichts von eurer Anbetung gewußt"},
  {"surah_number": 10, "verse_number": 30, "content": "Dort erfahrt jede Seele, was sie (an Taten) vorausgeschickt hat. Und sie werden zu Allah, ihrem wahren Herrn, zuruckgebracht, und das, was sie zu erdichten pflegten, wird ihnen entschwunden sein"},
  {
    "surah_number": 10,
    "verse_number": 31,
    "content":
        "Sprich: \"Wer versorgt euch vom Himmel her und aus der Erde? Oder wer ist es, der Gewalt uber die Ohren und die Augen hat? Und wer bringt das Lebendige aus dem Toten hervor und das Tote aus dem Lebendigen? Und wer sorgt fur alle Dinge?\" Sie werden sagen: \"Allah\". So sprich: \"Wollt ihr Ihn denn nicht furchten"
  },
  {"surah_number": 10, "verse_number": 32, "content": "Das ist Allah, euer wahrer Herr. Was sollte also nach der Wahrheit (ubrig) bleiben als der Irrtum? Wie lasset ihr euch abwenden"},
  {"surah_number": 10, "verse_number": 33, "content": "Und so hat sich das Wort deines Herrn gegen die Emporer bewahrheitet, (namlich,) daß sie nicht glauben"},
  {"surah_number": 10, "verse_number": 34, "content": "Sprich: \"Ist unter euren Teilhabern etwa einer der eine Schopfung hervorbringt und sie dann wiederholen laßt?\" Sprich: \"Allah ist es, Der die Schopfung hervorbringt und sie wiederholen laßt. Wohin also lasset ihr euch abwenden"},
  {
    "surah_number": 10,
    "verse_number": 35,
    "content":
        "Sprich: \"Ist unter euren Teilhabern etwa einer, der zur Wahrheit leitet?\" Sprich: \"Allah ist es, Der zur Wahrheit leitet. Ist nun Der, Der zur Wahrheit leitet, nicht der Gefolgschaft wurdiger als der, der den Weg nicht zu finden vermag, es sei denn, er wird selbst geleitet? Was fehlt euch also? Wie urteilt ihr nur"
  },
  {"surah_number": 10, "verse_number": 36, "content": "Und die meisten von ihnen folgen bloß einer Vermutung; doch eine Vermutung nutzt nichts gegenuber der Wahrheit. Siehe, Allah weiß recht wohl, was sie tun"},
  {"surah_number": 10, "verse_number": 37, "content": "Und dieser Quran hatte nicht ersonnen werden konnen, außer durch Allah. Vielmehr ist er eine Bestatigung dessen, was ihm vorausging, und eine Darlegung des Buches - daruber herrscht kein Zweifel vom Herrn der Welten"},
  {"surah_number": 10, "verse_number": 38, "content": "Oder wollen sie etwa sagen: \"Er hat ihn erdichtet?\" Sprich: \"Bringt denn eine Sura gleicher Art hervor und ruft, wen ihr nur konnt, außer Allah, wenn ihr wahrhaftig seid"},
  {"surah_number": 10, "verse_number": 39, "content": "Nein; aber sie haben das geleugnet, was sie an Wissen nicht umfassen ten, und ebensowenig zuganglich war ihnen seine Deutung. Ebenso leugneten auch jene, die vor ihnen waren. Doch siehe, wie das Ende der Ungerechten war"},
  {"surah_number": 10, "verse_number": 40, "content": "Unter ihnen sind solche, die daran glauben, und andere, die nicht daran glauben, und dein Herr kennt jene wohl, die Verderben stiften"},
  {"surah_number": 10, "verse_number": 41, "content": "Und wenn sie dich der Luge bezichtigen, so sprich: \"Fur mich ist mein Werk und fur euch ist euer Werk. Ihr seid nicht verantwortlich fur das, was ich tue, und ich bin nicht verantwortlich fur das, was ihr tut"},
  {"surah_number": 10, "verse_number": 42, "content": "Und unter ihnen sind solche, die dir zuhoren. Aber kannst du die Tauben horend machen, obwohl sie nicht begreifen"},
  {"surah_number": 10, "verse_number": 43, "content": "Und unter ihnen sind solche, die auf dich schauen. Aber kannst du den Blinden den Weg weisen, obwohl sie nicht sehen"},
  {"surah_number": 10, "verse_number": 44, "content": "Wahrlich, Allah fugt den Menschen kein Unrecht zu; die Menschen aber begehen Unrecht gegen sich selbst"},
  {
    "surah_number": 10,
    "verse_number": 45,
    "content":
        "Und an dem Tage, an dem Er sie (vor Sich) versammelt, (kommt es ihnen so vor,) als hatten sie (seit ihrem Tod) nur eine Stunde an einem Tage (im Grabe) verweilt. Sie werden einander erkennen. Verloren wahrlich haben jene, die die Begegnung mit Allah leugneten und nicht rechtgeleitet waren"
  },
  {"surah_number": 10, "verse_number": 46, "content": "Und ob Wir dir einige Dinge zeigen, die Wir ihnen angedroht haben, oder (ob Wir) dich sterben lassen: zu Uns werden sie dann heimkehren; hernach ist Allah Zeuge all dessen, was sie tun"},
  {"surah_number": 10, "verse_number": 47, "content": "Und fur jede Nation ist ein Gesandter (bestimmt) Wenn also ihr Gesandter kommt, so wird zwischen ihnen in Gerechtigkeit entschieden, und kein Unrecht widerfahrt ihnen"},
  {"surah_number": 10, "verse_number": 48, "content": "Und sie sagen: \"Wann wird dieses Versprechen (verwirklicht werden), wenn ihr wahrhaftig seid"},
  {
    "surah_number": 10,
    "verse_number": 49,
    "content": "Sprich: \"Ich vermag mir selbst weder zu schaden noch zu nutzen, es sei denn, Allah will es. Jeder Gemeinschaft ist eine Frist bestimmt; und wenn ihre Frist um ist, so konnen sie nicht (hinter ihr) eine Stunde zuruckbleiben, noch konnen sie ihr vorausgehen"
  },
  {"surah_number": 10, "verse_number": 50, "content": "Sprich: \"Was meint ihr? Wenn Seine Strafe uber euch kommt, bei Nacht oder bei Tage, wie werden die Verbrecher sich ihr entziehen"},
  {"surah_number": 10, "verse_number": 51, "content": "Wollt ihr erst dann an sie glauben, wenn sie eintrifft? Wie? Jetzt? Und doch wolltet ihr sie beschleunigen"},
  {"surah_number": 10, "verse_number": 52, "content": "Dann wird zu den Ungerechten gesagt werden: \"Kostet nun die Strafe der Ewigkeit. Erhaltet ihr denn etwas anderes als das, wofur ihr vorgesorgt habt"},
  {"surah_number": 10, "verse_number": 53, "content": "Und sie fragen dich: \"Ist das die Wahrheit?\" Sprich: \"Ja, bei meinem Herrn! Es ist ganz gewiß die Wahrheit; und ihr konnt es nicht verhindern"},
  {
    "surah_number": 10,
    "verse_number": 54,
    "content":
        "Und wenn eine jede Seele, die Unrecht begangen hat, alles besaße, was auf Erden ist, wurde sie versuchen, sich damit (von der Strafe) loszukaufen. Und sie werden Reue empfinden, wenn sie sehen, wie die Strafe (uber sie) kommt. Und es wird zwischen ihnen in Gerechtigkeit entschieden werden, und sie sollen kein Unrecht erleiden"
  },
  {"surah_number": 10, "verse_number": 55, "content": "Wisset, Allahs ist, was in den Himmeln und was auf Erden ist. Wisset, Allah s Verheißung ist wahr! Doch die meisten von ihnen wissen es nicht"},
  {"surah_number": 10, "verse_number": 56, "content": "Er macht lebendig und laßt sterben, und zu Ihm kehrt ihr zuruck"},
  {"surah_number": 10, "verse_number": 57, "content": "O ihr Menschen! Nunmehr ist von eurem Herrn eine Ermahnung zu euch gekommen und eine Heilung fur das, was euch in eurer Brust bewegt, und eine Fuhrung und Barmherzigkeit fur die Glaubigen"},
  {"surah_number": 10, "verse_number": 58, "content": "Sprich: \"Durch die Gnade Allahs und durch Seine Barmherzigkeit; daruber sollen sie sich nun freuen. Das ist besser als das, was sie anhaufen"},
  {"surah_number": 10, "verse_number": 59, "content": "Sprich: \"Habt ihr das betrachtet, was Allah euch an Nahrung hinabgesandt hat, woraus ihr aber (etwas) Verbotenes und Erlaubtes gemacht habt?\" Sprich: \"Hat Allah euch (das) gestattet oder erdichtet ihr Lugen gegen Allah"},
  {"surah_number": 10, "verse_number": 60, "content": "Was meinen wohl jene, die Lugen gegen Allah erdichten, vom Tage der Auferstehung? Wahrlich Allah ist Gnadenvoll gegen die Menschen, jedoch die meisten von ihnen sind nicht dankbar"},
  {
    "surah_number": 10,
    "verse_number": 61,
    "content":
        "Du unternimmst nichts, und du verliest von diesem (Buch) keinen Teil des Quran, und ihr begeht keine Tat, ohne daß Wir eure Zeugen sind, wenn ihr damit vollauf beschaftigt seid. Und auch nicht das Gewicht eines Staubchens auf Erden oder im Himmel ist vor deinem Herrn verborgen. Und es gibt nichts, weder etwas Kleineres als dies noch etwas Großeres, das nicht in einem Buche voller Klarheit stunde"
  },
  {"surah_number": 10, "verse_number": 62, "content": "Wisset, daß uber Allahs Schutzlinge keine Furcht kommen wird, noch sollen sie traurig sein"},
  {"surah_number": 10, "verse_number": 63, "content": "Diejenigen, die da glauben und rechtschaffen sind"},
  {"surah_number": 10, "verse_number": 64, "content": "Fur sie ist die frohe Botschaft im diesseitigen Leben sowie im Jenseits (bestimmt) Unabanderlich sind Allahs Worte - das ist wahrlich der gewaltige Gewinn"},
  {"surah_number": 10, "verse_number": 65, "content": "Und sei nicht betrubt uber ihre Rede. Alle Erhabenheit gebuhrt Allah allein. Er ist der Allhorende, der Allwissende"},
  {"surah_number": 10, "verse_number": 66, "content": "Wisset, daß Allahs ist, wer immer in den Himmeln und wer immer auf der Erde ist. Und diejenigen, die da andere außer Allah anrufen, folgen nicht (diesen) Teilhabern; sie folgen nur einem Wahn, und sie vermuten nur"},
  {"surah_number": 10, "verse_number": 67, "content": "Er ist es, Der die Nacht fur euch gemacht hat, auf daß ihr in ihr ruhet, und den Tag voll von Licht. Wahrlich, hierin liegen Zeichen fur die Leute, die horen konnen"},
  {
    "surah_number": 10,
    "verse_number": 68,
    "content": "Sie sagen: \"Allah hat Sich einen Sohn genommen.\" Gepriesen sei Er! Er ist der Sich-Selbst-Genugende. Sein ist, was in den Himmeln und was auf der Erde ist. Ihr habt keinen Beweis hierfur. Wollt ihr von Allah etwas behaupten, was ihr nicht wisset"
  },
  {"surah_number": 10, "verse_number": 69, "content": "Sprich: \"Jene, die eine Luge gegen Allah erdichten, werden keinen Erfolg haben"},
  {"surah_number": 10, "verse_number": 70, "content": "Eine Weile Genuß in dieser Welt - dann werden sie zu Uns heimkehren. Dann werden Wir sie die strenge Strafe dafur kosten lassen, daß sie unglaubig waren"},
  {
    "surah_number": 10,
    "verse_number": 71,
    "content":
        "Und verlies ihnen die Geschichte von Noah, als er zu seinem Volke sprach: \"O mein Volk, wenn mein Rang und meine Ermahnung durch die Zeichen Allahs fur euch unertraglich sind, so setze ich mein Vertrauen in Allah; so beschließt nur eure Angelegenheiten und versammelt eure Teilhaber, und belaßt euer Planen nicht im Verborgenen, sondern handelt gegen mich, und gebt mir keine Wartezeit"
  },
  {"surah_number": 10, "verse_number": 72, "content": "Kehrt ihr (mir) aber den Rucken, so habe ich von euch keinen Lohn verlangt. Mein Lohn ist allein bei Allah, und mir wurde befohlen, zu den Gottergebenen zu gehoren"},
  {
    "surah_number": 10,
    "verse_number": 73,
    "content":
        "Doch sie bezichtigten ihn der Luge; darum retteten Wir ihn und die, die bei ihm im Schiff waren. Und Wir machten sie zu den Nachfolgern (der Menschen), wahrend Wir jene ertrinken ließen, die Unsere Zeichen fur Lugen hielten. Schau also, wie das Ende derer war, die gewarnt worden waren"
  },
  {"surah_number": 10, "verse_number": 74, "content": "Dann schickten Wir nach ihm Gesandte zu ihrem Volk, und sie brachten ihnen klare Beweise. Allein sie wollten unmoglich an das glauben, was sie zuvor verleugnet hatten. So versiegeln Wir die Herzen der Ubertreter"},
  {"surah_number": 10, "verse_number": 75, "content": "Dann schickten Wir nach ihnen Moses und Aaron mit Unseren Zeichen zu Pharao und seinen Vornehmen; sie aber waren hochmutig. Und sie waren ein verbrecherisches Volk"},
  {"surah_number": 10, "verse_number": 76, "content": "Als nun die Wahrheit von Uns zu ihnen kam, da sagten sie: \"Das ist gewiß ein offenkundiger Zauber"},
  {"surah_number": 10, "verse_number": 77, "content": "Moses sprach: \"Sagt ihr (solches) von der Wahrheit, nachdem sie zu euch gekommen ist? Ist das Zauberei? Und die Zauberer haben niemals Erfolg"},
  {"surah_number": 10, "verse_number": 78, "content": "Sie sagten: \"Bist du zu uns gekommen, um uns von dem abzulenken, was wir bei unseren Vatern vorfanden, und wollt ihr beide die Oberhand im Lande haben? Wir aber wollen euch nicht glauben"},
  {"surah_number": 10, "verse_number": 79, "content": "Da sprach Pharao: \"Bringt mir einen jeden kundigen Zauberer herbei"},
  {"surah_number": 10, "verse_number": 80, "content": "Als nun die Zauberer kamen, sprach Moses zu ihnen: \"Werft, was ihr zu werfen habt"},
  {"surah_number": 10, "verse_number": 81, "content": "Als sie dann geworfen hatten sprach Moses: \"Was ihr gebracht habt, ist Zauberei. Allah wird es sicher zunichte machen. Denn wahrlich, Allah laßt das Werk der Verderbensstifter nicht gedeihen"},
  {"surah_number": 10, "verse_number": 82, "content": "Und Allah starkt die Wahrheit durch Seine Worte, auch wenn es die Verbrecher verwunschen"},
  {
    "surah_number": 10,
    "verse_number": 83,
    "content": "Und niemand bekannte sich zu Moses, bis auf einige junge Menschen aus seinem Volk - voller Furcht vor Pharao und seinen Vornehmen, er (Pharao) wurde sie verfolgen. Und in der Tat war Pharao ein Tyrann im Land, und wahrlich, er war einer der Maßlosen"
  },
  {"surah_number": 10, "verse_number": 84, "content": "Und Moses sprach: \"O mein Volk, habt ihr an Allah geglaubt, so vertraut nun auf Ihn, wenn ihr euch (Ihm) wirklich ergeben habt"},
  {"surah_number": 10, "verse_number": 85, "content": "Sie sagten: \"Auf Allah vertrauen wir. Unser Herr, mache uns nicht zu einer Versuchung fur das Volk der Ungerechten"},
  {"surah_number": 10, "verse_number": 86, "content": "Und errette uns durch Deine Barmherzigkeit von dem Volk der Unglaubigen"},
  {"surah_number": 10, "verse_number": 87, "content": "Und Wir gaben Moses und seinem Bruder ein: \"Nehmt in Agypten einige Hauser fur euer Volk und macht sie zur Begegnungsstatte und verrichtet das Gebet.\" Und (weiter): \"Verkunde den Glaubigen die frohe Botschaft"},
  {
    "surah_number": 10,
    "verse_number": 88,
    "content":
        "Und Moses sprach: \"Unser Herr, Du gabst die Pracht sowie die Reichtumer im diesseitigen Leben dem Pharao und seinen Vornehmen, unser Herr, damit sie von Deinem Weg abhalten. Unser Herr, zerstore ihre Reichtumer und triff ihre Herzen, so daß sie nicht glauben, ehe sie die schmerzliche Strafe erleben"
  },
  {"surah_number": 10, "verse_number": 89, "content": "Er sprach: \"Euer Gebet ist erhort. Seid ihr beide denn aufrichtig und folgt nicht dem Weg derer, die unwissend sind"},
  {
    "surah_number": 10,
    "verse_number": 90,
    "content":
        "Und Wir fuhrten die Kinder Israels durch das Meer; und Pharao mit seinen Heerscharen verfolgte sie widerrechtlich und feindlich, bis er nahe daran war, zu ertrinken, (und) sprach: \"Ich glaube, daß kein Gott ist als Der, an Den die Kinder Israels glauben, und ich gehore nun zu den Gottergebenen"
  },
  {"surah_number": 10, "verse_number": 91, "content": "Wie? Jetzt? Wo du bisher ungehorsam und einer derer warst, die Unheil stifteten"},
  {"surah_number": 10, "verse_number": 92, "content": "Nun wollen Wir dich heute dem Leibe nach erretten, auf daß du ein Beweis fur diejenigen seiest, die nach dir kommen. Und es gibt sicher viele Menschen, die Unseren Zeichen keine Beachtung schenken"},
  {
    "surah_number": 10,
    "verse_number": 93,
    "content":
        "Wahrlich, Wir bereiteten den Kindern Israels ein wahrhaftig sicheres Dasein und versorgten sie mit guten Dingen; und sie waren nicht eher uneins, als bis das Wissen zu ihnen kam. Wahrlich, am Tage der Auferstehung wird dein Herr zwischen ihnen daruber entscheiden, woruber sie uneins waren"
  },
  {"surah_number": 10, "verse_number": 94, "content": "Und falls du im Zweifel uber das bist, was Wir zu dir niedersandten, so frage diejenigen, die vor dir die Schrift gelesen haben. Wahrlich, die Wahrheit ist von deinem Herrn zu dir gekommen; sei also nicht einer der Zweifler"},
  {"surah_number": 10, "verse_number": 95, "content": "Und gehore auch nicht zu jenen, die Allahs Zeichen fur Lugen halten, da du sonst einer der Verlierenden sein wirst"},
  {"surah_number": 10, "verse_number": 96, "content": "Wahrlich, diejenigen, gegen die das Wort deines Herrn ergangen ist, werden nicht glaubig sein"},
  {"surah_number": 10, "verse_number": 97, "content": "auch wenn zu ihnen irgendein Zeichen kame, bis sie die schmerzliche Strafe sehen"},
  {
    "surah_number": 10,
    "verse_number": 98,
    "content": "Gab es denn kein Volk außer dem Volke Jonas, das so glauben konnte daß ihnen ihr Glaube (etwas) genutzt hatte? Als sie glaubten, da nahmen Wir die Strafe der Schande in diesem Leben von ihnen fort und versorgten sie auf eine (beschrankte) Zeit"
  },
  {"surah_number": 10, "verse_number": 99, "content": "Und hatte dein Herr es gewollt, so hatten alle, die insgesamt auf der Erde sind, geglaubt. Willst du also die Menschen dazu zwingen, Glaubige zu werden"},
  {"surah_number": 10, "verse_number": 100, "content": "Niemandem steht es zu zu glauben, es sei denn mit Allahs Erlaubnis. Und Er laßt (Seinen) Zorn auf jene herab, die ihre Vernunft (dazu) nicht gebrauchen wollen"},
  {"surah_number": 10, "verse_number": 101, "content": "Sprich: \"Schaut doch, was in den Himmeln und auf der Erde ist.\" Aber den Leuten, die nicht glauben, helfen die Zeichen und die Warnungen nichts"},
  {"surah_number": 10, "verse_number": 102, "content": "Was erwarten sie denn anderes als die Tage jener, die vor ihnen dahingegangen sind? Sprich: \"Wartet denn, (und) ich warte mit euch"},
  {"surah_number": 10, "verse_number": 103, "content": "Dann werden Wir Unsere Gesandten und jene, die da glauben, erretten. Also obliegt es Uns, die Glaubigen zu retten"},
  {
    "surah_number": 10,
    "verse_number": 104,
    "content": "Sprich: \"O ihr Menschen, wenn ihr uber meine Religion im Zweifel seid, dann (wisset), ich verehre nicht die, welche ihr statt Allah verehrt, sondern ich verehre Allah allein, Der euch abberufen wird; und mir wurde befohlen, einer der Glaubigen zu sein"
  },
  {"surah_number": 10, "verse_number": 105, "content": "Und (mir wurde befohlen): \"Richte dein Antlitz auf die einzig wahre Religion, und sei nicht einer der Gotzendiener"},
  {"surah_number": 10, "verse_number": 106, "content": "Und rufe nicht statt Allah (etwas) anderes an, das dir weder nutzt noch schadet. Tatest du es, dann warest du gewiß unter den Ungerechten"},
  {
    "surah_number": 10,
    "verse_number": 107,
    "content":
        "Und wenn dich Allah mit einem Ubel treffen will, so gibt es keinen, der es hinwegnehmen kann, außer Ihm; und wenn Er dir etwas Gutes erweisen will, so gibt es keinen, der Seine Gnade verhindern kann. Er laßt sie unter Seinen Dienern zukommen, wem Er will, und Er ist der Allverzeihende, der Barmherzige"
  },
  {
    "surah_number": 10,
    "verse_number": 108,
    "content": "Sprich: \"O ihr Menschen, nun ist die Wahrheit von eurem Herrn zu euch gekommen. Wer nun dem rechten Weg folgt, der folge ihm allein zum Heil seiner eigenen Seele; und wer in die Irre geht, der geht nur zu seinem eigenen Schaden irre. Und ich bin nicht euer Huter"
  },
  {"surah_number": 10, "verse_number": 109, "content": "Und folge dem, was dir offenbart wurde, und sei geduldig, bis Allah richtet; denn Er ist der beste Richter"},
  {"surah_number": 11, "verse_number": 1, "content": "Alif Lam Ra'(Dies ist) ein Buch, dessen Verse vervollkommnet und dann im einzelnen erklart worden sind - von einem Allweisen, Allkundigen"},
  {"surah_number": 11, "verse_number": 2, "content": "(Es lehrt euch,) daß ihr keinen außer Allah anbeten sollt. Ich bin euch von Ihm (als) ein Warner und ein Bringer froher Botschaft (gesandt worden)"},
  {
    "surah_number": 11,
    "verse_number": 3,
    "content":
        "(Und ich bin zu euch gesandt worden, auf) daß ihr Vergebung von eurem Herrn erflehet und euch dann reumutig zu Ihm bekehret. Er wird euch dann bis zum Ende einer festgesetzten Frist reich versorgen. Und Seine Huld wird Er einem jeden gewahren, der sie verdient. Kehrt ihr euch jedoch (von Ihm) ab; wahrlich, dann furchte ich fur euch die Strafe eines großen Tages"
  },
  {"surah_number": 11, "verse_number": 4, "content": "Zu Allah werdet ihr heimkehren; und Er hat Macht uber alle Dinge"},
  {"surah_number": 11, "verse_number": 5, "content": "Gewiß nun, sie verschließen ihre Brust, um sich vor Ihm zu verstecken. Doch siehe, wenn sie sich auch mit ihren Gewandern bedecken, weiß Er, was sie verbergen und was sie offenbaren. Wahrlich, Er weiß wohl, was in den Herzen gehegt wird"},
  {"surah_number": 11, "verse_number": 6, "content": "Und es gibt kein Geschopf auf der Erde, dessen Versorgung nicht Allah oblage. Und Er kennt seinen Aufenthaltsort und seine Heimstatt. Alles ist in einer deutlichen Schrift (verzeichnet)"},
  {
    "surah_number": 11,
    "verse_number": 7,
    "content":
        "Und Er ist es, Der die Himmel und die Erde in sechs Tagen erschuf - und Sein Reich war auf dem Wasser -, damit Er prufe, wer von euch die beste Tat begehe. Und wenn du sprichst: \"Ihr werdet wahrlich nach dem Tode auferweckt werden\" dann werden die Unglaubigen gewiß sagen: \"Das ist nichts als offenkundige Zauberei"
  },
  {
    "surah_number": 11,
    "verse_number": 8,
    "content":
        "Und wenn Wir aber ihre Bestrafung bis zu einem be rechneten Zeitpunkt aufschoben, wurden sie sicherlich sagen: \"Was halt sie zuruck?\" Nun wahrlich, an dem Tage, an dem sie zu ihnen kommen wird, da wird keiner (sie) von ihnen abwenden konnen; und das, was sie zu verspotten pflegten, wird sie rings umschließen"
  },
  {"surah_number": 11, "verse_number": 9, "content": "Und wenn Wir dem Menschen Unsere Gnade zu kosten geben und sie ihm daraufhin fortnehmen, ist er verzweifelt und undankbar"},
  {"surah_number": 11, "verse_number": 10, "content": "Und wenn Wir ihm nach einer Drangsal, die ihn getroffen hat, eine Gabe bescheren, sagt er sicherlich: \"Das Ubel ist von mir gewichen.\" Siehe, er ist frohlockend und prahlend"},
  {"surah_number": 11, "verse_number": 11, "content": "Ausgenommen (von der Strafe) sind diejenigen, die geduldig sind und gute Werke tun. Ihnen wird Vergebung und ein großer Lohn zuteil sein"},
  {
    "surah_number": 11,
    "verse_number": 12,
    "content":
        "Vielleicht wirst du nun einen Teil von dem aufgeben, was dir offenbart wurde; und deine Brust wird davon so bedruckt sein, daß sie sagen: \"Warum ist nicht ein Schatz zu ihm niedergesandt worden oder ein Engel mit ihm gekommen?\" Du aber bist nur ein Warner, und Allah ist der Huter aller Dinge"
  },
  {"surah_number": 11, "verse_number": 13, "content": "Oder werden sie sagen: \"Er hat es erdichtet?\" Sprich: \"So bringt doch zehn ebenburtig erdichtete Suren hervor und ruft an, wen ihr vermogt, außer Allah, wenn ihr wahrhaftig seid"},
  {"surah_number": 11, "verse_number": 14, "content": "Und wenn sie darauf nicht reagieren, dann wisset, es ist mit Allahs Wissen offenbart worden; und es gibt keinen Gott außer Ihm. Wollt ihr euch nun ergeben"},
  {"surah_number": 11, "verse_number": 15, "content": "Diejenigen, die das irdische Leben und seine Pracht begehren, wollen Wir fur ihre irdischen Werke voll belohnen, und sie sollen (auf Erden) kein Unrecht erleiden"},
  {"surah_number": 11, "verse_number": 16, "content": "Diese sind es, die im Jenseits nichts als das Feuer erhalten sollen, und das, was sie hienieden gewirkt haben, wird nichtig sein, und eitel (wird) all das (sein), was sie zu tun pflegten"},
  {
    "surah_number": 11,
    "verse_number": 17,
    "content":
        "Kann denn der, der einen deutlichen Beweis von seinem Herrn besitzt und dem Sein Zeuge folgen soll und dem das Buch Moses' als Fuhrung und Barmherzigkeit vorausging(, ein Betruger sein)? Diese werden an ihn glauben, und wer von den Gruppen nicht an ihn glaubt das Feuer soll sein verheißener Ort sein. Sei daher daruber nicht im Zweifel. Dies ist die Wahrheit von deinem Herrn; jedoch die meisten Menschen glauben es nicht"
  },
  {
    "surah_number": 11,
    "verse_number": 18,
    "content": "Und wer ist ungerechter als der, der eine Luge gegen Allah erdichtet? Diese werden ihrem Herrn vorgefuhrt werden, und die Zeugen werden sagen: \"Das sind die, die gegen ihren Herrn logen.\" Hort! Wahrlich, der Fluch Allahs lastet auf den Ungerechten"
  },
  {"surah_number": 11, "verse_number": 19, "content": "die vom Wege Allahs abhalten und ihn zu krummen suchen. Und diese sind es, die nicht an das Jenseits glauben"},
  {"surah_number": 11, "verse_number": 20, "content": "sie vermogen nimmermehr im Lande zu siegen, noch haben sie irgendeinen Beschutzer vor Allah. Verdoppelt soll ihnen die Strafe werden! Sie konnten weder horen noch konnten sie sehen"},
  {"surah_number": 11, "verse_number": 21, "content": "Diese sind es, die ihrer selbst verlustig gegangen sind, und was sie ersonnen haben, soll ihnen nichts nutzen"},
  {"surah_number": 11, "verse_number": 22, "content": "(Es besteht) kein Zweifel (daruber), daß sie im Jenseits die großten Verlierer sein werden"},
  {"surah_number": 11, "verse_number": 23, "content": "Diejenigen aber, die glauben und gute Werke tun und sich vor ihrem Herrn demutigen, sind die Bewohner des Paradieses; darin sollen sie auf ewig verweilen"},
  {"surah_number": 11, "verse_number": 24, "content": "Der Fall der beiden Parteien ist wie der (Fall) des Blinden und des Tauben und des Sehenden und des Horenden. Sind nun beide wohl in der gleichen Lage? Wollt ihr denn nicht daran denken"},
  {"surah_number": 11, "verse_number": 25, "content": "Wir entsandten ja Noah zu seinem Volke (und er sagte): \"Wahrlich, ich bin fur euch ein deutlicher Warner"},
  {"surah_number": 11, "verse_number": 26, "content": "daß ihr keinen anderen außer Allah dient. Ich furchte fur euch die Strafe eines schmerzlichen Tages"},
  {
    "surah_number": 11,
    "verse_number": 27,
    "content":
        "Die Vornehmen seines Volkes, die nicht glaubten, sagten: \"Wir sehen in dir nur einen Menschen unseresgleichen und wir sehen, daß dir keine als jene gefolgt sind, die aller außeren Erscheinung nach die Niedrigsten unter uns sind, noch sehen wir in euch irgendeinen Vorzug vor uns; nein, wir erachten euch fur Lugner"
  },
  {"surah_number": 11, "verse_number": 28, "content": "Er sprach: \"O mein Volk, (ihr) seht nicht ein, daß ich einen klaren Beweis von meinem Herrn habe; und Er hat mir Seine Barmherzigkeit gewahrt, die euch aber verborgen geblieben ist. Sollen wir sie euch da aufzwingen, wo sie euch zuwider ist"},
  {"surah_number": 11, "verse_number": 29, "content": "O mein Volk, ich verlange von euch kein Entgelt dafur. Mein Lohn ist allein bei Allah. Und ich werde gewiß nicht jene, die glauben, verstoßen. Sie werden ihrem Herrn begegnen. Allein, ich erachte euch fur ein Volk, das unwissend ist"},
  {"surah_number": 11, "verse_number": 30, "content": "O mein Volk, wer wurde mir gegen Allah helfen, wenn ich sie verstieße? Wollt ihr (das) denn nicht einsehen"},
  {
    "surah_number": 11,
    "verse_number": 31,
    "content":
        "Und ich sage nicht zu euch: \"Ich besitze die Schatze Allahs\", noch kenne ich das Verborgene, noch erklare ich: \"Ich bin ein Engel.\" Noch sage ich von denen, die eure Augen verachten: \"Allah wird ihnen niemals (etwas) Gutes gewahren.\" Allah kennt am besten, was in ihrem Inneren vorgeht; wahrlich, ich gehorte sonst zu denen, die ungerecht sind"
  },
  {"surah_number": 11, "verse_number": 32, "content": "Sie sagten: \"O Noah, du hast schon (genug) mit uns gehadert und gar lange mit uns gehadert; so bring uns denn her, was du uns androhst, wenn du zu den Wahrhaftigen gehorst"},
  {"surah_number": 11, "verse_number": 33, "content": "Er sprach: \"Allah allein wird es euch bringen, wenn Er will, und ihr vermogt es nicht zu verhindern"},
  {"surah_number": 11, "verse_number": 34, "content": "Und mein Rat, den ich euch geben will, wird euch nichts nutzen, wenn Allah euch abirren lassen will. Er ist euer Herr; und zu Ihm werdet ihr zuruckgebracht werden"},
  {"surah_number": 11, "verse_number": 35, "content": "Ob sie sagen werden: \"Er hat es ersonnen?\" Sprich: \"Wenn ich es ersonnen habe, so sei dies meine Sunde; und ich bin nicht verantwortlich fur das, was ihr an Sunden begeht"},
  {"surah_number": 11, "verse_number": 36, "content": "Und es wurde Noah offenbart: \"Keiner von deinem Volk wird (dir) glauben, außer jenen, die (dir) bereits geglaubt haben: sei darum nicht traurig uber ihr Tun"},
  {"surah_number": 11, "verse_number": 37, "content": "Und baue das Schiff unter Unserer Aufsicht und nach Unserer Anweisung, und lege bei Mir keine Fursprache fur diejenigen ein, die gefrevelt haben; denn diese werden ertrinken"},
  {"surah_number": 11, "verse_number": 38, "content": "Und er baute also das Schiff; sooft die Vornehmen seines Volkes an ihm vorubergingen, verspotteten sie ihn. Er sprach: \"Verspottet ihr uns, so werden auch wir euch verspotten, gerade so, wie ihr spottet"},
  {"surah_number": 11, "verse_number": 39, "content": "Ihr werdet dann erfahren, wer es ist, uber den eine Strafe kommen wird, die ihn mit Schande bedeckt, und wen eine immerwahrende Strafe treffen wird"},
  {
    "surah_number": 11,
    "verse_number": 40,
    "content":
        "Alsdann erging Unser Befehl und die Fluten (der Erde) brachen hervor. Da sprachen Wir: \"Bringe in das Schiff je zwei von allen (Arten) hinein, Parchen, und deine Familie mit Ausnahme derer, gegen die das Wort bereits ergangen ist, und die Glaubigen.\" Und keiner glaubte ihm, außer einer kleinen Schar"
  },
  {"surah_number": 11, "verse_number": 41, "content": "Und er sprach: \"Steigt hinein! Im Namen Allahs erfolgt die Ausfahrt und die Landung. Mein Herr ist wahrlich Allverzeihend, Barmherzig"},
  {"surah_number": 11, "verse_number": 42, "content": "Und es (das Schiff) fuhr mit ihnen uber Wogen gleich Bergen einher, und Noah rief zu seinem Sohn, der sich abseits hielt: \"O mein Sohn, steig mit uns ein und bleibe nicht bei den Unglaubigen"},
  {
    "surah_number": 11,
    "verse_number": 43,
    "content":
        "Er sagte: \"Ich will mich sogleich auf einen Berg begeben, der mich vor dem Wasser retten wird.\" Er sagte: \"Es gibt heute keinen Retter vor Allahs Befehl - (Rettung) gibt es nur fur jene, derer Er Sich erbarmt.\" Und die Woge brach zwischen den beiden herein, (und) so war er unter denen, die ertranken"
  },
  {
    "surah_number": 11,
    "verse_number": 44,
    "content": "Und es wurde befohlen: \"O Erde, verschlinge dein Wasser, o Himmel, hore auf (zu regnen)!\" Und das Wasser begann zu sinken, und die Angelegenheit war entschieden. Und das Schiff kam auf dem Gudyy zur Rast. Und es wurde befohlen: \"Fort mit dem Volk der Frevler"
  },
  {"surah_number": 11, "verse_number": 45, "content": "Und Noah rief zu seinem Herrn und sagte: \"Mein Herr, mein Sohn gehort doch zu meiner Familie, und Dein Versprechen ist doch wahr, und Du bist der beste Richter"},
  {"surah_number": 11, "verse_number": 46, "content": "Er sprach: \"O Noah, er gehort nicht zu deiner Familie; siehe, dies ist kein rechtschaffenes Benehmen. So frage Mich nicht nach dem, von dem du keine Kenntnis hast. Ich ermahne dich, damit du nicht einer der Toren wirst"},
  {"surah_number": 11, "verse_number": 47, "content": "Er sagte: \"Mein Herr, ich nehme meine Zuflucht bei Dir davor daß ich Dich nach dem frage, wovon ich keine Kenntnis habe. Und wenn Du mir nicht verzeihst und Dich meiner nicht erbarmst, so werde ich unter den Verlierenden sein"},
  {
    "surah_number": 11,
    "verse_number": 48,
    "content":
        "Es wurde befohlen: \"O Noah, geh an Land, (und sei) mit Unserem Frieden begleitet! Und Segnungen (seien) uber dir und uber den Geschlechtern, die bei dir sind! Und es werden andere Geschlechter kommen, denen Wir Versorgung gewahren; dann aber wird Unsere schmerzliche Strafe sie treffen"
  },
  {"surah_number": 11, "verse_number": 49, "content": "Das ist einer der Berichte von den verborgenen Dingen, die Wir dir offenbaren. Zuvor kanntest du sie nicht, weder du noch dein Volk. So harre denn aus; denn der Ausgang entscheidet zugunsten der Gottesfurchtigen"},
  {"surah_number": 11, "verse_number": 50, "content": "Und zu den `Ad (entsandten Wir) ihren Bruder Hud. Er sagte: \"O mein Volk, dient Allah. Ihr habt keinen anderen Gott außer Ihm. Ihr seid nichts anderes als Lugner"},
  {"surah_number": 11, "verse_number": 51, "content": "O mein Volk, ich verlange von euch keinen Lohn dafur; seht, mein Lohn ist einzig bei Dem, Der mich erschuf. Wollt ihr es denn nicht begreifen"},
  {"surah_number": 11, "verse_number": 52, "content": "O mein Volk, erfleht Vergebung von eurem Herrn, dann bekehrt euch reumutig zu Ihm; Er wird (den Segen) in reichlicher Weise vom Himmel uber euch schicken und wird euch Kraft zu eurer Kraft hinzugeben. So wendet euch nicht als Schuldige ab"},
  {"surah_number": 11, "verse_number": 53, "content": "Sie sagten: \"O Hud, du hast uns kein deutliches Zeichen gebracht, und wir wollen unsere Gotter nicht auf dein Wort hin verlassen, noch wollen wir dir glauben"},
  {"surah_number": 11, "verse_number": 54, "content": "Wir konnen nur sagen, daß einige unserer Gotter dich mit einem Ubel heimgesucht haben.\" Er sagte: \"Ich rufe Allah zum Zeugen an, und bezeugt auch ihr, daß ich nicht an dem teilhabe, was ihr (Ihm) zur Seite stellt"},
  {"surah_number": 11, "verse_number": 55, "content": "außer Ihm; so schmiedet denn Plane gegen mich, ihr alle, und gewahrt mir keine Frist"},
  {"surah_number": 11, "verse_number": 56, "content": "Ich aber vertraue auf Allah, meinen Herrn und euren Herrn. Kein Geschopfbewegt sich (auf Erden), das Er nicht in Seine Richtung steuert. Seht, mein Herr ist auf dem geraden Weg"},
  {
    "surah_number": 11,
    "verse_number": 57,
    "content": "Wenn ihr euch nun (von Ihm) abkehrt, so habe ich euch schon uberbracht, womit ich zu euch entsandt wurde; und mein Herr wird ein anderes Volk an eure Stelle setzen. Und ihr konnt Ihm keineswegs schaden. Wahrlich, mein Herr ist Huter uber alle Dinge"
  },
  {"surah_number": 11, "verse_number": 58, "content": "Und als Unser Befehl kam, da erretteten Wir Hud und diejenigen, die mit ihm glaubig waren, durch Unsere Barmherzigkeit. Und Wir erretteten sie vor schwerer Strafe"},
  {"surah_number": 11, "verse_number": 59, "content": "Und diese waren die `Ad, die die Zeichen ihres Herrn leugneten und Seinen Gesandten nicht gehorchten und der Aufforderung eines jeden abschweifenden Tyrannen folgten"},
  {"surah_number": 11, "verse_number": 60, "content": "Ein Fluch verfolgte sie auf dieser Welt und (wird sie) am Tag der Auferstehung (verfolgen) Siehe, die `Ad haben sich gegen ihren Herrn undankbar erwiesen. Siehe, verstoßen sind die `Ad, das Volk Huds"},
  {
    "surah_number": 11,
    "verse_number": 61,
    "content":
        "Und zu den Tamud (entsandten Wir) ihren Bruder Salih; er sagte: \"O mein Volk, dient Allah; ihr habt keinen anderen Gott außer Ihm. Er hat euch aus der Erde hervorgebracht und ließ euch darauf ansiedeln. So erfleht Seine Vergebung, dann bekehrt euch zu Ihm. Wahrlich, mein Herr ist nahe (und) erhort die Gebete"
  },
  {"surah_number": 11, "verse_number": 62, "content": "Sie sagten: \"O Salih, du warst zuvor unter uns der Mittelpunkt der Hoffnung. Willst du uns verwehren, das anzubeten, was unsere Vater anbeteten? Und wir befinden uns wahrhaftig in beunruhigendem Zweifel uber das, wozu du uns aufforderst"},
  {
    "surah_number": 11,
    "verse_number": 63,
    "content": "Er sagte: \"O mein Volk, was meint ihr, wenn ich einen klaren Beweis von meinem Herrn habe und Er hat mir Seine Barmherzigkeit erwiesen -, wer wird mir dann gegen Allah helfen, wenn ich Ihm ungehorsam bin? So wurdet ihr mein Verderben nur fordern"
  },
  {"surah_number": 11, "verse_number": 64, "content": "O mein Volk, dies ist die Kamelstute Allahs als ein Zeichen fur euch; so lasset sie auf Allahs Erde weiden und fugt ihr kein Leid zu, damit euch nicht baldige Strafe erfasse"},
  {"surah_number": 11, "verse_number": 65, "content": "Doch sie zerschnitten ihr die Sehnen; da sagte er: \"Ergotzt euch in euren Hausern (noch) drei Tage. Das ist ein Urteil, das nicht zu verleugnen ist"},
  {"surah_number": 11, "verse_number": 66, "content": "Als Unser Befehl eintraf, da erretteten Wir Salih und diejenigen, die mit ihm glaubig waren, durch Unsere Barmherzigkeit, (Wir erretteten sie) vor der Schmach jenes Tages. Wahrlich, dein Herr ist der Allmachtige, der Erhabene"},
  {"surah_number": 11, "verse_number": 67, "content": "Da kam uber diejenigen, die frevelten, der Schrei; und da lagen sie in ihren Hausern leblos hingestreckt"},
  {"surah_number": 11, "verse_number": 68, "content": "als hatten sie nie darin gewohnt. Siehe, die Tamud zeigten sich undankbar gegen ihren Herrn; siehe, verstoßen sind die Tamud"},
  {"surah_number": 11, "verse_number": 69, "content": "Und es kamen Unsere Gesandten mit froher Botschaft zu Abraham. Sie sprachen: \"Friede!\" Er sagte: \"Friede!\" und es dauerte nicht lange, bis er ein gebratenes Kalb herbeibrachte"},
  {"surah_number": 11, "verse_number": 70, "content": "Als er aber sah, daß ihre Hande sich nicht danach ausstreckten, fand er sie befremdend und empfand Furcht vor ihnen. Sie sprachen: \"Furchte dich nicht; denn wir sind zum Volke Lots entsandt"},
  {"surah_number": 11, "verse_number": 71, "content": "Und seine Frau stand dabei und lachte, worauf Wir ihr die frohe Botschaft von (ihrem kunftigen Sohn) Isaak und von (dessen kunftigem Sohn) Jakob nach Isaak verkundeten"},
  {"surah_number": 11, "verse_number": 72, "content": "Sie sagte: \"Ach, wehe mir! Soll ich ein Kind gebaren, wo ich doch eine alte Frau bin und dieser mein Ehemann ein Greis ist? Das ware wahrlich eine wunderbare Sache"},
  {"surah_number": 11, "verse_number": 73, "content": "Da sprachen jene: \"Wunderst du dich uber den Beschluß Allahs? Allahs Gnade und Seine Segnungen sind uber euch, o Leute des Hauses. Wahrlich, Er ist Preiswurdig, Ruhmvoll"},
  {"surah_number": 11, "verse_number": 74, "content": "Als die Furcht von Abraham abließ und die frohe Botschaft zu ihm kam, da begann er, mit Uns uber das Volk Lots zu streiten"},
  {"surah_number": 11, "verse_number": 75, "content": "ahrlich, Abraham war milde, mitleidend und bußfertig"},
  {"surah_number": 11, "verse_number": 76, "content": "O Abraham, laß von diesem (Streit) ab. Siehe, schon ist der Befehl deines Herrn ergangen, und uber sie bricht ganz gewiß die unabwendbare Strafe herein"},
  {"surah_number": 11, "verse_number": 77, "content": "Und als Unsere Gesandten zu Lot kamen geriet er ihretwegen in Bedrangnis, wurde ratlos und entsetzt und sagte: \"Das ist ein schwerer Tag"},
  {
    "surah_number": 11,
    "verse_number": 78,
    "content":
        "Und sein Volk kam eilends zu ihm gelaufen; und schon zuvor hatten sie Schlechtigkeiten verubt. Er sagte: \"O mein Volk, dies hier sind meine Tochter; sie sind reiner fur euch (als meine Gaste) So furchtet Allah und bringt keine Schande hinsichtlich meiner Gaste uber mich. Ist denn kein redlicher Mann unter euch"
  },
  {"surah_number": 11, "verse_number": 79, "content": "Sie sagten: \"Du weißt recht wohl, daß wir keine Absicht hinsichtlich deiner Tochter hegen, und du weißt wohl, was wir wollen"},
  {"surah_number": 11, "verse_number": 80, "content": "Er sagte: \"Hatte ich doch Macht uber euch, oder konnte ich mich an eine starke Stutze lehnen"},
  {
    "surah_number": 11,
    "verse_number": 81,
    "content":
        "Sie sprachen: \"O Lot, wir sind Gesandte deines Herrn. Sie sollen dich nimmermehr erreichen. So mache dich mit den Deinen in einer Phase der Nacht auf; und niemand von euch wende sich um, außer deiner Frau. Gewiß, was jene dort treffen wird, das wird auch sie treffen. Siehe, am Morgen ist ihr Termin. Ist nicht der Morgen nahe"
  },
  {"surah_number": 11, "verse_number": 82, "content": "Als nun Unser Befehl eintraf, da kehrten Wir in dieser (Stadt) das Oberste zuunterst und ließen auch brennende Steine niedergehen, die wie Regentropfen aufeinander folgten"},
  {"surah_number": 11, "verse_number": 83, "content": "Und (dies war fur sie) bei deinem Herrn aufgezeichnet. Und das gleiche liegt auch den Ungerechten nicht fern"},
  {
    "surah_number": 11,
    "verse_number": 84,
    "content": "Und zu den Madyan (entsandten Wir) ihren Bruder Suaib. Er sagte: \"O mein Volk, dient Allah. Ihr habt keinen anderen Gott außer Ihm. Und verkurzt nicht Maß und Gewicht. Ich sehe euch im Wohlsein, aber ich furchte fur euch die Strafe eines vollig vernichtenden Tages"
  },
  {"surah_number": 11, "verse_number": 85, "content": "O mein Volk, gebt volles Maß und Gewicht nach Richtigkeit und betrugt die Menschen nicht um ihr Eigentum und treibt im Land nicht euer Unwesen, indem ihr Unheil anrichtet"},
  {"surah_number": 11, "verse_number": 86, "content": "Das, was Allah (bei euch) ließ, ist besser fur euch, wenn ihr Glaubige seid. Und ich bin nicht euer Wachter"},
  {"surah_number": 11, "verse_number": 87, "content": "Sie sagten: \"O Suaib, heißt dein Gebet, daß wir das verlassen sollen, was unsere Vater anbeteten, oder daß wir aufhoren sollen, mit unserem Besitz zu tun, was uns gefallt? Du bist doch wahrlich der Milde und der Mundige"},
  {
    "surah_number": 11,
    "verse_number": 88,
    "content":
        "Er sagte: \"O mein Volk, seht ihr nicht ein, daß ich einen deutlichen Beweis von meinem Herrn habe und (daß) Er mir Seine Sorge angedeihen ließ? Und ich will gegen euch nicht so handeln, wie ich es euch zu unterlassen bitte. Ich will nur Besse rung, soweit ich (es erreichen) kann. Es ist keine Macht in mir als durch Allah. Auf Ihn vertraue ich und an Ihn wende ich mich"
  },
  {"surah_number": 11, "verse_number": 89, "content": "O mein Volk, moge die Feindseligkeit gegen mich euch nicht dazu fuhren, daß euch das gleiche wie das trifft, was das Volk Noahs oder das Volk Huds oder das Volk Salihs getroffen hat; und das (Schicksal) des Volkes Lots ist euch nicht fern"},
  {"surah_number": 11, "verse_number": 90, "content": "Und bittet euren Herrn um Vergebung, (und) dann bekehrt euch zu Ihm. Wahrlich, mein Herr ist Barmherzig, Liebevoll"},
  {"surah_number": 11, "verse_number": 91, "content": "Sie sagten: \"O Suaib, wir verstehen nicht viel von dem, was du sprichst, und wir sehen nur, daß du unter uns schwach bist. Und ware nicht dein Stamm, wurden wir dich steinigen. Und du wirst kein Mitleid in uns wecken"},
  {"surah_number": 11, "verse_number": 92, "content": "Er sagte: \"O mein Volk, ist mein Stamm bei euch geehrter als Allah? Und ihr nehmt Ihn nicht ernst. Doch wahrlich, mein Herr umfaßt alles, was ihr tut"},
  {"surah_number": 11, "verse_number": 93, "content": "O mein Volk, handelt eurem Standpunkt gemaß, auch ich handle (meinem Standpunkt gemaß) Bald werdet ihr erfahren, auf wen eine Strafe niederfallen wird, die ihn schandet, und wer ein Lugner ist. Und paßt nur auf; seht, ich passe mit euch auf"},
  {"surah_number": 11, "verse_number": 94, "content": "Und als Unser Befehl eintraf, da erretteten Wir Suaib und diejenigen, die mit ihm glaubig waren, durch Unsere Barmherzigkeit; und der Schrei erfaßte die, welche gefrevelt hatten, so daß sie leblos in ihren Hausern lagen"},
  {"surah_number": 11, "verse_number": 95, "content": "so als hatten sie nie darin gewohnt. Wahrlich, Madyan wurde gerade so verstoßen wie (das Volk) Tamud verstoßen worden war"},
  {"surah_number": 11, "verse_number": 96, "content": "Wahrlich, Wir entsandten Moses mit Unseren Zeichen und offenbarem Beweis"},
  {"surah_number": 11, "verse_number": 97, "content": "zu Pharao und seinen Vornehmen; jedoch sie folgten Pharaos Befehl, und Pharaos Befehl war keineswegs klug"},
  {"surah_number": 11, "verse_number": 98, "content": "Am Tage der Auferstehung soll er seinem Volk vorangehen und sie in das Feuer hinabfuhren. Und schlimm ist das Ziel, wohin sie gelangen"},
  {"surah_number": 11, "verse_number": 99, "content": "Und ein Fluch verfolgt sie hienieden und (wird sie) am Tage der Auferstehung (verfolgen) Schlimm ist die Gabe, die (ihnen) gegeben wird"},
  {"surah_number": 11, "verse_number": 100, "content": "Das ist die Kunde von den Stadten, die Wir dir erzahlen. Manche von ihnen stehen noch aufrecht da, und (manche) sind zerstort worden"},
  {"surah_number": 11, "verse_number": 101, "content": "Nicht Wir taten ihnen Unrecht, sondern sie taten sich selber Unrecht an; und ihre Gotter, die sie statt Allah anriefen, nutzten ihnen uberhaupt nichts, als der Befehl deines Herrn eintraf; sie sturzten sie nur noch mehr ins Verderben"},
  {"surah_number": 11, "verse_number": 102, "content": "Und so ist der Griff deines Herrn, wenn Er die Stadte erfaßt, weil sie freveln. Wahrlich, Sein Griff ist schmerzhaft, streng"},
  {"surah_number": 11, "verse_number": 103, "content": "Darin liegt gewiß ein Zeichen fur den, der die Strafe des Jenseits furchtet. Das ist ein Tag, an dem die Menschen versammelt werden, und das ist ein Tag, den jeder erleben wird"},
  {"surah_number": 11, "verse_number": 104, "content": "Und Wir verschieben ihn nur bis zu einem berechneten Termin"},
  {"surah_number": 11, "verse_number": 105, "content": "Wenn jener (Tag) kommt, dann wird keine Seele sprechen, es sei denn mit Seiner Erlaubnis; unter ihnen gibt es dann welche, die unselig und welche, die selig sind"},
  {"surah_number": 11, "verse_number": 106, "content": "Was nun diejenigen angeht, die unselig sind, so werden sie ins Feuer gelangen, worin sie seufzen und schluchzen werden"},
  {"surah_number": 11, "verse_number": 107, "content": "Darin werden sie auf ewig bleiben, solange die Himmel und die Erde dauern, es sei denn, dein Herr will es anders. Wahrlich, dein Herr tut, was Er will"},
  {"surah_number": 11, "verse_number": 108, "content": "Was aber diejenigen angeht, die glucklich sind, so werden sie im Paradies sein, und sie werden darin auf ewig verweilen, solange die Himmel und die Erde dauern, es sei denn, dein Herr will es anders eine Gabe, die nicht unterbrochen wird"},
  {"surah_number": 11, "verse_number": 109, "content": "Sei darum nicht im Zweifel daruber, was diese Leute verehren; sie verehren nur, was ihre Vater zuvor verehrten, und Wir wollen ihnen wahrlich ihren vollen Anteil unverkurzt gewahren"},
  {
    "surah_number": 11,
    "verse_number": 110,
    "content": "Und wahrlich, Wir haben Moses die Schrift gegeben, doch dann entstand Uneinigkeit uber sie; und ware nicht schon zuvor ein Wort von deinem Herrn ergangen, ware es zwischen ihnen entschieden worden; und sie befinden sich daruber immer noch in bedenklichem Zweifel"
  },
  {"surah_number": 11, "verse_number": 111, "content": "Und allen wird dein Herrsicher ihre Taten vergelten. Er ist gut uber das unterrichtet, was sie tun"},
  {"surah_number": 11, "verse_number": 112, "content": "andle du darum aufrichtig, wie dir befohlen worden ist - und die (sollen aufrichtig handeln,) die sich mit dir bekehrt haben. Und seid nicht aufsassig; wahrlich, Er ist dessen ansichtig, was ihr tut"},
  {"surah_number": 11, "verse_number": 113, "content": "Und neigt euch nicht zu den Ungerech ten, damit euch das Feuer nicht erfasse. Und ihr werdet keine Beschutzer außer Allah haben, noch wird euch geholfen werden"},
  {"surah_number": 11, "verse_number": 114, "content": "Und verrichte das Gebet an den beiden Tagesenden und in den Stunden der Nacht, die dem Tage naher sind. Wahrlich, die guten Taten tilgen die bosen. Das ist eine Ermahnung fur die Nachdenkli chen"},
  {"surah_number": 11, "verse_number": 115, "content": "Und sei geduldig; denn wahrlich, Allah laßt den Lohn der Rechtschaffenen nicht verlorengehen"},
  {
    "surah_number": 11,
    "verse_number": 116,
    "content":
        "Waren nur unter den Geschlechtern vor euch Leute von Verstand gewesen, die der Verderbtheit auf Erden hatten entgegensteuern konnen - ausgenommen die wenigen, die Wir aus ihrer Zahl erretteten! Doch die Frevler folgten dem, was ihnen Genuß und Behagen versprach, und sie ubertraten (die Gebote)"
  },
  {"surah_number": 11, "verse_number": 117, "content": "Und dein Herr ist nicht Der, Der die Stadte zu Unrecht zerstoren wurde, wenn ihre Bewohner rechtschaffen waren"},
  {"surah_number": 11, "verse_number": 118, "content": "Und hatte dein Herr es gewollt, so hatte Er die Menschen alle zu einer einzigen Gemeinde gemacht; doch sie wollten nicht davon ablassen, uneins zu sein"},
  {"surah_number": 11, "verse_number": 119, "content": "Ausgenommen davon sind jene, derer dein Herr Sich erbarmt hat, und dazu hat Er sie erschaffen. Und das Wort deines Herrn ist in Erfullung gegangen: \"Wahrlich, Ich werde Gahannam mit den Ginn und den Menschen insgesamt fullen"},
  {"surah_number": 11, "verse_number": 120, "content": "Und Wir berichten dir von den Geschichten der Gesandten, um dein Herz zu festigen. Und hierin ist die Wahrheit zu dir gekommen und eine Ermahnung und eine Erinnerung fur die Glaubigen"},
  {"surah_number": 11, "verse_number": 121, "content": "Und sprich zu denen, die nicht glauben: \"Handelt eurem Standpunkt gemaß, auch wir handeln (unserem Standpunkt gemaß)"},
  {"surah_number": 11, "verse_number": 122, "content": "Und wartet nur, auch wir warten"},
  {"surah_number": 11, "verse_number": 123, "content": "Und Allahs ist das Verborgene in den Himmeln und auf der Erde, zu Ihm werden alle Angelegenheiten zuruckgebracht werden. So bete Ihn an und vertraue auf Ihn; und dein Herr ist nicht achtlos eures Tuns"},
  {"surah_number": 12, "verse_number": 1, "content": "Alif Lam Ra ' Das sind die Verse der deutlichen Schrift"},
  {"surah_number": 12, "verse_number": 2, "content": "Wir haben sie als Quran auf Arabisch offenbart, auf daß ihr (sie) begreifen moget"},
  {"surah_number": 12, "verse_number": 3, "content": "Wir erzahlen dir die schonste der Geschichten, indem Wir dir diesen Quran offenbaren, obwohl du zuvor unter denen warst die keine Kenntnis besaßen"},
  {"surah_number": 12, "verse_number": 4, "content": "Damals sagte Yusuf zu seinem Vater: \"O mein Vater, ich sah elf Sterne und die Sonne und den Mond, (und) ich sah sie vor mir niederfallen"},
  {"surah_number": 12, "verse_number": 5, "content": "Er sagte: \"Du, mein Sohnchen, erzahle deinen Traum nicht deinen Brudern, sie werden sonst eine List gegen dich ersinnen; denn Satan ist dem Menschen ein offenkundiger Feind"},
  {
    "surah_number": 12,
    "verse_number": 6,
    "content": "Und so wird dein Herr dich erwahlen und dich die Deutung der Traume lehren und Seine Huld an dir und an dem Geschlecht Jakobs vollenden, so wie Er sie zuvor an zweien deiner Vorvater vollendete, an Abraham und Isaak. Wahrlich, dein Herr ist Allwissend, Allweise"
  },
  {"surah_number": 12, "verse_number": 7, "content": "Gewiß, in (der Geschichte von) Yusuf und seinen Brudern liegen Zeichen fur die Ratsuchenden"},
  {"surah_number": 12, "verse_number": 8, "content": "Damals sagten sie: \"Wahrlich, Yusuf und sein Bruder sind unserem Vater lieber als wir, obwohl wir mehrere sind. Unser Vater befindet sich gewiß in einem offenkundigen Irrtum"},
  {"surah_number": 12, "verse_number": 9, "content": "Totet Yusuf oder vertreibt ihn in ein fernes Land; frei (fur euch) wird damit das Antlitz eures Vaters, und ihr werdet danach gute Leute sein"},
  {"surah_number": 12, "verse_number": 10, "content": "Es sagte einer von ihnen: \"Totet Yusuf nicht; wenn ihr aber vorhabt, etwas zu unternehmen, dann werft ihn in die Tiefe eines Brunnens: moge jemand von den Reisenden ihn herausziehen"},
  {"surah_number": 12, "verse_number": 11, "content": "Sie sagten: \"O unser Vater, warum vertraust du uns Yusuf nicht an, obwohl wir es wahrhaftig gut mit ihm meinen"},
  {"surah_number": 12, "verse_number": 12, "content": "Schicke ihn morgen mit uns, damit er sich vergnuge und spiele, und wir wollen gut auf ihn aufpassen"},
  {"surah_number": 12, "verse_number": 13, "content": "Er sagte: \"Es macht mich traurig, wenn ihr ihn mit fortnehmt, und ich furchte, der Wolf konnte ihn fressen, wenn ihr nicht auf ihn achtgebt"},
  {"surah_number": 12, "verse_number": 14, "content": "Sie sagten: \"Wenn ihn der Wolf frißt, obgleich wir zu mehre ren sind, dann werden wir wahrlich die Verlierenden sein"},
  {"surah_number": 12, "verse_number": 15, "content": "Und als sie ihn also mit sich fortnahmen und beschlossen, ihn in die Tiefe des Brunnens zu werfen gaben Wir ihm ein: \"Du wirst ihnen diese ihre Tat dereinst sicherlich verkunden, ohne daß sie es merken"},
  {"surah_number": 12, "verse_number": 16, "content": "Und am Abend kamen sie weinend zu ihrem Va ter"},
  {"surah_number": 12, "verse_number": 17, "content": "Sie sagten: \"O unser Vater, wir liefen miteinander um die Wette und ließen Yusuf bei unseren Sachen zuruck, und da hat ihn der Wolf gefressen; du wirst uns doch nicht glauben, auch wenn wir die Wahrheit; aussprechen"},
  {"surah_number": 12, "verse_number": 18, "content": "Und sie hatten falsches Blut auf sein Hemd gebracht. Er sagte: \"Nein, ihr habt das geplant. Doch schon geduldig sein. Und Allah sei um Hilfe wider das gebeten, was ihr beschreibt"},
  {"surah_number": 12, "verse_number": 19, "content": "Und dann kam eine Karawane, und sie schickte ihren Wasserschopfer voraus. Er ließ seinen Eimer herab; und er sagte: \"O Glucksbotschaft! Hier ist ein Jungling!\" Und sie verbargen ihn wie eine Ware, und Allah wußte wohl, was sie taten"},
  {"surah_number": 12, "verse_number": 20, "content": "Und sie verkauften ihn zu einem schabigen Preis fur einige Dirhams; denn (an ihm) hatten sie kein Interesse"},
  {
    "surah_number": 12,
    "verse_number": 21,
    "content":
        "Und derjenige (Mann) aus Agypten, der ihn gekauft hatte, sagte zu seiner Frau: \"Nimm ihn freundlich auf. Vielleicht kann er uns einmal nutzlich werden, oder wir nehmen ihn als Sohn an.\" Und so gaben Wir Yusuf Macht im Land, und Wir lehrten ihn (auch) die Deutung der Traume. Und Allah setzt das durch, was Er beschließt. Die meisten Menschen aber wissen es nicht"
  },
  {"surah_number": 12, "verse_number": 22, "content": "Und als er zum Mann heranwuchs, verliehen Wir ihm Weisheit und Wissen. Und so belohnen Wir diejenigen, die Gutes tun"},
  {
    "surah_number": 12,
    "verse_number": 23,
    "content":
        "Und sie (die Frau), in deren Haus er war, versuchte ihn zu verfuhren gegen seinen Willen; und sie verriegelte die Turen und sagte: \"Nun komm zu mir!\" Er sagte: \"Ich suche Zuflucht bei Allah. Er ist mein Herr. Er hat meinen Aufenthalt ehrenvoll gemacht. Wahrlich, die Frevler erlangen keinen Erfolg"
  },
  {
    "surah_number": 12,
    "verse_number": 24,
    "content": "Und sie begehrte ihn (und) auch er hatte sie begehrt, wenn er nicht ein deutliches Zeichen von seinem Herrn gesehen hatte. Das geschah, um Schlechtigkeit und Unsittlichkeit von ihm abzuwenden. Wahrlich, er war einer Unserer auserwahlten Diener"
  },
  {
    "surah_number": 12,
    "verse_number": 25,
    "content": "Und sie eilten beide zur Tur, und sie zerriß sein Hemd von hinten, und sie trafen an der Tur auf ihren Mann. Sie sagte: \"Was soll der Lohn desjenigen sein, der gegen deine Familie etwas Boses plante, wenn nicht das Gefangnis oder sonst irgendeine schmerzliche Strafe"
  },
  {"surah_number": 12, "verse_number": 26, "content": "Er sagte: \"Sie war es, die mich gegen meinen Willen zu verfuhren suchte.\" Und ein Zeuge aus ihrer Familie bezeugte es: \"Wenn sein Hemd vorne zerrissen ist, dann hat sie die Wahrheit gesprochen und er ist ein Lugner"},
  {"surah_number": 12, "verse_number": 27, "content": "Wenn sein Hemd jedoch hinten zerrissen ist, so hat sie gelogen, und er ist der, der die Wahrheit sagte"},
  {"surah_number": 12, "verse_number": 28, "content": "Als er nun sah, daß sein Hemd hinten zerrissen war, da sagte er: \"Wahrlich, das ist eine List von euch (Weibern); eure List ist wahrlich groß"},
  {"surah_number": 12, "verse_number": 29, "content": "O Yusuf, wende dich ab von dieser Sache, und du (, o Frau), bitte um Vergebung fur deine Sunde. Denn gewiß, du gehorst zu den Schuldigen"},
  {"surah_number": 12, "verse_number": 30, "content": "Und einige Frauen in der Stadt sagten: \"Die Frau des `Aziz verlangt von ihrem Burschen die Hingabe gegen seinen Willen; sie ist ganz verliebt in ihn. Wahrlich, wir sehen sie in einem offenbaren Irrtum"},
  {
    "surah_number": 12,
    "verse_number": 31,
    "content":
        "Als sie von ihren Ranken hone, da sandte sie ihnen (Einladungen) und bereitete ein Gastmahl fur sie und gab einer jeden von ihnen ein Messer und sagte (zu Yusuf): \"Komm heraus zu ihnen!\" Als sie ihn sahen, bestaunten sie ihn und schnitten sich (dabei) in die Hande und sagten: \"Allah bewahre! Das ist kein Mensch, das ist nichts als ein edler Engel"
  },
  {
    "surah_number": 12,
    "verse_number": 32,
    "content":
        "Sie sagte: \"Und dieser ist es, um dessentwillen ihr mich getadelt habt. Ich habe allerdings versucht, ihn gegen seinen Willen zu verfuhren, doch er bewahrte sich. Wenn er nun nicht tut, was ich ihm befehle, so soll er unweigerlich ins Gefangnis geworfen werden und einer der Gedemutigten sein"
  },
  {"surah_number": 12, "verse_number": 33, "content": "Er sagte: \"O mein Herr, mir ist das Gefangnis lieber als das, wozu sie mich auffordern; und wenn Du ihre List nicht von mir abwendest, so konnte ich mich ihnen zuneigen und einer der Unwissenden sein"},
  {"surah_number": 12, "verse_number": 34, "content": "Da erhorte ihn sein Herr und wendete ihre List von ihm ab. Wahrlich, Er ist der Allhorende, der Allwissende"},
  {"surah_number": 12, "verse_number": 35, "content": "Hierauf, nachdem sie die Zeichen (seiner Unschuld) gesehen hatten, schien es ihnen angebracht (zu sein), ihn eine Zeitlang einzukerkern"},
  {
    "surah_number": 12,
    "verse_number": 36,
    "content":
        "Und mit ihm kamen zwei junge Manner ins Gefangnis. Der eine von ihnen sagte: \"Ich sah mich Wein auspressen.\" Und der andere sagte: \"Ich sah mich auf meinem Kopf Brot tragen, von dem die Vogel fraßen. Verkunde uns die Deutung hiervon; denn wir sehen, daß du einer der Rechtschaffenen bist"
  },
  {
    "surah_number": 12,
    "verse_number": 37,
    "content":
        "Er sprach: \"Jedes Essen, mit dem ihr versorgt werdet, wird euch nicht eher gebracht werden, bevor ich euch hiervon berichtet habe, und zwar noch, ehe es zu euch kommt. Dies (geschieht) auf Grund dessen, was mich mein Herr gelehrt hat. Ich habe die Religion jener Leute verlassen, die nicht an Allah glauben und Leugner des Jenseits sind"
  },
  {"surah_number": 12, "verse_number": 38, "content": "Und ich folge der Religion meiner Vater Abraham und Isaak und Jakob. Uns geziemt es nicht, Allah irgend etwas zur Seite zu stellen. Dies ist etwas von Allahs Huld gegen uns und gegen die Menschheit, jedoch die meisten Menschen sind undankbar"},
  {"surah_number": 12, "verse_number": 39, "content": "O meine beiden Kerkergenossen, sind Herren in großerer Anzahl besser oder (ist) Allah (besser), der Eine, der Allmachtige"},
  {
    "surah_number": 12,
    "verse_number": 40,
    "content":
        "Statt Ihm verehrt ihr nichts anderes als Namen, die ihr selbst genannt habt, ihr und eure Vater; Allah hat dazu keine Ermachtigung herabgesandt. Die Entscheidung liegt einzig bei Allah. Er hat geboten, Ihn allein zu verehren. Das ist der richtige Glaube, jedoch die meisten Menschen wissen es nicht"
  },
  {
    "surah_number": 12,
    "verse_number": 41,
    "content": "O meine beiden Kerkergenossen, was den einen von euch anbelangt, so wird er seinem Herrn Wein ausschenken; und was den anderen anbelangt, so wird er gekreuzigt werden, so daß die Vogel von seinem Kopfe fressen. Die Sache, woruber ihr Auskunft verlangtet, ist beschlossen"
  },
  {"surah_number": 12, "verse_number": 42, "content": "Und er sagte zu dem von den beiden, von dem er glaubte, er wurde entkommen: \"Erwahne mich bei deinem Herrn.\" Doch Satan ließ ihn vergessen es bei seinem Herrn zu erwahnen, (und) so blieb er noch einige Jahre im Gefangnis"},
  {
    "surah_number": 12,
    "verse_number": 43,
    "content": "Und der Konig sprach: \"Ich sehe sieben fette Kuhe, und sie werden von sieben mageren gefressen; und ich sehe sieben grune Ahren und(sieben) andere durre. O ihr Vornehmen, erklart mir die Bedeutung meines Traums, wenn ihr einen Traum auszulegen versteht"
  },
  {"surah_number": 12, "verse_number": 44, "content": "Sie sagten: \"Ein Wirrnis von Traumen! Und nicht wir sind jene, die das Deuten der Traume beherrschen"},
  {"surah_number": 12, "verse_number": 45, "content": "Und derjenige von den beiden, der (dem Kerker) entkommen war und sich nach geraumer Zeit (wieder an Yusuf) erinnerte, sprach: \"Ich will euch die Deutung davon wissen lassen, darum schickt mich (zu Yusuf)"},
  {
    "surah_number": 12,
    "verse_number": 46,
    "content": "Yusuf, o du Wahrhaftiger, erklare uns die Bedeutung von sieben fetten Kuhen, die von sieben mageren gefressen werden, und (die Bedeutung) von sieben grunen Ahren und (sieben) anderen durren, auf daß ich zuruckkehre zu den Leuten, damit sie es erfahren"
  },
  {"surah_number": 12, "verse_number": 47, "content": "Er sprach: \"lhr werdet ununterbrochen sieben Jahre lang saen und hart arbeiten; und was ihr erntet, belaßt auf den Ahren, bis auf das wenige, was ihr esset"},
  {"surah_number": 12, "verse_number": 48, "content": "Danach werden dann sieben schwere Jahre kommen, die alles aufzehren, was ihr an Vorrat fur sie aufgespeichert habt, bis auf das wenige, was ihr bewahren moget"},
  {"surah_number": 12, "verse_number": 49, "content": "Danach wird ein Jahr kommen, in welchem die Menschen Erleichterung finden und in welchem sie (Fruchte) pressen"},
  {"surah_number": 12, "verse_number": 50, "content": "Und der Konig sprach: \"Bringt ihn zu mir!\" Doch als der Bote zu ihm kam, sprach er: \"Kehre zuruck zu deinem Herrn und frage ihn, wie es den Frauen ergeht, die sich in die Hande schnitten; denn mein Herr kennt ihre List recht wohl"},
  {
    "surah_number": 12,
    "verse_number": 51,
    "content":
        "Er sprach (zu den Frauen): \"Wie stand es um euch, als ihr eure Verfuhrungskunste an Yusuf gegen seinen Willen ausprobiertet?\" Sie sagten: \"Allah bewahre! Wir haben nichts Boses uber ihn erfahren!\" Da sprach die Frau des `Aziz: \"Nun ist die Wahrheit ans Licht gekommen. Ich versuchte, ihn gegen seinen Willen zu verfuhren, und er gehort sicherlich zu den Wahrhaftigen"
  },
  {"surah_number": 12, "verse_number": 52, "content": "(Yusuf sprach): \"Dies (kommt ans Licht), damit er (`Aziz) erfahrt, daß ich in (seiner) Abwesenheit gegen ihn nicht treulos war, und damit Allah die List der Treulosen nicht gelingen laßt"},
  {"surah_number": 12, "verse_number": 53, "content": "Und ich behaupte nicht, daß ich unschuldig bin; denn das (Menschen-)Wesen gebietet oft Boses; davon sind jene ausgenommen, derer mein Herr Sich erbarmt. Wahrlich, mein Herr ist Allverzeihend, Barmherzig"},
  {"surah_number": 12, "verse_number": 54, "content": "Und der Konig sprach: \"Bringt ihn zu mir, ich will ihn fur mich vorbehalten.\" Als er mit ihm geredet hatte, sprach er: \"Du bist von heute an bei uns in Sicherheit und genießt unser Vertrauen"},
  {"surah_number": 12, "verse_number": 55, "content": "Er sprach: \"Setze mich uber die Schatzkammern des Landes ein; denn ich bin ein wohlerfahrener Huter"},
  {"surah_number": 12, "verse_number": 56, "content": "Und so verliehen Wir Yusuf Macht im Lande; er weilte darin, wo immer es ihm gefiel. Wir gewahren Unsere Gnade, wem Wir wollen, und Wir lassen den Lohn der Rechtschaffenen nicht verlorengehen"},
  {"surah_number": 12, "verse_number": 57, "content": "Der Lohn des Jenseits aber ist besser fur jene, die glauben und (Allah) furchten"},
  {"surah_number": 12, "verse_number": 58, "content": "Und es kamen die Bruder Yusufs und traten zu ihm ein; er erkannte sie, sie aber erkannten ihn nicht"},
  {"surah_number": 12, "verse_number": 59, "content": "Und als er sie mit ihrem Bedarf ausgerustet hatte, da sprach er: \"Bringt mir euren Bruder von eures Vaters Seite. Seht ihr nicht, daß ich volles Maß gebe und daß ich der beste Gastgeber bin"},
  {"surah_number": 12, "verse_number": 60, "content": "Doch wenn ihr ihn mir nicht bringt, dann sollt ihr kein Maß von mir erhalten, noch sollt ihr mir nahe kommen"},
  {"surah_number": 12, "verse_number": 61, "content": "Sie sagten: \"Wir wollen versuchen, ihn von seinem Vater zu trennen; und das tun wir bestimmt"},
  {"surah_number": 12, "verse_number": 62, "content": "Und er sprach zu seinen Dienern: \"Steckt ihre Ware in ihre Satteltaschen, so daß sie sie erkennen konnen, wenn sie zu ihren Angehorigen zuruckgekehrt sind; vielleicht kommen sie wieder"},
  {"surah_number": 12, "verse_number": 63, "content": "Als sie zu ihrem Vater zuruckgekehrt waren, sagten sie: \"O unser Vater, das Maß ist uns verweigert worden; so schicke unseren Bruder mit uns, so daß wir das Maß erhalten; und wir wollen ihn huten"},
  {"surah_number": 12, "verse_number": 64, "content": "Er sprach: \"Ich kann ihn euch nicht anders anvertrauen, als ich euch zuvor seinen Bruder anvertraut habe. Doch Allah ist der beste Beschutzer, und Er ist der Barmherzigste Erbarmer"},
  {
    "surah_number": 12,
    "verse_number": 65,
    "content":
        "Und als sie ihr Gepack offneten, da fanden sie, daß ihnen ihre Ware wiedergegeben worden war. Sie sagten: \"O unser Vater, was konnen wir (uns) mehr wunschen? Diese unsere Ware ist uns zuruckgegeben worden. Wir werden den Vorrat fur unsere Familie heimbringen und unseren Bruder behuten, und uberdies werden wir das Maß einer Kamellast haben. Das ist ein leicht erhaltliches Maß"
  },
  {
    "surah_number": 12,
    "verse_number": 66,
    "content":
        "Er sprach: \"Ich werde ihn nicht mit euch schicken, ehe ihr mir nicht ein festes Versprechen im Namen Allahs gebt, daß ihr ihn mir sicher wiederbringt, es sei denn, daß euch selbst die Ruckkehr abgeschnitten wird.\" Als sie ihm ihr festes Versprechen gegeben hatten, sprach er: \"Allah wacht uber das, was wir sagen"
  },
  {
    "surah_number": 12,
    "verse_number": 67,
    "content": "Und er sprach: \"O meine Sohne, zieht nicht durch ein einziges Tor ein, sondern zieht durch verschiedene Tore ein; ich kann euch nichts gegen Allah nutzen. Die Entscheidung ruht bei Allah allein. Auf Ihn vertraue ich, und auf Ihn sollen die Vertrauenden vertrauen"
  },
  {
    "surah_number": 12,
    "verse_number": 68,
    "content": "Daß sie auf die Art eingezogen waren wie ihr Vater es ihnen geboten hatte, konnte nichts gegen Allah nutzen; nur wurde auf diese Weise das Verlangen in Jakobs Seele befriedigt; und er besaß gewiß großes Wissen, weil Wir ihn gelehrt hatten, allein die meisten Menschen wissen es nicht"
  },
  {"surah_number": 12, "verse_number": 69, "content": "Und als sie vor Yusuf traten, nahm er seinen Bruder zu sich. Er sprach: \"Ich bin dein Bruder; so sei nicht traurig uber das, was sie getan haben"},
  {"surah_number": 12, "verse_number": 70, "content": "Als er sie dann mit ihrem Bedarf ausgerustet hatte, steckte er den Trinkbecher (des Konigs) in die Satteltaschen seines Bruders. Dann gab ein Ausrufer bekannt: \"O ihr Kamelfuhrer, ihr seid wahrhaftig Diebe"},
  {"surah_number": 12, "verse_number": 71, "content": "Sie sagten, indem sie sich zu ihnen wandten: \"Was vermißt ihr"},
  {"surah_number": 12, "verse_number": 72, "content": "Jene sagten: \"Wir vermissen den Maßbecher des Konigs, und wer ihn wiederbringt, soll eine Kamellast erhalten, ich burge dafur"},
  {"surah_number": 12, "verse_number": 73, "content": "Sie sagten: \"Bei Allah, ihr wisset doch, daß wir nicht gekommen sind, um Unheil im Land zu stiften, und wir sind keine Diebe"},
  {"surah_number": 12, "verse_number": 74, "content": "Sie sagten: \"Was soll dann die Strafe dafur sein, wenn ihr Lugner seid"},
  {"surah_number": 12, "verse_number": 75, "content": "Sie sagten: \"Die Strafe dafur sei: Der, in dessen Satteltaschen er (der Becher) gefunden wird, soll selbst das Entgelt dafur sein. So belohnen wir die Ubeltater"},
  {
    "surah_number": 12,
    "verse_number": 76,
    "content":
        "Da begann er ihre Sacke vor dem Sack seines Bruders (zu durchsuchen); dann zog er ihn aus dem Sack seines Bruders hervor. So richteten Wir es fur Yusuf ein; er hatte seinen Bruder nach dem Gesetz des Konigs nicht aufhalten konnen; es sei denn, Allah hatte es so gewollt. Wir erhohen um Rangstufen wen Wir wollen; und uber jedem, der Wissen hat, ist der Eine, Der noch mehr weiß"
  },
  {
    "surah_number": 12,
    "verse_number": 77,
    "content": "Sie sagten: \"Hat er gestohlen, so hat zuvor schon sein Bruder Diebstahl begangen.\" Jedoch Yusuf hielt es in seinem Herzen geheim und offenbarte es ihnen nicht. Er sprach: \"lhr (scheint) in der Tat ubler (als das) zu sein; und Allah weiß am besten, was ihr behauptet"
  },
  {"surah_number": 12, "verse_number": 78, "content": "Sie sagten: \"O `Aziz, er hat einen greisen Vater, so nimm einen von uns statt seiner an; denn wir sehen, du gehorst zu denen, die Gutes tun"},
  {"surah_number": 12, "verse_number": 79, "content": "Er sprach: \"Allah behute, daß wir einen anderen nehmen sollten als den, bei dem wir unsere Sachen gefunden haben; wir waren sonst wahrlich ungerecht"},
  {
    "surah_number": 12,
    "verse_number": 80,
    "content":
        "Als sie die Hoffnung, ihn umzustimmen, aufgegeben hatten, gingen sie zu vertraulicher Beratung abseits. Es sprach ihr Altester: \"Wisset ihr nicht, daß euer Vater von euch ein festes Versprechen im Namen Allahs entgegengenommen hat und daß ihr zuvor in eurer Pflicht gegen Yusuf gefehlt habt? Ich will darum das Land nicht eher verlassen, bis mein Vater es mir erlaubt oder Allah fur mich entscheidet; und Er ist der beste Richter"
  },
  {"surah_number": 12, "verse_number": 81, "content": "Kehrt ihr zu eurem Vater zuruck und sprecht: \"O unser Vater, dein Sohn hat gestohlen; und wir haben nur ausgesagt, was wir wußten, und wir konnten keine Wachter des Verborgenen sein"},
  {"surah_number": 12, "verse_number": 82, "content": "Frage nur in der Stadt nach, in der wir waren, und in der Karawane, mit der wir kamen; gewiß, wir sagen die Wahrheit"},
  {"surah_number": 12, "verse_number": 83, "content": "Er sprach: \"Nein, ihr habt euch etwas vorgemacht. Doch schon geduldig sein. Vielleicht wird Allah sie mir alle wiederbringen; denn Er ist der Allwissende, der Allweise"},
  {"surah_number": 12, "verse_number": 84, "content": "Und er wandte sich von ihnen ab und sprach: \"O mein Kummer um Yusuf!\" Und seine Augen wurden vor Traurigkeit trub, (doch) dann beherrschte er sich"},
  {"surah_number": 12, "verse_number": 85, "content": "Sie sagten: \"Bei Allah, du horst nicht auf, von Yusuf zu sprechen, bis du dich ganz verzehrt hast oder zu denen gehorst, die zugrunde gehen"},
  {"surah_number": 12, "verse_number": 86, "content": "Er sprach: \"Ich beklage nur meinen Kummer und meinen Gram vor Allah, und ich weiß von Allah, was ihr nicht wisset"},
  {"surah_number": 12, "verse_number": 87, "content": "O meine Sohne, zieht aus und forscht nach Yusuf und seinem Bruder und zweifelt nicht an Allahs Erbarmen; denn an Allahs Erbarmen zweifelt nur das unglaubige Volk"},
  {
    "surah_number": 12,
    "verse_number": 88,
    "content": "Als sie dann vor ihn (Yusuf) traten, da sagten sie: \"O `Aziz, die Not hat uns und unsere Familie geschlagen, und wir haben Ware von geringem Wert mitgebracht; so gib uns das volle Maß und sei wohltatig gegen uns. Wahrlich, Allah belohnt die Wohltatigen"
  },
  {"surah_number": 12, "verse_number": 89, "content": "Er sprach: \"Wisset ihr, was ihr Yusuf und seinem Bruder antatet, weil ihr toricht waret"},
  {
    "surah_number": 12,
    "verse_number": 90,
    "content": "Sie sagten: \"Bist du etwa gar Yusuf?\" Er sprach: \"Ich bin Yusuf, und dies ist mein Bruder. Allah ist wahrlich gnadig gegen uns gewesen. Wahrlich, wer rechtschaffen und geduldig ist nimmermehr laßt Allah den Lohn derer, die Gutes tun, verlorengehen"
  },
  {"surah_number": 12, "verse_number": 91, "content": "Sie sagten: \"Bei Allah, Allah hat dich wahrhaftig vor uns bevorzugt, und wir sind wahrlich schuldig gewesen"},
  {"surah_number": 12, "verse_number": 92, "content": "Er sprach: \"Kein Tadel treffe euch heute. Moge Allah euch vergeben! Denn Er ist der Barmherzigste Erbarmer"},
  {"surah_number": 12, "verse_number": 93, "content": "Nehmt dies mein Hemd und legt es auf das Gesicht meines Vaters; dann kann er (wieder) sehen. Und bringt eure Familien allesamt zu mir"},
  {"surah_number": 12, "verse_number": 94, "content": "Als die Karawane aufgebrochen war, sprach ihr Vater: \"Wahrlich, ich spure den Geruch Yusufs, auch wenn ihr mich fur schwachsinnig haltet"},
  {"surah_number": 12, "verse_number": 95, "content": "Sie sagten: \"Bei Allah, du befindest dich gewiß in deinem alten Irrtum"},
  {"surah_number": 12, "verse_number": 96, "content": "Als dann der Freudenbote kam, da legte er es (das Hemd) auf sein Gesicht, und da wurde er wieder sehend. Er sprach: \"Habe ich euch nicht gesagt: Ich weiß von Allah was ihr nicht wisset"},
  {"surah_number": 12, "verse_number": 97, "content": "Sie sagten: \"O unser Vater bitte fur uns um Vergebung unserer Sunden; denn wir sind wahrhaftig schuldig gewesen"},
  {"surah_number": 12, "verse_number": 98, "content": "Er sprach: \"Ich will Vergebung fur euch von meinem Herrn erbitten. Wahrlich, Er ist der Allvergebende, der Barmherzige"},
  {"surah_number": 12, "verse_number": 99, "content": "Als sie dann vor Yusuf traten, nahm er seine Eltern bei sich auf und sprach: \"Zieht in Agypten in Sicherheit ein, wie Allah es will"},
  {
    "surah_number": 12,
    "verse_number": 100,
    "content":
        "Und er hob seine Eltern auf den Thron, und sie warfen sich vor ihm nieder. Und er sprach: \"O mein Vater, dies ist die Deutung meines Traumes von damals. Mein Herr hat ihn wahrgemacht. Und Er hat mich gutig behandelt, als Er mich aus dem Kerker fuhrte und euch aus der Wuste herbrachte, nachdem Satan zwischen mir und meinen Brudern Zwietracht gestiftet hatte. Wahrlich, mein Herr ist Gutig, zu wem Er will; denn Er ist der Allwissende, der Allweise"
  },
  {
    "surah_number": 12,
    "verse_number": 101,
    "content": "O mein Herr, Du hast mir nun die Herrschaft verliehen und mich die Deutung der Traume gelehrt. O Schopfer der Himmel und der Erde, Du bist mein Beschutzer in dieser Welt und im Jenseits. Laß mich als (Dir) ergeben sterben und vereine mich mit den Rechtschaffenen"
  },
  {"surah_number": 12, "verse_number": 102, "content": "Dies ist die Kunde von dem Verborgenen, das Wir dir offenbaren. Du warst nicht bei ihnen, als sie sich uber ihren Plan einigten, indem sie Ranke schmiedeten"},
  {"surah_number": 12, "verse_number": 103, "content": "Und die meisten Menschen werden nicht glauben, magst du es auch noch so eifrig wunschen"},
  {"surah_number": 12, "verse_number": 104, "content": "Und du verlangst von ihnen keinen Lohn dafur. Vielmehr ist es eine Ermahnung fur alle Welten"},
  {"surah_number": 12, "verse_number": 105, "content": "Und wie viele Zeichen sind in den Himmeln und auf Erden, an denen sie vorbeigehen, indem sie sich von ihnen abwenden"},
  {"surah_number": 12, "verse_number": 106, "content": "Und die meisten von ihnen glauben nicht an Allah, ohne (Ihm) Gotter zur Seite zu stellen"},
  {"surah_number": 12, "verse_number": 107, "content": "Sind sie denn sicher davor, daß nicht eine uberwaltigende Strafe von Allah uber sie kommt, oder daß nicht plotzlich die Stunde uber sie kommt, wahrend sie nichtsahnend sind"},
  {"surah_number": 12, "verse_number": 108, "content": "Sprich: \"Das ist mein Weg: Ich rufe zu Allah; ich und diejenigen, die mir folgen, sind uns daruber im klaren. Und gepriesen sei Allah; und ich gehore nicht zu den Gotzendienern"},
  {
    "surah_number": 12,
    "verse_number": 109,
    "content":
        "Auch vor dir entsandten Wir nur Manner, denen Wir die Offenbarung gaben, aus dem Volk der Stadte. Sind sie denn nicht im Lande umhergezogen, so daß sie schauen konnten, wie das Ende derer war, die vor ihnen lebten? Und gewiß, die Wohnstatt des Jenseits ist besser fur die Gottesfurchtigen. Wollt ihr es denn nicht begreifen"
  },
  {"surah_number": 12, "verse_number": 110, "content": "Als nun die Gesandten die Hoffnung aufgaben und dachten, sie wurden belogen, kam Unsere Hilfe zu ihnen; da wurden jene errettet, die Wir wollten. Und Unsere Strafe kann nicht von dem sundigen Volk abgewendet werden"},
  {
    "surah_number": 12,
    "verse_number": 111,
    "content": "Wahrlich, in ihren Geschichten ist eine Lehre fur die Verstandigen. Es ist keine erdichtete Rede, sondern eine Bestatigung dessen, was ihm vorausging, und eine deutliche Darlegung aller Dinge und eine Fuhrung und eine Barmherzigkeit fur ein glaubiges Volk"
  },
  {"surah_number": 13, "verse_number": 1, "content": "Alif Lam Mim Ra'. Das sind die Verse des Buches. Und was dir von deinem Herrn hinabgesandt wurde, ist die Wahrheit. Jedoch die meisten Menschen glauben nicht"},
  {
    "surah_number": 13,
    "verse_number": 2,
    "content":
        "Allah ist es, Der die Himmel, die ihr sehen konnt, ohne Stutzpfeiler emporgehoben hat. Dann herrschte Er uber Sein Reich. Und Er machte (Sich) Sonne und Mond dienstbar; jedes (Gestirn) lauft seine Bahn in einer vorgezeichneten Frist. Er bestimmt alle Dinge. Er macht die Zeichen deutlich, auf daß ihr an die Begegnung mit eurem Herrn fest glauben moget"
  },
  {
    "surah_number": 13,
    "verse_number": 3,
    "content": "Und Er ist es, Der die Erde ausdehnte und feststehende Berge und Flusse in ihr grundete. Und Er schuf auf ihr Fruchte aller Art, ein Paar von jeder (Art) Er laßt die Nacht den Tag bedecken. Wahrlich, hierin liegen Zeichen fur ein nachdenkendes Volk"
  },
  {
    "surah_number": 13,
    "verse_number": 4,
    "content":
        "Und auf der Erde liegen dicht beieinander Landstriche und Garten von Weinstocken, Kornfeldern und Dattelpalmen, die auf Doppel- und auf Einzelstammen aus einer Wurzel zusammenwachsen; sie werden mit demselben Wasser getrankt, dennoch lassen Wir die einen von ihnen die anderen an Frucht ubertreffen. Hierin liegen wahrlich Zeichen fur ein verstandiges Volk"
  },
  {
    "surah_number": 13,
    "verse_number": 5,
    "content":
        "Wenn du dich wunderst, so ist ihre Rede wunderlich: \"Wie? Wenn wir zu Staub geworden sind, dann sollen wir in einer Neuschopfung sein?\" Diese sind es, die ihrem Herrn den Glauben versagen, und diese sind es, die Fesseln um ihren Hals haben werden; und sie werden die Bewohner des Feuers sein, darin werden sie auf ewig bleiben"
  },
  {
    "surah_number": 13,
    "verse_number": 6,
    "content": "Sie werden dich eher die Strafe als die Wohltat beschleunigen heißen, obwohl vor ihnen beispielgebende Bestrafungen erfolgt sind. Wahrlich, dein Herr ist immer bereit, den Menschen zu vergeben, trotz ihrer Missetaten, und siehe, dein Herr ist streng im Strafen"
  },
  {"surah_number": 13, "verse_number": 7, "content": "Und diejenigen, die nicht glauben, sagen: \"Warum wurde kein Zeichen von seinem Herrn zu ihm herabgesandt?\" Du bist nur ein Warner. Und fur jedes Volk wird ein Fuhrer (eingesetzt)"},
  {"surah_number": 13, "verse_number": 8, "content": "Allah weiß, was jedes weibliche Wesen tragt, und wann der Mutterschoß abnimmt und wann er zunimmt. Und bei Ihm geschehen alle Dinge nach Maß"},
  {"surah_number": 13, "verse_number": 9, "content": "(Er ist) der Kenner des Verborgenen und des Sichtbaren, der Große, der Erhabenste"},
  {"surah_number": 13, "verse_number": 10, "content": "Diejenigen, die unter euch das Wort verhehlen und die es offen aussprechen, sind (vor Ihm) gleich; so wie die, die sich in der Nacht verbergen und die am Tag offen hervortreten"},
  {
    "surah_number": 13,
    "verse_number": 11,
    "content":
        "Er (der Gesandte) hat Beschutzer vor und hinter sich; sie behuten ihn auf Allahs Geheiß. Gewiß, Allah andert die Lage eines Volkes nicht, ehe sie (die Leute) nicht selbst das andern, was in ihren Herzen ist. Und wenn Allah einem Volk etwas Ubeles zufugen will, so gibt es dagegen keine Abwehr, und sie haben keinen Helfer außer Ihm"
  },
  {"surah_number": 13, "verse_number": 12, "content": "Er ist es, Der euch den Blitz in Furcht und Hoffnung sehen laßt; und Er laßt die schweren Wolken aufsteigen"},
  {"surah_number": 13, "verse_number": 13, "content": "Und der Donner lobpreist Seine Herrlichkeit; und (genauso lobpreisen) Ihn die Engel in Ehrfurcht. Und Er sendet die Blitzschlage und trifft damit, wen Er will; doch streiten sie uber Allah, wahrend Er streng in der Vergeltung ist"},
  {
    "surah_number": 13,
    "verse_number": 14,
    "content":
        "Ihm gebuhrt der aufrichtige Anruf; und jene, die sie statt Ihm anrufen, kommen ihnen mit nichts entgegen; (sie sind) wie jener, der seine beiden Hande nach Wasser ausstreckt, damit es seinen Mund erreiche und ihn doch nicht erreicht. Und das Bitten der Unglaubigen ist vollig verfehlt"
  },
  {"surah_number": 13, "verse_number": 15, "content": "Wer immer in den Himmeln und auf der Erde ist, wirft sich vor Allah in Anbetung nieder, willig oder widerwillig, und (ebenfalls tun dies) ihre Schatten am Morgen und am Abend"},
  {
    "surah_number": 13,
    "verse_number": 16,
    "content":
        "Sprich: \"Wer ist der Herr der Himmel und der Erde?\" Sprich: \"Allah.\" Sprich: \"Habt ihr euch Helfer außer Ihm genommen, die sich selbst weder nutzen noch schaden konnen?\" Sprich: \"Konnen der Blinde und der Sehende gleich sein? Oder kann die Finsternis dem Licht gleich sein? Oder stellen sie Allah Teilhaber zur Seite die eine Schopfung wie die Seine erschaffen haben, So das (beide) Schopfungen ihnen gleichartig erscheinen?\" Sprich: \"Allah ist der Schopfer aller Dinge, und Er ist der Einzige, der Allmachtige"
  },
  {
    "surah_number": 13,
    "verse_number": 17,
    "content":
        "Er sendet Wasser vom Himmel herab, so daß die Taler nach ihrem Maß durchstromt werden, und die Flut tragt Schaum auf der Oberflache. Und ein ahnlicher Schaum ist in dem, was sie im Feuer aus Verlangen nach Schmuck und Gerat erhitzen. So verdeutlicht Allah Wahrheit und Falschheit. Der Schaum aber, der vergeht wie die Blasen; das aber, was den Menschen nutzt, bleibt auf der Erde zuruck. Und so pragt Allah die Gleichnisse"
  },
  {
    "surah_number": 13,
    "verse_number": 18,
    "content":
        "Fur diejenigen, die auf ihren Herrn horen, ist das Gute vorgesehen; diejenigen aber, die nicht auf Ihn horen - hatten sie auch alles, was auf Erden ist, und noch einmal soviel dazu -, wurden sich gerne damit loskaufen. Diese sind es, die eine schlimme Abrechnung haben werden, und ihre Herberge ist Gahannam. Welch ein elender Ruheplatz"
  },
  {"surah_number": 13, "verse_number": 19, "content": "Ist denn der, der weiß, daß das die Wahrheit ist, was zu dir von deinem Herrn hinabgesandt wurde, einem Blinden gleich? Nur diejenigen, die Verstand haben, lassen sich mahnen"},
  {"surah_number": 13, "verse_number": 20, "content": "Es sind jene, die ihr Versprechen Allah gegenuber einhalten und den Bund (mit Ihm) nicht brechen"},
  {"surah_number": 13, "verse_number": 21, "content": "und es sind jene, welche verbinden, was Allah zu verbinden geboten hat, und die ihren Herrn furchten und sich auf einen schlechten Ausgang der Abrechnung gefaßt machen"},
  {
    "surah_number": 13,
    "verse_number": 22,
    "content":
        "und es sind jene, die im Verlangen nach dem Wohlgefallen ihres Herrn geduldig bleiben und das Gebet verrichten und von dem, was Wir ihnen gegeben haben, im Verborgenen und offentlich spenden und das Bose durch das Gute abwehren - diese sind es, denen der Lohn der Wohnstatt zuteil wird"
  },
  {"surah_number": 13, "verse_number": 23, "content": "die Garten von Eden. Dort werden sie eingehen und die von ihren Eltern und ihren Frauen und ihren Nachkommen, die rechtschaffen sind. Und die Engel werden zu ihnen durch jegliches Tor treten"},
  {"surah_number": 13, "verse_number": 24, "content": "Friede sei mit euch, da ihr geduldig waret; seht, wie herrlich der Lohn der Wohnstatt ist"},
  {"surah_number": 13, "verse_number": 25, "content": "Diejenigen aber, die den Bund Allahs brechen, nachdem (sie) ihn geschlossen haben, und zerreißen, was Allah zu verbinden geboten hat, und Unheil auf Erden stiften auf ihnen lastet der Fluch, und sie haben eine schlimme Wohnstatt"},
  {"surah_number": 13, "verse_number": 26, "content": "Allah erweitert und beschrankt die Mittel zum Unterhalt, wem Er will. Sie freuen sich des irdischen Lebens, doch das diesseitige Leben ist im Vergleich mit dem jenseitigen nur ein vergangliches Gut"},
  {"surah_number": 13, "verse_number": 27, "content": "Und jene, die unglaubig sind, sagen: \"Warum ist ihm von seinem Herrn kein Zeichen herabgesandt worden?\" Sprich: \"Allah laßt zugrunde gehen, wen Er will, und leitet die zu Sich, welche sich bekehren"},
  {"surah_number": 13, "verse_number": 28, "content": "Es sind jene, die glauben und deren Herzen Trost finden im Gedenken an Allah. Wahrlich, im Gedenken Allahs werden die Herzen ruhig"},
  {"surah_number": 13, "verse_number": 29, "content": "Denen, die da glauben und gute Werke tun, wird Gluck und eine treffliche Heimstatt zuteil sein"},
  {
    "surah_number": 13,
    "verse_number": 30,
    "content":
        "Und so haben Wir dich zu einem Volk gesandt, vor dem bereits andere Volker dahingegangen sind, auf daß du ihnen verkunden mogest, was Wir dir offenbarten; und doch glauben sie nicht an den Allerbarmer. Sprich: \"Er ist mein Herr; kein Gott ist da außer Ihm. In Ihn setze ich mein Vertrauen und zu Ihm werde ich heimkehren"
  },
  {
    "surah_number": 13,
    "verse_number": 31,
    "content":
        "Und gabe es auch einen Quran, durch den Berge versetzt oder die Erde gespalten oder durch den zu den Toten gesprochen werden konnte (, wurden sie doch nicht an ihn glauben) Nein! Die Sache ruht vollig bei Allah. Haben denn die Glaubigen nicht den Versuch (, daran zu zweifeln,) aufgegeben, daß, wenn Allah gewollt hatte, Er sicher der ganzen Menschheit hatte den Weg weisen konnen? Und die Katastrophe wird nicht aufhoren, die Unglaubigen um dessentwillen zu treffen, was sie gewirkt haben, oder sich nahe bei ihren Wohnstatten niederzulassen, bis Allahs Verheißung sich erfullt hat. Wahrlich, Allah verfehlt den Termin nicht"
  },
  {"surah_number": 13, "verse_number": 32, "content": "Und gewiß sind schon vor dir Gesandte verspottet worden, doch Ich gewahrte denen eine Frist, die unglaubig waren. Dann erfaßte Ich sie, und wie war Meine Strafe"},
  {
    "surah_number": 13,
    "verse_number": 33,
    "content":
        "Ist denn Der, Der uber alles, was sie begehen, wacht (, den angeblichen Gottern gleich)? Dennoch stellen sie Allah Gotter zur Seite. Sprich: \"Nennt sie!\" Wollt ihr Ihm etwas verkunden, was Er auf Erden nicht kennt? Oder ist es nur leere Rede? Nein, aber der bose Plan der Unglaubigen ist ihnen schon vorgegaukelt worden, und sie sind vom (rechten) Weg abgehalten worden. Und derjenige, den Allah zum Irrenden erklart, soll keinen finden, der ihn rechtleitet"
  },
  {"surah_number": 13, "verse_number": 34, "content": "Fur sie ist eine Strafe im Diesseits bestimmt; und die Strafe des Jenseits ist gewiß (noch) harter, und sie werden keinen Beschutzer vor Allah haben"},
  {
    "surah_number": 13,
    "verse_number": 35,
    "content": "(Dies ist) die Beschreibung des Paradieses, das den Gottesfurchtigen versprochen worden ist: Bache durchfließen es; seine Fruchte wie sein Schatten sind immerwahrend. Das ist der Lohn derer, die gottesfurchtig sind; und der Lohn der Unglaubigen ist das Feuer"
  },
  {
    "surah_number": 13,
    "verse_number": 36,
    "content":
        "Und die, denen Wir die Schrift gegeben haben, freuen sich uber das, was zu dir hinabgesandt wurde. Und unter den Gruppen sind einige, die einen Teil davon leugnen. Sprich: \"Mir wurde befohlen, Allah zu dienen, und nicht, Ihm Gotter zur Seite zu stellen. Zu Ihm rufe ich und zu Ihm werde ich heimkehren"
  },
  {"surah_number": 13, "verse_number": 37, "content": "Und so haben Wir ihn (den Quran) hinabgesandt als eine Entscheidung in arabischer Sprache. Und wenn du ihren personlichen Neigungen folgst, obwohl zu dir das Wissen gekommen war, so sollst du keinen Beschutzer oder Retter vor Allah haben"},
  {"surah_number": 13, "verse_number": 38, "content": "Wahrlich, schon vor dir entsandten Wir Gesandte und gaben ihnen Frauen und Kinder. Und es ist fur einen Gesandten nicht moglich, ein Zeichen zu bringen, es sei denn auf Allahs Geheiß. Alles geschieht zu einem vorbestimmten Termin"},
  {"surah_number": 13, "verse_number": 39, "content": "Allah loscht aus und laßt bestehen, was Er will, und bei Ihm ist die Urschrift des Buches"},
  {"surah_number": 13, "verse_number": 40, "content": "Und ob Wir dich einen Teil von dem sehen lassen, was Wir ihnen androhen, oder ob Wir dich sterben lassen - dir obliegt nur die Verkundigung und Uns die Abrechnung"},
  {"surah_number": 13, "verse_number": 41, "content": "Sehen sie denn nicht, daß Wir vom Land Besitz ergreifen und es von allen Seiten in seiner Große schmalern? Und Allah richtet; da ist keiner, der Seine Entscheidung widerrufen kann. Und Er ist schnell im Abrechnen"},
  {"surah_number": 13, "verse_number": 42, "content": "Diejenigen, die vor ihnen waren, haben auch Plane geschmiedet, doch alles Planen ist Allahs Sache. Er weiß, was ein jeder begeht; und die Unglaubigen werden erfahren, wem die endgultige Wohnstatt zuteil wird"},
  {"surah_number": 13, "verse_number": 43, "content": "Und die Unglaubigen sagen: \"Du bist nicht entsandt worden.\" Sprich: \"Allah genugt als Zeuge gegen mich und euch und auch gegen diejenigen, die die Kenntnis der Schrift haben"},
  {"surah_number": 14, "verse_number": 1, "content": "Alif Lam Ra (Dies ist) ein Buch, das Wir zu dir hinabgesandt haben, auf daß du die Menschen mit der Erlaubnis ihres Herrn aus den Finsternissen zum Licht fuhren mogest, auf den Weg des Erhabenen, des Preiswurdigen"},
  {"surah_number": 14, "verse_number": 2, "content": "Allahs ist, was in den Himmeln und was auf der Erde ist. Und wehe den Unglaubigen wegen der schrecklichen Strafe"},
  {"surah_number": 14, "verse_number": 3, "content": "Es sind jene, die das Leben des Diesseits dem des Jenseits vorziehen und von Allahs Weg abhalten und ihn zu krummen trachten. Sie sind es, die im großen Irrtum weit gegangen sind"},
  {"surah_number": 14, "verse_number": 4, "content": "Und Wir schickten keinen Gesandten, es sei denn mit der Sprache seines Volkes, auf daß er sie aufklare. Dann erklart Allah zum Irrenden, wen Er will, und leitet recht, wen Er will. Und Er ist der Erhabene, der Allweise"},
  {"surah_number": 14, "verse_number": 5, "content": "Und wahrlich, Wir entsandten Moses mit Unseren Zeichen (und sprachen): \"Fuhre dein Volk aus den Finsternissen zum Licht und erinnere es an die Tage Allahs! \" Wahrlich, darin liegen Zeichen fur jeden Geduldigen, Dankbaren"},
  {
    "surah_number": 14,
    "verse_number": 6,
    "content": "Und damals sprach Moses zu seinem Volk: \"Denkt an die Gnade Allahs an euch, als Er euch vor den Leuten Pharaos errettete, die euch mit schlimmer Qual bedruckten, eure Sohne abschlachteten und eure Frauen am Leben ließen; und darin lag eine gewaltige Prufung von eurem Herrn fur euch"
  },
  {"surah_number": 14, "verse_number": 7, "content": "Und da kundigte euer Herr an: \"Wenn ihr dankbar seid, so will Ich euch wahrlich mehr geben; seid ihr aber undankbar, dann ist Meine Strafe wahrlich streng"},
  {"surah_number": 14, "verse_number": 8, "content": "Und Moses sprach: \"Wenn ihr unglaubig seid, ihr und wer sonst noch alles auf Erden ist - wahrlich, Allah ist auf keinen angewiesen, Preiswurdig"},
  {
    "surah_number": 14,
    "verse_number": 9,
    "content":
        "Kam zu euch (denn) nicht die Kunde von jenen, die vor euch waren - von dem Volk Noahs, den Ads und Tamuds - und von denen, die nach ihnen (kamen)? Niemand kennt sie außer Allah. Ihre Gesandten kamen mit deutlichen Zeichen zu ihnen, jedoch sie hielten ihnen die Hande vor den Mund und sagten: \"Wir glauben nicht an das, womit ihr gesandt worden seid, und wir befinden uns wahrlich in bedenklichem Zweifel uber das, wozu ihr uns auffordert"
  },
  {
    "surah_number": 14,
    "verse_number": 10,
    "content":
        "Ihre Gesandten sagten: \"Existiert etwa ein Zweifel uber Allah, den Schopfer der Himmel und der Erde? Er ruft euch, damit Er euch eure Sunden vergebe und euch Aufschub bis zu einer bestimmten Frist gewahre.\" Sie sagten: \"lhr seid nur Menschen wie wir; ihr wollt uns von dem abhalten, was unsere Vater zu verehren pflegten. So bringt uns einen deutlichen Beweis"
  },
  {
    "surah_number": 14,
    "verse_number": 11,
    "content": "Ihre Gesandten sagten zu ihnen: \"Wir sind nur Menschen wie ihr, jedoch Allah erweist Gnade wem von Seinen Dienern Er will. Und wir besitzen keine Macht dazu, euch einen Beweis zu bringen, es sei denn mit Allahs Erlaubnis. Und auf Allah sollen die Glaubigen vertrauen"
  },
  {
    "surah_number": 14,
    "verse_number": 12,
    "content": "Und warum sollten wir nicht auf Allah vertrauen, wo Er uns doch unsere Wege gewiesen hat? Und wir wollen gewiß mit Geduld alles ertragen, was ihr uns an Leid zufugt; auf Allah mogen denn diejenigen vertrauen, die sich auf (Allah) verlassen wollen"
  },
  {
    "surah_number": 14,
    "verse_number": 13,
    "content": "Und jene, die unglaubig waren, sagten zu ihren Gesandten: \"Wahrlich, wir werden euch sicherlich aus unserem Land vertreiben, wenn ihr nicht zu unserer Religion zuruckkehrt.\" Da gab ihr Herr ihnen ein: \"Wir werden gewiß jene zugrunde gehen lassen, die Frevler sind"
  },
  {"surah_number": 14, "verse_number": 14, "content": "und Wir werden euch wahrlich nach ihnen das Land bewohnen lassen. Das gilt fur den, der das Auftreten vor Mir furchtet, und auch das furchtet, was Meine Drohung angeht"},
  {"surah_number": 14, "verse_number": 15, "content": "Und sie erflehten den Sieg, und jeder hartnackige Tyrann schlug fehl"},
  {"surah_number": 14, "verse_number": 16, "content": "und hinter ihm ruckt Gahannam her, und er wird von Eiter getrankt werden"},
  {"surah_number": 14, "verse_number": 17, "content": "er wird ihn hinunterschlucken und kaum daran Genuß finden. Und der Tod wird von allen Seiten zu ihm kommen, doch er wird nicht sterben. Und (zusatzlich) kommt hinterher noch eine strenge Strafe"},
  {
    "surah_number": 14,
    "verse_number": 18,
    "content": "Die Lage derer, die nicht an ihren Herrn glauben, ist die: Ihre Werke sind gleich Asche, auf die der Wind an einem sturmischen Tag heftig blast. Sie sollen keine Macht haben uber das, was sie verdienen. Das ist wahrlich die weitgegangene Irrefuhrung"
  },
  {"surah_number": 14, "verse_number": 19, "content": "Siehst du denn nicht, daß Allah die Himmel und die Erde in Wirklichkeit geschaffen hat? Wenn Er will, so kann Er euch hinwegnehmen und eine neue Schopfung hervorbringen"},
  {"surah_number": 14, "verse_number": 20, "content": "Und das ist fur Allah keineswegs schwer"},
  {
    "surah_number": 14,
    "verse_number": 21,
    "content":
        "Und sie werden allesamt vor Allah hintreten; dann werden die Schwachen zu den Hochmutigen sagen: \"Gewiß, wir waren eure Gefolgs leute; konnt ihr uns also nicht etwas von der Strafe Allahs abnehmen?\" Sie werden sagen: \"Hatte Allah uns den Weg gewiesen, wir hatten euch sicherlich den Weg gewiesen. Es ist gleich fur uns, ob wir Ungeduld zeigen oder geduldig bleiben: es gibt fur uns kein Entrinnen"
  },
  {
    "surah_number": 14,
    "verse_number": 22,
    "content":
        "Und wenn die Sache entschieden worden ist, dann wird Satan sagen: \"Allah hat euch ein wahres Versprechen gegeben, ich aber versprach euch etwas und hielt es nicht. Und ich hatte keine Macht uber euch, außer euch zu rufen; und ihr gehorchtet mir. So tadelt nicht mich, sondern tadelt euch selber. Ich kann euch nicht retten, noch konnt ihr mich retten. Ich habe es schon von mir gewiesen, daß ihr mich (Allah) zur Seite stelltet.\" Den Missetatern wird wahrlich eine schmerzliche Strafe zuteil sein"
  },
  {"surah_number": 14, "verse_number": 23, "content": "Und diejenigen, die da glauben und gute Werke tun, werden in Garten eingefuhrt werden, durch die Bache fließen, um mit der Erlaubnis ihres Herrn ewig darin zu wohnen. Ihr Gruß dort wird lauten: \"Friede"},
  {"surah_number": 14, "verse_number": 24, "content": "Siehst du nicht, wie Allah das Gleichnis eines guten Wortes pragt? (Es ist) wie ein guter Baum, dessen Wurzeln fest sind und dessen Zweige bis zum Himmel (ragen)"},
  {"surah_number": 14, "verse_number": 25, "content": "Er bringt seine Frucht zu jeder Zeit mit der Erlaubnis seines Herrn hervor. Und Allah pragt Gleichnisse fur die Menschen, auf daß sie nachdenken mogen"},
  {"surah_number": 14, "verse_number": 26, "content": "Und das Gleichnis eines schlechten Wortes aber ist wie ein schlechter Baum, der aus der Erde entwurzelt ist und keinen Halt im Boden hat"},
  {"surah_number": 14, "verse_number": 27, "content": "Allah starkt die Glaubigen mit dem fest gegrundeten Wort, in diesem Leben wie im kunftigen; und Allah laßt die Frevler irregehen; und Allah tut, was Er will"},
  {"surah_number": 14, "verse_number": 28, "content": "Siehst du nicht jene, die Allahs Gnade mit der Undankbarkeit vertauschten und ihr Volk in die Statte des Verderbens brachten"},
  {"surah_number": 14, "verse_number": 29, "content": "Es ist Gahannam. Darin werden sie brennen, und das ist eine schlimme Unterkunft"},
  {"surah_number": 14, "verse_number": 30, "content": "Und sie haben Allah Nebenbuhler zur Seite gesetzt, damit (die Menschen) von Seinem Weg abirren. Sprich: \"Vergnugt euch eine Weile, dann aber endet eure Reise im Feuer"},
  {"surah_number": 14, "verse_number": 31, "content": "Sprich zu Meinen Dienern, die glaubig sind, auf daß sie das Gebet verrichten und von dem, was Wir ihnen gegeben haben, im Verborgenen und offentlich spenden mogen, bevor ein Tag kommt, an dem es weder Handel noch Freundschaft geben wird"},
  {
    "surah_number": 14,
    "verse_number": 32,
    "content":
        "Allah ist es, der die Himmel und die Erde erschuf und Wasser aus den Wolken niederregnen ließ und damit Fruchte zu eurem Unterhalt hervorbrachte; und Er hat euch die Schiffe dienstbar gemacht, damit sie auf dem Meer auf Seinen Befehl fahren, und Er hat euch die Flusse dienstbar gemacht"
  },
  {"surah_number": 14, "verse_number": 33, "content": "Und Er machte euch die Sonne und den Mond dienstbar, die voller Eifer sind. Und dienstbar machte Er euch die Nacht und den Tag"},
  {"surah_number": 14, "verse_number": 34, "content": "Und Er gab euch alles, was ihr von Ihm begehrtet; und wenn ihr Allahs Wohltaten aufzahlen wolltet, wurdet ihr sie nicht vollstandig erfassen konnen. Siehe, der Mensch ist wahrlich frevelhaft, undankbar"},
  {"surah_number": 14, "verse_number": 35, "content": "Und damals sprach Abraham: \"Mein Herr, mache diese Stadt zu einer Statte des Friedens und bewahre mich und meine Kinder davor, die Gotzen anzubeten"},
  {"surah_number": 14, "verse_number": 36, "content": "mein Herr, sie haben viele Menschen irregeleitet. Wer mir nun folgt, der gehort sicher zu mir; und wer mir nicht gehorcht siehe, Du bist Allverzeihend, Barmherzig"},
  {
    "surah_number": 14,
    "verse_number": 37,
    "content":
        "Unser Herr, ich habe einen Teil meiner Nachkommenschaft in einem unfruchtbaren Tal nahe bei Deinem heiligen Haus angesiedelt, o unser Herr, auf daß sie das Gebet verrichten mogen. So mache ihnen die Herzen der Menschen zugeneigt und versorge sie mit Fruchten, damit sie dankbar sein mogen"
  },
  {"surah_number": 14, "verse_number": 38, "content": "Unser Herr, Du weißt, was wir verbergen und was wir kundtun. Und vor Allah ist nichts verborgen, ob auf Erden oder im Himmel"},
  {"surah_number": 14, "verse_number": 39, "content": "Alles Lob gebuhrt Allah, Der mir, ungeachtet des Alters, Ismael und Isaak geschenkt hat. Wahrlich, mein Herr ist der Erhorer des Gebets"},
  {"surah_number": 14, "verse_number": 40, "content": "Mein Herr, hilf mir, daß ich und meine Kinder das Ge bet verrichten. Unser Herr! Und nimm mein Gebet an"},
  {"surah_number": 14, "verse_number": 41, "content": "Unser Herr, vergib mir und meinen Eltern und den Glaubigen an dem Tage, an dem die Abrechnung stattfinden wird"},
  {"surah_number": 14, "verse_number": 42, "content": "Und denke nicht, daß Allah nicht beachtet, was die Frevler tun. Er gewahrt ihnen nur einen Aufschub bis zu dem Tage, an dem die Augen starr blicken werden"},
  {"surah_number": 14, "verse_number": 43, "content": "Angstvoll eilen sie vorwarts, mit hochgereckten Kopfen, (und) ihr Blick kehrt nicht zu ihnen zuruck, und ihre Herzen sind leer"},
  {
    "surah_number": 14,
    "verse_number": 44,
    "content":
        "Und warne die Menschen vor dem Tag, an dem die Strafe uber sie kommen wird. Dann werden die Frevler sagen: \"Unser Herr, gib uns fur eine kurze Frist Aufschub. Wir wollen Deinem Ruf Gehor schenken und den Gesandten Folge leisten.\" - \"Habt ihr nicht zuvor geschworen, der Untergang werde euch nicht treffen"
  },
  {"surah_number": 14, "verse_number": 45, "content": "Und ihr wohnt in den Wohnungen derer, die gegen sich selbst frevelten, und es ist euch deutlich gemacht worden, wie Wir mit ihnen verfuhren; und Wir haben euch klare Beweise geliefert"},
  {"surah_number": 14, "verse_number": 46, "content": "Und sie haben bereits ihre Ranke geschmiedet, aber ihre Ranke sind bei Allah, und waren ihre Ranke derart, daß sie Berge versetzen sollten"},
  {"surah_number": 14, "verse_number": 47, "content": "Denke nicht, daß Allah Sein Versprechen Seinen Gesandten gegenuber nicht halten werde; gewiß, Allah ist Erhaben, (ist Der,) Der Seine Vergeltung fuhlen laßt"},
  {"surah_number": 14, "verse_number": 48, "content": "an dem Tage, da die Erde in eine andere Erde verwandelt werden wird, und auch die Himmel (verwandelt werden); und sie werden (alle) vor Allah treten, den Einzigen, den Allgewaltigen"},
  {"surah_number": 14, "verse_number": 49, "content": "Und an jenem Tage wirst du die Sunder in Ketten gefesselt sehen"},
  {"surah_number": 14, "verse_number": 50, "content": "Ihre Hemden werden aus Teer sein, und das Feuer wird ihre Gesichter bedecken"},
  {"surah_number": 14, "verse_number": 51, "content": "auf daß Allah jedem vergelte, was er begangen hat. Wahrlich, Allah ist schnell im Abrechnen"},
  {"surah_number": 14, "verse_number": 52, "content": "Dies ist eine Kundgebung an die Menschen, auf daß sie sich dadurch warnen lassen und auf daß sie wissen mogen, daß nur Er der Einzige Gott ist, und auf daß diejenigen, die Verstand haben, sich mahnen lassen"},
  {"surah_number": 15, "verse_number": 1, "content": "Alif Lam Ra Dies sind die Verse des Buches und des deutlichen Quran"},
  {"surah_number": 15, "verse_number": 2, "content": "Es mag wohl sein, daß die Unglaubigen wunschen, sie waren Muslime geworden"},
  {"surah_number": 15, "verse_number": 3, "content": "Lasse sie essen und genießen und von Hoffnung abgelenkt werden; bald werden sie es erfahren"},
  {"surah_number": 15, "verse_number": 4, "content": "Und Wir haben nie eine Stadt zerstort, ohne daß ihr eine wohlbekannte Frist bemessen gewesen ware"},
  {"surah_number": 15, "verse_number": 5, "content": "Kein Volk kann seine Frist uberschreiten; noch konnen sie dahinter zuruckbleiben"},
  {"surah_number": 15, "verse_number": 6, "content": "Und sie sagten: \" O du, zu dem die Ermahnung herabgesandt wurde, du bist wahrlich ein Verruckter"},
  {"surah_number": 15, "verse_number": 7, "content": "Warum bringst du nicht Engel zu uns, wenn du einer der Wahrhaftigen bist"},
  {"surah_number": 15, "verse_number": 8, "content": "Wahrlich, Wir senden keine Engel hinab, außer aus triftigem Grunde, und dann wird ihnen kein Aufschub gewahrt"},
  {"surah_number": 15, "verse_number": 9, "content": "Wahrlich, Wir Selbst haben diese Ermah nung hinabgesandt, und sicherlich werden Wir ihr Huter sein"},
  {"surah_number": 15, "verse_number": 10, "content": "Und wahrlich, Wir entsandten schon vor dir (Gesandte) zu den Parteien der Fruheren"},
  {"surah_number": 15, "verse_number": 11, "content": "Und nie kam ein Gesandter zu ihnen, uber den sie nicht gespottet hatten"},
  {"surah_number": 15, "verse_number": 12, "content": "So lassen Wir diese (Spottlust) in die Herzen der Sunder einziehen"},
  {"surah_number": 15, "verse_number": 13, "content": "Sie glauben nicht daran, obwohl sich das Beispiel der Fruheren ereignet hat"},
  {"surah_number": 15, "verse_number": 14, "content": "Und selbst wenn Wir ihnen ein Tor des Himmels offneten und sie begannen dadurch hinaufzusteigen"},
  {"surah_number": 15, "verse_number": 15, "content": "sie wurden gewiß sagen: \"Nur unsere Blicke sind benommen; wahrlich, man hat uns nur etwas vorgegaukelt"},
  {"surah_number": 15, "verse_number": 16, "content": "Wahrlich, Wir haben Turme in den Himmel gesetzt und ihn fur diejenigen, die ihn anschauen, ausge schmuckt"},
  {"surah_number": 15, "verse_number": 17, "content": "Und Wir haben ihn vor jedem verfluchten Satan bewahrt"},
  {"surah_number": 15, "verse_number": 18, "content": "außer vor jenem, der heimlich lauscht, (und den) dann eine wirkungsvolle Flamme verfolgt"},
  {"surah_number": 15, "verse_number": 19, "content": "Und die Erde haben Wir ausgedehnt und darauf feste Berge gesetzt, und Wir ließen alles auf ihr wachsen, was ausgewogen ist"},
  {"surah_number": 15, "verse_number": 20, "content": "Und Wir schufen dar auf Mittel zu eurem Unterhalt und dem derer, die ihr nicht versorgt"},
  {"surah_number": 15, "verse_number": 21, "content": "Und es gibt nichts, von dem Wir keine Schatze hatten; aber Wir senden es nur in bestimmtem Maß hinab"},
  {"surah_number": 15, "verse_number": 22, "content": "Und Wir senden die fruchtbar machenden Winde (hinab) Dann senden Wir Wasser aus den Wolken nieder, dann geben Wir es euch zu trinken; und ihr hattet es nicht aufspeichern konnen"},
  {"surah_number": 15, "verse_number": 23, "content": "Und wahrlich, Wir Selbst machen lebendig und lassen sterben; und Wir allein sind die Erben"},
  {"surah_number": 15, "verse_number": 24, "content": "Und Wir kennen wohl jene unter euch, die voranschreiten, und Wir kennen wohl jene, die zuruckbleiben"},
  {"surah_number": 15, "verse_number": 25, "content": "Und wahrlich, es ist dein Herr, Der sie versammeln wird. Siehe, Er ist Allweise, Allwissend"},
  {"surah_number": 15, "verse_number": 26, "content": "Und wahrlich, Wir haben den Menschen erschaffen aus trockenem, tonendem Lehm, aus schwarzem, zu Gestalt gebildetem Schlamm"},
  {"surah_number": 15, "verse_number": 27, "content": "Und die Ginn erschufen Wir zuvor aus dem Feuer der sengenden Glut"},
  {"surah_number": 15, "verse_number": 28, "content": "Und damals sprach dein Herr zu den Engeln: \"lch bin im Begriff, den Menschen aus trockenem, tonendem Lehm zu erschaffen, aus schwarzem, zu Gestalt gebildetem Schlamm"},
  {"surah_number": 15, "verse_number": 29, "content": "Wenn Ich ihn nun vollkommen geformt und ihm Meinen Geist eingehaucht habe, dann werft euch vor ihm nieder"},
  {"surah_number": 15, "verse_number": 30, "content": "Da warfen sich die Engel allesamt nieder"},
  {"surah_number": 15, "verse_number": 31, "content": "außer Iblis; er weigerte sich, unter den Sich-Niederwerfenden zu sein"},
  {"surah_number": 15, "verse_number": 32, "content": "Er sprach: \"O Iblis, was ist mit dir, daß du nicht unter den SichNiederwerfenden sein wolltest"},
  {"surah_number": 15, "verse_number": 33, "content": "Er sprach: \"Nimmermehr werde ich mich vor einem Menschen niederwerfen, den Du aus trockenem, tonendem Lehm geschaffen hast, aus schwarzem, zu Gestalt gebildetem Schlamm"},
  {"surah_number": 15, "verse_number": 34, "content": "Er sprach: \"Hinaus denn von hier; denn wahrlich, du bist verflucht"},
  {"surah_number": 15, "verse_number": 35, "content": "Der Fluch soll auf dir lasten bis zum Tage des Gerichts"},
  {"surah_number": 15, "verse_number": 36, "content": "Er sprach: \"Mein Herr, so gewahre mir einen Aufschub bis zu dem Tage, an dem sie auferweckt werden"},
  {"surah_number": 15, "verse_number": 37, "content": "Er sprach: \"Du bist unter denen, die Aufschub erlangen"},
  {"surah_number": 15, "verse_number": 38, "content": "bis zur vorbestimmten Zeit"},
  {"surah_number": 15, "verse_number": 39, "content": "Er sprach: \"Mein Herr, da Du mich hast abirren lassen, so will ich ihnen wahrlich (das Bose) auf Erden ausschmucken, und wahrlich, ich will sie allesamt irrefuhren"},
  {"surah_number": 15, "verse_number": 40, "content": "außer Deinen erwahlten Dienern unter ihnen"},
  {"surah_number": 15, "verse_number": 41, "content": "Er sprach: \"Dies ist ein gerader Weg, den Ich (dir) gewahre"},
  {"surah_number": 15, "verse_number": 42, "content": "Wahrlich, du sollst keine Macht uber Meine Diener haben, bis auf jene der Verfuhrten, die dir folgen"},
  {"surah_number": 15, "verse_number": 43, "content": "Und wahrlich, Gahannam ist ihnen allen der verheißene Ort"},
  {"surah_number": 15, "verse_number": 44, "content": "Sieben Tore hat sie, und jedem Tor ist ein Teil von ihnen zugewiesen"},
  {"surah_number": 15, "verse_number": 45, "content": "Wahrlich, die Gottesfurchtigen werden sich in Garten und an Quellen befinden"},
  {"surah_number": 15, "verse_number": 46, "content": "Tretet hinein in Frieden und Sicherheit"},
  {"surah_number": 15, "verse_number": 47, "content": "Und Wir wollen hinwegnehmen, was an Groll in ihren Herzen sein mag; bruderlich (sollen sie) auf Ruhesitzen einander gegenuber sitzen"},
  {"surah_number": 15, "verse_number": 48, "content": "Mudigkeit wird sie darin nicht beruhren, noch sollen sie jemals von dort vertrieben werden"},
  {"surah_number": 15, "verse_number": 49, "content": "Verkunde Meinen Dienern, daß Ich wahrlich der Allverzeihende, der Barmherzige bin"},
  {"surah_number": 15, "verse_number": 50, "content": "und daß Meine Strafe die schmerzliche Strafe ist"},
  {"surah_number": 15, "verse_number": 51, "content": "Und verkunde ihnen von den Gasten Abrahams"},
  {"surah_number": 15, "verse_number": 52, "content": "als sie bei ihm eintraten und sprachen: \"Friede!\" und er sprach: \"Wir haben Angst vor euch"},
  {"surah_number": 15, "verse_number": 53, "content": "Sie sprachen: \"Furchte dich nicht, wir bringen dir frohe Kunde von einem hochbegabten Knaben"},
  {"surah_number": 15, "verse_number": 54, "content": "Er sagte: \"Bringt ihr mir die frohe Kunde ungeachtet dessen, daß ich vom Alter getroffen bin? Welche frohe Kunde bringt ihr denn"},
  {"surah_number": 15, "verse_number": 55, "content": "Sie sprachen: \"Wir haben dir die frohe Kunde wahrheitsgemaß uberbracht; sei darum nicht einer derjenigen, die die Hoffnung aufgeben"},
  {"surah_number": 15, "verse_number": 56, "content": "Er sprach: \"Und wer außer den Verirrten zweifelt an der Barmherzigkeit seines Herrn"},
  {"surah_number": 15, "verse_number": 57, "content": "Er sprach: \"Was ist euer Auftrag, ihr Boten"},
  {"surah_number": 15, "verse_number": 58, "content": "Sie sprachen: \"Wir sind zu einem schuldigen Volk entsandt worden"},
  {"surah_number": 15, "verse_number": 59, "content": "ausgenommen die Anhanger Lots, die wir alle erretten sollen"},
  {"surah_number": 15, "verse_number": 60, "content": "bis auf seine Frau. Wir bestimmten, daß sie unter denen sein wird, die zuruckbleiben"},
  {"surah_number": 15, "verse_number": 61, "content": "Als die Boten zu den Anhangern Lots kamen"},
  {"surah_number": 15, "verse_number": 62, "content": "da sprach er: \"Wahrlich, ihr seid (uns) unbekannte Leute"},
  {"surah_number": 15, "verse_number": 63, "content": "Sie sprachen: \"Nein, aber wir sind mit dem zu dir gekommen, woran sie zweifelten"},
  {"surah_number": 15, "verse_number": 64, "content": "Und wir sind mit der Gerechtigkeit zu dir gekommen, und gewiß, wir sind wahrhaftig"},
  {"surah_number": 15, "verse_number": 65, "content": "So mache dich mit den Deinen in einer nachtlichen Stunde fort und ziehe hinter ihnen her. Und keiner von euch soll sich umwenden, sondern geht, wohin euch befohlen werden wird"},
  {"surah_number": 15, "verse_number": 66, "content": "Und Wir verkundeten ihm in dieser Angelegenheit, daß die Wurzel jener (Leute) am Morgen ausgerottet werden sollte"},
  {"surah_number": 15, "verse_number": 67, "content": "Und das Volk der Stadt kam voller Freude"},
  {"surah_number": 15, "verse_number": 68, "content": "Er sprach: \"Das sind meine Gaste, so tut mir keine Schande an"},
  {"surah_number": 15, "verse_number": 69, "content": "Und furchtet Allah und sturzt mich nicht in Schmach"},
  {"surah_number": 15, "verse_number": 70, "content": "Sie sagten: \"Haben wir dir nicht verboten, Leute (aufzunehmen)"},
  {"surah_number": 15, "verse_number": 71, "content": "Er sagte: \"Hier sind meine Tochter, wenn ihr etwas zu unternehmen beabsichtigt"},
  {"surah_number": 15, "verse_number": 72, "content": "Wahrhaftig! Sie waren in ihrem Rausch verblendet, so daß sie umherirrten"},
  {"surah_number": 15, "verse_number": 73, "content": "Da erfaßte der Schrei sie bei Sonnenaufgang"},
  {"surah_number": 15, "verse_number": 74, "content": "Und Wir kehrten das Oberste zuunterst, und Wir ließen auf sie brennende Steine nieder-regnen"},
  {"surah_number": 15, "verse_number": 75, "content": "Wahrlich, hierin liegen Zeichen fur die Einsichtigen"},
  {"surah_number": 15, "verse_number": 76, "content": "Und wahrlich, es stellt eine bleibende Lehre dar"},
  {"surah_number": 15, "verse_number": 77, "content": "Wahrlich, hierin ist ein Zeichen fur die Glaubigen"},
  {"surah_number": 15, "verse_number": 78, "content": "Und wahrlich, die Leute des Waldes waren gewiß Frevler"},
  {"surah_number": 15, "verse_number": 79, "content": "Und Wir rachten Uns an ihnen. Und beide liegen als eine erkennbare Mahnung da"},
  {"surah_number": 15, "verse_number": 80, "content": "Auch das Volk von Al- Higr hielt die Gesandten fur Lugner"},
  {"surah_number": 15, "verse_number": 81, "content": "Und Wir gaben (ihnen) Unsere Zeichen, sie aber wandten sich von ihnen ab"},
  {"surah_number": 15, "verse_number": 82, "content": "Und sie pflegten sich Wohnungen zur Sicherheit in die Berge einzu hauen"},
  {"surah_number": 15, "verse_number": 83, "content": "Jedoch die Strafe erfaßte sie am Morgen"},
  {"surah_number": 15, "verse_number": 84, "content": "Und alles, was sie sich erworben hatten, nutzte ihnen nichts"},
  {"surah_number": 15, "verse_number": 85, "content": "Wir erschufen die Himmel und die Erde und das, was zwischen beiden ist, nicht anders als in gerechter Weise; und die Stunde kommt gewiß. Darum ube Vergebung in schoner Weise"},
  {"surah_number": 15, "verse_number": 86, "content": "Wahrlich, dein Herr - Er ist der Schopfer, der Allwissende"},
  {"surah_number": 15, "verse_number": 87, "content": "Und Wir gaben dir wahrlich die sieben Doppelverse und den großartigen Quran"},
  {"surah_number": 15, "verse_number": 88, "content": "Und laß deine Augen nicht auf das abschweifen, was Wir manchen von ihnen zu kurzer Nutznießung verliehen haben, und sei auch nicht traurig ihretwegen; und senke deinen Flugel auf die Glaubigen"},
  {"surah_number": 15, "verse_number": 89, "content": "Und sprich: \"lch bin gewiß der deutliche Warner"},
  {"surah_number": 15, "verse_number": 90, "content": "Wie Wir (die Strafe) auf jene herabsandten, die sich abgespalten haben"},
  {"surah_number": 15, "verse_number": 91, "content": "und den Quran fur lauter Lugen erklarten"},
  {"surah_number": 15, "verse_number": 92, "content": "Darum, bei deinem Herrn, werden Wir sie sicherlich alle zur Rechenschaft ziehen"},
  {"surah_number": 15, "verse_number": 93, "content": "um dessentwillen, was sie zu tun pflegten"},
  {"surah_number": 15, "verse_number": 94, "content": "So tue kund, was dir befohlen wurde, und wende dich von den Gotzendienern ab"},
  {"surah_number": 15, "verse_number": 95, "content": "Wir werden dir sicherlich gegen die Spotter genugen"},
  {"surah_number": 15, "verse_number": 96, "content": "die einen anderen Gott neben Allah setzen, doch bald werden sie es wissen"},
  {"surah_number": 15, "verse_number": 97, "content": "Und wahrlich, Wir wissen, daß deine Brust beklommen wird wegen dem, was sie reden"},
  {"surah_number": 15, "verse_number": 98, "content": "Aber lobpreise deinen Herrn und sei unter den SichNiederwerfenden"},
  {"surah_number": 15, "verse_number": 99, "content": "Und diene deinem Herrn, bis die Gewißheit zu dir kommt"},
  {"surah_number": 16, "verse_number": 1, "content": "Der Befehl Allahs kommt, so sucht ihn nicht zu beschleunigen. Gepriesen ist Er und Erhaben uber all das, was sie anbeten"},
  {"surah_number": 16, "verse_number": 2, "content": "Er sendet die Engel auf Seinen Befehl mit der Offenbarung zu dem von Seinen Dienern hernieder, zu dem Er will: \"Warnt (die Menschen,) daß kein Gott da ist außer Mir. Mich allein sollt ihr furchten"},
  {"surah_number": 16, "verse_number": 3, "content": "Er hat die Himmel und die Erde in gerechter Weise erschaffen. Erhaben ist Er uber all das, was sie anbeten"},
  {"surah_number": 16, "verse_number": 4, "content": "Er hat den Menschen aus einem Tropfen erschaffen, doch seht, nun ist er ein offenkundiger Gegner"},
  {"surah_number": 16, "verse_number": 5, "content": "Und das Vieh hat Er erschaffen, ihr habt an ihm Warme und Nutzen; und davon esset ihr"},
  {"surah_number": 16, "verse_number": 6, "content": "Und es ist schon fur euch, wenn ihr (es) abends eintreibt und morgens austreibt"},
  {"surah_number": 16, "verse_number": 7, "content": "und sie tragen eure Lasten in ein Land, das ihr nicht erreichen konntet, es sei denn mit großer Muhsal. Wahrlich, euer Herr ist Gutig, Barmherzig"},
  {"surah_number": 16, "verse_number": 8, "content": "Und (erschaffen hat Er) Pferde, Maultiere und Esel zum Reiten und zum Schmuck. Und Er wird erschaffen, was ihr (noch) nicht kennt"},
  {"surah_number": 16, "verse_number": 9, "content": "Allah weist den Weg. Es gibt solche, die (vom Weg) abweichen. Und hatte Er es gewollt, hatte Er euch allesamt den Weg gewiesen"},
  {"surah_number": 16, "verse_number": 10, "content": "Er ist es, Der Wasser aus den Wolken herniedersendet; davon habt ihr zu trinken, und davon wachsen die Gebusche, an denen ihr (euer Vieh) weiden lasset"},
  {"surah_number": 16, "verse_number": 11, "content": "Damit laßt Er fur euch Korn sprießen und den Olbaum und die Dattelpalme und die Trauben und Fruchte aller Art. Wahrlich, darin liegt ein Zeichen fur nachdenkende Leute"},
  {"surah_number": 16, "verse_number": 12, "content": "Und Er hat fur euch die Nacht und den Tag dienstbar gemacht und die Sonne und den Mond; und die Sterne sind auf Seinen Befehl hin dienstbar. Wahrlich, darin liegen Zeichen fur die Leute, die Verstand haben"},
  {"surah_number": 16, "verse_number": 13, "content": "Und was Er auf der Erde fur euch erschaffen hat, ist mannigfach an Farben. Wahrlich, darin liegt ein Zeichen fur Leute, die sich mahnen lassen"},
  {
    "surah_number": 16,
    "verse_number": 14,
    "content": "Und Er ist es, Der (euch) das Meer dienstbar gemacht hat, auf daß ihr zartes Fleisch daraus esset und Schmuck daraus gewinnt, um ihn euch anzulegen. Und du siehst, wie die Schiffe es durchfahren, auf daß ihr Seine Huld suchet und auf daß ihr dankbar sein moget"
  },
  {"surah_number": 16, "verse_number": 15, "content": "Und Er hat feste Berge auf der Erde gegrundet, damit sie nicht mit euch wanke, und Flusse und Wege, damit ihr recht gehen moget"},
  {"surah_number": 16, "verse_number": 16, "content": "und (Er hat) Wegzeichen (geschaffen); und durch die Gestirne finden sie die Richtung"},
  {"surah_number": 16, "verse_number": 17, "content": "Ist nun wohl Der, Der erschafft, dem gleich, der nicht erschafft? Wollt ihr euch nicht ermahnen lassen"},
  {"surah_number": 16, "verse_number": 18, "content": "Und wenn ihr Allahs Wohltaten aufzahlen wolltet, wurdet ihr sie nicht restlos erfassen konnen. Wahrlich, Allah ist Allverzeihend, Barmherzig"},
  {"surah_number": 16, "verse_number": 19, "content": "Und Allah weiß, was ihr verbergt und was ihr kundtut"},
  {"surah_number": 16, "verse_number": 20, "content": "Und jene, die sie statt Allah anrufen, schaffen nichts - sind sie doch selbst erschaffen"},
  {"surah_number": 16, "verse_number": 21, "content": "Tot sind sie, nicht lebendig; und sie wissen nicht, wann sie erweckt werden"},
  {"surah_number": 16, "verse_number": 22, "content": "Euer Gott ist der Einzige Gott. Und die, die nicht ans Jenseits glauben - ihre Herzen sind (der Wahrheit) fremd, und sie sind hochmutig"},
  {"surah_number": 16, "verse_number": 23, "content": "Unzweifelhaft kennt Allah, was sie verbergen und was sie kundtun. Wahrlich, Er liebt die Hochmutigen nicht"},
  {"surah_number": 16, "verse_number": 24, "content": "Und wenn sie gefragt werden: \"Was (haltet ihr) von dem, was euer Herr niedergesandt hat?\" sagen sie: \"(Das sind) Fabeln der Fruheren"},
  {"surah_number": 16, "verse_number": 25, "content": "(Dies ist so), damit sie am Tage der Auferstehung ihre Lasten vollstandig tragen und einen Teil der Lasten derer, die sie ohne Wissen irrefuhren. Wahrlich, schlimm ist das, was sie tragen"},
  {"surah_number": 16, "verse_number": 26, "content": "Diejenigen, die vor ihnen waren, planten auch Ranke, doch Allah ruttelte ihren Bau an den Grundmauern, so daß das Dach von oben her auf sie sturzte; und die Strafe kam uber sie, ohne daß sie ahnten, woher"},
  {
    "surah_number": 16,
    "verse_number": 27,
    "content": "Dann wird Er sie am Tage der Auferstehung zuschanden machen und sprechen: \"Wo sind nun Meine \"Teilhaber\", um deretwillen ihr strittet?\" Die mit Erkenntnis Begabten werden sagen: \"An diesem Tage wird Schande und Unheil den Unglaubigen (zuteil sein)"
  },
  {"surah_number": 16, "verse_number": 28, "content": "Diejenigen, die von den Engeln abberufen werden - da sie gegen sich selber gefrevelt haben und nun unterwurfig geworden sind (sagen): \"Wir pflegten ja, nichts Boses zu tun.\" Nein, wahrlich, Allah weiß wohl, was ihr zu tun pflegtet"},
  {"surah_number": 16, "verse_number": 29, "content": "So tretet durch die Tore der Gahannam ein, um darin zu wohnen. Schlimm ist wahrlich die Wohnstatt der Hochmutigen"},
  {
    "surah_number": 16,
    "verse_number": 30,
    "content":
        "Und (wenn) zu den Gottesfurchtigen gesprochen wird: \"Was (haltet ihr) von dem, was euer Herr herabgesandt hat?\" sagen sie: \"Etwas Gutes!\" Fur die, welche Gutes tun, ist Gutes in dieser Welt, und die Wohnstatt des Jenseits ist noch besser. Herrlich wahrlich ist die Wohnstatt der Gottesfurchtigen"
  },
  {"surah_number": 16, "verse_number": 31, "content": "Die Garten von Eden, die sie betreten werden: Bache durchfließen sie. Darin werden sie haben, was sie begehren. Und so belohnt Allah die Gottesfurchtigen"},
  {"surah_number": 16, "verse_number": 32, "content": "(Zu ihnen), die von den Engeln friedlich abberufen werden, sprechen die Engel: \"Friede sei auf euch! Tretet ein in das Paradies fur das, was ihr zu tun pflegtet"},
  {"surah_number": 16, "verse_number": 33, "content": "Worauf warten sie denn, wenn nicht darauf, daß die Engel zu ihnen kommen oder daß der Befehl deines Herrn eintrifft? So taten schon jene, die vor ihnen waren. Allah war nicht ungerecht gegen sie, jedoch sie waren ungerecht gegen sich selber"},
  {"surah_number": 16, "verse_number": 34, "content": "So ereilten sie die bosen Folgen ihres Tuns, und das, was sie zu verhohnen pflegten, umschloß sie von allen Seiten"},
  {
    "surah_number": 16,
    "verse_number": 35,
    "content":
        "Und die Gotzendiener sagen: \"Hatte Allah es so gewollt, so hatten wir nichts außer Ihm angebetet, weder wir noch unsere Vater, noch hatten wir etwas ohne Seinen (Befehl) verboten.\" Genauso taten schon jene vor ihnen. Jedoch sind die Gesandten fur irgend etwas außer fur die deutliche Verkundigung verantwortlich"
  },
  {
    "surah_number": 16,
    "verse_number": 36,
    "content":
        "Und in jedem Volk erweckten Wir einen Gesandten (, der da predigte): \"Dient Allah und meidet die Gotzen.\" Dann waren unter ihnen einige, die Allah leitete, und es waren unter ihnen einige, die das Schicksal des Irrtums erlitten. So reist auf der Erde umher und seht, wie das Ende der Leugner war"
  },
  {"surah_number": 16, "verse_number": 37, "content": "Wenn du fur sie den rechten Weg begehrst, dann (wisse, daß) Allah nicht jenen den Weg weist, die (andere) irrefuhren; noch gibt es fur sie Helfer"},
  {"surah_number": 16, "verse_number": 38, "content": "Und sie schworen bei Allah ihre festen Eide, Allah werde jene nicht auferwecken, die sterben. Aber nein! Ihn bindet ein Versprechen, das Ihm obliegt jedoch die meisten Menschen wissen es nicht"},
  {"surah_number": 16, "verse_number": 39, "content": "Er wird ihnen das klar machen, woruber sie uneins waren, damit jene, die unglaubig sind, wissen, daß sie Lugner sind"},
  {"surah_number": 16, "verse_number": 40, "content": "Unser Wort, das Wir fur eine Sache sprechen, wenn Wir sie wollen, ist nur: \"Sei!\" und sie ist"},
  {"surah_number": 16, "verse_number": 41, "content": "Und denjenigen, die um Allahs willen ausgewandert sind, nachdem sie unterdruckt worden waren, werden Wir sicherlich eine schone Wohnstatt in der Welt geben; und wahrlich, der Lohn des Jenseits ist (noch) großer, wenn sie es nur wußten"},
  {"surah_number": 16, "verse_number": 42, "content": "(es sind) diejenigen, die geduldig geblieben sind und auf ihren Herrn vertrauen"},
  {"surah_number": 16, "verse_number": 43, "content": "Und vor dir entsandten Wir (auch) nur Manner, denen Wir die Offenbarung gegeben haben; so fragt die, welche die Ermahnung besitzen, wenn ihr (etwas) nicht wisset"},
  {"surah_number": 16, "verse_number": 44, "content": "(Wir entsandten sie) mit den deutlichen Zeichen und mit den Buchern; und zu dir haben Wir die Ermahnung hinabgesandt, auf daß du den Menschen erklarest, was ihnen hinabgesandt wurde, und auf daß sie nachdenken mogen"},
  {"surah_number": 16, "verse_number": 45, "content": "Sind denn jene, die Boses planen, sicher da vor, daß Allah sie nicht in die Erde versinken laßt, oder daß die Strafe nicht uber sie kommen wird, wenn sie es nicht ahnen"},
  {"surah_number": 16, "verse_number": 46, "content": "Oder (sind jene sicher davor,) daß Er sie nicht in ihrem Hin und Her ergreift, so daß sie nicht imstande sein werden, dies zu vereiteln"},
  {"surah_number": 16, "verse_number": 47, "content": "Oder (sind jene sicher davor,) daß Er sie nicht durch vorgewarnte Vernichtung erfaßt? Euer Herr ist wahrlich Gutig Barmherzig"},
  {"surah_number": 16, "verse_number": 48, "content": "Haben sie denn nicht gesehen, wie die Schatten eines jeden Dings, das Allah erschuf, sich nach rechts und nach links wenden und sich vor Allah niederwerfen, wahrend sie gedemutigt werden"},
  {"surah_number": 16, "verse_number": 49, "content": "Und was an Geschopfen in den Himmeln und auf Erden ist, wirft sich vor Allah in Anbetung nieder; genauso die Engel, und sie betragen sich nicht hochmutig"},
  {"surah_number": 16, "verse_number": 50, "content": "Sie furchten ihren Herrn uber sich und tun, was ihnen befohlen wird"},
  {"surah_number": 16, "verse_number": 51, "content": "Und Allah hat gesprochen: \"Nehmet euch nicht zwei Gotter. Er ist der Einzige Gott. So furchtet Mich allein"},
  {"surah_number": 16, "verse_number": 52, "content": "Und Sein ist, was in den Himmeln und auf Erden ist, und Ihm gebuhrt die immerwahrende Furcht. Wollt ihr also einen anderen furchten außer Allah"},
  {"surah_number": 16, "verse_number": 53, "content": "Was ihr Gutes habt - es ist von Allah; und wenn euch ein Schaden trifft, dann fleht ihr Ihn um Hilfe an"},
  {"surah_number": 16, "verse_number": 54, "content": "Doch wenn Er dann den Schaden von euch hinwegnimmt, seht, da (beginnt) ein Teil von euch, ihrem Herrn Gotter zur Seite zu stellen"},
  {"surah_number": 16, "verse_number": 55, "content": "und das zu verleugnen, was Wir ihnen beschert haben. Wohlan, vergnugt euch nur eine Weile; bald aber werdet ihr es wissen"},
  {"surah_number": 16, "verse_number": 56, "content": "Und fur die, von denen sie nichts wissen, setzen sie einen Teil von dem beiseite, was Wir ihnen beschert haben. Bei Allah, ihr werdet sicherlich zur Rechenschaft gezogen werden fur all das, was ihr erdichtet"},
  {"surah_number": 16, "verse_number": 57, "content": "Und sie dichten Allah Tochter an. Gepriesen sei Er! Und sich selbst behalten sie vor, was sie begehren"},
  {"surah_number": 16, "verse_number": 58, "content": "Und wenn einem von ihnen die Nachricht von (der Geburt) einer Tochter uberbracht wird, so verfinstert sich sein Gesicht, und er unterdruckt den inneren Schmerz"},
  {"surah_number": 16, "verse_number": 59, "content": "Er verbirgt sich vor den Leuten aufgrund der schlimmen Nachricht, die er erhalten hat: Soll er sie behalten trotz der Schande, oder (soll er sie) in der Erde verscharren? Wahrlich, ubel ist, wie sie urteilen"},
  {"surah_number": 16, "verse_number": 60, "content": "Diejenigen, die nicht an das Jenseits glauben, sind mit dem Schlechten zu vergleichen; Allah aber mit dem Hochsten, und Er ist der Erhabene, der Allweise"},
  {
    "surah_number": 16,
    "verse_number": 61,
    "content":
        "Und wenn Allah die Menschen fur ihr Freveln bestrafen wollte, wurde Er nicht ein einziges Lebewesen darauf (auf der Erde) bestehen lassen; doch Er gewahrt ihnen Aufschub bis zu einer bestimmten Frist; und wenn ihre Frist um ist, dann konnen sie auch nicht eine Stunde dahinter zuruckbleiben, noch konnen sie (ihr) vorauseilen"
  },
  {"surah_number": 16, "verse_number": 62, "content": "Und sie schreiben Allah zu, was ihnen (selbst) mißliebig ist, und ihre Zungen außern die Luge, daß sie das Beste erhalten wurden. Zweifellos wird ihnen das Feuer zuteil werden, und (darin) sollen sie unbeachtet bleiben"},
  {"surah_number": 16, "verse_number": 63, "content": "Bei Allah, Wir schickten wahrlich schon Gesandte zu den Volkern vor dir, doch Satan ließ ihnen ihre Werke wohlgefallig erscheinen. So ist er heute ihr Schutzherr, und ihnen wird eine schmerzliche Strafe zuteil sein"},
  {"surah_number": 16, "verse_number": 64, "content": "Und Wir haben dir das Buch nur deshalb hinabgesandt, auf daß du ihnen das erklarest, woruber sie uneinig sind, und (Wir haben es) als Fuhrung und Barmherzigkeit fur die Leute, die glauben (, hinabgesandt)"},
  {"surah_number": 16, "verse_number": 65, "content": "Und Allah hat Wasser vom Himmel herniedergesandt und damit die Erde nach ihrem Tod belebt. Wahrlich, darin liegt ein Zeichen fur Leute, die horen konnen"},
  {"surah_number": 16, "verse_number": 66, "content": "Wahrlich, auch am Vieh habt ihr eine Lehre. Wir geben euch von dem zu trinken, was in ihren Leibern (ist): Zwischen Kot und Blut (ist) in der Mitte Milch, die denen lauter (und) angenehm ist, die sie trinken"},
  {"surah_number": 16, "verse_number": 67, "content": "Und von den Fruchten der Dattelpalmen und den Beeren macht ihr euch Rauschtrank und gute Speise. Wahrlich, darin liegt ein Zeichen fur die Leute, die Verstand haben"},
  {"surah_number": 16, "verse_number": 68, "content": "Und dein Herr hat der Biene eingegeben: \"Baue dir Hauser in den Bergen und in den Baumen und in dem, was sie errichten"},
  {
    "surah_number": 16,
    "verse_number": 69,
    "content": "Dann iß von allen Fruchten und folge den Wegen deines Herrn, (die Er dir) leicht gemacht hat.\" Aus ihren Leibern kommt ein Trank, mannigfach an Farbe. Darin liegt ein Heilmittel fur die Menschen. Wahrlich, hierin ist ein Zeichen fur Leute, die nachdenken"
  },
  {
    "surah_number": 16,
    "verse_number": 70,
    "content": "Und Allah hat euch erschaffen, dann laßt Er euch sterben; und es gibt manche unter euch, die ins hinfallige Greisenalter getrieben werden, so daß sie nichts wissen, nachdem (sie) doch Wissen (besessen haben) Wahrlich, Allah ist Allwissend, Allmachtig"
  },
  {
    "surah_number": 16,
    "verse_number": 71,
    "content": "Und Allah hat einige von euch vor den anderen mit Gaben begunstigt. Und doch wollen die Begunstigten nichts von ihren Gaben denen zuruckgeben, die sie von Rechts wegen besitzen, so daß sie gleich (beteiligt) waren. Wollen sie denn Allahs Huld verleugnen"
  },
  {"surah_number": 16, "verse_number": 72, "content": "Und Allah gab euch Gattinnen aus euch selbst, und aus euren Gattinnen machte Er euch Sohne und Enkelkinder, und Er hat euch mit Gutem versorgt. Wollen sie da an Nichtiges glauben und Allahs Huld verleugnen"},
  {"surah_number": 16, "verse_number": 73, "content": "Dennoch verehren sie statt Allah solche, die nicht die Macht haben, ihnen irgendwelche Gaben von den Himmeln oder der Erde zu gewahren, noch konnen sie je solche Macht erlangen"},
  {"surah_number": 16, "verse_number": 74, "content": "So sollt ihr Allah keine Gleichnisse pragen. Gewiß, Allah weiß, und ihr wisset nicht"},
  {
    "surah_number": 16,
    "verse_number": 75,
    "content":
        "Allah gibt (euch) das Gleichnis (von zwei Mannern) an die Hand: von einem Sklaven, einem Leibeigenen, der uber nichts Gewalt hat, und von einem (Freien), den Wir Selbst reichlich versorgt haben, und der davon im Verborgenen und offenkundig spendet. Sind diese gleich? Preis sei Allah! Doch die meisten von ihnen wissen es nicht"
  },
  {
    "surah_number": 16,
    "verse_number": 76,
    "content":
        "Und Allah gibt (euch) ein (anderes) Gleichnis von zwei Mannern an die Hand: der eine von ihnen ist stumm, er hat uber nichts Gewalt und ist fur seinen Dienstherrn eine Last; wo er ihn auch hinschicken mag - er bringt nichts Gutes (zuruck) Kann er dem gleich sein, der Gerechtigkeit gebietet und der selbst auf dem geraden Weg ist"
  },
  {"surah_number": 16, "verse_number": 77, "content": "Und Allah gehort das Ungesehene der Himmel und der Erde; und was die Stunde anbelangt, so ist sie nur einen Augenblick (entfernt), nein, sie ist noch naher. Gewiß, Allah hat Macht uber alle Dinge"},
  {"surah_number": 16, "verse_number": 78, "content": "Und Allah hat euch aus dem Schoß eurer Mutter hervorgebracht, ohne daß ihr etwas wußtet, und Er gab euch Ohren und Augen und Herzen, auf daß ihr danken moget"},
  {"surah_number": 16, "verse_number": 79, "content": "Sehen sie nicht die Vogel, die im Luftraum des Himmels in Dienstbarkeit gehalten sind? Keiner halt sie aufrecht außer Allah. Wahrlich, darin liegen Zeichen fur die Leute, die glauben"},
  {
    "surah_number": 16,
    "verse_number": 80,
    "content":
        "Und Allah hat euch eure Hauser zu einem Ruheplatz gemacht, und Er hat euch aus den Hauten der Tiere Zelte gemacht, die ihr leicht zur Zeit eurer Reise und zur Zeit eures Aufenthalts handhaben konnt; und ihre Wolle und ihr Pelz und ihr Haar (gab Er euch) zu Gebrauchsgegenstanden und zur Nutznießung fur eine (bestimmte) Zeit"
  },
  {
    "surah_number": 16,
    "verse_number": 81,
    "content":
        "Und Allah hat euch aus dem, was Er geschaffen hat, schattenspendende Dinge gemacht, und in den Bergen hat Er euch Schutzwinkel gemacht, und Er hat euch Gewander gemacht, die euch vor Hitze schutzen, und Panzerhemden, die euch im Kampf schutzen. So vollendet Er Seine Gnade an euch, auf daß ihr (Ihm) ergeben sein moget"
  },
  {"surah_number": 16, "verse_number": 82, "content": "Doch wenn sie sich abkehren, dann bist du fur nichts verantwortlich außer fur die klare Verkundigung"},
  {"surah_number": 16, "verse_number": 83, "content": "Sie kennen Allahs Gnade, und sie leugnen sie doch; und die meisten von ihnen sind unglaubig"},
  {"surah_number": 16, "verse_number": 84, "content": "Und am Tage, da Wir aus jeglichem Volk einen Zeugen erwecken werden, wird denen, die nicht glauben, nichts gestattet werden, noch wird ihre Entschuldigung angenommen werden"},
  {"surah_number": 16, "verse_number": 85, "content": "Und wenn jene, die Unrecht begangen haben, die Strafe erst erleben, dann wird sie ihnen nicht leicht gemacht, noch werden sie Aufschub erlangen"},
  {"surah_number": 16, "verse_number": 86, "content": "Und wenn die, welche Gotter anbeten, ihre Gotter sehen werden, so werden sie sagen: \"Unser Herr, das sind unsere Gotter, die wir statt Deiner anriefen.\" Darauf werden sie ihnen die Beschuldigung zuruckgeben: \"Wahrlich, ihr seid Lugner"},
  {"surah_number": 16, "verse_number": 87, "content": "Und an jenem Tage werden sie Allah (ihre) Unterwerfung anbieten, und alles, was sie zu erdichten pflegten, wird sie im Stich lassen"},
  {"surah_number": 16, "verse_number": 88, "content": "Die unglaubig sind und von Allahs Weg abhalten - zu (deren) Strafe werden Wir noch eine Strafe hinzufugen, weil sie Unheil anrichteten"},
  {
    "surah_number": 16,
    "verse_number": 89,
    "content":
        "Und am Tage, da Wir in jeglichem Volk einen Zeugen aus ihren eigenen Reihen gegen sie selbst erwecken werden, wollen Wir dich als Zeugen bringen gegen diese. Und Wir haben dir das Buch zur Erklarung aller Dinge herniedergesandt, und als Fuhrung und Barmherzigkeit und frohe Botschaft fur die Gottergebenen"
  },
  {
    "surah_number": 16,
    "verse_number": 90,
    "content": "Wahrlich, Allah gebietet, gerecht (zu handeln), uneigennutzig Gutes zu tun und freigebig gegenuber den Verwandten zu sein; und Er verbietet, was schandlich und abscheulich und gewalttatig ist. Er ermahnt euch; vielleicht werdet ihr die Ermahnung annehmen"
  },
  {"surah_number": 16, "verse_number": 91, "content": "Haltet den Bund mit Allah ein, wenn ihr einen Bund geschlossen habt; und brecht die Eide nicht nach ihrer Bekraftigung, wo ihr doch Allah zum Burgen fur euch gemacht habt. Wahrlich, Allah weiß, was ihr tut"},
  {
    "surah_number": 16,
    "verse_number": 92,
    "content":
        "Und seid nicht wie jene (Frau), die ihre Strickarbeiten aufloste, nachdem sie diese angefertigt hatte. Ihr macht eure Eide zu einem Mittel, euch gegenseitig zu betrugen, (aus Furcht,) ein Volk konnte sonst machtiger werden als ein anderes. Allah stellt euch damit nur auf die Probe, und am Tage der Auferstehung wird Er euch das klar machen, woruber ihr uneinig waret"
  },
  {"surah_number": 16, "verse_number": 93, "content": "Und hatte Allah es gewollt, hatte Er euch sicherlich zu einer einzigen Gemeinde gemacht; jedoch Er laßt irregehen, wen Er will, und fuhrt richtig, wen Er will, und ihr werdet gewiß zur Rechenschaft gezogen werden fur das, was ihr getan habt"},
  {
    "surah_number": 16,
    "verse_number": 94,
    "content": "Und macht eure Eide nicht zu einem Mittel, euch gegenseitig zu betrugen; sonst wird euer Fuß ausgleiten, nachdem er fest aufgetreten ist, und ihr werdet dafur vom Ubel kosten, daß ihr von Allahs Weg abgehalten habt, und euch wird eine strenge Strafe zuteil sein"
  },
  {"surah_number": 16, "verse_number": 95, "content": "Und verschachert Allahs Bund nicht zu einem armseligen Preis. Wahrlich, was bei Allah ist, ist besser fur euch, wenn ihr es nur wußtet"},
  {"surah_number": 16, "verse_number": 96, "content": "Was bei euch ist, vergeht, und was bei Allah ist, bleibt. Und Wir werden gewiß denen, die standhaft sind, ihren Lohn nach der besten ihrer Taten bemessen"},
  {"surah_number": 16, "verse_number": 97, "content": "Dem, der recht handelt - ob Mann oder Frau - und glaubig ist, werden Wir gewiß ein gutes Leben gewahren; und Wir werden gewiß solchen (Leuten) ihren Lohn nach der besten ihrer Taten bemessen"},
  {"surah_number": 16, "verse_number": 98, "content": "Und wenn du den Quran liest, so suche bei Allah Zuflucht vor dem Satan, dem Verfluchten"},
  {"surah_number": 16, "verse_number": 99, "content": "Wahrlich, er hat keine Macht uber die, welche da glauben und auf ihren Herrn vertrauen"},
  {"surah_number": 16, "verse_number": 100, "content": "Siehe, seine Macht erstreckt sich nur auf jene, die bei ihm Beistand suchen und die (Allah) Gotter zur Seite stellen"},
  {"surah_number": 16, "verse_number": 101, "content": "Und wenn Wir einen Vers an Stelle eines anderen bringen - und Allah weiß am besten, was Er offenbart -, sagen sie: \"Du bist nur ein Erdichter.\" Nein, aber die meisten von ihnen wissen es nicht"},
  {
    "surah_number": 16,
    "verse_number": 102,
    "content": "Sprich: \"Der Geist der Heiligkeit hat ihn (den Quran) von deinem Herrn in Wahrheit herabgebracht, auf daß Er die festige, die da glauben, und (er hat den Quran) zu einer Fuhrung und einer frohen Botschaft fur die Gottergebenen (herabgebracht)"
  },
  {"surah_number": 16, "verse_number": 103, "content": "Und Wir wissen wahrlich, daß sie sagen, wer ihn lehrt, sei nur ein Mensch. Die Sprache dessen jedoch, auf den sie hinweisen, ist eine fremde, wahrend dies hier eine deutliche arabische Sprache ist"},
  {"surah_number": 16, "verse_number": 104, "content": "Denen, die nun nicht an die Zeichen Allahs glauben, wird Allah nicht den Weg weisen, und ihnen wird eine schmerzliche Strafe zuteil sein"},
  {"surah_number": 16, "verse_number": 105, "content": "Wahrlich, es sind ja nur jene, die nicht an die Zeichen Allahs glauben, die da Falsches erdichten; und sie allein sind die Lugner"},
  {
    "surah_number": 16,
    "verse_number": 106,
    "content": "Wer Allah verleugnet, nachdem er geglaubt hat - den allein ausgenommen, der (dazu) gezwungen wird, wahrend sein Herz im Glauben Frieden findet -, auf jenen aber, die ihre Brust dem Unglauben offnen, lastet Allahs Zorn; und ihnen wird eine strenge Strafe zuteil sein"
  },
  {"surah_number": 16, "verse_number": 107, "content": "Dies (ist so), weil sie das Leben des Diesseits dem des Jenseits vorgezogen haben, und weil Allah das Volk der Unglaubigen nicht leitet"},
  {"surah_number": 16, "verse_number": 108, "content": "Sie sind es, auf deren Herzen und Ohren und Augen Allah ein Siegel gesetzt hat. Und sie sind die Achtlosen"},
  {"surah_number": 16, "verse_number": 109, "content": "Zweifellos sind sie es, die im Jenseits die Verlierenden sein werden"},
  {"surah_number": 16, "verse_number": 110, "content": "Alsdann wird dein Herr denen gegenuber, die auswanderten, nachdem sie verfolgt worden waren, und dann kampften und geduldig blieben - siehe, (ihnen gegenuber) wird dein Herr hernach gewiß Allverzeihend, Barmherzig sein"},
  {"surah_number": 16, "verse_number": 111, "content": "An dem Tage, da jede Seele kommen wird, um sich selbst zu rechtfertigen, und da jeder Seele voll vergolten wird, was sie getan hat, sollen sie kein Unrecht erleiden"},
  {
    "surah_number": 16,
    "verse_number": 112,
    "content":
        "Und Allah gibt das Gleichnis von einer Stadt: Sie genoß Sicherheit und Frieden und wurde reichlich aus allen Orten versorgt; doch sie leugnete die Wohltaten Allahs. Darum ließ Allah sie eine umfassende Not des Hungers und der Furcht kosten fur das, was sie (ihre Bewohner) zu tun pflegten"
  },
  {"surah_number": 16, "verse_number": 113, "content": "Und wahrlich, zu ihnen war ein Gesandter aus ihrer Mitte gekommen, sie aber verleugneten ihn; da ereilte sie die Strafe, weil sie Frevler waren"},
  {"surah_number": 16, "verse_number": 114, "content": "Darum esset nun von den erlaubten guten Dingen, womit Allah euch versorgt hat; und seid dankbar fur Allahs Huld, wenn Er es ist, Dem ihr dient"},
  {
    "surah_number": 16,
    "verse_number": 115,
    "content":
        "Verwehrt hat Er euch nur das von selbst Verendete und Blut und Schweinefleisch und das, woruber ein anderer Name als Allahs angerufen worden ist. Wer aber genotigt wird, (davon zu essen,) ohne die Gebote ubertreten zu wollen und ohne das Maß zu uberschreiten -, wahrlich, Allah ist dann Allverzeihend, Barm herzig"
  },
  {"surah_number": 16, "verse_number": 116, "content": "Und sagt nicht aufgrund der Falschheit eurer Zungen: \"Das ist erlaubt, und das ist verboten\", so daß ihr eine Luge gegen Allah erdichtet. Wahrlich, diejenigen, die eine Luge gegen Allah erdichten, haben keinen Erfolg"},
  {"surah_number": 16, "verse_number": 117, "content": "Wenig Genuß (haben sie), (dann) aber wird ihnen eine schmerzliche Strafe zuteil sein"},
  {"surah_number": 16, "verse_number": 118, "content": "Und denen, die Juden sind, haben Wir zuvor schon all das verboten, was Wir dir mitteilen. Und nicht Wir taten ihnen Unrecht, sondern sie taten sich selber Unrecht"},
  {"surah_number": 16, "verse_number": 119, "content": "Alsdann wird dein Herr gegen die, die in Unwissenheit Boses tun und es danach bereuen und sich bessern -, wahrlich, (ihnen gegenuber) wird dein Herr hernach Allverzeihend, Barmherzig sein"},
  {"surah_number": 16, "verse_number": 120, "content": "Wahrlich, Abraham war ein Vorbild: (er war) gehorsam gegen Allah, lauter im Glauben, und er gehorte nicht zu den Gotzendienern"},
  {"surah_number": 16, "verse_number": 121, "content": "er war dankbar fur Seine Wohltaten; Er erwahlte ihn und leitete ihn auf den geraden Weg"},
  {"surah_number": 16, "verse_number": 122, "content": "Und Wir gewahrten ihm Gutes in dieser Welt, und im Jenseits wird er sicherlich unter den Rechtschaffenen sein"},
  {"surah_number": 16, "verse_number": 123, "content": "Alsdann haben Wir dir offenbart: \"Folge dem Weg Abrahams, des Lauteren im Glauben, der nicht zu den Gotzendienern gehorte"},
  {"surah_number": 16, "verse_number": 124, "content": "Der Sabbat wurde denen auferlegt, die uber ihn uneins waren; und dein Herr wird gewiß am Tage der Auferstehung zwischen ihnen uber das richten, woruber sie uneins waren"},
  {"surah_number": 16, "verse_number": 125, "content": "Rufe zum Weg deines Herrn mit Weisheit und schoner Ermahnung auf, und streite mit ihnen auf die beste Art. Wahrlich, dein Herr weiß am besten, wer von Seinem Wege abgeirrt ist; und Er kennt jene am besten, die rechtgeleitet sind"},
  {"surah_number": 16, "verse_number": 126, "content": "Und wenn ihr bestraft, dann bestraft in dem Maße, wie euch Unrecht zugefugt wurde; wollt ihr es aber geduldig ertragen, dann ist das wahrlich das Beste fur die Geduldigen"},
  {"surah_number": 16, "verse_number": 127, "content": "und harre in Geduld aus; deine Geduld aber kommt nur von Allah. Und sei weder traurig uber sie, noch beunruhigt wegen ihrer Ranke"},
  {"surah_number": 16, "verse_number": 128, "content": "Wahrlich, Allah ist mit denen, die gottesfurchtig sind und Gutes tun"},
  {"surah_number": 17, "verse_number": 1, "content": "Gepriesen sei Der, Der bei Nacht Seinen Diener von der heiligen Moschee zu der fernen Moschee, deren Umgebung Wir gesegnet haben, hinfuhrte, auf daß Wir ihm einige Unserer Zeichen zeigten. Wahrlich, Er ist der Allhorende, der Allsehende"},
  {"surah_number": 17, "verse_number": 2, "content": "Und Wir gaben Moses die Schrift und machten sie zu einer Fuhrung fur die Kinder Israels (und sprachen): \"Nehmt keinen zum Beschutzer außer Mir"},
  {"surah_number": 17, "verse_number": 3, "content": "o ihr Nachkommen derer, die Wir mit Noah trugen! Er war wahrlich ein dankbarer Diener"},
  {"surah_number": 17, "verse_number": 4, "content": "Und Wir hatten den Kindern Israels in der Schrift dargelegt: \"Seht, ihr werdet gewiß zweimal im Land Unheil anrichten, und ihr werdet gewiß sehr uberheblich sein"},
  {"surah_number": 17, "verse_number": 5, "content": "Wenn nun die Zeit fur die erste der beiden Verheißungen in Erfullung kam, da sandten Wir gegen euch Diener von Uns, die mit gewaltiger Kriegsmacht begabt waren; und sie drangen in das Wohngebiet ein, und so wurde die Verheißung erfullt"},
  {"surah_number": 17, "verse_number": 6, "content": "Dann gaben Wir euch wiederum die Macht uber sie und starkten euch mit Reichtum und Kindern und vermehrten eure Zahl"},
  {
    "surah_number": 17,
    "verse_number": 7,
    "content":
        "Wenn ihr Gutes tut, so tut ihr Gutes fur eure eigenen Seelen; und wenn ihr Boses tut, so ist es gegen sie. Als nun die Zeit fur die zweite Verheißung in Erfullung kam (, entsandten Wir Diener), damit sie eure Gesichter demutigten und die Moschee betreten, wie sie sie das erste Mal betreten hatten, und alles, was sie erobert hatten, bis auf den Grund zerstorten"
  },
  {"surah_number": 17, "verse_number": 8, "content": "Es ist moglich, daß euer Herr Sich eurer erbarmt; doch wenn ihr zuruckkehrt, so wollen (auch) Wir zuruckkehren; und Wir haben Gahannam zu einem Gefangnis fur die Unglaubigen gemacht"},
  {"surah_number": 17, "verse_number": 9, "content": "Wahrlich, dieser Quran leitet zum wirklich Richtigen und bringt den Glaubigen, die gute Taten verrichten, die frohe Botschaft, auf daß ihnen großer Lohn zuteil werde"},
  {"surah_number": 17, "verse_number": 10, "content": "Und Wir haben denen, die nicht an das Jenseits glauben, eine schmerzliche Strafe bereitet"},
  {"surah_number": 17, "verse_number": 11, "content": "Und der Mensch bittet um das Schlimme, wie er um das Gute bittet; und der Mensch ist voreilig"},
  {
    "surah_number": 17,
    "verse_number": 12,
    "content":
        "Und Wir machten die Nacht und den Tag zu zwei Zeichen, indem Wir das Zeichen der Nacht geloscht haben, und das Zeichen des Tages haben Wir sichtbar gemacht, damit ihr nach der Fulle eures Herrn trachtet und die Zahlung der Jahre und die Rechenkunst kennt. Und jegliches Ding haben Wir durch eine deutliche Erklarung klar gemacht"
  },
  {"surah_number": 17, "verse_number": 13, "content": "Und einem jeden Menschen haben Wir seine Taten an den Nacken geheftet; und am Tage der Auferstehung werden Wir ihm ein Buch herausbringen, das ihm geoffnet vorgelegt wird"},
  {"surah_number": 17, "verse_number": 14, "content": "lies dein Buch. Heute genugt deine eigene Seele, um die Abrechnung gegen dich vorzunehmen"},
  {
    "surah_number": 17,
    "verse_number": 15,
    "content": "Wer den rechten Weg befolgt, der befolgt ihn nur zu seinem eigenen Heil; und wer irregeht, der geht allein zu seinem eignen Schaden irre. Und keine lasttragende Seele soll die Last einer anderen tragen. Und Wir bestrafen nie, ohne zuvor einen Gesandten geschickt zu haben"
  },
  {"surah_number": 17, "verse_number": 16, "content": "Und wenn Wir eine Stadt zu zerstoren beabsichtigen, lassen Wir Unseren Befehl an ihre Wohlhabenden ergehen; wenn sie in ihr freveln, so wird der Richtspruch gegen sie fallig, und Wir zerstoren sie bis auf den Grund"},
  {"surah_number": 17, "verse_number": 17, "content": "Und wie viele Geschlechter haben Wir nach Noah vernichtet! Und dein Herr kennt und sieht die Sunden Seiner Diener zur Genuge"},
  {"surah_number": 17, "verse_number": 18, "content": "Wenn einer das Irdische begehrt, bereiten Wir ihm schnell das, was Wir wollen - dem, der Uns beliebt; danach haben Wir Gahannam fur ihn bestimmt, in der er brennt, verdammt und verstoßen"},
  {"surah_number": 17, "verse_number": 19, "content": "Und wenn aber einer das Jenseits begehrt und es beharrlich erstrebt und glaubig ist - dessen Eifer wird mit Dank belohnt"},
  {"surah_number": 17, "verse_number": 20, "content": "Ihnen allen, diesen und jenen, gewahren Wir die Gabe deines Herrn. Und die Gabe deines Herrn ist keinesfalls beschrankt"},
  {"surah_number": 17, "verse_number": 21, "content": "Schau, wie Wir die einen von ihnen uber die anderen erhoht haben; und wahrhaftig, das Jenseits soll noch mehr Range und Auszeichnungen enthalten"},
  {"surah_number": 17, "verse_number": 22, "content": "Setze neben Allah keinen anderen Gott, auf daß du nicht mit Schimpf bedeckt und verlassen dasitzt"},
  {
    "surah_number": 17,
    "verse_number": 23,
    "content": "Und dein Herr hat befohlen: \"Verehrt keinen außer Ihm und (erweist) den Eltern Gute. Wenn ein Elternteil oder beide bei dir ein hohes Alter erreichen, so sage dann nicht \"Pfui!\" zu ihnen und fahre sie nicht an, sondern sprich zu ihnen in ehrerbietiger Weise"
  },
  {"surah_number": 17, "verse_number": 24, "content": "Und senke fur sie in Barmherzigkeit den Flugel der Demut und sprich: \"Mein Herr, erbarme Dich ihrer (ebenso mitleidig), wie sie mich als Kleines aufgezogen haben"},
  {"surah_number": 17, "verse_number": 25, "content": "Euer Herr weiß am besten, was in euren Seelen ist: Wenn ihr rechtgesinnt seid, dann ist Er gewiß Verzeihend gegenuber den SichBekehrenden"},
  {"surah_number": 17, "verse_number": 26, "content": "Und gib dem Verwandten, was ihm gebuhrt, und ebenso dem Armen und dem Sohn des Weges, aber sei (dabei) nicht ausgesprochen verschwenderisch"},
  {"surah_number": 17, "verse_number": 27, "content": "Denn die Verschwender sind Bruder der Satane, und Satan war undankbar gegen seinen Herrn"},
  {"surah_number": 17, "verse_number": 28, "content": "Und wenn du dich von ihnen abwendest - im Trachten nach der Barmherzigkeit deines Herrn, auf die du hoffst -, so sprich zu ihnen angenehme Worte"},
  {"surah_number": 17, "verse_number": 29, "content": "Und laß deine Hand nicht an deinen Hals gefesselt sein, aber strecke sie auch nicht zu weit geoffnet aus, damit du nicht getadelt (und) zerschlagen niedersitzen mußt"},
  {"surah_number": 17, "verse_number": 30, "content": "Wahrlich, dein Herr erweitert und beschrankt (dem), dem Er will, die Mittel zum Unterhalt; denn Er kennt und sieht Seine Diener wohl"},
  {"surah_number": 17, "verse_number": 31, "content": "Und totet eure Kinder nicht aus Furcht vor Armut; Wir sorgen fur sie und fur euch. Wahrlich, sie zu toten ist ein großer Fehler"},
  {"surah_number": 17, "verse_number": 32, "content": "Und kommt der Unzucht nicht nahe; seht, das ist eine Schandlichkeit und ein ubler Weg"},
  {
    "surah_number": 17,
    "verse_number": 33,
    "content": "Und totet nicht das Leben, das Allah unverletzlich gemacht hat, es sei denn zu Recht. Und wer da ungerechterweise getotet wird - dessen Erben haben Wir gewiß Ermachtigung (zur Vergeltung) gegeben; doch soll er im Toten nicht maßlos sein; denn er findet (Unsere) Hilfe"
  },
  {"surah_number": 17, "verse_number": 34, "content": "Und tastet nicht das Gut der Waise an, es sei denn zu (ihrem) Besten, bis sie die Reife erreicht hat. Und haltet die Verpflichtung ein; denn uber die Verpflichtung muß Rechenschaft abgelegt werden"},
  {"surah_number": 17, "verse_number": 35, "content": "Und gebt volles Maß, wenn ihr meßt, und wagt mit richtiger Waage; das ist durchaus vorteilhaft und letzten Endes das Beste"},
  {"surah_number": 17, "verse_number": 36, "content": "Und verfolge nicht das, wovon du keine Kenntnis hast. Wahrlich, das Ohr und das Auge und das Herz sie alle sollen zur Rechenschaft gezogen werden"},
  {"surah_number": 17, "verse_number": 37, "content": "Und wandle nicht uberheblich auf der Erde; denn du kannst weder die Erde durchbrechen, noch kannst du die Berge an Hohe erreichen"},
  {"surah_number": 17, "verse_number": 38, "content": "Dasselbe all dessen ist hassenswert vor deinem Herrn"},
  {"surah_number": 17, "verse_number": 39, "content": "Dies ist ein Teil von der Weisheit, die dir dein Herr offenbart hat. Und setze neben Allah keinen anderen Gott, auf daß du nicht in Gahannam geworfen werdest, verdammt und verstoßen"},
  {"surah_number": 17, "verse_number": 40, "content": "Hat euer Herr euch denn mit Sohnen bevorzugt und fur Sich Selbst Tochter von den Engeln genommen? Wahrlich, ihr sprecht da ein großes Wort"},
  {"surah_number": 17, "verse_number": 41, "content": "Und wahrlich, Wir haben in diesem Quran (manches) auf verschiedene Art dargelegt, damit sie ermahnt seien, doch es mehrt nur ihren Widerwillen"},
  {"surah_number": 17, "verse_number": 42, "content": "Sprich: \"Gabe es neben Ihm noch andere Gotter, wie sie behaupten, dann hatten sie gewiß versucht, einen Weg zum Herrn des Throns einzuschlagen"},
  {"surah_number": 17, "verse_number": 43, "content": "Gepriesen sei Er und in großer Weise Erhaben uber all das, was sie behaupten"},
  {"surah_number": 17, "verse_number": 44, "content": "Die sieben Himmel und die Erde und alle darin lobpreisen Ihn; und es gibt nichts, was Seine Herrlichkeit nicht preist; ihr aber versteht deren Lobpreisung nicht. Wahrlich, Er ist Nachsichtig, Allverzeihend"},
  {"surah_number": 17, "verse_number": 45, "content": "Und wenn du den Quran verliest, legen Wir zwischen dir und jenen, die nicht an das Jenseits glauben, eine unsichtbare Scheidewand"},
  {"surah_number": 17, "verse_number": 46, "content": "Und Wir legen Hullen auf ihre Herzen, so daß sie ihn nicht verstehen, und in ihre Ohren Taubheit. Und wenn du im Quran deinen Herrn nennst, Ihn allein, so wenden sie ihren Rucken in Widerwillen ab"},
  {"surah_number": 17, "verse_number": 47, "content": "Wir wissen am besten, worauf sie horchen, wenn sie dir zuhoren, und wenn sie sich insgeheim bereden, wahrend die Frevler sagen: \"lhr folgt nur einem Manne, der einem Zauber zum Opfer gefallen ist"},
  {"surah_number": 17, "verse_number": 48, "content": "Schau, wie sie von dir Gleichnisse pragen und damit so sehr in die Irre gegangen sind, daß sie nicht in der Lage sind, den Weg zu finden"},
  {"surah_number": 17, "verse_number": 49, "content": "Und sie sagen: \"Wenn wir zu Gebeinen und Staub geworden sind, sollen wir dann wirklich zu einer neuen Schopfung auferweckt werden"},
  {"surah_number": 17, "verse_number": 50, "content": "Sprich: \"Ob ihr Steine oder Eisen"},
  {
    "surah_number": 17,
    "verse_number": 51,
    "content":
        "oder sonst eine Schopfung von der Art seid, die nach eurem Sinn am schwersten wiegt.\" Dann werden sie sagen: \"Wer soll uns ins Leben zuruckrufen?\" Sprich: \"Er, Der euch das erste Mal erschuf.\" Dann werden sie ihre Kopfe vor dir schutteln und sagen: \"Wann geschieht es?\" Sprich: \"Vielleicht geschieht es gar bald"
  },
  {"surah_number": 17, "verse_number": 52, "content": "An dem Tage, an dem Er euch ruft, da werdet ihr Ihm lobpreisend entgegenkommen und meinen, ihr hattet nur kurz verweilt"},
  {"surah_number": 17, "verse_number": 53, "content": "Und sprich zu Meinen Dienern, sie mochten nur das Beste reden; denn Satan stiftet zwischen ihnen Zwietracht. Wahrlich, Satan ist dem Menschen ein offenkundiger Feind"},
  {"surah_number": 17, "verse_number": 54, "content": "Euer Herr kennt euch am besten. Wenn Er will, so wird Er Sich eurer erbarmen, oder wenn Er will, so wird Er euch bestrafen. Und Wir haben dich nicht als ihren Wachter entsandt"},
  {"surah_number": 17, "verse_number": 55, "content": "Und dein Herr kennt jene am besten, die in den Himmeln und auf der Erde sind. Und wahrlich, Wir erhohten einige der Propheten uber die anderen, und David gaben Wir ein Buch"},
  {"surah_number": 17, "verse_number": 56, "content": "Sprich: \"Ruft doch die an, die ihr neben Ihm wahnt; sie haben keine Macht, weder das Unheil von euch zu nehmen noch es abzuwenden"},
  {"surah_number": 17, "verse_number": 57, "content": "Jene, die sie anrufen, suchen selbst die Nahe ihres Herrn (und wetteifern untereinander,) wer von ihnen (Ihm) am nachsten sei - und hoffen auf Sein Erbarmen und furchten Seine Strafe. Wahrlich, die Strafe deines Herrn ist zu furchten"},
  {"surah_number": 17, "verse_number": 58, "content": "Es gibt keine Stadt, die Wir nicht vor dem Tage der Auferstehung vernichten oder der (Wir) keine strenge Strafe auferlegen werden. Das ist in dem Buche niedergeschrieben"},
  {"surah_number": 17, "verse_number": 59, "content": "Und nichts konnte Uns hindern, Zeichen zu senden, obwohl die Fruheren sie verworfen hatten. Und Wir gaben den Tamud die Kamelstute als ein sichtbares Zeichen, doch sie frevelten an ihr. Und Wir senden Zeichen, nur um abzuschrecken"},
  {
    "surah_number": 17,
    "verse_number": 60,
    "content":
        "Und Wir sprachen da zu dir: \"Dein Herr umfaßt die Menschen.\" Und Wir haben das Traumgesicht, das Wir dich sehen ließen, nur als eine Prufung fur die Menschen gemacht und ebenso den verfluchten Baum im Quran. Und Wir warnen sie, jedoch es bestarkt sie nur noch in ihrer großen Ruchlosigkeit"
  },
  {"surah_number": 17, "verse_number": 61, "content": "Und als Wir zu den Engeln sprachen: \"Werft euch vor Adam nieder!\" da warfen sie sich nieder, außer Iblis. Er sagte: \"Soll ich mich vor einem niederwerfen, den Du aus Ton erschaffen hast"},
  {"surah_number": 17, "verse_number": 62, "content": "Er sagte (weiter): \"Was denkst Du? Dieser ist es, den Du hoher geehrt hast als mich! Willst Du mir eine Frist bis zum Tage der Auferstehung geben, so will ich gewiß Gewalt uber seine Nachkommen - bis auf einige wenige - erlangen"},
  {"surah_number": 17, "verse_number": 63, "content": "Er sprach: \"Fort mit dir! Und wer von ihnen dir folgt wahrlich, Gahannam soll deren aller Lohn sein, ein ausgiebiger Lohn"},
  {"surah_number": 17, "verse_number": 64, "content": "Und betore nun mit deiner Stimme von ihnen, wen du vermagst, und treibe dein Roß und dein Fußvolk gegen sie und habe an ihren Vermogen und Kindern teil und mache ihnen Versprechungen.\" - Und Satan verspricht ihnen nur Trug"},
  {"surah_number": 17, "verse_number": 65, "content": "Uber Meine Diener aber wirst du gewiß keine Macht haben.\" Und dein Herr genugt als Beschutzer"},
  {"surah_number": 17, "verse_number": 66, "content": "Euer Herr ist es, Der die Schiffe auf dem Meer fur euch treibt, auf daß ihr nach Seiner Gnade trachten moget. Wahrlich, Er ist gegen euch Barmherzig"},
  {"surah_number": 17, "verse_number": 67, "content": "Und wenn euch auf dem Meer ein Unheil trifft: verloren sind jene die ihr an Seiner Statt anruft. Hat Er euch aber ans Land gerettet, dann kehrt ihr euch (von Ihm) ab; denn der Mensch ist undankbar"},
  {"surah_number": 17, "verse_number": 68, "content": "Fuhlt ihr euch denn sicher davor, daß Er euch nicht auf dem Festland versinken laßt, oder daß Er einen heftigen Sandsturm gegen euch schickt, (so daß) ihr dann keinen Beschutzer fur euch findet"},
  {
    "surah_number": 17,
    "verse_number": 69,
    "content": "Oder fuhlt ihr euch sicher davor, daß Er euch nicht noch ein zweites Mal dorthin zuruckschickt und einen Sturmwind gegen euch entsendet und euch fur euren Unglauben ertrinken laßt? Dafur werdet ihr dann Uns gegenuber keinen Racher fur euch finden"
  },
  {"surah_number": 17, "verse_number": 70, "content": "Und wahrlich, Wir haben die Kinder Adams geehrt und sie uber Land und Meer getragen und sie mit guten Dingen versorgt und sie ausgezeichnet eine Auszeichnung vor jenen vielen, die Wir erschaffen haben"},
  {"surah_number": 17, "verse_number": 71, "content": "An jenem Tage, da werden Wir ein jedes Volk mit seinem Fuhrer vorladen. Diejenigen, die dann ihr Buch in ihrer Rechten empfangen werden, werden ihr Buch verlesen und nicht das geringste Unrecht erleiden"},
  {"surah_number": 17, "verse_number": 72, "content": "Wer aber blind ist in dieser (Welt), der wird auch im Jenseits blind sein und noch weiter vom Weg abirren"},
  {"surah_number": 17, "verse_number": 73, "content": "Und sie hatten dich beinahe in schwere Bedrangnis um dessentwillen gebracht, was Wir dir offenbarten, damit du etwas anderes uber Uns erdichten mogest; und dann hatten sie dich gewiß zu ihrem Freund erklart"},
  {"surah_number": 17, "verse_number": 74, "content": "Hatten Wir dich aber nicht gefestigt, hattest du dich ihnen nur wenig zugeneigt"},
  {"surah_number": 17, "verse_number": 75, "content": "Dann hatten Wir dich das Doppelte im Leben kosten lassen und das Doppelte im Tode; und du hattest keinen Helfer Uns gegenuber gefunden"},
  {"surah_number": 17, "verse_number": 76, "content": "Und in der Tat hatten sie dich fast aus dem Lande verscheucht, indem sie dich daraus vertreiben wollten; dann aber waren sie nach dir nur eine kurze Zeitspanne (dort) geblieben"},
  {"surah_number": 17, "verse_number": 77, "content": "In dieser Weise verfuhren (Wir) mit Unseren Gesandten, die Wir vor dir schickten; und du wirst keine Anderung in Unserem Verfahren finden"},
  {"surah_number": 17, "verse_number": 78, "content": "Verrichte dein Gebet vom Neigen der Sonne an bis zum Dunkel der Nacht, und (lies) den Quran bei Tagesanbruch. Wahrlich (besonders bei der Lesung) des Quran bei Tagesanbruch (sind die Engel) zugegen"},
  {"surah_number": 17, "verse_number": 79, "content": "Und unterbrich deswegen (fur die Lesung) in der Nacht deinen Schlaf (vollbringe) diese (Leistung) freiwillig. Es mag sein, daß dich dein Herr (dafur) zu einem loblichen Rang erweckt"},
  {"surah_number": 17, "verse_number": 80, "content": "Und sprich: \"O mein Herr, laß meinen Eingang einen guten Eingang sein und laß meinen Ausgang einen guten Ausgang sein. Und gewahre mir Deine hilfreiche Kraft"},
  {"surah_number": 17, "verse_number": 81, "content": "Und sprich: \"Gekommen ist die Wahrheit und dahingeschwunden ist die Falschheit; wahrlich, das Falsche verschwindet bestimmt"},
  {"surah_number": 17, "verse_number": 82, "content": "Und Wir senden vom Quran das hinab, was eine Heilung und Barmherzigkeit fur die Glaubigen ist; den Ungerechten aber mehrt es nur den Schaden"},
  {"surah_number": 17, "verse_number": 83, "content": "Und wenn Wir dem Menschen Gnade erweisen, wendet er sich ab und geht beiseite; wenn ihn aber ein Ubel trifft, gibt er sich der Verzweiflung hin"},
  {"surah_number": 17, "verse_number": 84, "content": "Sprich: \"Ein jeder handelt gemaß seiner eigenen Art, und euer Herr weiß am besten, wer den rechten Weg geht"},
  {"surah_number": 17, "verse_number": 85, "content": "Und sie befragen dich uber die Seele. Sprich: \"Die Seele ist eine Angelegenheit meines Herrn; und euch ist vom Wissen nur wenig gegeben"},
  {"surah_number": 17, "verse_number": 86, "content": "Und wenn Wir es wollten, konnten Wir gewiß das wieder fortnehmen, was Wir dir offenbart haben; du fandest dann fur dich in dieser Sache keinen Beschutzer Uns gegenuber"},
  {"surah_number": 17, "verse_number": 87, "content": "außer der Barmherzigkeit deines Herrn. Wahrlich, Seine Gnade gegen dich ist groß"},
  {"surah_number": 17, "verse_number": 88, "content": "Sprich: \"Wenn sich auch die Menschen und die Ginn vereinigten, um diesem Quran etwas Gleiches hervorzubringen, brachten sie doch nichts Gleiches hervor, selbst wenn sie einander beistunden"},
  {"surah_number": 17, "verse_number": 89, "content": "Und wahrlich, Wir haben fur die Menschen in diesem Quran allerlei abgewandelte Beispiele angefuhrt; aber die meisten Menschen wollen nichts anderes als unglaubig zu sein"},
  {"surah_number": 17, "verse_number": 90, "content": "Und sie sagen: \"Wir werden dir nimmermehr glauben, bis du uns einen Quell aus der Erde hervorsprudeln laßt"},
  {"surah_number": 17, "verse_number": 91, "content": "oder (bis) du einen Garten mit Dattelpalmen und Trauben hast und mittendrin Bache in reichlicher Weise hervorsprudeln laßt"},
  {"surah_number": 17, "verse_number": 92, "content": "oder (bis) du den Himmel uber uns in Stucken einsturzen laßt, wie du es behauptest, oder (bis du) Allah und die Engel vor unser Angesicht bringst"},
  {
    "surah_number": 17,
    "verse_number": 93,
    "content": "oder (bis) du ein prunkvolles Haus besitzt oder zum Himmel aufsteigst; und wir werden nicht eher an deinen Aufstieg glauben, bis du uns ein Buch hinabsendest, das wir lesen konnen.\" Sprich: \"Preis sei meinem Herrn! Bin ich denn etwas anderes als ein Mensch, ein Gesandter"
  },
  {"surah_number": 17, "verse_number": 94, "content": "Und nichts hat die Menschen abgehalten zu glauben, als die Fuhrung zu ihnen kam; doch sie sagten nur: \"Hat Allah einen Menschen als Gesandten geschickt"},
  {"surah_number": 17, "verse_number": 95, "content": "Sprich: \"Waren auf Erden friedlich und in Ruhe wandelnde Engel gewesen, hatten Wir ihnen gewiß einen Engel vom Himmel als Gesandten geschickt"},
  {"surah_number": 17, "verse_number": 96, "content": "Sprich: \"Allah genugt als Zeuge gegen mich und euch; wahrlich, Er weiß und sieht alles von Seinen Dienern"},
  {
    "surah_number": 17,
    "verse_number": 97,
    "content":
        "Und der, den Allah leitet, ist der Rechtgeleitete; die aber, die Er zu Irrenden erklart - fur die wirst du keine Helfer finden, außer Ihm. Und Wir werden sie am Tage der Auferstehung versammeln, (und sie werden) auf ihren Angesichtern (liegen), blind, stumm und taub. Ihre Herberge wird Gahannam sein; sooft (ihr Feuer) nachlaßt, um so starker fachen Wir die Flamme an"
  },
  {"surah_number": 17, "verse_number": 98, "content": "Das ist ihr Lohn, weil sie Unsere Zeichen verwarfen und sagten: \"Wie? Wenn wir zu Gebein und Staub geworden sind, sollen wir wirklich zu einer neuen Schopfung auferweckt werden"},
  {
    "surah_number": 17,
    "verse_number": 99,
    "content": "Haben sie nicht gesehen, daß Allah, Der die Himmel und die Erde erschuf, imstande ist, ihresgleichen zu schaffen? Und Er hat eine Frist fur sie bestimmt, uber die kein Zweifel herrscht. Allein die Frevler verwerfen alles, nur nicht den Unglauben"
  },
  {"surah_number": 17, "verse_number": 100, "content": "Sprich: \"Besaßet ihr die Schatze der Barmherzigkeit meines Herrn, wahrlich, ihr wurdet (sie) aus Furcht vor dem Ausgeben (fur) euch zuruckbehalten; denn der Mensch ist geizig"},
  {"surah_number": 17, "verse_number": 101, "content": "Und wahrlich, Wir hatten Moses neun offenbare Zeichen gegeben. Frage nur die Kinder Israels. Als er zu ihnen kam, sprach Pharao zu ihm: \"lch halte dich, o Moses, zweifellos fur ein Opfer des Zaubers"},
  {"surah_number": 17, "verse_number": 102, "content": "Er sprach: \"Du weißt recht wohl, daß kein anderer als der Herr der Himmel und der Erde diese (Zeichen) als Zeugnisse herabgesandt hat; und ich halte dich, o Pharao, zweifellos fur ein Opfer des Unheils"},
  {"surah_number": 17, "verse_number": 103, "content": "Da beschloß er, sie aus dem Lande zu vertreiben; doch Wir ertrankten ihn und diejenigen, die mit ihm waren, allesamt"},
  {"surah_number": 17, "verse_number": 104, "content": "Und Wir sprachen nach ihm zu den Kindern Israels: \"Wohnt in dem Lande; und wenn die Zeit der zweiten Verheißung kommt, dann werden Wir euch als eine gesammelte Schar herbeibringen"},
  {"surah_number": 17, "verse_number": 105, "content": "Und mit der Wahrheit haben Wir (den Quran) hinabgesandt, und mit der Wahrheit kam er hernieder. Und dich entsandten Wir nur als Bringer froher Botschaft und Warner"},
  {"surah_number": 17, "verse_number": 106, "content": "Und Wir haben den Quran in Abschnitten offenbart, damit du ihn den Menschen in Ruhe verlesen mogest, und Wir sandten ihn nach und nach hinab"},
  {"surah_number": 17, "verse_number": 107, "content": "Sprich: \"Ob ihr an ihn glaubt oder nicht glaubt, wahrlich, jene, denen zuvor das Wissen gegeben wurde, fallen, wenn er ihnen verlesen wird, anbetend auf ihr Angesicht nieder"},
  {"surah_number": 17, "verse_number": 108, "content": "und sagen: \"Preis sei unserem Herrn! Siehe, die Verheißung unseres Herrn wird sich wahrlich erfullen"},
  {"surah_number": 17, "verse_number": 109, "content": "Und weinend fallen sie anbetend auf ihr Angesicht nieder, und ihre Demut nimmt zu"},
  {"surah_number": 17, "verse_number": 110, "content": "Sprich: \"Ruft Allah an oder ruft den Barmherzigen an - bei welchem (Namen) ihr (Ihn) immer anruft, Ihm stehen die Schonsten Namen zu.\" Und sprich dein Gebet nicht zu laut, und flustere es auch nicht zu leise, sondern suche einen Mittelweg"},
  {"surah_number": 17, "verse_number": 111, "content": "Und sprich: \" Alles Lob gebuhrt Allah, Der Sich keinen Sohn genommen hat und niemanden in der Herrschaft neben Sich noch sonst einen Gehilfen aus Ohnmacht hat.\" Und ruhme Seine Große in gebuhrender Weise"},
  {"surah_number": 18, "verse_number": 1, "content": "Alles Lob gebuhrt Allah, Der zu Seinem Diener das Buch herabsandte und nichts Krummes darein legte"},
  {"surah_number": 18, "verse_number": 2, "content": "(Es ist) frei von Widerspruchen, damit es Seine strenge Strafe androhe und den Glaubigen, die gute Werke tun, die frohe Botschaft bringe, auf daß ihnen ein schoner Lohn zuteil werde"},
  {"surah_number": 18, "verse_number": 3, "content": "worin (im Paradies) sie auf ewig weilen werden"},
  {"surah_number": 18, "verse_number": 4, "content": "(und) damit es jene warne, die da sa gen: \"Allah hat Sich einen Sohn genommen"},
  {"surah_number": 18, "verse_number": 5, "content": "Sie haben keinerlei Kenntnis davon, noch hatten sie ihre Vater. Ungeheuerlich ist das Wort, das aus ihrem Munde kommt. Sie sprechen nichts als Luge"},
  {"surah_number": 18, "verse_number": 6, "content": "So wirst du dich vielleicht noch aus Kummer uber sie zu Tode gramen, wenn sie dieser Rede keinen Glauben schenken"},
  {"surah_number": 18, "verse_number": 7, "content": "Wahrlich, Wir machten alles, was auf der Erde ist, zu einem Schmuck fur sie, auf daß Wir sie pruften, wer unter ihnen der Beste im Wirken sei"},
  {"surah_number": 18, "verse_number": 8, "content": "Und gewiß, Wir werden alles, was auf ihr ist, in durren Wustenstaub verwandeln"},
  {"surah_number": 18, "verse_number": 9, "content": "Meinst du wohl, die Gefahrten in der Hohle und Ar-Raqim seien ein Wunder unter Unseren Zeichen"},
  {"surah_number": 18, "verse_number": 10, "content": "Da nahmen die jungen Manner in der Hohle Zuflucht und sagten: \"Unser Herr, gewahre uns Deine Barmherzigkeit und bereite uns einen Weg fur unsere Sache"},
  {"surah_number": 18, "verse_number": 11, "content": "Sodann versiegelten Wir in der Hohle ihre Ohren fur eine Anzahl von Jahren"},
  {"surah_number": 18, "verse_number": 12, "content": "Dann erweckten Wir sie, damit Wir erfuhren, welche von den beiden Scharen die Zeit ihres Verweilens am besten berechnet habe"},
  {"surah_number": 18, "verse_number": 13, "content": "Wir wollen dir ihre Geschichte wahrheitsgemaß berichten: Sie waren junge Manner, die an ihren Herrn glaubten, und Wir ließen ihnen zunehmend Rechtleitung zukommen"},
  {"surah_number": 18, "verse_number": 14, "content": "Und Wir starkten ihre Herzen, als sie aufstanden und sagten: \"Unser Herr ist der Herr der Himmel und der Erde. Nie werden wir einen (anderen) Gott außer Ihm anrufen; sonst wurden wir ja etwas Unsinniges aussprechen"},
  {"surah_number": 18, "verse_number": 15, "content": "Dieses unser Volk hat Gotter statt Seiner angenommen. Warum bringen sie dann keinen klaren Beweis dafur? Und wer verubt einen großeren Frevel, als der, der eine Luge gegen Allah erdichtet"},
  {"surah_number": 18, "verse_number": 16, "content": "Und wenn ihr euch von ihnen und von dem, was sie statt Allah anbeten, zuruckzieht, so sucht Zuflucht in der Hohle; euer Herr wird Seine Barmherzigkeit uber euch breiten und euch einen trostlichen Ausweg aus eurer Lage weisen"},
  {
    "surah_number": 18,
    "verse_number": 17,
    "content":
        "Und hattest du sehen konnen, wie die Sonne, als sie aufging, sich von ihrer Hohle nach rechts wegneigte, und als sie unterging, sich von ihnen nach links abwandte; und (wie) sie in einem Hohlraum mitten dazwischen waren. Das gehort zu den Zeichen Allahs. Wen Allah leitet, der ist rechtgeleitet; doch wen Er irregehen laßt, fur den wirst du keinen Helfer finden, der ihn fuhrt"
  },
  {
    "surah_number": 18,
    "verse_number": 18,
    "content":
        "Du konntest sie fur wach halten, aber sie schlafen; und Wir ließen sie sich auf die rechte Seite und auf die linke Seite drehen, wahrend ihr Hund seine Vor derpfoten auf der Schwelle ausstreckte. Hattest du sie so erblickt, hattest du gewiß vor ihnen die Flucht ergriffen, und es hatte dir vor ihnen gegraut"
  },
  {
    "surah_number": 18,
    "verse_number": 19,
    "content":
        "Und so erweckten Wir sie, damit sie einander befragen konnten. Ein Sprecher von ihnen sprach: \"Wie lange habt ihr verweilt?\" Sie sagten: \"Wir verweilten einen Tag oder den Teil eines Tages.\" (Andere) sagten: \"Euer Herr kennt die (Zeit), die ihr verbracht habt, am besten. Nun entsendet einen von euch mit dieser eurer Silbermunze zur Stadt; und er soll sehen, wer von ihren (Bewohnern) die reinste Speise hat, und soll euch davon einen Vorrat bringen. Er muß aber freundlich sein und soll ja nichts uber euch verlauten lassen"
  },
  {"surah_number": 18, "verse_number": 20, "content": "denn wenn sie (etwas) von euch erfahren wurden, wurden sie euch steinigen oder euch zu ihrem Glauben zuruckbringen, und ihr wurdet dann nimmermehr erfolgreich sein"},
  {
    "surah_number": 18,
    "verse_number": 21,
    "content":
        "Und so ließen Wir sie (die Leute) sie finden, damit sie erkennen mogen, daß Allahs Verheißung wahr ist und daß uber die Stunde kein Zweifel herrscht. Und da stritten sie (die Leute) untereinander uber sie und sagten: \"Errichtet uber ihnen einen Bau.\" Ihr Herr kennt sie am besten. Jene, deren Ansicht siegte, sagten: \"Wir wollen unbedingt eine Moschee uber ihnen errichten"
  },
  {
    "surah_number": 18,
    "verse_number": 22,
    "content":
        "(Manche) werden sagen: \"(Sie waren) drei; ihr vierter war ihr Hund\", und (andere) werden sagen: \"(Sie waren) zu funft, ihr sechster war ihr Hund\", indem sie in Unkenntnis herumraten, und (andere) sagen: \"(Es waren) sieben; ihr achter war ihr Hund.\" Sprich: \"Mein Herr kennt ihre Zahl am besten. Niemand weiß sie, bis auf einige wenige.\" So streite nicht uber sie, es sei denn, (du hattest) einen klaren Beweis, und suche keine Kunde uber sie bei irgendeinem von ihnen (zu erlangen)"
  },
  {"surah_number": 18, "verse_number": 23, "content": "Und sprich nie von einer Sache: \"lch werde es morgen tun"},
  {"surah_number": 18, "verse_number": 24, "content": "es sei denn (du fugst hinzu): \"So Allah will\". Und gedenke deines Herrn, wenn du dies vergessen hast, und sprich: \"lch hoffe, mein Herr wird mich noch naher als diesmal zum rechten Wege fuhren"},
  {"surah_number": 18, "verse_number": 25, "content": "Und sie blieben dreihundert Jahre lang in ihrer Hohle, und neun wurden hinzugefugt"},
  {"surah_number": 18, "verse_number": 26, "content": "Sprich: \"Allah weiß am besten wie lange sie verweilten.\" Ihm gehoren das Verborgene der Himmel und der Erde. Wie sehend ist Er! Und wie horend! Sie haben keinen Helfer außer Ihm, und Er teilt Seine Befehlsgewalt mit keinem"},
  {"surah_number": 18, "verse_number": 27, "content": "Und verlies, was dir von dem Buche deines Herrn offenbart wurde. Da ist keiner, der Seine Worte verandern konnte, und du wirst außer bei Ihm keine Zuflucht finden"},
  {
    "surah_number": 18,
    "verse_number": 28,
    "content":
        "Und geduldige dich zusammen mit denjenigen, die ihren Herrn morgens und abends anrufen im Trachten nach Seinem Wohlgefallen; und laß deine Blicke nicht uber sie hinauswandern, indem du nach dem Schmuck des irdischen Lebens trachtest; und gehorche nicht dem, dessen Herz Wir achtlos fur die Erinnerung an Uns machten, (und gehorche nicht dem,) der seinen Gelusten folgt und kein Maß und Ziel kennt"
  },
  {
    "surah_number": 18,
    "verse_number": 29,
    "content":
        "Und sprich: \"Es ist die Wahrheit von eurem Herrn: darum laß den glaubig sein, der will, und den unglaubig sein, der will. Siehe, Wir haben fur die Frevler ein Feuer bereitet, dessen Zeltdecke sie umschließen wird. Und wenn sie um Hilfe schreien, so wird ihnen mit Wasser gleich geschmolzenem Metall, das die Gesichter verbrennt, geholfen werden. Wie schrecklich ist der Trank, und wie schlimm ist die Raststatte"
  },
  {"surah_number": 18, "verse_number": 30, "content": "Wahrlich, die da glauben und gute Werke tun wahrlich, Wir lassen den Lohn derjenigen, die gute Werke tun, nicht verloren gehen"},
  {
    "surah_number": 18,
    "verse_number": 31,
    "content":
        "Sie sind es, denen die Garten von Eden, durch welche Bache fließen, zuteil werden. Darin werden sie mit Armspangen aus Gold geschmuckt und in grune Gewander aus feiner Seide und Brokat gekleidet sein, und darin lehnen (sie) auf erhohten Sitzen. Wie herrlich ist der Lohn und wie schon ist die Raststatte"
  },
  {"surah_number": 18, "verse_number": 32, "content": "Und stelle ihnen das Gleichnis von zwei Mannern vor: fur den einen von ihnen schufen Wir zwei Rebgarten und umgaben sie mit Dattelpalmen, und dazwischen legten Wir Kornfelder an"},
  {"surah_number": 18, "verse_number": 33, "content": "Beide brachten ihre Fruchte hervor und versagten in nichts. Und in ihrer Mitte ließen Wir einen Bach hervorsprudeln"},
  {"surah_number": 18, "verse_number": 34, "content": "Und (der Garten) trug fur ihn Fruchte. Er sprach in herausforderndem Ton zu seinem Gefahrten: \"lch bin reicher als du an Besitz und machtiger an Gefolgschaft"},
  {"surah_number": 18, "verse_number": 35, "content": "Und er betrat seinen Garten, wahrend er sundig gegen sich selbst war. Er sagte: \"lch kann mir nicht vorstellen, daß dieser je zugrunde gehen wird"},
  {"surah_number": 18, "verse_number": 36, "content": "noch glaube ich, daß die Stunde heraufkommen wird. Selbst wenn ich zu meinem Herrn zuruckgebracht werde, so werde ich ganz gewiß einen besseren Aufenthalt als diesen finden"},
  {"surah_number": 18, "verse_number": 37, "content": "Sein Gefahrte sagte zu ihm, indem er sich mit ihm auseinandersetzte: \"Glaubst du denn nicht an Ihn, Der dich aus Erde erschaffen hat, dann aus einem Samentropfen, (und) dich dann zu einem vollkommenen Mann bildete"},
  {"surah_number": 18, "verse_number": 38, "content": "Was jedoch mich betrifft Allah ist mein Herr, und nie will ich meinem Herrn etwas anders zur Seite stellen"},
  {"surah_number": 18, "verse_number": 39, "content": "Warum hast du nicht damals, als du deinen Garten betratest, gesagt: \"Wie Allah es will (, so geschehe es); es gibt keine Macht, außer bei Allah\"; wenn du mich auch geringer als dich selbst an Besitz und Nachkommenschaft siehst"},
  {"surah_number": 18, "verse_number": 40, "content": "So wird mein Herr mir vielleicht (etwas) Besseres als deinen Garten geben und wird auf ihn ein Strafgericht vom Himmel niedersenden, so daß er zu odem Boden wird"},
  {"surah_number": 18, "verse_number": 41, "content": "Oder sein Wasser versiegt so tief im Boden, daß du nimmer imstande sein wirst, es herauszuholen"},
  {"surah_number": 18, "verse_number": 42, "content": "Da wurde seine Frucht verwustet, und er begann die Hande zu ringen wegen alldem, was er fur den (Garten), dessen Bau zerfallen dalag, ausgegeben hatte. Er sprach: \"Hatte ich doch meinem Herrn niemanden zur Seite gestellt"},
  {"surah_number": 18, "verse_number": 43, "content": "Und er hatte keine Schar, die ihm gegen Allah zu helfen vermochte, und er konnte sich selbst nicht wehren"},
  {"surah_number": 18, "verse_number": 44, "content": "Insofern gibt es Schutz nur bei Allah, dem Wahren. Er ist der Beste im Belohnen und der Beste, was den Ausgang (der Dinge) anbelangt"},
  {
    "surah_number": 18,
    "verse_number": 45,
    "content": "Und prage ihnen das Gleichnis vom irdischen Leben: Es ist wie das Wasser, das Wir vom Himmel herniedersenden, mit dem die Pflanzen der Erde sich sattigen, und welche dann durre Spreu werden, die der Wind verweht. Und Allah hat Macht uber alle Dinge"
  },
  {"surah_number": 18, "verse_number": 46, "content": "Besitztum und Kinder sind Schmuck des irdischen Lebens. Die bleibenden guten Werke aber sind lohnender bei deinem Herrn und hoffnungsvoller"},
  {"surah_number": 18, "verse_number": 47, "content": "Und am Tage, da Wir die Berge vergehen lassen werden, wirst du die Erde kahl sehen, und Wir werden sie (die Volker der Erde) versammeln und werden keinen von ihnen zurucklassen"},
  {"surah_number": 18, "verse_number": 48, "content": "Und sie werden in Reihen vor deinen Herrn gefuhrt: \"Nun seid ihr zu Uns gekommen, so wie Wir euch erstmals erschufen. Ihr aber dachtet, Wir wurden euch nie einen Termin der Erfullung setzen"},
  {
    "surah_number": 18,
    "verse_number": 49,
    "content":
        "Und das Buch wird (ihnen) vorgelegt, und du wirst die Schuldigen in Angsten wegen dem sehen, was darin ist; und sie werden sagen: \"O wehe uns! Was fur ein Buch ist das! Es laßt nichts aus, ob klein oder groß, sondern halt alles aufgezeichnet.\" Und sie werden all das, was sie getan haben, gegenwartig finden; und dein Herr tut keinem Unrecht"
  },
  {
    "surah_number": 18,
    "verse_number": 50,
    "content":
        "Und da sprachen Wir zu den Engeln: \"Werft euch vor Adam nieder\" und sie warfen sich nieder, außer Iblis. Er war einer der Ginn, so war er ungehorsam gegen den Befehl seines Herrn. Wollt ihr nun ihn und seine Nachkommenschaft statt Meiner zu Beschutzern nehmen, wo sie doch eure Feinde sind? Schlimm ist dieser Tausch fur die Frevler"
  },
  {"surah_number": 18, "verse_number": 51, "content": "Ich nahm sie nicht zu Zeugen, weder bei der Schopfung der Himmel und der Erde noch bei ihrer eigenen ja, Ich nehme die Verfuhrer nie zum Beistand"},
  {"surah_number": 18, "verse_number": 52, "content": "Und am Tage, da wird Er sprechen: \"Ruft die herbei, von denen ihr vorgabt, sie seien Meine Teilhaber.\" Dann werden sie sie rufen, doch sie werden ihnen nicht antworten; und Wir werden einen Abgrund zwischen ihnen auftun"},
  {"surah_number": 18, "verse_number": 53, "content": "Und die Schuldigen werden das Feuer sehen und ahnen, daß sie hineinsturzen werden; und sie werden kein Entrinnen daraus finden"},
  {"surah_number": 18, "verse_number": 54, "content": "Wahrlich, Wir haben in diesem Quran den Menschen allerlei Gleichnisse erlautert, doch von allen Geschopfen ist der Mensch am streitsuchtigsten"},
  {
    "surah_number": 18,
    "verse_number": 55,
    "content": "Und nichts hinderte die Menschen daran zu glauben, als die Rechtleitung zu ihnen kam, und ihren Herrn um Verzeihung zu bitten, (es sei) denn, (sie warteten) bis das Beispiel(hafte Schicksal) der Fruheren uber sie kame oder (ihnen) die Strafe offen vor Augen gehalten wurde"
  },
  {
    "surah_number": 18,
    "verse_number": 56,
    "content": "Und Wir schickten die Gesandten ja nur als Bringer froher Botschaft und als Warner. Die aber, die unglaubig sind, streiten in Falschheit, um dadurch die Wahrheit zu widerlegen. Und sie verspotten Meine Zeichen und das, womit sie gewarnt werden"
  },
  {
    "surah_number": 18,
    "verse_number": 57,
    "content":
        "Und wer ist ungerechter als der, der an die Zeichen seines Herrn gemahnt wurde, sich aber von ihnen abwandte und vergaß, was seine Hande vorausgeschickt hatten? Wahrlich, Wir haben Schleier uber ihre Herzen gelegt, so daß sie es nicht begreifen, und Taubheit in ihre Ohren. Und selbst wenn du sie zum rechten Weg rufst, werden sie nie den rechten Weg einschlagen"
  },
  {
    "surah_number": 18,
    "verse_number": 58,
    "content": "Und dein Herr ist der Vergebungsreiche, voll der Barmherzigkeit. Wollte Er sie zur Rechenschaft ziehen fur das, was sie begehen, dann wurde Er gewiß ihre Bestrafung beschleunigen. Allein sie haben eine festgesetzte Frist, vor der sie keine Zuflucht finden werden"
  },
  {"surah_number": 18, "verse_number": 59, "content": "Und diese Stadte! Wir zerstorten sie, als sie Frevel begingen. Und Wir setzten eine Frist fur ihre Zerstorung"},
  {"surah_number": 18, "verse_number": 60, "content": "Und Moses sagte zu sei nem jungen Diener: \"lch will nicht eher rasten, als bis ich den Zusammenfluß der beiden Meere erreicht habe, und sollte ich jahrhundertelang wandern"},
  {"surah_number": 18, "verse_number": 61, "content": "Doch als sie den Zusammenfluß der beiden (Meere) erreicht hatten, da vergaßen sie ihren Fisch; und dieser nahm seinen Weg und schwamm ins Meer hinaus"},
  {"surah_number": 18, "verse_number": 62, "content": "Und als sie weitergegangen waren, sagte er zu seinem jungen Diener: \"Bring uns unsere Speise. Wir haben wahrlich auf dieser unserer Reise große Anstrengungen auf uns genommen"},
  {"surah_number": 18, "verse_number": 63, "content": "Er sagte: \"Hast du nicht gesehen, daß sich der Fisch da auf wundersame Weise ins Meer begab, als wir auf dem Felsen rasteten und ich ihn vergaß und kein (anderer) als Satan ließ mich vergessen, ihn zu erwahnen"},
  {"surah_number": 18, "verse_number": 64, "content": "Er sagte: \"Das ist es was wir wollten.\" Da kehrten sie beide um und schritten auf ihren Spuren zuruck"},
  {"surah_number": 18, "verse_number": 65, "content": "Dann fanden sie einen Unserer Diener, dem Wir Unsere Barmherzigkeit verliehen und den Wir Unser Wissen gelehrt hatten"},
  {"surah_number": 18, "verse_number": 66, "content": "Moses sagte zu ihm: \"Darf ich dir folgen, auf daß du mich uber das rechte Handeln belehrest, wie du gelehrt worden bist"},
  {"surah_number": 18, "verse_number": 67, "content": "Er sagte: \"Du vermagst nimmer bei mir in Geduld auszuharren"},
  {"surah_number": 18, "verse_number": 68, "content": "Und wie konntest du bei Dingen geduldig sein, von denen dir keine Kunde gegeben worden ist"},
  {"surah_number": 18, "verse_number": 69, "content": "Er sagte: \"Du wirst mich, so Allah will, geduldig finden, und ich werde gegen keinen deiner Befehle ungehorsam sein"},
  {"surah_number": 18, "verse_number": 70, "content": "Er sagte: \"Nun gut. Wenn du mir folgen willst, so frage mich nach nichts, bis ich es dir von selbst erklare"},
  {"surah_number": 18, "verse_number": 71, "content": "So machten sich beide auf den Weg, bis sie in ein Schiff stiegen, in das er ein Loch schlug. Er (Moses) sagte: \"Schlugst du ein Loch hinein, um seine Mannschaft zu ertranken? Wahrlich, du hast etwas Schreckliches be gangen"},
  {"surah_number": 18, "verse_number": 72, "content": "Er sagte: \"Habe ich nicht gesagt, du wurdest es nimmer fertigbringen, bei mir in Geduld auszuharren"},
  {"surah_number": 18, "verse_number": 73, "content": "Er (Moses) sagte: \"Stelle mich nicht meines Vergessens wegen zur Rede, und sei deswegen nicht streng mit mir"},
  {"surah_number": 18, "verse_number": 74, "content": "So zogen sie weiter, bis sie einen Jungling trafen, den er erschlug. Er (Moses) sagte: \"Hast du einen unschuldigen Menschen erschlagen, ohne daß (er) einen anderen (erschlagen hatte)? Wahrlich, du hast etwas Verabscheuliches getan"},
  {"surah_number": 18, "verse_number": 75, "content": "Er sagte: \"Habe ich dir nicht gesagt, du wurdest es nimmer fertigbringen, bei mir in Geduld auszuharren"},
  {"surah_number": 18, "verse_number": 76, "content": "Er (Moses) sagte: \"Wenn ich dich nochmal nach etwas frage, so begleite mich nicht weiter; von mir aus warst du dann entschuldigt"},
  {
    "surah_number": 18,
    "verse_number": 77,
    "content":
        "So zogen sie weiter, bis sie bei den Bewohnern einer Stadt ankamen und von ihnen Gastfreundschaft erbaten; diese aber weigerten sich, sie zu bewirten. Nun fanden sie dort eine Mauer, die einzusturzen drohte, und er richtete sie auf. Er (Moses) sagte: \"Wenn du es gewollt hattest, hattest du einen Arbeitslohn dafur erhalten konnen"
  },
  {"surah_number": 18, "verse_number": 78, "content": "Er sagte: \"Dies fuhrt zur Trennung zwischen mir und dir. Doch will ich dir die Bedeutung von dem sagen, was du nicht in Geduld zu ertragen vermochtest"},
  {"surah_number": 18, "verse_number": 79, "content": "Was das Schiff anbelangt, so gehorte es armen Leuten, die auf dem Meer arbeiteten, und ich wollte es beschadigen; denn hinter ihnen war ein Konig, der jedes Schiff beschlagnahmte"},
  {"surah_number": 18, "verse_number": 80, "content": "Und was den Jungling anbelangt, so waren seine Eltern Glaubige, und wir furchteten, er konnte Schmach durch Widersetzlichkeit und Unglauben uber sie bringen"},
  {"surah_number": 18, "verse_number": 81, "content": "So wollten wir, daß ihr Herr ihnen zum Tausch (ein Kind) gebe, das redlicher als dieses und anhanglicher ware"},
  {
    "surah_number": 18,
    "verse_number": 82,
    "content":
        "Und was nun die Mauer anbelangt, so gehorte sie zwei Waisenknaben in der Stadt, und darunter lag ein Schatz fur sie (verborgen), und ihr Vater war ein rechtschaffener Mann gewesen; so wunschte dein Herr, daß sie ihre Volljahrigkeit erreichen und ihren Schatz heben mogen als eine Barmherzigkeit deines Herrn; und ich tat es nicht aus eigenem Ermessen. Das ist die Bedeutung dessen, was du nicht in Geduld zu ertragen vermochtest"
  },
  {"surah_number": 18, "verse_number": 83, "content": "Und sie fragen dich nach Du-l-Qarnain. Sprich: \"lch will euch etwas daruber berichten"},
  {"surah_number": 18, "verse_number": 84, "content": "Wir haben ihm Macht auf Erden und die Mittel zu allem gegeben"},
  {"surah_number": 18, "verse_number": 85, "content": "So folgte er dem gegebenen Weg"},
  {"surah_number": 18, "verse_number": 86, "content": "bis er den Ort, an dem die Sonne untergeht, erreichte; er fand sie in einem Quell von schlammigem Wasser untergehen, und dort fand er ein Volk. Wir sprachen: \"O Du- l-Qarnain, entweder strafe sie oder behandle sie mit Gute"},
  {"surah_number": 18, "verse_number": 87, "content": "Er sagte: \"Den, der da frevelt, werden wir sicherlich bestrafen; dann soll er zu seinem Herrn zuruckgebracht werden, und Er wird ihn in graßlicher Weise bestrafen"},
  {"surah_number": 18, "verse_number": 88, "content": "Dem aber, der glaubig ist und Gutes tut, wird herrlicher Lohn zuteil werden; und wir werden zu ihm in angenehmer Weise uber unsere Angelegenheiten sprechen"},
  {"surah_number": 18, "verse_number": 89, "content": "Darauf folgte er dem gegebenen Weg"},
  {"surah_number": 18, "verse_number": 90, "content": "bis er den Ort, an dem die Sonne aufgeht, erreichte; er fand sie uber einem Volk aufgehen, dem Wir keinen Schutz gegen sie gegeben, hatten"},
  {"surah_number": 18, "verse_number": 91, "content": "Und so war es; und Wir umfaßten mit Wissen, wie es um ihn bestellt war"},
  {"surah_number": 18, "verse_number": 92, "content": "Hierauf folgte er dem gegebenen Weg"},
  {"surah_number": 18, "verse_number": 93, "content": "bis er zwischen die beiden Walle gelangte; er fand hinter diesen ein Volk, das kaum eine Sprache verstehen konnte"},
  {"surah_number": 18, "verse_number": 94, "content": "Sie sagten: \"O Du-I- Qarnain, Gog und Magog stiften Unheil im Lande; sollen wir dir nun Tribut zahlen unter der Bedingung, daß du zwischen uns und ihnen einen Wall errichtest"},
  {"surah_number": 18, "verse_number": 95, "content": "Er sagte: \"Die Macht, die mein Herr mir gegeben hat, ist besser. So helft mir denn mit all eurer Kraft, damit ich zwischen euch und ihnen einen Damm errichten kann"},
  {"surah_number": 18, "verse_number": 96, "content": "Bringt mir Eisenstucke.\" Als er die Kluft zwischen den beiden Bollwerken ausgefullt hatte, sagte er: \"Blast!\" Als er es (das Eisen) feurig gemacht hatte, sagte er: \"Bringt mir geschmolzenes Kupfer, ich will es daruber gießen"},
  {"surah_number": 18, "verse_number": 97, "content": "So vermochten sie es nicht sie (die Damme) zu erklimmen, noch konnten sie sie durchbrechen"},
  {"surah_number": 18, "verse_number": 98, "content": "Er sagte: \"Das ist die Gnade meines Herrn; doch wenn die Verheißung meines Herrn in Erfullung geht, wird Er sie zu Schutt zerfallen lassen; und die Verheißung meines Herrn ist wahr"},
  {"surah_number": 18, "verse_number": 99, "content": "An jenem Tage werden Wir die einen von ihnen wie Wogen gegen die anderen ansturmen lassen, und es wird in den Sur gestoßen. Dann werden Wir sie allzumal (vor Uns) versammeln"},
  {"surah_number": 18, "verse_number": 100, "content": "Und den Unglaubigen stellen Wir an jenem Tage Gahannam in deutlicher Weise vor Augen"},
  {"surah_number": 18, "verse_number": 101, "content": "ihnen, deren Augen vor Meiner Mahnung verhullt waren und die nicht einmal horen konnten"},
  {"surah_number": 18, "verse_number": 102, "content": "Meinen die Unglaubigen etwa, sie konnten meine Diener an Meiner Statt zu Beschutzern nehmen? Wahrlich, Wir haben den Unglaubigen Gahannam als Hort bereitet"},
  {"surah_number": 18, "verse_number": 103, "content": "Sprich: \"Sollen Wir euch die nennen, die bezuglich ihrer Werke die großten Verlierer sind"},
  {"surah_number": 18, "verse_number": 104, "content": "Das sind die, deren Eifer im irdischen Leben in die Irre ging, wahrend sie meinen, sie taten gar etwas Gutes"},
  {"surah_number": 18, "verse_number": 105, "content": "Das sind jene, die die Zeichen ihres Herrn und die Begegnung mit Ihm leugnen. Darum sind ihre Werke nichtig, und am Tage der Auferstehung werden Wir ihnen kein Gewicht beimessen"},
  {"surah_number": 18, "verse_number": 106, "content": "Dies ist ihr Lohn Gahannam, weil sie unglaubig waren und mit Meinen Zeichen und mit Meinen Gesandten Spott trieben"},
  {"surah_number": 18, "verse_number": 107, "content": "Wahrlich, jene, die da glauben und gute Werke tun ihnen wird das Paradies von Al-Firdaus ein Hort sein"},
  {"surah_number": 18, "verse_number": 108, "content": "in dem sie auf ewig verweilen werden; von dort werden sie nicht weggehen wollen"},
  {"surah_number": 18, "verse_number": 109, "content": "Sprich: \"Ware das Meer Tinte fur die Worte meines Herrn, wahrlich, das Meer wurde versiegen, ehe die Worte meines Herrn zu Ende gingen, auch wenn wir noch ein gleiches als Nachschub brachten"},
  {
    "surah_number": 18,
    "verse_number": 110,
    "content": "Sprich: \"lch bin nur ein Mensch wie ihr, doch mir ist offenbart worden, daß euer Gott ein Einziger Gott ist. Moge denn derjenige, der auf die Begegnung mit seinem Herrn hofft, gute Werke tun und keinen anderen einbeziehen in den Dienst an seinem Herrn"
  },
  {"surah_number": 19, "verse_number": 1, "content": "Kaf Ha Ya Ain, Sad"},
  {"surah_number": 19, "verse_number": 2, "content": "Erwahnt wird (hier) die Barmherzigkeit deines Herrn gegen Seinen Diener Zacharias"},
  {"surah_number": 19, "verse_number": 3, "content": "Als er seinen Herrn mit leisem Ruf anrief"},
  {"surah_number": 19, "verse_number": 4, "content": "sagte er: \"Mein Herr, mein Gebein ist nun schwach geworden, und die Haare meines Hauptes schimmern grau, doch niemals, mein Herr, bin ich mit meiner Bitte an Dich unglucklich gewesen"},
  {"surah_number": 19, "verse_number": 5, "content": "Nun aber furchte ich meine Verwandten, die mich uberleben werden, und meine Frau ist unfruchtbar geworden. Gewahre Du mir darum einen Nachfolger"},
  {"surah_number": 19, "verse_number": 6, "content": "auf daß er mein Erbe sei und der Erbe des Hauses Jakobs. Und mache ihn, mein Herr, (Dir) wohlgefallig"},
  {"surah_number": 19, "verse_number": 7, "content": "O Zacharias, Wir geben dir die frohe Botschaft von einem Sohn, dessen Name Yahya sein soll. Wir haben noch keinen dieses Namens geschaffen"},
  {"surah_number": 19, "verse_number": 8, "content": "Er sagte: \"Mein Herr, wie soll mir ein Sohn (geboren) werden, wo doch meine Frau unfruchtbar ist und ich schon das fortgeschrittene Alter erreicht habe"},
  {"surah_number": 19, "verse_number": 9, "content": "Er sprach: \"So ist es; dein Herr aber spricht: \"Es ist Mir ein leichtes, und Ich habe dich zuvor geschaffen, als du ein Nichts warst"},
  {"surah_number": 19, "verse_number": 10, "content": "Er sagte: \"Mein Herr, gib mir ein Zeichen.\" Er sprach: \"Dein Zeichen sei, daß du wiewohl gesund drei (Tage und) Nachte lang nicht zu den Menschen reden sollst"},
  {"surah_number": 19, "verse_number": 11, "content": "So trat er aus dem Tempel heraus vor sein Volk und gab ihm zu verstehen, daß es (Allah) am Morgen und am Abend zu preisen habe"},
  {"surah_number": 19, "verse_number": 12, "content": "O Yahya, halte das Buch kraftvoll fest.\" Und wir verliehen ihm Weisheit im Knabenalter"},
  {"surah_number": 19, "verse_number": 13, "content": "und ein liebevolles Gemut von Uns und Reinheit. Und er war fromm"},
  {"surah_number": 19, "verse_number": 14, "content": "und ehrerbietig gegen seine Eltern. Und er war nicht hochfahrend, trotzig"},
  {"surah_number": 19, "verse_number": 15, "content": "Und Friede war auf ihm an dem Tage, als er geboren wurde, und an dem Tage, als er starb, und (Friede wird auf ihm sein) an dem Tage, wenn er wieder zum Leben erweckt wird"},
  {"surah_number": 19, "verse_number": 16, "content": "Und ermahne im Buch Maria. Als sie sich von ihrer Familie nach einem ostlichen Ort zuruckzog"},
  {"surah_number": 19, "verse_number": 17, "content": "und sich vor ihr abschirmte, da sandten Wir Unseren Engel Gabriel zu ihr, und er erschien ihr in der Gestalt eines vollkommenen Menschen"},
  {"surah_number": 19, "verse_number": 18, "content": "und sie sagte: \"lch nehme meine Zuflucht vor dir bei dem Allerbarmer, (laß ab von mir,) wenn du Gottesfurcht hast"},
  {"surah_number": 19, "verse_number": 19, "content": "Er sprach: \"lch bin der Bote deines Herrn. (Er hat mich zu dir geschickt,) auf daß ich dir einen reinen Sohn beschere"},
  {"surah_number": 19, "verse_number": 20, "content": "Sie sagte: \"Wie soll mir ein Sohn (geschenkt) werden, wo mich doch kein Mann (je) beruhrt hat und ich auch keine Hure bin"},
  {"surah_number": 19, "verse_number": 21, "content": "Er sprach: \"So ist es; dein Herr aber spricht: \"Es ist Mir ein leichtes, und Wir machen ihn zu einem Zeichen fur die Menschen und zu Unserer Barmherzigkeit, und dies ist eine beschlossene Sache"},
  {"surah_number": 19, "verse_number": 22, "content": "Und so empfing sie ihn und zog sich mit ihm an einen entlegenen Ort zuruck"},
  {"surah_number": 19, "verse_number": 23, "content": "Und die Wehen der Geburt trieben sie zum Stamm einer Dattelpalme. Sie sagte: \"O ware ich doch zuvor gestorben und ware ganz und gar vergessen"},
  {"surah_number": 19, "verse_number": 24, "content": "Da rief er ihr von unten her zu: \"Sei nicht traurig. Dein Herr hat dir ein Bachlein fließen lassen"},
  {"surah_number": 19, "verse_number": 25, "content": "und schuttele den Stamm der Palme in deine Richtung, und sie wird frische reife Datteln auf dich fallen lassen"},
  {"surah_number": 19, "verse_number": 26, "content": "So iß und trink und sei frohen Mutes. Und wenn du einen Menschen siehst, dann sprich: \"lch habe dem Allerbarmer zu fasten gelobt, darum will ich heute mit keinem Menschen reden"},
  {"surah_number": 19, "verse_number": 27, "content": "Dann brachte sie ihn auf dem Arm zu den Ihren. Sie sagten: \"O Maria, du hast etwas Unerhortes getan"},
  {"surah_number": 19, "verse_number": 28, "content": "O Schwester Aarons, dein Vater war kein Bosewicht, und deine Mutter war keine Hure"},
  {"surah_number": 19, "verse_number": 29, "content": "Da zeigte sie auf ihn. Sie sagten: \"Wie sollen wir zu einem reden, der noch ein Kind in der Wiege ist"},
  {"surah_number": 19, "verse_number": 30, "content": "Er (Jesus) sprach: \"lch bin ein Diener Allahs: Er hat mir das Buch gegeben und mich zu einem Propheten gemacht"},
  {"surah_number": 19, "verse_number": 31, "content": "Und Er gab mir Seinen Segen, wo ich auch sein moge, und Er befahl mir Gebet und Zakah, solange ich lebe"},
  {"surah_number": 19, "verse_number": 32, "content": "und ehrerbietig gegen meine Mutter (zu sein); Er hat mich nicht gewalttatig und unselig gemacht"},
  {"surah_number": 19, "verse_number": 33, "content": "Und Friede war uber mir an dem Tage, als ich geboren wurde, und (Friede wird uber mir sein) an dem Tage, wenn ich sterben werde, und an dem Tage, wenn ich wieder zum Leben erweckt werde"},
  {"surah_number": 19, "verse_number": 34, "content": "Dies ist Jesus, Sohn der Maria (dies ist) eine Aussage der Wahrheit, uber die sie uneins sind"},
  {"surah_number": 19, "verse_number": 35, "content": "Es geziemt Allah nicht, Sich einen Sohn zu nehmen. Gepriesen sei Er! Wenn Er etwas beschließt, so spricht Er nur: \"Sei!\" und es ist"},
  {"surah_number": 19, "verse_number": 36, "content": "Wahrlich, Allah ist mein Herr und euer Herr. So dient Ihm! Das ist ein gerader Weg"},
  {"surah_number": 19, "verse_number": 37, "content": "Doch die Parteien wurden uneinig untereinander; wehe darum denen, die unglaubig sind; sie werden einen großen Tag erleben"},
  {"surah_number": 19, "verse_number": 38, "content": "Wie gut wird ihr Horen und Sehen an dem Tage sein, wo sie zu Uns kommen werden! Heute aber befinden sich die Frevler in offenbarem Irrtum"},
  {"surah_number": 19, "verse_number": 39, "content": "Und warne sie vor dem Tag der Verbitterung, wenn alles entschieden werden wird, wahrend sie (jetzt) noch in Sorglosigkeit leben und immer noch nicht glauben"},
  {"surah_number": 19, "verse_number": 40, "content": "Wir sind es, Die die Erde, und alle, die auf ihr sind, erben werden, und zu Uns werden sie zuruckgebracht"},
  {"surah_number": 19, "verse_number": 41, "content": "Und erwahne in diesem Buch Abraham. Er war ein Wahrhaftiger, ein Prophet"},
  {"surah_number": 19, "verse_number": 42, "content": "als er zu seinem Vater sagte: \"O mein Vater, warum verehrst du das, was nicht hort und sieht und dir nichts nutzen kann"},
  {"surah_number": 19, "verse_number": 43, "content": "O mein Vater, zu mir ist ein Wissen gekommen, das nicht zu dir kam; so folge mir, ich will dich auf den ebenen Weg leiten"},
  {"surah_number": 19, "verse_number": 44, "content": "O mein Vater, diene nicht dem Satan; denn Satan emport sich gegen den Allerbarmer"},
  {"surah_number": 19, "verse_number": 45, "content": "O mein Vater, siehe, ich furchte, es konnte dich die Strafe des Allerbarmers treffen, und dann wirst du ein Freund des Satans werden"},
  {"surah_number": 19, "verse_number": 46, "content": "Er sagte: \"Wendest du dich von meinen Gottern ab, o Abraham? Wenn du (damit) nicht aufhorst, so werde ich dich wahrlich steinigen. Verlaß mich fur lange Zeit"},
  {"surah_number": 19, "verse_number": 47, "content": "Er (Abraham) sagte: \"Friede sei auf dir! Ich will bei meinem Herrn um Vergebung fur dich bitten; denn Er ist gnadig gegen mich"},
  {"surah_number": 19, "verse_number": 48, "content": "Und ich werde mich von euch und von dem, was ihr statt Allah anruft, fernhalten; und ich will zu meinem Herrn beten; ich werde durch das Gebet zu meinem Herrn bestimmt nicht unglucklich sein"},
  {"surah_number": 19, "verse_number": 49, "content": "Als er sich nun von ihnen und von dem, was sie statt Allah verehrten, getrennt hatte, da bescherten Wir ihm Isaak und Jakob und machten beide zu Propheten"},
  {"surah_number": 19, "verse_number": 50, "content": "Und Wir verliehen ihnen Unsere Barmherzigkeit; und Wir gaben ihnen einen guten Ruf und hohes Ansehen"},
  {"surah_number": 19, "verse_number": 51, "content": "Und erwahne in diesem Buch Moses. Er war wahrlich ein Erwahlter; und er war ein Gesandter, ein Prophet"},
  {"surah_number": 19, "verse_number": 52, "content": "Wir riefen ihn von der rechten Seite des Berges und ließen ihn zu einer vertraulichen Unterredung naher treten"},
  {"surah_number": 19, "verse_number": 53, "content": "Und Wir machten ihm in Unserer Barmherzigkeit seinen Bruder Aaron zum Propheten"},
  {"surah_number": 19, "verse_number": 54, "content": "Und erwahne in diesem Buch Ismael. Er blieb wahrlich seinem Versprechen treu und war ein Gesandter, ein Prophet"},
  {"surah_number": 19, "verse_number": 55, "content": "und er pflegte seinen Angehorigen Gebet und Zakah ans Herz zu legen und war seinem Herrn wohlgefallig"},
  {"surah_number": 19, "verse_number": 56, "content": "Und ermahne in diesem Buch Idris. Er war ein Wahrhaftiger, ein Prophet"},
  {"surah_number": 19, "verse_number": 57, "content": "Wir erhoben ihn zu hohem Rang"},
  {
    "surah_number": 19,
    "verse_number": 58,
    "content":
        "sie waren jene, denen Allah Gnade erwiesen hat. (Sie entstammten) der Nachkommenschaft Adams und derer, die Wir mit Nah trugen, und der Nachkommenschaft Abrahams und Israels und derer, die Wir rechtgeleitet und erwahlt hatten. Wenn ihnen die Verse des Allerbarmers verlesen wurden, fielen sie betend und weinend nieder"
  },
  {"surah_number": 19, "verse_number": 59, "content": "Dann aber kamen nach ihnen Nachfahren, die das Gebet vernachlassigten und ihren Leidenschaften folgten. So gehen sie nun sicher dem Untergang entgegen"},
  {"surah_number": 19, "verse_number": 60, "content": "außer denen, die bereuen und glauben und rechtschaffen handeln. Diese werden ins Paradies eingehen, und sie werden kein Unrecht erleiden"},
  {"surah_number": 19, "verse_number": 61, "content": "(in den) Garten von Eden, die der Allerbarmer Seinen Dienern im Verborgenen verhieß. Wahrlich, Seine Verheißung wird in Erfullung gehen"},
  {"surah_number": 19, "verse_number": 62, "content": "Sie horen dort kein leeres Gerede, sondern (genießen) nur Frieden; und sie werden dort ihren Unterhalt morgens und abends empfangen"},
  {"surah_number": 19, "verse_number": 63, "content": "Dies ist das Paradies, das Wir jenen Unserer Diener zum Erbe geben, die gottesfurchtig sind"},
  {"surah_number": 19, "verse_number": 64, "content": "Wir (Engel) kommen nur auf den Befehl deines Herrn hernieder. Sein ist alles, was vor uns und was hinter uns und was dazwischen ist; und dein Herr ist nicht vergeßlich"},
  {"surah_number": 19, "verse_number": 65, "content": "(Er ist der) Herr der Himmel und der Erde und all dessen, was zwischen beiden liegt. So diene Ihm, und sei beharrlich in Seinem Dienst. Kennst du etwa einen, der Ihm gleich ware"},
  {"surah_number": 19, "verse_number": 66, "content": "Und der Mensch sagt: \"Wie? Wenn ich tot bin, soll ich dann wirklich (wieder) zum Leben auferstehen"},
  {"surah_number": 19, "verse_number": 67, "content": "Bedenkt der Mensch denn nicht, daß Wir ihn zuvor erschufen, als er ein Nichts war"},
  {"surah_number": 19, "verse_number": 68, "content": "Und, bei deinem Herrn, Wir werden sie ganz gewiß versammeln und auch die Satane; dann werden Wir sie auf den Knien rund um Gahannam bringen"},
  {"surah_number": 19, "verse_number": 69, "content": "Alsdann werden Wir aus jeder Partei diejenigen herausgreifen, die am trotzigsten in der Emporung gegen den Allerbarmer waren"},
  {"surah_number": 19, "verse_number": 70, "content": "Und Wir kennen die am besten, die es am meisten verdienen, darein zu gehen"},
  {"surah_number": 19, "verse_number": 71, "content": "Und keiner ist unter euch, der nicht daran vorbeikommen wird das ist eine von deinem Herrn beschlossene Fugung"},
  {"surah_number": 19, "verse_number": 72, "content": "Dann werden Wir die Gottesfurchtigen erretten, die Frevler aber werden Wir darin auf den Knien belassen"},
  {"surah_number": 19, "verse_number": 73, "content": "Und wenn ihnen Unsere deutlichen Zeichen verlesen werden, sagen die Unglaubigen zu den Glaubigen: \"Welche der beiden Gruppen ist besser gestellt und gibt eine eindrucksvollere Versammlung ab"},
  {"surah_number": 19, "verse_number": 74, "content": "Und wieviele Geschlechter vor ihnen haben Wir schon vernichtet, (die) ansehnlicher an Besitz und außerer Erscheinung (waren)"},
  {
    "surah_number": 19,
    "verse_number": 75,
    "content": "Sprich: \"Diejenigen, die sich im Irrtum befinden, erhalten langes Gewahren vom Allerbarmer, bis sie das sehen, was ihnen angedroht wurde ob es nun die Strafe ist oder die Stunde, und dann erkennen, wer in der schlechteren Lage und schwacher an Streitmacht ist"
  },
  {"surah_number": 19, "verse_number": 76, "content": "Und Allah bestarkt die, die rechtgeleitet sind, in Seiner Fuhrung. Die bleibenden guten Werke aber sind lohnender bei deinem Herrn und fruchtbarer"},
  {"surah_number": 19, "verse_number": 77, "content": "Hast du wohl den gesehen, der Unsere Zeichen leugnet und sagt: \"Ganz gewiß werde ich Vermogen und Kinder erhalten"},
  {"surah_number": 19, "verse_number": 78, "content": "Hatte er denn Zugang zum Verborgenen oder hat er vom Allerbarmer ein Versprechen entgegengenommen"},
  {"surah_number": 19, "verse_number": 79, "content": "Nein; Wir werden aufschreiben, was er sagt, und Wir werden fur ihn die Strafe verlangern"},
  {"surah_number": 19, "verse_number": 80, "content": "Und Wir werden all das von ihm erben, wovon er redet, und er wird allein zu Uns kommen"},
  {"surah_number": 19, "verse_number": 81, "content": "Und sie haben sich Gotter statt Allah genommen, auf daß sie ihnen zur Ehre gereichen"},
  {"surah_number": 19, "verse_number": 82, "content": "Nein; sie werden einst ihre Verehrung leugnen und derer Widersacher sein"},
  {"surah_number": 19, "verse_number": 83, "content": "Siehst du nicht, daß Wir die Satane auf die Unglaubigen losgelassen haben, um sie aufzureizen"},
  {"surah_number": 19, "verse_number": 84, "content": "Darum geh nicht eilig gegen sie vor; Wir zahlen ihre (Taten) genau ab"},
  {"surah_number": 19, "verse_number": 85, "content": "Am Tage, da Wir die Gottesfurchtigen als eine Abordnung vor dem Allerbarmer versammeln"},
  {"surah_number": 19, "verse_number": 86, "content": "werden Wir die Schuldigen wie eine Herde zu Gahannam treiben"},
  {"surah_number": 19, "verse_number": 87, "content": "Sie werden kein Anrecht auf Fursprache haben mit Ausnahme dessen, der vom Allerbarmer ein Versprechen empfangen hat"},
  {"surah_number": 19, "verse_number": 88, "content": "Und sie sagen: \"Der Allerbarmer hat Sich einen Sohn genommen"},
  {"surah_number": 19, "verse_number": 89, "content": "Wahrhaftig, ihr habt da etwas Ungeheuerliches begangen"},
  {"surah_number": 19, "verse_number": 90, "content": "Beinahe werden die Himmel zerreißen und die Erde auseinanderbersten und beinahe werden die Berge in Trummern zusammensturzen"},
  {"surah_number": 19, "verse_number": 91, "content": "weil sie dem Allerbarmer einen Sohn zugeschrieben haben"},
  {"surah_number": 19, "verse_number": 92, "content": "Und es geziemt dem Allerbarmer nicht, Sich einen Sohn zu nehmen"},
  {"surah_number": 19, "verse_number": 93, "content": "Da ist keiner in den Himmeln noch auf der Erde, der dem Allerbarmer anders denn als Diener sich nahen durfte"},
  {"surah_number": 19, "verse_number": 94, "content": "Wahrlich, Er hat sie grundlich erfaßt und Er hat alle genau gezahlt"},
  {"surah_number": 19, "verse_number": 95, "content": "Und jeder von ihnen wird am Tage der Auferstehung allein zu Ihm kommen"},
  {"surah_number": 19, "verse_number": 96, "content": "Diejenigen, die da glauben und gute Werke tun ihnen wird der Allerbarmer Liebe zukommen lassen"},
  {"surah_number": 19, "verse_number": 97, "content": "Darum haben Wir ihn (den Quran) (dir) in deiner Sprache leicht (verstandlich) gemacht, damit du durch ihn den Gottesfurchtigen die frohe Botschaft verkunden und die Streitsuchtigen warnen mogest"},
  {"surah_number": 19, "verse_number": 98, "content": "Und wieviele Geschlechter haben Wir vor ihnen vernichtet! Kannst du auch nur einen von ihnen spuren oder auch nur einen Laut von ihnen horen"},
  {"surah_number": 20, "verse_number": 1, "content": "Ta Ha"},
  {"surah_number": 20, "verse_number": 2, "content": "Wir haben den Quran nicht auf dich herabgesandt, um dich unglucklich zu machen"},
  {"surah_number": 20, "verse_number": 3, "content": "sondern als eine Ermahnung fur den, der (Allah) furchtet"},
  {"surah_number": 20, "verse_number": 4, "content": "(Dies ist) eine Offenbarung von Ihm, Der die Erde und die hohen Himmel erschuf"},
  {"surah_number": 20, "verse_number": 5, "content": "(Er ist) der Allerbarmer, Der uber Sein Reich majestatisch herrscht"},
  {"surah_number": 20, "verse_number": 6, "content": "Sein ist, was in den Himmeln und was auf Erden ist und was zwischen beiden und was unter dem Erdreich liegt"},
  {"surah_number": 20, "verse_number": 7, "content": "Und wenn du das Wort laut aussprichst, dann wahrlich, kennt Er das Geheime und das, was noch verborgener ist"},
  {"surah_number": 20, "verse_number": 8, "content": "Allah - es ist kein Gott außer Ihm. Ihm kommen die Schonsten Namen zu"},
  {"surah_number": 20, "verse_number": 9, "content": "Ist dir Moses' Geschichte nicht zu Ohren gekommen"},
  {"surah_number": 20, "verse_number": 10, "content": "Als er ein Feuer sah, sagte er zu seinen Angehorigen: \"Bleibt (hier), ich habe ein Feuer wahrgenommen; vielleicht kann ich euch ein Stuck Glut davon bringen oder den rechten Weg am Feuer finden"},
  {"surah_number": 20, "verse_number": 11, "content": "Und wie er naher herankam, wurde er angerufen: \"O Moses"},
  {"surah_number": 20, "verse_number": 12, "content": "Ich bin es, dein Herr. So zieh deine Schuhe aus; denn du bist im heiligen Wadi Tuwa"},
  {"surah_number": 20, "verse_number": 13, "content": "Und Ich habe dich erwahlt; so hore denn auf das, was offenbart wird"},
  {"surah_number": 20, "verse_number": 14, "content": "Wahrlich, Ich bin Allah. Es ist kein Gott außer Mir; darum diene Mir und verrichte das Gebet zu Meinem Gedenken"},
  {"surah_number": 20, "verse_number": 15, "content": "Wahrlich, die Stunde kommt bestimmt. Ich halte sie fest verborgen, auf daß jede Seele nach ihrem Bemuhen belohnt werde"},
  {"surah_number": 20, "verse_number": 16, "content": "Darum laß dich nicht von dem, der hieran nicht glaubt und seinen Gelusten folgt, davon abhalten, damit du nicht untergehest"},
  {"surah_number": 20, "verse_number": 17, "content": "Und was ist das in deiner Rechten, o Moses"},
  {"surah_number": 20, "verse_number": 18, "content": "Er sagte: \"Das ist mein Stock; ich stutze mich darauf und schlage damit Laub fur meine Schafe ab, und ich habe fur ihn noch andere Verwendungen"},
  {"surah_number": 20, "verse_number": 19, "content": "Er sprach: \"Wirf ihn hin, o Moses"},
  {"surah_number": 20, "verse_number": 20, "content": "Da warf er ihn hin, und siehe, er wurde zu einer Schlange, die umhereilte"},
  {"surah_number": 20, "verse_number": 21, "content": "Er sprach: \"Ergreife ihn und furchte dich nicht. Wir werden ihn in seinen fruheren Zustand zuruckbringen"},
  {"surah_number": 20, "verse_number": 22, "content": "Und stecke deine Hand dicht unter deinen Arm: sie wird weiß hervorkommen, ohne ein Ubel - (dies ist) noch ein weiteres Zeichen"},
  {"surah_number": 20, "verse_number": 23, "content": "auf daß Wir dir etwas von Unseren großten Zeichen zeigen"},
  {"surah_number": 20, "verse_number": 24, "content": "Gehe zu Pharao; denn er ist aufsassig geworden"},
  {"surah_number": 20, "verse_number": 25, "content": "Er sagte: \"Mein Herr, gib mir die Bereitschaft (dazu)"},
  {"surah_number": 20, "verse_number": 26, "content": "und erleichtere mir meine Aufgabe"},
  {"surah_number": 20, "verse_number": 27, "content": "und lose den Knoten meiner Zunge"},
  {"surah_number": 20, "verse_number": 28, "content": "damit sie meine Rede verstehen mogen"},
  {"surah_number": 20, "verse_number": 29, "content": "Und gib mir einen Beistand aus meiner Familie mit"},
  {"surah_number": 20, "verse_number": 30, "content": "Aaron, meinen Bruder"},
  {"surah_number": 20, "verse_number": 31, "content": "mit dem ich meine Kraft steigere"},
  {"surah_number": 20, "verse_number": 32, "content": "und laß ihn an meiner Aufgabe teilhaben"},
  {"surah_number": 20, "verse_number": 33, "content": "auf daß wir Dich oft preisen mogen"},
  {"surah_number": 20, "verse_number": 34, "content": "und Deiner oft gedenken"},
  {"surah_number": 20, "verse_number": 35, "content": "denn Du kennst uns am besten"},
  {"surah_number": 20, "verse_number": 36, "content": "Er sprach: \"Dein Wunsch ist dir gewahrt, o Moses"},
  {"surah_number": 20, "verse_number": 37, "content": "Und sicher haben Wir dir (auch schon) ein andermal Gnade erwiesen"},
  {"surah_number": 20, "verse_number": 38, "content": "als Wir deiner Mutter jene Weisung eingaben"},
  {"surah_number": 20, "verse_number": 39, "content": "Lege ihn in einen Kasten und wirf ihn in den Fluß, dann wird der Fluß ihn ans Ufer spulen, so daß ein Feind von Mir und ihm ihn aufnehmen wird.\" Und Ich habe auf dich Liebe von Mir gelegt; damit du unter Meinem Auge aufgezogen wurdest"},
  {
    "surah_number": 20,
    "verse_number": 40,
    "content":
        "Damals ging deine Schwester hin und sagte: \"Soll ich euch jemanden empfehlen, der ihn betreuen konnte?\" So gaben Wir dich deiner Mutter wieder, auf daß ihr Auge von Freude erfullt wurde und sie sich nicht gramte. Und du erschlugst einen Menschen, Wir aber erretteten dich aus der Trubsal. Dann pruften Wir dich auf mannigfache Art. Und du verweiltest jahrelang unter dem Volk von Madyan. Hierauf kamst du hierher, o Moses, gemaß einer bestimmten Fugung"
  },
  {"surah_number": 20, "verse_number": 41, "content": "Und Ich habe dich fur Mich ausgewahlt"},
  {"surah_number": 20, "verse_number": 42, "content": "Geht denn mit Meinen Zeichen hin, du und dein Bruder, und lasset nicht (darin) nach, Meiner zu gedenken"},
  {"surah_number": 20, "verse_number": 43, "content": "Geht beide zu Pharao; denn er ist aufsassig geworden"},
  {"surah_number": 20, "verse_number": 44, "content": "Jedoch sprechet zu ihm in sanfter Sprache; vielleicht laßt er sich mahnen oder furchtet sich"},
  {"surah_number": 20, "verse_number": 45, "content": "Beide sagten: \"Unser Herr, wir furchten, er konnte sich an uns vergreifen oder das Ausmaß seiner Gewalt ubersteigen"},
  {"surah_number": 20, "verse_number": 46, "content": "Er sprach: \"Furchtet euch nicht; denn Ich bin mit euch beiden. Ich hore und Ich sehe"},
  {
    "surah_number": 20,
    "verse_number": 47,
    "content": "So geht denn beide zu ihm hin und sprecht: \"Wir sind zwei Gesandte deines Herrn; so laß die Kinder Israels mit uns ziehen; und bestrafe sie nicht. Wir haben dir in Wahrheit ein Zeichen von deinem Herrn gebracht und Friede sei auf dem, der der Rechtleitung folgt"
  },
  {"surah_number": 20, "verse_number": 48, "content": "Es ist uns offenbart worden, daß die Strafe uber den kommen wird, der (den Glauben) verwirft und sich (von ihm) abwendet"},
  {"surah_number": 20, "verse_number": 49, "content": "(Pharao) sagte: \"Wer ist euer beider Herr, o Moses"},
  {"surah_number": 20, "verse_number": 50, "content": "Er sagte: \"Unser Herr ist Der, Der jedem Ding seine Schopfungsart gab, alsdann es zu seiner Bestimmung fuhrte"},
  {"surah_number": 20, "verse_number": 51, "content": "Er (Pharao) sagte: \"Und wie steht es dann um die fruheren Geschlechter"},
  {"surah_number": 20, "verse_number": 52, "content": "Er sagte: \"Das Wissen um sie steht bei meinem Herrn in einem Buch. Weder irrt mein Herr, noch vergißt Er"},
  {"surah_number": 20, "verse_number": 53, "content": "(Er ist es,) Der die Erde fur euch zu einem Lager gemacht hat und Wege uber sie fur euch hinlaufen laßt und Regen vom Himmel hernieder sendet. Und damit bringen Wir Paare von Pflanzenarten hervor"},
  {"surah_number": 20, "verse_number": 54, "content": "Esset denn und weidet euer Vieh.\" Wahrlich, hierin liegen Zeichen fur Leute von Verstand"},
  {"surah_number": 20, "verse_number": 55, "content": "Aus ihr (der Erde) haben Wir euch erschaffen, und in sie werden Wir euch zuruckkehren lassen, und aus ihr bringen Wir euch abermals hervor"},
  {"surah_number": 20, "verse_number": 56, "content": "Und wir ließen ihn (Pharao) Unsere Zeichen allesamt schauen; doch er verwarf (sie) und weigerte sich (zu glauben)"},
  {"surah_number": 20, "verse_number": 57, "content": "Er sagte: \"Bist du zu uns gekommen, o Moses, um uns durch deinen Zauber aus unserem Lande zu treiben"},
  {"surah_number": 20, "verse_number": 58, "content": "Aber wir werden dir sicher einen diesem Zauber gleichen bringen; so setze einen Termin an einem neutralen Ort fur uns fest, den wir nicht verfehlen werden - weder wir noch du"},
  {"surah_number": 20, "verse_number": 59, "content": "Er (Moses) sagte: \"Euer Termin sei auf den Tag des Festes (gelegt), und veranlasse, daß sich die Leute bereits am Vormittag versammeln"},
  {"surah_number": 20, "verse_number": 60, "content": "Da wandte sich Pharao ab und plante eine List, und dann kam er"},
  {"surah_number": 20, "verse_number": 61, "content": "Moses sagte zu ihnen: \"Wehe euch, ersinnt keine Luge gegen Allah, damit Er euch nicht durch eine Strafe vernichte. Wer eine Luge ersinnt, der wird eine Enttau schung erleben"},
  {"surah_number": 20, "verse_number": 62, "content": "Dann stritten sie sich uber ihre Sache und berieten (sich) insgeheim"},
  {"surah_number": 20, "verse_number": 63, "content": "Sie sagten: \"Diese beiden sind sicher Zauberer, die euch durch ihren Zauber aus eurem Land treiben und eure vortreffliche Lebensweise beseitigen wollen"},
  {"surah_number": 20, "verse_number": 64, "content": "So zeigt, was ihr an Macht habt und kommt dann wohlgereiht nach vorn. Und wer heute die Oberhand gewinnt, der wird Erfolg haben"},
  {"surah_number": 20, "verse_number": 65, "content": "Sie sagten: \"O Moses, entweder wirfst du (den Stock zuerst), oder wir werden die Ersten (beim Werfen) sein"},
  {"surah_number": 20, "verse_number": 66, "content": "Er sagte: \"Nein; werft ihr nur!\" Da siehe, ihre Stricke und ihre Stocke erschienen ihm durch ihre Zauberei; als ob sie umhereilten"},
  {"surah_number": 20, "verse_number": 67, "content": "Und Moses verspurte Furcht in seiner Seele"},
  {"surah_number": 20, "verse_number": 68, "content": "Wir sprachen: \"Furchte dich nicht; denn du wirst die Oberhand gewinnen"},
  {"surah_number": 20, "verse_number": 69, "content": "Und wirf nur, was in deiner Rechten ist; es wird das verschlingen, was sie gemacht haben; denn das, was sie gemacht haben, ist nur die List eines Zauberers. Und ein Zauberer soll keinen Erfolg haben, woher er auch kommen mag"},
  {"surah_number": 20, "verse_number": 70, "content": "Da warfen die Zauberer sich nieder. Sie sagten: \"Wir glauben an den Herrn Aarons und Moses"},
  {
    "surah_number": 20,
    "verse_number": 71,
    "content":
        "Er (Pharao) sagte: \"Glaubt ihr an ihn bevor ich es euch erlaube? Er muß wohl euer Meister sein, der euch die Zauberei lehrte. Wahrhaftig, ich will euch darum die Hande und Fuße wechselweise abhauen (lassen), und wahrhaftig, ich will euch an den Stammen der Palmen kreuzigen (lassen); dann werdet ihr bestimmt erfahren, wer von uns strenger und nachhaltiger im Strafen ist"
  },
  {
    "surah_number": 20,
    "verse_number": 72,
    "content": "Sie sagten: \"Wir wollen dir in keiner Weise den Vorzug geben vor den deutlichen Zeichen, die zu uns gekommen sind, noch (vor Dem,) Der uns erschaffen hat. Gebiete, was du gebieten magst: du kannst ja doch nur uber dieses irdische Leben gebieten"
  },
  {"surah_number": 20, "verse_number": 73, "content": "Wir glauben an unseren Herrn, auf daß Er uns unsere Sunden und die Zauberei, zu der du uns genotigt hast, vergebe. Allah ist der Beste und der Bestandigste"},
  {"surah_number": 20, "verse_number": 74, "content": "Wahrlich, fur den, der im Zustand der Sundhaftigkeit zu seinem Herrn kommt, ist Gahannam (bestimmt); darin soll er weder sterben noch leben"},
  {"surah_number": 20, "verse_number": 75, "content": "Denen aber, die als Glaubige zu Ihm kommen (und) gute Taten vollbracht haben, sollen die hochsten Rangstufen zuteil werden"},
  {"surah_number": 20, "verse_number": 76, "content": "die Garten von Eden, durch die Bache fließen; darin werden sie auf ewig verweilen. Und das ist der Lohn derer, die sich rein halten"},
  {"surah_number": 20, "verse_number": 77, "content": "Und wahrlich, Wir offenbarten Moses: \"Fuhre Meine Diener bei Nacht hinweg und schlage ihnen eine trockene Straße durch das Meer. Du brauchst nicht zu furchten, eingeholt zu werden, noch brauchst du dir sonst (irgendwelche) Sorgen zu machen"},
  {"surah_number": 20, "verse_number": 78, "content": "Darauf verfolgte sie Pharao mit seinen Heerscharen, und es kam (etwas) aus dem Meer uber sie, was sie uberwaltigte"},
  {"surah_number": 20, "verse_number": 79, "content": "Und Pharao fuhrte sein Volk in die Irre und fuhrte (es) nicht den rechten Weg"},
  {"surah_number": 20, "verse_number": 80, "content": "O ihr Kinder Israels, Wir erretteten euch von eurem Feinde, und Wir schlossen einen Bund an der rechten Seite des Berges mit euch und sandten Manna und Wachteln auf euch herab"},
  {"surah_number": 20, "verse_number": 81, "content": "Esset nun von den guten Dingen, die Wir euch gegeben haben, doch uberschreitet dabei nicht das Maß, damit Mein Zorn nicht auf euch niederfahre; denn der, auf den Mein Zorn niederfahrt, geht unter"},
  {"surah_number": 20, "verse_number": 82, "content": "Und doch gewahre Ich dem Verzeihung, der bereut und glaubt und das Gute tut und dann der Fuhrung folgt"},
  {"surah_number": 20, "verse_number": 83, "content": "Und was hat dich so eilig von deinem Volke weggetrieben, o Moses"},
  {"surah_number": 20, "verse_number": 84, "content": "Er sagte: \"Sie folgen meiner Spur, und ich bin zu Dir geeilt, mein Herr, damit Du wohl zufrieden bist"},
  {"surah_number": 20, "verse_number": 85, "content": "Er sprach: \"Siehe, Wir haben dein Volk in deiner Abwesenheit gepruft, und der Samiryy hat sie verfuhrt"},
  {
    "surah_number": 20,
    "verse_number": 86,
    "content":
        "Da kehrte Moses zornig und voller Bedauern zu seinem Volk zuruck. Er sagte: \"O mein Volk, hat euer Herr euch nicht eine schone Verheißung gegeben? Erschien euch etwa die anberaumte Zeit zu lang, oder wolltet ihr, daß der Zorn eures Herrn auf euch niederfahre, als ihr euer Versprechen mir gegenuber bracht"
  },
  {"surah_number": 20, "verse_number": 87, "content": "Sie sagten: \"Nicht aus freien Stucken haben wir das Versprechen dir gegenuber gebrochen: allein, wir waren beladen mit der Last der Schmucksachen des Volkes; wir warfen sie fort, und das gleiche tat auch der Samiryy"},
  {"surah_number": 20, "verse_number": 88, "content": "Dann brachte er ihnen ein leibhaftiges Kalb, das blokte, hervor. Und sie sagten: \"Das ist euer Gott und der Gott Moses'; er hat (ihn) vergessen"},
  {"surah_number": 20, "verse_number": 89, "content": "Konnten sie denn nicht sehen, daß es ihnen keine Antwort gab und keine Macht hatte, ihnen weder zu schaden noch zu nutzen"},
  {"surah_number": 20, "verse_number": 90, "content": "Und doch hatte Aaron zuvor zu ihnen gesagt: \"O mein Volk, dadurch seid ihr nur gepruft worden. Wahrlich, euer Herr ist der Allerbarmer; darum folgt mir und gehorcht meinem Befehl"},
  {"surah_number": 20, "verse_number": 91, "content": "Sie sagten: \"Wir werden keineswegs aufhoren, es anzubeten, bis Moses zu uns zuruckkehrt"},
  {"surah_number": 20, "verse_number": 92, "content": "Er (Moses) sagte: \"O Aaron, was hinderte dich, als du sie irregehen sahst"},
  {"surah_number": 20, "verse_number": 93, "content": "mir zu folgen? Bist du denn meinem Befehl ungehorsam gewesen"},
  {"surah_number": 20, "verse_number": 94, "content": "Er sagte: \"O Sohn meiner Mutter, greife nicht nach meinem Bart, noch nach meinem Kopf. Ich furchtete, du konntest sagen: \"Du hast die Kinder Israels gespalten und mein Wort nicht beachtet"},
  {"surah_number": 20, "verse_number": 95, "content": "Er sagte: \"Und was hast du zu sagen, o Samiryy"},
  {"surah_number": 20, "verse_number": 96, "content": "Er sagte: \"lch bemerkte, was sie nicht wahrnehmen konnten. Da faßte ich eine Handvoll Erde von der Spur des Gesandten und warf sie hin. So habe ich es mir selber eingeredet"},
  {
    "surah_number": 20,
    "verse_number": 97,
    "content":
        "Er (Moses) sagte: \"Geh denn hin! Du sollst dein ganzes Leben lang sagen mussen: \"Beruhrt (mich) nicht\"; und dann ist da ein Zeitpunkt fur dich, dem du nicht entgehen wirst. So schaue nun auf deinen \"Gott\", dessen ergebener Anbeter du geworden bist. Wir werden ihn ganz gewiß verbrennen und ihn darauf ins Meer streuen"
  },
  {"surah_number": 20, "verse_number": 98, "content": "Wahrlich, euer Gott ist Allah, außer Dem kein Gott da ist. Er umfaßt alle Dinge mit Wissen"},
  {"surah_number": 20, "verse_number": 99, "content": "Damit also erzahlen Wir dir so manche Geschichte von dem, was zuvor geschah. Und Wir haben dir Unsere Ermahnung erteilt"},
  {"surah_number": 20, "verse_number": 100, "content": "Wer sich abkehrt, der wird wahrlich am Tage der Auferstehung eine Last tragen"},
  {"surah_number": 20, "verse_number": 101, "content": "(In der Holle) werden sie auf ewig verweilen; und schwer wird ihnen die Burde am Tage der Auferstehung sein"},
  {"surah_number": 20, "verse_number": 102, "content": "an dem Tage, da in den Sur gestoßen wird. Und an jenem Tage werden Wir die Schuldigen als Blauaugige versammeln"},
  {"surah_number": 20, "verse_number": 103, "content": "sie werden einander heimlich zuflustern: \"lhr weiltet nur zehn (Tage im Grab)"},
  {"surah_number": 20, "verse_number": 104, "content": "Wir wissen am besten, was sie sagen. Dann wird der Vernunftigste von ihnen sagen: \"Nur einen Tag verweiltet ihr"},
  {"surah_number": 20, "verse_number": 105, "content": "Und sie werden dich nach den Bergen fragen. Sprich: \"Mein Herr wird sie vollstandig sprengen"},
  {"surah_number": 20, "verse_number": 106, "content": "Und Er wird sie als kahle Ebene zuruck lassen"},
  {"surah_number": 20, "verse_number": 107, "content": "worin du weder Krummungen noch Unebenheiten sehen wirst"},
  {"surah_number": 20, "verse_number": 108, "content": "An jenem Tage werden sie dem Rufe folgen, der keine Abweichung kennt; alle Stimmen werden vor dem Allerbarmer gesenkt sein, und du wirst nichts als Flustern horen"},
  {"surah_number": 20, "verse_number": 109, "content": "An jenem Tage wird keinem Fursprache etwas nutzen - außer jenem, dem der Allerbarmer (dazu) die Erlaubnis gibt und dessen Wort Ihm wohlgefallig ist"},
  {"surah_number": 20, "verse_number": 110, "content": "Er kennt alles, was vor ihnen ist und was hinter ihnen ist; sie aber konnen es nicht mit Wissen umfassen"},
  {"surah_number": 20, "verse_number": 111, "content": "Und die Gesichter werden sich demutig vor dem Lebendigen, dem Ewig-Bestandigen, neigen. Und hoffnungslos wahrlich ist jener, der (die Last des) Frevels tragt"},
  {"surah_number": 20, "verse_number": 112, "content": "Und der aber, der gute Werke tut und dabei glaubig ist, wird weder Ungerechtigkeit noch Unterdruckung (zu) furchten (haben)"},
  {"surah_number": 20, "verse_number": 113, "content": "Und somit haben Wir (die Schrift) als arabischen Quran niedergesandt, und Wir haben darin gewisse Warnungen klar gemacht, auf daß sie (Ihn) furchten mogen oder (wunschen mogen,) daß er ihnen eine Ermahnung bringe"},
  {"surah_number": 20, "verse_number": 114, "content": "Hoch Erhaben ist Allah, der wahre Konig! Und uberhaste dich nicht mit dem Quran, ehe seine Offenbarung dir nicht vollstandig zuteil geworden ist, sondern sprich: \"O mein Herr, mehre mein Wissen"},
  {"surah_number": 20, "verse_number": 115, "content": "Und wahrlich, Wir schlossen zuvor einen Bund mit Adam, aber er vergaß (ihn); Wir fanden in ihm kein Ausharrungsvermogen"},
  {"surah_number": 20, "verse_number": 116, "content": "Und als Wir da zu den Engeln sprachen: \"Werft euch vor Adam nieder\", da warfen sie sich nieder, außer Iblis. Er weigerte sich"},
  {"surah_number": 20, "verse_number": 117, "content": "Sodann sprachen Wir: \"O Adam, dieser ist dir und deiner Frau ein Feind; (achtet darauf,) daß er euch nicht beide aus dem Garten treibt! Sonst wurdest du unglucklich sein"},
  {"surah_number": 20, "verse_number": 118, "content": "Es ist fur dich gesorgt, daß du darin weder Hunger fuhlen noch nackt sein sollst"},
  {"surah_number": 20, "verse_number": 119, "content": "Und du sollst darin nicht dursten noch der Sonnenhitze ausgesetzt sein"},
  {"surah_number": 20, "verse_number": 120, "content": "Jedoch Satan flusterte ihm Boses ein; er sagte: \"O Adam, soll ich dich zum Baume der Ewigkeit fuhren und zu einem Konigreich, das nimmer vergeht"},
  {"surah_number": 20, "verse_number": 121, "content": "Da aßen sie beide davon, so daß ihnen ihre Bloße ersichtlich wurde, und sie begannen, die Blatter des Gartens uber sich zusammenzustecken. Und Adam befolgte das Gebot seines Herrn nicht und ging irre"},
  {"surah_number": 20, "verse_number": 122, "content": "Hierauf erwahlte ihn sein Herr und wandte Sich ihm mit Erbarmen und Rechtleitung zu"},
  {"surah_number": 20, "verse_number": 123, "content": "Er sprach: \"Geht von hier allesamt hinunter, der eine von euch soll des anderen Feind sein! Und wenn Meine Fuhrung zu euch kommt, dann wird der, der Meiner Fuhrung folgt, nicht zugrunde gehen, noch wird er Ungluck erleiden"},
  {"surah_number": 20, "verse_number": 124, "content": "Und dem, der sich jedoch von Meiner Ermahnung abkehrt, wird ein Leben in Drangsal beschieden sein, und am Tage der Auferstehung werden Wir ihn blind vor Uns fuhren"},
  {"surah_number": 20, "verse_number": 125, "content": "Er wird sagen: \"Mein Herr, warum hast Du mich blind (vor Dich) gefuhrt, obwohl ich (zuvor) sehen konnte"},
  {"surah_number": 20, "verse_number": 126, "content": "Er wird sprechen: \"Es sind ja Unsere Zeichen zu dir gekommen, und du hast sie mißachtet: also wirst heute nun du mißachtet sein"},
  {"surah_number": 20, "verse_number": 127, "content": "Und ebenso belohnen Wir auch den, der maßlos ist und nicht an die Zeichen seines Herrn glaubt; und die Strafe des Jenseits ist wahrlich strenger und nachhaltiger"},
  {"surah_number": 20, "verse_number": 128, "content": "Leuchtet es ihnen (denn) nicht ein, wie viele Geschlechter vor ihnen Wir schon vernichteten, in deren Wohnstatten sie umherwandern? Darin liegen Zeichen fur die Leute, die Verstand haben"},
  {"surah_number": 20, "verse_number": 129, "content": "Und ware nicht zuvor ein Wort von deinem Herrn ergangen, so ware die (Strafe) fallig; (genauso ist es) mit der Frist, die vorbestimmt ist"},
  {"surah_number": 20, "verse_number": 130, "content": "Ertrage denn geduldig, was sie sagen, und lobpreise deinen Herrn vor dem Aufgang und vor dem Untergang der Sonne, und verherrliche (ihn) in den Nachtstunden und an den Tagesenden, auf daß du wahre Gluckseligkeit finden mogest"},
  {"surah_number": 20, "verse_number": 131, "content": "Und richte deinen Blick nicht auf das, was Wir einigen von ihnen zu (kurzem) Genuß gewahrten den Glanz des irdischen Lebens -, um sie dadurch zu prufen. Denn die Versorgung deines Herrn ist besser und bleibender"},
  {"surah_number": 20, "verse_number": 132, "content": "Und fordere die Deinen zum Gebet auf und sei (selbst) darin ausdauernd. Wir verlangen keinen Unterhalt von dir; Wir Selbst sorgen fur dich. Und der Ausgang ist durch Gottesfurcht vorgegeben"},
  {"surah_number": 20, "verse_number": 133, "content": "Und sie sagen: \"Warum bringt er uns kein Zeichen von seinem Herrn?\" Ist zu ihnen denn nicht der klarste Beweis fur das gekommen, was in den fruheren Schriften steht"},
  {"surah_number": 20, "verse_number": 134, "content": "Und hatten Wir sie vordem durch eine Strafe vernichtet, dann hatten sie gewiß gesagt: \"Unser Herr, warum schicktest Du uns keinen Gesandten, (der uns hatte helfen konnen,) Deine Gebote zu befolgen, ehe wir gedemutigt und beschamt wurden"},
  {"surah_number": 20, "verse_number": 135, "content": "Sprich: \"Ein jeder wartet; so wartet auch ihr, und ihr werdet erfahren, wer die Befolger des ebenen Weges sind und wer rechtgeleitet ist"},
  {"surah_number": 21, "verse_number": 1, "content": "Den Menschen ist die Zeit ihrer Abrechnung nahe geruckt; und doch wenden sie sich in Achtlosigkeit ab"},
  {"surah_number": 21, "verse_number": 2, "content": "Keine neue Ermahnung von ihrem Herrn kommt zu ihnen, die sie nicht leichtfertig anhorten"},
  {"surah_number": 21, "verse_number": 3, "content": "Ihre Herzen belustigen sich anderweitig. Und sie besprechen sich insgeheim - sie, die da freveln - (, dann sagen sie): \"Ist dieser etwa ein anderer Mensch als wir? Wollt ihr euch denn gegen (bessere) Einsicht auf Zauberei einlassen"},
  {"surah_number": 21, "verse_number": 4, "content": "Er sagte: \"Mein Herr weiß, was im Himmel und auf Erden gesprochen wird, und Er ist der Allhorende, der Allwissende"},
  {"surah_number": 21, "verse_number": 5, "content": "Nein\", sagen sie, \"(das) aber (sind) wirre Traume; nein, er hat ihn erdichtet; nein, er ist (nur) ein Dichter. Moge er uns doch ein Zeichen in der Art bringen, wie die fruheren entsandt wurden"},
  {"surah_number": 21, "verse_number": 6, "content": "Nie hatte vor ihnen irgendeine Stadt je geglaubt, die Wir vernichteten. Wurden sie denn glauben"},
  {"surah_number": 21, "verse_number": 7, "content": "Und Wir entsandten vor dir lediglich Manner, denen Wir die Offenbarung zuteil werden ließen - fragt nur diejenigen, die von der Ermahnung wissen, wenn ihr (davon) nichts wisset"},
  {"surah_number": 21, "verse_number": 8, "content": "Und Wir machten ihnen keinen Leib, daß sie keiner Speise bedurft hatten, noch daß sie ewig lebten"},
  {"surah_number": 21, "verse_number": 9, "content": "Dann erfullten Wir ihnen das Versprechen; und Wir erretteten sie und die, die Wir wollten; die Ubertreter aber vertilgten Wir"},
  {"surah_number": 21, "verse_number": 10, "content": "Wahrlich, Wir haben euch ein Buch hinabgesandt, worin eure Ehre liegt; wollt ihr es denn nicht begreifen"},
  {"surah_number": 21, "verse_number": 11, "content": "Wie so manche Stadt, voll der Ungerechtigkeit, haben Wir schon niedergebrochen und nach ihr ein anderes Volk erweckt"},
  {"surah_number": 21, "verse_number": 12, "content": "Und da sie Unsere Strafe spurten, siehe, da begannen sie davor zu fliehen"},
  {"surah_number": 21, "verse_number": 13, "content": "Flieht nicht, sondern kehrt zu dem Behagen, das ihr genießen durftet, und zu euren Wohnstatten zuruck, damit ihr befragt werden konnt"},
  {"surah_number": 21, "verse_number": 14, "content": "Sie sagten: \" O wehe uns, wir waren wahrlich Frevler"},
  {"surah_number": 21, "verse_number": 15, "content": "Und dieser ihr Ruf horte nicht eher auf, als bis Wir sie niedermahten und in Asche verwandelten"},
  {"surah_number": 21, "verse_number": 16, "content": "Und Wir erschufen Himmel und Erde und das, was zwischen beiden ist, nicht zum Spiel"},
  {"surah_number": 21, "verse_number": 17, "content": "Hatten Wir Uns einen Zeitvertreib schaffen wollen, so hatten Wir dies von Uns aus vorgenommen, wenn Wir das uberhaupt hatten tun wollen"},
  {"surah_number": 21, "verse_number": 18, "content": "Vielmehr werfen Wir die Wahrheit gegen die Luge, und diese wird dadurch lebensunfahig gemacht. Und wehe euch im Hinblick darauf, was ihr aussagt"},
  {"surah_number": 21, "verse_number": 19, "content": "Und Sein ist, wer in den Himmeln und auf der Erde ist. Und die bei Ihm sind, sind nicht zu stolz, Ihm zu dienen, noch werden sie lustlos sein"},
  {"surah_number": 21, "verse_number": 20, "content": "Sie verherrlichen (ihn) bei Nacht und Tag; (und) sie lassen (darin) nicht nach"},
  {"surah_number": 21, "verse_number": 21, "content": "Oder haben sie sich Gotter von der Erde genommen, die lebendig machen"},
  {"surah_number": 21, "verse_number": 22, "content": "Gabe es in (Himmel und Erde) Gotter außer Allah, dann waren wahrlich beide dem Unheil verfallen. Gepriesen sei denn Allah, der Herr des Thrones, Hoch Erhaben uber das, was sie beschreiben"},
  {"surah_number": 21, "verse_number": 23, "content": "Er wird nicht befragt nach dem, was Er tut; sie aber werden befragt (nach dem, was sie tun)"},
  {
    "surah_number": 21,
    "verse_number": 24,
    "content": "Haben sie sich Gotter außer Ihm genommen? Sprich: \"Bringt euren Beweis herbei. Dieser (Quran) ist eine Ermahnung fur jene, die mit mir sind, und eine Ermahnung fur die, die vor mir waren.\" Doch die meisten von ihnen kennen die Wahrheit nicht, und so wenden sie sich (von ihr) ab"
  },
  {"surah_number": 21, "verse_number": 25, "content": "Und Wir schickten keinen Gesandten vor dir, dem Wir nicht offenbart haben: \"Es ist kein Gott außer Mir, darum dient nur Mir"},
  {"surah_number": 21, "verse_number": 26, "content": "Und sie sagen: \"Der Allerbarmer hat Sich einen Sohn genommen.\" Gepriesen sei Er! Nein, aber sie sind (nur) geehrte Diener"},
  {"surah_number": 21, "verse_number": 27, "content": "sie kommen Ihm beim Sprechen nicht zuvor, und sie handeln nur nach Seinem Befehl"},
  {"surah_number": 21, "verse_number": 28, "content": "Er weiß, was vor ihnen und was hinter ihnen ist, und sie legen keine Fursprache ein; außer fur den, an dem Er Wohlgefallen hat, und sie zagen aus Furcht vor Ihm"},
  {"surah_number": 21, "verse_number": 29, "content": "Und dem, der von ihnen sagen wollte: \"lch bin ein Gott neben Ihm\", wurden Wir es mit Gahannam vergelten. Und ebenso vergelten Wir es den Ungerechten"},
  {"surah_number": 21, "verse_number": 30, "content": "Haben die Unglaubigen nicht gesehen, daß die Himmel und die Erde eine Einheit waren, die Wir dann zerteilten? Und Wir machten aus dem Wasser alles Lebendige. Wollen sie denn nicht glauben"},
  {"surah_number": 21, "verse_number": 31, "content": "Und feste Berge haben Wir in der Erde gegrundet, auf daß sie nicht mit ihnen wanke; und Wir haben auf ihr gangbare Passe angelegt, damit sie sich zurecht finden"},
  {"surah_number": 21, "verse_number": 32, "content": "Und Wir machten den Himmel zu einem wohlgeschutzten Dach; dennoch kehren sie sich von seinem Zeichen ab"},
  {"surah_number": 21, "verse_number": 33, "content": "Und Er ist es, Der die Nacht und den Tag erschuf und die Sonne und den Mond. Sie schweben, ein jedes (Gestirn) auf seiner Laufbahn"},
  {"surah_number": 21, "verse_number": 34, "content": "Wir gewahrten keinem Menschenwesen vor dir das ewige Leben. Als ob sie es waren, die ewig leben konnten, wenn du gestorben warst"},
  {"surah_number": 21, "verse_number": 35, "content": "Jede Seele wird den Tod kosten; und Wir stellen euch mit Bosem und mit Gutem auf die Probe; und zu Uns werdet ihr zu ruckgebracht"},
  {"surah_number": 21, "verse_number": 36, "content": "Und wenn die Unglaubigen dich sehen, so treiben sie nur Spott mit dir: \"Ist das der, der eure Gotter (in abfalliger Weise) erwahnt?\" wahrend sie es doch selbst sind, die die Erwahnung des Allerbarmers verleugnen"},
  {"surah_number": 21, "verse_number": 37, "content": "Der Mensch ist ein Geschopf der Eilfertigkeit. Ich werde euch Meine Zeichen zeigen, aber fordert nicht von Mir, daß Ich Mich ubereile"},
  {"surah_number": 21, "verse_number": 38, "content": "Und sie sagen: \"Wann wird diese Verheißung (in Erfullung gehen), wenn ihr wahrhaftig seid"},
  {"surah_number": 21, "verse_number": 39, "content": "Wenn die Unglaubigen nur die Zeit wußten, wo sie nicht imstande sein werden, das Feuer von ihren Gesichtern oder ihren Rucken fernzuhalten! Und keine Hilfe wird ihnen zuteil sein"},
  {"surah_number": 21, "verse_number": 40, "content": "Nein, es wird unversehens uber sie kommen, so daß sie in Verwirrung gesturzt werden; und sie werden es nicht abwehren konnen, noch werden sie Aufschub erlangen"},
  {"surah_number": 21, "verse_number": 41, "content": "Und es sind wahrlich schon vor dir Gesandte verspottet worden, dann aber traf jene, die Spott trieben, das, woruber sie spotteten"},
  {"surah_number": 21, "verse_number": 42, "content": "Sprich: \"Wer beschutzt euch bei Nacht und bei Tag vor dem Allerbarmer?\" Und doch kehren sie sich von der Ermahnung ihres Herrn ab"},
  {"surah_number": 21, "verse_number": 43, "content": "Oder haben sie etwa Gotter, die sie vor Uns beschutzen konnen? Sie vermogen sich nicht selbst zu helfen, noch kann ihnen gegen Uns geholfen werden"},
  {"surah_number": 21, "verse_number": 44, "content": "Nein, Wir ließen diese und ihre Vater leben, bis das Leben ihnen lang wurde. Sehen sie denn nicht, daß Wir uber das Land kommen und es an seinen Enden schmalern? Konnen sie denn siegen"},
  {"surah_number": 21, "verse_number": 45, "content": "Sprich: \"lch warne euch nur mit der Offenbarung.\" Jedoch die Tauben horen den Ruf nicht, wenn sie gewarnt werden"},
  {"surah_number": 21, "verse_number": 46, "content": "Und wenn sie ein Hauch von der Strafe deines Herrn beruhrt, dann werden sie sicher sagen: \"O wehe uns, wir waren wahrlich Frevler"},
  {"surah_number": 21, "verse_number": 47, "content": "Und Wir werden Waagen der Gerechtigkeit fur den Tag der Auferstehung aufstellen, so daß keine Seele in irgendeiner Weise Unrecht erleiden wird. Und ware es das Gewicht eines Senfkorns, wurden Wir es hervorbringen. Und Wir genugen als Rechner"},
  {"surah_number": 21, "verse_number": 48, "content": "Und wahrlich, Wir gaben Moses und Aaron die Unterscheidung als Licht und Ermahnung fur die Gottesfurchtigen"},
  {"surah_number": 21, "verse_number": 49, "content": "die ihren Herrn im verborgenen furchten und sich vor der Stunde Sorge tragen"},
  {"surah_number": 21, "verse_number": 50, "content": "Und dieser (Quran) ist eine segensreiche Ermahnung, die Wir hinabgesandt haben. Wollt ihr sie nun verwerfen"},
  {"surah_number": 21, "verse_number": 51, "content": "Und vordem gaben Wir Abraham seine Recht schaffenheit; denn Wir kannten ihn"},
  {"surah_number": 21, "verse_number": 52, "content": "Als er zu seinem Vater und seinem Volke sagte: \"Was sind das fur Bildwerke, denen ihr so ergeben seid"},
  {"surah_number": 21, "verse_number": 53, "content": "sagten sie: \"Wir fanden, daß unsere Vater stets diese verehrten"},
  {"surah_number": 21, "verse_number": 54, "content": "Er sagte: \"Wahrlich, ihr selbst wie auch eure Vater seid im deutlichen Irrtum gewesen"},
  {"surah_number": 21, "verse_number": 55, "content": "Sie sagten: \"Bringst du uns die Wahrheit, oder gehorst du zu denen, die einen Scherz (mit uns) treiben"},
  {"surah_number": 21, "verse_number": 56, "content": "Er sagte: \"Nein, euer Herr ist der Herr der Himmel und der Erde, Der sie erschuf; und ich bin einer, der dies bezeugt"},
  {"surah_number": 21, "verse_number": 57, "content": "Und, bei Allah, ich will gewiß gegen eure Gotzen verfahren, nachdem ihr kehrt gemacht habt und weggegangen seid"},
  {"surah_number": 21, "verse_number": 58, "content": "Alsdann schlug er sie in Stucke - mit Ausnahme des großten von ihnen -, damit sie sich an ihn wenden konnten"},
  {"surah_number": 21, "verse_number": 59, "content": "Sie sagten: \"Wer hat unseren Gottern dies angetan? Er muß wahrlich ein Frevler sein"},
  {"surah_number": 21, "verse_number": 60, "content": "Sie sagten: \"Wir horten einen jungen Mann von ihnen reden; Abraham heißt er"},
  {"surah_number": 21, "verse_number": 61, "content": "Sie sagten: \"So bringt ihn vor die Augen der Menschen damit sie das bezeugen"},
  {"surah_number": 21, "verse_number": 62, "content": "Sie sagten: \"Bist du es gewesen, der unseren Gottern dies angetan hat, O Abraham"},
  {"surah_number": 21, "verse_number": 63, "content": "Er sagte: \"Nein, dieser da, der großte von ihnen, hat es getan. Fragt sie doch, wenn sie reden konnen"},
  {"surah_number": 21, "verse_number": 64, "content": "Da wandten sie sich einander zu und sagten: \"Wir selber sind wahrhaftig im Unrecht"},
  {"surah_number": 21, "verse_number": 65, "content": "Dann wurden sie ruckfallig: \"Du weißt recht wohl, daß diese nicht reden konnen"},
  {"surah_number": 21, "verse_number": 66, "content": "Er sagte: \"Verehrt ihr denn statt Allah das, was euch weder den geringsten Nutzen bringen noch euch schaden kann"},
  {"surah_number": 21, "verse_number": 67, "content": "Pfui uber euch und uber das, was ihr statt Allah anbetet! Wollt ihr es denn nicht begreifen"},
  {"surah_number": 21, "verse_number": 68, "content": "Sie sagten: \"Verbrennt ihn und helft euren Gottern, wenn ihr etwas tun wollt"},
  {"surah_number": 21, "verse_number": 69, "content": "Wir sprachen: \"O Feuer, sei kuhl und ein Frieden fur Abraham"},
  {"surah_number": 21, "verse_number": 70, "content": "Und sie strebten, ihm Boses zu tun, allein Wir machten sie zu den großten Verlierern"},
  {"surah_number": 21, "verse_number": 71, "content": "Und Wir retteten ihn und Lot in das Land, das Wir fur die Welten gesegnet hatten"},
  {"surah_number": 21, "verse_number": 72, "content": "Und Wir schenkten ihm Isaak und dazu Jakob, und Wir machten jeden von ihnen rechtschaffen"},
  {"surah_number": 21, "verse_number": 73, "content": "Und Wir machten sie zu Vorbildern, die auf unser Geheiß (die Menschen) rechtleiteten, und Wir gaben ihnen ein, Gutes zu tun, das Gebet zu verrichten und die Zakah zu entrichten. Und sie verehrten Uns allein"},
  {"surah_number": 21, "verse_number": 74, "content": "Und Lot gaben Wir Weisheit und Wissen. Und Wir retteten ihn aus der Stadt, die Schandlichkeiten beging. Sie waren wahrlich ein ruchloses Volk und Emporer"},
  {"surah_number": 21, "verse_number": 75, "content": "Und Wir ließen ihn in Unsere Barmherzigkeit eingehen; denn er war einer der Rechtschaffenen"},
  {"surah_number": 21, "verse_number": 76, "content": "wie auch Noah, als er vordem (zu Uns) rief; Wir erhorten ihn und retteten ihn und seine Angehorigen aus großer Drangsal"},
  {"surah_number": 21, "verse_number": 77, "content": "Und Wir halfen ihm gegen das Volk, das Unsere Zeichen verwarf. Sie waren wahrlich ein ruchloses Volk; so ertrankten Wir sie alle"},
  {"surah_number": 21, "verse_number": 78, "content": "Und David und Salomo richteten uber den Acker, worin sich die Schafe eines Volkes zur Nachtzeit verliefen und weideten; und Wir waren Zeugen ihres Urteilspruches"},
  {"surah_number": 21, "verse_number": 79, "content": "Wir gaben Salomo volle Einsicht in die Sache, und jedem (von ihnen) gaben Wir Weisheit und Wissen. Und Wir machten die Berge und die Vogel dienstbar, mit David zusammen (Allahs) Preis zu verkunden, und Wir konnten das tun"},
  {"surah_number": 21, "verse_number": 80, "content": "Und Wir lehrten ihn das Verfertigen eurer Panzerhemden, auf daß sie euch in eurem Kampf schutzen mogen. Wollt ihr denn nicht dankbar sein"},
  {"surah_number": 21, "verse_number": 81, "content": "Und Salomo (machten Wir) den Wind (dienstbar), der in seinem Auftrage in das Land blies, das Wir gesegnet hatten. Und Wir besitzen Kenntnis von allen Dingen"},
  {"surah_number": 21, "verse_number": 82, "content": "und von den Satanen, die fur ihn tauchten und dazu noch andere Werke verrichteten; und Wir Selbst beaufsichtigten sie"},
  {"surah_number": 21, "verse_number": 83, "content": "Und Hiob rief zu seinem Herrn: \"Unheil hat mich geschlagen, und Du bist der Barmherzigste aller Barmherzigen"},
  {"surah_number": 21, "verse_number": 84, "content": "Da erhorten Wir ihn und nahmen sein Unheil hinweg, und Wir gaben ihm seine Familie (wieder) und noch einmal so viele dazu - aus Unserer Barmherzigkeit und als Ermahnung fur die (Uns) Verehrenden"},
  {"surah_number": 21, "verse_number": 85, "content": "Und Ismael und Idris und Du-l-Kifl; sie alle zahlten zu den Standhaften"},
  {"surah_number": 21, "verse_number": 86, "content": "Und Wir ließen sie in Unsere Barmherzigkeit eingehen; denn sie gehorten zu den Rechtschaffenen"},
  {"surah_number": 21, "verse_number": 87, "content": "Und Du-n-Nun ging im Zorn hinweg und war uberzeugt, daß Wir ihn nie in Betrubnis bringen wurden; und da rief er in der dichten Finsternis: \"Es ist kein Gott außer Dir. Gepriesen seist Du Ich bin wahrlich einer der Frevler gewesen"},
  {"surah_number": 21, "verse_number": 88, "content": "Da erhorten Wir ihn und retteten ihn aus seiner Bedrangnis; und genauso retten Wir die Glaubigen"},
  {"surah_number": 21, "verse_number": 89, "content": "Und Zacharias rief zu seinem Herrn: \"Mein Herr, laß mich nicht einsam bleiben; denn Du bist der Beste der Erben"},
  {"surah_number": 21, "verse_number": 90, "content": "Da erhorten Wir ihn und schenkten ihm Jahya und heilten seine Frau. Sie pflegten miteinander in guten Taten zu wetteifern, und sie riefen Uns in Hoffnung und in Furcht an und waren demutig vor Uns"},
  {"surah_number": 21, "verse_number": 91, "content": "Und der die ihre Keuschheit wahrte, hauchten Wir von Unserem Geist ein und machten sie und ihren Sohn zu einem Zeichen fur die Welten"},
  {"surah_number": 21, "verse_number": 92, "content": "Diese eure Gemeinschaft ist eine einheitliche Gemeinschaft; und Ich bin euer Herr, darum dient Mir"},
  {"surah_number": 21, "verse_number": 93, "content": "Sie aber sind untereinander zerstritten; alle werden sie zu Uns zuruckkehren"},
  {"surah_number": 21, "verse_number": 94, "content": "Das Bemuhen dessen, der also gute Werke tut und glaubig ist, wird nicht unbelohnt bleiben. Wir werden es gewiß verzeichnen"},
  {"surah_number": 21, "verse_number": 95, "content": "Und fur eine Stadt, die Wir zerstort haben, ist es unwiderruflich festgelegt, nicht zuruckzukehren"},
  {"surah_number": 21, "verse_number": 96, "content": "bis dann, wenn Gog und Magog freigelassen werden, und sie von allen Hohen herbeieilen"},
  {"surah_number": 21, "verse_number": 97, "content": "Und die wahre Verheißung naht; siehe dann werden die Augen derer, die unglaubig waren, starr blicken: \"O wehe uns, wir haben in der Tat nicht daran gedacht; ja, wir waren Frevler"},
  {"surah_number": 21, "verse_number": 98, "content": "Wahrlich, ihr und das, was ihr statt Allah anbetet, seid Brennstoff der Gahannam. Dahin werdet ihr kommen mussen"},
  {"surah_number": 21, "verse_number": 99, "content": "Waren diese Gotter gewesen, waren sie nicht dahin gekommen; doch sie mussen alle auf ewig darin bleiben"},
  {"surah_number": 21, "verse_number": 100, "content": "Darin werden sie stohnen; doch darin werden sie nicht horen konnen"},
  {"surah_number": 21, "verse_number": 101, "content": "Diejenigen aber, an welche (Unsere Verheißung) eines herrlichen Lohns schon vordem ergangen ist, werden von ihr (der Holle) weit entfernt sein"},
  {"surah_number": 21, "verse_number": 102, "content": "sie werden nicht den leisesten Laut von ihr horen, wahrend sie auf ewig in dem verweilen, was ihre Seelen begehren"},
  {"surah_number": 21, "verse_number": 103, "content": "Der großte Schrecken wird sie nicht betruben, und die Engel werden ihnen entgegenkommen: \"Das ist euer Tag, der euch verheißen wurde"},
  {
    "surah_number": 21,
    "verse_number": 104,
    "content": "An dem Tage, da werden Wir den Himmel zusammenrollen, wie die Schriftrollen zusammengerollt werden. (So) wie Wir die erste Schopfung begonnen haben, werden Wir sie wiederholen - bindend fur Uns ist die Verheißung; wahrlich, Wir werden (sie) erfullen"
  },
  {"surah_number": 21, "verse_number": 105, "content": "Und Wir haben bereits nach der Ermahnung in dem Zabur geschrieben, daß das Land von Meinen rechtschaffenen Dienern beerbt wird"},
  {"surah_number": 21, "verse_number": 106, "content": "Hierin liegt wahrlich eine Botschaft fur ein Volk, das (Allah) dient"},
  {"surah_number": 21, "verse_number": 107, "content": "Und Wir entsandten dich nur aus Barmherzigkeit fur alle Welten"},
  {"surah_number": 21, "verse_number": 108, "content": "Sprich: \" Mir wird lediglich offenbart, daß euer Gott nur ein Einziger Gott ist! Wollt ihr euch denn nicht ergeben"},
  {"surah_number": 21, "verse_number": 109, "content": "Doch wenn sie (dir) den Rucken kehren, so sprich: \"lch habe euch die Kunde in gerechter Weise entboten und ich weiß nicht, ob nah oder ferne ist, was euch verheißen wird"},
  {"surah_number": 21, "verse_number": 110, "content": "Wahrlich, Er kennt, was in eurer Rede offenkundig ist, und Er weiß, was ihr verheimlicht"},
  {"surah_number": 21, "verse_number": 111, "content": "Und ich weiß nicht, ob es vielleicht nur eine Prufung fur euch oder ein Nießbrauch auf bestimmte Zeit ist"},
  {"surah_number": 21, "verse_number": 112, "content": "Er sagte: \"Mein Herr, richte in Wahr heit. Und unser Herr ist der Allerbarmer, Dessen Hilfe gegen das, was ihr behauptet, anzuflehen ist"},
  {"surah_number": 22, "verse_number": 1, "content": "O Ihr Menschen, furchtet euren Herrn; denn das Beben der Stunde ist wahrlich etwas Gewaltiges"},
  {"surah_number": 22, "verse_number": 2, "content": "An dem Tage, da ihr es seht, wird jede Stillende ihren Saugling vergessen und jede Schwangere ihre Burde abwerfen; und du wirst die Menschen trunken sehen, obwohl sie nicht trunken sind; aber die Strafe Allahs ist gewaltig"},
  {"surah_number": 22, "verse_number": 3, "content": "Und unter den Menschen ist manch einer, der ohne Wissen uber Allah streitet und jedem in der Emporung hartnackigen Satan folgt"},
  {"surah_number": 22, "verse_number": 4, "content": "und uber den beschlossen ist, daß, wer ihn zum Beschutzer nimmt, von ihm irregefuhrt und zur Strafe des Hollenbrands gefuhrt wird"},
  {
    "surah_number": 22,
    "verse_number": 5,
    "content":
        "O ihr Menschen, wenn ihr uber die Auferstehung im Zweifel seid, so (bedenkt,) daß Wir euch aus Erde erschaffen haben, dann aus einem Samentropfen, dann aus einem Blutklumpen, dann aus einem Klumpen Fleisch, teils geformt und teils ungeformt, auf daß Wir es euch deutlich machen. Und Wir lassen bis zu einem bestimmten Zeitpunkt in den Mutterschoßen ruhen, was Wir wollen; dann bringen Wir euch als Kinder hervor; dann (lassen Wir euch groß werden,) auf daß ihr eure Vollkraft erreicht. Und mancher von euch wird abberufen, und mancher von euch wird zu einem hinfalligen Greisenalter gefuhrt, so daß er, nachdem er gewußt hatte, nichts mehr weiß. Und du siehst die Erde leblos, doch wenn Wir Wasser uber sie niedersenden, dann regt sie sich und schwillt und laßt alle Arten von entzuckenden Paaren hervorsprießen"
  },
  {"surah_number": 22, "verse_number": 6, "content": "Dies (ist so), weil Allah die Wahrheit ist und weil Er es ist, Der die Toten lebendig macht, und weil Er die Macht uber alles hat"},
  {"surah_number": 22, "verse_number": 7, "content": "und weil die Stunde kommt - daruber herrscht kein Zweifel - und weil Allah alle erwecken wird, die in den Grabern ruhen"},
  {"surah_number": 22, "verse_number": 8, "content": "Und unter den Menschen ist manch einer, der ohne Wissen oder Fuhrung oder ein erleuchtendes Buch uber Allah streitet"},
  {"surah_number": 22, "verse_number": 9, "content": "Dem, der sich hochmutig von Allahs Weg abwendet, ist Schande im Diesseits bestimmt; und am Tage der Auferstehung werden Wir ihn die Strafe des Verbrennens kosten lassen"},
  {"surah_number": 22, "verse_number": 10, "content": "Dies ist fur das, was deine Hande vorausgeschickt haben: denn Allah ist nicht ungerecht gegen Seine Diener"},
  {
    "surah_number": 22,
    "verse_number": 11,
    "content": "Und unter den Menschen ist manch einer, der Allah nur am Rande dient. Wenn ihn Gutes trifft, so ist er damit zufrieden; trifft ihn aber eine Prufung, dann kehrt er zu seinem (fruheren) Weg zuruck. Er verliert diese Welt so gut wie die kunftige. Das ist der deutliche Verlust"
  },
  {"surah_number": 22, "verse_number": 12, "content": "Er ruft statt Allah das an, was ihm weder zu schaden noch zu nutzen vermag. Dies ist der weitbegangene Irrtum"},
  {"surah_number": 22, "verse_number": 13, "content": "Er ruft den an, dessen Schaden naher ist als sein Nutzen. Ubel ist wahrlich der Beschutzer und ubel ist wahrlich der Gefahrte"},
  {"surah_number": 22, "verse_number": 14, "content": "Wahrlich, Allah wird jene, die glauben und gute Werke tun, in Garten fuhren, durch die Bache fließen; siehe, Allah tut, was Er will"},
  {"surah_number": 22, "verse_number": 15, "content": "Wer da meint, daß Allah ihm niemals im Diesseits und im Jenseits helfen werde, der soll doch mit Hilfe eines Seils zum Himmel emporsteigen und es abschneiden. Dann soll er sehen, ob seine List das hinweg nehmen wird, was (ihn) erzurnt"},
  {"surah_number": 22, "verse_number": 16, "content": "Und so haben Wir ihn (den Quran) als deutliches Zeichen hinabgesandt, und gewiß, Allah weist dem den Weg, dem Er will"},
  {"surah_number": 22, "verse_number": 17, "content": "Siehe, zwischen denen, die glaubig sind, und den Juden und den Sabaern und den Christen und den Magiern und den Gotzendienern wird Allah wahrlich am Tage der Auferstehung richten; denn Allah ist der Zeuge aller Dinge"},
  {
    "surah_number": 22,
    "verse_number": 18,
    "content":
        "Hast du nicht gesehen, daß sich vor Allah anbetend niederwirft, wer in den Himmeln und auf Erden ist - ebenso die Sonne und der Mond und die Sterne und die Berge und die Baume und die Tiere und viele Menschen? Vielen aber ist die Strafe fallig geworden. Und wer von Allah erniedrigt wird, dem kann keiner Ehre geben. Wahrlich, Allah tut, was Er will"
  },
  {"surah_number": 22, "verse_number": 19, "content": "Diese beiden sind zwei Streitende, die uber ihren Herrn hadern. Fur die, die nun unglaubig sind, werden Kleider aus Feuer zurechtgeschnitten werden; siedendes Wasser wird uber ihre Kopfe gegossen werden"},
  {"surah_number": 22, "verse_number": 20, "content": "wodurch das, was in ihren Bauchen ist, und ihre Haut schmelzen wird"},
  {"surah_number": 22, "verse_number": 21, "content": "Und ihnen sind eiserne Keulen bestimmt"},
  {"surah_number": 22, "verse_number": 22, "content": "Sooft sie aus Bedrangnis daraus zu entrinnen streben, sollen sie wieder dahin zuruckgetrieben werden; und (es heißt): \"Kostet die Strafe des Verbrennens"},
  {"surah_number": 22, "verse_number": 23, "content": "Doch Allah wird jene, die glaubig sind und gute Werke tun, in Garten fuhren, durch welche Bache fließen. Sie sollen darin mit Annspangen von Gold und Perlen geschmuckt sein, und ihre Gewander darin sollen aus Seide sein"},
  {"surah_number": 22, "verse_number": 24, "content": "Und sie werden zu lauterster Rede rechtgeleitet werden, und sie werden zu dem Weg des Preiswurdigen rechtgefuhrt werden"},
  {
    "surah_number": 22,
    "verse_number": 25,
    "content":
        "Diejenigen aber, die unglaubig sind und vom Weg Allahs abhalten und von der heiligen Moschee - die Wir zum Wohl aller Menschen bestimmt haben, gleichviel ob sie dort angesiedelt oder Wustenbewohner sind -, und diejenigen, die hier durch Ruchlosigkeit irgendeinen krummen Weg suchen: Wir werden sie schmerzliche Strafe kosten lassen"
  },
  {"surah_number": 22, "verse_number": 26, "content": "Und als Wir fur Abraham die Statte des Hauses bestimmten (, sprachen Wir): \"Setze Mir nichts zur Seite und halte Mein Haus rein fur die (es) Umkreisenden, Betenden und Sich-Niederwerfenden"},
  {"surah_number": 22, "verse_number": 27, "content": "Und rufe die Menschen zur Pilgerfahrt auf. Sie werden zu Fuß und auf jedem mageren Kamel aus allen entfernsten Gegenden zu dir kommen"},
  {"surah_number": 22, "verse_number": 28, "content": "auf daß sie allerlei Vorteile wahrnehmen und wahrend einer bestimmten Anzahl von Tagen des Namens Allahs fur das gedenken mogen, was Er ihnen an Vieh gegeben hat. Darum esset davon und speist den Notleidenden, den Bedurftigen"},
  {"surah_number": 22, "verse_number": 29, "content": "Dann sollen sie ihre personliche Reinigung vollziehen und ihre Gelubde erfullen und um das Altehrwurdige Haus wandeln"},
  {"surah_number": 22, "verse_number": 30, "content": "Somit wird es fur den, der die Gebote Allahs ehrt, gut vor seinem Herrn sein. Erlaubt ist euch alles Vieh mit Ausnahme dessen, was euch angesagt worden ist. Meidet darum den Greuel der Gotzen und meidet das Wort der Luge"},
  {"surah_number": 22, "verse_number": 31, "content": "Seid lauter gegen Allah, ohne daß ihr Ihm etwas zur Seite stellt. Und wer Allah etwas zur Seite stellt, ist (so), als fiele er vom Himmel und die Vogel erhaschten ihn oder der Wind verwehte ihn an einen fernen Ort"},
  {"surah_number": 22, "verse_number": 32, "content": "Somit ruhrt das wahrlich von der Gottesfurchtigkeit der Herzen her, wenn einer die Gebote Allahs ehrt"},
  {"surah_number": 22, "verse_number": 33, "content": "Die (Opfertiere) bringen euch Nutzen fur eine bestimmte Frist, dann aber ist ihr (Opfer-)Platz bei dem Altehrwurdigen Haus"},
  {"surah_number": 22, "verse_number": 34, "content": "Und jedem Volk gaben Wir eine Anleitung zur Opferung, auf daß sie des Namens Allahs fur das gedenken mogen, was Er ihnen an Vieh gegeben hat. So ist euer Gott ein Einziger Gott; darum ergebt euch Ihm. Und die frohe Botschaft gib den Demutigen"},
  {"surah_number": 22, "verse_number": 35, "content": "deren Herzen mit Furcht erfullt sind, wenn Allah erwahnt wird, und die geduldig ertragen, was sie trifft, und die das Gebet verrichten und von dem spenden, was Wir ihnen gegeben haben"},
  {
    "surah_number": 22,
    "verse_number": 36,
    "content":
        "Und fur den Opferbrauch Allahs haben Wir fur euch die großen Kamele bestimmt. An ihnen habt ihr viel Gutes. So sprecht den Namen Allahs uber sie aus, wenn sie gereiht dastehen. Und wenn ihre Seiten (auf dem Boden) liegen, so esset davon und speist den Genugsamen und den Bittenden. So haben Wir sie euch dienstbar gemacht, auf daß ihr dankbar sein moget"
  },
  {
    "surah_number": 22,
    "verse_number": 37,
    "content": "Ihr Fleisch erreicht Allah nicht, noch tut es ihr Blut, sondern eure Ehrfurcht ist es, die Ihn erreicht. In der Weise hat Er sie euch dienstbar gemacht, auf daß ihr Allah fur Seine Rechtleitung preiset. Und gib denen frohe Botschaft, die Gutes tun"
  },
  {"surah_number": 22, "verse_number": 38, "content": "Wahrlich, Allah verteidigt jene, die glaubig sind. Gewiß, Allah liebt keinen Treulosen, Undankbaren"},
  {"surah_number": 22, "verse_number": 39, "content": "Die Erlaubnis (, sich zu verteidigen,) ist denen gegeben, die bekampft werden, weil ihnen Unrecht geschah - und Allah hat wahrlich die Macht, ihnen zu helfen"},
  {
    "surah_number": 22,
    "verse_number": 40,
    "content":
        "jenen, die schuldlos aus ihren Hausern vertrieben wurden, nur weil sie sagten: \"Unser Herr ist Allah.\" Und wenn Allah nicht die einen Menschen durch die anderen zuruckgehalten hatte, so waren gewiß Klausen, Kirchen, Synagogen und Moscheen, in denen der Name Allahs oft genannt wird, niedergerissen worden. Und Allah wird sicher dem beistehen, der Ihm beisteht. Allah ist wahrlich Allmachtig, Erhaben"
  },
  {"surah_number": 22, "verse_number": 41, "content": "Jenen, die, wenn Wir ihnen auf Erden die Oberhand gegeben haben, das Gebet verrichten und die Zakah entrichten und Gutes gebieten und Boses verbieten (, steht Allah bei) Und Allah bestimmt den Ausgang aller Dinge"},
  {"surah_number": 22, "verse_number": 42, "content": "Wenn sie dich der Luge bezichtigen, so waren schon vor ihnen das Volk Noahs und die `Ad und die Tamud Verleugner"},
  {"surah_number": 22, "verse_number": 43, "content": "sowie auch das Volk Abrahams und das Volk Lots"},
  {"surah_number": 22, "verse_number": 44, "content": "und die Bewohner von Madyan. Auch Moses wurde der Luge bezichtigt. Ich gewahrte alsdann den Unglaubigen Aufschub; dann erfaßte Ich sie, und wie war es mit der Verleugnung"},
  {"surah_number": 22, "verse_number": 45, "content": "Wie so manche Stadt haben Wir zerstort, weil sie voll des Frevels war, daß sie nunmehr in Trummern steht, und (wie so) manch verlassenen Brunnen und manch hochragendes Schloß (haben Wir zerstort)"},
  {"surah_number": 22, "verse_number": 46, "content": "Sind sie denn nicht im Lande umhergereist, so daß sie Herzen haben konnten, um zu begreifen, oder Ohren, um zu horen? Denn wahrlich, es sind ja nicht die Augen, die blind sind, sondern blind sind die Herzen in der Brust"},
  {"surah_number": 22, "verse_number": 47, "content": "Und sie fordern dich auf, die Strafe zu beschleunigen, doch Allah wird nie Sein Versprechen brechen. Wahrlich, ein Tag bei deinem Herrn ist gleich tausend Jahre nach eurer Zeitrechnung"},
  {"surah_number": 22, "verse_number": 48, "content": "Und manch einer Stadt gewahrte Ich Aufschub, obgleich sie voll des Frevels war. Zuletzt aber erfaßte Ich sie, und zu Mir ist die Heimkehr"},
  {"surah_number": 22, "verse_number": 49, "content": "Sprich: \"O ihr Menschen, ich bin euch nur ein deutlicher Warner"},
  {"surah_number": 22, "verse_number": 50, "content": "Fur diejenigen die glauben und gute Werke tun, ist Vergebung und eine ehrenvolle Versorgung bereit"},
  {"surah_number": 22, "verse_number": 51, "content": "Diejenigen aber, die gegen Unsere Zeichen eifern und (sie) zu besiegen versuchen - diese sind die Bewohner der Gahim"},
  {
    "surah_number": 22,
    "verse_number": 52,
    "content": "Und Wir schickten vor dir keinen Gesandten oder Propheten, dem, wenn er etwas wunschte, Satan seinen Wunsch nicht (zu) beeinflussen (trachtete) Doch Allah macht zunichte, was Satan einstreut. Dann setzt Allah Seine Zeichen fest. Und Allah ist Allwissend, Allweise"
  },
  {"surah_number": 22, "verse_number": 53, "content": "(Er laßt dies zu,) damit Er das, was Satan einstreut, zur Prufung fur die machen kann, in deren Herzen Krankheit ist und deren Herzen verhartet sind; wahrlich, die Frevler befinden sich in außerster Auflehnung"},
  {
    "surah_number": 22,
    "verse_number": 54,
    "content": "Und (Er laßt dies zu,) damit diejenigen, denen das Wissen gegeben wurde, erkennen, daß es die Wahrheit von deinem Herrn ist, auf daß sie daran glauben und ihre Herzen sich Ihm friedvoll unterwerfen mogen. Und siehe, Allah leitet jene, die glaubig sind, auf den geraden Weg"
  },
  {"surah_number": 22, "verse_number": 55, "content": "Und die Unglaubigen werden nicht (eher) aufhoren, daran Zweifel zu hegen, bis die Stunde unerwartet uber sie hereinbricht oder die Strafe eines unheilvollen Tages uber sie kommt"},
  {"surah_number": 22, "verse_number": 56, "content": "Das Konigreich wird an jenem Tage Allah gehoren. Er wird zwischen ihnen richten. Also werden jene, die glaubig sind und gute Werke tun, in den Garten der Wonne sein"},
  {"surah_number": 22, "verse_number": 57, "content": "Fur diejenigen aber, die unglaubig sind und Unsere Zeichen verwerfen, ist eine Strafe (vorgesehen), die schmachvoll ist"},
  {"surah_number": 22, "verse_number": 58, "content": "Und denjenigen, die um Allahs willen auswandern und dann erschlagen werden oder sterben, wird Allah eine stattliche Versorgung bereiten. Wahrlich, Allah - Er ist der beste Versorger"},
  {"surah_number": 22, "verse_number": 59, "content": "Er wird sie gewiß in einen Ort eingehen lassen, mit dem sie wohl zufrieden sind. Und Allah ist wahrlich Allwissend, Nachsichtig"},
  {"surah_number": 22, "verse_number": 60, "content": "Das (soll so sein) Und dem, der Vergeltung in dem Maße ubt, in dem ihm Unrecht zugefugt worden ist, und dann (wiederum) Unrecht erleidet -, dem wird Allah sicherlich zum Sieg verhelfen. Wahrlich, Allah ist Allvergebend, Allverzeihend"},
  {"surah_number": 22, "verse_number": 61, "content": "Dies (geschieht) deshalb, weil Allah die Nacht in den Tag und den Tag in die Nacht ubergehen laßt und weil Allah Allhorend, Allsehend ist"},
  {"surah_number": 22, "verse_number": 62, "content": "Dies ist (so), weil Allah die Wahrheit ist, und (weil) das, was sie an Seiner Statt anrufen, die Luge ist und weil Allah der Erhabene ist, der Große"},
  {"surah_number": 22, "verse_number": 63, "content": "Hast du denn nicht gesehen, daß Allah Wasser vom Himmel herabsendet und (daß) die Erde grun wird? Allah ist wahrlich Gutig, Allkundig"},
  {"surah_number": 22, "verse_number": 64, "content": "Sein ist, was in den Himmeln und was auf der Erde ist, und Allah ist es, Der wahrlich auf keinen angewiesen ist, der Preiswurdige"},
  {
    "surah_number": 22,
    "verse_number": 65,
    "content":
        "Hast du denn nicht gesehen, daß Allah euch dienstbar gemacht hat, was auf Erden ist, und (daß) die Schiffe durcheilen das Meer auf Sein Geheiß? Und Er halt den Himmel zuruck, damit er nicht auf die Erde fallt, es sei denn, mit Seiner Erlaubnis. Wahrlich, Allah ist Gutig und Barmherzig gegen die Menschen"
  },
  {"surah_number": 22, "verse_number": 66, "content": "Und Er ist es, Der euch das Leben gab, und dann wird Er euch sterben lassen, und dann wird Er euch (wieder) lebendig machen. Wahrlich, der Mensch ist hochst undankbar"},
  {"surah_number": 22, "verse_number": 67, "content": "Einem jeden Volke haben Wir Andachtsriten gegeben, die sie befolgen; sie sollen daher nicht mit dir uber diese Sache streiten; sondern rufe (du sie) zu deinem Herrn. Wahrlich, du folgst der rechten Fuhrung"},
  {"surah_number": 22, "verse_number": 68, "content": "Wenn sie jedoch mit dir hieruber streiten, so sprich: \"Allah weiß am besten, was ihr tut"},
  {"surah_number": 22, "verse_number": 69, "content": "Allah wird zwischen euch am Tage der Auferstehung uber das richten, woruber ihr uneinig waret"},
  {"surah_number": 22, "verse_number": 70, "content": "Weißt du nicht, daß Allah das kennt, was im Himmel und was auf der Erde ist? Wahrlich, das steht in einem Buch, das ist fur Allah ein Leichtes"},
  {"surah_number": 22, "verse_number": 71, "content": "Und sie verehren an Allahs Statt das, wofur Er keine Ermachtigung herabgesandt hat und wovon sie keine Kenntnis haben. Und fur die Ungerechten gibt es keinen Helfer"},
  {
    "surah_number": 22,
    "verse_number": 72,
    "content":
        "Und wenn Unsere Verse ihnen verlesen werden, dann kannst du auf dem Antlitz derer, die unglaubig sind, Ablehnung wahrnehmen. Sie mochten am liebsten uber die herfallen, die ihnen Unsere Verse verlesen. Sprich: \"Soll ich euch von etwas Schlimmerem als diesem Kunde geben? Dem Feuer! Allah hat es denen verheißen, die unglaubig sind. Und das ist eine uble Bestimmung"
  },
  {
    "surah_number": 22,
    "verse_number": 73,
    "content":
        "O ihr Menschen, ein Gleichnis ist gepragt, so hort darauf: Gewiß, jene, die ihr an Allahs Statt anruft, werden in keiner Weise vermogen, eine Fliege zu erschaffen, auch dann nicht, wenn sie sich dazu zusammentaten. Und wenn die Fliege ihnen etwas raubte, konnten sie es ihr nicht entreißen. Schwach ist der Suchende wie der Gesuchte"
  },
  {"surah_number": 22, "verse_number": 74, "content": "Sie bewerten Allah nicht nach Seinem wahren Wert. Gewiß, Allah ist Stark, Erhaben"},
  {"surah_number": 22, "verse_number": 75, "content": "Allah erwahlt aus den Engeln Boten und (ebenfalls) aus den Menschen. Siehe, Allah ist Allhorend, Allsehend"},
  {"surah_number": 22, "verse_number": 76, "content": "Er weiß, was vor ihnen ist und was hinter ihnen ist; und zu Allah sollen alle Angelegenheiten zuruckgebracht werden"},
  {"surah_number": 22, "verse_number": 77, "content": "O ihr, die ihr glaubt, verneigt euch und werft euch in Anbetung nieder und verehrt euren Herrn und tut das Gute, auf daß ihr Erfolg haben moget"},
  {
    "surah_number": 22,
    "verse_number": 78,
    "content":
        "Und eifert in Allahs Sache, wie dafur geeifert werden soll. Er hat euch erwahlt und hat euch nichts auferlegt, was euch in der Religion bedrucken konnte, der Religion eures Vaters Abraham. Er (Allah) ist es, Der euch vordem schon Muslime nannte und (nun) in diesem (Buche), damit der Gesandte Zeuge uber euch sei und damit ihr Zeugen uber die Menschen sein moget. Also verrichtet das Gebet und entrichtet die Zakah und haltet an Allah fest. Er ist euer Beschutzer, ein vortrefflicher Beschutzer und ein vortrefflicher Helfer"
  },
  {"surah_number": 23, "verse_number": 1, "content": "Wahrlich, erfolgreich sind die Glaubigen"},
  {"surah_number": 23, "verse_number": 2, "content": "die in ihren Gebeten voller Demut sind"},
  {"surah_number": 23, "verse_number": 3, "content": "und die sich von allem leeren Gerede fernhalten"},
  {"surah_number": 23, "verse_number": 4, "content": "und die die Zakah entrichten"},
  {"surah_number": 23, "verse_number": 5, "content": "und ihre Schamteile bewahren"},
  {"surah_number": 23, "verse_number": 6, "content": "außer gegenuber ihren Gattinnen oder denen, die sie von Rechts wegen besitzen; denn dann sind sie nicht zu tadeln"},
  {"surah_number": 23, "verse_number": 7, "content": "Diejenigen aber, die daruber hinaus etwas begehren, sind Ubertreter"},
  {"surah_number": 23, "verse_number": 8, "content": "Und diejenigen, die das ihnen anvertraute Gut und ihre Verpflichtung huten"},
  {"surah_number": 23, "verse_number": 9, "content": "und die ihre Gebete einhalten"},
  {"surah_number": 23, "verse_number": 10, "content": "dies sind die Erben"},
  {"surah_number": 23, "verse_number": 11, "content": "die Al-Firdaus erben werden. Auf ewig werden sie darin verweilen"},
  {"surah_number": 23, "verse_number": 12, "content": "Und wahrlich, Wir erschufen den Menschen aus einer Substanz aus Lehm"},
  {"surah_number": 23, "verse_number": 13, "content": "Alsdann setzten Wir ihn als Samentropfen an eine sichere Ruhestatte"},
  {
    "surah_number": 23,
    "verse_number": 14,
    "content":
        "Dann bildeten Wir den Tropfen zu einem Blutklumpen; dann bildeten Wir den Blutklumpen zu einem Fleischklumpen; dann bildeten Wir aus dem Fleischklumpen Knochen; dann bekleideten Wir die Knochen mit Fleisch; dann entwickelten Wir es zu einer anderen Schopfung. So sei denn Allah gepriesen, der beste Schopfer"
  },
  {"surah_number": 23, "verse_number": 15, "content": "Dann, danach, werdet ihr mit Gewißheit sterben"},
  {"surah_number": 23, "verse_number": 16, "content": "Dann werdet ihr am Tage der Auferstehung erweckt werden"},
  {"surah_number": 23, "verse_number": 17, "content": "Und Wir haben wahrlich uber euch sieben Himmelsspharen geschaffen, und nie sind Wir gegen die Schopfung unachtsam gewesen"},
  {"surah_number": 23, "verse_number": 18, "content": "Und Wir sandten Wasser vom Himmel in bestimmtem Maß nieder, und Wir ließen es in der Erde ruhen; und Wir vermogen es wieder hinwegzunehmen"},
  {"surah_number": 23, "verse_number": 19, "content": "Dann haben Wir damit fur euch Garten mit Dattelpalmen und Beeren hervorgebracht; an ihnen habt ihr reichlich Fruchte, und von ihnen esset ihr"},
  {"surah_number": 23, "verse_number": 20, "content": "Und (Wir haben) einen Baum (hervorgebracht), der aus dem Berge Sinai emporwachst; er gibt Ol und Wurze fur die Essenden"},
  {"surah_number": 23, "verse_number": 21, "content": "Und betrachtet das Vieh als Lehre fur euch. Wir geben euch von dem zu trinken, was in ihren Leibern ist, und ihr habt von ihnen vielerlei Nutzen, und von ihnen esset ihr"},
  {"surah_number": 23, "verse_number": 22, "content": "Und auf ihnen wie auch in Schiffen werdet ihr getragen"},
  {"surah_number": 23, "verse_number": 23, "content": "Und Wir sandten wahrlich Noah zu seinem Volk, und er sagte: \"O mein Volk, dient Allah. Ihr habt keinen anderen Gott außer Ihm. Wollt ihr also nicht gottesfurchtig sein"},
  {
    "surah_number": 23,
    "verse_number": 24,
    "content": "Aber die Vornehmen seines Volks, die unglaubig waren, sagten: \"Er ist nur ein Mensch wie ihr; er mochte sich bloß uber euch erheben. Hatte Allah gewollt, hatte Er doch gewiß Engel hinabsenden konnen. Wir haben nie von solchem unter unseren Vorvatern gehort"
  },
  {"surah_number": 23, "verse_number": 25, "content": "Er ist nichts anderes als ein Mann, der unter Besessenheit leidet; wartet darum eine Weile mit ihm"},
  {"surah_number": 23, "verse_number": 26, "content": "Er sagte: \"Mein Herr, hilf mir wider ihre Beschuldigung der Luge"},
  {
    "surah_number": 23,
    "verse_number": 27,
    "content":
        "So offenbarten Wir ihm: \"Baue das Schiff unter Unserer Aufsicht und gemaß Unserer Eingebung. Und wenn Unser Befehl ergeht und die Oberflache der Erde (Wasser) hervorwallen laßt, dann nimm ein Paar von jeglicher Gattung an Bord sowie deine Angehorigen mit Ausnahme derer, gegen die das Wort bereits ergangen ist. Und sprich Mich nicht deretwegen an, die gefrevelt haben; denn sie werden ertrankt"
  },
  {"surah_number": 23, "verse_number": 28, "content": "Und wenn du dich auf dem Schiff eingerichtet hast - du und die, die bei dir sind -, dann sprich: \"Alles Lob gebuhrt Allah, Der uns vor dem ruchlosen Volk errettet hat"},
  {"surah_number": 23, "verse_number": 29, "content": "Und sprich: \"Mein Herr, gewahre mir eine gesegnete Unterkunft; denn Du bist der Beste, Der fur die Unterkunft sorgt"},
  {"surah_number": 23, "verse_number": 30, "content": "Wahrlich, hierin liegen Zeichen, und Wir stellen (euch) nur auf die Probe"},
  {"surah_number": 23, "verse_number": 31, "content": "Dann ließen Wir nach ihnen andere Geschlechter entstehen"},
  {"surah_number": 23, "verse_number": 32, "content": "Alsdann sandten Wir ihnen einen Gesandten aus ihrer Mitte (, der sagte): \"Dient Allah. Ihr habt doch keinen anderen Gott als Ihn. Wollt ihr also nicht gottesfurchtig sein"},
  {
    "surah_number": 23,
    "verse_number": 33,
    "content": "Und die Vornehmen seines Volks, die unglaubig waren und die Begegnung im Jenseits leugneten und denen Wir das Wohlleben des irdischen Lebens beschert hatten, sagten: \"Das ist nur ein Mensch wie ihr. Er ißt von dem, was ihr esset, und trinkt von dem, was ihr trinkt"
  },
  {"surah_number": 23, "verse_number": 34, "content": "Und wenn ihr einem Menschen euresgleichen gehorcht, dann werdet ihr gewiß Verlierende sein"},
  {"surah_number": 23, "verse_number": 35, "content": "Verheißt er euch etwa, daß ihr, wenn ihr tot seid und Staub und Gebeine geworden seid, wieder auferstehen werdet"},
  {"surah_number": 23, "verse_number": 36, "content": "Weit, weit hergeholt ist das, was euch da verheißen wird"},
  {"surah_number": 23, "verse_number": 37, "content": "Es gibt kein anderes Leben als unser Leben auf der Erde; wir sterben und wir leben, doch wir werden nicht wieder erweckt werden"},
  {"surah_number": 23, "verse_number": 38, "content": "Er ist nur ein Mensch, der eine Luge gegen Allah erdichtet hat; und wir wollen ihm nicht glauben"},
  {"surah_number": 23, "verse_number": 39, "content": "Er sagte: \"Mein Herr, hilf mir wider ihre Beschuldigung der Luge"},
  {"surah_number": 23, "verse_number": 40, "content": "Er sprach: \"In kurzer Zeit werden sie sicher reumutig werden"},
  {"surah_number": 23, "verse_number": 41, "content": "Da erfaßte sie der Schrei nach Gerechtigkeit, und Wir machten sie zu Spreu. Verflucht sei denn das Volk, das Frevel begeht"},
  {"surah_number": 23, "verse_number": 42, "content": "Dann ließen Wir nach ihnen andere Geschlechter entstehen"},
  {"surah_number": 23, "verse_number": 43, "content": "Kein Volk kann seine festgesetzte Frist beschleunigen, noch kann es sie hinauszogern"},
  {
    "surah_number": 23,
    "verse_number": 44,
    "content": "Dann entsandten Wir Unsere Gesandten, einen nach dem anderen. Sooft ein Gesandter zu seinem Volke kam, ziehen sie ihn der Luge. So ließen Wir sie einander folgen und machten ihr (Schicksal) zu (viel erzahlten) Geschichten. Verflucht sei denn das Volk, das nicht glaubt"
  },
  {"surah_number": 23, "verse_number": 45, "content": "Alsdann sandten Wir Moses und seinen Bruder Aaron mit Unseren Zeichen und einer klaren Vollmacht"},
  {"surah_number": 23, "verse_number": 46, "content": "zu Pharao und seinen Vornehmen; doch sie wandten sich verachtlich ab; denn sie waren ein hochmutiges Volk"},
  {"surah_number": 23, "verse_number": 47, "content": "Sie sagten: \"Sollen wir an zwei uns gleichen Menschen glauben, wo ihr Volk uns doch dienstbar ist"},
  {"surah_number": 23, "verse_number": 48, "content": "So ziehen sie beide der Luge, und sie gehorten zu denen, die venichtet wurden"},
  {"surah_number": 23, "verse_number": 49, "content": "Und wahrlich, Wir gaben Moses das Buch, auf daß sie dem rechten Weg folgen mogen"},
  {"surah_number": 23, "verse_number": 50, "content": "Und Wir machten den Sohn der Maria und seine Mutter zu einem Zeichen und gewahrten ihnen Zuflucht zu einem Hugel mit einer grunen Flache und einem fließenden Quell"},
  {"surah_number": 23, "verse_number": 51, "content": "O ihr Gesandten, esset von den reinen Dingen und tut Gutes. Wahrlich, Ich weiß recht wohl, was ihr tut"},
  {"surah_number": 23, "verse_number": 52, "content": "Und diese eure Gemeinschaft ist eine einheitliche Gemeinschaft, und Ich bin euer Herr. So furchtet Mich"},
  {"surah_number": 23, "verse_number": 53, "content": "Aber sie wurden untereinander uneinig und spalteten sich in Parteien, und jede Partei freute sich uber das, was sie selbst hatte"},
  {"surah_number": 23, "verse_number": 54, "content": "Darum uberlaß sie eine Zeitlang ihrer Unwissenheit"},
  {"surah_number": 23, "verse_number": 55, "content": "Meinen sie denn, wenn Wir sie reichlich mit Glucksgutern und Sohnen versorgen"},
  {"surah_number": 23, "verse_number": 56, "content": "wurden Wir Uns beeilen, ihnen Gutes zu tun? Nein, sie nehmen es nicht wahr"},
  {"surah_number": 23, "verse_number": 57, "content": "Wahrlich, jene, die sich aus Furcht vor ihrem Herrn Sorge tragen"},
  {"surah_number": 23, "verse_number": 58, "content": "und jene, die an die Zeichen ihres Herrn glauben"},
  {"surah_number": 23, "verse_number": 59, "content": "und jene, die ihrem Herrn nichts zur Seite stellen"},
  {"surah_number": 23, "verse_number": 60, "content": "und jene, die da spenden, was zu spenden ist, und jene, deren Herzen beben, weil sie zu ihrem Herrn zuruckkehren werden"},
  {"surah_number": 23, "verse_number": 61, "content": "sie sind es, die sich bei guten Werken beeilen und ihnen darin voraus sind"},
  {"surah_number": 23, "verse_number": 62, "content": "Und Wir fordern von keiner Seele etwas uber das hinaus, was sie zu leisten vermag. Und Wir haben ein Buch, das die Wahrheit spricht; und es soll ihnen kein Unrecht geschehen"},
  {"surah_number": 23, "verse_number": 63, "content": "Nein, ihre Herzen beachten dieses (Buch) ganz und gar nicht, und außerdem gibt es da Tatigkeiten von ihnen, die sie fortfuhren"},
  {"surah_number": 23, "verse_number": 64, "content": "siehe, bis daß sie, wenn Wir die Wohlhabenden unter ihnen mit Strafe erfassen, um Hilfe rufen"},
  {"surah_number": 23, "verse_number": 65, "content": "Schreit heute nicht um Hilfe; denn ihr werdet bei Uns keine Hilfe finden"},
  {"surah_number": 23, "verse_number": 66, "content": "Meine Verse wurden euch doch verlesen, ihr aber pflegtet euch auf euren Fersen umzukehren"},
  {"surah_number": 23, "verse_number": 67, "content": "hochmutig erwahntet ihr ihn (den Quran) und nachts meidet ihr ihn"},
  {"surah_number": 23, "verse_number": 68, "content": "Haben sie denn das Wort nicht bedacht, oder ist zu ihnen das gekommen, was nicht zu ihren Vorvatern kam"},
  {"surah_number": 23, "verse_number": 69, "content": "Oder kennen sie ihren Gesandten nicht, so daß sie ihn verleugnen"},
  {"surah_number": 23, "verse_number": 70, "content": "Oder sagen sie: \"Er ist ein Besessener?\" Nein, er hat ihnen die Wahrheit gebracht, und die meisten von ihnen hassen die Wahrheit"},
  {
    "surah_number": 23,
    "verse_number": 71,
    "content": "Und wenn die Wahrheit sich nach ihren Begierden gerichtet hatte; wahrlich, die Himmel und die Erde und wer darin ist, waren in Unordnung gesturzt worden. Nein, Wir haben ihnen ihre Mahnung gebracht, doch von ihrer eigenen Mahnung kehren sie sich ab"
  },
  {"surah_number": 23, "verse_number": 72, "content": "Oder forderst du etwa von ihnen einen Lohn? Doch der Lohn deines Herrn ist besser; und Er ist der beste Versorger"},
  {"surah_number": 23, "verse_number": 73, "content": "Und gewiß, du rufst sie zu einem geraden Weg auf"},
  {"surah_number": 23, "verse_number": 74, "content": "Und jene, die nicht an das Jenseits glauben, weichen wahrlich von dem Weg ab"},
  {"surah_number": 23, "verse_number": 75, "content": "Und hatten Wir Uns ihrer erbarmt und sie von ihrer Drangsal befreit, wurden sie dennoch in ihrer Widerspenstigkeit verharren und verblendet in die Irre gehen"},
  {"surah_number": 23, "verse_number": 76, "content": "Und wahrlich, Wir haben sie mit Strafe erfaßt, doch sie haben sich ihrem Herrn weder unterworfen, noch haben sie sich gedemutigt"},
  {"surah_number": 23, "verse_number": 77, "content": "bis daß Wir ihnen ein Tor zu strenger Strafe offnen. Siehe, da werden sie hieruber in Verzweiflung sturzen"},
  {"surah_number": 23, "verse_number": 78, "content": "Und Er ist es, Der euch Ohren, Augen und Herzen geschaffen hat. Wie wenig dankbar seid ihr"},
  {"surah_number": 23, "verse_number": 79, "content": "Und Er ist es, Der euch auf der Erde vermehrt hat, und vor Ihm werdet ihr versammelt werden"},
  {"surah_number": 23, "verse_number": 80, "content": "Und Er ist es, Der leben und sterben laßt, und in Seinen Handen ruht der Wechsel von Nacht und Tag. Wollt ihr es denn nicht begreifen"},
  {"surah_number": 23, "verse_number": 81, "content": "Nein, sie sagten aber, wie schon die Fruheren sagten"},
  {"surah_number": 23, "verse_number": 82, "content": "Sie sagten: \"Wie? Wenn wir gestorben und zu Staub und Gebein geworden sind, sollen wir dann wirklich aufer weckt werden"},
  {"surah_number": 23, "verse_number": 83, "content": "Dies ist uns verheißen worden, uns und zuvor unseren Vatern. Das sind ja nichts als Fabeln der Fruheren"},
  {"surah_number": 23, "verse_number": 84, "content": "Sprich: \"Wessen ist die Erde, und (wessen,) wer auf ihr ist, wenn ihr es wisset"},
  {"surah_number": 23, "verse_number": 85, "content": "Sie werden sagen: \"Allah.\" Sprich: \"Wollt ihr denn nicht nachdenken"},
  {"surah_number": 23, "verse_number": 86, "content": "Sprich: \"Wer ist der Herr der sieben Himmel und der Herr des Gewaltigen Throns"},
  {"surah_number": 23, "verse_number": 87, "content": "Sie werden sagen: \"(Sie sind) Allahs.\" Sprich: \"Wollt ihr denn nicht gottesfurchtig sein"},
  {"surah_number": 23, "verse_number": 88, "content": "Sprich: \"Wer ist es in Dessen Hand die Herrschaft uber alle Dinge ist, und Der Schutz gewahrt, aber vor Dem es keinen Schutz gibt, wenn ihr es wisset"},
  {"surah_number": 23, "verse_number": 89, "content": "Sie werden sagen: \"(All dies ist) Allahs.\" Sprich: \"Wieso also seid ihr verblendet"},
  {"surah_number": 23, "verse_number": 90, "content": "Doch Wir haben ihnen die Wahrheit gebracht, und wahrlich, sie leugnen (sie)"},
  {
    "surah_number": 23,
    "verse_number": 91,
    "content":
        "Allah hat Sich keinen Sohn genommen, noch ist irgendein Gott neben Ihm: sonst wurde jeder Gott mit sich fortgenommen haben, was er erschaffen hatte, und die einen von ihnen hatten sich sicher gegen die anderen erhoben. Gepriesen sei Allah (und Erhaben) uber all das, was sie beschreiben"
  },
  {"surah_number": 23, "verse_number": 92, "content": "(Er ist) der Kenner des Verborgenen und des Offenbaren! Erhaben ist Er darum uber das, was sie Ihm beigesellen"},
  {"surah_number": 23, "verse_number": 93, "content": "Sprich: \"Mein Herr, wenn Du mich schauen lassen willst, was ihnen angedroht wird"},
  {"surah_number": 23, "verse_number": 94, "content": "dann, mein Herr, setze mich nicht zu dem Volk der Frevler"},
  {"surah_number": 23, "verse_number": 95, "content": "Und Wir haben wahrlich die Macht, dich schauen zu lassen, was Wir ihnen androhen"},
  {"surah_number": 23, "verse_number": 96, "content": "Wehre das Bose mit dem ab, was das Beste ist. Wir wissen recht wohl, was fur Dinge sie behaupten"},
  {"surah_number": 23, "verse_number": 97, "content": "Und sprich: \"Mein Herr, ich nehme meine Zuflucht bei Dir vor den Einflusterungen der Satane"},
  {"surah_number": 23, "verse_number": 98, "content": "Und ich nehme meine Zuflucht bei Dir, mein Herr, damit sie sich mir nicht nahern"},
  {"surah_number": 23, "verse_number": 99, "content": "Wenn dann der Tod an einen von ihnen herantritt, sagt er: \"Mein Herr, bringe mich zuruck"},
  {"surah_number": 23, "verse_number": 100, "content": "auf daß ich Gutes tue von dem, was ich unterlassen habe.\" Keineswegs, es ist nur ein Wort, das er ausspricht. Und hinter ihnen liegt eine Trennstrecke zuruck bis zu dem Tage, an dem sie auferweckt werden"},
  {"surah_number": 23, "verse_number": 101, "content": "Wenn dann der Stoß in den Sur erfolgt ist, gibt es zwischen ihnen an jenem Tage keine Verwandtschaftsbande (mehr), und sie werden einander nicht befragen"},
  {"surah_number": 23, "verse_number": 102, "content": "Dann werden die, deren Waagschalen schwer sind, die Erfolgreichen sein"},
  {"surah_number": 23, "verse_number": 103, "content": "Jene aber, deren Waagschalen leicht sind, werden die sein, die ihrer selbst verlustig gegangen sind; in Gahannam werden sie auf ewig bleiben"},
  {"surah_number": 23, "verse_number": 104, "content": "Das Feuer wird ihre Gesichter verbrennen, und sie werden darin mißgebildet sein"},
  {"surah_number": 23, "verse_number": 105, "content": "Wurden euch Meine Verse nicht verlesen, ohne daß ihr diese fur Luge erklart habt"},
  {"surah_number": 23, "verse_number": 106, "content": "Sie werden sagen: \"Unser Herr, unsere Unseligkeit uberkam uns, und wir waren ein irrendes Volk"},
  {"surah_number": 23, "verse_number": 107, "content": "Unser Herr, fuhre uns aus ihr (der Holle) heraus. Wenn wir (zum Ungehorsam) zuruckkehren, dann werden wir wahrlich Frevler sein"},
  {"surah_number": 23, "verse_number": 108, "content": "Er wird sprechen: \"Hinab mit euch darein, und redet nicht mit Mir"},
  {"surah_number": 23, "verse_number": 109, "content": "Wahrlich, es gab eine Anzahl unter Meinen Dienern, die zu sagen pflegten: \"Unser Herr, wir glauben; vergib uns darum und erbarme Dich unser; denn Du bist der beste Erbarmer"},
  {"surah_number": 23, "verse_number": 110, "content": "Ihr aber habt sie mit Spott behandelt, so sehr, daß sie euch Meine Ermahnung vergessen ließen, wahrend ihr sie auslachtet"},
  {"surah_number": 23, "verse_number": 111, "content": "Ich habe sie heute belohnt; denn sie waren geduldig. Wahrlich, sie sind es, die den Sieg erreicht haben"},
  {"surah_number": 23, "verse_number": 112, "content": "Er wird sprechen: \"Wie viele Jahre verweiltet ihr auf Erden"},
  {"surah_number": 23, "verse_number": 113, "content": "Sie sagen: \"Wir verweilten einen Tag oder den Teil eines Tages; doch frage diejenigen, die rechnen konnen"},
  {"surah_number": 23, "verse_number": 114, "content": "Er spricht: \"lhr verweiltet nur kurze Zeit, wenn ihr es nur wußtet"},
  {"surah_number": 23, "verse_number": 115, "content": "Glaubtet ihr denn, Wir hatten euch in Sinnlosigkeit erschaffen, und ihr wurdet nicht zu Uns zuruckgebracht"},
  {"surah_number": 23, "verse_number": 116, "content": "Darum ist Allah, der Wahre Konig, Hoch Erhaben. Es ist kein Gott außer Ihm, dem Herrn des Wurdigen Throns"},
  {"surah_number": 23, "verse_number": 117, "content": "Und wer neben Allah einen anderen Gott anruft, fur den er keinen Beweis hat, der wird seinem Herrn Rechenschaft abzulegen haben. Wahrlich, die Unglaubigen haben kei nen Erfolg"},
  {"surah_number": 23, "verse_number": 118, "content": "Und sprich: \"Mein Herr, vergib (uns) und habe Erbarmen (mit uns); denn Du bist der beste Erbarmer"},
  {"surah_number": 24, "verse_number": 1, "content": "(Dies ist) eine Sura, die Wir hinabsandten und die Wir zum Gesetz erhoben, und worin Wir deutliche Zeichen offenbarten, auf das ihr ermahnt sein moget"},
  {
    "surah_number": 24,
    "verse_number": 2,
    "content":
        "Peitscht die Unzuchtige und den Unzuchtigen gegebenenfalls jeweils mit hundert Peitschenhieben aus; und lasset euch angesichts dieser Vorschrift Allahs nicht von Mitleid mit den beiden ergreifen, wenn ihr an Allah und an den Jungsten Tag glaubt. Und eine Anzahl der Glaubigen soll ihrer Pein beiwohnen"
  },
  {"surah_number": 24, "verse_number": 3, "content": "Ein Unzuchtiger darf nur eine Unzuchtige oder eine Gotzendienerin heiraten, und eine Unzuchtige darf nur einen Unzuchtigen oder einen Gotzendiener heiraten; den Glaubigen aber ist das verwehrt"},
  {"surah_number": 24, "verse_number": 4, "content": "Und denjenigen, die ehrbaren Frauen (Unkeuschheit) vorwerfen, jedoch nicht vier Zeugen (dafur) beibringen, verabreicht achtzig Peitschenhiebe. Und lasset ihre Zeugenaussage niemals gelten; denn sie sind es, die Frevler sind"},
  {"surah_number": 24, "verse_number": 5, "content": "außer jenen, die es hernach bereuen und sich bessern; denn wahrlich, Allah ist Allvergebend, Barmherzig"},
  {"surah_number": 24, "verse_number": 6, "content": "Und (was) jene (betrifft), die ihren Gattinnen (Ehebruch) vorwerfen und keine Zeugen (dafur) außer sich selber haben - von solchen Leuten soll die Aussage des Mannes allein (genugen), wenn er viermal bei Allah schwort, daß er die Wahrheit rede"},
  {"surah_number": 24, "verse_number": 7, "content": "und (sein) funfter (Eid) soll sein, daß der Fluch Allahs auf ihm lasten moge, falls er ein Lugner sei"},
  {"surah_number": 24, "verse_number": 8, "content": "Von ihr aber soll die Strafe abgewendet werden, wenn sie viermal den Schwur bei Allah leistet, daß er ein Lugner sei"},
  {"surah_number": 24, "verse_number": 9, "content": "Und (ihr) funfter (Eid) soll sein, daß Allahs Zorn auf ihr lasten moge, falls er die Wahrheit rede"},
  {"surah_number": 24, "verse_number": 10, "content": "Ware nicht Allahs Huld und Seine Barmherzigkeit uber euch und ware Allah nicht Viel vergebend, Allweise (, waret ihr verloren gewesen)"},
  {
    "surah_number": 24,
    "verse_number": 11,
    "content":
        "Diejenigen, welche die große Luge vorbrachten, bilden eine Gruppe von euch. Glaubt nicht, dies sei ubel fur euch; im Gegenteil, es gereicht euch zum Guten. Jedem von ihnen soll die Sunde, die er begangen hat (, vergolten werden); und der von ihnen, der den Hauptanteil daran verschuldete, soll eine schwere Strafe erlei den"
  },
  {"surah_number": 24, "verse_number": 12, "content": "Warum dachten die glaubigen Manner und die glaubigen Frauen, als ihr es hortet, nichts Gutes von ihren eigenen Leuten und sagten: \"Das ist eine offenkundige Luge"},
  {"surah_number": 24, "verse_number": 13, "content": "Warum brachten sie dafur nicht vier Zeugen bei? Da sie keine Zeugen beigebracht haben sind sie es also, die vor Allah die Lugner sind"},
  {"surah_number": 24, "verse_number": 14, "content": "Ware nicht Allahs Huld und Seine Barmherzigkeit im Diesseits und im Jenseits uber euch, hatte euch fur das, worauf ihr euch einließt eine schwere Strafe getroffen"},
  {"surah_number": 24, "verse_number": 15, "content": "Als ihr es mit euren Zungen ubernahmt und ihr mit eurem Mund das ausspracht, wovon ihr keine Kenntnis hattet, da hieltet ihr es fur eine geringe Sache, wahrend es vor Allah eine große war"},
  {"surah_number": 24, "verse_number": 16, "content": "Und warum sagtet ihr nicht, als ihr es hortet: \"Es kommt uns nicht zu, daruber zu reden. Gepriesen seist Du! Dies ist eine arge Verleumdung"},
  {"surah_number": 24, "verse_number": 17, "content": "Allah ermahnt euch, nie wieder dergleichen zu begehen, wenn ihr Glaubige seid"},
  {"surah_number": 24, "verse_number": 18, "content": "Und Allah erklart euch die Gebote; denn Allah ist Allwissend, Allweise"},
  {"surah_number": 24, "verse_number": 19, "content": "Wahrlich, jenen, die wunschen, daß sich Unzucht unter den Glaubigen verbreite, wird im Diesseits und im Jenseits eine schmerzliche Strafe zuteil sein. Und Allah weiß, und ihr wisset nicht"},
  {"surah_number": 24, "verse_number": 20, "content": "Und ware nicht Allahs Huld und Seine Barmherzigkeit uber euch, und ware Allah nicht Gutig, Erbarmend (, waret ihr zugrunde gegangen)"},
  {
    "surah_number": 24,
    "verse_number": 21,
    "content":
        "O ihr, die ihr glaubt, folgt nicht den Schritten des Satans. Und wer den Schritten des Satans folgt, der gebietet gewiß Schandliches und Unrechtes. Und ware nicht uber euch Allahs Huld und Seine Barmherzigkeit, nicht einer von euch ware rein geworden; doch Allah macht rein, wen Er will. Und Allah ist Allhorend, Allwissend"
  },
  {
    "surah_number": 24,
    "verse_number": 22,
    "content":
        "Und die unter euch, die Reichtum im Uberfluß besitzen, sollen nicht schworen, den Anverwandten und den Bedurftigen und den auf Allahs Weg Ausgewanderten nichts zu geben. Sie sollen (vielmehr) vergeben und verzeihen. Wunscht ihr nicht, daß Allah euch vergebe? Und Allah ist Allvergebend, Barmherzig"
  },
  {"surah_number": 24, "verse_number": 23, "content": "Diejenigen, welche den ehrbaren, unbedachten, glaubigen Frauen (Unkeuschheit) vorwerfen, sind im Diesseits und im Jenseits verflucht. Ihnen wird eine schwere Strafe zuteil sein"},
  {"surah_number": 24, "verse_number": 24, "content": "an dem Tage, wo ihre Zungen und ihre Hande und ihre Fuße gegen sie das bezeugen werden, was sie getan haben"},
  {"surah_number": 24, "verse_number": 25, "content": "An dem Tage wird Allah ihnen (alles) nach Gebuhr heimzahlen, und sie werden erfahren, daß Allah allein die lautere Wahrheit ist"},
  {
    "surah_number": 24,
    "verse_number": 26,
    "content": "Schlechte Frauen sind fur schlechte Manner, und schlechte Manner sind fur schlechte Frauen. Und gute Frauen sind fur gute Manner, und gute Manner sind fur gute Frauen; sie sind frei von all dem, was sie (die Verleumder) sagen. Auf sie wartet Vergebung und eine ehrenvolle Versorgung"
  },
  {"surah_number": 24, "verse_number": 27, "content": "O ihr, die ihr glaubt, betretet keine anderen Wohnungen als die euren, bevor ihr nicht um Erlaubnis gebeten und ihre Bewohner gegrußt habt. Das ist besser fur euch, wenn ihr euch ermahnen lasset"},
  {"surah_number": 24, "verse_number": 28, "content": "Und wenn ihr niemanden darin findet, so tretet nicht eher ein, als bis euch die Erlaubnis (dazu) gegeben wird. Und wenn zu euch gesprochen wird: \"Kehrt um\", dann kehrt um; das ist reiner fur euch. Und Allah weiß wohl, was ihr tut"},
  {"surah_number": 24, "verse_number": 29, "content": "Es ist fur euch keine Sunde, wenn ihr in unbewohnte Hauser eintretet, die euch von Nutzen sind. Und Allah weiß, was ihr kundtut und was ihr verbergt"},
  {"surah_number": 24, "verse_number": 30, "content": "Sprich zu den glaubigen Mannern, daß sie ihre Blicke zu Boden schlagen und ihre Keuschheit wahren sollen. Das ist reiner fur sie. Wahrlich, Allah ist dessen, was sie tun, recht wohl kundig"},
  {
    "surah_number": 24,
    "verse_number": 31,
    "content":
        "Und sprich zu den glaubigen Frauen, daß sie ihre Blicke zu Boden schlagen und ihre Keuschheit wahren und ihren Schmuck nicht zur Schau tragen sollen - bis auf das, was davon sichtbar sein darf, und daß sie ihren Schal um ihre Kleidungsausschnitte schlagen und ihren Schmuck vor niemand (anderem) enthullen sollen als vor ihren Gatten oder Vatern oder den Vatern ihrer Gatten oder ihren Sohnen oder den Sohnen ihrer Gatten oder ihren Brudern oder den Sohnen ihrer Bruder oder Sohnen ihrer Schwestern oder ihren Frauen oder denen, die sie von Rechts wegen besitzen, oder solchen von ihren mannlichen Dienern, die keinen Geschlechtstrieb mehr haben, und den Kindern, die der Bloße der Frauen keine Beachtung schenken. Und sie sollen ihre Fuße nicht so (auf den Boden) stampfen, daß bekannt wird, was sie von ihrem Schmuck verbergen. Und wendet euch allesamt reumutig Allah zu, o ihr Glaubigen, auf daß ihr erfolgreich sein moget"
  },
  {"surah_number": 24, "verse_number": 32, "content": "Und verheiratet diejenigen von euch, die ledig sind, und die guten unter euren Sklaven, mannliche wie weibliche. Wenn sie arm sind, so wird Allah sie aus Seiner Fulle reich machen; denn Allah ist Allumfassend, Allwissend"},
  {
    "surah_number": 24,
    "verse_number": 33,
    "content":
        "Und diejenigen, die keine (Gelegenheit) zur Ehe finden, sollen sich keusch halten, bis Allah sie aus Seiner Fulle reich macht. Und jene, die ihr von Rechts wegen besitzt - wenn welche von ihnen eine Freilassungsurkunde begehren, (so) stellt sie ihnen aus, falls ihr von ihnen Gutes wisset; und gebt ihnen von Allahs Reichtum, den Er euch gegeben hat. Und zwingt eure Sklavinnen nicht zur Prostitution, wenn sie ein ehrbares Leben fuhren wollen, nur um die Guter des irdischen Lebens zu erlangen. Werden sie aber (zur Prostitution) gezwungen, dann wird Allah gewiß nach ihrem erzwungenen Tun Allvergebend und Barmherzig (zu ihnen) sein"
  },
  {"surah_number": 24, "verse_number": 34, "content": "Und wahrlich, Wir haben euch deutliche Zeichen niedergesandt und das Beispiel derer, die vor euch dahingingen, und eine Ermahnung fur die Gottesfurchtigen"},
  {
    "surah_number": 24,
    "verse_number": 35,
    "content":
        "Allah ist das Licht der Himmel und der Erde. Sein Licht ist gleich einer Nische, in der sich eine Lampe befindet: Die Lampe ist in einem Glas; das Glas gleich einem funkelnden Stern. Angezundet (wird die Lampe) von einem gesegneten Olbaum, der weder ostlich noch westlich ist, dessen Ol beinahe leuchten wurde, auch wenn das Feuer es nicht beruhrte. Licht uber Licht. Allah leitet zu Seinem Licht, wen Er will. Und Allah pragt Gleichnisse fur die Menschen, und Allah kennt alle Dinge"
  },
  {"surah_number": 24, "verse_number": 36, "content": "(Es ist) in Hausern, fur die Allah die Erlaubnis erteilte, sie sollen errichtet werden und Sein Name soll darin verkundet werden. Darin preisen (sie) Ihn am Morgen und am Abend"},
  {"surah_number": 24, "verse_number": 37, "content": "Manner, die weder Ware noch Handel vom Gedenken an Allah abhalt und der Verrichtung des Gebets und dem Entrichten der Zakah; sie furchten einen Tag, an dem sich Herzen und Augen verdrehen werden"},
  {"surah_number": 24, "verse_number": 38, "content": "Damit Allah sie belohne fur die besten ihrer Taten und ihnen reichlich gebe aus Seiner Fulle. Und Allah versorgt ja, wen Er will, ohne zu rechnen"},
  {
    "surah_number": 24,
    "verse_number": 39,
    "content": "Die aber unglaubig sind - ihre Taten sind wie eine Luftspiegelung in einer Ebene: Der Durstende halt sie fur Wasser, bis er, wenn er hinzutritt, sie als Nichts vorfindet. Doch nahebei findet er Allah, Der ihm seine Abrechnung vollzieht; und Allah ist schnell im Abrechnen"
  },
  {
    "surah_number": 24,
    "verse_number": 40,
    "content":
        "Oder (die Unglaubigen sind) wie Finsternisse in einem tiefen Meer: Eine Woge bedeckt es, uber ihr ist (noch) eine Woge, daruber ist eine Wolke; Finsternisse, eine uber der anderen. Wenn er seine Hand ausstreckt, kann er sie kaum sehen; und wem Allah kein Licht gibt - fur den ist kein Licht"
  },
  {"surah_number": 24, "verse_number": 41, "content": "Hast du nicht gesehen, daß Allah es ist, Den alle lobpreisen, die in den Himmeln und auf Erden sind, und sogar die Vogel im Schwebeflug? Jedes (Geschopf) kennt seine eigene (Weise von) Gebet und Lobpreisung. Und Allah weiß wohl, was sie tun"},
  {"surah_number": 24, "verse_number": 42, "content": "Und Allahs ist das Konigreich der Himmel und der Erde, und zu Allah ist die Heimkehr"},
  {
    "surah_number": 24,
    "verse_number": 43,
    "content":
        "Hast du nicht gesehen, daß Allah die Wolken einhertreibt, sie dann zusammenfugt, sie dann aufeinander schichtet, so daß du Regen aus ihrer Mitte hervorstromen siehst? Und Er sendet vom Himmel Berge (von Wolken) nieder, in denen Hagel ist, und Er trifft damit, wen Er will, und wendet ihn ab, von wem Er will. Der Glanz Seines Blitzes nimmt fast das Augenlicht"
  },
  {"surah_number": 24, "verse_number": 44, "content": "Allah laßt die Nacht und den Tag wechseln. Hierin liegt wahrlich eine Lehre fur solche, die sehen konnen"},
  {
    "surah_number": 24,
    "verse_number": 45,
    "content":
        "Und Allah hat jedes Lebewesen aus Wasser erschaffen. Unter ihnen sind manche, die auf ihren Bauchen kriechen, und unter ihnen sind manche, die auf zwei Beinen gehen, und unter ihnen sind manche, die sich auf vieren fortbewegen. Allah schafft, was Er will. Wahrlich, Allah hat Macht uber alle Dinge"
  },
  {"surah_number": 24, "verse_number": 46, "content": "Wahrlich, Wir haben deutliche Zeichen herabgesandt. Und Allah leitet, wen Er will, auf den geraden Weg"},
  {"surah_number": 24, "verse_number": 47, "content": "Und sie sagen: \"Wir glauben an Allah und an den Gesandten, und wir gehorchen.\" Hierauf aber wenden sich einige von ihnen ab. Und dies sind keine Glaubigen"},
  {"surah_number": 24, "verse_number": 48, "content": "Und wenn sie zu Allah und Seinem Gesandten gerufen werden, damit er zwischen ihnen richte, siehe, dann wendet sich eine Gruppe von ihnen ab"},
  {"surah_number": 24, "verse_number": 49, "content": "Doch wenn das Recht auf ihrer Seite ist, dann kommen sie zu ihm in aller Unterwurfigkeit gelaufen"},
  {"surah_number": 24, "verse_number": 50, "content": "Ist Krankheit in ihren Herzen? Oder zweifeln sie, oder furchten sie, daß Allah und Sein Gesandter ungerecht gegen sie sein wurden? Nein, sie sind es selbst, die Unrecht begehen"},
  {"surah_number": 24, "verse_number": 51, "content": "Doch die Rede der Glaubigen, wenn sie zu Allah und Seinem Gesandten gerufen werden, damit Er zwischen ihnen richten moge, ist nichts anderes als: \"Wir horen und wir gehorchen.\" Und sie sind es, die Erfolg haben werden"},
  {"surah_number": 24, "verse_number": 52, "content": "Und wer Allah und Seinem Gesandten gehorcht und Allah furchtet und sich vor Ihm in acht nimmt: solche sind es, die gluckselig sind"},
  {"surah_number": 24, "verse_number": 53, "content": "Und sie schworen bei Allah ihre festen Eide, sie wurden, wenn du es ihnen befohlest, gewiß ausziehen. Sprich: \"Schwort nicht! Euer Gehorsam ist (uns) bekannt!\" Wahrlich, Allah ist dessen wohl kundig, was ihr tut"},
  {
    "surah_number": 24,
    "verse_number": 54,
    "content":
        "Sprich: \"Gehorcht Allah und gehorcht dem Gesandten.\" Doch wenn ihr euch (von ihm) abkehrt, dann ist er nur fur das verantwortlich, was ihm auferlegt wurde, und ihr seid nur fur das verantwortlich, was euch auferlegt wurde. Und wenn ihr ihm gehorcht, so werdet ihr dem rechten Weg folgen. Und dem Gesandten obliegt nur die deutliche Verkundigung"
  },
  {
    "surah_number": 24,
    "verse_number": 55,
    "content":
        "Verheißen hat Allah denen, die von euch glauben und gute Werke tun, daß Er sie gewiß zu Nachfolgern auf der Erde machen wird, wie Er jene, die vor ihnen waren, zu Nachfolgern machte; und daß Er gewiß fur sie ihre Religion befestigen wird, die Er fur sie auserwahlt hat; und daß Er gewiß ihren (Stand) nach ihrer Furcht in Frieden und Sicherheit verwandeln wird, auf daß sie Mich verehren (und) Mir nichts zur Seite stellen. Wer aber hernach undankbar ist, wird ein Frevler sein"
  },
  {"surah_number": 24, "verse_number": 56, "content": "Und verrichtet das Gebet und entrichtet die Zakah und gehorcht dem Gesandten, auf daß ihr Barmherzigkeit empfangen moget"},
  {"surah_number": 24, "verse_number": 57, "content": "Denke nicht, die, die da unglaubig sind, konnten (ihm) auf Erden entrinnen; ihre Herberge ist das Feuer; und das ist wahrlich ein schlimmes Ende"},
  {
    "surah_number": 24,
    "verse_number": 58,
    "content":
        "O ihr, die ihr glaubt, es sollen (sogar) die, die ihr von Rechts wegen besitzt, und die unter euch, die noch nicht die Reife erlangt haben, euch zu drei Zeiten um Erlaubnis bitten: vor dem Morgengebet, und dann, wenn ihr eure Kleider wegen der Mittagshitze ablegt, und nach dem Abendgebet - (denn dies sind) fur euch drei Zeiten der Zuruckgezogenheit. Danach ist es fur euch und fur sie keine Sunde, wenn die einen von euch sich um die anderen kummern. So macht euch Allah die Zeichen klar, und Allah ist Allwissend, Allweise"
  },
  {"surah_number": 24, "verse_number": 59, "content": "Und wenn die Kinder unter euch ihre Reife erlangen, dann sollen (auch) sie um Erlaubnis bitten, gerade so wie die, die vor ihnen um Erlaubnis gebeten haben. So macht euch Allah Seine Zeichen klar; denn Allah ist Allwissend, Allweise"},
  {
    "surah_number": 24,
    "verse_number": 60,
    "content": "(Was nun) die alteren Frauen (betrifft), die nicht mehr auf Heirat hoffen konnen, so trifft sie kein Vorwurf, wenn sie ihre Tucher ablegen, ohne ihre Zierde zur Schau zu stellen. Aber wenn sie sich dessen enthalten, ist das besser fur sie. Und Allah ist Allhorend, Allwissend"
  },
  {
    "surah_number": 24,
    "verse_number": 61,
    "content":
        "Kein Vorwurf trifft den Blinden, noch trifft ein Vorwurf den Gehbehinderten, kein Vorwurf trifft den Kranken oder euch selbst, wenn ihr in euren eigenen Hausern esset oder den Hausern eurer Vater oder den Hausern eurer Mutter oder den Hausern eurer Bruder oder den Hausern eurer Schwestern oder den Hausern eurer Vatersbruder oder den Hausern eurer Vatersschwestern oder den Hausern eurer Mutterbruder oder den Hausern eurer Mutterschwestern oder in einem (Haus), dessen Schlussel in eurer Obhut sind, oder (in dem Haus) eures Freundes. Es ist keine Sunde fur euch, ob ihr nur zusammen oder getrennt esset. Doch wenn ihr in Hauser eintretet, so begrußt einander mit einem gesegneten, lauteren Gruß von Allah. So macht euch Allah die Gebote klar, auf daß ihr (sie) begreifen moget"
  },
  {
    "surah_number": 24,
    "verse_number": 62,
    "content":
        "Nur diejenigen sind Glaubige, die an Allah und an Seinen Gesandten glauben, und diejenigen, die, wenn sie in einer fur alle wichtigen Angelegenheit bei ihm sind, nicht eher fortgehen, als sie ihn um Erlaubnis (dazu) gebeten haben. Die, die dich um Erlaubnis bitten, sind diejenigen, die (wirklich) an Allah und Seinen Gesandten glauben. Wenn sie dich also um Erlaubnis fur irgendeine eigene Angelegenheit bitten, so erteile dem von ihnen die Erlaubnis, dem du willst, und bitte Allah fur sie um Verzeihung. Wahrlich, Allah ist Allverzeihend, Barmherzig"
  },
  {
    "surah_number": 24,
    "verse_number": 63,
    "content":
        "Erachtet nicht den Ruf des Gesandten unter euch als dem Ruf des einen oder anderen von euch gleichrangig. Allah kennt diejenigen unter euch, die sich hinwegstehlen, indem sie sich verstecken. So mogen sich die, die sich seinem Befehl widersetzen, (davor) huten, daß sie nicht Drangsal befalle oder eine schmerzliche Strafe treffe"
  },
  {"surah_number": 24, "verse_number": 64, "content": "Ist es nicht so, daß Allahs ist, was in den Himmeln und auf der Erde ist? Er kennt euren Zustand wohl. Und an dem Tage, wo sie zu Ihm zuruckgebracht werden, da wird Er ihnen verkunden, was sie getan haben! Und Allah weiß alle Dinge wohl"},
  {"surah_number": 25, "verse_number": 1, "content": "Voller Segen ist Er, Der die Unterscheidung zu Seinem Diener hinabgesandt hat, auf daß er ein Warner fur die Welten sei"},
  {"surah_number": 25, "verse_number": 2, "content": "Er (ist es), Dessen das Konigreich der Himmel und der Erde ist, Der Sich keinen Sohn genommen hat und Der keinen Partner im Konigreich hat und Der jegliches Ding erschaffen und ihm das rechte Maß gegeben hat"},
  {"surah_number": 25, "verse_number": 3, "content": "Und doch haben sie sich Gotter außer Ihm genommen, die nichts erschaffen haben, sondern selbst erschaffen worden sind, die weder fur sich selber Macht uber Schaden und Nutzen noch Macht uber Leben und Tod und Auferweckung haben"},
  {"surah_number": 25, "verse_number": 4, "content": "Und jene, die unglaubig sind, sagen: \"Dies ist ja nichts als eine Luge, die er erdichtet hat; und andere Leute haben ihm dabei geholfen.\" Wahrlich, sie haben da Ungerechtigkeiten und Lugen vorgebracht"},
  {"surah_number": 25, "verse_number": 5, "content": "Und sie sagen: \"(Das sind) Fabeln der Fruheren: er hat sie aufschreiben lassen, und sie werden ihm am Morgen und am Abend diktiert"},
  {"surah_number": 25, "verse_number": 6, "content": "Sprich: \"Er, Der das Verborgene von Himmel und Erde kennt, hat ihn herabgesandt. Er ist wahrlich Allverzeihend, Barmherzig"},
  {"surah_number": 25, "verse_number": 7, "content": "Und sie sagen: \"Was ist mit diesem Gesandten, daß er Speise ißt und auf den Markten umhergeht? Warum ist kein Engel zu ihm herabgesandt worden, um als Warner bei ihm zu sein"},
  {"surah_number": 25, "verse_number": 8, "content": "Oder (warum ist) ihm kein Schatz herabgeworfen oder kein Garten gegeben worden, wovon er essen konnte?\" Und die Ungerechten sagen: \"lhr folgt nur einem Mann, der einem Zauber zum Opfer gefallen ist"},
  {"surah_number": 25, "verse_number": 9, "content": "Schau, wie sie dir Gleichnisse pragen! Sie sind irregegangen und vermogen keinen (Ausweg) zu finden"},
  {"surah_number": 25, "verse_number": 10, "content": "Voller Segen ist Er, Der, wenn Er will, dir Besseres als all dies gewahren wird - Garten, durch die Bache fließen - und dir (auch) Palaste geben wird"},
  {"surah_number": 25, "verse_number": 11, "content": "Nein, sie leugnen die Stunde; und denen, welche die Stunde leugnen, haben Wir einen Hollenbrand bereitet"},
  {"surah_number": 25, "verse_number": 12, "content": "Wenn er sie aus der Ferne wahrnimmt, werden sie horen, wie er grollt und laut aufheult"},
  {"surah_number": 25, "verse_number": 13, "content": "Und wenn sie zusammengekettet in den engen Raum (des Feuers) geworfen werden, dann werden sie dort die Vernichtung wunschen"},
  {"surah_number": 25, "verse_number": 14, "content": "Wunscht heute nicht nur einmal die Vernichtung, sondern wunscht die Vernichtung mehrere Male"},
  {"surah_number": 25, "verse_number": 15, "content": "Sprich: \"Ist dies nun besser oder das Paradies der Ewigkeit, das den Gerechten verheißen wurde? Es wird ihre Belohnung und Bestimmung sein"},
  {"surah_number": 25, "verse_number": 16, "content": "Darin werden sie haben, was immer sie begehren, (und sie werden) auf ewig (darin) verweilen. Dies ist eine Verheißung, die bindend fur deinen Herrn ist"},
  {"surah_number": 25, "verse_number": 17, "content": "Und an dem Tage, da Er sie und jene, die sie an Allahs Statt verehren, versammeln wird, da wird Er fragen: \"Waret ihr es, die Meine Diener irregefuhrt habt, oder sind sie (von) selbst von dem Weg abgeirrt"},
  {"surah_number": 25, "verse_number": 18, "content": "Sie werden sagen: \"Preis Dir! Es geziemte uns nicht, andere Beschutzer als Dich anzunehmen; Du aber beschertest ihnen und ihren Vatern die guten Dinge (dieses Lebens), bis sie die Ermahnung vergaßen und ein verlorenes Volk wurden"},
  {"surah_number": 25, "verse_number": 19, "content": "Nun haben sie euch fur das, was ihr sagtet, der Luge bezichtigt, und ihr konnt weder (die Strafe) abwenden noch (euch) helfen.\" Und den, der von euch Unrecht tut, werden Wir eine große Strafe kosten lassen"},
  {"surah_number": 25, "verse_number": 20, "content": "Auch vor dir schickten Wir keine Gesandten, die keine Speise aßen oder nicht auf den Markten umhergingen. Allein, Wir machen die einen unter euch zur Prufung fur die anderen. Wollt ihr denn geduldig sein? Und dein Herr ist Allsehend"},
  {
    "surah_number": 25,
    "verse_number": 21,
    "content": "Und diejenigen, die nicht mit Unserer Begegnung rechnen, sagen: \"Warum werden keine Engel zu uns herniedergesandt? Oder (warum) sollten wir (nicht) unseren Herrn schauen?\" Wahrlich, sie denken zu hoch von sich und haben die Schranken arg uberschritten"
  },
  {"surah_number": 25, "verse_number": 22, "content": "Am Tage, wenn sie die Engel sehen: Keine frohe Botschaft (sei) fur die Schuldigen an diesem Tage! Und sie werden sagen: \"Das sei verwehrt, verboten"},
  {"surah_number": 25, "verse_number": 23, "content": "Und Wir werden Uns den Werken zuwenden, die sie gewirkt haben, und werden sie wie verwehte Staubchen zunichte machen"},
  {"surah_number": 25, "verse_number": 24, "content": "Die Bewohner des Paradieses werden an jenem Tage die bessere Wohnstatt und den wurdigeren Ruheplatz haben"},
  {"surah_number": 25, "verse_number": 25, "content": "Und an dem Tage wird sich der Himmel mitsamt den Wolken spalten und die Engel werden ununterbrochen herabgesandt"},
  {"surah_number": 25, "verse_number": 26, "content": "Das Konigreich, das wahrhaftige - an jenem Tage wird es des Allerbarmers sein; und ein Tag soll es sein, (der) schwer fur die Unglaubigen (ist)"},
  {"surah_number": 25, "verse_number": 27, "content": "Am Tage, da der Ungerechte sich in die Hande beißen wird, da wird er sagen: \" O ware ich doch den Weg mit dem Gesandten gegangen"},
  {"surah_number": 25, "verse_number": 28, "content": "O wehe mir! Hatte ich doch nimmermehr den Soundso zum Freund genommen"},
  {"surah_number": 25, "verse_number": 29, "content": "Wahrlich, er fuhrte mich irre, hinweg von der Ermahnung, nachdem sie zu mir gekommen war.\" Und Satan laßt den Menschen im Stich"},
  {"surah_number": 25, "verse_number": 30, "content": "Und der Gesandte sagte: \"O mein Herr, mein Volk hat wirklich diesen Quran von sich gewiesen"},
  {"surah_number": 25, "verse_number": 31, "content": "Und so gaben Wir jedem Propheten einen Feind aus den Reihen der Sunder; doch dein Herr genugt als Fuhrer und als Helfer"},
  {"surah_number": 25, "verse_number": 32, "content": "Und jene, die unglaubig sind, sagen: \"Warum ist ihm der Quran nicht in einem Zuge herabgesandt worden?\" Dies (geschieht), weil Wir dein Herz dadurch starken wollen, und Wir haben seine Anordnung recht gut gemacht"},
  {"surah_number": 25, "verse_number": 33, "content": "Und sie legen dir keinen Einwand vor, ohne daß Wir die Wahrheit und die schonste Erklarung brachten"},
  {"surah_number": 25, "verse_number": 34, "content": "Diejenigen, die auf ihren Gesichtern in Gahannam versammelt werden - sie werden in der schlimmsten Lage und vom Wege am weitesten abgeirrt sein"},
  {"surah_number": 25, "verse_number": 35, "content": "Und wahrlich, Wir gaben Moses die Schrift, und zugleich haben Wir (ihm) seinen Bruder Aaron als Helfer zur Seite gestellt"},
  {"surah_number": 25, "verse_number": 36, "content": "Dann sprachen Wir: \"Geht beide zum Volk, das Unsere Zeichen verworfen hat\": dann zerstorten Wir sie alle vollstandig"},
  {"surah_number": 25, "verse_number": 37, "content": "Und das Volk Noahs: Als sie die Gesandten verleugneten, ertrankten Wir sie und machten sie zu einem Zeichen fur die Menschen. Und Wir haben fur die Ungerechten eine schmerzliche Strafe bereitet"},
  {"surah_number": 25, "verse_number": 38, "content": "Und so auch (fur) die `Ad, die Tamud und die Leute vom Brunnen und so viele der Geschlechter zwischen ihnen"},
  {"surah_number": 25, "verse_number": 39, "content": "Ihnen allen pragten Wir Gleichnisse; und sie alle zerstorten Wir vollstandig"},
  {"surah_number": 25, "verse_number": 40, "content": "Und wahrlich, sie kamen voruber an der Stadt, auf die ein Unheilsregen niederging. Haben sie sie denn nicht gesehen? Nein, sie haben nicht mit der Auferstehung gerechnet"},
  {"surah_number": 25, "verse_number": 41, "content": "Und wenn sie dich sehen, treiben sie nur Spott mit dir: \"Ist das der, den Allah als Gesandten erweckt hat"},
  {"surah_number": 25, "verse_number": 42, "content": "Wahrlich, er hatte uns beinahe irregefuhrt, hinweg von unseren Gottern, hatten wir nicht geduldig an ihnen festgehalten.\" Und sie werden erfahren, wenn sie die Strafe sehen, wer weiter vom Weg abgeirrt ist"},
  {"surah_number": 25, "verse_number": 43, "content": "Hast du den gesehen, der seine personliche Neigung zu seinem Gott erhebt? Konntest du wohl sein Wachter sein"},
  {"surah_number": 25, "verse_number": 44, "content": "Meinst du etwa, daß die meisten von ihnen horten oder verstanden? Sie sind nur wie das Vieh - nein, sie sind noch weiter vom Weg abgeirrt"},
  {"surah_number": 25, "verse_number": 45, "content": "Hast du nicht gesehen, wie dein Herr den Schatten verlangert? Und hatte Er gewollt hatte Er ihn stillstehen lassen. Dann haben Wir die Sonne zu seinem Wegweiser gemacht"},
  {"surah_number": 25, "verse_number": 46, "content": "Dann ziehen Wir ihn sachte zu Uns"},
  {"surah_number": 25, "verse_number": 47, "content": "Und Er ist es, Der euch die Nacht zu einer Verhullung und den Schlaf zur Ruhe und den Tag zur Regsamkeit gemacht"},
  {"surah_number": 25, "verse_number": 48, "content": "Und Er ist es, Der die Winde als Freudenboten Seiner Barmherzigkeit herabsendet; und Wir senden reines Wasser aus den Wolken nieder"},
  {"surah_number": 25, "verse_number": 49, "content": "auf daß Wir damit ein totes Land lebendig machen und es Unserer Schopfung zu trinken geben - dem Vieh und den Menschen in großer Zahl"},
  {"surah_number": 25, "verse_number": 50, "content": "Und Wir haben es (das Wasser) unter ihnen verteilt, um sie zu ermahnen. Die meisten Menschen jedoch lehnen alles ab, nur nicht den Unglauben"},
  {"surah_number": 25, "verse_number": 51, "content": "Hatten Wir es gewollt, hatten Wir gewiß in jeder Stadt einen Warner erwecken konnen"},
  {"surah_number": 25, "verse_number": 52, "content": "So gehorche nicht den Unglaubigen, sondern eifere mit ihm (dem Quran) in großem Eifer gegen sie"},
  {"surah_number": 25, "verse_number": 53, "content": "Und Er ist es, Der den beiden Gewassern freien Lauf gelassen hat zu fließen das eine (ist) wohlschmeckend, suß, und das andere salzig, bitter; und zwischen ihnen hat Er eine Scheidewand und eine sichere Schranke gemacht"},
  {"surah_number": 25, "verse_number": 54, "content": "und Er ist es, Der den Menschen aus Wasser erschaffen hat und ihm Blutsverwandtschaft und Schwagerschaft gab; und All machtig ist dein Herr"},
  {"surah_number": 25, "verse_number": 55, "content": "Dennoch verehren sie statt Allah das, was ihnen weder nutzen noch schaden kann. Der Unglaubige ist ein Helfer gegen seinen Herrn"},
  {"surah_number": 25, "verse_number": 56, "content": "Und Wir haben dich nur als Bringer froher Botschaft und als Warner gesandt"},
  {"surah_number": 25, "verse_number": 57, "content": "Sprich: \"lch verlange von euch keinen Lohn dafur, nur (das eine:) daß jeder, der will, den Weg zu seinem Herrn einschlagen mag"},
  {"surah_number": 25, "verse_number": 58, "content": "Und vertraue auf den Lebendigen, Der nicht stirbt, und preise Seine Lobenswurdigkeit. Es genugt, daß Er gegenuber den Sunden Seiner Diener ein Kenner ist"},
  {"surah_number": 25, "verse_number": 59, "content": "Er, Der die Himmel und die Erde und das, was zwischen beiden ist, in sechs Tagen erschuf, wandte Sich alsdann Seinem Reich zu. Der Allerbarmer: Befrage uber Ihn einen, der Kenntnis (von Ihm) hat"},
  {
    "surah_number": 25,
    "verse_number": 60,
    "content": "Und wenn zu ihnen gesprochen wird: \"Fallt vor dem Allerbarmer in Anbetung nieder!\" sagen sie: \"Und was ist der Allerbarmer? Sollen wir vor irgend etwas in Anbetung niederfallen, nur weil du es uns befiehlst?\" Und es verstarkt nur ihren Widerwillen"
  },
  {"surah_number": 25, "verse_number": 61, "content": "Voller Segen ist Er, Der Burgen im Himmel gemacht und eine Leuchte und einen scheinenden Mond darein gestellt hat"},
  {"surah_number": 25, "verse_number": 62, "content": "Und Er ist es, Der die Nacht und den Tag, die einander folgen, gemacht hat fur einen, der (daran) denken oder (dafur) dankbar sein moge"},
  {"surah_number": 25, "verse_number": 63, "content": "Und die Diener des Allerbarmers sind diejenigen, die sanftmutig auf der Erde schreiten; und wenn die Unwissenden sie anreden, sprechen sie friedlich (zu ihnen)"},
  {"surah_number": 25, "verse_number": 64, "content": "sie sind jene, die die Nacht dazu verbringen, um sich niederzuwerfen und zu beten"},
  {"surah_number": 25, "verse_number": 65, "content": "Und sie sind es, die sagen: \"Unser Herr, wende von uns die Strafe der Gahannam ab; denn wahr lich, ihre Pein ist eine bedruckende Qual"},
  {"surah_number": 25, "verse_number": 66, "content": "Sie ist wahrlich schlimm als Ruhestatt und als Aufenthalt"},
  {"surah_number": 25, "verse_number": 67, "content": "Und die, die, wenn sie spenden, weder verschwenderisch noch geizig sind; dazwischen gibt es einen Mittelweg"},
  {
    "surah_number": 25,
    "verse_number": 68,
    "content": "Und die, welche keinen anderen Gott außer Allah anrufen und niemanden toten, dessen Leben Allah unverletzlich gemacht hat - es sei denn, (sie toten) dem Recht nach -, und keine Unzucht begehen: und wer das aber tut, der soll dafur zu bußen haben"
  },
  {"surah_number": 25, "verse_number": 69, "content": "Verdoppelt soll ihm die Strafe am Tage der Auferstehung werden, und er soll darin auf ewig in Schmach bleiben"},
  {"surah_number": 25, "verse_number": 70, "content": "außer denen, die bereuen und glauben und gute Werke tun; denn deren bose Taten wird Allah in gute umwandeln; und Allah ist ja Allverzeihend, Barmherzig"},
  {"surah_number": 25, "verse_number": 71, "content": "Und der, der bereut und Gutes tut, der wendet sich in wahrhafter Reue Allah zu"},
  {"surah_number": 25, "verse_number": 72, "content": "Und diejenigen, die nichts Falsches bezeugen, und die, wenn sie un terwegs leeres Gerede horen, mit Wurde (daran) vorbeigehen"},
  {"surah_number": 25, "verse_number": 73, "content": "Und diejenigen, die, wenn sie mit den Zeichen ihres Herrn ermahnt werden, deswegen nicht wie taub und blind daraufsturzen"},
  {"surah_number": 25, "verse_number": 74, "content": "Und diejenigen, welche sagen: \"Unser Herr, gewahre uns an unseren Frauen und Kindern Augentrost und mache uns zu einem Vorbild fur die Gottesfurchtigen"},
  {"surah_number": 25, "verse_number": 75, "content": "Diese werden mit der hochsten Statte (im Paradies) belohnt, weil sie geduldig waren; und Gruß und Frieden werden sie dort empfangen"},
  {"surah_number": 25, "verse_number": 76, "content": "Ewig darin verweilend: herrlich ist es als Ruhestatt und als Aufenthalt"},
  {"surah_number": 25, "verse_number": 77, "content": "Sprich: \"Was kummert Sich mein Herr um euch, wenn ihr nicht (zu Ihm) betet? Ihr habt (Ihn) ja geleugnet, und das wird (euch) nun anhaften"},
  {"surah_number": 26, "verse_number": 1, "content": "Ta' Sin Mim"},
  {"surah_number": 26, "verse_number": 2, "content": "Das sind die Verse des deutlichen Buches"},
  {"surah_number": 26, "verse_number": 3, "content": "Vielleicht gramst du dich noch zu Tode daruber, daß sie nicht glauben"},
  {"surah_number": 26, "verse_number": 4, "content": "Wenn Wir wollen konnen Wir ihnen ein Zeichen vom Himmel niedersenden, so daß ihre Nacken sich demutig davor beugen"},
  {"surah_number": 26, "verse_number": 5, "content": "Aber nie kommt zu ihnen eine neue Ermahnung vom Aller barmer, ohne daß sie sich davon abkehren"},
  {"surah_number": 26, "verse_number": 6, "content": "Sie haben tatsachlich (die Ermahnung) verworfen; bald aber wird von dem Kunde zu ihnen kommen, was sie verspotteten"},
  {"surah_number": 26, "verse_number": 7, "content": "Haben sie nicht die Erde betrachtet wieviel Wir auf ihr von jeglicher herrlichen Gattung wachsen ließen"},
  {"surah_number": 26, "verse_number": 8, "content": "Darin liegt wahrlich ein Zeichen; jedoch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 9, "content": "Und dein Herr: Er ist wahrlich der Allmachtige, der Barmherzige"},
  {"surah_number": 26, "verse_number": 10, "content": "Und da rief dein Herr Moses an: \"Geh zu dem Volk der Ungerechten"},
  {"surah_number": 26, "verse_number": 11, "content": "dem Volke Pharaos. Wollen sie denn nicht gottesfurchtig sein"},
  {"surah_number": 26, "verse_number": 12, "content": "Er sagte: \"Mein Herr, ich furchte, sie werden mich fur einen Lugner halten"},
  {"surah_number": 26, "verse_number": 13, "content": "und meine Brust wird beklemmt, und meine Zunge den Redefluß versagt. Schicke darum zu Aaron"},
  {"surah_number": 26, "verse_number": 14, "content": "Auch haben sie eine Schuldklage gegen mich erhoben, deshalb furchte ich, daß sie mich umbringen"},
  {"surah_number": 26, "verse_number": 15, "content": "Er sprach: \"Keineswegs! Geht nur beide mit Unseren Zeichen hin. Wir sind mit euch; Wir werden mit euch zuhoren"},
  {"surah_number": 26, "verse_number": 16, "content": "Geht denn zu Pharao und sagt: \"Wir beide sind die Boten des Herrn der Welten"},
  {"surah_number": 26, "verse_number": 17, "content": "Laß die Kinder Israels mit uns zie hen"},
  {"surah_number": 26, "verse_number": 18, "content": "Er (Pharao) sagte: \"Haben wir dich nicht als Kind bei uns aufgezogen? Und du hast viele Jahre deines Lebens bei uns verbracht"},
  {"surah_number": 26, "verse_number": 19, "content": "Und du begingst jene deine Tat, die du begangen hast, und du warst undankbar"},
  {"surah_number": 26, "verse_number": 20, "content": "Er (Moses) sagte: \"lch tat es damals, als ich auf dem Irrweg war"},
  {"surah_number": 26, "verse_number": 21, "content": "Dann floh ich von euch, weil ich euch furchtete; doch (nun) hat mir mein Herr Weisheit geschenkt und mich zu einem Gesandten gemacht"},
  {"surah_number": 26, "verse_number": 22, "content": "Und die Gnade, die du mir vorhaltst, ist die, daß du die Kinder Israels geknechtet hast"},
  {"surah_number": 26, "verse_number": 23, "content": "Pharao sagte: \"Und was ist der Herr der Welten"},
  {"surah_number": 26, "verse_number": 24, "content": "Er (Moses) sagte: \"Er ist der Herr der Himmel und der Erde und dessen, was zwischen den beiden ist, wenn ihr nur Gewißheit wolltet"},
  {"surah_number": 26, "verse_number": 25, "content": "Er (Pharao) sagte zu denen, die um ihn waren: \"Hort ihr nicht"},
  {"surah_number": 26, "verse_number": 26, "content": "Er (Moses) sagte: \" Er ist euer Herr und der Herr eurer Vorvater"},
  {"surah_number": 26, "verse_number": 27, "content": "Er (Pharao) sagte: \"Dieser euer Gesandter, der zu euch entsandt wurde, ist wahrlich ein Besessener"},
  {"surah_number": 26, "verse_number": 28, "content": "Er (Moses) sagte: \"Er ist der Herr des Ostens und des Westens und dessen, was zwischen den beiden ist, wenn ihr es nur begreifen wurdet"},
  {"surah_number": 26, "verse_number": 29, "content": "Er (Pharao) sagte: \"Wenn du einen anderen Gott als mich annimmst, so werde ich dich ganz gewiß zum Gefangnisinsassen machen"},
  {"surah_number": 26, "verse_number": 30, "content": "Er (Moses) sagte: \"Wie? Selbst wenn ich dir etwas bringe, das offenkundig ist"},
  {"surah_number": 26, "verse_number": 31, "content": "Er (Pharao) sagte: \"So bringe es, wenn du die Wahrheit redest"},
  {"surah_number": 26, "verse_number": 32, "content": "Da warf (Moses) seinen Stock hin, und siehe, er wurde eine Schlange, ganz deutlich"},
  {"surah_number": 26, "verse_number": 33, "content": "Und er zog seine Hand hervor, und siehe, sie erschien den Zuschauern weiß"},
  {"surah_number": 26, "verse_number": 34, "content": "Er (Pharao) sagte zu den Vornehmen um ihn: \"Das ist wahrlich ein erfahrener Zauberer"},
  {"surah_number": 26, "verse_number": 35, "content": "Er will euch durch seine Zauberei aus eurem Lande vertreiben. Was ratet ihr nun"},
  {"surah_number": 26, "verse_number": 36, "content": "Sie sagten: \"Halte ihn und seinen Bruder hin und sende Ausrufer in die Stadte"},
  {"surah_number": 26, "verse_number": 37, "content": "die dir alle erfahrenen Zauberer bringen sollen"},
  {"surah_number": 26, "verse_number": 38, "content": "So wurden die Zauberer zur anberaumten Zeit an einem bestimmten Tage versammelt"},
  {"surah_number": 26, "verse_number": 39, "content": "Und es wurde zu den Menschen gesprochen: \"Seid ihr alle da"},
  {"surah_number": 26, "verse_number": 40, "content": "so daß wir den Zauberern folgen konnen, wenn sie die Sieger sind"},
  {"surah_number": 26, "verse_number": 41, "content": "Als die Zauberer kamen, da sagten sie zu Pharao: \"Wird es auch eine Belohnung fur uns geben, wenn wir die Sieger sind"},
  {"surah_number": 26, "verse_number": 42, "content": "Er sagte: \"Ja, und dann werdet ihr zu unseren Nachsten gehoren"},
  {"surah_number": 26, "verse_number": 43, "content": "Moses sagte zu ihnen: \"Werft hin, was ihr zu werfen habt"},
  {"surah_number": 26, "verse_number": 44, "content": "Da warfen sie ihre Stricke und ihre Stocke hin und sagten: \"Bei Pharaos Macht, wir sind es, die sicher siegen werden"},
  {"surah_number": 26, "verse_number": 45, "content": "Dann warf Moses seinen Stock hin, und siehe, er verschlang (all) das, was sie vorgetauscht hatten"},
  {"surah_number": 26, "verse_number": 46, "content": "Da warfen sich die Zauberer anbetend nieder"},
  {"surah_number": 26, "verse_number": 47, "content": "Sie sagten: \"Wir glauben an den Herrn der Welten"},
  {"surah_number": 26, "verse_number": 48, "content": "den Herrn Moses' und Aarons"},
  {
    "surah_number": 26,
    "verse_number": 49,
    "content":
        "Er (Pharao) sagte: \"Glaubt ihr an ihn, bevor ich es euch erlaube? Er ist sicher euer Meister, der euch die Zauberei gelehrt hat. Aber bald sollt ihr es erfahren. Wahrhaftig, ich werde euch die Hande und Fuße wech selweise abhauen (lassen), und wahrhaftig, ich will euch alle kreuzigen (lassen)"
  },
  {"surah_number": 26, "verse_number": 50, "content": "Sie sagten: \"Darin liegt kein Schaden; denn wir werden zu unserem Herrn zuruckkehren"},
  {"surah_number": 26, "verse_number": 51, "content": "Wir hoffen ernsthaft, unser Herr werde uns unsere Sunden vergeben, da wir die ersten der Glaubigen sind"},
  {"surah_number": 26, "verse_number": 52, "content": "Und Wir offenbarten Moses: \"Fuhre Meine Diener nachts hinweg; denn ihr werdet verfolgt werden"},
  {"surah_number": 26, "verse_number": 53, "content": "Und Pharao sandte (Boten) in die Stadte, um zu einer Versammlung aufzurufen"},
  {"surah_number": 26, "verse_number": 54, "content": "Diese sind nur ein kleiner Haufen"},
  {"surah_number": 26, "verse_number": 55, "content": "dennoch haben sie uns erzurnt"},
  {"surah_number": 26, "verse_number": 56, "content": "und wir sind eine wachsame Menge"},
  {"surah_number": 26, "verse_number": 57, "content": "So vertrieben Wir sie aus Garten und von Quellen"},
  {"surah_number": 26, "verse_number": 58, "content": "und aus Schatzen und ehrenvollen Wohnsitzen"},
  {"surah_number": 26, "verse_number": 59, "content": "So (geschah es); und Wir gaben sie den Kindern Israels zum Erbe"},
  {"surah_number": 26, "verse_number": 60, "content": "Und sie verfolgten sie bei Sonnenaufgang"},
  {"surah_number": 26, "verse_number": 61, "content": "Als die beiden Scharen einander ansichtig wurden, sagten die Gefahrten Moses': \"Wir werden sicher eingeholt"},
  {"surah_number": 26, "verse_number": 62, "content": "Er sagte: \"Keineswegs! Mein Herr ist mit mir. Er wird mich richtig fuhren"},
  {"surah_number": 26, "verse_number": 63, "content": "Darauf offenbarten Wir Moses: \"Schlage das Meer mit deinem Stock.\" Und es teilte sich, und jeder Teil erhob sich wie ein gewaltiger Berg"},
  {"surah_number": 26, "verse_number": 64, "content": "Und Wir ließen alsdann die anderen nahe herankommen"},
  {"surah_number": 26, "verse_number": 65, "content": "Und Wir erretteten Moses und alle, die mit ihm waren"},
  {"surah_number": 26, "verse_number": 66, "content": "Dann ertrankten Wir die anderen"},
  {"surah_number": 26, "verse_number": 67, "content": "Hierin ist wahrlich ein Zeichen; doch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 68, "content": "Und wahrlich, dein Herr ist der Allmachtige, der Barmherzige"},
  {"surah_number": 26, "verse_number": 69, "content": "Und verlies ihnen die Geschichte Abrahams"},
  {"surah_number": 26, "verse_number": 70, "content": "als er zu seinem Vater und seinem Volke sagte: \"Was betet ihr an"},
  {"surah_number": 26, "verse_number": 71, "content": "Sie sagten: \"Wir beten Gotzen an, und wir sind ihnen anhaltend zugetan"},
  {"surah_number": 26, "verse_number": 72, "content": "Er sagte: \"Horen sie euch, wenn ihr (sie) anruft"},
  {"surah_number": 26, "verse_number": 73, "content": "Oder nutzen sie oder schaden sie euch"},
  {"surah_number": 26, "verse_number": 74, "content": "Sie sagten: \"Nein, aber wir fanden unsere Vater das gleiche tun"},
  {"surah_number": 26, "verse_number": 75, "content": "Er sagte: \"Seht ihr denn nicht, was ihr da angebetet habt"},
  {"surah_number": 26, "verse_number": 76, "content": "ihr und eure Vorvater"},
  {"surah_number": 26, "verse_number": 77, "content": "Sie sind mir feindlich (gesonnen); nicht aber der Herr der Welten"},
  {"surah_number": 26, "verse_number": 78, "content": "Der mich erschaffen hat; und Er ist es, Der mich richtig fuhrt"},
  {"surah_number": 26, "verse_number": 79, "content": "und Der mir Speise und Trank gibt"},
  {"surah_number": 26, "verse_number": 80, "content": "Und wenn ich krank bin, ist Er es, Der mich heilt"},
  {"surah_number": 26, "verse_number": 81, "content": "und (Er ist es,) Der mich sterben lassen wird und mich dann wieder zum Leben zuruckbringt"},
  {"surah_number": 26, "verse_number": 82, "content": "und von Dem ich hoffe, daß Er mir meine Fehler am Tage des Gerichts vergeben werde"},
  {"surah_number": 26, "verse_number": 83, "content": "Mein Herr, schenke mir Weisheit und fuge mich zu den Rechtschaffenen"},
  {"surah_number": 26, "verse_number": 84, "content": "und verleih mir einen guten Ruf bei den kunftigen Geschlechtern"},
  {"surah_number": 26, "verse_number": 85, "content": "Und mache mich zu einem der Erben des Paradieses der Wonne"},
  {"surah_number": 26, "verse_number": 86, "content": "und vergib meinem Vater; denn er war einer der Irrenden"},
  {"surah_number": 26, "verse_number": 87, "content": "und tue mir an dem Tage, da (die Menschen) auferweckt werden, keine Schande an"},
  {"surah_number": 26, "verse_number": 88, "content": "an dem Tage, da weder Besitz noch Sohne (etwas) nutzen"},
  {"surah_number": 26, "verse_number": 89, "content": "sondern nur der (gerettet werden wird), der mit reinem Herzen zu Allah kommt"},
  {"surah_number": 26, "verse_number": 90, "content": "Und das Paradies wird den Gottesfurchtigen nahegebracht werden"},
  {"surah_number": 26, "verse_number": 91, "content": "Und die Gahim wird denen vor Augen gehalten, die abgeirrt sind"},
  {"surah_number": 26, "verse_number": 92, "content": "und es wird zu ihnen gesprochen: \"Wo ist nun das, was ihr anzubeten pflegtet"},
  {"surah_number": 26, "verse_number": 93, "content": "statt Allah? Konnen sie euch helfen oder sich selber helfen"},
  {"surah_number": 26, "verse_number": 94, "content": "Dann werden sie kopfuber (in die Holle) hineingesturzt werden, sie und diejenigen, die abgeirrt sind"},
  {"surah_number": 26, "verse_number": 95, "content": "und Iblis' Scharen allesamt"},
  {"surah_number": 26, "verse_number": 96, "content": "Sie sagen, indem sie miteinander daruber streiten"},
  {"surah_number": 26, "verse_number": 97, "content": "Bei Allah, wir waren in einem offenkundigen Irrtum"},
  {"surah_number": 26, "verse_number": 98, "content": "als wir euch dem Herrn der Welten gleichsetzten"},
  {"surah_number": 26, "verse_number": 99, "content": "Und es waren nur die Schuldigen, die uns irre fuhrten"},
  {"surah_number": 26, "verse_number": 100, "content": "und nun haben wir weder Fursprecher"},
  {"surah_number": 26, "verse_number": 101, "content": "noch einen treuen Freund"},
  {"surah_number": 26, "verse_number": 102, "content": "Wenn es doch fur uns eine Ruckkehr gabe, (dann) waren wir unter den Glaubigen"},
  {"surah_number": 26, "verse_number": 103, "content": "Hierin ist wahrlich ein Zeichen, jedoch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 104, "content": "Und wahrlich, dein Herr - Er ist der Allmachtige, der Barmherzige"},
  {"surah_number": 26, "verse_number": 105, "content": "Das Volk Noahs bezichtigte den Gesandten der Luge"},
  {"surah_number": 26, "verse_number": 106, "content": "als ihr Bruder Noah zu ihnen sagte: \"Wollt ihr nicht gottesfurchtig sein"},
  {"surah_number": 26, "verse_number": 107, "content": "In Wahrheit, ich bin euch ein vertrauenswurdiger Gesandter"},
  {"surah_number": 26, "verse_number": 108, "content": "So furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 109, "content": "Und ich verlange von euch keinen Lohn dafur; mein Lohn ist allein beim Herrn der Welten"},
  {"surah_number": 26, "verse_number": 110, "content": "So furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 111, "content": "Sie sagten: \"Sollen wir dir glauben, wo es (doch nur) die Niedrigsten sind, die dir folgen"},
  {"surah_number": 26, "verse_number": 112, "content": "Er sagte: \"Und welche Kenntnis habe ich von dem, was sie getan haben"},
  {"surah_number": 26, "verse_number": 113, "content": "Ihre Rechenschaft ist einzig die Sache meines Herrn, wenn ihr euch dessen nur bewußt waret"},
  {"surah_number": 26, "verse_number": 114, "content": "Und ich werde die Glaubigen gewiß nicht hinauswerfen"},
  {"surah_number": 26, "verse_number": 115, "content": "Ich bin nichts (anderes) als ein aufklarender Warner"},
  {"surah_number": 26, "verse_number": 116, "content": "Sie sagten: \"Wenn du (davon) nicht ablaßt, o Noah, so wirst du sicherlich gesteinigt werden"},
  {"surah_number": 26, "verse_number": 117, "content": "Er sagte: \"Mein Herr, mein Volk hat mich fur einen Lugner gehalten"},
  {"surah_number": 26, "verse_number": 118, "content": "Darum richte entscheidend zwischen mir und ihnen; und rette mich und die Glaubigen, die mit mir sind"},
  {"surah_number": 26, "verse_number": 119, "content": "So erretteten Wir ihn und jene, die mit ihm in dem beladenen Schiff waren"},
  {"surah_number": 26, "verse_number": 120, "content": "Dann ertrankten Wir hernach jene, die zuruckblieben"},
  {"surah_number": 26, "verse_number": 121, "content": "Wahrlich, hierin ist ein Zeichen, jedoch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 122, "content": "Und wahrlich, dein Herr - Er ist der Erhabene, der Barmherzige"},
  {"surah_number": 26, "verse_number": 123, "content": "Die `Ad bezichtigten den Gesandten der Luge"},
  {"surah_number": 26, "verse_number": 124, "content": "als ihr Bruder Hud zu ihnen sagte: \"Wollt ihr nicht gottesfurchtig sein"},
  {"surah_number": 26, "verse_number": 125, "content": "In Wahrheit, ich bin euch ein vertrauenswurdiger Gesandter"},
  {"surah_number": 26, "verse_number": 126, "content": "darum furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 127, "content": "Und ich verlange von euch keinen Lohn dafur; mein Lohn ist allein beim Herrn der Welten"},
  {"surah_number": 26, "verse_number": 128, "content": "Baut ihr Mahnmale auf jeder Anhohe, um euch zu vergnugen"},
  {"surah_number": 26, "verse_number": 129, "content": "Und errichtet ihr Burgen, als solltet ihr lange leben"},
  {"surah_number": 26, "verse_number": 130, "content": "Und wenn ihr zupackt, so benehmt ihr euch gewalttatig"},
  {"surah_number": 26, "verse_number": 131, "content": "So furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 132, "content": "Ja, furchtet Den, Der euch mit dem versorgt hat, was ihr kennt"},
  {"surah_number": 26, "verse_number": 133, "content": "Er hat euch mit Vieh und Sohnen versorgt"},
  {"surah_number": 26, "verse_number": 134, "content": "und Garten und Quellen"},
  {"surah_number": 26, "verse_number": 135, "content": "Wahrlich, ich furchte fur euch die Strafe eines gewaltigen Tages"},
  {"surah_number": 26, "verse_number": 136, "content": "Sie sagten: \"Es ist uns gleich ob du (uns) predigst oder ob du (uns) nicht pre digst"},
  {"surah_number": 26, "verse_number": 137, "content": "Dies ist nichts als ein Brauch der Fruheren"},
  {"surah_number": 26, "verse_number": 138, "content": "und wir werden nicht bestraft werden"},
  {"surah_number": 26, "verse_number": 139, "content": "So hielten sie ihn fur einen Lugner, und Wir vernichteten sie. Hierin ist wahrlich ein Zeichen, jedoch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 140, "content": "Und wahrlich, dein Herr - Er ist der Erhabene, der Barmherzige"},
  {"surah_number": 26, "verse_number": 141, "content": "Die Tamud bezichtigten den Gesandten der Luge"},
  {"surah_number": 26, "verse_number": 142, "content": "als ihr Bruder, Sahih zu ihnen sagte: \"Wollt ihr nicht gottesfurchtig sein"},
  {"surah_number": 26, "verse_number": 143, "content": "In Wahrheit, ich bin euch ein vertrauenswurdiger Gesandter"},
  {"surah_number": 26, "verse_number": 144, "content": "So furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 145, "content": "Und ich verlange von euch keinen Lohn dafur; mein Lohn ist allein beim Herrn der Welten"},
  {"surah_number": 26, "verse_number": 146, "content": "Werdet ihr etwa sicher zuruckbleiben unter den Dingen, die hier sind"},
  {"surah_number": 26, "verse_number": 147, "content": "unter Garten und Quellen"},
  {"surah_number": 26, "verse_number": 148, "content": "und Kornfeldern und Dattelpalmen mit Blutendolden, die fast brechen"},
  {"surah_number": 26, "verse_number": 149, "content": "Und aus den Bergen hauet ihr euch Wohnungen geschickt aus"},
  {"surah_number": 26, "verse_number": 150, "content": "So furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 151, "content": "Und gehorcht nicht dem Befehl derer, die nicht maßhalten"},
  {"surah_number": 26, "verse_number": 152, "content": "die Verderben auf Erden stiften und keine guten Werke verrichten"},
  {"surah_number": 26, "verse_number": 153, "content": "Sie sagten: \"Du bist nur einer, der dem Zauber zum Opfer gefallen ist"},
  {"surah_number": 26, "verse_number": 154, "content": "Du bist nichts (anderes) als ein Mensch wie wir. So bringe ein Zeichen, wenn du zu den Wahrhaftigen gehorst"},
  {"surah_number": 26, "verse_number": 155, "content": "Er sagte: \"Hier ist eine Kamelstute; sie hat (ihre) Trinkzeit, und ihr habt (eure) Trinkzeit an einem bestimmten Tag"},
  {"surah_number": 26, "verse_number": 156, "content": "Beruhrt sie nicht mit Bosem, damit euch nicht die Strafe eines gewaltigen Tages ereile"},
  {"surah_number": 26, "verse_number": 157, "content": "Sie aber schnitten ihr die Sehnen durch; und danach wurden sie reumutig"},
  {"surah_number": 26, "verse_number": 158, "content": "Allein die Strafe ereilte sie. Hierin ist wahrlich ein Zeichen, jedoch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 159, "content": "Und wahrlich, dein Herr - Er ist der Erhabene, der Barmherzige"},
  {"surah_number": 26, "verse_number": 160, "content": "Das Volk Lots bezichtigte den Gesandten der Luge"},
  {"surah_number": 26, "verse_number": 161, "content": "als ihr Bruder Lot zu ihnen sagte: \"Wollt ihr nicht gottesfurchtig sein"},
  {"surah_number": 26, "verse_number": 162, "content": "In Wahrheit, ich bin euch ein vertrauenswurdiger Gesandter"},
  {"surah_number": 26, "verse_number": 163, "content": "So furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 164, "content": "Und ich verlange von euch keinen Lohn dafur; mein Lohn ist allein beim Herrn der Welten"},
  {"surah_number": 26, "verse_number": 165, "content": "Vergeht ihr euch unter allen Geschopfen an Mannern"},
  {"surah_number": 26, "verse_number": 166, "content": "und lasset eure Frauen (beiseite), die euer Herr fur euch geschaffen hat? Nein, ihr seid ein Volk, das die Schranken uberschreitet"},
  {"surah_number": 26, "verse_number": 167, "content": "Sie sagten: \"Wenn du (davon) nicht ablaßt, o Lot, so wirst du gewiß einer der Verbannten sein"},
  {"surah_number": 26, "verse_number": 168, "content": "Er sagte: \"lch verabscheue euer Treiben"},
  {"surah_number": 26, "verse_number": 169, "content": "Mein Herr, rette mich und die Meinen vor dem, was sie tun"},
  {"surah_number": 26, "verse_number": 170, "content": "So erretteten Wir ihn und die Seinen allesamt"},
  {"surah_number": 26, "verse_number": 171, "content": "bis auf eine alte Frau, die unter denen war, die zuruckblieben"},
  {"surah_number": 26, "verse_number": 172, "content": "Dann vernichteten Wir die an deren"},
  {"surah_number": 26, "verse_number": 173, "content": "Und Wir ließen einen Regen auf sie niederregnen; und schlimm war der Regen fur die Gewarnten"},
  {"surah_number": 26, "verse_number": 174, "content": "Hierin liegt wahrlich ein Zeichen, jedoch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 175, "content": "Wahrlich, dein Herr - Er ist der Erhabene, der Barmherzige"},
  {"surah_number": 26, "verse_number": 176, "content": "Das Volk vom Walde bezichtigte den Gesandten der Luge"},
  {"surah_number": 26, "verse_number": 177, "content": "als Suaib zu ihnen sagte: \"Wollt ihr nicht gottes furchtig sein"},
  {"surah_number": 26, "verse_number": 178, "content": "In Wahrheit, ich bin euch ein ver trauenswurdiger Gesandter"},
  {"surah_number": 26, "verse_number": 179, "content": "So furchtet Allah und gehorcht mir"},
  {"surah_number": 26, "verse_number": 180, "content": "Und ich verlange von euch keinen Lohn dafur; mein Lohn ist allein beim Herrn der Welten"},
  {"surah_number": 26, "verse_number": 181, "content": "Gebt volles Maß und gehort nicht zu denen, die weniger geben"},
  {"surah_number": 26, "verse_number": 182, "content": "und wiegt mit richtiger Waage"},
  {"surah_number": 26, "verse_number": 183, "content": "Und vermindert den Menschen nicht ihr Gut und handelt nicht verderbend im Lande, indem ihr Unheil anrichtet"},
  {"surah_number": 26, "verse_number": 184, "content": "Und furchtet Den, Der euch und die fruheren Geschlechter erschuf"},
  {"surah_number": 26, "verse_number": 185, "content": "Sie sagten: \"Du bist nur einer, der dem Zauber zum Opfer gefallen ist"},
  {"surah_number": 26, "verse_number": 186, "content": "Und du bist nichts (anderes) als ein Mensch wie wir, und wir halten dich fur einen Lugner"},
  {"surah_number": 26, "verse_number": 187, "content": "So laß Brocken vom Himmel auf uns niederfallen, wenn du zu den Wahrhaftigen gehorst"},
  {"surah_number": 26, "verse_number": 188, "content": "Er sagte: \"Mein Herr weiß am besten, was ihr tut"},
  {"surah_number": 26, "verse_number": 189, "content": "Und sie erklarten ihn fur einen Lugner. So ereilte sie die Strafe am Tage der schattenspendenden Wolke. Das war wahrlich die Strafe eines gewaltigen Tages"},
  {"surah_number": 26, "verse_number": 190, "content": "Hierin ist wahrlich ein Zeichen, jedoch die meisten von ihnen glauben es nicht"},
  {"surah_number": 26, "verse_number": 191, "content": "Wahrlich, dein Herr - Er ist der Erhabene, der Barmherzige"},
  {"surah_number": 26, "verse_number": 192, "content": "Und wahrlich, dies ist eine Offenbarung vom Herrn der Welten"},
  {"surah_number": 26, "verse_number": 193, "content": "die vom vertrauenswurdigen Gabriel, herabgebracht worden ist"},
  {"surah_number": 26, "verse_number": 194, "content": "auf dein Herz, auf daß du einer der Warner sein mogest"},
  {"surah_number": 26, "verse_number": 195, "content": "in arabischer Sprache, die deutlich ist"},
  {"surah_number": 26, "verse_number": 196, "content": "Und ganz gewiß steht dies in den Schriften der Fruheren"},
  {"surah_number": 26, "verse_number": 197, "content": "Gilt es ihnen denn nicht als Zeichen, daß die Kundigen unter den Kindern Israels ihn kennen"},
  {"surah_number": 26, "verse_number": 198, "content": "Und hatten Wir ihn zu einem Nichtaraber hinabgesandt"},
  {"surah_number": 26, "verse_number": 199, "content": "und hatte er ihn ihnen vorgelesen, wurden sie (doch) nie an ihn geglaubt haben"},
  {"surah_number": 26, "verse_number": 200, "content": "So haben Wir ihn in die Herzen der Sunder einziehen lassen"},
  {"surah_number": 26, "verse_number": 201, "content": "Sie werden nicht an ihn glauben, bis sie die schmerzliche Strafe erleben"},
  {"surah_number": 26, "verse_number": 202, "content": "Doch sie wird unversehens uber sie kommen, ohne daß sie es merken"},
  {"surah_number": 26, "verse_number": 203, "content": "Dann werden sie sagen: \"Wird uns eine Frist gewahrt"},
  {"surah_number": 26, "verse_number": 204, "content": "Ist es denn Unsere Strafe, die sie beschleunigen wollen"},
  {"surah_number": 26, "verse_number": 205, "content": "Siehst du es nicht, wenn Wir sie jahrelang genießen lassen"},
  {"surah_number": 26, "verse_number": 206, "content": "Dann aber kommt das zu ihnen, was ihnen angedroht wurde"},
  {"surah_number": 26, "verse_number": 207, "content": "Nichts nutzt ihnen dann all das, was sie genießen durften"},
  {"surah_number": 26, "verse_number": 208, "content": "Und nie zerstorten Wir eine Stadt, ohne daß sie Warner gehabt hatte"},
  {"surah_number": 26, "verse_number": 209, "content": "zur Ermahnung; und nie sind Wir ungerecht"},
  {"surah_number": 26, "verse_number": 210, "content": "Und die Satane haben ihn (den Quran) nicht herabgebracht"},
  {"surah_number": 26, "verse_number": 211, "content": "noch schickt es sich fur sie, noch vermogen sie es"},
  {"surah_number": 26, "verse_number": 212, "content": "denn sie sind vom Lauschen ferngehalten"},
  {"surah_number": 26, "verse_number": 213, "content": "Rufe daher außer Allah keinen anderen Gott an, damit du nicht zu denen gehorst, die bestraft werden"},
  {"surah_number": 26, "verse_number": 214, "content": "Und warne deine nachsten Verwandten"},
  {"surah_number": 26, "verse_number": 215, "content": "und senke deinen Flugel uber die Glaubigen, die dir folgen"},
  {"surah_number": 26, "verse_number": 216, "content": "Sind sie dir dann aber ungehorsam, so sprich: \"lch bin schuldlos an dem, was ihr tut"},
  {"surah_number": 26, "verse_number": 217, "content": "Und vertraue auf den Erhabenen, den Barmherzigen"},
  {"surah_number": 26, "verse_number": 218, "content": "Der dich sieht, wenn du (im Gebet) dastehst"},
  {"surah_number": 26, "verse_number": 219, "content": "und (Der) deine Bewegungen inmitten derer, die sich (vor Ihm) niederwerfen, (sieht)"},
  {"surah_number": 26, "verse_number": 220, "content": "denn Er ist der Allhorende, der Allwissende"},
  {"surah_number": 26, "verse_number": 221, "content": "Soll ich euch verkunden, auf wen die Satane herniederfahren"},
  {"surah_number": 26, "verse_number": 222, "content": "Sie fahren auf jeden gewohnheitsmaßigen Lugner und Sunder hernieder"},
  {"surah_number": 26, "verse_number": 223, "content": "sie horchen, und die meisten von ihnen sind Lugner"},
  {"surah_number": 26, "verse_number": 224, "content": "Und die Dichter - es sind die Irrenden, die ihnen folgen"},
  {"surah_number": 26, "verse_number": 225, "content": "Hast du nicht gesehen, wie sie verwirrt in jedem Tal umherwandeln"},
  {"surah_number": 26, "verse_number": 226, "content": "und wie sie reden, was sie nicht tun"},
  {
    "surah_number": 26,
    "verse_number": 227,
    "content": "Die (sind von der Strafe) ausgenommen, die glauben und gute Werke verrichten und Allahs des ofteren gedenken und die sich widersetzen, nachdem ihnen Unrecht widerfuhr. Und die Ungerechten werden bald erfahren, zu welchem Ort sie zuruckkehren werden"
  },
  {"surah_number": 27, "verse_number": 1, "content": "Ta Sin. Dies sind die Verse des Quran - eines deutlichen Buches"},
  {"surah_number": 27, "verse_number": 2, "content": "(Es ist) eine Fuhrung und frohe Botschaft fur die, die glauben"},
  {"surah_number": 27, "verse_number": 3, "content": "das Gebet verrichten und die Zakah entrichten und fest mit dem Jenseits rechnen"},
  {"surah_number": 27, "verse_number": 4, "content": "Denjenigen aber, die nicht an das Jenseits glauben, haben Wir ihre Werke schon erscheinen lassen; so wandern sie nur in Verwirrung (umher)"},
  {"surah_number": 27, "verse_number": 5, "content": "Das sind die, derer eine schlimme Strafe harrt, und sie allein sind es, die im Jenseits die großten Verlierer sind"},
  {"surah_number": 27, "verse_number": 6, "content": "Und wahrlich, du empfangst den Quran von einem Allweisen, Allwissenden"},
  {"surah_number": 27, "verse_number": 7, "content": "Damals sagte Moses zu seinen Angehorigen: \"lch nahm ein Feuer wahr. Ich will euch von dort Kunde bringen, oder ich will zu euch eine brennende Glut herbeischaffen, auf daß ihr euch warmen moget"},
  {"surah_number": 27, "verse_number": 8, "content": "Und als er zu ihm kam, wurde gerufen: \"Gesegnet soll der sein, der in dem Feuerbereich ist und der, der sich in ihrer Nahe befindet; und gepriesen sei Allah, der Herr der Welten"},
  {"surah_number": 27, "verse_number": 9, "content": "O Moses, Ich bin Allah, der Erhabene, der Allweise"},
  {"surah_number": 27, "verse_number": 10, "content": "Und wirf deinen Stock hin.\" Doch als er ihn sich regen sah als ware er eine Riesenschlange, da wandte er sich zur Flucht und schaute nicht zuruck. \"O Moses, furchte dich nicht. Wahrlich, bei Mir furchten sich nicht die Gesandten"},
  {"surah_number": 27, "verse_number": 11, "content": "Wer aber Unrecht tut und dann Gutes an Stelle des Bosen setzt, dann, wahrlich, bin Ich Allverzeihend, Barmherzig"},
  {"surah_number": 27, "verse_number": 12, "content": "Und steck deine Hand unter deinen Arm; sie wird ohne ein Ubel weiß hervorkommen - (dies ist eines) der neun Zeichen fur Pharao und sein Volk; denn sie sind ein frevelhaftes Volk"},
  {"surah_number": 27, "verse_number": 13, "content": "Doch als Unsere sichtbaren Zeichen zu ihnen kamen, sagten sie: \"Das ist offenkundige Zauberei"},
  {"surah_number": 27, "verse_number": 14, "content": "Und sie verwarfen sie in Ungerechtigkeit und Hochmut, wahrend ihre Seelen doch von ihnen uberzeugt waren. Siehe nun, wie das Ende derer war, die Unheil anrichteten"},
  {"surah_number": 27, "verse_number": 15, "content": "Und wahrlich, Wir gaben David und Salomo Wissen, und beide sagten: \" Alles Lob gebuhrt Allah, Der uns erhoht hat uber viele Seiner glaubigen Diener"},
  {"surah_number": 27, "verse_number": 16, "content": "Und Salomo wurde Davids Erbe, und er sagte: \"O ihr Menschen, die Sprache der Vogel ist uns gelehrt worden; und alles wurde uns beschert. Das ist wahrlich die offenbare Huld"},
  {"surah_number": 27, "verse_number": 17, "content": "Und dort vor Salomo wurden dessen Heerscharen der Ginn und Menschen und Vogel versammelt, und sie standen in Reih und Glied geordnet"},
  {"surah_number": 27, "verse_number": 18, "content": "bis dann, als sie zum Tale der Ameisen kamen, eine Ameise (darunter) sagte: \"O ihr Ameisen, geht in eure Wohnungen hinein, damit euch Salomo und seine Heerscharen nicht zertreten, ohne daß sie es merken"},
  {
    "surah_number": 27,
    "verse_number": 19,
    "content": "Da lachelte er heiter uber ihre Worte und sagte: Mein Herr gib mir ein, dankbar fur die Gnade zu sein, die Du mir und meinen Eltern gewahrt hast, und (gib mir ein,) Gutes zu tun, das Dir wohlgefallig sei, und nimm mich in Deiner Barmherzigkeit unter Deine rechtschaffenen Diener auf"
  },
  {"surah_number": 27, "verse_number": 20, "content": "Und er musterte die Vogel und sagte: \"Wie kommt es, daß ich den Wiedehopf nicht sehe? Ist er abwesend"},
  {"surah_number": 27, "verse_number": 21, "content": "Ich werde ihn gewiß mit strenger Strafe bestrafen, oder ich werde ihn toten, es sei denn, er bringt mir einen triftigen Grund vor"},
  {"surah_number": 27, "verse_number": 22, "content": "Und dieser blieb nicht lange fern, dann sprach er: \"lch habe eine Erfahrung gemacht, die du nicht gemacht hast; und ich bin aus Saba' mit sicherer Nachricht zu dir gekommen"},
  {"surah_number": 27, "verse_number": 23, "content": "Dort fand ich eine Frau, die uber sie herrscht, und ihr ist alles beschert worden, und sie besitzt einen großartigen Thron"},
  {"surah_number": 27, "verse_number": 24, "content": "Ich fand sie und ihr Volk die Sonne statt Allah anbeten; und Satan hat ihnen ihre Werke ausgeschmuckt und hat sie vom Weg (Allahs) abgehalten, so daß sie dem Weg nicht folgen"},
  {"surah_number": 27, "verse_number": 25, "content": "Wollen sie nicht Allah verehren, Der ans Licht bringt, was in den Himmeln und auf Erden verborgen ist, und Der weiß, was ihr verhehlt und was ihr offenbart"},
  {"surah_number": 27, "verse_number": 26, "content": "Allah! Es ist kein Gott außer Ihm, dem Herrn des Gewaltigen Throns"},
  {"surah_number": 27, "verse_number": 27, "content": "Er (Salomo) sagte: \"Wir werden sehen, ob du die Wahrheit gesprochen hast oder ob du zu den Lugnern gehorst"},
  {"surah_number": 27, "verse_number": 28, "content": "Geh mit diesem Brief von mir und wirf ihn vor sie hin, sodann zieh dich von ihnen zuruck und schau, was sie erwidern"},
  {"surah_number": 27, "verse_number": 29, "content": "Sie (die Konigin) sagte: \"Ihr Vornehmen, ein ehrenvoller Brief ist mir uberbracht worden"},
  {"surah_number": 27, "verse_number": 30, "content": "Er ist von Salomo, und er lautet: \"Im Namen Allahs, des Allerbarmers, des Barmherzigen"},
  {"surah_number": 27, "verse_number": 31, "content": "Seid nicht uberheblich gegen mich, sondern kommt zu mir in Ergebenheit"},
  {"surah_number": 27, "verse_number": 32, "content": "Sie sagte: \"O ihr Vornehmen, ratet mir in dieser Sache. Ich entscheide keine Angelegenheit, solange ihr nicht zugegen seid"},
  {"surah_number": 27, "verse_number": 33, "content": "Sie sagten: \"Wir besitzen Kraft und eine starke Kriegsmacht, aber dir obliegt der Befehl; sieh nun zu, was du befehlen willst"},
  {"surah_number": 27, "verse_number": 34, "content": "Sie sagte: \"Wahrlich, wenn Konige in ein Land eindringen, verwusten sie es und machen die hochsten seiner Bewohner zu den niedrigsten. So verfahren sie"},
  {"surah_number": 27, "verse_number": 35, "content": "Ich aber will ihnen ein Geschenk schicken und will abwarten, was die Boten (fur eine Antwort) zuruckbringen"},
  {"surah_number": 27, "verse_number": 36, "content": "Als (der Uberbringer) zu Salomo kam, sagte (dieser): \"Schuttet ihr Reichtumer uber mich aus? Jedoch was Allah mir gegeben hat, ist besser als das, was Er euch gegeben hat. Nein, ihr seid es, die sich an dieser Gabe erfreuen"},
  {"surah_number": 27, "verse_number": 37, "content": "Kehre zu ihnen zuruck; denn wir werden ganz gewiß mit Heerscharen zu ihnen kommen, gegen die sie keine Macht haben werden, und wir werden sie von dort in Schmach forttreiben, und sie werden sich gedemutigt fuhlen"},
  {"surah_number": 27, "verse_number": 38, "content": "Er sagte: \"O ihr Vornehmen, wer von euch bringt mir ihren Thron, bevor sie in Ergebenheit zu mir kommen"},
  {"surah_number": 27, "verse_number": 39, "content": "Da sprach ein kraftvoller (Riese) von den Ginn: \"lch will ihn dir bringen, ehe du dich von deinem Feldlager erhebst; wahrlich, ich habe die Starke dazu und bin vertrauenswurdig"},
  {
    "surah_number": 27,
    "verse_number": 40,
    "content":
        "Da sprach einer der Kenntnis von der Schrift besaß: \"lch bringe ihn dir innerhalb eines Augenzwinkerns von dir.\" Und da er ihn vor sich stehen sah, sagte er: \"Dies geschieht durch die Gnade meines Herrn, um mich zu prufen, ob ich dankbar oder undankbar bin. Und wer dankbar ist, der ist dankbar zum Heil seiner eigenen Seele; wer aber undankbar ist - siehe, mein Herr ist auf keinen angewiesen, Großzugig"
  },
  {"surah_number": 27, "verse_number": 41, "content": "Er sagte: \"Macht ihr ihren Thron unkenntlich; wir wollen sehen, ob sie dem rechten Weg folgt oder ob sie zu denen gehort, die nicht dem rechten Weg folgen"},
  {"surah_number": 27, "verse_number": 42, "content": "Als sie kam, da wurde gesprochen: \"Ist dein Thron wie dieser?\" Sie sagte: \"Es ist, als ware er ein und derselbe.\" Und uns wurde (davon) Wissen, schon vor ihr, verliehen; und wir hatten uns bereits (Allah) ergeben"},
  {"surah_number": 27, "verse_number": 43, "content": "Und es hielt sie von dem ab, was sie statt Allah zu verehren pflegte; denn sie gehorte zu einem unglaubigen Volk"},
  {
    "surah_number": 27,
    "verse_number": 44,
    "content":
        "Es wurde zu ihr gesprochen: \"Tritt ein in den Palast.\" Und da sie ihn sah, hielt sie ihn fur einen Wasserspiegel und entbloßte ihre Beine. Er (Salomo) sagte: \"Es ist ein Palast, getafelt und gepflastert mit geglattetem Glas.\" Sie sagte: \"Mein Herr, ich habe wahrlich gegen meine eigene Seele gesundigt; und ich ergebe mich mit Salomo Allah, dem Herrn der Welten"
  },
  {"surah_number": 27, "verse_number": 45, "content": "Und wahrlich, Wir entsandten zu den Tamud ihren Bruder Salih (, der sagte): \"Verehrt Allah!\" Doch siehe, sie spalteten sich in zwei Parteien, die miteinander stritten"},
  {"surah_number": 27, "verse_number": 46, "content": "Er sagte: \"O mein Volk, weshalb wollt ihr lieber das Bose beschleunigt sehen als das Gute? Warum bittet ihr Allah nicht um Vergebung damit euch Barmherzigkeit zuteil werde"},
  {"surah_number": 27, "verse_number": 47, "content": "Sie sagten: \"Wir ahnen Boses von dir und von denen, die mit dir sind.\" Er sagte: \"Euer Unheil ist bereits bei Allah. Nein, ihr seid ein Volk, das gepruft wird"},
  {"surah_number": 27, "verse_number": 48, "content": "Und es waren in der Stadt neun Fuhrer, die Unheil im Lande stifteten, und sie wollten nicht fur Ordnung sorgen"},
  {
    "surah_number": 27,
    "verse_number": 49,
    "content": "Sie sagten: \"Schwort einander bei Allah, daß wir ihn (Salih) und seine Angehorigen gewiß bei Nacht uberfallen und dann zu seinen Hinterlassenen sagen wollen: \"Wir waren keine Zeugen vom Untergang seiner Familie, und wir reden bestimmt die Wahrheit"
  },
  {"surah_number": 27, "verse_number": 50, "content": "Sie schmiedeten einen Plan, (und) auch Wir schmiedeten einen Plan, aber sie gewahrten es nicht"},
  {"surah_number": 27, "verse_number": 51, "content": "Sieh nun wie der Ausgang ihres Planes war; denn Wir vernichteten sie und all ihr Volk ganz und gar"},
  {"surah_number": 27, "verse_number": 52, "content": "Und dort sind ihre Hauser, verfallen ihres Frevelns wegen. Hierin ist wahrlich ein Zeichen fur Leute, die es wissen"},
  {"surah_number": 27, "verse_number": 53, "content": "Und Wir erretteten jene, die glaubten und gottesfurchtig waren"},
  {"surah_number": 27, "verse_number": 54, "content": "Und (gedenke) Lots, als er zu seinem Volke sagte: \"Wollt ihr Schandlichkeiten begehen, wo ihr doch einsichtig seid"},
  {"surah_number": 27, "verse_number": 55, "content": "Wollt ihr euch wirklich in (eurer) Sinnenlust mit Mannern statt mit Frauen abgeben? Nein, ihr seid ein unwissendes Volk"},
  {"surah_number": 27, "verse_number": 56, "content": "Doch die Antwort seines Volkes war nichts anderes als: \"Treibt Lots Familie aus eurer Stadt hinaus: denn sie sind Leute, die rein sein mochten"},
  {"surah_number": 27, "verse_number": 57, "content": "Also erretteten Wir ihn und die Seinen bis auf seine Frau; sie ließen Wir bei jenen, die zuruckblieben"},
  {"surah_number": 27, "verse_number": 58, "content": "Und Wir ließen einen Regen auf sie niedergehen; und schlimm war der Regen fur die Gewarnten"},
  {"surah_number": 27, "verse_number": 59, "content": "Sprich: \"Aller Preis gebuhrt Allah, und Frieden sei uber jenen von Seinen Dienern, die Er auserwahlt hat. Ist Allah besser oder das, was sie anbeten"},
  {
    "surah_number": 27,
    "verse_number": 60,
    "content":
        "Wer hat denn Himmel und Erde geschaffen, und wer sendet Wasser vom Himmel fur euch nieder, durch das Wir in Schonheit herrliche Garten sprießen lassen? Ihr vermogt nicht ihre Baume sprießen zu lassen. Existiert wohl ein Gott neben Allah? Nein, sie sind ein Volk, das Gotter neben Gott stellt"
  },
  {
    "surah_number": 27,
    "verse_number": 61,
    "content": "Wer hat denn die Erde zu einer Ruhestatt gemacht und Flusse durch ihre Mitte gefuhrt und feste Berge auf ihr gegrundet und eine Schranke zwischen die beiden Meere gesetzt? Existiert wohl ein Gott neben Allah? Nein, die meisten von ihnen wissen es nicht"
  },
  {"surah_number": 27, "verse_number": 62, "content": "Wer antwortet denn dem Bedrangten, wenn er Ihn anruft, und nimmt das Ubel hinweg und macht euch zu Nachfolgern auf Erden? Existiert wohl ein Gott neben Allah? Geringfugig ist das, was ihr (davon) bedenkt"},
  {"surah_number": 27, "verse_number": 63, "content": "Wer leitet euch in den Finsternissen uber Land und Meer, und wer sendet die Winde als Freudenboten Seiner Barmherzigkeit voraus? Existiert wohl ein Gott neben Allah? Hoch Erhaben ist Allah uber das, was sie (ihm) beigesellen"},
  {"surah_number": 27, "verse_number": 64, "content": "Wer ruft denn zum Beginn die Schopfung hervor und wiederholt sie hierauf, und wer versorgt euch vom Himmel und von der Erde? Existiert wohl ein Gott neben Allah? Sprich: \"Bringt euren Beweis herbei, wenn ihr wahrhaftig seid"},
  {"surah_number": 27, "verse_number": 65, "content": "Sprich: \"Niemand in den Himmeln und auf Erden kennt das Verborgene außer Allah; und sie wissen nicht, wann sie wiederauferweckt werden"},
  {"surah_number": 27, "verse_number": 66, "content": "Nein, ihr Wissen uber das Jenseits hat ganzlich versagt; nein, sie befinden sich im Zweifel daruber; nein, sie sind ihm gegenuber blind"},
  {"surah_number": 27, "verse_number": 67, "content": "Und jene, die unglaubig sind, sagen: \"Wie? Wenn wir und unsere Vater zu Staub geworden sind, sollen wir dann wirklich wieder hervorgebracht werden"},
  {"surah_number": 27, "verse_number": 68, "content": "Verheißen wurde uns dies zuvor - uns und unseren Vatern; dies sind ja nur Fabeln der Fruheren"},
  {"surah_number": 27, "verse_number": 69, "content": "Sprich: \"Reist auf der Erde umher und seht, wie der Ausgang der Sundigen war"},
  {"surah_number": 27, "verse_number": 70, "content": "Sei ihretwegen nicht traurig, noch sei deswegen bedrangt, was sie an Ranken schmieden"},
  {"surah_number": 27, "verse_number": 71, "content": "Und sie sagen: \"Wann wird diese Verheißung (erfullt werden), wenn ihr die Wahrheit redet"},
  {"surah_number": 27, "verse_number": 72, "content": "Sprich: \"Vielleicht ist ein Teil von dem, was ihr zu beschleunigen trachtet, schon nahe herangekommen"},
  {"surah_number": 27, "verse_number": 73, "content": "Und wahrlich, dein Herr ist Huldreich gegen die Menschen, doch die meisten von ihnen sind nicht dankbar"},
  {"surah_number": 27, "verse_number": 74, "content": "Und dein Herr kennt wohl, was ihre Herzen verhehlen und was sie kundtun"},
  {"surah_number": 27, "verse_number": 75, "content": "Und (es gibt) nichts Verborgenes im Himmel oder auf Erden, das nicht in einem deutlichen Buch stunde"},
  {"surah_number": 27, "verse_number": 76, "content": "Wahrlich, dieser Quran erklart den Kindern Israels das meiste von dem, woruber sie uneins sind"},
  {"surah_number": 27, "verse_number": 77, "content": "Und er ist wahrlich eine Fuhrung und eine Barmherzigkeit fur die Glaubigen"},
  {"surah_number": 27, "verse_number": 78, "content": "Wahrlich, dein Herr wird zwischen ihnen durch Seinen Spruch entscheiden, und Er ist der Allmachtige, der Allwissende"},
  {"surah_number": 27, "verse_number": 79, "content": "Vertraue also auf Allah; denn du verfahrst nach der offenkundigen Wahrheit"},
  {"surah_number": 27, "verse_number": 80, "content": "Du kannst die Toten weder horend machen, noch kannst du bewirken, daß die Tauben den Anruf horen, wenn sie (dir) den Rucken kehren"},
  {"surah_number": 27, "verse_number": 81, "content": "noch kannst du die Blinden aus ihrem Irrtum leiten. Du kannst nur diejenigen horend machen, die an Unsere Zeichen glauben und die sich (Uns) ergeben"},
  {"surah_number": 27, "verse_number": 82, "content": "Und wenn der Befehl gegen sie ergeht, dann werden Wir fur sie ein Tier aus der Erde hervorbringen, das zu ihnen spricht, daß die Menschen nicht an Unsere Zeichen glaubten"},
  {"surah_number": 27, "verse_number": 83, "content": "Und (mahne sie an) den Tag, wo Wir aus jedem Volk eine Schar derer versammeln werden, die Unsere Zeichen fur Luge erklarten; und sie sollen in Reih und Glied gehalten werden"},
  {"surah_number": 27, "verse_number": 84, "content": "bis Er sprechen wird, wenn sie kommen: \"Habt ihr Meine Zeichen fur Luge erklart, obwohl ihr sie nicht kanntet? Oder was war es, was ihr tatet"},
  {"surah_number": 27, "verse_number": 85, "content": "Und der Befehl wird ihres Frevels wegen gegen sie ergehen, und sie werden nicht reden"},
  {"surah_number": 27, "verse_number": 86, "content": "Haben sie denn nicht gesehen, daß Wir die Nacht geschaffen haben, damit sie darin ruhen mogen, und den Tag, (damit sie) sehen (mogen)? Hierin sind wahrlich Zeichen fur Leute, die glauben"},
  {"surah_number": 27, "verse_number": 87, "content": "Und an dem Tage, wenn in den Sur gestoßen wird, hat jeder mit dem Schrecken zu kampfen, der in den Himmeln und der auf Erden ist, ausgenommen der, den Allah will. Und alle sollen demutig zu Ihm kommen"},
  {"surah_number": 27, "verse_number": 88, "content": "Und die Berge, die du fur fest haltst, wirst du wie Wolken dahingehen sehen: Das (ist das) Werk Allahs, Der alles vollendet hat. Wahrlich, Er weiß wohl, was ihr tut"},
  {"surah_number": 27, "verse_number": 89, "content": "Dem, der Gutes vollbringt, wird Besseres als das zuteil sein, und sie werden vor dem Schrecken an jenem Tag sicher sein"},
  {"surah_number": 27, "verse_number": 90, "content": "Und die Gesichter derjenigen, die Schlechtes vollbringen, sollen ins Feuer gesturzt werden: \"Seid ihr fur das belohnt worden, was ihr getan habt"},
  {"surah_number": 27, "verse_number": 91, "content": "Wahrlich, mir ist nur befohlen worden, dem Herrn dieser Stadt zu dienen, die Er geheiligt hat, und Sein sind alle Dinge; und mir ist befohlen worden, einer der Gottergebenen zu sein"},
  {"surah_number": 27, "verse_number": 92, "content": "und den Quran zu verlesen.\" Wer also dem rechten Weg folgt, der folgt ihm nur zu seinem eigenen Besten; (wenn) einer irregeht, so sprich: \"lch bin nur einer der Warner"},
  {"surah_number": 27, "verse_number": 93, "content": "Und sprich: \"Aller Preis gebuhrt Allah; Er wird euch Seine Zeichen zeigen, und ihr werdet sie erkennen.\" Und dein Herr ist dessen nicht unachtsam, was ihr tut"},
  {"surah_number": 28, "verse_number": 1, "content": "Ta Sin Mim"},
  {"surah_number": 28, "verse_number": 2, "content": "Das sind die Verse des deutlichen Buches"},
  {"surah_number": 28, "verse_number": 3, "content": "Wir verlesen dir wahrheitsgemaß einen Teil der Geschichte von Moses und Pharao, fur Leute, die glauben"},
  {"surah_number": 28, "verse_number": 4, "content": "Wahrlich, Pharao betrug sich hochmutig im Land und machte dessen Bewohner zu Parteien. Eine Gruppe davon pflegte er zu unterdrucken, indem er ihre Sohne erschlug und ihre Frauen leben ließ. Wahrlich, er war einer der Unheilstifter"},
  {"surah_number": 28, "verse_number": 5, "content": "Und Wir wollten denen, die im Lande als schwach erachtet worden waren, Huld erweisen und sie zu Fuhrern machen und zu Erben einsetzen"},
  {"surah_number": 28, "verse_number": 6, "content": "und ihnen die Oberhand im Lande geben und Pharao und Haman und ihren Heerscharen durch sie (die Schwachen) das zeigen, was sie befurchteten"},
  {"surah_number": 28, "verse_number": 7, "content": "Und Wir gaben der Mutter von Moses ein: \"Sauge ihn; und wenn du fur ihn furchtest, so wirf ihn in den Fluß und furchte dich nicht und betrube dich nicht; denn Wir werden ihn dir wiedergeben und ihn zu einem Gesandten machen"},
  {"surah_number": 28, "verse_number": 8, "content": "Und die Angehorigen Pharaos lasen ihn auf, so daß er ihnen zum Feind wurde und Kummer verursachte; denn Pharao und Haman und ihre Heerscharen waren Missetater"},
  {"surah_number": 28, "verse_number": 9, "content": "Und die Frau des Pharao sagte: (\"Er ist) mir und dir eine Augenweide! Totet ihn nicht. Vielleicht erweist er sich als nutzlich fur uns, oder wir nehmen ihn als Sohn an.\" Aber sie waren ahnungslos"},
  {"surah_number": 28, "verse_number": 10, "content": "Und das Herz von Moses' Mutter war (von jeder Hoffnung) entleert. Fast hatte sie ihr Geheimnis preisgegeben, wenn Wir nicht ihr Herz gestarkt hatten, damit sie am Glauben festhielte"},
  {"surah_number": 28, "verse_number": 11, "content": "Sie sagte zu seiner Schwester: \"Spure ihm nach.\" So beobachtete sie ihn von weitem; und jene ahnten nichts"},
  {"surah_number": 28, "verse_number": 12, "content": "Und vordem hatten Wir ihm die Ammen verwehrt. Da sagte sie (seine Schwester): \"Soll ich euch eine Familie nennen, wo man ihn fur euch aufziehen und ihm wohlgesinnt sein wurde"},
  {"surah_number": 28, "verse_number": 13, "content": "Dann gaben Wir ihn seiner Mutter zuruck, damit ihr Auge mit Freude erfullt wurde und damit sie sich nicht gramte und damit sie wußte, daß Allahs Verheißung wahr sei. Jedoch die meisten von ihnen wissen es nicht"},
  {"surah_number": 28, "verse_number": 14, "content": "Und als er seine Vollkraft erreicht hatte und reif geworden war, verliehen Wir ihm Weisheit und Wissen; so belohnen Wir jene, die Gutes tun"},
  {
    "surah_number": 28,
    "verse_number": 15,
    "content":
        "Und er betrat die Stadt um eine Zeit, da ihre Bewohner in einem Zustand der Unachtsamkeit waren; und er fand da zwei Manner, die miteinander kampften. Der eine war von seiner eigenen Partei und der andere von seinen Feinden. Jener, der von seiner Partei war, rief ihn zu Hilfe gegen den, der von seinen Feinden war. So schlug Moses ihn zuruck; doch es fuhrte zu seinem Tod. Er sagte: \"Das ist ein Werk des Satans; er ist ein Feind, ein offenbarer Verfuhrer"
  },
  {"surah_number": 28, "verse_number": 16, "content": "Er sagte: \"Mein Herr, ich habe mir selbst Unrecht getan, so vergib mir.\" So verzieh Er ihm; denn Er ist der Allverzeihende, der Barmherzige"},
  {"surah_number": 28, "verse_number": 17, "content": "Er sagte: \"Mein Herr, da Du mir gnadig gewesen bist, will ich niemals ein Helfer der Sunder sein"},
  {"surah_number": 28, "verse_number": 18, "content": "Dann wurde er in der Stadt furchtsam (und war) auf der Hut; und siehe, jener, der ihn zuvor um Hilfe gerufen hatte, schrie nach ihm um Beistand. Da sagte Moses zu ihm: \"Du bist offensichtlich auf dem falschen Weg"},
  {
    "surah_number": 28,
    "verse_number": 19,
    "content": "Und als er sich entschloß, Hand an den Mann zu legen, der ihrer beider Feind war, sagte er: \"O Moses, willst du mich toten, so wie du gestern einen Menschen getotet hast? Du versuchst nur, ein Tyrann im Land zu werden, und du willst kein Friedensstifter sein"
  },
  {"surah_number": 28, "verse_number": 20, "content": "Da kam ein Mann vom außersten Ende der Stadt angelaufen. Er sagte: \"O Moses, die Vornehmen beraten sich gegen dich, um dich zu toten. Darum mache dich fort; denn ich rate dir gut"},
  {"surah_number": 28, "verse_number": 21, "content": "Da zog er furchtsam aus ihr hinaus und spahte umher. Er sagte: \"Mein Herr rette mich vor dem ungerechten Volk"},
  {"surah_number": 28, "verse_number": 22, "content": "Und als er sich in Richtung Madyan begab, sagte er: \"lch hoffe, mein Herr wird mich auf den rechten Weg leiten"},
  {
    "surah_number": 28,
    "verse_number": 23,
    "content":
        "Als er zum Wasser von Madyan kam, fand er dort eine Schar von Leuten, die (ihr Vieh) trankten. Und neben ihnen fand er zwei Frauen, die (ihr Vieh) zuruckhielten. Er sagte: \" Was ist mit euch?\" Sie sagten: \"Wir konnen (unser Vieh) nicht eher tranken, als bis die Hirten (ihre Herden) fortgetrieben haben, und unser Vater ist ein hochbetagter Greis"
  },
  {"surah_number": 28, "verse_number": 24, "content": "Da trankte er (ihre Herde) fur sie. Dann zog er sich in den Schatten zuruck und sagte: \"Mein Herr, ich bedarf des Guten, was immer es (auch) sei, das Du auf mich herabsenden magst"},
  {
    "surah_number": 28,
    "verse_number": 25,
    "content":
        "Und eine der beiden (Frauen) kam zu ihm gelaufen voller Scham. Sie sagte: \"Siehe, mein Vater ladet dich ein, damit er dich dafur belohnen kann, daß du unser Vieh getrankt hast.\" Als er nun zu ihm kam und ihm seine Geschichte erzahlte, sagte er: \"Furchte dich nicht; du bist dem ungerechten Volk entronnen"
  },
  {"surah_number": 28, "verse_number": 26, "content": "Da sagte eine der beiden: \"O mein Vater, stell ihn in deinen Dienst ein; denn der beste Mann, den du einstellen kannst, ist wahrlich der, der stark und ehrlich ist"},
  {
    "surah_number": 28,
    "verse_number": 27,
    "content":
        "Er sagte: \"lch will dir eine von diesen meinen zwei Tochtern zur Frau geben unter der Bedingung, daß du dich mir auf acht Jahre zum Dienst verpflichtest. Willst du dann zehn (Jahre) vollmachen, so steht es bei dir. Ich mochte aber nicht hart zu dir sein; du wirst in mir, so Allah will, einen rechtschaffenen (Mann) finden"
  },
  {"surah_number": 28, "verse_number": 28, "content": "Er sagte: \"Das sei zwischen mir und dir (abgemacht) Welche der beiden Fristen ich auch erfulle - es soll mich kein Vorwurf treffen; und Allah ist Zeuge dessen, was wir sagen"},
  {
    "surah_number": 28,
    "verse_number": 29,
    "content":
        "Als Moses nun die Frist erfullt hatte und mit seinen Angehorigen reiste, gewahrte er an der Seite des Berges ein Feuer. Er sagte zu seinen Angehorigen: \"Bleibt hier ich habe ein Feuer wahrgenommen; vielleicht kann ich euch eine Nachricht von dort bringen oder eine Feuersglut, so daß ihr euch warmen konnt"
  },
  {"surah_number": 28, "verse_number": 30, "content": "Und als er zu ihm kam, da wurde er von der rechten Seite des Tales am gesegneten Ort aus dem Baum angerufen: O Moses, wahrlich, Ich bin Allah, der Herr der Welten"},
  {"surah_number": 28, "verse_number": 31, "content": "Und wirf deinen Stock hin.\" Als er ihn sich regen sah, als ware er eine Riesenschlange, da wandte er sich zur Flucht und schaute nicht zuruck. \"O Moses, tritt vor und furchte dich nicht; denn du gehorst zu jenen, die in Sicherheit sind"},
  {
    "surah_number": 28,
    "verse_number": 32,
    "content": "Steck deine Hand in deinen Hemdschlitz; sie wird ohne Ubel weiß hervorkommen; und zieh deinen Arm an dich, um die Angstlichkeit von dir zu jagen. Das sollen zwei Beweise von deinem Herrn fur Pharao und seine Vornehmen sein; denn sie sind ein frevelndes Volk"
  },
  {"surah_number": 28, "verse_number": 33, "content": "Er sagte: \"Mein Herr, ich habe einen von ihnen erschlagen, und ich furchte, sie werden mich toten"},
  {"surah_number": 28, "verse_number": 34, "content": "Und mein Bruder Aaron - er ist beredter als ich mit seiner Zunge; sende ihn darum als Helfer mit mir, auf daß er mich bestatige; denn ich furchte, sie werden mich der Falschheit bezichtigen"},
  {"surah_number": 28, "verse_number": 35, "content": "Er sprach: \"Wir wollen dich mit deinem Bruder starken, und Wir wollen euch beiden Macht geben, so daß sie euch nicht erreichen werden - durch Unsere Zeichen. Ihr beide und die, welche euch folgen, werden die Sieger sein"},
  {"surah_number": 28, "verse_number": 36, "content": "Als dann Moses zu ihnen mit Unseren deutlichen Zeichen kam, da sagten sie: \"Das ist nichts als ein Zaubertrug, und wir haben von unseren Vorvatern nie dergleichen gehort"},
  {"surah_number": 28, "verse_number": 37, "content": "Und Moses sagte: \"Mein Herr weiß am besten, wer es ist, der Seine Fuhrung gebracht hat, und wem der gluckselige Lohn der Wohnstatt zuteil werden wird. Wahrlich, die Frevler haben nie Erfolg"},
  {
    "surah_number": 28,
    "verse_number": 38,
    "content": "Und Pharao sagte: \"O ihr Vornehmen, ich kenne keinen anderen Gott fur euch au ßer mir; so brenne mir, O Haman, (Ziegel aus) Ton und mache mir einen Turm, damit ich den Gott Moses' erblicken kann, obgleich ich ihn gewiß fur einen Lugner erachte"
  },
  {"surah_number": 28, "verse_number": 39, "content": "Und er und seine Heerscharen verhielten sich hochmutig im Lande, ohne die Rechtfertigung dazu. Und sie meinten, nie zu Uns zuruckgebracht zu werden"},
  {"surah_number": 28, "verse_number": 40, "content": "So erfaßten Wir ihn und seine Heerscharen und setzten sie mitten im Meer aus. Schau darum, wie der Ausgang der Missetater war"},
  {"surah_number": 28, "verse_number": 41, "content": "Und Wir machten sie zu Fuhrern, welche (Menschen) zum Feuer luden; und am Tage der Auferstehung werden sie keinen Beistand finden"},
  {"surah_number": 28, "verse_number": 42, "content": "Und Wir ließen ihnen in dieser Welt einen Fluch folgen; und am Tage der Auferstehung werden sie unter den Verabscheuten sein"},
  {"surah_number": 28, "verse_number": 43, "content": "Und wahrlich, Wir gaben Moses die Schrift, nachdem Wir die fruheren Geschlechter vernichtet hatten, als ein Mittel zur Ermahnung fur die Menschen und als Fuhrung und Barmherzigkeit, auf daß sie (Meiner Macht) eingedenk sein mogen"},
  {"surah_number": 28, "verse_number": 44, "content": "Und du warst weder auf der westlichen Seite, als Wir Moses den Auftrag gaben, noch warst du unter den Augenzeugen"},
  {"surah_number": 28, "verse_number": 45, "content": "Jedoch Wir ließen (nach Moses) Generationen entstehen, und das Leben wurde ihnen lang! Und du verweiltest nicht unter dem Volk von Madyan, um ihnen Unsere Zeichen vorzutragen; Wir aber schickten (ihnen) Gesandte"},
  {
    "surah_number": 28,
    "verse_number": 46,
    "content": "Und du warst nicht auf der Seite des Berges als Wir (Moses) anriefen. Vielmehr (haben Wir dich) als eine Barmherzigkeit deines Herrn (entsandt), damit du ein Volk warnen mogest, zu dem vor dir kein Warner gekommen war, auf daß sie ermahnt seien"
  },
  {
    "surah_number": 28,
    "verse_number": 47,
    "content":
        "Und ware es nicht (nur deshalb), daß sie nicht sagen konnen, wenn ein Ungluck sie um dessentwillen treffen sollte, was ihre Hande vorausgeschickt haben: \"Unser Herr, warum hast Du uns keine Gesandten geschickt, daß wir Deine Zeichen hatten befolgen konnen und dann unter den Glaubigen gewesen waren"
  },
  {
    "surah_number": 28,
    "verse_number": 48,
    "content":
        "Doch als nun die Wahrheit von Uns zu ihnen kam, da sagten sie: \"Warum ist ihm nicht das gleiche gegeben worden wie das, was Moses gegeben wurde?\" Haben sie denn nicht das geleugnet, was Moses zuvor gegeben wurde? Sie hatten gesagt: \"(Dies sind) zwei Zauberwerke, die einander stutzen.\" Und sie sagten: \"Wir leugnen beide"
  },
  {"surah_number": 28, "verse_number": 49, "content": "Sprich: \"So bringt ein Buch von Allah herbei, das eine bessere Fuhrung als diese beiden hat, damit ich ihm folge, wenn ihr wahrhaftig seid"},
  {"surah_number": 28, "verse_number": 50, "content": "Doch wenn sie dir keine Folge leisten, dann wisse, daß sie nur ihren eigenen Neigungen folgen. Und wer ist irrender als der, der ohne Fuhrung Allahs seinen eigenen Neigungen folgt? Wahrlich, Allah leitet das ungerechte Volk nicht"},
  {"surah_number": 28, "verse_number": 51, "content": "Und wahrlich, Wir haben ihnen das Wort immer wieder ubermittelt, auf daß sie ermahnt seien"},
  {"surah_number": 28, "verse_number": 52, "content": "Diejenigen, denen Wir die Schrift zuvor gegeben haben, glauben an sie"},
  {"surah_number": 28, "verse_number": 53, "content": "Und wenn sie ihnen verlesen wird, dann sagen sie: \"Wir glauben daran. Wahrlich, es ist die Wahrheit von unserem Herrn; wir hatten uns (Ihm) schon vordem ergeben"},
  {"surah_number": 28, "verse_number": 54, "content": "Diese werden ihren Lohn zweimal erhalten, weil sie geduldig waren und das Bose durch das Gute abwehrten und von dem spendeten, was Wir ihnen gegeben hatten"},
  {"surah_number": 28, "verse_number": 55, "content": "Und wenn sie leeres Gerede horen, so wenden sie sich davon ab und sagen: \"Fur uns (seien) unsere Taten und fur euch (seien) eure Taten. Friede sei mit euch! Wir suchen keine Unwissenden"},
  {"surah_number": 28, "verse_number": 56, "content": "Wahrlich, du kannst dem den Weg nicht weisen, den du liebst; Allah aber weist dem den Weg, dem Er will; und Er kennt jene am besten, die die Fuhrung annehmen"},
  {
    "surah_number": 28,
    "verse_number": 57,
    "content": "Und sie sagen: \"Wenn wir deiner Fuhrung folgten, so wurden wir von unserem Land weggerissen werden.\" Haben Wir denn kein sicheres Schutzgebiet errichtet, zu dem die Fruchte aller Art gebracht werden als Versorgung von Uns? Jedoch die meisten von ihnen wissen es nicht"
  },
  {"surah_number": 28, "verse_number": 58, "content": "Und wie so manche Stadt haben Wir vernichtet, die sich ihrer Fulle des Unterhalts ruhmte! Und dort stehen ihre Wohnstat ten, die nach ihnen nicht bewohnt worden sind - mit Ausnahme einiger weniger. Und Wir wurden die Erben"},
  {"surah_number": 28, "verse_number": 59, "content": "Und dein Herr wurde nie die Stadte zerstoren, ohne zuvor in ihrer Mitte einen Gesandten erweckt zu haben, der ihnen Unsere Verse verliest; noch zerstorten Wir die Stadte, ohne daß ihre Bewohner (zuvor) ge frevelt hatten"},
  {"surah_number": 28, "verse_number": 60, "content": "Und was euch auch an Dingen gegeben wurde - es ist nur eine zeitweilige Nutznießung des irdischen Lebens und sein Schmuck; und das aber, was bei Allah ist, ist besser und bleibender. Wollt ihr denn nicht begreifen"},
  {"surah_number": 28, "verse_number": 61, "content": "Ist denn der, dem Wir eine schone Verheißung gaben, die er erfullt sehen wird, gleich jenem, den Wir mit den guten Dingen des irdischen Lebens versorgt haben? Doch dann, am Tage der Auferstehung, wird er unter den Vorgefuhrten sein"},
  {"surah_number": 28, "verse_number": 62, "content": "An jenem Tage wird Er sie rufen und sprechen: \"Wo sind nun Meine Partner, die ihr wahntet"},
  {"surah_number": 28, "verse_number": 63, "content": "Diejenigen, uber die das Urteil gefallt wird, sagen: \"Unser Herr, dies sind jene, die wir irrefuhrten. Wir fuhrten sie irre, wie wir selbst irregingen. Wir sprechen uns vor Dir (von ihnen) los. Nicht wir waren es, die sie anbeteten"},
  {"surah_number": 28, "verse_number": 64, "content": "Und es wird gesprochen: \"Ruft eure Gotter an.\" Und sie werden sie anrufen, doch jene werden ihnen nicht antworten. Und sie werden die Strafe erleben. Waren sie doch dem rechten Weg gefolgt"},
  {"surah_number": 28, "verse_number": 65, "content": "Und an jenem Tage wird Er sie rufen und sprechen: \"Welche Antwort gabt ihr den Gesandten"},
  {"surah_number": 28, "verse_number": 66, "content": "Dann werden ihnen an jenem Tage alle Ausreden dunkel werden, und sie werden einander nicht befragen konnen"},
  {"surah_number": 28, "verse_number": 67, "content": "Der aber, der bereut und glaubt und das Rechte wirkt - (es ist) wohl, daß er unter den Erfolgreichen sein wird"},
  {"surah_number": 28, "verse_number": 68, "content": "Und dein Herr erschafft, was Er will, und erwahlt, was Ihm gefallt. Nicht ihnen steht die Wahl zu. Gepriesen sei Allah und Hoch Erhaben uber das, was sie anbeten"},
  {"surah_number": 28, "verse_number": 69, "content": "Und dein Herr weiß, was ihre Herzen verbergen und was sie offenbaren"},
  {"surah_number": 28, "verse_number": 70, "content": "Und Er ist Allah; es ist kein Gott außer Ihm. Ihm gebuhrt aller Preis am Anfang und am Ende. Sein ist die Herrschaft, und zu Ihm werdet ihr zuruckgebracht"},
  {"surah_number": 28, "verse_number": 71, "content": "Sprich: \"Was haltet ihr davon, wenn Allah die Nacht uber euch bis zum Tage der Auferstehung dauern ließe? Welcher Gott außer Allah konnte euch ein Licht bringen? Wollt ihr denn nicht horen"},
  {"surah_number": 28, "verse_number": 72, "content": "Sprich: \"Was haltet ihr davon, wenn Allah den Tag uber euch bis zum Tage der Auferstehung dauern ließe? Welcher Gott außer Allah konnte euch eine Nacht zur Ruhe bringen? Wollt ihr es denn nicht einsehen"},
  {"surah_number": 28, "verse_number": 73, "content": "Und in Seiner Barmherzigkeit schuf Er fur euch die Nacht und den Tag, auf daß ihr darin ruhen moget und auf daß ihr nach Seiner Huld trachtet und dankbar sein moget"},
  {"surah_number": 28, "verse_number": 74, "content": "Und an jenem Tage wird Er sie rufen und sprechen: \"Wo sind nun Meine Partner, die ihr wahntet"},
  {"surah_number": 28, "verse_number": 75, "content": "Und Wir werden aus jedem Volke einen Zeugen holen und sprechen: \"Bringt euren Beweis herbei.\" Dann werden sie erkennen, daß die Wahrheit Allahs ist. Und das, was sie zu erdichten pflegten, wird fur sie verloren sein"},
  {
    "surah_number": 28,
    "verse_number": 76,
    "content": "Wahrlich, Korah gehorte zum Volke Moses', und doch unterdruckte er es. Und wir gaben ihm so viel Schatze, daß ihre Schlussel sicher eine Burde fur eine Schar von Starken gewesen waren. Da sagte sein Volk zu ihm: \"Freue dich nicht; denn Allah liebt diejenigen nicht, die frohlocken"
  },
  {"surah_number": 28, "verse_number": 77, "content": "Sondern suche in dem, was Allah dir gegeben hat, die Wohnstatt des Jenseits; und vergiß deinen Teil an der Welt nicht; und tue Gutes, wie Allah dir Gutes getan hat; und begehre kein Unheil auf Erden; denn Allah liebt die Unheilstifter nicht"},
  {
    "surah_number": 28,
    "verse_number": 78,
    "content":
        "Er sagte: \"Es (die Schatze) wurde mir nur um des Wissens willen, das ich besitze, gegeben.\" Wußte er denn nicht, daß Allah vor ihm schon Generationen vernichtet hatte, die noch gewaltigere Macht und großeren Reichtum als er besaßen? Und die Schuldigen werden nicht nach ihren Sunden befragt"
  },
  {"surah_number": 28, "verse_number": 79, "content": "So ging er denn in seinem Schmuck hinaus zu seinem Volk. Jene nun, die nach dem Leben in dieser Welt begierig waren, sagten: \"O wenn wir doch das gleiche besaßen wie das, was Korah gegeben wurde! Er hat wahrlich großes Gluck"},
  {"surah_number": 28, "verse_number": 80, "content": "Die aber, denen Wissen zuteil geworden war, sagten: \"Wehe euch, Allahs Lohn ist besser fur den, der glaubt und gute Werke tut; und keiner wird ihn erlangen außer den Geduldigen"},
  {"surah_number": 28, "verse_number": 81, "content": "Dann ließen Wir ihn von der Erde verschlingen und (auch) sein Haus; und er hatte keine Schar, die ihm gegen Allah helfen konnte, noch konnte er sich (selbst) retten"},
  {
    "surah_number": 28,
    "verse_number": 82,
    "content":
        "Und jene, die sich noch tags zuvor an seine Stelle gewunscht hatten, sagten: \"Ah sieh! Es ist wahrlich Allah, Der denen von Seinen Dienern die Mittel zum Unterhalt erweitert und beschrankt, denen Er will. Ware uns Allah nicht Gnadig gewesen, hatte Er uns (von der Erde) verschlingen lassen. Ah sieh! Die Unglaubigen haben nie Erfolg"
  },
  {"surah_number": 28, "verse_number": 83, "content": "Jene Wohnstatt im Jenseits! Wir geben sie denen, die weder Selbsterhohung auf Erden noch irgendein (anderes) Verderbnis begehren. Und der Ausgang ist fur die Gottesfurchti gen"},
  {"surah_number": 28, "verse_number": 84, "content": "Wer Gutes vollbringt, soll Besseres als das erhalten; wer jedoch eine bose Tat vollbringt - jene, die bose Werke tun, sollen nur gemaß dem belohnt werden, was sie getan haben"},
  {"surah_number": 28, "verse_number": 85, "content": "Wahrlich, Er, Der den Quran bindend fur dich gemacht hat - Er wird dich zur Statte der Wiederkehr zuruckbringen. Sprich: \"Mein Herr weiß am besten, wer es ist, der auf dem rechten Weg ist, und wer sich in einem offenbaren Irrtum befindet"},
  {"surah_number": 28, "verse_number": 86, "content": "Und du hattest selbst keine Hoffnung, daß dir das Buch offenbart wurde; allein es ist eine Barmherzigkeit deines Herrn; darum sei nicht ein Beistand fur die Unglaubigen"},
  {"surah_number": 28, "verse_number": 87, "content": "Und laß dich von niemandem von den Zeichen Allahs abwenden, nachdem sie zu dir niedergesandt worden sind; und rufe (die Menschen) zu deinem Herrn und sei nicht einer der Gotzendiener"},
  {"surah_number": 28, "verse_number": 88, "content": "Und rufe neben Allah keinen anderen Gott an. Es ist kein Gott außer Ihm. Alle Dinge sind verganglich, bis auf Sein Angesicht. Sein ist die Herrschaft und zu Ihm werdet ihr zuruckgebracht werden"},
  {"surah_number": 29, "verse_number": 1, "content": "Alif Lam Mim"},
  {"surah_number": 29, "verse_number": 2, "content": "Meinen die Menschen, sie wurden in Ruhe gelassen werden, wenn sie bloß sagten: \"Wir glauben\", und meinen sie, sie wurden nicht auf die Probe gestellt"},
  {"surah_number": 29, "verse_number": 3, "content": "Und Wir stellten doch die auf die Probe, die vor ihnen waren. Also wird Allah gewiß die erkennen, die wahrhaftig sind, und gewiß wird Er die Lugner er kennen"},
  {"surah_number": 29, "verse_number": 4, "content": "Oder glauben diejenigen, die bose Taten begehen, daß sie Uns entrinnen konnten? Ubel ist es, wie sie urteilen"},
  {"surah_number": 29, "verse_number": 5, "content": "Wer mit Allahs Begegnung rechnet, (der soll wissen, daß) Allahs angesetzte Frist sicher ablauft. Und Er ist der Allhorende, der Allwissende"},
  {"surah_number": 29, "verse_number": 6, "content": "Und wer da eifert, eifert nur fur seine eigene Seele; denn Allah ist auf niemanden von allen Welten angewiesen"},
  {"surah_number": 29, "verse_number": 7, "content": "Und jene, die glauben und gute Werke tun - wahrlich, Wir werden ihre Ubel von ihnen nehmen und ihnen den besten Lohn fur ihre Taten geben"},
  {
    "surah_number": 29,
    "verse_number": 8,
    "content": "Und Wir haben dem Menschen anbefohlen, seinen Eltern Gutes zu tun. Doch wenn sie dich zwingen wollen, Mir das zur Seite zu stellen, wovon du keine Kenntnis hast, so gehorche ihnen nicht. Zu Mir werdet ihr heimkehren, (und) dann will Ich euch verkunden, was ihr getan habt"
  },
  {"surah_number": 29, "verse_number": 9, "content": "Und jene, die glauben und gute Werke tun - wahrlich, Wir werden sie bei den Rechtschaffenen einfuhren"},
  {
    "surah_number": 29,
    "verse_number": 10,
    "content":
        "Und unter den Menschen sind manche, die sagen: \"Wir glauben an Allah\", doch wenn sie fur Allahs Sache Ungemach erleiden, so betrachten sie die Anfeindung von Menschen als der Strafe Allahs gleich. Kommt aber Hilfe von deinem Herrn, dann sagen sie gewiß: \"Wahrlich, wir waren mit euch.\" Ist es nicht Allah, Der am besten weiß, was in den Herzen aller Geschopfe ist"
  },
  {"surah_number": 29, "verse_number": 11, "content": "Und Allah wird sicherlich die bezeichnen, die glauben, und Er wird sicherlich die Heuchler bezeichnen"},
  {"surah_number": 29, "verse_number": 12, "content": "Und die Unglaubigen sagen zu denen, die glauben: \"Wenn ihr unserem Weg folgt, so wollen wir eure Sunden tragen.\" Sie konnen doch nichts von ihren Sunden tragen. Sie sind gewiß Lugner"},
  {"surah_number": 29, "verse_number": 13, "content": "Aber sie sollen wahrlich ihre eigenen Lasten tragen und (noch) Lasten zu ihren Lasten dazu. Und sie werden gewiß am Tage der Auferstehung uber das befragt werden, was sie erdichtet haben"},
  {"surah_number": 29, "verse_number": 14, "content": "Und wahrlich, Wir sandten Noah zu seinem Volke, und er weilte unter ihnen eintausend Jahre weniger funfzig Jahre. Da ereilte sie die Sintflut, weil sie Missetater waren"},
  {"surah_number": 29, "verse_number": 15, "content": "Aber Wir erretteten ihn und die Schiffsinsassen; und Wir machten sie zu einem Zeichen fur alle Volker"},
  {"surah_number": 29, "verse_number": 16, "content": "Und Abraham sagte zu seinem Volk: \"Dient Allah und furchtet Ihn. Das ist besser fur euch, wenn ihr es wußtet"},
  {"surah_number": 29, "verse_number": 17, "content": "Ihr dient nur Gotzen statt Allah, und ihr ersinnt eine Luge. Jene, die ihr statt Allah dient, vermogen euch nicht zu versorgen. Sucht darum bei Allah die Versorgung und dient Ihm und seid Ihm dankbar. Zu Ihm werdet ihr zuruckgebracht werden"},
  {"surah_number": 29, "verse_number": 18, "content": "Und wenn ihr es fur Luge erklart, so haben Generationen vor euch es fur Luge gehalten. Und dem Gesandten obliegt nur die deutliche Verkundigung"},
  {"surah_number": 29, "verse_number": 19, "content": "Sehen sie denn nicht, wie Allah die Schopfung hervorbringt und sie dann wiederholt? Das ist wahrlich ein leichtes fur Allah"},
  {"surah_number": 29, "verse_number": 20, "content": "Sprich: \"Zieht auf Erden umher und schauet, wie Er ein erstes Mal die Schopfung hervorbrachte. Sodann ruft Allah die zweite Schopfung hervor.\" Wahrlich, Allah hat Macht uber alle Dinge"},
  {"surah_number": 29, "verse_number": 21, "content": "Er bestraft, wen Er will, und erweist Barmherzigkeit, wem Er will; und zu Ihm werdet ihr zuruckkehren"},
  {"surah_number": 29, "verse_number": 22, "content": "Und ihr konnt (Allahs Plane) nicht vereiteln, weder auf Erden noch im Himmel, noch habt ihr einen Beschutzer oder Helfer außer Allah"},
  {"surah_number": 29, "verse_number": 23, "content": "Und diejenigen, die nicht an die Zeichen Allahs und an die Begegnung mit Ihm glauben - sie sind es, die an Meiner Barmherzigkeit zweifeln. Und ihnen wird eine schmerzliche Strafe zuteil sein"},
  {"surah_number": 29, "verse_number": 24, "content": "Die Antwort seines Volkes waren nur die Worte: \"Erschlagt ihn (Abraham) oder verbrennt ihn.\" Doch Allah errettete ihn aus dem Feuer. Hierin liegen wahrlich Zeichen fur Leute, die glauben"},
  {
    "surah_number": 29,
    "verse_number": 25,
    "content": "Und er sagte: \"lhr habt euch nur Gotzen statt Allah genommen um die Beziehung zueinander im irdischen Leben zu pflegen. Dann aber, am Tage der Auferstehung, werdet ihr einander verleugnen und verfluchen. Und eure Herberge wird das Feuer sein; und ihr werdet keine Helfer finden"
  },
  {"surah_number": 29, "verse_number": 26, "content": "Da glaubte Lot ihm; und (Abraham) sagte: \"lch werde zu meinem Herrn auswandern; Er ist der Allmachtige, der Allweise"},
  {"surah_number": 29, "verse_number": 27, "content": "Und Wir schenkten ihm Isaak und Jakob und gaben seinen Nachkommen das Prophetentum und die Schrift; und Wir gaben ihm seinen Lohn im Diesseits; und im Jenseits wird er gewiß unter den Rechtschaffenen sein"},
  {"surah_number": 29, "verse_number": 28, "content": "Und da sagte Lot zu seinem Volk: \"lhr begeht eine Schandlichkeit, die keiner von allen Menschen je vor euch begangen hat"},
  {"surah_number": 29, "verse_number": 29, "content": "Vergeht ihr euch tatsachlich an Mannern und macht die Wege unsicher? Und bei euren Versammlungen begeht ihr Abscheuliches!\" Jedoch die Antwort seines Volkes waren nur die Worte: \"Bringe Allahs Strafe uber uns, wenn du die Wahrheit redest"},
  {"surah_number": 29, "verse_number": 30, "content": "Er sagte: \"Hilf mir mein Herr, gegen das Volk, das frevelt"},
  {"surah_number": 29, "verse_number": 31, "content": "Und als Unsere Boten Abraham die frohe Botschaft brachten, sprachen sie: \"Wir kommen, um die Bewohner dieser Stadt zu vernichten; denn ihre Bewohner sind Missetater"},
  {"surah_number": 29, "verse_number": 32, "content": "Er sagte: \"Doch Lot ist dort.\"Sie sprachen: \"Wir wissen recht wohl, wer dort ist. Gewiß, wir werden ihn und die Seinen erretten, bis auf seine Frau, die zu denen gehort, die zuruckbleiben sollen"},
  {
    "surah_number": 29,
    "verse_number": 33,
    "content": "Und als Unsere Boten zu Lot kamen, war er ihretwegen besorgt und fuhlte sich außerstande, ihnen zu helfen. Sie sprachen: \"Furchte dich nicht und sei nicht traurig, wir wollen dich und die Deinen sicher retten, bis auf deine Frau, die zu denen gehort, die zuruckbleiben sollen"
  },
  {"surah_number": 29, "verse_number": 34, "content": "Wir werden uber die Bewohner dieser Stadt eine Strafe vom Himmel niedergehen lassen, weil sie gefrevelt haben"},
  {"surah_number": 29, "verse_number": 35, "content": "Und Wir haben davon ein klares Zeichen fur die Leute zuruckgelassen, die Verstand haben"},
  {"surah_number": 29, "verse_number": 36, "content": "Und zu Madyan (entsandten Wir) ihren Bruder Suaib, der sagte: \"O mein Volk, dient Allah und furchtet den Jungsten Tag und tobt nicht aus auf Erden, indem ihr Unheil stiftet"},
  {"surah_number": 29, "verse_number": 37, "content": "Jedoch sie erklarten ihn fur einen Lugner. Da erfaßte sie ein heftiges Beben, und sie lagen in ihren Wohnungen auf dem Boden"},
  {"surah_number": 29, "verse_number": 38, "content": "Und (Wir vernichteten) die `Ad und die Tamud; und dies ist aus ihren Wohnstatten fur euch klar ersichtlich. Und Satan ließ ihnen ihre Werke wohlgefallig erscheinen und hielt sie von dem Weg ab, obwohl sie einsichtig waren"},
  {"surah_number": 29, "verse_number": 39, "content": "Und (Wir vernichteten) Korah und Pharao und Haman. Und Moses kam wahrlich mit deutlichen Beweisen zu ihnen, doch sie betrugen sich hochmutig auf Erden; (Uns) aber konnten sie nicht entrinnen"},
  {
    "surah_number": 29,
    "verse_number": 40,
    "content":
        "So erfaßten Wir einen jeden in seiner Sunde; es waren unter ihnen welche, gegen die Wir einen Sandsturm schickten, und welche, die der Schrei ereilte, und welche, unter denen Wir die Erde versinken ließen, und welche, die Wir ertrankten. Und nicht Allah wollte ihnen Unrecht tun, sondern sie taten sich selbst Unrecht"
  },
  {"surah_number": 29, "verse_number": 41, "content": "Das Gleichnis derer, die sich Helfer außer Allah nehmen, ist wie das Gleichnis von der Spinne, die sich ein Haus macht; und das gebrechlichste der Hauser ist gewiß das Haus der Spinne - wenn sie es nur begriffen"},
  {"surah_number": 29, "verse_number": 42, "content": "Wahrlich, Allah kennt all das, was sie an Seiner Statt anrufen; und Er ist der Allmachtige, der Allweise"},
  {"surah_number": 29, "verse_number": 43, "content": "Und dies sind Gleichnisse, die Wir den Menschen pragen; doch es verstehen sie nur jene, die Wissen haben"},
  {"surah_number": 29, "verse_number": 44, "content": "Allah erschuf die Himmel und die Erde in makelloser Weise. Hierin liegt wahrlich ein Zeichen fur die Glaubigen"},
  {"surah_number": 29, "verse_number": 45, "content": "Verlies, was dir von dem Buche offenbart wurde, und verrichte das Gebet. Wahrlich, das Gebet halt von schandlichen und abscheulichen Dingen ab; und Allahs zu gedenken, ist gewiß das Hochste. Und Allah weiß, was ihr begeht"},
  {
    "surah_number": 29,
    "verse_number": 46,
    "content":
        "Und streitet nicht mit dem Volk der Schrift; es sei denn auf die beste Art und Weise. Ausgenommen davon sind jene, die ungerecht sind. Und sprecht: \"Wir glauben an das, was zu uns herabgesandt wurde und was zu euch herabgesandt wurde; und unser Gott und euer Gott ist Einer; und Ihm sind wir ergeben"
  },
  {"surah_number": 29, "verse_number": 47, "content": "Und somit haben Wir dir das Buch herniedergesandt, und so glauben diejenigen daran, denen Wir das Buch gegeben haben; und unter diesen sind einige, die daran glauben. Es sind aber nur die Unglaubigen, die Unsere Zeichen leugnen"},
  {"surah_number": 29, "verse_number": 48, "content": "Und nie zuvor hast du in einem Buch gelesen, noch konntest du eines mit deiner Rechten schreiben; sonst hatten die Verleugner daran gezweifelt"},
  {"surah_number": 29, "verse_number": 49, "content": "Nein, es sind klare Zeichen in den Herzen derer, denen das Wissen gegeben wurde. Es gibt keinen, der Unsere Zeichen leugnet außer den Ungerechten"},
  {"surah_number": 29, "verse_number": 50, "content": "Und sie sagten: \"Warum wurden keine Zeichen zu ihm von seinem Herrn herabgesandt?\" Sprich: \"Die Zeichen sind allein bei Allah, und ich bin nur ein deutlicher Warner"},
  {"surah_number": 29, "verse_number": 51, "content": "Genugt es ihnen denn nicht, daß Wir dir das Buch herniedergesandt haben, das ihnen verlesen wird? Wahrlich, hierin ist eine Barmherzigkeit und Ermahnung fur ein Volk, das glaubt"},
  {"surah_number": 29, "verse_number": 52, "content": "Sprich: \"Allah genugt als Zeuge gegen mich und euch. Er weiß, was in den Himmeln und was auf Erden ist. Und diejenigen, die das Falsche annehmen und Allah ablehnen - das sind die Verlierenden"},
  {"surah_number": 29, "verse_number": 53, "content": "Und sie verlangen von dir, daß du die Strafe beschleunigen sollst. Ware nicht eine Frist festgesetzt worden, hatte die Strafe sie schon ereilt; und sie wird gewiß unerwartet uber sie kommen, ohne daß sie es merken"},
  {"surah_number": 29, "verse_number": 54, "content": "Sie verlangen von dir, daß du die Strafe beschleunigen sollst; doch wahrlich, Gahannam wird die Unglaubigen einschließen"},
  {"surah_number": 29, "verse_number": 55, "content": "An dem Tage, da die Strafe sie von oben und von ihren Fußen her uberwaltigen wird, wird Er sprechen: \"Kostet nun die (Fruchte) eurer Taten"},
  {"surah_number": 29, "verse_number": 56, "content": "O Meine Diener, die ihr glaubt, Meine Erde ist weit. Darum verehrt nur Mich"},
  {"surah_number": 29, "verse_number": 57, "content": "Jede Seele wird den Tod kosten; zu Uns werdet ihr dann zuruckgebracht"},
  {"surah_number": 29, "verse_number": 58, "content": "Und jene, die glauben und gute Werke tun, beherbergen Wir in den oberen Gemachern des Paradieses, durch das Bache fließen. Darin verweilen sie auf immerdar. Herrlich ist der Lohn derjenigen, die wohltatig sind"},
  {"surah_number": 29, "verse_number": 59, "content": "die da standhaft sind und auf ihren Herrn vertrauen"},
  {"surah_number": 29, "verse_number": 60, "content": "Und wie viele Tiere gibt es, die nicht ihre eigene Versorgung tragen. Allah versorgt sie und euch. Und Er ist der Allhorende, der Allwissende"},
  {"surah_number": 29, "verse_number": 61, "content": "Und wenn du sie fragst: \"Wer hat die Himmel und die Erde geschaffen und euch die Sonne und den Mond dienstbar gemacht?\" dann werden sie gewiß sagen: \"Allah.\" Wieso lassen sie sich dann (von Allah) abwenden"},
  {"surah_number": 29, "verse_number": 62, "content": "Allah erweitert und beschrankt die Mittel zum Unterhalt dem von Seinen Dienern, den Er will. Wahrlich, Allah besitzt die volle Kenntnis von allen Dingen"},
  {"surah_number": 29, "verse_number": 63, "content": "Und wenn du sie fragst: \"Wer sendet Wasser vom Himmel nieder und belebt damit die Erde nach ihrem Tod?\" - dann werden sie gewiß sagen: \"Allah.\" Sprich: \"Aller Preis gebuhrt Allah.\" Jedoch die meisten von ihnen begreifen es nicht"},
  {"surah_number": 29, "verse_number": 64, "content": "Dieses irdische Leben ist nichts als ein Zeitvertreib und ein Spiel; die Wohnstatt des Jenseits aber - das ist das eigentliche Leben, wenn sie es nur wußten"},
  {"surah_number": 29, "verse_number": 65, "content": "Und wenn sie ein Schiff besteigen, dann rufen sie Allah reinen Glaubens an. Bringt Er sie dann aber heil ans Land, siehe, dann stellen sie (ihm) Gotter zur Seite"},
  {"surah_number": 29, "verse_number": 66, "content": "und leugnen somit das, was Wir ihnen beschert haben, und ergotzen sich. Bald aber werden sie es erfahren"},
  {"surah_number": 29, "verse_number": 67, "content": "Haben sie denn nicht gesehen, daß Wir ein Schutzgebiet sicher gemacht haben, wahrend die (anderen) Menschen in ihrer Umgebung hinweggerissen werden? Wollen sie da noch an (etwas) Falsches glauben und die Huld Allahs leugnen"},
  {"surah_number": 29, "verse_number": 68, "content": "Und wer ist ungerechter als jener, der eine Luge gegen Allah erdichtet oder die Wahrheit verwirft, wenn sie zu ihm kommt? Existiert denn fur die Unglaubigen keine Wohnstatt in Gahannam"},
  {"surah_number": 29, "verse_number": 69, "content": "Und diejenigen, die in Unserer Sache wetteifern - Wir werden sie gewiß auf Unseren Wegen leiten. Wahrlich, Allah ist mit denen, die Gutes tun"},
  {"surah_number": 30, "verse_number": 1, "content": "Alif Lam Mim"},
  {"surah_number": 30, "verse_number": 2, "content": "Besiegt sind die Byzantiner"},
  {"surah_number": 30, "verse_number": 3, "content": "in dem nahegelegenen Land, doch sie werden nach ihrer Niederlage siegen"},
  {"surah_number": 30, "verse_number": 4, "content": "in wenigen Jahren - Allahs ist die Herrschaft vorher und nachher -, und an jenem Tage werden sich die Glaubigen freuen"},
  {"surah_number": 30, "verse_number": 5, "content": "uber Allahs Hilfe. Er hilft, wem Er will; und Er ist der Allmachtige, der Barmherzige"},
  {"surah_number": 30, "verse_number": 6, "content": "(Das ist) die Verheißung Allahs - Allah bricht Seine Verheißung nicht -; allein die meisten Menschen wissen es nicht"},
  {"surah_number": 30, "verse_number": 7, "content": "Sie kennen nur die Außenseite des diesseitigen Lebens; das Jenseits aber beachten sie gar nicht"},
  {
    "surah_number": 30,
    "verse_number": 8,
    "content": "Haben sie sich denn uber sich selbst keine Gedanken gemacht? Allah hat die Himmel und die Erde und das, was zwischen beiden ist, nur in gerechter Weise und fur eine bestimmte Frist geschaffen. Doch wahrlich, viele der Menschen glauben nicht an die Begegnung mit ihrem Herrn"
  },
  {
    "surah_number": 30,
    "verse_number": 9,
    "content":
        "Sind sie denn nicht im Lande umhergezogen, so daß sie schauen konnten, wie das Ende derer war, die vor ihnen lebten? Jene waren noch machtiger als sie, und sie bebauten das Land und bevolkerten es mehr, als diese es bevolkert haben. Und ihre Gesandten kamen mit den Beweisen zu ihnen. Und nicht Allah wollte ihnen Unrecht antun, sondern sie taten sich selbst Unrecht"
  },
  {"surah_number": 30, "verse_number": 10, "content": "Und dann war das Ende derer, die selbes begingen, das schlimmste, daß sie die Zeichen Allahs verwarfen und uber sie zu spotten pflegten"},
  {"surah_number": 30, "verse_number": 11, "content": "Allah bringt die Schopfung hervor; sodann laßt er sie wiederholen; dann werdet ihr zu Ihm zuruckgebracht"},
  {"surah_number": 30, "verse_number": 12, "content": "Und an dem Tage, da die Stunde herankommt, werden die Schuldigen von Verzweiflung uberwaltigt sein"},
  {"surah_number": 30, "verse_number": 13, "content": "Denn keiner von ihren Partnern wird ihr Fursprecher sein; und sie werden ihre Partner verleugnen"},
  {"surah_number": 30, "verse_number": 14, "content": "Und an dem Tage, da die Stunde herankommt, an jenem Tage werden sie voneinander getrennt sein"},
  {"surah_number": 30, "verse_number": 15, "content": "Was nun die betrifft, die glaubten und gute Werke taten, so werden sie in einem Garten Gluck finden"},
  {"surah_number": 30, "verse_number": 16, "content": "Jene aber, die unglaubig waren und Unsere Zeichen und die Begegnung (mit Uns) im Jenseits leugneten, werden zur Bestrafung vorgefuhrt"},
  {"surah_number": 30, "verse_number": 17, "content": "Preis sei denn Allah, wenn ihr den Abend und wenn ihr den Morgen verbringt"},
  {"surah_number": 30, "verse_number": 18, "content": "denn Ihm gebuhrt alles Lob in den Himmeln und auf Erden und am Abend und zu eurer Mittagsruhe"},
  {"surah_number": 30, "verse_number": 19, "content": "Er laßt das Lebendige aus dem Toten und das Tote aus dem Lebendigen hervorgehen; und Er belebt die Erde nach ihrem Tod, und in gleicher Weise sollt ihr wieder hervorgebracht werden"},
  {"surah_number": 30, "verse_number": 20, "content": "Und unter Seinen Zeichen ist dies, daß Er euch aus Erde erschuf; alsdann, seht, seid ihr Menschen geworden, die sich vermehren"},
  {
    "surah_number": 30,
    "verse_number": 21,
    "content": "Und unter Seinen Zeichen ist dies, daß Er Gattinnen fur euch aus euch selber schuf, auf daß ihr Frieden bei ihnen fandet; und Er hat Zuneigung und Barmherzigkeit zwischen euch gesetzt. Hierin liegen wahrlich Zeichen fur ein Volk, das nachdenkt"
  },
  {"surah_number": 30, "verse_number": 22, "content": "Und unter Seinen Zeichen sind die Schopfung der Himmel und der Erde und die Verschiedenheit eurer Sprachen und Farben. Hierin sind wahrlich Zeichen fur die Wissenden"},
  {"surah_number": 30, "verse_number": 23, "content": "Und zu Seinen Zeichen zahlt euer Schlafen bei Nacht und euer Trachten nach Seiner Gnadenfulle bei Tage. Hierin sind wahrlich Zeichen fur ein Volk, das hort"},
  {"surah_number": 30, "verse_number": 24, "content": "Und zu Seinen Zeichen gehort dies, daß Er euch den Blitz zu Furcht und Hoffnung zeigt und Wasser vom Himmel herniedersendet und damit die Erde nach ihrem Tod belebt. Hierin sind wahrlich Zeichen fur ein Volk, das begreift"},
  {"surah_number": 30, "verse_number": 25, "content": "Und unter Seinen Zeichen ist dies, daß Himmel und Erde auf Sein Geheiß hin fest stehen. Als dann, wenn Er euch mit einem Ruf aus der Erde ruft, seht, dann werdet ihr (daraus) hervorgehen"},
  {"surah_number": 30, "verse_number": 26, "content": "Und Sein ist, wer in den Himmeln und auf der Erde ist. Alle sind Ihm gehorsam"},
  {"surah_number": 30, "verse_number": 27, "content": "Und Er ist es, Der die Schopfung hervorbringt, dann wiederholt Er sie, und dies fallt Ihm noch leichter. Und Er Selbst stellt das schonste Gleichnis in den Himmeln und auf Erden dar; und Er ist der Allmachtige, der Allweise"},
  {
    "surah_number": 30,
    "verse_number": 28,
    "content": "Er pragt euch ein Gleichnis eurer eigenen Lage. Habt ihr unter denen, die ihr von Rechts wegen besitzt, Teilhaber an dem, was Wir euch beschert haben? Seid ihr darin also gleich (und) furchtet sie, wie ihr einander furchtet? So machen Wir die Zeichen klar fur ein Volk, das begreift"
  },
  {"surah_number": 30, "verse_number": 29, "content": "Doch die Ungerechten folgen ohne Wissen ihren personlichen Neigungen. Und wer kann den leiten, den Allah zum Irrenden erklart? Fur solche wird es keine Helfer geben"},
  {
    "surah_number": 30,
    "verse_number": 30,
    "content": "So richte dein Antlitz in aufrichtiger Weise auf den Glauben; (dies entspricht) der naturlichen Veranlagung, mit der Allah die Menschen geschaffen hat. Es gibt keine Veranderung an Allahs Schopfung. Das ist der bestandige Glaube. Allein die meisten Menschen wissen es nicht"
  },
  {"surah_number": 30, "verse_number": 31, "content": "Wendet euch zu Ihm und furchtet Ihn und verrichtet das Gebet und seid nicht unter den Gotzendienern"},
  {"surah_number": 30, "verse_number": 32, "content": "Von denen, die ihren Glauben gespalten haben und zu Parteien geworden sind - jede Partei freut sich uber das, was sie selbst hat"},
  {"surah_number": 30, "verse_number": 33, "content": "Und wenn die Menschen ein Schaden trifft, dann rufen sie ihren Herrn an und wenden sich reumutig zu Ihm; hernach aber, wenn Er sie dann von Seiner Barmherzigkeit kosten laßt, siehe, dann stellen einige von ihnen ihrem Herrn Gotter zur Seite"},
  {"surah_number": 30, "verse_number": 34, "content": "und verleugnen somit das, was Wir ihnen gegeben haben. Ergotzt euch denn (eine Weile), bald jedoch werdet ihr es wissen"},
  {"surah_number": 30, "verse_number": 35, "content": "Haben Wir ihnen etwa eine Ermachtigung niedergesandt, die fur das sprache, was sie Ihm zur Seite stellen"},
  {"surah_number": 30, "verse_number": 36, "content": "Und wenn Wir die Menschen Barmherzigkeit kosten lassen, freuen sie sich daruber; doch wenn sie ein Ubel um dessentwillen befallt, was ihre eigenen Hande vorausgeschickt haben, siehe, dann verzweifeln sie"},
  {"surah_number": 30, "verse_number": 37, "content": "Haben sie denn nicht gesehen, daß Allah dem, dem Er will, die Mittel zum Unterhalt erweitert und beschrankt? Hierin sind wahrlich Zeichen fur Leute, die glauben"},
  {"surah_number": 30, "verse_number": 38, "content": "So gib dem Verwandten, was ihm zusteht, wie auch dem Bedurftigen und dem Sohn des Weges. Das ist das Beste fur die, die nach Allahs Antlitz verlangen, und sie sind die Erfolgreichen"},
  {
    "surah_number": 30,
    "verse_number": 39,
    "content": "Und was immer ihr auf Zinsen verleiht, damit es sich mit dem Gut der Menschen ver mehre; es vermehrt sich nicht vor Allah; doch was ihr an Zakah entrichtet, indem ihr nach Allahs Antlitz verlangt, sie sind es, die vielfache Mehrung empfangen werden"
  },
  {
    "surah_number": 30,
    "verse_number": 40,
    "content": "Allah ist es, Der euch erschaffen hat, und dann hat Er euch versorgt; dann wird Er euch sterben lassen, und dann wird Er euch wieder lebendig machen. Ist etwa unter euren Gottern einer, der davon etwas vollbringen konnte? Gepriesen sei Er und Hoch Erhaben uber das, was sie anbeten"
  },
  {"surah_number": 30, "verse_number": 41, "content": "Unheil ist auf dem Festland und auf dem Meer sichtbar geworden um dessentwillen, was die Hande der Menschen gewirkt haben, auf daß Er sie die (Fruchte) so mancher ihrer Handlungen kosten lasse, damit sie sich besannen"},
  {"surah_number": 30, "verse_number": 42, "content": "Sprich: \"Wandert auf Erden umher und seht, wie das Ende derer zuvor war! Die meisten von ihnen waren Gotzendiener"},
  {"surah_number": 30, "verse_number": 43, "content": "Richte also dein Antlitz auf den bestandigen Glauben, bevor der Tag kommt, an dem es gegen Allah keine Wehr gibt. An jenem Tage werden sie auseinan derbrechen"},
  {"surah_number": 30, "verse_number": 44, "content": "Wenn jemand unglaubig ist; so lastet auf ihm sein Unglaube! Und der, der Rechtes tut, bereitet es fur sich selbst vor"},
  {"surah_number": 30, "verse_number": 45, "content": "auf daß Er aus Seiner Gnadenfulle diejenigen belohnen moge, die glauben und das Rechte tun. Wahrlich, Er liebt die Unglaubigen nicht"},
  {
    "surah_number": 30,
    "verse_number": 46,
    "content": "Und zu Seinen Zeichen gehort dies, daß Er die Winde mit frohen Botschaften entsendet, auf daß Er euch von Seiner Barmherzigkeit kosten lasse, und auf daß die Schiffe auf Sein Geheiß hin fahren mogen, und auf daß ihr nach Seiner Huld trachtet, und auf daß ihr dankbar sein moget"
  },
  {"surah_number": 30, "verse_number": 47, "content": "Und wahrlich, Wir schickten schon Gesandte vor dir zu ihren eigenen Leuten. Sie brachten ihnen klare Beweise. Dann bestraften Wir die Schuldigen; und es oblag Uns, den Glaubigen zu helfen"},
  {
    "surah_number": 30,
    "verse_number": 48,
    "content":
        "Allah ist es, Der die Winde entsendet, so daß sie Wolken zusammentreiben. Dann breitet Er sie am Himmel aus, wie Er will, und hauft sie Schicht auf Schicht auf; und du siehst den Regen aus ihrer Mitte hervorbrechen. Und wenn Er ihn auf die von Seinen Dienern, die Er will, fallen laßt, siehe, dann ahnen sie Gutes"
  },
  {"surah_number": 30, "verse_number": 49, "content": "obwohl sie zuvor, ehe er auf sie niedergesandt wurde, hoffnungslos waren"},
  {"surah_number": 30, "verse_number": 50, "content": "Schau dann auf die Spuren von Allahs Barmherzigkeit, wie Er die Erde nach ihrem Tode belebt. Wahrlich, Derselbe wird auch die Toten erwecken; denn Er hat Macht uber alle Dinge"},
  {"surah_number": 30, "verse_number": 51, "content": "Und wenn Wir einen Wind entsendeten und sie sie (die Ernte) vergilbt (versengt) sahen, so wurden sie gewiß danach undankbar sein"},
  {"surah_number": 30, "verse_number": 52, "content": "Weder kannst du die Toten horend machen, noch kannst du die Tauben den Ruf horen lassen, wenn sie (Allah) den Rucken kehren"},
  {"surah_number": 30, "verse_number": 53, "content": "noch wirst du die Blinden aus ihrem Irrweg leiten konnen. Nur die wirst du horend machen, die an Unsere Zeichen glauben und sich (Ihm) ergeben"},
  {"surah_number": 30, "verse_number": 54, "content": "Allah ist es, Der euch in Schwache erschaffen hat, und nach der Schwache gab Er (euch) Starke. Dann wiederum, nach der Starke, gab Er (euch) Schwache und graues Haar. Er schafft, was Er will. Und Er ist der Allwissende, der Allmachtige"},
  {"surah_number": 30, "verse_number": 55, "content": "Und an dem Tage, wo die Stunde herankommt, werden die Schuldigen schworen, daß sie nicht langer als eine Stunde (auf Erden) weilten - so sehr sind sie an (Selbsttauschung gewohnt)"},
  {"surah_number": 30, "verse_number": 56, "content": "Doch die, denen Kenntnis und Glauben verliehen wurden, sagen: \"lhr habt wahrlich gemaß dem Buche Allahs bis zum Tage der Auferstehung (im Grabe) geweilt. Und das ist der Tag der Auferstehung, allein ihr wolltet es nicht wissen"},
  {"surah_number": 30, "verse_number": 57, "content": "So werden denn an jenem Tage den Ungerechten ihre Ausreden nichts fruchten, noch werden sie Gunst finden"},
  {"surah_number": 30, "verse_number": 58, "content": "Und wahrlich, Wir haben den Menschen in diesem Quran allerlei Gleichnisse gepragt; aber wenn du ihnen ein Zeichen bringst, dann werden jene, die unglaubig sind, sicher sagen: \"lhr bringt nur Unsinn vor"},
  {"surah_number": 30, "verse_number": 59, "content": "Und so versiegelt Allah die Herzen derer, die unwissend sind"},
  {"surah_number": 30, "verse_number": 60, "content": "So sei geduldig. Wahrlich, das Versprechen Allahs ist wahr. Und laß dich nicht von jenen ins Wanken bringen, die keine Gewißheit haben"},
  {"surah_number": 31, "verse_number": 1, "content": "Alif Lam Mim"},
  {"surah_number": 31, "verse_number": 2, "content": "Dies sind die Verse der vollkommenen Schrift"},
  {"surah_number": 31, "verse_number": 3, "content": "eine Fuhrung und eine Barmherzigkeit fur jene, die Gutes tun"},
  {"surah_number": 31, "verse_number": 4, "content": "die das Gebet verrichten und die Zakah entrichten und fest ans Jenseits glauben"},
  {"surah_number": 31, "verse_number": 5, "content": "Sie sind es, die der Fuhrung ihres Herrn folgen, und sie sind es, die erfolgreich sind"},
  {"surah_number": 31, "verse_number": 6, "content": "Und unter den Menschen gibt es solche, die leeres Gerede vorziehen, um (Menschen) ohne Wissen von Allahs Weg hinweg in die Irre zu fuh ren, und um damit Spott zu treiben. Solchen (Menschen) harrt eine schmahliche Strafe"},
  {"surah_number": 31, "verse_number": 7, "content": "Und wenn ihm Unsere Verse verlesen werden, so kehrt er sich uberheblich (von ihnen) ab, als hatte er sie nicht gehort, als waren seine Ohren schwerhorig. So kunde ihm eine schmerzliche Strafe an"},
  {"surah_number": 31, "verse_number": 8, "content": "Wahrlich, denen, die da glauben und gute Werke tun, werden Garten der Wonne zuteil sein"},
  {"surah_number": 31, "verse_number": 9, "content": "Darin werden sie auf ewig weilen. (Dies ist) eine Verheißung Allahs in Wahrheit! Und Er ist der Allmachtige, der Allweise"},
  {
    "surah_number": 31,
    "verse_number": 10,
    "content": "Er hat den Himmel ohne Stutzen erschaffen, wie ihr seht, und Er hat in der Erde feste Berge gegrundet, damit sie nicht ins Schwanken gerat, und hat allerlei Getier uber sie verstreut. Und Wir senden Wasser aus dem Himmel nieder und lassen jede herrliche Gattung auf ihr sprießen"
  },
  {"surah_number": 31, "verse_number": 11, "content": "Dies ist Allahs Schopfung. Zeigt mir nun, was andere außer Ihm geschaffen haben. Nein, die Ungerechten befinden sich in einem offenkundigen Irrtum"},
  {"surah_number": 31, "verse_number": 12, "content": "Und wahrlich, Wir verliehen Luqman Weisheit, auf daß er Allah dankbar sein moge: denn wer da dankbar ist, der ist dankbar zum Besten seiner eigenen Seele. Ist aber einer undankbar, dann ist Allah wahrlich auf keinen angewiesen, Preiswurdig"},
  {"surah_number": 31, "verse_number": 13, "content": "Und da sagte Luqman zu seinem Sohn, indem er ihn ermahnte: \"O mein Sohn, setze Allah keine Gotter zur Seite; denn Gotzendienst ist wahrlich ein gewaltiges Unrecht"},
  {"surah_number": 31, "verse_number": 14, "content": "Und Wir haben dem Menschen im Hinblick auf seine Eltern anbefohlen seine Mutter trug ihn in Schwache uber Schwache, und seine Entwohnung erfordert zwei Jahre -: \"Sei Mir und deinen Eltern dankbar. Zu Mir ist die Heimkehr"},
  {
    "surah_number": 31,
    "verse_number": 15,
    "content":
        "Doch wenn sie dich auffordern, Mir das zur Seite zu setzen, wovon du keine Kenntnis hast, dann gehorche ihnen nicht. In weltlichen Dingen aber verkehre mit ihnen auf gutige Weise. Doch folge dem Weg dessen, der sich zu Mir wendet. Dann werdet ihr zu Mir zuruckkehren, und Ich werde euch das verkunden, was ihr getan habt"
  },
  {"surah_number": 31, "verse_number": 16, "content": "O mein Sohn, hatte es auch nur das Gewicht eines Senfkorns und ware es in einem Felsen oder in den Himmeln oder in der Erde, Allah wurde es gewiß hervorbringen. Wahrlich, Allah ist Gnadig, Kundig"},
  {"surah_number": 31, "verse_number": 17, "content": "O mein Sohn, verrichte das Gebet und gebiete Gutes und verbiete Boses und ertrage geduldig, was dich auch treffen mag. Das ist wahrlich eine Starke in allen Dingen"},
  {"surah_number": 31, "verse_number": 18, "content": "Und weise den Menschen nicht verachtlich deine Wange und schreite nicht mit Heiterkeit auf Erden; denn Allah liebt keine eingebildeten Prahler"},
  {"surah_number": 31, "verse_number": 19, "content": "Und schreite gemessenen Schritts und dampfe deine Stimme; denn wahrlich, die widerwartigste der Stimmen ist die Stimme des Esels"},
  {
    "surah_number": 31,
    "verse_number": 20,
    "content":
        "Habt ihr denn nicht gesehen, daß Allah euch alles dienstbar gemacht hat, was in den Himmeln und was auf der Erde ist, und (daß Er) Seine Wohltaten reichlich uber euch ergossen hat - in sichtbarer und unsichtbarer Weise? Und doch gibt es unter den Menschen so manchen, der ohne Kenntnis und ohne Fuhrung und ohne ein erleuchtendes Buch (zu besitzen) uber Allah streitet"
  },
  {"surah_number": 31, "verse_number": 21, "content": "Und wenn zu ihnen gesagt wird: \"Folgt dem, was Allah herniedergesandt hat\", dann sagen sie: \"Nein, wir wollen dem folgen wobei wir unsere Vater vorfanden.\" Wie? Selbst dann wenn Satan sie zu der Strafe des brennenden Feuers ladt"},
  {"surah_number": 31, "verse_number": 22, "content": "Und der aber, der sein Antlitz auf Allah richtet und Gutes tut, hat wahrlich die festeste Handhabe ergriffen. Und bei Allah ruht das Ende aller Dinge"},
  {"surah_number": 31, "verse_number": 23, "content": "Und wenn jemand unglaubig ist, so laß dich nicht durch seinen Unglauben bekummern. Zu Uns werden sie heimkehren, dann werden Wir ihnen verkunden, was sie getan haben; denn Allah weiß recht wohl, was in den Herzen ist"},
  {"surah_number": 31, "verse_number": 24, "content": "Wir lassen sie sich eine kleine Weile ergotzen, dann aber werden Wir sie strenger Strafe zutreiben"},
  {"surah_number": 31, "verse_number": 25, "content": "Und wenn du sie fragst: \"Wer schuf die Himmel und die Erde?\" dann werden sie gewiß sagen: \"Allah.\" Sprich: \"Alles Lob gebuhrt Allah.\" Jedoch die meisten von ihnen wissen es nicht"},
  {"surah_number": 31, "verse_number": 26, "content": "Allahs ist, was in den Himmeln und auf Erden ist. Wahrlich, Allah ist Der, Der auf keinen angewiesen ist, der Preiswurdige"},
  {"surah_number": 31, "verse_number": 27, "content": "Und wenn alle Baume, die auf der Erde sind, Schreibrohre waren und der Ozean (Tinte), und sieben Ozeane wurden sie mit Nachschub versorgen, selbst dann konnten Allahs Worte nicht erschopft werden. Wahrlich, Allah ist Allmachtig, Allweise"},
  {"surah_number": 31, "verse_number": 28, "content": "Eure Erschaffung und eure Auferstehung sind (fur Ihn so leicht wie die) eines einzigen Wesens. Wahrlich, Allah ist Allhorend, Allsehend"},
  {
    "surah_number": 31,
    "verse_number": 29,
    "content":
        "Hast du denn nicht gesehen, daß Allah die Nacht in den Tag und den Tag in die Nacht ubergehen laßt, und daß Er (euch) die Sonne und den Mond dienstbar gemacht hat, so daß jedes (Gestirn auf seiner Bahn) zu einem bestimmten Ziel lauft, und daß Allah wohl dessen kundig ist, was ihr tut"
  },
  {"surah_number": 31, "verse_number": 30, "content": "Dies (ist so), weil Allah allein die Wahrheit ist und was sie sonst außer Ihm anrufen, ist das Falsche und weil Allah, Er, der Erhabene ist, der Große"},
  {"surah_number": 31, "verse_number": 31, "content": "Hast du denn nicht gesehen, daß die Schiffe durch Allahs Gnade auf dem Meer fahren, auf daß Er euch Seine Zeichen zeige? Hierin sind wahrlich Zeichen fur jeden Standhaften, Dankbaren"},
  {
    "surah_number": 31,
    "verse_number": 32,
    "content": "Und wenn Wogen sie gleich Hullen bedecken, dann rufen sie Allah in lauterem Glauben an; doch rettet Er sie dann ans Land, so sind es (nur) einige von ihnen, die einen gemaßigten Standpunkt vertreten. Und niemand leugnet Unsere Zeichen, außer allen Treulosen, Undankbaren"
  },
  {
    "surah_number": 31,
    "verse_number": 33,
    "content":
        "O ihr Menschen, furchtet euren Herrn und furchtet den Tag, da kein Vater fur seinen Sohn etwas unternehmen kann und kein Sohn fur seinen Vater im geringsten etwas unternehmen kann. Wahrlich, Allahs Verheißung ist wahr. Darum soll das Leben dieser Welt euch nicht verfuhren, noch sollt ihr euch uber Allah mit (eurem) Denken selbst tauschen"
  },
  {
    "surah_number": 31,
    "verse_number": 34,
    "content": "Wahrlich, bei Allah allein ist die Kenntnis der Stunde. Er sendet den Regen nieder, und Er weiß, was in den Mutterschoßen ist. Und niemand weiß, was er sich morgen zufugen wird, und niemand weiß, in welchem Lande er sterben wird. Wahrlich, Allah ist Allwissend, Allkundig"
  },
  {"surah_number": 32, "verse_number": 1, "content": "Alif Lam Mim"},
  {"surah_number": 32, "verse_number": 2, "content": "Die Offenbarung des Buches stammt ohne Zweifel vom Herrn der Welten"},
  {"surah_number": 32, "verse_number": 3, "content": "Oder sagen sie: \"Er hat es erdichtet?\" Nein, es ist die Wahrheit von deinem Herrn, auf daß du ein Volk warnest, zu dem vor dir kein Warner gekommen ist, damit sie dem rechten Weg folgen mogen"},
  {
    "surah_number": 32,
    "verse_number": 4,
    "content": "Allah ist es, Der die Himmel und die Erde und alles, was zwischen beiden ist, in sechs Tagen erschuf; dann wandte Er Sich Seinem Reich majestatisch zu. Ihr habt außer Ihm weder einen wahren Freund noch einen Fursprecher. Wollt ihr euch denn nicht ermahnen lassen"
  },
  {"surah_number": 32, "verse_number": 5, "content": "Er verwaltet die An gelegenheiten von Himmel und Erde, (und) dann werden sie wieder zu Ihm emporsteigen in einem Tage, dessen Lange nach eurer Zeitrechnung tausend Jahre betragt"},
  {"surah_number": 32, "verse_number": 6, "content": "Er ist der Kenner des Verborgenen und des Sichtbaren, der Allmachtige, der Barmherzige"},
  {"surah_number": 32, "verse_number": 7, "content": "Der alles gut gemacht hat, was Er erschuf. Und Er begann die Schopfung des Menschen aus Ton"},
  {"surah_number": 32, "verse_number": 8, "content": "Hierauf machte Er seine Nachkommenschaft aus einer nichtbeachtenswerten Flussigkeit"},
  {"surah_number": 32, "verse_number": 9, "content": "Dann formte Er ihn und hauchte ihm von Seinem Geist ein. Und Er hat euch Gehor und Augenlicht und Herzen gegeben. Doch euer Dank ist recht gering"},
  {"surah_number": 32, "verse_number": 10, "content": "Und sie sagen: \"Wie? Wenn wir in der Erde verschwunden sind, dann sollen wir in einer neuen Schopfung sein?\" Nein, sie sind hinsichtlich der Begegnung mit ihrem Herrn unglaubig"},
  {"surah_number": 32, "verse_number": 11, "content": "Sprich: \"Der Engel des Todes, der uber euch eingesetzt wurde wird euch abberufen; dann werdet ihr zu eurem Herrn zuruckgebracht"},
  {"surah_number": 32, "verse_number": 12, "content": "Konntest du nur sehen, wie die Schuldigen ihre Kopfe vor ihrem Herrn hangen lassen werden: \"Unser Herr, nun haben wir gesehen und gehort, so sende uns zuruck, auf daß wir Gutes tun mogen; denn nun haben wir Gewißheit"},
  {"surah_number": 32, "verse_number": 13, "content": "Und hatten Wir gewollt, hatten Wir jedem seinen Weg gezeigt; jedoch Mein Wort soll geschehen: \"Fullen will Ich Gahannam mit den Ginn und mit den Menschen allzumal"},
  {"surah_number": 32, "verse_number": 14, "content": "So kostet (die Strafe); denn ihr vergaßt das Eintreffen dieses euren Tages. (Auch) Wir haben euch vergessen. Kostet denn die Strafe der Ewigkeit um dessentwillen, was ihr getan habt"},
  {"surah_number": 32, "verse_number": 15, "content": "Nur jene glauben an Unsere Zeichen, die sich, wenn sie an sie gemahnt werden, niederwerfen und das Lob ihres Herrn preisen; und sie sind nicht hochmutig"},
  {"surah_number": 32, "verse_number": 16, "content": "Ihre Seiten halten sich fern von (ihren) Betten; sie rufen ihren Herrn in Furcht und Hoffnung an und spenden von dem, was Wir ihnen gegeben haben"},
  {"surah_number": 32, "verse_number": 17, "content": "Doch niemand weiß, welche Augenweide fur sie als Lohn fur ihre Taten verborgen ist"},
  {"surah_number": 32, "verse_number": 18, "content": "Ist wohl jener, der glaubig ist, dem gleich, der frevelt? Sie sind nicht gleich"},
  {"surah_number": 32, "verse_number": 19, "content": "Jene aber, die glauben und gute Werke tun, werden Garten der Geborgenheit haben als einen Wohnsitz fur das, was sie getan haben"},
  {"surah_number": 32, "verse_number": 20, "content": "Jene aber, die ungehorsam sind - ihre Herberge wird das Feuer sein. Sooft sie daraus entfliehen wollen, werden sie wieder dahin zuruckgetrieben, und es wird zu ihnen gesprochen: \"Kostet nun die Strafe des Feuers, die ihr zu leugnen pflegtet"},
  {"surah_number": 32, "verse_number": 21, "content": "Und wahrlich, Wir werden sie vor der großeren Strafe von der diesseitigen Strafe kosten lassen, damit sie sich vielleicht doch noch bekehren"},
  {"surah_number": 32, "verse_number": 22, "content": "Und wer ist ungerechter als jener, der an die Zeichen seines Herrn gemahnt wird und sich dann doch von ihnen abwendet? Wahrlich, Wir werden Uns an den Sundern rachen"},
  {"surah_number": 32, "verse_number": 23, "content": "Und wahrlich, Wir gaben Moses das Buch - zweifle deshalb nicht daran, daß auch du es bekommst -, und Wir machten es zu einer Fuhrung fur die Kinder Israels"},
  {"surah_number": 32, "verse_number": 24, "content": "Und Wir erweckten Fuhrer aus ihrer Mitte, die (das Volk) nach Unserem Gebot leiteten, weil sie geduldig waren und fest an Unsere Zeichen glaubten"},
  {"surah_number": 32, "verse_number": 25, "content": "Wahrlich, dein Herr ist es, Der zwischen ihnen am Tage der Auferstehung uber das richten wird, woruber sie uneinig waren"},
  {"surah_number": 32, "verse_number": 26, "content": "Ist ihnen nicht klar, wie viele Geschlechter Wir schon vor ihnen vernichtet haben, in deren Wohnstatten sie nun wandeln? Hierin liegen wahrlich Zeichen. Wollen sie also nicht horen"},
  {"surah_number": 32, "verse_number": 27, "content": "Haben sie denn nicht gesehen, daß Wir das Wasser auf das durre Land treiben und dadurch Gewachs hervorbringen, an dem ihr Vieh und auch sie selber sich laben? Wollen sie also nicht sehen"},
  {"surah_number": 32, "verse_number": 28, "content": "Und sie sagen: \"Wann wird dieser Sieg kommen, wenn ihr die Wahrheit redet"},
  {"surah_number": 32, "verse_number": 29, "content": "Sprich: \"Am Tage des Sieges wird den Unglaubigen ihr Glaube nichts nutzen, noch werden sie Aufschub erlangen"},
  {"surah_number": 32, "verse_number": 30, "content": "So wende dich denn von ihnen ab und warte; auch sie warten"},
  {"surah_number": 33, "verse_number": 1, "content": "O Prophet, furchte Allah und gehorche nicht den Unglaubigen und den Heuchlern. Wahrlich, Allah ist Allwissend, Allweise"},
  {"surah_number": 33, "verse_number": 2, "content": "Und folge dem, was dir von deinem Herrn offenbart wird. Wahrlich, Allah ist wohl all dessen kundig, was ihr tut"},
  {"surah_number": 33, "verse_number": 3, "content": "Und vertraue auf Allah; denn Allah genugt als Beschutzer"},
  {
    "surah_number": 33,
    "verse_number": 4,
    "content":
        "Allah hat keinem Manne zwei Herzen in seinem Inneren gegeben, noch hat Er jene unter euren Frauen, von denen ihr euch scheidet mit der Formel, sie seien euch verwehrt wie der Rucken eurer Mutter, zu euren (wirklichen) Muttern gemacht, noch hat Er eure adoptierten Sohne zu euren (wirklichen) Sohnen gemacht. Das ist (nur) Gerede aus euren Mundern; Allah aber spricht die Wahrheit, und Er zeigt (euch) den Weg"
  },
  {
    "surah_number": 33,
    "verse_number": 5,
    "content":
        "Nennt sie (eure Adoptivsohne) nach ihren Vatern. Das ist gerechter vor Allah. Wenn ihr jedoch ihre Vater nicht kennt, so sind sie eure Bruder im Glauben und eure Schutzlinge. Und wenn ihr versehentlich darin gefehlt habt, so ist das keine Sunde von euch, sondern (Sunde ist) nur das, was eure Herzen vorsatzlich tun. Und Allah ist wahrlich Allverzeihend, Barmherzig"
  },
  {
    "surah_number": 33,
    "verse_number": 6,
    "content":
        "Der Prophet steht den Glaubigen naher als sie sich selber, und seine Frauen sind ihre Mutter. Und Blutsverwandte sind einander naher als die (ubrigen) Glaubigen und die Ausgewanderten - gemaß dem Buche Allahs -, es sei denn, daß ihr euren Schutzlingen Gute erweist. Das ist in dem Buche niedergeschrieben"
  },
  {"surah_number": 33, "verse_number": 7, "content": "Und dann gingen Wir mit den Propheten den Bund ein und mit dir und mit Noah und Abraham und Moses und mit Jesus, dem Sohn der Maria. Und Wir gingen mit ihnen einen gewaltigen Bund ein"},
  {"surah_number": 33, "verse_number": 8, "content": "auf daß Er die Wahrhaftigen nach ihrer Wahrhaftigkeit befrage. Und fur die Unglaubigen hat Er eine schmerzliche Strafe bereitet"},
  {"surah_number": 33, "verse_number": 9, "content": "O ihr, die ihr glaubt! Gedenkt der Gnade Allahs gegen euch, als Heerscharen gegen euch heranruckten; und Wir sandten gegen sie einen Wind und Heerscharen, die ihr nicht gesehen hattet. Und Allah sieht, was ihr tut"},
  {"surah_number": 33, "verse_number": 10, "content": "Als sie von oben und von unten her uber euch kamen, und als die Augen rollten und die Herzen in die Kehle stiegen und ihr verschiedene Gedanken uber Allah hegtet"},
  {"surah_number": 33, "verse_number": 11, "content": "damals wurden die Glaubigen gepruft, und sie wurden in heftigem Maße erschuttert"},
  {"surah_number": 33, "verse_number": 12, "content": "Und da sagten die Heuchler und die, in deren Herzen Krankheit war: \"Allah und Sein Gesandter haben uns nur Trug verheißen"},
  {
    "surah_number": 33,
    "verse_number": 13,
    "content":
        "Und alsdann sagte eine Gruppe von ihnen: \"O ihr Leute von Yatrib, ihr konnt (ihnen) nicht standhalten, darum kehrt zuruck.\" Und ein Teil von ihnen bat den Propheten um Erlaubnis und sagte: \"Unsere Wohnungen sind schutzlos.\" Und sie waren nicht schutzlos. Sie wollten eben nur fliehen"
  },
  {"surah_number": 33, "verse_number": 14, "content": "Und wenn der Zutritt gegen sie von allen Seiten her erzwungen wurde, und wenn sie dann aufgefordert wurden, (vom Islam) abzufallen, wurden sie es sogleich tun; dann wurden sie darin nicht lange weilen konnen"},
  {"surah_number": 33, "verse_number": 15, "content": "Und sie hatten doch in Wahrheit zuvor mit Allah den Bund geschlossen, daß sie nicht den Rucken zur Flucht wenden wurden. Und uber den Bund mit Allah muß Rechenschaft abgelegt werden"},
  {"surah_number": 33, "verse_number": 16, "content": "Sprich: \"Die Flucht wird euch nimmermehr nutzen, wenn ihr dem Tod oder der Niedermetzelung entflieht; dann werdet ihr nur wenig genießen"},
  {"surah_number": 33, "verse_number": 17, "content": "Sprich: \"Wer ist es, der euch vor Allah schutzen kann, wenn Er vorhat, uber euch ein Ubel zu verhangen, oder wenn Er vorhat, euch Barmherzigkeit zu erweisen? Und sie werden fur sich außer Allah weder Beschutzer noch Helfer finden"},
  {"surah_number": 33, "verse_number": 18, "content": "Allah kennt doch diejenigen unter euch, die (die Menschen vom Weg) abhalten, und diejenigen, die zu ihren Brudern sagen: \"Kommt her zu uns\"; und sie lassen sich nur selten in Kriege ein"},
  {
    "surah_number": 33,
    "verse_number": 19,
    "content":
        "(und sind) euch gegenuber geizig. Naht aber Gefahr, dann siehst du sie nach dir ausschauen - mit rollenden Augen wie einer, der aus Todesfurcht in Ohnmacht fallt. Doch wenn dann die Angst vorbei ist, dann treffen sie euch mit scharfen Zungen in ihrer Gier nach Gut. Diese haben nicht geglaubt; darum hat Allah ihre Werke zunichte gemacht. Und das ist fur Allah ein leichtes"
  },
  {
    "surah_number": 33,
    "verse_number": 20,
    "content": "Sie meinen, daß die Verbundeten noch nicht abgezogen seien; und wenn die Verbundeten kommen sollten, so wurden sie lieber bei den nomadischen Arabern in der Wuste sein und dort Nachrichten uber euch erfragen. Und wenn sie bei euch waren, so wurden sie nur wenig kampfen"
  },
  {"surah_number": 33, "verse_number": 21, "content": "Wahrlich, ihr habt an dem Gesandten Allahs ein schones Vorbild fur jeden, der auf Allah und den Letzten Tag hofft und Allahs haufig gedenkt"},
  {"surah_number": 33, "verse_number": 22, "content": "Und als die Glaubigen die Verbundeten sahen, da sagten sie: \"Das ist es, was Allah und Sein Gesandter uns verheißen haben; und Allah und Sein Gesandter sprachen die Wahrheit.\" Und es verstarkte nur ihren Glauben und ihre Ergebung"},
  {
    "surah_number": 33,
    "verse_number": 23,
    "content": "Unter den Glaubigen sind Leute, die dem Bundnis, das sie mit Allah geschlossen hatten, die Treue hielten. Es sind welche unter ihnen, die ihr Gelubde erfullt haben, und welche, die noch warten, und sie haben nichts verandert, nicht im geringsten"
  },
  {"surah_number": 33, "verse_number": 24, "content": "Allah belohne die Wahrhaftigen fur ihre Wahrhaftigkeit und bestrafe die Heuchler, wenn Er will, oder wende Sich ihnen in Barmherzigkeit zu. Wahrlich, Allah ist Allverzeihend, Barmherzig"},
  {"surah_number": 33, "verse_number": 25, "content": "Und Allah schlug die Unglaubigen in ihrem Grimm zuruck; sie erlangten keinen Vorteil. Und Allah ersparte den Glaubigen den Kampf. Und Allah ist Allmachtig, Erhaben"},
  {"surah_number": 33, "verse_number": 26, "content": "Und Er brachte die aus dem Volk der Schrift, die ihnen halfen, von ihren Burgen herunter und warf Schrecken in ihre Herzen. Einen Teil totetet ihr, und einen Teil nahmt ihr gefangen"},
  {"surah_number": 33, "verse_number": 27, "content": "Und Er ließ euch ihr Land erben und ihre Hauser und ihren Besitz und ein Land, in das ihr nie den Fuß gesetzt hattet. Und Allah hat Macht uber alle Dinge"},
  {"surah_number": 33, "verse_number": 28, "content": "O Prophet! Sprich zu deinen Frauen: \"Wenn ihr das Leben in dieser Welt und seinen Schmuck begehrt, so kommt, ich will euch eine Gabe reichen und euch dann auf schone Art entlassen"},
  {"surah_number": 33, "verse_number": 29, "content": "Doch wenn ihr Allah und Seinen Gesandten und die Wohnstatt des Jenseits begehrt, dann wahrlich, hat Allah fur die unter euch, die Gutes tun, einen herrlichen Lohn, bereitet"},
  {"surah_number": 33, "verse_number": 30, "content": "O Frauen des Propheten! Wenn eine von euch eine offenkundige Schandlichkeit begeht, so wird ihr die Strafe verdoppelt. Und das ist fur Allah ein leichtes"},
  {"surah_number": 33, "verse_number": 31, "content": "Doch welche von euch Allah und Seinem Gesandten gehorsam ist und Gutes tut - ihr werden Wir ihren Lohn zweimal geben; und Wir haben fur sie eine ehrenvolle Versorgung bereitet"},
  {"surah_number": 33, "verse_number": 32, "content": "O Frauen des Propheten, ihr seid nicht wie andere Frauen! Wenn ihr gottesfurchtig sein wollt, dann seid nicht unterwurfig im Reden, damit nicht der, in dessen Herzen Krankheit ist, Erwartungen hege, sondern redet in geziemenden Worten"},
  {
    "surah_number": 33,
    "verse_number": 33,
    "content":
        "Und bleibt in euren Hausern und prunkt nicht wie in den Zeiten der Gahiliya und verrichtet das Gebet und entrichtet die Zakah und gehorcht Allah und Seinem Gesandten. Allah will nur jegliches Ubel von euch verschwinden lassen, ihr Leute des Hauses, und euch stets in vollkommener Weise rein halten"
  },
  {"surah_number": 33, "verse_number": 34, "content": "Und gedenkt der Verse Allahs und der Weisheiten, die in euren Hausern verlesen werden; denn Allah ist Gutig, Allkundig"},
  {
    "surah_number": 33,
    "verse_number": 35,
    "content":
        "Wahrlich, die muslimischen Manner und die muslimischen Frauen, die glaubigen Manner und die glaubigen Frauen, die gehorsamen Manner und die gehorsamen Frauen, die wahrhaftigen Manner und die wahrhaftigen Frauen, die geduldigen Manner und die geduldigen Frauen, die demutigen Manner und die demutigen Frauen, die Manner, die Almosen geben, und die Frauen, die Almosen geben, die Manner, die fasten, und die Frauen, die fasten, die Manner, die ihre Keuschheit wahren, und die Frauen, die ihre Keuschheit wahren, die Manner, die Allahs haufig gedenken, und die Frauen, die (Allahs haufig) gedenken - Allah hat ihnen (allen) Vergebung und großen Lohn bereitet"
  },
  {
    "surah_number": 33,
    "verse_number": 36,
    "content":
        "Und es ziemt sich nicht fur einen glaubigen Mann oder eine glaubige Frau, daß sie - wenn Allah und Sein Gesandter eine Angelegenheit beschlossen haben - eine andere Wahl in ihrer Angelegenheit treffen. Und der, der Allah und Seinem Gesandten nicht gehorcht, geht wahrlich in of fenkundiger Weise irre"
  },
  {
    "surah_number": 33,
    "verse_number": 37,
    "content":
        "Und da sagtest du zu dem, dem Allah Gnade erwiesen hatte und dem du Gnade erwiesen hattest: \"Behalte deine Frau fur dich und furchte Allah.\" Und du verbargst das, was du in dir hegtest, das, was Allah ans Licht bringen wollte, und du furchtetest die Menschen, wahrend Allah es ist, Den du in Wirklichkeit furchten sollst. Dann aber, als Zaid seine eheliche Beziehung mit ihr beendet hatte, verbanden Wir sie ehelich mit dir, damit die Glaubigen in Bezug auf die Frauen ihrer angenommenen Sohne nicht in Verlegenheit gebracht wurden, wenn sie ihre ehelichen Beziehungen mit ihnen beendet hatten. Und Allahs Be fehl muß vollzogen werden"
  },
  {"surah_number": 33, "verse_number": 38, "content": "Es bringt den Propheten in keine Verlegenheit, was Allah fur ihn angeordnet hat. Das war Allahs Vorgehen gegen jene, die vordem dahingingen, und Allahs Befehl ist ein unabanderlicher Beschluß"},
  {"surah_number": 33, "verse_number": 39, "content": "jene, die Allah s Botschaften ausrichteten und Ihn furchteten und niemanden außer Allah furchteten. Und Allah genugt als Rechner"},
  {"surah_number": 33, "verse_number": 40, "content": "Muhammad ist nicht der Vater eines eurer Manner, sondern der Gesandte Allahs und der letzte aller Propheten, und Allah besitzt die volle Kenntnis aller Dinge"},
  {"surah_number": 33, "verse_number": 41, "content": "O ihr, die ihr glaubt! Gedenkt Allahs in haufigem Gedenken"},
  {"surah_number": 33, "verse_number": 42, "content": "und lobpreist Ihn morgens und abends"},
  {"surah_number": 33, "verse_number": 43, "content": "Er ist es, Der euch segnet, und Seine Engel bitten (darum) fur euch, daß Er euch aus den Finsternissen zum Licht fuhre. Und Er ist Barmherzig gegen die Glaubigen"},
  {"surah_number": 33, "verse_number": 44, "content": "Ihr Gruß an dem Tage, da sie Ihm begegnen, wird sein: \"Frieden!\" Und Er hat fur sie einen ehrenvollen Lohn bereitet"},
  {"surah_number": 33, "verse_number": 45, "content": "O Prophet, Wir haben dich als einen Zeugen, als Bringer froher Botschaft und als Warner entsandt"},
  {"surah_number": 33, "verse_number": 46, "content": "und mit Seiner Erlaubnis als einen Ausrufer zu Allah und als eine lichtspendene Leuchte"},
  {"surah_number": 33, "verse_number": 47, "content": "Und verkunde den Glaubigen die frohe Botschaft, daß ihnen von Allah große Huld zuteil werde"},
  {"surah_number": 33, "verse_number": 48, "content": "Und gehorche nicht den Unglaubigen und den Heuchlern und beachte ihre Belastigung nicht, und vertraue auf Allah; denn Allah genugt als Beschutzer"},
  {
    "surah_number": 33,
    "verse_number": 49,
    "content": "O ihr, die ihr glaubt! Wenn ihr glaubige Frauen heiratet und euch dann von ihnen scheiden lasset, ehe ihr sie beruhrt habt, so besteht fur euch ihnen gegenuber keine Wartefrist, die sie einhalten mußten. Darum beschenkt sie und entlaßt sie auf geziemende Weise"
  },
  {
    "surah_number": 33,
    "verse_number": 50,
    "content":
        "O Prophet, Wir erlaubten dir deine Gattinnen, denen du ihre Brautgabe gegeben hast, und jene, die du von Rechts wegen aus (der Zahl) derer besitzt, die Allah dir als Kriegsbeute gegeben hat, und die Tochter deines Vaterbruders und die Tochter deiner Vaterschwestern und die Tochter deines Mutterbruders und die Tochter deiner Mutterschwestern, die mit dir ausgewandert sind, und jedwede glaubige Frau, die sich dem Propheten schenkt, vorausgesetzt, daß der Prophet sie zu heiraten wunscht; (dies gilt) nur fur dich und nicht fur die Glaubigen. Wir haben bereits bekanntgegeben, was Wir ihnen bezuglich ihrer Frauen und jener, die sie von Rechts wegen besitzen, verordnet haben, so daß sich (daraus) keine Verlegenheit fur dich ergibt. Und Allah ist Allverzeihend, Barmherzig"
  },
  {
    "surah_number": 33,
    "verse_number": 51,
    "content":
        "Du darfst die von ihnen entlassen, die du (zu entlassen) wunschst, und du darfst die behalten, die du (zu behalten) wunschst; und wenn du eine, die du entlassen hast, wieder aufnehmen willst, dann trifft dich kein Vorwurf. Das ist dazu angetan, daß ihre Blicke Zufriedenheit ausstrahlen und sie sich nicht gramen und sie alle zufrieden sein mogen mit dem, was du ihnen zu geben hast. Und Allah weiß, was in euren Herzen ist; denn Allah ist Allwissend, Nachsichtig"
  },
  {
    "surah_number": 33,
    "verse_number": 52,
    "content": "Es ist dir nicht erlaubt, kunftig (andere) Frauen (zu heiraten), noch sie gegen (andere) Frauen einzutauschen, auch wenn ihre Schonheit dir gefallt; (davon sind) nur die ausgenommen, die du von Rechts wegen besitzt. Und Allah wacht uber alle Dinge"
  },
  {
    "surah_number": 33,
    "verse_number": 53,
    "content":
        "O ihr, die ihr glaubt! Betretet nicht die Hauser des Propheten, es sei denn, daß euch zu einer Mahlzeit (dazu) Erlaubnis gegeben wurde. Und wartet nicht (erst) auf deren Zubereitung, sondern tretet (zur rechten Zeit) ein, wann immer ihr eingeladen seid. Und wenn ihr gespeist habt, dann geht auseinander und lasset euch nicht aus Geselligkeit in eine weitere Unterhaltung verwickeln. Das verursacht dem Propheten Ungelegenheit, und er ist scheu vor euch, jedoch Allah ist nicht scheu vor der Wahrheit. Und wenn ihr sie (seine Frauen) um irgend etwas zu bitten habt, so bittet sie hinter einem Vorhang. Das ist reiner fur eure Herzen und ihre Herzen. Und es geziemt euch nicht, den Gesandten Allahs zu belastigen, noch (geziemt es euch,) seine Frauen jemals nach ihm zu heiraten. Wahrlich, das wurde vor Allah eine Ungeheuerlichkeit sein"
  },
  {"surah_number": 33, "verse_number": 54, "content": "Ob ihr eine Sache offenkundig tut oder sie verbergt, wahrlich, Allah kennt alle Dinge"},
  {
    "surah_number": 33,
    "verse_number": 55,
    "content":
        "Es ist kein Vergehen von ihnen, (sich) ihren Vatern (zu zeigen) oder ihren Sohnen oder ihren Brudern oder den Sohnen ihrer Bruder oder den Sohnen ihrer Schwestern oder ihren Frauen oder denen, die sie von Rechts wegen besitzen. Und furchtet Allah; wahrlich, Allah ist Zeuge aller Dinge"
  },
  {"surah_number": 33, "verse_number": 56, "content": "Wahrlich, Allah sendet Segnungen auf den Propheten, und Seine Engel bitten darum fur ihn. O ihr, die ihr glaubt, bittet (auch) ihr fur ihn und wunscht ihm Frieden in aller Ehrerbietung"},
  {"surah_number": 33, "verse_number": 57, "content": "Wahrlich, diejenigen, die Allah und Seinen Gesandten Ungemach zufugen - Allah hat sie in dieser Welt und im Jenseits verflucht und hat ihnen eine schmahliche Strafe berei tet"},
  {"surah_number": 33, "verse_number": 58, "content": "Und diejenigen, die glaubigen Mannern und glaubigen Frauen ungerechterweise Ungemach zufugen, laden gewiß (die Schuld) der Verleumdung und eine offenkundige Sunde auf sich"},
  {
    "surah_number": 33,
    "verse_number": 59,
    "content": "O Prophet! Sprich zu deinen Frauen und deinen Tochtern und zu den Frauen der Glaubigen, sie sollen ihre Ubergewander reichlich uber sich ziehen. So ist es am ehesten gewahrleistet, daß sie (dann) erkannt und nicht belastigt werden. Und Allah ist Allverzeihend, Barmherzig"
  },
  {
    "surah_number": 33,
    "verse_number": 60,
    "content": "Wenn die Heuchler und diejenigen, in deren Herzen Krankheit ist, und die, welche Geruchte in der Stadt verbreiten, nicht (von ihrem Tun) ablassen, dann werden Wir dich sicher gegen sie anspornen, dann werden sie nur noch fur kurze Zeit in ihr deine Nachbarn sein"
  },
  {"surah_number": 33, "verse_number": 61, "content": "Verflucht seien sie! Wo immer sie gefunden werden, sollen sie ergriffen und rucksichtslos hingerichtet werden"},
  {"surah_number": 33, "verse_number": 62, "content": "So geschah der Wille Allahs im Falle derer, die vordem hingingen; und du wirst in Allahs Willen nie einen Wandel finden"},
  {"surah_number": 33, "verse_number": 63, "content": "Die Menschen befragen dich uber die Stunde. Sprich: \"Das Wissen um sie ist allein bei Allah\", und wie kannst du (das) wissen? Vielleicht ist die Stunde nahe"},
  {"surah_number": 33, "verse_number": 64, "content": "Wahrlich, Allah hat die Unglaubigen verflucht und hat fur sie ein flammendes Feuer bereitet"},
  {"surah_number": 33, "verse_number": 65, "content": "worin sie auf ewig bleiben. Sie werden weder Beschutzer noch Helfer finden"},
  {"surah_number": 33, "verse_number": 66, "content": "An dem Tage, da ihre Gesichter im Feuer gewendet werden, da werden sie sagen: \"O wenn wir doch Allah gehorcht hatten; und hatten wir (doch auch) dem Gesandten gehorcht"},
  {"surah_number": 33, "verse_number": 67, "content": "Und sie werden sagen: \"Unser Herr, wir gehorchten unseren Hauptern und unseren Großen, und sie fuhrten uns irre (und) vom Weg ab"},
  {"surah_number": 33, "verse_number": 68, "content": "Unser Herr, gib ihnen die zweifache Strafe und verfluche sie mit einem gewaltigen Fluch"},
  {"surah_number": 33, "verse_number": 69, "content": "O ihr, die ihr glaubt! Seid nicht wie jene, die Moses krankten; jedoch Allah bewies seine Unschuld in der Sache, die sie (gegen ihn) vorbrachten. Und er war ehrenwert vor Allah"},
  {"surah_number": 33, "verse_number": 70, "content": "O ihr, die ihr glaubt! Furchtet Allah und sprecht aufrichtige Worte"},
  {"surah_number": 33, "verse_number": 71, "content": "auf daß Er eure Taten segensreich fordere und euch eure Sunden vergebe. Und wer Allah und Seinem Gesandten gehorcht, der hat gewiß einen gewaltigen Gewinn erlangt"},
  {"surah_number": 33, "verse_number": 72, "content": "Wahrlich, Wir boten das Treuhanderamt den Himmeln und der Erde und den Bergen an; doch sie weigerten sich, es zu tragen, und schreckten davor zuruck. Aber der Mensch nahm es auf sich. Wahrlich, er ist sehr ungerecht, unwissend"},
  {"surah_number": 33, "verse_number": 73, "content": "Allah wird Heuchler und Heuchlerinnen sowie Gotzendiener und Gotzendienerinnen bestrafen; und Allah kehrt Sich in Barmherzigkeit glaubigen Mannern und glaubigen Frauen zu; denn Allah ist Allverzeihend, Barmherzig"},
  {"surah_number": 34, "verse_number": 1, "content": "Alles Lob gebuhrt Allah, Dessen ist, was in den Himmeln und was auf Erden ist, und Sein ist alles Lob im Jenseits; und Er ist der Allweise, der Allkundige"},
  {"surah_number": 34, "verse_number": 2, "content": "Er weiß, was in die Erde eingeht und was aus ihr hervorkommt, und was vom Himmel herniedersteigt und was zu ihm aufsteigt; und Er ist der Barmherzige, der All verzeihende"},
  {
    "surah_number": 34,
    "verse_number": 3,
    "content":
        "Und es sagen diejenigen, die unglaubig sind: \"Wir werden die Stunde nicht erleben.\" Sprich: \"Ja doch, bei meinem Herrn, dem Kenner des Verborgenen, sie wird gewiß uber euch kommen! Nicht einmal das Gewicht eines Staubchens in den Himmeln oder auf Erden ist vor Ihm verborgen; noch gibt es etwas Kleineres oder Großeres als dieses, das nicht in einem deutlichen Buch stunde"
  },
  {"surah_number": 34, "verse_number": 4, "content": "Er belohnt gewiß diejenigen, die glauben und gute Werke tun. Solche sind es, die Vergebung und eine ehrenvolle Versorgung erhalten werden"},
  {"surah_number": 34, "verse_number": 5, "content": "Und jene aber, die versuchen, Unsere Zeichen zu entkraften - sie sind es, denen eine Strafe schmerzlicher Pein zuteil wird"},
  {"surah_number": 34, "verse_number": 6, "content": "Und die, denen das Wissen gegeben wurde, sehen, daß das, was dir von deinem Herrn offenbart worden ist, die Wahrheit selbst ist und zu dem Weg des Allmachtigen, des Preiswurdigen leitet"},
  {"surah_number": 34, "verse_number": 7, "content": "Und jene, die unglaubig sind, sagen: \"Sollen wir euch einen Mann zeigen, der euch berichtet, ihr wurdet, wenn ihr ganz in Stucke zerfallen seid, auch dann noch als neue Schopfung auferstehen"},
  {"surah_number": 34, "verse_number": 8, "content": "Hat er eine Luge gegen Allah ersonnen oder ist er ein vom Wahn Besessener?\" Nein, (nicht er,) sondern jene, die nicht an das Jenseits glauben, befinden sich in der Pein und im weitgegangenen Irrtum"},
  {
    "surah_number": 34,
    "verse_number": 9,
    "content": "Haben sie denn nicht gesehen, was vom Himmel und von der Erde vor ihnen und was hinter ihnen ist? Wenn Wir wollten, ließen Wir sie in der Erde versinken oder ließen Wir auf sie Brocken vom Himmel fallen. Hierin liegt wahrlich ein Zeichen fur jeden Diener, der sich bekehrt"
  },
  {"surah_number": 34, "verse_number": 10, "content": "Und wahrlich, Wir verliehen David Unsere Huld: \"O ihr Berge lobpreiset mit ihm (Allah), und auch ihr Vogel, (lobpreiset Ihn)!\" Und schmiegsam machten Wir ihm das Eisen"},
  {"surah_number": 34, "verse_number": 11, "content": "(Und Wir sprachen:) \"Verfertige lange Panzerhemden und fuge die Maschen des Kettenwerks fein ineinander. Und verrichtet gute Taten; denn Ich sehe alles, was ihr tut"},
  {
    "surah_number": 34,
    "verse_number": 12,
    "content":
        "Und Salomo (machten Wir) den Wind (dienstbar); sein Herweg dauerte einen Monat, und sein Hinweg dauerte einen Monat. Und Wir verflussigten ihm eine Metallquelle. Und von den Ginn gab es welche, die unter ihm auf Geheiß seines Herrn arbeiteten. Und sollte einer von ihnen sich von Unserem Befehl abwenden, so wurden Wir ihn die Strafe des flammenden Feuers kosten lassen"
  },
  {"surah_number": 34, "verse_number": 13, "content": "Sie machten fur ihn, was er begehrte: Palaste und Bildwerke, Becken wie Teiche und feststehende Kochbottiche: \"Wirkt ihr vom Hause Davids in Dankbarkeit.\" Und nur wenige von Meinen Dienern sind dankbar"},
  {
    "surah_number": 34,
    "verse_number": 14,
    "content": "Und als Wir uber ihn den Tod verhangt hatten, da zeigte ihnen nichts seinen Tod an außer einem Tier aus der Erde, das seinen Stock zerfraß; so gewahrten die Ginn deutlich, wie er fiel, so daß sie, hatten sie das Verborgene gekannt, nicht in schmahlicher Pein hatten bleiben mussen"
  },
  {"surah_number": 34, "verse_number": 15, "content": "Es gab wahrlich ein Zeichen fur Saba' in ihrem Heimatland: zwei Garten zur Rechten und zur Linken: \"Esset von den Gaben eures Herrn und seid Ihm dankbar. (Euer ist) eine Stadt, die gut ist, und ein Herr, Der Allverzeihend ist"},
  {"surah_number": 34, "verse_number": 16, "content": "Jedoch sie kehrten sich ab; da sandten Wir eine reißende Flut gegen sie. Und Wir gaben ihnen an Stelle ihrer Garten zwei Garten mit bitterer Frucht und Tamarisken und wenigen Lotosbaumen"},
  {"surah_number": 34, "verse_number": 17, "content": "Solches gaben Wir ihnen zum Lohn fur ihre Undankbarkeit; und so belohnen Wir keinen (anderen) als den Undankbaren"},
  {"surah_number": 34, "verse_number": 18, "content": "Und Wir setzten zwischen sie und die Stadte, die Wir gesegnet hatten, (andere) hochragende Stadte, und Wir erleichterten das Reisen zwischen ihnen: \"Reist zwischen ihnen bei Tag und Nacht in Sicherheit umher"},
  {
    "surah_number": 34,
    "verse_number": 19,
    "content":
        "Jedoch sie sagten: \"Unser Herr, vergroßere die Entfernung zwischen unseren Reisezielen.\" Und sie sundigten gegen sich selber; so machten Wir sie zu (abschreckenden) Geschichten (fur die Nachfahren), und Wir ließen sie in Stucke zerfallen. Hierin sind wahrlich Zeichen fur jeden Geduldigen, Dankbaren"
  },
  {"surah_number": 34, "verse_number": 20, "content": "Und Iblis bewies wahrlich die Richtigkeit seiner Meinung von ihnen; dann folgten sie ihm mit Ausnahme einer Schar von Glaubigen"},
  {"surah_number": 34, "verse_number": 21, "content": "Und er hatte keine Macht uber sie; allein Wir wollten denjenigen, der ans Jenseits glaubte, vor dem auszeichnen, der Zweifel daruber hegte. Und dein Herr wacht uber alle Dinge"},
  {"surah_number": 34, "verse_number": 22, "content": "Sprich: \"Ruft doch jene an, die ihr vermutet neben Allah. Sie haben nicht einmal uber das Gewicht eines Staubchens in den Himmeln oder auf Erden Macht, noch haben sie einen Anteil an beiden, noch hat Er einen Helfer unter ihnen"},
  {
    "surah_number": 34,
    "verse_number": 23,
    "content": "Auch nutzt bei Ihm keine Fursprache, außer fur den, bei dem Er es erlaubt, so daß, wenn der Schrecken aus ihren Seelen gewichen ist und sie sagen: \"Was hat euer Herr gesprochen?\" sie sagen werden: \"Die Wahrheit.\" Und Er ist der Erhabene, der Große"
  },
  {"surah_number": 34, "verse_number": 24, "content": "Sprich: \"Wer gibt euch Nahrung von den Himmeln und der Erde?\" Sprich: \"Allah. Entweder wir sind oder ihr seid auf dem rechten Weg oder in offenkundigem Irrtum"},
  {"surah_number": 34, "verse_number": 25, "content": "Sprich: \"lhr sollt nicht nach unseren Sunden befragt werden, noch werden wir nach dem, was ihr tut, befragt werden"},
  {"surah_number": 34, "verse_number": 26, "content": "Sprich: \"Unser Herr wird uns alle zusammenbringen, dann wird Er zwischen uns in Gerechtigkeit richten; und Er ist der beste Richter, der Allwissende"},
  {"surah_number": 34, "verse_number": 27, "content": "Sprich: \"Zeigt mir jene, die ihr Ihm als Gotter zur Seite gesetzt habt! Nichts! Er aber ist Allah, der Allmachtige, der Allweise"},
  {"surah_number": 34, "verse_number": 28, "content": "Und Wir haben dich nur als Bringer froher Botschaft und Warner fur alle Menschen entsandt; jedoch die meisten Menschen wissen es nicht"},
  {"surah_number": 34, "verse_number": 29, "content": "Und sie sagen: \"Wann wird dieses Versprechen erfullt, wenn ihr wahrhaftig seid"},
  {"surah_number": 34, "verse_number": 30, "content": "Sprich: \"Euch ist die Frist eines Tages festgesetzt, hinter der ihr weder eine Stunde zuruckbleiben noch ihr vorausgehen konnt"},
  {
    "surah_number": 34,
    "verse_number": 31,
    "content":
        "Und jene, die unglaubig sind sagen: \"Wir wollen keineswegs an diesen Quran glauben, noch an das, was vor ihm gewesen ist.\" Und konntest du nur sehen, wie sich die Ungerechten wechselseitig die Schuld zuweisen, wenn sie vor ihren Herrn gestellt werden! Diejenigen, die unterdruckt waren, werden dann zu denen, die hochmutig waren, sagen: \"Waret ihr nicht gewesen, waren wir ganz gewiß Glaubige geworden"
  },
  {"surah_number": 34, "verse_number": 32, "content": "Jene, die hochmutig waren werden zu denen, die unterdruckt waren, sagen: \"Waren wir es etwa, die euch vom rechten Weg abhielten, nachdem er (der Quran) zu euch gekommen war? Nein, ihr selbst waret die Schuldigen"},
  {
    "surah_number": 34,
    "verse_number": 33,
    "content":
        "Und jene, die unterdruckt waren, werden zu denen, die hochmutig waren, sagen: \"Nein, aber es war (euer) Rankeschmieden bei Nacht und Tag, als ihr uns befahlt, nicht an Allah zu glauben und Ihm Gotter zur Seite zu setzen.\" Und in ihrem Innern werden sie von Reue erfullt sein, wenn sie die Strafe sehen; und Wir werden Fesseln um die Nac ken derer legen, die unglaubig waren. Sie werden nur fur das entlohnt werden, was sie getan haben"
  },
  {"surah_number": 34, "verse_number": 34, "content": "Und Wir entsandten zu keiner Stadt einen Warner, ohne daß die, die darin ein Leben in Wohlstand fuhrten, gesprochen hatten: \"Gewiß, wir leugnen das, womit ihr gesandt worden seid"},
  {"surah_number": 34, "verse_number": 35, "content": "Und sie sagten: \"Wir haben mehr Guter und Kinder (als ihr); und wir werden nicht bestraft werden"},
  {"surah_number": 34, "verse_number": 36, "content": "Sprich: \"Wahrlich, mein Herr erweitert und beschrankt dem die Mittel zum Unterhalt, dem Er will; jedoch die meisten Menschen wissen es nicht"},
  {
    "surah_number": 34,
    "verse_number": 37,
    "content": "Und es ist weder euer Gut, noch sind es eure Kinder, die euch Uns nahe bringen werden; die aber, die glauben und gute Werke tun, die sollen den zweifachen Lohn fur das, was sie getan haben, erhalten. Und in den Obergemachern (des Paradieses) werden sie sicher wohnen"
  },
  {"surah_number": 34, "verse_number": 38, "content": "Doch jene, die versuchen, Unsere Zeichen zu entkraften - sie sind es, die der Strafe zugefuhrt werden"},
  {"surah_number": 34, "verse_number": 39, "content": "Sprich: \"Wahrlich, mein Herr erweitert und beschrankt dem von Seinen Dienern die Mittel zum Unterhalt, dem Er will. Und was immer ihr spendet, Er wird es ersetzen; und Er ist der beste Versorger"},
  {"surah_number": 34, "verse_number": 40, "content": "Und am Tage, wo Er sie alle versammeln (und) dann zu den Engeln sprechen wird: \"Sind diese es, die euch dienten"},
  {"surah_number": 34, "verse_number": 41, "content": "werden sie sagen: \"Preis (sei) Dir! Dich haben Wir zum Beschutzer, nicht sie. Nein, sie dienten den Ginn; an sie haben die meisten von ihnen geglaubt"},
  {"surah_number": 34, "verse_number": 42, "content": "So sollt ihr heute einander weder nutzen noch schaden konnen.\" Und zu denen, die frevelten, werden Wir sprechen: \"Kostet die Strafe des Feuers, das ihr zu leugnen pflegtet"},
  {
    "surah_number": 34,
    "verse_number": 43,
    "content":
        "Und wenn ihnen Unsere deutlichen Verse verlesen werden, sagen sie: \"Dieser ist nichts weiter als ein Mann, der euch von dem abwenden mochte, was eure Vater verehrten.\" Und sie sagen: \"Dieser (Quran) ist nichts (anderes) als eine erdichtete Luge.\" Und diejenigen, die unglaubig sind, sagen von der Wahrheit, wenn sie zu ihnen kommt: \"Das ist nichts als eine offenkundige Zauberei"
  },
  {"surah_number": 34, "verse_number": 44, "content": "Und Wir gaben ihnen keine Bucher, die sie (hatten) studieren (konnen), noch sandten Wir ihnen einen Warner vor dir"},
  {
    "surah_number": 34,
    "verse_number": 45,
    "content": "Jene, die vor ihnen waren, leugneten ebenfalls - und diese haben nicht den zehnten Teil von dem erreicht, was Wir jenen gegeben hatten -, und doch bezichtigten sie Meine Gesandten der Luge. Doch wie war (die Folge davon,) daß sie Mich verleugneten"
  },
  {
    "surah_number": 34,
    "verse_number": 46,
    "content": "Sprich: \"lch mahne euch nur an eines: daß ihr euch ernsthaft mit Allahs Sache - zu zweit oder einzeln - befaßt und dann nachdenken sollt. Es ist in eurem Gefahrten (dem Propheten) keine Besessenheit; er ist fur euch nur ein Warner vor einer bevorstehenden strengen Strafe"
  },
  {"surah_number": 34, "verse_number": 47, "content": "Sprich: \"lch habe von euch keinen Lohn verlangt; denn dieser (Lohn) kommt euch zugute. Mein Lohn ist allein bei Allah; und Er ist der Zeuge aller Dinge"},
  {"surah_number": 34, "verse_number": 48, "content": "Sprich: \"Wahrlich, mein Herr, der Kenner des Verborgenen schleudert (euch) die Wahrheit entgegen"},
  {"surah_number": 34, "verse_number": 49, "content": "Sprich: \"Die Wahrheit ist gekommen, und das Falsche kann weder etwas erschaffen noch etwas zuruckbringen"},
  {"surah_number": 34, "verse_number": 50, "content": "Sprich: \"Wenn ich irre, so irre nur ich selbst; und wenn ich rechtgeleitet bin, so geschieht es durch das, was mein Herr mir offenbart hat. Wahrlich, Er ist der Allhorende, der Nahe"},
  {"surah_number": 34, "verse_number": 51, "content": "Konntest du nur sehen wenn sie mit Furcht geschlagen sein werden! Dann wird es (fur sie) kein Entrinnen geben; denn sie werden aus nachster Nahe erfaßt werden"},
  {"surah_number": 34, "verse_number": 52, "content": "Und sie werden sagen: \"(Nun) glauben wir daran.\" Allein, wie kann das Erlangen (des Glaubens) ihnen an einem (so) fernen Orte moglich sein"},
  {"surah_number": 34, "verse_number": 53, "content": "wenn sie zuvor nicht daran geglaubt haben? Und sie außern Mutmaßungen von einem fernen Ort aus"},
  {"surah_number": 34, "verse_number": 54, "content": "Und ein Abgrund ist zwischen ihnen und ihren Begierden aufgerissen worden, wie es ihresgleichen schon zuvor widerfuhr. Auch sie befanden sich in beunruhigendem Zweifel"},
  {"surah_number": 35, "verse_number": 1, "content": "Alles Lob gebuhrt Allah, dem Schopfer der Himmel und der Erde, Der die Engel, mit je zwei, drei und vier Flugeln, zu Boten gemacht hat. Er fugt der Schopfung hinzu, was Ihm gefallt; Allah hat wahrlich Macht uber alle Dinge"},
  {"surah_number": 35, "verse_number": 2, "content": "Was Allah den Menschen an Barmherzigkeit gewahrt, das kann keiner zuruckhalten; und was Er zuruckhalt, das kann nach Ihm keiner freigeben; und Er ist der Allmachtige, der Allweise"},
  {"surah_number": 35, "verse_number": 3, "content": "O ihr Menschen, gedenkt der Gnade Allahs gegen euch. Gibt es einen Schopfer außer Allah, der euch vom Himmel und von der Erde her versorgt? Es ist kein Gott außer Ihm. Wie konnt ihr euch da (von Ihm) abwenden"},
  {"surah_number": 35, "verse_number": 4, "content": "Und wenn sie dich der Luge bezichtigen: schon die Gesandten vor dir sind der Luge bezichtigt worden; und zu Allah werden die Angelegenheiten zuruckgebracht"},
  {"surah_number": 35, "verse_number": 5, "content": "O ihr Menschen, wahrlich, die Verheißung Allahs ist wahr, darum lasset euch nicht vom diesseitigen Leben betoren, und lasset euch nicht vom Betorer uber Allah betoren"},
  {"surah_number": 35, "verse_number": 6, "content": "Wahrlich, Satan ist euer Feind; so haltet ihn fur einen Feind. Er ruft seine Anhanger nur herbei, damit sie zu Bewohnern des flammenden Feuers werden"},
  {"surah_number": 35, "verse_number": 7, "content": "Denjenigen, die unglaubig sind, wird eine strenge Strafe zuteil sein. Denen aber, die glauben und gute Werke tun, wird Vergebung und großer Lohn zuteil sein"},
  {
    "surah_number": 35,
    "verse_number": 8,
    "content": "Ist etwa der, dem das Bose seines Tuns verschont wird, so daß er es als gut ansieht(, rechtgeleitet)? Gewiß, Allah laßt in die Irre gehen, wen Er. will, und leitet recht, wen Er will. Laß darum deine Seele nicht in Seufzern uber sie hinschwinden. Wahrlich, Allah weiß, was sie tun"
  },
  {"surah_number": 35, "verse_number": 9, "content": "Und Allah ist es, Der die Winde sendet, die das Gewolk hochtreiben. Dann treiben Wir es uber eine tote Stadt und beleben damit die Erde nach ihrem Tode. Ebenso wird es bei der Auferstehung der (Toten) sein"},
  {
    "surah_number": 35,
    "verse_number": 10,
    "content": "Wer da Erhabenheit begehrt (, der wisse), daß alle Erhabenheit Allah gehort. Zu Ihm steigt das gute Wort empor, und rechtschaffenes Werk wird es hochtreiben lassen. Und diejenigen, die Boses planen fur sie ist eine strenge Strafe (bestimmt); und ihr Planen wird unwirksam sein"
  },
  {
    "surah_number": 35,
    "verse_number": 11,
    "content":
        "Und Allah hat euch aus Erde erschaffen, dann aus einem Samentropfen, dann machte Er euch zu Paaren. Und kein weibliches Wesen wird schwanger oder entbindet ohne Sein Wissen. Und keinem, der ein langes Leben hat, wird sein Leben verlangert, noch wird sein Leben irgendwie verringert, ohne daß es in einem Buch stunde. Das ist ein leichtes fur Allah"
  },
  {
    "surah_number": 35,
    "verse_number": 12,
    "content":
        "Und die beiden Gewasser sind nicht gleich: dieses (ist) wohlschmeckend, suß und angenehm zu trinken, und das andere (ist) salzig, bitter. Und aus den beiden esset ihr zartes Fleisch und holt Schmucksachen, die ihr tragt. Und du siehst die Schiffe darauf fahren, auf daß ihr nach Seiner Huld trachten und vielleicht doch dankbar sein moget"
  },
  {
    "surah_number": 35,
    "verse_number": 13,
    "content":
        "Er laßt die Nacht in den Tag und den Tag in die Nacht ubergehen. Und Er hat (euch) die Sonne und den Mond dienstbar gemacht; ein jedes (Gestirn) umlauft seine Bahn auf eine bestimmte Zeit. Dies ist Allah, euer Herr; Sein ist das Reich, und jenen, die ihr statt Ihm anruft, gehort noch nicht einmal Macht uber das Hautchen eines Dattelkerns"
  },
  {
    "surah_number": 35,
    "verse_number": 14,
    "content": "Wenn ihr sie bittet, horen sie eure Bitte nicht; und wenn sie diese auch horen wurden, so wurden sie euch nichts in Erfullung bringen. Und am Tage der Auferstehung werden sie leugnen, daß ihr (sie) zu Gottern nahmt. Und niemand kann dich so unterrichten wie der Eine, Der Kundig ist"
  },
  {"surah_number": 35, "verse_number": 15, "content": "O ihr Menschen, ihr seid arm und auf Allah angewiesen, Allah aber ist auf keinen angewiesen und ist des Lobes Wurdig"},
  {"surah_number": 35, "verse_number": 16, "content": "Wenn Er will, kann Er euch hinwegnehmen und eine neue Schopfung hervorbringen"},
  {"surah_number": 35, "verse_number": 17, "content": "Und das ist fur Allah nicht schwer"},
  {
    "surah_number": 35,
    "verse_number": 18,
    "content":
        "Und keine lasttragende (Seele) soll die Last einer anderen tragen; und wenn eine schwerbeladene um ihrer Last willen ruft, soll nichts davon getragen werden, und handelte es sich auch um einen Verwandten. Du kannst die allein warnen, die ihren Herrn im geheimen furchten und das Gebet verrichten. Und wer sich reinigt, der reinigt sich nur zu seinem eigenen Vorteil; und zu Allah ist die Heimkehr"
  },
  {"surah_number": 35, "verse_number": 19, "content": "Und wahrlich, der Blinde ist dem Sehenden nicht gleich"},
  {"surah_number": 35, "verse_number": 20, "content": "noch ist es die Finsternis dem Lichte"},
  {"surah_number": 35, "verse_number": 21, "content": "noch ist es der Schatten der Sonnenhitze"},
  {"surah_number": 35, "verse_number": 22, "content": "noch sind die Lebenden den Toten gleich. Wahrlich, Allah macht horend, wen Er will; und du kannst diejenigen nicht horend machen, die in den Grabern sind"},
  {"surah_number": 35, "verse_number": 23, "content": "Wahrlich, du bist nur ein Warner"},
  {"surah_number": 35, "verse_number": 24, "content": "Wahrlich, Wir haben dich mit der Wahrheit als Bringer froher Botschaft und als Warner entsandt; und es gibt kein Volk, in dem es nicht einmal schon Warner gegeben hatte"},
  {"surah_number": 35, "verse_number": 25, "content": "Und wenn sie dich der Luge bezichtigen, so haben auch jene schon, die vor ihnen waren, (die Propheten) der Luge bezichtigt. Ihre Gesandten kamen mit klaren Beweisen und mit den Buchern und mit der erleuchtenden Schrift zu ihnen"},
  {"surah_number": 35, "verse_number": 26, "content": "Dann erfaßte Ich jene, die unglaubig waren, und wie war es mit der Verleugnung"},
  {"surah_number": 35, "verse_number": 27, "content": "Hast du nicht gesehen, daß Allah Wasser vom Himmel herniedersendet? Dann bringen Wir damit Fruchte mit mannigfachen Farben hervor; und in den Bergen sind weiße und rote, verschiedenfarbige und rabenschwarze Schichten"},
  {"surah_number": 35, "verse_number": 28, "content": "Und bei Mensch und Tier und Vieh (gibt es) auch verschiedene Farben. Wahrlich, nur die Wissenden unter Seinen Dienern furchten Allah. Wahrlich, Allah ist Erhaben, Allverzeihend"},
  {"surah_number": 35, "verse_number": 29, "content": "Wahrlich, diejenigen, die Allahs Buch verlesen und das Gebet verrichten und von dem, was Wir ihnen gegeben haben, insgeheim und offenkundig spenden, rechnen mit einem Handel, der nie pleite sein wird"},
  {"surah_number": 35, "verse_number": 30, "content": "Damit gibt Er ihnen ihren vollen Lohn und noch mehr aus Seiner Huld hinzu; Er ist wahrlich Allverzeihend, Dankbar"},
  {"surah_number": 35, "verse_number": 31, "content": "Und das, was Wir dir in dem Buch offenbart haben, ist die Wahrheit, die das bestatigt, was ihm vorausging. Gewiß, Allah kennt und durchschaut Seine Diener recht wohl"},
  {
    "surah_number": 35,
    "verse_number": 32,
    "content":
        "Alsdann gaben Wir das Buch jenen von Unseren Dienern zum Erbe, die Wir erwahlten. Und unter ihnen sind einige, die gegen sich selbst freveln, und unter ihnen sind einige, die den gemaßigten Standpunkt einnehmen, und unter ihnen sind einige, die nach den guten Dingen mit Allahs Erlaubnis wetteifern. Das ist die große Huld"
  },
  {"surah_number": 35, "verse_number": 33, "content": "Garten von Eden! Sie werden sie betreten. Geschmuckt werden sie darin sein mit Armspangen aus Gold und Perlen, und ihre Kleidung darin wird aus Seide sein"},
  {"surah_number": 35, "verse_number": 34, "content": "Und sie werden sagen: \"Alles Lob gebuhrt Allah, Der die Traurigkeit von uns genommen hat. Unser Herr ist wahrlich Allverzeihend, Dankbar"},
  {"surah_number": 35, "verse_number": 35, "content": "Der uns in Seiner Huld in der Wohnstatt der Ewigkeit ansassig machte. Keine Muhsal beruhrt uns darin, noch beruhrt uns darin Mudigkeit"},
  {"surah_number": 35, "verse_number": 36, "content": "Und fur diejenigen, die unglaubig sind, ist das Feuer der Gahannam. Der Tod wird nicht uber sie verhangt, so daß sie sterben konnten; noch wird ihnen etwas von ihrer Strafe erleichtert. So vergelten Wir jedem Undankbaren"},
  {
    "surah_number": 35,
    "verse_number": 37,
    "content":
        "Und sie werden darin schreien: \"Unser Herr, bringe uns (aus der Holle) heraus; wir wollen rechtschaffene Werke tun, anders als wir (zuvor) zu tun pflegten.\" \"Gaben Wir euch nicht ein genugend langes Leben, so daß ein jeder, der sich besinnen wollte, sich darin hatte besinnen konnen? Und (uberdies) kam der Warner zu euch. So kostet nun (die Strafe); denn Frevler haben keinen Helfer"
  },
  {"surah_number": 35, "verse_number": 38, "content": "Wahrlich, Allah kennt das Verborgene der Himmel und der Erde. Wahrlich, Er kennt alles, was in den Herzen ist"},
  {
    "surah_number": 35,
    "verse_number": 39,
    "content": "Er ist es, Der euch zu Statthaltern auf Erden gemacht hat. Wer aber unglaubig ist - auf ihm (laste) sein Unglaube! Und den Unglaubigen kann ihr Unglaube nur den Widerwillen ihres Herrn mehren, und ihr Unglaube kann den Unglaubigen nur den Verlust vergroßern"
  },
  {
    "surah_number": 35,
    "verse_number": 40,
    "content":
        "Sprich: \"Habt ihr die Gotter gesehen die ihr statt Allah anruft? Zeigt mir, was sie von der Erde erschufen. Oder haben sie einen Anteil (im Reich) der Himmel?\" Oder haben Wir ihnen ein Buch gegeben woraus sie einen Beweis hatten (entnehmen konnen)? Nein, die Ungerechten verheißen einander nur Trug"
  },
  {"surah_number": 35, "verse_number": 41, "content": "Wahrlich, Allah halt die Himmel und die Erde, damit sie nicht weichen. Und wurden sie weichen, so gabe es keinen außer Ihm, der sie halten konnte. Wahrlich, Er ist Nachsichtig, Allverzeihend"},
  {"surah_number": 35, "verse_number": 42, "content": "Und sie schworen bei Allah ihre festen Eide; wenn ein Warner zu ihnen kame, wurden sie der Fuhrung besser folgen als eines der anderen Volker. Doch als dann in der Tat ein Warner zu ihnen kam, so bestarkte sie das nur in ihrer Abneigung"},
  {
    "surah_number": 35,
    "verse_number": 43,
    "content":
        "(Sie sind) in Hochmut auf Erden und bose im Planen. Doch der bose Plan fangt nur seine Urheber ein. Erwarten sie denn etwas anderes als das Vorgehen gegenuber den Fruheren? Aber in Allahs Vorgehen wirst du nie eine Anderung finden; und in Allahs Verfahren wirst du nie einen Wechsel finden"
  },
  {
    "surah_number": 35,
    "verse_number": 44,
    "content": "Sind sie denn nicht im Lande umhergezogen, so daß sie schauen konnten, wie das Ende derer war, die vor ihnen lebten? Und sie hatten mehr Kraft als sie selber. Und nichts vermag Allah in den Himmeln oder auf Erden zu hemmen; denn Er ist Allwissend, Allmachtig"
  },
  {
    "surah_number": 35,
    "verse_number": 45,
    "content": "Und wollte Allah die Menschen fur alles bestrafen, was sie tun, wurde Er nicht ein Lebewesen auf der (Erd-)oberflache ubrig lassen; doch Er gewahrt ihnen Aufschub bis zu einer bestimmten Frist; und wenn ihre Frist um ist, dann durchschaut Allah Seine Diener"
  },
  {"surah_number": 36, "verse_number": 1, "content": "Ya Sin"},
  {"surah_number": 36, "verse_number": 2, "content": "Beim vollkommenen Quran"},
  {"surah_number": 36, "verse_number": 3, "content": "du bist wahrlich einer der Gesandten"},
  {"surah_number": 36, "verse_number": 4, "content": "der auf einem geraden Weg ist"},
  {"surah_number": 36, "verse_number": 5, "content": "(Dies ist) eine Offenbarung des Erhabenen, des Barmherzigen"},
  {"surah_number": 36, "verse_number": 6, "content": "auf daß du Leute warnest, deren Vater nicht gewarnt worden sind, und die daher achtlos sind"},
  {"surah_number": 36, "verse_number": 7, "content": "Bereits hat sich das Wort gegen die meisten von ihnen als wahr erwiesen; denn sie glauben nicht"},
  {"surah_number": 36, "verse_number": 8, "content": "Um ihren Hals haben Wir Fesseln gelegt, die bis an das Kinn reichen, so daß ihr Haupt hochgezwangt ist"},
  {"surah_number": 36, "verse_number": 9, "content": "Und Wir haben vor ihnen einen Wall errichtet und ebenso hinter ihnen einen Wall (errichtet), und Wir haben sie verhullt, so daß sie nicht sehen konnen"},
  {"surah_number": 36, "verse_number": 10, "content": "Und ihnen ist es gleich, ob du sie warnst oder ob du sie nicht warnst; sie werden nicht glauben"},
  {"surah_number": 36, "verse_number": 11, "content": "Du vermagst nur den zu warnen, der die Ermahnung befolgt und den Allerbarmer im Verborgenen furchtet. Gib ihm darum die frohe Botschaft von Vergebung und einem ehrenvollen Lohn"},
  {"surah_number": 36, "verse_number": 12, "content": "Wahrlich, Wir sind es, Die die Toten beleben, und Wir schreiben das auf, was sie begehen, zugleich mit dem, was sie zurucklassen; und alle Dinge haben Wir in einem deutlichen Buch verzeichnet"},
  {"surah_number": 36, "verse_number": 13, "content": "Und prage ihnen ein Gleichnis von den Leuten der Stadt, als die Abgesandten zu ihr kamen"},
  {"surah_number": 36, "verse_number": 14, "content": "Als Wir zwei zu ihnen schickten und sie von ihnen fur Lugner gehalten wurden, da starkten Wir (sie) durch einen dritten, und sie sagten: \"Wir sind zu euch entsandt worden"},
  {"surah_number": 36, "verse_number": 15, "content": "Jene sagten: \"lhr seid nur Menschen wie wir; und der Allerbarmer hat nichts herabgesandt. Ihr sprecht nichts als Lugen"},
  {"surah_number": 36, "verse_number": 16, "content": "Sie sagten: \"Unser Herr weiß, daß wir wahrlich Abgesandte zu euch sind"},
  {"surah_number": 36, "verse_number": 17, "content": "Und uns obliegt nur die klare Verkundigung"},
  {"surah_number": 36, "verse_number": 18, "content": "Sie sagten: \"Wir ahnen Boses von euch. Wenn ihr (davon) nicht ablaßt, so werden wir euch gewiß steinigen, und euch wird sicher unsere schmerzliche Strafe treffen"},
  {"surah_number": 36, "verse_number": 19, "content": "Sie sagten: \"Euer Unheil liegt bei euch selbst. Liegt es daran, daß ihr ermahnt werdet? Nein, ihr seid Leute, die das Maß uberschreiten"},
  {"surah_number": 36, "verse_number": 20, "content": "Und es kam vom entferntsten Teil der Stadt ein Mann angelaufen. Er sagte: \"O meine Leute, folgt den Gesandten"},
  {"surah_number": 36, "verse_number": 21, "content": "Folgt denen, die keinen Lohn von euch fordern und die rechtgeleitet sind"},
  {"surah_number": 36, "verse_number": 22, "content": "Und warum sollte ich nicht Dem dienen, Der mich erschaffen hat und zu Dem ihr zuruckgebracht werdet"},
  {"surah_number": 36, "verse_number": 23, "content": "Soll ich etwa andere außer Ihm zu Gottern nehmen? Wenn der Allerbarmer mir ein Leid zufugen will, so wird ihre Fursprache mir nichts nutzen, noch konnen sie mich retten"},
  {"surah_number": 36, "verse_number": 24, "content": "Dann befande ich mich wahrlich in einem offenkundigen Irrtum"},
  {"surah_number": 36, "verse_number": 25, "content": "Ich glaube an euren Herrn; darum hort mich an"},
  {"surah_number": 36, "verse_number": 26, "content": "Da wurde (zu ihm) gesprochen: \"Geh in das Paradies ein.\" Er sagte: \"O wenn doch meine Leute wußten"},
  {"surah_number": 36, "verse_number": 27, "content": "wie mein Herr mir vergeben und mich zu einem der Geehrten gemacht hat"},
  {"surah_number": 36, "verse_number": 28, "content": "Und nach ihm sandten Wir gegen seine Leute kein Heer vom Himmel herab, noch pflegten Wir (eins) zu senden"},
  {"surah_number": 36, "verse_number": 29, "content": "Es war nur ein einziger Schrei, und siehe, sie lagen reglos da"},
  {"surah_number": 36, "verse_number": 30, "content": "Wehe uber die Menschen! Kein Gesandter kommt zu ihnen, den sie nicht verspotteten"},
  {"surah_number": 36, "verse_number": 31, "content": "Haben sie nicht gesehen, wie viele Generationen Wir schon vor ihnen vernichtet haben (und) daß sie nicht zu ihnen zuruckkehren"},
  {"surah_number": 36, "verse_number": 32, "content": "Jedoch sie alle, allesamt versammelt, werden sicher vor Uns gebracht werden"},
  {"surah_number": 36, "verse_number": 33, "content": "Und ein Zeichen ist ihnen die tote Erde. Wir beleben sie und bringen aus ihr Korn hervor, von dem sie essen"},
  {"surah_number": 36, "verse_number": 34, "content": "Und Wir haben auf ihr Garten mit Dattelpalmen und Beeren angelegt, und Wir ließen Quellen aus ihr entspringen"},
  {"surah_number": 36, "verse_number": 35, "content": "damit sie von ihren Fruchten essen; und ihre Hande schufen sie nicht. Wollen sie da nicht dankbar sein"},
  {"surah_number": 36, "verse_number": 36, "content": "Preis (sei) Ihm, Der die Arten alle paarweise geschaffen hat von dem, was die Erde sprießen laßt, und von ihnen selber und von dem, was sie nicht kennen"},
  {"surah_number": 36, "verse_number": 37, "content": "Und ein Zeichen ist ihnen die Nacht. Wir entziehen ihr das Tageslicht, und siehe, sie sind in Finsternis"},
  {"surah_number": 36, "verse_number": 38, "content": "Und die Sonne eilt dem ihr gesetzten Ziel zu. Das ist die Anordnung des Erhabenen, des Allwissenden"},
  {"surah_number": 36, "verse_number": 39, "content": "Und fur den Mond haben Wir Stationen bestimmt, bis er wie der alte Dattelrispenstiel wiederkehrt"},
  {"surah_number": 36, "verse_number": 40, "content": "Weder hat die Sonne den Mond einzuholen, noch eilt die Nacht dem Tage voraus; und alle schweben auf einer Umlaufbahn"},
  {"surah_number": 36, "verse_number": 41, "content": "Und ein Zeichen ist es ihnen, daß Wir ihre Nachkommenschaft in dem beladenen Schiff trugen"},
  {"surah_number": 36, "verse_number": 42, "content": "Und Wir schufen ihnen etwas von gleicher Art, worauf sie fahren"},
  {"surah_number": 36, "verse_number": 43, "content": "Und wenn Wir wollten, so konnten Wir sie ertrinken lassen; dann wurden sie weder Helfer haben, noch konnten sie gerettet werden"},
  {"surah_number": 36, "verse_number": 44, "content": "außer durch Unsere Barmherzigkeit und zu einem Nießbrauch auf gewisse Zeit"},
  {"surah_number": 36, "verse_number": 45, "content": "Und wenn zu ihnen gesprochen wird: \"Hutet euch vor dem, was vor euch ist und was hinter euch ist, auf daß ihr Erbarmen finden moget\" (, so beachten sie die Warnung nicht)"},
  {"surah_number": 36, "verse_number": 46, "content": "Und es kommt kein Zeichen von den Zeichen ihres Herrn zu ihnen, ohne daß sie sich davon abwenden"},
  {
    "surah_number": 36,
    "verse_number": 47,
    "content": "Und wenn zu ihnen gesprochen wird: \"Spendet von dem, was Allah euch gegeben bat\", sagen die Unglaubigen zu den Glaubigen: \"Sollen wir einen speisen, den Allah hatte speisen konnen, wenn Er es gewollt hatte? Ihr befindet euch da zweifellos in einem offenkundigen Irrtum"
  },
  {"surah_number": 36, "verse_number": 48, "content": "Und sie sagen: \"Wann wird diese Verheißung (in Erfullung gehen), wenn ihr die Wahrheit sagt"},
  {"surah_number": 36, "verse_number": 49, "content": "Sie warten nur auf einen einzigen Schrei, der sie erfassen wird, wahrend sie noch streiten"},
  {"surah_number": 36, "verse_number": 50, "content": "Und sie werden weder imstande sein, ein Vermachtnis zu treffen, noch werden sie zu ihren Angehorigen zuruckkehren"},
  {"surah_number": 36, "verse_number": 51, "content": "Und in den, Sur wird gestoßen, und siehe, sie eilen aus ihren Grabern zu ihrem Herrn hervor"},
  {"surah_number": 36, "verse_number": 52, "content": "Sie werden sagen: \"O wehe uns! Wer hat uns von unserer Liegestelle erweckt? Das ist es, was der Allerbarmer (uns) verheißen hatte, und die Gesandten sagten doch die Wahrheit"},
  {"surah_number": 36, "verse_number": 53, "content": "Es wird nur ein einziger Schrei sein, und siehe, sie werden alle vor Uns gebracht werden"},
  {"surah_number": 36, "verse_number": 54, "content": "Nun, heute wird keine Seele im geringsten ein Unrecht erleiden; und ihr sollt nur fur das entlohnt werden, was ihr zu tun pflegtet"},
  {"surah_number": 36, "verse_number": 55, "content": "Wahrlich, die Bewohner des Paradieses sind heute beschaftigt und erfreuen sich am Genuß"},
  {"surah_number": 36, "verse_number": 56, "content": "Sie und ihre Gattinnen liegen im Schatten auf Ruhebetten gestutzt"},
  {"surah_number": 36, "verse_number": 57, "content": "Fruchte werden sie darin haben, und sie werden bekommen, was immer sie (auch) begehren"},
  {"surah_number": 36, "verse_number": 58, "content": "Frieden!\" - (dies ist) eine Botschaft von einem Sich Erbarmenden Herrn"},
  {"surah_number": 36, "verse_number": 59, "content": "Und (es wird gesprochen): \"Sondert euch heute ab, o ihr Schuldigen"},
  {"surah_number": 36, "verse_number": 60, "content": "Habe Ich euch, ihr Kinder Adams, nicht geboten, nicht Satan zu dienen - denn er ist euer offenkundiger Feind"},
  {"surah_number": 36, "verse_number": 61, "content": "sondern Mir allein zu dienen? Das ist der gerade Weg"},
  {"surah_number": 36, "verse_number": 62, "content": "Und doch hat er eine große Menge von euch irregefuhrt. Hattet ihr denn keinen Verstand"},
  {"surah_number": 36, "verse_number": 63, "content": "Das ist Gahannam, die euch angedroht wurde"},
  {"surah_number": 36, "verse_number": 64, "content": "brennt heute in ihr dafur, daß ihr unglaubig waret"},
  {"surah_number": 36, "verse_number": 65, "content": "Heute versiegeln Wir ihre Munder, jedoch ihre Hande werden zu Uns sprechen, und ihre Fuße werden all das bezeugen, was sie erworben haben"},
  {"surah_number": 36, "verse_number": 66, "content": "Und hatten Wir gewollt, hatten Wir ihr Augenlicht ausloschen konnen; dann wurden sie versuchen, moglichst schnell auf den rechten Weg zu kommen. Aber wie hatten sie etwas sehen konnen"},
  {"surah_number": 36, "verse_number": 67, "content": "Und hatten Wir gewollt, hatten Wir sie verwandeln konnen, wo sie waren; dann waren sie nicht imstande gewesen, vorwarts oder ruckwarts zu gehen"},
  {"surah_number": 36, "verse_number": 68, "content": "Und den, dem Wir ein langes Leben geben, setzen wir korperlichem Verfall aus. Wollen sie es denn nicht begreifen"},
  {"surah_number": 36, "verse_number": 69, "content": "Und Wir haben ihn nicht die Dichtung gelehrt, noch ziemte sie sich fur ihn. Dies ist nichts als eine Ermahnung und ein deutlicher Quran"},
  {"surah_number": 36, "verse_number": 70, "content": "auf daß er jeden warne, der am Leben ist und auf daß das Wort gegen die Unglaubigen in Erfullung gehe"},
  {"surah_number": 36, "verse_number": 71, "content": "Haben sie nicht gesehen, daß Wir von den Dingen, die Unsere Hande gemacht haben, fur sie das Vieh schufen, dessen Besitzer sie geworden sind"},
  {"surah_number": 36, "verse_number": 72, "content": "Und Wir haben es ihnen dienstbar gemacht, so daß manche von ihnen zum Reiten dienen und manche Nahrung geben"},
  {"surah_number": 36, "verse_number": 73, "content": "Und sie haben an ihnen noch (anderen) Nutzen und (auch) Trank. Wollen sie also nicht dankbar sein"},
  {"surah_number": 36, "verse_number": 74, "content": "Und sie haben sich statt Allah Gotter genommen, damit ihnen geholfen wurde"},
  {"surah_number": 36, "verse_number": 75, "content": "Sie vermogen ihnen nicht zu helfen; vielmehr sind sie selbst als eine Heerschar fur sie im Einsatz"},
  {"surah_number": 36, "verse_number": 76, "content": "so laß dich daher von ihrer Rede nicht betruben. Wir wissen, was sie verbergen und was sie offenkund tun"},
  {"surah_number": 36, "verse_number": 77, "content": "Weiß der Mensch denn nicht, daß Wir ihn aus einem Samentropfen erschufen? Und siehe da, er ist ein offenkundiger Widersacher"},
  {"surah_number": 36, "verse_number": 78, "content": "Und er pragt Uns Gleichnisse und vergißt seine eigene Erschaffung. Er sagt: \"Wer kann die Gebeine beleben, wenn sie morsch geworden sind"},
  {"surah_number": 36, "verse_number": 79, "content": "Sprich: \"Er, Der sie das erstemal erschuf - Er wird sie beleben; denn Er kennt jegliche Schopfung"},
  {"surah_number": 36, "verse_number": 80, "content": "Er, Der fur euch Feuer aus den grunen Baumen hervorbringt; und siehe, davon habt ihr dann Brennmaterial"},
  {"surah_number": 36, "verse_number": 81, "content": "Ist Er, Der die Himmel und die Erde erschuf, nicht imstande, ihresgleichen zu erschaffen?\" Doch, und Er ist der Erschaffer, der Allwissende"},
  {"surah_number": 36, "verse_number": 82, "content": "Wenn Er ein Ding will, lautet Sein Befehl nur: \"Sei!\" und es ist"},
  {"surah_number": 36, "verse_number": 83, "content": "Also gepriesen sei Der, in Dessen Hand die Herrschaft uber alle Dinge ruht und zu Dem ihr zuruckgebracht werdet"},
  {"surah_number": 37, "verse_number": 1, "content": "Bei den Sich-Reihenden in Reihen"},
  {"surah_number": 37, "verse_number": 2, "content": "und denen, die grob zurechtweisen"},
  {"surah_number": 37, "verse_number": 3, "content": "und denen, die Ermahnung verlesen"},
  {"surah_number": 37, "verse_number": 4, "content": "Wahrlich, euer Gott ist Einzig"},
  {"surah_number": 37, "verse_number": 5, "content": "Herr der Himmel und der Erde und all dessen, was zwischen beiden ist, und der Herr aller Orte des Sonnenaufgangs"},
  {"surah_number": 37, "verse_number": 6, "content": "Wir haben den untersten Himmel mit einem Schmuck ausgeschmuckt: den Sternen"},
  {"surah_number": 37, "verse_number": 7, "content": "die einen Schutz vor jedem aufruhrerischen Satan bilden"},
  {"surah_number": 37, "verse_number": 8, "content": "Sie konnen nichts bei den hoheren Bewohnern erlauschen, und sie werden von allen Seiten beworfen"},
  {"surah_number": 37, "verse_number": 9, "content": "als Ausgestoßene; und fur sie ist eine dauernde Strafe (vorgesehen)"},
  {"surah_number": 37, "verse_number": 10, "content": "mit Ausnahme dessen, der etwas aufschnappt, doch ihn verfolgt ein flammendes Feuer von durchbohrender Helligkeit"},
  {"surah_number": 37, "verse_number": 11, "content": "Frage sie darum, ob sie schwerer zu erschaffen sind oder die, die Wir erschaffen haben? Denn Wir haben sie aus formbarem Lehm erschaffen"},
  {"surah_number": 37, "verse_number": 12, "content": "Nein, du staunst, und sie spotten"},
  {"surah_number": 37, "verse_number": 13, "content": "Und wenn sie ermahnt werden, so beachten sie es nicht"},
  {"surah_number": 37, "verse_number": 14, "content": "Und wenn sie ein Zeichen sehen, so verdrehen sie es zu Spott"},
  {"surah_number": 37, "verse_number": 15, "content": "und sie sagen: \"Das ist nichts als offenkundige Zauberei"},
  {"surah_number": 37, "verse_number": 16, "content": "Wie? Wenn wir tot sind und zu Staub und Gebeinen geworden sind, dann sollen wir wieder auferweckt werden"},
  {"surah_number": 37, "verse_number": 17, "content": "Oder etwa unsere Vorvater"},
  {"surah_number": 37, "verse_number": 18, "content": "Sprich: \"Jawohl; und dabei werdet ihr gedemutigt"},
  {"surah_number": 37, "verse_number": 19, "content": "Dann wird nur ein einziger Schrei herrschen, und siehe, schon schauen sie zu"},
  {"surah_number": 37, "verse_number": 20, "content": "Und sie werden sagen: \"O wehe uns! Das ist der Tag des Gerichts"},
  {"surah_number": 37, "verse_number": 21, "content": "Das ist der Tag der Entscheidung, den ihr fur gelogen hieltet"},
  {"surah_number": 37, "verse_number": 22, "content": "Versammelt jene, die frevelten, und ihre (Gleichgesinnungs)Paare und das, was sie verehrt haben"},
  {"surah_number": 37, "verse_number": 23, "content": "statt Allah, und fuhrt sie zum Wege der Gahim"},
  {"surah_number": 37, "verse_number": 24, "content": "Und haltet sie an; denn sie sollen befragt werden"},
  {"surah_number": 37, "verse_number": 25, "content": "Was ist euch, daß ihr einander nicht helft"},
  {"surah_number": 37, "verse_number": 26, "content": "Nein, heute ergeben sie sich"},
  {"surah_number": 37, "verse_number": 27, "content": "Und sie wenden sich aneinander und fragen sich gegenseitig"},
  {"surah_number": 37, "verse_number": 28, "content": "Sie werden sagen: \"Wahrlich, ihr pflegtet von rechts zu uns zu kommen"},
  {"surah_number": 37, "verse_number": 29, "content": "Jene werden antworten: \"Nein, ihr waret selbst keine Glaubigen"},
  {"surah_number": 37, "verse_number": 30, "content": "Und wir hatten keine Macht uber euch; ihr aber waret ein gewalttatiges Volk"},
  {"surah_number": 37, "verse_number": 31, "content": "Nun hat sich das Wort unseres Herrn gegen uns erfullt. Wir werden gewiß (die Strafe) kosten mussen"},
  {"surah_number": 37, "verse_number": 32, "content": "Und wir verfuhrten euch, weil wir selbst Irrende waren"},
  {"surah_number": 37, "verse_number": 33, "content": "An jenem Tage werden sie alle Teilhaber an der Strafe sein"},
  {"surah_number": 37, "verse_number": 34, "content": "So verfahren Wir mit den Schuldigen"},
  {"surah_number": 37, "verse_number": 35, "content": "denn als zu ihnen gesprochen wurde: \"Es ist kein Gott außer Allah\", da verhielten sie sich hochmutig"},
  {"surah_number": 37, "verse_number": 36, "content": "und sagten: \"Sollen wir unsere Gotter wegen eines besessenen Dichters aufgeben"},
  {"surah_number": 37, "verse_number": 37, "content": "Nein, er hat die Wahrheit gebracht und die Gesandten bestatigt"},
  {"surah_number": 37, "verse_number": 38, "content": "Ihr werdet sicher die peinvolle Strafe kosten"},
  {"surah_number": 37, "verse_number": 39, "content": "Und ihr werdet nur fur das, was ihr selbst gewirkt habt, belohnt werden"},
  {"surah_number": 37, "verse_number": 40, "content": "Ausgenommen (davon sind) die erwahlten Diener Allahs"},
  {"surah_number": 37, "verse_number": 41, "content": "Diese sollen eine zuvor bekannte Versorgung erhalten"},
  {"surah_number": 37, "verse_number": 42, "content": "Fruchte; und sie sollen geehrt werden"},
  {"surah_number": 37, "verse_number": 43, "content": "in den Garten der Wonne"},
  {"surah_number": 37, "verse_number": 44, "content": "auf Ruhebetten einander gegenuber (sitzend)"},
  {"surah_number": 37, "verse_number": 45, "content": "wahrend ein unerschopflicher Becher unter ihnen die Runde macht"},
  {"surah_number": 37, "verse_number": 46, "content": "(Er ist) weiß und wohlschmeckend fur die Trinkenden"},
  {"surah_number": 37, "verse_number": 47, "content": "Er wird keinen Rausch erzeugen, noch werden sie davon mude werden"},
  {"surah_number": 37, "verse_number": 48, "content": "Und bei ihnen werden (Keusche) sein, die zuchtig aus großen Augen blicken"},
  {"surah_number": 37, "verse_number": 49, "content": "als ob sie verborgene Perlen waren"},
  {"surah_number": 37, "verse_number": 50, "content": "Und einige von ihnen werden sich an die anderen wenden, um sich gegenseitig zu befragen"},
  {"surah_number": 37, "verse_number": 51, "content": "Einer ihrer Sprecher wird sagen: \"lch hatte einen Gefahrten"},
  {"surah_number": 37, "verse_number": 52, "content": "der zu fragen pflegte: \"Haltst du tatsachlich (die Auferstehung) fur wahr"},
  {"surah_number": 37, "verse_number": 53, "content": "Wenn wir tot sind und zu Staub und Gebeinen geworden sind, dann sollen uns (unsere Taten) wirklich vergolten werden"},
  {"surah_number": 37, "verse_number": 54, "content": "Er wird fragen: \"Wollt ihr (ihn) schauen"},
  {"surah_number": 37, "verse_number": 55, "content": "Dann wird er schauen und ihn inmitten der Gahim sehen"},
  {"surah_number": 37, "verse_number": 56, "content": "Er wird sagen: \"Bei Allah, beinahe hattest du mich ins Verderben gesturzt"},
  {"surah_number": 37, "verse_number": 57, "content": "Und ware nicht die Gnade meines Herrn gewesen, hatte ich sicher zu denen gehort, die (zum Feuer) gebracht werden"},
  {"surah_number": 37, "verse_number": 58, "content": "Ist es nicht so, daß wir nicht sterben werden"},
  {"surah_number": 37, "verse_number": 59, "content": "außer bei unserem ersten Tod? Und wir sollen nicht bestraft werden"},
  {"surah_number": 37, "verse_number": 60, "content": "Wahrlich, das ist der gewaltige Gewinn"},
  {"surah_number": 37, "verse_number": 61, "content": "Fur solches wie dies denn mogen die Wirkenden wirken"},
  {"surah_number": 37, "verse_number": 62, "content": "Ist dies besser als Bewirtung oder der Baum des Zaqqwn"},
  {"surah_number": 37, "verse_number": 63, "content": "Denn Wir haben ihn zu einer Versuchung fur die Missetater gemacht"},
  {"surah_number": 37, "verse_number": 64, "content": "Er ist ein Baum, der aus dem Grunde der Gahim emporwachst"},
  {"surah_number": 37, "verse_number": 65, "content": "Seine Fruchte scheinen wie Kopfe der Satane zu sein"},
  {"surah_number": 37, "verse_number": 66, "content": "Sie sollen davon essen und (ihre) Bauche damit fullen"},
  {"surah_number": 37, "verse_number": 67, "content": "Darauf sollen sie eine Mischung von siedendem Wasser (zum Trank) erhalten"},
  {"surah_number": 37, "verse_number": 68, "content": "Danach sollen sie zur Gahim zuruck kehren"},
  {"surah_number": 37, "verse_number": 69, "content": "Sie fanden ihre Vater als Irrende vor"},
  {"surah_number": 37, "verse_number": 70, "content": "und so traten sie eilends in ihre Fußstapfen"},
  {"surah_number": 37, "verse_number": 71, "content": "Und die meisten der Vorfahren waren vor ihnen irregegangen"},
  {"surah_number": 37, "verse_number": 72, "content": "Und wahrlich, Wir hatten ihnen Warner gesandt"},
  {"surah_number": 37, "verse_number": 73, "content": "Also, siehe nun, wie da der Ausgang derer war, die gewarnt worden waren"},
  {"surah_number": 37, "verse_number": 74, "content": "mit Ausnahme der erwahlten Diener Allahs"},
  {"surah_number": 37, "verse_number": 75, "content": "Und wahrlich, Noah rief Uns an, und Wir sind es, die am besten erhoren"},
  {"surah_number": 37, "verse_number": 76, "content": "Und Wir erretteten ihn und die Seinen aus der großen Bedrangnis"},
  {"surah_number": 37, "verse_number": 77, "content": "Und Wir machten seine Nachkommenschaft zu den einzig Uberlebenden"},
  {"surah_number": 37, "verse_number": 78, "content": "Und Wir bewahrten seinen Namen unter den kunftigen Geschlechtern"},
  {"surah_number": 37, "verse_number": 79, "content": "Friede sei auf Noah in allen Welten"},
  {"surah_number": 37, "verse_number": 80, "content": "So belohnen Wir jene, die Gutes wirken"},
  {"surah_number": 37, "verse_number": 81, "content": "Er gehorte zu Unseren glaubigen Dienern"},
  {"surah_number": 37, "verse_number": 82, "content": "Dann ließen Wir die anderen ertrinken"},
  {"surah_number": 37, "verse_number": 83, "content": "Und wahrlich, von seiner Art war Abraham"},
  {"surah_number": 37, "verse_number": 84, "content": "als er zu seinem Herrn mit heilem Herzen kam"},
  {"surah_number": 37, "verse_number": 85, "content": "(und) als er zu seinem Vater und zu seinem Volk sagte: \"Was verehrt ihr da"},
  {"surah_number": 37, "verse_number": 86, "content": "Ist es eine Luge - Gotter außer Allah -, was ihr begehrt"},
  {"surah_number": 37, "verse_number": 87, "content": "Was denkt ihr denn vom Herrn der Welten"},
  {"surah_number": 37, "verse_number": 88, "content": "Dann warf er einen Blick zu den Sternen"},
  {"surah_number": 37, "verse_number": 89, "content": "und sagte: \"Mir ist ubel"},
  {"surah_number": 37, "verse_number": 90, "content": "Da kehrten sie ihm den Rucken (und) gingen fort"},
  {"surah_number": 37, "verse_number": 91, "content": "Nun wandte er sich heimlich an ihre Gotter und sagte: \"Wollt ihr nicht essen"},
  {"surah_number": 37, "verse_number": 92, "content": "Was ist euch, daß ihr nicht redet"},
  {"surah_number": 37, "verse_number": 93, "content": "Dann begann er sie plotzlich mit der Rechten zu schlagen"},
  {"surah_number": 37, "verse_number": 94, "content": "Da kamen sie zu ihm geeilt"},
  {"surah_number": 37, "verse_number": 95, "content": "Er sagte: \"Verehrt ihr das, was ihr gemeißelt habt"},
  {"surah_number": 37, "verse_number": 96, "content": "obwohl Allah euch und das, was ihr gemacht habt, erschaffen hat"},
  {"surah_number": 37, "verse_number": 97, "content": "Sie sagten: \"Baut einen Bau fur ihn und werft ihn in die Gahim"},
  {"surah_number": 37, "verse_number": 98, "content": "Sie wollten Ranke gegen ihn schmieden, allein Wir machten sie zu den Niedrig sten"},
  {"surah_number": 37, "verse_number": 99, "content": "Und er sagte: \"Seht, ich gehe zu meinem Herrn, Der mich rechtleiten wird"},
  {"surah_number": 37, "verse_number": 100, "content": "Mein Herr, gewahre mir einen rechtschaffenen (Sohn)"},
  {"surah_number": 37, "verse_number": 101, "content": "Dann gaben Wir ihm die frohe Botschaft von einem sanftmutigen Sohn"},
  {
    "surah_number": 37,
    "verse_number": 102,
    "content": "Als er alt genug war, um mit ihm zu arbeiten, sagte er. O mein Sohn, ich sehe im Traum, daß ich dich schlachte. Nun schau, was meinst du dazu?\" Er sagte: \"O mein Vater, tu, wie dir befohlen wird; du sollst mich - so Allah will - unter den Geduldigen finden"
  },
  {"surah_number": 37, "verse_number": 103, "content": "Als sie sich beide (Allahs Willen) ergeben hatten und er ihn mit der Stirn auf den Boden hingelegt hatte"},
  {"surah_number": 37, "verse_number": 104, "content": "da riefen Wir ihm zu: \"O Abraham"},
  {"surah_number": 37, "verse_number": 105, "content": "du hast bereits das Traumgesicht erfullt.\" So belohnen Wir die, die Gutes tun"},
  {"surah_number": 37, "verse_number": 106, "content": "Wahrlich, das ist offenkundig eine schwere Prufung"},
  {"surah_number": 37, "verse_number": 107, "content": "Und Wir losten ihn durch ein großes Schlachttier aus"},
  {"surah_number": 37, "verse_number": 108, "content": "Und Wir bewahrten seinen Namen unter den kunftigen Geschlechtern"},
  {"surah_number": 37, "verse_number": 109, "content": "Friede sei auf Abraham"},
  {"surah_number": 37, "verse_number": 110, "content": "So belohnen Wir die, die Gutes tun"},
  {"surah_number": 37, "verse_number": 111, "content": "Er gehorte zu Unseren glaubigen Dienern"},
  {"surah_number": 37, "verse_number": 112, "content": "Und Wir gaben ihm die frohe Botschaft von Isaak, einem Propheten, der zu den Rechtschaffenen gehorte"},
  {"surah_number": 37, "verse_number": 113, "content": "Und Wir segneten ihn und Isaak. Und unter ihren Nachkommen sind (manche), die Gutes tun, und (andere), die offenkundig gegen sich selbst freveln"},
  {"surah_number": 37, "verse_number": 114, "content": "Und wahrlich, Wir hatten Uns (auch) gegen Moses und Aaron Gnadig erwiesen"},
  {"surah_number": 37, "verse_number": 115, "content": "Und Wir erretteten sie beide und ihr Volk aus der großen Bedrangnis"},
  {"surah_number": 37, "verse_number": 116, "content": "Und Wir halfen ihnen; so waren sie es, die siegten"},
  {"surah_number": 37, "verse_number": 117, "content": "Und Wir gaben ihnen die deutliche Schrift"},
  {"surah_number": 37, "verse_number": 118, "content": "Und Wir fuhrten sie auf den geraden Weg"},
  {"surah_number": 37, "verse_number": 119, "content": "Und Wir bewahrten ihren Namen unter den kunftigen Geschlechtern"},
  {"surah_number": 37, "verse_number": 120, "content": "Friede sei auf Moses und Aaron"},
  {"surah_number": 37, "verse_number": 121, "content": "So belohnen Wir die, die Gutes tun"},
  {"surah_number": 37, "verse_number": 122, "content": "Sie gehorten beide zu Unseren glaubigen Dienern"},
  {"surah_number": 37, "verse_number": 123, "content": "Und wahrlich, Elias war auch einer der Gesandten"},
  {"surah_number": 37, "verse_number": 124, "content": "Da sagte er zu seinem Volk: \"Wollt ihr nicht gottesfurchtig sein"},
  {"surah_number": 37, "verse_number": 125, "content": "Wollt ihr 8a`1 (euren Gotzen) anrufen und den besten Schopfer verlassen"},
  {"surah_number": 37, "verse_number": 126, "content": "Allah, euren Herrn und den Herrn eurer Vorvater"},
  {"surah_number": 37, "verse_number": 127, "content": "Jedoch sie erklarten ihn als Lugner, und sie werden bestimmt (zum Gericht) gebracht werden"},
  {"surah_number": 37, "verse_number": 128, "content": "ausgenommen die erwahlten Diener Allahs"},
  {"surah_number": 37, "verse_number": 129, "content": "Und Wir bewahrten seinen Namen unter den kunftigen Geschlechtern"},
  {"surah_number": 37, "verse_number": 130, "content": "Friede sei auf Il-Yasin"},
  {"surah_number": 37, "verse_number": 131, "content": "So belohnen Wir die, die Gutes tun"},
  {"surah_number": 37, "verse_number": 132, "content": "Er gehorte zu Unseren glaubigen Dienern"},
  {"surah_number": 37, "verse_number": 133, "content": "Und wahrlich, (auch) Lot war einer der Gesandten"},
  {"surah_number": 37, "verse_number": 134, "content": "Da erretteten Wir ihn und alle die Seinen"},
  {"surah_number": 37, "verse_number": 135, "content": "ausgenommen eine alte Frau, die mit den (anderen) zuruckblieb"},
  {"surah_number": 37, "verse_number": 136, "content": "Hierauf zerstorten Wir die anderen"},
  {"surah_number": 37, "verse_number": 137, "content": "Und wahrlich, ihr geht an ihnen am Morgen voruber"},
  {"surah_number": 37, "verse_number": 138, "content": "und auch am Abend. Wollt ihr es da nicht begreifen"},
  {"surah_number": 37, "verse_number": 139, "content": "Und sicher war Jonas einer der Gesandten"},
  {"surah_number": 37, "verse_number": 140, "content": "Da floh er zu dem beladenen Schiff"},
  {"surah_number": 37, "verse_number": 141, "content": "und loste und verlor dabei"},
  {"surah_number": 37, "verse_number": 142, "content": "Und der große Fisch verschlang ihn, wahrend er (Jonas, sich selbst) tadelte"},
  {"surah_number": 37, "verse_number": 143, "content": "Wenn er nicht zu jenen gehort hatte, die (Allah) preisen"},
  {"surah_number": 37, "verse_number": 144, "content": "ware er gewiß in dessen Bauch bis zum Tage der Auferstehung geblieben"},
  {"surah_number": 37, "verse_number": 145, "content": "Dann warfen Wir ihn ins Freie, und er war krank"},
  {"surah_number": 37, "verse_number": 146, "content": "Und Wir ließen eine Kurbispflanze uber ihm wachsen"},
  {"surah_number": 37, "verse_number": 147, "content": "Und Wir entsandten ihn zu hunderttausend oder mehr (Menschen)"},
  {"surah_number": 37, "verse_number": 148, "content": "Und sie wurden glaubig; so gewahrten Wir ihnen fur eine Zeitlang Versorgung"},
  {"surah_number": 37, "verse_number": 149, "content": "Nun frage sie, ob dein Herr Tochter hat, wahrend sie Sohne haben"},
  {"surah_number": 37, "verse_number": 150, "content": "Oder haben Wir etwa die Engel weiblich erschaffen, wahrend sie zugegen waren"},
  {"surah_number": 37, "verse_number": 151, "content": "Es ist bloß ihre eigene Luge, wenn sie sagen"},
  {"surah_number": 37, "verse_number": 152, "content": "Allah hat gezeugt\"; und sie sind wahrlich Lugner"},
  {"surah_number": 37, "verse_number": 153, "content": "Hat Er Tochter den Sohnen vorgezogen"},
  {"surah_number": 37, "verse_number": 154, "content": "Was verwirrt euch? Wie urteilt ihr nur"},
  {"surah_number": 37, "verse_number": 155, "content": "Wollt ihr euch denn nicht besinnen"},
  {"surah_number": 37, "verse_number": 156, "content": "Oder habt ihr einen klaren Beweis"},
  {"surah_number": 37, "verse_number": 157, "content": "Sodann bringt euer Buch herbei, wenn ihr wahrhaftig seid"},
  {"surah_number": 37, "verse_number": 158, "content": "Und sie unterstellten Ihm eine Blutsverwandschaft mit den Ginn; wahrend die Ginn doch recht wohl wissen, daß sie (vor Ihn zum Gericht) gebracht werden sollen"},
  {"surah_number": 37, "verse_number": 159, "content": "Gepriesen sei Allah hoch uber all das, was sie beschreiben"},
  {"surah_number": 37, "verse_number": 160, "content": "Ausgenommen (davon sind) die erwahlten Diener Allahs"},
  {"surah_number": 37, "verse_number": 161, "content": "Wahrlich, ihr und das, was ihr verehrt"},
  {"surah_number": 37, "verse_number": 162, "content": "ihr vermogt nicht (einen) gegen Ihn zu verfuhren"},
  {"surah_number": 37, "verse_number": 163, "content": "mit Ausnahme dessen, der in der Gahim brennen wird"},
  {"surah_number": 37, "verse_number": 164, "content": "Und da ist keiner unter uns (Engeln), der nicht seinen (ihm) zugewiesenen Platz hatte"},
  {"surah_number": 37, "verse_number": 165, "content": "Und wahrlich, wir sind die in Reihen Geordneten"},
  {"surah_number": 37, "verse_number": 166, "content": "Und wahrlich, wir sind es, die (Allah) preisen"},
  {"surah_number": 37, "verse_number": 167, "content": "Und sie pflegten doch zu sagen"},
  {"surah_number": 37, "verse_number": 168, "content": "Hatten wir nur eine Mahnung von Seiten der Fruheren (erhalten)"},
  {"surah_number": 37, "verse_number": 169, "content": "so waren wir sicher Allahs erwahlte Diener gewesen"},
  {"surah_number": 37, "verse_number": 170, "content": "Dennoch glauben sie nicht an ihn (den Quran), allein sie werden es bald erfahren"},
  {"surah_number": 37, "verse_number": 171, "content": "Wahrlich, Unser Wort ist schon an Unsere gesandten Diener ergangen"},
  {"surah_number": 37, "verse_number": 172, "content": "sie sind wahrlich die, denen geholfen wird"},
  {"surah_number": 37, "verse_number": 173, "content": "Und Unsere Heerschar wird sicher siegreich sein"},
  {"surah_number": 37, "verse_number": 174, "content": "Darum wende dich von ihnen fur eine Zeitlang ab"},
  {"surah_number": 37, "verse_number": 175, "content": "und beobachte sie; denn sie werden bald sehen"},
  {"surah_number": 37, "verse_number": 176, "content": "Ist es etwa Unsere Strafe, die sie beschleunigen mochten"},
  {"surah_number": 37, "verse_number": 177, "content": "Doch wenn sie uber ihr Gebiet herabkommt, dann wird der Morgen fur die Gewarnten ubel sein"},
  {"surah_number": 37, "verse_number": 178, "content": "So wende dich von ihnen fur eine Zeitlang ab"},
  {"surah_number": 37, "verse_number": 179, "content": "und beobachte (sie); denn sie werden bald sehen"},
  {"surah_number": 37, "verse_number": 180, "content": "Gepriesen sei dein Herr, der Herr der Erhabenheit, Hoch uber dem, was sie beschreiben"},
  {"surah_number": 37, "verse_number": 181, "content": "Und Friede sei auf den Gesandten"},
  {"surah_number": 37, "verse_number": 182, "content": "Und alles Lob gebuhrt Allah, dem Herrn der Welten"},
  {"surah_number": 38, "verse_number": 1, "content": "Sad. Beim Quran mit der Ermahnung"},
  {"surah_number": 38, "verse_number": 2, "content": "Die, die aber unglaubig sind, verharren in falschem Stolz und Feindseligkeit"},
  {"surah_number": 38, "verse_number": 3, "content": "Wie oft ließen Wir manches Geschlecht schon vor ihnen zugrunde gehen! Sie schrien, als keine Zeit mehr zum Entrinnen war"},
  {"surah_number": 38, "verse_number": 4, "content": "Und sie wundern sich, daß ein Warner aus ihrer Mitte zu ihnen gekommen ist; und die Unglaubigen sagen: \"Das ist ein Zauberer, ein Lugner"},
  {"surah_number": 38, "verse_number": 5, "content": "Macht er die Gotter zu einem einzigen Gott? Dies ist wahrlich ein wunderbares Ding"},
  {"surah_number": 38, "verse_number": 6, "content": "Und die Vornehmen unter ihnen liefen davon (und sagten): \"Geht und haltet an euren Gottern fest. Das ist es, was man beabsichtigt"},
  {"surah_number": 38, "verse_number": 7, "content": "Wir haben hiervon nie etwas in der fruheren Religion gehort. Dies ist nichts als eine Dichtung"},
  {"surah_number": 38, "verse_number": 8, "content": "Ist die Ermahnung unter uns nur zu ihm allein gekommen?\" Nein, sie hegen Zweifel uber Meine Er mahnung. Nein, sie haben Meine Strafe nicht gekostet"},
  {"surah_number": 38, "verse_number": 9, "content": "Besitzen sie etwa die Schatze der Barmherzigkeit deines Herrn, des Allmachtigen, des Gabenverleihers"},
  {"surah_number": 38, "verse_number": 10, "content": "Oder gehort ihnen das Konigreich der Himmel und der Erde und dessen, was zwischen beiden ist? Mogen sie nur weiter Mittel und Wege (gegen Mich) ersinnen"},
  {"surah_number": 38, "verse_number": 11, "content": "Eine Heerschar der Verbundeten wird in die Flucht geschlagen werden"},
  {"surah_number": 38, "verse_number": 12, "content": "Vor ihnen haben schon geleugnet: das Volk Noahs und die `Ad und Pharao mit den bodenfesten Bauten"},
  {"surah_number": 38, "verse_number": 13, "content": "und die Tamud und das Volk Lots und die Bewohner des Waldes diese waren die Verbundeten"},
  {"surah_number": 38, "verse_number": 14, "content": "Ein jeder hatte die Gesandten der Luge bezichtigt, darum war Meine Strafe fallig gewesen"},
  {"surah_number": 38, "verse_number": 15, "content": "Und diese erwarteten nichts als einen einzigen Schrei, fur den es keinen Aufschub gibt"},
  {"surah_number": 38, "verse_number": 16, "content": "Und sie sagen: \"Unser Herr, beschleunige Unseren Teil (der Strafe) vor dem Tage der Abrechnung"},
  {"surah_number": 38, "verse_number": 17, "content": "Ertrage in Geduld, was sie reden, und gedenke Unseres Dieners David, des Kraftvollen. Er war gehorsam"},
  {"surah_number": 38, "verse_number": 18, "content": "Wahrlich, Wir machten (ihm) die Berge dienstbar, um mit ihm am Abend und beim Sonnenaufgang (Allah) zu lobpreisen"},
  {"surah_number": 38, "verse_number": 19, "content": "Und die Vogel in Scharen - alle waren sie ihm gehorsam"},
  {"surah_number": 38, "verse_number": 20, "content": "Und Wir festigten sein Konigreich und gaben ihm Weisheit und ein unwiderrufliches Urteilsvermogen"},
  {"surah_number": 38, "verse_number": 21, "content": "Ist die Geschichte von den Streitenden auch zu dir gelangt? Wie sie uber die Mauer (seines) Gebetsgemachs kletterten"},
  {
    "surah_number": 38,
    "verse_number": 22,
    "content":
        "(und) wie sie bei David eindrangen und er sich vor ihnen furchtete? Sie sagten: \"Furchte dich nicht. (Wir sind) zwei Streitende, von denen einer sich gegen den anderen vergangen hat; richte darum in Gerechtigkeit zwischen uns und handle nicht ungerecht und leite uns auf den ebenen Weg"
  },
  {"surah_number": 38, "verse_number": 23, "content": "Dieser ist mein Bruder; er hat neunundneunzig Mutterschafe, und ich habe ein einziges Mutterschaf. Dennoch sagte er: \"Ubergib es mir\", und hat mich in der Rede uberwunden"},
  {
    "surah_number": 38,
    "verse_number": 24,
    "content":
        "Er (David) sagte: \"Wahrlich, er hat ein Unrecht an dir verubt, als er dein Mutterschaf zu seinen eigenen Mutterschafen hinzu verlangte. Und gewiß, viele Teilhaber vergehen sich gegeneinander; nur die (sind davon) ausgenommen, die glauben und gute Werke tun; und das sind wenige.\" Und David merkte, daß Wir ihn auf die Probe gestellt hatten; also bat er seinen Herrn um Verzeihung und fiel betend nieder und bekehrte sich"
  },
  {"surah_number": 38, "verse_number": 25, "content": "Darum vergaben Wir ihm dies; und wahrlich, er hatte nahen Zutritt zu Uns und eine herrliche Einkehr (bei Uns)"},
  {
    "surah_number": 38,
    "verse_number": 26,
    "content":
        "O David, Wir haben dich zu einem Nachfolger auf Erden gemacht; richte darum zwischen den Menschen in Gerechtigkeit, und folge nicht (deinen) personlichen Neigungen, damit sie dich nicht vom Wege Allahs abirren lassen.\" Wahrlich jenen, die von Allahs Weg abirren, wird eine strenge Strafe zuteil sein, weil sie den Tag der Abrechnung vergaßen"
  },
  {"surah_number": 38, "verse_number": 27, "content": "Und Wir haben den Himmel und die Erde und das, was zwischen beiden ist, nicht sinnlos erschaffen. Das ist die Ansicht derer, die unglaubig sind. Wehe denn den Unglaubigen wegen des Feuers"},
  {"surah_number": 38, "verse_number": 28, "content": "Oder sollen Wir etwa diejenigen, die glauben und gute Werke tun, gleich denen behandeln, die Verderben auf Erden stiften? Oder sollen Wir die Gottesfurchtigen wie die Unverschamten behandeln"},
  {"surah_number": 38, "verse_number": 29, "content": "Es ist ein Buch voll des Segens, das Wir zu dir hinabgesandt haben, auf daß sie uber seine Verse nachdenken, und auf daß diejenigen ermahnt werden mogen, die verstandig sind"},
  {"surah_number": 38, "verse_number": 30, "content": "Und Wir bescherten David Salomo. (Er war) ein vortrefflicher Diener; stets wandte er sich (zu Allah)"},
  {"surah_number": 38, "verse_number": 31, "content": "Als vor ihn zur Abendstunde schnelltrabende Rennpferde aus edelster Zucht gebracht wurden"},
  {"surah_number": 38, "verse_number": 32, "content": "sagte er: \"lch habe die guten Dinge dieser Welt sehr lieb und habe dabei versaumt, meines Herrn zu gedenken\" - bis sie (die Sonne) hinter dem Horizont verborgen war"},
  {"surah_number": 38, "verse_number": 33, "content": "Bringt sie zu mir zuruck.\" Dann begann er uber (ihre) Beine und Halse zu streichen"},
  {"surah_number": 38, "verse_number": 34, "content": "Und wahrlich, Wir stellten Salomo auf die Probe, und Wir setzten einen Leib auf seinen Thron. Dann bekehrte er sich"},
  {"surah_number": 38, "verse_number": 35, "content": "Er sagte: \"O mein Herr, vergib mir und gewahre mir ein Konigreich, wie es keinem nach mir geziemt; wahrlich, Du bist der Gabenverleiher"},
  {"surah_number": 38, "verse_number": 36, "content": "Darauf machten Wir ihm den Wind dienstbar, auf daß er auf sein Geheiß hin sanft wehte, wohin er wollte"},
  {"surah_number": 38, "verse_number": 37, "content": "Und (Wir machten ihm) die Satane, alle die Erbauer und Taucher"},
  {"surah_number": 38, "verse_number": 38, "content": "wie auch andere, die in Fesseln aneinander gekettet waren (, dienstbar)"},
  {"surah_number": 38, "verse_number": 39, "content": "Dies ist Unsere Gabe, so erweise dich als Wohltater oder sei zuruckhaltend (im Geben), ohne abzurechnen"},
  {"surah_number": 38, "verse_number": 40, "content": "Und sicher hatte er nahen Zutritt zu Uns und eine herrliche Einkehr (bei Uns)"},
  {"surah_number": 38, "verse_number": 41, "content": "Und gedenke Unseres Dieners Hiob, als er seinen Herrn anrief: \"Satan hat mich beruhrt mit Muhsal und Pein"},
  {"surah_number": 38, "verse_number": 42, "content": "Stampfe mit deinem Fuß auf. Hier ist kuhles Wasser zum Waschen und zum Trinken"},
  {"surah_number": 38, "verse_number": 43, "content": "Und Wir schenkten ihm seine Angehorigen (wieder) und noch einmal so viele dazu von Uns als Barmherzigkeit und als Ermahnung fur die Verstandigen"},
  {"surah_number": 38, "verse_number": 44, "content": "Und (Wir sprachen): \"Nimm ein Bundel in deine Hand und schlage damit zu und werde nicht eidbruchig.\" Wahrlich, Wir fanden ihn geduldig. (Er war) ein vortrefflicher Diener; stets wandte er sich (Allah) zu"},
  {"surah_number": 38, "verse_number": 45, "content": "Und gedenke Unserer Diener Abraham, Isaak und Jakob - Manner von Kraft und Einsicht"},
  {"surah_number": 38, "verse_number": 46, "content": "Wir erwahlten sie zu einem besonderen Zweck - zur Erinnerung an die Wohnstatt (des Jenseits)"},
  {"surah_number": 38, "verse_number": 47, "content": "Und wahrlich, vor Uns gehoren sie zu den Auserwahlten, den Besten"},
  {"surah_number": 38, "verse_number": 48, "content": "Und gedenke Ismaels, Elisas und Du-l-Kifls; alle gehoren sie zu den Besten"},
  {"surah_number": 38, "verse_number": 49, "content": "Dies ist eine Ermahnung; den Rechtschaffenen wird gewiß eine herrliche Statte der Ruckkehr zuteil sein"},
  {"surah_number": 38, "verse_number": 50, "content": "die Garten von Eden, deren Tore fur sie geoffnet sind"},
  {"surah_number": 38, "verse_number": 51, "content": "Dort werden sie zuruckgelehnt ruhen; dort werden sie Fruchte in Mengen und reichlich Trank haben"},
  {"surah_number": 38, "verse_number": 52, "content": "Und bei ihnen werden (Keusche) sein, die zuchtig blicken, (und) Gefahrtinnen gleichen Alters"},
  {"surah_number": 38, "verse_number": 53, "content": "Dies ist, was euch fur den Tag der Abrechnung verheißen wurde"},
  {"surah_number": 38, "verse_number": 54, "content": "Wahrlich, das ist Unsere Versorgung; nie wird sie sich erschopfen"},
  {"surah_number": 38, "verse_number": 55, "content": "Dies ist (fur die Glaubigen) Doch fur die Widerspenstigen ist eine uble Statte der Ruckkehr (bestimmt)"},
  {"surah_number": 38, "verse_number": 56, "content": "Gahannam, die sie betreten werden; welch schlimme Statte"},
  {"surah_number": 38, "verse_number": 57, "content": "Dies ist (fur die Unglaubigen). Mogen sie es denn kosten: siedendes Wasser und eine Trankmischung aus Blut und Eiter"},
  {"surah_number": 38, "verse_number": 58, "content": "und anderes gleicher Art und in Vielfalt"},
  {"surah_number": 38, "verse_number": 59, "content": "Hier ist eine Schar, die mit euch zusammen hineingesturzt wird.\" Kein Willkomm (sei) ihnen! Sie sollen im Feuer brennen"},
  {"surah_number": 38, "verse_number": 60, "content": "Sie werden sagen: \"Nein, ihr seid es. Kein Willkomm (sei) euch denn! Ihr seid es, die uns dies bereitet haben. Und welch schlimmer Ort ist das"},
  {"surah_number": 38, "verse_number": 61, "content": "Sie werden sagen: \"Unser Herr, wer immer uns dies bereitete fuge ihm die doppelte Strafe im Feuer hinzu"},
  {"surah_number": 38, "verse_number": 62, "content": "Und sie werden sagen: \" Was ist mit uns geschehen, daß wir nicht die Leute sehen, die wir zu den Bosen zu zahlen pflegten"},
  {"surah_number": 38, "verse_number": 63, "content": "Sollte es sein, daß wir sie (zu Unrecht) zum Gespott gemacht haben, oder haben unsere Augen sie verfehlt"},
  {"surah_number": 38, "verse_number": 64, "content": "Wahrlich, das ist in der Tat ein Redestreit der Bewohner des Feuers untereinander"},
  {"surah_number": 38, "verse_number": 65, "content": "Sprich: \"lch bin nur ein Warner; und es ist kein Gott außer Allah, dem Einzigen, dem Allbezwingenden"},
  {"surah_number": 38, "verse_number": 66, "content": "dem Herrn der Himmel und der Erde und dessen was zwischen beiden ist, dem Erhabenen, dem Allverzeihenden"},
  {"surah_number": 38, "verse_number": 67, "content": "Sprich: \"Es ist eine große Kunde"},
  {"surah_number": 38, "verse_number": 68, "content": "Ihr wendet euch jedoch davon ab"},
  {"surah_number": 38, "verse_number": 69, "content": "Ich hatte keine Kunde von den hoheren Bewohnern, da sie untereinander stritten"},
  {"surah_number": 38, "verse_number": 70, "content": "Nur dies wurde mir offenbart, daß ich nur ein deutlicher Warner bin"},
  {"surah_number": 38, "verse_number": 71, "content": "Da sprach dein Herr zu den Engeln: \"Es ist Mein Wille, einen Menschen aus Ton zu erschaffen"},
  {"surah_number": 38, "verse_number": 72, "content": "Und wenn Ich ihn gebildet und Meinen Geist in ihn eingehaucht habe, dann fallt vor ihm nieder"},
  {"surah_number": 38, "verse_number": 73, "content": "Da warfen sich alle Engel nieder"},
  {"surah_number": 38, "verse_number": 74, "content": "bis auf Iblis. Er wandte sich hochmutig ab und war unglaubig"},
  {"surah_number": 38, "verse_number": 75, "content": "Er sprach: \"O Iblis, was hindert dich daran, dich vor etwas niederzuwerfen, das Ich mit Meinen Handen geschaffen habe? Bist du hochmutig oder gehorst du zu den Uberheblichen"},
  {"surah_number": 38, "verse_number": 76, "content": "Er sagte: \"lch bin besser als er. Du erschufst mich aus Feuer, und ihn hast Du aus Ton erschaffen"},
  {"surah_number": 38, "verse_number": 77, "content": "Er sprach: \"So geh hinaus von hier; denn du bist ein Verfluchter"},
  {"surah_number": 38, "verse_number": 78, "content": "Und Mein Fluch soll auf dir bis zum Tage des Gerichts lasten"},
  {"surah_number": 38, "verse_number": 79, "content": "Er sagte: \"O mein Herr, gewahre mir eine Frist bis zu dem Tage, an dem sie auferweckt werden"},
  {"surah_number": 38, "verse_number": 80, "content": "Er sprach: \"Also, wird dir die Frist gewahrt"},
  {"surah_number": 38, "verse_number": 81, "content": "bis zum Tage einer vorbestimmten Zeit"},
  {"surah_number": 38, "verse_number": 82, "content": "Er sagte: \"Bei Deiner Erhabenheit, ich will sie sicher alle in die Irre fuhren"},
  {"surah_number": 38, "verse_number": 83, "content": "Ausgenommen (davon sind) Deine erwahlten Diener unter ihnen"},
  {"surah_number": 38, "verse_number": 84, "content": "Er sprach: \"Dann ist dies die Wahrheit, und Ich rede die Wahrheit"},
  {"surah_number": 38, "verse_number": 85, "content": "daß Ich wahrlich Gahannam mit dir und denen, die dir folgen, insgesamt fullen werde"},
  {"surah_number": 38, "verse_number": 86, "content": "Sprich: \"lch verlange von euch keinen Lohn dafur, noch bin ich einer derer, die sich (etwas) anmaßen"},
  {"surah_number": 38, "verse_number": 87, "content": "Dieser (Quran) ist nichts als eine Ermahnung fur die Welten"},
  {"surah_number": 38, "verse_number": 88, "content": "Und ihr werdet sicher seine Kunde nach einer Zeit kennen"},
  {"surah_number": 39, "verse_number": 1, "content": "Die Offenbarung des Buches stammt von Allah, dem Erhabenen, dem Allweisen"},
  {"surah_number": 39, "verse_number": 2, "content": "Wahrlich, Wir haben dir das Buch mit der Wahrheit hinabgesandt; so diene denn Allah in lauterem Gehorsam Ihm gegenuber"},
  {
    "surah_number": 39,
    "verse_number": 3,
    "content":
        "Wahrlich, Allah (allein) gebuhrt lauterer Gehorsam. Und diejenigen, die sich andere zu Beschutzern nehmen statt Ihn (, sagen): \"Wir dienen ihnen nur, damit sie uns Allah nahebringen.\" Wahrlich, Allah wird zwischen ihnen uber das, woruber sie uneins sind, richten. Wahrlich, Allah weist nicht dem den Weg, der ein Lugner, ein Undankbarer ist"
  },
  {"surah_number": 39, "verse_number": 4, "content": "Hatte Allah Sich einen Sohn nehmen wollen, hatte Er wahlen konnen, was Ihm beliebte, von dem, was Er erschaffen hat. Preis (sei) Ihm! Er ist Allah, der Einzige, der Allbezwingende"},
  {
    "surah_number": 39,
    "verse_number": 5,
    "content":
        "Er schuf die Himmel und die Erde in gerechter Weise. Er laßt die Nacht uber den Tag und den Tag uber die Nacht rollen; und Er hat (euch) die Sonne und den Mond dienstbar gemacht; ein jedes (Gestirn) lauft fur eine bestimmte Frist. Wahrlich, Er allein ist der Erhabene, der Allverzeihende"
  },
  {
    "surah_number": 39,
    "verse_number": 6,
    "content":
        "Er schuf euch aus einem einzigen Wesen, dann machte Er aus diesem seine Gattin, und Er erschuf fur euch acht Haustiere in Paaren. Er erschafft euch in den Schoßen eurer Mutter, Schopfung nach Schopfung, in drei Finsternissen. Das ist Allah, euer Herr. Sein ist das Reich. Es ist kein Gott außer Ihm. Wie lasset ihr euch da (von Ihm) abwenden"
  },
  {
    "surah_number": 39,
    "verse_number": 7,
    "content":
        "Wenn ihr undankbar seid, so ist Allah auf keinen von euch angewiesen. Und Er findet nicht Wohlgefallen am Unglauben Seiner Diener; doch wenn ihr aber dankbar seid, so gefallt Ihm das an euch. Und keine lasttragende (Seele) soll die Last einer anderen tragen. Danach werdet ihr zu eurem Herrn heimkehren; und Er wird euch verkunden, was ihr zu tun pflegtet. Wahrlich, Er weiß wohl, was in den Herzen ist"
  },
  {
    "surah_number": 39,
    "verse_number": 8,
    "content":
        "Und wenn den Menschen ein Unheil trifft, so ruft er seinen Herrn an und wendet sich bußfertig Ihm zu. Dann aber, wenn Er ihm eine Gnade von Sich aus gewahrt hat, vergißt er, um was er Ihn zuvor zu bitten pflegte, und setzt Allah Partner zur Seite, auf daß er (die Menschen) von Seinem Wege ab in die Irre fuhre. Sprich: \"Vergnuge dich mit deinem Unglauben fur eine kurze Zeit; denn du gehorst zu den Bewohnern des Feuers"
  },
  {
    "surah_number": 39,
    "verse_number": 9,
    "content":
        "Ist etwa jener, der zu Allah in den Nachtstunden - sich niederwerfend und stehend - betet, der sich vor dem Jenseits furchtet und auf die Barmherzigkeit seines Herrn hofft (einem Ungehorsamen gleich)? Sprich: \"Sind solche, die wissen, denen gleich, die nicht wissen?\" Allein nur diejenigen lassen sich warnen, die verstandig sind"
  },
  {
    "surah_number": 39,
    "verse_number": 10,
    "content": "Sprich: \"O meine Diener, die ihr glaubig seid, furchtet euren Herrn. Fur diejenigen, die in dieser Welt Gutes tun, ist Gutes (bestimmt) Und Allahs Erde ist weit. Wahrlich, den Geduldigen wird ihr Lohn (von Allah) ohne zu rechnen gewahrt werden"
  },
  {"surah_number": 39, "verse_number": 11, "content": "Sprich: \"Mir wurde befohlen, Allah zu dienen, in lauterem Glauben Ihm gegenuber"},
  {"surah_number": 39, "verse_number": 12, "content": "Und mir wurde befohlen, der erste der Gottergebenen zu sein"},
  {"surah_number": 39, "verse_number": 13, "content": "Sprich: \"Wahrlich, ich furchte die Strafe eines gewaltigen Tages, wenn ich meinem Herrn ungehorsam ware"},
  {"surah_number": 39, "verse_number": 14, "content": "Sprich: \"Allah ist es, Dem ich in meinem lauteren Glauben Ihm gegenuber diene"},
  {"surah_number": 39, "verse_number": 15, "content": "Verehrt statt Ihm nur, was ihr wollt.\" Sprich: \"Wahrlich, die Verlierenden werden jene sein, die sich selbst und die Ihren am Tage der Auferstehung verlieren.\" Wahrlich, das ist ein offenkundiger Verlust"},
  {"surah_number": 39, "verse_number": 16, "content": "Sie werden uber sich Schichten von dem Feuer haben und unter sich (eben solche) Schichten. Das ist es, wovor Allah Seine Diener warnt: \"O Meine Diener, darum furchtet Mich"},
  {"surah_number": 39, "verse_number": 17, "content": "Und diejenigen, die falsche Gotter anzubeten scheuen und sich zu Allah wenden - fur sie ist die frohe Botschaft (bestimmt) Gib denn die frohe Botschaft Meinen Dienern"},
  {"surah_number": 39, "verse_number": 18, "content": "es sind jene, die auf das Wort horen und dem besten von ihm folgen. Sie sind es, denen Allah den Weg gewiesen hat, und sie sind es, die Verstand besitzen"},
  {"surah_number": 39, "verse_number": 19, "content": "Ist denn der, gegen den das Strafurteil fallig geworden ist (in der Lage, gerettet zu werden)? Kannst du etwa den retten, der im Feuer ist"},
  {"surah_number": 39, "verse_number": 20, "content": "Fur die jedoch, die ihren Herrn furchten, sind Hochgemacher uber Hochgemacher erbaut, unter denen Bache fließen. (Dies ist) eine Verheißung Allahs - Allah bricht Sein Versprechen nicht"},
  {
    "surah_number": 39,
    "verse_number": 21,
    "content":
        "Hast Du nicht gesehen, daß Allah Wasser vom Himmel niedersendet und es als Quellen in die Erde dringen laßt und dadurch Gewachs hervorbringt, das mannigfaltig an Farben ist? Dann wird es reif, und du siehst es gelb werden; dann laßt Er es in Stucke zerbrechen. Hierin liegt wahrlich eine Mahnung fur Leute von Verstand"
  },
  {
    "surah_number": 39,
    "verse_number": 22,
    "content": "Ist denn der, dem Allah die Brust fur den Islam geweitet hat, so daß er ein Licht von seinem Herrn empfangt (einem Unglaubigen gleich)? Wehe darum denen, deren Herzen vor dem Gedanken an Allah verhartet sind! Sie sind es, die sich in einem offenkundigen Irrtum befinden"
  },
  {
    "surah_number": 39,
    "verse_number": 23,
    "content":
        "Allah hat die schonste Botschaft, ein Buch, hinabgesandt, eine sich gleichartig wiederholende Schrift; vor der denen, die ihren Herrn furchten, die Haut erschauert; dann erweicht sich ihre Haut und ihr Herz zum Gedenken Allahs. Das ist die Fuhrung Allahs; Er leitet damit recht, wen Er will. Und der, den Allah zum Irrenden erklart, wird keinen Fuhrer ha ben"
  },
  {"surah_number": 39, "verse_number": 24, "content": "Ist denn der, der mit seinem Angesicht vor der schrecklichen Strafe am Tage der Auferstehung Schutz sucht (, dem gleich, der gedankenlos ist)? Und zu den Ungerechten wird gesprochen werden: \"Kostet nun, was ihr verdientet"},
  {"surah_number": 39, "verse_number": 25, "content": "Es leugneten jene, die vor ihnen waren; da kam die Strafe uber sie, als sie es nicht ahnten"},
  {"surah_number": 39, "verse_number": 26, "content": "Und Allah ließ sie im irdischen Leben Schande kosten; doch die Strafe im Jenseits wird gewiß großer sein, wenn sie es nur wußten"},
  {"surah_number": 39, "verse_number": 27, "content": "Und wahrlich Wir haben den Menschen in diesem Quran allerlei Gleichnisse gepragt, auf daß sie ermahnt werden"},
  {"surah_number": 39, "verse_number": 28, "content": "(Wir haben ihn) als einen arabischen Quran (hinabgesandt), an dem nichts ist, was (vom geraden Weg) abweichen wurde, auf daß sie gottesfurchtig sein mogen"},
  {
    "surah_number": 39,
    "verse_number": 29,
    "content": "Allah pragt ein Gleichnis von einem Mann, der mehreren Herren gehort, die unter sich im Zwiespalt sind, und (von) einem Mann, der einem einzigen Herrn gehort. Sind sie beide einander gleich? Alles Lob gebuhrt Allah. Jedoch die meisten von ihnen wissen es nicht"
  },
  {"surah_number": 39, "verse_number": 30, "content": "Wahrlich, du wirst sterben und auch sie werden sterben"},
  {"surah_number": 39, "verse_number": 31, "content": "dann, am Tage der Auferstehung, werdet ihr wahrlich vor eurem Herrn miteinander streiten"},
  {"surah_number": 39, "verse_number": 32, "content": "Wer begeht also ein großeres Unrecht als einer, der Lugen gegen Allah vorbringt, oder einer, der die Wahrheit verwirft, wenn sie zu ihm kommt? Ist nicht in Gahannam eine Herberge fur die Unglaubigen"},
  {"surah_number": 39, "verse_number": 33, "content": "Und der, der die Wahrheit bringt, und (der, der) sie annimmt - das sind die Gottesfurchtigen"},
  {"surah_number": 39, "verse_number": 34, "content": "Sie werden alles, was sie wunschen, bei ihrem Herrn haben. Das ist der Lohn derer, die Gutes tun"},
  {"surah_number": 39, "verse_number": 35, "content": "auf daß Allah von ihnen das Schlimmste hinwegnehmen werde von dem, was sie getan haben, und ihnen ihren Lohn geben werde gemaß dem Besten, das sie zu tun pflegten"},
  {"surah_number": 39, "verse_number": 36, "content": "Genugt Allah Seinem Diener nicht? Und doch mochten sie dich mit jenen außer Ihm in Furcht versetzen. Und fur einen, den Allah zum Irrenden erklart, gibt es keinen Fuhrer"},
  {"surah_number": 39, "verse_number": 37, "content": "Und fur einen, den Allah rechtleitet, gibt es keinen, der ihn irrefuhren konnte. Ist Allah nicht Erhaben und Machtig, um Vergeltung zu nehmen"},
  {
    "surah_number": 39,
    "verse_number": 38,
    "content":
        "Und wenn du sie fragst: \"Wer schuf die Himmel und die Erde?\" so werden sie sicher sagen: \"Allah.\" Sprich: \"Seht ihr denn, was ihr außer Allah anruft? Wenn Allah mir Schaden zufugen will, konnen sie (dann) den Schaden entfernen? Oder wenn Er mir Barmherzigkeit erweisen will, konnen sie (dann) Seine Barmherzigkeit verhindern?\" Sprich: \" Allah genugt mir. Auf Ihn vertrauen die Vertrauenden"
  },
  {"surah_number": 39, "verse_number": 39, "content": "Sprich: \"O mein Volk, handelt eurem Standpunkt gemaß; (auch) ich handle (so); (bald aber) werdet ihr erfahren"},
  {"surah_number": 39, "verse_number": 40, "content": "uber wen eine Strafe kommt, die ihn schanden wird, und auf wen eine ewige Strafe niederfahrt"},
  {
    "surah_number": 39,
    "verse_number": 41,
    "content": "Wahrlich, Wir haben dir das Buch mit der Wahrheit fur die Menschen hinabgesandt. Wer dann rechtgeleitet ist, der ist es zu seinem eigenen Besten; und wer irregeht, der geht dann irre zu seinem (eigenen) Schaden. Und du bist nicht ihr Sachwalter"
  },
  {
    "surah_number": 39,
    "verse_number": 42,
    "content":
        "Allah nimmt die Seelen (der Menschen) zur Zeit ihres Sterbens (zu Sich) und (auch die Seelen) derer, die nicht gestorben sind, wenn sie schlafen. Dann halt Er die zuruck, uber die Er den Tod verhangt hat, und schickt die anderen (wieder) bis zu einer bestimmten Frist (ins Leben zuruck) Hierin sind sicher Zeichen fur Leute, die nachdenken"
  },
  {"surah_number": 39, "verse_number": 43, "content": "Oder haben sie etwa statt Allah Fursprecher genommen? Sprich: \"Selbst wenn sie keine Macht uber irgend etwas besitzen und keinen Verstand"},
  {"surah_number": 39, "verse_number": 44, "content": "Sprich: \"Alle Fursprache gehort Allah. Sein ist das Konigreich der Himmel und der Erde. Und zu Ihm werdet ihr zuruckgebracht"},
  {"surah_number": 39, "verse_number": 45, "content": "Und wenn Allah als Einziger genannt wird, dann krampfen sich die Herzen derer, die nicht ans Jenseits glauben, in Widerwillen zusammen; werden aber die genannt, die statt Ihm (verehrt werden), siehe, dann beginnen sie zu frohlocken"},
  {"surah_number": 39, "verse_number": 46, "content": "Sprich: \"O Allah! Schopfer der Himmel und der Erde! Kenner des Verborgenen und des Offenbaren! Du allein wirst zwischen Deinen Dienern richten uber das, woruber sie uneins waren"},
  {
    "surah_number": 39,
    "verse_number": 47,
    "content": "Besaßen diejenigen, die gefrevelt haben, auch alles, was auf Erden ist, und noch einmal soviel dazu, wurden sie sich gewiß damit von der schlimmen Strafe am Tage der Auferstehung loskaufen wollen; aber es wird ihnen von Allah das erscheinen, mit dem sie nimmermehr gerechnet haben"
  },
  {"surah_number": 39, "verse_number": 48, "content": "Und das Bose dessen, was sie gewirkt haben, wird ihnen deutlich werden, und es wird sie das umschließen, woruber sie zu spotten pflegten"},
  {
    "surah_number": 39,
    "verse_number": 49,
    "content": "Wenn nun den Menschen ein Schaden trifft, so ruft er Uns an. Dann aber, wenn Wir ihm Unsere Gnade zuteil werden lassen, sagt er: \"Dies wurde mir nur auf Grund (meines) Wissens gegeben.\" Nein, es ist bloß eine Prufung; jedoch die meisten von ihnen wissen es nicht"
  },
  {"surah_number": 39, "verse_number": 50, "content": "Die vor ihnen waren, sagten auch schon so, doch all das, was sie erworben hatten, nutzte ihnen nichts"},
  {"surah_number": 39, "verse_number": 51, "content": "Und das Bose dessen, was sie gewirkt hatten, erfaßte sie; und diejenigen von diesen, die Unrecht tun, wird (auch) das Bose dessen, was sie gewirkt haben, erfassen; und sie konnen sich dem nicht entziehen"},
  {"surah_number": 39, "verse_number": 52, "content": "Wissen sie nicht, daß Allah dem die Mittel zum Unterhalt erweitert und beschrankt, dem Er will? Wahrlich, hierin liegen Zeichen fur Leute, die glauben"},
  {"surah_number": 39, "verse_number": 53, "content": "Sprich: \"O meine Diener, die ihr euch gegen eure eigenen Seelen vergangen habt, verzweifelt nicht an Allahs Barmherzigkeit; denn Allah vergibt alle Sunden; Er ist der Allverzeihende, der Barmherzige"},
  {"surah_number": 39, "verse_number": 54, "content": "Und kehrt euch zu eurem Herrn, und ergebt euch Ihm, bevor die Strafe uber euch kommt; (denn) dann werdet ihr keine Hilfe finden"},
  {"surah_number": 39, "verse_number": 55, "content": "Und folgt dem Besten, das zu euch von eurem Herrn herabgesandt wurde, bevor die Strafe unversehens uber euch kommt, wahrend ihr es nicht merkt"},
  {"surah_number": 39, "verse_number": 56, "content": "damit nicht etwa einer spreche: \"O wehe mir um dessentwillen, was ich gegenuber Allah versaumte! Denn wahrlich, ich gehorte zu den Spottern"},
  {"surah_number": 39, "verse_number": 57, "content": "oder damit nicht etwa einer spreche: \"Hatte mich Allah geleitet, so ware auch ich unter den Gottesfurchtigen gewesen"},
  {"surah_number": 39, "verse_number": 58, "content": "oder damit nicht einer spreche, wenn er die Strafe sieht: \"Gabe es fur mich doch eine Wiederkehr, dann wollte ich unter denen sein, die Gutes tun"},
  {"surah_number": 39, "verse_number": 59, "content": "Nein; es kamen zu dir Meine Zeichen, aber du verwarfst sie, und du warst hochmutig und warst einer der Unglaubigen"},
  {"surah_number": 39, "verse_number": 60, "content": "Und am Tage der Auferstehung wirst du diejenigen, die uber Allah logen, mit geschwarzten Gesichtern sehen. Ist nicht in Gahannam ein Aufenthalt fur die Hochmutigen"},
  {"surah_number": 39, "verse_number": 61, "content": "Und Allah wird diejenigen retten, die (ihn) furchteten, und ihnen Erfolg (verleihen) Weder wird sie das Ubel beruhren, noch werden sie trauern"},
  {"surah_number": 39, "verse_number": 62, "content": "Allah ist der Schopfer aller Dinge, und Er ist der Erhalter aller Dinge"},
  {"surah_number": 39, "verse_number": 63, "content": "Sein sind die Schlussel der Himmel und der Erde; und jene, die nicht an die Zeichen Allah s glauben, sind die Verlierenden"},
  {"surah_number": 39, "verse_number": 64, "content": "Sprich: \"Verlangt ihr von mir etwa, daß ich (etwas) anderes als Allah anbeten, ihr Toren"},
  {"surah_number": 39, "verse_number": 65, "content": "(Dies,) wo es dir doch offenbart worden ist, wie schon denen vor dir: \"Wenn du (Allah) Nebengotter zur Seite stellst, so wird sich dein Werk sicher als eitel erweisen, und du wirst gewiß unter den Verlierenden sein"},
  {"surah_number": 39, "verse_number": 66, "content": "Nein, diene denn Allah und sei einer der Dankbaren"},
  {
    "surah_number": 39,
    "verse_number": 67,
    "content": "Und sie haben Allah nicht richtig nach Seinem Wert eingeschatzt. Und am Tage der Auferstehung wird die ganze Erde in Seinem Griff sein, und die Himmel werden in Seiner Rechten zusammengerollt sein. Preis (sei) Ihm! Hoch Erhaben ist Er uber das, was sie anbeten"
  },
  {
    "surah_number": 39,
    "verse_number": 68,
    "content": "Und in den Sur wird gestoßen, und alle, die in den Himmeln sind, und alle, die auf Erden sind, werden tot niedersturzen; mit Ausnahme derjenigen, die Allah (ausnehmen) will. Dann wird wiederum in den Sur gestoßen, und siehe, da stehen sie auf und schauen zu"
  },
  {
    "surah_number": 39,
    "verse_number": 69,
    "content": "Und das (Versammlungs-) Gelande wird erstrahlen im Lichte seines Herrn, und das Buch wird vorgelegt, und die Propheten und die Zeugen werden herbeigebracht; und es wird zwischen ihnen in Gerechtigkeit gerichtet werden, und sie sollen kein Unrecht erleiden"
  },
  {"surah_number": 39, "verse_number": 70, "content": "Und jedem von ihnen wird voll vergolten werden, was er getan hat; und Er weiß am besten, was sie tun"},
  {
    "surah_number": 39,
    "verse_number": 71,
    "content":
        "Und die Unglaubigen werden in Scharen zu Gahannam gefuhrt werden, bis daß, wenn sie sie erreichen, sich ihre Pforten offnen und ihre Wachter zu ihnen sprechen: \"Sind nicht Gesandte aus eurer Mitte zu euch gekommen, um euch die Verse eures Herrn zu verlesen und euch vor dem Eintreffen dieses euren Tages zu warnen?\" Sie werden sagen: \"Ja!\" Doch das Strafurteil ist in Gerechtigkeit gegen die Unglaubigen fallig geworden"
  },
  {"surah_number": 39, "verse_number": 72, "content": "Es wird gesprochen werden: \"Geht denn ein durch die Pforten der Gahannam und bleibt darin auf ewig! Und ubel ist die Wohnstatt der Hochmutigen"},
  {
    "surah_number": 39,
    "verse_number": 73,
    "content": "Und jene, die ihren Herrn furchteten, werden in Scharen in das Paradies gefuhrt werden, bis daß, wenn sie es erreichen, seine Pforten sich Tffnen und seine Wachter zu ihnen sprechen: \"Friede sei auf euch! Seid glucklich und geht dort ein und weilt auf ewig darin"
  },
  {
    "surah_number": 39,
    "verse_number": 74,
    "content": "Sie werden sagen: \"Alles Lob gebuhrt Allah, Der Seine Verheißung an uns erfullt hat und uns die (Paradies-) Landschaft zum Erbe gegeben hat, so daß wir im Paradies wohnen konnen, wo immer es uns gefallt.\" Wie schon ist also der Lohn derer, die (dafur) vorgesorgt haben"
  },
  {
    "surah_number": 39,
    "verse_number": 75,
    "content": "Und du wirst die Engel auf allen Seiten den Thron umgeben sehen, wo sie das Lob ihres Herrn preisen. Und es wird zwischen ihnen in Gerechtigkeit entschieden werden. Und es wird gesprochen werden: \"Alles Lob gebuhrt Allah, dem Herrn der Welten"
  },
  {"surah_number": 40, "verse_number": 1, "content": "Ha Mim"},
  {"surah_number": 40, "verse_number": 2, "content": "Die Offenbarung des Buches stammt von Allah, dem Erhabenen, dem Allwissenden"},
  {"surah_number": 40, "verse_number": 3, "content": "dem die Sunde Vergebenden und die Reue Abnehmenden, dem Strengen in der Bestrafung, dem Besitzer der Gnadenfulle. Es ist kein Gott außer Ihm. Zu Ihm ist die Heimkehr"},
  {"surah_number": 40, "verse_number": 4, "content": "Niemand streitet uber die Zeichen Allahs, außer denen, die unglaubig sind. Laß dich darum von ihrem Hin- und Herziehen im Lande nicht tauschen"},
  {
    "surah_number": 40,
    "verse_number": 5,
    "content": "Vor ihnen leugneten das Volk Noahs und die Verbundeten nach ihnen. Und jedes Volk hat sich vorgenommen, seinen Gesandten zu ergreifen, und sie stritten mit Falschheit, um die Wahrheit damit zu widerlegen. Dann erfaßte Ich sie, und wie war Meine Strafe"
  },
  {"surah_number": 40, "verse_number": 6, "content": "Und somit wurde das Wort deines Herrn gegen die Unglaubigen fallig, daß sie die Bewohner des Feuers seien"},
  {
    "surah_number": 40,
    "verse_number": 7,
    "content":
        "Die, die den Thron tragen, und die, die ihn umringen, preisen das Lob ihres Herrn und glauben an Ihn und erbitten Vergebung fur jene, die glaubig sind: \"Unser Herr, Du umfaßt alle Dinge mit Barmherzigkeit und Wissen. Vergib darum denen, die bereuen und Deinem Weg folgen; und bewahre sie vor der Strafe der Gahim"
  },
  {"surah_number": 40, "verse_number": 8, "content": "Unser Herr, und laß sie eintreten in das Paradies von Eden, das Du ihnen verheißen hast, wie auch jenen ihrer Vater und ihrer Frauen und ihrer Kinder, die rechtschaffen sind. Gewiß, Du bist der Erhabene, der Allweise"},
  {"surah_number": 40, "verse_number": 9, "content": "Und bewahre sie vor dem Ubel; denn wen Du an jenem Tage vor Ubel bewahrst ihm hast Du wahrlich Barmherzigkeit erwiesen. Und das ist der gewaltige Gewinn"},
  {"surah_number": 40, "verse_number": 10, "content": "Wahrlich, den Unglaubigen wird zugerufen: \"Allahs Widerwille (gegen euch) ist großer als euer eigener Widerwille gegen euch selbst, als ihr zum Glauben aufgerufen wurdet und im Unglauben verharrtet"},
  {"surah_number": 40, "verse_number": 11, "content": "Sie werden sagen: \"Unser Herr Du hast uns zweimal sterben lassen und uns zweimal lebendig gemacht, und wir bekennen unsere Sunden. Ist da nun ein Weg, um zu entkommen"},
  {"surah_number": 40, "verse_number": 12, "content": "Dies ist so, weil ihr unglaubig bliebt, als Allah allein angerufen wurde; doch als Ihm Gotter zur Seite gesetzt wurden, da glaubtet ihr. Die Entscheidung liegt allein bei Allah, dem Hohen, dem Großen"},
  {"surah_number": 40, "verse_number": 13, "content": "Er ist es, Der euch Seine Zeichen zeigt und euch die Mittel zum Unterhalt vom Himmel hinabsendet; doch keiner außer dem laßt sich ermahnen, der sich bekehrt"},
  {"surah_number": 40, "verse_number": 14, "content": "Ruft denn Allah in lauterem Gehorsam Ihm gegenuber an, und sollte es auch den Unglaubigen zuwider sein"},
  {"surah_number": 40, "verse_number": 15, "content": "(Er ist) der Erhabene uber alle Rangstufen, der Herr des Thrones! Nach Seinem Geheiß sendet Er das offenbarte Wort demjenigen Seiner Diener, dem Er will, auf daß er vor dem Tag der Begegnung warne"},
  {"surah_number": 40, "verse_number": 16, "content": "dem Tage, an dem sie ohne Versteck zu sehen sind und nichts von ihnen vor Allah verborgen bleibt. Wessen ist das Konigreich an diesem Tage? (Es ist) Allahs, des Einzigen, des Allbezwingenden"},
  {"surah_number": 40, "verse_number": 17, "content": "Heute wird jedem vergolten werden, was er verdient hat. Heute gibt es keine Ungerechtigkeit! Wahrlich, Allah ist schnell im Abrechnen"},
  {"surah_number": 40, "verse_number": 18, "content": "Und warne sie vor dem immer naher kommenden Tag, da die Herzen voll innerer Trauer zur Kehle emporsteigen werden. Die Ungerechten werden weder einen vertrauten Freund noch einen Fursprecher haben, auf den gehort werden konnte"},
  {"surah_number": 40, "verse_number": 19, "content": "Er kennt die Verraterei der Blicke und alles, was die Herzen verbergen"},
  {"surah_number": 40, "verse_number": 20, "content": "Und Allah richtet in Gerechtigkeit; die aber, die sie an Seiner Statt anrufen, konnen nicht richten. Wahrlich, Allah ist der Allhorende, der Allsehende"},
  {
    "surah_number": 40,
    "verse_number": 21,
    "content":
        "Sind sie denn nicht im Lande umhergezogen, so daß sie schauen konnten, wie das Ende derer war, die vor ihnen lebten? Diese waren viel machtiger als sie und hinterließen mehr Spuren im Land. Und doch erfaßte sie Allah um ihrer Sunden willen, und sie hatten keinen, der sie vor Allah hatte schutzen konnen"
  },
  {"surah_number": 40, "verse_number": 22, "content": "Das geschah, weil ihre Gesandten zu ihnen mit deutlichen Zeichen kamen, sie aber nicht glaubten; darum erfaßte sie Allah. Wahrlich, Er ist Stark und streng im Strafen"},
  {"surah_number": 40, "verse_number": 23, "content": "Und wahrlich, Wir entsandten Moses mit Unseren Zeichen und mit einer klaren Machtbefugnis"},
  {"surah_number": 40, "verse_number": 24, "content": "zu Pharao und Haman und Korah; jedoch sie sagten: \"(Er ist nichts anderes als) ein Zauberer, ein Betruger"},
  {"surah_number": 40, "verse_number": 25, "content": "Und als er zu ihnen mit der Wahrheit von Uns kam, da sagten sie: \"Totet die Sohne derer, die mit ihm glauben, und laßet ihre Frauen am Leben.\" Doch der Anschlag der Unglaubigen ist vollig verfehlt"},
  {"surah_number": 40, "verse_number": 26, "content": "Und Pharao sagte: \"Laßet mich, ich will Moses toten; und laßet ihn seinen Herrn anrufen. Ich furchte, er konnte sonst euren Glauben andern oder Unheil im Land stiften"},
  {"surah_number": 40, "verse_number": 27, "content": "Und Moses sagte: \"lch nehme meine Zuflucht zu meinem Herrn und eurem Herrn vor jedem Uberheblichen, der nicht an den Tag der Abrechnung glaubt"},
  {
    "surah_number": 40,
    "verse_number": 28,
    "content":
        "Und es sagte ein glaubiger Mann von den Leuten Pharaos, der seinen Glauben geheimhielt: \"Wollt ihr einen Mann toten, weil er sagt: \"Mein Herr ist Allah\", obwohl er mit klaren Beweisen von eurem Herrn zu euch gekommen ist? Wenn er ein Lugner ist, so lastet seine Luge auf ihm; ist er aber wahrhaftig, dann wird euch ein Teil von dem treffen, was er euch androht. Wahrlich, Allah weist nicht dem den Weg, der maßlos (und) ein Lugner ist"
  },
  {
    "surah_number": 40,
    "verse_number": 29,
    "content":
        "O meine Leute, euch gehort heute die Herrschaft; denn ihr habt die Oberhand im Land. Wer aber wird uns vor der Strafe Allahs schutzen, wenn sie uber uns kommt?\" Pharao sagte: \"lch weise euch nur auf das hin, was ich selbst sehe, und ich leite euch nur auf den Weg der Rechtschaffenheit"
  },
  {"surah_number": 40, "verse_number": 30, "content": "Da sagte jener, der glaubig war: \"O mein Volk, ich furchte fur euch das gleiche, was den Verbundeten (widerfuhr)"},
  {"surah_number": 40, "verse_number": 31, "content": "das gleiche, was dem Volke Noahs und den `Ad und den Tamud und denen nach ihnen widerfuhr. Und Allah will keine Ungerechtigkeit gegen die Menschen"},
  {"surah_number": 40, "verse_number": 32, "content": "O mein Volk, ich furchte fur euch den Tag der gegenseitigen Hilferufe"},
  {"surah_number": 40, "verse_number": 33, "content": "den Tag, an dem ihr den Rucken zur Flucht wenden werdet. Keinen Beschirmer werdet ihr wider Allah haben. Und der, den Allah zum Irrenden erklart, wird keinen Fuhrer finden"},
  {
    "surah_number": 40,
    "verse_number": 34,
    "content":
        "Und Yusuf kam ja vordem mit deutlichen Beweisen zu euch, jedoch ihr hortet nicht auf, das zu bezweifeln, womit er zu euch kam, bis ihr dann, als er starb, sagtet: Allah wird nimmermehr einen Gesandten nach ihm erstehen lassen.\" Also erklart Allah jene zu Irrenden, die maßlos (und) Zweifler sind"
  },
  {
    "surah_number": 40,
    "verse_number": 35,
    "content": "solche, die uber die Zeichen Allahs streiten, ohne daß irgendeine Ermachtigung (dazu) zu ihnen gekommen ware. Außerst hassenswert ist das fur Allah und fur jene, die glaubig sind. Also versiegelt Allah das Herz eines jeden Uberheblichen, Gewalttatigen"
  },
  {"surah_number": 40, "verse_number": 36, "content": "Und Pharao sagte: \"O Haman, baue mir einen Turm, so daß ich die Zugange erreiche"},
  {
    "surah_number": 40,
    "verse_number": 37,
    "content": "die Zugange zu den Himmeln, damit ich ihn sehen kann, den Gott Moses', und ich halte ihn wahrlich fur einen Lugner.\" Also erschien Pharao das Bose seines Tuns im schonsten Licht, und er wurde von dem Weg abgewendet; und der Plan Pharaos schlug fehl"
  },
  {"surah_number": 40, "verse_number": 38, "content": "Und jener, der glaubig war, sagte: \"O mein Volk, folgt mir. Ich will euch zu dem Weg der Rechtschaffenheit leiten"},
  {"surah_number": 40, "verse_number": 39, "content": "O mein Volk, das Leben auf dieser Erde ist wahrlich nur ein verganglicher Genuß; und das Jenseits allein ist wahrlich die dauernde Heimstatt"},
  {
    "surah_number": 40,
    "verse_number": 40,
    "content": "Wer Boses tut, dem soll nur mit Gleichem vergolten werden; wer aber Gutes tut - sei es Mann oder Frau und dabei glaubig ist -, diese werden ins Paradies eintreten; darin werden sie mit Unterhalt versorgt werden, ohne daß daruber Rechnung gefuhrt wird"
  },
  {"surah_number": 40, "verse_number": 41, "content": "O mein Volk, wie kommt es mir zu, daß ich euch zum Heil aufrufe, wahrend ihr mich zum Feuer ruft"},
  {"surah_number": 40, "verse_number": 42, "content": "Ihr ruft mich auf, Allah zu verleugnen und Ihm Gotter zur Seite zu stellen, von denen ich keine Kenntnis habe. Ich aber rufe euch zu dem Erhabenen, dem Vergebungsreichen"},
  {"surah_number": 40, "verse_number": 43, "content": "(Es besteht) kein Zweifel, daß das, wozu ihr mich ruft, keinen Anspruch in dieser Welt oder im Jenseits besitzt; und wir werden zu Allah heimkehren, und die Ubertreter werden Bewohner des Feuers sein"},
  {"surah_number": 40, "verse_number": 44, "content": "Bald werdet ihr an das denken, was ich zu euch sage Und ich lege meine Sache Allah zur Entscheidung vor; denn Allah durchschaut die Menschen"},
  {"surah_number": 40, "verse_number": 45, "content": "So schutzte ihn Allah vor den Ubeln dessen, was sie planten; und eine schlimme Strafe ereilte die Leute Pharaos"},
  {"surah_number": 40, "verse_number": 46, "content": "(das) Feuer - sie sind ihm morgens und abends ausgesetzt. Und am Tage, wenn die Stunde kommen wird(, da wird gesprochen werden): \"Laßt Pharaos Leute die strengste Strafe antreten"},
  {"surah_number": 40, "verse_number": 47, "content": "Wenn sie miteinander im Feuer streiten, werden die Schwachen zu den Hochmutigen sagen: \"Wir waren ja eure Anhanger; werdet ihr uns deshalb nicht einen Teil des Feuers abnehmen"},
  {"surah_number": 40, "verse_number": 48, "content": "Jene, die hochmutig waren, werden sagen: \"Wir sind alle darin. Allah hat nun zwischen den Menschen gerichtet"},
  {"surah_number": 40, "verse_number": 49, "content": "Und die im Feuer werden zu den Wachtern der Gahannam sagen: \"Bittet euren Herrn, daß Er uns einen Tag von der Strafe erlasse"},
  {"surah_number": 40, "verse_number": 50, "content": "Sie werden sprechen: \"Kamen nicht eure Gesandten mit klaren Beweisen zu euch?\" Jene werden sagen: \"Doch.\" (Die Wachter) werden sprechen: \"So fahret fort zu bitten.\" Doch die Bitte der Unglaubigen ist vollig verfehlt"},
  {"surah_number": 40, "verse_number": 51, "content": "Wahrlich, helfen werden Wir Unseren Gesandten und denen, die glaubig sind, im diesseitigen Leben und an dem Tage, wo die Zeugen vortreten werden"},
  {"surah_number": 40, "verse_number": 52, "content": "am Tage, an dem den Frevlern ihre Ausrede nichts nutzen wird; und sie werden zu Fluch und schlimmem Aufenthalt (verdammt) sein"},
  {"surah_number": 40, "verse_number": 53, "content": "Und Wir gaben wahrlich Moses die Fuhrung und machten die Kinder Israels zu Erben der Schrift"},
  {"surah_number": 40, "verse_number": 54, "content": "einer Fuhrung und Ermahnung fur die Verstandigen"},
  {"surah_number": 40, "verse_number": 55, "content": "So sei denn geduldig. Wahrlich, die Verheißung Allahs ist wahr. Und suche Vergebung fur deine Missetaten und lobpreise deinen Herrn am Abend und am Morgen"},
  {
    "surah_number": 40,
    "verse_number": 56,
    "content": "Wahrlich, diejenigen, die uber die Zeichen Allahs streiten, ohne daß irgendeine Ermachtigung (dazu) zu ihnen (gekommen ware) nichts ist in ihren Herzen als Großmannssucht -, sie werden es nicht erreichen. So nimm Zuflucht bei Allah. Wahrlich, Er ist der Allhorende, der Allsehende"
  },
  {"surah_number": 40, "verse_number": 57, "content": "Wahrlich, die Schopfung der Himmel und der Erde ist großer als die Schopfung der Menschen; allein die meisten Menschen wissen es nicht"},
  {"surah_number": 40, "verse_number": 58, "content": "Doch der Blinde und der Sehende sind nicht gleich; noch sind jene, die glauben und gute Werke tun, denen (gleich), die Boses tun. Wenig ist es, was ihr zu bedenken pflegt"},
  {"surah_number": 40, "verse_number": 59, "content": "Die Stunde kommt gewiß, daruber herrscht kein Zweifel; doch die meisten Menschen glauben es nicht"},
  {"surah_number": 40, "verse_number": 60, "content": "Und euer Herr sprach: \"Bittet Mich; Ich will eure Bitte erhoren. Die aber, die zu Aberheblich sind, um Mir zu dienen, werden unterwurfig in Gahannam eintreten"},
  {"surah_number": 40, "verse_number": 61, "content": "Allah ist es, Der fur euch die Nacht gemacht hat, auf daß ihr darin ruht, und den hellichten Tag. Wahrlich, Allah ist der Herr der Gnadenfulle gegenuber der Menschheit, jedoch die meisten Menschen danken (ihm dafur) nicht"},
  {"surah_number": 40, "verse_number": 62, "content": "Das ist Allah, euer Herr, der Schopfer aller Dinge. Es ist kein Gott außer Ihm. Wie lasset ihr euch (von Ihm) abwenden"},
  {"surah_number": 40, "verse_number": 63, "content": "So lassen sich nur die abwenden, die Allahs Zeichen leugnen"},
  {
    "surah_number": 40,
    "verse_number": 64,
    "content": "Allah ist es, Der die Erde fur euch als festen Grund und den Himmel als Bau geschaffen hat und Der euch Gestalt gegeben und eure Gestalten schon gemacht hat und euch mit guten Dingen versorgt hat. Das ist Allah, euer Herr. Segensreich ist darum Allah, der Herr der Welten"
  },
  {"surah_number": 40, "verse_number": 65, "content": "Er ist der Ewiglebendige. Es ist kein Gott außer Ihm. So ruft Ihn an, in lauterem Gehorsam Ihm gegenuber. Alles Lob gebuhrt Allah, dem Herrn der Welten"},
  {"surah_number": 40, "verse_number": 66, "content": "Sprich: \"Mir wurde verboten, denen zu dienen, die ihr statt Allah anruft, nachdem zu mir deutliche Beweise von meinem Herrn gekommen sind; und mir wurde befohlen, mich dem Herrn der Welten zu ergeben"},
  {
    "surah_number": 40,
    "verse_number": 67,
    "content":
        "Er ist es, Der euch aus Erde erschuf, dann aus einem Samentropfen, dann aus einem Blutklumpen, dann laßt Er euch als Kind (daraus) hervorgehen, dann (laßt Er euch wachsen,) auf daß ihr eure Vollkraft erreichen moget, dann (laßt Er) euch alt werden wenngleich einige von euch vorher zum Sterben abberufen werden und (Er laßt euch leben,) damit ihr eine bestimmte Frist erreichet und damit ihr begreifen lernet"
  },
  {"surah_number": 40, "verse_number": 68, "content": "Er ist es, Der ins Leben ruft und sterben laßt. Und wenn Er etwas bestimmt hat, so spricht Er zu ihm nur: \"Sei!\" und es ist"},
  {"surah_number": 40, "verse_number": 69, "content": "Hast du nicht die gesehen, die uber Allahs Zeichen streiten? Wie lassen sie sich abwenden"},
  {"surah_number": 40, "verse_number": 70, "content": "(Es sind) jene, die weder an das Buch noch an das glauben, womit Wir Unsere Gesandten geschickt haben. Bald aber werden sie es wissen"},
  {"surah_number": 40, "verse_number": 71, "content": "wenn die Eisenfesseln und Ketten um ihren Nacken (gelegt) sein werden. Sie werden gezerrt werden"},
  {"surah_number": 40, "verse_number": 72, "content": "in siedendes Wasser; dann werden sie ins Feuer gezogen werden"},
  {"surah_number": 40, "verse_number": 73, "content": "Dann wird zu ihnen gesprochen: \"Wo sind nun jene, die ihr anzubeten pflegtet"},
  {"surah_number": 40, "verse_number": 74, "content": "statt Allah?\" Sie werden sagen: \"Sie sind von uns gewichen. Nein, wir riefen zuvor nichts an.\" So fuhrt Allah die Unglaubigen irre"},
  {"surah_number": 40, "verse_number": 75, "content": "Dies (geschieht), weil ihr auf Erden ohne Recht frohlocktet, und weil ihr ausgelassen waret"},
  {"surah_number": 40, "verse_number": 76, "content": "Geht nun ein durch die Tore der Gahannam, um auf ewig darin zu bleiben. Ubel ist nun die Wohnstatt der Uberheblichen"},
  {
    "surah_number": 40,
    "verse_number": 77,
    "content": "So sei denn standhaft. Wahrlich, die Verheißung Allahs ist wahr. Und ob Wir dir (die Erfullung) von einigen der Dinge zeigen, die Wir ihnen angedroht haben, oder (ob) Wir dich (vorher) sterben lassen; zu Uns werden sie (jedenfalls) zuruckgebracht"
  },
  {
    "surah_number": 40,
    "verse_number": 78,
    "content":
        "Und sicher entsandten Wir schon Gesandte vor dir; darunter sind manche, von denen Wir dir bereits berichtet haben, und es sind darunter manche, von denen Wir dir nicht berichtet haben; und kein Gesandter hatte ohne Allahs Erlaubnis ein Zeichen bringen konnen. Doch wenn Allahs Befehl ergeht, dann wird die Sache zu Recht entschieden, und dann fallen diejenigen dem Verlust anheim, die versucht haben, (Allahs Befehl) außer Kraft zu setzen"
  },
  {"surah_number": 40, "verse_number": 79, "content": "Allah ist es, Der fur euch die Tiere gemacht hat, damit ihr auf den einen reiten und von den anderen essen konnt"},
  {"surah_number": 40, "verse_number": 80, "content": "Und ihr habt noch (anderen) Nutzen an ihnen - damit ihr durch sie jegliches Bedurfnis befriedigen konnt, das in euren Herzen sein mag. Und auf ihnen und auf Schiffen werdet ihr getragen"},
  {"surah_number": 40, "verse_number": 81, "content": "Und Er zeigt euch Seine Zeichen; welches der Zeichen Allahs wollt ihr denn verleugnen"},
  {
    "surah_number": 40,
    "verse_number": 82,
    "content": "Sind sie denn nicht im Lande umhergezogen, so daß sie schauen konnten, wie das Ende derer war, die vor ihnen lebten? Sie waren zahlreicher als diese und viel machtiger und hinterließen mehr Spuren auf Erden. Doch alles, was sie erwarben, nutzte ihnen nichts"
  },
  {"surah_number": 40, "verse_number": 83, "content": "Und als ihre Gesandten mit deutlichen Beweisen zu ihnen kamen, da frohlockten sie uber das Wissen, das sie (selbst) besaßen. Und das, woruber sie zu spotten pflegten, umfing sie"},
  {"surah_number": 40, "verse_number": 84, "content": "Und als sie Unsere Strafe sahen, sagten sie: \"Wir glauben an Allah als den Einigen, und wir verwerfen all das, was wir Ihm zur Seite zu stellen pflegten"},
  {"surah_number": 40, "verse_number": 85, "content": "Aber ihr Glaube - als sie Unsere Strafe sahen - konnte ihnen nichts mehr nutzen. Dies ist Allahs Gebot, das stets gegenuber Seinen Dienern befolgt worden ist. Und so gingen die Unglaubigen zugrunde"},
  {"surah_number": 41, "verse_number": 1, "content": "Ha Mim"},
  {"surah_number": 41, "verse_number": 2, "content": "(Dies ist) eine Offenbarung von dem Allerbarmer, dem Barmherzigen"},
  {"surah_number": 41, "verse_number": 3, "content": "ein Buch, dessen Verse als Quran in arabischer Sprache klar gemacht worden sind fur Leute, die Wissen besitzen"},
  {"surah_number": 41, "verse_number": 4, "content": "als Bringer froher Botschaft und Warner. Doch die meisten von ihnen kehren sich (von ihm) ab, so daß sie (ihn) nicht horen"},
  {"surah_number": 41, "verse_number": 5, "content": "Und sie sagen: \"Unsere Herzen sind vor dem verhullt, wozu du uns berufst, und unsere Ohren sind taub, und zwischen uns und dir ist eine Scheidewand. So handle, auch wir handeln"},
  {"surah_number": 41, "verse_number": 6, "content": "Sprich: \"lch bin nur ein Mensch wie ihr. Mir wird offenbart, daß euer Gott ein Einziger Gott ist; so seid aufrichtig gegen Ihn und bittet Ihn um Vergebung.\" Und wehe den Gotzendienern"},
  {"surah_number": 41, "verse_number": 7, "content": "die nicht die Zakah entrichten und die das Jenseits leugnen"},
  {"surah_number": 41, "verse_number": 8, "content": "Diejenigen, die aber glauben und gute Werke tun, werden einen nimmer endenden Lohn erhalten"},
  {"surah_number": 41, "verse_number": 9, "content": "Sprich: \"Leugnet ihr Den wirklich, Der die Erde in zwei Tagen schuf? Und dichtet ihr Ihm Nebenbuhler an?\" Er nur ist der Herr der Welten"},
  {"surah_number": 41, "verse_number": 10, "content": "Und Er grundete in ihr feste Berge, die sie uberragen, und segnete sie und ordnete auf ihr ihre Nahrung in richtigem Verhaltnis in vier Tagen - gleichmaßig fur die Suchenden"},
  {"surah_number": 41, "verse_number": 11, "content": "Dann wandte Er Sich zum Himmel, welcher noch Nebel war, und sprach zu ihm und zu der Erde: \"Kommt ihr beide, willig oder widerwillig.\" Sie sprachen: \"Wir kommen willig"},
  {"surah_number": 41, "verse_number": 12, "content": "So vollendete Er sie als sieben Himmel in zwei Tagen, und jedem Himmel wies Er seine Aufgabe zu. Und Wir schmuckten den untersten Himmel mit Leuchten (, welche auch) zum Schutz (dienen) Das ist die Schopfung des Erhabenen, des Allwissenden"},
  {"surah_number": 41, "verse_number": 13, "content": "Doch wenn sie sich abkehren, so sprich: \"lch habe euch vor einem Blitzschlag wie dem Blitzschlag von `Ad und Tamud gewarnt"},
  {"surah_number": 41, "verse_number": 14, "content": "Da kamen ihre Gesandten zu ihnen von vorn und hinten (und sagten): \"Dient keinem außer Allah.\" Da sagten Sie: \"Hatte unser Herr es gewollt, hatte Er zweifellos Engel herabgesandt. So lehnen wir das ab, womit ihr gesandt worden seid"},
  {
    "surah_number": 41,
    "verse_number": 15,
    "content": "Was nun die `Ad anbelangt, so betrugen sie sich ohne Recht hochmutig auf Erden und sagten: \"Wer hat mehr Macht als wir?\" Konnten sie denn nicht sehen, daß Allah, Der sie erschuf, machtiger ist als sie? Jedoch sie fuhren fort, Unsere Zeichen zu leugnen"
  },
  {
    "surah_number": 41,
    "verse_number": 16,
    "content": "Darum sandten Wir gegen sie einen eiskalten Wind mehrere unheilvolle Tage hindurch, auf daß Wir sie die Strafe der Schmach in diesem Leben kosten ließen. Und die Strafe des Jenseits wird gewiß noch schmahlicher sein, und es wird ihnen nicht geholfen werden"
  },
  {"surah_number": 41, "verse_number": 17, "content": "Und was die Tamud anbelangt, so wiesen Wir ihnen den Weg, sie aber zogen die Blindheit dem rechten Weg vor; darum erfaßte sie die blitzschlagartige Strafe der Erniedrigung um dessentwillen, was sie begangen hatten"},
  {"surah_number": 41, "verse_number": 18, "content": "Und Wir erretteten jene, die glaubten und gottesfurchtig waren"},
  {"surah_number": 41, "verse_number": 19, "content": "Und an dem Tage, wo die Feinde Allahs allesamt vor dem Feuer versammelt werden, da werden sie in Gruppen geteilt"},
  {"surah_number": 41, "verse_number": 20, "content": "bis daß, wenn sie es erreichen, ihre Ohren und ihre Augen und ihre Haut Zeugnis gegen sie von dem ablegen, was sie zu tun pflegten"},
  {
    "surah_number": 41,
    "verse_number": 21,
    "content": "Und sie werden zu ihren Hauten sagen: \" Warum zeugt ihr gegen uns?\" Sie werden sprechen: \"Allah hat uns das Reden verliehen - Er, Der einem jeden Ding das Reden verliehen hat. Und Er ist es, Der euch erstmals erschuf, und zu Ihm seid ihr zuruckgebracht worden"
  },
  {"surah_number": 41, "verse_number": 22, "content": "Und ihr pflegtet (eure Sunden) nicht so zu verbergen, daß eure Ohren und eure Augen und eure Haut nicht Zeugnis gegen euch ablegen konnten; vielmehr meintet ihr, Allah wußte nicht viel von dem, was ihr zu tun pflegtet"},
  {"surah_number": 41, "verse_number": 23, "content": "Und das, was ihr von eurem Herrn meintet, hat euch ins Verderben gefuhrt; so wurdet ihr die Verlierenden"},
  {"surah_number": 41, "verse_number": 24, "content": "Selbst wenn sie es nun aushalten, so ist doch das Feuer ihre Wohnstatt; und wenn sie um Gnade bitten, so wird ihnen keine Gnade erwiesen"},
  {
    "surah_number": 41,
    "verse_number": 25,
    "content":
        "Und Wir hatten Gefahrten fur sie bestimmt, die ihnen das als wohlgefallig erscheinen ließen, was vor ihnen war und was hinter ihnen war; und (so) wurde der Spruch fallig gegen sie, zusammen mit den Scharen der Ginn und der Menschen, die vor ihnen hingegangen waren. Gewiß, sie waren die Verlierer"
  },
  {"surah_number": 41, "verse_number": 26, "content": "Und die Unglaubigen sagten: \"Hort nicht auf diesen Quran, sondern fugt von euren Worten (etwas hinzu), damit ihr die Oberhand behaltet"},
  {"surah_number": 41, "verse_number": 27, "content": "Aber gewiß werden Wir den Unglaubigen eine strenge Strafe zu kosten geben, und gewiß werden Wir ihnen ihre schlimmsten Taten vergelten"},
  {"surah_number": 41, "verse_number": 28, "content": "Das ist der Lohn der Feinde Allahs: das Feuer. Darin wird ihnen die Wohnstatt der Ewigkeit zuteil sein - als Vergeltung dafur, daß sie Unseren Zeichen gegenuber undankbar waren"},
  {"surah_number": 41, "verse_number": 29, "content": "Und die Unglaubigen werden sagen: \"Unser Herr, zeige uns jene der Ginn und der Menschen, die uns irrefuhrten, damit wir sie mit unseren Fußen treten, so daß sie zu den Niedrigsten gehoren"},
  {
    "surah_number": 41,
    "verse_number": 30,
    "content": "Wahrlich diejenigen, die sagen: \"Unser Herr ist Allah\", und die sich dann aufrichtig verhalten - zu ihnen steigen die Engel nieder (und sprechen): \"Furchtet euch nicht und seid nicht traurig, und erfreut euch des Paradieses, das euch verheißen wurde"
  },
  {"surah_number": 41, "verse_number": 31, "content": "Wir sind eure Beschutzer im irdischen Leben und im Jenseits. In ihm werdet ihr alles haben, was ihr euch wunscht, und in ihm werdet ihr alles haben, wonach ihr verlangt"},
  {"surah_number": 41, "verse_number": 32, "content": "(dies ist) eine Bewirtung von einem Vergebenden, Barmherzigen"},
  {"surah_number": 41, "verse_number": 33, "content": "Und wer ist besser in der Rede als einer, der zu Allah ruft und Gutes tut und sagt: \"lch bin einer der Gottergebenen"},
  {"surah_number": 41, "verse_number": 34, "content": "Und nimmer sind das Gute und das Bose gleich. Wehre (das Bose) in bester Art ab, und siehe da, der, zwischen dem und dir Feindschaft herrschte, wird wie ein treuer Freund sein"},
  {"surah_number": 41, "verse_number": 35, "content": "Aber dies wird nur denen gewahrt, die geduldig sind; und dies wird nur denen gewahrt, die ein großes Gluck haben"},
  {"surah_number": 41, "verse_number": 36, "content": "Und wenn du von Seiten des Satans zu einer Untat aufgestachelt wirst, dann nimm deine Zuflucht bei Allah. Wahrlich, Er ist der Allhorende, der Allwissende"},
  {
    "surah_number": 41,
    "verse_number": 37,
    "content": "Und zu Seinen Zeichen gehoren die Nacht und der Tag und die Sonne und der Mond. Werft euch nicht vor der Sonne anbetend nieder, und auch nicht vor dem Mond, sondern werft euch anbetend vor Allah nieder, Der sie erschuf, wenn Er es ist, Den ihr verehrt"
  },
  {"surah_number": 41, "verse_number": 38, "content": "Wenn sie sich aber in Hochmut (von Ihm) abwenden, so preisen Ihn bei Nacht und Tag diejenigen, die bei deinem Herrn sind, und sie sind darin unermudlich"},
  {"surah_number": 41, "verse_number": 39, "content": "Und unter Seinen Zeichen ist, daß du die Erde leblos siehst, doch wenn Wir Wasser auf sie niedersenden, dann regt sie sich und schwillt. Er, Der sie belebte, wird sicher auch die Toten lebendig machen; denn Er hat Macht uber alle Dinge"},
  {"surah_number": 41, "verse_number": 40, "content": "Wahrlich, diejenigen, die Unsere Zeichen entstellen, sind Uns nicht verborgen. Ist etwa der, der ins Feuer geworfen wird, besser als jener, der am Tage der Auferstehung sicher hervorgeht? Tut, was ihr wollt; denn Er sieht alles, was ihr tut"},
  {"surah_number": 41, "verse_number": 41, "content": "Wahrlich, diejenigen, die nicht an die Ermahnung glaubten, als sie zu ihnen kam (, werden bestraft) Und wahrlich, es ist ein ehrwurdiges Buch"},
  {"surah_number": 41, "verse_number": 42, "content": "Falschheit kann nicht an es herankommen, weder von vorn noch von hinten. Es ist eine Offenbarung von einem Allweisen, des Lobes Wurdigen"},
  {"surah_number": 41, "verse_number": 43, "content": "Nichts anderes wird dir gesagt, als was schon den Gesandten vor dir gesagt wurde. Deinem Herrn eignet wahrlich die Vergebung, (ihm eignet) aber auch die schmerzliche Bestrafung"},
  {
    "surah_number": 41,
    "verse_number": 44,
    "content":
        "Hatten Wir ihn als einen Quran in einer fremden Sprache abgefaßt, hatten sie gesagt: \"Warum sind seine Verse nicht in einer fremden und in einer arabischen (Sprache) klar gemacht worden?\" Sprich: \"Er ist eine Fuhrung und eine Heilung fur die Glaubigen.\" Doch diejenigen, die nicht glauben - ihre Ohren sind taub, und er bleibt ihrem Blick entzogen; diese werden von einem weit entfernten Ort angerufen"
  },
  {
    "surah_number": 41,
    "verse_number": 45,
    "content": "Und wahrlich, Wir gaben Moses die Schrift, doch dann entstand Uneinigkeit uber sie. Ware nicht zuvor ein Wort von deinem Herrn ergangen, ware gewiß zwischen ihnen entschieden worden; aber wahrhaftig, sie befinden sich in beunruhigendem Zweifel uber ihn (den Quran)"
  },
  {"surah_number": 41, "verse_number": 46, "content": "Wenn jemand das Rechte tut, so tut er es fur sich selbst; und wenn jemand Boses tut, so handelt er gegen sich selbst. Und dein Herr ist niemals ungerecht gegen die Menschen"},
  {
    "surah_number": 41,
    "verse_number": 47,
    "content":
        "Auf Ihn allein ist das Wissen um die Stunde zuruckzufuhren. Keine Fruchte kommen aus ihren Hullen hervor, und kein weibliches Wesen empfangt oder gebart, es sei denn mit Seinem Wissen. Und an dem Tage, wenn Er ihnen zurufen wird: \"Wo sind Meine Partner?\" da werden sie sagen: \"Wir gestehen Dir, keiner von uns ist (ihr) Zeuge (gewesen)"
  },
  {"surah_number": 41, "verse_number": 48, "content": "Und alles, was sie zuvor anzurufen pflegten, wird sie im Stich lassen, und sie werden einsehen, daß sie keine Zuflucht haben"},
  {"surah_number": 41, "verse_number": 49, "content": "Keine Mudigkeit trifft den Menschen, wenn er (etwas) Gutes erbitten will; doch wenn ihn ein Ubel beruhrt, dann verzweifelt er (und) gibt alle Hoffnungen auf"},
  {
    "surah_number": 41,
    "verse_number": 50,
    "content":
        "Und wenn Wir ihn Unsere Barmherzigkeit kosten lassen, nachdem ihn ein Leid betroffen hat, so sagt er sicher: \"Das steht mir zu; und ich glaube nicht, daß die Stunde kommen wird. Doch wenn ich zu meinem Herrn zuruckgebracht werden sollte, dann wurde ich gewiß das Beste bei Ihm finden.\" Aber Wir werden den Unglaubigen wahrlich alles ankundigen, was sie getan haben, und Wir werden sie si cherlich von einer harten Strafe kosten lassen"
  },
  {"surah_number": 41, "verse_number": 51, "content": "Und wenn Wir dem Menschen Gnade erweisen, dann kehrt er sich (von Uns) ab und geht seitwarts; doch wenn ihn ein Ubel beruhrt, siehe, dann beginnt er, Bittgebete in großzugiger Weise zu sprechen"},
  {"surah_number": 41, "verse_number": 52, "content": "Sprich: \"Besinnt euch: Wenn es von Allah ist und ihr nicht daran glaubt - wer ist irrender als einer, der mit seiner Zwietracht weit gegangen ist"},
  {"surah_number": 41, "verse_number": 53, "content": "Wir werden sie Unsere Zeichen uberall auf Erden und an ihnen selbst sehen lassen, damit ihnen deutlich wird, daß es die Wahrheit ist. Genugt es denn nicht, daß dein Herr Zeuge aller Dinge ist"},
  {"surah_number": 41, "verse_number": 54, "content": "Doch sie hegen Zweifel an der Begegnung mit ihrem Herrn. Wahrlich, Er umfaßt alle Dinge"},
  {"surah_number": 42, "verse_number": 1, "content": "Ha Mim"},
  {"surah_number": 42, "verse_number": 2, "content": "Ain Sin Qaf"},
  {"surah_number": 42, "verse_number": 3, "content": "So offenbart Allah, der Erhabene, der Allweise, dir und denen, die vor dir waren"},
  {"surah_number": 42, "verse_number": 4, "content": "Sein ist, was in den Himmeln und was auf Erden ist, und Er ist der Hohe, der Große"},
  {"surah_number": 42, "verse_number": 5, "content": "Fast neigen sich die Himmel, um von oben her zu brechen, (so) auch (wenn) die Engel ihren Herrn mit Seiner Lobpreisung verherrlichen und Vergebung fur die auf Erden erflehen. Siehe, Allah ist wahrlich der Vergebende, der Barmherzige"},
  {"surah_number": 42, "verse_number": 6, "content": "Und jene, die sich Beschutzer außer Ihm nehmen - Allah gibt auf sie acht; und du bist nicht ihr Wachter"},
  {
    "surah_number": 42,
    "verse_number": 7,
    "content": "Und so haben Wir dir den Quran auf Arabisch offenbart, auf daß du die Mutter der Stadte warnest und alle rings um sie, und (auf daß) du (sie) vor dem Tag der Versammlung warnest, uber den kein Zweifel herrscht. Eine Gruppe wird im Paradies sein und eine Gruppe im flammenden Feuer"
  },
  {"surah_number": 42, "verse_number": 8, "content": "Hatte Allah gewollt, hatte Er sie zu einer einzigen Gemeinde gemacht; jedoch laßt Er in Seine Barmherzigkeit ein, wen Er will. Und die Ungerechten werden weder Beschutzer noch Helfer haben"},
  {"surah_number": 42, "verse_number": 9, "content": "Oder haben sie sich etwa Beschutzer außer Ihm genommen? Doch Allah allein ist der Beschutzer; und Er macht die Toten lebendig; und Er hat Macht uber alle Dinge"},
  {"surah_number": 42, "verse_number": 10, "content": "Und uber was immer ihr uneins seid; die Entscheidung daruber ruht bei Allah. Das ist Allah, mein Herr; auf Ihn vertraue ich, und zu Ihm wende ich mich"},
  {"surah_number": 42, "verse_number": 11, "content": "Der Schopfer der Himmel und der Erde - Er hat aus euch selbst Gattinnen fur euch gemacht und Paare aus den Tieren. Dadurch vermehrt Er euch. Es gibt nichts Seinesgleichen; und Er ist der Allhorende, der Allsehende"},
  {"surah_number": 42, "verse_number": 12, "content": "Sein sind die Schlussel der Himmel und der Erde. Er erweitert und beschrankt dem die Mittel zum Unterhalt, dem Er will. Wahrlich, Er ist der Allwissende aller Dinge"},
  {
    "surah_number": 42,
    "verse_number": 13,
    "content":
        "Er verordnete fur euch die Religion, die Er Noah anbefahl und die Wir dir offenbart haben und die Wir Abraham und Moses und Jesus anbefohlen haben. Namlich (die), in der Einhaltung der Religion treu zu bleiben und euch deswegen nicht zu spalten. Hart ist fur die Gotzendiener das, wozu du sie aufrufst. Allah erwahlt dazu, wen Er will, und leitet dazu den, der sich bekehrt"
  },
  {
    "surah_number": 42,
    "verse_number": 14,
    "content":
        "Und sie zerfielen erst dann in Spaltung, nachdem das Wissen zu ihnen gekommen war; denn unter ihnen (entstand) selbstsuchtiger Neid. Und ware nicht bereits ein Wort von deinem Herrn uber eine bestimmte Frist ergangen ware gewiß zwischen ihnen entschieden worden. Wahrlich, jene, denen nach ihnen das Buch zum Erbe gegeben wurde, befinden sich in bedenklichem Zweifel daruber"
  },
  {
    "surah_number": 42,
    "verse_number": 15,
    "content":
        "Zu diesem (Glauben) also rufe (sie) auf. Und bleibe aufrichtig, wie dir befohlen wurde, und folge ihren personlichen Neigungen nicht, sondern sprich: \"lch glaube an das Buch, was immer es sei, das Allah herabgesandt hat, und mir ist befohlen worden, gerecht zwischen euch zu richten. Allah ist unser Herr und euer Herr. Fur uns unsere Werke und fur euch eure Werke! Kein Beweisgrund ist zwischen uns und euch. Allah wird uns zusammenbringen, und zu Ihm ist die Heimkehr"
  },
  {"surah_number": 42, "verse_number": 16, "content": "Und diejenigen, die uber Allah streiten, nachdem Er gehort worden ist, deren Beweisfuhrung vor ihrem Herrn ist nichtig; auf ihnen ist Zorn, und ihnen wird eine strenge Strafe zuteil sein"},
  {"surah_number": 42, "verse_number": 17, "content": "Allah ist es, Der das Buch mit der Wahrheit und der Gerechtigkeit herabgesandt hat. Und wie kannst du es wissen: vielleicht steht die Stunde nahe bevor"},
  {"surah_number": 42, "verse_number": 18, "content": "Diejenigen, die nicht an sie glauben, wunschen sie zu beschleunigen; die aber, die (an sie) glauben, haben Furcht vor ihr und wissen, daß es die Wahrheit ist. Wahrlich, diejenigen, die an der Stunde zweifeln, sind weit in die Irre (gegangen)"},
  {"surah_number": 42, "verse_number": 19, "content": "Allah ist Gutig gegen Seine Diener. Er versorgt, wen Er will. Und Er ist der Starke, der Erhabene"},
  {"surah_number": 42, "verse_number": 20, "content": "Dem, der die Ernte des Jenseits begehrt, vermehren Wir seine Ernte; und dem, der die Ernte dieser Welt begehrt, geben Wir davon, doch am Jenseits wird er keinen Anteil haben"},
  {
    "surah_number": 42,
    "verse_number": 21,
    "content": "Oder haben sie etwa Partner, die ihnen eine Glaubenslehre vorgeschrieben haben, die Allah nicht verordnet hat? Und ware es nicht bis zum Urteilsspruch aufgeschoben worden, ware zwischen ihnen schon gerichtet worden. Und gewiß, den Frevlern wird eine schmerzliche Strafe zuteil sein"
  },
  {
    "surah_number": 42,
    "verse_number": 22,
    "content": "Du wirst die Frevler in Furcht sehen wegen dem, was sie begangen haben, und es wird sicherlich auf sie hereinbrechen. Jene aber, die glauben und gute Werke tun, werden in den Paradiesgarten sein. Sie sollen bei ihrem Herrn alles finden, was sie begehren. Das ist die große Huld"
  },
  {
    "surah_number": 42,
    "verse_number": 23,
    "content":
        "Dies ist es, wovon Allah Seinen Dienern, die glauben und gute Werke tun, die frohe Botschaft gibt. Sprich: \"lch verlange von euch keinen Lohn dafur, es sei denn die Liebe zu den Ver wandten.\" Und dem, der eine gute Tat begeht, verschonern Wir sie noch. Wahrlich, Allah ist Allverzeihend, Dankbar"
  },
  {
    "surah_number": 42,
    "verse_number": 24,
    "content": "Oder sagen sie etwa: \"Er hat eine Luge gegen Allah ersonnen?\" Wenn Allah so wollte, konnte Er dein Herz versiegeln. Doch Allah loscht die Falschheit aus und bewahrt die Wahrheit durch Seine Worte. Wahrlich, Er weiß recht wohl, was in den Herzen ist"
  },
  {"surah_number": 42, "verse_number": 25, "content": "Und Er ist es, Der Reue von Seinen Dienern annimmt und Sunden vergibt und weiß, was ihr tut"},
  {"surah_number": 42, "verse_number": 26, "content": "Und Er erhort diejenigen, die glaubig sind und gute Werke tun, und gibt ihnen noch mehr von Seiner Gna denfulle; den Unglaubigen aber wird eine strenge Strafe zuteil sein"},
  {"surah_number": 42, "verse_number": 27, "content": "Und wenn Allah die Mittel zum Unterhalt fur Seine Diener erweitern wurde, so wurden sie ubermutig auf Erden sein; doch Er sendet (Seine Gaben) in dem Maße hinab, wie Er es will; denn Er kennt und durchschaut Seine Diener recht wohl"},
  {"surah_number": 42, "verse_number": 28, "content": "Und Er ist es, Der den Regen hinabsendet, nachdem sie verzweifelten, und Seine Barmherzigkeit ausbreitet. Und Er ist der Beschutzer, der Preiswurdige"},
  {"surah_number": 42, "verse_number": 29, "content": "Und zu Seinen Zeichen gehort die Schopfung der Himmel und der Erde und jeglicher Lebewesen, die Er beiden eingegeben hat. Und Er hat die Macht dazu, sie allesamt zu versammeln, wenn Er will"},
  {"surah_number": 42, "verse_number": 30, "content": "Und was euch an Ungluck treffen mag, es erfolgt auf Grund dessen, was eure Hande gewirkt haben. Und Er vergibt vieles"},
  {"surah_number": 42, "verse_number": 31, "content": "Und ihr konnt auf Erden nicht siegen, noch habt ihr einen Freund oder Helfer außer Allah"},
  {"surah_number": 42, "verse_number": 32, "content": "Und zu Seinen Zeichen gehoren die wie Berge auf dem Meer fahrenden Schiffe"},
  {"surah_number": 42, "verse_number": 33, "content": "Wenn Er will, so kann Er den Wind stillhalten, so daß sie reglos auf seiner Oberflache liegen - hierin sind wahrlich Zeichen fur jeden Standhaften, Dankbaren"},
  {"surah_number": 42, "verse_number": 34, "content": "oder Er kann sie untergehen lassen um dessentwillen, was sie begangen haben - und Er vergibt vieles"},
  {"surah_number": 42, "verse_number": 35, "content": "und damit jene, die uber Allahs Zeichen streiten, begreifen, daß sie keine Zuflucht haben"},
  {"surah_number": 42, "verse_number": 36, "content": "Was euch (auch) immer gegeben wird: es ist nur ein vorubergehender Genuß dieses irdischen Lebens, und das, was bei Allah ist, ist besser und dauerhafter fur jene, die glauben und auf ihren Herrn vertrauen"},
  {"surah_number": 42, "verse_number": 37, "content": "und (fur jene, die) die schwersten Sunden und Schandlichkeiten meiden und, wenn sie zornig sind, vergeben"},
  {"surah_number": 42, "verse_number": 38, "content": "und (fur jene,) die auf ihren Herrn horen und das Gebet verrichten und deren Handlungsweise (eine Sache) gegenseitiger Beratung ist, und die von dem spenden, was Wir ihnen gegeben haben"},
  {"surah_number": 42, "verse_number": 39, "content": "und (fur jene,) die sich wehren, wenn ihnen Gewalt angetan wird"},
  {"surah_number": 42, "verse_number": 40, "content": "Die Vergeltung fur eine Ubeltat soll ein Ubel gleichen Ausmaßes sein; dessen Lohn aber, der vergibt und Besserung bewirkt, ruht sicher bei Allah. Wahrlich, Er liebt die Ungerechten nicht"},
  {"surah_number": 42, "verse_number": 41, "content": "Jedoch trifft kein Tadel jene, die sich wehren, nachdem ihnen Unrecht widerfahren ist"},
  {"surah_number": 42, "verse_number": 42, "content": "Tadel trifft nur solche, die den Menschen Unrecht zufugen und auf Erden ohne Rechtfertigung freveln. Ihnen wird eine schmerzliche Strafe zuteil sein"},
  {"surah_number": 42, "verse_number": 43, "content": "Und wahrlich, wer geduldig ist und vergibt - das ist gewiß eine Tugend der Entschlossenheit in allen Dingen"},
  {"surah_number": 42, "verse_number": 44, "content": "Und wen Allah irrefuhrt fur ihn gibt es außer Ihm keinen Beschutzer. Und du wirst die Frevler sehen, wie sie, wenn sie die Strafe sehen, sagen: \"Gibt es denn keinen Weg zur Ruckkehr"},
  {
    "surah_number": 42,
    "verse_number": 45,
    "content":
        "Und du wirst sie ihr ausgesetzt sehen, gedemutigt, voller Schmach (und) mit verstohlenem Blick schauend. Diejenigen aber, die glaubig sind, werden sagen: \"Wahrlich, die Verlierenden sind diejenigen, die am Tage der Auferstehung ihrer selbst und ihrer Angehorigen verlustig gegangen sind.\" Wahrlich, die Frevler sind langdauernder Pein (ausgesetzt)"
  },
  {"surah_number": 42, "verse_number": 46, "content": "Und sie haben keine Helfer, die ihnen gegen Allah helfen (konnten) Und fur einen, den Allah zum Irrtum fuhrt, gibt es keinen Ausweg"},
  {"surah_number": 42, "verse_number": 47, "content": "Hort auf euren Herrn, bevor ein Tag kommt, den niemand gegen Allah verwehren kann. An jenem Tag wird es fur euch keine Zuflucht geben, noch gibt es fur euch irgendeine Moglichkeit, (eure Sunden) zu leugnen"},
  {
    "surah_number": 42,
    "verse_number": 48,
    "content":
        "Kehren sie sich jedoch (vom Glauben) ab, so haben Wir dich nicht als deren Wachter entsandt. Deine Pflicht ist nur die Verkundigung. Wenn Wir dem Menschen von Unserer Barmherzigkeit zu kosten geben, so freut er sich uber sie. Doch wenn ein Unheil sie um dessentwillen trifft, was ihre Hande vorausgeschickt haben: siehe, dann ist der Mensch undankbar"
  },
  {"surah_number": 42, "verse_number": 49, "content": "Allahs ist das Konigreich der Himmel und der Erde. Er schafft, was Er will. Er beschert Madchen, wem Er will, und Er beschert die Knaben, wem Er will"},
  {"surah_number": 42, "verse_number": 50, "content": "Oder Er gibt beide, Knaben und Madchen, und Er macht unfruchtbar, wen Er will; Er ist Allwissend, Allmachtig"},
  {"surah_number": 42, "verse_number": 51, "content": "Und keinem Menschen steht es zu, daß Allah zu ihm sprechen sollte, außer durch Eingebung oder hinter einem Vorhang oder, indem Er einen Boten schickt, um durch Sein Geheiß zu offenbaren, was Er will; Er ist Erhaben, Allweise"},
  {
    "surah_number": 42,
    "verse_number": 52,
    "content":
        "Und so haben Wir dir nach Unserem Gebot ein Wort offenbart. Weder wußtest du, was die Schrift noch was der Glaube ist. Doch Wir haben sie (die Offenbarung) zu einem Licht gemacht, mit dem Wir jenen von Unseren Dienern, denen Wir wollen, den Weg weisen. Wahrlich, du leitest (sie) auf den geraden Weg"
  },
  {"surah_number": 42, "verse_number": 53, "content": "den Weg Allahs, Dem alles gehort, was in den Himmeln und was auf Erden ist. Wahrlich, zu Allah kehren alle Dinge zuruck"},
  {"surah_number": 43, "verse_number": 1, "content": "Ha Mim"},
  {"surah_number": 43, "verse_number": 2, "content": "Bei dem deutlichen Buch"},
  {"surah_number": 43, "verse_number": 3, "content": "Wahrlich Wir haben ihn zu einem Quran in arabischer Sprache gemacht, auf daß ihr (es) verstehen moget"},
  {"surah_number": 43, "verse_number": 4, "content": "Und wahrlich, er ist bei Uns in der Mutterschrift hochgehoben (und) voller Weisheit"},
  {"surah_number": 43, "verse_number": 5, "content": "Sollen Wir da die Ermahnung von euch abwenden, weil ihr ein zugelloses Volk seid"},
  {"surah_number": 43, "verse_number": 6, "content": "Wie viele Propheten entsandten Wir schon zu den fruheren Volkern"},
  {"surah_number": 43, "verse_number": 7, "content": "Und nie kam ein Prophet zu ihnen, den sie nicht verspottet hatten"},
  {"surah_number": 43, "verse_number": 8, "content": "Darum vertilgten Wir die, die machtiger als diese waren, und das (abschreckende) Beispiel der Fruheren ist zuvor ergangen"},
  {"surah_number": 43, "verse_number": 9, "content": "Und wenn du sie fragst: \"Wer erschuf die Himmel und die Erde?\" werden sie sicher sagen: \"Der Erhabene, der Allwissende hat sie erschaffen"},
  {"surah_number": 43, "verse_number": 10, "content": "(Der,) Der die Erde fur euch zu einem Lager gemacht hat; und gemacht hat Er fur euch Wege auf ihr, auf daß ihr dem rechten Wege folgen moget"},
  {"surah_number": 43, "verse_number": 11, "content": "Und (Er ist es,) Der Wasser vom Himmel nach Maß herniedersendet, durch das Wir ein totes Land zum Leben erwecken ebenso sollt auch ihr auferweckt werden"},
  {"surah_number": 43, "verse_number": 12, "content": "Und (Er ist es,) Der alle Arten paarweise erschaffen hat und fur euch Schiffe gemacht hat und Tiere, auf denen ihr reitet"},
  {
    "surah_number": 43,
    "verse_number": 13,
    "content": "so daß ihr fest auf ihrem Rucken sitzt (und) dann, wenn ihr euch fest auf sie gesetzt habt, der Gnade eures Herrn eingedenk seid und sprechen moget: \"Preis (sei) Ihm, Der uns dies dienstbar gemacht hat, und wir waren hierzu nicht imstande gewesen"
  },
  {"surah_number": 43, "verse_number": 14, "content": "Und zu unserem Herrn werden wir sicher zuruckkehren"},
  {"surah_number": 43, "verse_number": 15, "content": "Und aus Seinen Dienern machen sie einen Teil Seiner Selbst. Wahrlich, der Mensch ist offenkundig undankbar"},
  {"surah_number": 43, "verse_number": 16, "content": "Hat Er etwa Tochter von dem genommen, was Er erschaffen hat, und euch mit Sohnen ausgezeichnet"},
  {"surah_number": 43, "verse_number": 17, "content": "Und doch, wenn einem von ihnen Kunde von dem gegeben wird, was er dem Allerbarmer zuschreibt, dann wird sein Gesicht schwarz und er erstickt vor Gram"},
  {"surah_number": 43, "verse_number": 18, "content": "(Ist es) etwa einer, der im Glanz aufgezogen wird und nicht beredt im Wortstreit ist"},
  {"surah_number": 43, "verse_number": 19, "content": "Und sie machen die Engel, die Diener des Allerbarmers sind, zu weiblichen Wesen. Waren sie etwa Zeugen ihrer Erschaffung? Ihr Zeugnis wird niedergeschrieben, und sie werden befragt werden"},
  {"surah_number": 43, "verse_number": 20, "content": "Und sie sagen: \"Hatte der Allerbarmer es gewollt, hatten wir sie nicht verehrt.\" Sie haben keinerlei Kenntnis hiervon; sie mutmaßen nur"},
  {"surah_number": 43, "verse_number": 21, "content": "Oder haben Wir ihnen etwa ein Buch vor diesem (Quran) gegeben, an dem sie festhalten"},
  {"surah_number": 43, "verse_number": 22, "content": "Nein, sie sagen: \"Wir fanden unsere Vater auf einem Weg und wir lassen uns von ihren Fußstapfen leiten"},
  {"surah_number": 43, "verse_number": 23, "content": "Und ebenso sandten Wir keinen Warner vor dir in irgendeine Stadt, ohne daß die Reichen darin gesagt hatten: \"Wir fanden unsere Vater auf einem Weg, und wir treten in ihre Fußstapfen"},
  {"surah_number": 43, "verse_number": 24, "content": "(lhr Warner) sagte: \"Wie? Auch wenn ich euch eine bessere Fuhrung bringe als die, welche ihr eure Vater befolgen saht?\". Sie sagten: \"Wir leugnen das, womit ihr gesandt worden seid"},
  {"surah_number": 43, "verse_number": 25, "content": "Also vergalten Wir es ihnen. Siehe nun, wie das Ende der Leugner war"},
  {"surah_number": 43, "verse_number": 26, "content": "Und da sagte Abraham zu seinem Vater und seinem Volk: \"lch sage mich von dem los, was ihr anbetet"},
  {"surah_number": 43, "verse_number": 27, "content": "statt Dem, Der mich erschuf; denn Er wird mich rechtleiten"},
  {"surah_number": 43, "verse_number": 28, "content": "Und er machte es zu einem bleibenden Wort unter seiner Nachkommenschaft, auf daß sie darauf (zum Glauben) zuruckfinden mogen"},
  {"surah_number": 43, "verse_number": 29, "content": "Nein, aber Ich ließ sie und ihre Vater in Fulle leben, bis die Wahrheit und ein deutlicher Gesandter zu ihnen kamen"},
  {"surah_number": 43, "verse_number": 30, "content": "Doch als die Wahrheit zu ihnen kam, da sagten sie: \"Das ist Zauberei, und wir glauben nicht daran"},
  {"surah_number": 43, "verse_number": 31, "content": "Und sie sagten: \"Warum ist dieser Quran nicht zu einem angesehenen Mann aus den beiden Stadten herabgesandt worden"},
  {
    "surah_number": 43,
    "verse_number": 32,
    "content":
        "Sind sie es, die die Barmherzigkeit deines Herrn zu verteilen haben? Wir Selbst verteilen unter ihnen ihren Lebensunterhalt im irdischen Leben, und Wir erhohen einige von ihnen uber die anderen im Rang, auf daß die einen die anderen in den Dienst nehmen mogen. Und die Barmherzigkeit deines Herrn ist besser als das, was sie anhaufen"
  },
  {
    "surah_number": 43,
    "verse_number": 33,
    "content": "Und wenn es nicht (geschehen) ware, daß alle Menschen zu einer einzigen Gemeinde (vereint) worden waren, hatten Wir denen, die nicht an den Allerbarmer glauben, Dacher aus Silber fur ihre Hauser gegeben und Treppen, um (zu ihnen) hinaufzusteigen"
  },
  {"surah_number": 43, "verse_number": 34, "content": "und Turen fur ihre Hauser und Ruhebetten, um darauf zu liegen"},
  {"surah_number": 43, "verse_number": 35, "content": "und Prunk. Doch all das ist nichts (anderes) als eine Versorgung fur dieses irdische Leben. Und das Jenseits bei deinem Herrn ist den Rechtschaffenen (vorbehalten)"},
  {"surah_number": 43, "verse_number": 36, "content": "Und fur den, der sich vom Gedenken an den Allerbarmer abwendet, bestimmen Wir einen Satan, der sein Begleiter sein wird"},
  {"surah_number": 43, "verse_number": 37, "content": "Und wahrlich, sie wenden sie vom Weg ab, jedoch meinen sie, sie seien rechtgeleitet"},
  {"surah_number": 43, "verse_number": 38, "content": "bis zuletzt, wenn ein solcher zu Uns kommt, er (zu seinem Begleiter) sagt: \"O lage doch zwischen mir und dir die Entfernung zwischen dem Osten und dem Westen!\" Was fur ein schlimmer Begleiter ist er doch"},
  {"surah_number": 43, "verse_number": 39, "content": "Und euch soll an diesem Tage nichts nutzen; denn ihr habt gefrevelt, so daß ihr die Strafe gemeinsam erleidet"},
  {"surah_number": 43, "verse_number": 40, "content": "Kannst du etwa die Tauben horend machen oder die Blinden rechtleiten oder den, der sich in einem offenkundigen Irrtum befindet"},
  {"surah_number": 43, "verse_number": 41, "content": "Und sollten Wir dich fortnehmen, werden Wir sicher Uns an ihnen rachen"},
  {"surah_number": 43, "verse_number": 42, "content": "Oder Wir werden dir zeigen, was Wir ihnen angedroht haben; denn Wir haben vollige Macht uber sie"},
  {"surah_number": 43, "verse_number": 43, "content": "Also halte denn an dem fest, was dir offenbart worden ist; denn du bist auf dem geraden Weg"},
  {"surah_number": 43, "verse_number": 44, "content": "Und es ist wahrlich eine Ermahnung fur dich und fur dein Volk, und ihr werdet zur Rechenschaft gezogen werden"},
  {"surah_number": 43, "verse_number": 45, "content": "Und frage jene Unserer Gesandten, die Wir vor dir entsandt haben: \"Machten Wir etwa außer dem Allerbarmer Gotter, denen man dienen soll"},
  {"surah_number": 43, "verse_number": 46, "content": "Und wahrlich, Wir sandten Moses mit Unseren Zeichen zu Pharao und seinen Vornehmen; da sagte er: \"lch bin ein Gesandter vom Herrn der Welten"},
  {"surah_number": 43, "verse_number": 47, "content": "Doch als er mit Unseren Zeichen zu ihnen kam, siehe, da lachten sie uber sie"},
  {"surah_number": 43, "verse_number": 48, "content": "Und Wir zeigten ihnen kein Zeichen, das nicht großer als das vorherige gewesen ware, und Wir erfaßten sie mit der Strafe, auf daß sie sich bekehren mogen"},
  {"surah_number": 43, "verse_number": 49, "content": "Und sie sagten: \"O du Zauberer, bete fur uns zu deinem Herrn, gemaß dem, was Er dir verheißen hat; denn wir werden dann rechtgeleitet sein"},
  {"surah_number": 43, "verse_number": 50, "content": "Doch als Wir die Strafe von ihnen nahmen, siehe, da brachen sie ihr Wort"},
  {"surah_number": 43, "verse_number": 51, "content": "Und Pharao ließ unter seinem Volk ausrufen: \"O mein Volk, gehoren mir nicht das Konigreich von Agypten und diese Strome, die mir zu Fußen fließen? Konnt ihr denn nicht sehen"},
  {"surah_number": 43, "verse_number": 52, "content": "Oder bin ich nicht besser als dieser da, der verachtlich ist und sich kaum verstandlich ausdrucken kann"},
  {"surah_number": 43, "verse_number": 53, "content": "Warum sind ihm dann nicht Armbander aus Gold angelegt worden oder (warum sind dann nicht) Engel mit ihm im Geleit gekommen"},
  {"surah_number": 43, "verse_number": 54, "content": "So verleitete er sein Volk zur Narrheit, und sie gehorchten ihm. Sie waren wahrlich ein frevelhaftes Volk"},
  {"surah_number": 43, "verse_number": 55, "content": "Nachdem sie Uns erzurnt hatten, nahmen Wir Vergeltung an ihnen und ertrankten sie allesamt"},
  {"surah_number": 43, "verse_number": 56, "content": "Alsdann machten Wir sie zum Vergangenen und zu einem Beispiel fur die Spateren"},
  {"surah_number": 43, "verse_number": 57, "content": "Und wann immer die Rede von dem Sohn der Maria ist, siehe, dann bricht dein Volk daruber in Geschrei aus"},
  {"surah_number": 43, "verse_number": 58, "content": "und sie sagen: \"Sind unsere Gotter besser oder er?\" Sie erwahnen das vor dir nur aus Widerspruchsgeist. Nein, sie sind aber ein streitsuchtiges Volk"},
  {"surah_number": 43, "verse_number": 59, "content": "Er war nur ein Diener, dem Wir Gnade erwiesen hatten, und Wir machten ihn zu einem Beispiel fur die Kinder Israels"},
  {"surah_number": 43, "verse_number": 60, "content": "Und wenn Wir es wollten, konnten Wir aus euren Reihen Engel hervorgehen lassen, (um euch) auf Erden zu ersetzen"},
  {"surah_number": 43, "verse_number": 61, "content": "Doch wahrlich, er (Jesus) ist ein Vorzeichen der Stunde. So bezweifelt sie nicht, sondern folgt Mir. Das ist ein gerader Weg"},
  {"surah_number": 43, "verse_number": 62, "content": "Und lasset euch nicht von Satan abwenden. Gewiß, er ist euer offenkundiger Feind"},
  {"surah_number": 43, "verse_number": 63, "content": "Und als Jesus mit klaren Beweisen kam, sagte er: \"Wahrlich, ich bin mit der Weisheit zu euch gekommen, und um euch etwas von dem zu verdeutlichen, woruber ihr uneinig seid. So furchtet Allah und gehorcht mir"},
  {"surah_number": 43, "verse_number": 64, "content": "Wahrlich, Allah allein ist mein Herr und euer Herr. Darum dient Ihm. Das ist ein gerader Weg"},
  {"surah_number": 43, "verse_number": 65, "content": "Doch die Parteien wurden dann untereinander uneinig; wehe dann denen, die freveln, vor der Strafe eines schmerzlichen Tages"},
  {"surah_number": 43, "verse_number": 66, "content": "Sie warten nur darauf, daß die Stunde plotzlich uber sie kommt, ohne daß sie es merken"},
  {"surah_number": 43, "verse_number": 67, "content": "Die Freunde werden an jenem Tage einer des anderen Feind sein, außer den Gottesfurchtigen"},
  {"surah_number": 43, "verse_number": 68, "content": "O Meine Diener, keine Furcht soll euch an diesem Tage bedrucken, noch sollt ihr traurig sein"},
  {"surah_number": 43, "verse_number": 69, "content": "ihr, die ihr an Unsere Zeichen glaubtet und (Uns) ergeben waret"},
  {"surah_number": 43, "verse_number": 70, "content": "Tretet in das Paradies ein, ihr und eure Gattinnen, (und seid) gluckselig"},
  {"surah_number": 43, "verse_number": 71, "content": "Schusseln aus Gold und Becher werden unter ihnen kreisen, und darin wird alles sein, was die Herzen begehren und (woran) die Augen sich ergotzen \"Und ewig werdet ihr darin verweilen"},
  {"surah_number": 43, "verse_number": 72, "content": "Und dies ist das Paradies, zu dessen Erben ihr berufen wurdet um dessentwillen, was ihr zu tun pflegtet"},
  {"surah_number": 43, "verse_number": 73, "content": "Darin gibt es fur euch Fruchte in Menge, von denen ihr essen konnt"},
  {"surah_number": 43, "verse_number": 74, "content": "Wahrlich, die Sunder werden ewig in der Strafe der Gahannam verharren"},
  {"surah_number": 43, "verse_number": 75, "content": "sie wird fur sie nicht gemildert werden, und sie werden in ihr von Verzweiflung erfaßt werden"},
  {"surah_number": 43, "verse_number": 76, "content": "Nicht Wir taten ihnen Unrecht, sondern sie selbst taten (sich) Unrecht"},
  {"surah_number": 43, "verse_number": 77, "content": "Und sie werden rufen: \"O Malik, laß deinen Herrn ein Ende mit uns machen!\" Er wird sprechen: \"lhr mußt bleiben"},
  {"surah_number": 43, "verse_number": 78, "content": "Wir brachten euch gewiß die Wahrheit; jedoch die meisten von euch verabscheuten die Wahrheit"},
  {"surah_number": 43, "verse_number": 79, "content": "Oder haben sie sich fur einen Plan entschlossen? Nun, auch Wir haben Uns entschlossen"},
  {"surah_number": 43, "verse_number": 80, "content": "Oder meinen sie etwa, daß Wir nicht horen, was sie geheimhalten und was sie besprechen? Doch, und Unsere Boten bei ihnen schreiben es auf"},
  {"surah_number": 43, "verse_number": 81, "content": "Sprich: \"Hatte der Allerbarmer einen Sohn, so ware ich der erste, der (ihn) angebetet hatte"},
  {"surah_number": 43, "verse_number": 82, "content": "Gepriesen sei der Herr der Himmel und der Erde, der Herr des Thrones, Der frei ist von all dem, was sie behaupten"},
  {"surah_number": 43, "verse_number": 83, "content": "So laß sie sich in eitler Rede ergehen und sich vergnugen, bis sie ihrem Tag begegnen, der ihnen angedroht wird"},
  {"surah_number": 43, "verse_number": 84, "content": "Und Er ist der Gott im Himmel und der Gott auf Erden, und Er ist der Allweise, der Allwissende"},
  {"surah_number": 43, "verse_number": 85, "content": "Segensreich ist Er, Dessen das Konigreich der Himmel und der Erde ist und all das, was zwischen beiden ist; und bei Ihm ruht die Kenntnis der Stunde, und zu Ihm sollt ihr zuruckgebracht werden"},
  {"surah_number": 43, "verse_number": 86, "content": "Und diejenigen, die sie statt Seiner anbeten, haben kein Furspracherecht, mit Ausnahme dessen, der die Wahrheit bezeugt, und sie wissen Bescheid"},
  {"surah_number": 43, "verse_number": 87, "content": "Und wenn du sie fragst: \"Wer schuf sie?\" werden sie sicher sagen: \"Allah.\" Wie lassen sie sich da (von Ihm) abwenden"},
  {"surah_number": 43, "verse_number": 88, "content": "Und seine Aussage (lautet): \"O mein Herr! Dies ist ein Volk, das nicht glauben will"},
  {"surah_number": 43, "verse_number": 89, "content": "Darum wende dich von ihnen ab und sprich: \"Frieden!\" Und bald werden sie es wissen"},
  {"surah_number": 44, "verse_number": 1, "content": "Ha Mim"},
  {"surah_number": 44, "verse_number": 2, "content": "Bei dem deutlichen Buch"},
  {"surah_number": 44, "verse_number": 3, "content": "Wahrlich, Wir haben es in einer gesegneten Nacht herabgesandt wahrlich, Wir haben damit gewarnt"},
  {"surah_number": 44, "verse_number": 4, "content": "in dieser (Nacht) wird jegliche weise Sache entschieden"},
  {"surah_number": 44, "verse_number": 5, "content": "auf Grund Unseres Befehls. Wahrlich, Wir haben (Gesandte) geschickt"},
  {"surah_number": 44, "verse_number": 6, "content": "als eine Barmherzigkeit von deinem Herrn; Er ist der Allhorende, der Allwissende"},
  {"surah_number": 44, "verse_number": 7, "content": "der Herr der Himmel und der Erde und all dessen, was zwischen beiden ist, wenn ihr Gewißheit (im Glauben) hattet"},
  {"surah_number": 44, "verse_number": 8, "content": "Es ist kein Gott außer Ihm. Er macht lebendig und laßt sterben - (Er ist) euer Herr und der Herr eurer Vorvater"},
  {"surah_number": 44, "verse_number": 9, "content": "Doch sie sind im Zweifel und betreiben ein Spiel"},
  {"surah_number": 44, "verse_number": 10, "content": "Darum aber erwarte den Tag, an dem der Himmel einen sichtbaren Rauch hervorbringt"},
  {"surah_number": 44, "verse_number": 11, "content": "der die Menschen einhullen wird. Das wird eine schmerzliche Qual sein"},
  {"surah_number": 44, "verse_number": 12, "content": "Unser Herr, nimm die Pein von uns; wir wollen glauben"},
  {"surah_number": 44, "verse_number": 13, "content": "Wie konnen sie lernen, wo doch ein aufklarender Gesandter zu ihnen gekommen ist"},
  {"surah_number": 44, "verse_number": 14, "content": "Und sie haben sich von ihm abgewandt und gesagt: \"(Er hat es) einstudiert, (er ist) besessen"},
  {"surah_number": 44, "verse_number": 15, "content": "Wir werden die Strafe geringfugig hinwegnehmen, ihr aber werdet ruckfallig werden"},
  {"surah_number": 44, "verse_number": 16, "content": "an dem Tage, wo Wir (euch) den großten Schlag versetzen. Wahrlich, Wir werden Uns rachen"},
  {"surah_number": 44, "verse_number": 17, "content": "Und vor ihnen haben Wir schon das Volk Pharaos gepruft, und zu ihnen kam ein ehrenwerter Gesandter"},
  {"surah_number": 44, "verse_number": 18, "content": "(der sagte): \"Ubergebt mir die Diener Allahs. Ich bin fur euch ein vertrauenswurdiger Gesandter"},
  {"surah_number": 44, "verse_number": 19, "content": "Und uberhebt euch nicht gegen Allah. Ich komme mit einem offenkundigen Beweis zu euch"},
  {"surah_number": 44, "verse_number": 20, "content": "und ich nehme meine Zuflucht bei meinem Herrn und eurem Herrn, damit ihr mich nicht steinigt"},
  {"surah_number": 44, "verse_number": 21, "content": "Und wenn ihr mir nicht glaubt, so haltet euch von mir fern"},
  {"surah_number": 44, "verse_number": 22, "content": "Dann rief er zu seinem Herrn: \"Dies ist ein sundhaftes Volk"},
  {"surah_number": 44, "verse_number": 23, "content": "(Allah sprach:) \"Fuhre Meine Diener in der Nacht fort; ihr werdet verfolgt werden"},
  {"surah_number": 44, "verse_number": 24, "content": "Und verlaß das Meer, (wenn es) reglos ist. Sie sind ein Heer, das ertrankt wird"},
  {"surah_number": 44, "verse_number": 25, "content": "Wie zahlreich waren die Garten und die Quellen, die sie zuruckließen"},
  {"surah_number": 44, "verse_number": 26, "content": "Und die Kornfelder und die ehrenvollen Statten"},
  {"surah_number": 44, "verse_number": 27, "content": "Und (wie war) das Wohlleben, dessen sie sich erfreut hatten"},
  {"surah_number": 44, "verse_number": 28, "content": "So geschah es, daß Wir all dies einem anderen Volk zum Erbe gaben"},
  {"surah_number": 44, "verse_number": 29, "content": "Weder Himmel noch Erde weinten uber sie, noch wurde ihnen eine Frist gewahrt"},
  {"surah_number": 44, "verse_number": 30, "content": "Und wahrlich, Wir erretteten die Kinder Israels vor der schimpflichen Pein"},
  {"surah_number": 44, "verse_number": 31, "content": "vor Pharao; denn er war hochmutig, einer der Maßlosen"},
  {"surah_number": 44, "verse_number": 32, "content": "Und wahrlich, Wir erwahlten sie auf Grund (Unseres) Wissens vor den Volkern"},
  {"surah_number": 44, "verse_number": 33, "content": "Und Wir gaben ihnen von den Zeichen, in welchen eine offenkundige Prufung lag"},
  {"surah_number": 44, "verse_number": 34, "content": "Wahrlich, diese behaupten"},
  {"surah_number": 44, "verse_number": 35, "content": "Es gibt nur unseren ersten Tod, und wir werden nicht wiedererweckt werden"},
  {"surah_number": 44, "verse_number": 36, "content": "So bringt doch unsere Vater (zuruck), wenn ihr die Wahrheit redet"},
  {"surah_number": 44, "verse_number": 37, "content": "Sind sie besser oder das Volk des Tubba` und jene, die vor ihnen waren? Wir vertilgten sie; denn sie waren Verbrecher"},
  {"surah_number": 44, "verse_number": 38, "content": "Und Wir erschufen die Himmel und die Erde, und das, was zwischen beiden ist, nicht zum Zeitvertreib"},
  {"surah_number": 44, "verse_number": 39, "content": "Wir erschufen sie nur in gerechter Weise, jedoch die rneisten von ihnen wissen es nicht"},
  {"surah_number": 44, "verse_number": 40, "content": "Der Tag der Entscheidung ist wahrlich die festgesetzte Zeit fur sie alle"},
  {"surah_number": 44, "verse_number": 41, "content": "jener Tag, an dem ein Schutzherr einem Schutzbefohlenen nichts nutzen kann, und an dem ihnen nicht geholfen wird"},
  {"surah_number": 44, "verse_number": 42, "content": "Die (sind davon) ausgenommen, derer Allah Sich erbarmt; denn Er ist der Erhabene, der Barmherzige"},
  {"surah_number": 44, "verse_number": 43, "content": "Wahrlich, der Baum des Zaqqum"},
  {"surah_number": 44, "verse_number": 44, "content": "ist die Speise des Sunders"},
  {"surah_number": 44, "verse_number": 45, "content": "Wie geschmolzenes Kupfer wird er in (ihren) Bauchen brodeln"},
  {"surah_number": 44, "verse_number": 46, "content": "wie das Brodeln kochenden Wassers"},
  {"surah_number": 44, "verse_number": 47, "content": "Ergreift ihn und zerrt ihn in die Mitte der Gahim-Flammen"},
  {"surah_number": 44, "verse_number": 48, "content": "Dann gießt auf sein Haupt die Pein des siedenden Wassers"},
  {"surah_number": 44, "verse_number": 49, "content": "Koste! Du (meintest) doch, der Erhabene, der Wurdige zu sein"},
  {"surah_number": 44, "verse_number": 50, "content": "Siehe, dies ist nun das, woran ihr zu zweifeln pflegtet"},
  {"surah_number": 44, "verse_number": 51, "content": "Wahrlich, die Gottesfurchtigen sind an einer Statte der Sicherheit"},
  {"surah_number": 44, "verse_number": 52, "content": "in Garten mit Quellen"},
  {"surah_number": 44, "verse_number": 53, "content": "gekleidet in Seide und Brokat sitzen (sie) einander gegenuber"},
  {"surah_number": 44, "verse_number": 54, "content": "So (wird es sein). Und Wir werden sie mit Huris vermahlen"},
  {"surah_number": 44, "verse_number": 55, "content": "Sie werden dort Fruchte jeder Art verlangen (und) in Sicherheit (leben)"},
  {"surah_number": 44, "verse_number": 56, "content": "Den Tod werden sie dort nicht kosten, außer dem ersten Tod. Und Er wird sie vor der Strafe der Gahim bewahren"},
  {"surah_number": 44, "verse_number": 57, "content": "als eine Gnade von deinem Herrn. Das ist das hochste Gluck"},
  {"surah_number": 44, "verse_number": 58, "content": "Wir haben ihn (den Quran) in deiner Sprache leicht gemacht, damit sie sich ermahnen lassen"},
  {"surah_number": 44, "verse_number": 59, "content": "So gib acht; siehe, sie geben auch acht"},
  {"surah_number": 45, "verse_number": 1, "content": "Ha Mim"},
  {"surah_number": 45, "verse_number": 2, "content": "Die Offenbarung des Buches stammt von Allah, dem Erhabenen, dem Allweisen"},
  {"surah_number": 45, "verse_number": 3, "content": "Wahrlich, in den Himmeln und auf der Erde sind Zeichen fur die Glaubigen"},
  {"surah_number": 45, "verse_number": 4, "content": "Und in eurer Erschaffung und all den Geschopfen, die Er (uber die Erde) verbreitet hat, sind Zeichen fur Leute, die einen festen Glauben haben"},
  {"surah_number": 45, "verse_number": 5, "content": "Und in dem Wechsel von Nacht und Tag und in dem, was Allah vom Himmel an Versorgung niedersendet, wodurch Er die Erde nach ihrem Tod neu belebt, und in dem Wechsel der Winde sind Zeichen fur Leute, die Verstand besitzen"},
  {"surah_number": 45, "verse_number": 6, "content": "Dies sind die Verse Allahs, die Wir dir in Wahrheit verlesen. An welche Verkundigung wollen sie denn nach Allah und Seinen Versen (sonst) glauben"},
  {"surah_number": 45, "verse_number": 7, "content": "Wehe jedem sundigen Lugner"},
  {"surah_number": 45, "verse_number": 8, "content": "der Allahs Verse hort, wenn sie ihm verlesen werden, und dann in Hochmut verharrt, als hatte er sie nicht gehort darum verkunde ihm eine qualvolle Strafe"},
  {"surah_number": 45, "verse_number": 9, "content": "Und (wehe) dem, der, wenn er etwas von Unseren Zeichen kennenlernt, Spott damit treibt! Fur solche (Sunder) ist eine schmahliche Strafe (vorgesehen)"},
  {"surah_number": 45, "verse_number": 10, "content": "Hinter ihnen ist Gahannam; und das, was sie erworben haben, soll ihnen nichts nutzen, auch nicht die Beschutzer, die sie sich statt Allah genommen haben. Und ihnen wird eine gewaltige Strafe zuteil sein"},
  {"surah_number": 45, "verse_number": 11, "content": "Dieser (Quran) ist eine Rechtleitung. Und denjenigen, welche die Zeichen ihres Herrn leugnen, wird eine qualvolle Strafe zugemessen werden"},
  {"surah_number": 45, "verse_number": 12, "content": "Allah ist es, Der euch das Meer dienstbar gemacht hat, auf daß die Schiffe darauf nach Seinem Geheiß fahren, und auf daß ihr nach Seiner Gnadenfulle trachten und dankbar sein moget"},
  {"surah_number": 45, "verse_number": 13, "content": "Und Er hat das fur euch dienstbar gemacht, was in den Himmeln und auf Erden ist; alles ist von Ihm. Hierin liegen wahrlich Zeichen fur die Leute, die nachdenken"},
  {"surah_number": 45, "verse_number": 14, "content": "Sprich zu denen, die glauben, sie mogen denen vergeben, die nicht mit den Tagen Allahs rechnen, auf daß Er die Leute fur das belohne, was sie verdienen"},
  {"surah_number": 45, "verse_number": 15, "content": "Wer Gutes tut, der tut es fur seine eigene Seele, und wer Unrecht begeht, der begeht es gegen sich selbst. Alsdann werdet ihr zu eurem Herrn zuruckgebracht werden"},
  {"surah_number": 45, "verse_number": 16, "content": "Und wahrlich, Wir gaben den Kindern Israels die Schrift und die Herrschaft und das Prophetentum, und Wir versorgten sie mit guten Dingen und bevorzugten sie vor den Volkern"},
  {
    "surah_number": 45,
    "verse_number": 17,
    "content":
        "Und Wir gaben ihnen deutliche Weisungen in der Sache. Und sie wurden nicht eher uneins, als bis das Wissen zu ihnen gekommen war; denn zwischen ihnen (entstand) selbstsuchtiger Neid. Dein Herr wird gewiß zwischen ihnen am Tage der Auferstehung uber das richten, woruber sie uneins waren"
  },
  {"surah_number": 45, "verse_number": 18, "content": "Alsdann brachten Wir dich auf einen klaren Weg in der Sache (des Glaubens); so befolge ihn, und folge nicht den personlichen Neigungen derer, die nicht wissen"},
  {"surah_number": 45, "verse_number": 19, "content": "Sie werden dir sicher nichts gegen Allah nutzen. Und was die Ungerechten anbelangt, so sind einige von ihnen die Beschutzer anderer; Allah aber ist der Beschutzer der Gottesfurchtigen"},
  {"surah_number": 45, "verse_number": 20, "content": "Dies sind (die Mittel) zur Aufklarung fur die Menschheit und eine Fuhrung und Barmherzigkeit fur Leute, die einen festen Glauben haben"},
  {"surah_number": 45, "verse_number": 21, "content": "Meinen die, die Boses veruben, etwa, daß Wir sie wie die behandeln wurden, die glauben und gute Werke tun, so daß ihr Leben und ihr Tod gleich sein wurden? Schlimm ist es, wie sie urteilen"},
  {"surah_number": 45, "verse_number": 22, "content": "Und Allah hat die Himmel und die Erde in Wahrheit geschaffen, und daher wird jeder fur das belohnt werden, was er verdient hat; und sie sollen kein Unrecht erleiden"},
  {
    "surah_number": 45,
    "verse_number": 23,
    "content":
        "Hast du den gesehen, der sich seine eigene Neigung zum Gott nimmt und den Allah auf Grund (Seines) Wissens zum Irrenden erklart, und dem Er Ohren und Herz versiegelt, und auf dessen Augen Er einen Schleier gelegt hat? Wer sollte ihn außer Allah wohl richtig fuhren? Wollt ihr euch da nicht ermahnen lassen"
  },
  {"surah_number": 45, "verse_number": 24, "content": "Und sie sagen: \"Es gibt nichts als dies unser irdisches Leben; wir sterben und wir leben, und nichts als die Zeit, die uns vernichtet.\" Jedoch sie besitzen daruber kein Wissen; sie vermuten es nur"},
  {"surah_number": 45, "verse_number": 25, "content": "Und wenn ihnen Unsere deutlichen Verse verlesen werden, so wenden sie lediglich ein: \"Bringt unsere Vater (zuruck), wenn ihr wahrhaftig seid"},
  {"surah_number": 45, "verse_number": 26, "content": "Sprich: \"Allah gibt euch das Leben und laßt euch dann sterben; dann wird Er euch am Tage der Auferstehung (vor Sich) versammeln; daruber besteht kein Zweifel; jedoch die meisten Menschen wissen es nicht"},
  {"surah_number": 45, "verse_number": 27, "content": "Und Allahs ist das Konigreich der Himmel und der Erde; und an dem Tage, wenn die Stunde kommen soll - an jenem Tage werden die Lugner die Verlierer sein"},
  {"surah_number": 45, "verse_number": 28, "content": "Und du wirst jede Nation auf Knien sehen. Jede Nation wird zu ihrem Buch gerufen werden: \"Heute sollt ihr fur das, was ihr getan habt, belohnt werden"},
  {"surah_number": 45, "verse_number": 29, "content": "Das ist Unser Buch; es bezeugt die Wahrheit gegen euch. Wir ließen alles aufschreiben, was ihr getan hattet"},
  {"surah_number": 45, "verse_number": 30, "content": "Was nun die betrifft, die glaubten und gute Werke vollbrachten ihr Herr wird sie in Seine Barmherzigkeit fuhren. Das ist die offenbare Gluckseligkeit"},
  {"surah_number": 45, "verse_number": 31, "content": "Doch zu jenen, die unglaubig waren(, wird gesprochen): \"Sind euch Meine Verse nicht verlesen worden? Ihr aber waret hochmutig und ihr wurdet ein sundiges Volk"},
  {"surah_number": 45, "verse_number": 32, "content": "Und als gesprochen wurde: \"Die Verheißung Allahs ist wahr, und uber die Stunde herrscht kein Zweifel\", da sagtet ihr: \"Wir wissen nicht, was die Stunde ist; wir halten (sie) fur einen Wahn, und wir sind (von ihr) nicht uberzeugt"},
  {"surah_number": 45, "verse_number": 33, "content": "Und die bosen Folgen ihres Tuns sind ihnen deutlich geworden; und es umschließt sie das, woruber sie zu spotten pflegten"},
  {"surah_number": 45, "verse_number": 34, "content": "Und es wird gesprochen: \"Heute haben Wir euch vergessen, so wie ihr das Eintreffen dieses euren Tages vergaßt. Eure Herberge ist das Feuer, und ihr habt keine Helfer"},
  {"surah_number": 45, "verse_number": 35, "content": "Dies (geschieht), weil ihr Allahs Zeichen zum Gespott machtet und (weil) euch das irdische Leben betrog.\" Darum sollen sie an jenem Tage nicht von dort herausgebracht werden, noch wird es ihnen erlaubt sein, (ihr Unrecht) wiedergutzumachen"},
  {"surah_number": 45, "verse_number": 36, "content": "Alles Lob gebuhrt denn Allah, dem Herrn der Himmel und dem Herrn der Erde, dem Herrn der Welten"},
  {"surah_number": 45, "verse_number": 37, "content": "Sein ist die Erhabenheit in den Himmeln und auf der Erde; und Er ist der Erhabene, der Allweise"},
  {"surah_number": 46, "verse_number": 1, "content": "Ha Mim"},
  {"surah_number": 46, "verse_number": 2, "content": "Die Offenbarung des Buches stammt von Allah, dem Allmachtigen, dem Allweisen"},
  {"surah_number": 46, "verse_number": 3, "content": "Wir haben die Himmel und die Erde und das, was zwischen beiden ist, nicht anders als in Wahrheit und fur eine bestimmte Zeit erschaffen, diejenigen aber, die nicht daran glauben, wovor sie gewarnt wurden, wenden sich ab"},
  {
    "surah_number": 46,
    "verse_number": 4,
    "content": "Sprich: \"Wisset ihr, was das ist, was ihr statt Allah anruft? Zeigt mir, was sie von der Erde erschaffen haben. Oder haben sie einen Anteil an den Himmeln? Bringt mir ein Buch herbei, das vor diesem da war, oder eine Spur von Wissen, wenn ihr wahrhaftig seid"
  },
  {"surah_number": 46, "verse_number": 5, "content": "Und wer irrt mehr als jener, der statt Allah solche anruft, die ihn bis zum Tage der Auferstehung nicht erhoren werden und die von seinem Anruf ahnungslos sind"},
  {"surah_number": 46, "verse_number": 6, "content": "Und wenn die Menschen (vor Mir) versammelt werden, werden sie ihre Feinde sein und ihre Anbetung verleugnen"},
  {"surah_number": 46, "verse_number": 7, "content": "Und wenn ihnen Unsere deutlichen Verse verlesen werden, sagen die Unglaubigen von der Wahrheit, wenn sie zu ihnen kommt: \"Das ist offenkundige Zauberei"},
  {
    "surah_number": 46,
    "verse_number": 8,
    "content": "Oder sie sagen: \"Er hat ihn (den Quran) erdichtet?\" Sprich: \"Wenn ich ihn erdichtet habe, konnt ihr mir nichts gegen Allah nutzen. Er weiß am besten, in was fur Reden ihr euch ergeht. Er genugt als Zeuge gegen mich und euch. Und Er ist der Allvergebende, der Barmherzige"
  },
  {"surah_number": 46, "verse_number": 9, "content": "Sprich: \"lch bin kein neuer Erfinder unter den Gesandten, und ich weiß nicht, was mit mir oder mit euch geschehen wird. Ich folge nur dem, was mir offenbart wird; und ich bin nur ein deutlicher Warner"},
  {
    "surah_number": 46,
    "verse_number": 10,
    "content": "Sprich: \"Sagt mir, wenn dies (der Quran) nun aber von Allah stammt, und ihr lehnt es ab, obwohl ein Zeuge von den Kindern Israels bezeugt hat, was ihm gleicht. Er hat geglaubt; ihr aber seid allzu hochmutig!\" Wahrlich, Allah weist dem ungerechten Volk nicht den Weg"
  },
  {"surah_number": 46, "verse_number": 11, "content": "Und die Unglaubigen sagen von den Glaubigen: \"Ware er (der Quran) etwas Gutes, hatten sie ihn nicht vor uns erlangt.\" Und da sie sich nicht von ihm leiten lassen, sagen sie: \"Dies ist eine alte Luge"},
  {"surah_number": 46, "verse_number": 12, "content": "Und vor ihm war schon das Buch von Moses eine Fuhrung und Barmherzigkeit; und dies hier ist ein Buch der Bestatigung in arabischer Sprache, auf daß es diejenigen warne, die freveln, und denen eine frohe Botschaft (bringe), die Gutes tun"},
  {"surah_number": 46, "verse_number": 13, "content": "Wahrlich, die da sagen: \"Unser Herr ist Allah\" und danach aufrichtig bleiben keine Furcht soll uber sie kommen, noch sollen sie traurig sein"},
  {"surah_number": 46, "verse_number": 14, "content": "diese sind die Bewohner des Paradieses; darin sollen sie auf ewig verweilen, als Belohnung fur das, was sie zu tun pflegten"},
  {
    "surah_number": 46,
    "verse_number": 15,
    "content":
        "Und Wir haben dem Menschen anbefohlen, gegen seine Eltern gutig zu sein. Seine Mutter trug ihn mit Widerwillen, und mit Widerwillen brachte sie ihn zur Welt. Und ihn zu tragen und ihn zu entwohnen erfordert dreißig Monate, bis er dann, wenn er seine Vollkraft erlangt und vierzig Jahre erreicht hat, sagt: \"Mein Herr, sporne mich an, dankbar zu sein fur Deine Gnade, die Du mir und meinen Eltern erwiesen hast, und (sporne mich an,) Rechtes zu wirken, das Dir wohlgefallen mag. Und laß mir meine Nachkommenschaft rechtschaffen sein. Siehe, ich wende mich zu Dir; und ich bin einer der Gottergebenen"
  },
  {"surah_number": 46, "verse_number": 16, "content": "Das sind die, von denen Wir die guten Werke annehmen, die sie getan haben, und deren uble Werke Wir ubergehen. (Sie gehoren) zu den Bewohnern des Paradieses - in Erfullung der wahrhaftigen Verheißung, die ihnen verheißen wurde"},
  {
    "surah_number": 46,
    "verse_number": 17,
    "content":
        "Und derjenige, der zu seinen Eltern sagt: \"Pfui uber euch! Verkundet ihr mir daß ich auferstehen soll, obwohl schon vor mir Geschlechter dahingegangen sind?\" und (der ihnen, wenn) sie beide Allah zu Hilfe rufen (und zu ihm sagen): \"Wehe dir, glaube! Denn die Verheißung Allahs ist wahr\", sagt: \"Das sind nichts als Fabeln der Alten"
  },
  {"surah_number": 46, "verse_number": 18, "content": "(der gehort zu) jenen, gegen welche der Spruch (der Strafe) fallig wurde, zusammen mit den Scharen, die vor ihnen hingingen von den Ginn und den Menschen. Wahrlich, sie sind die Verlierer gewesen"},
  {"surah_number": 46, "verse_number": 19, "content": "Und fur alle gibt es Rangstufen gemaß dem, was sie getan haben, auf daß Er ihnen ihre Taten voll heimzahle; und kein Unrecht soll ihnen widerfahren"},
  {
    "surah_number": 46,
    "verse_number": 20,
    "content":
        "Und an dem Tage, wenn die Unglaubigen dem Feuer ausgesetzt werden(, wird gesprochen): \"lhr habt eure guten Dinge im diesseitigen Leben aufgezehrt, und ihr hattet Genuß daran. Heute nun sollt ihr mit der Strafe der Schmach belohnt werden, weil ihr ohne jegliches Recht auf Erden hochmutig und weil ihr Frevler waret"
  },
  {"surah_number": 46, "verse_number": 21, "content": "Und gedenke des Bruders der `Ad, als er sein Volk bei den Sanddunen warnte - und Warner sind schon vor ihm gewesen und nach ihm - (und sagte): \"Dient Allah allein; denn ich furchte fur euch die Strafe eines großen Tages"},
  {"surah_number": 46, "verse_number": 22, "content": "Sie sagten: \"Bist du zu uns gekommen, um uns von unseren Gottern abzuwenden? So bring uns das, was du uns androhst, wenn du einer der Wahrhaftigen bist"},
  {"surah_number": 46, "verse_number": 23, "content": "Er sagte: \"Das Wissen (daruber) ist einzig bei Allah. Und ich richte euch nur das aus, womit ich entsandt wurde; jedoch ich sehe, ihr seid ein unwissendes Volk"},
  {
    "surah_number": 46,
    "verse_number": 24,
    "content": "Dann aber, als sie sahen, wie es sich ihren Talern gleich einer Wolke naherte, sagten sie: \"Das ist eine Wolke, die uns Regen geben wird.\" \"Nein, es ist vielmehr das, was ihr zu beschleunigen trachtetet ein Wind, der eine schmerzliche Strafe mitfuhrt"
  },
  {"surah_number": 46, "verse_number": 25, "content": "Er wird alles zerstoren auf den Befehl seines Herrn.\" Und am Morgen sah man nur noch ihre Wohnungen; so uben Wir Vergeltung am schuldigen Volk"},
  {
    "surah_number": 46,
    "verse_number": 26,
    "content":
        "Und Wir hatten ihnen Gewalt uber das gegeben, woruber Wir euch keine gegeben haben; und Wir hatten ihnen Ohren und Augen und Herzen gegeben. Aber weder ihre Ohren noch ihre Augen noch ihre Herzen nutzten ihnen im geringsten (etwas), da sie die Zeichen Allahs leugneten; und sie wurden von dem erfaßt, woruber sie zu spotten pflegten"
  },
  {"surah_number": 46, "verse_number": 27, "content": "Und Wir haben bereits Stadte rings um euch zerstort; und Wir haben die Zeichen abgewandelt, damit sie sich bekehren mogen"},
  {"surah_number": 46, "verse_number": 28, "content": "Warum haben ihnen denn die nicht geholfen, die sie sich statt Allah zu Gottern genommen hatten (und die sie Ihm) nahebringen wollten? Nein, sie entschwanden ihnen. Das war ihre Luge und das, was sie zu erdichten pflegten"},
  {"surah_number": 46, "verse_number": 29, "content": "Und da wandten Wir dir eine Schar Ginn zu, die den Quran vernehmen konnten; und als sie bei ihm zugegen waren, sprachen sie: \"Hort zu\", und als er zu Ende war, kehrten sie zu ihrem Volk zuruck (und) warnten (es)"},
  {"surah_number": 46, "verse_number": 30, "content": "Sie sprachen: \"O unser Volk, wir haben ein Buch gehort das nach Moses herabgesandt worden ist (und) welches das bestatigt, was schon vor ihm da gewesen ist; es leitet zur Wahrheit und zu dem geraden Weg"},
  {"surah_number": 46, "verse_number": 31, "content": "O unser Volk, hort auf Allahs Rufer und glaubt an Ihn. Er wird euch eure Sunden vergeben und euch vor qualvoller Strafe schutzen"},
  {"surah_number": 46, "verse_number": 32, "content": "Und der, der nicht auf Allahs Rufer hort, kann (ihm) nicht auf Erden entrinnen, noch kann er Beschutzer außer Ihm haben. Solche befinden sich in einem offenkundigen Irrtum"},
  {"surah_number": 46, "verse_number": 33, "content": "Haben sie nicht gesehen, daß Allah, Der die Himmel und die Erde erschuf und bei ihrer Erschaffung nicht mude wurde, auch vermag, die Toten lebendig zu machen? Wahrlich, Er hat Macht uber alle Dinge"},
  {"surah_number": 46, "verse_number": 34, "content": "Und an dem Tage, an dem die Unglaubigen dem Feuer ausgesetzt werden (, heißt es): \"Ist dies nicht die Wahrheit?\" Sie werden sagen: \"Doch, bei unserem Herrn.\" Er spricht dann: \"So kostet nun die Strafe dafur, daß ihr unglaubig waret"},
  {
    "surah_number": 46,
    "verse_number": 35,
    "content":
        "So gedulde dich denn, wie es die Gesandten taten, die geduldig waren; und uberhaste dich nicht ihretwegen. An dem Tage, an dem sie das schauen, was ihnen angedroht wird, wird es ihnen vorkommen, als hatten sie nur eine Stunde eines Tages (im Grabe) verweilt. (Dies ist) eine Ermahnung! Soll niemand anders als das frevelnde Volk vertilgt werden"
  },
  {"surah_number": 47, "verse_number": 1, "content": "Die Werke derjenigen, die unglaubig sind und vom Weg Allahs abhalten, macht Er zunichte"},
  {"surah_number": 47, "verse_number": 2, "content": "Denjenigen aber, die glaubig sind und gute Werke tun und an das glauben, was auf Muhammad herabgesandt worden ist - und es ist ja die Wahrheit von ihrem Herrn -, denen tilgt Er ihre schlechten Taten und stellt ihre gute Lage wieder her"},
  {"surah_number": 47, "verse_number": 3, "content": "Dies (ist deshalb so), weil jene, die unglaubig sind, dem Trugerischen folgen, und weil diejenigen, die glaubig sind, der Wahrheit ihres Herrn folgen. So beschreibt Allah den Menschen ihre Lage"},
  {
    "surah_number": 47,
    "verse_number": 4,
    "content":
        "Wenn ihr auf die stoßt, die unglaubig sind, so haut (ihnen) auf den Nacken; und wenn ihr sie schließlich siegreich niedergekampft habt, dann schnurt ihre Fesseln fest. (Fordert) dann hernach entweder Gnade oder Losegeld, bis der Krieg seine Lasten (von euch) wegnimmt. Das ist so. Und hatte Allah es gewollt, hatte Er sie Selbst vertilgen konnen; aber Er wollte die einen von euch durch die anderen prufen. Und diejenigen, die auf Allahs Weg gefallen sind - nie wird Er ihre Werke zunichte machen"
  },
  {"surah_number": 47, "verse_number": 5, "content": "Er wird sie rechtleiten und ihren Stand verbessern"},
  {"surah_number": 47, "verse_number": 6, "content": "und sie ins Paradies fuhren, das Er ihnen zu erkennen gegeben hat"},
  {"surah_number": 47, "verse_number": 7, "content": "O ihr, die ihr glaubt, wenn ihr Allahs (Sache) helft, so wird Er euch helfen und euren Fußen festen Halt geben"},
  {"surah_number": 47, "verse_number": 8, "content": "Die aber unglaubig sind - nieder mit ihnen! Er laßt ihre Werke fehlgehen"},
  {"surah_number": 47, "verse_number": 9, "content": "Dies (ist so), weil sie das hassen, was Allah herabgesandt hat; so macht Er ihre Werke zunichte"},
  {"surah_number": 47, "verse_number": 10, "content": "Sind sie denn nicht im Lande umhergezogen, so daß sie schauen konnten, wie das Ende derer war, die vor ihnen lebten? Allah richtete sie zugrunde, und fur die Unglaubigen ist das gleiche wie ihnen bestimmt"},
  {"surah_number": 47, "verse_number": 11, "content": "Dies ist (so), weil Allah der Beschutzer derer ist, die glauben; die Unglaubigen aber haben keinen Beschutzer"},
  {"surah_number": 47, "verse_number": 12, "content": "Allah laßt diejenigen, die glauben und gute Werke tun, in Garten eingehen, in denen Bache fließen; die aber, die unglaubig sind, genießen und fressen wie das Vieh, und das Feuer wird ihre Wohnstatt sein"},
  {"surah_number": 47, "verse_number": 13, "content": "Und wie manche Stadt, machtiger als deine Stadt, die dich vertrieb, haben Wir schon zerstort; und sie hatten keinen Helfer"},
  {"surah_number": 47, "verse_number": 14, "content": "Ist denn der, der sich auf einen klaren Beweis von seinem Herrn stutzt, denen gleich, denen das Ubel ihres Tuns schon dargestellt wurde und die ihren bosen Neigungen folgen"},
  {
    "surah_number": 47,
    "verse_number": 15,
    "content":
        "(So ist) die Lage des Paradieses, das den Gottesfurchtigen verheißen wurde: Darin sind Bache von Wasser, das nicht faulig wird, und Bache von Milch, deren Geschmack sich nicht andert, und Bache von berauschendem Getrank - ein Genuß fur die Trinkenden - und Bache von gelautertem Honig. Und darin werden sie Fruchte aller Art bekommen und Vergebung von ihrem Herrn. Konnen sie wohl jenen gleich sein, die ewig im Feuer sind, und denen siedendes Wasser zu trinken gegeben wird, das ihre Darme zerreißt"
  },
  {
    "surah_number": 47,
    "verse_number": 16,
    "content": "Unter ihnen sind einige, die auf dich horen, doch wenn sie von dir fortgehen, sagen sie jenen, denen das Wissen gegeben wurde: \"Was hat er da soeben gesagt?\" Das sind diejenigen, deren Herzen Allah versiegelt hat und die ihren Neigungen folgen"
  },
  {"surah_number": 47, "verse_number": 17, "content": "Denen aber, die rechtgeleitet sind, verstarkt Er die Fuhrung und verleiht ihnen Got tesfurchtigkeit"},
  {"surah_number": 47, "verse_number": 18, "content": "Sie warten nur auf die Stunde, daß sie plotzlich uber sie komme. Die Zeichen dafur sind schon eingetroffen. Doch was wird ihnen ihr Erinnern nutzen, wenn sie uber sie gekommen ist"},
  {"surah_number": 47, "verse_number": 19, "content": "Wisse nun, daß kein Gott außer Allah ist, und bitte um Vergebung fur deine Schuld und fur die glaubigen Manner und die glaubigen Frauen. Und Allah weiß, wo ihr verkehrt oder euch aufhaltet"},
  {
    "surah_number": 47,
    "verse_number": 20,
    "content":
        "Und die da glauben, sa gen: \"Warum wird keine Sura herabgesandt?\" Doch wenn eine entscheidende Sura herabgesandt wird und darin von Kampf die Rede ist, dann siehst du die, in deren Herzen Krankheit ist, dich mit dem Blick eines (Menschen) anschauen, der im Sterben von Ohnmacht befallen wird; also wehe ihnen"
  },
  {"surah_number": 47, "verse_number": 21, "content": "Gehorsam und geziemende Worte (standen ihnen besser an) Und wenn die Sache beschlossen ist, dann ware es fur sie am besten, sie wurden Allah gegenuber aufrichtig sein"},
  {"surah_number": 47, "verse_number": 22, "content": "(Wollt) ihr denn, indem ihr euch (vom Glauben) abwendet, Verderben im Land anrichten und die Bande eurer Blutsverwandtschaft zerreißen"},
  {"surah_number": 47, "verse_number": 23, "content": "Diese sind es, die von Allah verflucht sind, so daß Er sie taub macht und ihre Augen erblinden laßt"},
  {"surah_number": 47, "verse_number": 24, "content": "Wollen sie also nicht uber den Quran nachdenken, oder ist es (so), daß ihre Herzen verschlossen sind"},
  {"surah_number": 47, "verse_number": 25, "content": "Wahrlich jene, die den Rucken kehren, nachdem ihnen die Rechtleitung sichtbar geworden ist - Satan hat es ihnen eingeredet und ihnen falsche Hoffnungen gemacht"},
  {"surah_number": 47, "verse_number": 26, "content": "Dies (geschah), weil sie zu denen, die das hassen, was Allah herniedersandte, sagten: \"Wir wollen euch in einigen Sachen folgen.\" Und Allah kennt ihre Heimlichkeiten"},
  {"surah_number": 47, "verse_number": 27, "content": "Was aber, wenn (die) Engel sie abberufen und sie ins Gesicht und auf den Rucken schlagen"},
  {"surah_number": 47, "verse_number": 28, "content": "Dies (geschieht), weil sie dem folgten, was Allah erzurnte, und das haßten, was ihm wohlgefallig war. So macht Er ihre Werke zunichte"},
  {"surah_number": 47, "verse_number": 29, "content": "Oder meinen etwa die, in deren Herzen Krankheit ist, Allah wurde ihren Groll nicht an den Tag bringen"},
  {"surah_number": 47, "verse_number": 30, "content": "Und wenn Wir es wollten, konnten Wir sie dir zeigen, so daß du sie an ihren Merkmalen erkennen wurdest. Und du sollst sie gewiß am Klang der Rede erkennen. Und Allah kennt euer Tun"},
  {"surah_number": 47, "verse_number": 31, "content": "Und Wir wollen euch sicherlich prufen, bis Wir diejenigen von euch ausscheiden, die kampfen und standhaft sind. Und Wir wollen eure Verhaltensweise bekannt geben"},
  {"surah_number": 47, "verse_number": 32, "content": "Wahrlich, jene, die unglaubig sind und die sich von Allahs Weg abwenden und sich dem Gesandten widersetzen, nachdem ihnen der Weg sichtbar geworden ist - sie werden Allah in nichts schaden; doch Er wird ihre Werke zunichte machen"},
  {"surah_number": 47, "verse_number": 33, "content": "O ihr, die ihr glaubt, gehorcht Allah und gehorcht dem Gesandten und vereitelt nicht eure Werke"},
  {"surah_number": 47, "verse_number": 34, "content": "Wahrlich jene, die unglaubig sind und die sich von Allahs Weg abwenden und dann als Unglaubige sterben - ihnen wird Allah gewiß nicht verzeihen"},
  {"surah_number": 47, "verse_number": 35, "content": "So lasset (im Kampf) nicht nach und ruft nicht zum Waffenstillstand auf, wo ihr doch die Oberhand habt. Und Allah ist mit euch, und Er wird euch eure Taten nicht schmalern"},
  {"surah_number": 47, "verse_number": 36, "content": "Wahrlich, das diesseitige Leben ist nur ein Spiel und Zeitvertreib, und wenn ihr glaubig und gottesfurchtig seid, so wird Er euch euren Lohn geben und wird nicht euer Gut von euch verlangen"},
  {"surah_number": 47, "verse_number": 37, "content": "Sollte Er es von euch verlangen und drangen, wurdet ihr geizig sein, und Er wurde euren Groll an den Tag bringen"},
  {
    "surah_number": 47,
    "verse_number": 38,
    "content":
        "Seht, ihr seid diejenigen, die (dazu) berufen sind, auf Allahs Weg zu spenden; doch unter euch sind manche, die geizig sind. Und wer geizig ist, der geizt nur gegen sich selber; und Allah ist Derjenige, Der reich ist, und ihr seid die Armen. Und wenn ihr (ihm) den Rucken kehrt, so wird Er ein anderes Volk an eure Stelle setzen; und sie werden nicht so sein wie ihr"
  },
  {"surah_number": 48, "verse_number": 1, "content": "Wahrlich, Wir haben dir einen offenkundigen Sieg beschieden"},
  {"surah_number": 48, "verse_number": 2, "content": "auf daß Allah dir deine vergangene und kunftige Schuld vergebe, und auf daß Er Seine Gnade an dir vollende und dich auf einen geraden Weg leite"},
  {"surah_number": 48, "verse_number": 3, "content": "und auf daß Allah dir zu einem wurdigen Sieg verhelfe"},
  {"surah_number": 48, "verse_number": 4, "content": "Er ist es, Der die Ruhe in die Herzen der Glaubigen niedersandte, damit sie ihrem Glauben Glau ben hinzufugen - und Allahs sind die Heerscharen der Himmel und der Erde, und Allah ist Allwissend, Allweise"},
  {"surah_number": 48, "verse_number": 5, "content": "auf daß Er die glaubigen Manner und die glaubigen Frauen einfuhre in Garten, durch die Bache fließen, um ewig darin zu verweilen, und auf daß Er ihre Missetaten von Ihnen nehme - und das ist vor Allah ein großer Gewinn"},
  {
    "surah_number": 48,
    "verse_number": 6,
    "content":
        "und (auf daß Er) die Heuchler und Heuchlerinnen und die Gotzendiener und Gotzen dienerinnen bestrafe, die uble Gedanken uber Allah hegen. Auf solche (Sunder) wird ein boses Unheil niederfallen; und Allah ist zornig auf sie; und Er hat sie verflucht und Gahannam fur sie bereitet. Und das ist eine uble Bestimmung"
  },
  {"surah_number": 48, "verse_number": 7, "content": "Und Allahs sind die Heerscharen der Himmel und der Erde; und Allah ist Erhaben, Allweise"},
  {"surah_number": 48, "verse_number": 8, "content": "Wahrlich, Wir haben dich als Zeugen und als Bringer froher Botschaft und als Warner gesandt"},
  {"surah_number": 48, "verse_number": 9, "content": "auf daß ihr an Allah und Seinen Gesandten glauben und ihm helfen und ihn ehren und Ihn (Allah) morgens und abends preisen moget"},
  {
    "surah_number": 48,
    "verse_number": 10,
    "content":
        "Wahrlich, diejenigen, die dir huldigen - sie huldigen in der Tat nur Allah; die Hand Allahs ist uber ihren Handen. Und wer daher den Eid bricht, bricht ihn zu seinem eigenen Schaden; dem aber, der das halt, wozu er sich Allah gegenuber verpflichtet hat, wird Er einen gewaltigen Lohn geben"
  },
  {
    "surah_number": 48,
    "verse_number": 11,
    "content":
        "Diejenigen unter den Wustenarabern, die zuruckblieben, werden zu dir sagen: \"Beschaftigt haben uns unsere Besitztumer und unsere Familien, darum bitte um Verzeihung fur uns.\" Sie sagen mit ihren Zungen, was nicht in ihren Herzen ist. Sprich: \"Wer vermag etwas fur euch bei Allah (auszurichten), wenn Er euch Schaden oder Nutzen zufugen will? Nein, Allah ist dessen wohl kundig, was ihr tut"
  },
  {"surah_number": 48, "verse_number": 12, "content": "Nein, ihr meintet, daß der Gesandte und die Glaubigen nimmermehr zu ihren Familien zuruckkehren wurden, und das wurde euren Herzen wohlgefallig gemacht, und ihr hegtet einen bosen Gedanken, und ihr waret ein verderbtes Volk"},
  {"surah_number": 48, "verse_number": 13, "content": "Und jene, die nicht an Allah und Seinen Gesandten glauben - fur die Unglaubigen haben Wir ein flammendes Feuer bereitet"},
  {"surah_number": 48, "verse_number": 14, "content": "Und Allahs ist das Konigreich der Himmel und der Erde. Er vergibt, wem Er will, und bestraft, wen Er will, und Allah ist Allvergebend, Barmherzig"},
  {
    "surah_number": 48,
    "verse_number": 15,
    "content":
        "Diejenigen, die zuruckblieben, werden sagen: \"Wenn ihr nach Beute auszieht, die ihr zu nehmen beabsichtigt, so erlaubt uns, euch zu folgen.\" Sie wollen Allahs Spruch andern. Sprich: \"lhr sollt uns nicht folgen; so hat Allah zuvor gesprochen.\" Dann werden sie sagen: \"Nein, aber ihr beneidet uns.\" Das nicht, jedoch sie verstehen nur wenig"
  },
  {
    "surah_number": 48,
    "verse_number": 16,
    "content":
        "Sprich zu den Wustenarabern, die zuruckblieben: \"lhr sollt gegen ein Volk von starken Kriegern aufgerufen werden; ihr kampft gegen sie, es sei denn, sie treten zum Islam Aber; und wenn ihr gehorcht, wird Allah euch einen schonen Lohn geben; doch wenn ihr (ihm) den Rucken kehrt, wie ihr ihn zuvor gekehrt habt, dann wird Er euch mit qualvoller Strafe bestrafen"
  },
  {
    "surah_number": 48,
    "verse_number": 17,
    "content":
        "Kein Tadel trifft den Blinden noch trifft ein Tadel den Gehbehinderten, noch trifft ein Tadel den Kranken. Und den, der Allah und Seinem Gesandten gehorcht, wird Er in Garten fuhren, durch die Bache fließen; doch den, der (ihm) den Rucken kehrt, wird Er mit schmerzlicher Strafe bestrafen"
  },
  {"surah_number": 48, "verse_number": 18, "content": "Allah ist wohl zufrieden mit den Glaubigen, weil sie dir unter dem Baum Treue gelobten; und Er wußte, was in ihren Herzen war, dann sandte Er Ruhe auf sie hinab und belohnte sie mit einem nahen Sieg"},
  {"surah_number": 48, "verse_number": 19, "content": "und mit viel Beute, die sie machen werden. Und Allah ist Erhaben, Allweise"},
  {
    "surah_number": 48,
    "verse_number": 20,
    "content": "Allah hat euch viel Beute verheißen, die ihr machen werdet, und Er hat euch dies eilends aufgegeben und hat die Hande der Menschen von euch abgehalten, auf daß es ein Zeichen fur die Glaubigen sei und auf daß Er euch auf einen geraden Weg leite"
  },
  {"surah_number": 48, "verse_number": 21, "content": "Und einen anderen (Teil der Beute), den ihr noch nicht zu erlangen vermochtet, hat Allah jedoch in Seiner Macht. Und Allah hat Macht uber alle Dinge"},
  {"surah_number": 48, "verse_number": 22, "content": "Und wenn die Unglaubigen euch bekampft hatten, hatten sie (euch) gewiß den Rucken gekehrt; dann hatten sie weder Beschutzer noch Helfer finden konnen"},
  {"surah_number": 48, "verse_number": 23, "content": "Derart ist Allahs Vorgehen, wie es zuvor gewesen ist; und nie wirst du in Allahs Vorgehen einen Wandel finden"},
  {"surah_number": 48, "verse_number": 24, "content": "Und Er ist es, Der ihre Hande von euch abhielt und eure Hande von ihnen in dem Tal von Makka, nachdem Er euch den Sieg uber sie gegeben hatte. Und Allah sieht alles, was ihr tut"},
  {
    "surah_number": 48,
    "verse_number": 25,
    "content":
        "Sie sind es, die unglaubig waren und euch von der heiligen Moschee fernhielten und die Opfertiere daran hinderten, ihren Bestimmungsort zu erreichen. Und ware es nicht wegen der glaubigen Manner und der glaubigen Frauen gewesen, die ihr nicht kanntet und die ihr vielleicht unwissentlich niedergetreten hattet, so daß ihr euch an ihnen versundigt hattet, (hattet ihr kampfen konnen) Damit Allah in Seine Gnade fuhre, wen Er will. Waren sie getrennt gewesen, hatten Wir sicher jene unter ihnen, die unglaubig waren, mit schmerzlicher Strafe bestraft"
  },
  {
    "surah_number": 48,
    "verse_number": 26,
    "content":
        "Als die Unglaubigen in ihren Herzen Parteilichkeit hegten - die Parteilichkeit der Gahiliya -, sandte Allah auf Seinen Gesandten und auf die Glaubigen Seine Ruhe hinab und ließ sie an dem Wort der Gottesfurcht festhalten, und sie hatten wohl Anspruch auf dieses (Wort) und waren seiner wurdig. Und Allah weiß uber alle Dinge Bescheid"
  },
  {
    "surah_number": 48,
    "verse_number": 27,
    "content":
        "Wahrlich, Allah hat Seinem Gesandten das Traumgesicht zu Wirklichkeit gemacht. Ihr werdet gewiß; denn Allah wollte (es so), in Sicherheit in die heilige Moschee, mit geschorenem Haupt oder kurzgeschnittenem Haar eintreten; ihr werdet keine Furcht haben. Doch Er wußte, was ihr nicht wußtet; und Er hat (euch) außer diesem (Sieg) einen nahen Sieg bestimmt"
  },
  {"surah_number": 48, "verse_number": 28, "content": "Er ist es, Der Seinen Gesandten mit der Fuhrung und der wahren Religion geschickt hat, auf daß Er sie uber jede andere Religion siegen lasse. Und Allah genugt als Zeuge"},
  {
    "surah_number": 48,
    "verse_number": 29,
    "content":
        "Muhammad ist der Gesandte Allahs. Und die, die mit ihm sind, sind hart gegen die Unglaubigen, doch barmherzig zueinander. Du siehst sie sich (im Gebet) beugen, niederwerfen (und) Allahs Huld und Wohlgefallen erstreben. Ihre Merkmale befinden sich auf ihren Gesichtern: die Spuren der Niederwerfungen. Das ist ihre Beschreibung in der Thora. Und ihre Beschreibung im Evangelium lautet: (Sie sind) gleich dem ausgesaten Samenkorn, das seinen Schoßling treibt, ihn dann stark werden laßt, dann wird er dick und steht fest auf seinem Halm, zur Freude derer, die die Saat ausgestreut haben - auf daß Er die Unglaubigen bei ihrem (Anblick) in Wut entbrennen lasse. Allah hat denjenigen, die glauben und gute Werke tun, Vergebung und einen gewaltigen Lohn verheißen"
  },
  {"surah_number": 49, "verse_number": 1, "content": "O ihr, die ihr glaubt, kommt nicht Allah und Seinem Gesandten zuvor, und furchtet Allah. Wahrlich, Allah ist Allhorend, Allwissend"},
  {"surah_number": 49, "verse_number": 2, "content": "O ihr, die ihr glaubt, erhebt nicht eure Stimmen uber die Stimme des Propheten und redet nicht so laut zu ihm, wie ihr zueinander redet, so daß eure Werke nicht eitel werden, ohne daß ihr es merkt"},
  {"surah_number": 49, "verse_number": 3, "content": "Wahrlich, diejenigen, die ihre Stimmen dampfen in der Gegenwart des Gesandten Allahs, sind es, deren Herzen Allah zur Gottesfurcht gelautert hat. Fur sie ist Vergebung und ein gewaltiger Lohn (bereitet)"},
  {"surah_number": 49, "verse_number": 4, "content": "Wahrlich, jene, die dich von außerhalb der Gemacher her rufen - die meisten von ihnen haben keinen Verstand"},
  {"surah_number": 49, "verse_number": 5, "content": "Und wenn sie sich geduldeten, bis du zu ihnen herauskamest, so ware es besser fur sie gewesen. Doch Allah ist Allvergebend, Barmherzig"},
  {"surah_number": 49, "verse_number": 6, "content": "O ihr, die ihr glaubt, wenn ein Frevler euch eine Kunde bringt, so vergewissert euch (dessen), damit ihr nicht anderen Leuten in Unwissenheit ein Unrecht zufugt und hernach bereuen mußt, was ihr getan habt"},
  {
    "surah_number": 49,
    "verse_number": 7,
    "content":
        "Und wisset, daß der Gesandte Allahs unter euch ist. Wurde er sich in so vielen Dingen nach euren Wunschen richten, wurdet ihr sicher in Bedrangnis kommen; jedoch Allah hat euch den Glauben lieb gemacht und sehr begehrenswert fur eure Herzen; und Er hat euch Unglauben, Widersetzlichkeit und Ungehorsam verabscheuenswert gemacht. Das sind jene, die der rechten Bahn folgen"
  },
  {"surah_number": 49, "verse_number": 8, "content": "durch die Huld und die Gnade Allahs. Und Allah ist Allwissend, Allweise"},
  {
    "surah_number": 49,
    "verse_number": 9,
    "content":
        "Und wenn zwei Parteien der Glaubigen einander bekampfen, dann stiftet Frieden zwischen ihnen; wenn jedoch eine von ihnen sich gegen die andere vergeht, so bekampft diejenige, die im Unrecht ist, bis sie sich Allahs Befehl fugt. Fugt sie sich, so stiftet in Ge rechtigkeit Frieden zwischen ihnen und seid gerecht. Wahrlich, Allah liebt die Gerechten"
  },
  {"surah_number": 49, "verse_number": 10, "content": "Die Glaubigen sind ja Bruder. So stiftet Frieden zwischen euren Brudern und furchtet Allah, auf daß euch Barmherzigkeit erwiesen werde"},
  {
    "surah_number": 49,
    "verse_number": 11,
    "content":
        "O ihr, die ihr glaubt! Laßt nicht eine Schar uber die andere spotten, vielleicht sind diese besser als jene; noch (lasset) Frauen uber (andere) Frauen (spotten), vielleicht sind diese besser als jene. Und verleumdet einander nicht und gebt einander keine Schimpfnamen. Schlimm ist die Bezeichnung der Sundhaftigkeit, nachdem man den Glauben (angenommen) hat, und jene die nicht umkehren - das sind die Ungerechten"
  },
  {
    "surah_number": 49,
    "verse_number": 12,
    "content":
        "O ihr, die ihr glaubt! Vermeidet haufigen Argwohn; denn mancher Argwohn ist Sunde. Und spioniert nicht und fuhrt keine uble Nachrede ubereinander. Wurde wohl einer von euch gerne das Fleisch seines toten Bruders essen? Sicher wurdet ihr es verabscheuen. So furchtet Allah. Wahrlich, Allah ist Gnadig, Barmherzig"
  },
  {
    "surah_number": 49,
    "verse_number": 13,
    "content": "O ihr Menschen, Wir haben euch aus Mann und Frau erschaffen und euch zu Volkern und Stammen gemacht, auf daß ihr einander erkennen moget. Wahrlich, vor Allah ist von euch der Angesehenste, welcher der Gottesfurchtigste ist. Wahrlich, Allah ist Allwissend, Allkundig"
  },
  {
    "surah_number": 49,
    "verse_number": 14,
    "content":
        "Die Wustenaraber sagen: \"Wir glauben.\" Sprich: \"lhr glaubt nicht; sagt vielmehr: \"Wir haben den Islam angenommen\", und der Glaube ist noch nicht in eure Herzen eingedrungen.\" Wenn ihr aber Allah und Seinem Gesandten gehorcht, so wird Er euch nichts von euren Werken verringern. Wahrlich, Allah ist Allvergebend, Barmherzig"
  },
  {"surah_number": 49, "verse_number": 15, "content": "Die Glaubigen sind nur jene, die an Allah und Seinen Gesandten glauben und dann nicht (am Glauben) zweifeln und sich mit ihrem Besitz und ihrem eigenen Leben fur Allahs Sache einsetzen. Das sind die Wahrhaftigen"},
  {"surah_number": 49, "verse_number": 16, "content": "Sprich: \"Wollt ihr Allah uber eure Religion belehren, wo Allah doch alles kennt, was in den Himmeln und was auf Erden ist, und Allah alle Dinge weiß"},
  {
    "surah_number": 49,
    "verse_number": 17,
    "content": "Sie halten es dir als eine Gnade vor, daß sie den Islam angenommen haben. Sprich: \"Haltet mir eure Annahme des Islam nicht als eine Gnade vor. Vielmehr halt Allah euch Seine Gnade vor, indem Er euch zu dem Glauben geleitet hat, wenn ihr wahrhaftig seid"
  },
  {"surah_number": 49, "verse_number": 18, "content": "Wahrlich, Allah kennt das Verborgene der Himmel und der Erde; und Allah sieht alles, was ihr tut"},
  {"surah_number": 50, "verse_number": 1, "content": "Qaf Beim ruhmvollen Quran"},
  {"surah_number": 50, "verse_number": 2, "content": "Aber sie staunen, daß zu ihnen ein Warner aus ihrer Mitte gekommen ist. Und die Unglaubigen sagen: \"Das ist eine merkwurdige Sache"},
  {"surah_number": 50, "verse_number": 3, "content": "Wie? Wenn wir tot sind und zu Staub geworden sind (, dann sollen wir wieder auferweckt werden)? Das ist eine Wiederkehr, die weit abseits liegt"},
  {"surah_number": 50, "verse_number": 4, "content": "Wir wissen wohl, was die Erde von ihnen wegnimmt, und bei Uns ist ein Buch, das alles aufzeichnet"},
  {"surah_number": 50, "verse_number": 5, "content": "Nein, sie haben die Wahrheit, als sie zu ihnen kam, fur eine Luge erklart, und nun befinden sie sich in einem Zustand der Verwirrung"},
  {"surah_number": 50, "verse_number": 6, "content": "Haben sie nicht zum Himmel uber ihnen emporgeschaut, wie Wir ihn erbaut und geschmuckt haben und daß er keine Risse aufweist"},
  {"surah_number": 50, "verse_number": 7, "content": "Und die Erde Wir haben sie ausgebreitet und feste Berge darauf gesetzt; und Wir ließen auf ihr all die herrlichen Paare hervorsprießen"},
  {"surah_number": 50, "verse_number": 8, "content": "als Aufklarung und Ermahnung fur jeden Diener, der sich bekehrt"},
  {"surah_number": 50, "verse_number": 9, "content": "Und vom Himmel senden Wir Wasser hernieder, das voll des Segens ist, und bringen damit Garten und Korn zum Ernten hervor"},
  {"surah_number": 50, "verse_number": 10, "content": "und hochragende Palmen mit dicht stehenden Fruchtscheiden"},
  {"surah_number": 50, "verse_number": 11, "content": "als Versorgung fur die Diener; und Wir beleben damit ein totes Land. So wird die Auferstehung sein"},
  {"surah_number": 50, "verse_number": 12, "content": "Schon vor ihnen leugneten das Volk Noahs und das Volk des Brunnens und die Tamud"},
  {"surah_number": 50, "verse_number": 13, "content": "und die `Ad und Pharao und die Bruder des Lot"},
  {"surah_number": 50, "verse_number": 14, "content": "und die Waldbewohner und das Volk von Tubba`. Alle diese haben die Gesandten der Luge bezichtigt. Darum war Meine Drohung in Erfullung gegangen"},
  {"surah_number": 50, "verse_number": 15, "content": "Sind Wir denn durch die erste Schopfung ermudet? Nein, aber sie sind sich nicht im klaren uber eine neue Schopfung"},
  {"surah_number": 50, "verse_number": 16, "content": "Und wahrlich, Wir erschufen den Menschen, und Wir wissen, was er in seinem Innern hegt; und Wir sind ihm naher als (seine) Hals schlagader"},
  {"surah_number": 50, "verse_number": 17, "content": "Wenn die zwei aufnehmenden (Engel etwas) niederschreiben, zur Rechten und zur Linken sitzend"},
  {"surah_number": 50, "verse_number": 18, "content": "spricht er kein Wort aus, ohne daß neben ihm ein Aufpasser ware, der stets bereit (ist, es aufzuzeichnen)"},
  {"surah_number": 50, "verse_number": 19, "content": "und es kam die Benommenheit des Todeskampfes in Gerechtigkeit: \"Das ist es, dem du zu entrinnen suchtest"},
  {"surah_number": 50, "verse_number": 20, "content": "Und es wird in den Sur gestoßen: \"Dies ist der Tag der Drohung"},
  {"surah_number": 50, "verse_number": 21, "content": "Und jede Seele ist gekommen; mit ihr wird ein Treiber und ein Zeuge sein"},
  {"surah_number": 50, "verse_number": 22, "content": "Wahrlich, du warst dessen ahnungslos gewesen; nun haben Wir deine Hulle von dir genommen, so daß dein Blick heute scharf ist"},
  {"surah_number": 50, "verse_number": 23, "content": "Und sein Gefahrte spricht: \"Hier (ist), was ich bereit habe"},
  {"surah_number": 50, "verse_number": 24, "content": "Werft ihr beide in Gahannam einen jeden undankbaren Hartnackigen"},
  {"surah_number": 50, "verse_number": 25, "content": "den Behinderer des Guten, den Ubertreter, den Zweifler"},
  {"surah_number": 50, "verse_number": 26, "content": "der einen anderen Gott neben Allah setzte. Werft denn ihr beide ihn in die schreckliche Pein"},
  {"surah_number": 50, "verse_number": 27, "content": "Sein Gefahrte spricht: \"O unser Herr, ich verfuhrte ihn nicht zur Emporung, sondern er selbst ging zu weit in die Irre"},
  {"surah_number": 50, "verse_number": 28, "content": "Er spricht: \"Streitet nicht vor Mir, wo Ich euch doch die Warnung im voraus gesandt hatte"},
  {"surah_number": 50, "verse_number": 29, "content": "Das Wort wird bei Mir nicht abgeandert, und Ich bin in nichts ungerecht gegen die Diener"},
  {"surah_number": 50, "verse_number": 30, "content": "An jenem Tage sprechen Wir zu Gahannam:: \"Bist du angefullt?\" und sie spricht: \"Gibt es noch mehr"},
  {"surah_number": 50, "verse_number": 31, "content": "Und das Paradies wird den Gottesfurchtigen nahe geruckt, (und es ist) nicht langer fern"},
  {"surah_number": 50, "verse_number": 32, "content": "Das ist es, was jedem von euch verheißen wurde, der reumutig war und sich in acht nahm"},
  {"surah_number": 50, "verse_number": 33, "content": "der den Allerbarmer im geheimen furchtete und mit reuigem Herzen (zu Ihm) kam"},
  {"surah_number": 50, "verse_number": 34, "content": "Geht darin (ins Paradies) ein in Frieden. Dies ist der Tag der Ewigkeit"},
  {"surah_number": 50, "verse_number": 35, "content": "Sie haben darin, was immer sie begehren, und bei Uns ist noch weit mehr"},
  {"surah_number": 50, "verse_number": 36, "content": "Und wieviele Generationen haben Wir schon vor ihnen vertilgt, die schlagkraftiger waren als sie und im Lande umherzogen! Gab es da ein Entrinnen"},
  {"surah_number": 50, "verse_number": 37, "content": "Hierin liegt wahrlich eine Ermahnung fur den, der ein Herz hat oder zuhort und bei der Sache ist"},
  {"surah_number": 50, "verse_number": 38, "content": "Und wahrlich, Wir erschufen die Himmel und die Erde und das, was zwischen beiden ist, in sechs Tagen, und keine Ermudung beruhrte Uns"},
  {"surah_number": 50, "verse_number": 39, "content": "So ertrage geduldig, was sie sagen, und lobpreise deinen Herrn vor dem Aufgang der Sonne und vor dem (Sonnen-)untergang"},
  {"surah_number": 50, "verse_number": 40, "content": "und lobpreise Ihn in einem Teil der Nacht und nach jedem Gebet"},
  {"surah_number": 50, "verse_number": 41, "content": "Und lausche am Tage, wenn der Rufer von einem nahen Ort (zum Jungsten Gericht) rufen wird"},
  {"surah_number": 50, "verse_number": 42, "content": "Der Tag, wenn sie in Wahrheit den Schrei horen werden - das wird dann der Tag der Auferstehung sein"},
  {"surah_number": 50, "verse_number": 43, "content": "Wahrlich, Wir allein machen lebendig und lassen sterben, und zu Uns ist die Heimkehr"},
  {"surah_number": 50, "verse_number": 44, "content": "An dem Tage, wenn sich die Erde spalten und freigeben wird, worauf sie sich beeilen, dann wird ein Versammeln vor sich gehen, (das) fur Uns ganz leicht (ist)"},
  {"surah_number": 50, "verse_number": 45, "content": "Wir wissen am besten, was sie sagen; und du hast keine Gewalt uber sie. Ermahne darum durch den Quran den, der Meine Drohung furchtet"},
  {"surah_number": 51, "verse_number": 1, "content": "Bei den heftig aufwirbelnden (Winden)"},
  {"surah_number": 51, "verse_number": 2, "content": "dann den lasttragenden (Wolken)"},
  {"surah_number": 51, "verse_number": 3, "content": "dann den leicht dahinziehenden (Schiffen)"},
  {"surah_number": 51, "verse_number": 4, "content": "und den, den Befehl ausfuhrenden (Engeln)"},
  {"surah_number": 51, "verse_number": 5, "content": "Wahrlich, was euch angedroht wird, ist wahr"},
  {"surah_number": 51, "verse_number": 6, "content": "Und das Gericht wird ganz sicher eintreffen"},
  {"surah_number": 51, "verse_number": 7, "content": "Und bei dem Himmel mit seiner makellosen Bauweise"},
  {"surah_number": 51, "verse_number": 8, "content": "Wahrlich, ihr seid in eine widerspruchsvolle Rede verwickelt"},
  {"surah_number": 51, "verse_number": 9, "content": "Der allein wird von der (Wahrheit) abgewendet, der sich davon abbringen laßt"},
  {"surah_number": 51, "verse_number": 10, "content": "Verflucht seien die, die Mutmaßungen anstellen"},
  {"surah_number": 51, "verse_number": 11, "content": "die in ihrer Verblendung achtlos sind"},
  {"surah_number": 51, "verse_number": 12, "content": "Sie fragen: \"Wann wird der Tag des Gerichts sein"},
  {"surah_number": 51, "verse_number": 13, "content": "Es wird der Tag sein, an dem sie im Feuer gepeinigt werden"},
  {"surah_number": 51, "verse_number": 14, "content": "Kostet nun eure Pein. Das ist es, was ihr zu beschleunigen wunschtet"},
  {"surah_number": 51, "verse_number": 15, "content": "Wahrlich, die Gottesfurchtigen werden inmitten von Garten und Quellen sein"},
  {"surah_number": 51, "verse_number": 16, "content": "(und das) empfangen, was ihr Herr ihnen gegeben hat, weil sie vordem Gutes zu tun pflegten"},
  {"surah_number": 51, "verse_number": 17, "content": "Sie schliefen nur einen kleinen Teil der Nacht"},
  {"surah_number": 51, "verse_number": 18, "content": "und vor Tagesanbruch suchten sie stets Vergebung"},
  {"surah_number": 51, "verse_number": 19, "content": "und von ihrem Vermogen war ein Anteil fur den Bittenden und den Unbemittelten bestimmt"},
  {"surah_number": 51, "verse_number": 20, "content": "Und auf Erden existieren Zeichen fur jene, die fest im Glauben sind"},
  {"surah_number": 51, "verse_number": 21, "content": "und in euch selber. Wollt ihr es denn nicht sehen"},
  {"surah_number": 51, "verse_number": 22, "content": "Und im Himmel ist eure Versorgung und das, was euch verheißen wird"},
  {"surah_number": 51, "verse_number": 23, "content": "Darum, bei dem Herrn des Himmels und der Erde - dies ist gewiß wahr, eben wie (es wahr ist,) daß ihr redet"},
  {"surah_number": 51, "verse_number": 24, "content": "Ist die Geschichte von Abrahams geehrten Gasten nicht zu dir ge kommen"},
  {"surah_number": 51, "verse_number": 25, "content": "Als sie bei ihm eintraten und sprachen: \"Frieden!\" sagte er: \"Frieden, unbekannte Leute"},
  {"surah_number": 51, "verse_number": 26, "content": "Und er ging unauffallig zu seinen Angehorigen und brachte ein gemastetes Kalb"},
  {"surah_number": 51, "verse_number": 27, "content": "Und er setzte es ihnen vor. Er sagte: \"Wollt ihr nicht essen"},
  {"surah_number": 51, "verse_number": 28, "content": "Es erfaßte ihn Furcht vor ihnen. Sie sprachen: \"Furchte dich nicht.\" Dann gaben sie ihm die frohe Nachricht von einem klugen Knaben"},
  {"surah_number": 51, "verse_number": 29, "content": "Da kam seine Frau in Aufregung heran, und sie schlug ihre Wange und sagte: \"(Ich bin doch) eine unfruchtbare alte Frau"},
  {"surah_number": 51, "verse_number": 30, "content": "Sie sprachen: \"Das ist so, aber dein Herr hat gesprochen. Wahrlich, Er ist der Allweise, der Allwissende"},
  {"surah_number": 51, "verse_number": 31, "content": "(Abraham) sagte: \"Wohlan, was ist euer Auftrag, ihr Boten"},
  {"surah_number": 51, "verse_number": 32, "content": "Sie sprachen: \"Wir sind zu einem schuldigen Volke entsandt worden"},
  {"surah_number": 51, "verse_number": 33, "content": "auf daß wir Steine von Ton auf sie niedersenden"},
  {"surah_number": 51, "verse_number": 34, "content": "die von deinem Herrn fur diejenigen gekennzeichnet sind, die nicht maßhalten"},
  {"surah_number": 51, "verse_number": 35, "content": "Und Wir ließen alle die Glaubigen, die dort waren, fortgehen"},
  {"surah_number": 51, "verse_number": 36, "content": "Wir fanden dort nur ein Haus von den Gottergebenen"},
  {"surah_number": 51, "verse_number": 37, "content": "Und Wir hinterließen darin ein Zeichen fur jene, die die qualvolle Strafe furchten"},
  {"surah_number": 51, "verse_number": 38, "content": "Und (ein weiteres Zeichen war) in Moses, als Wir ihn zu Pharao mit offenkundiger Beweismacht sandten"},
  {"surah_number": 51, "verse_number": 39, "content": "Da drehte er sich im Gefuhl seiner Starke um und sagte: \"(Dies ist) ein Zauberer oder ein Wahnsinniger"},
  {"surah_number": 51, "verse_number": 40, "content": "So erfaßten Wir ihn und seine Heerscharen und warfen sie ins Meer; und er ist zu tadeln"},
  {"surah_number": 51, "verse_number": 41, "content": "Und (ein Zeichen war) in den `Ad, als Wir den verheerenden Wind gegen sie sandten"},
  {"surah_number": 51, "verse_number": 42, "content": "er ließ nichts von alledem, was er heimsuchte, zuruck, ohne daß er alles gleichsam morsch gemacht hatte"},
  {"surah_number": 51, "verse_number": 43, "content": "Und (ein Zeichen war) in den Tamud, als zu ihnen gesprochen wurde: \"Genießt (das Leben) nur eine Weile"},
  {"surah_number": 51, "verse_number": 44, "content": "Doch sie trotzten dem Befehl ihres Herrn. So ereilte sie der Blitzschlag, als sie dahin schauten"},
  {"surah_number": 51, "verse_number": 45, "content": "Und sie vermochten nicht (wieder) aufzustehen, noch fanden sie Hilfe"},
  {"surah_number": 51, "verse_number": 46, "content": "Und vordem (vertilgten Wir) das Volk Noahs; denn sie waren ein frevelhaftes Volk"},
  {"surah_number": 51, "verse_number": 47, "content": "Und den Himmel haben Wir mit (Unserer) Kraft erbaut; und siehe, wie Wir ihn reichlich geweitet haben"},
  {"surah_number": 51, "verse_number": 48, "content": "Und die Erde haben Wir ausgebreitet, und wie schon breiten Wir aus"},
  {"surah_number": 51, "verse_number": 49, "content": "Und von jeglichem Wesen haben Wir Paare erschaffen, auf daß ihr euch vielleicht doch besinnen moget"},
  {"surah_number": 51, "verse_number": 50, "content": "Flieht darum zu Allah. Ich bin zu euch als deutlicher Warner von Ihm (gesandt worden)"},
  {"surah_number": 51, "verse_number": 51, "content": "Und setzt keinen anderen Gott neben Allah. Ich bin zu euch als deutlicher Warner von Ihm (gesandt worden)"},
  {"surah_number": 51, "verse_number": 52, "content": "So kam auch zu denen vor ihnen kein Gesandter, ohne daß sie gesagt hatten: \"(Dies ist) ein Zauberer oder ein Besessener"},
  {"surah_number": 51, "verse_number": 53, "content": "Haben sie es etwa einander ans Herz gelegt? Sie sind vielmehr aufsassige Leute"},
  {"surah_number": 51, "verse_number": 54, "content": "So kehre dich von ihnen ab; und dich soll kein Tadel treffen"},
  {"surah_number": 51, "verse_number": 55, "content": "Doch fahre fort, (sie) zu ermahnen; denn die Ermahnung nutzt den Glaubigen"},
  {"surah_number": 51, "verse_number": 56, "content": "Und Ich habe die Ginn und die Menschen nur darum erschaffen, damit sie Mir dienen (sollen)"},
  {"surah_number": 51, "verse_number": 57, "content": "Ich will keine Versorgung von ihnen haben, noch will Ich, daß sie Mir Speise geben"},
  {"surah_number": 51, "verse_number": 58, "content": "Wahrlich, Allah allein ist der Versorger, der Starke und Festigkeit besitzt"},
  {"surah_number": 51, "verse_number": 59, "content": "Und fur jene, die Unrecht tun, ist ein Anteil an Sundhaftigkeit (vorgesehen) wie der Anteil ihrer Gefahrten; sie sollen Mich darum nicht bitten, (die Strafe) zu beschleunigen"},
  {"surah_number": 51, "verse_number": 60, "content": "Wehe also denen, die unglaubig sind, ihres Tages wegen, der ihnen angedroht ist"},
  {"surah_number": 52, "verse_number": 1, "content": "Bei dem Berge"},
  {"surah_number": 52, "verse_number": 2, "content": "und bei dem Buche, das geschrieben ist"},
  {"surah_number": 52, "verse_number": 3, "content": "auf feinem, ausgebreitetem Pergament"},
  {"surah_number": 52, "verse_number": 4, "content": "und bei dem viel besuchten Haus"},
  {"surah_number": 52, "verse_number": 5, "content": "und bei der hochgehobenen Decke"},
  {"surah_number": 52, "verse_number": 6, "content": "und beim brennenden Meer"},
  {"surah_number": 52, "verse_number": 7, "content": "Wahrlich, die Strafe deines Herrn trifft sicher ein"},
  {"surah_number": 52, "verse_number": 8, "content": "Niemand kann sie abwehren"},
  {"surah_number": 52, "verse_number": 9, "content": "am Tage, an dem der Himmel heftig schwankt"},
  {"surah_number": 52, "verse_number": 10, "content": "und die Berge sich von der Stelle bewegen"},
  {"surah_number": 52, "verse_number": 11, "content": "Wehe also an jenem Tage den Leugnern"},
  {"surah_number": 52, "verse_number": 12, "content": "die mit eitler Rede ein Spiel treiben"},
  {"surah_number": 52, "verse_number": 13, "content": "Am Tage, wenn sie nach und nach in Gahannam gestoßen werden, (wird gesprochen)"},
  {"surah_number": 52, "verse_number": 14, "content": "Das ist das Feuer, das ihr zu leugnen pflegtet"},
  {"surah_number": 52, "verse_number": 15, "content": "Ist dies wohl ein Zauberwerk, oder seht ihr es nicht"},
  {"surah_number": 52, "verse_number": 16, "content": "Brennt darin; und ob ihr euch geduldig oder ungeduldig erweist, es wird fur euch gleich sein. Ihr werdet nur fur das entlohnt, was ihr getan habt"},
  {"surah_number": 52, "verse_number": 17, "content": "Wahrlich, die Gottesfurchtigen sind in Garten in (einem Zustand) der Wonne"},
  {"surah_number": 52, "verse_number": 18, "content": "sie erfreuen sich der Gaben, die ihr Herr ihnen beschert hat, und ihr Herr wird sie vor der Pein der Gahim bewahren"},
  {"surah_number": 52, "verse_number": 19, "content": "Esset und trinkt und lasset es euch wohl bekommen fur das, was ihr getan habt"},
  {"surah_number": 52, "verse_number": 20, "content": "Gelehnt werden sie sein auf Ruhebetten in Reihen. Und Wir werden sie mit Huris vermahlen"},
  {
    "surah_number": 52,
    "verse_number": 21,
    "content": "Und diejenigen, die glauben und (diejenigen, die ihnen) folgen im Glauben von ihren Nachkommen; mit denen wollen Wir ihre Nachkommen vereinen. Und Wir werden ihnen ihre Werke nicht im geringsten schmalern. Jedermann ist von dem abhangig, was er gewirkt hat"
  },
  {"surah_number": 52, "verse_number": 22, "content": "Und Wir werden sie mit Fruchten und Fleisch versorgen, wie sie es nur wunschen mogen"},
  {"surah_number": 52, "verse_number": 23, "content": "Dort greifen sie nach einem Becher, durch den man weder zu sinnlosem Gerede noch zu Sundhaftigkeit verleitet wird"},
  {"surah_number": 52, "verse_number": 24, "content": "und sie werden von ihren Junglingen bedient, als ob sie wohlverwahrte Perlen waren"},
  {"surah_number": 52, "verse_number": 25, "content": "Und sie wenden sich fragend aneinander"},
  {"surah_number": 52, "verse_number": 26, "content": "(und) sie sagen: \"Wahrlich, fruher bei unseren Angehorigen waren wir angstlich"},
  {"surah_number": 52, "verse_number": 27, "content": "doch Allah ist uns Gnadig gewesen und hat uns vor der Pein des sengenden Windes bewahrt"},
  {"surah_number": 52, "verse_number": 28, "content": "Wir pflegten vormals zu Ihm zu beten. Er ist der Gutige, der Barmherzige"},
  {"surah_number": 52, "verse_number": 29, "content": "So ermahne darum; auf Grund der Gnade deines Herrn bist du weder ein Wahrsager noch ein Besessener"},
  {"surah_number": 52, "verse_number": 30, "content": "Oder sagen sie etwa: \"(Er ist) ein Dichter; wir wollen das Unheil abwarten, das die Zeit uber ihn bringen wird"},
  {"surah_number": 52, "verse_number": 31, "content": "Sprich: \"Wartet nur! Ich bin mit euch bei den Wartenden"},
  {"surah_number": 52, "verse_number": 32, "content": "Ist es ihr Verstand, der ihnen solches anbefiehlt, oder sind sie ein widerspenstiges Volk"},
  {"surah_number": 52, "verse_number": 33, "content": "Oder sagen sie etwa: \"Er hat ihn (den Quran) aus der Luft gegriffen?\" Nein, aber sie wollen es nicht glauben"},
  {"surah_number": 52, "verse_number": 34, "content": "Laß sie denn eine Verkundigung gleicher Art beibringen, wenn sie die Wahrheit sagen"},
  {"surah_number": 52, "verse_number": 35, "content": "Oder sind sie wohl durch nichts erschaffen worden, oder sind sie gar selbst die Schopfer"},
  {"surah_number": 52, "verse_number": 36, "content": "Oder schufen sie die Himmel und die Erde? Nein, aber sie haben keine Gewißheit"},
  {"surah_number": 52, "verse_number": 37, "content": "Oder haben sie die Schatze deines Herrn zu eigen, oder sind sie die Herrschenden"},
  {"surah_number": 52, "verse_number": 38, "content": "Oder haben sie eine Leiter, auf der sie lauschen konnen? Dann moge ihr Lauscher einen deutlichen Beweis beibringen"},
  {"surah_number": 52, "verse_number": 39, "content": "Oder hat Er wohl die Tochter, und habt ihr die Sohne"},
  {"surah_number": 52, "verse_number": 40, "content": "Oder verlangst du einen Lohn von ihnen, so daß sie mit einer Schuldenlast beladen sind"},
  {"surah_number": 52, "verse_number": 41, "content": "Oder haben sie Kenntnis von dem Verborgenen, so daß sie (es) niederschreiben"},
  {"surah_number": 52, "verse_number": 42, "content": "Oder beabsichtigen sie, eine List anzuwenden? Die Unglaubigen sind es, die uberlistet werden"},
  {"surah_number": 52, "verse_number": 43, "content": "Oder haben sie einen (anderen) Gott statt Allah? Hoch Erhaben ist Allah uber all das, was sie (Ihm) beigesellen"},
  {"surah_number": 52, "verse_number": 44, "content": "Und sahen sie ein Stuck vom Himmel niederfallen, wurden sie sagen: \"(Das ist nur) ein Haufen Wolken"},
  {"surah_number": 52, "verse_number": 45, "content": "So laß sie allein, bis sie ihrem Tag begegnen, an dem sie vom Blitzschlag getroffen werden"},
  {"surah_number": 52, "verse_number": 46, "content": "dem Tag, an dem ihnen ihre List nicht im geringsten nutzt, noch wird ihnen geholfen werden"},
  {"surah_number": 52, "verse_number": 47, "content": "Und fur jene, die freveln, ist wahrlich (noch) eine Strafe außer dieser (bestimmt) Jedoch die meisten von ihnen wissen es nicht"},
  {"surah_number": 52, "verse_number": 48, "content": "So sei denn geduldig mit dem Befehl deines Herrn; denn du stehst unter Unserer Aufsicht; und lobpreise deinen Herrn, wenn du aufstehst"},
  {"surah_number": 52, "verse_number": 49, "content": "Und preise Ihn auch wahrend eines Teils der Nacht und beim Weichen der Sterne"},
  {"surah_number": 53, "verse_number": 1, "content": "Beim Stern, wenn er heruntersaust"},
  {"surah_number": 53, "verse_number": 2, "content": "Euer Gefahrte ist weder verwirrt, noch befindet er sich im Unrecht"},
  {"surah_number": 53, "verse_number": 3, "content": "noch spricht er aus Begierde"},
  {"surah_number": 53, "verse_number": 4, "content": "Vielmehr ist es eine Offenbarung, die (ihm) eingegeben wird"},
  {"surah_number": 53, "verse_number": 5, "content": "Gelehrt hat ihn einer, der uber starke Macht verfugt"},
  {"surah_number": 53, "verse_number": 6, "content": "dessen Macht sich auf alles erstreckt; darum stand er aufrecht da"},
  {"surah_number": 53, "verse_number": 7, "content": "als er am obersten Horizont war"},
  {"surah_number": 53, "verse_number": 8, "content": "Hierauf naherte er sich; kam dann nach unten"},
  {"surah_number": 53, "verse_number": 9, "content": "bis er eine Entfernung von zwei Bogenlangen erreicht hatte oder noch naher"},
  {"surah_number": 53, "verse_number": 10, "content": "Und er offenbarte Seinem Diener, was er offenbarte"},
  {"surah_number": 53, "verse_number": 11, "content": "(Und) dessen Herz hielt es nicht fur gelogen, was er sah"},
  {"surah_number": 53, "verse_number": 12, "content": "Wollt ihr da mit ihm uber das streiten, was er sah"},
  {"surah_number": 53, "verse_number": 13, "content": "Und er sah ihn bei einer anderen Begegnung"},
  {"surah_number": 53, "verse_number": 14, "content": "beim Lotusbaum am außersten Ende"},
  {"surah_number": 53, "verse_number": 15, "content": "an dem das Paradies der Geborgenheit liegt"},
  {"surah_number": 53, "verse_number": 16, "content": "Dabei uberflutete den Lotusbaum, was (ihn) uberflutete"},
  {"surah_number": 53, "verse_number": 17, "content": "Da wankte der Blick nicht, noch schweifte er ab"},
  {"surah_number": 53, "verse_number": 18, "content": "Wahrlich, er hatte eines der großten Zeichen seines Herrn gesehen"},
  {"surah_number": 53, "verse_number": 19, "content": "Was haltet ihr nun von Al-Lat und AI-`Uzza"},
  {"surah_number": 53, "verse_number": 20, "content": "und Manah, der dritten der anderen"},
  {"surah_number": 53, "verse_number": 21, "content": "Wie? Sollten euch die Knaben zustehen und Ihm die Madchen"},
  {"surah_number": 53, "verse_number": 22, "content": "Das ware wahrhaftig eine unbillige Verteilung"},
  {
    "surah_number": 53,
    "verse_number": 23,
    "content": "Wahrlich, es sind nur die Namen, die ihr euch ausgedacht habt ihr und eure Vater -, fur die Allah keinerlei Ermachtigung hinabgesandt hat. Sie folgen einem bloßen Wahn und ihren personlichen Neigungen, obwohl doch die Weisung ihres Herrn zu ihnen kam"
  },
  {"surah_number": 53, "verse_number": 24, "content": "Kann der Mensch denn haben, was er nur wunscht"},
  {"surah_number": 53, "verse_number": 25, "content": "Aber Allahs ist das Diesseits und das Jenseits"},
  {"surah_number": 53, "verse_number": 26, "content": "Und so mancher Engel ist in den Himmeln, dessen Fursprache nichts nutzen wird, es sei denn, nachdem Allah dem die Erlaubnis (dazu) gegeben hat, dem Er will und der Ihm beliebt"},
  {"surah_number": 53, "verse_number": 27, "content": "Solche, die nicht an das Jenseits glauben, benennen die Engel mit weiblichen Namen"},
  {"surah_number": 53, "verse_number": 28, "content": "Jedoch sie besitzen kein Wissen hiervon. Sie gehen nur Vermutungen nach; und Vermutungen ersetzen nicht im geringsten die Wahrheit"},
  {"surah_number": 53, "verse_number": 29, "content": "Darum wende dich von dem ab, der Unserer Ermahnung den Rucken kehrt und nichts als das Leben in dieser Welt begehrt"},
  {"surah_number": 53, "verse_number": 30, "content": "Das ist die Summe ihres Wissens. Wahrlich, dein Herr kennt denjenigen recht wohl, der von Seinem Wege abirrt, und Er kennt auch jenen wohl, der den Weg befolgt"},
  {"surah_number": 53, "verse_number": 31, "content": "Und Allahs ist, was in den Himmeln und was auf Erden ist, auf daß Er denen, die Boses tun, ihren Lohn fur das gebe, was sie gewirkt haben; und auf daß Er die, die Gutes tun, mit dem Allerbesten belohne"},
  {
    "surah_number": 53,
    "verse_number": 32,
    "content":
        "Jene, die die großen Sunden und Schandlichkeiten meiden - mit Ausnahme der Lappalien - wahrlich, dein Herr ist von weitumfassender Vergebung. Er kennt euch sehr wohl; als Er euch aus der Erde hervorbrachte, und als ihr Embryos in den Leibern eurer Mutter waret. Darum erklart euch nicht selber als rein. Er kennt diejenigen am besten, die (Ihn) furchten"
  },
  {"surah_number": 53, "verse_number": 33, "content": "Siehst du den, der sich abkehrt"},
  {"surah_number": 53, "verse_number": 34, "content": "und wenig gibt und geizt"},
  {"surah_number": 53, "verse_number": 35, "content": "Hat er wohl die Kenntnis des Verborgenen, so daß er es sehen konnte"},
  {"surah_number": 53, "verse_number": 36, "content": "Oder ist ihm nicht erzahlt worden, was in den Schriftblattern Moses' steht"},
  {"surah_number": 53, "verse_number": 37, "content": "und Abrahams, der (die Gebote) erfullte"},
  {"surah_number": 53, "verse_number": 38, "content": "(Geschrieben steht,) daß keine lasttragende (Seele) die Last einer anderen tragen soll"},
  {"surah_number": 53, "verse_number": 39, "content": "und daß dem Menschen nichts anderes zuteil wird als das, wonach er strebt"},
  {"surah_number": 53, "verse_number": 40, "content": "und daß sein Streben bald sichtbar wird"},
  {"surah_number": 53, "verse_number": 41, "content": "Dann wird er dafur mit reichlichem Lohn belohnt werden"},
  {"surah_number": 53, "verse_number": 42, "content": "Und (es steht geschrieben,) daß es bei deinem Herrn enden wird"},
  {"surah_number": 53, "verse_number": 43, "content": "und daß Er es ist, Der zum Lachen und Weinen bringt"},
  {"surah_number": 53, "verse_number": 44, "content": "und daß Er es ist, Der sterben laßt und lebendig macht"},
  {"surah_number": 53, "verse_number": 45, "content": "und daß Er die Paare (als) mannliche und weibliche (Wesen) erschaffen hat"},
  {"surah_number": 53, "verse_number": 46, "content": "aus einem Samentropfen, der ausgestoßen wird"},
  {"surah_number": 53, "verse_number": 47, "content": "und daß Ihm die zweite Schopfung obliegt"},
  {"surah_number": 53, "verse_number": 48, "content": "und daß Er allein reich und arm macht"},
  {"surah_number": 53, "verse_number": 49, "content": "und daß Er der Herr des Sirius ist"},
  {"surah_number": 53, "verse_number": 50, "content": "und daß Er die einstigen `Ad vernichtete"},
  {"surah_number": 53, "verse_number": 51, "content": "und die Tamud, und keinen verschonte"},
  {"surah_number": 53, "verse_number": 52, "content": "und (daß Er) vordem das Volk Noahs (vernichtete) - wahrlich, sie waren hochst ungerecht und widerspenstig"},
  {"surah_number": 53, "verse_number": 53, "content": "Und Er ließ die verderbten Stadte einsturzen"},
  {"surah_number": 53, "verse_number": 54, "content": "so daß sie bedeckte, was (sie) bedeckte"},
  {"surah_number": 53, "verse_number": 55, "content": "Welche Wohltaten deines Herrn willst du denn bestreiten"},
  {"surah_number": 53, "verse_number": 56, "content": "Dies ist ein Warner wie die fruheren Warner"},
  {"surah_number": 53, "verse_number": 57, "content": "Die Stunde naht"},
  {"surah_number": 53, "verse_number": 58, "content": "Keiner außer Allah kann sie abwenden"},
  {"surah_number": 53, "verse_number": 59, "content": "Wundert ihr euch uber diese Verkundigung"},
  {"surah_number": 53, "verse_number": 60, "content": "Und ihr lacht; aber Weinen tut ihr nicht"},
  {"surah_number": 53, "verse_number": 61, "content": "Und wollt ihr achtlos (hinsichtlich dieser Verkundigung) bleiben"},
  {"surah_number": 53, "verse_number": 62, "content": "So fallt denn vor Allah anbetend nieder und dient (Ihm)"},
  {"surah_number": 54, "verse_number": 1, "content": "Die Stunde ist nahe gekommen, und der Mond hat sich gespalten"},
  {"surah_number": 54, "verse_number": 2, "content": "Doch wenn sie ein Zeichen sehen, wenden sie sich ab und sagen: \"(Dies ist) ein ewiges Zauberwerk"},
  {"surah_number": 54, "verse_number": 3, "content": "Und sie leugnen und folgen ihren Gelusten. Doch alles steht endgultig fest"},
  {"surah_number": 54, "verse_number": 4, "content": "Und zu ihnen kamen schon einige Geschichten, die abschreckende Warnungen enthalten"},
  {"surah_number": 54, "verse_number": 5, "content": "vollendete Weisheit; allein selbst die Warnungen richteten (bei ihnen) nichts aus"},
  {"surah_number": 54, "verse_number": 6, "content": "Darum wende dich von ihnen ab. Am Tage, an dem der Rufer (sie) zu schlimmem Geschehen rufen wird"},
  {"surah_number": 54, "verse_number": 7, "content": "werden sie mit niedergeschlagenen Blicken aus den Grabern hervorkommen, als waren sie weithin zerstreute Heuschrecken"},
  {"surah_number": 54, "verse_number": 8, "content": "(und) hasten dem Rufer entgegen. Die Unglaubigen werden sagen: \"Das ist ein schrecklicher Tag"},
  {"surah_number": 54, "verse_number": 9, "content": "Vor ihnen schon leugnete das Volk Noahs; ja, sie leugneten Unseren Diener und sagten: \"(Er ist) ein Besessener.\" Und er war eingeschuchtert"},
  {"surah_number": 54, "verse_number": 10, "content": "Da betete er zu seinem Herrn: \"lch bin uberwaltigt, so hilf Du (mir)"},
  {"surah_number": 54, "verse_number": 11, "content": "So offneten Wir denn die Tore des Himmels dem stromenden Wasser"},
  {"surah_number": 54, "verse_number": 12, "content": "und aus der Erde ließen Wir Quellen hervorsprudeln; so vereinigte sich das Wasser zu einem beschlossenen Zweck"},
  {"surah_number": 54, "verse_number": 13, "content": "Und Wir trugen ihn auf einem Gefuge aus Planken und Nageln"},
  {"surah_number": 54, "verse_number": 14, "content": "Es trieb unter Unserer Aufsicht dahin, als Belohnung fur denjenigen, der Undank geerntet hatte"},
  {"surah_number": 54, "verse_number": 15, "content": "Und Wir machten es zu einem Zeichen. Gibt es also einen, der daraus eine Lehre zieht"},
  {"surah_number": 54, "verse_number": 16, "content": "Wie war denn Meine Strafe und Meine Warnung"},
  {"surah_number": 54, "verse_number": 17, "content": "Und wahrlich, Wir haben den Quran zur Ermahnung leicht gemacht. Gibt es also einen, der ermahnt sein mag"},
  {"surah_number": 54, "verse_number": 18, "content": "Die `Ad verleugneten (die Warner) Wie war denn Meine Strafe und Meine Warnung"},
  {"surah_number": 54, "verse_number": 19, "content": "Wir sandten gegen sie an einem unheilvollen Tag einen eiskalten Sturmwind, (der) ununterbrochen wutete"},
  {"surah_number": 54, "verse_number": 20, "content": "Er raffte die Menschen dahin, als waren sie Stamme schon entwurzelter Palmen"},
  {"surah_number": 54, "verse_number": 21, "content": "Wie war denn Meine Strafe und Meine Warnung"},
  {"surah_number": 54, "verse_number": 22, "content": "Und wahrlich, Wir haben den Quran zur Ermahnung leicht gemacht. Gibt es also einen, der ermahnt sein mag"},
  {"surah_number": 54, "verse_number": 23, "content": "Die Tamud verleugneten die Warner"},
  {"surah_number": 54, "verse_number": 24, "content": "und sie sagten: \"Wie? Einem Menschen aus unserer Mitte, einem einzelnen sollen wir folgen? Dann befanden wir uns wahrlich im Irrtum und in brennender Pein"},
  {"surah_number": 54, "verse_number": 25, "content": "Ist die Ermahnung ihm (allein) von uns allen gegeben worden? Nein, er ist ein unverschamter Lugner"},
  {"surah_number": 54, "verse_number": 26, "content": "Morgen werden sie erfahren, wer der unverschamte Lugner ist"},
  {"surah_number": 54, "verse_number": 27, "content": "Wir werden die Kamelstute als eine Prufung fur sie schicken. Darum beobachte sie und harre in Geduld aus"},
  {"surah_number": 54, "verse_number": 28, "content": "Und verkunde ihnen, daß das Wasser zwischen ihnen geteilt ist, (also) soll jede Trinkzeit eingehalten werden"},
  {"surah_number": 54, "verse_number": 29, "content": "Doch sie riefen ihren Gefahrten, und er packte (sie) und schnitt (ihr) die Sehnen durch"},
  {"surah_number": 54, "verse_number": 30, "content": "Wie war also Meine Strafe und Meine Warnung"},
  {"surah_number": 54, "verse_number": 31, "content": "Wahrlich, Wir entsandten einen einzigen Schrei auf sie, und sie wurden wie durre, zertretene Stoppeln"},
  {"surah_number": 54, "verse_number": 32, "content": "Und wahrlich, Wir haben den Quran zur Ermahnung leicht gemacht. Gibt es also einen, der ermahnt sein mag"},
  {"surah_number": 54, "verse_number": 33, "content": "Das Volk Lots verleugnete die Warner"},
  {"surah_number": 54, "verse_number": 34, "content": "Da sandten Wir einen Steinregen uber sie; ausgenommen (davon war) die Familie Lots, die Wir vor dem Morgengrauen erretteten"},
  {"surah_number": 54, "verse_number": 35, "content": "als eine Gnade von Uns. So belohnen Wir den, der dankbar ist"},
  {"surah_number": 54, "verse_number": 36, "content": "Und er hatte sie in der Tat vor Unserer Strafe gewarnt, sie aber stritten mit den Warnern"},
  {"surah_number": 54, "verse_number": 37, "content": "Und sie versuchten ihn zu uberreden, ihnen seine Gaste auszuliefern. Daher blendeten Wir ihre Augen (und sprachen): \"Kostet nun Meine Strafe und Meine Warnung"},
  {"surah_number": 54, "verse_number": 38, "content": "Und in der Morgenfruhe ereilte sie eine dauernde Strafe"},
  {"surah_number": 54, "verse_number": 39, "content": "So kostet nun Meine Strafe und Meine Androhung"},
  {"surah_number": 54, "verse_number": 40, "content": "Und wahrlich, Wir haben den Quran zur Ermahnung leicht gemacht. Gibt es also einen, der ermahnt sein mag"},
  {"surah_number": 54, "verse_number": 41, "content": "Und zu dem Volke Pharaos kamen (ebenfalls) die Warner"},
  {"surah_number": 54, "verse_number": 42, "content": "Sie aber hielten all Unsere Zeichen fur Lugen; darum erfaßten Wir sie mit dem Griff eines Erhabenen, Allmachtigen"},
  {"surah_number": 54, "verse_number": 43, "content": "Sind die Unglaubigen unter euch etwa besser als jene? Oder habt ihr in den Schriften einen Freibrief"},
  {"surah_number": 54, "verse_number": 44, "content": "Oder sagen sie etwa: \"Wir sind allesamt eine siegreiche Schar"},
  {"surah_number": 54, "verse_number": 45, "content": "Die Scharen werden alle besiegt werden, und sie werden in die Flucht geschlagen"},
  {"surah_number": 54, "verse_number": 46, "content": "Nein, die Stunde ist ihr Termin; und die Stunde ist noch unheilvoller und bitterer"},
  {"surah_number": 54, "verse_number": 47, "content": "Wahrlich, die Schuldigen befinden sich im Irrtum und in brennender Pein"},
  {"surah_number": 54, "verse_number": 48, "content": "Am Tage, wo sie auf ihren Ge sichtern ins Feuer geschleift werden(, heißt es): \"Fuhlt die Beruhrung der Saqar"},
  {"surah_number": 54, "verse_number": 49, "content": "Wir haben jedoch ein jegliches Ding nach (rechtem) Maß geschaffen"},
  {"surah_number": 54, "verse_number": 50, "content": "Und Unser Befehl gleicht einem einzigen Akt - (so schnell) wie ein (einziger) Augenblick"},
  {"surah_number": 54, "verse_number": 51, "content": "Und Wir haben bereits Leute wie euch vertilgt. Doch gibt es (wenigstens) einen, der ermahnt sein mag"},
  {"surah_number": 54, "verse_number": 52, "content": "Und alles, was sie getan haben, steht in den Buchern"},
  {"surah_number": 54, "verse_number": 53, "content": "Und alles Kleine und Große ist niedergeschrieben"},
  {"surah_number": 54, "verse_number": 54, "content": "Wahrlich, die Gottesfurchtigen sind inmitten von Garten an Bachen"},
  {"surah_number": 54, "verse_number": 55, "content": "in einem wurdigen Wohnsitz in der Gegenwart eines Machtigen Konigs"},
  {"surah_number": 55, "verse_number": 1, "content": "Der Allerbarmer"},
  {"surah_number": 55, "verse_number": 2, "content": "hat den Quran gelehrt"},
  {"surah_number": 55, "verse_number": 3, "content": "Er hat den Menschen erschaffen"},
  {"surah_number": 55, "verse_number": 4, "content": "Er hat ihm das deutliche Reden beigebracht"},
  {"surah_number": 55, "verse_number": 5, "content": "Die Sonne und der Mond kreisen nach einer festgesetzten Berechnung"},
  {"surah_number": 55, "verse_number": 6, "content": "Und die Sterne und Baume fallen (vor Ihm) anbetend nieder"},
  {"surah_number": 55, "verse_number": 7, "content": "Und den Himmel hat Er emporgehoben. Und Er hat das (richtige) Abwiegen zum Gebot gemacht"},
  {"surah_number": 55, "verse_number": 8, "content": "auf daß ihr euch in der Waage nicht vergeht"},
  {"surah_number": 55, "verse_number": 9, "content": "so setzt das Gewicht in gerechter Weise und betrugt nicht beim Wiegen"},
  {"surah_number": 55, "verse_number": 10, "content": "Und Er hat die Erde fur die Geschopfe bereitet"},
  {"surah_number": 55, "verse_number": 11, "content": "auf ihr sind Fruchte und Palmen mit Fruchthullen"},
  {"surah_number": 55, "verse_number": 12, "content": "und Korn auf Halmen und duftende Pflanzen"},
  {"surah_number": 55, "verse_number": 13, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 14, "content": "Er hat den Menschen aus Ton erschaffen, gleich einer Topferware"},
  {"surah_number": 55, "verse_number": 15, "content": "Und die Ginn schuf Er aus rauchloser Feuerflamme"},
  {"surah_number": 55, "verse_number": 16, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 17, "content": "(Er ist) der Herr der beiden Osten und der Herr der beiden Westen"},
  {"surah_number": 55, "verse_number": 18, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 19, "content": "Er hat den beiden Gewassern, die einander begegnen, freien Lauf gelassen"},
  {"surah_number": 55, "verse_number": 20, "content": "Zwischen ihnen steht eine Scheidewand, so daß sie nicht ineinander ubergreifen"},
  {"surah_number": 55, "verse_number": 21, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 22, "content": "Perlen kommen aus beiden hervor und Korallen"},
  {"surah_number": 55, "verse_number": 23, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 24, "content": "Und Sein sind die hochragenden Schiffe auf dem Meer, gleich Berge"},
  {"surah_number": 55, "verse_number": 25, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 26, "content": "Alles, was auf (Erden) ist, wird vergehen"},
  {"surah_number": 55, "verse_number": 27, "content": "Aber das Angesicht deines Herrn bleibt bestehen - des Herrn der Majestat und der Ehre"},
  {"surah_number": 55, "verse_number": 28, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 29, "content": "Ihn bitten alle, die in den Himmeln und auf Erden sind. Er ist tagtaglich in jeglichem Einsatz"},
  {"surah_number": 55, "verse_number": 30, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 31, "content": "Bald aber werden Wir Uns um euch kummern, ihr beiden Gewichtigen"},
  {"surah_number": 55, "verse_number": 32, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 33, "content": "O Wesen der Ginn und der Menschen! Wenn ihr imstande seid, die Grenzen der Himmel und der Erde zu durchdringen, dann dringt hindurch. Doch ihr werdet nicht imstande sein durchzudringen, es sei denn mit der Macht (eures Herrn)"},
  {"surah_number": 55, "verse_number": 34, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 35, "content": "Entsandt werden soll gegen euch eine lodernde Flamme aus Feuer und Kupfer; dann werdet ihr beide euch nicht zu helfen wissen"},
  {"surah_number": 55, "verse_number": 36, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 37, "content": "Und wenn der Himmel sich spaltet und so geworden ist wie eine Rose, gleich rotem Leder"},
  {"surah_number": 55, "verse_number": 38, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 39, "content": "An jenem Tage werden weder Menschen noch Ginn nach ihren Sunden befragt"},
  {"surah_number": 55, "verse_number": 40, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 41, "content": "Die Schuldigen werden an ihren Merkmalen erkannt werden, und sie werden an ihren Stirnlocken und Fußen erfaßt werden"},
  {"surah_number": 55, "verse_number": 42, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 43, "content": "Das ist Gahannam, die die Schuldigen leugnen"},
  {"surah_number": 55, "verse_number": 44, "content": "Zwischen ihr und siedend heißem Wasser werden sie die Runde machen"},
  {"surah_number": 55, "verse_number": 45, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 46, "content": "Und dem aber, der sich vor der Gegenwart seines Herrn furchtet, werden zwei Garten zuteil sein"},
  {"surah_number": 55, "verse_number": 47, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 48, "content": "In beiden (Garten) wachsen vielerlei Baume"},
  {"surah_number": 55, "verse_number": 49, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 50, "content": "In beiden (Garten) fließen zwei Quellen"},
  {"surah_number": 55, "verse_number": 51, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 52, "content": "Darin wird es von jeglicher Fruchtart zwei Arten geben"},
  {"surah_number": 55, "verse_number": 53, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 54, "content": "Sie ruhen auf Kissen, die mit Brokat gefuttert sind. Und die Fruchte der beiden Garten sind nahe zur Hand"},
  {"surah_number": 55, "verse_number": 55, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 56, "content": "Darin sind (keusche Madchen) mit zuchtigem Blick, die weder Menschen noch Ginn vor ihnen beruhrt haben"},
  {"surah_number": 55, "verse_number": 57, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 58, "content": "(Es scheint,) als waren sie (die Madchen) Rubine und Korallen"},
  {"surah_number": 55, "verse_number": 59, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 60, "content": "Kann der Lohn fur Gute (etwas) anderes sein als Gute"},
  {"surah_number": 55, "verse_number": 61, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 62, "content": "Und neben diesen beiden gibt es noch zwei (andere) Garten"},
  {"surah_number": 55, "verse_number": 63, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 64, "content": "(Es sind Garten) mit dunkelgrunem Blattwerk"},
  {"surah_number": 55, "verse_number": 65, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 66, "content": "Darin fließen zwei Quellen, die reichlich Wasser spenden"},
  {"surah_number": 55, "verse_number": 67, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 68, "content": "In beiden (Garten) sind Fruchte und Dattelpalmen und Granatapfel"},
  {"surah_number": 55, "verse_number": 69, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 70, "content": "Darin sind gute und schone (Madchen)"},
  {"surah_number": 55, "verse_number": 71, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 72, "content": "(Es sind) Huris, wohlbehutet in Zelten"},
  {"surah_number": 55, "verse_number": 73, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 74, "content": "Vor ihnen haben weder Menschen noch Ginn sie (die Madchen) beruhrt"},
  {"surah_number": 55, "verse_number": 75, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 76, "content": "Sie ruhen auf grunen Kissen und schonen Teppichen"},
  {"surah_number": 55, "verse_number": 77, "content": "Welche der Wohltaten eures Herrn wollt ihr beide da leugnen"},
  {"surah_number": 55, "verse_number": 78, "content": "Segensreich ist der Name deines Herrn, des Herrn der Majestat und Ehre"},
  {"surah_number": 56, "verse_number": 1, "content": "Wenn das Ereignis eintrifft"},
  {"surah_number": 56, "verse_number": 2, "content": "gibt es keinen, der sein Eintreffen verleugnen konnte"},
  {"surah_number": 56, "verse_number": 3, "content": "Dann wird es (die einen) erniedrigen, (andere) wird es erhohen"},
  {"surah_number": 56, "verse_number": 4, "content": "Wenn die Erde heftig geschuttelt wird"},
  {"surah_number": 56, "verse_number": 5, "content": "und die Berge ganzlich zerbrockelt werden"},
  {"surah_number": 56, "verse_number": 6, "content": "dann sollen sie zu weithin zerstreutem Staub werden"},
  {"surah_number": 56, "verse_number": 7, "content": "Und ihr sollt in drei Gattungen (gegliedert) werden"},
  {"surah_number": 56, "verse_number": 8, "content": "(In) die zur Rechten was (wißt ihr) von denen die zur Rechten sein werden"},
  {"surah_number": 56, "verse_number": 9, "content": "Und (in) die zur Linken - was (wißt ihr) von denen, die zur Linken sein werden"},
  {"surah_number": 56, "verse_number": 10, "content": "Und (in) die Vordersten - (sie) werden die Vordersten sein"},
  {"surah_number": 56, "verse_number": 11, "content": "Das sind die, die Allah nahe sein werden"},
  {"surah_number": 56, "verse_number": 12, "content": "in den Garten der Wonne"},
  {"surah_number": 56, "verse_number": 13, "content": "(Dies sind) eine große Schar der Fruheren"},
  {"surah_number": 56, "verse_number": 14, "content": "und einige wenige der Spateren"},
  {"surah_number": 56, "verse_number": 15, "content": "Auf Polstern, die mit Gold durchwoben sind"},
  {"surah_number": 56, "verse_number": 16, "content": "lehnen (sie) auf diesen einander gegenuber"},
  {"surah_number": 56, "verse_number": 17, "content": "Bedient werden sie von Junglingen, die nicht altern"},
  {"surah_number": 56, "verse_number": 18, "content": "mit Bechern und Kru gen aus einer fließenden Quelle"},
  {"surah_number": 56, "verse_number": 19, "content": "Keinen Kopfschmerz werden sie davon bekommen, noch werden sie das Bewußtsein verlieren"},
  {"surah_number": 56, "verse_number": 20, "content": "Und Fruchte, die sie sich wunschen"},
  {"surah_number": 56, "verse_number": 21, "content": "und Fleisch vom Geflugel, das sie begehren"},
  {"surah_number": 56, "verse_number": 22, "content": "und Huris"},
  {"surah_number": 56, "verse_number": 23, "content": "wohlbehuteten Perlen gleich"},
  {"surah_number": 56, "verse_number": 24, "content": "(werden sie erhalten) als Belohnung fur das, was sie zu tun pflegten"},
  {"surah_number": 56, "verse_number": 25, "content": "Sie werden dort weder leeres Gerede noch Anschuldigung der Sunde horen"},
  {"surah_number": 56, "verse_number": 26, "content": "nur das Wort: \"Frieden, Frieden"},
  {"surah_number": 56, "verse_number": 27, "content": "Und die zur Rechten - was (wißt ihr) von denen, die zur Rechten sein werden"},
  {"surah_number": 56, "verse_number": 28, "content": "(Sie werden) unter dornlosen Lotusbaumen (sein)"},
  {"surah_number": 56, "verse_number": 29, "content": "und gebuschelten Bananen"},
  {"surah_number": 56, "verse_number": 30, "content": "und endlosem Schatten"},
  {"surah_number": 56, "verse_number": 31, "content": "bei fließendem Wasser"},
  {"surah_number": 56, "verse_number": 32, "content": "und vielen Fruchten"},
  {"surah_number": 56, "verse_number": 33, "content": "die weder zu Ende gehen, noch fur verboten erklart werden"},
  {"surah_number": 56, "verse_number": 34, "content": "und auf erhohten Ruhekissen"},
  {"surah_number": 56, "verse_number": 35, "content": "Wir haben sie (die Huris) in herrlicher Schopfung gestaltet"},
  {"surah_number": 56, "verse_number": 36, "content": "und sie zu Jungfrauen gemacht"},
  {"surah_number": 56, "verse_number": 37, "content": "zu liebevollen Altersgenossinnen"},
  {"surah_number": 56, "verse_number": 38, "content": "derer zur Rechten"},
  {"surah_number": 56, "verse_number": 39, "content": "(Dies sind) eine große Schar der Fruheren"},
  {"surah_number": 56, "verse_number": 40, "content": "und eine große Schar der Spateren"},
  {"surah_number": 56, "verse_number": 41, "content": "Und die zur Linken - was (wißt ihr) von denen, die zur Linken sein werden"},
  {"surah_number": 56, "verse_number": 42, "content": "(Sie werden) inmitten von gluhenden Winden und siedendem Wasser (sein)"},
  {"surah_number": 56, "verse_number": 43, "content": "und im Schatten schwarzen Rauches"},
  {"surah_number": 56, "verse_number": 44, "content": "der weder kuhl noch erfrischend ist"},
  {"surah_number": 56, "verse_number": 45, "content": "Vor diesem (Schicksal) wurden sie in der Tat mit Wohlleben verwohnt"},
  {"surah_number": 56, "verse_number": 46, "content": "und (sie) verharrten in großer Sunde"},
  {"surah_number": 56, "verse_number": 47, "content": "Und sie pflegten zu sagen: \"Wie? Wenn wir tot sind und zu Staub und Gebeinen geworden sind, dann sollen wir wirklich auferweckt werden"},
  {"surah_number": 56, "verse_number": 48, "content": "Und unsere Vorvater auch"},
  {"surah_number": 56, "verse_number": 49, "content": "Sprich: \"Wahrlich, die Fruheren und die Spateren"},
  {"surah_number": 56, "verse_number": 50, "content": "werden alle zur gesetzten Frist eines bestimmten Tages versammelt werden"},
  {"surah_number": 56, "verse_number": 51, "content": "Dann, o ihr Irregegangenen und Leugner"},
  {"surah_number": 56, "verse_number": 52, "content": "sollt ihr vom Baume Zaqqum essen"},
  {"surah_number": 56, "verse_number": 53, "content": "und damit eure Bauche fullen"},
  {"surah_number": 56, "verse_number": 54, "content": "und darauf von sie dendem Wasser trinken"},
  {"surah_number": 56, "verse_number": 55, "content": "Dann trinkt (ihr,) wie die durstigen Kamele trinken"},
  {"surah_number": 56, "verse_number": 56, "content": "Das wird ihre Bewirtung am Tage des Gerichts sein"},
  {"surah_number": 56, "verse_number": 57, "content": "Wir haben euch erschaffen. Warum wollt ihr da nicht die Wahrheit zugeben"},
  {"surah_number": 56, "verse_number": 58, "content": "Habt ihr betrachtet, was euch an Sperma entfließt"},
  {"surah_number": 56, "verse_number": 59, "content": "Erschafft ihr es oder sind Wir die Schopfer"},
  {"surah_number": 56, "verse_number": 60, "content": "Wir haben fur euch den Tod verordnet, und Wir sind nicht unfahig dazu"},
  {"surah_number": 56, "verse_number": 61, "content": "an eurer Stelle andere wie euch hervorzubringen und euch in einen Zustand zu versetzen, den ihr nicht kennt"},
  {"surah_number": 56, "verse_number": 62, "content": "Und ihr kennt doch gewiß die erste Schopfung. Warum also wollt ihr euch nicht besinnen"},
  {"surah_number": 56, "verse_number": 63, "content": "Habt ihr betrachtet, was ihr aussat"},
  {"surah_number": 56, "verse_number": 64, "content": "Seid ihr es, die es wachsen lassen, oder lassen Wir es wachsen"},
  {"surah_number": 56, "verse_number": 65, "content": "Wollten Wir, konnten Wir es in bruchiges Zeug verwandeln; dann wurdet ihr nicht aufhoren, euch zu beklagen"},
  {"surah_number": 56, "verse_number": 66, "content": "Wir sind zugrunde gerichtet"},
  {"surah_number": 56, "verse_number": 67, "content": "Nein, wir sind beraubt"},
  {"surah_number": 56, "verse_number": 68, "content": "Habt ihr das Was ser betrachtet, das ihr trinkt"},
  {"surah_number": 56, "verse_number": 69, "content": "Seid ihr es, die es aus den Wolken niedersenden, oder sind Wir es, die es niedersenden"},
  {"surah_number": 56, "verse_number": 70, "content": "Wollten Wir, konnten Wir es bittersalzig machen. Warum also dankt ihr (Mir) nicht"},
  {"surah_number": 56, "verse_number": 71, "content": "Habt ihr das Feuer betrachtet, das ihr entzundet"},
  {"surah_number": 56, "verse_number": 72, "content": "Seid ihr es, die den Baum dazu hervorbrachten, oder sind Wir dessen Urheber"},
  {"surah_number": 56, "verse_number": 73, "content": "Wir haben das (Feuer) zur Ermahnung (vor der Holle) erschaffen und zum Nutzen fur die Wanderer durch die Wildnisse"},
  {"surah_number": 56, "verse_number": 74, "content": "Darum preise den Namen deines Großen Herrn"},
  {"surah_number": 56, "verse_number": 75, "content": "Ich schwore bei den Stationen der Sterne"},
  {"surah_number": 56, "verse_number": 76, "content": "und wahrlich, das ist ein großer Schwur, wenn ihr es nur wußtet"},
  {"surah_number": 56, "verse_number": 77, "content": "daß dies wahrlich ein edler Quran ist"},
  {"surah_number": 56, "verse_number": 78, "content": "in einer wohlaufbewahrten Urschrift"},
  {"surah_number": 56, "verse_number": 79, "content": "Keiner kann sie beruhren, außer den Reinen"},
  {"surah_number": 56, "verse_number": 80, "content": "(Sie ist) eine Offenbarung vom Herrn der Welten"},
  {"surah_number": 56, "verse_number": 81, "content": "Wollt ihr denn gegenuber dieser Verkundigung undankbar sein"},
  {"surah_number": 56, "verse_number": 82, "content": "Und daß ihr (sie) leugnet, soll das euer Dank sein"},
  {"surah_number": 56, "verse_number": 83, "content": "Warum wohl, wenn (die Seele des Sterbenden) zur Kehle steigt"},
  {"surah_number": 56, "verse_number": 84, "content": "und ihr in jenem Augenblick zuschaut"},
  {"surah_number": 56, "verse_number": 85, "content": "Und Wir sind ihm naher als ihr, nur daß ihr es nicht seht"},
  {"surah_number": 56, "verse_number": 86, "content": "Warum wohl, wenn ihr nicht zur Re chenschaft gezogen werden sollt"},
  {"surah_number": 56, "verse_number": 87, "content": "Zwingt ihr sie nicht zuruck (in den Leib), wenn ihr wahrhaftig seid"},
  {"surah_number": 56, "verse_number": 88, "content": "Wenn er nun zu denen gehort, die (Allah) nahe sind"},
  {"surah_number": 56, "verse_number": 89, "content": "dann (wird er) Gluckseligkeit (genießen) und Dufte und Garten der Wonne"},
  {"surah_number": 56, "verse_number": 90, "content": "Und wenn er zu denen gehort, die zur Rechten sind"},
  {"surah_number": 56, "verse_number": 91, "content": "(wird ein) \"Friede sei auf dir\" von denen, die der Rechten angeho ren(, zugerufen)"},
  {"surah_number": 56, "verse_number": 92, "content": "Wenn er aber zu den Leugnern, Irregegangenen gehort"},
  {"surah_number": 56, "verse_number": 93, "content": "dann (wird ihm) eine Bewirtung mit siedendem Wasser zuteil"},
  {"surah_number": 56, "verse_number": 94, "content": "und Brennen in der Gahim"},
  {"surah_number": 56, "verse_number": 95, "content": "Wahrlich, dies ist die Wahrheit in aller Gewißheit"},
  {"surah_number": 56, "verse_number": 96, "content": "Lobpreise darum den Namen deines Großen Herrn"},
  {"surah_number": 57, "verse_number": 1, "content": "Es preist Allah, was in den Himmeln und was auf der Erde ist, und Er ist der Erhabene, der Allweise"},
  {"surah_number": 57, "verse_number": 2, "content": "Sein ist das Konigreich der Himmel und der Erde. Er macht lebendig und laßt sterben, und Er hat Macht uber alle Dinge"},
  {"surah_number": 57, "verse_number": 3, "content": "Er ist der Erste und der Letzte, der Sichtbare und der Verborgene, und Er ist der Kenner aller Dinge"},
  {
    "surah_number": 57,
    "verse_number": 4,
    "content":
        "Er ist es, Der die Himmel und die Erde in sechs Tagen erschuf, dann wandte Er Sich majestatisch Seinem Reich zu. Er weiß, was in die Erde eingeht und was aus ihr hervorkommt, was vom Himmel herniederkommt und was zu ihm aufsteigt. Und Er ist mit euch, wo immer ihr (auch) sein moget. Und Allah sieht alles, was ihr tut"
  },
  {"surah_number": 57, "verse_number": 5, "content": "Sein ist das Konigreich der Himmel und der Erde; und zu Allah werden alle Dinge zuruckgebracht"},
  {"surah_number": 57, "verse_number": 6, "content": "Er laßt die Nacht in den Tag und den Tag in die Nacht eintreten; und Er ist der Kenner all dessen, was (ihr) in den Herzen hegt"},
  {"surah_number": 57, "verse_number": 7, "content": "Glaubt an Allah und Seinen Gesandten und spendet von dem, zu dessen Erben Er euch gemacht hat. Und jenen von euch, die glauben und spenden, wird ein großer Lohn zuteil sein"},
  {"surah_number": 57, "verse_number": 8, "content": "Was ist euch, daß ihr nicht an Allah glaubt, obwohl der Gesandte euch aufruft, an euren Herrn zu glauben; und Er hat von euch bereits ein Versprechen abgenommen, wenn ihr Glaubige seid"},
  {"surah_number": 57, "verse_number": 9, "content": "Er ist es, Der deutliche Zeichen auf Seinen Diener hinabsendet, auf daß Er euch aus den Finsternissen ins Licht fuhre. Und wahrlich, Allah ist gegen euch Gnadig, Barmherzig"},
  {
    "surah_number": 57,
    "verse_number": 10,
    "content":
        "Was ist euch, daß ihr nicht fur Allahs Sache spendet, obwohl die Erbschaft der Himmel und der Erde Allah gehort? Es sind nicht gleich diejenigen unter euch, die spendeten und kampften vor dem Sieg. Sie stehen hoher im Rang als jene, die erst nachher spendeten und kampften. Allen aber verhieß Allah Gutes. Und Allah ist dessen wohl kundig, was ihr tut"
  },
  {"surah_number": 57, "verse_number": 11, "content": "Wer ist es, der Allah ein gutes Darlehen geben will? Er wird es ihm um ein Vielfaches mehren, und ihm wird ein wurdiger Lohn zuteil sein"},
  {
    "surah_number": 57,
    "verse_number": 12,
    "content":
        "Am Tage, da du die glaubigen Manner und die glaubigen Frauen sehen wirst, wahrend (die Strahlen) ihres Lichts vor ihnen und zu ihrer Rechten hervorbrechen (heißt es): \"Eine frohe Botschaft (sei) euch heute (beschieden)! In den Garten, durch die Bache fließen, werdet ihr auf ewig weilen. Das ist der gewaltige Gewinn"
  },
  {
    "surah_number": 57,
    "verse_number": 13,
    "content":
        "Am Tage, wenn die Heuchler und die Heuchlerinnen zu den Glaubigen sagen werden: \"Wartet auf uns! Wir wollen ein wenig von eurem Lichtstrahl bekommen\", da wird (zu ihnen) gesprochen werden: \"Kehrt zuruck und sucht (dort) Licht.\" Dann wird zwischen ihnen eine Mauer mit einem Tor darin errichtet werden. Innerhalb davon befindet sich die Barmherzigkeit und außerhalb davon die Strafe"
  },
  {
    "surah_number": 57,
    "verse_number": 14,
    "content": "Sie werden jenen (Glaubigen) zurufen: \"Waren wir nicht mit euch?\" Jene werden sagen: \"Doch, aber ihr versuchtet euch selber und wartetet und zweifeltet, und es betorten euch die eitlen Wunsche, bis Allahs Befehl kam. Und der Betorer hat euch uber Allah betort"
  },
  {"surah_number": 57, "verse_number": 15, "content": "So soll heute kein Losegeld angenommen werden weder von euch noch von den Unglaubigen. Eure Herberge ist das Feuer; es ist euer Schutzspender. Und eine schlimme Bestimmung ist das"},
  {
    "surah_number": 57,
    "verse_number": 16,
    "content":
        "Ist nicht fur die Glaubigen die Zeit gekommen, ihre Herzen zu demutigen vor der Ermahnung Allahs und vor der Wahrheit, die herabkam, und nicht so zu werden wie jene, denen zuvor die Schrift gegeben wurde und denen es zu lange dauerte, so daß ihre Herzen verstockt und viele von ihnen zu Frevlern wurden"
  },
  {"surah_number": 57, "verse_number": 17, "content": "Wisset, daß Allah die Erde nach ihrem Tode belebt. Wahrlich, Wir haben euch die Zeichen klar gemacht, auf daß ihr (sie) begreifen moget"},
  {"surah_number": 57, "verse_number": 18, "content": "Wahrlich, die mildtatigen Manner und die mildtatigen Frauen und jene, die Allah ein gutes Darlehen geben - es wird ihnen um ein Vielfaches gemehrt, und ihnen wird ein wurdiger Lohn zuteil sein"},
  {
    "surah_number": 57,
    "verse_number": 19,
    "content": "Und diejenigen, die an Allah und Seine Gesandten glauben, sind die Wahrhaftigen und die Bezeugenden vor ihrem Herrn; sie werden ihren Lohn und ihr Licht empfangen. Diejenigen aber, die unglaubig sind und Unsere Zeichen leugnen, sind die Insassen der Gahim"
  },
  {
    "surah_number": 57,
    "verse_number": 20,
    "content":
        "Wisset, daß wahrlich das diesseitige Leben nur ein Spiel und ein Zeitvertreib ist und ein Prunk und Geprahle unter euch und ein Wettrennen um Vermehrung von Gut und Kindern. Es gleicht dem reichlichen Regen, dessen Pflanzenwuchs den Saern gefallt. Dann verdorrt er; und du siehst ihn vergilben; hierauf wird er bruchig; und im Jenseits gibt es eine strenge Strafe, aber auch Vergebung von Allah und Wohlgefallen. Und das diesseitige Leben ist nichts anderes als eine Nutznießung, durch die man sich betoren laßt"
  },
  {
    "surah_number": 57,
    "verse_number": 21,
    "content":
        "Wetteifert denn miteinander um die Vergebung eures Herrn und um das Paradies, dessen Große gleich der Große des Himmels und der Erde ist. (Es ist fur) jene bereitet, die an Allah und Seine Gesandten glauben. Das ist Allahs Huld; Er gewahrt sie, wem Er will. Und Allah verfugt uber die große Huld"
  },
  {"surah_number": 57, "verse_number": 22, "content": "Es geschieht kein Unheil auf Erden oder an euch, das nicht in einem Buch (verzeichnet) ware, bevor Wir es ins Dasein rufen wahrlich, das ist fur Allah ein leichtes"},
  {"surah_number": 57, "verse_number": 23, "content": "auf daß ihr euch nicht daruber betruben moget, was euch entging, noch daruber frohlocken moget, was Er euch gegeben hat. Und Allah liebt keinen der eingebildeten Prahler"},
  {"surah_number": 57, "verse_number": 24, "content": "die geizig sind und die Menschen zum Geiz anhalten. Und wer da den Rucken wendet - siehe, Allah ist dann gewiß Der, Der auf keinen angewiesen, des Lobes Wurdig ist"},
  {
    "surah_number": 57,
    "verse_number": 25,
    "content":
        "Wahrlich, Wir schickten Unsere Gesandten mit klaren Beweisen und sandten mit ihnen das Buch und die Waagewerte herab, auf daß die Menschen Gerechtigkeit uben mogen. Und Wir schufen das Eisen, worin (Kraft zu) gewaltigem Krieg wie auch zu (vielerlei) Nutzen fur die Menschheit ist, damit Allah die bezeichne, die Ihm und Seinem Gesandten, wenngleich ungesehen, beistehen. Wahrlich, Allah ist Allmachtig, Erhaben"
  },
  {"surah_number": 57, "verse_number": 26, "content": "Und Wir entsandten ja auch Noah und Abraham und verbreiteten unter ihren Nachkommen das Prophetentum und die Schrift. Einige von ihnen waren auf dem rechten Weg, doch viele von ihnen waren Frevler"},
  {
    "surah_number": 57,
    "verse_number": 27,
    "content":
        "Dann ließen Wir Unsere Gesandten ihren Spuren folgen; und Wir ließen (ihnen) Jesus, den Sohn der Maria, folgen, und Wir gaben ihm das Evangelium. Und in die Herzen derer, die ihm folgten, legten Wir Gute und Barmherzigkeit. Doch das Monchtum, das sie im Trachten nach Allah s Wohlgefallen erfanden - das schrieben Wir ihnen nicht vor; und doch befolgten sie es nicht auf die richtige Art. Dennoch gaben Wir denen von ihnen, die glaubig waren, ihren Lohn, aber viele von ihnen waren Frevler"
  },
  {
    "surah_number": 57,
    "verse_number": 28,
    "content": "O ihr, die ihr glaubt, furchtet Allah und glaubt an Seinen Gesandten! Er wird euch einen doppelten Anteil von Seiner Barmherzigkeit geben und wird euch ein Licht bereiten, worin ihr wandeln werdet, und wird euch vergeben - und Allah ist Allvergebend, Barmherzig"
  },
  {
    "surah_number": 57,
    "verse_number": 29,
    "content": "damit die Leute der Schrift nicht meinen, daß sie (die Muslime) nicht imstande seien, die Huld Allahs zu erlangen, und (damit sie wissen), daß die Huld in Allahs Hand ist, auf daß Er sie verleihe, wem Er will. Und Allah verfugt uber die große Huld"
  },
  {"surah_number": 58, "verse_number": 1, "content": "Allah hat doch das Wort jener gehort, die mit dir wegen ihres Mannes stritt und sich vor Allah beklagte. Und Allah hat euer Gesprach gehort. Wahrlich, Allah ist Allhorend, Allsehend"},
  {
    "surah_number": 58,
    "verse_number": 2,
    "content":
        "Diejenigen von euch, die sich von ihren Frauen scheiden, indem sie sagen: \"Du bist mir (verboten) wie der Rucken meiner Mutter\", (irren; denn) ihre Mutter sind sie nicht; ihre Mutter sind einzig jene, die sie geboren haben; und sie außern da nur Worte, die unziemlich und unwahr sind; doch wahrlich, Allah ist Tilger der Sunden, Allvergebend"
  },
  {
    "surah_number": 58,
    "verse_number": 3,
    "content": "Und jene nun, die ihre Frauen Mutter nennen und dann zurucknehmen mochten, was sie gesagt haben - (die Buße dafur) ist die Befreiung eines Sklaven, bevor sie einander beruhren. Dies (wird euch gesagt), um euch zu ermahnen. Und Allah ist dessen wohl kundig, was ihr tut"
  },
  {
    "surah_number": 58,
    "verse_number": 4,
    "content":
        "Wer aber keine Moglichkeit (zur Freilassung eines Sklaven) findet, (soll) dann zwei Monate hintereinander fasten, bevor sie einander beruhren. Und wer es nicht vermag, (soll) dann sechzig Arme speisen. Dies (soll so sein), damit ihr an Allah und Seinen Gesandten glaubt. Das sind die Schranken Allahs; und den Unglaubigen wird eine qualvolle Strafe zuteil sein"
  },
  {
    "surah_number": 58,
    "verse_number": 5,
    "content": "Wahrlich, diejenigen, die sich Allah und Seinem Gesandten widersetzen, werden gewiß erniedrigt werden, eben wie die vor ihnen erniedrigt wurden; und wahrlich, Wir haben bereits deutliche Zeichen herniedergesandt. Und den Unglaubigen wird eine schmahliche Strafe zuteil sein"
  },
  {"surah_number": 58, "verse_number": 6, "content": "Am Tage, an dem Allah sie alle zusammen auferweckt, da wird Er ihnen verkunden, was sie getan haben. Allah hat die Rechnung daruber gefuhrt, wahrend sie es vergaßen. Und Allah ist der Zeuge aller Dinge"},
  {
    "surah_number": 58,
    "verse_number": 7,
    "content":
        "Siehst du denn nicht, daß Allah alles weiß, was in den Himmeln ist, und alles, was auf Erden ist? Keine geheime Unterredung zwischen dreien gibt es, bei der Er nicht vierter ware, noch eine zwischen funfen, bei der Er nicht sechster ware, noch zwischen weniger oder mehr als diesen, ohne daß Er mit ihnen ware, wo immer sie sein mogen. Dann wird Er ihnen am Tage der Auferstehung verkunden, was sie getan haben. Wahrlich, Allah ist uber alle Dinge Allwissend"
  },
  {
    "surah_number": 58,
    "verse_number": 8,
    "content":
        "Hast du nicht die gesehen, denen die geheime Verschworung verboten worden ist und die doch zu dem zuruckkehren, was ihnen verboten worden ist, und sich insgeheim zu Sunde und Ubertretung und Ungehorsam gegen den Gesandten verschworen? Und wenn sie zu dir kommen, so begrußen sie dich mit dem, womit dich Allah nicht begrußt hat; bei sich aber sagen sie: \"Warum bestraft uns Allah nicht fur das, was wir (gegen Ihn) sagen?\" Genugend fur sie ist Gahannam worin sie brennen werden; und ubel ist die Bestimmung"
  },
  {
    "surah_number": 58,
    "verse_number": 9,
    "content": "O ihr, die ihr glaubt, wenn ihr heimlich miteinander redet, so redet nicht in Sunde und Feindseligkeit und Ungehorsam gegen den Gesandten, sondern redet miteinander in Rechtschaffenheit und Gottesfurcht, und furchtet Allah, vor Dem ihr versammelt werdet"
  },
  {"surah_number": 58, "verse_number": 10, "content": "Die geheime Verschworung ruhrt allein von Satan her, der die betruben will, die glaubig sind; doch er kann ihnen nicht den geringsten Schaden zufugen, es sei denn mit Allahs Erlaubnis. Und auf Allah sollen die Glaubigen vertrauen"},
  {
    "surah_number": 58,
    "verse_number": 11,
    "content":
        "O ihr, die ihr glaubt, wenn in Versammlungen zu euch gesagt wird: \"Macht Platz!\" dann macht Platz; Allah wird ausgiebig Platz fur euch machen. Und wenn gesagt wird: \"Erhebt euch!\" - dann erhebt euch; Allah wird die unter euch, die glaubig sind, und die, denen Wissen gegeben wurde, um Rangstufen erhohen. Und Allah ist dessen wohl kundig, was ihr tut"
  },
  {
    "surah_number": 58,
    "verse_number": 12,
    "content": "O ihr, die ihr glaubt, wenn ihr euch mit dem Gesandten vertraulich beraten wollt, so schickt eurer vertraulichen Beratung Almosen voraus. Das ist besser fur euch und lauterer. Wenn ihr aber nicht (die Moglichkeit dazu) findet, dann ist Allah wahrlich All verzeihend, Barmherzig"
  },
  {
    "surah_number": 58,
    "verse_number": 13,
    "content":
        "Seid ihr wegen des Gebens von Almosen vor eurer vertraulichen Beratung besorgt? Nun denn, wenn ihr es nicht tut und Allah euch in Seine Barmherzigkeit aufnimmt, dann verrichtet das Gebet und entrichtet die Zakah und gehorcht Allah und Seinem Gesandten. Und Allah ist dessen wohl kundig, was ihr tut"
  },
  {"surah_number": 58, "verse_number": 14, "content": "Hast du nicht diejenigen gesehen, die sich ein Volk zu Freunden nehmen, dem Allah zurnt? Sie geho ren weder zu euch noch zu ihnen, und sie beschworen eine Luge, und sie sind sich dessen bewußt"},
  {"surah_number": 58, "verse_number": 15, "content": "Allah hat fur sie eine strenge Strafe bereitet. Ubel ist wahrlich das, was sie zu tun pflegen"},
  {"surah_number": 58, "verse_number": 16, "content": "Sie haben ihre Eide als Schutzbehauptung benutzt, und sie wenden vom Weg Allahs ab; ihnen wird darum eine erniedrigende Strafe zuteil sein"},
  {"surah_number": 58, "verse_number": 17, "content": "Weder ihre Reichtumer noch ihre Kinder werden ihnen im geringsten (etwas) gegen Allah nutzen. Sie sind die Bewohner des Feuers; darin mussen sie ewig bleiben"},
  {"surah_number": 58, "verse_number": 18, "content": "Am Tage, wenn Allah sie allesamt versammeln wird, da werden sie Ihm schworen, wie sie euch schworen, und sie werden meinen, sie fußten auf etwas. Wahrlich, sicherlich sind sie es, die zu den Lugnern gehoren"},
  {"surah_number": 58, "verse_number": 19, "content": "Satan hat sie vollig in die Macht bekommen und hat sie die Ermahnung Allahs vergessen lassen. Sie sind Satans Partei. Wahrlich, Satans Partei ist die verlierende"},
  {"surah_number": 58, "verse_number": 20, "content": "Wahrlich, jene, die sich Allah und Seinem Gesandten widersetzen, werden unter den Erniedrigsten sein"},
  {"surah_number": 58, "verse_number": 21, "content": "Allah hat es vorgeschrieben: Sicherlich werde Ich siegen, Ich und Meine Gesandten. Wahrlich, Allah ist Allmachtig, Erhaben"},
  {
    "surah_number": 58,
    "verse_number": 22,
    "content":
        "Du wirst kein Volk finden, das an Allah und an den Jungsten Tag glaubt und dabei diejenigen liebt, die sich Allah und Seinem Gesandten widersetzen, selbst wenn es ihre Vater waren oder ihre Sohne oder ihre Bruder oder ihre Verwandten. Das sind diejenigen, in deren Herzen Allah den Glauben eingeschrieben hat und die Er mit Seinem Sieg gestarkt hat. Er wird sie in Garten fuhren, durch die Bache fließen. Darin werden sie auf ewig weilen. Allah ist wohl zufrieden mit ihnen, und sie sind wohl zufrieden mit Ihm. Sie sind Allahs Partei. Wahrlich, Allahs Partei ist die erfolgreiche"
  },
  {"surah_number": 59, "verse_number": 1, "content": "Alles, was in den Himmeln, und alles, was auf Erden ist, preist Allah; und Er ist der Erhabene, der Allweise"},
  {
    "surah_number": 59,
    "verse_number": 2,
    "content":
        "Er ist es, Der diejenigen vom Volke der Schrift, die unglaubig waren, aus ihren Heimstatten zur ersten Versammlung austrieb. Ihr glaubtet nicht, daß sie hinausziehen wurden, und sie dachten, daß ihre Burgen sie gegen Allah schutzen wurden. Doch Allah kam von (dort) uber sie, woher sie es nicht erwarteten, und warf Schrecken in ihre Herzen, so daß sie ihre Hauser mit ihren eigenen Handen und den Handen der Glaubigen zerstorten. So zieht eine Lehre daraus, o die ihr Einsicht habt"
  },
  {"surah_number": 59, "verse_number": 3, "content": "Und ware es nicht gewesen, daß Allah fur sie die Verbannung angeordnet hatte, hatte Er sie sicher in (dieser) Welt bestraft; und im Jenseits wird ihnen die Strafe des Feuers zuteil sein"},
  {"surah_number": 59, "verse_number": 4, "content": "Dies (geschieht), weil sie sich Allah und Seinem Gesandten widersetzten; und wer sich Allah widersetzt - wahrlich, dann ist Allah streng im Strafen"},
  {"surah_number": 59, "verse_number": 5, "content": "Was ihr an Palmen umgehauen habt oder auf Wurzeln stehen ließet - es geschah mit Allahs Erlaubnis und (deswegen,) damit Er die Ubertreter in Schmach sturzen ließ"},
  {"surah_number": 59, "verse_number": 6, "content": "Und was Allah Seinem Gesandten als Beute von ihnen gegeben hat - ihr brauchtet weder Pferde noch Kamele dazu aufzubieten; aber Allah gibt Seinen Gesandten Gewalt uber wen Er will; und Allah hat Macht uber alle Dinge"},
  {
    "surah_number": 59,
    "verse_number": 7,
    "content":
        "Was Allah Seinem Gesandten gegeben hat, das ist fur Allah und fur den Gesandten und fur die Verwandten und die Waisen und die Armen und den Sohn des Weges, damit es nicht nur bei den Reichen unter euch umlauft. Und was euch der Gesandte gibt, das nehmt an; und was er euch untersagt, dessen enthaltet euch. Und furchtet Allah; wahrlich, Allah ist streng im Strafen"
  },
  {"surah_number": 59, "verse_number": 8, "content": "(Dies ist) fur die armen Auswanderer, die aus ihren Heimstatten und von ihren Besitztumern vertrieben wurden, wahrend sie nach Allahs Huld und Wohlgefallen trachteten und Allah und Seinem Gesandten beistanden. Diese sind die Wahrhaftigen"},
  {
    "surah_number": 59,
    "verse_number": 9,
    "content":
        "Und jene, die vor ihnen in der Behausung (des Islam) wohnten und im Glauben heimisch geworden sind, lieben jene, die bei ihnen Zuflucht suchten, und hegen in sich kein Verlangen nach dem, was ihnen gegeben wurde, sondern sehen (die Fluchtlinge gern) vor ihnen selbst bevorzugt, auch wenn sie selbst in Durftigkeit leben. Und wer vor seiner eigenen Habsucht bewahrt ist - das sind die Erfolgreichen"
  },
  {"surah_number": 59, "verse_number": 10, "content": "Und diejenigen, die nach ihnen kamen, sagen: \"Unser Herr, vergib uns und unseren Brudern, die uns im Glauben vorangingen, und laß in unsere Herzen keinen Groll gegen die Glaubigen. Unser Herr! Du bist wahrlich Gutig, Barmherzig"},
  {
    "surah_number": 59,
    "verse_number": 11,
    "content":
        "Hast du nicht die gesehen, die Heuchler sind? Sie sagen zu ihren Brudern vom Volk der Schrift, die unglaubig sind: \"Wenn ihr vertrieben werdet, so werden wir sicher mit euch ziehen, und wir werden nie jemandem gegen euch gehorchen; und wenn ihr angegriffen werdet, so werden wir euch sicher helfen.\" Doch Allah ist Zeuge, daß sie gewiß Lugner sind"
  },
  {
    "surah_number": 59,
    "verse_number": 12,
    "content": "Wenn sie vertrieben wurden, wurden sie nie mit ihnen ausziehen; und wenn sie angegriffen wurden, wurden sie ihnen niemals helfen. Und wenn sie ihnen schon helfen, so werden sie sicher den Rucken wenden (und fliehen); und dann sollen sie (selbst) keine Hilfe finden"
  },
  {"surah_number": 59, "verse_number": 13, "content": "Wahrlich, sie hegen großere Furcht vor euch in ihren Herzen als vor Allah. Dies (ist so), weil sie ein Volk sind, das nichts begreift"},
  {
    "surah_number": 59,
    "verse_number": 14,
    "content":
        "Sie wurden euch nicht bekampfen - nicht einmal alle zusammen -, außer in befestigten Stadten oder hinter Mauern, (obgleich) ihre Heldentaten untereinander groß sind. Du wurdest denken, sie seien eine Einheit, aber ihre Herzen sind uneinig. Dies (ist so), weil sie ein Volk sind, das keinen Verstand hat"
  },
  {"surah_number": 59, "verse_number": 15, "content": "gleich jenen, die kurz vor ihnen die bosen Folgen ihrer Handlungsweise kosteten. Und fur sie ist eine qualvolle Strafe (bereitet)"},
  {"surah_number": 59, "verse_number": 16, "content": "wie dem Satan, wenn er zu dem Menschen spricht: \"Sei unglaubig!\" Ist er aber unglaubig, so spricht er: \"lch habe nichts mit dir zu schaffen; denn ich furchte Allah, den Herrn der Welten"},
  {"surah_number": 59, "verse_number": 17, "content": "Und die Folge (davon) wird sein, daß beide im Feuer sein werden und darin auf ewig bleiben. Und das ist der Lohn der Ungerechten"},
  {"surah_number": 59, "verse_number": 18, "content": "O ihr, die ihr glaubt, furchtet Allah; und eine jede Seele schaue nach dem, was sie fur morgen vorausschickt. Und furchtet Allah; wahrlich, Allah ist dessen wohl kundig, was ihr tut"},
  {"surah_number": 59, "verse_number": 19, "content": "Und seid nicht wie jene, die Allah vergaßen und die Er darum ihre eigenen Seelen vergessen ließ. Das sind die Frevler"},
  {"surah_number": 59, "verse_number": 20, "content": "Nicht gleich sind die Bewohner des Feuers und die Bewohner des Paradieses. Es sind die Bewohner des Paradieses, die erfolgreich sind"},
  {"surah_number": 59, "verse_number": 21, "content": "Hatten Wir diesen Quran auf einen Berg herabgesandt, hattest du gesehen, wie er sich gedemutigt und aus Furcht vor Allah gespalten hatte. Und solche Gleichnisse pragen Wir fur die Menschen, auf daß sie nachdenken mogen"},
  {"surah_number": 59, "verse_number": 22, "content": "Er ist Allah, außer Dem kein Gott ist; Er ist der Kenner des Verborgenen und des Sichtbaren. Er ist der Allerbarmer, der Barmherzige"},
  {
    "surah_number": 59,
    "verse_number": 23,
    "content": "Er ist Allah, außer Dem kein Gott ist; Er ist der Herrscher, der Einzig Heilige, der Friede, der Verleiher von Sicherheit, der Uberwacher, der Erhabene, der Unterwerfer, der Majestatische. Gepriesen sei Allah uber all das, was sie (Ihm) beigesellen"
  },
  {"surah_number": 59, "verse_number": 24, "content": "Er ist Allah, der Schopfer, der Bildner, der Gestalter. Ihm stehen die schonsten Namen zu. Alles, was in den Himmeln und auf Erden ist, preist Ihn, und Er ist der Erhabene, der Allweise"},
  {
    "surah_number": 60,
    "verse_number": 1,
    "content":
        "O ihr, die ihr glaubt, nehmt euch nicht Meine Feinde und eure Feinde zu Beschutzern, indem ihr ihnen Zuneigung gutmutig zeigt, wo sie doch die Wahrheit leugnen, die zu euch gekommen ist, und den Gesandten und euch selbst austreiben, weil ihr an Allah, euren Herrn, glaubt. Wenn ihr zum Kampf fur Meine Sache und im Trachten nach Meinem Wohlgefallen ausgezogen seid, gebt ihr ihnen insgeheim Zuneigung zu verstehen, wahrend Ich doch am besten weiß, was ihr verbergt und was ihr kundtut. Und der, der von euch das tut, ist sicherlich vom geraden Weg abgeirrt"
  },
  {"surah_number": 60, "verse_number": 2, "content": "Wenn sie die Oberhand uber euch gewinnen, dann werden sie sich gegen euch als Feinde betragen und ihre Hande und Zungen zum Bosen gegen euch ausstrecken; und sie wunschen instandig, daß ihr unglaubig wurdet"},
  {"surah_number": 60, "verse_number": 3, "content": "Weder eure Blutsverwandtschaft noch eure Kinder werden euch nutzen. Er wird zwischen euch am Tage der Auferstehung entscheiden. Und Allah sieht alles, was ihr tut"},
  {
    "surah_number": 60,
    "verse_number": 4,
    "content":
        "Ihr habt bereits ein vortreffliches Beispiel an Abraham und denen mit ihm, als sie zu ihrem Volk sagten: \"Wir haben nichts mit euch noch mit dem zu schaffen was ihr statt Allah anbetet. Wir verwerfen euch. Und zwischen uns und euch ist offenbar fur immer Feindschaft und Haß entstanden; (solange,) bis ihr an Allah glaubt und an Ihn allein\" - abgesehen von Abrahams Wort zu seinem Vater: \"lch will gewiß fur dich um Verzeihung bitten, obwohl ich nicht die Macht dazu habe, bei Allah fur dich etwas auszurichten.\" (Sie beteten:) \"Unser Herr, in Dich setzen wir unser Vertrauen, und zu Dir kehren wir reumutig zuruck, und zu Dir ist die letzte Einkehr"
  },
  {"surah_number": 60, "verse_number": 5, "content": "Unser Herr, mache uns nicht zu einer Versuchung fur die Unglaubigen und vergib uns, unser Herr; denn Du, und Du allein, bist der Erhabene, der Allweise"},
  {"surah_number": 60, "verse_number": 6, "content": "Wahrlich, ihr habt an ihnen ein vortreffliches Beispiel (und so) ein jeder, der auf Allah hofft und den Jungsten Tag furchtet. Und wer sich abwendet - wahrlich, Allah ist es, Der auf keinen angewiesen, Der des Lobes Wurdige"},
  {"surah_number": 60, "verse_number": 7, "content": "Vielleicht wird Allah Zuneigung setzen zwischen euch und denen unter ihnen, mit denen ihr in Feindschaft lebt; denn Allah ist Allmachtig und Allah ist Allverzeihend, Barmherzig"},
  {"surah_number": 60, "verse_number": 8, "content": "Allah verbietet euch nicht, gegen jene, die euch nicht des Glaubens wegen bekampft haben und euch nicht aus euren Hausern vertrieben haben, gutig zu sein und redlich mit ihnen zu verfahren; wahrlich, Allah liebt die Gerechten"},
  {
    "surah_number": 60,
    "verse_number": 9,
    "content": "Doch Allah verbietet euch, mit denen, die euch des Glaubens wegen bekampft haben und euch aus euren Hausern vertrieben und (anderen) geholfen haben, euch zu vertreiben, Freundschaft zu schließen. Und wer mit ihnen Freundschaft schließt - das sind die Missetater"
  },
  {
    "surah_number": 60,
    "verse_number": 10,
    "content":
        "O ihr, die ihr glaubt, wenn glaubige Frauen als Fluchtlinge zu euch kommen, so pruft sie. Allah weiß am besten, wie ihr Glaube in Wirklichkeit ist. Wenn ihr sie dann glaubig findet, so schickt sie nicht zu den Unglaubigen zuruck. Diese (Frauen) sind ihnen nicht erlaubt, noch sind sie (als Ehemanner) diesen (Frauen) erlaubt. Jedoch zahlt (ihren unglaubigen Ehemannern) das zuruck, was sie (fur sie) ausgegeben haben. Und es ist keine Sunde fur euch, sie zu heiraten, wenn ihr ihnen ihre Brautgabe gegeben habt. Und haltet nicht am Eheband mit den unglaubigen Frauen fest, sondern verlangt das zuruck, was ihr (fur sie) ausgegeben habt, und lasset (die Unglaubigen) das zuruckverlangen, was sie (fur sie) ausgegeben haben. Das ist Allahs Gebot. Er richtet zwischen euch. Und Allah ist Allwissend, Allweise"
  },
  {
    "surah_number": 60,
    "verse_number": 11,
    "content": "Und wenn irgendeine von euren Frauen von euch zu den Unglaubigen fortgeht, dann gebt, wenn ihr (bei den Unglaubigen) Beute macht, jenen (Glaubigen), deren Frauen fortgegangen sind, das gleiche von dem, was sie (fur ihre Frauen) ausgegeben haben. Und furchtet Allah, an Den ihr glaubt"
  },
  {
    "surah_number": 60,
    "verse_number": 12,
    "content":
        "O Prophet! Wenn glaubige Frauen zu dir kommen und dir den Treueeid leisten, daß sie Allah nichts zur Seite stellen und daß sie weder stehlen noch Unzucht begehen, noch ihre Kinder toten, noch ein Unrecht begehen werden zwischen ihren Handen und Beinen, das sie selbst wissentlich ersonnen haben, noch dir ungehorsam sein werden in dem, was rechtens ist, dann nimm ihren Treueeid an und bitte Allah um Vergebung fur sie. Wahrlich, Allah ist Allvergebend, Barmher zig"
  },
  {
    "surah_number": 60,
    "verse_number": 13,
    "content": "O ihr, die ihr glaubt, schließt keine Freundschaft mit einem Volk, dem Allah zurnt; denn sie haben die Hoffnung auf das Jenseits gerade so aufgegeben, wie die Unglaubigen die Hoffnung auf die Wiederbelebung derer aufgegeben haben, die in den Grabern liegen"
  },
  {"surah_number": 61, "verse_number": 1, "content": "Es preist Allah, was in den Himmeln und was auf der Erde ist; und Er ist der Erhabene, der Allweise"},
  {"surah_number": 61, "verse_number": 2, "content": "O ihr, die ihr glaubt, warum sagt ihr, was ihr nicht tut"},
  {"surah_number": 61, "verse_number": 3, "content": "Hochst hassenswert ist es vor Allah, daß ihr sagt, was ihr nicht tut"},
  {"surah_number": 61, "verse_number": 4, "content": "Wahrlich, Allah liebt diejenigen, die fur Seine Sache kampfen, (in eine Schlachtordnung) gereiht, als waren sie ein festgefugtes Mauerwerk"},
  {
    "surah_number": 61,
    "verse_number": 5,
    "content": "Und da sagte Moses zu seinem Volk: \"O mein Volk, warum krankt ihr mich, wo ihr doch wisset, daß ich Allah s Gesandter bei euch bin?\" Als sie dann (vom Glauben) abschweiften, da ließ Allah ihre Herzen abschweifen. Und Allah leitet kein frevelhaftes Volk"
  },
  {
    "surah_number": 61,
    "verse_number": 6,
    "content":
        "Und da sagte Jesus, der Sohn der Maria: \"O ihr Kinder Israels, ich bin Allahs Gesandter bei euch, der Bestatiger dessen, was von der Thora vor mir gewesen ist, und Bringer der frohen Botschaft eines Gesandten, der nach mir kommen wird. Sein Name wird Ahmad sein \" Und als er zu ihnen mit den Beweisen kam, sagten sie: \"Das ist ein offenkundiger Zauber"
  },
  {"surah_number": 61, "verse_number": 7, "content": "Und wer ist ungerechter als der, der gegen Allah eine Luge erdichtet und selbst zum Islam aufgefordert wird? Und Allah leitet kein ungerechtes Volk"},
  {"surah_number": 61, "verse_number": 8, "content": "Sie wollen Allahs Licht mit ihren Mundern ausloschen, doch Allah wird Sein Licht vollenden, auch wenn die Unglaubigen es verwunschen"},
  {"surah_number": 61, "verse_number": 9, "content": "Er ist es, Der Seinen Gesandten mit der Fuhrung und der wahren Religion geschickt hat, auf daß Er sie uber alle Religionen siegen lasse, auch wenn die Gotzendiener es verwunschen"},
  {"surah_number": 61, "verse_number": 10, "content": "O ihr, die ihr glaubt, soll Ich euch (den Weg) zu einem Handel weisen, der euch vor qualvoller Strafe retten wird"},
  {"surah_number": 61, "verse_number": 11, "content": "Ihr sollt an Allah und an Seinen Gesandten glauben und sollt fur Allahs Sache mit eurem Gut und eurem Blut streiten. Das ist besser fur euch, wenn ihr es nur wußtet"},
  {"surah_number": 61, "verse_number": 12, "content": "Er wird euch eure Sunden vergeben und euch in Garten fuhren, durch die Bache fließen, und in gute Wohnungen in den Garten von Eden. Das ist die große Gluckseligkeit"},
  {"surah_number": 61, "verse_number": 13, "content": "Und noch etwas anderes, das ihr liebt, (wird euch zuteil sein): Hilfe von Allah und naher Sieg. So verkunde den Glaubigen die frohe Botschaft"},
  {
    "surah_number": 61,
    "verse_number": 14,
    "content":
        "O ihr, die ihr glaubt, seid Allahs Helfer wie Jesus, der Sohn der Maria, (als er) zu den Jungern sagte: \"Wer sind meine Helfer fur Allah?\" Die Junger sagten: \"Wir sind Allahs Helfer.\" So glaubte ein Teil der Kinder Israels, wahrend ein Teil unglaubig blieb. Da verliehen Wir denen, die glaubten, Starke gegen ihren Feind, und sie wurden siegreich"
  },
  {"surah_number": 62, "verse_number": 1, "content": "Was in den Himmeln und was auf der Erde ist, preist Allah, Den Herrscher, Den Einzig Heiligen, den Erhabenen, den Allweisen"},
  {
    "surah_number": 62,
    "verse_number": 2,
    "content": "Er ist es, Der unter den Analphabeten einen Gesandten aus ihrer Mitte erweckt hat, um ihnen Seine Verse zu verlesen und sie zu reinigen und sie die Schrift und die Weisheit zu lehren, obwohl sie sich zuvor in einem offenkundigen Irrtum befanden"
  },
  {"surah_number": 62, "verse_number": 3, "content": "und anderen von ihnen, die sie noch nicht eingeholt haben. Und Er ist der Erhabene, der Allweise"},
  {"surah_number": 62, "verse_number": 4, "content": "Das ist Allahs Huld; Er gewahrt sie, wem Er will; und Allah ist der Herr der großen Huld"},
  {"surah_number": 62, "verse_number": 5, "content": "Das Gleichnis derer, denen die Thora auferlegt wurde, und die ihr dann nicht nachlebten, ist wie das Gleichnis eines Esels, der Bucher tragt. Ubel steht es um die Leute, die Allahs Zeichen leugnen. Und Allah rechtleitet kein ungerechtes Volk"},
  {"surah_number": 62, "verse_number": 6, "content": "Sprich: \"O die ihr Juden seid, wenn ihr meint, ihr seid die Schutzbefohlenen Allahs unter Ausschluß der anderen Menschen, dann wunscht euch den Tod, wenn ihr wahrhaftig seid"},
  {"surah_number": 62, "verse_number": 7, "content": "Doch sie werden sich ihn niemals wunschen um dessentwillen, was ihre Hande ihnen vorausgeschickt haben. Und Allah kennt die Ungerechten recht wohl"},
  {"surah_number": 62, "verse_number": 8, "content": "Sprich: \"Der Tod vor dem ihr flieht, wird euch sicher ereilen. Dann werdet ihr zu Dem zuruckgebracht werden, Der es kennt, das Verborgene und das Sichtbare; und Er wird euch verkunden, was ihr zu tun pflegtet"},
  {"surah_number": 62, "verse_number": 9, "content": "O ihr, die ihr glaubt, wenn zum Freitagsgebet gerufen wird, dann eilt zum Gedenken Allahs und stellt den Geschaftsbetrieb ein. Das ist besser fur euch, wenn ihr es nur wußtet"},
  {"surah_number": 62, "verse_number": 10, "content": "Und wenn das Gebet beendet ist, dann zerstreut euch im Land und trachtet nach Allahs Gnadenfulle und gedenkt Allahs haufig, auf daß ihr Erfolg haben moget"},
  {"surah_number": 62, "verse_number": 11, "content": "Doch wenn sie eine Handelsware oder ein Spiel sehen, dann brechen sie sogleich dazu auf und lassen dich (im Gebet) stehen. Sprich: \"Was bei Allah ist, das ist besser als Spiel und Handelsware, und Allah ist der beste Versorger"},
  {"surah_number": 63, "verse_number": 1, "content": "Wenn die Heuchler zu dir kommen, sagen sie: \"Wir bezeugen, daß du in Wahrheit der Gesandte Allahs bist.\" Und Allah weiß, daß du wahrhaftig Sein Gesandter bist. Doch Allah bezeugt, daß die Heuchler Lugner sind"},
  {"surah_number": 63, "verse_number": 2, "content": "Sie haben ihre Eide als Schutzbehauptung vorgebracht; so wenden sie vom Wege Allahs ab. Schlimm ist wahrlich das, was sie zu tun pflegen"},
  {"surah_number": 63, "verse_number": 3, "content": "Dies (ist so), weil sie glaubten und hernach unglaubig wurden. So ist ein Siegel auf ihre Herzen gesetzt worden, so daß sie nicht be greifen (konnen)"},
  {
    "surah_number": 63,
    "verse_number": 4,
    "content":
        "Und wenn du sie siehst, so gefallen dir ihre Gestalten; und wenn sie sprechen, horchst du auf ihre Rede. Sie sind, als waren sie aufgerichtete Holzklotze. Sie glauben, jeder Schrei sei gegen sie (gerichtet) Sie sind der Feind, darum nimm dich vor ihnen in acht. Allahs Fluch uber sie! Wie werden sie abgewendet"
  },
  {"surah_number": 63, "verse_number": 5, "content": "Und wenn zu ihnen gesprochen wird: \"Kommt her, der Gesandte Allahs will fur euch um Verzeihung bitten\", dann wenden sie ihre Kopfe zur Seite, und du siehst, wie sie sich in Hochmut abkehren"},
  {"surah_number": 63, "verse_number": 6, "content": "Es ist ihnen gleich, ob du fur sie um Verzeihung bittest oder nicht fur sie um Verzeihung bittest. Allah wird ihnen nie verzeihen; wahrlich, Allah rechtleitet kein frevelhaftes Volk"},
  {"surah_number": 63, "verse_number": 7, "content": "Sie sind es, die sagen: \"Spendet nicht fur die, die mit dem Gesandten Allahs sind, bis sie (ihn) verlassen\", wahrend doch die Schatze der Himmel und der Erde Allahs sind; allein die Heuchler verstehen nichts"},
  {"surah_number": 63, "verse_number": 8, "content": "Sie sagen: \"Wenn wir nach Madina zuruckkehren, dann wird der Wurdigste sicher den Geringsten aus ihr vertreiben\", obwohl die Wurdigkeit nur Allah und Seinem Gesandten und den Glaubigen zusteht; aber die Heuchler wissen es nicht"},
  {"surah_number": 63, "verse_number": 9, "content": "O ihr, die ihr glaubt, lasset euch durch euer Vermogen und eure Kinder nicht vom Gedenken an Allah abhalten. Und wer das tut - das sind die Verlierenden"},
  {"surah_number": 63, "verse_number": 10, "content": "Und spendet von dem, was Wir euch gegeben haben, bevor einen von euch der Tod ereilt und er sagt: \"Mein Herr! Wenn Du mir nur Aufschub fur eine kurze Frist gewahren wurdest, dann wurde ich Almosen geben und einer der Rechtschaffenen sein"},
  {"surah_number": 63, "verse_number": 11, "content": "Und nie wird Allah jemandem Aufschub gewahren, wenn seine Frist um ist; und Allah ist dessen wohl kundig, was ihr tut"},
  {"surah_number": 64, "verse_number": 1, "content": "Es preist Allah, was in den Himmeln und auf der Erde ist; Sein ist das Konigreich und Sein ist das Lob, und Er hat Macht uber alle Dinge"},
  {"surah_number": 64, "verse_number": 2, "content": "Er ist es, Der euch erschaffen hat, aber einige von euch sind unglaubig und einige von euch sind glaubig; und Allah durchschaut das, was ihr tut"},
  {"surah_number": 64, "verse_number": 3, "content": "Er schuf die Himmel und die Erde in gerechter Weise, und Er gestaltete euch und machte eure Gestalt schon, und zu Ihm ist die Heimkehr"},
  {"surah_number": 64, "verse_number": 4, "content": "Er weiß, was in den Himmeln und auf Erden ist, und Er weiß, was ihr geheimhaltet und was ihr offenkund tut; und Allah kennt alles, was in den Herzen ist"},
  {"surah_number": 64, "verse_number": 5, "content": "Ist nicht die Geschichte von denen, die zuvor unglaubig waren, zu euch gekommen? So kosteten sie die bosen Folgen ihres Betragens, und ihnen wird eine qualvolle Strafe zuteil sein"},
  {
    "surah_number": 64,
    "verse_number": 6,
    "content": "Dies (soll so sein), weil ihre Gesandten mit klaren Beweisen zu ihnen kamen, sie aber sagten: \"Sollen Menschen uns rechtleiten?\" Also glaubten sie nicht und wandten sich ab, doch Allah hat dies nicht notig; und Allah ist auf keinen angewiesen, des Lobes Wurdig"
  },
  {"surah_number": 64, "verse_number": 7, "content": "Diejenigen, die da unglaubig sind, behaupten, sie wurden nicht auferweckt werden. Sprich: \"Doch, bei meinem Herrn, ihr werdet gewiß auferweckt werden; dann wird euch gewiß verkundet, was ihr getan habt. Und das ist fur Allah ein leichtes"},
  {"surah_number": 64, "verse_number": 8, "content": "Darum glaubt an Allah und Seinen Gesandten und an das Licht, das Wir herniedergesandt haben. Und Allah ist dessen wohl kundig, was ihr tut"},
  {
    "surah_number": 64,
    "verse_number": 9,
    "content":
        "Der Zeitpunkt, wo Er euch am Tage der Versammlung versammeln wird - das wird der Tag des verlorenen Gewinns sein. Und wer an Allah glaubt und das Rechte tut - Er wird seine Ubel von ihm nehmen und wird ihn in Garten fuhren, durch die Bache fließen, worin (sie) auf ewig verweilen. Das ist das große Gluck"
  },
  {"surah_number": 64, "verse_number": 10, "content": "Diejenigen aber, die unglaubig sind und Unsere Zeichen leugnen, sollen die Bewohner des Feuers sein. Darin bleiben sie auf ewig; und das ist eine schlimme Bestimmung"},
  {"surah_number": 64, "verse_number": 11, "content": "Kein Ungluck trifft ein, es sei denn mit Allahs Erlaubnis. Und wer an Allah glaubt, dem leitet Er sein Herz. Und Allah weiß alle Dinge"},
  {"surah_number": 64, "verse_number": 12, "content": "Und gehorcht Allah und gehorcht dem Gesandten. Doch wenn ihr euch (von ihm) abkehrt, dann obliegt Unserem Gesandten nur die Pflicht zur deutlichen Verkundigung"},
  {"surah_number": 64, "verse_number": 13, "content": "Allah! Es ist kein Gott außer Ihm; und auf Allah sollen die Glaubigen vertrauen"},
  {"surah_number": 64, "verse_number": 14, "content": "O ihr, die ihr glaubt, wahrlich, unter euren Frauen und Kindern sind welche, die euch feindlich gesonnen sind; so hutet euch vor ihnen. Und wenn ihr verzeiht und Nachsicht ubt und vergebt, dann ist Allah Allvergebend, Barmherzig"},
  {"surah_number": 64, "verse_number": 15, "content": "Eure Reichtumer und eure Kinder sind wahrlich eine Versuchung; doch bei Allah ist großer Lohn"},
  {"surah_number": 64, "verse_number": 16, "content": "So furchtet Allah, soviel ihr nur konnt, und hort und gehorcht und spendet; es wird fur euch selbst besser sein. Und wer vor seiner eigenen Habsucht bewahrt ist das sind die Erfolgreichen"},
  {"surah_number": 64, "verse_number": 17, "content": "Wenn ihr Allah ein gutes Darlehen gebt, so wird Er es euch um ein Vielfaches vermehren und wird euch vergeben; und Allah ist Dankbar, Nachsichtig"},
  {"surah_number": 64, "verse_number": 18, "content": "der Kenner des Verborgenen und des Sichtbaren, der Erhabene, der Allweise"},
  {
    "surah_number": 65,
    "verse_number": 1,
    "content":
        "O du Prophet, wenn ihr euch von den Frauen scheidet, so scheidet euch von ihnen zu ihrer festgesetzten Zeit und berechnet die Zeit und furchtet Allah, euren Herrn. Treibt sie nicht aus ihren Hausern noch lasset sie hinausgehen; es sei denn, sie hatten eine offenkundige Schandlichkeit begangen. Und dies sind Allahs Gebote; und wer Allahs Gebote ubertritt, der hat sich selber Unrecht getan. Du weißt nicht, ob Allah danach etwas (Unvermutetes) geschehen lassen wurde"
  },
  {
    "surah_number": 65,
    "verse_number": 2,
    "content":
        "Wenn sie aber ihren Termin erreicht haben, dann haltet sie in Gute zuruck oder trennt euch in Gute von ihnen; und nehmt als Zeugen Leute von Billigkeit unter euch, und legt Zeugnis vor Allah ab. Dies ist eine Ermahnung fur diejenigen, die an Allah und an den Jungsten Tag glauben; und dem, der Allah furchtet, verschafft Er einen Ausweg"
  },
  {"surah_number": 65, "verse_number": 3, "content": "und versorgt ihn in der Art und Weise, mit der er nicht rechnet. Und wer auf Allah vertraut - fur den ist Er sein Genuge. Wahrlich, Allah setzt durch, was Er will; siehe Allah hat fur alles eine Bestimmung gemacht"},
  {
    "surah_number": 65,
    "verse_number": 4,
    "content":
        "Wenn ihr Zweifel hegt (uber) jene eurer Frauen, die keine Menstruation mehr erhoffen, (dann wisset, daß) ihre Frist drei Monate betragt, und (das gleiche gilt fur) diejenigen, die noch keine Menstruation gehabt haben. Und fur die Schwangeren soll die Frist solange dauern, bis sie zur Welt bringen, was sie getragen haben. Und dem, der Allah furchtet, wird Er Erleichterung in seinen Angelegenheiten verschaffen"
  },
  {"surah_number": 65, "verse_number": 5, "content": "Das ist Allahs Befehl, den Er euch herabgesandt hat. Und wer Allah furchtet - Er wird seine Ubel von ihm nehmen und ihm einen wurdigen Lohn geben"},
  {
    "surah_number": 65,
    "verse_number": 6,
    "content":
        "Laßt sie wohnen, wo ihr wohnt, gemaß euren Mitteln; und tut ihnen nichts zuleide in der Absicht, es ihnen schwer zu machen. Und wenn sie schwanger sind, so bestreitet ihren Unterhalt, bis sie zur Welt bringen, was sie getragen haben. Und wenn sie (das Kind) fur euch stillen, (dann) gebt ihnen ihren Lohn und geht gutig miteinander um; wenn ihr aber Schwierigkeiten miteinander habt, dann soll eine andere (das Kind) fur den (Vater) stillen"
  },
  {
    "surah_number": 65,
    "verse_number": 7,
    "content":
        "Jeder soll aus seiner Fulle ausgeben, wenn er die Fulle hat; und der, dessen Mittel beschrankt sind, soll gemaß dem ausgeben, was ihm Allah gegeben hat. Allah fordert von keiner Seele etwas uber das hinaus, was Er ihr gegeben hat. Allah wird nach einer Bedrangnis Erleichterung schaffen"
  },
  {"surah_number": 65, "verse_number": 8, "content": "Wie so manche Stadt widersetzte sich dem Befehl ihres Herrn und Seiner Gesandten, und Wir zogen sie streng zur Rechenschaft und bestraften sie mit graßlicher Strafe"},
  {"surah_number": 65, "verse_number": 9, "content": "So kostete sie die bosen Folgen ihres Betragens, und am Ende ihres Betragens stand ein Verlust"},
  {"surah_number": 65, "verse_number": 10, "content": "Allah hat fur sie eine strenge Strafe bereitet; so furchtet Allah, o ihr Leute von Verstand, die ihr glaubt. Allah hat euch wahrlich eine Ermahnung herniedergesandt"},
  {
    "surah_number": 65,
    "verse_number": 11,
    "content":
        "einen Gesandten, der euch die deutlichen Verse Allahs verliest, auf daß er jene, die glauben und gute Werke tun, aus den Finsternissen ans Licht fuhre. Und den, der an Allah glaubt und recht handelt, wird Er in Garten fuhren, durch die Bache fließen, worin (er) auf ewig verweilen wird. Allah hat ihm wahrlich eine treffliche Versorgung gewahrt"
  },
  {"surah_number": 65, "verse_number": 12, "content": "Allah ist es, Der sieben Himmel erschuf und von der Erde die gleiche Anzahl. Der Befehl steigt zwischen ihnen herab, auf daß ihr erfahren moget, daß Allah uber alle Dinge Macht hat und daß Allahs Wissen alle Dinge umfaßt"},
  {"surah_number": 66, "verse_number": 1, "content": "O Prophet! Warum verbietest du das, was Allah dir erlaubt hat, um nach der Zufriedenheit deiner Frauen zu trachten? Und Allah ist Allvergebend, Barmherzig"},
  {"surah_number": 66, "verse_number": 2, "content": "Wahrlich, Allah hat fur euch die Losung eurer Eide angeordnet, und Allah ist euer Beschutzer; und Er ist der Allwissende, der Allweise"},
  {
    "surah_number": 66,
    "verse_number": 3,
    "content":
        "Und als der Prophet sich zu einer seiner Frauen im Vertrauen geaußert hatte und sie es dann kundtat und Allah ihm davon Kenntnis gab, da ließ er (sie) einen Teil davon wissen und verschwieg einen Teil. Und als er es ihr vorhielt, da sagte sie: \"Wer hat dich davon unterrichtet?\" Er sagte: \"Unterrichtet hat mich der Allwissende, der Allkundige"
  },
  {
    "surah_number": 66,
    "verse_number": 4,
    "content":
        "Wenn ihr beide euch Allah reumutig zuwendet, so sind eure Herzen bereits (dazu) geneigt. Doch wenn ihr euch gegenseitig gegen ihn unterstutzt, wahrlich, dann ist Allah sein Beschutzer; und Gabriel und die Rechtschaffenen unter den Glaubigen (sind ebenso seine Beschutzer); und außerdem sind die Engel (seine) Helfer"
  },
  {"surah_number": 66, "verse_number": 5, "content": "Vielleicht wird sein Herr ihm, wenn er sich von euch scheidet, an eurer Stelle bessere Frauen als euch geben, muslimische, glaubige, gehorsame, reuige, fromme, fastende (Frauen), Taiyibat und Jungfrauen"},
  {
    "surah_number": 66,
    "verse_number": 6,
    "content": "O ihr, die ihr glaubt, rettet euch und die Euren vor einem Feuer, dessen Brennstoff Menschen und Steine sind, woruber strenge, gewaltige Engel gesetzt sind, die Allah nicht ungehorsam sind in dem, was Er ihnen befiehlt, und die alles vollbringen, was ihnen befohlen wird"
  },
  {"surah_number": 66, "verse_number": 7, "content": "O die ihr unglaubig seid, bringt heute keine Entschuldigung vor. Ihr werdet nur fur das belohnt, was ihr zu tun pflegtet"},
  {
    "surah_number": 66,
    "verse_number": 8,
    "content":
        "O ihr, die ihr glaubt, wendet euch in aufrichtiger Reue zu Allah. Vielleicht wird euer Herr eure Ubel von euch nehmen und euch in Garten fuhren, durch die Bache fließen; am Tage, da Allah den Propheten nicht zuschanden macht, noch jene, die mit ihm glauben. Ihr Licht wird vor ihnen her eilen und auf ihrer Rechten (sein) Sie werden sagen: \"Unser Herr mache unser Licht fur uns vollkommen und vergib uns; denn Du hast Macht uber alle Dinge"
  },
  {"surah_number": 66, "verse_number": 9, "content": "O Prophet! Bekampfe die Unglaubigen und die Heuchler; und sei streng gegen sie. Ihre Herberge wird Gahannam sein, und dies ist ein schlimmes Ende"},
  {
    "surah_number": 66,
    "verse_number": 10,
    "content":
        "Allah legt denen die unglaubig sind, das Beispiel von Noahs Frau und von Lots Frau vor: Diese zwei waren unter zwei Unserer rechtschaffenen Diener, doch sie handelten untreu an ihnen. Darum nutzten sie ihnen nichts gegen Allah, und es wurde gesprochen: \"Geht ihr beide ins Feuer ein zusammen mit denen, die (darin) eingehen"
  },
  {"surah_number": 66, "verse_number": 11, "content": "Und Allah legt denen, die glauben, das Beispiel von Pharaos Frau vor, als sie sagte: \"Mein Herr! Baue mir ein Haus bei Dir im Paradies und befreie mich von Pharao und seinen Taten und befreie mich von dem Volk der Ungerechten"},
  {"surah_number": 66, "verse_number": 12, "content": "Und (Allah legt das Beispiel) von Maria, der Tochter `lmrans, (vor,) die ihre Scham bewahrte - darum hauchten Wir von Unserem Geist in diese ein; und sie glaubte an die Worte ihres Herrn und an Seine Schrift und war eine der Gehorsamen"},
  {"surah_number": 67, "verse_number": 1, "content": "Segensreich ist Der, in Dessen Hand die Herrschaft ruht; und Er hat Macht uber alle Dinge"},
  {"surah_number": 67, "verse_number": 2, "content": "(Er,) Der den Tod erschaffen hat und das Leben, auf daß Er euch prufe, wer von euch die besseren Taten verrichte; und Er ist der Erhabene, der Allvergebende"},
  {"surah_number": 67, "verse_number": 3, "content": "Der die sieben Himmel in Schichten erschaffen hat. Keinen Fehler kannst du in der Schopfung des Allerbarmers sehen. So wende den Blick (zu ihnen) zuruck: erblickst du irgendeinen Mangel"},
  {"surah_number": 67, "verse_number": 4, "content": "Dann wende den Blick abermals zum zweiten Mal zuruck: so wird dein Blick nur ermudet und geschwacht zu dir zuruckkehren"},
  {"surah_number": 67, "verse_number": 5, "content": "Und Wir haben den untersten Himmel wahrlich mit Leuchten geschmuckt, und Wir haben sie zu einem Mittel zur Vernichtung der Satane gemacht, und fur sie haben Wir die Strafe des flammenden Feuers bereitet"},
  {"surah_number": 67, "verse_number": 6, "content": "Und fur jene, die nicht an ihren Herrn glauben, ist die Strafe der Gahannam, und eine uble Bestimmung ist das"},
  {"surah_number": 67, "verse_number": 7, "content": "Wenn sie hin eingeworfen werden, dann werden sie sie aufheulen horen, wahrend sie in Wallung gerat"},
  {"surah_number": 67, "verse_number": 8, "content": "Fast mochte sie bersten vor Wut. Sooft eine Schar hineingeworfen wird, werden ihre Wachter sie fragen: \"Ist denn kein Warner zu euch gekommen"},
  {"surah_number": 67, "verse_number": 9, "content": "Sie werden sagen: \"Doch, sicherlich, es kam ein Warner zu uns, aber wir leugneten es und sagten: \"Allah hat nichts herabgesandt; ihr befindet euch bloß in einem großen Irrtum"},
  {"surah_number": 67, "verse_number": 10, "content": "Und sie werden (weiter) sagen: \"Hatten wir nur zugehort oder Verstand gehabt, waren wir nicht unter den Bewohnern des flammenden Feuers gewesen"},
  {"surah_number": 67, "verse_number": 11, "content": "So werden sie ihre Sundhaftigkeit zugeben; doch nieder mit den Bewohnern des flammenden Feuers"},
  {"surah_number": 67, "verse_number": 12, "content": "Wahrlich, diejenigen, die ihren Herrn im verborgenen furchten, werden Vergebung und einen großen Lohn erhalten"},
  {"surah_number": 67, "verse_number": 13, "content": "Und ob ihr euer Wort verbergt oder es offen verkundet, Er kennt die innersten Gedanken der Herzen"},
  {"surah_number": 67, "verse_number": 14, "content": "Kennt Er den denn nicht, den Er erschaffen hat? Und Er ist der Nachsichtige, der Allkundige"},
  {"surah_number": 67, "verse_number": 15, "content": "Er ist es, Der euch die Erde untertan gemacht hat; wandert also auf ihren Wegen und genießt Seine Versorgung. Und zu Ihm fuhrt die Auferstehung"},
  {"surah_number": 67, "verse_number": 16, "content": "Fuhlt ihr euch sicher davor, daß Der, Der im Himmel ist, nicht die Erde unter euch versinken laßt, und sie dann ins Wanken gerat"},
  {"surah_number": 67, "verse_number": 17, "content": "Oder fuhlt ihr euch sicher davor, daß Der, Der im Himmel ist, nicht einen Sandsturm gegen euch schickt? Dann werdet ihr wissen, wie Meine Warnung war"},
  {"surah_number": 67, "verse_number": 18, "content": "Und schon jene leugneten, die vor ihnen waren; wie war dann (die Folge) Meiner Verleugnung"},
  {"surah_number": 67, "verse_number": 19, "content": "Haben sie nicht die Vogel uber sich gesehen, wie sie ihre Flugel ausbreiten und sie dann einziehen? Kein Anderer als der Allerbarmer halt sie zuruck. Wahrlich, Er durchschaut alle Dinge"},
  {"surah_number": 67, "verse_number": 20, "content": "Oder wer ist es, der eine Heerschar fur euch ware, um euch gegen den Allerbarmer beizustehen? Die Unglaubigen sind wahrlich vollig betort"},
  {"surah_number": 67, "verse_number": 21, "content": "Oder wer ist es, der euch versorgen wurde, wenn Er Seine Versorgung zuruckhielte? Nein, sie verharren in Trotz und in Widerwillen"},
  {"surah_number": 67, "verse_number": 22, "content": "Wie? Ist denn der, der mit zur Erde gebeugtem Gesicht einhergeht, besser rechtgeleitet als jener, der aufrecht auf dem geraden Weg geht"},
  {"surah_number": 67, "verse_number": 23, "content": "Sprich: \"Er ist es, Der euch ins Dasein rief und Der euch Ohren und Augen und Herzen gab; (aber) gering ist es, was ihr an Dank wißt"},
  {"surah_number": 67, "verse_number": 24, "content": "Sprich: \"Er ist es, Der euch auf Erden mehrte, und vor Ihm werdet ihr versammelt werden"},
  {"surah_number": 67, "verse_number": 25, "content": "Und sie sagen: \"Wann wird sich diese Androhung erfullen, wenn ihr wahrhaftig seid"},
  {"surah_number": 67, "verse_number": 26, "content": "Sprich:\" Das Wissen (darum) ist wahrlich bei Allah, und ich bin nur ein deutlicher Warner"},
  {"surah_number": 67, "verse_number": 27, "content": "Doch wenn sie es nahe sehen, dann werden die Gesichter derer, die unglaubig sind, verzerrt sein, und es wird gesprochen werden: \"Das ist es wonach ihr verlangt habt"},
  {"surah_number": 67, "verse_number": 28, "content": "Sprich: \"Was meint ihr wenn Allah mich und diejenigen, die mit mir sind, vernichten sollte, oder wenn Er uns Barmherzigkeit erweisen sollte, wer wurde (dann) die Unglaubigen vor qualvoller Strafe schutzen"},
  {"surah_number": 67, "verse_number": 29, "content": "Sprich: \"Er ist der Allerbarmer; an Ihn glauben wir und auf Ihn vertrauen wir. Ihr werdet bald erfahren, wer sich in einem offenbaren Irrtum befindet"},
  {"surah_number": 67, "verse_number": 30, "content": "Sprich: \"Was meint ihr, wenn euer Wasser versickern wurde, wer konnte euch dann reichlich Wasser bringen"},
  {"surah_number": 68, "verse_number": 1, "content": "Nun - und beim Schreibrohr und bei dem, was sie nie derschreiben"},
  {"surah_number": 68, "verse_number": 2, "content": "Wahrlich, du bist - durch die Gnade deines Herrn - kein Besessener"},
  {"surah_number": 68, "verse_number": 3, "content": "Und fur dich ist gewiß ein Lohn bestimmt, der dir nicht vorenthalten wird"},
  {"surah_number": 68, "verse_number": 4, "content": "Und du verfugst wahrlich uber großartige Tugendeigenschaften"},
  {"surah_number": 68, "verse_number": 5, "content": "Also wirst du sehen und sie werden auch sehen"},
  {"surah_number": 68, "verse_number": 6, "content": "wer von euch der Besessene ist"},
  {"surah_number": 68, "verse_number": 7, "content": "Wahrlich, dein Herr weiß am besten, wer von Seinem Weg abirrt, und Er kennt auch die Rechtgeleiteten am besten"},
  {"surah_number": 68, "verse_number": 8, "content": "Darum richte dich nicht nach den Wunschen der Leugner"},
  {"surah_number": 68, "verse_number": 9, "content": "Sie wunschen, daß du dich (ihnen gegenuber) entgegenkommend verhaltst, dann wurden (auch) sie sich (dir gegenuber) entgegenkommend verhalten"},
  {"surah_number": 68, "verse_number": 10, "content": "Und fuge dich nicht irgendeinem verachtlichen Schwuremacher"},
  {"surah_number": 68, "verse_number": 11, "content": "Verleumder, einem, der umhergeht, um uble Nachrede zu verbreiten"},
  {"surah_number": 68, "verse_number": 12, "content": "einem Behinderer des Guten, Ubertreter, Sunder"},
  {"surah_number": 68, "verse_number": 13, "content": "groben Benehmens, einem Bastard"},
  {"surah_number": 68, "verse_number": 14, "content": "Nur weil er Reichtumer und Kinder besitzt"},
  {"surah_number": 68, "verse_number": 15, "content": "sagt er, wenn ihm Unsere Verse verlesen werden: \"(Dies sind) Fabeln der Alten"},
  {"surah_number": 68, "verse_number": 16, "content": "Wir wollen ihn auf der Nase brandmarken"},
  {"surah_number": 68, "verse_number": 17, "content": "Wir prufen sie, wie Wir die Eigentumer des Gartens pruften, als sie schworen, sie wurden sicherlich (all) seine Fruchte am Morgen pflucken"},
  {"surah_number": 68, "verse_number": 18, "content": "Und sie machten keinen Vorbehalt"},
  {"surah_number": 68, "verse_number": 19, "content": "Dann kam eine Heimsuchung deines Herrn uber ihn, wahrend sie schliefen"},
  {"surah_number": 68, "verse_number": 20, "content": "Und am Morgen war (der Garten) bereits verwustet"},
  {"surah_number": 68, "verse_number": 21, "content": "Dann riefen sie am Morgen einander zu"},
  {"surah_number": 68, "verse_number": 22, "content": "Geht in der Fruhe zu eurem Acker hinaus, wenn ihr ernten mochtet"},
  {"surah_number": 68, "verse_number": 23, "content": "Und sie machten sich auf den Weg und redeten dabei flusternd miteinander"},
  {"surah_number": 68, "verse_number": 24, "content": "Zu euch hinein darf ihn heute kein Armer betreten"},
  {"surah_number": 68, "verse_number": 25, "content": "Und sie gingen in der Fruhe hin mit dem festen Vorsatz, geizig zu sein"},
  {"surah_number": 68, "verse_number": 26, "content": "Doch als sie ihn sahen, sagten sie: \"Wahrlich, wir befinden uns im Irrtum"},
  {"surah_number": 68, "verse_number": 27, "content": "Nein, wir sind beraubt"},
  {"surah_number": 68, "verse_number": 28, "content": "Der Gemaßigte unter ihnen sagte: \"Habe ich euch nicht gesagt: \"Warum preist ihr (Allah) nicht"},
  {"surah_number": 68, "verse_number": 29, "content": "(Nun) sagten sie: \"Preis sei unserem Herrn! Gewiß, wir sind ungerecht gewesen"},
  {"surah_number": 68, "verse_number": 30, "content": "Dann wandten sich einige von ihnen an die anderen, indem sie sich gegenseitig Vorwurfe machten"},
  {"surah_number": 68, "verse_number": 31, "content": "Sie sagten: \"Wehe uns! Wir waren wahrlich widerspenstig"},
  {"surah_number": 68, "verse_number": 32, "content": "Vielleicht wird unser Herr uns einen besseren (Garten) als Ersatz fur diesen geben; wir flehen demutig zu unserem Herrn"},
  {"surah_number": 68, "verse_number": 33, "content": "So ist die Strafe. Und wahrlich, die Strafe des Jenseits ist (noch) schwerer. Wenn sie es nur wußten"},
  {"surah_number": 68, "verse_number": 34, "content": "Fur die Gerechten sind wahrlich Garten der Wonne bei ihrem Herrn (bestimmt)"},
  {"surah_number": 68, "verse_number": 35, "content": "Sollten Wir etwa die Gottergebenen wie die Schuldigen behandeln"},
  {"surah_number": 68, "verse_number": 36, "content": "Was ist euch? Wie urteilt ihr"},
  {"surah_number": 68, "verse_number": 37, "content": "Oder habt ihr etwa ein Buch, in dem ihr studiert"},
  {"surah_number": 68, "verse_number": 38, "content": "so daß ihr danach alles erhalten sollt, was ihr wunscht"},
  {"surah_number": 68, "verse_number": 39, "content": "Oder habt ihr Gelobnisse von Uns - bindend bis zum Tage der Auferstehung -, daß alles fur euch sei, was ihr befehlt"},
  {"surah_number": 68, "verse_number": 40, "content": "Frage sie, wer von ihnen dafur burgen mag"},
  {"surah_number": 68, "verse_number": 41, "content": "Oder haben sie Partner? So sollen sie ihre Partner herbeibringen, wenn sie die Wahrheit reden"},
  {"surah_number": 68, "verse_number": 42, "content": "Am Tage, wenn die Beine entbloßt werden und sie aufgefordert werden, sich anbetend niederzuwerfen, werden sie es nicht konnen"},
  {"surah_number": 68, "verse_number": 43, "content": "Ihre Blicke werden niedergeschlagen sein, (und) Schande wird sie bedecken; denn sie waren (vergebens) aufgefordert worden, sich anbetend niederzuwerfen, als sie wohlbehalten waren"},
  {"surah_number": 68, "verse_number": 44, "content": "So uberlaß Mir diejenigen, die diese Verkundigung leugnen. Wir werden sie Schritt um Schritt gehen lassen, ohne daß sie es wissen"},
  {"surah_number": 68, "verse_number": 45, "content": "Und Ich gewahre ihnen einen Aufschub; wahrlich, Mein Plan ist vollkommen"},
  {"surah_number": 68, "verse_number": 46, "content": "Oder verlangst du einen Lohn von ihnen, so daß sie sich von einer Schuldenlast bedruckt fuhlen"},
  {"surah_number": 68, "verse_number": 47, "content": "Oder ist das Verborgene bei ihnen, so daß sie (es) niederschreiben konnen"},
  {"surah_number": 68, "verse_number": 48, "content": "So warte geduldig auf den Befehl deines Herrn, und sei nicht wie der Mann des Fisches (Jonas), als er (seinen Herrn) anrief, wahrend er von Kummer erfullt war"},
  {"surah_number": 68, "verse_number": 49, "content": "Ware ihm keine Gnade von seinem Herrn erwiesen worden, ware er sicher an ein kahles Land geworfen worden, und er ware geschmaht worden"},
  {"surah_number": 68, "verse_number": 50, "content": "Doch sein Herr erwahlte ihn und machte ihn zu einem der Rechtschaffenen"},
  {"surah_number": 68, "verse_number": 51, "content": "Und jene, die unglaubig sind, mochten dich gerne mit ihren (zornigen) Blicken zu Fall bringen, wenn sie die Ermahnung horen; und sie sagen: \"Er ist gewiß verruckt"},
  {"surah_number": 68, "verse_number": 52, "content": "Und es ist nichts anderes als eine Ermahnung fur alle Welten"},
  {"surah_number": 69, "verse_number": 1, "content": "Al-Haqqa"},
  {"surah_number": 69, "verse_number": 2, "content": "Was ist Al-Haqqa"},
  {"surah_number": 69, "verse_number": 3, "content": "Und wie kannst du wissen, was Al-Haqqa ist"},
  {"surah_number": 69, "verse_number": 4, "content": "Die Tamud und die `Ad leugneten Al- Qaria"},
  {"surah_number": 69, "verse_number": 5, "content": "Dann, was die Tamud anbelangt, so wurden sie durch einen furchterlichen Schall vernichtet"},
  {"surah_number": 69, "verse_number": 6, "content": "Und was die `Ad anbelangt, so wurden sie durch einen gewaltigen, eiskalten Wind vernichtet"},
  {"surah_number": 69, "verse_number": 7, "content": "den Er sieben Nachte und acht Tage lang ununterbrochen gegen sie wuten ließ, so daß du das Volk hattest sehen konnen, dort niedergestreckt, als waren sie hohle Palm stamme"},
  {"surah_number": 69, "verse_number": 8, "content": "Siehst du von ihnen einen ubrig (geblieben)"},
  {"surah_number": 69, "verse_number": 9, "content": "Und Pharao und diejenigen, die vor ihm waren, und die zusammengesturzten Stadte waren großen Frevels schuldig"},
  {"surah_number": 69, "verse_number": 10, "content": "und sie waren widerspenstig gegen den Gesandten ihres Herrn, darum erfaßte Er sie mit drosselndem Griff"},
  {"surah_number": 69, "verse_number": 11, "content": "Siehe, als das Wasser schwoll, da trugen Wir euch auf dem Schiff"},
  {"surah_number": 69, "verse_number": 12, "content": "so daß Wir es zu einer Erinnerung fur euch machten, und auf daß bewahrende Ohren sie bewahren mogen"},
  {"surah_number": 69, "verse_number": 13, "content": "Und wenn in den Sur gestoßen wird mit einem einzigen Stoß"},
  {"surah_number": 69, "verse_number": 14, "content": "und die Erde samt den Bergen emporgehoben und dann mit einem einzigen Schlag niedergeschmettert wird"},
  {"surah_number": 69, "verse_number": 15, "content": "an jenem Tage wird das Ereignis schon eingetroffen sein"},
  {"surah_number": 69, "verse_number": 16, "content": "Und der Himmel wird sich spalten; denn an jenem Tage wird er bruchig sein"},
  {"surah_number": 69, "verse_number": 17, "content": "Und die Engel werden an seinen Randern stehen, und acht (Engel) werden an jenem Tage den Thron deines Herrn uber sich tragen"},
  {"surah_number": 69, "verse_number": 18, "content": "An jenem Tage werdet ihr (Allah) vorstellig sein - keines eurer Geheimnisse wird verborgen bleiben"},
  {"surah_number": 69, "verse_number": 19, "content": "Was dann den anbelangt, dem sein Buch in die Rechte gegeben wird, so wird er sagen: \"Wohlan, lest mein Buch"},
  {"surah_number": 69, "verse_number": 20, "content": "Wahrlich, ich habe damit gerechnet, daß ich meiner Rechenschaft begegnen werde"},
  {"surah_number": 69, "verse_number": 21, "content": "So wird er ein Wohlleben"},
  {"surah_number": 69, "verse_number": 22, "content": "in einem hochgelegenen Paradies fuhren"},
  {"surah_number": 69, "verse_number": 23, "content": "dessen Fruchte leicht erreichbar sind"},
  {"surah_number": 69, "verse_number": 24, "content": "Esset und trinkt und lasset es euch wohl bekommen fur das, was ihr in den vergangenen Tagen gewirkt habt"},
  {"surah_number": 69, "verse_number": 25, "content": "Was aber den anbelangt, dem sein Buch in die Linke gegeben wird, so wird er sagen: \"O ware mir mein Buch doch nicht gegeben worden"},
  {"surah_number": 69, "verse_number": 26, "content": "Und hatte ich doch nie erfahren, was meine Rechenschaft ist"},
  {"surah_number": 69, "verse_number": 27, "content": "O hatte doch der Tod (mit mir) ein Ende gemacht"},
  {"surah_number": 69, "verse_number": 28, "content": "Mein Besitz hat mir nichts genutzt"},
  {"surah_number": 69, "verse_number": 29, "content": "Meine Macht ist von mir gegangen"},
  {"surah_number": 69, "verse_number": 30, "content": "Ergreift ihn und fesselt ihn"},
  {"surah_number": 69, "verse_number": 31, "content": "dann lasset ihn hierauf in der Gahim brennen"},
  {"surah_number": 69, "verse_number": 32, "content": "Dann legt ihn in eine Kette, deren Lange siebzig Ellen mißt"},
  {"surah_number": 69, "verse_number": 33, "content": "denn er glaubte ja nicht an Allah, den Großen"},
  {"surah_number": 69, "verse_number": 34, "content": "und forderte nicht zur Speisung der Armen auf"},
  {"surah_number": 69, "verse_number": 35, "content": "Hier hat er nun heute keinen Freund"},
  {"surah_number": 69, "verse_number": 36, "content": "und keine Nahrung außer aus Wunden geflossenem Eiter"},
  {"surah_number": 69, "verse_number": 37, "content": "den nur die Sunder essen"},
  {"surah_number": 69, "verse_number": 38, "content": "Doch Ich schwore bei dem, was ihr seht"},
  {"surah_number": 69, "verse_number": 39, "content": "und bei dem, was ihr nicht seht"},
  {"surah_number": 69, "verse_number": 40, "content": "daß dies wahrlich das Wort (Allahs durch den Mund) eines ehrwurdigen Gesandten ist"},
  {"surah_number": 69, "verse_number": 41, "content": "Es ist aber nicht das Werk eines Dichters; wenig ist das, was ihr glaubt"},
  {"surah_number": 69, "verse_number": 42, "content": "noch ist es die Rede eines Wahrsagers; wenig ist das, was ihr bedenkt"},
  {"surah_number": 69, "verse_number": 43, "content": "(Es ist) eine Offenbarung vom Herrn der Welten"},
  {"surah_number": 69, "verse_number": 44, "content": "Und hatte er irgendwelche Ausspruche in Unserem Namen ersonnen"},
  {"surah_number": 69, "verse_number": 45, "content": "hatten Wir ihn gewiß bei der Rechten gefaßt"},
  {"surah_number": 69, "verse_number": 46, "content": "und ihm dann die Herzader durchschnitten"},
  {"surah_number": 69, "verse_number": 47, "content": "Und keiner von euch hatte (Uns) von ihm abhalten konnen"},
  {"surah_number": 69, "verse_number": 48, "content": "Und wahrlich, es ist eine Ermahnung fur die Gottesfurchtigen"},
  {"surah_number": 69, "verse_number": 49, "content": "Und Wir wissen wahrlich, daß einige von euch Leugner sind"},
  {"surah_number": 69, "verse_number": 50, "content": "Und wahrlich, es ist ein schmerzhaftes Bedauern fur die Unglaubigen"},
  {"surah_number": 69, "verse_number": 51, "content": "Und wahrlich, es ist die Wahrheit mit aller Gewißheit"},
  {"surah_number": 69, "verse_number": 52, "content": "Darum preise den Namen deines Großen Herrn"},
  {"surah_number": 70, "verse_number": 1, "content": "Ein Fragender fragt nach einer Strafe, die hereinbrechen wird"},
  {"surah_number": 70, "verse_number": 2, "content": "die fur die Unglaubigen unabwendbar ist"},
  {"surah_number": 70, "verse_number": 3, "content": "sie ist von Allah, Der uber die Himmelsleiter verfugt"},
  {"surah_number": 70, "verse_number": 4, "content": "Die Engel und Gabriel steigen zu Ihm auf in einem Tage, dessen Ausmaß funfzigtausend Jahre betragt"},
  {"surah_number": 70, "verse_number": 5, "content": "Harre darum schon geduldig aus"},
  {"surah_number": 70, "verse_number": 6, "content": "Sie meinen, er sei ferne"},
  {"surah_number": 70, "verse_number": 7, "content": "aber Wir sehen, er ist nahe"},
  {"surah_number": 70, "verse_number": 8, "content": "Am Tage, da der Himmel wie geschmolzenes Metall sein wird"},
  {"surah_number": 70, "verse_number": 9, "content": "und die Berge wie farbige Wollflocken"},
  {"surah_number": 70, "verse_number": 10, "content": "und ein Freund nicht mehr nach einem Freunde fragen wird"},
  {"surah_number": 70, "verse_number": 11, "content": "werden sie in Sichtweite zueinander gebracht werden, und der Schuldige wurde sich wohl (gern) loskaufen von der Strafe jenes Tages mit seinen Kindern"},
  {"surah_number": 70, "verse_number": 12, "content": "und seiner Frau und seinem Bruder"},
  {"surah_number": 70, "verse_number": 13, "content": "und seiner Verwandtschaft, die ihn beherbergt hat"},
  {"surah_number": 70, "verse_number": 14, "content": "und allen, die insgesamt auf Erden sind, wenn es ihn nur retten konnte"},
  {"surah_number": 70, "verse_number": 15, "content": "Nein! Es ist wahrlich eine Feuerflamme"},
  {"surah_number": 70, "verse_number": 16, "content": "die die Kopfhaut ganzlich wegbrennt"},
  {"surah_number": 70, "verse_number": 17, "content": "Den wird sie rufen, der (Mir) den Rucken kehrt und sich (von Mir) abwendet"},
  {"surah_number": 70, "verse_number": 18, "content": "und (Reichtum) aufhauft und hortet"},
  {"surah_number": 70, "verse_number": 19, "content": "Wahrlich, der Mensch ist (seiner Natur nach) kleinmutig geschaffen worden"},
  {"surah_number": 70, "verse_number": 20, "content": "Wenn ihn ein Unheil trifft, so gerat er in große Panik"},
  {"surah_number": 70, "verse_number": 21, "content": "doch wenn ihm (etwas) Gutes zukommt, ist er geizig"},
  {"surah_number": 70, "verse_number": 22, "content": "Nicht so sind diejenigen, die beten"},
  {"surah_number": 70, "verse_number": 23, "content": "und (die Verrichtung) ihrer Gebete einhalten"},
  {"surah_number": 70, "verse_number": 24, "content": "und die, in deren Besitztum ein bestimmter Anteil ist"},
  {"surah_number": 70, "verse_number": 25, "content": "fur den Bittenden und den Unbemittelten"},
  {"surah_number": 70, "verse_number": 26, "content": "und die, die an den Tag des Gerichts glauben"},
  {"surah_number": 70, "verse_number": 27, "content": "und die, die vor der Strafe ihres Herrn besorgt sind"},
  {"surah_number": 70, "verse_number": 28, "content": "wahrlich die Strafe ihres Herrn ist nichts, wovor man sicher sein konnte"},
  {"surah_number": 70, "verse_number": 29, "content": "und die, die ihre Scham bewahren"},
  {"surah_number": 70, "verse_number": 30, "content": "außer bei ihren Gattinnen oder denen, die sie von Rechts wegen besitzen; denn da sind sie nicht zu tadeln"},
  {"surah_number": 70, "verse_number": 31, "content": "Diejenigen aber, die daruber hinaus etwas suchen, das sind die Ubertreter"},
  {"surah_number": 70, "verse_number": 32, "content": "Und die, die mit dem ihnen anvertrauten Gut redlich umgehen und erfullen, wozu sie sich verpflichtet haben"},
  {"surah_number": 70, "verse_number": 33, "content": "und die, die in ihrer Zeugenaussage aufrichtig sind"},
  {"surah_number": 70, "verse_number": 34, "content": "und die, die ihr Gebet getreulich verrichten"},
  {"surah_number": 70, "verse_number": 35, "content": "diese sind es, die in den Garten hochgeehrt sein werden"},
  {"surah_number": 70, "verse_number": 36, "content": "Was aber ist mit denen, die unglaubig sind, daß sie auf dich zugelaufen kommen"},
  {"surah_number": 70, "verse_number": 37, "content": "von rechts und links, nach Gruppen plaziert"},
  {"surah_number": 70, "verse_number": 38, "content": "Hofft jeder einzelne von ihnen wohl, den Garten der Wonne zu betreten"},
  {"surah_number": 70, "verse_number": 39, "content": "Nimmermehr! Sie wissen doch, woraus Wir sie erschufen"},
  {"surah_number": 70, "verse_number": 40, "content": "Aber nein! Ich schwore beim Herrn der Aufgange und der Untergange, daß Wir imstande sind"},
  {"surah_number": 70, "verse_number": 41, "content": "bessere als sie an ihre Stelle zu setzen, und keiner kann Uns (daran) hindern"},
  {"surah_number": 70, "verse_number": 42, "content": "So laß sie nur plaudern und sich vergnugen, bis sie ihrem Tag begegnen, der ihnen angedroht wird"},
  {"surah_number": 70, "verse_number": 43, "content": "dem Tag, an dem sie aus ihren Grabern eilends hervorkommen, als eilten sie zu ihren Gotzenfiguren"},
  {"surah_number": 70, "verse_number": 44, "content": "Ihre Augen werden niedergeschlagen sein; Schmach wird sie bedecken. Das ist der Tag, der ihnen angedroht wird"},
  {"surah_number": 71, "verse_number": 1, "content": "Wahrlich, Wir sandten Noah zu seinem Volk (und sprachen:) \"Warne dein Volk, bevor uber sie eine schmerzliche Strafe kommt"},
  {"surah_number": 71, "verse_number": 2, "content": "Er sagte: \"O mein Volk! Wahrlich, ich bin fur euch ein deutlicher Warner"},
  {"surah_number": 71, "verse_number": 3, "content": "auf daß ihr Allah dienen und Ihn furchten und mir gehorchen moget"},
  {"surah_number": 71, "verse_number": 4, "content": "Dann wird Er euch etwas von euren Sunden vergeben und euch Aufschub bis zu einer bestimmten Frist gewahren. Wahrlich, Allahs Termin kann nicht verschoben werden, wenn er fallig ist - wenn ihr es nur wußtet"},
  {"surah_number": 71, "verse_number": 5, "content": "Er sagte: \"Mein Herr, ich habe mein Volk bei Nacht und Tag (zum Glauben) aufgerufen"},
  {"surah_number": 71, "verse_number": 6, "content": "Doch mein Ruf hat nur bewirkt, daß sie mehr und mehr davonliefen"},
  {"surah_number": 71, "verse_number": 7, "content": "und sooft ich sie rief, daß Du ihnen vergeben mogest, steckten sie ihre Finger in die Ohren und hullten sich in ihre Gewander und verharrten (in ihrem Zustand) und wurden allzu hochmutig"},
  {"surah_number": 71, "verse_number": 8, "content": "Dann rief ich sie in aller Offentlichkeit auf"},
  {"surah_number": 71, "verse_number": 9, "content": "Dann predigte ich ihnen offentlich, und ich redete zu ihnen im geheimen"},
  {"surah_number": 71, "verse_number": 10, "content": "und ich sagte: \"Sucht Vergebung bei eurem Herrn; denn Er ist Allvergebend"},
  {"surah_number": 71, "verse_number": 11, "content": "Er wird Regen fur euch in Fulle her niedersenden"},
  {"surah_number": 71, "verse_number": 12, "content": "und Er wird euch mit Glucksgutern und Kindern starken und wird euch Garten bescheren und fur euch Flusse stromen lassen"},
  {"surah_number": 71, "verse_number": 13, "content": "Was ist mit euch, daß ihr Allah nicht (in der Ihm gebuhrenden Weise) ehrt"},
  {"surah_number": 71, "verse_number": 14, "content": "wo Er euch doch in (verschiedenen) Phasen erschaffen hat"},
  {"surah_number": 71, "verse_number": 15, "content": "Habt ihr nicht gesehen, wie Allah sieben aufeinander geschichtete Himmel geschaffen hat"},
  {"surah_number": 71, "verse_number": 16, "content": "und den Mond als ein Licht in sie gesetzt hat? Und gemacht hat Er die Sonne zu einer Leuchte"},
  {"surah_number": 71, "verse_number": 17, "content": "Und Allah hat euch wie die Pflanzen aus der Erde wachsen lassen"},
  {"surah_number": 71, "verse_number": 18, "content": "Dann wird Er euch wieder in sie zuruckkehren lassen, und Er wird euch dann aus ihr hervorbringen"},
  {"surah_number": 71, "verse_number": 19, "content": "Und Allah hat die Erde fur euch zu einer ausgelegten Flache gemacht"},
  {"surah_number": 71, "verse_number": 20, "content": "auf daß ihr auf ihren gangbaren Wegen ziehen moget"},
  {"surah_number": 71, "verse_number": 21, "content": "Noah sagte: \"Mein Herr, sie haben mir nicht gehorcht und sind einem gefolgt, dessen Reichtum und Kinder nur sein Verderben verstarkt haben"},
  {"surah_number": 71, "verse_number": 22, "content": "Und sie haben gewaltige Ranke geschmiedet"},
  {"surah_number": 71, "verse_number": 23, "content": "Und sie sagen (zueinander): \"Laßt eure Gotter nicht im Stich. Und verlaßt weder Wadd noch Suwa noch Yagutund Yauq und Nasr"},
  {"surah_number": 71, "verse_number": 24, "content": "Und wahrlich, sie haben viele verfuhrt; so mache, daß die Ungerechten selber um so mehr in die Irre gehen"},
  {"surah_number": 71, "verse_number": 25, "content": "Ihrer Sunden wegen wurden sie ertrankt und in ein Feuer gebracht. Und dort konnten sie keine Helfer fur sich gegen Allah finden"},
  {"surah_number": 71, "verse_number": 26, "content": "Und Noah sagte: \"Mein Herr, laß auf der Erdoberflache keinen einzigen von den Unglaubigen (ubrig)"},
  {"surah_number": 71, "verse_number": 27, "content": "denn wenn Du sie laßt, so werden sie nur Deine Diener verfuhren und werden nur eine unverschamte Nachkommenschaft von Unglaubigen zeugen"},
  {"surah_number": 71, "verse_number": 28, "content": "Mein Herr, vergib mir und meinen Eltern und dem, der mein Haus glaubig betritt, und den glaubigen Mannern und den glaubigen Frauen; und sturze die Ungerechten aber um so tiefer ins Verderben"},
  {"surah_number": 72, "verse_number": 1, "content": "Sprich: \"Es wurde mir offenbart, daß eine Schar der Ginn zuhorte und dann sagte: \"Wahrlich, wir haben einen wunderbaren Quran gehort"},
  {"surah_number": 72, "verse_number": 2, "content": "der zur Rechtschaffenheit leitet; so haben wir an ihn geglaubt, und wir werden unserem Herrn nie jemanden zur Seite stellen"},
  {"surah_number": 72, "verse_number": 3, "content": "Und (wir haben gehort,) daß unser Herr - Erhaben ist Er - Sich weder Gattin noch Sohn genommen hat"},
  {"surah_number": 72, "verse_number": 4, "content": "und daß die Toren unter uns abscheuliche Lugen uber Allah zu außern pflegten"},
  {"surah_number": 72, "verse_number": 5, "content": "Und wir hatten angenommen, daß weder Menschen noch Ginn je eine Luge uber Allah sprechen wurden"},
  {"surah_number": 72, "verse_number": 6, "content": "und daß freilich einige Leute von den Menschen bei einigen Leuten der Ginn Schutz zu suchen pflegten, so daß sie letztere in ihrer Schlechtigkeit bestarkten"},
  {"surah_number": 72, "verse_number": 7, "content": "und daß sie freilich dachten, ebenso wie ihr denkt, Allah wurde nie einen (Propheten) erwecken"},
  {"surah_number": 72, "verse_number": 8, "content": "Und wir suchten den Himmel, doch wir fanden ihn mit starken Wachtern und (schießenden) Sternschnuppen erfullt"},
  {"surah_number": 72, "verse_number": 9, "content": "Und wir pflegten auf einigen seiner Sitze zu sitzen, um zu lauschen. Wer aber jetzt lauscht, der findet einen schießenden Stern fur sich auf der Lauer"},
  {"surah_number": 72, "verse_number": 10, "content": "Und wir wissen nicht, ob (etwas) Boses fur diejenigen beabsichtigt ist, die auf Erden sind, oder ob ihnen der Herr (etwas) Gutes zukommen lassen will"},
  {"surah_number": 72, "verse_number": 11, "content": "Und manche unter uns sind solche, die rechtschaffen (handeln), und manche unter uns sind weit davon entfernt; wir sind Sekten, die verschiedene Wege gehen"},
  {"surah_number": 72, "verse_number": 12, "content": "Und wir wissen, daß wir auf keine Weise Allah auf Erden zuschanden machen konnen, noch konnen wir Ihm durch Flucht entrinnen"},
  {"surah_number": 72, "verse_number": 13, "content": "Und als wir aber von der Rechtleitung vernahmen, da glaubten wir an sie. Und der, der an seinen Herrn glaubt, furchtet weder Einbuße noch Unrecht"},
  {"surah_number": 72, "verse_number": 14, "content": "Und manche unter uns sind Gottergebene, und manche unter uns sind vom rechten Weg abgewichen.\"\" Und die sich ergeben haben diese haben den rechten Weg gefunden"},
  {"surah_number": 72, "verse_number": 15, "content": "Diejenigen, die aber vom rechten Wege abweichen, werden Brennstoff der Gahannam sein"},
  {"surah_number": 72, "verse_number": 16, "content": "Wenn sie aber den (rechten) Weg einhalten, dann werden Wir ihnen reichlich Wasser zu trinken geben"},
  {"surah_number": 72, "verse_number": 17, "content": "um sie dadurch zu prufen. Wer sich dann von der Ermahnung seines Herrn abwendet - Er wird ihn in eine zunehmende Strafe stoßen"},
  {"surah_number": 72, "verse_number": 18, "content": "Und wahrlich, die Moscheen sind Allahs; so ruft niemanden neben Allah an"},
  {"surah_number": 72, "verse_number": 19, "content": "Und als der Diener Allahs aufstand, um zu Ihm zu beten, da umdrangten sie ihn, so daß sie sich fast erdruckten"},
  {"surah_number": 72, "verse_number": 20, "content": "Sprich: \"lch rufe einzig meinen Herrn an, und ich stelle Ihm niemanden zur Seite"},
  {"surah_number": 72, "verse_number": 21, "content": "Sprich: \"lch habe nicht die Macht, euch Schaden oder Nutzen zuzufugen"},
  {"surah_number": 72, "verse_number": 22, "content": "Sprich: \"Wahrlich, keiner kann mich vor Allah beschutzen, noch kann ich eine Zuflucht außer bei Ihm finden"},
  {"surah_number": 72, "verse_number": 23, "content": "(lch habe) nur die Ubermittlung (der Offenbarung) von Allah und Seine Botschaften (auszurichten)\" Und fur diejenigen, die sich Allah und Seinem Gesandten widersetzen, ist das Feuer der Holle bestimmt; darin werden sie auf ewig bleiben"},
  {"surah_number": 72, "verse_number": 24, "content": "Wenn sie dann das sehen werden, das ihnen angedroht wird, so werden sie erfahren, wer schwacher an Helfern und geringer an Zahl ist"},
  {"surah_number": 72, "verse_number": 25, "content": "Sprich: \"lch weiß nicht, ob das euch Angedrohte nahe ist, oder ob mein Herr eine lange Frist dafur angesetzt hat"},
  {"surah_number": 72, "verse_number": 26, "content": "Er ist der Kenner des Verborgenen - Er enthullt keinem Seine Kenntnis vom Verborgenen"},
  {"surah_number": 72, "verse_number": 27, "content": "außer allein dem, den Er unter Seinen Gesandten erwahlt hat. Und dann laßt Er vor ihm und hinter ihm eine Schutzwache"},
  {"surah_number": 72, "verse_number": 28, "content": "damit Er gewahrleistet sieht, daß sie (Seine Gesandten) die Botschaften ihres Herrn verkundet haben. Und Er umfaßt alles, was bei ihnen ist, und Er zeichnet alle Dinge ganz genau auf"},
  {"surah_number": 73, "verse_number": 1, "content": "O du Verhullter"},
  {"surah_number": 73, "verse_number": 2, "content": "Verbringe die Nacht stehend (im Gebet) bis auf wenige Zeit davon"},
  {"surah_number": 73, "verse_number": 3, "content": "die Halfte von ihr, oder verringere sie ein wenig"},
  {"surah_number": 73, "verse_number": 4, "content": "oder fuge ein wenig hinzu - und trage den Quran mit Tartil vor"},
  {"surah_number": 73, "verse_number": 5, "content": "Wahrlich, Wir legen dir da ein Wort auf, das gewichtig ist"},
  {"surah_number": 73, "verse_number": 6, "content": "Wahrlich, der Anbruch der Nacht ist die beste Zeit zur Selbstzucht und zur Erreichung von Aufrichtigkeit im Reden"},
  {"surah_number": 73, "verse_number": 7, "content": "Du hast ja gewiß wahrend des Tages eine lange Beschaftigung"},
  {"surah_number": 73, "verse_number": 8, "content": "Und gedenke des Namens deines Herrn und wende dich Ihm von ganzem Herzen zu"},
  {"surah_number": 73, "verse_number": 9, "content": "(Er ist) der Herr des Ostens und des Westens - es ist kein Gott außer Ihm; darum nimm Ihn zum Beschutzer"},
  {"surah_number": 73, "verse_number": 10, "content": "Und ertrage in Geduld alles, was sie reden; und halte dich von ihnen in angenehmer Weise zuruck"},
  {"surah_number": 73, "verse_number": 11, "content": "Und uberlaß Mir diejenigen, die (die Wahrheit) leugnen und sich des Wohllebens erfreuen; und gewahre ihnen eine kurze Frist"},
  {"surah_number": 73, "verse_number": 12, "content": "Bei Uns sind wahrlich Fesseln und Gahim"},
  {"surah_number": 73, "verse_number": 13, "content": "und erstickende Speise und schmerzliche Strafe"},
  {"surah_number": 73, "verse_number": 14, "content": "an dem Tage, da die Erde und die Berge erbeben und die Berge eine Menge zusammengesunkener Dunen sein werden"},
  {"surah_number": 73, "verse_number": 15, "content": "Wahrlich, Wir haben euch einen Gesandten geschickt, der euer Zeuge ist, wie Wir zu Pharao einen Gesandten geschickt hatten"},
  {"surah_number": 73, "verse_number": 16, "content": "Doch Pharao widersetzte sich dem Gesandten; darum erfaßten Wir ihn mit einem schrecklichen Strafgericht"},
  {"surah_number": 73, "verse_number": 17, "content": "Wie wollt ihr euch, wenn ihr unglaubig seid, wohl vor einem Tag schutzen, der Kinder zu Greisen macht"},
  {"surah_number": 73, "verse_number": 18, "content": "Der Himmel wird sich an ihm spalten! Seine Verheißung muß in Erfullung gehen"},
  {"surah_number": 73, "verse_number": 19, "content": "Dies ist wahrlich eine Ermahnung. So nehme nun, wer da will, den Weg zu seinem Herrn"},
  {
    "surah_number": 73,
    "verse_number": 20,
    "content":
        "Dein Herr weiß wahrlich, daß du (im Gebet etwas) weniger als zwei Drittel der Nacht stehst und (manchmal) eine Halfte oder ein Drittel (der Nacht), und ein Teil derer, die mit dir sind (, tut desgleichen) Und Allah bestimmt das Maß der Nacht und des Tages. Er weiß, daß ihr sie (die Ausdauer) nicht werdet verkraften konnen. Darum hat Er Sich euch mit Nachsicht zugewandt. So tragt denn so viel vom Quran vor, wie es (euch) leicht fallt. Er weiß, daß einige unter euch sein werden, die krank sind, und andere, die im Lande umherreisen - nach Allahs Gnadenfulle strebend -, und wieder andere, die fur Allahs Sache kampfen. So tragt von ihm das vor, was (euch) leicht fallt, und verrichtet das Gebet und entrichtet die Zakah und gebt Allah ein gutes Darlehen. Und das, was ihr an Gutem fur eure Seelen vorausschickt, werdet ihr bei Allah als besseren und großeren Lohn finden. Und bittet Allah um Vergebung. Wahrlich, Allah ist Allvergebend, Barmherzig"
  },
  {"surah_number": 74, "verse_number": 1, "content": "O du Bedeckter"},
  {"surah_number": 74, "verse_number": 2, "content": "Erhebe dich und warne"},
  {"surah_number": 74, "verse_number": 3, "content": "und verherrliche deinen Herrn"},
  {"surah_number": 74, "verse_number": 4, "content": "und reinige deine Kleider"},
  {"surah_number": 74, "verse_number": 5, "content": "und meide den Gotzendienst"},
  {"surah_number": 74, "verse_number": 6, "content": "und sei nicht wohltatig in Erwartung von personlichen Vorteilen"},
  {"surah_number": 74, "verse_number": 7, "content": "und sei standhaft um deines Herrn willen"},
  {"surah_number": 74, "verse_number": 8, "content": "Wenn in den Sur gestoßen wird"},
  {"surah_number": 74, "verse_number": 9, "content": "dann wird der Tag ein schwerer Tag sein"},
  {"surah_number": 74, "verse_number": 10, "content": "kein leichter fur die Unglaubigen"},
  {"surah_number": 74, "verse_number": 11, "content": "Laß Mich mit dem, den Ich als Einsamen geschaffen habe"},
  {"surah_number": 74, "verse_number": 12, "content": "und dem Ich Besitz in Fulle verlieh"},
  {"surah_number": 74, "verse_number": 13, "content": "und Sohne, die immer zugegen waren"},
  {"surah_number": 74, "verse_number": 14, "content": "und fur den Ich alle Bequemlichkeit bereitete"},
  {"surah_number": 74, "verse_number": 15, "content": "Dennoch wunscht er, daß Ich noch mehr gebe"},
  {"surah_number": 74, "verse_number": 16, "content": "Nein; denn er ist Unseren Zeichen feindlich gesonnen gewesen"},
  {"surah_number": 74, "verse_number": 17, "content": "Ich werde ihm bald schreckliche Muhsal aufburden"},
  {"surah_number": 74, "verse_number": 18, "content": "Siehe, er sann und wog ab"},
  {"surah_number": 74, "verse_number": 19, "content": "Darum Verderben uber ihn! Wie wog er ab"},
  {"surah_number": 74, "verse_number": 20, "content": "Wiederum Verderben uber ihn! Wie wog er ab"},
  {"surah_number": 74, "verse_number": 21, "content": "Dann schaute er"},
  {"surah_number": 74, "verse_number": 22, "content": "dann runzelte er die Stirn und blickte verdrießlich"},
  {"surah_number": 74, "verse_number": 23, "content": "dann wandte er sich ab und wurde hochmutig"},
  {"surah_number": 74, "verse_number": 24, "content": "und sagte: \"Das ist nichts als Zauberei die weitergegeben wird"},
  {"surah_number": 74, "verse_number": 25, "content": "Das ist nur ein Menschenwort"},
  {"surah_number": 74, "verse_number": 26, "content": "Bald werde Ich ihn in Saqar brennen lassen"},
  {"surah_number": 74, "verse_number": 27, "content": "Und wie kannst du wissen, was Saqar ist"},
  {"surah_number": 74, "verse_number": 28, "content": "Sie verschont nichts und laßt nichts ubrig"},
  {"surah_number": 74, "verse_number": 29, "content": "und wird von den Menschen aus großer Entfernung wahrgenommen"},
  {"surah_number": 74, "verse_number": 30, "content": "sie wird von neunzehn (Engeln) uberwacht"},
  {
    "surah_number": 74,
    "verse_number": 31,
    "content":
        "Und Wir haben einzig und allein Engel zu Hutern des Feuers gemacht. Und Wir setzten ihre Anzahl nicht fest, außer zur Prufung derer, die unglaubig sind, auf daß die, denen das Buch gegeben wurde, Gewißheit erreichen, und auf daß die, die glaubig sind, an Glauben zunehmen, und auf daß die, denen die Schrift gegeben wurde, und die Glaubigen, nicht zweifeln, und auf daß die, in deren Herzen Krankheit ist, und die Unglaubigen sagen mogen: \"Was meint Allah mit diesem Gleichnis?\" Somit erklart Allah zum Irrenden, wen Er will, und leitet recht, wen Er will. Und keiner kennt die Heerscharen deines Herrn als Er allein. Dies ist nur eine Ermahnung fur die Menschen"
  },
  {"surah_number": 74, "verse_number": 32, "content": "Nein, bei dem Mond"},
  {"surah_number": 74, "verse_number": 33, "content": "und bei der Nacht, wenn sie zu Ende geht"},
  {"surah_number": 74, "verse_number": 34, "content": "und bei dem Morgen, wenn er anbricht"},
  {"surah_number": 74, "verse_number": 35, "content": "Wahrlich, es ist eine der großten (Katastrophen)"},
  {"surah_number": 74, "verse_number": 36, "content": "eine Warnung fur die Menschen"},
  {"surah_number": 74, "verse_number": 37, "content": "fur die unter euch, die vorwarts schreiten oder zuruckbleiben wollen"},
  {"surah_number": 74, "verse_number": 38, "content": "Ein jeder wird fur das aufkommen, was er vorausgeschickt hat"},
  {"surah_number": 74, "verse_number": 39, "content": "ausgenommen die von der Rechten"},
  {"surah_number": 74, "verse_number": 40, "content": "die einander in Garten fragen"},
  {"surah_number": 74, "verse_number": 41, "content": "nach den Schuldigen"},
  {"surah_number": 74, "verse_number": 42, "content": "Was hat euch in Saqar gebracht"},
  {"surah_number": 74, "verse_number": 43, "content": "Sie sagen: \"Wir waren nicht bei denen die beteten"},
  {"surah_number": 74, "verse_number": 44, "content": "noch speisten wir die Armen"},
  {"surah_number": 74, "verse_number": 45, "content": "Und wir ließen uns ein im Geschwatz mit den Schwatzern"},
  {"surah_number": 74, "verse_number": 46, "content": "Und wir pflegten den Tag des Gerichts zu leugnen"},
  {"surah_number": 74, "verse_number": 47, "content": "bis der Tod uns ereilte"},
  {"surah_number": 74, "verse_number": 48, "content": "Darum wird ihnen die Fursprache der Fursprecher nichts nutzen"},
  {"surah_number": 74, "verse_number": 49, "content": "Was ist ihnen denn, daß sie sich von der Ermahnung abwenden"},
  {"surah_number": 74, "verse_number": 50, "content": "als waren sie erschreckte Wildesel"},
  {"surah_number": 74, "verse_number": 51, "content": "die vor einem Lowen fliehen"},
  {"surah_number": 74, "verse_number": 52, "content": "Nein, jeder von ihnen wunscht, es mochten ihm offene Tafeln der Offenbarung gegeben werden"},
  {"surah_number": 74, "verse_number": 53, "content": "Nein! Wahrlich, sie furchten nicht das Jenseits"},
  {"surah_number": 74, "verse_number": 54, "content": "Nein! Wahrlich, dies ist eine Ermahnung"},
  {"surah_number": 74, "verse_number": 55, "content": "So moge, wer da will, ihrer gedenken"},
  {"surah_number": 74, "verse_number": 56, "content": "Und sie werden sich nicht ermahnen lassen, bis es Allah so will. Er ist der Ehrfurcht und der Vergebung Wurdig"},
  {"surah_number": 75, "verse_number": 1, "content": "Ich schwore beim Tag der Auferstehung"},
  {"surah_number": 75, "verse_number": 2, "content": "und Ich schwore bei jeder reumutigen Seele"},
  {"surah_number": 75, "verse_number": 3, "content": "Meint der Mensch etwa, daß Wir seine Gebeine nicht sammeln werden"},
  {"surah_number": 75, "verse_number": 4, "content": "Aber ja, Wir sind imstande, seine Finger gleichmaßig zu formen"},
  {"surah_number": 75, "verse_number": 5, "content": "Doch der Mensch wunscht sich, Sundhaftigkeit vorauszuschicken"},
  {"surah_number": 75, "verse_number": 6, "content": "Er fragt: \"Wann wird der Tag der Auferstehung sein"},
  {"surah_number": 75, "verse_number": 7, "content": "Dann, wenn das Auge geblendet ist"},
  {"surah_number": 75, "verse_number": 8, "content": "und der Mond sich verfinstert"},
  {"surah_number": 75, "verse_number": 9, "content": "und die Sonne und der Mond miteinander vereinigt werden"},
  {"surah_number": 75, "verse_number": 10, "content": "An jenem Tage wird der Mensch sagen: \"Wohin (konnte ich) nun fliehen"},
  {"surah_number": 75, "verse_number": 11, "content": "Nein! Es gibt keine Zuflucht"},
  {"surah_number": 75, "verse_number": 12, "content": "(Nur) bei deinem Herrn wird an jenem Tage die Endstation sein"},
  {"surah_number": 75, "verse_number": 13, "content": "Verkundet wird dem Menschen an jenem Tage, was er vorausgeschickt und was er zuruckgelassen hat"},
  {"surah_number": 75, "verse_number": 14, "content": "Nein, der Mensch ist Zeuge gegen sich selber"},
  {"surah_number": 75, "verse_number": 15, "content": "auch wenn er seine Entschuldigungen vorbringt"},
  {"surah_number": 75, "verse_number": 16, "content": "Bewege deine Zunge nicht mit ihm (dem Quran), um dich damit zu ubereilen"},
  {"surah_number": 75, "verse_number": 17, "content": "Uns obliegt seine Sammlung und seine Verlesung"},
  {"surah_number": 75, "verse_number": 18, "content": "Darum folge seiner Verlesung, wenn Wir ihn verlesen lassen"},
  {"surah_number": 75, "verse_number": 19, "content": "dann obliegt Uns, seine Bedeutung darzulegen"},
  {"surah_number": 75, "verse_number": 20, "content": "Nein, ihr aber liebt das Weltliche"},
  {"surah_number": 75, "verse_number": 21, "content": "und vernachlassigt das Jenseits"},
  {"surah_number": 75, "verse_number": 22, "content": "An jenem Tage wird es strahlende Gesichter geben"},
  {"surah_number": 75, "verse_number": 23, "content": "die zu ihrem Herrn schauen"},
  {"surah_number": 75, "verse_number": 24, "content": "Und manche Gesichter werden an jenem Tage gramvoll sein"},
  {"surah_number": 75, "verse_number": 25, "content": "denn sie ahnen, daß ihnen bald darauf ein schreckliches Ungluck widerfahren soll"},
  {"surah_number": 75, "verse_number": 26, "content": "Ja! Wenn (die Seele eines Sterbenden) bis zum Schlusselbein emporsteigt"},
  {"surah_number": 75, "verse_number": 27, "content": "und gesprochen wird: \"Wer kann die Zauberformel sprechen(, um sie zu retten)"},
  {"surah_number": 75, "verse_number": 28, "content": "und er (der Mensch) wahnt, daß (die Stunde des) Abschieds gekommen ist"},
  {"surah_number": 75, "verse_number": 29, "content": "und (daß) sich Bein mit Bein (im Todeskampf) verfangt"},
  {"surah_number": 75, "verse_number": 30, "content": "dann wird an jenem Tage das Treiben zu deinem Herrn sein"},
  {"surah_number": 75, "verse_number": 31, "content": "denn er spendete nicht und betete nicht"},
  {"surah_number": 75, "verse_number": 32, "content": "sondern er leugnete und wandte sich (von Ihm) ab"},
  {"surah_number": 75, "verse_number": 33, "content": "Dann ging er mit stolzem Gang zu den Seinen"},
  {"surah_number": 75, "verse_number": 34, "content": "Wehe dir denn! Wehe"},
  {"surah_number": 75, "verse_number": 35, "content": "Und abermals wehe dir! Und nochmals wehe"},
  {"surah_number": 75, "verse_number": 36, "content": "Meint der Mensch etwa, er wurde sich selber uberlassen sein"},
  {"surah_number": 75, "verse_number": 37, "content": "War er nicht ein Erguß verspritzten Spermas"},
  {"surah_number": 75, "verse_number": 38, "content": "Dann wurde er ein Blutklumpen; dann bildete und vervollkommnete Er (ihn)"},
  {"surah_number": 75, "verse_number": 39, "content": "Alsdann schuf Er aus ihm ein Paar, den Mann und die Frau"},
  {"surah_number": 75, "verse_number": 40, "content": "Ist Er denn nicht imstande, die Toten ins Leben zu rufen"},
  {"surah_number": 76, "verse_number": 1, "content": "Gab es nicht fur den Menschen eine Zeit, da er nichts Nennenswertes war"},
  {"surah_number": 76, "verse_number": 2, "content": "Wahrlich, Wir erschufen den Menschen aus einer Ergußmischung, auf daß Wir ihn prufen mochten; dann machten Wir ihn horend und sehend"},
  {"surah_number": 76, "verse_number": 3, "content": "Wir haben ihm den rechten Weg gezeigt, mochte er nun dankbar oder undankbar sein"},
  {"surah_number": 76, "verse_number": 4, "content": "Wahr lich, Wir haben fur die Unglaubigen Ketten, eiserne Nackenfesseln und einen Feuerbrand bereitet"},
  {"surah_number": 76, "verse_number": 5, "content": "Die Rechtschaffenen aber trinken aus einem Becher, dem Kampfer beigemischt ist"},
  {"surah_number": 76, "verse_number": 6, "content": "(Er wird gespeist aus) einer Quelle, von der die Diener Allahs trinken, und die sie in reichlichem Maße hervorsprudeln lassen"},
  {"surah_number": 76, "verse_number": 7, "content": "Sie vollbringen das Gelubde, und sie furchten einen Tag, dessen Ubel sich weithin ausbreitet"},
  {"surah_number": 76, "verse_number": 8, "content": "Und sie geben Speise und mag sie ihnen (auch) noch so lieb sein dem Armen, der Waise und dem Gefangenen"},
  {"surah_number": 76, "verse_number": 9, "content": "(indem sie sagen:) \"Wir speisen euch nur um Allahs willen. Wir begehren von euch weder Lohn noch Dank dafur"},
  {"surah_number": 76, "verse_number": 10, "content": "Wahrlich, wir furchten von unserem Herrn einen finsteren, unheilvollen Tag"},
  {"surah_number": 76, "verse_number": 11, "content": "Darum wird Allah sie vor dem Ubel jenes Tages bewahren und ihnen Herzensfreude und Gluckseligkeit bescheren"},
  {"surah_number": 76, "verse_number": 12, "content": "Und Er wird sie fur ihre Geduld mit einem Paradies und seidenen (Gewandern) belohnen"},
  {"surah_number": 76, "verse_number": 13, "content": "Darin lehnen sie auf erhohten Sitzen, (und) sie werden dort weder Sonnenhitze noch Eiseskalte erleben"},
  {"surah_number": 76, "verse_number": 14, "content": "Und seine Schatten werden tief auf sie herabreichen, und seine gebuschelten Fruchte machen sich ganz leicht zu greifen"},
  {"surah_number": 76, "verse_number": 15, "content": "Und Trinkgefaße aus Silber werden unter ihnen die Runde machen, und Pokale, (durchsichtig) wie Glas"},
  {"surah_number": 76, "verse_number": 16, "content": "doch Glaser aus Silber, und sie werden ihren Umfang nach Maß bemessen"},
  {"surah_number": 76, "verse_number": 17, "content": "Und es wird ihnen dort ein Becher zu trinken gereicht werden, dem Ingwer beigemischt ist"},
  {"surah_number": 76, "verse_number": 18, "content": "(Er wird gespeist aus) einer Quelle darin, die Salsabil genannt wird"},
  {"surah_number": 76, "verse_number": 19, "content": "Und es werden sie dort ewig junge Knaben bedienen. Wenn du sie siehst, haltst du sie fur verstreute Perlen"},
  {"surah_number": 76, "verse_number": 20, "content": "Und wohin du dort auch schauen magst, so wirst du ein Wohlleben und ein großes Reich erblicken"},
  {"surah_number": 76, "verse_number": 21, "content": "Sie werden Gewander aus feiner, gruner Seide und Brokat tragen. Sie werden mit silbernen Spangen geschmuckt sein. Und ihr Herr wird sie von einem reinen Trank trinken lassen"},
  {"surah_number": 76, "verse_number": 22, "content": "Das ist euer Lohn, und euer Bemuhen ist mit Dank angenommen worden"},
  {"surah_number": 76, "verse_number": 23, "content": "Wahrlich, Wir Selbst haben dir den Quran als Offenbarung herabgesandt"},
  {"surah_number": 76, "verse_number": 24, "content": "So warte geduldig auf den Befehl deines Herrn und gehorche keinem, der ein Sunder oder ein Unglaubiger unter ihnen ist"},
  {"surah_number": 76, "verse_number": 25, "content": "Und gedenke des Namens deines Herrn am Morgen und am Abend"},
  {"surah_number": 76, "verse_number": 26, "content": "Und wirf dich in einem Teil der Nacht vor Ihm in Anbetung nieder und preise Seine Herrlichkeit einen langen Teil der Nacht hindurch"},
  {"surah_number": 76, "verse_number": 27, "content": "Wahrlich, diese lieben das Weltliche und vernachlassigen den Tag, der hinterher auf (ihnen) lastet"},
  {"surah_number": 76, "verse_number": 28, "content": "Wir haben sie erschaffen und ihrer Beschaffenheit Festigkeit verliehen; und wenn Wir wollen, konnen Wir andere ihresgleichen an ihre Stelle setzten"},
  {"surah_number": 76, "verse_number": 29, "content": "Wahrlich, dies ist eine Ermahnung. So moge, wer da will, einen Weg zu seinem Herrn einschlagen"},
  {"surah_number": 76, "verse_number": 30, "content": "Und ihr konnt nur wollen, wenn Allah will. Wahrlich, Allah ist Allwissend, Allweise"},
  {"surah_number": 76, "verse_number": 31, "content": "Er laßt, wen Er will, in Seine Barmherzigkeit eingehen, und fur die Ungerechten hat Er eine qualvolle Strafe bereitet"},
  {"surah_number": 77, "verse_number": 1, "content": "Bei den Windstoßen, die einander folgen"},
  {"surah_number": 77, "verse_number": 2, "content": "und bei den Sturmen, die durcheinander wirbeln"},
  {"surah_number": 77, "verse_number": 3, "content": "und bei den (Engeln), die stets (die Wolken) verbreiten"},
  {"surah_number": 77, "verse_number": 4, "content": "und dann zwischen (Gut und Bose) unterscheiden"},
  {"surah_number": 77, "verse_number": 5, "content": "und dann die Ermahnung uberall hinabtragen"},
  {"surah_number": 77, "verse_number": 6, "content": "um zu entschuldigen oder zu warnen"},
  {"surah_number": 77, "verse_number": 7, "content": "Wahrlich, was euch verheißen wird, wird bestimmt in Erfullung gehen"},
  {"surah_number": 77, "verse_number": 8, "content": "dann, wenn die Sterne erloschen"},
  {"surah_number": 77, "verse_number": 9, "content": "und der Himmel sich offnet"},
  {"surah_number": 77, "verse_number": 10, "content": "und wenn die Berge hinweggeblasen sind"},
  {"surah_number": 77, "verse_number": 11, "content": "und die Gesandten zu ihrer vorbestimmten Zeit gebracht werden"},
  {"surah_number": 77, "verse_number": 12, "content": "Fur welchen Tag sind (diese Geschehnisse) aufgeschoben worden"},
  {"surah_number": 77, "verse_number": 13, "content": "Fur den Tag der Entscheidung"},
  {"surah_number": 77, "verse_number": 14, "content": "Und wie kannst du wissen, was der Tag der Entscheidung ist"},
  {"surah_number": 77, "verse_number": 15, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 16, "content": "Haben Wir nicht die fruheren (Generationen) vernichtet"},
  {"surah_number": 77, "verse_number": 17, "content": "alsdann ihnen die spate ren folgen lassen"},
  {"surah_number": 77, "verse_number": 18, "content": "So verfahren Wir mit den Schuldigen"},
  {"surah_number": 77, "verse_number": 19, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 20, "content": "Schufen Wir euch nicht aus einer verachtlichen Flussigkeit"},
  {"surah_number": 77, "verse_number": 21, "content": "die Wir dann an eine geschutzte Bleibe brachten"},
  {"surah_number": 77, "verse_number": 22, "content": "fur eine bestimmte Fugung"},
  {"surah_number": 77, "verse_number": 23, "content": "So setzten Wir das Maß fest. Wie trefflich ist Unsere Bemessung"},
  {"surah_number": 77, "verse_number": 24, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 25, "content": "Haben Wir die Erde nicht zu eurer Aufnahme ge macht"},
  {"surah_number": 77, "verse_number": 26, "content": "fur die Lebenden und die Toten"},
  {"surah_number": 77, "verse_number": 27, "content": "und auf sie hohe Berge gesetzt und euch wohlschmeckendes Wasser zu trinken gegeben"},
  {"surah_number": 77, "verse_number": 28, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 29, "content": "Geht nun hin zu dem, was ihr verleugnet habt"},
  {"surah_number": 77, "verse_number": 30, "content": "Geht hin zu einem Schatten, der drei Verzweigungen hat"},
  {"surah_number": 77, "verse_number": 31, "content": "der weder Schatten spendet noch vor der Flamme schutzt"},
  {"surah_number": 77, "verse_number": 32, "content": "Siehe, sie (die Holle) wirft Funken gleich den Turmen eines Palastes empor"},
  {"surah_number": 77, "verse_number": 33, "content": "als waren sie Kamele von hellgelber Farbe"},
  {"surah_number": 77, "verse_number": 34, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 35, "content": "Das ist ein Tag, an dem ihnen die Sprache versagt"},
  {"surah_number": 77, "verse_number": 36, "content": "Es wird ihnen nicht erlaubt sein, Entschuldigungen vorzubringen"},
  {"surah_number": 77, "verse_number": 37, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 38, "content": "Dies ist der Tag der Entscheidung. Wir haben euch und die Fruheren versammelt"},
  {"surah_number": 77, "verse_number": 39, "content": "Habt ihr nun eine List, so setzt eure List gegen Mich ein"},
  {"surah_number": 77, "verse_number": 40, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 41, "content": "Die Gottesfurchtigen werden inmitten von Schatten und Quellen sein"},
  {"surah_number": 77, "verse_number": 42, "content": "und Fruchten, welche sie sich wunschen"},
  {"surah_number": 77, "verse_number": 43, "content": "Esset und trinkt in Gesundheit um dessentwillen, was ihr getan habt"},
  {"surah_number": 77, "verse_number": 44, "content": "Wahrlich, so belohnen Wir diejenigen, die Gutes tun"},
  {"surah_number": 77, "verse_number": 45, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 46, "content": "Esset und ergotzt euch eine kleine Weile. Gewiß, ihr seid die Sunder"},
  {"surah_number": 77, "verse_number": 47, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 48, "content": "Und wenn zu ihnen gesprochen wird: \"Beugt euch!\" beugen sie sich nicht"},
  {"surah_number": 77, "verse_number": 49, "content": "Wehe an jenem Tag den Leugnern"},
  {"surah_number": 77, "verse_number": 50, "content": "An welches Wort, nach diesem, wollen sie denn glauben"},
  {"surah_number": 78, "verse_number": 1, "content": "Wonach befragen sie einander"},
  {"surah_number": 78, "verse_number": 2, "content": "Nach einer gewaltigen Ankundigung"},
  {"surah_number": 78, "verse_number": 3, "content": "uber die sie uneinig sind"},
  {"surah_number": 78, "verse_number": 4, "content": "Nein! Sie werden es bald erfahren"},
  {"surah_number": 78, "verse_number": 5, "content": "Und abermals nein! Sie werden es bald erfahren"},
  {"surah_number": 78, "verse_number": 6, "content": "Haben Wir nicht die Erde zu einem Lager gemacht"},
  {"surah_number": 78, "verse_number": 7, "content": "und die Berge zu Pflocken"},
  {"surah_number": 78, "verse_number": 8, "content": "Und Wir haben euch in Paaren erschaffen"},
  {"surah_number": 78, "verse_number": 9, "content": "und Wir haben euch den Schlaf zur Ruhe gemacht"},
  {"surah_number": 78, "verse_number": 10, "content": "und die Nacht zu einer Hulle"},
  {"surah_number": 78, "verse_number": 11, "content": "und den Tag zum Erwerb des Unterhalts"},
  {"surah_number": 78, "verse_number": 12, "content": "und Wir haben uber euch sieben starke (Himmel) erbaut"},
  {"surah_number": 78, "verse_number": 13, "content": "und Wir haben eine hellbrennende Leuchte gemacht"},
  {"surah_number": 78, "verse_number": 14, "content": "und Wir senden aus den Regenwolken Wasser in Stromen hernieder"},
  {"surah_number": 78, "verse_number": 15, "content": "auf daß Wir damit Korn und Kraut hervorbringen"},
  {"surah_number": 78, "verse_number": 16, "content": "sowie uppige Garten"},
  {"surah_number": 78, "verse_number": 17, "content": "Wahrlich, der Tag der Entscheidung ist ein fester Termin"},
  {"surah_number": 78, "verse_number": 18, "content": "an jenem Tag, da in den Sur gestoßen wird und ihr in Scharen kommt"},
  {"surah_number": 78, "verse_number": 19, "content": "und der Himmel sich offnet und zu Toren wird"},
  {"surah_number": 78, "verse_number": 20, "content": "und die Berge sich bewegen und zur Luftspiegelung werden"},
  {"surah_number": 78, "verse_number": 21, "content": "Wahrlich, Gahannam ist ein Hinterhalt"},
  {"surah_number": 78, "verse_number": 22, "content": "eine Heimstatte fur die Widerspenstigen"},
  {"surah_number": 78, "verse_number": 23, "content": "die dort Epoche fur Epoche bleiben"},
  {"surah_number": 78, "verse_number": 24, "content": "sie werden dort weder Kuhle noch Trank kosten"},
  {"surah_number": 78, "verse_number": 25, "content": "außer siedendem Wasser und Eiter"},
  {"surah_number": 78, "verse_number": 26, "content": "(Dies ist) ein Lohn in angemessener Weise"},
  {"surah_number": 78, "verse_number": 27, "content": "(weil) sie mit keiner Rechenschaft gerechnet haben"},
  {"surah_number": 78, "verse_number": 28, "content": "und ganzlich Unsere Zeichen verleugneten"},
  {"surah_number": 78, "verse_number": 29, "content": "Und alle Dinge haben Wir restlos niedergeschrieben"},
  {"surah_number": 78, "verse_number": 30, "content": "Kostet! Wir werden es euch nicht anders mehren als in der Pein"},
  {"surah_number": 78, "verse_number": 31, "content": "Wahrlich, fur die Gottesfurchtigen gibt es einen Gewinn"},
  {"surah_number": 78, "verse_number": 32, "content": "Garten und Beerengehege"},
  {"surah_number": 78, "verse_number": 33, "content": "und Madchen mit schwellenden Brusten, Altersgenossinnen"},
  {"surah_number": 78, "verse_number": 34, "content": "und ubervolle Schalen"},
  {"surah_number": 78, "verse_number": 35, "content": "Dort horen sie weder Geschwatz noch Luge"},
  {"surah_number": 78, "verse_number": 36, "content": "(dies ist) ein Lohn von deinem Herrn - eine angemessene Gabe"},
  {"surah_number": 78, "verse_number": 37, "content": "Dem Herrn der Himmel und der Erde und alles dessen, was zwischen den beiden ist, dem Allerbarmer; Dem sie nichts vortragen konnen"},
  {"surah_number": 78, "verse_number": 38, "content": "Am Tage, da Gabriel und die Engel in Reihen stehen, da werden sie nicht sprechen durfen; ausgenommen der, dem der Allerbarmer es erlaubt, und der nur das Rechte spricht"},
  {"surah_number": 78, "verse_number": 39, "content": "Dies ist gewiß der Tag. So moge, wer da will, bei seinem Herrn Einkehr halten"},
  {"surah_number": 78, "verse_number": 40, "content": "Wahrlich, Wir haben euch gewarnt vor einer Strafe, die nahe bevorsteht: an einem Tag, da der Mensch erblicken wird, was seine Hande vorausgeschickt haben, und der Unglaubige sagen wird: \"O daß ich doch Staub ware"},
  {"surah_number": 79, "verse_number": 1, "content": "Bei den (Engeln, die die Seelen der Unglaubigen) heftig entreißen"},
  {"surah_number": 79, "verse_number": 2, "content": "und bei denen (, die die Seelen der Glaubigen) leicht emporheben"},
  {"surah_number": 79, "verse_number": 3, "content": "und bei denen (, die auf Geheiß Allahs zwischen Himmel und Erde) einher schweben"},
  {"surah_number": 79, "verse_number": 4, "content": "dann bei denen (, die mit den Seelen der Glaubigen ins Paradies) eifrig voraneilen"},
  {"surah_number": 79, "verse_number": 5, "content": "dann bei denen, die jegliche Angelegenheit (des irdischen Lebens) lenken"},
  {"surah_number": 79, "verse_number": 6, "content": "Eines Tages wird die Drohnende drohnen"},
  {"surah_number": 79, "verse_number": 7, "content": "gefolgt von der Darauffolgenden"},
  {"surah_number": 79, "verse_number": 8, "content": "Herzen werden an jenem Tage zittern"},
  {"surah_number": 79, "verse_number": 9, "content": "und ihre Augen werden niedergeschlagen sein"},
  {"surah_number": 79, "verse_number": 10, "content": "Sie sagen: \"Sollen wir wirklich in unseren fruheren Zustand zuruckgebracht werden"},
  {"surah_number": 79, "verse_number": 11, "content": "Wie? Selbst wenn wir verwestes Gebein geworden sind"},
  {"surah_number": 79, "verse_number": 12, "content": "Sie sagen: \"Das ware dann eine verlustreiche Wiederkehr"},
  {"surah_number": 79, "verse_number": 13, "content": "Es wird nur ein einziger Schreckenslaut sein"},
  {"surah_number": 79, "verse_number": 14, "content": "und siehe, sie sind dann auf der Erdoberflache"},
  {"surah_number": 79, "verse_number": 15, "content": "Ist die Geschichte von Moses zu dir gedrungen"},
  {"surah_number": 79, "verse_number": 16, "content": "Damals rief ihn sein Herr im heiligen Wadi Tuwa"},
  {"surah_number": 79, "verse_number": 17, "content": "Geh hin zu Pharao; denn er hat das Maß uberschritten"},
  {"surah_number": 79, "verse_number": 18, "content": "Sprich dann (zu ihm): \"Willst du dich nicht reinigen"},
  {"surah_number": 79, "verse_number": 19, "content": "Und ich werde dich zu deinem Herrn fuhren, auf daß du dich furchten mogest"},
  {"surah_number": 79, "verse_number": 20, "content": "So zeigte er ihm das große Wunder"},
  {"surah_number": 79, "verse_number": 21, "content": "Er aber leugnete und blieb ungehorsam"},
  {"surah_number": 79, "verse_number": 22, "content": "Dann kehrte er den Rucken und lief weg"},
  {"surah_number": 79, "verse_number": 23, "content": "versammelte alsdann (sein Volk) und rief aus"},
  {"surah_number": 79, "verse_number": 24, "content": "indem er sagte: \"lch bin euer hochster Herr"},
  {"surah_number": 79, "verse_number": 25, "content": "Da erfaßte ihn Allah zur Strafe fur jene und diese Tat"},
  {"surah_number": 79, "verse_number": 26, "content": "Hierin ist wahrlich eine Lehre fur den, der furchtet"},
  {"surah_number": 79, "verse_number": 27, "content": "Seid ihr denn schwerer zu erschaffen oder der Himmel, den Er gebaut hat"},
  {"surah_number": 79, "verse_number": 28, "content": "Er hat seine Hohe gehoben und ihn dann vollkommen gemacht"},
  {"surah_number": 79, "verse_number": 29, "content": "Und Er machte seine Nacht finster und ließ sein Tageslicht hervorgehen"},
  {"surah_number": 79, "verse_number": 30, "content": "Und Er breitete hernach die Erde aus"},
  {"surah_number": 79, "verse_number": 31, "content": "Aus ihr brachte Er ihr Wasser und ihr Weideland hervor"},
  {"surah_number": 79, "verse_number": 32, "content": "Und Er festigte die Berge"},
  {"surah_number": 79, "verse_number": 33, "content": "(dies alles) als eine Versorgung fur euch und fur euer Vieh"},
  {"surah_number": 79, "verse_number": 34, "content": "Doch wenn das großte Unheil kommt"},
  {"surah_number": 79, "verse_number": 35, "content": "an jenem Tag, da der Mensch sich (all) das ins Gedachtnis zuruckrufen wird, was er erstrebt hat"},
  {"surah_number": 79, "verse_number": 36, "content": "und die Gahim vor Augen gestellt wird fur den, der sieht"},
  {"surah_number": 79, "verse_number": 37, "content": "Wer aber aufsassig war"},
  {"surah_number": 79, "verse_number": 38, "content": "und das irdische Leben vorzog"},
  {"surah_number": 79, "verse_number": 39, "content": "so wird wahrlich die Gahim (seine) Herberge sein"},
  {"surah_number": 79, "verse_number": 40, "content": "Wer aber das Stehen vor seinem Herrn gefurchtet hatte und die eigne Seele von niedrem Gelust abhielt"},
  {"surah_number": 79, "verse_number": 41, "content": "so wird das Paradies sicherlich (seine) Herberge sein"},
  {"surah_number": 79, "verse_number": 42, "content": "Sie befragen dich wegen der Stunde: \"Wann wird ihr Termin wohl sein"},
  {"surah_number": 79, "verse_number": 43, "content": "Was weißt du von ihr zu sagen"},
  {"surah_number": 79, "verse_number": 44, "content": "Das endgultige Wissen darum ist allein deinem Herrn (vorbehalten)"},
  {"surah_number": 79, "verse_number": 45, "content": "Du bist nur ein Warner fur den, der sie furchtet"},
  {"surah_number": 79, "verse_number": 46, "content": "An jenem Tage, an dem sie sie schauen, als hatten sie (auf der Erde) nicht langer geweilt als einen Abend oder den Morgen darauf"},
  {"surah_number": 80, "verse_number": 1, "content": "Er runzelte die Stirn und wandte sich ab"},
  {"surah_number": 80, "verse_number": 2, "content": "als der blinde Mann zu ihm kam"},
  {"surah_number": 80, "verse_number": 3, "content": "Was laßt dich aber wissen, daß er sich nicht reinigen wollte"},
  {"surah_number": 80, "verse_number": 4, "content": "oder daß er Ermahnung suchte und ihm somit die Lehre nutzlich wurde"},
  {"surah_number": 80, "verse_number": 5, "content": "Wer aber es nicht fur notig halt"},
  {"surah_number": 80, "verse_number": 6, "content": "dem kommst du (bereitwillig) entgegen"},
  {"surah_number": 80, "verse_number": 7, "content": "ohne dir etwas daraus zu machen, daß er sich nicht reinigen will"},
  {"surah_number": 80, "verse_number": 8, "content": "Was aber den anbelangt, der in Eifer zu dir kommt"},
  {"surah_number": 80, "verse_number": 9, "content": "und gottesfurchtig ist"},
  {"surah_number": 80, "verse_number": 10, "content": "um den kummerst du dich nicht"},
  {"surah_number": 80, "verse_number": 11, "content": "Nicht so. Wahrlich, dies ist eine Ermahnung"},
  {"surah_number": 80, "verse_number": 12, "content": "so moge, wer da will, diesem eingedenk sein"},
  {"surah_number": 80, "verse_number": 13, "content": "Auf geehrten Seiten, die in Ehren gehalten werden"},
  {"surah_number": 80, "verse_number": 14, "content": "sind sie emporgehoben, rein"},
  {"surah_number": 80, "verse_number": 15, "content": "in den Handen rechtschaffener Sendboten"},
  {"surah_number": 80, "verse_number": 16, "content": "die edel und tugendhaft sind"},
  {"surah_number": 80, "verse_number": 17, "content": "Verderben auf den Menschen! Wie undankbar ist er"},
  {"surah_number": 80, "verse_number": 18, "content": "Woraus hat Er ihn erschaffen"},
  {"surah_number": 80, "verse_number": 19, "content": "Aus einem Samenerguß hat Er ihn erschaffen und gebildet"},
  {"surah_number": 80, "verse_number": 20, "content": "Dann ermoglicht Er ihm den Ausgang"},
  {"surah_number": 80, "verse_number": 21, "content": "Dann laßt Er ihn sterben und laßt ihn ins Grab bringen"},
  {"surah_number": 80, "verse_number": 22, "content": "Dann, wenn Er will, erweckt Er ihn wieder"},
  {"surah_number": 80, "verse_number": 23, "content": "Wahrlich; er hat nicht getan, was Er ihm geboten hat"},
  {"surah_number": 80, "verse_number": 24, "content": "So soll der Mensch doch seine Nahrung betrachten"},
  {"surah_number": 80, "verse_number": 25, "content": "Siehe, Wir gossen das Wasser in Fulle aus"},
  {"surah_number": 80, "verse_number": 26, "content": "alsdann spalteten Wir die Erde in wunderbarer Weise"},
  {"surah_number": 80, "verse_number": 27, "content": "und ließen Korn in ihr wachsen"},
  {"surah_number": 80, "verse_number": 28, "content": "und Reben und Gezweig"},
  {"surah_number": 80, "verse_number": 29, "content": "und Olbaume und Palmen"},
  {"surah_number": 80, "verse_number": 30, "content": "und dicht bepflanzte Gartengehege"},
  {"surah_number": 80, "verse_number": 31, "content": "und Obst und Futtergras"},
  {"surah_number": 80, "verse_number": 32, "content": "als Versorgung fur euch und euer Vieh"},
  {"surah_number": 80, "verse_number": 33, "content": "Doch wenn das betaubende Getose kommt"},
  {"surah_number": 80, "verse_number": 34, "content": "am Tage, da der Mensch seinen Bruder fluchtartig verlaßt"},
  {"surah_number": 80, "verse_number": 35, "content": "sowie seine Mutter und seinen Vater"},
  {"surah_number": 80, "verse_number": 36, "content": "und seine Frau und seine Sohne"},
  {"surah_number": 80, "verse_number": 37, "content": "an jenem Tage wird jeder eigene Sorgen genug haben, die ihn beschaftigen"},
  {"surah_number": 80, "verse_number": 38, "content": "An jenem Tage werden manche Gesichter strahlend sein"},
  {"surah_number": 80, "verse_number": 39, "content": "heiter und freudig"},
  {"surah_number": 80, "verse_number": 40, "content": "Und andere Gesichter, an jenem Tage, werden staubbedeckt sein"},
  {"surah_number": 80, "verse_number": 41, "content": "Trubung wird darauf liegen"},
  {"surah_number": 80, "verse_number": 42, "content": "Das sind die Unglaubigen, die Unverschamten"},
  {"surah_number": 81, "verse_number": 1, "content": "Wenn die Sonne eingerollt ist"},
  {"surah_number": 81, "verse_number": 2, "content": "und wenn die Sterne trube sind"},
  {"surah_number": 81, "verse_number": 3, "content": "und wenn die Berge fortbewegt werden"},
  {"surah_number": 81, "verse_number": 4, "content": "und wenn die trachtigen Kamelstuten vernachlassigt werden"},
  {"surah_number": 81, "verse_number": 5, "content": "und wenn wildes Getier versammelt wird"},
  {"surah_number": 81, "verse_number": 6, "content": "und wenn die Meere zu einem Flammenmeer werden"},
  {"surah_number": 81, "verse_number": 7, "content": "und wenn die Seelen (mit ihren Leibern) gepaart werden"},
  {"surah_number": 81, "verse_number": 8, "content": "und wenn das lebendig begrabene Madchen gefragt wird"},
  {"surah_number": 81, "verse_number": 9, "content": "Fur welch ein Verbrechen wurdest du getotet"},
  {"surah_number": 81, "verse_number": 10, "content": "Und wenn Schriften weithin aufgerollt werden"},
  {"surah_number": 81, "verse_number": 11, "content": "und wenn der Himmel weggezogen wird"},
  {"surah_number": 81, "verse_number": 12, "content": "und wenn die Gahim angefacht wird"},
  {"surah_number": 81, "verse_number": 13, "content": "und wenn das Paradies nahegeruckt wird"},
  {"surah_number": 81, "verse_number": 14, "content": "dann wird jede Seele wissen, was sie mitgebracht hat"},
  {"surah_number": 81, "verse_number": 15, "content": "Wahrlich, Ich schwore bei den rucklaufigen Sternen"},
  {"surah_number": 81, "verse_number": 16, "content": "den voraneilenden und den sich verbergenden"},
  {"surah_number": 81, "verse_number": 17, "content": "und bei der Nacht, wenn sie vergeht"},
  {"surah_number": 81, "verse_number": 18, "content": "und Ich schwore beim Morgen, wenn er zu aufatmen beginnt"},
  {"surah_number": 81, "verse_number": 19, "content": "daß dies in Wahrheit ein Wort eines edlen Boten ist"},
  {"surah_number": 81, "verse_number": 20, "content": "der mit Macht begabt ist bei dem Herrn des Throns und in Ansehen steht"},
  {"surah_number": 81, "verse_number": 21, "content": "dem gehorcht wird und der getreu ist"},
  {"surah_number": 81, "verse_number": 22, "content": "und euer Gefahrte ist nicht ein Besessener"},
  {"surah_number": 81, "verse_number": 23, "content": "Wahrlich, er sah ihn am klaren Horizont"},
  {"surah_number": 81, "verse_number": 24, "content": "Und er ist weder geizig hinsichtlich des Verborgenen"},
  {"surah_number": 81, "verse_number": 25, "content": "noch ist dies das Wort Satans, des Verfluchten"},
  {"surah_number": 81, "verse_number": 26, "content": "Wohin also wollt ihr gehen"},
  {"surah_number": 81, "verse_number": 27, "content": "Dies ist ja nur eine Ermahnung fur alle Welten"},
  {"surah_number": 81, "verse_number": 28, "content": "Fur denjenigen unter euch, der aufrichtig sein will"},
  {"surah_number": 81, "verse_number": 29, "content": "Und ihr werdet nicht wollen, es sei denn, daß Allah will, der Herr der Welten"},
  {"surah_number": 82, "verse_number": 1, "content": "Wenn der Himmel sich spaltet"},
  {"surah_number": 82, "verse_number": 2, "content": "und wenn die Sterne zerstreut sind"},
  {"surah_number": 82, "verse_number": 3, "content": "und wenn die Meere uber die Ufer treten"},
  {"surah_number": 82, "verse_number": 4, "content": "und wenn die Graber ausgeraumt werden"},
  {"surah_number": 82, "verse_number": 5, "content": "dann wird jede Seele wissen, was sie getan und was sie unterlassen hat"},
  {"surah_number": 82, "verse_number": 6, "content": "O du Mensch! Was hat dich hinsichtlich deines Ehrwurdigen Herrn betort"},
  {"surah_number": 82, "verse_number": 7, "content": "Der dich erschuf und dich dann ebenmaßig geformt, und in einer geraden Gestalt gemacht hat"},
  {"surah_number": 82, "verse_number": 8, "content": "In solchem Bild, das Er immer wollte, hat Er dich zusammengesetzt"},
  {"surah_number": 82, "verse_number": 9, "content": "Seht aber, wie ihr das Gericht leugnet"},
  {"surah_number": 82, "verse_number": 10, "content": "Und uber euch sind wahrlich Huter"},
  {"surah_number": 82, "verse_number": 11, "content": "edle Schreibende"},
  {"surah_number": 82, "verse_number": 12, "content": "die wissen, was ihr tut"},
  {"surah_number": 82, "verse_number": 13, "content": "Wahrlich, die Rechtschaffenen werden in der Wonne sein"},
  {"surah_number": 82, "verse_number": 14, "content": "und wahrlich, die Unverschamten werden in der Gahim sein"},
  {"surah_number": 82, "verse_number": 15, "content": "Sie werden dort brennen am Tage des Gerichts"},
  {"surah_number": 82, "verse_number": 16, "content": "Und sie werden nicht imstande sein, daraus zu entrinnen"},
  {"surah_number": 82, "verse_number": 17, "content": "Und was lehrt dich wissen, was der Tag des Gerichts ist"},
  {"surah_number": 82, "verse_number": 18, "content": "Und wiederum, was lehrt dich wissen, was der Tag des Gerichts ist"},
  {"surah_number": 82, "verse_number": 19, "content": "An jenem Tag wird keine Seele etwas fur eine andere Seele zu tun vermogen; und der Befehl an jenem Tage steht (einzig) Allah zu"},
  {"surah_number": 83, "verse_number": 1, "content": "Wehe denjenigen, die das Maß verkurzen"},
  {"surah_number": 83, "verse_number": 2, "content": "die, wenn sie sich von den Leuten zumessen lassen, volles Maß verlangen"},
  {"surah_number": 83, "verse_number": 3, "content": "Und dann jedoch, wenn sie es ihnen ausmessen oder auswagen, verkurzen sie es"},
  {"surah_number": 83, "verse_number": 4, "content": "Glauben diese nicht, daß sie auferweckt werden"},
  {"surah_number": 83, "verse_number": 5, "content": "an einem großen Tag"},
  {"surah_number": 83, "verse_number": 6, "content": "an dem die Menschen vor dem Herrn der Welten stehen werden"},
  {"surah_number": 83, "verse_number": 7, "content": "Nein! Das Buch der Unverschamten ist in Siggin"},
  {"surah_number": 83, "verse_number": 8, "content": "Und was lehrt dich wissen, was Siggin ist"},
  {"surah_number": 83, "verse_number": 9, "content": "(Es ist) ein geschriebenes Buch"},
  {"surah_number": 83, "verse_number": 10, "content": "Wehe an jenem Tage den Leugnern"},
  {"surah_number": 83, "verse_number": 11, "content": "die den Tag des Gerichts leugnen"},
  {"surah_number": 83, "verse_number": 12, "content": "Und es leugnet ihn keiner als ein jeder sundhafter Ubertreter"},
  {"surah_number": 83, "verse_number": 13, "content": "der, wenn ihm Unsere Verse verlesen werden, sagt: \"Fabeln der Fruheren"},
  {"surah_number": 83, "verse_number": 14, "content": "Nein, jedoch das, was sie zu tun pflegten, hat auf ihre Herzen Schmutz gelegt"},
  {"surah_number": 83, "verse_number": 15, "content": "Nein, sie werden an jenem Tage gewiß keinen Zugang zu ihrem Herrn haben"},
  {"surah_number": 83, "verse_number": 16, "content": "Dann werden sie doch in der Gahim brennen"},
  {"surah_number": 83, "verse_number": 17, "content": "Und es wird gesprochen werden: \"Dies ist es, was ihr zu leugnen pflegtet"},
  {"surah_number": 83, "verse_number": 18, "content": "Nein! Das Buch der Rechtschaffenen ist gewiß in Illiyun"},
  {"surah_number": 83, "verse_number": 19, "content": "Und was lehrt dich wissen, was `Illiyun ist"},
  {"surah_number": 83, "verse_number": 20, "content": "(Es ist) ein geschriebenes Buch"},
  {"surah_number": 83, "verse_number": 21, "content": "Die Erwahlten (Allahs) werden es sehen"},
  {"surah_number": 83, "verse_number": 22, "content": "Wahrlich, die Rechtschaffenen werden in Wonne sein"},
  {"surah_number": 83, "verse_number": 23, "content": "Auf Ruhesitzen werden sie zuschauen"},
  {"surah_number": 83, "verse_number": 24, "content": "Erkennen wirst du auf ihren Gesichtern den Glanz der Seligkeit"},
  {"surah_number": 83, "verse_number": 25, "content": "Ihnen wird ein reiner, versiegelter Trank gegeben"},
  {"surah_number": 83, "verse_number": 26, "content": "dessen Siegel Moschus ist - und um dies mogen die Begehrenden wetteifern"},
  {"surah_number": 83, "verse_number": 27, "content": "Und es wird ihm von Tasnim beige mischt sein"},
  {"surah_number": 83, "verse_number": 28, "content": "von einer Quelle, aus der die Erwahlten trinken werden"},
  {"surah_number": 83, "verse_number": 29, "content": "Jene Frevler haben sich uber die Glaubigen lustig gemacht"},
  {"surah_number": 83, "verse_number": 30, "content": "und wenn sie an ihnen vorubergingen, blinzelten sie einander zu"},
  {"surah_number": 83, "verse_number": 31, "content": "und wenn sie zu den Ihren zuruckkehrten, kehrten sie frohlockend zuruck"},
  {"surah_number": 83, "verse_number": 32, "content": "und wenn sie sie sahen, sagten sie: \"Das sind wahrlich Irrende"},
  {"surah_number": 83, "verse_number": 33, "content": "obwohl sie nicht als Huter uber sie gesandt worden waren"},
  {"surah_number": 83, "verse_number": 34, "content": "Heute aber sind die Glaubigen diejenigen, die sich uber die Unglaubigen lustig machen"},
  {"surah_number": 83, "verse_number": 35, "content": "sie schauen von ihren Ruhesitzen zu"},
  {"surah_number": 83, "verse_number": 36, "content": "Hat es sich fur die Unglaubigen gelohnt, was sie getan haben"},
  {"surah_number": 84, "verse_number": 1, "content": "Wenn der Himmel zerbricht"},
  {"surah_number": 84, "verse_number": 2, "content": "und seinem Herrn gehorcht und sich Ihm gefugig zeigt"},
  {"surah_number": 84, "verse_number": 3, "content": "und wenn die Erde ausgebreitet wird"},
  {"surah_number": 84, "verse_number": 4, "content": "und herauswirft, was sie verbirgt, und sich (von allem) freimacht"},
  {"surah_number": 84, "verse_number": 5, "content": "und ihrem Herrn gehorcht und sich Ihm gefugig zeigt"},
  {"surah_number": 84, "verse_number": 6, "content": "Du Mensch! Du strebst mit aller Muhe deinem Herrn zu; und du sollstIhm begegnen"},
  {"surah_number": 84, "verse_number": 7, "content": "Was nun den anbelangt, dem sein Buch in seine Rechte gegeben wird"},
  {"surah_number": 84, "verse_number": 8, "content": "der wird einer leichten Rechenschaft unterzogen sein"},
  {"surah_number": 84, "verse_number": 9, "content": "und wird frohlich zu seinen Angehorigen zuruckkehren"},
  {"surah_number": 84, "verse_number": 10, "content": "Was aber den anbelangt, dem sein Buch hinter seinem Rucken gegeben wird"},
  {"surah_number": 84, "verse_number": 11, "content": "der wird sich bald Vernichtung herbeiwunschen"},
  {"surah_number": 84, "verse_number": 12, "content": "und wird im Hollenfeuer brennen"},
  {"surah_number": 84, "verse_number": 13, "content": "er war gewiß glucklich unter seinen Angehorigen"},
  {"surah_number": 84, "verse_number": 14, "content": "Siehe, er dachte, daß er nie davon abkommen (und zu Allah zuruckkehren) wurde"},
  {"surah_number": 84, "verse_number": 15, "content": "Wahrlich, sein Herr hat ihn wohl durchschaut"},
  {"surah_number": 84, "verse_number": 16, "content": "Doch! Ich schwore bei der Abenddammerung"},
  {"surah_number": 84, "verse_number": 17, "content": "und bei der Nacht und dem, was sie verhullt"},
  {"surah_number": 84, "verse_number": 18, "content": "und bei dem Mond, wenn er voll wird"},
  {"surah_number": 84, "verse_number": 19, "content": "daß ihr sicherlich von einem Zustand (der Not) in den anderen versetzt werdet"},
  {"surah_number": 84, "verse_number": 20, "content": "Was also ist ihnen, daß sie nicht glauben"},
  {"surah_number": 84, "verse_number": 21, "content": "und wenn ihnen der Quran verlesen wird, sich nicht in Anbetung niederwerfen"},
  {"surah_number": 84, "verse_number": 22, "content": "Im Gegenteil, die da unglaubig sind, erklaren (die Botschaft Allahs) fur eine Luge"},
  {"surah_number": 84, "verse_number": 23, "content": "Und Allah weiß am besten, was sie verbergen"},
  {"surah_number": 84, "verse_number": 24, "content": "Darum verkunde ihnen eine schmerzliche Strafe"},
  {"surah_number": 84, "verse_number": 25, "content": "Nicht so denjenigen, die glauben und gute Werke tun - ihnen wird unendlicher Lohn zuteil sein"},
  {"surah_number": 85, "verse_number": 1, "content": "Beim Himmel mit seinen Turmen"},
  {"surah_number": 85, "verse_number": 2, "content": "und beim verheißenen Tage"},
  {"surah_number": 85, "verse_number": 3, "content": "und beim Zeugen und beim Bezeugten"},
  {"surah_number": 85, "verse_number": 4, "content": "Verflucht sind die Leute des Grabens"},
  {"surah_number": 85, "verse_number": 5, "content": "des Feuers, mit seinem Brennstoff"},
  {"surah_number": 85, "verse_number": 6, "content": "Wie sie daran saßen"},
  {"surah_number": 85, "verse_number": 7, "content": "Und sie werden das bezeugen, was sie den Glaubigen angetan haben"},
  {"surah_number": 85, "verse_number": 8, "content": "Und sie haßten sie aus keinem anderen Grund, als weil sie an Allah glaubten, den Erhabenen, den Preiswurdigen"},
  {"surah_number": 85, "verse_number": 9, "content": "Dem das Konigreich der Himmel und der Erde gehort; und Allah ist Zeuge von allem"},
  {"surah_number": 85, "verse_number": 10, "content": "Diejenigen, die die glaubigen Manner und die glaubigen Frauen heimsuchen und es dann nicht bereuen - fur sie ist die Strafe der Gahannam, und fur sie ist die Strafe des Brennens (bestimmt)"},
  {"surah_number": 85, "verse_number": 11, "content": "Doch jene, die glauben und gute Werke tun - fur sie sind die Garten, durch die Bache fließen (, bestimmt) Das ist der großte Gewinn"},
  {"surah_number": 85, "verse_number": 12, "content": "Wahrlich, die Rache deines Herrn ist enorm"},
  {"surah_number": 85, "verse_number": 13, "content": "Er ist es, Der erschafft und wiederkehren laßt"},
  {"surah_number": 85, "verse_number": 14, "content": "Und Er ist der Allvergebende, der Liebvolle"},
  {"surah_number": 85, "verse_number": 15, "content": "Der Herr des Ruhmvollen Throns"},
  {"surah_number": 85, "verse_number": 16, "content": "Er tut, was Er will"},
  {"surah_number": 85, "verse_number": 17, "content": "Hat die Geschichte von den Heerscharen dich erreicht"},
  {"surah_number": 85, "verse_number": 18, "content": "von Pharao und den Tamud"},
  {"surah_number": 85, "verse_number": 19, "content": "Nein, aber die Unglaubigen bestehen auf dem Leugnen"},
  {"surah_number": 85, "verse_number": 20, "content": "und Allah ist hinter ihnen her (und) umfaßt sie von allen Seiten"},
  {"surah_number": 85, "verse_number": 21, "content": "Ja, es ist ein ruhmvoller Quran"},
  {"surah_number": 85, "verse_number": 22, "content": "auf einer wohlverwahrten Tafel"},
  {"surah_number": 86, "verse_number": 1, "content": "Bei dem Himmel und bei At-Tariq"},
  {"surah_number": 86, "verse_number": 2, "content": "Und was lehrt dich wissen, was At-Tariq ist"},
  {"surah_number": 86, "verse_number": 3, "content": "(Es ist) ein Stern von durchdringender Helligkeit"},
  {"surah_number": 86, "verse_number": 4, "content": "Wahrlich, jede Seele hat uber sich einen Huter"},
  {"surah_number": 86, "verse_number": 5, "content": "Darum soll der Mensch denn bedenken, woraus er erschaffen ist"},
  {"surah_number": 86, "verse_number": 6, "content": "Erschaffen wurde er aus einer herausschießenden Flussigkeit"},
  {"surah_number": 86, "verse_number": 7, "content": "das zwischen den Lenden und den Rippen hervorkommt"},
  {"surah_number": 86, "verse_number": 8, "content": "Wahrlich, Er hat die Macht, ihn zuruckzubringen"},
  {"surah_number": 86, "verse_number": 9, "content": "am Tage, wenn die Geheimnisse enthullt werden"},
  {"surah_number": 86, "verse_number": 10, "content": "dann wird er keine Kraft und keinen Helfer haben"},
  {"surah_number": 86, "verse_number": 11, "content": "Und beim Himmel mit seiner Wiederkehr"},
  {"surah_number": 86, "verse_number": 12, "content": "und bei der Erde, die sich spaltet"},
  {"surah_number": 86, "verse_number": 13, "content": "Dies ist wahrlich ein entscheidendes, letztes Wort"},
  {"surah_number": 86, "verse_number": 14, "content": "und es ist nicht zum Scherzen"},
  {"surah_number": 86, "verse_number": 15, "content": "Wahrlich, sie planen eine List"},
  {"surah_number": 86, "verse_number": 16, "content": "Und Ich plane eine List"},
  {"surah_number": 86, "verse_number": 17, "content": "Darum gewahre nun den Unglaubigen Aufschub, ein klein wenig Aufschub"},
  {"surah_number": 87, "verse_number": 1, "content": "Preise den Namen deines Allerhochsten Herrn"},
  {"surah_number": 87, "verse_number": 2, "content": "Der erschaffen und geformt hat"},
  {"surah_number": 87, "verse_number": 3, "content": "Der bestimmt und leitet"},
  {"surah_number": 87, "verse_number": 4, "content": "Der die Weide hervorbringt"},
  {"surah_number": 87, "verse_number": 5, "content": "und sie zu versengter Spreu macht"},
  {"surah_number": 87, "verse_number": 6, "content": "Wir werden dir (den Quran) verlesen lassen, und du sollst (ihn) nicht vergessen"},
  {"surah_number": 87, "verse_number": 7, "content": "es sei denn, was Allah will; denn Er kennt das Offenkundige und das Verborgene"},
  {"surah_number": 87, "verse_number": 8, "content": "Und Wir werden es dir zum Heil leicht machen"},
  {"surah_number": 87, "verse_number": 9, "content": "So ermahne, wo die Ermahnung nutzt"},
  {"surah_number": 87, "verse_number": 10, "content": "Mahnen lassen wird sich derjenige, der gottesfurchtig ist"},
  {"surah_number": 87, "verse_number": 11, "content": "Ermahnung meiden wird der Unselige"},
  {"surah_number": 87, "verse_number": 12, "content": "der im großten Feuer brennt"},
  {"surah_number": 87, "verse_number": 13, "content": "und in ihm wird er weder sterben noch leben"},
  {"surah_number": 87, "verse_number": 14, "content": "Erfolgreich ist wahrlich derjenige, der sich rein halt"},
  {"surah_number": 87, "verse_number": 15, "content": "und des Namens seines Herrn gedenkt (und) alsdann betet"},
  {"surah_number": 87, "verse_number": 16, "content": "Doch ihr zieht das irdische Leben vor"},
  {"surah_number": 87, "verse_number": 17, "content": "wo doch das Jenseits besser und dauerhafter ist"},
  {"surah_number": 87, "verse_number": 18, "content": "Dies stand wahrlich in den ersten Schriften"},
  {"surah_number": 87, "verse_number": 19, "content": "den Schriften Abrahams und Moses"},
  {"surah_number": 88, "verse_number": 1, "content": "Hat die Geschichte der Al-Gasiya dich erreicht"},
  {"surah_number": 88, "verse_number": 2, "content": "Manche Gesichter werden an jenem Tag niedergeschlagen sein"},
  {"surah_number": 88, "verse_number": 3, "content": "sie werden sich plagen und abmuhen"},
  {"surah_number": 88, "verse_number": 4, "content": "sie werden in einem heißen Feuer brennen"},
  {"surah_number": 88, "verse_number": 5, "content": "sie werden aus einer kochendheißen Quelle trinken"},
  {"surah_number": 88, "verse_number": 6, "content": "fur sie wird es keine andere Speise geben außer Dornstrauchern"},
  {"surah_number": 88, "verse_number": 7, "content": "die weder nahren noch Hunger stillen"},
  {"surah_number": 88, "verse_number": 8, "content": "(Und manche) Gesichter werden an jenem Tage frohlich sein"},
  {"surah_number": 88, "verse_number": 9, "content": "wohlzufrieden mit ihrer Muhe"},
  {"surah_number": 88, "verse_number": 10, "content": "in einem hohen Garten"},
  {"surah_number": 88, "verse_number": 11, "content": "in dem sie kein Geschwatz horen"},
  {"surah_number": 88, "verse_number": 12, "content": "in dem eine stromende Quelle ist"},
  {"surah_number": 88, "verse_number": 13, "content": "in dem es erhohte Ruhebetten gibt"},
  {"surah_number": 88, "verse_number": 14, "content": "und bereitgestellte Becher"},
  {"surah_number": 88, "verse_number": 15, "content": "und aufgereihte Kissen"},
  {"surah_number": 88, "verse_number": 16, "content": "und ausgebreitete Teppiche"},
  {"surah_number": 88, "verse_number": 17, "content": "Schauen sie denn nicht zu den Kamelen, wie sie erschaffen sind"},
  {"surah_number": 88, "verse_number": 18, "content": "und zu dem Himmel, wie er emporgehoben ist"},
  {"surah_number": 88, "verse_number": 19, "content": "und zu den Bergen, wie sie aufgerichtet sind"},
  {"surah_number": 88, "verse_number": 20, "content": "und zu der Erde, wie sie ausgebreitet worden ist"},
  {"surah_number": 88, "verse_number": 21, "content": "So ermahne; denn du bist zwar ein Ermahner"},
  {"surah_number": 88, "verse_number": 22, "content": "du hast aber keine Macht uber sie"},
  {"surah_number": 88, "verse_number": 23, "content": "was jedoch den anbelangt, der sich abkehrt und im Unglauben verharrt"},
  {"surah_number": 88, "verse_number": 24, "content": "Allah wird ihn dann mit der schwersten Strafe bestrafen"},
  {"surah_number": 88, "verse_number": 25, "content": "Zu Uns ist ihre Heimkehr"},
  {"surah_number": 88, "verse_number": 26, "content": "Alsdann obliegt es Uns, mit ihnen abzurechnen"},
  {"surah_number": 89, "verse_number": 1, "content": "Bei der Morgenrote"},
  {"surah_number": 89, "verse_number": 2, "content": "und bei den zehn Nachten"},
  {"surah_number": 89, "verse_number": 3, "content": "und beim (an Zahl) Geraden und Ungeraden"},
  {"surah_number": 89, "verse_number": 4, "content": "und bei der Nacht, wenn sie vergeht"},
  {"surah_number": 89, "verse_number": 5, "content": "Ist hierin ein ausreichender Beweis fur einen, der Verstand hat"},
  {"surah_number": 89, "verse_number": 6, "content": "Hast du nicht gesehen, wie dein Herr mit den `Ad verfuhr"},
  {"surah_number": 89, "verse_number": 7, "content": "mit Iram, der Saulenreichen"},
  {"surah_number": 89, "verse_number": 8, "content": "dergleichen nicht erschaffen wurde in (anderen) Landern"},
  {"surah_number": 89, "verse_number": 9, "content": "Und den Tamud, die die Felsen im Tal aushohlten"},
  {"surah_number": 89, "verse_number": 10, "content": "Und Pharao mit seinen bodenfesten Bauten"},
  {"surah_number": 89, "verse_number": 11, "content": "Denjenigen, die im Lande gewalttatig waren"},
  {"surah_number": 89, "verse_number": 12, "content": "und dort viel Verderbnis stifteten"},
  {"surah_number": 89, "verse_number": 13, "content": "Darum ließ dein Herr die Geißel der Strafe auf sie schutten"},
  {"surah_number": 89, "verse_number": 14, "content": "Wahrlich, dein Herr ist auf der Hut"},
  {"surah_number": 89, "verse_number": 15, "content": "Wenn aber der Mensch von seinem Herrn gepruft wird, indem Er ihm Wohltaten erweist und Gnaden auf ihn hauft, dann sagt er: \"Mein Herr hat mich gewurdigt"},
  {"surah_number": 89, "verse_number": 16, "content": "Wenn Er ihn aber pruft, indem Er ihm seine Versorgung verkurzt, dann sagt er: \"Mein Herr hat mich erniedrigt"},
  {"surah_number": 89, "verse_number": 17, "content": "Nein, ihr seid nicht freigebig gegen die Waise"},
  {"surah_number": 89, "verse_number": 18, "content": "und treibt einander nicht an, den Armen zu speisen"},
  {"surah_number": 89, "verse_number": 19, "content": "Und ihr verzehrt das Erbe (anderer) ganz und gar"},
  {"surah_number": 89, "verse_number": 20, "content": "Und ihr liebt den Reichtum mit ubermaßiger Liebe"},
  {"surah_number": 89, "verse_number": 21, "content": "Nicht aber so wenn die Erde kurz und klein zermalmt wird"},
  {"surah_number": 89, "verse_number": 22, "content": "und dein Herr kommt und (auch) die Engel in Reihen auf Reihen (kommen)"},
  {"surah_number": 89, "verse_number": 23, "content": "und Gahannam an jenem Tage nahegebracht wird. An jenem Tage wird der Mensch bereit sein, sich mahnen zu lassen; aber was wird ihm dann das Mahnen nutzen"},
  {"surah_number": 89, "verse_number": 24, "content": "Er wird sagen: \"O hatte ich doch im voraus fur (dieses) mein Leben Sorge getragen"},
  {"surah_number": 89, "verse_number": 25, "content": "An jenem Tag wird niemand so bestrafen wie Er"},
  {"surah_number": 89, "verse_number": 26, "content": "und niemand wird so festbinden wie Er"},
  {"surah_number": 89, "verse_number": 27, "content": "O du ruhige Seele"},
  {"surah_number": 89, "verse_number": 28, "content": "Komm zufrieden zuruck zu deinem Herrn und mit (Allahs) Wohlwollen"},
  {"surah_number": 89, "verse_number": 29, "content": "so schließ' dich dem Kreis Meiner Diener an"},
  {"surah_number": 89, "verse_number": 30, "content": "Und tritt ein in Mein Paradies"},
  {"surah_number": 90, "verse_number": 1, "content": "Ich schwore bei dieser Ortschaft"},
  {"surah_number": 90, "verse_number": 2, "content": "Und du wohnst in dieser Ortschaft"},
  {"surah_number": 90, "verse_number": 3, "content": "und bei dem Zeugenden und bei dem, was er gezeugt hat"},
  {"surah_number": 90, "verse_number": 4, "content": "Wahrlich, Wir haben den Menschen (zu einem Dasein) in Bedrangnis erschaffen"},
  {"surah_number": 90, "verse_number": 5, "content": "Meint er, niemand habe Macht uber ihn"},
  {"surah_number": 90, "verse_number": 6, "content": "Er sagt: \"lch habe viel Vermogen ausgegeben"},
  {"surah_number": 90, "verse_number": 7, "content": "Meint er, niemand hatte ihn gesehen"},
  {"surah_number": 90, "verse_number": 8, "content": "Haben Wir ihm nicht zwei Augen gemacht"},
  {"surah_number": 90, "verse_number": 9, "content": "und eine Zunge und zwei Lippen"},
  {"surah_number": 90, "verse_number": 10, "content": "Und ihm haben Wir die beiden Wege gezeigt"},
  {"surah_number": 90, "verse_number": 11, "content": "Doch er bezwang das Hindernis nicht"},
  {"surah_number": 90, "verse_number": 12, "content": "Und was lehrt dich wissen, was das Hindernis ist"},
  {"surah_number": 90, "verse_number": 13, "content": "(Es sind:) das Befreien eines Nackens"},
  {"surah_number": 90, "verse_number": 14, "content": "oder an einem Tage wahrend der Hungersnot das Speisen"},
  {"surah_number": 90, "verse_number": 15, "content": "einer nahverwandten Waise"},
  {"surah_number": 90, "verse_number": 16, "content": "oder eines Armen, der sich im Staube walzt"},
  {"surah_number": 90, "verse_number": 17, "content": "(oder) alsdann unter denen zu sein, die glauben und einander ermahnen zur Geduld und einander ermahnen zur Barmherzigkeit"},
  {"surah_number": 90, "verse_number": 18, "content": "Dies sind diejenigen, die von der rechten (Seite) sind"},
  {"surah_number": 90, "verse_number": 19, "content": "Diejenigen aber, die nicht an Unsere Zeichen glauben sie werden von der linken (Seite) sein"},
  {"surah_number": 90, "verse_number": 20, "content": "sie werden vom Feuer ringsum eingeschlossen sein"},
  {"surah_number": 91, "verse_number": 1, "content": "Bei der Sonne und bei ihrem Morgenglanz"},
  {"surah_number": 91, "verse_number": 2, "content": "und bei dem Mond, wenn er ihr folgt"},
  {"surah_number": 91, "verse_number": 3, "content": "und bei dem Tage, wenn er sie erstrahlen laßt"},
  {"surah_number": 91, "verse_number": 4, "content": "und bei der Nacht, wenn sie sie bedeckt"},
  {"surah_number": 91, "verse_number": 5, "content": "und bei dem Himmel und bei Dem, Der ihn aufgebaut hat"},
  {"surah_number": 91, "verse_number": 6, "content": "und bei der Erde und bei Dem, Der sie ausgebreitet hat"},
  {"surah_number": 91, "verse_number": 7, "content": "und bei einer (jeden menschlichen) Seele und bei Dem, Der sie gebildet"},
  {"surah_number": 91, "verse_number": 8, "content": "und ihr den Sinn fur ihre Sundhaftigkeit und fur ihre Gottesfurcht eingegeben hat"},
  {"surah_number": 91, "verse_number": 9, "content": "Erfolgreich ist derjenige, der sie rein halt"},
  {"surah_number": 91, "verse_number": 10, "content": "und versagt hat derjenige, der sie verkommen laßt"},
  {"surah_number": 91, "verse_number": 11, "content": "So leugneten die Tamud (die Wahrheit) in ihrem Trotz"},
  {"surah_number": 91, "verse_number": 12, "content": "Als der Unseligste unter ihnen auftrat"},
  {"surah_number": 91, "verse_number": 13, "content": "da sagte der Gesandte Allahs zu ihnen: \"Haltet euch von der Kamelstute Allahs und von ihrer Tranke fern"},
  {"surah_number": 91, "verse_number": 14, "content": "Sie aber bezichtigten ihn der Luge und toteten sie; darum kam ihr Herr (zur Vergeltung) fur ihre Schuld mit Seinem Zorn uber sie und ebnete sie ein"},
  {"surah_number": 91, "verse_number": 15, "content": "Und Er furchtet ihre Folgen nicht"},
  {"surah_number": 92, "verse_number": 1, "content": "Bei der Nacht, wenn sie zudeckt"},
  {"surah_number": 92, "verse_number": 2, "content": "und bei dem Tage, wenn er erstrahlt"},
  {"surah_number": 92, "verse_number": 3, "content": "und bei Dem, Der das Mannliche und das Weibliche erschaffen hat"},
  {"surah_number": 92, "verse_number": 4, "content": "Wahrlich, euer Eifer ist verschieden"},
  {"surah_number": 92, "verse_number": 5, "content": "Jener aber, der gibt und gottesfurchtig ist"},
  {"surah_number": 92, "verse_number": 6, "content": "und an das Beste glaubt"},
  {"surah_number": 92, "verse_number": 7, "content": "dem wollen Wir den Weg zum Heil leicht machen"},
  {"surah_number": 92, "verse_number": 8, "content": "Jener aber, der geizt und gleichgultig ist"},
  {"surah_number": 92, "verse_number": 9, "content": "und das Beste leugnet"},
  {"surah_number": 92, "verse_number": 10, "content": "dem wollen Wir den Weg zur Drangsal leicht machen"},
  {"surah_number": 92, "verse_number": 11, "content": "Und sein Vermogen soll ihm nichts nutzen, wenn er zugrunde geht"},
  {"surah_number": 92, "verse_number": 12, "content": "Wahrlich, Uns obliegt die Rechtleitung"},
  {"surah_number": 92, "verse_number": 13, "content": "Und Uns gehort das Jenseits und das Diesseits"},
  {"surah_number": 92, "verse_number": 14, "content": "darum warne Ich euch vor einem loderenden Feuer"},
  {"surah_number": 92, "verse_number": 15, "content": "in dem nur derjenige brennen wird, der unselig ist"},
  {"surah_number": 92, "verse_number": 16, "content": "der da leugnet und den Rucken kehrt"},
  {"surah_number": 92, "verse_number": 17, "content": "Geschont von ihm wird derjenige sein, der gottesfurchtig ist"},
  {"surah_number": 92, "verse_number": 18, "content": "der sein Vermogen hergibt, um sich zu reinigen"},
  {"surah_number": 92, "verse_number": 19, "content": "und der keinem eine Gunst schuldet, die zuruckgezahlt werden mußte"},
  {"surah_number": 92, "verse_number": 20, "content": "außer im Trachten nach dem Wohlgefallen seines Herrn, des Allerhochsten"},
  {"surah_number": 92, "verse_number": 21, "content": "Und er wird wohlzufrieden sein"},
  {"surah_number": 93, "verse_number": 1, "content": "Beim Vormittag"},
  {"surah_number": 93, "verse_number": 2, "content": "und bei der Nacht, wenn alles still ist"},
  {"surah_number": 93, "verse_number": 3, "content": "Dein Herr hat dich weder verlassen, noch verabscheut"},
  {"surah_number": 93, "verse_number": 4, "content": "Wahrlich, das Jenseits ist besser fur dich als das Diesseits"},
  {"surah_number": 93, "verse_number": 5, "content": "Und wahrlich, dein Herr wird dir geben und du wirst wohlzufrieden sein"},
  {"surah_number": 93, "verse_number": 6, "content": "Hat Er dich nicht als Waise gefunden und aufgenommen"},
  {"surah_number": 93, "verse_number": 7, "content": "und dich auf dem Irrweg gefunden und richtig gefuhrt"},
  {"surah_number": 93, "verse_number": 8, "content": "und dich durftig gefunden und reich gemacht"},
  {"surah_number": 93, "verse_number": 9, "content": "Darum unterdrucke nicht die Waise"},
  {"surah_number": 93, "verse_number": 10, "content": "und fahre nicht den Bettler an"},
  {"surah_number": 93, "verse_number": 11, "content": "und sprich uberall von der Gnade deines Herrn"},
  {"surah_number": 94, "verse_number": 1, "content": "Haben Wir dir nicht deine Brust geweitet"},
  {"surah_number": 94, "verse_number": 2, "content": "und dir deine Last abgenommen"},
  {"surah_number": 94, "verse_number": 3, "content": "die schwer auf deinem Rucken lastete"},
  {"surah_number": 94, "verse_number": 4, "content": "und deinen Namen erhoht"},
  {"surah_number": 94, "verse_number": 5, "content": "Also, wahrlich, mit der Drangsal geht Erleichterung einher"},
  {"surah_number": 94, "verse_number": 6, "content": "wahrlich, mit der Drangsal geht Erleichterung (einher)"},
  {"surah_number": 94, "verse_number": 7, "content": "Also, wenn du (mit allem) fertig bist, dann muhe dich ab"},
  {"surah_number": 94, "verse_number": 8, "content": "und begehre die Nahe deines Herrn"},
  {"surah_number": 95, "verse_number": 1, "content": "Beim Feigenbaum und beim Olbaum"},
  {"surah_number": 95, "verse_number": 2, "content": "und beim Berge Sinai"},
  {"surah_number": 95, "verse_number": 3, "content": "und bei dieser sicheren Ortschaft"},
  {"surah_number": 95, "verse_number": 4, "content": "Wahrlich, Wir haben den Menschen in bester Form erschaffen"},
  {"surah_number": 95, "verse_number": 5, "content": "Alsdann haben Wir ihn in die niedrigste Tiefe zuruckgebracht"},
  {"surah_number": 95, "verse_number": 6, "content": "ausgenommen (davon) sind diejenigen, die glauben und Gutes tun; ihnen wird ein unverkurzter Lohn zuteil sein"},
  {"surah_number": 95, "verse_number": 7, "content": "Und was veranlaßt dich hernach, die Religion zu leugnen"},
  {"surah_number": 95, "verse_number": 8, "content": "Ist nicht Allah der gerechteste Richter"},
  {"surah_number": 96, "verse_number": 1, "content": "Lies im Namen deines Herrn, Der erschuf"},
  {"surah_number": 96, "verse_number": 2, "content": "Er erschuf den Menschen aus einem Blutklumpen"},
  {"surah_number": 96, "verse_number": 3, "content": "Lies; denn dein Herr ist Allgutig"},
  {"surah_number": 96, "verse_number": 4, "content": "Der mit dem Schreibrohr lehrt"},
  {"surah_number": 96, "verse_number": 5, "content": "lehrt den Menschen, was er nicht wußte"},
  {"surah_number": 96, "verse_number": 6, "content": "Keineswegs! Wahrlich, der Mensch ubt Gewalttatigkeit"},
  {"surah_number": 96, "verse_number": 7, "content": "weil er sich im Reichtum sieht"},
  {"surah_number": 96, "verse_number": 8, "content": "Wahrlich, zu deinem Herrn ist die Heimkehr"},
  {"surah_number": 96, "verse_number": 9, "content": "Hast du den gesehen, der da verwehrt"},
  {"surah_number": 96, "verse_number": 10, "content": "(Unserem) Diener, daß er betet"},
  {"surah_number": 96, "verse_number": 11, "content": "Hast du gesehen, ob er auf dem rechten Weg ist"},
  {"surah_number": 96, "verse_number": 12, "content": "oder zur Gerechtigkeit auffordert"},
  {"surah_number": 96, "verse_number": 13, "content": "Hast du (den) gesehen, der unglaubig ist und sich abwendet"},
  {"surah_number": 96, "verse_number": 14, "content": "Weiß er nicht, daß Allah (ihn) sieht"},
  {"surah_number": 96, "verse_number": 15, "content": "Keineswegs! Wenn er nicht (davon) ablaßt, werden Wir ihn gewiß ergreifen bei der Stirnlocke"},
  {"surah_number": 96, "verse_number": 16, "content": "der lugenden, sundigen Stirnlocke"},
  {"surah_number": 96, "verse_number": 17, "content": "er mag dann seine Mitverschworenen anrufen"},
  {"surah_number": 96, "verse_number": 18, "content": "Wir werden die Hollenwachter herbeirufen"},
  {"surah_number": 96, "verse_number": 19, "content": "gehorche ihm doch nicht und wirf dich in Anbetung nieder und nahe dich (Allah)"},
  {"surah_number": 97, "verse_number": 1, "content": "Wahrlich, Wir haben ihn (den Quran) hinabgesandt in der Nacht von Al-Qadr"},
  {"surah_number": 97, "verse_number": 2, "content": "Und was lehrt dich wissen, ist? was die Nacht von Al-Qadr"},
  {"surah_number": 97, "verse_number": 3, "content": "Die Nacht von Al Qadr ist besser als tausend Monate"},
  {"surah_number": 97, "verse_number": 4, "content": "In ihr steigen die Engel und Gabriel herab mit der Erlaubnis ihres Herrn zu jeglichem Geheiß"},
  {"surah_number": 97, "verse_number": 5, "content": "Frieden ist sie bis zum Anbruch der Morgenrote"},
  {"surah_number": 98, "verse_number": 1, "content": "Diejenigen, die unglaubig sind unter dem Volk der Schrift und die Gotzendiener konnen (von ihrem Irrtum) nichtbefreit werden, bis der deutliche Beweis zu ihnen kommt"},
  {"surah_number": 98, "verse_number": 2, "content": "ein Gesandter von Allah, der (ihnen) reinerhaltene (Quran-) Abschnitte verliest"},
  {"surah_number": 98, "verse_number": 3, "content": "in denen geradlinigeVorschriften enthalten sind"},
  {"surah_number": 98, "verse_number": 4, "content": "Und die, denen die Schrift gegeben wurde, waren nicht eher gespalten, alsbis der deutliche Beweis zu ihnen gekommen war"},
  {"surah_number": 98, "verse_number": 5, "content": "Und doch war ihnen nichts anderes befohlen worden, als Allah treu in lauterem Glauben zu dienen und das Gebet zu verrichten und die Zakah zu entrichten. Und das ist die Religion der Geradlinigkeit"},
  {"surah_number": 98, "verse_number": 6, "content": "Wahrlich, jene, die unglaubig sind unter dem Volk der Schrift und die Gotzendiener werden im Feuer der Gahannam sein; ewig werden sie darin bleiben; diese sind die schlechtesten der Geschopfe"},
  {"surah_number": 98, "verse_number": 7, "content": "Wahrlich, diejenigen aber, die glauben und gute Werke tun, sind die besten der Geschopfe"},
  {"surah_number": 98, "verse_number": 8, "content": "Ihr Lohn bei ihrem Herrn sind die Garten von Eden, durcheilt von Bachen; ewig und immerdar werden sie darin verweilen. Allah ist mit ihnen wohlzufrieden und sie sind wohlzufrieden mit Ihm. Dies ist fur den, der seinen Herrn furchtet"},
  {"surah_number": 99, "verse_number": 1, "content": "Wenn die Erde in aller Heftigkeit erbebt"},
  {"surah_number": 99, "verse_number": 2, "content": "und wenn die Erde ihre Lasten herausgibt"},
  {"surah_number": 99, "verse_number": 3, "content": "und wenn der Mensch sagt: \"Was ist mit ihr"},
  {"surah_number": 99, "verse_number": 4, "content": "An jenem Tage wird sie ihre Geschichten erzahlen"},
  {"surah_number": 99, "verse_number": 5, "content": "so wie ihr Herr (es) ihr eingegeben hat"},
  {"surah_number": 99, "verse_number": 6, "content": "An jenem Tage kommen die Menschen in Gruppen zerstreut hervor, damit ihnen ihre Werke gezeigt werden"},
  {"surah_number": 99, "verse_number": 7, "content": "Wer auch nur eines Staubchens Gewicht Gutes tut, der wird es dann sehen"},
  {"surah_number": 99, "verse_number": 8, "content": "Und wer auch nur eines Staubchens Gewicht Boses tut, der wird es dann sehen"},
  {"surah_number": 100, "verse_number": 1, "content": "Bei den schnaubenden Rennern"},
  {"surah_number": 100, "verse_number": 2, "content": "die dann Feuerfunken schlagen"},
  {"surah_number": 100, "verse_number": 3, "content": "alsdann fruhmorgens ansturmen"},
  {"surah_number": 100, "verse_number": 4, "content": "und damit Staub aufwirbeln"},
  {"surah_number": 100, "verse_number": 5, "content": "und so in die Mitte (des Feindes) eindringen"},
  {"surah_number": 100, "verse_number": 6, "content": "Wahrlich, der Mensch ist undankbar gegen seinen Herrn"},
  {"surah_number": 100, "verse_number": 7, "content": "und wahrlich, er bezeugt es selber"},
  {"surah_number": 100, "verse_number": 8, "content": "und wahrlich, stark ist seine Liebe zum (irdischen) Gut"},
  {"surah_number": 100, "verse_number": 9, "content": "Weiß er denn nicht, wenn der Inhalt der Graber herausgeworfen wird"},
  {"surah_number": 100, "verse_number": 10, "content": "und das herausgeholt wird, was in den Herzen ist"},
  {"surah_number": 100, "verse_number": 11, "content": "daß ihr Herr sie wahrlich an jenem Tag wohl kennt"},
  {"surah_number": 101, "verse_number": 1, "content": "Al-Qaria"},
  {"surah_number": 101, "verse_number": 2, "content": "Was ist AI-Qaria"},
  {"surah_number": 101, "verse_number": 3, "content": "Und was laßt dich wissen, was Al-Qaria ist"},
  {"surah_number": 101, "verse_number": 4, "content": "An einem Tage, da die Menschen gleich verstreuten Motten sein werden"},
  {"surah_number": 101, "verse_number": 5, "content": "und die Berge gleich bunter, zerflockter Wolle"},
  {"surah_number": 101, "verse_number": 6, "content": "dann wird der, dessen Waage schwer ist"},
  {"surah_number": 101, "verse_number": 7, "content": "ein Wohlleben genießen"},
  {"surah_number": 101, "verse_number": 8, "content": "dem aber, dessen Waage leicht ist"},
  {"surah_number": 101, "verse_number": 9, "content": "wird die Hawiya sein Endziel sein"},
  {"surah_number": 101, "verse_number": 10, "content": "Und was lehrt dich wissen, was die (Hawiya) ist"},
  {"surah_number": 101, "verse_number": 11, "content": "(Sie ist) ein gluhendes Feuer"},
  {"surah_number": 102, "verse_number": 1, "content": "Das Streben nach Mehr lenkt euch solange ab"},
  {"surah_number": 102, "verse_number": 2, "content": "bis ihr die Graber besucht"},
  {"surah_number": 102, "verse_number": 3, "content": "Aber nein! Ihr werdet es bald erfahren"},
  {"surah_number": 102, "verse_number": 4, "content": "Wiederum: Aber nein! Ihr werdet es bald erfahren"},
  {"surah_number": 102, "verse_number": 5, "content": "Aber nein! Wenn ihr es sicher wußtet"},
  {"surah_number": 102, "verse_number": 6, "content": "Ihr werdet die Gahim sehen"},
  {"surah_number": 102, "verse_number": 7, "content": "Doch, ihr sollt sie noch mit dem Auge der Gewißheit sehen"},
  {"surah_number": 102, "verse_number": 8, "content": "Dann werdet ihr, an jenem Tage, nach dem Wohlstand befragt"},
  {"surah_number": 103, "verse_number": 1, "content": "Beim Nachmittag"},
  {"surah_number": 103, "verse_number": 2, "content": "Die Menschen sind wahrhaftig im Verlust"},
  {"surah_number": 103, "verse_number": 3, "content": "außer denjenigen, die glauben und gute Werke tun und sich gegenseitig die Wahrheit ans Herz legen und sich gegenseitig zur Geduld anhalten"},
  {"surah_number": 104, "verse_number": 1, "content": "Wehe jedem Stichler, Verleumder"},
  {"surah_number": 104, "verse_number": 2, "content": "der ein Vermogen zusammentragt und es gezahlt zurucklegt"},
  {"surah_number": 104, "verse_number": 3, "content": "Er meint, daß sein Vermogen ihn unsterblich mache"},
  {"surah_number": 104, "verse_number": 4, "content": "Aber nein! Er wird wahrlich in Al-Hutama geschleudert werden"},
  {"surah_number": 104, "verse_number": 5, "content": "Doch was laßt dich wissen, was Al-Hutama ist"},
  {"surah_number": 104, "verse_number": 6, "content": "(Es ist) Allahs angezundetes Feuer"},
  {"surah_number": 104, "verse_number": 7, "content": "das bis zu dem (innersten der) Herzen vordringt"},
  {"surah_number": 104, "verse_number": 8, "content": "Es schlagt uber ihnen zusammen"},
  {"surah_number": 104, "verse_number": 9, "content": "in langgestreckten Saulen"},
  {"surah_number": 105, "verse_number": 1, "content": "Hast du nicht gesehen, wie dein Herr mit den Leuten des Elefanten verfahren ist"},
  {"surah_number": 105, "verse_number": 2, "content": "Hat Er nicht ihre List mißlingen lassen"},
  {"surah_number": 105, "verse_number": 3, "content": "und Vogel in Scharen uber sie gesandt"},
  {"surah_number": 105, "verse_number": 4, "content": "die sie mit brennenden Steinen bewarfen"},
  {"surah_number": 105, "verse_number": 5, "content": "und sie dadurch wie abgefressene Saat gemacht"},
  {"surah_number": 106, "verse_number": 1, "content": "Fur die Vereinigung der Qurais"},
  {"surah_number": 106, "verse_number": 2, "content": "(fur) ihre Vereinigung zur Reise in der Karawane des Winters und des Sommers"},
  {"surah_number": 106, "verse_number": 3, "content": "So sollen sie denn dem Herrn dieses Hauses dienen"},
  {"surah_number": 106, "verse_number": 4, "content": "Der sie speist nachdem sie gehungert haben, und ihnen Sicherheit gewahrleistet, nachdem sie in Angst lebten"},
  {"surah_number": 107, "verse_number": 1, "content": "Hast du den gesehen, der das Gericht leugnet"},
  {"surah_number": 107, "verse_number": 2, "content": "Das ist der, der die Waise wegstoßt"},
  {"surah_number": 107, "verse_number": 3, "content": "und nicht zur Speisung des Armen anspornt"},
  {"surah_number": 107, "verse_number": 4, "content": "Wehe denjenigen Betenden"},
  {"surah_number": 107, "verse_number": 5, "content": "die (bei der Verrichtung) ihres Gebets nachlassig sind"},
  {"surah_number": 107, "verse_number": 6, "content": "die (nur dabei) gesehen werden wollen"},
  {"surah_number": 107, "verse_number": 7, "content": "und die Hilfeleistung verweigern"},
  {"surah_number": 108, "verse_number": 1, "content": "Wir haben dir die Uberfulle gegeben"},
  {"surah_number": 108, "verse_number": 2, "content": "Darum bete zu deinem Herrn und schlachte (Opfertiere)"},
  {"surah_number": 108, "verse_number": 3, "content": "Wahrlich, der dich haßt, ist es, der (vom Segen der Nachkommenschaft) abgeschnitten ist"},
  {"surah_number": 109, "verse_number": 1, "content": "Sprich: \"O ihr Unglaubigen"},
  {"surah_number": 109, "verse_number": 2, "content": "Ich diene nicht dem, dem ihr dient"},
  {"surah_number": 109, "verse_number": 3, "content": "und ihr dient nicht Dem, Dem ich diene"},
  {"surah_number": 109, "verse_number": 4, "content": "Und ich werde nicht Diener dessen sein, dem ihr dient"},
  {"surah_number": 109, "verse_number": 5, "content": "und ihr werdet nicht Diener Dessen sein, Dem ich diene"},
  {"surah_number": 109, "verse_number": 6, "content": "Ihr habt eure Religion und ich habe meine Religion"},
  {"surah_number": 110, "verse_number": 1, "content": "Wenn die Hilfe Allahs kommt und der Sieg"},
  {"surah_number": 110, "verse_number": 2, "content": "und du die Menschen zur Religion Allahs in Scharen ubertreten siehst"},
  {"surah_number": 110, "verse_number": 3, "content": "dann lobpreise deinen Herrn und bitte Ihn um Vergebung! Er ist wahrlich Der, Der die Reue an nimmt"},
  {"surah_number": 111, "verse_number": 1, "content": "Zugrunde gehen sollen die Hande Abu Lahabs! Und (auch er selbst) soll zugrunde gehen"},
  {"surah_number": 111, "verse_number": 2, "content": "Nichts soll ihm sein Vermogen nutzen, noch das, was er erworben hat"},
  {"surah_number": 111, "verse_number": 3, "content": "er wird in einem flammenden Feuer brennen"},
  {"surah_number": 111, "verse_number": 4, "content": "und seine Frau wird das Brennholz tragen"},
  {"surah_number": 111, "verse_number": 5, "content": "Um ihren Hals ist ein Strick aus Palmfasern"},
  {"surah_number": 112, "verse_number": 1, "content": "Sprich: \"Er ist Allah, ein Einziger"},
  {"surah_number": 112, "verse_number": 2, "content": "Allah, der Absolute (ewig Unabhangige, von Dem alles abhangt)"},
  {"surah_number": 112, "verse_number": 3, "content": "Er zeugt nicht und ist nicht gezeugt worden"},
  {"surah_number": 112, "verse_number": 4, "content": "und Ihm ebenburtig ist keiner"},
  {"surah_number": 113, "verse_number": 1, "content": "Sprich: \"ich nehme meine Zuflucht beim Herrn des Fruhlichts"},
  {"surah_number": 113, "verse_number": 2, "content": "vor dem Ubel dessen, was Er erschaffen hat"},
  {"surah_number": 113, "verse_number": 3, "content": "und vor dem Ubel der Dunkelheit, wenn sie hereinbricht"},
  {"surah_number": 113, "verse_number": 4, "content": "und vor dem Ubel der Knotenanblaserinnen"},
  {"surah_number": 113, "verse_number": 5, "content": "und vor dem Ubel eines (jeden) Neiders, wenn er neidet"},
  {"surah_number": 114, "verse_number": 1, "content": "Sprich: \"lch nehme meine Zuflucht beim Herrn der Menschen"},
  {"surah_number": 114, "verse_number": 2, "content": "dem Konig der Menschen"},
  {"surah_number": 114, "verse_number": 3, "content": "dem Gott der Menschen"},
  {"surah_number": 114, "verse_number": 4, "content": "vor dem Ubel des Einflusterers, der entweicht und wiederkehrt"},
  {"surah_number": 114, "verse_number": 5, "content": "der in die Herzen der Menschen einflustert"},
  {"surah_number": 114, "verse_number": 6, "content": "sei dieser aus den Ginn oder den Menschen"}
];
