    "surah_number": 37,
    "verse_number": 11,
    "content": "اب اِن سے پوچھو، اِن کی پیدائش زیادہ مشکل ہے یا اُن چیزوں کی جو ہم نے پیدا کر رکھی ہیں؟ اِن کو تو ہم نے لیس دار گارے سے پیدا کیا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 12,
    "content": "تم (اللہ کی قدرت کے کرشموں پر) حیران ہو اور یہ اس کا مذاق اڑا رہے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 13,
    "content": "سمجھایا جاتا ہے تو سمجھ کر نہیں دیتے",
  },
  {
    "surah_number": 37,
    "verse_number": 14,
    "content": "کوئی نشانی دیکھتے ہیں تو اسے ٹھٹھوں میں اڑاتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 15,
    "content": "اور کہتے ہیں 'یہ تو صریح جادو ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 16,
    "content": "بھلا کہیں ایسا ہو سکتا ہے کہ جب ہم مر چکے ہوں اور مٹی بن جائیں اور ہڈیوں کا پنجر رہ جائیں اُس وقت ہم پھر زندہ کر کے اٹھا کھڑے کیے جائیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 17,
    "content": "اور کیا ہمارے اگلے وقتوں کے آبا و اجداد بھی اٹھائے جائیں گے؟",
  },
  {
    "surah_number": 37,
    "verse_number": 18,
    "content": "اِن سے کہو ہاں، اور تم (خدا کے مقابلے میں) بے بس ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 19,
    "content": "بس ایک ہی جھڑکی ہو گی اور یکایک یہ اپنی آنکھوں سے (وہ سب کچھ جس کی خبر دی جا رہی ہے) دیکھ رہے ہوں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 20,
    "content": "اُس وقت یہ کہیں گے 'ہائے ہماری کم بختی، یہ تو یوم الجزا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 21,
    "content": "یہ وہی فیصلے کا دن ہے جسے تم جھٹلایا کرتے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 22,
    "content": "(حکم ہو گا) گھیر لاؤ سب ظالموں اور ان کے ساتھیوں اور اُن معبودوں کو جن کی وہ خدا کو چھوڑ کر بندگی کیا کرتے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 23,
    "content": "پھر ان سب کو جہنم کا راستہ دکھاؤ",
  },
  {
    "surah_number": 37,
    "verse_number": 24,
    "content": "اور ذرا اِنہیں ٹھیراؤ، اِن سے کچھ پوچھنا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 25,
    "content": "کیا ہو گیا تمہیں، اب کیوں ایک دوسرے کی مدد نہیں کرتے؟",
  },
  {
    "surah_number": 37,
    "verse_number": 26,
    "content": "ارے، آج تو یہ اپنے آپ کو (اور ایک دوسرے کو) حوالے کیے دے رہے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 27,
    "content": "اس کے بعد یہ ایک دوسرے کی طرف مڑیں گے اور باہم تکرار شروع کر دیں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 28,
    "content": "(پیروی کرنے والے اپنے پیشواؤں سے) کہیں گے، 'تم ہمارے پاس سیدھے رخ سے آتے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 29,
    "content": "وہ جواب دیں گے، 'نہیں بلکہ تم خود ایمان لانے والے نہ تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 30,
    "content": "ہمارا تم پر کوئی زور نہ تھا، تم خود ہی سرکش لوگ تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 31,
    "content": "آخرکار ہم اپنے رب کے اِس فرمان کے مستحق ہو گئے کہ ہم عذاب کا مزا چکھنے والے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 32,
    "content": "سو ہم نے تم کو بہکا یا، ہم خود بہکے ہوئے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 33,
    "content": "اِس طرح وہ سب اُس روز عذاب میں مشترک ہوں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 34,
    "content": "ہم مجرموں کے ساتھ یہی کچھ کیا کرتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 35,
    "content": "یہ وہ لوگ تھے کہ جب ان سے کہا جاتا 'اللہ کے سوا کوئی معبود بر حق نہیں ہے' تو یہ گھمنڈ میں آ جاتے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 36,
    "content": "اور کہتے تھے 'کیا ہم ایک شاعر مجنون کی خاطر اپنے معبودوں کو چھوڑ دیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 37,
    "content": "حالانکہ وہ حق لے کر آیا تھا اور اس نے رسولوں کی تصدیق کی تھی",
  },
  {
    "surah_number": 37,
    "verse_number": 38,
    "content": "(اب ان سے کہا جائے گا کہ) تم لازماً دردناک سزا کا مزا چکھنے والے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 39,
    "content": "اور تمہیں جو بدلہ بھی دیا جا رہا ہے اُنہی اعمال کا دیا جا رہا ہے جو تم کرتے رہے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 40,
    "content": "مگر اللہ کے چیدہ بندے (اس انجام بد سے) محفوظ ہوں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 41,
    "content": "ان کے لیے جانا بوجھا رزق ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 42,
    "content": "ہر طرح کی لذیذ چیزیں،",
  },
  {
    "surah_number": 37,
    "verse_number": 43,
    "content": "اور نعمت بھری جنتیں",
  },
  {
    "surah_number": 37,
    "verse_number": 44,
    "content": "جن میں وہ عزت کے ساتھ رکھے جائیں گے تختوں پر آمنے سامنے بیٹھیں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 45,
    "content": "شراب کے چشموں سے ساغر بھر بھر کر ان کے درمیان پھرائے جائیں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 46,
    "content": "چمکتی ہوئی شراب، جو پینے والوں کے لیے لذّت ہو گی",
  },
  {
    "surah_number": 37,
    "verse_number": 47,
    "content": "نہ ان کے جسم کو اس سے کوئی ضرر ہوگا اور نہ ان کی عقل اس سے خراب ہو گی",
  },
  {
    "surah_number": 37,
    "verse_number": 48,
    "content": "اور ان کے پاس نگاہیں بچانے والی، خوبصورت آنکھوں والی عورتیں ہوں گی",
  },
  {
    "surah_number": 37,
    "verse_number": 49,
    "content": "ایسی نازک جیسے انڈے کے چھلکے کے نیچے چھپی ہوئی جھلی",
  },
  {
    "surah_number": 37,
    "verse_number": 50,
    "content": "پھر وہ ایک دوسرے کی طرف متوجہ ہو کر حالات پوچھیں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 51,
    "content": "ان میں سے ایک کہے گا، 'دنیا میں میرا ایک ہم نشین تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 52,
    "content": "جو مجھ سے کہا کرتا تھا، کیا تم بھی تصدیق کرنے والوں میں سے ہو؟",
  },
  {
    "surah_number": 37,
    "verse_number": 53,
    "content": "کیا واقعی جب ہم مر چکے ہوں گے اور مٹی ہو جائیں گے اور ہڈیوں کا پنجر بن کر رہ جائیں گے تو ہمیں جزا و سزا دی جائے گی؟",
  },
  {
    "surah_number": 37,
    "verse_number": 54,
    "content": "اب کیا آپ لوگ دیکھنا چاہتے ہیں کہ وہ صاحب اب کہاں ہیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 55,
    "content": "یہ کہہ کر جونہی وہ جھکے گا تو جہنم کی گہرائی میں اس کو دیکھ لے گا",
  },
  {
    "surah_number": 37,
    "verse_number": 56,
    "content": "اور اس سے خطاب کر کے کہے گا 'خدا کی قسم، تُو تو مجھے تباہ ہی کر دینے والا تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 57,
    "content": "میرے رب کا فضل شامل حال نہ ہوتا تو آج میں بھی اُن لوگوں میں سے ہوتا جو پکڑے ہوئے آئے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 58,
    "content": "اچھا تو کیا اب ہم مرنے والے نہیں ہیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 59,
    "content": "موت جو ہمیں آنی تھی وہ بس پہلے آ چکی؟ اب ہمیں کوئی عذاب نہیں ہونا؟",
  },
  {
    "surah_number": 37,
    "verse_number": 60,
    "content": "یقیناً یہی عظیم الشان کامیابی ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 61,
    "content": "ایسی ہی کامیابی کے لیے عمل کرنے والوں کو عمل کرنا چاہیے",
  },
  {
    "surah_number": 37,
    "verse_number": 62,
    "content": "بولو، یہ ضیافت اچھی ہے یا زقوم کا درخت؟",
  },
  {
    "surah_number": 37,
    "verse_number": 63,
    "content": "ہم نے اُس درخت کو ظالموں کے لیے فتنہ بنا دیا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 64,
    "content": "وہ ایک درخت ہے جو جہنم کی تہ سے نکلتا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 65,
    "content": "اُس کے شگوفے ایسے ہیں جیسے شیطانوں کے سر",
  },
  {
    "surah_number": 37,
    "verse_number": 66,
    "content": "جہنم کے لوگ اُسے کھائیں گے اور اسی سے پیٹ بھریں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 67,
    "content": "پھر اس پر پینے کے لیے کھولتا ہوا پانی ملے گا",
  },
  {
    "surah_number": 37,
    "verse_number": 68,
    "content": "اور اس کے بعد ان کی واپسی اُسی آتش دوزخ کی طرف ہو گی",
  },
  {
    "surah_number": 37,
    "verse_number": 69,
    "content": "یہ وہ لوگ ہیں جنہوں نے اپنے باپ دادا کو گمراہ پایا",
  },
  {
    "surah_number": 37,
    "verse_number": 70,
    "content": "اور انہی کے نقش قدم پر دوڑ چلے",
  },
  {
    "surah_number": 37,
    "verse_number": 71,
    "content": "حالانکہ ان سے پہلے بہت سے لوگ گمراہ ہو چکے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 72,
    "content": "اور اُن میں ہم نے تنبیہ کرنے والے رسول بھیجے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 73,
    "content": "اب دیکھ لو کہ اُن تنبیہ کیے جانے والوں کا کیا انجام ہوا",
  },
  {
    "surah_number": 37,
    "verse_number": 74,
    "content": "اس بد انجامی سے بس اللہ کے وہی بندے بچے ہیں جنہیں اس نے اپنے لیے خالص کر لیا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 75,
    "content": "ہم کو (اِس سے پہلے) نوحؑ نے پکارا تھا، تو دیکھو کہ ہم کیسے اچھے جواب دینے والے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 76,
    "content": "ہم نے اُس کو اور اس کے گھر والوں کو کرب عظیم سے بچا لیا",
  },
  {
    "surah_number": 37,
    "verse_number": 77,
    "content": "اور اسی کی نسل کو باقی رکھا",
  },
  {
    "surah_number": 37,
    "verse_number": 78,
    "content": "اور بعد کی نسلوں میں اس کی تعریف و توصیف چھوڑ دی",
  },
  {
    "surah_number": 37,
    "verse_number": 79,
    "content": "سلام ہے نوحؑ پر تمام دنیا والوں میں",
  },
  {
    "surah_number": 37,
    "verse_number": 80,
    "content": "ہم نیکی کرنے والوں کو ایسی ہی جزا دیا کرتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 81,
    "content": "در حقیقت وہ ہمارے مومن بندوں میں سے تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 82,
    "content": "پھر دوسرے گروہ کو ہم نے غرق کر دیا",
  },
  {
    "surah_number": 37,
    "verse_number": 83,
    "content": "اور نوحؑ ہی کے طریقے پر چلنے والا ابراہیمؑ تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 84,
    "content": "جب وہ اپنے رب کے حضور قلب سلیم لے کر آیا",
  },
  {
    "surah_number": 37,
    "verse_number": 85,
    "content": "جب اُس نے اپنے باپ اور اپنی قوم سے کہا 'یہ کیا چیزیں ہیں جن کی تم عبادت کر رہے ہو؟",
  },
  {
    "surah_number": 37,
    "verse_number": 86,
    "content": "کیا اللہ کو چھوڑ کر جھوٹ گھڑے ہوئے معبود چاہتے ہو؟",
  },
  {
    "surah_number": 37,
    "verse_number": 87,
    "content": "آخر اللہ ربّ العالمین کے بارے میں تمہارا کیا گمان ہے؟",
  },
  {
    "surah_number": 37,
    "verse_number": 88,
    "content": "پھر اس نے تاروں پر ایک نگاہ ڈالی",
  },
  {
    "surah_number": 37,
    "verse_number": 89,
    "content": "اور کہا میری طبیعت خراب ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 90,
    "content": "چنانچہ وہ لوگ اسے چھوڑ کر چلے گئے",
  },
  {
    "surah_number": 37,
    "verse_number": 91,
    "content": "اُن کے پیچھے وہ چپکے سے اُن کے معبودوں کے مندر میں گھس گیا اور بولا ' آپ لوگ کھاتے کیوں نہیں ہیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 92,
    "content": "کیا ہو گیا، آپ لوگ بولتے بھی نہیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 93,
    "content": "اس کے بعد وہ اُن پر پل پڑا اور سیدھے ہاتھ سے خوب ضربیں لگائیں",
  },
  {
    "surah_number": 37,
    "verse_number": 94,
    "content": "(واپس آ کر) وہ لوگ بھاگے بھاگے اس کے پاس آئے",
  },
  {
    "surah_number": 37,
    "verse_number": 95,
    "content": "اس نے کہا 'کیا تم اپنی ہی تراشی ہوئی چیزوں کو پوجتے ہو؟",
  },
  {
    "surah_number": 37,
    "verse_number": 96,
    "content": "حالانکہ اللہ ہی نے تم کو بھی پیدا کیا ہے اور اُن چیزوں کو بھی جنہیں تم بناتے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 97,
    "content": "انہوں نے آپس میں کہا 'اس کے لیے ایک الاؤ تیار کرو اور اسے دہکتی ہوئی آگ کے ڈھیر میں پھینک دو",
  },
  {
    "surah_number": 37,
    "verse_number": 98,
    "content": "انہوں نے اس کے خلاف ایک کاروائی کرنی چاہی تھی، مگر ہم نے انہی کو نیچا دکھا دیا",
  },
  {
    "surah_number": 37,
    "verse_number": 99,
    "content": "ابراہیمؑ نے کہا 'میں اپنے رب کی طرف جاتا ہوں، وہی میری رہنمائی کرے گا",
  },
  {
    "surah_number": 37,
    "verse_number": 100,
    "content": "اے پروردگار، مجھے ایک بیٹا عطا کر جو صالحوں میں سے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 101,
    "content": "(اس دعا کے جواب میں) ہم نے اس کو ایک حلیم (بردبار) لڑکے کی بشارت دی",
  },
  {
    "surah_number": 37,
    "verse_number": 102,
    "content": "وہ لڑکا جب اس کے ساتھ دوڑ دھوپ کرنے کی عمر کو پہنچ گیا تو (ایک روز) ابراہیمؑ نے اس سے کہا، 'بیٹا، میں خواب میں دیکھتا ہوں کہ میں تجھے ذبح کر رہا ہوں، اب تو بتا، تیرا کیا خیال ہے؟' اُس نے کہا، 'ابا جان، جو کچھ آپ کو حکم دیا جا رہا ہے اسے کر ڈالیے، آپ انشاءاللہ مجھے صابروں میں سے پائیں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 103,
    "content": "آخر کو جب اِن دونوں نے سر تسلیم خم کر دیا اور ابراہیمؑ نے بیٹے کو ماتھے کے بل گرا دیا",
  },
  {
    "surah_number": 37,
    "verse_number": 104,
    "content": "اور ہم نے ندا دی کہ 'اے ابراہیمؑ",
  },
  {
    "surah_number": 37,
    "verse_number": 105,
    "content": "تو نے خواب سچ کر دکھایا ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 106,
    "content": "یقیناً یہ ایک کھلی آزمائش تھی",
  },
  {
    "surah_number": 37,
    "verse_number": 107,
    "content": "اور ہم نے ایک بڑی قربانی فدیے میں دے کر اس بچے کو چھڑا لیا",
  },
  {
    "surah_number": 37,
    "verse_number": 108,
    "content": "اور اس کی تعریف و توصیف ہمیشہ کے لیے بعد کی نسلوں میں چھوڑ دی",
  },
  {
    "surah_number": 37,
    "verse_number": 109,
    "content": "سلام ہے ابراہیمؑ پر",
  },
  {
    "surah_number": 37,
    "verse_number": 110,
    "content": "ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 111,
    "content": "یقیناً وہ ہمارے مومن بندوں میں سے تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 112,
    "content": "اور ہم نے اسے اسحاقؑ کی بشارت دی، ایک نبی صالحین میں سے",
  },
  {
    "surah_number": 37,
    "verse_number": 113,
    "content": "اور اسے اور اسحاقؑ کو برکت دی اب ان دونوں کی ذریّت میں سے کوئی محسن ہے اور کوئی اپنے نفس پر صریح ظلم کرنے والا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 114,
    "content": "اور ہم نے موسیٰؑ و ہارونؑ پر احسان کیا",
  },
  {
    "surah_number": 37,
    "verse_number": 115,
    "content": "اُن کو اور ان کی قوم کو کرب عظیم سے نجات دی",
  },
  {
    "surah_number": 37,
    "verse_number": 116,
    "content": "اُنہیں نصرت بخشی جس کی وجہ سے وہی غالب رہے",
  },
  {
    "surah_number": 37,
    "verse_number": 117,
    "content": "ان کو نہایت واضح کتاب عطا کی",
  },
  {
    "surah_number": 37,
    "verse_number": 118,
    "content": "انہیں راہ راست دکھائی",
  },
  {
    "surah_number": 37,
    "verse_number": 119,
    "content": "اور بعد کی نسلوں میں ان کا ذکر خیر باقی رکھا",
  },
  {
    "surah_number": 37,
    "verse_number": 120,
    "content": "سلام ہے موسیٰؑ اور ہارونؑ پر",
  },
  {
    "surah_number": 37,
    "verse_number": 121,
    "content": "ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 122,
    "content": "در حقیقت وہ ہمارے مومن بندوں میں سے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 123,
    "content": "اور الیاسؑ بھی یقیناً مرسلین میں سے تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 124,
    "content": "یاد کرو جب اس نے اپنی قوم سے کہا تھا کہ 'تم لوگ ڈرتے نہیں ہو؟",
  },
  {
    "surah_number": 37,
    "verse_number": 125,
    "content": "کیا تم بعل کو پکارتے ہو اور احسن الخالقین کو چھوڑ دیتے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 126,
    "content": "اُس اللہ کو جو تمہارا اور تمہارے اگلے پچھلے آبا و اجداد کا رب ہے؟",
  },
  {
    "surah_number": 37,
    "verse_number": 127,
    "content": "مگر انہوں نے اسے جھٹلا دیا، سو اب یقیناً وہ سزا کے لیے پیش کیے جانے والے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 128,
    "content": "بجز اُن بندگان خدا کے جن کو خالص کر لیا گیا تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 129,
    "content": "اور الیاسؑ کا ذکر خیر ہم نے بعد کی نسلوں میں باقی رکھا",
  },
  {
    "surah_number": 37,
    "verse_number": 130,
    "content": "سلام ہے الیاسؑ پر",
  },
  {
    "surah_number": 37,
    "verse_number": 131,
    "content": "ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 132,
    "content": "واقعی وہ ہمارے مومن بندوں میں سے تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 133,
    "content": "اور لوطؑ بھی انہی لوگوں میں سے تھا جو رسول بنا کر بھیجے گئے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 134,
    "content": "یاد کرو جب ہم نے اس کو اور اس کے سب گھر والوں کو نجات دی",
  },
  {
    "surah_number": 37,
    "verse_number": 135,
    "content": "سوائے ایک بڑھیا کے جو پیچھے رہ جانے والوں میں سے تھی",
  },
  {
    "surah_number": 37,
    "verse_number": 136,
    "content": "پھر باقی سب کو تہس نہس کر دیا",
  },
  {
    "surah_number": 37,
    "verse_number": 137,
    "content": "آج تم شب و روز اُن کے اجڑے دیار پر سے گزرتے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 138,
    "content": "کیا تم کو عقل نہیں آتی؟",
  },
  {
    "surah_number": 37,
    "verse_number": 139,
    "content": "اور یقیناً یونسؑ بھی رسولوں میں سے تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 140,
    "content": "یاد کرو جب وہ ایک بھری کشتی کی طرف بھاگ نکلا",
  },
  {
    "surah_number": 37,
    "verse_number": 141,
    "content": "پھر قرعہ اندازی میں شریک ہوا اور اس میں مات کھائی",
  },
  {
    "surah_number": 37,
    "verse_number": 142,
    "content": "آخرکار مچھلی نے اسے نگل لیا اور وہ ملامت زدہ تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 143,
    "content": "اب اگر وہ تسبیح کرنے والوں میں سے نہ ہوتا تو",
  },
  {
    "surah_number": 37,
    "verse_number": 144,
    "content": "روز قیامت تک اسی مچھلی کے پیٹ میں رہتا",
  },
  {
    "surah_number": 37,
    "verse_number": 145,
    "content": "آخرکار ہم نے اسے بڑی سقیم حالت میں ایک چٹیل زمین پر پھینک دیا",
  },
  {
    "surah_number": 37,
    "verse_number": 146,
    "content": "اور اُس پر ایک بیل دار درخت اگا دیا",
  },
  {
    "surah_number": 37,
    "verse_number": 147,
    "content": "اس کے بعد ہم نے اُسے ایک لاکھ، یا اس سے زائد لوگوں کی طرف بھیجا",
  },
  {
    "surah_number": 37,
    "verse_number": 148,
    "content": "وہ ایمان لائے اور ہم نے ایک وقت خاص تک انہیں باقی رکھا",
  },
  {
    "surah_number": 37,
    "verse_number": 149,
    "content": "پھر ذرا اِن لوگوں سے پوچھو، کیا (اِن کے دل کو یہ بات لگتی ہے کہ) تمہارے رب کے لیے تو ہوں بیٹیاں اور ان کے لیے ہوں بیٹے",
  },
  {
    "surah_number": 37,
    "verse_number": 150,
    "content": "کیا واقعی ہم نے ملائکہ کو عورتیں ہی بنا یا ہے اور یہ آنکھوں دیکھی بات کہہ رہے ہیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 151,
    "content": "خوب سن رکھو، دراصل یہ لوگ اپنی من گھڑت سے یہ بات کہتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 152,
    "content": "کہ اللہ اولاد رکھتا ہے، اور فی الواقع یہ جھوٹے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 153,
    "content": "کیا اللہ نے بیٹوں کے بجائے بیٹیاں اپنے لیے پسند کر لیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 154,
    "content": "تمہیں کیا ہو گیا ہے، کیسے حکم لگا رہے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 155,
    "content": "کیا تمہیں ہوش نہیں آتا",
  },
  {
    "surah_number": 37,
    "verse_number": 156,
    "content": "یا پھر تمہارے پاس اپنی ان باتوں کے لیے کوئی صاف سند ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 157,
    "content": "تو لاؤ اپنی وہ کتاب اگر تم سچے ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 158,
    "content": "اِنہوں نے اللہ اور ملائکہ کے درمیان نسب کا رشتہ بنا رکھا ہے، حالانکہ ملائکہ خوب جانتے ہیں کہ یہ لوگ مجرم کی حیثیت سے پیش ہونے والے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 159,
    "content": "(اور وہ کہتے ہیں کہ) 'اللہ اُن صفات سے پاک ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 160,
    "content": "جو اُس کے خالص بندوں کے سوا دوسرے لوگ اس کی طرف منسوب کرتے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 161,
    "content": "پس تم اور تمہارے یہ معبود",
  },
  {
    "surah_number": 37,
    "verse_number": 162,
    "content": "اللہ سے کسی کو پھیر نہیں سکتے",
  },
  {
    "surah_number": 37,
    "verse_number": 163,
    "content": "مگر صرف اُس کو جو دوزخ کی بھڑکتی ہوئی آگ میں جھلسنے والا ہو",
  },
  {
    "surah_number": 37,
    "verse_number": 164,
    "content": "اور ہمارا حال تو یہ ہے کہ ہم میں سے ہر ایک کا ایک مقام مقرر ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 165,
    "content": "اور ہم صف بستہ خدمت گار ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 166,
    "content": "اور تسبیح کرنے والے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 167,
    "content": "یہ لوگ پہلے تو کہا کرتے تھے",
  },
  {
    "surah_number": 37,
    "verse_number": 168,
    "content": "کہ کاش ہمارے پاس وہ 'ذکر' ہوتا جو پچھلی قوموں کو ملا تھا",
  },
  {
    "surah_number": 37,
    "verse_number": 169,
    "content": "تو ہم اللہ کے چیدہ بندے ہوتے",
  },
  {
    "surah_number": 37,
    "verse_number": 170,
    "content": "مگر (جب وہ آ گیا) تو انہوں نے اس کا انکار کر دیا اب عنقریب اِنہیں (اِس روش کا نتیجہ) معلوم ہو جائے گا",
  },
  {
    "surah_number": 37,
    "verse_number": 171,
    "content": "اپنے بھیجے ہوئے بندوں سے ہم پہلے ہی وعدہ کر چکے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 172,
    "content": "کہ یقیناً ان کی مدد کی جائے گی",
  },
  {
    "surah_number": 37,
    "verse_number": 173,
    "content": "اور ہمارا لشکر ہی غالب ہو کر رہے گا",
  },
  {
    "surah_number": 37,
    "verse_number": 174,
    "content": "پس اے نبیؐ، ذرا کچھ مدّت تک انہیں اِن کے حال پر چھوڑ دو",
  },
  {
    "surah_number": 37,
    "verse_number": 175,
    "content": "اور دیکھتے رہو، عنقریب یہ خود بھی دیکھ لیں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 176,
    "content": "کیا یہ ہمارے عذاب کے لیے جلدی مچا رہے ہیں؟",
  },
  {
    "surah_number": 37,
    "verse_number": 177,
    "content": "جب وہ اِن کے صحن میں آ اترے گا تو وہ دن اُن لوگوں کے لیے بہت برا ہو گا جنہیں متنبہ کیا جا چکا ہے",
  },
  {
    "surah_number": 37,
    "verse_number": 178,
    "content": "بس ذرا اِنہیں کچھ مدت کے لیے چھوڑ دو",
  },
  {
    "surah_number": 37,
    "verse_number": 179,
    "content": "اور دیکھتے رہو، عنقریب یہ خود دیکھ لیں گے",
  },
  {
    "surah_number": 37,
    "verse_number": 180,
    "content": "پاک ہے تیرا رب، عزت کا مالک، اُن تمام باتوں سے جو یہ لوگ بنا رہے ہیں",
  },
  {
    "surah_number": 37,
    "verse_number": 181,
    "content": "اور سلام ہے مرسلین پر",
  },
  {
    "surah_number": 37,
    "verse_number": 182,
    "content": "اور ساری تعریف اللہ ربّ العالمین ہی کے لیے ہے",
  },
  {
    "surah_number": 38,
    "verse_number": 1,
    "content": "ص، قسم ہے نصیحت بھرے قرآن کی",
  },
  {
    "surah_number": 38,
    "verse_number": 2,
    "content": "بلکہ یہی لوگ، جنہوں نے ماننے سے انکار کیا ہے، سخت تکبر اور ضد میں مبتلا ہیں",
  },
  {
    "surah_number": 38,
    "verse_number": 3,
    "content": "اِن سے پہلے ہم ایسی کتنی ہی قوموں کو ہلاک کر چکے ہیں (اور جب اُن کی شامت آئی ہے) تو وہ چیخ اٹھے ہیں، مگر وہ وقت بچنے کا نہیں ہوتا",
  },
  {
    "surah_number": 38,
    "verse_number": 4,
    "content": "اِن لوگوں کو اس بات پر بڑا تعجب ہوا کہ ایک ڈرانے والا خود اِنہی میں سے آگیا منکرین کہنے لگے کہ 'یہ ساحرہے، سخت جھوٹا ہے",
  },
  {
    "surah_number": 38,
    "verse_number": 5,
    "content": "کیا اِس نے سارے خداؤں کی جگہ بس ایک ہی خدا بنا ڈالا؟ یہ تو بڑی عجیب بات ہے",
  },
  {
    "surah_number": 38,
    "verse_number": 6,
    "content": "اور سرداران قوم یہ کہتے ہوئے نکل گئے کہ 'چلو اور ڈٹے رہو اپنے معبودوں کی عبادت پر یہ بات تو کسی اور ہی غرض سے کہی جا رہی ہے",
  },
  {
