    "surah_number": 43,
    "verse_number": 69,
    "content": "کہا جائے گا کہ 'اے میرے بندو، آج تمہارے لیے کوئی خوف نہیں اور نہ تمہیں کوئی غم لاحق ہوگا",
  },
  {
    "surah_number": 43,
    "verse_number": 70,
    "content": "داخل ہو جاؤ جنت میں تم اور تمہاری بیویاں، تمہیں خوش کر دیا جائے گا",
  },
  {
    "surah_number": 43,
    "verse_number": 71,
    "content": "اُن کے آگے سونے کے تھال اور ساغر گردش کرائے جائیں گے اور ہر من بھاتی اور نگاہوں کو لذت دینے والی چیز وہاں موجود ہو گی ان سے کہا جائے گا، 'تم اب یہاں ہمیشہ رہو گے",
  },
  {
    "surah_number": 43,
    "verse_number": 72,
    "content": "تم اِس جنت کے وارث اپنے اُن اعمال کی وجہ سے ہوئے ہو جو تم دنیا میں کرتے رہے",
  },
  {
    "surah_number": 43,
    "verse_number": 73,
    "content": "تمہارے لیے یہاں بکثرت فواکہ موجود ہیں جنہیں تم کھاؤ گے",
  },
  {
    "surah_number": 43,
    "verse_number": 74,
    "content": "رہے مجرمین، تو وہ ہمیشہ جہنم کے عذاب میں مبتلا رہیں گے",
  },
  {
    "surah_number": 43,
    "verse_number": 75,
    "content": "کبھی اُن کے عذاب میں کمی نہ ہو گی، اور وہ اس میں مایوس پڑے ہوں گے",
  },
  {
    "surah_number": 43,
    "verse_number": 76,
    "content": "ان پر ہم نے ظلم نہیں کیا بلکہ وہ خود ہی اپنے اوپر ظلم کرتے رہے",
  },
  {
    "surah_number": 43,
    "verse_number": 77,
    "content": "وہ پکاریں گے، 'اے مالک، تیرا رب ہمارا کام ہی تمام کر دے تو اچھا ہے' وہ جواب دے گا، 'تم یوں ہی پڑے رہو گے",
  },
  {
    "surah_number": 43,
    "verse_number": 78,
    "content": "ہم تمہارے پاس حق لے کر آئے تھے مگر تم میں سے اکثر کو حق ہی ناگوار تھا",
  },
  {
    "surah_number": 43,
    "verse_number": 79,
    "content": "کیا اِن لوگوں نے کوئی اقدام کرنے کا فیصلہ کر لیا ہے؟ اچھا تو ہم بھی پھر ایک فیصلہ کیے لیتے ہیں",
  },
  {
    "surah_number": 43,
    "verse_number": 80,
    "content": "کیا اِنہوں نے یہ سمجھ رکھا ہے کہ ہم اِن کی راز کی باتیں اور اِن کی سرگوشیاں سنتے نہیں ہیں؟ ہم سب کچھ سن رہے ہیں اور ہمارے فرشتے اِن کے پاس ہی لکھ رہے ہیں",
  },
  {
    "surah_number": 43,
    "verse_number": 81,
    "content": "اِن سے کہو، 'اگر واقعی رحمان کی کوئی اولاد ہوتی تو سب سے پہلے عبادت کرنے والا میں ہوتا",
  },
  {
    "surah_number": 43,
    "verse_number": 82,
    "content": "پاک ہے آسمانوں اور زمین کا فرماں روا عرش کا مالک، اُن ساری باتوں سے جو یہ لوگ اُس کی طرف منسوب کرتے ہیں",
  },
  {
    "surah_number": 43,
    "verse_number": 83,
    "content": "اچھا، اِنہیں اپنے باطل خیالات میں غرق اور اپنے کھیل میں منہمک رہنے دو، یہاں تک کہ یہ اپنا وہ دن دیکھ لیں جس کا اِنہیں خوف دلایا جا رہا ہے",
  },
  {
    "surah_number": 43,
    "verse_number": 84,
    "content": "وہی ایک آسمان میں بھی خدا ہے اور زمین میں بھی خدا، اور وہی حکیم و علیم ہے",
  },
  {
    "surah_number": 43,
    "verse_number": 85,
    "content": "بہت بالا و برتر ہے وہ جس کے قبضے میں زمین اور آسمانوں اور ہر اُس چیز کی بادشاہی ہے جو زمین و آسمان کے درمیان پائی جاتی ہے اور وہی قیامت کی گھڑی کا علم رکھتا ہے، اور اسی کی طرف تم سب پلٹائے جانے والے ہو",
  },
  {
    "surah_number": 43,
    "verse_number": 86,
    "content": "اُس کو چھوڑ کر یہ لوگ جنہیں پکارتے ہیں وہ کسی شفاعت کا اختیار نہیں رکھتے، الا یہ کہ کوئی علم کی بنا پر حق کی شہادت دے",
  },
  {
    "surah_number": 43,
    "verse_number": 87,
    "content": "اور اگر تم اِن سے پوچھو کہ اِنہیں کس نے پیدا کیا ہے تو یہ خود کہیں گے کہ اللہ نے پھر کہاں سے یہ دھوکا کھا رہے ہیں",
  },
  {
    "surah_number": 43,
    "verse_number": 88,
    "content": "قسم ہے رسولؐ کے اِس قول کی کہ اے رب، یہ وہ لوگ ہیں جو مان کر نہیں دیتے",
  },
  {
    "surah_number": 43,
    "verse_number": 89,
    "content": "اچھا، اے نبیؐ، اِن سے درگزر کرو اور کہہ دو کہ سلام ہے تمہیں، عنقریب اِنہیں معلوم ہو جائے گا",
  },
  {
    "surah_number": 44,
    "verse_number": 1,
    "content": "ح م",
  },
  {
    "surah_number": 44,
    "verse_number": 2,
    "content": "قسم ہے اِس کتاب مبین کی",
  },
  {
    "surah_number": 44,
    "verse_number": 3,
    "content": "کہ ہم نے اِسے ایک بڑی خیر و برکت والی رات میں نازل کیا ہے، کیونکہ ہم لوگوں کو متنبہ کرنے کا ارادہ رکھتے تھے",
  },
  {
    "surah_number": 44,
    "verse_number": 4,
    "content": "یہ وہ رات تھی جس میں ہر معاملہ کا حکیمانہ فیصلہ",
  },
  {
    "surah_number": 44,
    "verse_number": 5,
    "content": "ہمارے حکم سے صادر کیا جاتا ہے ہم ایک رسول بھیجنے والے تھے",
  },
  {
    "surah_number": 44,
    "verse_number": 6,
    "content": "تیرے رب کی رحمت کے طور پر یقیناً وہی سب کچھ سننے اور جاننے والا ہے",
  },
  {
    "surah_number": 44,
    "verse_number": 7,
    "content": "آسمانوں اور زمین کا رب اور ہر اُس چیز کا رب جو آسمان و زمین کے درمیان ہے اگر تم لوگ واقعی یقین رکھنے والے ہو",
  },
  {
    "surah_number": 44,
    "verse_number": 8,
    "content": "کوئی معبود اُس کے سوا نہیں ہے وہی زندگی عطا کرتا ہے اور وہی موت دیتا ہے تمہارا رب اور تمہارے اُن اسلاف کا رب جو پہلے گزر چکے ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 9,
    "content": "(مگر فی الواقع اِن لوگوں کو یقین نہیں ہے) بلکہ یہ اپنے شک میں پڑے کھیل رہے ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 10,
    "content": "اچھا انتظار کرو اُس دن کا جب آسمان صریح دھواں لیے ہوئے آئے گا",
  },
  {
    "surah_number": 44,
    "verse_number": 11,
    "content": "اور وہ لوگوں پر چھا جائے گا، یہ ہے درد ناک سزا",
  },
  {
    "surah_number": 44,
    "verse_number": 12,
    "content": "(اب کہتے ہیں کہ) ' پروردگار، ہم پر سے یہ عذاب ٹال دے، ہم ایمان لاتے ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 13,
    "content": "اِن کی غفلت کہاں دور ہوتی ہے؟ اِن کا حال تو یہ ہے کہ اِن کے پاس رسول مبین آ گیا",
  },
  {
    "surah_number": 44,
    "verse_number": 14,
    "content": "پھر بھی یہ اُس کی طرف ملتفت نہ ہوئے اور کہا کہ 'یہ تو سکھایا پڑھایا باولا ہے",
  },
  {
    "surah_number": 44,
    "verse_number": 15,
    "content": "ہم ذرا عذاب ہٹائے دیتے ہیں، تم لوگ پھر وہی کچھ کرو گے جو پہلے کر رہے تھے",
  },
  {
    "surah_number": 44,
    "verse_number": 16,
    "content": "جس روز ہم بڑی ضرب لگائیں گے وہ دن ہوگا جب ہم تم سے انتقام لیں گے",
  },
  {
    "surah_number": 44,
    "verse_number": 17,
    "content": "ہم اِن سے پہلے فرعون کی قوم کو اِسی آزمائش میں ڈال چکے ہیں اُن کے پاس ایک نہایت شریف رسول آیا",
  },
  {
    "surah_number": 44,
    "verse_number": 18,
    "content": "اور اس نے کہا 'اللہ کے بندوں کو میرے حوالے کرو، میں تمہارے لیے ایک امانت دار رسول ہوں",
  },
  {
    "surah_number": 44,
    "verse_number": 19,
    "content": "اللہ کے مقابلے میں سرکشی نہ کرو میں تمہارے سامنے (اپنی ماموریت کی) صریح سند پیش کرتا ہوں",
  },
  {
    "surah_number": 44,
    "verse_number": 20,
    "content": "اور میں اپنے رب اور تمہارے رب کی پناہ لے چکا ہوں اِس سے کہ تم مجھ پر حملہ آور ہو",
  },
  {
    "surah_number": 44,
    "verse_number": 21,
    "content": "اگر تم میری بات نہیں مانتے تو مجھ پر ہاتھ ڈالنے سے باز رہو",
  },
  {
    "surah_number": 44,
    "verse_number": 22,
    "content": "آخرکار اُس نے اپنے رب کو پکارا کہ یہ لوگ مجرم ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 23,
    "content": "(جواب دیا گیا) اچھا تو راتوں رات میرے بندوں کو لے کر چل پڑ تم لوگوں کا پیچھا کیا جائے گا",
  },
  {
    "surah_number": 44,
    "verse_number": 24,
    "content": "سمندر کو اُس کے حال پر کھلا چھوڑ دے یہ سارا لشکر غرق ہونے والا ہے",
  },
  {
    "surah_number": 44,
    "verse_number": 25,
    "content": "کتنے ہی باغ اور چشمے",
  },
  {
    "surah_number": 44,
    "verse_number": 26,
    "content": "اور کھیت اور شاندار محل تھے جو وہ چھوڑ گئے",
  },
  {
    "surah_number": 44,
    "verse_number": 27,
    "content": "کتنے ہی عیش کے سر و سامان، جن میں وہ مزے کر رہے تھے اُن کے پیچھے دھرے رہ گئے",
  },
  {
    "surah_number": 44,
    "verse_number": 28,
    "content": "یہ ہوا اُن کا انجام، اور ہم نے دوسروں کو اِن چیزوں کا وارث بنا دیا",
  },
  {
    "surah_number": 44,
    "verse_number": 29,
    "content": "پھر نہ آسمان اُن پر رویا نہ زمین، اور ذرا سی مہلت بھی ان کو نہ دی گئی",
  },
  {
    "surah_number": 44,
    "verse_number": 30,
    "content": "اِس طرح بنی اسرائیل کو ہم نے سخت ذلت کے عذاب",
  },
  {
    "surah_number": 44,
    "verse_number": 31,
    "content": "فرعون سے نجات دی جو حد سے گزر جانے والوں میں فی الواقع بڑے اونچے درجے کا آدمی تھا",
  },
  {
    "surah_number": 44,
    "verse_number": 32,
    "content": "اور اُن کی حالت جانتے ہوئے، اُن کو دنیا کی دوسری قوموں پر ترجیح دی",
  },
  {
    "surah_number": 44,
    "verse_number": 33,
    "content": "اور اُنہیں ایسی نشانیاں دکھائیں جن میں صریح آزمائش تھی",
  },
  {
    "surah_number": 44,
    "verse_number": 34,
    "content": "یہ لوگ کہتے ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 35,
    "content": "ہماری پہلی موت کے سوا اور کچھ نہیں اُس کے بعد ہم دوبارہ اٹھائے جانے والے نہیں ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 36,
    "content": "اگر تم سچے ہو تو اٹھا لاؤ ہمارے باپ دادا کو",
  },
  {
    "surah_number": 44,
    "verse_number": 37,
    "content": "یہ بہتر ہیں یا تبع کی قوم اور اُس سے پہلے کے لوگ؟ ہم نے ان کو اِسی بنا پر تباہ کیا کہ وہ مجرم ہوگئے تھے",
  },
  {
    "surah_number": 44,
    "verse_number": 38,
    "content": "یہ آسمان و زمین اور اِن کے درمیان کی چیزیں ہم نے کچھ کھیل کے طور پر نہیں بنا دی ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 39,
    "content": "اِن کو ہم نے برحق پیدا کیا ہے، مگر اکثر لوگ جانتے نہیں ہیں",
  },
  {
    "surah_number": 44,
    "verse_number": 40,
    "content": "اِن سب کے اٹھائے جانے کے لیے طے شدہ وقت فیصلے کا دن ہے",
  },
  {
    "surah_number": 44,
    "verse_number": 41,
    "content": "وہ دن جب کوئی عزیز قریب اپنے کسی عزیز قریب کے کچھ بھی کام نہ آئے گا، اور نہ کہیں سے انہیں کوئی مدد پہنچے گی",
  },
  {
    "surah_number": 44,
    "verse_number": 42,
    "content": "سوائے اِس کے کہ اللہ ہی کسی پر رحم کرے، وہ زبردست اور رحیم ہے",
  },
  {
    "surah_number": 44,
    "verse_number": 43,
    "content": "زقوم کا درخت",
  },
  {
    "surah_number": 44,
    "verse_number": 44,
    "content": "گناہ گار کا کھاجا ہوگا",
  },
  {
    "surah_number": 44,
    "verse_number": 45,
    "content": "تیل کی تلچھٹ جیسا، پیٹ میں اِس طرح جوش کھائے گا",
  },
  {
    "surah_number": 44,
    "verse_number": 46,
    "content": "جیسے کھولتا ہوا پانی جوش کھاتا ہے",
  },
  {
    "surah_number": 44,
    "verse_number": 47,
    "content": "پکڑو اِسے اور رگیدتے ہوئے لے جاؤ اِس کو جہنم کے بیچوں بیچ",
  },
  {
    "surah_number": 44,
    "verse_number": 48,
    "content": "اور انڈیل دو اِس کے سر پر کھولتے پانی کا عذاب",
  },
  {
    "surah_number": 44,
    "verse_number": 49,
    "content": "چکھ اس کا مزا، بڑا زبردست عزت دار آدمی ہے تُو",
  },
  {
    "surah_number": 44,
    "verse_number": 50,
    "content": "یہ وہی چیز ہے جس کے آنے میں تم لوگ شک رکھتے تھے",
  },
  {
    "surah_number": 44,
    "verse_number": 51,
    "content": "خدا ترس لوگ امن کی جگہ میں ہوں گے",
  },
  {
    "surah_number": 44,
    "verse_number": 52,
    "content": "باغوں اور چشموں میں",
  },
  {
    "surah_number": 44,
    "verse_number": 53,
    "content": "حریر و دیبا کے لباس پہنے، آمنے سامنے بیٹھے ہوں گے",
  },
  {
    "surah_number": 44,
    "verse_number": 54,
    "content": "یہ ہوگی ان کی شان اور ہم گوری گوری آہو چشم عورتیں ان سے بیاہ دیں گے",
  },
  {
    "surah_number": 44,
    "verse_number": 55,
    "content": "وہاں وہ اطمینان سے ہر طرح کی لذیذ چیزیں طلب کریں گے",
  },
  {
    "surah_number": 44,
    "verse_number": 56,
