    "surah_number": 68,
    "verse_number": 11,
    "content": "طعنے دیتا ہے، چغلیاں کھاتا پھرتا ہے",
  },
  {
    "surah_number": 68,
    "verse_number": 12,
    "content": "بھلائی سے روکتا ہے، ظلم و زیادتی میں حد سے گزر جانے والا ہے، سخت بد اعمال ہے، جفا کار ہے",
  },
  {
    "surah_number": 68,
    "verse_number": 13,
    "content": "اور اَن سب عیوب کے ساتھ بد اصل ہے",
  },
  {
    "surah_number": 68,
    "verse_number": 14,
    "content": "اِس بنا پر کہ وہ بہت مال و اولاد رکھتا ہے",
  },
  {
    "surah_number": 68,
    "verse_number": 15,
    "content": "جب ہماری آیات اُس کو سنائی جاتی ہیں تو کہتا ہے یہ تو اگلے وقتوں کے افسانے ہیں",
  },
  {
    "surah_number": 68,
    "verse_number": 16,
    "content": "عنقریب ہم اس کی سونڈ پر داغ لگائیں گے",
  },
  {
    "surah_number": 68,
    "verse_number": 17,
    "content": "ہم نے اِن (اہل مکہ) کو اُسی طرح آزمائش میں ڈالا ہے جس طرح ایک باغ کے مالکوں کو آزمائش میں ڈالا تھا، جب اُنہوں نے قسم کھائی کہ صبح سویرے ضرور اپنے باغ کے پھل توڑیں گے",
  },
  {
    "surah_number": 68,
    "verse_number": 18,
    "content": "اور وہ کوئی استثناء نہیں کر رہے تھے",
  },
  {
    "surah_number": 68,
    "verse_number": 19,
    "content": "رات کو وہ سوئے پڑے تھے کہ تمہارے رب کی طرف سے ایک بلا اس باغ پر پھر گئی",
  },
  {
    "surah_number": 68,
    "verse_number": 20,
    "content": "اور اُس کا حال ایسا ہو گیا جیسے کٹی ہوئی فصل ہو",
  },
  {
    "surah_number": 68,
    "verse_number": 21,
    "content": "صبح اُن لوگوں نے ایک دوسرے کو پکارا",
  },
  {
    "surah_number": 68,
    "verse_number": 22,
    "content": "کہ اگر پھل توڑنے ہیں تو سویرے سویرے اپنی کھیتی کی طرف نکل چلو",
  },
  {
    "surah_number": 68,
    "verse_number": 23,
    "content": "چنانچہ وہ چل پڑے اور آپس میں چپکے چپکے کہتے جاتے تھے",
  },
  {
    "surah_number": 68,
    "verse_number": 24,
    "content": "کہ آج کوئی مسکین تمہارے پاس باغ میں نہ آنے پائے",
  },
  {
    "surah_number": 68,
    "verse_number": 25,
    "content": "وہ کچھ نہ دینے کا فیصلہ کیے ہوئے صبح سویرے جلدی جلدی اِس طرح وہاں گئے جیسے کہ وہ (پھل توڑنے پر) قادر ہیں",
  },
  {
    "surah_number": 68,
    "verse_number": 26,
    "content": "مگر جب باغ کو دیکھا تو کہنے لگے 'ہم راستہ بھول گئے ہیں",
  },
  {
    "surah_number": 68,
    "verse_number": 27,
    "content": "نہیں، بلکہ ہم محروم رہ گئے",
  },
  {
    "surah_number": 68,
    "verse_number": 28,
    "content": "اُن میں جو سب سے بہتر آدمی تھا اُس نے کہا 'میں نے تم سے کہا نہ تھا کہ تم تسبیح کیوں نہیں کرتے؟",
  },
  {
    "surah_number": 68,
    "verse_number": 29,
    "content": "و ہ پکار اٹھے پاک ہے ہمارا رب، واقعی ہم گناہ گار تھے",
  },
  {
    "surah_number": 68,
    "verse_number": 30,
    "content": "پھر اُن میں سے ہر ایک دوسرے کو ملامت کرنے لگا",
  },
  {
    "surah_number": 68,
    "verse_number": 31,
    "content": "آخر کو انہوں نے کہا 'افسوس ہمارے حال پر، بے شک ہم سرکش ہو گئے تھے",
  },
  {
    "surah_number": 68,
    "verse_number": 32,
    "content": "بعید نہیں کہ ہمارا رب ہمیں بدلے میں اِس سے بہتر باغ عطا فرمائے، ہم اپنے رب کی طرف رجوع کرتے ہیں",
  },
  {
    "surah_number": 68,
    "verse_number": 33,
    "content": "ایسا ہوتا ہے عذاب اور آخرت کا عذاب اِس سے بھی بڑا ہے، کاش یہ لوگ اِس کو جانتے",
  },
  {
    "surah_number": 68,
    "verse_number": 34,
    "content": "یقیناً خدا ترس لوگوں کے لیے اُن کے رب کے ہاں نعمت بھری جنتیں ہیں",
  },
  {
    "surah_number": 68,
    "verse_number": 35,
    "content": "کیا ہم فرماں برداروں کا حال مجرموں کا سا کر دیں؟",
  },
  {
    "surah_number": 68,
    "verse_number": 36,
    "content": "تم لوگوں کو کیا ہو گیا ہے، تم کیسے حکم لگاتے ہو؟",
  },
  {
    "surah_number": 68,
    "verse_number": 37,
    "content": "کیا تمہارے پاس کوئی کتاب ہے جس میں تم یہ پڑھتے ہو",
  },
  {
    "surah_number": 68,
    "verse_number": 38,
    "content": "کہ تمہارے لیے ضرور وہاں وہی کچھ ہے جو تم اپنے لیے پسند کرتے ہو؟",
  },
  {
    "surah_number": 68,
    "verse_number": 39,
    "content": "یا پھر کیا تمہارے لیے روز قیامت تک ہم پر کچھ عہد و پیمان ثابت ہیں کہ تمہیں وہی کچھ ملے گا جس کا تم حکم لگاؤ؟",
  },
  {
    "surah_number": 68,
    "verse_number": 40,
    "content": "اِن سے پوچھو تم میں سے کون اِس کا ضامن ہے؟",
  },
  {
    "surah_number": 68,
    "verse_number": 41,
    "content": "یا پھر اِن کے ٹھیرائے ہوئے کچھ شریک ہیں (جنہوں نے اِس کا ذمہ لیا ہو)؟ یہ بات ہے تو لائیں اپنے شریکوں کو اگر یہ سچے ہیں",
  },
  {
    "surah_number": 68,
    "verse_number": 42,
    "content": "جس روز سخت وقت آ پڑے گا اور لوگوں کو سجدہ کرنے کے لیے بلایا جائے گا تو یہ لوگ سجدہ نہ کر سکیں گے",
  },
  {
    "surah_number": 68,
    "verse_number": 43,
    "content": "اِن کی نگاہیں نیچی ہوں گی، ذلت اِن پر چھا رہی ہوگی یہ جب صحیح و سالم تھے اُس وقت اِنہیں سجدے کے لیے بلایا جاتا تھا (اور یہ انکار کرتے تھے)",
  },
  {
    "surah_number": 68,
    "verse_number": 44,
    "content": "پس اے نبیؐ، تم اِس کلام کے جھٹلانے والوں کا معاملہ مجھ پر چھوڑ دو ہم ایسے طریقہ سے اِن کو بتدریج تباہی کی طرف لے جائیں گے کہ اِن کو خبر بھی نہ ہوگی",
  },
  {
    "surah_number": 68,
    "verse_number": 45,
    "content": "میں اِن کی رسی دراز کر رہا ہوں، میری چال بڑی زبردست ہے",
  },
  {
    "surah_number": 68,
    "verse_number": 46,
    "content": "کیا تم اِن سے کوئی اجر طلب کر رہے ہو کہ یہ اس چٹی کے بوجھ تلے دبے جا رہے ہوں؟",
  },
  {
    "surah_number": 68,
    "verse_number": 47,
    "content": "کیا اِن کے پاس غیب کا علم ہے جسے یہ لکھ رہے ہوں؟",
  },
  {
    "surah_number": 68,
    "verse_number": 48,
    "content": "پس اپنے رب کا فیصلہ صادر ہونے تک صبر کرو اور مچھلی والے (یونس علیہ اسلام) کی طرح نہ ہو جاؤ، جب اُس نے پکارا تھا اور وہ غم سے بھرا ہوا تھا",
  },
  {
    "surah_number": 68,
    "verse_number": 49,
    "content": "اگر اس کے رب کی مہربانی اُس کے شامل حال نہ ہو جاتی تو وہ مذموم ہو کر چٹیل میدان میں پھینک دیا جاتا",
  },
  {
    "surah_number": 68,
    "verse_number": 50,
    "content": "آخرکار اُس کے رب نے اسے برگزیدہ فرما لیا اور اِسے صالح بندوں میں شامل کر دیا",
  },
  {
    "surah_number": 68,
    "verse_number": 51,
    "content": "جب یہ کافر لوگ کلام نصیحت (قرآن) سنتے ہیں تو تمہیں ایسی نظروں سے دیکھتے ہیں کہ گویا تمہارے قدم اکھاڑ دیں گے، اور کہتے ہیں یہ ضرور دیوانہ ہے",
  },
  {
    "surah_number": 68,
    "verse_number": 52,
    "content": "حالانکہ یہ تو سارے جہان والوں کے لیے ایک نصیحت ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 1,
    "content": "ہونی شدنی",
  },
  {
    "surah_number": 69,
    "verse_number": 2,
    "content": "کیا ہے وہ ہونی شدنی؟",
  },
  {
    "surah_number": 69,
    "verse_number": 3,
    "content": "اور تم کیا جانو کہ وہ کیا ہے ہونی شدنی؟",
  },
  {
    "surah_number": 69,
    "verse_number": 4,
    "content": "ثمود اور عاد نے اُس اچانک ٹوٹ پڑنے والی آفت کو جھٹلایا",
  },
  {
    "surah_number": 69,
    "verse_number": 5,
    "content": "تو ثمود ایک سخت حادثہ میں ہلاک کیے گئے",
  },
  {
    "surah_number": 69,
    "verse_number": 6,
    "content": "اور عاد ایک بڑی شدید طوفانی آندھی سے تباہ کر دیے گئے",
  },
  {
    "surah_number": 69,
    "verse_number": 7,
    "content": "اللہ تعالیٰ نے اُس کو مسلسل سات رات اور آٹھ دن اُن پر مسلط رکھا (تم وہاں ہوتے تو) دیکھتے کہ وہ وہاں اِس طرح پچھڑے پڑے ہیں جیسے وہ کھجور کے بوسیدہ تنے ہوں",
  },
  {
    "surah_number": 69,
    "verse_number": 8,
    "content": "اب کیا اُن میں سے کوئی تمہیں باقی بچا نظر آتا ہے؟",
  },
  {
    "surah_number": 69,
    "verse_number": 9,
    "content": "اور اِسی خطائے عظیم کا ارتکاب فرعون اور اُس سے پہلے کے لوگوں نے اور تل پٹ ہو جانے والی بستیوں نے کیا",
  },
  {
    "surah_number": 69,
    "verse_number": 10,
    "content": "ان سب نے اپنے رب کے رسول کی بات نہ مانی تو اُس نے اُن کو بڑی سختی کے ساتھ پکڑا",
  },
  {
    "surah_number": 69,
    "verse_number": 11,
    "content": "جب پانی کا طوفان حد سے گزر گیا تو ہم نے تم کو کشتی میں سوار کر دیا تھا",
  },
  {
    "surah_number": 69,
    "verse_number": 12,
    "content": "تاکہ اِس واقعہ کو تمہارے لیے ایک سبق آموز یادگار بنا دیں اور یاد رکھنے والے کان اس کی یاد محفوظ رکھیں",
  },
  {
    "surah_number": 69,
    "verse_number": 13,
    "content": "پھر جب ایک دفعہ صور میں پھونک مار دی جائے گی",
  },
  {
    "surah_number": 69,
    "verse_number": 14,
    "content": "اور زمین اور پہاڑوں کو اٹھا کر ایک ہی چوٹ میں ریزہ ریزہ کر دیا جائے گا",
  },
  {
    "surah_number": 69,
    "verse_number": 15,
    "content": "اُس روز وہ ہونے والا واقعہ پیش آ جائے گا",
  },
  {
    "surah_number": 69,
    "verse_number": 16,
    "content": "اُس دن آسمان پھٹے گا اور اس کی بندش ڈھیلی پڑ جائے گی",
  },
  {
    "surah_number": 69,
    "verse_number": 17,
    "content": "فرشتے اس کے اطراف و جوانب میں ہوں گے اور آٹھ فرشتے اُس روز تیرے رب کا عرش اپنے اوپر اٹھائے ہوئے ہوں گے",
  },
  {
    "surah_number": 69,
    "verse_number": 18,
    "content": "وہ دن ہوگا جب تم لوگ پیش کیے جاؤ گے، تمہارا کوئی راز بھی چھپا نہ رہ جائے گا",
  },
  {
    "surah_number": 69,
    "verse_number": 19,
    "content": "اُس وقت جس کا نامہ اعمال اُس کے سیدھے ہاتھ میں دیا جائے گا وہ کہے گا 'لو دیکھو، پڑھو میرا نامہ اعمال",
  },
  {
    "surah_number": 69,
    "verse_number": 20,
    "content": "میں سمجھتا تھا کہ مجھے ضرور اپنا حساب ملنے والا ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 21,
    "content": "پس وہ دل پسند عیش میں ہوگا",
  },
  {
    "surah_number": 69,
    "verse_number": 22,
    "content": "عالی مقام جنت میں",
  },
  {
    "surah_number": 69,
    "verse_number": 23,
    "content": "جس کے پھلوں کے گچھے جھکے پڑ رہے ہوں گے",
  },
  {
    "surah_number": 69,
    "verse_number": 24,
    "content": "(ایسے لوگوں سے کہا جائے گا) مزے سے کھاؤ اور پیو اپنے اُن اعمال کے بدلے جو تم نے گزرے ہوئے دنوں میں کیے ہیں",
  },
  {
    "surah_number": 69,
    "verse_number": 25,
    "content": "اور جس کا نامہ اعمال اُس کے بائیں ہاتھ میں دیا جائے گا وہ کہے گا 'کاش میرا اعمال نامہ مجھے نہ دیا گیا ہوتا",
  },
  {
    "surah_number": 69,
    "verse_number": 26,
    "content": "اور میں نہ جانتا کہ میرا حساب کیا ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 27,
    "content": "کاش میری وہی موت (جو دنیا میں آئی تھی) فیصلہ کن ہوتی",
  },
  {
    "surah_number": 69,
    "verse_number": 28,
    "content": "آج میرا مال میرے کچھ کام نہ آیا",
  },
  {
    "surah_number": 69,
    "verse_number": 29,
    "content": "میرا سارا اقتدار ختم ہو گیا",
  },
  {
    "surah_number": 69,
    "verse_number": 30,
    "content": "(حکم ہو گا) پکڑو اِسے اور اِس کی گردن میں طوق ڈال دو",
  },
  {
    "surah_number": 69,
    "verse_number": 31,
    "content": "پھر اِسے جہنم میں جھونک دو",
  },
  {
    "surah_number": 69,
    "verse_number": 32,
    "content": "پھر اِس کو ستر ہاتھ لمبی زنجیر میں جکڑ دو",
  },
  {
    "surah_number": 69,
    "verse_number": 33,
    "content": "یہ نہ اللہ بزرگ و برتر پر ایمان لاتا تھا",
  },
  {
    "surah_number": 69,
    "verse_number": 34,
    "content": "اور نہ مسکین کو کھانا کھلانے کی ترغیب دیتا تھا",
  },
  {
    "surah_number": 69,
    "verse_number": 35,
    "content": "لہٰذا آج نہ یہاں اِس کا کوئی یار غم خوار ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 36,
    "content": "اور نہ زخموں کے دھوون کے سوا اِس کے لیے کوئی کھانا",
  },
  {
    "surah_number": 69,
    "verse_number": 37,
    "content": "جسے خطا کاروں کے سوا کوئی نہیں کھاتا",
  },
  {
    "surah_number": 69,
    "verse_number": 38,
    "content": "پس نہیں، میں قسم کھاتا ہوں اُن چیزوں کی بھی جو تم دیکھتے ہو",
  },
  {
    "surah_number": 69,
    "verse_number": 39,
    "content": "اور اُن کی بھی جنہیں تم نہیں دیکھتے",
  },
  {
    "surah_number": 69,
    "verse_number": 40,
    "content": "یہ ایک رسول کریم کا قول ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 41,
    "content": "کسی شاعر کا قول نہیں ہے، تم لوگ کم ہی ایمان لاتے ہو",
  },
  {
    "surah_number": 69,
    "verse_number": 42,
    "content": "اور نہ یہ کسی کاہن کا قول ہے، تم لوگ کم ہی غور کرتے ہو",
  },
  {
    "surah_number": 69,
    "verse_number": 43,
    "content": "یہ رب العالمین کی طرف سے نازل ہوا ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 44,
    "content": "اور اگر اس (نبی) نے خود گھڑ کر کوئی بات ہماری طرف منسوب کی ہوتی",
  },
  {
    "surah_number": 69,
    "verse_number": 45,
    "content": "تو ہم اِس کا دایاں ہاتھ پکڑ لیتے",
  },
  {
    "surah_number": 69,
    "verse_number": 46,
    "content": "اور اِس کی رگ گردن کاٹ ڈالتے",
  },
  {
    "surah_number": 69,
    "verse_number": 47,
    "content": "پھر تم میں سے کوئی (ہمیں) اس کام سے روکنے والا نہ ہوتا",
  },
  {
    "surah_number": 69,
    "verse_number": 48,
    "content": "درحقیقت یہ پرہیزگار لوگوں کے لیے ایک نصیحت ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 49,
    "content": "اور ہم جانتے ہیں کہ تم میں سے کچھ لوگ جھٹلانے والے ہیں",
  },
  {
    "surah_number": 69,
    "verse_number": 50,
    "content": "ایسے کافروں کے لیے یقیناً یہ موجب حسرت ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 51,
    "content": "اور یہ بالکل یقینی حق ہے",
  },
  {
    "surah_number": 69,
    "verse_number": 52,
    "content": "پس اے نبیؐ، اپنے رب عظیم کے نام کی تسبیح کرو",
  },
  {
    "surah_number": 70,
    "verse_number": 1,
    "content": "مانگنے والے نے عذاب مانگا ہے، (وہ عذاب) جو ضرور واقع ہونے والا ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 2,
    "content": "کافروں کے لیے ہے، کوئی اُسے دفع کرنے والا نہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 3,
    "content": "اُس خدا کی طرف سے ہے جو عروج کے زینوں کا مالک ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 4,
    "content": "ملائکہ اور روح اُس کے حضور چڑھ کر جاتے ہیں ایک ایسے دن میں جس کی مقدار پچاس ہزار سال ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 5,
    "content": "پس اے نبیؐ، صبر کرو، شائستہ صبر",
  },
  {
    "surah_number": 70,
    "verse_number": 6,
    "content": "یہ لوگ اُسے دور سمجھتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 7,
    "content": "اور ہم اسے قریب دیکھ رہے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 8,
    "content": "(وہ عذاب اُس روز ہوگا) جس روز آسمان پگھلی ہوئی چاندی کی طرح ہو جائے گا",
  },
  {
    "surah_number": 70,
    "verse_number": 9,
    "content": "اور پہاڑ رنگ برنگ کے دھنکے ہوئے اون جیسے ہو جائیں گے",
  },
  {
    "surah_number": 70,
    "verse_number": 10,
    "content": "اور کوئی جگری دوست اپنے جگری دوست کو نہ پوچھے گا",
  },
  {
    "surah_number": 70,
    "verse_number": 11,
    "content": "حالانکہ وہ ایک دوسرے کو دکھائے جائیں گے مجرم چاہے گا کہ اس دن کے عذاب سے بچنے کے لیے اپنی اولاد کو",
  },
  {
    "surah_number": 70,
    "verse_number": 12,
    "content": "اپنی بیوی کو، اپنے بھائی کو",
  },
  {
    "surah_number": 70,
    "verse_number": 13,
    "content": "اپنے قریب ترین خاندان کو جو اسے پناہ دینے والا تھا",
  },
  {
    "surah_number": 70,
    "verse_number": 14,
    "content": "اور روئے زمین کے سب لوگوں کو فدیہ میں دیدے اور یہ تدبیر اُسے نجات دلا دے",
  },
  {
    "surah_number": 70,
    "verse_number": 15,
    "content": "ہرگز نہیں وہ تو بھڑکتی ہوئی آگ کی لپٹ ہوگی",
  },
  {
    "surah_number": 70,
    "verse_number": 16,
    "content": "جو گوشت پوست کو چاٹ جائے گی",
  },
  {
    "surah_number": 70,
    "verse_number": 17,
    "content": "پکار پکار کر اپنی طرف بلائے گی ہر اُس شخص کو جس نے حق سے منہ موڑا اور پیٹھ پھیری",
  },
  {
    "surah_number": 70,
    "verse_number": 18,
    "content": "اور مال جمع کیا اور سینت سینت کر رکھا",
  },
  {
    "surah_number": 70,
    "verse_number": 19,
    "content": "انسان تھڑدلا پیدا کیا گیا ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 20,
    "content": "جب اس پر مصیبت آتی ہے تو گھبرا اٹھتا ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 21,
    "content": "اور جب اسے خوشحالی نصیب ہوتی ہے تو بخل کرنے لگتا ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 22,
    "content": "مگر وہ لوگ (اِس عیب سے بچے ہوئے ہیں) جو نماز پڑھنے والے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 23,
    "content": "جو اپنی نماز کی ہمیشہ پابندی کرتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 24,
    "content": "جن کے مالوں میں،",
  },
  {
    "surah_number": 70,
    "verse_number": 25,
    "content": "سائل اور محروم کا ایک حق مقرر ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 26,
    "content": "جو روز جزا کو برحق مانتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 27,
    "content": "جو اپنے رب کے عذاب سے ڈرتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 28,
    "content": "کیونکہ اُن کے رب کا عذاب ایسی چیز نہیں ہے جس سے کوئی بے خوف ہو",
  },
  {
    "surah_number": 70,
    "verse_number": 29,
    "content": "جو اپنی شرمگاہوں کی حفاظت کرتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 30,
    "content": "بجز اپنی بیویوں یا اپنی مملوکہ عورتوں کے جن سے محفوظ نہ رکھنے میں ان پر کوئی ملامت نہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 31,
    "content": "البتہ جو اس کے علاوہ کچھ اور چاہیں وہی حد سے تجاوز کرنے والے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 32,
    "content": "جو اپنی امانتوں کی حفاظت اور اپنے عہد کا پاس کرتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 33,
    "content": "جو اپنی گواہیوں میں راست بازی پر قائم رہتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 34,
    "content": "اور جو اپنی نماز کی حفاظت کرتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 35,
    "content": "یہ لوگ عزت کے ساتھ جنت کے باغوں میں رہیں گے",
  },
  {
    "surah_number": 70,
    "verse_number": 36,
    "content": "پس اے نبیؐ، کیا بات ہے کہ یہ منکرین دائیں اور بائیں سے،",
  },
  {
    "surah_number": 70,
    "verse_number": 37,
    "content": "گروہ در گروہ تمہاری طرف دوڑے چلے آ رہے ہیں؟",
  },
  {
    "surah_number": 70,
    "verse_number": 38,
    "content": "کیا اِن میں سے ہر ایک یہ لالچ رکھتا ہے کہ وہ نعمت بھری جنت میں داخل کر دیا جائے گا؟",
  },
  {
    "surah_number": 70,
    "verse_number": 39,
    "content": "ہرگز نہیں ہم نے جس چیز سے اِن کو پیدا کیا ہے اُسے یہ خود جانتے ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 40,
    "content": "پس نہیں، میں قسم کھاتا ہوں مشرقوں اور مغربوں کے مالک کی، ہم اِس پر قادر ہیں",
  },
  {
    "surah_number": 70,
    "verse_number": 41,
    "content": "کہ اِن کی جگہ اِن سے بہتر لوگ لے آئیں اور کوئی ہم سے بازی لے جانے والا نہیں ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 42,
    "content": "لہٰذا اِنہیں اپنی بیہودہ باتوں اور اپنے کھیل میں پڑا رہنے دو یہاں تک کہ یہ اپنے اُس دن تک پہنچ جائیں جس کا ان سے وعدہ کیا جا رہا ہے",
  },
  {
    "surah_number": 70,
    "verse_number": 43,
    "content": "جب یہ اپنی قبروں سے نکل کر اِس طرح دوڑے جا رہے ہوں گے جیسے اپنے بتوں کے استھانوں کی طرف دوڑ رہے ہوں",
  },
  {
    "surah_number": 70,
    "verse_number": 44,
    "content": "اِن کی نگاہیں جھکی ہوئی ہوں گی، ذلت اِن پر چھا رہی ہوگی وہ دن ہے جس کا اِن سے وعدہ کیا جا رہا ہے",
  },
  {
    "surah_number": 71,
    "verse_number": 1,
    "content": "ہم نے نوحؑ کو اس کی قوم کی طرف بھیجا (اس ہدایت کے ساتھ) کہ اپنی قوم کے لوگوں کو خبردار کر دے قبل اس کے کہ ان پر ایک دردناک عذاب آئے",
  },
  {
    "surah_number": 71,
    "verse_number": 2,
    "content": "ا س نے کہا 'اے میری قوم کے لوگو، میں تمہارے لیے ایک صاف صاف خبردار کردینے والا (پیغمبر) ہوں",
  },
  {
    "surah_number": 71,
    "verse_number": 3,
    "content": "(تم کو آگاہ کرتا ہوں) کہ اللہ کی بندگی کرو اور اس سے ڈرو اور میری اطاعت کرو",
  },
  {
    "surah_number": 71,
    "verse_number": 4,
    "content": "اللہ تمہارے گناہوں سے درگزر فرمائے گا اور تمہیں ایک وقت مقرر تک باقی رکھے گا حقیقت یہ ہے کہ اللہ کا مقرر کیا ہوا وقت جب آ جاتا ہے تو پھر ٹالا نہیں جاتا کاش تمہیں اِس کا علم ہو",
  },
  {
    "surah_number": 71,
    "verse_number": 5,
    "content": "اُس نے عرض کیا 'اے میرے رب، میں نے اپنی قوم کے لوگوں کو شب و روز پکارا",
  },
  {
    "surah_number": 71,
    "verse_number": 6,
    "content": "مگر میری پکار نے اُن کے فرار ہی میں اضافہ کیا",
  },
  {
    "surah_number": 71,
    "verse_number": 7,
    "content": "اور جب بھی میں نے اُن کو بلایا تاکہ تو اُنہیں معاف کر دے، انہوں نے کانوں میں انگلیاں ٹھونس لیں اور اپنے کپڑوں سے منہ ڈھانک لیے اور اپنی روش پر اڑ گئے اور بڑا تکبر کیا",
  },
  {
    "surah_number": 71,
    "verse_number": 8,
    "content": "پھر میں نے ان کو ہانکے پکارے دعوت دی",
  },
  {
    "surah_number": 71,
    "verse_number": 9,
    "content": "پھر میں نے علانیہ بھی ان کو تبلیغ کی اور چپکے چپکے بھی سمجھایا",
  },
  {
    "surah_number": 71,
    "verse_number": 10,
    "content": "میں نے کہا اپنے رب سے معافی مانگو، بے شک وہ بڑا معاف کرنے والا ہے",
  },
  {
    "surah_number": 71,
    "verse_number": 11,
    "content": "وہ تم پر آسمان سے خوب بارشیں برسائے گا",
  },
  {
    "surah_number": 71,
    "verse_number": 12,
    "content": "تمہیں مال اور اولاد سے نوازے گا، تمہارے لیے باغ پیدا کرے گا اور تمہارے لیے نہریں جاری کر دے گا",
  },
  {
    "surah_number": 71,
    "verse_number": 13,
    "content": "تمہیں کیا ہو گیا ہے کہ اللہ کے لیے تم کسی وقار کی توقع نہیں رکھتے؟",
  },
  {
    "surah_number": 71,
    "verse_number": 14,
    "content": "حالانکہ اُس نے طرح طرح سے تمہیں بنایا ہے",
  },
  {
    "surah_number": 71,
    "verse_number": 15,
    "content": "کیا دیکھتے نہیں ہو کہ اللہ نے کس طرح سات آسمان تہ بر تہ بنائے",
  },
  {
    "surah_number": 71,
    "verse_number": 16,
    "content": "اور اُن میں چاند کو نور اور سورج کو چراغ بنایا؟",
  },
  {
    "surah_number": 71,
    "verse_number": 17,
    "content": "اور اللہ نے تم کو زمین سے عجیب طرح اگایا",
  },
  {
    "surah_number": 71,
    "verse_number": 18,
    "content": "پھر وہ تمہیں اِسی زمین میں واپس لے جائے گا اور اِس سے یکایک تم کو نکال کھڑا کرے گا",
  },
  {
    "surah_number": 71,
    "verse_number": 19,
    "content": "اور اللہ نے زمین کو تمہارے لیے فرش کی طرح بچھا دیا",
  },
  {
    "surah_number": 71,
    "verse_number": 20,
    "content": "تاکہ تم اس کے اندر کھلے راستوں میں چلو",
  },
  {
    "surah_number": 71,
    "verse_number": 21,
    "content": "نوحؑ نے کہا، 'میرے رب، اُنہوں نے میری بات رد کر دی اور اُن (رئیسوں) کی پیروی کی جو مال اور اولاد پا کر اور زیادہ نامراد ہو گئے ہیں",
  },
  {
    "surah_number": 71,
    "verse_number": 22,
    "content": "اِن لوگوں نے بڑا بھاری مکر کا جال پھیلا رکھا ہے",
  },
  {
    "surah_number": 71,
    "verse_number": 23,
    "content": "اِنہوں نے کہا ہرگز نہ چھوڑو اپنے معبودوں کو، اور نہ چھوڑو وَدّ اور سُواع کو، اور نہ یَغُوث اور یَعُوق اور نَسر کو",
  },
  {
    "surah_number": 71,
    "verse_number": 24,
    "content": "انہوں نے بہت لوگوں کو گمراہ کیا ہے، اور تو بھی اِن ظالموں کو گمراہی کے سوا کسی چیز میں ترقی نہ دے",
  },
  {
    "surah_number": 71,
    "verse_number": 25,
    "content": "اپنی خطاؤں کی بنا پر ہی وہ غرق کیے گئے اور آگ میں جھونک دیے گئے، پھر انہوں نے اپنے لیے اللہ سے بچانے والا کوئی مددگار نہ پایا",
  },
  {
    "surah_number": 71,
    "verse_number": 26,
    "content": "اور نوحؑ نے کہا، 'میرے رب، اِن کافروں میں سے کوئی زمین پر بسنے والا نہ چھوڑ",
  },
  {
    "surah_number": 71,
    "verse_number": 27,
    "content": "اگر تو نے اِن کو چھوڑ دیا تو یہ تیرے بندوں کو گمراہ کریں گے اور اِن کی نسل سے جو بھی پیدا ہوگا بدکار اور سخت کافر ہی ہوگا",
  },
  {
    "surah_number": 71,
    "verse_number": 28,
    "content": "میرے رب، مجھے اور میرے والدین کو اور ہر اس شخص کو جو میرے گھر میں مومن کی حیثیت سے داخل ہوا ہے، اور سب مومن مردوں اور عورتوں کو معاف فرما دے، اور ظالموں کے لیے ہلاکت کے سوا کسی چیز میں اضافہ نہ کر",
  },
  {
