  },
  {
    "surah_number": 74,
    "verse_number": 18,
    "content": "اس نے سوچا اور کچھ بات بنانے کی کوشش کی",
  },
  {
    "surah_number": 74,
    "verse_number": 19,
    "content": "تو خدا کی مار اس پر، کیسی بات بنانے کی کوشش کی",
  },
  {
    "surah_number": 74,
    "verse_number": 20,
    "content": "ہاں، خدا کی مار اُس پر، کیسی بات بنانے کی کوشش کی",
  },
  {
    "surah_number": 74,
    "verse_number": 21,
    "content": "پھر (لوگوں کی طرف) دیکھا",
  },
  {
    "surah_number": 74,
    "verse_number": 22,
    "content": "پھر پیشانی سیکڑی اور منہ بنایا",
  },
  {
    "surah_number": 74,
    "verse_number": 23,
    "content": "پھر پلٹا اور تکبر میں پڑ گیا",
  },
  {
    "surah_number": 74,
    "verse_number": 24,
    "content": "آخرکار بولا کہ یہ کچھ نہیں ہے مگر ایک جادو جو پہلے سے چلا آ رہا ہے",
  },
  {
    "surah_number": 74,
    "verse_number": 25,
    "content": "یہ تو یہ ایک انسانی کلام ہے",
  },
  {
    "surah_number": 74,
    "verse_number": 26,
    "content": "عنقریب میں اسے دوزخ میں جھونک دوں گا",
  },
  {
    "surah_number": 74,
    "verse_number": 27,
    "content": "اور تم کیا جانو کہ کیا ہے وہ دوزخ؟",
  },
  {
    "surah_number": 74,
    "verse_number": 28,
    "content": "نہ باقی رکھے نہ چھوڑے",
  },
  {
    "surah_number": 74,
    "verse_number": 29,
    "content": "کھال جھلس دینے والی",
  },
  {
    "surah_number": 74,
    "verse_number": 30,
    "content": "انیس کارکن اُس پر مقرر ہیں",
  },
  {
    "surah_number": 74,
    "verse_number": 31,
    "content": "ہم نے دوزخ کے یہ کارکن فرشتے بنائے ہیں، اور ان کی تعداد کو کافروں کے لیے فتنہ بنا دیا ہے، تاکہ اہل کتاب کو یقین آ جائے اور ایمان لانے والوں کا ایمان بڑھے، اور اہل کتاب اور مومنین کسی شک میں نہ رہیں، اور دل کے بیمار اور کفار یہ کہیں کہ بھلا اللہ کا اِس عجیب بات سے کیا مطلب ہو سکتا ہے اِس طرح اللہ جسے چاہتا ہے گمراہ کر دیتا ہے اور جسے چاہتا ہے ہدایت بخش دیتا ہے اور تیرے رب کے لشکروں کو خود اُس کے سوا کوئی نہیں جانتا اور اس دوزخ کا ذکر اِس کے سوا کسی غرض کے لیے نہیں کیا گیا ہے کہ لوگوں کو اس سے نصیحت ہو",
  },
  {
    "surah_number": 74,
    "verse_number": 32,
    "content": "ہرگز نہیں، قسم ہے چاند کی",
  },
  {
    "surah_number": 74,
    "verse_number": 33,
    "content": "اور رات کی جبکہ وہ پلٹتی ہے",
  },
  {
    "surah_number": 74,
    "verse_number": 34,
    "content": "اور صبح کی جبکہ وہ روشن ہوتی ہے",
  },
  {
    "surah_number": 74,
    "verse_number": 35,
    "content": "یہ دوزخ بھی بڑی چیزوں میں سے ایک ہے",
  },
  {
    "surah_number": 74,
    "verse_number": 36,
    "content": "انسانوں کے لیے ڈراوا",
  },
  {
    "surah_number": 74,
    "verse_number": 37,
    "content": "تم میں سے ہر اُس شخص کے لیے ڈراوا جو آگے بڑھنا چاہے یا پیچھے رہ جانا چاہے",
  },
  {
    "surah_number": 74,
    "verse_number": 38,
    "content": "ہر متنفس، اپنے کسب کے بدلے رہن ہے",
  },
  {
    "surah_number": 74,
    "verse_number": 39,
    "content": "دائیں بازو والوں کے سوا",
  },
  {
    "surah_number": 74,
    "verse_number": 40,
    "content": "جو جنتوں میں ہوں گے وہاں وہ،",
  },
  {
    "surah_number": 74,
    "verse_number": 41,
    "content": "مجرموں سے پوچھیں گے",
  },
  {
    "surah_number": 74,
    "verse_number": 42,
    "content": "تمہیں کیا چیز دوزخ میں لے گئی؟",
  },
  {
    "surah_number": 74,
    "verse_number": 43,
    "content": "وہ کہیں گے 'ہم نماز پڑھنے والوں میں سے نہ تھے",
  },
  {
    "surah_number": 74,
    "verse_number": 44,
    "content": "اور مسکین کو کھانا نہیں کھلاتے تھے",
  },
  {
    "surah_number": 74,
    "verse_number": 45,
    "content": "اور حق کے خلاف باتیں بنانے والوں کے ساتھ مل کر ہم بھی باتیں بنانے لگتے تھے",
  },
  {
    "surah_number": 74,
    "verse_number": 46,
    "content": "اور روز جزا کو جھوٹ قرار دیتے تھے",
  },
  {
    "surah_number": 74,
    "verse_number": 47,
    "content": "یہاں تک کہ ہمیں اُس یقینی چیز سے سابقہ پیش آ گیا",
  },
  {
    "surah_number": 74,
    "verse_number": 48,
    "content": "اُس وقت سفارش کرنے والوں کی سفارش ان کے کسی کام نہ آئے گی",
  },
  {
    "surah_number": 74,
    "verse_number": 49,
    "content": "آخر اِن لوگوں کو کیا ہو گیا ہے کہ یہ اِس نصیحت سے منہ موڑ رہے ہیں",
  },
  {
    "surah_number": 74,
    "verse_number": 50,
    "content": "گویا یہ جنگلی گدھے ہیں",
  },
  {
    "surah_number": 74,
    "verse_number": 51,
    "content": "جو شیر سے ڈر کر بھاگ پڑے ہیں",
  },
  {
    "surah_number": 74,
    "verse_number": 52,
    "content": "بلکہ اِن میں سے تو ہر ایک یہ چاہتا ہے کہ اُس کے نام کھلے خط بھیجے جائیں",
  },
  {
    "surah_number": 74,
    "verse_number": 53,
    "content": "ہرگز نہیں، اصل بات یہ ہے کہ یہ آخرت کا خوف نہیں رکھتے",
  },
  {
    "surah_number": 74,
    "verse_number": 54,
    "content": "ہرگز نہیں، یہ تو ایک نصیحت ہے",
  },
  {
    "surah_number": 74,
    "verse_number": 55,
    "content": "اب جس کا جی چاہے اس سے سبق حاصل کر لے",
  },
  {
    "surah_number": 74,
    "verse_number": 56,
    "content": "اور یہ کوئی سبق حاصل نہ کریں گے الا یہ کہ اللہ ہی ایسا چاہے وہ اس کا حق دار ہے کہ اُس سے تقویٰ کیا جائے اور وہ اس کا اہل ہے کہ (تقویٰ کرنے والوں کو) بخش دے",
  },
  {
    "surah_number": 75,
    "verse_number": 1,
    "content": "نہیں، میں قسم کھاتا ہوں قیامت کے دن کی",
  },
  {
    "surah_number": 75,
    "verse_number": 2,
    "content": "اور نہیں، میں قسم کھاتا ہوں ملامت کرنے والے نفس کی",
  },
  {
    "surah_number": 75,
    "verse_number": 3,
    "content": "کیا انسان یہ سمجھ رہا ہے کہ ہم اُس کی ہڈیوں کو جمع نہ کر سکیں گے؟",
  },
  {
    "surah_number": 75,
    "verse_number": 4,
    "content": "ہم تو اس کی انگلیوں کی پور پور تک ٹھیک بنا دینے پر قادر ہیں",
  },
  {
    "surah_number": 75,
    "verse_number": 5,
    "content": "مگر انسان چاہتا یہ ہے کہ آگے بھی بد اعمالیاں کرتا رہے",
  },
  {
    "surah_number": 75,
    "verse_number": 6,
    "content": "پوچھتا ہے 'آخر کب آنا ہے وہ قیامت کا دن؟",
  },
  {
    "surah_number": 75,
    "verse_number": 7,
    "content": "پھر جب دیدے پتھرا جائیں گے",
  },
  {
    "surah_number": 75,
    "verse_number": 8,
    "content": "اور چاند بے نور ہو جائیگا",
  },
  {
    "surah_number": 75,
    "verse_number": 9,
    "content": "اور چاند سورج ملا کر ایک کر دیے جائیں گے",
  },
  {
    "surah_number": 75,
    "verse_number": 10,
    "content": "اُس وقت یہی انسان کہے گا 'کہاں بھاگ کر جاؤں؟",
  },
  {
    "surah_number": 75,
    "verse_number": 11,
    "content": "ہرگز نہیں، وہاں کوئی جائے پناہ نہ ہوگی",
  },
  {
    "surah_number": 75,
    "verse_number": 12,
    "content": "اُس روز تیرے رب ہی کے سامنے جا کر ٹھیرنا ہوگا",
  },
  {
    "surah_number": 75,
    "verse_number": 13,
    "content": "اُس روز انسان کو اس کا سب اگلا پچھلا کیا کرایا بتا دیا جائے گا",
  },
  {
    "surah_number": 75,
    "verse_number": 14,
    "content": "بلکہ انسان خود ہی اپنے آپ کو خوب جانتا ہے",
  },
  {
    "surah_number": 75,
    "verse_number": 15,
    "content": "چاہے وہ کتنی ہی معذرتیں پیش کرے",
  },
  {
    "surah_number": 75,
    "verse_number": 16,
    "content": "اے نبیؐ، اِس وحی کو جلدی جلدی یاد کرنے کے لیے اپنی زبان کو حرکت نہ دو",
  },
  {
    "surah_number": 75,
    "verse_number": 17,
    "content": "اِس کو یاد کرا دینا اور پڑھوا دینا ہمارے ذمہ ہے",
  },
  {
    "surah_number": 75,
    "verse_number": 18,
    "content": "لہٰذا جب ہم اِسے پڑھ رہے ہوں اُس وقت تم اِس کی قرات کو غور سے سنتے رہو",
  },
  {
    "surah_number": 75,
    "verse_number": 19,
    "content": "پھر اس کا مطلب سمجھا دینا بھی ہمارے ذمہ ہے",
  },
  {
    "surah_number": 75,
    "verse_number": 20,
    "content": "ہرگز نہیں، اصل بات یہ ہے کہ تم لوگ جلدی حاصل ہونے والی چیز (یعنی دنیا) سے محبت رکھتے ہو",
  },
  {
    "surah_number": 75,
    "verse_number": 21,
    "content": "اور آخرت کو چھوڑ دیتے ہو",
  },
  {
    "surah_number": 75,
    "verse_number": 22,
    "content": "اُس روز کچھ چہرے تر و تازہ ہونگے",
  },
  {
    "surah_number": 75,
    "verse_number": 23,
    "content": "اپنے رب کی طرف دیکھ رہے ہونگے",
  },
  {
    "surah_number": 75,
    "verse_number": 24,
    "content": "اور کچھ چہرے اداس ہوں گے",
  },
  {
    "surah_number": 75,
    "verse_number": 25,
    "content": "اور سمجھ رہے ہوں گے کہ اُن کے ساتھ کمر توڑ برتاؤ ہونے والا ہے",
  },
  {
    "surah_number": 75,
    "verse_number": 26,
    "content": "ہرگز نہیں، جب جان حلق تک پہنچ جائے گی",
  },
  {
    "surah_number": 75,
    "verse_number": 27,
    "content": "اور کہا جائے گا کہ ہے کوئی جھاڑ پھونک کرنے والا",
  },
  {
    "surah_number": 75,
    "verse_number": 28,
    "content": "اور آدمی سمجھ لے گا کہ یہ دنیا سے جدائی کا وقت ہے",
  },
  {
    "surah_number": 75,
    "verse_number": 29,
    "content": "اور پنڈلی سے پنڈلی جڑ جائے گی",
  },
  {
    "surah_number": 75,
    "verse_number": 30,
    "content": "وہ دن ہوگا تیرے رب کی طرف روانگی کا",
  },
  {
    "surah_number": 75,
    "verse_number": 31,
    "content": "مگر اُس نے نہ سچ مانا، اور نہ نماز پڑھی",
  },
  {
    "surah_number": 75,
    "verse_number": 32,
    "content": "بلکہ جھٹلایا اور پلٹ گیا",
  },
  {
    "surah_number": 75,
    "verse_number": 33,
    "content": "پھر اکڑتا ہوا اپنے گھر والوں کی طرف چل دیا",
  },
  {
    "surah_number": 75,
    "verse_number": 34,
    "content": "یہ روش تیرے ہی لیے سزاوار ہے اور تجھی کو زیب دیتی ہے",
  },
  {
    "surah_number": 75,
    "verse_number": 35,
    "content": "ہاں یہ روش تیرے ہی لیے سزاوار ہے اور تجھی کو زیب دیتی ہے",
  },
  {
    "surah_number": 75,
    "verse_number": 36,
    "content": "کیا انسان نے یہ سمجھ رکھا ہے کہ وہ یونہی مہمل چھوڑ دیا جائے گا؟",
  },
  {
    "surah_number": 75,
    "verse_number": 37,
    "content": "کیا وہ ایک حقیر پانی کا نطفہ نہ تھا جو (رحم مادر میں) ٹپکایا جاتا ہے؟",
  },
  {
    "surah_number": 75,
    "verse_number": 38,
    "content": "پھر وہ ایک لوتھڑا بنا، پھر اللہ نے اس کا جسم بنایا اور اس کے اعضا درست کیے",
  },
  {
    "surah_number": 75,
    "verse_number": 39,
    "content": "پھر اس سے مرد اور عورت کی دو قسمیں بنائیں",
  },
  {
    "surah_number": 75,
    "verse_number": 40,
    "content": "کیا وہ اِس پر قادر نہیں ہے کہ مرنے والوں کو پھر زندہ کر دے؟",
  },
  {
    "surah_number": 76,
    "verse_number": 1,
    "content": "کیا انسان پر لا متناہی زمانے کا ایک وقت ایسا بھی گزرا ہے جب وہ کوئی قابل ذکر چیز نہ تھا؟",
  },
  {
    "surah_number": 76,
    "verse_number": 2,
    "content": "ہم نے انسان کو ایک مخلوط نطفے سے پیدا کیا تاکہ اس کا امتحان لیں اور اِس غرض کے لیے ہم نے اسے سننے اور دیکھنے والا بنایا",
  },
  {
    "surah_number": 76,
    "verse_number": 3,
    "content": "ہم نے اسے راستہ دکھا دیا، خواہ شکر کرنے والا بنے یا کفر کرنے والا",
  },
  {
    "surah_number": 76,
    "verse_number": 4,
    "content": "کفر کرنے والوں کے لیے ہم نے زنجیریں اور طوق اور بھڑکتی ہوئی آگ مہیا کر رکھی ہے",
  },
  {
    "surah_number": 76,
    "verse_number": 5,
    "content": "نیک لوگ (جنت میں) شراب کے ایسے ساغر پئیں گے جن میں آب کافور کی آمیزش ہوگی",
  },
  {
    "surah_number": 76,
    "verse_number": 6,
    "content": "یہ ایک بہتا چشمہ ہوگا جس کے پانی کے ساتھ اللہ کے بندے شراب پئیں گے اور جہاں چاہیں گے بسہولت اس کی شاخیں لیں گے",
  },
  {
    "surah_number": 76,
    "verse_number": 7,
    "content": "یہ وہ لوگ ہونگے جو (دنیا میں) نذر پوری کرتے ہیں، اور اُس دن سے ڈرتے ہیں جس کی آفت ہر طرف پھیلی ہوئی ہوگی",
  },
  {
    "surah_number": 76,
    "verse_number": 8,
    "content": "اور اللہ کی محبت میں مسکین اور یتیم اور قیدی کو کھانا کھلاتے ہیں",
  },
  {
    "surah_number": 76,
    "verse_number": 9,
    "content": "(اور اُن سے کہتے ہیں کہ) ہم تمہیں صرف اللہ کی خاطر کھلا رہے ہیں، ہم تم سے نہ کوئی بدلہ چاہتے ہیں نہ شکریہ",
  },
  {
    "surah_number": 76,
    "verse_number": 10,
    "content": "ہمیں تو اپنے رب سے اُس دن کے عذاب کا خوف لاحق ہے جو سخت مصیبت کا انتہائی طویل دن ہوگا",
  },
  {
    "surah_number": 76,
    "verse_number": 11,
    "content": "پس اللہ تعالیٰ انہیں اُس دن کے شر سے بچا لے گا اور انہیں تازگی اور سرور بخشے گا",
  },
  {
    "surah_number": 76,
    "verse_number": 12,
    "content": "اور اُن کے صبر کے بدلے میں اُنہیں جنت اور ریشمی لباس عطا کریگا",
  },
  {
    "surah_number": 76,
    "verse_number": 13,
    "content": "وہاں وہ اونچی مسندوں پر تکیے لگائے بیٹھے ہونگے نہ اُنہیں دھوپ کی گرمی ستائے گی نہ جاڑے کی ٹھر",
  },
  {
    "surah_number": 76,
    "verse_number": 14,
    "content": "جنت کی چھاؤں ان پر جھکی ہوئی سایہ کر رہی ہوگی، اور اُس کے پھل ہر وقت ان کے بس میں ہوں گے (کہ جس طرح چاہیں انہیں توڑ لیں)",
  },
  {
    "surah_number": 76,
    "verse_number": 15,
    "content": "اُن کے آگے چاندی کے برتن اور شیشے کے پیالے گردش کرائے جا رہے ہونگے",
  },
  {
    "surah_number": 76,
    "verse_number": 16,
    "content": "شیشے بھی وہ جو چاندی کی قسم کے ہونگے، اور ان کو (منتظمین جنت نے) ٹھیک اندازے کے مطابق بھرا ہوگا",
  },
  {
    "surah_number": 76,
    "verse_number": 17,
    "content": "ان کو وہاں ایسی شراب کے جام پلائے جائیں گے جس میں سونٹھ کی آمیزش ہوگی",
  },
  {
    "surah_number": 76,
    "verse_number": 18,
    "content": "یہ جنت کا ایک چشمہ ہوگا جسے سلسبیل کہا جاتا ہے",
  },
  {
    "surah_number": 76,
    "verse_number": 19,
    "content": "ان کی خدمت کے لیے ایسے لڑکے دوڑتے پھر رہے ہوں گے جو ہمیشہ لڑکے ہی رہیں گے تم انہیں دیکھو تو سمجھو کہ موتی ہیں جو بکھیر دیے گئے ہیں",
  },
  {
    "surah_number": 76,
    "verse_number": 20,
    "content": "وہاں جدھر بھی تم نگاہ ڈالو گے نعمتیں ہی نعمتیں اور ایک بڑی سلطنت کا سر و سامان تمہیں نظر آئے گا",
  },
  {
    "surah_number": 76,
    "verse_number": 21,
    "content": "اُن کے اوپر باریک ریشم کے سبز لباس اور اطلس و دیبا کے کپڑے ہوں گے، ان کو چاندی کے کنگن پہنا ئے جائیں گے، اور ان کا رب ان کو نہایت پاکیزہ شراب پلائے گا",
  },
  {
    "surah_number": 76,
    "verse_number": 22,
    "content": "یہ ہے تمہاری جزا اور تمہاری کارگزاری قابل قدر ٹھیری ہے",
  },
  {
    "surah_number": 76,
    "verse_number": 23,
    "content": "اے نبیؐ، ہم نے ہی تم پر یہ قرآن تھوڑا تھوڑا کر کے نازل کیا ہے",
  },
  {
    "surah_number": 76,
    "verse_number": 24,
    "content": "لہٰذا تم اپنے رب کے حکم پر صبر کرو، اور اِن میں سے کسی بد عمل یا منکر حق کی بات نہ مانو",
  },
  {
    "surah_number": 76,
    "verse_number": 25,
    "content": "اپنے رب کا نام صبح و شام یاد کرو",
  },
  {
    "surah_number": 76,
    "verse_number": 26,
    "content": "رات کو بھی اس کے حضور سجدہ ریز ہو، اور رات کے طویل اوقات میں اُس کی تسبیح کرتے رہو",
  },
  {
    "surah_number": 76,
    "verse_number": 27,
    "content": "یہ لوگ تو جلدی حاصل ہونے والی چیز (دنیا) سے محبت رکھتے ہیں اور آگے جو بھاری دن آنے والا ہے اسے نظر انداز کر دیتے ہیں",
  },
  {
    "surah_number": 76,
    "verse_number": 28,
    "content": "ہم نے ہی اِن کو پیدا کیا ہے اور ان کے جوڑ بند مضبوط کیے ہیں، اور ہم جب چاہیں اِن کی شکلوں کو بدل کر رکھ دیں",
  },
  {
    "surah_number": 76,
    "verse_number": 29,
    "content": "یہ ایک نصیحت ہے، اب جس کا جی چاہے اپنے رب کی طرف جانے کا راستہ اختیار کر لے",
  },
  {
    "surah_number": 76,
    "verse_number": 30,
    "content": "اور تمہارے چاہنے سے کچھ نہیں ہوتا جب تک اللہ نہ چاہے یقیناً اللہ بڑا علیم و حکیم ہے",
  },
  {
    "surah_number": 76,
    "verse_number": 31,
    "content": "اپنی رحمت میں جس کو چاہتا ہے داخل کرتا ہے، اور ظالموں کے لیے اس نے دردناک عذاب تیار کر رکھا ہے",
  },
  {
    "surah_number": 77,
    "verse_number": 1,
    "content": "قسم ہے اُن (ہواؤں) کی جو پے در پے بھیجی جاتی ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 2,
    "content": "پھر طوفانی رفتار سے چلتی ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 3,
    "content": "اور (بادلوں کو) اٹھا کر پھیلاتی ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 4,
    "content": "پھر (اُن کو) پھاڑ کر جدا کرتی ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 5,
    "content": "پھر (دلوں میں خدا کی) یاد ڈالتی ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 6,
    "content": "عذر کے طور پر یا ڈراوے کے طور پر",
  },
  {
    "surah_number": 77,
    "verse_number": 7,
    "content": "جس چیز کا تم سے وعدہ کیا جا رہا ہے وہ ضرور واقع ہونے والی ہے",
  },
  {
    "surah_number": 77,
    "verse_number": 8,
    "content": "پھر جب ستارے ماند پڑ جائیں گے",
  },
  {
    "surah_number": 77,
    "verse_number": 9,
    "content": "اور آسمان پھاڑ دیا جائے گا",
  },
  {
    "surah_number": 77,
    "verse_number": 10,
    "content": "اور پہاڑ دھنک ڈالے جائیں گے",
  },
  {
    "surah_number": 77,
    "verse_number": 11,
    "content": "اور رسولوں کی حاضری کا وقت آ پہنچے گا (اس روز وہ چیز واقع ہو جائے گی)",
  },
  {
    "surah_number": 77,
    "verse_number": 12,
    "content": "کس روز کے لیے یہ کام اٹھا رکھا گیا ہے؟",
  },
  {
    "surah_number": 77,
    "verse_number": 13,
    "content": "فیصلے کے روز کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 14,
    "content": "اور تمہیں کیا خبر کہ وہ فیصلے کا دن کیا ہے؟",
  },
  {
    "surah_number": 77,
    "verse_number": 15,
    "content": "تباہی ہے اُس دن جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 16,
    "content": "کیا ہم نے اگلوں کو ہلاک نہیں کیا؟",
  },
  {
    "surah_number": 77,
    "verse_number": 17,
    "content": "پھر اُنہی کے پیچھے ہم بعد والوں کو چلتا کریں گے",
  },
  {
    "surah_number": 77,
    "verse_number": 18,
    "content": "مجرموں کے ساتھ ہم یہی کچھ کیا کرتے ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 19,
    "content": "تباہی ہے اُس دن جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 20,
    "content": "کیا ہم نے ایک حقیر پانی سے تمہیں پیدا نہیں کیا",
  },
  {
    "surah_number": 77,
    "verse_number": 21,
    "content": "اور ایک مقررہ مدت تک،",
  },
  {
    "surah_number": 77,
    "verse_number": 22,
    "content": "اُسے ایک محفوظ جگہ ٹھیرائے رکھا؟",
  },
  {
    "surah_number": 77,
    "verse_number": 23,
    "content": "تو دیکھو، ہم اِس پر قادر تھے، پس ہم بہت اچھی قدرت رکھنے والے ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 24,
    "content": "تباہی ہے اُس روز جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 25,
    "content": "کیا ہم نے زمین کو سمیٹ کر رکھنے والی نہیں بنایا",
  },
  {
    "surah_number": 77,
    "verse_number": 26,
    "content": "زندوں کے لیے بھی اور مُردوں کے لیے بھی",
  },
  {
    "surah_number": 77,
    "verse_number": 27,
    "content": "اور اس میں بلند و بالا پہاڑ جمائے، اور تمہیں میٹھا پانی پلایا؟",
  },
  {
    "surah_number": 77,
    "verse_number": 28,
    "content": "تباہی ہے اُس روز جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 29,
    "content": "چلو اب اُسی چیز کی طرف جسے تم جھٹلایا کرتے تھے",
  },
  {
    "surah_number": 77,
    "verse_number": 30,
    "content": "چلو اُس سائے کی طرف جو تین شاخوں والا ہے",
  },
  {
    "surah_number": 77,
    "verse_number": 31,
    "content": "نہ ٹھنڈک پہنچانے والا اور نہ آگ کی لپٹ سے بچانے والا",
  },
  {
    "surah_number": 77,
    "verse_number": 32,
    "content": "وہ آگ محل جیسی بڑی بڑی چنگاریاں پھینکے گی",
  },
  {
    "surah_number": 77,
    "verse_number": 33,
    "content": "(جو اچھلتی ہوئی یوں محسوس ہوں گی) گویا کہ وہ زرد اونٹ ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 34,
    "content": "تباہی ہے اُس روز جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 35,
    "content": "یہ وہ دن ہے جس میں وہ نہ کچھ بولیں گے",
  },
  {
    "surah_number": 77,
    "verse_number": 36,
    "content": "اور نہ اُنہیں موقع دیا جائے گا کہ کوئی عذر پیش کریں",
  },
  {
    "surah_number": 77,
    "verse_number": 37,
    "content": "تباہی ہے اُس دن جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 38,
    "content": "یہ فیصلے کا دن ہے ہم نے تمہیں اور تم سے پہلے گزرے ہوئے لوگوں کو جمع کر دیا ہے",
  },
  {
    "surah_number": 77,
    "verse_number": 39,
    "content": "اب اگر کوئی چال تم چل سکتے ہو تو میرے مقابلہ میں چل دیکھو",
  },
  {
    "surah_number": 77,
    "verse_number": 40,
    "content": "تباہی ہے اُس دن جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 41,
    "content": "متقی لوگ آج سایوں اور چشموں میں ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 42,
    "content": "اور جو پھل وہ چاہیں (اُن کے لیے حاضر ہیں)",
  },
  {
    "surah_number": 77,
    "verse_number": 43,
    "content": "کھاؤ اور پیو مزے سے اپنے اُن اعمال کے صلے میں جو تم کرتے رہے ہو",
  },
  {
    "surah_number": 77,
    "verse_number": 44,
    "content": "ہم نیک لوگوں کو ایسی ہی جزا دیتے ہیں",
  },
  {
    "surah_number": 77,
    "verse_number": 45,
    "content": "تباہی ہے اُس روز جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 46,
    "content": "کھا لو اور مزے کر لو تھوڑے دن حقیقت میں تم لوگ مجرم ہو",
  },
  {
    "surah_number": 77,
    "verse_number": 47,
    "content": "تباہی ہے اُس روز جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 48,
    "content": "جب اِن سے کہا جاتا ہے کہ (اللہ کے آگے) جھکو تو نہیں جھکتے",
  },
  {
    "surah_number": 77,
    "verse_number": 49,
    "content": "تباہی ہے اُس روز جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 77,
    "verse_number": 50,
    "content": "اب اِس (قرآن) کے بعد اور کونسا کلام ایسا ہو سکتا ہے جس پر یہ ایمان لائیں؟",
  },
  {
    "surah_number": 78,
    "verse_number": 1,
    "content": "یہ لوگ کس چیز کے بارے میں پوچھ گچھ کر رہے ہیں؟",
  },
  {
    "surah_number": 78,
    "verse_number": 2,
    "content": "کیا اُس بڑی خبر کے بارے میں",
  },
  {
    "surah_number": 78,
    "verse_number": 3,
    "content": "جس کے متعلق یہ مختلف چہ میگوئیاں کرنے میں لگے ہوئے ہیں؟",
  },
  {
    "surah_number": 78,
    "verse_number": 4,
    "content": "ہرگز نہیں، عنقریب اِنہیں معلوم ہو جائیگا",
  },
  {
    "surah_number": 78,
    "verse_number": 5,
    "content": "ہاں، ہرگز نہیں، عنقریب اِنہیں معلوم ہو جائے گا",
  },
  {
    "surah_number": 78,
    "verse_number": 6,
    "content": "کیا یہ واقعہ نہیں ہے کہ ہم نے زمین کو فرش بنایا",
  },
  {
    "surah_number": 78,
    "verse_number": 7,
    "content": "اور پہاڑوں کو میخوں کی طرح گاڑ دیا",
  },
  {
    "surah_number": 78,
    "verse_number": 8,
    "content": "اور تمہیں (مَردوں اور عورتوں کے) جوڑوں کی شکل میں پیدا کیا",
  },
  {
    "surah_number": 78,
    "verse_number": 9,
    "content": "اور تمہاری نیند کو باعث سکون بنایا",
  },
  {
    "surah_number": 78,
    "verse_number": 10,
    "content": "اور رات کو پردہ پوش",
  },
  {
    "surah_number": 78,
    "verse_number": 11,
    "content": "اور دن کو معاش کا وقت بنایا",
  },
  {
    "surah_number": 78,
    "verse_number": 12,
    "content": "اور تمہارے اوپر سات مضبوط آسمان قائم کیے",
  },
  {
    "surah_number": 78,
    "verse_number": 13,
    "content": "اور ایک نہایت روشن اور گرم چراغ پیدا کیا",
  },
  {
    "surah_number": 78,
    "verse_number": 14,
    "content": "اور بادلوں سے لگاتار بارش برسائی",
  },
  {
    "surah_number": 78,
    "verse_number": 15,
    "content": "تاکہ اس کے ذریعہ سے غلہ اور سبزی",
  },
  {
    "surah_number": 78,
    "verse_number": 16,
    "content": "اور گھنے باغ اگائیں؟",
  },
  {
    "surah_number": 78,
    "verse_number": 17,
    "content": "بے شک فیصلے کا دن ایک مقرر وقت ہے",
  },
  {
    "surah_number": 78,
    "verse_number": 18,
    "content": "جس روز صور میں پھونک مار دی جائے گی، تم فوج در فوج نکل آؤ گے",
  },
  {
    "surah_number": 78,
    "verse_number": 19,
    "content": "اور آسمان کھول دیا جائے گا حتیٰ کہ وہ دروازے ہی دروازے بن کر رہ جائے گا",
  },
  {
    "surah_number": 78,
    "verse_number": 20,
    "content": "اور پہاڑ چلائے جائیں گے یہاں تک کہ وہ سراب ہو جائیں گے",
  },
  {
    "surah_number": 78,
    "verse_number": 21,
    "content": "درحقیقت جہنم ایک گھات ہے",
  },
  {
    "surah_number": 78,
    "verse_number": 22,
    "content": "سرکشوں کا ٹھکانا",
  },
  {
    "surah_number": 78,
    "verse_number": 23,
    "content": "جس میں وہ مدتوں پڑے رہیں گے",
  },
  {
    "surah_number": 78,
    "verse_number": 24,
    "content": "اُس کے اندر کسی ٹھنڈک اور پینے کے قابل کسی چیز کا مزہ وہ نہ چکھیں گے",
  },
  {
    "surah_number": 78,
    "verse_number": 25,
    "content": "کچھ ملے گا تو بس گرم پانی اور زخموں کا دھوون",
  },
  {
    "surah_number": 78,
    "verse_number": 26,
    "content": "(اُن کے کرتوتوں) کا بھرپور بدلہ",
  },
  {
    "surah_number": 78,
    "verse_number": 27,
    "content": "وہ کسی حساب کی توقع نہ رکھتے تھے",
  },
  {
    "surah_number": 78,
    "verse_number": 28,
    "content": "اور ہماری آیات کو انہوں نے بالکل جھٹلا دیا تھا",
  },
  {
    "surah_number": 78,
    "verse_number": 29,
    "content": "اور حال یہ تھا کہ ہم نے ہر چیز گن گن کر لکھ رکھی تھی",
  },
  {
    "surah_number": 78,
    "verse_number": 30,
    "content": "اب چکھو مزہ، ہم تمہارے لیے عذاب کے سوا کسی چیز میں ہرگز اضافہ نہ کریں گے",
  },
  {
    "surah_number": 78,
    "verse_number": 31,
    "content": "یقیناً متقیوں کے لیے کامرانی کا ایک مقام ہے",
  },
  {
    "surah_number": 78,
    "verse_number": 32,
    "content": "باغ اور انگور",
  },
  {
    "surah_number": 78,
    "verse_number": 33,
    "content": "اور نوخیر ہم سن لڑکیاں",
  },
  {
    "surah_number": 78,
    "verse_number": 34,
    "content": "اور چھلکتے ہوئے جام",
  },
  {
    "surah_number": 78,
    "verse_number": 35,
    "content": "وہاں کوئی لغو اور جھوٹی بات وہ نہ سنیں گے",
  },
  {
    "surah_number": 78,
    "verse_number": 36,
    "content": "جزا اور کافی انعام تمہارے رب کی طرف سے",
  },
  {
    "surah_number": 78,
    "verse_number": 37,
    "content": "اُس نہایت مہربان خدا کی طرف سے جو زمین اور آسمانوں کا اور ان کے درمیان کی ہر چیز کا مالک ہے جس کے سامنے کسی کو بولنے کا یارا نہیں",
  },
  {
    "surah_number": 78,
    "verse_number": 38,
    "content": "جس روز روح اور ملائکہ صف بستہ کھڑے ہونگے، کوئی نہ بولے گا سوائے اُس کے جسے رحمٰن اجازت دے اور جو ٹھیک بات کہے",
  },
  {
    "surah_number": 78,
    "verse_number": 39,
    "content": "وہ دن برحق ہے، اب جس کا جی چاہے اپنے رب کی طرف پلٹنے کا راستہ اختیار کر لے",
  },
  {
    "surah_number": 78,
    "verse_number": 40,
    "content": "ہم نے تم لوگوں کو اُس عذاب سے ڈرا دیا ہے جو قریب آ لگا ہے جس روز آدمی وہ سب کچھ دیکھ لے گا جو اس کے ہاتھوں نے آگے بھیجا ہے، اور کافر پکار اٹھے گا کہ کاش میں خاک ہوتا",
  },
  {
    "surah_number": 79,
    "verse_number": 1,
    "content": "قسم ہے اُن (فرشتوں کی) جو ڈوب کر کھینچتے ہیں",
  },
  {
    "surah_number": 79,
    "verse_number": 2,
    "content": "اور آہستگی سے نکال لے جاتے ہیں",
  },
  {
    "surah_number": 79,
    "verse_number": 3,
    "content": "اور (اُن فرشتوں کی جو کائنات میں) تیزی سے تیرتے پھرتے ہیں",
  },
  {
    "surah_number": 79,
    "verse_number": 4,
    "content": "پھر (حکم بجا لانے میں) سبقت کرتے ہیں",
  },
  {
    "surah_number": 79,
    "verse_number": 5,
    "content": "پھر (احکام الٰہی کے مطابق) معاملات کا انتظام چلاتے ہیں",
  },
  {
    "surah_number": 79,
    "verse_number": 6,
    "content": "جس روز ہلا مارے گا زلزلے کا جھٹکا",
  },
  {
    "surah_number": 79,
    "verse_number": 7,
    "content": "اور اس کے پیچھے ایک اور جھٹکا پڑے گا",
  },
  {
    "surah_number": 79,
    "verse_number": 8,
    "content": "کچھ دل ہوں گے جو اُس روز خوف سے کانپ رہے ہوں گے",
  },
  {
    "surah_number": 79,
    "verse_number": 9,
    "content": "نگاہیں اُن کی سہمی ہوئی ہوں گی",
  },
  {
    "surah_number": 79,
    "verse_number": 10,
    "content": "یہ لوگ کہتے ہیں 'کیا واقعی ہم پلٹا کر پھر واپس لائے جائیں گے؟",
  },
  {
    "surah_number": 79,
    "verse_number": 11,
    "content": "کیا جب ہم کھوکھلی بوسیدہ ہڈیاں بن چکے ہوں گے؟",
  },
  {
    "surah_number": 79,
    "verse_number": 12,
    "content": "کہنے لگے 'یہ واپسی تو پھر بڑے گھاٹے کی ہوگی",
  },
  {
    "surah_number": 79,
    "verse_number": 13,
    "content": "حالانکہ یہ بس اتنا کام ہے کہ ایک زور کی ڈانٹ پڑے گی",
  },
  {
    "surah_number": 79,
    "verse_number": 14,
    "content": "اور یکایک یہ کھلے میدان میں موجود ہوں گے",
  },
  {
    "surah_number": 79,
    "verse_number": 15,
    "content": "کیا تمہیں موسیٰؑ کے قصے کی خبر پہنچی ہے؟",
  },
  {
    "surah_number": 79,
    "verse_number": 16,
    "content": "جب اس کے رب نے اُسے طویٰ کی مقدس وادی میں پکارا تھا",
  },
  {
    "surah_number": 79,
    "verse_number": 17,
    "content": "کہ 'فرعون کے پاس جا، وہ سرکش ہو گیا ہے",
  },
  {
    "surah_number": 79,
    "verse_number": 18,
    "content": "اور اس سے کہہ کیا تو اِس کے لیے تیار ہے کہ پاکیزگی اختیار کرے",
  },
  {
    "surah_number": 79,
    "verse_number": 19,
    "content": "اور میں تیرے رب کی طرف تیری رہنمائی کروں تو (اُس کا) خوف تیرے اندر پیدا ہو؟",
  },
  {
    "surah_number": 79,
    "verse_number": 20,
    "content": "پھر موسیٰؑ نے (فرعون کے پاس جا کر) اُس کو بڑی نشانی دکھائی",
  },
  {
    "surah_number": 79,
    "verse_number": 21,
    "content": "مگر اُس نے جھٹلا دیا اور نہ مانا",
  },
  {
    "surah_number": 79,
    "verse_number": 22,
    "content": "پھر چالبازیاں کرنے کے لیے پلٹا",
  },
  {
    "surah_number": 79,
    "verse_number": 23,
    "content": "اور لوگوں کو جمع کر کے اس نے پکار کر کہا",
  },
  {
    "surah_number": 79,
    "verse_number": 24,
    "content": "میں تمہارا سب سے بڑا رب ہوں",
  },
  {
    "surah_number": 79,
    "verse_number": 25,
    "content": "آخرکار اللہ نے اسے آخرت اور دنیا کے عذاب میں پکڑ لیا",
  },
  {
    "surah_number": 79,
    "verse_number": 26,
    "content": "درحقیقت اِس میں بڑی عبرت ہے ہر اُس شخص کے لیے جو ڈرے",
  },
  {
    "surah_number": 79,
    "verse_number": 27,
    "content": "کیا تم لوگوں کی تخلیق زیادہ سخت کام ہے یا آسمان کی؟ اللہ نے اُس کو بنایا",
  },
  {
    "surah_number": 79,
    "verse_number": 28,
    "content": "اُس کی چھت خوب اونچی اٹھائی پھر اُس کا توازن قائم کیا",
  },
  {
    "surah_number": 79,
    "verse_number": 29,
    "content": "اور اُس کی رات ڈھانکی اور اُس کا دن نکالا",
  },
  {
    "surah_number": 79,
    "verse_number": 30,
    "content": "اِس کے بعد زمین کو اس نے بچھایا",
  },
  {
    "surah_number": 79,
    "verse_number": 31,
    "content": "اُس کے اندر سے اُس کا پانی اور چارہ نکالا",
  },
  {
    "surah_number": 79,
    "verse_number": 32,
    "content": "اور پہاڑ اس میں گاڑ دیے",
  },
  {
    "surah_number": 79,
    "verse_number": 33,
    "content": "سامان زیست کے طور پر تمہارے لیے اور تمہارے مویشیوں کے لیے",
  },
  {
    "surah_number": 79,
    "verse_number": 34,
    "content": "پھر جب وہ ہنگامہ عظیم برپا ہوگا",
  },
  {
    "surah_number": 79,
    "verse_number": 35,
    "content": "جس روز انسان اپنا سب کیا دھرا یاد کرے گا",
  },
  {
    "surah_number": 79,
    "verse_number": 36,
    "content": "اور ہر دیکھنے والے کے سامنے دوزخ کھول کر رکھ دی جائے گی",
  },
  {
    "surah_number": 79,
    "verse_number": 37,
    "content": "تو جس نے سرکشی کی تھی",
  },
  {
    "surah_number": 79,
    "verse_number": 38,
    "content": "اور دنیا کی زندگی کو ترجیح دی تھی",
  },
  {
    "surah_number": 79,
    "verse_number": 39,
    "content": "دوزخ ہی اس کا ٹھکانا ہوگی",
  },
  {
    "surah_number": 79,
    "verse_number": 40,
    "content": "اور جس نے اپنے رب کے سامنے کھڑے ہونے کا خوف کیا تھا اور نفس کو بری خواہشات سے باز رکھا تھا",
  },
  {
    "surah_number": 79,
    "verse_number": 41,
    "content": "جنت اس کا ٹھکانا ہوگی",
  },
  {
    "surah_number": 79,
    "verse_number": 42,
    "content": "یہ لوگ تم سے پوچھتے ہیں کہ 'آخر وہ گھڑی کب آ کر ٹھیرے گی؟",
  },
  {
    "surah_number": 79,
    "verse_number": 43,
    "content": "تمہارا کیا کام کہ اس کا وقت بتاؤ",
  },
  {
    "surah_number": 79,
    "verse_number": 44,
    "content": "اس کا علم تو اللہ پر ختم ہے",
  },
  {
    "surah_number": 79,
    "verse_number": 45,
    "content": "تم صرف خبردار کرنے والے ہو ہر اُس شخص کو جو اُس کا خوف کرے",
  },
  {
    "surah_number": 79,
    "verse_number": 46,
    "content": "جس روز یہ لوگ اسے دیکھ لیں گے تو انہیں یوں محسوس ہوگا کہ (یہ دنیا میں یا حالت موت میں) بس ایک دن کے پچھلے پہر یا اگلے پہر تک ٹھیرے ہیں",
  },
  {
    "surah_number": 80,
    "verse_number": 1,
    "content": "ترش رو ہوا، اور بے رخی برتی",
  },
  {
    "surah_number": 80,
    "verse_number": 2,
    "content": "اِس بات پر کہ وہ اندھا اُس کے پاس آ گیا",
  },
  {
    "surah_number": 80,
    "verse_number": 3,
    "content": "تمہیں کیا خبر، شاید وہ سدھر جائے",
  },
  {
    "surah_number": 80,
    "verse_number": 4,
    "content": "یا نصیحت پر دھیان دے، اور نصیحت کرنا اس کے لیے نافع ہو؟",
  },
  {
    "surah_number": 80,
    "verse_number": 5,
    "content": "جو شخص بے پروائی برتتا ہے",
  },
  {
    "surah_number": 80,
    "verse_number": 6,
    "content": "اس کی طرف تو تم توجہ کرتے ہو",
  },
  {
    "surah_number": 80,
    "verse_number": 7,
    "content": "حالانکہ اگر وہ نہ سدھرے تو تم پر اس کی کیا ذمہ داری ہے؟",
  },
  {
    "surah_number": 80,
    "verse_number": 8,
    "content": "اور جو خود تمہارے پاس دوڑا آتا ہے",
  },
  {
    "surah_number": 80,
    "verse_number": 9,
    "content": "اور وہ ڈر رہا ہوتا ہے",
  },
  {
    "surah_number": 80,
    "verse_number": 10,
    "content": "اس سے تم بے رخی برتتے ہو",
  },
  {
    "surah_number": 80,
    "verse_number": 11,
    "content": "ہرگز نہیں، یہ تو ایک نصیحت ہے",
  },
  {
    "surah_number": 80,
    "verse_number": 12,
    "content": "جس کا جی چاہے اِسے قبول کرے",
  },
  {
    "surah_number": 80,
    "verse_number": 13,
    "content": "یہ ایسے صحیفوں میں درج ہے جو مکرم ہیں",
  },
  {
    "surah_number": 80,
    "verse_number": 14,
    "content": "بلند مرتبہ ہیں، پاکیزہ ہیں",
  },
  {
    "surah_number": 80,
    "verse_number": 15,
    "content": "معزز اور نیک کاتبوں کے",
  },
  {
    "surah_number": 80,
    "verse_number": 16,
    "content": "ہاتھوں میں رہتے ہیں",
  },
  {
    "surah_number": 80,
    "verse_number": 17,
    "content": "لعنت ہو انسان پر، کیسا سخت منکر حق ہے یہ",
  },
  {
    "surah_number": 80,
    "verse_number": 18,
    "content": "کس چیز سے اللہ نے اِسے پیدا کیا ہے؟",
  },
  {
    "surah_number": 80,
    "verse_number": 19,
    "content": "نطفہ کی ایک بوند سے اللہ نے اِسے پیدا کیا، پھر اِس کی تقدیر مقرر کی",
  },
  {
    "surah_number": 80,
    "verse_number": 20,
    "content": "پھر اِس کے لیے زندگی کی راہ آسان کی",
  },
  {
    "surah_number": 80,
    "verse_number": 21,
    "content": "پھر اِسے موت دی اور قبر میں پہنچایا",
  },
  {
    "surah_number": 80,
    "verse_number": 22,
    "content": "پھر جب چاہے وہ اِسے دوبارہ اٹھا کھڑا کرے",
  },
  {
    "surah_number": 80,
    "verse_number": 23,
    "content": "ہرگز نہیں، اِس نے وہ فرض ادا نہیں کیا جس کا اللہ نے اِسے حکم دیا تھا",
  },
  {
    "surah_number": 80,
    "verse_number": 24,
    "content": "پھر ذرا انسان اپنی خوراک کو دیکھے",
  },
  {
    "surah_number": 80,
    "verse_number": 25,
    "content": "ہم نے خوب پانی لنڈھایا",
  },
  {
    "surah_number": 80,
    "verse_number": 26,
    "content": "پھر زمین کو عجیب طرح پھاڑا",
  },
  {
    "surah_number": 80,
    "verse_number": 27,
    "content": "پھر اُس کے اندر اگائے غلے",
  },
  {
    "surah_number": 80,
    "verse_number": 28,
    "content": "اور انگور اور ترکاریاں",
  },
  {
    "surah_number": 80,
    "verse_number": 29,
    "content": "اور زیتون اور کھجوریں",
  },
  {
    "surah_number": 80,
    "verse_number": 30,
    "content": "اور گھنے باغ",
  },
  {
    "surah_number": 80,
    "verse_number": 31,
    "content": "اور طرح طرح کے پھل، اور چارے",
  },
  {
    "surah_number": 80,
    "verse_number": 32,
    "content": "تمہارے لیے اور تمہارے مویشیوں کے لیے سامان زیست کے طور پر",
  },
  {
    "surah_number": 80,
    "verse_number": 33,
    "content": "آخرکار جب وہ کان بہرے کر دینے والی آواز بلند ہوگی",
  },
  {
    "surah_number": 80,
    "verse_number": 34,
    "content": "اُس روز آدمی اپنے بھائی",
  },
  {
    "surah_number": 80,
    "verse_number": 35,
    "content": "اور اپنی ماں اور اپنے باپ",
  },
  {
    "surah_number": 80,
    "verse_number": 36,
    "content": "اور اپنی بیوی اور اپنی اولاد سے بھاگے گا",
  },
  {
    "surah_number": 80,
    "verse_number": 37,
    "content": "ان میں سے ہر شخص پر اس دن ایسا وقت آ پڑے گا کہ اسے اپنے سوا کسی کا ہوش نہ ہوگا",
  },
  {
    "surah_number": 80,
    "verse_number": 38,
    "content": "کچھ چہرے اُس روز دمک رہے ہوں گے",
  },
  {
    "surah_number": 80,
    "verse_number": 39,
    "content": "ہشاش بشاش اور خوش و خرم ہوں گے",
  },
  {
    "surah_number": 80,
    "verse_number": 40,
    "content": "اور کچھ چہروں پر اس روز خاک اڑ رہی ہوگی",
  },
  {
    "surah_number": 80,
    "verse_number": 41,
    "content": "اور کلونس چھائی ہوئی ہوگی",
  },
  {
    "surah_number": 80,
    "verse_number": 42,
    "content": "یہی کافر و فاجر لوگ ہوں گے",
  },
  {
    "surah_number": 81,
    "verse_number": 1,
    "content": "جب سورج لپیٹ دیا جائے گا",
  },
  {
    "surah_number": 81,
    "verse_number": 2,
    "content": "اور جب تارے بکھر جائیں گے",
  },
  {
    "surah_number": 81,
    "verse_number": 3,
    "content": "اور جب پہاڑ چلائے جائیں گے",
  },
  {
    "surah_number": 81,
    "verse_number": 4,
    "content": "اور جب دس مہینے کی حاملہ اونٹنیاں اپنے حال پر چھوڑ دی جائیں گی",
  },
  {
    "surah_number": 81,
    "verse_number": 5,
    "content": "اور جب جنگلی جانور سمیٹ کر اکٹھے کر دیے جائیں گے",
  },
  {
    "surah_number": 81,
    "verse_number": 6,
    "content": "اور جب سمندر بھڑکا دیے جائیں گے",
  },
  {
    "surah_number": 81,
    "verse_number": 7,
    "content": "اور جب جانیں (جسموں سے) جوڑ دی جائیں گی",
  },
  {
    "surah_number": 81,
    "verse_number": 8,
    "content": "اور جب زندہ گاڑی ہوئی لڑکی سے پوچھا جائے گا",
  },
  {
    "surah_number": 81,
    "verse_number": 9,
    "content": "کہ وہ کس قصور میں ماری گئی؟",
  },
  {
    "surah_number": 81,
    "verse_number": 10,
    "content": "اور جب اعمال نامے کھولے جائیں گے",
  },
  {
    "surah_number": 81,
    "verse_number": 11,
    "content": "اور جب آسمان کا پردہ ہٹا دیا جائے گا",
  },
  {
    "surah_number": 81,
    "verse_number": 12,
    "content": "اور جب جہنم دہکائی جائے گی",
  },
  {
    "surah_number": 81,
    "verse_number": 13,
    "content": "اور جب جنت قریب لے آئی جائے گی",
  },
  {
    "surah_number": 81,
    "verse_number": 14,
    "content": "اُس وقت ہر شخص کو معلوم ہو جائے گا کہ وہ کیا لے کر آیا ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 15,
    "content": "پس نہیں، میں قسم کھاتا ہوں",
  },
  {
    "surah_number": 81,
    "verse_number": 16,
    "content": "پلٹنے اور چھپ جانے والے تاروں کی",
  },
  {
    "surah_number": 81,
    "verse_number": 17,
    "content": "اور رات کی جبکہ وہ رخصت ہوئی",
  },
  {
    "surah_number": 81,
    "verse_number": 18,
    "content": "اور صبح کی جبکہ اس نے سانس لیا",
  },
  {
    "surah_number": 81,
    "verse_number": 19,
    "content": "یہ فی الواقع ایک بزرگ پیغام بر کا قول ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 20,
    "content": "جو بڑی توانائی رکھتا ہے، عرش والے کے ہاں بلند مرتبہ ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 21,
    "content": "وہاں اُس کا حکم مانا جاتا ہے، وہ با اعتماد ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 22,
    "content": "اور (اے اہل مکہ) تمہارا رفیق مجنون نہیں ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 23,
    "content": "اُس نے اُس پیغام بر کو روشن افق پر دیکھا ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 24,
    "content": "اور وہ غیب (کے اِس علم کو لوگوں تک پہنچانے) کے معاملے میں بخیل نہیں ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 25,
    "content": "اور یہ کسی شیطان مردود کا قول نہیں ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 26,
    "content": "پھر تم لوگ کدھر چلے جا رہے ہو؟",
  },
  {
    "surah_number": 81,
    "verse_number": 27,
    "content": "یہ تو سارے جہان والوں کے لیے ایک نصیحت ہے",
  },
  {
    "surah_number": 81,
    "verse_number": 28,
    "content": "تم میں سے ہر اُس شخص کے لیے جو راہ راست پر چلنا چاہتا ہو",
  },
  {
    "surah_number": 81,
    "verse_number": 29,
    "content": "اور تمہارے چاہنے سے کچھ نہیں ہوتا جب تک اللہ رب العالمین نہ چاہے",
  },
  {
    "surah_number": 82,
    "verse_number": 1,
    "content": "جب آسمان پھٹ جائے گا",
  },
  {
    "surah_number": 82,
    "verse_number": 2,
    "content": "اور جب تارے بکھر جائیں گے",
  },
  {
    "surah_number": 82,
    "verse_number": 3,
    "content": "اور جب سمندر پھاڑ دیے جائیں گے",
  },
  {
    "surah_number": 82,
    "verse_number": 4,
    "content": "اور جب قبریں کھول دی جائیں گی",
  },
  {
    "surah_number": 82,
    "verse_number": 5,
    "content": "اُس وقت ہر شخص کو اُس کا اگلا پچھلا سب کیا دھرا معلوم ہو جائے گا",
  },
  {
    "surah_number": 82,
    "verse_number": 6,
    "content": "اے انسان، کس چیز نے تجھے اپنے اُس رب کریم کی طرف سے دھوکے میں ڈال دیا",
  },
  {
    "surah_number": 82,
    "verse_number": 7,
    "content": "جس نے تجھے پیدا کیا، تجھے نک سک سے درست کیا، تجھے متناسب بنایا",
  },
  {
    "surah_number": 82,
    "verse_number": 8,
    "content": "اور جس صورت میں چاہا تجھ کو جوڑ کر تیار کیا؟",
  },
  {
    "surah_number": 82,
    "verse_number": 9,
    "content": "ہرگز نہیں، بلکہ (اصل بات یہ ہے کہ) تم لوگ جزا و سزا کو جھٹلاتے ہو",
  },
  {
    "surah_number": 82,
    "verse_number": 10,
    "content": "حالانکہ تم پر نگراں مقرر ہیں",
  },
  {
    "surah_number": 82,
    "verse_number": 11,
    "content": "ایسے معزز کاتب",
  },
  {
    "surah_number": 82,
    "verse_number": 12,
    "content": "جو تمہارے ہر فعل کو جانتے ہیں",
  },
  {
    "surah_number": 82,
    "verse_number": 13,
    "content": "یقیناً نیک لوگ مزے میں ہوں گے",
  },
  {
    "surah_number": 82,
    "verse_number": 14,
    "content": "اور بے شک بدکار لوگ جہنم میں جائیں گے",
  },
  {
    "surah_number": 82,
    "verse_number": 15,
    "content": "جزا کے دن وہ اس میں داخل ہوں گے",
  },
  {
    "surah_number": 82,
    "verse_number": 16,
    "content": "اور اُس سے ہرگز غائب نہ ہو سکیں گے",
  },
  {
    "surah_number": 82,
    "verse_number": 17,
    "content": "اور تم کیا جانتے ہو کہ وہ جزا کا دن کیا ہے؟",
  },
  {
    "surah_number": 82,
    "verse_number": 18,
    "content": "ہاں، تمہیں کیا خبر کہ وہ جزا کا دن کیا ہے؟",
  },
  {
    "surah_number": 82,
    "verse_number": 19,
    "content": "یہ وہ دن ہے جب کسی شخص کے لیے کچھ کرنا کسی کے بس میں نہ ہوگا، فیصلہ اُس دن بالکل اللہ کے اختیار میں ہوگا",
  },
  {
    "surah_number": 83,
    "verse_number": 1,
    "content": "تباہی ہے ڈنڈی مارنے والوں کے لیے",
  },
  {
    "surah_number": 83,
    "verse_number": 2,
    "content": "جن کا حال یہ ہے کہ جب لوگوں سے لیتے ہیں تو پورا پورا لیتے ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 3,
    "content": "اور جب ان کو ناپ کر یا تول کر دیتے ہیں تو انہیں گھاٹا دیتے ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 4,
    "content": "کیا یہ لوگ نہیں سمجھتے کہ ایک بڑے دن،",
  },
  {
    "surah_number": 83,
    "verse_number": 5,
    "content": "یہ اٹھا کر لائے جانے والے ہیں؟",
  },
  {
    "surah_number": 83,
    "verse_number": 6,
    "content": "اُس دن جبکہ سب لوگ رب العالمین کے سامنے کھڑے ہوں گے",
  },
  {
    "surah_number": 83,
    "verse_number": 7,
    "content": "ہرگز نہیں، یقیناً بد کاروں کا نامہ اعمال قید خانے کے دفتر میں ہے",
  },
  {
    "surah_number": 83,
    "verse_number": 8,
    "content": "اور تمہیں کیا معلوم کہ وہ قید خانے کا دفتر کیا ہے؟",
  },
  {
    "surah_number": 83,
    "verse_number": 9,
    "content": "ایک کتاب ہے لکھی ہوئی",
  },
  {
    "surah_number": 83,
    "verse_number": 10,
    "content": "تباہی ہے اُس روز جھٹلانے والوں کے لیے",
  },
  {
    "surah_number": 83,
    "verse_number": 11,
    "content": "جو روز جزا کو جھٹلاتے ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 12,
    "content": "اور اُسے نہیں جھٹلاتا مگر ہر وہ شخص جو حد سے گزر جانے والا بد عمل ہے",
  },
  {
    "surah_number": 83,
    "verse_number": 13,
    "content": "اُسے جب ہماری آیات سنائی جاتی ہیں تو کہتا ہے یہ تو اگلے وقتوں کی کہانیاں ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 14,
    "content": "ہرگز نہیں، بلکہ دراصل اِن لوگوں کے دلوں پر اِن کے برے اعمال کا زنگ چڑھ گیا ہے",
  },
  {
    "surah_number": 83,
    "verse_number": 15,
    "content": "ہرگز نہیں، بالیقین اُس روز یہ اپنے رب کی دید سے محروم رکھے جائیں گے",
  },
  {
    "surah_number": 83,
    "verse_number": 16,
    "content": "پھر یہ جہنم میں جا پڑیں گے",
  },
  {
    "surah_number": 83,
    "verse_number": 17,
    "content": "پھر اِن سے کہا جائے گا کہ یہ وہی چیز ہے جسے تم جھٹلایا کرتے تھے",
  },
  {
    "surah_number": 83,
    "verse_number": 18,
    "content": "ہرگز نہیں، بے شک نیک آدمیوں کا نامہ اعمال بلند پایہ لوگوں کے دفتر میں ہے",
  },
  {
    "surah_number": 83,
    "verse_number": 19,
    "content": "اور تمہیں کیا خبر کہ کیا ہے وہ بلند پایہ لوگوں کا دفتر؟",
  },
  {
    "surah_number": 83,
    "verse_number": 20,
    "content": "ایک لکھی ہوئی کتاب ہے",
  },
  {
    "surah_number": 83,
    "verse_number": 21,
    "content": "جس کی نگہداشت مقرب فرشتے کرتے ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 22,
    "content": "بے شک نیک لوگ بڑے مزے میں ہوں گے",
  },
  {
    "surah_number": 83,
    "verse_number": 23,
    "content": "اونچی مسندوں پر بیٹھے نظارے کر رہے ہوں گے",
  },
  {
    "surah_number": 83,
    "verse_number": 24,
    "content": "ان کے چہروں پر تم خوشحالی کی رونق محسوس کرو گے",
  },
  {
    "surah_number": 83,
    "verse_number": 25,
    "content": "ان کو نفیس ترین سر بند شراب پلائی جائے گی جس پر مشک کی مہر لگی ہوگی",
  },
  {
    "surah_number": 83,
    "verse_number": 26,
    "content": "جو لوگ دوسروں پر بازی لے جانا چاہتے ہوں وہ اِس چیز کو حاصل کرنے میں بازی لے جانے کی کوشش کریں",
  },
  {
    "surah_number": 83,
    "verse_number": 27,
    "content": "اُس شراب میں تسنیم کی آمیزش ہوگی",
  },
  {
    "surah_number": 83,
    "verse_number": 28,
    "content": "یہ ایک چشمہ ہے جس کے پانی کے ساتھ مقرب لوگ شراب پئیں گے",
  },
  {
    "surah_number": 83,
    "verse_number": 29,
    "content": "مجرم لوگ دنیا میں ایمان لانے والوں کا مذاق اڑاتے تھے",
  },
  {
    "surah_number": 83,
    "verse_number": 30,
    "content": "جب اُن کے پاس سے گزرتے تو آنکھیں مار مار کر اُن کی طرف اشارے کرتے تھے",
  },
  {
    "surah_number": 83,
    "verse_number": 31,
    "content": "اپنے گھروں کی طرف پلٹتے تو مزے لیتے ہوئے پلٹتے تھے",
  },
  {
    "surah_number": 83,
    "verse_number": 32,
    "content": "اور جب انہیں دیکھتے تو کہتے تھے کہ یہ بہکے ہوئے لوگ ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 33,
    "content": "حالانکہ وہ اُن پر نگراں بنا کر نہیں بھیجے گئے تھے",
  },
  {
    "surah_number": 83,
    "verse_number": 34,
    "content": "آج ایمان لانے والے کفار پر ہنس رہے ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 35,
    "content": "مسندوں پر بیٹھے ہوئے ان کا حال دیکھ رہے ہیں",
  },
  {
    "surah_number": 83,
    "verse_number": 36,
    "content": "مل گیا نا کافروں کو اُن حرکتوں کا ثواب جو وہ کیا کرتے تھے",
  },
  {
    "surah_number": 84,
    "verse_number": 1,
    "content": "جب آسمان پھٹ جائے گا",
  },
  {
    "surah_number": 84,
    "verse_number": 2,
    "content": "اور اپنے رب کے فرمان کی تعمیل کرے گا اور اُس کے لیے حق یہی ہے (کہ اپنے رب کا حکم مانے)",
  },
  {
    "surah_number": 84,
    "verse_number": 3,
    "content": "اور جب زمین پھیلا دی جائے گی",
  },
  {
    "surah_number": 84,
    "verse_number": 4,
    "content": "اور جو کچھ اس کے اندر ہے اُسے باہر پھینک کر خالی ہو جائے گی",
  },
  {
    "surah_number": 84,
    "verse_number": 5,
    "content": "اور اپنے رب کے حکم کی تعمیل کرے گی اور اُس کے لیے حق یہی ہے (کہ اس کی تعمیل کرے)",
  },
  {
    "surah_number": 84,
    "verse_number": 6,
    "content": "اے انسان، تو کشاں کشاں اپنے رب کی طرف چلا جا رہا ہے، اور اُس سے ملنے والا ہے",
  },
  {
    "surah_number": 84,
    "verse_number": 7,
    "content": "پھر جس کا نامہ اعمال اُس کے سیدھے ہاتھ میں دیا گیا",
  },
  {
    "surah_number": 84,
    "verse_number": 8,
    "content": "اُس سے ہلکا حساب لیا جائے گا",
  },
  {
    "surah_number": 84,
    "verse_number": 9,
    "content": "اور وہ اپنے لوگوں کی طرف خوش خوش پلٹے گا",
  },
  {
    "surah_number": 84,
    "verse_number": 10,
    "content": "رہا وہ شخص جس کا نامہ اعمال اُس کی پیٹھ کے پیچھے دیا جائے گا",
  },
  {
    "surah_number": 84,
    "verse_number": 11,
    "content": "تو وہ موت کو پکارے گا",
  },
  {
    "surah_number": 84,
    "verse_number": 12,
    "content": "اور بھڑکتی ہوئی آگ میں جا پڑے گا",
  },
  {
    "surah_number": 84,
    "verse_number": 13,
    "content": "وہ اپنے گھر والوں میں مگن تھا",
  },
  {
    "surah_number": 84,
    "verse_number": 14,
    "content": "اُس نے سمجھا تھا کہ اسے کبھی پلٹنا نہیں ہے",
  },
  {
    "surah_number": 84,
    "verse_number": 15,
    "content": "پلٹنا کیسے نہ تھا، اُس کا رب اُس کے کرتوت دیکھ رہا تھا",
  },
  {
    "surah_number": 84,
    "verse_number": 16,
    "content": "پس نہیں، میں قسم کھاتا ہوں شفق کی",
  },
  {
    "surah_number": 84,
    "verse_number": 17,
    "content": "اور رات کی اور جو کچھ وہ سمیٹ لیتی ہے",
  },
  {
    "surah_number": 84,
    "verse_number": 18,
    "content": "اور چاند کی جب کہ وہ ماہ کامل ہو جاتا ہے",
  },
  {
    "surah_number": 84,
    "verse_number": 19,
    "content": "تم کو ضرور درجہ بدرجہ ایک حالت سے دوسری حالت کی طرف گزرتے چلے جانا ہے",
  },
  {
    "surah_number": 84,
    "verse_number": 20,
    "content": "پھر اِن لوگوں کو کیا ہو گیا ہے کہ یہ ایمان نہیں لاتے",
  },
  {
    "surah_number": 84,
    "verse_number": 21,
    "content": "اور جب قرآن اِن کے سامنے پڑھا جاتا ہے تو سجدہ نہیں کرتے؟",
  },
  {
    "surah_number": 84,
    "verse_number": 22,
    "content": "بلکہ یہ منکرین تو الٹا جھٹلاتے ہیں",
  },
  {
    "surah_number": 84,
    "verse_number": 23,
    "content": "حالانکہ جو کچھ یہ (اپنے نامہ اعمال میں) جمع کر رہے ہیں اللہ اُسے خوب جانتا ہے",
  },
  {
    "surah_number": 84,
    "verse_number": 24,
    "content": "لہٰذا اِن کو دردناک عذاب کی بشارت دے دو",
  },
  {
    "surah_number": 84,
    "verse_number": 25,
    "content": "البتہ جو لوگ ایمان لے آئے ہیں اور جنہوں نے نیک عمل کیے ہیں ان کے لیے کبھی ختم نہ ہونے والا اجر ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 1,
    "content": "قسم ہے مضبوط قلعوں والے آسمان کی",
  },
  {
    "surah_number": 85,
    "verse_number": 2,
    "content": "اور اُس دن کی جس کا وعدہ کیا گیا ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 3,
    "content": "اور دیکھنے والے کی اور دیکھی جانے والی چیز کی",
  },
  {
    "surah_number": 85,
    "verse_number": 4,
    "content": "کہ مارے گئے گڑھے والے",
  },
  {
    "surah_number": 85,
    "verse_number": 5,
    "content": "(اُس گڑھے والے) جس میں خوب بھڑکتے ہوئے ایندھن کی آگ تھی",
  },
  {
    "surah_number": 85,
    "verse_number": 6,
    "content": "جبکہ وہ اُس گڑھے کے کنارے بیٹھے ہوئے تھے",
  },
  {
    "surah_number": 85,
    "verse_number": 7,
    "content": "اور جو کچھ وہ ایمان لانے والوں کے ساتھ کر رہے تھے اُسے دیکھ رہے تھے",
  },
  {
    "surah_number": 85,
    "verse_number": 8,
    "content": "اور اُن اہل ایمان سے اُن کی دشمنی اِس کے سوا کسی وجہ سے نہ تھی کہ وہ اُس خدا پر ایمان لے آئے تھے جو زبردست اور اپنی ذات میں آپ محمود ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 9,
    "content": "جو آسمانوں اور زمین کی سلطنت کا مالک ہے، اور وہ خدا سب کچھ دیکھ رہا ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 10,
    "content": "جن لوگوں نے مومن مردوں اور عورتوں پر ظلم و ستم توڑا اور پھر اس سے تائب نہ ہوئے، یقیناً اُن کے لیے جہنم کا عذاب ہے اور ان کے لیے جلائے جانے کی سزا ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 11,
    "content": "جو لوگ ایمان لائے اور جنہوں نے نیک عمل کیے، یقیناً اُن کے لیے جنت کے باغ ہیں جن کے نیچے نہریں بہتی ہوں گی، یہ ہے بڑی کامیابی",
  },
  {
    "surah_number": 85,
    "verse_number": 12,
    "content": "درحقیقت تمہارے رب کی پکڑ بڑی سخت ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 13,
    "content": "وہی پہلی بار پیدا کرتا ہے اور وہی دوبارہ پیدا کرے گا",
  },
  {
    "surah_number": 85,
    "verse_number": 14,
    "content": "اور وہ بخشنے والا ہے، محبت کرنے والا ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 15,
    "content": "عرش کا مالک ہے، بزرگ و برتر ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 16,
    "content": "اور جو کچھ چاہے کر ڈالنے والا ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 17,
    "content": "کیا تمہیں لشکروں کی خبر پہنچی ہے؟",
  },
  {
    "surah_number": 85,
    "verse_number": 18,
    "content": "فرعون اور ثمود (کے لشکروں) کی؟",
  },
  {
    "surah_number": 85,
    "verse_number": 19,
    "content": "مگر جنہوں نے کفر کیا ہے وہ جھٹلانے میں لگے ہوئے ہیں",
  },
  {
    "surah_number": 85,
    "verse_number": 20,
    "content": "حالانکہ اللہ نے ان کو گھیرے میں لے رکھا ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 21,
    "content": "(اُن کے جھٹلانے سے اِس قرآن کا کچھ نہیں بگڑتا) بلکہ یہ قرآن بلند پایہ ہے",
  },
  {
    "surah_number": 85,
    "verse_number": 22,
    "content": "اُس لوح میں (نقش ہے) جو محفوظ ہے",
  },
  {
    "surah_number": 86,
    "verse_number": 1,
    "content": "قسم ہے آسمان کی اور رات کو نمودار ہونے والے کی",
  },
  {
    "surah_number": 86,
    "verse_number": 2,
    "content": "اور تم کیا جانو کہ وہ رات کو نمودار ہونے والا کیا ہے؟",
  },
  {
    "surah_number": 86,
    "verse_number": 3,
    "content": "چمکتا ہوا تارا",
  },
  {
    "surah_number": 86,
    "verse_number": 4,
    "content": "کوئی جان ایسی نہیں ہے جس کے اوپر کوئی نگہبان نہ ہو",
  },
  {
    "surah_number": 86,
    "verse_number": 5,
    "content": "پھر ذرا انسان یہی دیکھ لے کہ وہ کس چیز سے پیدا کیا گیا ہے",
  },
  {
    "surah_number": 86,
    "verse_number": 6,
    "content": "ایک اچھلنے والے پانی سے پیدا کیا گیا ہے",
  },
  {
    "surah_number": 86,
    "verse_number": 7,
    "content": "جو پیٹھ اور سینے کی ہڈیوں کے درمیان سے نکلتا ہے",
  },
  {
    "surah_number": 86,
    "verse_number": 8,
    "content": "یقیناً وہ (خالق) اُسے دوبارہ پیدا کرنے پر قادر ہے",
  },
  {
    "surah_number": 86,
    "verse_number": 9,
    "content": "جس روز پوشیدہ اسرار کی جانچ پڑتال ہوگی",
  },
  {
    "surah_number": 86,
    "verse_number": 10,
    "content": "اُس وقت انسان کے پاس نہ خود اپنا کوئی زور ہوگا اور نہ کوئی اس کی مدد کرنے والا ہوگا",
  },
  {
    "surah_number": 86,
    "verse_number": 11,
    "content": "قسم ہے بارش برسانے والے آسمان کی",
  },
  {
    "surah_number": 86,
    "verse_number": 12,
    "content": "اور (نباتات اگتے وقت) پھٹ جانے والی زمین کی",
  },
  {
    "surah_number": 86,
    "verse_number": 13,
    "content": "یہ ایک جچی تلی بات ہے",
  },
  {
    "surah_number": 86,
    "verse_number": 14,
    "content": "ہنسی مذاق نہیں ہے",
  },
  {
    "surah_number": 86,
    "verse_number": 15,
    "content": "یہ لوگ چالیں چل رہے ہیں",
  },
  {
    "surah_number": 86,
    "verse_number": 16,
    "content": "اور میں بھی ایک چال چل رہا ہوں",
  },
  {
    "surah_number": 86,
    "verse_number": 17,
    "content": "پس چھوڑ دو اے نبیؐ، اِن کافروں کو اک ذرا کی ذرا اِن کے حال پر چھوڑ دو",
  },
  {
    "surah_number": 87,
    "verse_number": 1,
    "content": "(اے نبیؐ) اپنے رب برتر کے نام کی تسبیح کرو",
  },
  {
    "surah_number": 87,
    "verse_number": 2,
    "content": "جس نے پیدا کیا اور تناسب قائم کیا",
  },
  {
    "surah_number": 87,
    "verse_number": 3,
    "content": "جس نے تقدیر بنائی پھر راہ دکھائی",
  },
  {
    "surah_number": 87,
    "verse_number": 4,
    "content": "جس نے نباتات اگائیں",
  },
  {
    "surah_number": 87,
    "verse_number": 5,
    "content": "پھر اُن کو سیاہ کوڑا کرکٹ بنا دیا",
  },
  {
    "surah_number": 87,
    "verse_number": 6,
    "content": "ہم تمہیں پڑھوا دیں گے، پھر تم نہیں بھولو گے",
  },
  {
    "surah_number": 87,
    "verse_number": 7,
    "content": "سوائے اُس کے جو اللہ چاہے، وہ ظاہر کو بھی جانتا ہے اور جو کچھ پوشیدہ ہے اُس کو بھی",
  },
  {
    "surah_number": 87,
    "verse_number": 8,
    "content": "اور ہم تمہیں آسان طریقے کی سہولت دیتے ہیں",
  },
  {
    "surah_number": 87,
    "verse_number": 9,
    "content": "لہٰذا تم نصیحت کرو اگر نصیحت نافع ہو",
  },
  {
    "surah_number": 87,
    "verse_number": 10,
    "content": "جو شخص ڈرتا ہے وہ نصیحت قبول کر لے گا",
  },
  {
    "surah_number": 87,
    "verse_number": 11,
    "content": "اور اس سے گریز کریگا وہ انتہائی بد بخت",
  },
  {
    "surah_number": 87,
    "verse_number": 12,
    "content": "جو بڑی آگ میں جائے گا",
  },
  {
    "surah_number": 87,
    "verse_number": 13,
    "content": "پھر نہ اس میں مرے گا نہ جیے گا",
  },
  {
    "surah_number": 87,
    "verse_number": 14,
    "content": "فلاح پا گیا وہ جس نے پاکیزگی اختیار کی",
  },
  {
    "surah_number": 87,
    "verse_number": 15,
    "content": "اور اپنے رب کا نام یاد کیا پھر نماز پڑھی",
  },
  {
    "surah_number": 87,
    "verse_number": 16,
    "content": "مگر تم لوگ دنیا کی زندگی کو ترجیح دیتے ہو",
  },
  {
    "surah_number": 87,
    "verse_number": 17,
    "content": "حالانکہ آخرت بہتر ہے اور باقی رہنے والی ہے",
  },
  {
    "surah_number": 87,
    "verse_number": 18,
    "content": "یہی بات پہلے آئے ہوئے صحیفوں میں بھی کہی گئی تھی",
  },
  {
    "surah_number": 87,
    "verse_number": 19,
    "content": "ابراہیمؑ اور موسیٰؑ کے صحیفوں میں",
  },
  {
    "surah_number": 88,
    "verse_number": 1,
    "content": "کیا تمہیں اُس چھا جانے والی آفت کی خبر پہنچی ہے؟",
  },
  {
    "surah_number": 88,
    "verse_number": 2,
    "content": "کچھ چہرے اُس روز خوف زدہ ہونگے",
  },
  {
    "surah_number": 88,
    "verse_number": 3,
    "content": "سخت مشقت کر رہے ہونگے",
  },
  {
    "surah_number": 88,
    "verse_number": 4,
    "content": "تھکے جاتے ہونگے، شدید آگ میں جھلس رہے ہونگے",
  },
  {
    "surah_number": 88,
    "verse_number": 5,
    "content": "کھولتے ہوئے چشمے کا پانی انہیں پینے کو دیا جائے گا",
  },
  {
    "surah_number": 88,
    "verse_number": 6,
    "content": "خار دار سوکھی گھاس کے سوا کوئی کھانا اُن کے لیے نہ ہوگا",
  },
  {
    "surah_number": 88,
    "verse_number": 7,
    "content": "جو نہ موٹا کرے نہ بھوک مٹائے",
  },
  {
    "surah_number": 88,
    "verse_number": 8,
    "content": "کچھ چہرے اُس روز با رونق ہوں گے",
  },
  {
    "surah_number": 88,
    "verse_number": 9,
    "content": "اپنی کار گزاری پر خوش ہونگے",
  },
  {
    "surah_number": 88,
    "verse_number": 10,
    "content": "عالی مقام جنت میں ہوں گے",
  },
  {
    "surah_number": 88,
    "verse_number": 11,
    "content": "کوئی بیہودہ بات وہاں نہ سنیں گے",
  },
  {
    "surah_number": 88,
    "verse_number": 12,
    "content": "اُس میں چشمے رواں ہونگے",
  },
  {
    "surah_number": 88,
    "verse_number": 13,
    "content": "اُس کے اندر اونچی مسندیں ہوں گی",
  },
  {
    "surah_number": 88,
    "verse_number": 14,
    "content": "ساغر رکھے ہوئے ہوں گے",
  },
  {
    "surah_number": 88,
    "verse_number": 15,
    "content": "گاؤ تکیوں کی قطاریں لگی ہوں گی",
  },
  {
    "surah_number": 88,
    "verse_number": 16,
    "content": "اور نفیس فرش بچھے ہوئے ہوں گے",
  },
  {
    "surah_number": 88,
    "verse_number": 17,
    "content": "(یہ لوگ نہیں مانتے) تو کیا یہ اونٹوں کو نہیں دیکھتے کہ کیسے بنائے گئے؟",
  },
  {
    "surah_number": 88,
    "verse_number": 18,
    "content": "آسمان کو نہیں دیکھتے کہ کیسے اٹھایا گیا؟",
  },
  {
    "surah_number": 88,
    "verse_number": 19,
    "content": "پہاڑوں کو نہیں دیکھتے کہ کیسے جمائے گئے؟",
  },
  {
    "surah_number": 88,
    "verse_number": 20,
    "content": "اور زمین کو نہیں دیکھتے کہ کیسے بچھائی گئی؟",
  },
  {
    "surah_number": 88,
    "verse_number": 21,
    "content": "اچھا تو (اے نبیؐ) نصیحت کیے جاؤ، تم بس نصیحت ہی کرنے والے ہو",
  },
  {
    "surah_number": 88,
    "verse_number": 22,
    "content": "کچھ ان پر جبر کرنے والے نہیں ہو",
  },
  {
    "surah_number": 88,
    "verse_number": 23,
    "content": "البتہ جو شخص منہ موڑے گا اور انکار کرے گا",
  },
  {
    "surah_number": 88,
    "verse_number": 24,
    "content": "تو اللہ اس کو بھاری سزا دے گا",
  },
  {
    "surah_number": 88,
    "verse_number": 25,
    "content": "اِن لوگوں کو پلٹنا ہماری طرف ہی ہے",
  },
  {
    "surah_number": 88,
    "verse_number": 26,
    "content": "پھر اِن کا حساب لینا ہمارے ہی ذمہ ہے",
  },
  {
    "surah_number": 89,
    "verse_number": 1,
    "content": "قسم ہے فجر کی",
  },
  {
    "surah_number": 89,
    "verse_number": 2,
    "content": "اور دس راتوں کی",
  },
  {
    "surah_number": 89,
    "verse_number": 3,
    "content": "اور جفت اور طاق کی",
  },
  {
    "surah_number": 89,
    "verse_number": 4,
    "content": "اور رات کی جبکہ وہ رخصت ہو رہی ہو",
  },
  {
    "surah_number": 89,
    "verse_number": 5,
    "content": "کیا اِس میں کسی صاحب عقل کے لیے کوئی قسم ہے؟",
  },
  {
    "surah_number": 89,
    "verse_number": 6,
    "content": "تم نے دیکھا نہیں کہ تمہارے رب نے کیا برتاؤ کیا",
  },
  {
    "surah_number": 89,
    "verse_number": 7,
    "content": "اونچے ستونوں والے عاد ارم کے ساتھ",
  },
  {
    "surah_number": 89,
    "verse_number": 8,
    "content": "جن کے مانند کوئی قوم دنیا کے ملکوں میں پیدا نہیں کی گئی تھی؟",
  },
  {
    "surah_number": 89,
    "verse_number": 9,
    "content": "اور ثمود کے ساتھ جنہوں نے وادی میں چٹانیں تراشی تھیں؟",
  },
  {
    "surah_number": 89,
    "verse_number": 10,
    "content": "اور میخوں والے فرعون کے ساتھ؟",
  },
  {
    "surah_number": 89,
    "verse_number": 11,
    "content": "یہ وہ لوگ تھے جنہوں نے دنیا کے ملکوں میں بڑی سرکشی کی تھی",
  },
  {
    "surah_number": 89,
    "verse_number": 12,
    "content": "اور ان میں بہت فساد پھیلایا تھا",
  },
  {
    "surah_number": 89,
    "verse_number": 13,
    "content": "آخرکار تمہارے رب نے ان پر عذاب کا کوڑا برسا دیا",
  },
  {
    "surah_number": 89,
    "verse_number": 14,
    "content": "حقیقت یہ ہے کہ تمہارا رب گھات لگائے ہوئے ہے",
  },
  {
    "surah_number": 89,
    "verse_number": 15,
    "content": "مگر انسان کا حال یہ ہے کہ اس کا رب جب اُس کو آزمائش میں ڈالتا ہے اور اُسے عزت اور نعمت دیتا ہے تو وہ کہتا ہے کہ میرے رب نے مجھے عزت دار بنا دیا",
  },
  {
    "surah_number": 89,
    "verse_number": 16,
    "content": "اور جب وہ اُس کو آزمائش میں ڈالتا ہے اور اُس کا رزق اُس پر تنگ کر دیتا ہے تو وہ کہتا ہے کہ میرے رب نے مجھے ذلیل کر دیا",
  },
  {
    "surah_number": 89,
    "verse_number": 17,
    "content": "ہرگز نہیں، بلکہ تم یتیم سے عزت کا سلوک نہیں کرتے",
  },
  {
    "surah_number": 89,
    "verse_number": 18,
    "content": "اور مسکین کو کھانا کھلانے پر ایک دوسرے کو نہیں اکساتے",
  },
  {
    "surah_number": 89,
    "verse_number": 19,
    "content": "اور میراث کا سارا مال سمیٹ کر کھا جاتے ہو",
  },
  {
    "surah_number": 89,
    "verse_number": 20,
    "content": "اور مال کی محبت میں بری طرح گرفتار ہو",
  },
  {
    "surah_number": 89,
    "verse_number": 21,
    "content": "ہرگز نہیں، جب زمین پے در پے کوٹ کوٹ کر ریگ زار بنا دی جائے گی",
  },
  {
    "surah_number": 89,
    "verse_number": 22,
    "content": "اور تمہارا رب جلوہ فرما ہوگا اِس حال میں کہ فرشتے صف در صف کھڑے ہوں گے",
  },
  {
    "surah_number": 89,
    "verse_number": 23,
    "content": "اور جہنم اُس روز سامنے لے آئی جائے گی، اُس دن انسان کو سمجھ آئے گی اور اس وقت اُس کے سمجھنے کا کیا حاصل؟",
  },
  {
    "surah_number": 89,
    "verse_number": 24,
    "content": "وہ کہے گا کہ کاش میں نے اپنی اِس زندگی کے لیے کچھ پیشگی سامان کیا ہوتا",
  },
  {
    "surah_number": 89,
    "verse_number": 25,
    "content": "پھر اُس دن اللہ جو عذاب دے گا ویسا عذاب دینے والا کوئی نہیں",
  },
  {
    "surah_number": 89,
    "verse_number": 26,
    "content": "اور اللہ جیسا باندھے گا ویسا باندھنے والا کوئی نہیں",
  },
  {
    "surah_number": 89,
    "verse_number": 27,
    "content": "(دوسری طرف ارشاد ہوگا) اے نفس مطمئن",
  },
  {
    "surah_number": 89,
    "verse_number": 28,
    "content": "چل اپنے رب کی طرف، اِس حال میں کہ تو (اپنے انجام نیک سے) خوش (اور اپنے رب کے نزدیک) پسندیدہ ہے",
  },
  {
    "surah_number": 89,
    "verse_number": 29,
    "content": "شامل ہو جا میرے (نیک) بندوں میں",
  },
  {
    "surah_number": 89,
    "verse_number": 30,
    "content": "اور داخل ہو جا میری جنت میں",
  },
  {
    "surah_number": 90,
    "verse_number": 1,
    "content": "نہیں، میں قسم کھاتا ہوں اِس شہر کی",
  },
  {
    "surah_number": 90,
    "verse_number": 2,
    "content": "اور حال یہ ہے کہ (اے نبیؐ) اِس شہر میں تم کو حلال کر لیا گیا ہے",
  },
  {
    "surah_number": 90,
    "verse_number": 3,
    "content": "اور قسم کھاتا ہوں باپ کی اور اس اولاد کی جو اس سے پیدا ہوئی",
  },
  {
    "surah_number": 90,
    "verse_number": 4,
    "content": "درحقیقت ہم نے انسان کو مشقت میں پیدا کیا ہے",
  },
  {
    "surah_number": 90,
    "verse_number": 5,
    "content": "کیا اُس نے یہ سمجھ رکھا ہے کہ اُس پر کوئی قابو نہ پا سکے گا؟",
  },
  {
    "surah_number": 90,
    "verse_number": 6,
    "content": "کہتا ہے کہ میں نے ڈھیروں مال اڑا دیا",
  },
  {
    "surah_number": 90,
    "verse_number": 7,
    "content": "کیا وہ سمجھتا ہے کہ کسی نے اُس کو نہیں دیکھا؟",
  },
  {
    "surah_number": 90,
    "verse_number": 8,
    "content": "کیا ہم نے اُسے دو آنکھیں",
  },
  {
    "surah_number": 90,
    "verse_number": 9,
    "content": "اور ایک زبان اور دو ہونٹ نہیں دیے؟",
  },
  {
    "surah_number": 90,
    "verse_number": 10,
    "content": "اور دونوں نمایاں راستے اُسے (نہیں) دکھا دیے؟",
  },
  {
    "surah_number": 90,
    "verse_number": 11,
    "content": "مگر اس نے دشوار گزار گھاٹی سے گزرنے کی ہمت نہ کی",
  },
  {
    "surah_number": 90,
    "verse_number": 12,
    "content": "اور تم کیا جانو کہ کیا ہے وہ دشوار گزار گھاٹی؟",
  },
  {
    "surah_number": 90,
    "verse_number": 13,
    "content": "کسی گردن کو غلامی سے چھڑانا",
  },
  {
    "surah_number": 90,
    "verse_number": 14,
    "content": "یا فاقے کے دن",
  },
  {
    "surah_number": 90,
    "verse_number": 15,
    "content": "کسی قریبی یتیم",
  },
  {
    "surah_number": 90,
    "verse_number": 16,
    "content": "یا خاک نشین مسکین کو کھانا کھلانا",
  },
  {
    "surah_number": 90,
    "verse_number": 17,
    "content": "پھر (اس کے ساتھ یہ کہ) آدمی اُن لوگوں میں شامل ہو جو ایمان لائے اور جنہوں نے ایک دوسرے کو صبر اور (خلق خدا پر) رحم کی تلقین کی",
  },
  {
    "surah_number": 90,
    "verse_number": 18,
    "content": "یہ لوگ ہیں دائیں بازو والے",
  },
  {
    "surah_number": 90,
    "verse_number": 19,
    "content": "اور جنہوں نے ہماری آیات کو ماننے سے انکار کیا وہ بائیں بازو والے ہیں",
  },
  {
    "surah_number": 90,
    "verse_number": 20,
    "content": "ان پر آگ چھائی ہوئی ہوگی",
  },
  {
    "surah_number": 91,
    "verse_number": 1,
    "content": "سورج اور اُس کی دھوپ کی قسم",
  },
  {
    "surah_number": 91,
    "verse_number": 2,
    "content": "اور چاند کی قسم جبکہ وہ اُس کے پیچھے آتا ہے",
  },
  {
    "surah_number": 91,
    "verse_number": 3,
    "content": "اور دن کی قسم جبکہ وہ (سورج کو) نمایاں کر دیتا ہے",
  },
  {
    "surah_number": 91,
    "verse_number": 4,
    "content": "اور رات کی قسم جبکہ وہ (سورج کو) ڈھانک لیتی ہے",
  },
  {
    "surah_number": 91,
    "verse_number": 5,
    "content": "اور آسمان کی اور اُس ذات کی قسم جس نے اُسے قائم کیا",
  },
  {
    "surah_number": 91,
    "verse_number": 6,
    "content": "اور زمین کی اور اُس ذات کی قسم جس نے اُسے بچھایا",
  },
  {
    "surah_number": 91,
    "verse_number": 7,
    "content": "اور نفس انسانی کی اور اُس ذات کی قسم جس نے اُسے ہموار کیا",
  },
  {
    "surah_number": 91,
    "verse_number": 8,
    "content": "پھر اُس کی بدی اور اُس کی پرہیز گاری اس پر الہام کر دی",
  },
  {
    "surah_number": 91,
    "verse_number": 9,
    "content": "یقیناً فلاح پا گیا وہ جس نے نفس کا تزکیہ کیا",
  },
  {
    "surah_number": 91,
    "verse_number": 10,
    "content": "اور نامراد ہوا وہ جس نے اُس کو دبا دیا",
  },
  {
    "surah_number": 91,
    "verse_number": 11,
    "content": "ثمود نے اپنی سرکشی کی بنا پر جھٹلایا",
  },
  {
    "surah_number": 91,
    "verse_number": 12,
    "content": "جب اُس قوم کا سب سے زیادہ شقی آدمی بپھر کر اٹھا",
  },
  {
    "surah_number": 91,
    "verse_number": 13,
    "content": "تو اللہ کے رسول نے اُن لوگوں سے کہا کہ خبردار، اللہ کی اونٹنی کو (ہاتھ نہ لگانا) اوراُس کے پانی پینے (میں مانع نہ ہونا)",
  },
  {
    "surah_number": 91,
    "verse_number": 14,
    "content": "مگر انہوں نے اُس کی بات کو جھوٹا قرار دیا اور اونٹنی کو مار ڈالا آخرکار اُن کے گناہ کی پاداش میں ان کے رب نے ان پر ایسی آفت توڑی کہ ایک ساتھ سب کو پیوند خاک کر دیا",
  },
  {
    "surah_number": 91,
    "verse_number": 15,
    "content": "اور اسے (اپنے ا س فعل کے) کسی برے نتیجے کا کوئی خوف نہیں ہے",
  },
  {
    "surah_number": 92,
    "verse_number": 1,
    "content": "قسم ہے رات کی جبکہ وہ چھا جائے",
  },
  {
    "surah_number": 92,
    "verse_number": 2,
    "content": "اور دن کی جبکہ وہ روشن ہو",
  },
  {
    "surah_number": 92,
    "verse_number": 3,
    "content": "اور اُس ذات کی جس نے نر اور مادہ کو پیدا کیا",
  },
  {
    "surah_number": 92,
    "verse_number": 4,
    "content": "درحقیقت تم لوگوں کی کوششیں مختلف قسم کی ہیں",
  },
  {
    "surah_number": 92,
    "verse_number": 5,
    "content": "تو جس نے (راہ خدا میں) مال دیا اور (خدا کی نافرمانی سے) پرہیز کیا",
  },
  {
    "surah_number": 92,
    "verse_number": 6,
    "content": "اور بھلائی کو سچ مانا",
  },
  {
    "surah_number": 92,
    "verse_number": 7,
    "content": "اس کو ہم آسان راستے کے لیے سہولت دیں گے",
  },
  {
    "surah_number": 92,
    "verse_number": 8,
    "content": "اور جس نے بخل کیا اور (اپنے خدا سے) بے نیازی برتی",
  },
  {
    "surah_number": 92,
    "verse_number": 9,
    "content": "اور بھلائی کو جھٹلایا",
  },
  {
    "surah_number": 92,
    "verse_number": 10,
    "content": "اس کو ہم سخت راستے کے لیے سہولت دیں گے",
  },
  {
    "surah_number": 92,
    "verse_number": 11,
    "content": "اور اُس کا مال آخر اُس کے کس کام آئے گا جبکہ وہ ہلاک ہو جائے؟",
  },
  {
    "surah_number": 92,
    "verse_number": 12,
    "content": "بے شک راستہ بتانا ہمارے ذمہ ہے",
  },
  {
    "surah_number": 92,
    "verse_number": 13,
    "content": "اور درحقیقت آخرت اور دنیا، دونوں کے ہم ہی مالک ہیں",
  },
  {
    "surah_number": 92,
    "verse_number": 14,
    "content": "پس میں نے تم کو خبردار کر دیا ہے بھڑکتی ہوئی آگ سے",
  },
  {
    "surah_number": 92,
    "verse_number": 15,
    "content": "اُس میں نہیں جھلسے گا مگر وہ انتہائی بد بخت",
  },
  {
    "surah_number": 92,
    "verse_number": 16,
    "content": "جس نے جھٹلایا اور منہ پھیرا",
  },
  {
    "surah_number": 92,
    "verse_number": 17,
    "content": "اور اُس سے دور رکھا جائیگا وہ نہایت پرہیزگار",
  },
  {
    "surah_number": 92,
    "verse_number": 18,
    "content": "جو پاکیزہ ہونے کی خاطر اپنا مال دیتا ہے",
  },
  {
    "surah_number": 92,
    "verse_number": 19,
    "content": "اُس پر کسی کا کوئی احسان نہیں ہے جس کا بدلہ اُسے دینا ہو",
  },
  {
    "surah_number": 92,
    "verse_number": 20,
    "content": "وہ تو صرف اپنے رب برتر کی رضا جوئی کے لیے یہ کام کرتا ہے",
  },
  {
    "surah_number": 92,
    "verse_number": 21,
    "content": "اور ضرور وہ (اُس سے) خوش ہوگا",
  },
  {
    "surah_number": 93,
    "verse_number": 1,
    "content": "قسم ہے روز روشن کی",
  },
  {
    "surah_number": 93,
    "verse_number": 2,
    "content": "اور رات کی جبکہ وہ سکون کے ساتھ طاری ہو جائے",
  },
  {
    "surah_number": 93,
    "verse_number": 3,
    "content": "(اے نبیؐ) تمہارے رب نے تم کو ہرگز نہیں چھوڑا اور نہ وہ ناراض ہوا",
  },
  {
    "surah_number": 93,
    "verse_number": 4,
    "content": "اور یقیناً تمہارے لیے بعد کا دور پہلے دور سے بہتر ہے",
  },
  {
    "surah_number": 93,
    "verse_number": 5,
    "content": "اور عنقریب تمہارا رب تم کو اتنا دے گا کہ تم خوش ہو جاؤ گے",
  },
  {
    "surah_number": 93,
    "verse_number": 6,
    "content": "کیا اس نے تم کو یتیم نہیں پایا اور پھر ٹھکانا فراہم کیا؟",
  },
  {
    "surah_number": 93,
    "verse_number": 7,
    "content": "اور تمہیں ناواقف راہ پایا اور پھر ہدایت بخشی",
  },
  {
    "surah_number": 93,
    "verse_number": 8,
    "content": "اور تمہیں نادار پایا اور پھر مالدار کر دیا",
  },
  {
    "surah_number": 93,
    "verse_number": 9,
    "content": "لہٰذا یتیم پر سختی نہ کرو",
  },
  {
    "surah_number": 93,
    "verse_number": 10,
    "content": "اور سائل کو نہ جھڑکو",
  },
  {
    "surah_number": 93,
    "verse_number": 11,
    "content": "اور اپنے رب کی نعمت کا اظہار کرو",
  },
  {
    "surah_number": 94,
    "verse_number": 1,
    "content": "(اے نبیؐ) کیا ہم نے تمہارا سینہ تمہارے لیے کھول نہیں دیا؟",
  },
  {
    "surah_number": 94,
    "verse_number": 2,
    "content": "اور تم پر سے وہ بھاری بوجھ اتار دیا",
  },
  {
    "surah_number": 94,
    "verse_number": 3,
    "content": "جو تمہاری کمر توڑے ڈال رہا تھا",
  },
  {
    "surah_number": 94,
    "verse_number": 4,
    "content": "اور تمہاری خاطر تمہارے ذکر کا آوازہ بلند کر دیا",
  },
  {
    "surah_number": 94,
    "verse_number": 5,
    "content": "پس حقیقت یہ ہے کہ تنگی کے ساتھ فراخی بھی ہے",
  },
  {
    "surah_number": 94,
    "verse_number": 6,
    "content": "بے شک تنگی کے ساتھ فراخی بھی ہے",
  },
  {
    "surah_number": 94,
    "verse_number": 7,
    "content": "لہٰذا جب تم فارغ ہو تو عبادت کی مشقت میں لگ جاؤ",
  },
  {
    "surah_number": 94,
    "verse_number": 8,
    "content": "اور اپنے رب ہی کی طرف راغب ہو",
  },
  {
    "surah_number": 95,
    "verse_number": 1,
    "content": "قسم ہے انجیر اور زیتون کی",
  },
  {
    "surah_number": 95,
    "verse_number": 2,
    "content": "اور طور سینا کی",
  },
  {
    "surah_number": 95,
    "verse_number": 3,
    "content": "اور اِس پرامن شہر (مکہ) کی",
  },
  {
    "surah_number": 95,
    "verse_number": 4,
    "content": "ہم نے انسان کو بہترین ساخت پر پیدا کیا",
  },
  {
    "surah_number": 95,
    "verse_number": 5,
    "content": "پھر اُسے الٹا پھیر کر ہم نے سب نیچوں سے نیچ کر دیا",
  },
  {
    "surah_number": 95,
    "verse_number": 6,
    "content": "سوائے اُن لوگوں کے جو ایمان لائے اور نیک عمل کرتے رہے کہ ان کے لیے کبھی ختم نہ ہونے والا اجر ہے",
  },
  {
    "surah_number": 95,
    "verse_number": 7,
    "content": "پس (اے نبیؐ) اس کے بعد کون جزا و سزا کے معاملہ میں تم کو جھٹلا سکتا ہے؟",
  },
  {
    "surah_number": 95,
    "verse_number": 8,
    "content": "کیا اللہ سب حاکموں سے بڑا حاکم نہیں ہے؟",
  },
  {
    "surah_number": 96,
    "verse_number": 1,
    "content": "پڑھو (اے نبیؐ) اپنے رب کے نام کے ساتھ جس نے پیدا کیا",
  },
  {
    "surah_number": 96,
    "verse_number": 2,
    "content": "جمے ہوئے خون کے ایک لوتھڑے سے انسان کی تخلیق کی",
  },
  {
    "surah_number": 96,
    "verse_number": 3,
    "content": "پڑھو، اور تمہارا رب بڑا کریم ہے",
  },
  {
    "surah_number": 96,
    "verse_number": 4,
    "content": "جس نے قلم کے ذریعہ سے علم سکھایا",
  },
  {
    "surah_number": 96,
    "verse_number": 5,
    "content": "انسان کو وہ علم دیا جسے وہ نہ جانتا تھا",
  },
  {
    "surah_number": 96,
    "verse_number": 6,
    "content": "ہرگز نہیں، انسان سرکشی کرتا ہے",
  },
  {
    "surah_number": 96,
    "verse_number": 7,
    "content": "اِس بنا پر کہ وہ اپنے آپ کو بے نیاز دیکھتا ہے",
  },
  {
    "surah_number": 96,
    "verse_number": 8,
    "content": "پلٹنا یقیناً تیرے رب ہی کی طرف ہے",
  },
  {
    "surah_number": 96,
    "verse_number": 9,
    "content": "تم نے دیکھا اُس شخص کو",
  },
  {
    "surah_number": 96,
    "verse_number": 10,
    "content": "جو ایک بندے کو منع کرتا ہے جبکہ وہ نماز پڑھتا ہو؟",
  },
  {
    "surah_number": 96,
    "verse_number": 11,
    "content": "تمہارا کیا خیال ہے اگر (وہ بندہ) راہ راست پر ہو",
  },
  {
    "surah_number": 96,
    "verse_number": 12,
    "content": "یا پرہیزگاری کی تلقین کرتا ہو؟",
  },
  {
    "surah_number": 96,
    "verse_number": 13,
    "content": "تمہارا کیا خیال ہے اگر (یہ منع کرنے والا شخص حق کو) جھٹلاتا اور منہ موڑتا ہو؟",
  },
  {
    "surah_number": 96,
    "verse_number": 14,
    "content": "کیا وہ نہیں جانتا کہ اللہ دیکھ رہا ہے؟",
  },
  {
    "surah_number": 96,
    "verse_number": 15,
    "content": "ہرگز نہیں، اگر وہ باز نہ آیا تو ہم اس کی پیشانی کے بال پکڑ کر اسے کھینچیں گے",
  },
  {
    "surah_number": 96,
    "verse_number": 16,
    "content": "اُس پیشانی کو جو جھوٹی اور سخت خطا کار ہے",
  },
  {
    "surah_number": 96,
    "verse_number": 17,
    "content": "وہ بلا لے اپنے حامیوں کی ٹولی کو",
  },
  {
    "surah_number": 96,
    "verse_number": 18,
    "content": "ہم بھی عذاب کے فرشتوں کو بلا لیں گے",
  },
  {
    "surah_number": 96,
    "verse_number": 19,
    "content": "ہرگز نہیں، اُس کی بات نہ مانو اور سجدہ کرو اور (اپنے رب کا) قرب حاصل کرو",
  },
  {
    "surah_number": 97,
    "verse_number": 1,
    "content": "ہم نے اِس (قرآن) کو شب قدر میں نازل کیا ہے",
  },
  {
    "surah_number": 97,
    "verse_number": 2,
    "content": "اور تم کیا جانو کہ شب قدر کیا ہے؟",
  },
  {
    "surah_number": 97,
    "verse_number": 3,
    "content": "شب قدر ہزار مہینوں سے زیادہ بہتر ہے",
  },
  {
    "surah_number": 97,
    "verse_number": 4,
    "content": "فرشتے اور روح اُس میں اپنے رب کے اذن سے ہر حکم لے کر اترتے ہیں",
  },
  {
    "surah_number": 97,
    "verse_number": 5,
    "content": "وہ رات سراسر سلامتی ہے طلوع فجر تک",
  },
  {
    "surah_number": 98,
    "verse_number": 1,
    "content": "اہل کتاب اور مشرکین میں سے جو لوگ کافر تھے (وہ اپنے کفر سے) باز آنے والے نہ تھے جب تک کہ ان کے پاس دلیل روشن نہ آ جائے",
  },
  {
    "surah_number": 98,
    "verse_number": 2,
    "content": "(یعنی) اللہ کی طرف سے ایک رسول جو پاک صحیفے پڑھ کر سنائے",
  },
  {
    "surah_number": 98,
    "verse_number": 3,
    "content": "جن میں بالکل راست اور درست تحریریں لکھی ہوئی ہوں",
  },
  {
    "surah_number": 98,
    "verse_number": 4,
    "content": "پہلے جن لوگوں کو کتاب دی گئی تھی اُن میں تفرقہ برپا نہیں ہوا مگر اِس کے بعد کہ اُن کے پاس (راہ راست) کا بیان واضح آ چکا تھا",
  },
  {
    "surah_number": 98,
    "verse_number": 5,
    "content": "اور اُن کو اِس کے سوا کوئی علم نہیں دیا گیا تھا کہ اللہ کی بندگی کریں اپنے دین کو اُس کے لیے خالص کر کے، بالکل یکسو ہو کر، اور نماز قائم کریں اور زکوٰۃ دیں یہی نہایت صحیح و درست دین ہے",
  },
  {
    "surah_number": 98,
    "verse_number": 6,
    "content": "اہل کتاب اور مشرکین میں سے جن لوگوں نے کفر کیا ہے وہ یقیناً جہنم کی آگ میں جائیں گے اور ہمیشہ اس میں رہیں گے، یہ لوگ بد ترین خلائق ہیں",
  },
  {
    "surah_number": 98,
    "verse_number": 7,
    "content": "جو لوگ ایمان لے آئے اور جنہوں نے نیک عمل کیے، وہ یقیناً بہترین خلائق ہیں",
  },
  {
    "surah_number": 98,
    "verse_number": 8,
    "content": "اُن کی جزا اُن کے رب کے ہاں دائمی قیام کی جنتیں ہیں جن کے نیچے نہریں بہ رہی ہوں گی، وہ ان میں ہمیشہ ہمیشہ رہیں گے اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے یہ کچھ ہے اُس شخص کے لیے جس نے اپنے رب کا خوف کیا ہو",
  },
  {
    "surah_number": 99,
    "verse_number": 1,
    "content": "جب زمین اپنی پوری شدت کے ساتھ ہلا ڈالی جائے گی",
  },
  {
    "surah_number": 99,
    "verse_number": 2,
    "content": "اور زمین اپنے اندر کے سارے بوجھ نکال کر باہر ڈال دے گی",
  },
  {
    "surah_number": 99,
    "verse_number": 3,
    "content": "اور انسان کہے گا کہ یہ اِس کو کیا ہو رہا ہے",
  },
  {
    "surah_number": 99,
    "verse_number": 4,
    "content": "اُس روز وہ اپنے (اوپر گزرے ہوئے) حالات بیان کرے گی",
  },
  {
    "surah_number": 99,
    "verse_number": 5,
    "content": "کیونکہ تیرے رب نے اُسے (ایساکرنے کا) حکم دیا ہوگا",
  },
  {
    "surah_number": 99,
    "verse_number": 6,
    "content": "اُس روز لوگ متفرق حالت میں پلٹیں گے تاکہ اُن کے اعمال اُن کو دکھائے جائیں",
  },
  {
    "surah_number": 99,
    "verse_number": 7,
    "content": "پھر جس نے ذرہ برابر نیکی کی ہوگی وہ اس کو دیکھ لے گا",
  },
  {
    "surah_number": 99,
    "verse_number": 8,
    "content": "اور جس نے ذرہ برابر بدی کی ہوگی وہ اس کو دیکھ لے گا",
  },
  {
    "surah_number": 100,
    "verse_number": 1,
    "content": "قسم ہے اُن (گھوڑوں) کی جو پھنکارے مارتے ہوئے دوڑتے ہیں",
  },
  {
    "surah_number": 100,
    "verse_number": 2,
    "content": "پھر (اپنی ٹاپوں سے) چنگاریاں جھاڑتے ہیں",
  },
  {
    "surah_number": 100,
    "verse_number": 3,
    "content": "پھر صبح سویرے چھاپہ مارتے ہیں",
  },
  {
    "surah_number": 100,
    "verse_number": 4,
    "content": "پھر اس موقع پر گرد و غبار اڑاتے ہیں",
  },
  {
    "surah_number": 100,
    "verse_number": 5,
    "content": "پھر اِسی حالت میں کسی مجمع کے اندر جا گھستے ہیں",
  },
  {
    "surah_number": 100,
    "verse_number": 6,
    "content": "حقیقت یہ ہے کہ انسان اپنے رب کا بڑا ناشکرا ہے",
  },
  {
    "surah_number": 100,
    "verse_number": 7,
    "content": "اور وہ خود اِس پر گواہ ہے",
  },
  {
    "surah_number": 100,
    "verse_number": 8,
    "content": "اور وہ مال و دولت کی محبت میں بری طرح مبتلا ہے",
  },
  {
    "surah_number": 100,
    "verse_number": 9,
    "content": "تو کیا وہ اُس حقیقت کو نہیں جانتا جب قبروں میں جو کچھ (مدفون) ہے اُسے نکال لیا جائے گا",
  },
  {
    "surah_number": 100,
    "verse_number": 10,
    "content": "اور سینوں میں جو کچھ (مخفی) ہے اُسے برآمد کر کے اس کی جانچ پڑتال کی جائے گی؟",
  },
  {
    "surah_number": 100,
    "verse_number": 11,
    "content": "یقیناً اُن کا رب اُس روز اُن سے خوب باخبر ہوگا",
  },
  {
    "surah_number": 101,
    "verse_number": 1,
    "content": "عظیم حادثہ",
  },
  {
    "surah_number": 101,
    "verse_number": 2,
    "content": "کیا ہے وہ عظیم حادثہ؟",
  },
  {
    "surah_number": 101,
    "verse_number": 3,
    "content": "تم کیا جانو کہ وہ عظیم حادثہ کیا ہے؟",
  },
  {
    "surah_number": 101,
    "verse_number": 4,
    "content": "وہ دن جب لوگ بکھرے ہوئے پروانوں کی طرح",
  },
  {
    "surah_number": 101,
    "verse_number": 5,
    "content": "اور پہاڑ رنگ برنگ کے دھنکے ہوئے اون کی طرح ہوں گے",
  },
  {
    "surah_number": 101,
    "verse_number": 6,
    "content": "پھر جس کے پلڑے بھاری ہوں گے",
  },
  {
    "surah_number": 101,
    "verse_number": 7,
    "content": "وہ دل پسند عیش میں ہوگا",
  },
  {
    "surah_number": 101,
    "verse_number": 8,
    "content": "اور جس کے پلڑے ہلکے ہوں گے",
  },
  {
    "surah_number": 101,
    "verse_number": 9,
    "content": "اس کی جائے قرار گہری کھائی ہوگی",
  },
  {
    "surah_number": 101,
    "verse_number": 10,
    "content": "اور تمہیں کیا خبر کہ وہ کیا چیز ہے؟",
  },
  {
    "surah_number": 101,
    "verse_number": 11,
    "content": "بھڑکتی ہوئی آگ",
  },
  {
    "surah_number": 102,
    "verse_number": 1,
    "content": "تم لوگوں کو زیادہ سے زیادہ اور ایک دوسرے سے بڑھ کر دنیا حاصل کرنے کی دھن نے غفلت میں ڈال رکھا ہے",
  },
  {
    "surah_number": 102,
    "verse_number": 2,
    "content": "یہاں تک کہ (اسی فکر میں) تم لب گور تک پہنچ جاتے ہو",
  },
  {
    "surah_number": 102,
    "verse_number": 3,
    "content": "ہرگز نہیں، عنقریب تم کو معلوم ہو جائے گا",
  },
  {
    "surah_number": 102,
    "verse_number": 4,
    "content": "پھر (سن لو کہ) ہرگز نہیں، عنقریب تم کو معلوم ہو جائے گا",
  },
  {
    "surah_number": 102,
    "verse_number": 5,
    "content": "ہرگز نہیں، اگر تم یقینی علم کی حیثیت سے (اِس روش کے انجام کو) جانتے ہوتے (تو تمہارا یہ طرز عمل نہ ہوتا)",
  },
  {
    "surah_number": 102,
    "verse_number": 6,
    "content": "تم دوزخ دیکھ کر رہو گے",
  },
  {
    "surah_number": 102,
    "verse_number": 7,
    "content": "پھر (سن لو کہ) تم بالکل یقین کے ساتھ اُسے دیکھ لو گے",
  },
  {
    "surah_number": 102,
    "verse_number": 8,
    "content": "پھر ضرور اُس روز تم سے اِن نعمتوں کے بارے میں جواب طلبی کی جائے گی",
  },
  {
    "surah_number": 103,
    "verse_number": 1,
    "content": "زمانے کی قسم",
  },
  {
    "surah_number": 103,
    "verse_number": 2,
    "content": "انسان درحقیقت بڑے خسارے میں ہے",
  },
  {
    "surah_number": 103,
    "verse_number": 3,
    "content": "سوائے اُن لوگوں کے جو ایمان لائے، اور نیک اعمال کرتے رہے، اور ایک دوسرے کو حق کی نصیحت اور صبر کی تلقین کرتے رہے",
  },
  {
    "surah_number": 104,
    "verse_number": 1,
    "content": "تباہی ہے ہر اُس شخص کے لیے جو (منہ در منہ) لوگوں پر طعن اور (پیٹھ پیچھے) برائیاں کرنے کا خوگر ہے",
  },
  {
    "surah_number": 104,
    "verse_number": 2,
    "content": "جس نے مال جمع کیا اور اُسے گن گن کر رکھا",
  },
  {
    "surah_number": 104,
    "verse_number": 3,
    "content": "وہ سمجھتا ہے کہ اُس کا مال ہمیشہ اُس کے پاس رہے گا",
  },
  {
    "surah_number": 104,
    "verse_number": 4,
    "content": "ہرگز نہیں، وہ شخص تو چکنا چور کر دینے والی جگہ میں پھینک دیا جائے گا",
  },
  {
    "surah_number": 104,
    "verse_number": 5,
    "content": "اور تم کیا جانو کہ کیا ہے وہ چکنا چور کر دینے والی جگہ؟",
  },
  {
    "surah_number": 104,
    "verse_number": 6,
    "content": "اللہ کی آگ، خوب بھڑکائی ہوئی",
  },
  {
    "surah_number": 104,
    "verse_number": 7,
    "content": "جو دلوں تک پہنچے گی",
  },
  {
    "surah_number": 104,
    "verse_number": 8,
    "content": "وہ اُن پر ڈھانک کر بند کر دی جائے گی",
  },
  {
    "surah_number": 104,
    "verse_number": 9,
    "content": "(اِس حالت میں کہ وہ) اونچے اونچے ستونوں میں (گھرے ہوئے ہوں گے)",
  },
  {
    "surah_number": 105,
    "verse_number": 1,
    "content": "تم نے دیکھا نہیں کہ تمہارے رب نے ہاتھی والوں کے ساتھ کیا کیا؟",
  },
  {
    "surah_number": 105,
    "verse_number": 2,
    "content": "کیا اُس نے اُن کی تدبیر کو اکارت نہیں کر دیا؟",
  },
  {
    "surah_number": 105,
    "verse_number": 3,
    "content": "اور اُن پر پرندوں کے جھنڈ کے جھنڈ بھیج دیے",
  },
  {
    "surah_number": 105,
    "verse_number": 4,
    "content": "جو اُن پر پکی ہوئی مٹی کے پتھر پھینک رہے تھے",
  },
  {
    "surah_number": 105,
    "verse_number": 5,
    "content": "پھر اُن کا یہ حال کر دیا جیسے جانوروں کا کھایا ہوا بھوسا",
  },
  {
    "surah_number": 106,
    "verse_number": 1,
    "content": "چونکہ قریش مانوس ہوئے",
  },
  {
    "surah_number": 106,
    "verse_number": 2,
    "content": "(یعنی) جاڑے اور گرمی کے سفروں سے مانوس",
  },
  {
